(window.webpackJsonp=window.webpackJsonp||[]).push([[741],{764:function(p){p.exports={chapterTitle:null,title:"\u7b2c579\u7ae0 \u56de\u6821\u4e0a\u8bfe",content:"<article><h2>\u7b2c579\u7ae0 \u56de\u6821\u4e0a\u8bfe</h2><p>\u3000\u3000\u76f4\u5230\u516d\u6708\u4e0a\u65ec\u5728\u8054\u4fe1\u96c6\u56e2\u4e0e\u98de\u5229\u6d66\u7b7e\u8ba2\u624b\u673a\u751f\u4ea7\u5408\u4f5c\u534f\u8bae\u4e4b\u540e\uff0c\u90ae\u7535\u90e8\u624d\u8feb\u4e8e\u5404\u65b9\u9762\u7684\u538b\u529b\u6b63\u5f0f\u505a\u51fa\u201c\u540c\u610f\u7231\u8fbe\u3001\u4e1c\u5174\u3001\u8054\u8baf\u3001\u8054\u4fe1\u7b49\u56db\u5bb6\u516c\u53f8\u751f\u4ea7\u79fb\u52a8\u901a\u4fe1\u7ec8\u7aef\u8bbe\u5907\u7ecf\u68c0\u6d4b\u5408\u683c\u5165\u7f51\u201d\u7684\u6279\u590d\u3002</p><p>\u3000\u3000\u6bd4\u8f83\u8d77\u8054\u4fe1\u4e0e\u98de\u5229\u6d66\u7b7e\u7f72\u7684\u5408\u4f5c\u534f\u8bae\u53ea\u662f\u91c7\u8d2d\u98de\u5229\u6d66\u534a\u6210\u54c1\u624b\u673a\u914d\u4ef6\u5230\u56fd\u5185\u7ec4\u88c5\u8d34\u724c\u9500\u552e\u7684\u6a21\u5f0f\uff0c\u9526\u6e56\u4ece\u5fb7\u4eea\u5f15\u8fdb\u7684\u9664\u57fa\u5e26\u82af\u7247\u4e4b\u5916\u7684\u6240\u6709\u6280\u672f\uff0c\u751a\u81f3\u5305\u62ecSTN\u7ea7\u7684\u6db2\u6676\u663e\u793a\u5c4f\u751f\u4ea7\u6280\u672f\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u6240\u8c13\u7684\u6280\u672f\u540d\u8bcd\u4e0d\u719f\u6089\uff0c\u5f53\u4e01\u69d0\u5c06\u6837\u54c1\u62ff\u7ed9\u4ed6\u770b\uff0c\u5c31\u77e5\u9053\u6240\u8c13\u7684STN\u7ea7\u6db2\u6676\uff0c\u4e0d\u8fc7\u662f\u4ed6\u6240\u6700\u5e38\u63a5\u89e6\u5230\u7684\u9ed1\u767d\u6db2\u6676\u5c4f\u800c\u5df2\uff0c\u65e9\u671f\u7684\u6570\u5b57\u624b\u673a\u90fd\u4f7f\u7528\u8fd9\u79cd\u5c4f\uff1bSTN\u6db2\u6676\u663e\u793a\u5c4f\u5728\u56fd\u5185\u6709\u4e8c\u5341\u51e0\u6761\u7ebf\uff0c\u5373\u4f7f\u5fb7\u4eea\u8f6c\u8ba9\u7684\u6280\u672f\u91cc\u6709\u72ec\u5230\u4e4b\u5904\uff0c\u4e5f\u53ea\u80fd\u5f52\u5165\u4f4e\u7aef\u6280\u672f\u4e4b\u5217\u3002\u4e01\u69d0\u89e3\u91ca\u8bf4STN\u7ea7\u6db2\u6676\u6709\u56fe\u50cf\u5904\u7406\u6280\u672f\u652f\u6301\u53ef\u4ee5\u663e\u793a\u5f69\u8272\u56fe\u50cf\uff0c\u5f20\u606a\u770b\u5230\u6837\u54c1\uff0c\u5c31\u77e5\u9053\u8fd9\u662f\u65e5\u540e\u4f2a\u5f69\u5c4f\u624b\u673a\u5e38\u7528\u7684\u663e\u793a\u5c4f\uff0c\u8272\u5f69\u9eef\u6de1\u4e0d\u8bf4\uff0c\u529f\u8017\u8fd8\u5927\u3002\u6240\u8c13\u201c\u4e00\u65e5\u4e00\u5145\u201d\u578b\u7684\u56fd\u4ea7\u5f69\u5c4f\u624b\u673a\u5c31\u4f7f\u7528\u8fd9\u79cd\u4f2a\u5f69\u5c4f\uff0c\u4e0d\u8fc7\u4e5f\u8981\u7b49\u5230\u96f6\u4e00\u3001\u96f6\u4e8c\u5e74\u624d\u80fd\u5145\u585e\u5e02\u573a\u3002</p><p>\u3000\u3000\u6db2\u6676\u5c4f\u53ef\u4ee5\u8bf4\u662f\u65e5\u540e\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\u6240\u5fc5\u7528\u7684\u914d\u4ef6\uff0c\u9526\u6e56\u65d7\u4e0b\u5df2\u7ecf\u5f00\u53d1\u51fa\u6765\u7684\u51e0\u7c7b\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\u5f71\u789f\u673a\u3001\u7535\u5b50\u8bcd\u5178\u3001\u638c\u4e0a\u7535\u8111\u4ee5\u53ca\u624b\u673a\u8fd8\u6709MP3\u64ad\u653e\u5668\uff0c\u90fd\u8981\u7528\u4e0a\u6db2\u6676\u5c4f\u3002\u9526\u6e56\u65e5\u540e\u4f1a\u4e13\u6ce8\u4e8e\u6d88\u8d39\u7535\u5b50\u5e02\u573a\uff0c\u4f46\u662f\u5f20\u606a\u5fc3\u91cc\u6700\u6e05\u695a\uff0c\u4e94\u5e74\u4e4b\u540e\uff0c\u6db2\u6676\u7535\u89c6\u4ee5\u53ca\u6db2\u6676\u7535\u8111\u663e\u793a\u5c4f\u7684\u666e\u53ca\u624d\u662f\u771f\u6b63\u4fc3\u4f7f\u6db2\u6676\u4ea7\u4e1a\u5168\u7403\u5d1b\u8d77\u7684\u5e02\u573a\u5927\u673a\u9047\u3002</p><p>\u3000\u3000\u597d\u5427\uff0c\u6709\u4e9b\u5de5\u4f5c\u63d0\u524d\u505a\u8d77\u6765\u4e5f\u662f\u53ef\u4ee5\u7684\uff0c\u6280\u672f\u4ece\u4f4e\u7aef\u5230\u9ad8\u7aef\u53d1\u5c55\uff0c\u603b\u9700\u8981\u4e00\u4e2a\u5f15\u8fdb\u3001\u6d88\u5316\u3001\u7d2f\u79ef\u7684\u8fc7\u7a0b\u3002\u91cf\u4ea7\u7684\u597d\u5904\u5c31\u662f\u4ee5\u7231\u8fbe\u65d7\u4e0b\u7684\u4ea7\u54c1\u5bf9\u914d\u4ef6\u7684\u9700\u6c42\u91cf\u53ef\u4ee5\u6700\u5927\u9650\u5ea6\u7684\u964d\u4f4e\u914d\u4ef6\u7684\u751f\u4ea7\u3001\u7814\u53d1\u6210\u672c\u3002</p><p>\u3000\u3000\u8d85\u8584\u9ad8\u6e05\u7535\u89c6\u6280\u672f\uff0c\u4e5d\u4e03\u5e74\u5df2\u6709\u96cf\u5f62\uff0c\u57fa\u672c\u5f62\u6210\u56db\u79cd\u6280\u672f\u9635\u8425\uff0c\u9664\u4e86\u6db2\u6676\u7535\u89c6\u6280\u672f\u4e4b\u5916\uff0c\u56fd\u5185\u8f83\u4e3a\u719f\u6089\u7684\u8fd8\u6709\u7b49\u79bb\u5b50\u9ad8\u6e05\u7535\u89c6\u6280\u672f\u3002</p><p>\u3000\u3000\u5728\u6d77\u5916\u7535\u5b50\u5de5\u4e1a\u5de8\u5934\u4e3a\u56db\u79cd\u6280\u672f\u53d1\u5c55\u65b9\u5411\u72b9\u8c6b\u4e0d\u51b3\u4e4b\u65f6\uff0c\u5f20\u606a\u5f53\u7136\u80fd\u65ad\u5b9aTFT\u6db2\u6676\u663e\u793a\u662f\u6570\u5e74\u4e4b\u540e\u9ad8\u6e05\u7535\u89c6\u6280\u672f\u53d1\u5c55\u7684\u4e3b\u6d41\uff1b\u5bf9\u8fd9\u79cd\u8d8b\u52bf\u7684\u6e05\u6670\u8ba4\u77e5\u662f\u9526\u6e56\u6700\u5927\u7684\u4f18\u52bf\u3002</p><p>\u3000\u3000\u9075\u5faa\u6d88\u8d39\u7535\u5b50\u6280\u672f\u53d1\u5c55\u7684\u8d8b\u52bf\uff0c\u5f15\u8fdb\u3001\u6d88\u5316\u5e76\u53d1\u5c55\u76f8\u5173\u7684\u6d88\u8d39\u7535\u5b50\u6280\u672f\uff0c\u624d\u662f\u5f20\u606a\u7ed9\u9526\u6e56\u5236\u5b9a\u7684\u6700\u7ec8\u9009\u62e9\u8981\u8d70\u7684\u6280\u672f\u6269\u5f20\u4e4b\u8def\u3002</p><p>\u3000\u3000\u5f20\u606a\u671f\u5f85\u6a61\u6811\u56ed\u8ba1\u5212\u80fd\u57f9\u517b\u51fa\u4e00\u6279\u771f\u6b63\u5177\u5907\u4e16\u754c\u7ea7\u6c34\u51c6\u7684\u5b9e\u9a8c\u5ba4\uff0c\u4e3a\u9526\u6e56\u5728\u5168\u7403\u6d88\u8d39\u7535\u5b50\u7684\u53d1\u5c55\u63d0\u4f9b\u575a\u5b9e\u7684\u6280\u672f\u57fa\u7840\u3002</p><p>\u3000\u3000\u516d\u6708\u4e0a\u65ec\uff0c\u5728\u7231\u8fbe\u901a\u4fe1\u7535\u5b50\u6709\u9650\u516c\u53f8\u6302\u724c\u6210\u7acb\u7684\u540c\u65f6\uff0c\u9526\u6e56\u8fd8\u540c\u65f6\u6ce8\u518c\u6210\u7acb\u7231\u8fbe\u6676\u663e\u7535\u5b50\u4e0e\u7231\u8fbe\u65b0\u6750\u6599\u6709\u9650\u516c\u53f8\u3002</p><p>\u3000\u3000\u9526\u6e56\u8fd8\u6709\u4e00\u9879\u4f18\u52bf\u662f\u8054\u4fe1\u3001\u8054\u8baf\u4ee5\u53ca\u4e1c\u5174\u7b49\u516c\u53f8\u6240\u65e0\u6cd5\u6bd4\u62df\u7684\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u5c06\u9664\u57fa\u5e26\u82af\u7247\u4e4b\u5916\u7684\u6240\u6709\u624b\u673a\u751f\u4ea7\u6280\u672f\u8f6c\u8ba9\u7ed9\u9526\u6e56\u4e4b\u65f6\uff0c\u5c31\u662f\u8981\u4e0b\u51b3\u5fc3\u780d\u6389\u8fd9\u4e9b\u975e\u6838\u5fc3\u4e1a\u52a1\uff0c\u8fd9\u662f\u5fb7\u4eea\u9996\u5e2d\u6267\u884c\u5b98\u3001\u8463\u4e8b\u957f\u5b89\u5409\u4f2f\u7684\u201c\u6838\u5fc3\u5316\u3001\u4e13\u4e1a\u5316\u201d\u7ecf\u8425\u7406\u5ff5\u2014\u2014\u4e5f\u662f\u6b27\u7f8e\u4f01\u4e1a\u7684\u4e3b\u6d41\u601d\u60f3\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u4e1a\u52a1\u780d\u6389\u4e4b\u540e\uff0c\u5fb7\u4eea\u7684\u76f8\u5173\u5de5\u4f5c\u4eba\u5458\u4f1a\u8fdb\u884c\u5185\u90e8\u5c97\u4f4d\u5206\u6d41\uff0c\u5f53\u7136\u4e5f\u4f1a\u6709\u4e00\u90e8\u5206\u5de5\u7a0b\u5e08\u8981\u7ed9\u88c1\u5458\uff1b\u6839\u636e\u9526\u6e56\u4e0e\u5fb7\u4eea\u7684\u534f\u8bae\uff0c\u9526\u6e56\u63a5\u53d7\u4e86\u4e00\u6279\u5de5\u7a0b\u5e08\u2014\u2014\u8fd9\u4e5f\u4e3b\u8981\u5f97\u76ca\u4e8e\u9526\u6e56\u4e24\u5e74\u591a\u6765\u4e0e\u5fb7\u4eea\u6109\u5feb\u7684\u5408\u4f5c\uff0c\u5fb7\u4eea\u5728\u53d1\u5c55\u6838\u5fc3\u624b\u673a\u57fa\u5e26\u82af\u7247\u6280\u672f\u7684\u540c\u65f6\u4e5f\u9700\u8981\u76f8\u5173\u5916\u56f4\u5e94\u7528\u6280\u672f\u7684\u652f\u6301\uff0c\u4ed6\u4eec\u9009\u62e9\u5728\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u4f9d\u8d56\u4e8e\u9526\u6e56\u8fd9\u4e2a\u5408\u4f5c\u4f19\u4f34\uff0c\u8fd9\u4e5f\u5c06\u4f7f\u5f97\u9526\u6e56\u7684\u624b\u673a\u5236\u9020\u6280\u672f\u80fd\u7d27\u7d27\u8ddf\u4e0a\u5fb7\u4eea\u7684\u57fa\u5e26\u82af\u7247\u6280\u672f\u3002</p><p>\u3000\u3000\u4e00\u90e8\u5206\u5de5\u7a0b\u5e08\u5e76\u5165ESS\u7684\u7814\u53d1\u5c0f\u7ec4\uff0c\u4f7f\u5f97ESS\u53ea\u7528\u4e86\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u5728\u516d\u6708\u4e0a\u65ec\u5c31\u62ff\u51fa\u50cf\u6a21\u50cf\u6837\u7684\u624b\u673a\u6837\u673a\uff1b\u4e00\u90e8\u5206\u5177\u5907\u534e\u88d4\u80cc\u666f\u3001\u5bf9\u56fd\u5185\u6587\u5316\u6bd4\u8f83\u8ba4\u540c\u6216\u8005\u62e5\u6709\u4e00\u5b9a\u751f\u4ea7\u7ba1\u7406\u7ecf\u9a8c\u7684\u5de5\u7a0b\u5e08\u5219\u5e76\u5165\u751f\u4ea7\u7b79\u5907\u5c0f\u7ec4\u3002\u751f\u4ea7\u7b79\u5907\u4e3b\u8981\u662f\u6210\u5957\u751f\u4ea7\u8bbe\u5907\u7684\u5f15\u8fdb\u4e0e\u65b0\u5458\u5de5\u53ca\u6280\u672f\u4eba\u5458\u7684\u57f9\u8bad\uff1b\u81f3\u4e8e\u751f\u4ea7\u57fa\u5730\u7684\u5efa\u8bbe\u2014\u2014\u57ce\u5357\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u65e9\u5c31\u5efa\u6210\u4e00\u6279\u6807\u51c6\u7684\u7535\u5b50\u5382\u623f\uff0c\u9526\u6e56\u8981\u8ffd\u4e0a\u6d77\u5916\u7535\u5b50\u5de8\u5934\uff0c\u7edd\u4e0d\u5e94\u8be5\u56e0\u4e3a\u57fa\u5efa\u7684\u56e0\u7d20\u800c\u5ef6\u8bef\u5b9d\u8d35\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u5373\u4f7f\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u7b49\u516c\u53f8\u6709\u575a\u5b9a\u7684\u51b3\u5fc3\u8981\u4f7f\u624b\u673a\u751f\u4ea7\u56fd\u4ea7\u5316\uff0c\u4e5f\u9700\u8981\u6bd4\u9526\u6e56\u8d70\u4e00\u6bb5\u66f4\u6f2b\u957f\u7684\u9053\u8def\u2014\u2014\u4f55\u51b5\u4ed6\u4eec\u73b0\u5728\u6839\u672c\u5c31\u6ca1\u6709\u8fd9\u4e2a\u51b3\u5fc3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8c01\u80fd\u60f3\u8c61\u56fd\u5185\u5341\u5e74\u540e\u6700\u4f4e\u7aef\u7684\u5c71\u5be8\u624b\u673a\u5e02\u573a\u5c31\u6709\u8d85\u8fc7\u5343\u4ebf\u7684\u89c4\u6a21\uff0c\u66f4\u4e0d\u7528\u8bf4\u4e2d\u9ad8\u7aef\u5e02\u573a\u4e86\uff0c\u800c\u540c\u65f6\u671fMP3\u97f3\u4e50\u64ad\u653e\u5668\u7684\u5168\u7403\u5e02\u573a\u603b\u89c4\u6a21\u751a\u81f3\u4e0d\u8db3\u5343\u4ebf\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u80fd\u6b7b\u6b7b\u7684\u54ac\u4e0a\u4eca\u5e74\u5341\u5e74\u6d88\u8d39\u7535\u5b50\u5e02\u573a\u6700\u5927\u7684\u4e00\u5757\u80a5\u8089\uff0c\u5f20\u606a\u5dee\u4e0d\u591a\u5728\u5916\u9762\u6d41\u6d6a\u4e86\u534a\u5e74\uff0c\u8fd9\u534a\u5e74\u65f6\u95f4\u91cc\uff0c\u4ed6\u56de\u6d77\u5dde\u7684\u65f6\u95f4\u4e0d\u8db3\u4e00\u4e2a\u6708\u3002</p><p>\u3000\u3000\u516d\u6708\u4e0a\u65ec\uff0c\u5b66\u5e9c\u5df7\u5c01\u95ed\u8d77\u6765\u8fdb\u884c\u62c6\u9664\u5de5\u4f5c\uff0c\u6a61\u6811\u56ed\u8ba1\u5212\u4e2d\u7684\u521b\u4e1a\u56ed\u4e1c\u533a\u5730\u7f06\u5e03\u7ebf\u5de5\u4f5c\u63a5\u8fd1\u5c3e\u58f0\uff0c\u624b\u673a\u724c\u7167\u7684\u5de8\u5927\u60ac\u5ff5\u94ff\u7136\u843d\u5730\uff0c\u62ff\u7740ESS\u7814\u53d1\u5c0f\u7ec4\u5f00\u53d1\u51fa\u6765\u7684\u7b2c\u4e00\u53ea\u624b\u673a\u6837\u673a\uff0c\u5f20\u606a\u56de\u5230\u6d77\u5dde\uff0c\u4ed6\u8fd9\u65f6\u62b9\u62b9\u5c41\u80a1\u5c06\u6240\u6709\u7684\u4e8b\u52a1\u4ea4\u7ed9\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u8096\u664b\u6210\u3001\u5468\u6e38\u3001\u8521\u7eef\u5a1f\u7b49\u4eba\u3002</p><p>\u3000\u3000\u516d\u6708\u4e0a\u65ec\uff0c\u6d77\u5dde\u5929\u6c14\u5df2\u7ecf\u6709\u4e9b\u708e\u70ed\u4e86\uff0c\u7a7f\u7740T\u6064\u886b\uff0c\u5750\u4e34\u7a97\u7684\u5ea7\u4f4d\u4e0a\uff0c\u5f20\u606a\u4e00\u4f1a\u513f\u62ac\u5934\u770b\u7740\u7a97\u5916\u98ce\u8f7b\u4e91\u6de1\u3001\u4e00\u78a7\u5982\u6d17\u7684\u5929\u7a7a\uff0c\u4e00\u4f1a\u513f\u628a\u73a9\u7740\u624b\u91cc\u7684\u624b\u673a\u3002\u7ad9\u5728\u8bb2\u53f0\u4e0a\u7684\u6570\u5b66\u8001\u5e08\u770b\u7740\u5f20\u606a\u60a0\u95f2\u7684\u6a21\u6837\uff0c\u5fc3\u91cc\u5c31\u50cf\u7ed9\u585e\u4e86\u4e00\u56e2\u6742\u8349\u4f3c\u7684\u90c1\u95f7\uff0c\u4ed6\u5fc3\u91cc\u6709\u4e00\u80a1\u51b2\u52a8\uff0c\u60f3\u8d70\u5230\u5f20\u606a\u7684\u8ddf\u524d\u5bf9\u4ed6\u8bf4\uff1a\u8fd9\u4f4d\u540c\u5b66\uff0c\u5c31\u5269\u4e0b\u4e00\u4e2a\u6708\u4e86\uff0c\u4f60\u4fdd\u9001\u4e1c\u6d77\u5927\u5b66\u7684\u4e8b\u60c5\u90fd\u5df2\u7ecf\u786e\u5b9a\u4e0b\u6765\u4e86\uff0c\u4f60\u5c31\u6ca1\u6709\u5fc5\u8981\u518d\u5230\u5b66\u6821\u6765\u7ed9\u6211\u4eec\u8fd9\u4e9b\u8001\u5e08\u5fc3\u91cc\u6dfb\u5835\u4e86\u3002</p><p>\u3000\u3000\u73ed\u4e0a\u6709\u4e00\u4e2a\u7537\u5b69\u5b50\u662f\u8fd9\u5b66\u671f\u624d\u901a\u8fc7\u5173\u7cfb\u4ece\u5916\u6821\u8f6c\u8fdb\u6765\u7684\uff0c\u6307\u671b\u6700\u540e\u4e00\u5b66\u671f\u5728\u6d77\u5dde\u4e00\u4e2d\u80fd\u51b2\u523a\u4e00\u4e0b\u3002\u4ed6\u4e0d\u8ba4\u8bc6\u5f20\u606a\uff0c\u4e0b\u5348\u7a81\u7136\u770b\u5230\u6559\u5ba4\u91cc\u591a\u4e86\u4e00\u4e2a\u964c\u751f\u7684\u4eba\uff0c\u4ed6\u5341\u5206\u8be7\u5f02\uff1a\u8c01\u4ed6\u5988\u65e0\u804a\u8fd9\u65f6\u5019\u8f6c\u5b66\uff0c\u5bb6\u91cc\u6709\u80fd\u8010\u5c31\u5f80\u897f\u85cf\u3001\u5185\u8499\u7684\u5b66\u6821\u8f6c\u554a\uff01\u90a3\u91cc\u9ad8\u8003\u80fd\u6709\u4e2a\u4e09\u56db\u767e\u5206\u5c31\u80fd\u4e0a\u4e1c\u5927\u3001\u6e05\u534e\u4e86\u3002\u770b\u5230\u73ed\u4e0a\u7684\u6d3b\u8dc3\u4eba\u7269\u675c\u98de\u3001\u5510\u5a67\u8ddf\u8fd9\u5c0f\u5b50\u4e00\u4ed8\u5f88\u719f\u6089\u3001\u751a\u81f3\u53ef\u4ee5\u8bf4\u5f97\u4e0a\u4eb2\u6635\u7684\u6a21\u6837\uff0c\u5fc3\u91cc\u96be\u514d\u5992\u5fcc\uff0c\u5fcd\u4e0d\u4f4f\u5728\u8bfe\u5802\u4e0a\u6084\u6084\u7684\u95ee\u540c\u684c\uff1a\u201c\u8fd9\u5c0f\u5b50\u662f\u8c01\u554a\uff0c\u8fd9\u4e48\u725b\u6bd4\uff0c\u4e0a\u8bfe\u8fd8\u73a9\u624b\u673a\uff0c\u674e\u8000\u5149\u773c\u775b\u779f\u4e86\u90a3\u8fb9\u51e0\u56de\uff0c\u90fd\u4e0d\u6562\u8bf4\u4ed6\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u554a\uff0c\u201d\u4ed6\u540c\u684c\u6781\u4e3a\u90c1\u95f7\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u6211\u9ad8\u4e2d\u4e09\u5e74\u5047\u671f\u52a0\u8d77\u6765\u90fd\u4e0d\u77e5\u9053\u6709\u6ca1\u6709\u534a\u5e74\uff0c\u4ed6\u51fa\u73b0\u5728\u5b66\u6821\u7684\u65f6\u95f4\u52a0\u8d77\u6765\u90fd\u4e0d\u6653\u5f97\u6709\u6ca1\u6709\u534a\u5e74\uff0c\u8fd9\u8fd8\u8981\u7b97\u4e0a\u4ed6\u5728\u5b66\u6821\u9732\u4e2a\u9762\u5c31\u8d70\u7684\u65e5\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u662f\u6211\u4eec\u73ed\u7684\uff0c\u8fd9\u5e74\u5934\u4e0d\u5174\u8bf4\u4e00\u4e2d\u56db\u5927\u6076\u5c11\u4e86\uff0c\u4ed6\u7684\u786e\u662f\u56db\u5927\u6076\u5c11\u4e4b\u9996\uff0c\u8fd9\u5b66\u671f\u597d\u50cf\u6ca1\u89c1\u4ed6\u5728\u6559\u5ba4\u91cc\u51fa\u73b0\u8fc7\uff0c\u771f\u662f\u5947\u4e86\u602a\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u54e6\uff01\u201d\u90a3\u5c0f\u5b50\u53ea\u89c9\u5934\u9876\u5403\u75bc\uff0c\u60ca\u60e7\u7684\u62ac\u8d77\u5934\u770b\u89c1\u6570\u5b66\u8001\u5e08\u674e\u8000\u5149\u6b63\u6012\u76ee\u77aa\u7740\u4ed6\uff0c\u7c89\u7b14\u5934\u5728\u8bfe\u684c\u4e86\u8df3\u4e86\u51e0\u4e0b\u53c8\u843d\u5230\u5730\u4e0a\uff0c\u4ed6\u5fc3\u91cc\u4e00\u865a\uff0c\u62c5\u5fc3\u7ed9\u62ce\u51fa\u53bb\u6559\u8bad\u4e00\u901a\uff0c\u4f4e\u7740\u5934\uff0c\u773c\u89d2\u77a5\u7740\u4e00\u8138\u4e25\u8083\u7684\u674e\u8000\u5149\uff0c\u5c31\u6015\u4ed6\u8fd9\u65f6\u5019\u671d\u81ea\u5df1\u8d70\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u53ee\u94c3\u94c3\u2026\u2026\u201d\u6559\u5ba4\u5916\u65e0\u5899\u89d2\u4e0a\u7535\u94c3\u6025\u4fc3\u7684\u54cd\u8d77\u6765\uff0c\u4e0b\u8bfe\u7684\u65f6\u95f4\u5230\u4e86\u3002</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u5f20\u606a\u8212\u5c55\u8eab\u4f53\uff0c\u677f\u51f3\u5750\u4e86\u534a\u5929\uff0c\u7adf\u7136\u4f1a\u8170\u808c\u9178\u9ebb\uff0c\u771f\u662f\u597d\u4e45\u6ca1\u6709\u4f53\u5473\u5b66\u6821\u751f\u6d3b\u4e86\u3002</p><p>\u3000\u3000\u201c\u597d\u5427\uff0c\u73b0\u5728\u6709\u65f6\u95f4\u542c\u4f60\u8bf4\u4e86\uff0c\u201d\u5510\u5a67\u5c06\u6367\u7740\u7684\u51e0\u672c\u4e66\u653e\u5230\u5f20\u606a\u7684\u4e66\u684c\u4e0a\uff0c\u5750\u5230\u4ed6\u7684\u5bf9\u9762\uff0c\u624b\u638c\u53e0\u653e\u5728\u4e66\u684c\u4e0a\uff0c\u5f20\u606a\u770b\u5230\u7684\u6700\u7f8e\u4e3d\u7684\u4e0b\u5df4\u78d5\u5728\u624b\u80cc\u4e0a\uff0c\u6ef4\u6e9c\u6e9c\u6c34\u94f6\u4e38\u4f3c\u7684\u773c\u77b3\u51dd\u7738\u770b\u7740\u5f20\u606a\uff0c\u201c\u8fd8\u4ee5\u4e3a\u4f60\u90fd\u4e0d\u53ef\u80fd\u51fa\u73b0\u5728\u6559\u5ba4\u91cc\uff0c\u600e\u4e48\u4e00\u58f0\u62db\u547c\u90fd\u4e0d\u6253\uff0c\u4e2d\u5348\u5c31\u7a81\u7136\u5750\u5230\u6559\u5ba4\u91cc\uff1f\u5bb3\u5f97\u6211\u4e0b\u5348\u90fd\u6ca1\u6709\u5fc3\u601d\u542c\u8bfe\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u771f\u662f\u4e00\u4e0b\u5348\u90fd\u6ca1\u6709\u5fc3\u601d\u542c\u8bfe\uff1f\u201d\u5f20\u606a\u4e5f\u5b66\u5510\u5a67\u5c06\u4e0b\u5df4\u78d5\u5728\u624b\u80cc\u4e0a\uff0c\u4e24\u4eba\u7684\u773c\u775b\u5c31\u8ddd\u79bb\u516b\u516c\u5206\u7684\u6837\u5b50\u51dd\u671b\u7740\uff0c\u80fd\u770b\u5230\u5bf9\u65b9\u77b3\u5b54\u91cc\u81ea\u5df1\u7684\u5f71\u5b50\uff0c\u201c\u7a81\u7136\u60f3\u56de\u6765\u8ddf\u4f60\u4e00\u8d77\u6e21\u8fc7\u6700\u540e\u4e00\u4e2a\u6708\u7684\u9ad8\u4e2d\u65f6\u5149\uff0c\u5c31\u628a\u6240\u6709\u4e8b\u60c5\u90fd\u4e22\u5f00\u4e86\u3002\u4f60\u8981\u662f\u4e0d\u9ad8\u5174\uff0c\u90a3\u6211\u660e\u5929\u8d77\u5c31\u5b85\u5728\u5bb6\u91cc\u597d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8c01\u8bf4\u4e0d\u9ad8\u5174\u4e86\uff1f\u201d\u5510\u5a67\u62bf\u5634\u751c\u751c\u7684\u7b11\u7740\uff0c\u201c\u4e0d\u8fc7\u4f60\u4e0d\u8bb8\u62ff\u8bdd\u54c4\u6211\u54e6\uff0c\u6211\u53ef\u662f\u5f88\u8ba4\u771f\u671f\u5f85\u4f60\u8fd9\u4e00\u4e2a\u6708\u80fd\u966a\u6211\u3002\u201d\u4f38\u51fa\u6676\u83b9\u5254\u900f\u7684\u5c0f\u62c7\u6307\u6446\u4e86\u6446\uff0c\u773c\u775b\u95ea\u7740\u7075\u9ee0\u7684\u5149\u8292\uff0c\u201c\u4f60\u8fd8\u8981\u5e2e\u6211\u590d\u4e60\u529f\u8bfe\u554a\uff0c\u4e0d\u8bb8\u6363\u4e71\uff01\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u95ee\u9898\u2026\u2026\u201d\u5f20\u606a\u4f38\u51fa\u5c3e\u6307\u8ddf\u5510\u5a67\u52fe\u4e86\u52fe\uff0c\u89c6\u7ebf\u5374\u6ca1\u6709\u79bb\u5f00\u5510\u5a67\u957f\u4e14\u5a9a\u7684\u773c\u7738\uff0c\u8fd9\u53cc\u7f8e\u4e3d\u800c\u7075\u52a8\u7684\u773c\u7738\u4f3c\u4e4e\u8ba9\u81ea\u5df1\u6c38\u8fdc\u90fd\u770b\u4e0d\u817b\u3002</p><p>\u3000\u3000\u9ad8\u8003\u5230\u6700\u540e\u4e00\u4e2a\u6708\uff0c\u5bf9\u4e8e\u6d77\u5dde\u4e00\u4e2d\u7684\u5b66\u751f\u6765\u8bf4\uff0c\u5df2\u7ecf\u4e0d\u80fd\u7b97\u51b2\u523a\u9636\u6bb5\u4e86\uff0c\u5fc3\u6001\u7684\u8c03\u6574\u624d\u662f\u9ad8\u8003\u524d\u6700\u91cd\u8981\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u60f3\u60f3\u81ea\u5df1\u524d\u4e16\u4e34\u8003\u524d\u7684\u4e00\u4e2a\u6708\u7684\u5149\u666f\uff0c\u6709\u7740\u9ad8\u8003\u524d\u7126\u8e81\u7684\u60c5\u7eea\uff0c\u90a3\u65f6\u5510\u5a67\u6b63\u5fd9\u7740\u6253\u7406\u51fa\u56fd\u7684\u4e8b\u60c5\uff0c\u6108\u53d1\u4fc3\u4f7f\u81ea\u5df1\u7684\u60c5\u7eea\u6e38\u79bb\u5728\u5d29\u6e83\u7684\u8fb9\u7f18\uff0c\u771f\u4e0d\u6653\u5f97\u5f53\u65f6\u662f\u600e\u4e48\u71ac\u8fc7\u6765\u7684\u3002</p><p>\u3000\u3000\u201c\u54b3\uff0c\u4e24\u4f4d\u7a0d\u5fae\u6536\u655b\u4e00\u4e9b\uff0c\u8fd9\u4e2a\u6559\u5ba4\u6697\u604b\u5510\u5a67\u7684\u53ef\u4e0d\u6b62\u4e00\u4e2a\u4e24\u4e2a\uff0c\u4f60\u5c0f\u5b50\u6709\u5fc5\u8981\u4e00\u56de\u6765\u5c31\u5f80\u522b\u4eba\u5fc3\u91cc\u624e\u5200\u5b50\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u9ad8\u5174\uff0c\u600e\u4e48\u7740\u5427\uff1f\u201d\u5510\u5a67\u55c5\u4e86\u55c5\u7cbe\u81f4\u79c0\u76f4\u7684\u9f3b\u5b50\uff0c\u795e\u6c14\u7684\u77a5\u4e86\u675c\u98de\u4e00\u773c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5934\u6b6a\u8fc7\u6765\u6795\u7740\u624b\u81c2\uff0c\u770b\u7740\u675c\u98de\u7b11\u4e86\u7b11\uff0c\u8fd9\u5c0f\u5b50\u5e74\u521d\u8fd8\u8bf4\u8981\u81ea\u5df1\u5e2e\u7740\u641e\u4e00\u4e2a\u4fdd\u9001\u540d\u989d\uff0c\u4e34\u5230\u6700\u540e\u53c8\u8bf4\u60f3\u51ed\u81ea\u5df1\u7684\u80fd\u529b\u9ad8\u8003\u51b2\u523a\u4e00\u628a\uff0c\u7b11\u7740\u95ee\u4ed6\uff1a\u201c\u57cb\u5934\u8bfb\u4e66\u7684\u6ecb\u5473\u5982\u4f55\uff0c\u6709\u628a\u63e1\u8003\u4e1c\u5927\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e2a\u540d\u989d\u6ca1\u8ba9\u7ed9\u522b\u4eba\u5427\uff1f\u201d\u675c\u98de\u5b09\u76ae\u7b11\u8138\u7684\u6324\u5230\u5f20\u606a\u8eab\u8fb9\u5750\u4e0b\uff0c\u4eb2\u70ed\u7684\u6367\u8d77\u4ed6\u7684\u624b\uff0c\u201c\u6211\u8fd9\u8fb9\u5148\u8003\u8003\u770b\uff0c\u8981\u662f\u4e0d\u884c\uff0c\u8fd8\u8981\u7528\u4f60\u90a3\u4e2a\u540d\u989d\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u8981\u662f\u675c\u98de\u4e0d\u53bb\u4e1c\u5927\uff0c\u81ea\u5df1\u4e00\u4e2a\u4eba\u5728\u4e1c\u5927\u8fd8\u662f\u5f88\u65e0\u804a\u7684\uff0c\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u5374\u8bf4\u9053\uff1a\u201c\u6211\u5f88\u76f8\u4fe1\u4f60\u7684\u51b3\u5fc3\u554a\uff0c\u518d\u8bf4\u4fdd\u9001\u90fd\u662f\u8003\u524d\u4fdd\u9001\uff0c\u4f60\u90fd\u8981\u53c2\u52a0\u9ad8\u8003\u4e86\uff0c\u90a3\u4e2a\u540d\u989d\u7559\u4e5f\u7559\u4e0d\u4f4f\u554a\u3002\u6ca1\u6709\u5173\u7cfb\uff0c\u4e1c\u5927\u4e0d\u662f\u8fd8\u6709\u6210\u4eba\u6559\u80b2\u5b66\u9662\uff0c\u8fd9\u4e2a\uff0c\u6211\u60f3\u4f60\u5e94\u8be5\u6709\u628a\u63e1\u8003\u4e0a\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u675c\u98de\u6324\u7709\u76b1\u8138\u3001\u4e00\u4ed8\u75db\u82e6\u7684\u8868\u60c5\uff0c\u4f3c\u4e4e\u60f3\u5230\u8003\u4e0a\u4e1c\u6d77\u5927\u5b66\u6210\u4eba\u6559\u80b2\u5b66\u9662\u7684\u60c5\u5f62\uff0c\u201c\u6211\u5988\u4f1a\u6740\u4e86\u6211\u7684\uff01\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u662f\u4f60\u6d3b\u8be5\uff01\u201d\u5510\u5a67\u5a07\u7b11\u4e86\u51e0\u58f0\uff0c\u63e1\u4f4f\u5f20\u606a\u7684\u624b\u6307\u5934\u6447\u4e86\u6447\uff0c\u201c\u4e0d\u8981\u7ba1\u4ed6\u6b7b\u6d3b\uff0c\u4ed6\u73b0\u5728\u90fd\u6709\u65f6\u95f4\u5e2e\u52a9\u521d\u4e8c\u5e74\u7ea7\u7684\u5c0f\u59b9\u59b9\u8865\u4e60\u529f\u8bfe\u2026\u2026\u201d</p><p>\u3000\u3000\u521d\u4e8c\u5e74\u7ea7\u7684\u5c0f\u59b9\u59b9\uff1f\u5f20\u606a\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u60f3\u8d77\u5468\u6653\u7490\u53ef\u4e0d\u662f\u5728\u8bfb\u521d\u4e8c\u561b\uff01</p><p>\u3000\u3000\u201c\u675c\u98de\uff0c\u4f60\u5e2e\u6211\u5c06\u8bd5\u5377\u53d1\u4e00\u4e0b\u2026\u2026\u54a6\uff0c\u5f20\u606a\uff0c\u4f60\u600e\u4e48\u5728\u6559\u5ba4\u91cc\uff1f\u201d\u674e\u829d\u82b3\u62ff\u7740\u4e00\u645e\u8bd5\u5377\u8d70\u5230\u8bb2\u53f0\u524d\uff0c\u6b63\u8981\u53eb\u675c\u98de\u5e2e\u5979\u53d1\u8bd5\u5377\uff0c\u4e4d\u773c\u770b\u89c1\u5f20\u606a\u7a81\u7136\u51fa\u73b0\u5728\u6559\u5ba4\u91cc\uff0c\u5341\u5206\u8be7\u5f02\uff0c\u96be\u602a\u521a\u624d\u770b\u5230\u674e\u8000\u5149\u8868\u60c5\u602a\u602a\u7684\uff0c\u539f\u6765\u662f\u5dee\u4e0d\u591a\u4e00\u4e2a\u5b66\u671f\u90fd\u6ca1\u6709\u9732\u9762\u7684\u5f20\u606a\u51fa\u73b0\u5728\u6559\u5ba4\u91cc\u3002</p><p>\u3000\u3000\u4e09\u5e74\u65f6\u95f4\u8fc7\u53bb\uff0c\u674e\u829d\u82b3\u5012\u662f\u8273\u4e3d\u4f9d\u65e7\uff0c\u8ba9\u4eba\u5f88\u96be\u60f3\u8c61\u5979\u5973\u513f\u5468\u6653\u7490\u90fd\u5341\u56db\u5c81\u4e86\uff0c\u4f3c\u4e4e\u5bb6\u5ead\u751f\u6d3b\u4e5f\u5df2\u6062\u590d\u5e73\u9759\uff0c\u795e\u91c7\u5955\u5955\uff0c\u96be\u602a\u675c\u98de\u8fd9\u5c0f\u5b50\u6b7b\u6d3b\u4e5f\u8981\u5728\u5b66\u6821\u91cc\u4e3a\u9ad8\u8003\u51b2\u523a\u4e09\u4e2a\u6708\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u56de\u6765\u4e0a\u8bfe\u4e86\uff0c\u201d\u5f20\u606a\u5e76\u4e0d\u4f1a\u53bb\u7ba1\u674e\u829d\u82b3\u7684\u4e2a\u4eba\u9690\u79c1\uff0c\u4f5c\u4e3a\u8001\u5e08\uff0c\u5979\u662f\u503c\u5f97\u5c0a\u656c\u7684\uff0c\u89c1\u674e\u829d\u82b3\u671d\u8fd9\u8fb9\u8d70\u8fc7\u6765\uff0c\u4e5f\u7ad9\u4e86\u8d77\u6765\u8ddf\u5979\u8bf4\u8bdd\uff0c\u201c\u9ad8\u4e2d\u751f\u6d3b\u5bf9\u8c01\u90fd\u662f\u96be\u5fd8\u7684\uff0c\u6211\u53ef\u4e0d\u60f3\u5728\u5916\u9762\u5c06\u6700\u540e\u4e00\u4e2a\u6708\u7684\u9ad8\u65f6\u5149\u90fd\u6643\u8361\u6389\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u2026\u2026\u80fd\u8fd9\u6837\u60f3\u6700\u597d\u4e86\uff0c\u201d\u674e\u829d\u82b3\u7b11\u4e86\u8d77\u6765\uff0c\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u538b\u4f4e\u58f0\u97f3\u8bf4\u9053\uff0c\u201c\u54e6\uff0c\u4f60\u4fdd\u9001\u4e1c\u5927\u7684\u4e8b\u60c5\uff0c\u5b66\u6821\u91cc\u6ca1\u6709\u8ddf\u540c\u5b66\u4eec\u8bf4\uff0c\u514d\u5f97\u6253\u51fb\u540c\u5b66\u4eec\u7684\u79ef\u6781\u6027\uff0c\u5c31\u5269\u4e0b\u4e00\u4e2a\u6708\uff0c\u6211\u4e5f\u662f\u5f88\u671f\u5f85\u770b\u5230\u4f60\u505a\u56de\u5b66\u751f\u7684\u6a21\u6837\u3002\u201d</p><p>\u3000\u3000\u9ad8\u4e2d\u4e09\u5e74\u5728\u5b66\u6821\u6643\u8361\u7684\u65f6\u95f4\u751a\u81f3\u6bd4\u5176\u4ed6\u4eba\u7684\u5047\u671f\u65f6\u95f4\u8fd8\u5c11\uff0c\u8981\u662f\u8ba9\u4e00\u7fa4\u5904\u4e8e\u5341\u516b\u4e5d\u5e74\u3001\u5bf9\u793e\u4f1a\u516c\u4e49\u7b49\u4e16\u754c\u89c2\u6b63\u8fdb\u884c\u4e25\u8083\u601d\u8003\u7684\u4eba\u77e5\u9053\u81ea\u5df1\u7ed9\u4fdd\u9001\u4e1c\u5927\u7684\u4e8b\u60c5\uff0c\u7684\u786e\u975e\u5e38\u7684\u6253\u51fb\u4ed6\u4eec\u7684\u79ef\u6781\u6027\uff1b\u8fd9\u4e2a\u793e\u4f1a\uff0c\u4ece\u6765\u90fd\u662f\u201c\u4e0d\u60a3\u7a77\u3001\u60a3\u4e0d\u5747\u201d\u7684\uff0c\u7279\u522b\u9ad8\u8003\u4e34\u8003\u524d\u7684\u4e00\u6bb5\u65f6\u95f4\uff0c\u4eba\u7684\u5fc3\u6001\u662f\u6700\u5bb9\u6613\u5931\u8861\u7684\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u674e\u8001\u5e08\u4f60\u8981\u4e0d\u63d0\uff0c\u6211\u90fd\u5dee\u70b9\u5fd8\u8bb0\u8fd9\u56de\u4e8b\u4e86\uff0c\u201d\u5f20\u606a\u63a5\u8fc7\u674e\u829d\u82b3\u624b\u91cc\u7684\u8bd5\u5377\uff0c\u201c\u73ed\u4e0a\u8981\u6709\u4ec0\u4e48\u6742\u4e8b\uff0c\u4f60\u5429\u5490\u6211\u505a\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u9526\u6e56\u5177\u4f53\u800c\u5fae\u7684\u4e8b\u60c5\uff0c\u6709\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u7b49\u4eba\u5728\uff0c\u5f20\u606a\u53ef\u4ee5\u8896\u624b\u65c1\u89c2\u4e86\uff0c\u5fd9\u788c\u4e86\u8fd9\u4e48\u4e45\uff0c\u9700\u8981\u6e05\u95f2\u4e0b\u6765\u597d\u597d\u7684\u601d\u8003\u4e00\u4e0b\uff0c\u5f20\u606a\u90fd\u60f3\u4e0d\u51fa\u6709\u4ec0\u4e48\u5730\u65b9\u80fd\u6bd4\u9ad8\u8003\u4e34\u8003\u524d\u7684\u6559\u5ba4\u66f4\u80fd\u8ba9\u4ed6\u653e\u8f7b\u677e\u60c5\u7eea\u601d\u8003\u95ee\u9898\u4e86\uff0c\u4ed6\u7684\u786e\u6253\u7b97\u5728\u5b66\u6821\u91cc\u597d\u597d\u6e21\u8fc7\u4ed6\u6700\u540e\u4e00\u4e2a\u6708\u7684\u9ad8\u4e2d\u65f6\u5149\u3002</p><p>\u3000\u3000\u674e\u829d\u82b3\u5c06\u539a\u539a\u4e00\u645e\u8bd5\u5377\u90fd\u4ea4\u7ed9\u5f20\u606a\uff0c\u770b\u7740\u8fd9\u4e2a\u5927\u6982\u662f\u81ea\u5df1\u4ece\u6559\u4e00\u751f\u90fd\u53ea\u53ef\u80fd\u9047\u5230\u4e00\u4e2a\u7684\u8fd9\u4e48\u6709\u7279\u8d28\u7684\u5b66\u751f\uff1a\u5bf9\u4e8e\u4e0d\u719f\u6089\u4ed6\u7684\u4eba\uff0c\u53ea\u5f53\u4ed6\u662f\u4e00\u4e2a\u5bb6\u5883\u4f18\u6e25\u7684\u7ea8\u7ed4\u5b50\u5f1f\uff0c\u7a0d\u7a0d\u5bf9\u4ed6\u6709\u6240\u63a5\u89e6\u7684\u4eba\uff0c\u4f1a\u53d1\u73b0\u4ed6\u7ed9\u8ff7\u56e2\u7b3c\u7f69\u7740\uff0c\u8ba9\u4eba\u770b\u4e0d\u6e05\u4ed6\u7684\u8eab\u5f71\u3002</p><p>\u3000\u3000\u674e\u829d\u82b3\u4e0d\u5f97\u4e0d\u627f\u8ba4\uff0c\u8981\u6ca1\u6709\u8fd9\u4e48\u4e00\u4e2a\u5b66\u751f\uff0c\u4e0d\u6653\u5f97\u81ea\u5df1\u7684\u751f\u6d3b\u4f1a\u53d8\u5f97\u591a\u4e48\u7cdf\u7cd5\u900f\u9876\uff1b\u4ec5\u7ba1\u81ea\u5df1\u5e76\u4e0d\u6653\u5f97\u8fd9\u4e2a\u5b66\u751f\u771f\u6b63\u7684\u9762\u76ee\u662f\u4f55\u7b49\u7684\u60ca\u4e16\u9a87\u4fd7\u3002</p><p>\u3000\u3000\u674e\u829d\u82b3\u8d70\u56de\u8bb2\u53f0\uff0c\u770b\u7740\u5f20\u606a\u3001\u675c\u98de\u5e2e\u5979\u5206\u53d1\u8bd5\u5377\uff0c\u5fc3\u91cc\u9887\u4e3a\u611f\u6168\uff0c\u8d70\u4e86\u4e00\u4f1a\u795e\uff0c\u62cd\u4e86\u62cd\u624b\u638c\uff0c\u8ba9\u7ad9\u5728\u5916\u9762\u8d70\u5eca\u4e0a\u4f11\u606f\u7684\u5b66\u751f\u90fd\u5230\u6559\u5ba4\u91cc\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u79bb\u9ad8\u8003\u53ea\u6709\u4e8c\u5341\u4e94\u5929\u4e86\uff0c\u5927\u5bb6\u4e5f\u770b\u5230\u73b0\u5728\u6bcf\u5929\u505a\u7684\u8bd5\u5377\u6bd4\u5e73\u65f6\u8981\u5c11\u8bb8\u591a\uff0c\u8fd9\u6bb5\u65f6\u95f4\u6700\u4e3b\u8981\u7684\u662f\u81ea\u5df1\u7684\u5fc3\u6001\u8c03\u6574\u597d\uff0c\u5c06\u590d\u4e60\u8fc7\u7684\u8981\u70b9\u3001\u91cd\u70b9\u68b3\u7406\u4e00\u904d\uff0c\u4e5f\u4e0d\u4f1a\u518d\u5f3a\u5236\u8981\u6c42\u4f4f\u5728\u5e02\u533a\u7684\u540c\u5b66\u6bcf\u5929\u90fd\u5fc5\u987b\u5230\u5b66\u6821\u4e0a\u591c\u81ea\u4fee\uff0c\u5f53\u7136\uff0c\u5927\u5bb6\u4e5f\u4e0d\u8981\u4ee5\u4e3a\u9ad8\u8003\u5df2\u7ecf\u7ed3\u675f\u4e86\u2026\u2026\u8fd9\u6b21\u53d1\u7684\u8bd5\u5377\uff0c\u5927\u5bb6\u4e24\u5929\u65f6\u95f4\u5185\u505a\u5b8c\u5c31\u53ef\u4ee5\u4e86\uff0c\u81ea\u5df1\u5408\u7406\u7684\u5b89\u6392\u4e00\u4e0b\u65f6\u95f4\uff0c\u62ff\u5230\u8bd5\u5377\u7684\u4eba\u53ef\u4ee5\u653e\u5b66\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5e2e\u5510\u5a67\u5c06\u8bd5\u5377\u90fd\u6536\u62fe\u5230\u4e66\u5305\u91cc\uff0c\u558a\u4e0a\u675c\u98de\uff0c\u4e00\u8d77\u8d70\u51fa\u6559\u5ba4\u3002</p><p>\u3000\u3000\u8d70\u5230\u697c\u4e0b\uff0c\u675c\u98de\u56db\u5904\u5f20\u671b\uff0c\u6ca1\u770b\u89c1\u5f20\u606a\u7684\u8f66\uff0c\u95ee\u4ed6\uff1a\u201c\u4f60\u7684\u8f66\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u8f66\uff1f\u201d\u5f20\u606a\u4e0e\u5510\u5a67\u5404\u62c9\u4e66\u5305\u5e26\u7684\u4e00\u8fb9\uff0c\u5c06\u6c89\u7538\u7538\u7684\u4e66\u5305\u63d0\u6e9c\u5728\u4e24\u4eba\u7684\u4e2d\u95f4\u6643\u8361\uff0c\u201c\u6211\u6bcf\u5929\u90fd\u8981\u966a\u5510\u5a67\u4e0a\u5b66\uff0c\u4e24\u4eba\u9a91\u4e00\u8f86\u8f66\u5c31\u884c\u4e86\u3002\u201d</p><p>\u3000\u3000\u6240\u6709\u7684\u6d6a\u6f2b\u90fd\u62b5\u4e0d\u4f4f\u4f9d\u5728\u604b\u4eba\u7684\u6000\u91cc\u5728\u5915\u9633\u4e0b\u9a91\u7740\u8f66\u62db\u6447\u8fc7\u5e02\u7684\u79bb\u5f00\u5b66\u6821\uff0c\u5510\u5a67\u62bf\u5634\u7b11\u7740\uff0c\u8d70\u5230\u505c\u8f66\u68da\u5c31\u6068\u5f97\u7259\u75d2\u75d2\u7684\u8981\u8e22\u5f20\u606a\u4e86\u2014\u2014\u5f20\u606a\u4e00\u5c41\u80a1\u5750\u5230\u8f66\u540e\u67b6\u4e0a\uff0c\u7b49\u7740\u5979\u9a91\u8f66\u8f7d\u4ed6\u5462\u3002</p></article>"}}}]);
