(window.webpackJsonp=window.webpackJsonp||[]).push([[741],{764:function(p){p.exports={chapterTitle:null,title:"\u7b2c740\u7ae0 \u75af\u72c2\u516c\u4ea4\u8f66",content:"<article><h2>\u7b2c740\u7ae0 \u75af\u72c2\u516c\u4ea4\u8f66</h2><p>\u3000\u3000\u5c31\u5728\u4e71\u77f3\u6ee9\u5916\u7684\u6c5f\u5824\u4e0b\uff0c\u5f20\u606a\u5c06\u8fd9\u6b21\u4e0e\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u63a5\u89e6\u7684\u60c5\u51b5\u8ddf\u738b\u7ef4\u5747\u505a\u4e86\u7c97\u7565\u7684\u901a\u62a5\uff0c\u8fd8\u5c31\u5efa\u90ba\u5e02\u6570\u5b57\u624b\u673a\u4ea7\u4e1a\u53d1\u5c55\u7684\u60c5\u51b5\uff0c\u4e00\u8d77\u8ba8\u8bba\u4e86\u4e00\u4e0b\u3002\u5ba4\u5916\u6c14\u6e29\u8981\u6709\u96f6\u4e0b\u516d\u4e03\u5ea6\uff0c\u98ce\u5f88\u5927\uff0c\u5c31\u7b97\u7ad9\u5728\u80cc\u98ce\u7684\u6c5f\u5824\u4e0a\uff0c\u8fd8\u662f\u7ed9\u4ece\u6c5f\u9762\u56de\u65cb\u8fc7\u6765\u7684\u51b7\u98ce\u5439\u5f97\u624b\u811a\u51b0\u51b7\u3002</p><p>\u3000\u3000\u8d76\u7740\u53bb\u4e2d\u6676\u5fae\u82af\u7684\u5efa\u8bbe\u5de5\u5730\u53bb\u8f6c\u4e86\u4e00\u5708\uff0c\u4e2d\u5348\u5c31\u7559\u5728\u5de5\u5730\u7528\u9910\u3002\u7528\u8fc7\u9910\u4e4b\u540e\uff0c\u738b\u7ef4\u5747\u3001\u59da\u6587\u76db\u56de\u533a\u91cc\uff0c\u5f20\u606a\u4e0e\u9648\u9759\u518d\u7ed5\u9053\u5c0f\u6c5f\u5927\u6865\u8fd4\u56de\u4e1c\u5927\u3002\u8fd8\u4ee5\u4e3a\u4e2d\u5348\u7684\u4ea4\u901a\u72b6\u51b5\u4f1a\u597d\u4e00\u4e9b\uff0c\u6ca1\u60f3\u5230\u8f66\u5b50\u521a\u4e0a\u5f15\u6865\uff0c\u5c31\u7ed9\u5835\u4e86\u4e2a\u4e25\u5b9e\uff0c\u8fdb\u9000\u4e0d\u5f97\u3002</p><p>\u3000\u3000\u6309\u4e0b\u8f66\u7a97\u63a2\u5934\u770b\u5916\u9762\uff0c\u6865\u9762\u4e0a\u5587\u53ed\u58f0\u4e00\u7247\uff0c\u90fd\u4e0d\u6653\u5f97\u53d1\u751f\u72b6\u51b5\u624d\u5835\u8f66\uff0c\u5f20\u606a\u624b\u62b1\u7740\u540e\u8111\u888b\uff0c\u5bf9\u9648\u9759\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u6211\u4eec\u8d70\u5230\u6865\u5bf9\u9762\u518d\u5750\u8f66\u56de\u5b66\u5e9c\u5df7\uff1f\u201d</p><p>\u3000\u3000\u9648\u9759\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u673a\u4f1a\u8d70\u4e00\u8d70\u5c0f\u6c5f\u5927\u6865\u5462\u2026\u2026\u201d\u5979\u5f53\u7136\u4e0d\u4ecb\u610f\u4e0e\u5f20\u606a\u5355\u72ec\u76f8\u5904\uff0c\u4e0e\u5f20\u606a\u4e0b\u4e86\u8f66\uff0c\u5c06\u9a6c\u6d77\u9f99\u4e00\u4e2a\u4eba\u4e22\u5728\u8f66\u4e0a\uff0c\u8d70\u5230\u4eba\u884c\u9053\u4e0a\uff0c\u5f80\u6c5f\u5357\u5cb8\u95f2\u5ead\u4fe1\u6b65\u7684\u8d70\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u6865\u9762\u4e0a\u6ca1\u6709\u906e\u6321\uff0c\u897f\u98ce\u4e25\u5bd2\u800c\u8086\u8650\uff0c\u6b65\u884c\u8fc7\u6865\u7684\u884c\u4eba\u5927\u90fd\u4fa7\u8fc7\u8eab\u5b50\u80cc\u7740\u98ce\u800c\u884c\u3002\u9648\u9759\u4fa7\u7740\u8eab\u5b50\uff0c\u53d1\u4e1d\u7ed9\u5439\u5f97\u51cc\u4e71\u7f20\u5728\u8138\u4e0a\uff0c\u5a07\u8273\u7ea2\u6da6\u7684\u5634\u5507\u4e0e\u9752\u9ed1\u900f\u7740\u5149\u6cfd\u7684\u53d1\u4e1d\u7f20\u5728\u4e00\u8d77\u6765\uff0c\u8272\u6cfd\u660e\u4e3d\uff0c\u5206\u5916\u7684\u8bf1\u4eba\u3002\u5f20\u606a\u770b\u4e86\u5206\u5fc3\uff0c\u811a\u7ed9\u6865\u9762\u652f\u51fa\u7684\u87ba\u6813\u5934\u7eca\u4e86\u4e00\u4e0b\uff0c\u9648\u9759\u53cd\u5e94\u5feb\u4f38\u624b\u62c9\u4f4f\u5f20\u606a\u6ca1\u6709\u8ba9\u4ed6\u8dcc\u5012\u51fa\u4e11\uff0c\u5374\u5fcd\u4e0d\u4f4f\u8981\u7b11\u51fa\u58f0\u6765\uff0c\u624b\u63a9\u7740\u5634\u5507\uff0c\u773c\u775b\u91cc\u6d41\u8f6c\u7684\u7738\u5149\u5a07\u5a9a\u3001\u98ce\u60c5\u65e0\u7aef\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u597d\u610f\u601d\u7684\u7b11\u4e86\u7b11\uff0c\u7ed9\u81ea\u5df1\u627e\u501f\u53e3\u8bf4\u9053\uff1a\u201c\u8fd9\u6865\u9762\u597d\u4e45\u6ca1\u4fee\u4e86\uff0c\u8c01\u6653\u5f97\u4eba\u884c\u9053\u4f1a\u7a81\u7136\u6709\u87ba\u6813\u5934\u652f\u51fa\u6765\uff1f\u201d\u89c1\u9648\u9759\u8fd8\u5728\u7b11\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\u8fd9\u4e9b\u5bb3\u4eba\u7684\u5996\u7cbe\uff0c\u51fa\u95e8\u90fd\u5e94\u8be5\u6234\u4e0a\u9762\u7f69\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e0d\u8bf4\u4f60\u8d3c\u7709\u9f20\u773c\u5e94\u8be5\u5c06\u773c\u73e0\u5b50\u6316\u6389\uff0c\u5374\u8981\u6211\u4eec\u5e26\u7740\u9762\u7f69\u4e0a\u8857\uff0c\u201d\u9648\u9759\u7b11\u7740\u8bf4\uff0c\u201c\u5408\u8ba1\u7740\u6211\u4eec\u90fd\u6bc1\u5bb9\u53bb\u66f4\u5408\u9002\u554a\u3002\u201d\u4f38\u624b\u5c06\u5439\u906e\u5728\u773c\u775b\u4e86\u7684\u53d1\u4e1d\u64a9\u5f00\uff0c\u53ef\u662f\u624b\u4e00\u653e\uff0c\u53d1\u4e1d\u53c8\u7ed9\u98ce\u5439\u4e71\u7f20\u5728\u8138\u4e0a\uff0c\u624b\u4f38\u5230\u8111\u540e\u6293\u7740\u5934\u53d1\u4e0e\u5f20\u606a\u5e76\u80a9\u8d70\u7740\u3002</p><p>\u3000\u3000\u201c\u4f60\u624b\u4e0d\u51b7\uff1f\u201d\u5f20\u606a\u95ee\u9648\u9759\u3002</p><p>\u3000\u3000\u201c\u4f60\u6709\u6ca1\u6709\u7ef3\u5b50\u4ec0\u4e48\u7684\u8ba9\u6211\u5c06\u5934\u53d1\u624e\u8d77\u6765\uff1f\u201d\u9648\u9759\u95ee\u9053\u3002\u96f6\u4e0b\u516d\u4e03\u5ea6\u7684\u5929\u6c14\u624b\u9732\u5728\u5916\u9762\u5c31\u4e00\u4f1a\u513f\u5c31\u7ed9\u51b7\u5f97\u751f\u75bc\uff0c\u8fd8\u62c5\u5fc3\u4f1a\u751f\u51bb\u75ae\uff0c\u771f\u662f\u5750\u8f66\u4e0d\u6653\u5f97\u5ba4\u5916\u7684\u4e25\u5bd2\uff0c\u8fd9\u65f6\u5019\u5012\u8981\u5403\u4e9b\u82e6\u3002</p><p>\u3000\u3000\u201c\u6211\u5e2e\u4f60\uff0c\u201d\u5f20\u606a\u8d70\u5230\u9648\u9759\u80cc\u540e\uff0c\u5c06\u5979\u7684\u957f\u53d1\u675f\u6210\u4e00\u675f\uff0c\u90fd\u585e\u5979\u7f8a\u7ed2\u5916\u5957\u7684\u8863\u9886\u91cc\uff0c\u8fd9\u6837\u5c31\u4e0d\u4f1a\u7ed9\u98ce\u5439\u4e71\u4e86\uff0c\u201c\u8fd9\u6837\u884c\u4e0d\u884c\uff1f\u201d</p><p>\u3000\u3000\u8ba9\u5f20\u606a\u4e03\u624b\u516b\u811a\u7684\u5c06\u5934\u53d1\u585e\u5230\u8863\u9886\u91cc\uff0c\u9648\u9759\u7406\u4e86\u7406\u989d\u524d\u7684\u5218\u6d77\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u540e\u80cc\u611f\u89c9\u4e71\u7cdf\u7cdf\u7684\uff0c\u6211\u624b\u591f\u4e0d\u7740\uff0c\u4f60\u5e2e\u6211\u7406\u4e00\u4e0b\u2026\u2026\u201d\u8bf4\u8fd9\u8bdd\u65f6\uff0c\u9648\u9759\u8fd8\u6ca1\u6709\u4ec0\u4e48\u611f\u89c9\uff0c\u5f53\u5f20\u606a\u7684\u624b\u5c31\u4ece\u5916\u5957\u4e0b\u6446\u4f38\u8fdb\u53bb\u5e2e\u5979\u7406\u5934\u53d1\uff0c\u9694\u7740\u8584\u8584\u7684\u7f8a\u7ed2\u886b\u611f\u89c9\u90a3\u53cc\u6e29\u70ed\u7684\u624b\u5076\u5c14\u7684\u8f7b\u89e6\uff0c\u624d\u5f97\u6653\u8fd9\u6837\u7684\u52a8\u4f5c\u8fc7\u4e8e\u4eb2\u5bc6\u4e86\u4e9b\uff0c\u80a9\u80cc\u90fd\u50f5\u5728\u90a3\u91cc\uff0c\u90a3\u4e00\u77ac\u95f4\uff0c\u5fc3\u5c16\u5c16\u90fd\u6f02\u6d6e\u4e86\u8d77\u6765\u3002\u6d51\u8eab\u53d1\u70eb\uff0c\u597d\u5728\u8138\u80cc\u7740\u5f20\u606a\uff0c\u9648\u9759\u53ea\u6709\u9759\u9759\u7684\u5438\u6c14\uff0c\u8ba9\u81ea\u5df1\u7684\u5fc3\u601d\u51b7\u9759\u4e0b\u6765\uff0c\u4e0d\u8981\u7ed9\u80cc\u540e\u7684\u5c0f\u7537\u4eba\u770b\u51fa\u5f02\u5e38\u624d\u597d\uff0c\u4e0d\u7136\u8138\u5c31\u8981\u4e22\u5927\u4e86\u3002</p><p>\u3000\u3000\u201c\u597d\u4e86\u2026\u2026\u201d\u5f20\u606a\u624b\u62bd\u51fa\u6765\uff0c\u8fd8\u5c06\u9648\u9759\u7684\u5916\u5957\u540e\u80a9\u7406\u4e86\u7406\uff0c\u4e30\u76db\u9ed1\u4eae\u7684\u957f\u53d1\u8d34\u7740\u8111\u888b\uff0c\u9648\u9759\u7684\u5934\u5f62\u5f88\u597d\u770b\uff0c\u201c\u73b0\u5728\u4e0d\u7528\u6015\u7ed9\u98ce\u5439\u4e71\u5934\u53d1\u4e86\u3002\u201d</p><p>\u3000\u3000\u9648\u9759\u8fd9\u4e0b\u5b50\u8981\u5c0f\u5fc3\u81ea\u5df1\u4f1a\u7ed9\u4eba\u884c\u9053\u53ef\u80fd\u7a81\u7136\u652f\u51fa\u6765\u7684\u87ba\u6813\u5934\u7eca\u5012\u4e86\uff0c\u5fc3\u91cc\u614c\u614c\u7684\uff0c\u624b\u63d2\u5728\u8863\u515c\u91cc\uff0c\u4e0e\u5f20\u606a\u5e76\u80a9\u5f80\u6c5f\u5bf9\u9762\u8d70\u53bb\u3002\u6865\u4e2d\u95f4\u6709\u4e24\u8f86\u5361\u8f66\u53d1\u751f\u64e6\u6302\uff0c\u5360\u636e\u4e86\u6574\u4e2a\u6865\u9762\uff0c\u4e00\u7fa4\u4eba\u56f4\u5728\u90a3\u91cc\uff0c\u4ea4\u8b66\u8fd8\u6ca1\u6709\u8d76\u8fc7\u6765\uff0c\u4e24\u8f86\u5361\u8f66\u7684\u53f8\u673a\u4f3c\u4e4e\u521a\u624d\u52a8\u8fc7\u624b\uff0c\u8fd9\u65f6\u5019\u53c9\u7740\u8170\u4f7f\u52b2\u7684\u5bf9\u9a82\uff0c\u5f20\u606a\u4e0e\u9648\u9759\u5728\u4eba\u7fa4\u5916\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u6ca1\u89c1\u4ed6\u4eec\u6709\u518d\u52a8\u624b\u7684\u610f\u601d\uff0c\u5c31\u7ee7\u7eed\u5f80\u524d\u8d70\u3002</p><p>\u3000\u3000\u7ad9\u5728\u5927\u6865\u5357\u8def\u7684\u516c\u4ea4\u8f66\u7ad9\u53f0\u524d\u7b49\u51fa\u79df\u8f66\uff0c\u4e00\u8f86\u9a76\u5f80\u71d5\u5f52\u5c71\u7684318\u8def\u516c\u4ea4\u8f66\u5148\u9a76\u8fc7\u6765\uff0c\u9648\u9759\u5bf9\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5750\u516c\u4ea4\u56de\u53bb\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u9648\u9759\u4e0a\u4e86\u8f66\uff0c\u624d\u60f3\u8d77318\u8def\u51fa\u4e1c\u534e\u95e8\u5c31\u5f80\u5357\u62d0\uff0c\u4ece\u71d5\u5f52\u5357\u8def\u5f00\u5f80\u71d5\u5f52\u5c71\u516c\u56ed\uff0c\u4e0d\u7ecf\u8fc7\u5b66\u5e9c\u5df7\u3002318\u8def\u516c\u4ea4\u8f66\u961f\u7684\u53f8\u673a\u662f\u5efa\u90ba\u6240\u6709\u516c\u4ea4\u53f8\u673a\u91cc\u5f00\u8f66\u6700\u75af\u72c2\u7684\uff0c\u9999\u6e2f\u90a3\u4e9b\u6240\u8c13\u7684\u98da\u8f66\u515a\uff0c\u5728318\u8def\u516c\u4ea4\u8f66\u53f8\u673a\u9762\u524d\u90fd\u6015\u8981\u9eef\u7136\u5931\u8272\uff0c\u5f20\u606a\u5c31\u4eb2\u773c\u770b\u5230\u53f8\u673a\u6025\u5239\u8f66\u65f6\u4e00\u540d\u5750\u5728\u6700\u540e\u4e00\u6392\u7684\u4e58\u5ba2\u98de\u6251\u5230\u8f66\u524d\u5934\u7684\u5f15\u64ce\u76d6\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8fd9\u4e8b\u8bf4\u7ed9\u9648\u9759\u542c\uff0c\u8ba9\u5979\u4e24\u53ea\u624b\u90fd\u6293\u4f4f\u6276\u624b\uff1a\u201c\u4f60\u8981\u89c9\u5f97\u6293\u6276\u624b\u592a\u51b7\uff0c\u6402\u4f4f\u6211\u7684\u8170\u4e5f\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u5f53\u6211\u5c0f\u5973\u5b69\u5b50\u8fd9\u4e48\u597d\u9a97\u3002\u201d\u9648\u9759\u5fc3\u91cc\u4e5f\u60f3\u6402\u7740\u5f20\u606a\u7684\u8170\u4f9d\u504e\u7ad9\u7740\uff0c\u4f46\u662f\u5374\u4e0d\u80fd\u7ed9\u8fd9\u4e48\u7b80\u5355\u7684\u7406\u7531\u7ed9\u8bf1\u60d1\u4e86\uff0c\u89c1\u5f20\u606a\u4e00\u672c\u6b63\u7ecf\u7684\u4e24\u53ea\u624b\u6293\u7d27\u6276\u624b\u7684\u6a21\u6837\uff0c\u54af\u54af\u5a07\u7b11\u4e86\u8d77\u6765\uff0c\u6447\u5934\u8868\u793a\u4e0d\u4fe1\uff0c\u5979\u624d\u4e0d\u4fe1\u5f20\u606a\u5728\u5efa\u90ba\u6709\u591a\u5c11\u6b21\u5750\u516c\u4ea4\u8f66\u7684\u673a\u4f1a\u3002\u5979\u4f38\u624b\u6478\u4e86\u6478\u94c1\u5236\u7684\u6276\u624b\uff0c\u51b0\u51b0\u7684\uff0c\u51b7\u5f97\u523a\u9aa8\uff0c\u5c31\u4f38\u51fa\u4e24\u6839\u624b\u6307\u642d\u5728\u4e0a\u9762\uff0c\u4e5f\u4e0d\u4f1a\u771f\u7684\u53bb\u6402\u5f20\u606a\u7684\u8170\u3002318\u8def\u516c\u4ea4\u8f66\u7684\u75af\u72c2\u4e0d\u7531\u5f97\u5979\u4e0d\u4fe1\uff0c\u5c31\u5728\u5979\u6447\u5934\u7684\u5f53\u513f\uff0c\u53f8\u673a\u4e3a\u4e86\u62a2\u5728\u524d\u9762\u7684\u4e00\u8f86318\u8def\u516c\u4ea4\u8f66\u4e4b\u524d\u8fdb\u7ad9\u53f0\uff0c\u731b\u7136\u6253\u65b9\u5411\u8d85\u8f66\uff0c\u60ef\u6027\u529b\u4e4b\u5927\uff0c\u5f20\u606a\u90fd\u6709\u8981\u7ed9\u7529\u98de\u8d77\u6765\u7684\u611f\u89c9\uff0c\u9648\u9759\u53ea\u6765\u5f97\u53ca\u6293\u5f20\u606a\u7684\u80f3\u818a\u624d\u6ca1\u6709\u7ed9\u7529\u6389\u8f66\u7684\u53e6\u4e00\u4fa7\uff0c\u8f66\u53a2\u91cc\u60ca\u547c\u8fde\u8fde\u3002\u9648\u9759\u8fd8\u6ca1\u6709\u6765\u5f97\u7ad9\u7a33\uff0c\u8d85\u8fc7\u524d\u9762\u7684\u516c\u4ea4\u8f66\uff0c\u53f8\u673a\u53c8\u731b\u7136\u53cd\u5411\u6253\u65b9\u5411\u76d8\u5207\u5230\u524d\u9762\u53bb\uff0c\u8fd9\u4e00\u4e0b\u8ba9\u9648\u9759\u6574\u4e2a\u4eba\u90fd\u649e\u5230\u5f20\u606a\u7684\u6000\u91cc\uff0c\u6402\u7d27\u4ed6\u7684\u8170\u624d\u7ad9\u5f97\u4f4f\u3002</p><p>\u3000\u3000\u9648\u9759\u7a7f\u7740\u9ad8\u8ddf\u7b52\u9774\uff0c\u53ea\u6bd4\u5f20\u606a\u7a0d\u77ee\u4e00\u7ebf\uff0c\u773c\u7738\u5b50\u770b\u7740\u5f20\u606a\u4f3c\u5927\u7406\u77f3\u96d5\u523b\u822c\u633a\u76f4\u3001\u7ebf\u6761\u5206\u660e\u7684\u9f3b\u6881\uff0c\u89c6\u7ebf\u5374\u4e0d\u6562\u518d\u5f80\u62ac\u4e00\u5206\uff0c\u89c6\u7ebf\u8981\u662f\u5bf9\u4e0a\uff0c\u5fc3\u601d\u80af\u5b9a\u4f1a\u4e71\u7cdf\u7cdf\u4e0d\u77e5\u6240\u63aa\uff0c\u611f\u89c9\u5230\u5f20\u606a\u6e29\u70ed\u7684\u9f3b\u606f\u6251\u5728\u81ea\u5df1\u7684\u9f3b\u7ffc\u3001\u5634\u5507\u4e0a\uff0c\u5fc3\u91cc\u6108\u53d1\u7684\u8ff7\u4e71\uff0c\u6709\u4e9b\u820d\u4e0d\u5f97\u677e\u5f00\u624b\u53bb\u6293\u6276\u624b\u3002</p><p>\u3000\u3000\u597d\u5728\u53f8\u673a\u5e2e\u5979\u505a\u51fa\u9009\u62e9\u3002\u540e\u9762\u7684\u516c\u4ea4\u8f66\u53f8\u673a\u770b\u7740\u81ea\u5df1\u7adf\u7136\u7ed9\u8d85\u8f66\uff0c\u5fc3\u91cc\u5f88\u4e0d\u723d\uff0c\u52a0\u5927\u6cb9\u95e8\u53cd\u8d85\u8fc7\u53bb\uff0c\u53f8\u673a\u7adf\u7136\u5728\u524d\u9762\u6253\u62db\u547c\uff1a\u201c\u5927\u5bb6\u90fd\u6293\u7d27\u6276\u624b\uff0c\u524d\u9762\u4e00\u7ad9\u4e0d\u505c\u4e86\uff0c\u5148\u5c06\u8fd9\u72d7\u65e5\u7684\u8d85\u8fc7\u53bb\u518d\u8bf4\u3002\u201d\u4e5f\u4e0d\u7406\u8f66\u53a2\u91cc\u6709\u4eba\u62b1\u6028\uff0c\u4e24\u8f86318\u8def\u516c\u4ea4\u8f66\u5c31\u5728\u5927\u6865\u5357\u8def\u4f60\u8ffd\u6211\u8d76\u7684\u98da\u8d77\u8f66\u6765\u3002</p><p>\u3000\u3000\u8fd9\u4fbf\u662f\u4e5d\u4e03\u5e74\u5efa\u90ba\u5e02\u91cc\u7684318\u8def\u516c\u4ea4\u8f66\uff0c\u5b8c\u5168\u53ef\u4ee5\u62cd\u4e00\u90e8\u300a\u75af\u72c2\u516c\u4ea4\u8f66\u300b\u7684\u7535\u5f71\u3002</p><p>\u3000\u3000\u529b\u5927\u8eab\u6c89\u7684\u4e58\u5ba2\u8fd8\u597d\u4e00\u4e9b\uff0c\u8eab\u4f53\u5355\u8584\u7684\u5973\u5b69\u5b50\u80fd\u5728\u8d85\u8f66\u65f6\u7ed9\u7529\u98de\u8d77\u6765\uff0c\u8f66\u53a2\u91cc\u7684\u5c16\u53eb\u58f0\u6b64\u8d77\u5f7c\u843d\u3002</p><p>\u3000\u3000\u9648\u9759\u6ca1\u6709\u6402\u4f4f\u5f20\u606a\u7684\u8170\uff0c\u677e\u5f00\u4e00\u53ea\u624b\u6293\u4f4f\u6276\u624b\uff0c\u53e6\u4e00\u53ea\u624b\u6293\u4f4f\u5f20\u606a\u7684\u80f3\u81c2\uff0c\u5f20\u606a\u4e5f\u677e\u5f00\u4e00\u53ea\u624b\u633d\u7740\u9648\u9759\u7684\u624b\u81c2\u3002\u9648\u9759\u8bf4\u9053\uff1a\u201c\u771f\u591f\u75af\u72c2\u7684\uff0c\u770b\u6765\u8fd8\u8981\u501f\u4f60\u7684\u624b\u7528\u4e00\u7528\u624d\u884c\u2026\u2026\u554a\uff01\u201d\u8bdd\u6ca1\u6709\u8bf4\u8bdd\uff0c\u516c\u4ea4\u8f66\u53c8\u5728\u53cd\u65b9\u5411\u731b\u6253\u65b9\u5411\u76d8\uff0c\u5979\u6574\u4e2a\u8eab\u4f53\u53c8\u6251\u5230\u5f20\u606a\u6000\u91cc\u53bb\u3002</p><p>\u3000\u3000\u201c\u90fd\u4e0d\u7528\u53bb\u6e38\u4e50\u573a\uff0c\u53bb\u6e38\u4e50\u573a\u90fd\u6ca1\u6709\u8fd9\u4e48\u523a\u6fc0\u2026\u2026\u201d\u5f20\u606a\u624b\u6258\u7d27\u9648\u9759\u7684\u8170\uff0c\u5e2e\u5979\u7ad9\u597d\uff0c\u8981\u5728\u8fd9\u8f66\u91cc\u7ad9\u7a33\uff0c\u8fd8\u771f\u8981\u4e24\u4e2a\u4eba\u9f50\u5fc3\u534f\u529b\u624d\u884c\uff0c\u95fb\u7740\u9648\u9759\u53d1\u9645\u4f20\u6765\u6e05\u5e7d\u7684\u9999\u5473\uff0c\u770b\u7740\u9648\u9759\u5a07\u5a9a\u3001\u7ed9\u7a81\u53d1\u7684\u72b6\u51b5\u641e\u5f97\u5fc3\u6d6e\u6c14\u8e81\u7ea2\u6251\u6251\u7684\u8138\u86cb\u3002</p><p>\u3000\u3000\u516c\u4ea4\u8f66\u53d8\u5411\u4ea7\u751f\u5de8\u5927\u7684\u529b\uff0c\u8981\u5c06\u8ba9\u9648\u9759\u5c06\u5f20\u606a\u62c9\u8fdb\u5979\u7684\u6000\u91cc\uff0c\u8981\u4e48\u8ba9\u5979\u6251\u8fdb\u5f20\u606a\u7684\u6000\u91cc\uff0c\u867d\u7136\u6bcf\u4e00\u6b21\u7684\u65f6\u95f4\u5f88\u77ed\uff0c\u4f46\u662f\u90a3\u4e48\u77ed\u7684\u65f6\u95f4\uff0c\u4e24\u4eba\u7684\u8eab\u4f53\u5374\u7d27\u7d27\u7684\u8d34\u5728\u4e00\u8d77\uff0c\u9648\u9759\u5bf9\u75af\u72c2\u7684\u516c\u4ea4\u8f66\u53f8\u673a\uff0c\u4e00\u70b9\u62b1\u6028\u7684\u5fc3\u601d\u90fd\u6ca1\u6709\u3002</p><p>\u3000\u3000\u539f\u6253\u7b97\u5728\u4e1c\u534e\u95e8\u90a3\u91cc\u4e0b\u8f66\u7684\uff0c\u53ef\u60dc\u53f8\u673a\u4e3a\u4e86\u81ea\u5df1\u98da\u8f66\u75db\u5feb\uff0c\u8fc7\u4e1c\u534e\u95e8\u65f6\u7adf\u7136\u6ca1\u6709\u4e3b\u52a8\u505c\u7ad9\u3002\u9648\u9759\u5fc3\u91cc\u60f3\u53ef\u4ee5\u6c38\u8fdc\u8fd9\u4e48\u4e58\u4e0b\u53bb\u5c31\u597d\u4e86\uff0c\u8f66\u5230\u71d5\u5f52\u5c71\u5357\u9e93\u7ec8\u70b9\u7ad9\uff0c\u5374\u4e0d\u5f97\u4e0d\u4e0b\u8f66\uff0c\u4e5f\u6ca1\u6709\u501f\u53e3\u7ee7\u7eed\u633d\u7740\u5f20\u606a\u7684\u624b\u81c2\u3002</p><p>\u3000\u3000\u201c\u611f\u89c9\u5982\u4f55\uff1f\u201d\u5f20\u606a\u770b\u7740\u6c14\u5598\u8138\u70ed\u7684\u9648\u9759\uff0c\u201c\u5e02\u91cc\u5bf9318\u8def\u516c\u4ea4\u8f66\u641e\u8bd5\u70b9\uff0c\u8ba9\u516c\u4ea4\u8f66\u7ed9\u53f8\u673a\u4e2a\u4eba\u627f\u5305\uff0c\u6240\u4ee5\u624d\u6709\u8fd9\u6837\u7684\u60c5\u51b5\u53d1\u751f\u3002\u73b0\u5728\u4ec0\u4e48\u90fd\u641e\u627f\u5305\uff0c\u4ee5\u4e3a\u627f\u5305\u5c31\u662f\u4e07\u7075\u4e39\u836f\uff0c\u90fd\u5fd8\u4e86\u5e02\u5185\u516c\u4ea4\u5e94\u8be5\u5c5e\u4e8e\u516c\u5171\u670d\u52a1\u7684\u9886\u57df\uff0c\u5e02\u6c11\u90fd\u7f34\u7a0e\u7f34\u7687\u7cae\uff0c\u8fd8\u8981\u6765\u4eab\u53d7\u8fd9\u79cd\u523a\u6fc0\uff01\u5509\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u7b11\u4e86\u7b11\uff0c\u5979\u53ef\u4e0d\u60f3\u8fd9\u65f6\u5019\u4e0e\u5f20\u606a\u8ba8\u8bba\u8fd9\u4e9b\u6c11\u751f\u95ee\u9898\uff0c\u8981\u662f\u80fd\u627e\u5230\u501f\u53e3\uff0c\u5979\u613f\u610f\u4e0e\u5f20\u606a\u518d\u5750318\u8def\u539f\u8def\u8fd4\u56de\uff0c\u8fd9\u4e48\u88ab\u8feb\u6402\u7d27\u5728\u4e00\u8d77\u7684\u611f\u89c9\u5728\u5fc3\u81c6\u6d4b\u95f4\u5f25\u6563\u7740\uff0c\u5373\u4f7f\u4e0d\u80fd\u518d\u4eab\u53d7\uff0c\u4e5f\u4e0d\u60f3\u8ba9\u611f\u89c9\u5f88\u5feb\u5c31\u6d88\u5931\u3002</p><p>\u3000\u3000\u56de\u5b66\u5e9c\u5df7\u8fd8\u662f\u5750\u516c\u4ea4\u8f66\uff0c\u4e0d\u8fc7\u4ece\u71d5\u5f52\u5c71\u5357\u9e93\u9a76\u51fa\u7684\u6e381\u516c\u4ea4\u662f\u8d9f\u5e02\u5185\u65c5\u6e38\u516c\u4ea4\uff0c\u53f8\u673a\u7684\u813e\u6c14\u5f88\u6e29\u548c\uff0c\u8f66\u53a2\u91cc\u4eba\u4e5f\u4e0d\u591a\uff0c\u90fd\u6709\u5ea7\u3002\u5230\u5b66\u5e9c\u5df7\u524d\u7684\u8f66\u7ad9\u4e0b\u8f66\uff0c\u9648\u9759\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u5bf9\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u665a\u4e0a\u6709\u6ca1\u6709\u7a7a\uff0c\u4f60\u603b\u8981\u8ba9\u6211\u6709\u673a\u4f1a\u8bf7\u4f60\u5403\u4e00\u987f\u996d\u5427\uff1f\u201d</p></article>"}}}]);
