(window.webpackJsonp=window.webpackJsonp||[]).push([[742],{765:function(p){p.exports={chapterTitle:null,title:"\u7b2c741\u7ae0 \u517b\u864e\u4e3a\u60a3",content:"<article><h2>\u7b2c741\u7ae0 \u517b\u864e\u4e3a\u60a3</h2><p>\u3000\u3000\u5e0c\u5c14\u987f\u5927\u9152\u5e97\u90a3\u91cc\u5df2\u7ecf\u7ed9\u5a92\u4f53\u8bb0\u8005\u56f4\u5f97\u6c34\u6cc4\u4e0d\u901a\uff0c\u4eca\u665a\u8bbe\u5728\u5e0c\u5c14\u987f\u5927\u9152\u5e97\u62db\u5f85\u5fb7\u5c14\u6cd5\u897f\u4e0e\u827e\u9ed8\u7684\u665a\u5bb4\uff0c\u662f\u6ca1\u6cd5\u51fa\u5e2d\u4e86\uff0c\u9648\u9759\u9080\u4ed6\u5171\u8fdb\u665a\u5bb4\uff0c\u5f20\u606a\u5c31\u4e00\u53e3\u7b54\u5e94\u4e0b\u6765\u3002\u5e76\u80a9\u5f80\u5b66\u5e9c\u5df7\u91cc\u8d70\uff0c\u98ce\u5f88\u5927\uff0c\u547c\u5578\u800c\u6765\u547c\u5578\u800c\u53bb\uff0c\u9b3c\u54ed\u72fc\u568e\u4f3c\u7684\uff0c\u8857\u4e0a\u884c\u4eba\u5f88\u5c11\uff0c\u90fd\u5306\u5306\u6765\u53bb\u3002</p><p>\u3000\u3000\u8d70\u5230\u521b\u57df\u7f51\u5427\u62d0\u89d2\u7684\u53c9\u8def\u53e3\uff0c\u9648\u9759\u95ee\u9053\uff1a\u201c\u8981\u4e0d\u8981\u53c2\u89c2\u4e00\u4e0b\u6211\u4eec\u8896\u73cd\u7684\u5c0f\u8bbe\u8ba1\u516c\u53f8\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5bf9\u4f60\u4eec\u5012\u62b1\u6709\u5f88\u5927\u7684\u671f\u5f85\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u4e0e\u9648\u9759\u4ece\u5185\u5df7\u53e3\u7684\u8f6c\u89d2\u697c\u68af\u8d70\u4e86\u4e09\u697c\u3002\u529e\u516c\u5ba4\u91cc\u7684\u94ed\u724c\u4e0a\u5199\u7740\u201c\u6307\u5357\u9488\u5de5\u4f5c\u5ba4\u201d\uff0c\u4e0d\u6653\u5f97\u8fd9\u6837\u7684\u540d\u5b57\u662f\u51fa\u4e8e\u8c01\u7684\u5174\u8da3\uff0c\u540d\u5b57\u662f\u6b21\u8981\uff0c\u5173\u952e\u8981\u80fd\u627f\u8f7d\u624b\u673a\u4ea7\u54c1\u8bbe\u8ba1\u7684\u529f\u80fd\u3002\u79d1\u738b\u9ad8\u79d1\u5c06\u5de5\u4f5c\u5ba4\u8bbe\u7acb\u5728\u8fd9\u91cc\uff0c\u4e00\u662f\u4e3a\u8c22\u5b50\u5609\u65b9\u4fbf\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u7f51\u7edc\u786c\u4ef6\u5b9e\u65bd\u9664\u4e86\u6a61\u6811\u56ed\u4e4b\u5916\uff0c\u5c31\u5b66\u5e9c\u5df7\u3001\u9752\u5e74\u516c\u5bd3\u8fd9\u8fb9\u6700\u597d\uff0c\u6b64\u65f6\u5df2\u6709\u5149\u7ea4\u63a5\u53e3\uff0c\u4f5c\u4e3a\u79d1\u738b\u9ad8\u65b0\u7684\u4e0b\u5c5e\u5b50\u516c\u53f8\u53c8\u4e0d\u80fd\u7533\u8bf7\u8fdb\u9a7b\u521b\u4e1a\u56ed\u3002</p><p>\u3000\u3000\u56fd\u5185\u7684\u6570\u5b57\u624b\u673a\u4ea7\u4e1a\u53ef\u4ee5\u8bf4\u662f\u4e00\u7a76\u4e8c\u767d\uff0c\u624b\u673a\u8bbe\u8ba1\u4e5f\u6ca1\u6709\u4ec0\u4e48\u57fa\u7840\u53ef\u8a00\uff0c\u9526\u6e56\u624b\u673a\u8bbe\u8ba1\uff0c\u4e5f\u4e3b\u8981\u4f9d\u8d56\u65e5\u672c\u3001\u97e9\u56fd\u4ee5\u53ca\u9999\u6e2f\u7684\u4ea7\u54c1\u8bbe\u8ba1\u4e2d\u5fc3\uff0c\u5317\u4eac\u7684\u4ea7\u54c1\u8bbe\u8ba1\u4e2d\u5fc3\uff0c\u76ee\u524d\u8fd8\u53ea\u57f9\u80b2\u4eba\u624d\u7684\u9636\u6bb5\u3002\u79d1\u738b\u9ad8\u79d1\u7684\u8bbe\u8ba1\u529b\u91cf\uff0c\u5e94\u8be5\u8bf4\u662f\u4ece\u97e9\u56fd\u8058\u8bf7\u4e24\u540d\u8bbe\u8ba1\u4eba\u5458\u624d\u7b97\u6709\u4e2a\u57fa\u7840\u5427\u3002\u5f20\u606a\u5efa\u8bae\u9648\u9759\uff0c\u5c31\u7b97\u79d1\u738b\u9ad8\u79d1\u6682\u65f6\u6ca1\u6709\u80fd\u529b\u5728\u97e9\u56fd\u6216\u65e5\u672c\u6210\u7acb\u4ea7\u54c1\u8bbe\u8ba1\u4e2d\u5fc3\uff0c\u4e5f\u8981\u4e0e\u90a3\u91cc\u7684\u8bbe\u8ba1\u516c\u53f8\u4fdd\u6301\u5bc6\u5207\u7684\u8054\u7cfb\uff0c\u60f3\u8981\u4e00\u4e0b\u5b50\u638c\u63e1\u6838\u5fc3\uff0c\u6240\u4ed8\u51fa\u7684\u4ee3\u4ef7\u662f\u6781\u4e3a\u60ca\u4eba\u7684\uff0c\u9526\u6e56\u4e5f\u662f\u4ece\u9999\u6e2f\u6536\u522e\u4e86\u56db\u5341\u4ebf\u7684\u5de8\u989d\u4e4b\u540e\u624d\u6709\u4e00\u5b9a\u7684\u4fe1\u5fc3\uff0c\u79d1\u738b\u9ad8\u79d1\u4e13\u5fc3\u505a\u8d44\u6e90\u6574\u5408\u4e0e\u4ea7\u54c1\u8bbe\u8ba1\u7684\u5de5\u4f5c\uff0c\u4e5f\u672a\u5fc5\u4e0d\u80fd\u83b7\u5f97\u6210\u529f\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5f20\u606a\u4e0e\u9648\u9759\u8d70\u8fdb\u8bbe\u8ba1\u516c\u53f8\uff0c\u8c22\u5b50\u5609\u5c06\u4e66\u672c\u62b1\u5728\u80f8\u524d\u6b63\u8981\u51fa\u95e8\uff0c\u5dee\u70b9\u649e\u5728\u4e00\u8d77\uff0c\u7738\u5b50\u72d0\u7591\u7684\u5728\u4ed6\u4eec\u4e24\u4eba\u8138\u4e0a\u8f6c\u4e86\u51e0\u5708\uff0c\u8bf4\u9053\uff0c\u201c\u600e\u4e48\u8fd9\u4e48\u65e9\u5c31\u56de\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u966a\u738b\u7ef4\u5747\u3001\u59da\u6587\u76db\u5403\u8fc7\u4e2d\u996d\u5c31\u56de\u6765\u4e86\uff0c\u8fc7\u6c5f\u65f6\u9047\u5230\u5835\u8f66\uff0c\u4e0d\u7136\u8fd8\u8981\u65e9\u56de\u6765\u2026\u2026\u201d\u60f3\u8d77\u516c\u4ea4\u8f66\u4e0a\u7684\u4e00\u5e55\uff0c\u9648\u9759\u8138\u9690\u9690\u53d1\u70eb\uff0c\u5979\u5f53\u7136\u4e0d\u4f1a\u8bf4\u51fa\u6765\u3002\u90c1\u840d\u4eca\u5929\u6e05\u6668\u5c31\u5306\u5306\u56de\u6d77\u5dde\u4e86\uff0c\u5979\u4e0e\u9648\u9759\u5f88\u96be\u90fd\u540c\u65f6\u79bb\u5f00\u6d77\u5dde\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u6b63\u597d\u8bf7\u5f20\u606a\u5230\u516c\u53f8\u770b\u4e00\u4e0b\uff0c\u4f60\u8981\u8d76\u7740\u53bb\u4e0a\u8bfe\uff1f\u201d</p><p>\u3000\u3000\u8c22\u5b50\u5609\u62b1\u7740\u4e66\u672c\uff0c\u72e1\u9ed8\u7684\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u67d0\u4eba\u90fd\u4ece\u6ca1\u5728\u6559\u5ba4\u91cc\u663e\u8fc7\u8eab\uff0c\u6211\u5076\u5c14\u7f3a\u5e2d\u4e00\u5802\u8bfe\u7edd\u5bf9\u7b97\u4e0d\u4e0a\u7f6a\u5927\u6076\u6781\u2026\u2026\u201d\u4e0d\u80af\u8d70\u4e86\u3002</p><p>\u3000\u3000\u8d70\u8fdb\u5de5\u4f5c\u5ba4\uff0c\u90a3\u4e00\u7537\u4e00\u5973\u4e24\u540d\u97e9\u56fd\u8bbe\u8ba1\u4eba\u5458\u55d1\u55d1\u5427\u5427\u7684\u62ff\u82f1\u8bed\u4e0e\u5de5\u4f5c\u5ba4\u91cc\u7684\u51e0\u540d\u9752\u5e74\u5458\u5de5\u4ea4\u6d41\uff0c\u770b\u7740\u9648\u9759\u3001\u5f20\u606a\u4e0e\u8c22\u5b50\u5609\u5979\u4eec\u8d70\u8fdb\u6765\uff0c\u795e\u60c5\u95f2\u6de1\u7684\u6253\u7740\u62db\u547c\u3002\u97e9\u56fd\u4eba\u7684\u82f1\u8bed\u53d1\u97f3\u4f3c\u4e4e\u5f88\u96be\u6807\u51c6\uff0c\u5f20\u606a\u542c\u4e86\u8d39\u52b2\uff0c\u4fbf\u7528\u97e9\u8bed\u4e0e\u4ed6\u4eec\u4ea4\u6d41\uff0c\u518d\u7ffb\u8bd1\u7ed9\u9648\u9759\u3001\u8c22\u5b50\u5609\u4ee5\u53ca\u5de5\u4f5c\u5ba4\u7684\u90a3\u51e0\u540d\u9752\u5e74\u5458\u5de5\u542c\u3002</p><p>\u3000\u3000\u8fc7\u4e8e\u6df1\u6da9\u7684\u4e13\u4e1a\u77e5\u8bc6\uff0c\u5f20\u606a\u6ca1\u6709\u53bb\u6d89\u730e\uff0c\u4f46\u5bf9\u624b\u673a\u8bbe\u8ba1\u601d\u8def\u7684\u601d\u89e3\uff0c\u5c31\u4e0d\u6bd4\u8fd9\u4e24\u540d\u97e9\u56fd\u8bbe\u8ba1\u4eba\u5458\u5dee\uff0c\u66f4\u4e0d\u8981\u8bf4\u5bf9\u672a\u6765\u624b\u673a\u53d1\u5c55\u8d8b\u52bf\u7684\u89c1\u89e3\u4e86\u3002</p><p>\u3000\u3000\u56fd\u5185\u5728\u624b\u673a\u8bbe\u8ba1\u65b9\u9762\u4e00\u7a77\u4e8c\u767d\uff0c\u5f20\u606a\u6562\u80af\u5b9a\u5de5\u4f5c\u5ba4\u7684\u5176\u4ed6\u5458\u4eba\u751a\u81f3\u5bf9\u624b\u673a\u8bbe\u8ba1\u7684\u6982\u5ff5\u90fd\u5f88\u964c\u751f\uff0c\u4e5f\u96be\u602a\u8fd9\u4e24\u540d\u97e9\u56fd\u8bbe\u8ba1\u4eba\u5458\u773c\u775b\u91cc\u6709\u6240\u8f7b\u6162\u3002\u8981\u60f3\u83b7\u5f97\u522b\u4eba\u7684\u5c0a\u91cd\uff0c\u5e76\u4e0d\u662f\u63d0\u4f9b\u9ad8\u85aa\u5c31\u53ef\u4ee5\u7684\uff0c\u5f20\u606a\u62ff\u97e9\u8bed\u5c31\u624b\u673a\u8bbe\u8ba1\u95ee\u9898\u4e0e\u4e24\u540d\u97e9\u56fd\u8bbe\u8ba1\u4eba\u5458\u4ea4\u6d41\u4e86\u7247\u523b\uff0c\u53ea\u7528\u4e86\u51e0\u4e2a\u95ee\u9898\u5c31\u5c06\u4ed6\u4eec\u95ee\u4f4f\uff0c\u5c06\u4ed6\u4eec\u5fc3\u91cc\u7684\u81ea\u5f97\u4e0e\u6020\u6162\u51fb\u5f97\u7c89\u788e\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u591a\u52a0\u9017\u7559\uff0c\u5de5\u4f5c\u5ba4\u7684\u89c4\u6a21\u4e5f\u5f88\u5c0f\uff0c\u770b\u4e0d\u4e86\u51e0\u773c\uff0c\u4ed6\u5c31\u544a\u8f9e\u79bb\u5f00\u3002\u7ea6\u597d\u665a\u4e0a\u4e00\u8d77\u5403\u996d\uff0c\u9648\u9759\u4e0d\u4f1a\u5c06\u8c22\u5b50\u5609\u4e22\u4e0b\uff0c\u5f20\u606a\u81ea\u7136\u4e5f\u6ca1\u6709\u5355\u72ec\u4e0e\u9648\u9759\u7ea6\u4f1a\u7684\u673a\u4f1a\u3002\u8d70\u4e4b\u524d\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u8ddf\u9648\u9759\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5de5\u4f5c\u5ba4\u5e94\u662f\u4f60\u4eec\u8fd1\u671f\u53d1\u5c55\u7684\u91cd\u70b9\uff0c\u4f60\u6765\u5f80\u5efa\u90ba\u7684\u673a\u4f1a\u4e5f\u591a\uff0c\u8981\u662f\u4f4f\u5bbe\u9986\u89c9\u5f97\u4e0d\u65b9\u4fbf\uff0c\u5b66\u5e9c\u5df7\u540e\u9762\u7684\u516c\u5bd3\uff0c\u4e0d\u6653\u5f97\u4f60\u4f1a\u4e0d\u4f1a\u559c\u6b22\uff1f\u201d</p><p>\u3000\u3000\u201c\u80fd\u8fd9\u6837\u5c31\u65b9\u4fbf\u591a\u4e86\uff0c\u4f4f\u5bbe\u9986\u5b64\u96f6\u96f6\u7684\u611f\u89c9\u771f\u7684\u5f88\u51c4\u51c9\u3002\u201d\u9648\u9759\u7b11\u7740\u8bf4\uff0c\u201c\u5b50\u5609\u80fd\u4e5f\u65b9\u4fbf\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u5b50\u5609\u72d0\u7591\u7684\u770b\u7740\u5f20\u606a\uff1a\u201c\u600e\u4e48\u611f\u89c9\u4f60\u522b\u6709\u7528\u5fc3\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u773c\u775b\u770b\u7740\u8c22\u5b50\u5609\uff0c\u6413\u4e86\u6413\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u8ba9\u4eba\u597d\u4f01\u56fe\u7684\uff1f\u201d</p><p>\u3000\u3000\u8c22\u5b50\u5609\u603b\u7a76\u6709\u7740\u5c0f\u5973\u5b69\u5b50\u7684\u5fc3\u6027\uff0c\u6709\u4e9b\u53d7\u4e0d\u4f4f\u5f20\u606a\u8fd1\u4f3c\u65e0\u8d56\u7684\u8bed\u8c03\uff0c\u54ac\u7740\u5634\u5507\u8bf4\uff1a\u201c\u73b0\u5728\u603b\u5f52\u8981\u8c22\u4f60\u7684\uff0c\u53ea\u4e0d\u8fc7\uff0c\u9526\u6e56\u771f\u7684\u5c31\u4e0d\u62c5\u5fc3\u79d1\u738b\u9ad8\u79d1\u603b\u6709\u4e00\u5929\u4f1a\u4e00\u9e23\u60ca\u4eba\u6210\u4e3a\u9526\u6e56\u7684\u7ade\u4e89\u5bf9\u624b\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f88\u671f\u5f85\u4f60\u4eec\u80fd\u2018\u4e09\u5e74\u4e0d\u9e23\u3001\u4e00\u9e23\u60ca\u4eba\u2019\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u505a\u4e86\u8fd9\u4e48\u591a\u753b\u86c7\u6dfb\u8db3\u7684\u4e8b\u60c5\uff0c\u4f1a\u62c5\u5fc3\u517b\u864e\u4e3a\u60a3\u5417\uff1f\u5b9e\u8bdd\u8bf4\uff0c\u4f1a\u62c5\u5fc3\uff0c\u4f46\u662f\u517b\u864e\u4e5f\u4e0d\u4ec5\u4ec5\u53ea\u517b\u79d1\u738b\u9ad8\u79d1\u4e00\u53ea\u3002\u6240\u8c13\u4e07\u7d2b\u5343\u7ea2\u624d\u662f\u6625\uff0c\u9526\u6e56\u4e00\u679d\u72ec\u79c0\u8fdc\u8fdc\u90fd\u4e0d\u8db3\u4ee5\u79f0\u5f97\u4e0a\u6625\u5929\u7684\u2026\u2026\u5982\u679c\u8fd9\u79cd\u60f3\u6cd5\u4f60\u5f88\u96be\u63a5\u53d7\uff0c\u5c06\u6211\u7406\u89e3\u6210\u522b\u6709\u7528\u5fc3\u4e5f\u65e0\u4e0d\u53ef\u3002\u201d\u89c1\u8c22\u5b50\u5609\u6ca1\u6709\u542d\u58f0\uff0c\u5f20\u606a\u4e0e\u9648\u9759\u7b11\u4e86\u7b11\uff0c\u5c31\u5148\u79bb\u5f00\u4e86\u3002</p><p>\u3000\u3000\u7b49\u5f20\u606a\u79bb\u5f00\u4e4b\u540e\uff0c\u8c22\u5b50\u5609\u624d\u4e0d\u670d\u6c14\u7684\u55c5\u4e86\u55c5\u9f3b\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u795e\u6c14\u4ec0\u4e48\uff0c\u6211\u4e00\u5b9a\u4f1a\u8ba9\u4ed6\u5927\u5403\u4e00\u60ca\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u60f3\u8d77\u5728\u6865\u4e0a\u5f20\u606a\u5e2e\u81ea\u5df1\u7406\u6574\u5934\u53d1\u7684\u6e29\u67d4\uff0c\u5fae\u62bf\u7740\u5634\u7b11\u4e86\u7b11\uff0c\u4e5f\u8bb8\u79d1\u738b\u9ad8\u79d1\u5728\u8fd9\u4e48\u591a\u516c\u53f8\u91cc\u6240\u53d7\u5230\u6b8a\u9047\u66f4\u8000\u773c\u4e00\u4e9b\uff0c\u4f46\u662f\u540c\u6837\u6709\u8bb8\u591a\u7684\u79d1\u6280\u578b\u516c\u53f8\u76f4\u63a5\u4ece\u9526\u6e56\u5b9e\u65bd\u7684\u63aa\u65bd\u4e2d\u83b7\u76ca\uff0c\u50cf\u65b0\u5143\u7535\u5668\u51e0\u4e4e\u5c31\u662f\u5728\u9526\u6e56\u7684\u6276\u6301\u53d1\u5c55\u5230\u4eca\u5929\u7684\u89c4\u6a21\u3002\u4e4b\u524d\u6216\u8bb8\u4f1a\u6709\u4eba\u8bf4\u9526\u6e56\u6276\u6301\u65b0\u5143\u662f\u4e3a\u4e86\u6253\u538b\u79d1\u738b\u3002\u9526\u6e56\u8f89\u714c\u7684\u5d1b\u8d77\u5c31\u65e0\u60c5\u7684\u7c89\u788e\u4e86\u8fd9\u79cd\u731c\u6d4b\uff0c\u9526\u6e56\u771f\u8981\u5bf9\u79d1\u738b\u4e0b\u624b\uff0c\u6839\u672c\u5c31\u4e0d\u9700\u8981\u501f\u52a9\u5916\u529b\u3002\u5c06\u6570\u5b57\u624b\u673a\u6280\u672f\u65e0\u507f\u7684\u516c\u5f00\uff0c\u62ff\u51fa\u4e09\u4ebf\u5de8\u989d\u6210\u7acb\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u6276\u6301\u79d1\u6280\u578b\u521b\u4e1a\u4f01\u4e1a\uff0c\u76f8\u4fe1\u9526\u6e56\u8fd8\u4f1a\u9646\u7eed\u51fa\u53f0\u6276\u6301\u5176\u4ed6\u79d1\u6280\u516c\u53f8\u7684\u63aa\u65bd\uff0c\u76ee\u7684\u53ea\u662f\u4e3a\u4e86\u59f9\u7d2b\u5ae3\u7ea2\u7684\u6625\u5929\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u6c14\u5ea6\u624d\u662f\u8ba9\u5176\u4ed6\u4eba\u671b\u5c18\u83ab\u53ca\u7684\u5730\u65b9\uff0c\u4e5f\u662f\u4ed6\u8eab\u4e0a\u6700\u8ff7\u4eba\u3001\u9189\u4eba\u4e4b\u5904\u3002\u9648\u9759\u8ba9\u5b50\u5609\u5feb\u4e0a\u8bfe\u53bb\uff0c\u5979\u6536\u62fe\u7740\u8d44\u6599\u8d70\u8fdb\u529e\u516c\u5ba4\uff0c\u60f3\u8d77\u5728\u516c\u4ea4\u8f66\u4e0a\u90a3\u201c\u88ab\u8feb\u201d\u7684\u76f8\u62e5\uff0c\u5fc3\u90fd\u8981\u9189\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e0e\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u7684\u8c08\u5224\u4e00\u5f00\u59cb\u5c31\u53d7\u5230\u5a92\u4f53\u7684\u5173\u6ce8\uff0c\u9526\u6e56\u81ea\u7136\u4e5f\u4e0d\u4f1a\u653e\u5f03\u6269\u5927\u5f71\u54cd\u7684\u673a\u4f1a\uff0c\u4e0d\u4ec5\u662f\u56fd\u5185\u7684\u5f71\u54cd\u529b\uff0c\u4e5f\u501f\u7740\u5fb7\u4eea\u7684\u9ad8\u679d\uff0c\u501f\u673a\u5c06\u5f71\u54cd\u529b\u5f80\u6d77\u5916\u5e02\u573a\u9010\u6b65\u7684\u6e17\u900f\u3002\u7b2c\u4e00\u5929\u6ca1\u6709\u4ec0\u4e48\u51c6\u5907\uff0c\u7b2c\u4e8c\u5929\u5c31\u5546\u8bae\u51fa\u4e00\u4e2a\u65b0\u95fb\u53d1\u5e03\u4f1a\u7684\u673a\u5236\uff0c\u6bcf\u5929\u8c08\u5224\u7ed3\u675f\u90fd\u8981\u6709\u9009\u62e9\u6027\u7684\u516c\u5e03\u4e00\u4e9b\u8c08\u5224\u7684\u4fe1\u606f\uff0c\u4e5f\u514d\u5f97\u5a92\u4f53\u8bb0\u8005\u665a\u4e0a\u90fd\u76ef\u7740\u4ed6\u4eec\u5c41\u80a1\u540e\u9762\u8ba9\u4ed6\u4eec\u4e0d\u5f97\u5b89\u795e\u3002</p><p>\u3000\u3000\u9996\u5148\u7ecf\u65b0\u95fb\u53d1\u5e03\u4f1a\u5411\u5916\u754c\u516c\u5e03\u7684\u7206\u70b8\u6027\u4fe1\u606f\u5c31\u662f\u4e09\u5bb6\u516c\u53f8\u5c06\u5171\u540c\u51fa\u8d44\u6210\u7acb\u4e00\u5bb6\u7c7b\u4f3c\u4e13\u5229\u6280\u672f\u8054\u76df\u7684\u79d1\u6280\u516c\u53f8\u6765\u63a5\u7ba1\u5206\u6563\u5728\u4e09\u5bb6\u516c\u53f8\u624b\u91cc\u7684SVCD\u789f\u673a\u89e3\u7801\u82af\u7247\u3001\u7cfb\u7edf\u63a7\u5236\u7b49\u65b9\u9762\u7684\u76f8\u5173\u4e13\u5229\u6280\u672f\uff0c\u4ee5\u540e\u5c06\u7531\u8fd9\u5bb6\u516c\u53f8\u6388\u6743\u8bb8\u53ef\u534a\u5bfc\u4f53\u5382\u5546\u751f\u4ea7\u9500\u552eSVCD\u89e3\u7801\u82af\u7247\u3001\u7cfb\u7edf\u63a7\u5236\u82af\u7247\uff0c\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u6240\u9662\u5bf9\u8fd9\u5bb6\u79d1\u6280\u516c\u53f8\u6301\u80a126\uff05\u3002\u65b0\u95fb\u53d1\u5e03\u4f1a\u4e5f\u540c\u65f6\u5ba3\u5e03\u5c06\u5927\u5e45\u5ea6\u8c03\u4f4e\u6388\u6743\u8bb8\u53ef\u8d39\u7528\uff0c\u8fd9\u5c06\u610f\u5473\u7740SVCD\u789f\u673a\u5e02\u573a\u552e\u4ef7\u5c06\u6709\u8fdb\u4e00\u6b65\u4e0b\u8c03\u7684\u7a7a\u95f4\u3002</p><p>\u3000\u3000\u901a\u8fc7\u7535\u89c6\u770b\u5230\u65b0\u95fb\u5a92\u4f53\u5bf9\u9526\u6e56\u8fd9\u6b21\u4e0e\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u8c08\u5224\u7684\u8ffd\u8e2a\u62a5\u9053\uff0c\u8c22\u5251\u5357\u611f\u5230\u624b\u8db3\u51b0\u51c9\u3002\u6b64\u65f6\u7684\u8c22\u5251\u5357\u4eba\u5728\u91d1\u5c71\uff0c\u4e3a\u662f\u7535\u5b50\u5de5\u4e1a\u5236\u9020\u57fa\u5730\u7684\u4e8b\u60c5\u5230\u91d1\u5c71\u3002\u591c\u665a\uff0c\u4ed6\u559c\u6b22\u7ad9\u5728\u91d1\u5c71\u5e02\u6700\u9ad8\u5efa\u7b51\u91d1\u4fe1\u5927\u53a6\u7684\u9876\u697c\u51dd\u89c6\u811a\u4e0b\u7684\u57ce\u5e02\uff0c\u4f1a\u8ba9\u4ed6\u6709\u4e00\u79cd\u5c06\u6574\u5ea7\u57ce\u5e02\u90fd\u8e29\u5728\u811a\u4e0b\u7684\u611f\u89c9\u3002\u4f46\u662f\u521a\u624d\u7684\u62a5\u9053\u8ba9\u4ed6\u611f\u89c9\u5f88\u7cdf\u7cd5\uff0c\u975e\u5e38\u7684\u7cdf\u7cd5\u3002</p><p>\u3000\u3000\u9526\u6e56\u7adf\u7136\u80fd\u5728\u62e5\u6709SVCD\u89e3\u7801\u82af\u7247\u6240\u6709\u76f8\u5173\u4e13\u5229\u6280\u672f\u7684\u79d1\u6280\u516c\u53f8\u6301\u80a1\u8d85\u8fc7\u56db\u5206\u4e4b\u4e00\uff0c\u90a3\u5c31\u610f\u5473\u7740\u9526\u6e56\u4e00\u5f00\u59cb\u5c31\u6df1\u5165\u7684\u52a0\u5165SVCD\u89e3\u7801\u82af\u7247\u6280\u672f\u7684\u7814\u53d1\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u662f\u591a\u4e45\u4e4b\u524d\u5c31\u5f00\u59cbSVCD\u89e3\u7801\u82af\u7247\u7684\u7814\u53d1\uff1f\u4e24\u5e74\u524d\u8fd8\u662f\u4e09\u5e74\u524d\uff1f\u53ef\u662f\u4e09\u5e74\u524d\uff0c\u9526\u6e56\u4e5f\u624d\u521a\u521a\u6210\u7acb\u554a\uff01</p><p>\u3000\u3000\u8c22\u5251\u5357\u8fd9\u4e00\u523b\u89c9\u5f97\u5f88\u65e0\u529b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6d77\u5dde\u5de5\u4e1a\u65b0\u533a\uff0c\u539f\u76ca\u9686\u9547\u7535\u5b50\u5de5\u4e1a\u56ed\uff0c\u65b0\u5143\u7535\u5668\u96c6\u56e2\u603b\u88c1\u4e01\u6587\u7965\u5f88\u5c11\u4f1a\u5728\u591c\u91cc\u4e5d\u70b9\u949f\u4e4b\u524d\u79bb\u5f00\u516c\u53f8\uff0c\u4eba\u8981\u52e4\u594b\uff0c\u4ed6\u60f3\u5c31\u7b97\u5176\u4ed6\u65b9\u9762\u7a0d\u6709\u4e0d\u8db3\uff0c\u52e4\u594b\u4e5f\u80fd\u5f25\u8865\u3002\u4ed6\u5f85\u4eba\u4e5f\u5bbd\u548c\uff0c\u9664\u4e86\u5fc5\u8981\u7559\u4e0b\u6765\u4ea4\u6d41\u7684\u5de5\u4f5c\u4eba\u5458\uff0c\u4ed6\u5e76\u4e0d\u559c\u6b22\u770b\u5230\u5176\u4ed6\u4eba\u65e0\u4e8b\u4e0b\u73ed\u540e\u8fd8\u966a\u4ed6\u8017\u5728\u529e\u516c\u5ba4\u91cc\u3002\u4ed6\u521a\u521a\u4ece\u4e2d\u73ed\u8f66\u95f4\u5de1\u89c6\u56de\u6765\uff0c\u8d70\u56de\u5230\u529e\u516c\u5ba4\uff0c\u4ed6\u4f84\u5973\u4e01\u8389\u534e\u7684\u7537\u53cb\uff0c\u4ed6\u4f9d\u4e3a\u52a9\u624b\u7684\u65b0\u5143\u7535\u5668\u5e02\u573a\u90e8\u526f\u603b\u7ecf\u7406\u5468\u5c0f\u5175\u544a\u8bc9\u4ed6\u521a\u4ece\u542c\u6765\u7684\u65b0\u95fb\u3002</p><p>\u3000\u3000\u4e01\u6587\u7965\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u79d1\u738b\u7adf\u7136\u5728\u4e00\u5e74\u524d\u60f3\u8d85\u8d8a\u8fd9\u6837\u7684\u516c\u53f8\uff0c\u8fd9\u65f6\u5019\u5927\u5bb6\u90fd\u6653\u5f97\u662f\u7b11\u8bdd\u4e86\uff0c\u4eba\u5bb6\u6839\u672c\u5c31\u6ca1\u6709\u5c06\u4ed6\u4eec\u653e\u5728\u773c\u91cc\uff0c\u4e4c\u9f9f\u60f3\u8d76\u4e0a\u5154\u5b50\uff0c\u4e5f\u8981\u5154\u5b50\u6253\u76f9\u624d\u884c\u2026\u2026\u201d\u53c8\u62cd\u4e86\u62cd\u5468\u5c0f\u5175\u7684\u80a9\u8180\uff0c\u8bed\u91cd\u5fc3\u957f\u7684\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u8981\u52aa\u529b\u554a\u3002\u201d</p></article>"}}}]);
