(window.webpackJsonp=window.webpackJsonp||[]).push([[745],{768:function(p){p.exports={chapterTitle:null,title:"\u7b2c744\u7ae0 \u82b3\u8e2a\u521d\u663e",content:"<article><h2>\u7b2c744\u7ae0 \u82b3\u8e2a\u521d\u663e</h2><p>\u3000\u3000\u79bb\u5f00\u9152\u4f1a\u8fd8\u4e0d\u5230\u591c\u91cc\u5341\u70b9\u949f\uff0c\u5e26\u7740\u4e9b\u5fae\u7684\u9189\u610f\uff0c\u8c22\u5251\u5357\u9a7e\u8f66\u4ece\u5b66\u5e9c\u5df7\u7ecf\u8fc7\u9a76\u5165\u4e1c\u5927\u6821\u56ed\u897f\u5317\u89d2\u4e0a\u7684\u6559\u804c\u5de5\u4f4f\u5b85\u533a\u3002\u7ecf\u8fc7\u706f\u706b\u7480\u74a8\u7684\u5b66\u5e9c\u5df7\u4e0e\u9752\u5e74\u516c\u5bd3\uff0c\u8c22\u5251\u5357\u5f00\u8f66\u7684\u901f\u5ea6\u66f4\u6162\uff0c\u770b\u5230\u706f\u706b\u901a\u660e\u7684\u521b\u57df\u7f51\u5427\uff0c\u8c22\u5251\u5357\u6653\u5f97\u9664\u4e86\u90a3\u4e2a\u5927\u6982\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u7ed9\u4eba\u8d85\u8d8a\u7684\u4eba\u4e4b\u5916\uff0c\u81ea\u5df1\u80cc\u540e\u8fd8\u6709\u8bb8\u591a\u8ffd\u8d76\u8005\u3002</p><p>\u3000\u3000\u201c\u6211\u4f4f\u5bbe\u9986\u91cc\u53bb\u5427\u2026\u2026\u201d\u8f66\u5b50\u8fdb\u4e1c\u6821\u95e8\uff0c\u8eab\u8fb9\u7684\u5973\u4eba\u5e26\u7740\u4e9b\u62c5\u5fe7\u4e0e\u4e0d\u5b89\u5bf9\u8c22\u5251\u5357\u8bf4\u3002</p><p>\u3000\u3000\u201c\u6211\u5927\u8205\u8fd9\u4eba\u5f88\u597d\u76f8\u5904\uff0c\u201d\u8c22\u5251\u5357\u5b89\u6170\u9053\uff0c\u201c\u4ed6\u4eec\u5bb6\u8981\u6ca1\u6709\u7a7a\u623f\u95f4\uff0c\u6211\u5c31\u8ddf\u4f60\u4e00\u8d77\u4f4f\u5bbe\u9986\u53bb\uff0c\u600e\u4e48\u820d\u5f97\u5c06\u4f60\u4e00\u4e2a\u4eba\u4e22\u5bbe\u9986\u91cc\uff1f\u201d</p><p>\u3000\u3000\u5f00\u8f66\u9a76\u5165\u4e1c\u5927\u6559\u804c\u5458\u7684\u4f4f\u5b85\u533a\uff0c\u8bb8\u591a\u4eba\u5bb6\u90fd\u5728\u5bb6\u5c5e\u697c\u5792\u4e86\u9e21\u7a9d\u517b\u4e9b\u9e21\u9e2d\uff0c\u7ed9\u6c7d\u8f66\u7684\u58f0\u97f3\u60ca\u52a8\uff0c\u9e21\u9e2d\u5728\u72ed\u4ec4\u7684\u7a7a\u95f4\u91cc\u6251\u817e\u9e23\u53eb\uff0c\u8fd8\u6742\u5939\u7740\u72ac\u5420\u58f0\uff0c\u51b7\u5bc2\u7684\u5c0f\u533a\u987f\u65f6\u70ed\u95f9\u4e86\u8bb8\u591a\u3002\u8f66\u524d\u706f\u626b\u8fc7\uff0c\u80fd\u591f\u770b\u89c1\u5c0f\u533a\u7684\u82b1\u56ed\u4e5f\u591a\u7ed9\u5228\u4e86\u79cd\u4e0a\u8471\u849c\u758f\u83dc\u3002</p><p>\u3000\u3000\u5728\u5f88\u591a\u4eba\u5370\u8c61\u91cc\u4e1c\u6d77\u5927\u5b66\u6559\u804c\u5458\u4e0e\u4ed6\u4eec\u7684\u5bb6\u5c5e\u4eec\u90fd\u662f\u9ad8\u77e5\u5206\u5b50\uff0c\u5728\u8fd9\u4e2a\u5e74\u4ee3\u4e00\u5b9a\u5c5e\u4e8e\u56fd\u5185\u751f\u6d3b\u54c1\u5473\u5f88\u9ad8\u7684\u4e00\u4e2a\u7fa4\u4f53\u3002\u5176\u5b9e\u4e0d\u7136\uff0c\u4ece\u89e3\u653e\u521d\u5230\u6539\u9769\u5f00\u653e\u524d\u7684\u8fd9\u6bb5\u65f6\u95f4\u77e5\u8bc6\u5206\u5b50\u662f\u81ed\u8001\u4e5d\uff0c\u8bb8\u591a\u5927\u5b66\u8bb2\u5e08\u3001\u6559\u6388\u90fd\u5a36\u519c\u6751\u5987\u5973\u4e3a\u59bb\uff0c\u52a0\u4e0a\u8bb8\u591a\u519b\u961f\u4e0a\u4e0b\u6765\u7684\u5b98\u5458\u53ca\u5bb6\u5c5e\u6df7\u8ff9\u8fdb\u6765\uff0c\u4e1c\u5927\u6574\u4e2a\u6559\u6388\u9ad8\u5e72\u697c\u5c0f\u533a\u91cc\u5c31\u7ed9\u6574\u5f97\u50cf\u519c\u5bb6\u5927\u9662\u4f3c\u7684\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5c06\u8f66\u5b50\u505c\u5728\u697c\u524d\uff0c\u8ddf\u8eab\u8fb9\u7684\u5973\u4eba\u89e3\u91ca\u8fd9\u662f\u4e1c\u5927\u7684\u56db\u5927\u602a\u73b0\u8c61\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u5bb6\u5728\u7b2c\u4e8c\u680b\u6559\u6388\u697c\u7684\u4e09\u697c\uff0c\u5c31\u592b\u5987\u4e24\u4eba\u52a0\u4e00\u4e2a\u4ece\u8001\u5bb6\u8bf7\u6765\u7684\u4fdd\u59c6\u4f4f\u7740\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u8fdb\u4e86\u5c4b\uff0c\u6253\u8fc7\u62db\u547c\uff0c\u5c31\u4e0e\u4ed6\u8205\u5468\u747e\u73ba\u8fdb\u4e66\u623f\u8bf4\u4e8b\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u624b\u91cc\u53ef\u80fd\u53c8\u638c\u63e1\u7740\u4e00\u9879\u65b0\u7684\u4ea7\u54c1\u6280\u672f\u2026\u2026\u201d\u8fdb\u4e66\u623f\uff0c\u8c22\u5251\u5357\u5c31\u5c06\u4ece\u674e\u5728\u6d19\u90a3\u91cc\u542c\u5230\u7684\u6d88\u606f\u544a\u8bc9\u4ed6\u8205\u5468\u747e\u73ba\uff0c\u201c\u4eca\u665a\u4e0e\u4e09\u661f\u7684\u4eba\u89c1\u9762\uff0c\u77e5\u9053\u4e00\u4e9b\u6d88\u606f\uff0c\u4ed6\u4eec\u8bf4\u7684\u5f88\u542b\u7cca\uff0c\u4e0d\u8fc7\u80fd\u770b\u51fa\u4ed6\u4eec\u4e5f\u5f88\u91cd\u89c6\u8fd9\u9879\u4ea7\u54c1\u6280\u672f\uff0c\u6b63\u7740\u624b\u5f00\u53d1\u76f8\u5173\u6280\u672f\uff0c\u5f88\u62c5\u5fc3\u7ed9\u9526\u6e56\u6377\u8db3\u5148\u767b\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u7740\u4ed6\u8205\u51dd\u89c6\u7740\u51b7\u5bc2\u5e7d\u6697\u7684\u7a97\u5916\uff0c\u8bf4\u9053\uff1a\u201c\u773c\u4e0b\u4f3c\u4e4e\u53ea\u80fd\u5bc4\u671b\u4ed6\u4eec\u81ea\u5df1\u72af\u9519\u8bef\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u5bf9\u6211\u4eec\u56fd\u5bb6\u7684\u4f53\u7cfb\u4e86\u89e3\u8fd8\u4e0d\u591f\u900f\u5f7b\uff0c\u201d\u5468\u747e\u73ba\u8f6c\u8fc7\u8eab\u6765\uff0c\u6de1\u6de1\u7684\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6ca1\u6709\u90a3\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\u3002\u6709\u4e9b\u4e8b\u60c5\uff0c\u6216\u8bb8\u6211\u4eec\u4f1a\u611f\u5230\u529b\u6709\u672a\u902e\uff0c\u4f46\u662f\u9526\u6e56\u5728\u4f53\u5236\u5185\u4f1a\u9047\u5230\u7684\u969c\u788d\u8981\u6bd4\u4e00\u822c\u4eba\u60f3\u8c61\u7684\u8981\u591a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8c22\u5251\u5357\u81ea\u77e5\u5bf9\u4e8e\u4ea7\u4e1a\u9762\u7684\u8ba4\u8bc6\u7684\u786e\u8c08\u4e0d\u4e0a\u5f88\u6df1\u523b\u3002</p><p>\u3000\u3000\u201c\u8fc7\u4e86\u4eca\u591c\u5c31\u662f\u4e5d\u516b\u5e74\u4e86\uff0c\u7b49\u8d75\u6d4e\u4e1c\u771f\u6b63\u6267\u638c\u56fd\u52a1\u9662\u4e4b\u540e\uff0c\u4e00\u5b9a\u4f1a\u5927\u529b\u63a8\u52a8\u56fd\u52a1\u9662\u673a\u6784\u6539\u5236\u3002\u73b0\u5728\u5df2\u7ecf\u6709\u4e9b\u6d88\u606f\u4f20\u51fa\u6765\u4e86\uff0c\u90ae\u7535\u90e8\u53ef\u80fd\u4f1a\u7ed9\u5206\u62c6\uff0c\u90ae\u7535\u90e8\u7684\u7535\u4fe1\u4e1a\u52a1\u5c06\u4e0e\u7535\u5b50\u5de5\u4e1a\u90e8\u5408\u5e76\uff0c\u8fd9\u4f3c\u4e4e\u4e5f\u662f\u5927\u52bf\u6240\u8d8b\uff0c\u201d\u5468\u747e\u73ba\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u4eca\u5e74\u80fd\u6e38\u5203\u6709\u4f59\u505a\u51fa\u8fd9\u4e48\u591a\u7684\u4e8b\u60c5\uff0c\u662f\u4ed6\u4eec\u94bb\u4e86\u90ae\u7535\u90e8\u4e0e\u7535\u5b50\u5de5\u4e1a\u90e8\u641e\u5bf9\u7acb\u7684\u7a7a\u5b50\u3002\u4e00\u65e6\u56fd\u52a1\u9662\u673a\u6784\u6539\u5236\uff0c\u4e24\u4e2a\u77db\u76fe\u91cd\u91cd\u7684\u90e8\u59d4\u7ed9\u5f3a\u884c\u5408\u5e76\u5230\u4e00\u4e2a\u5c71\u5934\u4e4b\u4e0b\uff0c\u4f60\u8bf4\u8fd9\u4e2a\u5c71\u5934\u4f1a\u5e0c\u671b\u770b\u5230\u4e00\u5bb6\u6c11\u8425\u4f01\u4e1a\u5f3a\u5927\u5230\u5f71\u54cd\u4ed6\u4eec\u5bf9\u6574\u4e2a\u56fd\u5185\u4fe1\u606f\u4ea7\u4e1a\u7684\u63a7\u5236\uff1f\u9526\u6e56\u773c\u4e0b\u7684\u683c\u5c40\u662f\u5f88\u5927\uff0c\u4f46\u662f\u4ed6\u4eec\u7684\u683c\u5c40\u8d8a\u5927\uff0c\u53ef\u80fd\u5c31\u8d8a\u4e0d\u8ba8\u67d0\u4e9b\u4eba\u7684\u559c\u6b22\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u747e\u73ba\u591a\u5c11\u611f\u5230\u4e9b\u843d\u5bde\uff0c\u4e0d\u7ba1\u600e\u4e48\u6765\u8bf4\uff0c\u4ed6\u9762\u5bf9\u4eca\u65e5\u7684\u9526\u6e56\uff0c\u603b\u6709\u4e9b\u529b\u4e0d\u4ece\u5fc3\u3001\u529b\u6709\u672a\u902e\u7684\u611f\u89c9\uff0c\u5c40\u9762\u4f3c\u4e4e\u65e9\u5c31\u4e0d\u5728\u81ea\u5df1\u7684\u638c\u63e1\u4e4b\u4e2d\u4e86\uff0c\u8fd9\u611f\u89c9\u8fd8\u771f\u4e0d\u53eb\u4eba\u597d\u53d7\u3002</p><p>\u3000\u3000\u6d89\u53ca\u5230\u6574\u4e2a\u56fd\u5bb6\u4ea7\u4e1a\u4f53\u5236\uff0c\u9526\u6e56\u6240\u9762\u4e34\u7684\u56f0\u5883\u4e0e\u969c\u788d\uff0c\u5374\u5df2\u7ecf\u4e0d\u662f\u79d1\u738b\u80fd\u53c2\u4e0e\u8fdb\u53bb\u7684\u6e38\u620f\uff0c\u8c22\u5251\u5357\u795e\u8272\u6709\u4e9b\u9eef\u7136\uff0c\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u90fd\u53c2\u52a0\u4eca\u5929\u7684\u9152\u4f1a\u4e86\uff0c\u4ed6\u4eec\u5bf9\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u5f88\u7528\u5fc3\uff0c\u8a00\u8bed\u95f4\u4e5f\u671f\u5f85\u4e09\u661f\u80fd\u505a\u4e9b\u4ec0\u4e48\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u4e0a\u6b21\u8ddf\u4f60\u5728\u7535\u8bdd\u8bf4\u8fc7\uff0c\u6ca1\u6709\u8be6\u7ec6\u7684\u8bf4\u3002\u6d77\u7c9f\u79d1\u6280\u7684\u521b\u529e\u4eba\u738b\u6d77\u7c9f\u5728\u4e1c\u5927\u8bfb\u5b8c\u535a\u58eb\u540e\u7559\u6821\u4efb\u6559\u4e86\u4e24\u5e74\u65f6\u95f4\u624d\u8f9e\u804c\u521b\u529e\u4e86\u516c\u53f8\uff0c\u8fd8\u4ee5\u4e3a\u8fd9\u6837\u7684\u89d2\u8272\u8981\u78e8\u5386\u4e00\u6bb5\u65f6\u95f4\u624d\u53ef\u80fd\u6210\u624d\uff0c\u6ca1\u60f3\u5230\u6084\u7136\u58f0\u606f\u7684\u5c31\u641e\u51fa\u8fd9\u6837\u7684\u52a8\u9759\uff0c\u8fd8\u771f\u662f\u8ba9\u4eba\u770b\u8d70\u4e86\u773c\u3002\u201d\u5468\u747e\u73ba\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4eca\u5929\u5728\u9152\u4f1a\u4e0a\u9047\u5230\u80e1\u5b97\u5e86\uff0c\u5012\u662f\u542c\u4ed6\u63d0\u8d77\u738b\u6d77\u7c9f\u8fd9\u4e2a\u4eba\uff0c\u4ed6\u5bf9\u738b\u6d77\u7c9f\u8fd9\u4eba\u6bd4\u8f83\u770b\u91cd\u2026\u2026\u201d\u8c22\u5251\u5357\u60f3\u8d77\u4eca\u665a\u9152\u4f1a\u4e0a\u80e1\u5b97\u5e86\u7a0d\u663e\u5f97\u6709\u4e9b\u6bb7\u52e4\u7684\u59ff\u6001\uff0c\u6709\u4e9b\u7591\u60d1\u4e0d\u89e3\uff0c\u201c\u4ee5\u524d\u90fd\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u5bb6\u516c\u53f8\uff0c\u600e\u4e48\u5c31\u7a81\u7136\u5192\u51fa\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6d77\u7c9f\u79d1\u6280\u6709\u80fd\u529b\u8054\u5408\u4e86\u4e2d\u79d1\u9662\u8f6f\u4ef6\u7814\u7a76\u6240\u4e0e\u7701\u79d1\u6280\u5385\u4e0b\u5c5e\u7684\u91d1\u9f0e\u79d1\u6280\u96c6\u56e2\u4e00\u8d77\u63a8\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\uff0c\u4e00\u5b9a\u6709\u4eba\u5728\u5e55\u540e\u63a8\u52a8\uff0c\u76f4\u5230\u80e1\u5b97\u5e86\u524d\u4e9b\u5929\u627e\u5230\u6211\uff0c\u6211\u624d\u6653\u5f97\u539f\u6765\u662f\u56fd\u5bb6\u8ba1\u59d4\u526f\u4e3b\u4efb\u845b\u5efa\u5fb7\u5728\u5e55\u540e\u7b56\u5212\uff0c\u7cbe\u5178\u5730\u4ea7\u5bf9\u6d77\u7c9f\u6d77\u6280\u63a7\u80a1\u2026\u2026\u201d\u5468\u747e\u73ba\u8bf4\u9053\uff0c\u201c\u5177\u4f53\u600e\u4e48\u56de\u4e8b\uff0c\u4e5f\u4e0d\u5927\u6e05\u695a\uff0c\u771f\u8981\u85cf\u7740\u4ec0\u4e48\u732b\u817b\uff0c\u53c8\u600e\u4e48\u53ef\u80fd\u8ba9\u4e8b\u5916\u4eba\u77e5\u9053\uff1f\u867d\u7136\u4f60\u4eec\u8c22\u5bb6\u4e0e\u7cbe\u5178\u5730\u4ea7\u5728\u6d77\u5dde\u6709\u7740\u5bc6\u5207\u7684\u5408\u4f5c\uff0c\u4f46\u662f\u8fd9\u4e9b\u4e0d\u4e3a\u4eba\u77e5\u7684\u79d8\u5bc6\uff0c\u60f3\u5fc5\u4e5f\u4e0d\u53ef\u80fd\u8ba9\u90a3\u4e2a\u5973\u4eba\u4e3b\u52a8\u5410\u9732\u51fa\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u80e1\u5b97\u5e86\u627e\u4f60\u505a\u4ec0\u4e48\uff1f\u201d\u8c22\u5251\u5357\u5230\u5efa\u90ba\u539f\u6765\u6ca1\u6709\u6253\u7b97\u4e0e\u6797\u96ea\u89c1\u9762\uff0c\u603b\u89c9\u5f97\u90a3\u4e2a\u5973\u4eba\u7684\u773c\u795e\u6709\u7740\u5543\u566c\u4eba\u7684\u6b32\u671b\u5728\u91cc\u5934\uff0c\u4e0e\u5979\u5996\u8273\u7684\u5916\u8868\u5f88\u4e0d\u8c10\u8c03\u3002</p><p>\u3000\u3000\u201c\u8096\u660e\u5efa\u4e0e\u80e1\u5b97\u5e86\u652f\u6301\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\uff0c\u7f57\u541b\u7684\u6001\u5ea6\u6a21\u68f1\u4e24\u53ef\u2014\u2014\u5bf9\u4e0d\u662f\u4ed6\u4eb2\u81ea\u63a8\u52a8\u7684\u9879\u76ee\uff0c\u7f57\u541b\u7684\u6001\u5ea6\u603b\u662f\u6a21\u68f1\u4e24\u53ef\uff0c\u5e72\u6210\u4e86\uff0c\u6709\u4ed6\u4e00\u4efd\u529f\u52b3\uff0c\u5e72\u7838\u4e86\uff0c\u4e5f\u4e0e\u4ed6\u6ca1\u4ec0\u4e48\u5e72\u7cfb\uff1b\u738b\u7ef4\u5747\u7684\u6001\u5ea6\u5c31\u6709\u4e9b\u6d88\u6781\uff0c\u5e0c\u671b\u80fd\u518d\u7814\u7a76\u4e00\u4e0b\u3002\u738b\u7ef4\u5747\u5728\u5e02\u91cc\u7684\u5730\u4f4d\u4f4e\uff0c\u6bd5\u7adf\u5728\u9ad8\u65b0\u533a\u5927\u6743\u5728\u63e1\uff0c\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u59cb\u7ec8\u7ed5\u4e0d\u8fc7\u738b\u7ef4\u5747\uff0c\u7279\u522b\u662f\u738b\u7ef4\u5747\u5982\u6b64\u7684\u6001\u5ea6\uff0c\u8ba9\u5176\u4ed6\u5e38\u59d4\u4e5f\u591a\u5c11\u6709\u4e9b\u8fdf\u7591\uff0c\u4e0d\u5927\u80af\u8868\u6001\uff0c\u8fd9\u5927\u6982\u662f\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u4ed6\u4eec\u4eca\u5929\u5bf9\u4e09\u661f\u6bd4\u8f83\u70ed\u5207\u7684\u539f\u56e0\u5427\uff0c\u4e09\u661f\u5728\u5efa\u90ba\u6295\u8d44\u7684\u7814\u7a76\u4e2d\u5fc3\u4e5f\u5305\u62ec\u8f6f\u4ef6\u4ea7\u54c1\u7684\u5f00\u53d1\u5427\uff1f\u201d\u5468\u747e\u73ba\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u65f6\u5019\uff0c\u6211\u4eec\u8fd9\u4e9b\u5e9f\u4eba\u603b\u7b97\u80fd\u6d3e\u4e0a\u4e9b\u7528\u573a\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u660e\u767d\u4e86\uff0c\u5728\u6001\u52bf\u66a7\u6627\u4e0d\u660e\u65f6\uff0c\u5b66\u672f\u754c\u7684\u58f0\u97f3\u5c31\u53ef\u4ee5\u7528\u6765\u9020\u52bf\u3002\u4ed6\u95ee\u9053\uff1a\u201c\u5927\u8205\u4f60\u662f\u600e\u4e48\u770b\u5f85\u8fd9\u4e2a\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u6709\u4e9b\u5927\u800c\u4e0d\u5f53\uff0c\u201d\u5468\u747e\u73ba\u5728\u5916\u7525\u9762\u524d\u6ca1\u6709\u4ec0\u4e48\u597d\u8bb3\u8a00\u7684\uff0c\u53c8\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u4f46\u662f\uff0c\u89c4\u6a21\u5c0f\u4e86\uff0c\u4e0e\u9526\u6e56\u7684\u6a61\u6811\u56ed\u8ba1\u5212\u76f8\u6bd4\u90fd\u6ca1\u6709\u4ec0\u4e48\u503c\u5f97\u79f0\u9053\u7684\u5730\u65b9\uff0c\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u8d39\u90a3\u4e48\u5927\u52b2\u8df3\u51fa\u6765\u8e66\u8fbe\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u4e00\u5207\u4e0d\u8fc7\u662f\u597d\u5927\u559c\u529f\u7f62\u4e86\uff0c\u8c22\u5251\u5357\u5fc3\u91cc\u60f3\u7740\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u53c8\u8bf4\u9053\uff1a\u201c\u6587\u7ae0\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u5e2e\u4ed6\u4eec\u5199\uff0c\u8d44\u6599\u8fd8\u5728\u6211\u8fd9\u91cc\uff0c\u4f60\u6709\u5174\u8da3\u665a\u4e0a\u53ef\u4ee5\u62bd\u7a7a\u770b\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5fb7\u5c14\u6cd5\u897f\u7ad9\u5728\u5e0c\u5c14\u987f\u5927\u9152\u5e97\u4e8c\u5341\u516b\u5c42\u7684\u8c6a\u534e\u5957\u95f4\u4e66\u623f\u91cc\uff0c\u8fd9\u662f\u4ed6\u5728\u4e2d\u56fd\u4e34\u65f6\u7684\u5de5\u4f5c\u95f4\u3002</p><p>\u3000\u3000\u900f\u660e\u7684\u843d\u5730\u7a97\u5916\u5c31\u662f\u8fd9\u5ea7\u57ce\u5e02\u6b23\u6b23\u5411\u8363\u7684\u591c\u665a\uff0c\u7480\u74a8\u82e5\u7e41\u661f\u7684\u8857\u706f\u50cf\u5149\u5e26\u4e00\u4e9b\u6a2a\u5367\u5728\u697c\u524d\uff0c\u4e2d\u95f4\u8fd8\u7f00\u9970\u7740\u706f\u7ea2\u9152\u7eff\u7684\u5962\u9761\u6c14\u606f\u3002</p><p>\u3000\u3000\u5fb7\u5c14\u6cd5\u897f\u62ff\u7740\u7ea2\u9152\u676f\uff0c\u671d\u7740\u4ed6\u5728\u4e2d\u56fd\u7684\u540c\u4e8b\u5468\u6b63\u9752\u4e3e\u8d77\u6765\uff0c\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7b2c\u4e00\u6b21\u5230\u4e2d\u56fd\u6765\uff0c\u4ed6\u5988\u7684\u611f\u89c9\u8fc7\u6765\u665a\u4e86\uff0c\u6ca1\u60f3\u5230\u8fd9\u4e2a\u56fd\u5bb6\u7adf\u7136\u5728\u8fd9\u4e2a\u9886\u57df\u8fd8\u80fd\u6709\u7ed9\u4eba\u60ca\u559c\u7684\u4e1c\u897f\u2026\u2026\u201d</p><p>\u3000\u3000\u5fb7\u4eea\u4e2d\u56fd\u533a\u603b\u88c1\u5468\u6b63\u9752\u5fae\u5fae\u4e00\u7b11\uff0c\u4e0d\u6653\u5f97\u5fb7\u5c14\u6cd5\u897f\u7684\u6c49\u8bed\u5728\u54ea\u91cc\u3001\u8ddf\u8c01\u5b66\u7684\uff0c\u56fd\u9a82\u7684\u54ac\u5b57\u5c24\u5176\u5f97\u5176\u4e2d\u7684\u4e09\u5473\u3002</p><p>\u3000\u3000\u9526\u6e56\u63d0\u524d\u4e00\u4e2a\u6708\u5c31\u63d0\u4f9bMP3\u786c\u4ef6\u64ad\u653e\u5668\u7684\u4e00\u4e9b\u6280\u672f\u8d44\u6599\uff0c\u4f46\u662f\u76f4\u5230\u5fb7\u5c14\u6cd5\u897f\u5230\u4e2d\u56fd\u4e4b\u540e\uff0c\u624d\u63d0\u4f9b\u6837\u673a\u7ed9\u4ed6\u4eec\u3002\u6bcf\u5929\u7528\u8fc7\u665a\u9910\u4e4b\u540e\u8fd8\u8981\u56de\u623f\u95f4\u62c6\u89e3\u9526\u6e56\u7684\u6837\u673a\uff0c\u4e0d\u662f\u4ec0\u4e48\u503c\u5f97\u70ab\u8000\u7684\u597d\u5dee\u4e8b\u3002\u5750\u4e0a\u7ba1\u7406\u5c97\u4f4d\u4e4b\u540e\uff0c\u624b\u91cc\u7684\u6280\u672f\u6d3b\u672a\u514d\u5c31\u653e\u677e\u6389\u4e86\uff0c\u8bf4\u5b9e\u8bdd\u8fd8\u4e0d\u5982\u4ece\u5317\u4eac\u76f4\u63a5\u8c03\u4e24\u540d\u5de5\u7a0b\u5e08\u5e72\u8fd9\u6d3b\u5408\u9002\uff0c\u53c8\u4e0d\u80fd\u5728\u5fb7\u5c14\u6cd5\u897f\u9762\u524d\u6d41\u9732\u8fd9\u79cd\u5ff5\u5934\uff0c\u53ea\u6709\u786c\u7740\u5934\u76ae\u4e0e\u5fb7\u5c14\u6cd5\u897f\u8fd8\u6709\u5fb7\u5c14\u6cd5\u897f\u4ece\u603b\u90e8\u5e26\u8fc7\u6765\u7684\u6280\u672f\u4eba\u5458\u4e00\u8d77\u62c6\u89e3\u5206\u6790\u6837\u673a\u6280\u672f\u3002</p><p>\u3000\u3000\u5728\u7d2b\u6a80\u6728\u7684\u5927\u4e66\u684c\u4e0a\uff0c\u96f6\u96f6\u6563\u6563\u7684\u6446\u4e86\u8bb8\u591a\u7535\u5b50\u5143\u5668\u4ef6\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u5411\u6211\u4eec\u8d2d\u5356\u6570\u5b57\u624b\u673a\u6280\u672f\u7684\u4f59\u6b3e\u8fd8\u5dee\u4e00\u534a\u624d\u4ed8\u6e05\u5462\uff0c\u4ed6\u4eec\u4eca\u5e74\u5374\u5f80ESS\u6295\u5165\u8fd1\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\uff0c\u5b89\u5409\u4f2f\u90a3\u5bb6\u4f19\u65e9\u5c31\u8bf4\u9526\u6e56\u662f\u4e00\u5bb6\u91ce\u5fc3\u52c3\u52c3\u7684\u4f01\u4e1a\uff0c\u5b89\u5409\u4f2f\u8fd9\u5bb6\u4f19\u770b\u95ee\u9898\u4e00\u5411\u5f88\u51c6\uff0c\u771f\u4ed6\u5988\u7684\u7cdf\u7cd5\uff0c\u8fd9\u6b21\u53c8\u8ba9\u4ed6\u8bf4\u4e2d\u4e86\uff0c\u201d\u5fb7\u5c14\u6cd5\u897f\u50cf\u6301\u7ea2\u9152\u676f\u90a3\u6837\u7684\u62ff\u8d77\u684c\u4e0a\u7684\u4e00\u679a\u7535\u5b50\u5143\u4ef6\u51d1\u5230\u773c\u524d\u4f3c\u4e4e\u5728\u6b23\u8d4f\u4e00\u4ef6\u827a\u672f\u54c1\uff0c\u201c\u5b8c\u7f8e\u7684\u53cc\u82af\u7247\u786c\u4ef6\u67b6\u6784\uff0c\u5c31\u7b97\u6211\u624b\u4e0b\u90a3\u73ed\u5154\u5d3d\u5b50\u6765\u5e72\uff0c\u4e5f\u4e0d\u4f1a\u6bd4\u8fd9\u66f4\u5b8c\u7f8e\u3002\u4e0d\u5f97\u4e0d\u627f\u8ba4\uff0c\u9526\u6e56\u603b\u662f\u7ed9\u4eba\u610f\u5916\u7684\u60ca\u559c\u3002\u201d</p><p>\u3000\u3000\u65c1\u4eba\u5f88\u96be\u7406\u89e3\u5fb7\u5c14\u6cd5\u897f\u4e3a\u4ec0\u4e48\u4f1a\u5bf9\u4e00\u679a\u7535\u8def\u677f\u4f1a\u6709\u5982\u6b64\u75f4\u8ff7\u7684\u773c\u795e\uff0c\u6280\u672f\u51fa\u8eab\u7684\u4eba\u603b\u662f\u80fd\u53d1\u73b0\u4e00\u822c\u4eba\u65e0\u6cd5\u53d1\u73b0\u7684\u5355\u7eaf\u7684\u7f8e\u3002</p><p>\u3000\u3000\u5355\u7eaf\u4ece\u5916\u89c2\u6765\u770b\uff0c\u684c\u4e0a\u8fd8\u6709\u4e00\u53ea\u6ca1\u6709\u7ed9\u62c6\u89e3\u7684\u64ad\u653e\u5668\u4f3c\u4e4e\u66f4\u7b26\u5408\u4eba\u7684\u5ba1\u7f8e\u89c2\u70b9\uff0c\u5973\u4eba\u5a07\u5c0f\u624b\u638c\u5927\u5c0f\u7684\u4e73\u767d\u8272\u957f\u65b9\u5f62\u673a\u58f3\uff0c\u84dd\u8272\u80cc\u5149\u7684\u663e\u793a\u5c4f\u5927\u7ea6\u5360\u5230\u56db\u5206\u4e4b\u4e00\u7684\u6b63\u9762\uff0c\u4e94\u679a\u6761\u5f62\u51f8\u51fa\u7684\u529f\u80fd\u6309\u952e\u5448\u73af\u5f62\u5206\u5e03\u3002\u9526\u6e56\u4ece\u7231\u8fbei19\u624b\u673a\u5f00\u59cb\u5728\u5de5\u827a\u8bbe\u8ba1\u65b9\u9762\u5c31\u7ed9\u4eba\u60ca\u8273\u7684\u611f\u89c9\uff0c\u8fd9\u6b3eMP3\u786c\u4ef6\u64ad\u653e\u5668\u7684\u5916\u89c2\u8bbe\u8ba1\u4e00\u6837\u7684\u7b80\u7ea6\u800c\u8fd1\u4f3c\u5b8c\u7f8e\uff0c\u5468\u6b63\u9752\u62ff\u540c\u6837\u8272\u7cfb\u7684\u8033\u673a\u585e\u5230\u8033\u6735\u91cc\uff0c\u582a\u4e0eCD\u5ab2\u7f8e\u7684\u97f3\u8d28\uff0c\u8fd9\u8bf4\u660e\u9526\u6e56\u5728\u97f3\u9891\u89e3\u7801\u65b9\u9762\u7684\u6280\u672f\u5df2\u7ecf\u8fbe\u5230\u4e00\u4e2a\u76f8\u5f53\u9ad8\u7684\u6c34\u51c6\u3002\u9648\u4fe1\u751f\u9009\u62e9\u53bb\u9526\u6e56\uff0c\u770b\u6765\u5e76\u4e0d\u662f\u4e00\u4e2a\u9519\u8bef\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u5c31\u662f\u8fd9\u6837\uff0c\u4f60\u624b\u91cc\u62ff\u7740\u8fd9\u4e2a\uff0c\u8fd8\u5dee\u4e00\u53ea\u659f\u6ee1\u7ea2\u9152\u7684\u73bb\u7483\u676f\uff0c\u201d\u5fb7\u5c14\u6cd5\u897f\u5c06\u624b\u91cc\u7684\u9ad8\u811a\u676f\u585e\u5230\u5468\u6b63\u9752\u624b\u91cc\uff0c\u62c9\u7740\u5468\u6b63\u9752\u7ad9\u8d77\u6765\uff0c\u201c\u4f60\u5e94\u8be5\u8ddf\u7740\u97f3\u4e50\u8f7b\u8f7b\u6447\u6446\u2026\u2026\u201d\u53c8\u76b1\u7740\u7709\u5934\u770b\u7740\u5468\u6b63\u9752\uff0c\u4f3c\u4e4e\u6709\u4e9b\u5730\u65b9\u4e0d\u5bf9\u52b2\uff0c\u201c\u4f60\u7ed9\u4eba\u7684\u5370\u8c61\u592a\u53e4\u677f\u4e86\uff0c\u6211\u8981\u662f\u90a3\u4e2a\u5c0f\u5b50\uff0c\u4e00\u5b9a\u4e0d\u5e0c\u671b\u4f60\u6765\u7ed9\u8fd9\u6b3e\u5bf9\u97f3\u54cd\u5e02\u573a\u6709\u7740\u5212\u65f6\u4ee3\u610f\u4e49\u7684\u7535\u5b50\u4ea7\u54c1\u4ee3\u8a00\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u53c8\u6ca1\u6709\u8fd9\u60f3\u6cd5\u2026\u2026\u201d\u5468\u6b63\u9752\u62b1\u6028\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5fb7\u5c14\u6cd5\u897f\u5bf9\u5468\u6b63\u9752\u7684\u62b1\u6028\u89c6\u82e5\u65e0\u7779\uff0c\u5c06MP3\u786c\u4ef6\u64ad\u653e\u5668\u4e0e\u7ea2\u9152\u676f\u4ece\u4ed6\u624b\u91cc\u593a\u8fc7\u6765\uff0c\u671d\u7740\u5750\u5728\u4e00\u65c1\u5a07\u5c0f\u7f8e\u4e3d\u7684\u79d8\u4e66\u8bf4\u9053\uff1a\u201c\u4f60\u8fc7\u6765\u5e2e\u5e2e\u5fd9\uff0c\u5c06\u5934\u53d1\u5f04\u5f97\u518d\u51cc\u4e71\u4e00\u4e9b\uff0c\u8981\u6709\u70b9\u8857\u5934\u5c11\u5973\u7684\u611f\u89c9\u2026\u2026\u8ddf\u7740\u97f3\u4e50\u6447\u6446\u8d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u8fd9\u4e48\u4e00\u6b3e\u53ef\u4ee5\u76f4\u63a5\u4ece\u8ba1\u7b97\u673a\u4e0b\u8f7d\u97f3\u4e50\u6765\u542c\u7684\u7535\u5b50\u4ea7\u54c1\uff0c\u5c31\u5e94\u8be5\u662f\u8fd9\u79cd\u5f62\u8c61\u95ee\u4e16\uff0c\u201d\u5fb7\u5c14\u6cd5\u897f\u53c8\u56de\u8fc7\u5934\u6765\u95ee\u5468\u6b63\u9752\uff0c\u201c\u6211\u662f\u4e0d\u662f\u8be5\u4e3b\u52a8\u8ddf\u9526\u6e56\u8981\u4e00\u4e9b\u5e7f\u544a\u54a8\u8be2\u8d39\uff1f\u201d</p><p>\u3000\u3000\u5468\u6b63\u9752\u65e0\u5948\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u8ddf\u5fb7\u5c14\u6cd5\u897f\u8bf4\u9053\uff1a\u201c\u8981\u662f\u5f20\u606a\u6216\u8005\u9648\u4fe1\u751f\u5728\u8fd9\u91cc\uff0c\u4f60\u8fd8\u8fd9\u4e48\u5174\u594b\uff0c\u5bf9\u6211\u4eec\u7684\u8c08\u5224\u53ef\u662f\u5f88\u4e0d\u5229\u554a\u2026\u2026\u201d\u4ee5\u524d\u8fd8\u771f\u662f\u6ca1\u6709\u89c1\u5230\u5fb7\u5c14\u6cd5\u897f\u5174\u594b\u5f97\u5fd8\u5f62\u7684\u65f6\u5019\uff0c\u5fb7\u5c14\u6cd5\u897f\u7ed9\u4eba\u7684\u5370\u8c61\u603b\u662f\u8fc7\u4e8e\u4e00\u672c\u6b63\u7ecf\u3002\u9526\u6e56\u7684\u8fd9\u4e2a\u73a9\u827a\u513f\u771f\u662f\u51fb\u4e2d\u4ed6\u7684\u547d\u95e8\u4e86\uff0c\u624d\u8ba9\u4ed6\u7a0d\u6709\u4e9b\u5931\u5e38\u3002\u6362\u4f5c\u65af\u9ad8\u67cf\u7684\u827e\u9ed8\u5982\u6b64\u8868\u73b0\u5c31\u518d\u6b63\u5e38\u4e0d\u8fc7\uff0c\u4e0d\u6653\u5f97\u9648\u4fe1\u751f\u4e0e\u5fb7\u5c14\u6cd5\u897f\u5728\u5fb7\u4eea\u603b\u88c1\u5171\u4e8b\u7684\u90a3\u6bb5\u7b80\u77ed\u65f6\u95f4\uff0c\u6709\u6ca1\u6709\u673a\u4f1a\u770b\u5230\u5fb7\u5c14\u6cd5\u897f\u6b64\u65f6\u7684\u53e6\u4e00\u9762\u3002</p><p>\u3000\u3000\u201c\u4e0d\u8981\u8fd9\u4e48\u4e00\u672c\u6b63\u7ecf\uff0c\u9648\u4fe1\u751f\u5e74\u521d\u9009\u62e9\u79bb\u5f00\u5fb7\u4eea\uff0c\u591a\u5c11\u4eba\u5e0c\u671b\u770b\u5230\u4ed6\u4f1a\u56e0\u6b64\u800c\u611f\u89c9\u540e\u6094\u83ab\u53ca\uff0c\u6211\u4e0d\u5426\u8ba4\u6211\u4e5f\u6709\u8fd9\u6837\u7684\u60f3\u6cd5\uff0c\u4e8b\u5b9e\u8bc1\u660e\u9648\u4fe1\u751f\u7684\u773c\u5149\u5e76\u4e0d\u5dee\uff0c\u201d\u5fb7\u5c14\u6cd5\u897f\u4f3c\u4e4e\u4e00\u70b9\u90fd\u4e0d\u60f3\u63a9\u9970\u81ea\u5df1\u7684\u5fc3\u5f7b\u5e95\u7684\u7ed9\u9526\u6e56\u5f00\u53d1\u51fa\u6765\u7684MP3\u786c\u4ef6\u64ad\u653e\u5668\u4fd8\u83b7\u53bb\u4e86\uff0c\u795e\u60c5\u5174\u594b\u7684\u8bf4\u9053\uff0c\u201c\u4e0d\u5426\u8ba4\u8fd9\u662f\u4e00\u6b3e\u975e\u5e38\u4f1f\u5927\u7684\u7535\u5b50\u4ea7\u54c1\uff0c\u4f46\u662f\u4e00\u5bb6\u5728\u6280\u672f\u4e0a\u5f88\u4e0d\u53d7\u91cd\u89c6\u7684\u516c\u53f8\u80fd\u6709\u5982\u6b64\u6770\u4f5c\uff0c\u8fd9\u672c\u8eab\u4e0d\u662f\u66f4\u7cbe\u5f69\u5417\uff1f\u597d\u5427\uff0c\u6211\u4eec\u6765\u597d\u597d\u7684\u60f3\u4e00\u60f3\uff0c\u6211\u4eec\u6709\u4ec0\u4e48\u4e1c\u897f\u662f\u9526\u6e56\u8feb\u5207\u5e0c\u671b\u5f97\u5230\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u89c1\u5fb7\u5c14\u6cd5\u897f\u90fd\u6709\u4e9b\u8feb\u4e0d\u53ca\u5f85\u7684\u6837\u5b50\uff0c\u5468\u6b63\u9752\u81ea\u7136\u4e0d\u4f1a\u626b\u4ed6\u7684\u5174\uff0c\u8fd9\u53ea\u4f1a\u8ba9\u9526\u6e56\u5728\u8c08\u5224\u4e2d\u5360\u636e\u66f4\u6709\u5229\u7684\u5730\u4f4d\uff0c\u4f46\u662f\u6bd4\u8d77\u80fd\u5171\u540c\u53c2\u4e0e\u4e00\u6b3e\u4f1f\u5927\u7684\u7535\u5b50\u4ea7\u54c1\u7684\u95ee\u4e16\uff0c\u5728\u8c08\u5224\u4e2d\u7a0d\u5fae\u5403\u70b9\u4e8f\uff0c\u4f3c\u4e4e\u5e76\u4e0d\u662f\u4ec0\u4e48\u9700\u8981\u4e00\u76f4\u6302\u5728\u5fc3\u4e0a\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e0e\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u8096\u664b\u6210\u7b49\u4eba\u8fd9\u4e9b\u5929\u5728\u5efa\u90ba\u4e3a\u4e86\u8c08\u5224\u65b9\u9762\uff0c\u4e5f\u4e00\u5e76\u4f4f\u8fdb\u5e0c\u5c14\u987f\u5927\u9152\u5e97\uff0c\u5c31\u4f4f\u5728\u5fb7\u5c14\u6cd5\u897f\u4e00\u884c\u4eba\u7684\u697c\u4e0b\uff0c\u4ed6\u4eec\u81ea\u7136\u4e0d\u6653\u5f97\u5fb7\u5c14\u6cd5\u897f\u5728\u697c\u4e0a\u5174\u594b\u7684\u53cd\u5e94\u3002</p><p>\u3000\u3000\u4e01\u69d0\u8fd8\u6709\u4e9b\u62c5\u5fe7\u7684\u8bf4\u9053\uff1a\u201c\u4e00\u679a\u591a\u5a92\u4f53\u5e94\u7528\u9886\u57df\u7684\u4e13\u7528\u578b\u5fae\u5904\u7406\u5668\u5728\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u4e1a\u7684\u5e94\u7528\u524d\u666f\u5e7f\u9614\uff0c\u4e0d\u6653\u5f97\u5fb7\u4eea\u8fd9\u6b21\u4f1a\u4e0d\u4f1a\u8f7b\u6613\u677e\u53e3\uff1f\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u66f4\u81ea\u4fe1\u4e00\u4e9b\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8981\u6c42\u5e76\u4e0d\u9ad8\uff0c\u5728\u591a\u5a92\u4f53\u5e94\u7528\u9886\u57df\u7684\u4e13\u7528\u578b\u5fae\u5904\u7406\u5668\u76ee\u524d\u8fd8\u6ca1\u6709\u53d1\u5c55\u5230\u975e\u5e38\u4e13\u4e1a\u7684\u5730\u6b65\uff0c\u6280\u672f\u6765\u6e90\u4e5f\u4e0d\u662f\u4ec5\u4ec5\u5c31\u5fb7\u4eea\u4e00\u5bb6\uff0c\u6211\u4eec\u751a\u81f3\u8fd8\u8981\u5148\u4eceARM\u516c\u53f8\u5148\u83b7\u5f97ARM\u5fae\u5904\u7406\u5668\u57fa\u672c\u67b6\u6784\u7684\u6388\u6743\uff0c\u624d\u80fd\u6784\u6210\u5b8c\u6574\u7684\u6280\u672f\u6388\u6743\u2026\u2026\u6bd4\u8d77\u672a\u6765MP3\u64ad\u653e\u5668\u5e9e\u5927\u7684\u786c\u4ef6\u5e02\u573a\uff0c\u5fb7\u4eea\u5176\u5b9e\u4ed8\u51fa\u7684\u5e76\u4e0d\u591a\u3002\u4f46\u662f\u9664\u4e86\u5fb7\u4eea\uff0c\u6211\u4eec\u8fd9\u65f6\u5019\u5728\u786c\u4ef6\u67b6\u6784\u4e0a\u627e\u4e0d\u5230\u66f4\u5408\u9002\u7684\u5408\u4f5c\u5bf9\u8c61\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6216\u8bb8\u518d\u6709\u4e09\u4e94\u5e74\u7684\u65f6\u95f4\uff0c\u6211\u4eec\u53ef\u4ee5\u5bf9\u5fb7\u4eea\u8981\u6c42\u66f4\u591a\uff0c\u201d\u8096\u664b\u6210\u8bf4\u9053\uff0c\u201c\u53ef\u60dc\u7b49\u4e0d\u4e86\u4e09\u4e94\u5e74\u7684\u65f6\u95f4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u7b97\u7b49\u4e09\u4e94\u5e74\uff0c\u5fb7\u4eea\u4e5f\u662f\u4e0d\u591a\u7684\u9009\u62e9\u5bf9\u8c61\u4e4b\u4e00\uff0c\u8c08\u5224\u5c31\u662f\u8fd9\u6837\uff0c\u5927\u5bb6\u90fd\u4f1a\u89c9\u5f97\u5403\u4e8f\uff0c\u504f\u504f\u606a\u5c11\u770b\u5f97\u5f00\uff0c\u653e\u7740\u5fb7\u5c14\u6cd5\u897f\u4e0d\u7ba1\uff0c\u4e0e\u827e\u9ed8\u51fa\u53bb\u559d\u82b1\u9152\u4e86\u3002\u201d\u82cf\u6d25\u4e1c\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u8c01\u90fd\u6653\u5f97\u6b64\u65f6\u7684\u65af\u9ad8\u67cf\u5bf9\u9526\u6e56\u8bf4\u6765\u6ca1\u6709\u4ec0\u4e48\u4e0d\u53ef\u66ff\u4ee3\u7684\u4f5c\u7528\uff0c\u5728MP3\u786c\u4ef6\u64ad\u653e\u5668\u7684\u4ea7\u54c1\u6280\u672f\u9886\u57df\uff0c\u9526\u6e56\u53ef\u4ee5\u5728\u6b27\u7f8e\u627e\u5230\u66f4\u591a\u5408\u9002\u7684\u5408\u4f5c\u5bf9\u8c61\uff0c\u53ea\u8981\u83b7\u5f97\u5fb7\u4eea\u5728\u786c\u4ef6\u67b6\u6784\u4e0a\u7684\u652f\u6301\uff0c\u9526\u6e56\u53ef\u4ee5\u6388\u6743\u66f4\u591a\u7684\u7535\u5b50\u5382\u5546\u751f\u4ea7MP3\u786c\u4ef6\u64ad\u653e\u5668\u3002</p><p>\u3000\u3000\u65af\u9ad8\u67cf\u65e2\u4e0d\u662f\u6700\u5408\u9002\u7684\u4e00\u5bb6\uff0c\u4e5f\u4e0d\u4f1a\u662f\u552f\u4e00\u7684\u4e00\u5bb6\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u53ea\u8981\u65af\u9ad8\u67cf\u80fd\u7b54\u5e94\u9526\u6e56\u7684\u5408\u4f5c\u6761\u4ef6\uff0c\u53ef\u4ee5\u4e3a\u540e\u6765\u8005\u63d0\u4f9b\u4e00\u4e2a\u5408\u4f5c\u7684\u6807\u51c6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u674e\u5728\u6d19\u80fd\u610f\u8bc6\u5230\u9526\u6e56\u5728\u65e5\u672c\u7684\u5b50\u516c\u53f8\u6536\u8d2d\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u3001\u53c8\u4e0e\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u4e09\u65b9\u9ad8\u5c42\u5728\u5efa\u90ba\u5bc6\u5207\u63a5\u89e6\u6781\u53ef\u80fd\u662f\u4e3a\u63a8\u51fa\u65b0\u7684\u6570\u5b57\u97f3\u9891\u4ea7\u54c1\u5728\u79d8\u5bc6\u78cb\u5546\u505a\u4e9b\u51c6\u5907\u5de5\u4f5c\uff0c\u8fd9\u662f\u56e0\u4e3a\u4e09\u661f\u7535\u5b50\u65d7\u4e0b\u4e5f\u6709\u4eba\u63d0\u51fa\u76f8\u7c7b\u4f3c\u7684\u4ea7\u54c1\u8bbe\u60f3\u3002\u63d0\u6848\u88ab\u9694\u7f6e\u4e86\u56db\u4e2a\u6708\u4e4b\u4e45\uff0c\u8fd9\u53ef\u80fd\u662f\u6781\u4e3a\u5173\u952e\u7684\u56db\u4e2a\u6708\uff0c\u674e\u5728\u6d19\u8ffd\u67e5\u5230\u97e9\u56fd\u603b\u90e8\uff0c\u624d\u6653\u5f97\u63d0\u6848\u9012\u4ea4\u4eba\u7adf\u7136\u5217\u5165\u4e0b\u4e2a\u6708\u7684\u88c1\u5458\u540d\u5355\u4e4b\u4e2d\u3002\u674e\u5728\u6d19\u4e0d\u5f97\u4e0d\u5c06\u62a5\u544a\u76f4\u63a5\u9012\u4ea4\u5230\u4ed6\u53d4\u53d4\u674e\u5065\u7199\u90a3\u91cc\uff0c\u4e09\u661f\u7535\u5b50\u8fd8\u7f3a\u4e4f\u62ff\u5f97\u51fa\u624b\u7684\u97f3\u9891\u97f3\u54cd\u4ea7\u54c1\uff0c\u8981\u662f\u8ba9\u8fd9\u6837\u7684\u4eba\u624d\u4ece\u4e09\u661f\u6d41\u5931\u51fa\u53bb\uff0c\u7edd\u5bf9\u65e0\u6cd5\u4f30\u91cf\u7684\u635f\u5931\u3002</p><p>\u3000\u3000\u56fd\u5185\u7684\u5a92\u4f53\u5374\u603b\u662f\u5728\u8ffd\u9010\u8868\u9762\u6027\u7684\u65b0\u95fb\uff0c\u8c01\u80fd\u6ce8\u610f\u5230\u9759\u4f0f\u5e73\u9759\u6d77\u9762\u4e0b\u6ce2\u6d9b\u6c79\u6d8c\u7684\u6f5c\u6d41\uff1f</p><p>\u3000\u3000\u5143\u65e6\u524d\u5915\u7684\u5348\u591c\u949f\u58f0\u6572\u8fc7\uff0c\u4e00\u822c\u4eba\u90fd\u4e0d\u4f1a\u610f\u8bc6\u5230\u81ea\u5df1\u7684\u751f\u6d3b\u4f1a\u53d1\u751f\u600e\u6837\u6084\u65e0\u58f0\u606f\u7684\u53d8\u5316\uff1f</p><p>\u3000\u3000\u4eba\u4eec\u5728\u4e2d\u592e\u7535\u89c6\u53f0\u4e00\u5957\u65b0\u95fb\u8054\u64ad\u540e\uff0c\u7b49\u7740\u5929\u6c14\u9884\u62a5\u65f6\u7684\u6700\u521d5\u79d2\u949f\u5c06\u770b\u4e0d\u5230\u719f\u6089\u4e86\u4e00\u5e74\u7684\u79d1\u738b\u789f\u673a\u5e7f\u544a\uff0c\u9999\u96ea\u6d77\u53d8\u9891\u51b0\u7bb1\u5374\u9996\u6b21\u51fa\u73b0\u5728\u592e\u89c6\u7684\u8367\u5c4f\u4e0a\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u8fd9\u5219\u5e7f\u544a\uff0c\u79d1\u738b\u524d\u540e\u652f\u4ed8\u4e86\u4e09\u4ebf\u4e94\u5343\u4e07\u7684\u5de8\u989d\uff0c\u54ac\u7740\u7259\u82e6\u82e6\u652f\u6491\u4e0b\u6765\uff0c\u9999\u96ea\u6d77\u82b1\u8d39\u7684\u4ee3\u4ef7\u751a\u81f3\u90fd\u4e0d\u5230\u79d1\u738b\u7684\u4e00\u534a\uff0c\u8fd9\u672c\u8eab\u5c31\u662f\u8db3\u5f97\u4eba\u4eec\u6d25\u6d25\u4e50\u9053\u7684\u8bdd\u9898\uff0c\u5bf9\u4e8e\u5f53\u4e8b\u8005\u6765\u8bf4\u5374\u6709\u7740\u522b\u4eba\u5c1d\u4e0d\u5230\u7684\u82e6\u6da9\u3002</p><p>\u3000\u3000\u5c31\u5728\u4e5d\u516b\u5e74\u5143\u65e6\u524d\u5915\uff0c\u7231\u8fbe\u96c6\u56e2\u53d1\u7ed9\u7535\u5b50\u5de5\u4e1a\u90e8\u7684\u4fe1\u51fd\u4e2d\u4ee5\u7231\u8fbe\u7535\u5b50\u4ece\u4e94\u6708\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u501f\u6b3e\u4e0a\u5e02\u3001\u5b9e\u9645\u4ece\u7231\u8fbe\u96c6\u56e2\u8131\u79bb\u51fa\u53bb\u4e3a\u7531\uff0c\u8981\u6c42\u7535\u5b50\u5de5\u4e1a\u90e8\u5728\u7edf\u8ba1\u4e5d\u4e03\u5e74\u7535\u5b50\u4fe1\u606f\u767e\u5f3a\u4f01\u4e1a\u7ecf\u6d4e\u6570\u636e\u65f6\uff0c\u5c06\u56db\u6708\u4e4b\u540e\u7231\u8fbe\u7535\u5b50\u7684\u789f\u673a\u4e1a\u52a1\u8425\u6536\u4ece\u7231\u8fbe\u96c6\u56e2\u603b\u8425\u6536\u6570\u636e\u4e2d\u5254\u9664\u51fa\u53bb\uff0c\u7231\u8fbe\u96c6\u56e2\u7edd\u4e0d\u4f1a\u4ee5\u865a\u5047\u7684\u6570\u636e\u6b3a\u9a97\u516c\u4f17\u3002</p><p>\u3000\u3000\u5254\u9664\u4e86\u56db\u6708\u4e4b\u540e\u7684\u789f\u673a\u4e1a\u52a1\u6570\u636e\uff0c\u7231\u8fbe\u96c6\u56e2\u4e5d\u4e03\u5e74\u7684\u4ea7\u54c1\u9500\u552e\u6536\u5165\u53ea\u6709\u4e09\u5341\u4ebf\uff0c\u6bd4\u4e5d\u516d\u5e74\u5927\u5e45\u4e0b\u964d25\uff05\u3002</p><p>\u3000\u3000\u5143\u65e6\u662f\u56fd\u5185\u6cd5\u5b9a\u7684\u5047\u65e5\uff0c\u5f88\u591a\u4eba\u5374\u65e0\u6cd5\u4eab\u53d7\u6cd5\u5b9a\u7684\u5047\u671f\u3002</p><p>\u3000\u3000\u5f20\u606a\u767e\u65e0\u804a\u8d56\u7684\u5750\u5728\u6c99\u53d1\u4e0a\u770b\u7740\u7535\u8111\u663e\u793a\u5c4f\u4e0a\u663e\u793a\u7684\u6700\u65b0\u505a\u51fa\u6765\u7684\u96c6\u56e2\u8425\u6536\u6570\u636e\uff0c\u62ac\u5934\u95ee\u9648\u4fe1\u751f\uff1a\u201c\u8fd9\u4e2a\u6570\u636e\u4f1a\u4e0d\u4f1a\u6bd4\u8f83\u8ba9\u67d0\u4e9b\u4eba\u6ee1\u610f\uff1f\u201d</p><p>\u3000\u3000\u201c\u79d1\u738b\u4e5d\u4e03\u5e74\u8425\u4e1a\u6536\u5165\u4e3a\u4e8c\u5341\u516d\u4ebf\uff0c\u65b0\u5143\u8425\u4e1a\u6536\u5165\u4e3a\u5341\u4e8c\u4ebf\uff0c\u4e09\u5341\u4ebf\u7684\u786e\u4e0d\u4f1a\u592a\u8d77\u773c\uff0c\u53ef\u80fd\u8981\u843d\u5230\u4e09\u5341\u540d\u4e4b\u5916\u3002\u4e0d\u8fc7\u5176\u4ed6\u6570\u636e\u5462\uff0c\u76c8\u5229\u80fd\u529b\u3001\u521b\u65b0\u6295\u5165\u3001\u7533\u8bf7\u4e13\u5229\u6570\u91cf\uff0c\u8fd9\u4e9b\u6570\u636e\u662f\u4e0d\u662f\u4e5f\u8981\u73b0\u7f16\uff1f\u201d\u9648\u4fe1\u751f\u95ee\u9053\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u7b11\u7740\u8bf4\uff1a\u201c\u4e09\u5341\u4ebf\u7684\u8425\u6536\uff0c\u5c06\u5143\u5668\u4ef6\u4ea7\u751f\u7684\u5229\u6da6\u5f52\u5165\u9526\u6e56\u7814\u7a76\u9662\uff0c\u4f46\u662f\u4ea7\u54c1\u9500\u552e\u4e0a\u4ea7\u751f\u7684\u5341\u4ebf\u51c0\u5229\uff0c\u8fd9\u4e2a\u6570\u636e\u62ff\u51fa\u53bb\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u59a5\u5427\uff1f\u5c31\u7b97\u6452\u9664\u6d77\u5916\u7684\u7814\u53d1\u6295\u5165\uff0c\u56fd\u5185\u7684\u7814\u53d1\u6295\u5165\u52a0\u4e0a\u6a61\u6811\u56ed\u4eca\u5e74\u7684\u6295\u8d44\u4e5f\u8981\u8d85\u8fc7\u5341\u4e2a\u4ebf\uff0c\u8fd9\u4e2a\u6570\u636e\u62ff\u51fa\u53bb\uff0c\u4f3c\u4e4e\u4e5f\u6709\u4e9b\u4e0d\u59a5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u611f\u89c9\u4eca\u5e74\u8d5a\u4e86\u5f88\u591a\u94b1\uff0c\u539f\u6765\u82b1\u51fa\u53bb\u66f4\u591a\u7684\u94b1\uff0c\u201d\u5f20\u606a\u7b11\u7740\u53f9\u6c14\uff0c\u4e00\u70b9\u90fd\u6ca1\u6709\u60cb\u60dc\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u60f3\u7535\u5b50\u5de5\u4e1a\u90e8\u8fd9\u51e0\u5c4a\u4e0d\u4f1a\u91c7\u7eb3\u8fd9\u4e9b\u6570\u636e\u4f5c\u4e3a\u8bc4\u5224\u6307\u6807\u7684\uff0c\u5c31\u4e0d\u8981\u53bb\u7ba1\u4ed6\u597d\u4e86\u3002\u4e5f\u4e0d\u662f\u7535\u5b50\u5de5\u4e1a\u90e8\u4e0d\u613f\u610f\uff0c\u4ed6\u4eec\u662f\u6ca1\u6709\u8fd9\u4e2a\u8138\u5c06\u521b\u65b0\u6295\u5165\u3001\u7533\u8bf7\u4e13\u5229\u6570\u91cf\u7b49\u6570\u636e\u4f5c\u4e3a\u8bc4\u5224\u7684\u6307\u6807\u2026\u2026\u201d\u60f3\u60f3\u4e5f\u771f\u662f\u53ef\u7b11\uff0c\u53bb\u5e74\u4e0a\u699c\u7684\u7535\u5b50\u767e\u5f3a\u4f01\u4e1a\uff0c\u6574\u4e2a\u4e5d\u4e03\u5e74\u7533\u8bf7\u4e13\u5229\u7684\u4f01\u4e1a\u7adf\u7136\u5360\u4e0d\u5230\u4e24\u6210\u3002</p></article>"}}}]);
