(window.webpackJsonp=window.webpackJsonp||[]).push([[752],{775:function(p){p.exports={chapterTitle:null,title:"\u7b2c589\u7ae0 \u5730\u72f1\u4e0e\u5929\u5802\u4e4b\u95f4",content:"<article><h2>\u7b2c589\u7ae0 \u5730\u72f1\u4e0e\u5929\u5802\u4e4b\u95f4</h2><p>\u3000\u3000\u5f20\u606a\u7ad9\u8d77\u8eab\u521a\u5f80\u5927\u5802\u95e8\u53e3\u51b2\u51fa\u4e24\u6b65\uff0c\u5c31\u770b\u89c1\u4e00\u4e2a\u4eba\u4f53\u63a0\u8fc7\u534a\u5c9b\u9152\u5e97\u5d4c\u7740\u91d1\u8fb9\u7684\u65cb\u8f6c\u73bb\u7483\u5927\u95e8\u7838\u843d\u5728\u5730\u4e0a\uff0c\u8089\u4f53\u7838\u5730\u7684\u58f0\u97f3\u539f\u6765\u662f\u8fd9\u6837\u7684\u54cd\u4eae\u2026\u2026</p><p>\u3000\u3000\u5085\u4fca\u7ad9\u5728\u524d\u9762\uff0c\u62ff\u8eab\u4f53\u6321\u4f4f\u4e24\u4e2a\u5973\u5b69\u5b50\u7684\u89c6\u7ebf\uff0c\u4f46\u662f\u4eb2\u773c\u7779\u4eba\u4ece\u9ad8\u697c\u4e00\u8dc3\u800c\u4e0b\u7684\u5fc3\u7406\u51b2\u51fb\uff0c\u8ba9\u5510\u5a67\u8138\u8272\u82cd\u767d\u3001\u624b\u8db3\u65e0\u63aa\u7684\u7ad9\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u8fc7\u53bb\uff0c\u7275\u8fc7\u5510\u5a67\u51b0\u51b7\u7684\u624b\uff1b\u675c\u98de\u6bd4\u5f20\u606a\u53cd\u5e94\u6162\u4e86\u534a\u62cd\uff0c\u4ed6\u4e0d\u6653\u5f97\u53d1\u751f\u4ec0\u4e48\u4e8b\uff0c\u53ea\u662f\u5f20\u606a\u63a5\u5230\u7535\u8bdd\u5c31\u5f80\u5927\u5802\u5916\u72c2\u5954\uff0c\u4ed6\u4e5f\u6beb\u4e0d\u72b9\u8c6b\u7684\u8d77\u8eab\u8ddf\u4e86\u51fa\u6765\u3002\u770b\u5230\u8eba\u5728\u5927\u5802\u5bcc\u4e3d\u5802\u7687\u65cb\u8f6c\u73bb\u7483\u95e8\u5916\u62bd\u6410\u7684\u4eba\u4f53\u5634\u89d2\u6d8c\u51fa\u8840\u6cab\uff0c\u9c9c\u8840\u6e85\u4e86\u4e00\u5730\uff1b\u675c\u98de\u6bd5\u7adf\u6ca1\u6709\u7ecf\u5386\u8fd9\u6837\u7684\u573a\u9762\uff0c\u8138\u8272\u77ac\u95f4\u715e\u767d\uff0c\u5f3a\u5fcd\u7740\u5fc3\u7406\u7684\u5de8\u5927\u51b2\u51fb\uff0c\u8d70\u8fc7\u53bb\uff0c\u8ddf\u76db\u590f\u5e76\u80a9\u7ad9\u5728\u4e00\u8d77\uff0c\u4fa7\u8fc7\u8138\u518d\u4e0d\u5fcd\u76ee\u7779\u3002</p><p>\u3000\u3000\u5927\u5802\u95e8\u5916\u7684\u5f02\u5e38\u5f15\u8d77\u534a\u5c9b\u9152\u5e97\u5185\u5916\u7684\u5c0f\u5c0f\u7684\u9a9a\u52a8\uff0c\u8bb8\u591a\u4eba\u90fd\u4ece\u5927\u5802\u8336\u5ea7\u91cc\u8dd1\u51fa\u6765\u7684\u89c2\u671b\uff1b\u9648\u9759\u4e0e\u8c22\u5b50\u5609\u643a\u624b\u4ece\u5916\u8fb9\u5f52\u6765\uff0c\u9648\u9759\u7684\u8138\u8272\u6709\u4e9b\u6194\u60b4\uff0c\u4e1c\u5357\u4e9a\u7ecf\u6d4e\u5f62\u52bf\u65e5\u76ca\u8d8b\u7d27\uff0c\u4f46\u662f\u4e5f\u6ca1\u6709\u60f3\u5230\u4eca\u5929\u5c31\u8f70\u7136\u5012\u5d29\uff0c\u89c1\u9152\u5e97\u95e8\u53e3\u56f4\u7740\u4eba\u7fa4\uff0c\u4e5f\u6ca1\u6709\u610f\u8bc6\u5230\u4ec0\u4e48\uff0c\u6324\u8fdb\u4eba\u7fa4\uff0c\u7ea4\u7ec6\u767d\u5ae9\u7684\u5c0f\u817f\u90fd\u5df2\u7ecf\u62ac\u8d77\uff0c\u5dee\u70b9\u4e00\u811a\u8e29\u8fdb\u8840\u6cca\u91cc\uff0c\u624d\u8d6b\u7136\u60ca\u89c9\uff0c\u5c16\u53eb\u4e00\u58f0\uff0c\u6025\u7740\u8981\u6536\u811a\uff0c\u8eab\u5b50\u5374\u5931\u53bb\u5e73\u8861\u8981\u51b2\u8f7b\u751f\u8005\u683d\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u6765\u5f97\u53ca\u62fd\u4e00\u628a\u5979\u7684\u624b\u81c2\uff0c\u4eba\u5374\u7ed9\u5979\u524d\u683d\u7684\u529b\u91cf\u62c9\u7740\u5f80\u524d\u8e09\u8dc4\u4e86\u4e00\u6b65\uff0c\u8fd8\u662f\u5085\u4fca\u53ca\u65f6\u6a2a\u8de8\u4e86\u4e00\u6b65\uff0c\u5c06\u9648\u9759\u6400\u4f4f\u3002</p><p>\u3000\u3000\u201c\u54c7\u2026\u2026\u201d\u9648\u9759\u6beb\u65e0\u5fc3\u7406\u51c6\u5907\u5c31\u76f4\u9762\u8840\u8089\u6a21\u7cca\u7684\u60e8\u72b6\uff0c\u80c3\u91cc\u7ffb\u6c5f\u5012\u6d77\uff0c\u6342\u7740\u5634\u9000\u51fa\u4eba\u7fa4\uff0c\u7ad9\u5230\u53f0\u9636\u8fb9\u5c06\u4e2d\u5348\u5403\u4e0b\u7684\u4e1c\u897f\u90fd\u5410\u4e86\u51fa\u6765\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u5012\u6ca1\u6709\u4ec0\u4e48\u4e0d\u9002\uff0c\u5c06\u624b\u5e15\u9012\u7ed9\u9648\u9759\uff0c\u5979\u8fd8\u6324\u8fdb\u6765\u770b\u4e86\u4e24\u773c\uff0c\u95ee\u5085\u4fca\uff1a\u201c\u8df3\u697c\uff1f\u201d</p><p>\u3000\u3000\u201c\u8df3\u697c\uff01\u201d\u5085\u4fca\u4ed6\u4eec\u662f\u8f7b\u773c\u76ee\u7779\u7684\u8f7b\u751f\u8005\u4ece\u4e8c\u5341\u516d\u5c42\u4e00\u8dc3\u800c\u4e0b\u7684\u60c5\u5f62\u3002</p><p>\u3000\u3000\u201c\u4eca\u5929\u8df3\u697c\u7684\u4eba\u6216\u8bb8\u8fd8\u4e0d\u4f1a\u592a\u591a\uff1f\u80fd\u4ece\u534a\u5c9b\u9152\u5e97\u4e00\u8dc3\u800c\u4e0b\uff0c\u4ed6\u4e5f\u4f1a\u6b7b\u5f97\u503c\u5f97\u3002\u201d\u8c22\u5b50\u5609\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u8fd9\u4e9b\u5929\u5728\u9152\u5e97\u5c31\u6ca1\u6709\u518d\u9047\u5230\u4ed6\uff0c\u534a\u5c9b\u9152\u5e97\u6bd5\u7adf\u6709\u4e09\u767e\u591a\u623f\u95f4\uff0c\u4f5c\u606f\u51fa\u5165\u65f6\u95f4\u4e0d\u540c\uff0c\u60f3\u8981\u9047\u4e0a\u4e5f\u4e0d\u662f\u5f88\u5bb9\u6613\u7684\u4e8b\u60c5\uff0c\u5979\u4e00\u76f4\u6000\u7591\u5f20\u606a\u8fd9\u4e2a\u65f6\u95f4\u51fa\u73b0\u5728\u9999\u6e2f\u662f\u4e3a\u6cf0\u94e2\u800c\u6765\uff0c\u6240\u4ee5\u624d\u8bd5\u63a2\u6027\u7684\u95ee\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u7406\u4f1a\u8c22\u5b50\u5609\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8fd9\u65f6\u5019\u90fd\u8981\u5c0a\u91cd\u6b7b\u8005\uff0c\u4ed6\u8f7b\u6402\u7740\u5510\u5a67\uff0c\u62ff\u8eab\u4f53\u6321\u7740\u5979\u7684\u89c6\u7ebf\uff0c\u4e5f\u6ca1\u6709\u7a7a\u5f53\u53bb\u5173\u5fc3\u9648\u9759\uff0c\u5bf9\u5085\u4fca\u8bf4\uff1a\u201c\u9999\u6e2f\u8b66\u5bdf\u6765\u4e86\u4f1a\u505a\u8c03\u67e5\uff0c\u4f60\u7559\u5728\u8fd9\u91cc\u914d\u5408\u4e00\u4e0b\u2026\u2026\u201d\u4ed6\u6402\u7740\u5510\u5a67\u4e0e\u675c\u98de\u3001\u76db\u590f\u4ed6\u4eec\u5148\u56de\u4e86\u623f\u95f4\u3002</p><p>\u3000\u3000\u5927\u7ea6\u8fc7\u4e86\u4e00\u4e2a\u5c0f\u65f6\uff0c\u5085\u4fca\u624d\u56de\u6765\u3002</p><p>\u3000\u3000\u4e5d\u9f99\u8b66\u7f72\u63a5\u5230\u62a5\u8b66\uff0c\u8fc5\u901f\u6d3e\u8b66\u5458\u8fc7\u6765\u5904\u7406\uff1a\u8f7b\u751f\u8005\u5728\u623f\u95f4\u7559\u4e0b\u9057\u4e66\uff0c\u9999\u6e2f\u5916\u6c47\u4ea4\u6613\u5e02\u573a\u7684\u5931\u8d25\u8005\uff0c\u4eca\u5929\u4e4b\u524d\uff0c\u4ed6\u8fd8\u662f\u62e5\u6709\u4e09\u767e\u4e03\u5341\u4e07\u7f8e\u5143\u7684\u5bcc\u7fc1\uff0c\u53ea\u662f\u770b\u9519\u4e86\u5f62\u52bf\uff0c\u8fd8\u4ee5\u4e3a\u6cf0\u56fd\u653f\u5e9c\u4f1a\u50cf\u524d\u4e24\u6b21\u4e00\u6837\u80fd\u4e00\u6b21\u6f02\u4eae\u7684\u53cd\u51fb\u2014\u2014\u4ece\u5929\u5802\u5230\u5730\u72f1\u53ea\u6709\u4e00\u77ac\u95f4\u7684\u65f6\u95f4\u800c\u5df2\uff1b\u4ed6\u6700\u540e\u5269\u4e0b\u7684\u51e0\u5343\u7f8e\u5143\uff0c\u5728\u9057\u5631\u91cc\uff0c\u4ed6\u7559\u7ed9\u4ed6\u79bb\u5a5a\u591a\u5e74\u7684\u524d\u59bb\u4e0e\u5973\u513f\u3002</p><p>\u3000\u3000\u9664\u4e86\u8d76\u8fc7\u6765\u5904\u7406\u7684\u8b66\u5458\u8bf4\uff0c\u4eca\u5e74\u4e5d\u9f99\u534a\u5c9b\u5df2\u7ecf\u53d1\u751f\u4e09\u8d77\u56e0\u7092\u6c47\u5931\u8d25\u8df3\u697c\u7684\u4eba\uff1a\u770b\u9519\u5f62\u52bf\uff0c\u505a\u9519\u4e86\u65b9\u5411\uff0c\u57fa\u672c\u4e0a\u6ca1\u6709\u518d\u633d\u56de\u7684\u4f59\u5730\uff0c\u867d\u7136\u6c79\u6d8c\u800c\u81f3\u7684\u8d27\u5e01\u6295\u673a\u8005\u591a\u4e3a\u7a7a\u5934\uff0c\u5374\u4e5f\u4e0d\u4e4f\u770b\u9519\u5f62\u52bf\u7684\u6295\u673a\u8005\u3002</p><p>\u3000\u3000\u201c\u6211\u966a\u5510\u5a67\u5979\u4eec\u53bb\u6e2f\u5c9b\u53e6\u627e\u4e00\u5bb6\u9152\u5e97\u4f4f\uff0c\u201d\u8003\u8651\u5230\u5510\u5a67\u6240\u627f\u53d7\u7684\u5fc3\u7406\u51b2\u51fb\uff0c\u5f20\u606a\u51b3\u5b9a\u9a6c\u4e0a\u5c31\u642c\u51fa\u534a\u5c9b\u9152\u5e97\uff0c\u8fd9\u6837\u4f1a\u8ba9\u5510\u5a67\u7684\u5fc3\u7406\u597d\u53d7\u4e00\u4e9b\uff0c\u53c8\u5bf9\u53f6\u5efa\u658c\u8bf4\uff0c\u201c\u8fd9\u8fb9\u5c31\u4ea4\u7ed9\u4f60\u4e86\u2026\u2026\u675c\u98de\u4e5f\u5148\u8ddf\u6211\u4eec\u8fc7\u53bb\uff0c\u7b49\u6211\u4eec\u56de\u6d77\u5dde\uff0c\u8ba9\u675c\u98de\u7559\u5728\u4f60\u8eab\u8fb9\u51e0\u5929\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u6b32\u8a00\u53c8\u6b62\uff0c\u5fcd\u4f4f\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u5f20\u606a\u7559\u5728\u8fd9\u91cc\u51e0\u5929\u90fd\u60a0\u7136\u81ea\u5f97\u4e0d\u5e72\u6b63\u4e8b\uff0c\u5374\u80fd\u5316\u89e3\u4ed6\u6240\u627f\u53d7\u7684\u538b\u529b\uff0c\u53ea\u662f\u5f20\u606a\u5173\u5fc3\u5510\u5a67\u66f4\u751a\u8fc7\u5176\u4ed6\uff0c\u4e5f\u4e0d\u4fbf\u8bf4\u4ec0\u4e48\uff0c\u53c8\u60f3\u8d77\u8fd9\u6bb5\u65f6\u95f4\u7279\u610f\u8d76\u53bb\u5efa\u90ba\u53c2\u4e0e\u5b66\u5e9c\u5df7\u6539\u9020\u5de5\u7a0b\u8bbe\u8ba1\u7684\u8bb8\u601d\uff0c\u53f6\u5efa\u658c\u5fc3\u60f3\u8fd9\u5bb6\u4f19\u600e\u4e48\u53ef\u4ee5\u5bf9\u4e24\u4e2a\u5973\u4eba\u90fd\u8fd9\u4e48\u7528\u5fc3\uff1f\u96be\u602a\u5973\u5b69\u5b50\u90fd\u559c\u6b22\u8fd9\u5bb6\u4f19\uff0c\u7fa1\u6155\u90fd\u7fa1\u6155\u4e0d\u6765\u3002</p><p>\u3000\u3000\u542c\u5f20\u606a\u6709\u610f\u5c06\u8fd9\u91cc\u7684\u4e8b\u60c5\u4e22\u4e0b\u76f4\u63a5\u56de\u6d77\u5dde\uff0c\u53f6\u5efa\u658c\u60f3\u4e86\u60f3\uff0c\u8fd8\u662f\u8bf4\u9053\uff1a\u201c\u4e8c\u53d4\u90a3\u8fb9\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u80fd\u505a\u7684\uff0c\u4e5f\u5df2\u7ecf\u5c3d\u529b\uff0c\u7559\u4e0b\u6765\u4f5c\u7528\u4e5f\u6709\u9650\u2026\u2026\u6211\u4f1a\u966a\u5510\u5a67\u5728\u6e2f\u5c9b\u90a3\u91cc\u518d\u6563\u51e0\u5929\u5fc3\uff0c\u8fd9\u8fb9\u5c31\u4e0d\u8fc7\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u70b9\u70b9\u5934\uff0c\u5f20\u606a\u8fd9\u5bb6\u4f19\u7ed9\u4eba\u6709\u518d\u4e16\u4e3a\u4eba\u7684\u6ca7\u6851\u4e0e\u4e25\u8083\uff0c\u6709\u4eba\u8df3\u697c\u8f7b\u751f\u4e0d\u4f1a\u7ed9\u4ed6\u5e26\u6765\u591a\u5927\u7684\u5fc3\u7406\u51b2\u51fb\uff0c\u6216\u8bb8\u6709\u7740\u66f4\u6df1\u5165\u7684\u601d\u8003\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4f4f\u8fdb\u592a\u5e73\u5c71\u9644\u8fd1\u7684\u4e1c\u65b9\u6587\u5316\u9152\u5e97\uff0c\u5929\u8272\u5df2\u9ed1\uff0c\u5510\u5a67\u4f9d\u7136\u6ca1\u80fd\u4ece\u76ee\u7779\u8df3\u697c\u8f7b\u751f\u7684\u5fc3\u7406\u51b2\u51fb\u4e2d\u6446\u8131\u51fa\u6765\uff0c\u5f20\u606a\u966a\u5979\u5355\u72ec\u5728\u623f\u95f4\u91cc\uff0c\u63e1\u7740\u5979\u7684\u624b\uff0c\u4e00\u76f4\u51b0\u51b0\u7684\uff0c\u62ff\u4e86\u4e00\u6761\u6bef\u5b50\u7ed9\u5979\u62ab\u4e0a\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u4e0d\u662f\u5f88\u6ca1\u7528\uff1f\u201d\u5510\u5a67\u62ff\u90a3\u53cc\u53d7\u4e86\u60ca\u5413\u3001\u695a\u695a\u53ef\u601c\u7684\u773c\u775b\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u4e0d\u4f1a\uff0c\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u5750\u5230\u5510\u5a67\u8eab\u8fb9\uff0c\u8ba9\u5979\u7684\u5934\u6795\u7740\u81ea\u5df1\u7684\u80a9\u8180\uff0c\u201c\u4f60\u591f\u575a\u5f3a\u4e86\uff0c\u8fd9\u4e2a\u4e16\u754c\u5176\u5b9e\u5f88\u6b8b\u9177\uff0c\u90a3\u4e2a\u4eba\u7684\u8df3\u697c\uff0c\u6211\u4eec\u4e5f\u9020\u4e86\u4e00\u5206\u5b7d\u2014\u2014\u4f46\u6211\u4eec\u53ea\u662f\u51b7\u9759\u7684\u5c48\u4ece\u4e8e\u6e38\u620f\u7684\u89c4\u5219\uff0c\u5728\u5730\u72f1\u4e0e\u5929\u5802\u4e4b\u95f4\uff0c\u6211\u4eec\u9009\u62e9\u4e86\u5929\u5802\uff1b\u6bd4\u8d77\u81ea\u5df1\u6216\u8005\u8bf4\u8fd9\u4e2a\u4e16\u754c\u7684\u6b8b\u9177\uff0c\u6211\u66f4\u75db\u6068\u4ed6\u4e0d\u80fd\u591f\u575a\u5f3a\u7684\u6d3b\u4e0b\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6216\u8bb8\u5904\u5883\u592a\u96be\u4e86\uff0c\u201d\u5510\u5a67\u4f9d\u7740\u5f20\u606a\u7684\u80f8\u53e3\uff0c\u201c\u6211\u4f1a\u60f3\u8d77\u4e5d\u56db\u5e74\u590f\u5929\u7684\u81ea\u5df1\uff0c\u6709\u79cd\u5929\u584c\u4e0b\u6765\u7684\u611f\u89c9\uff0c\u5e78\u597d\u6709\u4f60\u2014\u2014\u4f60\u8bf4\u90a3\u4eba\u662f\u4e0d\u662f\u4e5f\u8fd9\u4e48\u60f3\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u90a3\u65f6\u624d\u591a\u5927\uff1f\u201d\u60f3\u8d77\u5f80\u4e8b\uff0c\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u8fd8\u8bb0\u5f97\u6211\u8ddf\u4f60\u8bf4\u8fc7\u7684\u68a6\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u8001\u65e9\u53bb\u89c1\u5983\u84c9\u56de\u6d77\u5dde\u7684\u8def\u4e0a\uff0c\u4f60\u8ddf\u6211\u8bf4\u7684\u90a3\u4e2a\u68a6\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u90a3\u4e2a\u68a6\u91cc\uff0c\u6211\u5c06\u6211\u524d\u534a\u751f\u90fd\u5df2\u7ecf\u8fc7\u5b8c\uff0c\u6b64\u65f6\u7684\u611f\u89c9\uff0c\u5c31\u5982\u540c\u5728\u91cd\u6e29\u68a6\u4e2d\u7684\u4eba\u751f\u4e00\u6837\uff0c\u4e5d\u56db\u5e74\u590f\u5929\uff0c\u6211\u8fde\u7eed\u4e09\u5929\u56db\u5341\u4e8c\u5ea6\u7684\u9ad8\u70ed\uff0c\u6216\u8bb8\u5c31\u662f\u90a3\u6bb5\u65f6\u95f4\u9677\u5165\u660f\u8ff7\u4e4b\u4e2d\u505a\u4e86\u8fd9\u4e2a\u5947\u602a\u7684\u68a6\uff0c\u6211\u68a6\u89c1\u4f60\u7238\u7238\u5373\u4f7f\u53d7\u4e86\u5f88\u5927\u51a4\u5c48\u2014\u2014\u662f\u53d7\u4e86\u5f88\u5927\u51a4\u5c48\uff0c\u4e00\u76f4\u5230\u4e5d\u4e5d\u5e74\u624d\u6709\u4eba\u5e2e\u4f60\u7238\u7238\u6d17\u6e05\u51a4\u5c48\uff0c\u68a6\u4e2d\u7684\u4eba\u751f\uff0c\u6211\u4e5f\u662f\u90a3\u6837\u7684\u675f\u624b\u65e0\u7b56\u2014\u2014\u4f60\u8fd8\u662f\u575a\u5f3a\u7684\u9009\u62e9\u957f\u5927\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u662f\u5417\uff1f\u201d\u5510\u5a67\u695a\u695a\u52a8\u4eba\u7684\u7f8e\u7738\u51dd\u89c6\u7740\u5f20\u606a\uff0c\u201c\u90a3\u4e00\u5b9a\u662f\u4f60\u5728\u6211\u8eab\u8fb9\u7684\u7f18\u6545\u3002\u201d</p><p>\u3000\u3000\u201c\u662f\u7684\uff0c\u68a6\u4e2d\u7684\u4eba\u751f\uff0c\u6211\u4e00\u76f4\u5728\u4f60\u7684\u8eab\u8fb9\uff0c\u201d\u5f20\u606a\u8f7b\u8f7b\u7684\u629a\u7740\u5510\u5a67\u987a\u67d4\u7684\u79c0\u53d1\uff0c\u201c\u6211\u554a\uff0c\u4f3c\u4e4e\u5728\u68a6\u4e2d\u7684\u4eba\u751f\u624d\u9009\u62e9\u4e86\u575a\u5f3a\u2026\u2026\u201d</p><p>\u3000\u3000\u5230\u591c\u91cc\uff0c\u5510\u5a67\u4e5f\u4e0d\u987e\u4f1a\u8ba9\u522b\u4eba\u7b11\uff0c\u62b5\u5728\u5f20\u606a\u7684\u6000\u91cc\u6c89\u6c89\u7684\u7761\u53bb\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6cf0\u94e2\u5d29\u6e83\u4e4b\u540e\uff0c\u6cf0\u56fd\u4e2d\u592e\u94f6\u884c\u884c\u957f\u968f\u5373\u5ba3\u5e03\u8f9e\u804c\uff0c\u5728\u968f\u540e\u4e00\u5468\u65f6\u95f4\u91cc\u6cf0\u56fd\u653f\u5e9c\u5148\u540e\u5173\u95ed\u56db\u5341\u516b\u5bb6\u91d1\u878d\u673a\u5173\uff0c\u7ecf\u6d4e\u906d\u53d7\u91cd\u521b\uff0c\u5012\u95ed\u7684\u4f01\u4e1a\u4e0d\u8ba1\u5176\u6570\uff0c\u5760\u697c\u8f7b\u751f\u8005\u4e0d\u7edd\u4e8e\u8033\uff0c\u4f46\u662f\u8d27\u5e01\u6295\u673a\u8005\u4e0d\u4f1a\u56e0\u6b64\u4e2d\u6b62\u72e9\u730e\u7684\u72c2\u6b22\u76db\u5bb4\u2014\u2014\u5f20\u606a\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u4e2d\u6b62\u4ed6\u4eec\u7684\u5077\u730e\u2014\u2014\u91d1\u878d\u98ce\u66b4\u8fc5\u901f\u8513\u5ef6\u4e1c\u5357\u4e9a\u5176\u56fd\u5bb6\u548c\u5730\u533a\uff0c\u83f2\u5f8b\u5bbe\u3001\u9a6c\u6765\u897f\u4e9a\u7b49\u56fd\u7684\u8d27\u5e01\u4e5f\u968f\u540e\u906d\u53d7\u56fd\u9645\u6e38\u8d44\u7684\u653b\u51fb\uff0c\u6b64\u65f6\u98ce\u6f6e\u6d8c\u52a8\u4e4b\u66b4\u8c11\uff0c\u4ee4\u8fd9\u4e9b\u56fd\u5bb6\u60f3\u652f\u6491\u4f4f\u7b2c\u4e00\u6ce2\u653b\u51fb\u90fd\u56f0\u96be\uff0c\u90fd\u5148\u540e\u88ab\u8feb\u5ba3\u5e03\u653e\u5f03\u56fa\u5b9a\u6c47\u7387\uff0c\u4efb\u6c47\u7387\u5728\u77ed\u65f6\u95f4\u5185\u6781\u901f\u6ed1\u843d\uff0c\u5148\u540e\u7206\u53d1\u7ecf\u6d4e\u5371\u673a\uff1b\u5370\u5c3c\u7ecf\u6d4e\u5371\u673a\u7206\u53d1\u7a0d\u665a\uff0c\u4f46\u662f\u5370\u5c3c\u56fd\u5185\u7684\u7ecf\u6d4e\u72b6\u51b5\u66f4\u4e3a\u6076\u52a3\uff0c\u4e3b\u8981\u662f\u91d1\u878d\u5de8\u9cc4\u6700\u540e\u624d\u5f3a\u653b\u5370\u5c3c\u76fe\uff0c8\u67086\u65e5\uff0c\u5370\u5c3c\u592e\u884c\u5ba3\u5e03\u653e\u5f03\u56fa\u5b9a\u6c47\u7387\uff0c\u4efb\u6c47\u7387\u5927\u5e45\u4e0b\u6ed1\uff0c\u91d1\u878d\u98ce\u66b4\u8086\u8650\u88ad\u5377\u5370\u5c3c\uff0c\u5370\u5c3c\u76fe\u7d2f\u79ef\u91cd\u632b28\uff05\u3002</p><p>\u3000\u3000\u8fd9\u624d\u662f\u91d1\u878d\u98ce\u66b4\u7684\u7b2c\u4e00\u6ce2\u51b2\u51fb\uff0c\u98ce\u66b4\u88ad\u5377\u9664\u65b0\u52a0\u5761\u4e4b\u5916\u6240\u6709\u4e1c\u5357\u4e9a\u56fd\u5bb6\uff0c\u53f0\u6e7e\u3001\u9999\u6e2f\u4e24\u5730\u51ed\u501f\u81ea\u8eab\u7684\u7ecf\u6d4e\u4f18\u52bf\uff0c\u6682\u65f6\u7f6e\u8eab\u98ce\u66b4\u4e4b\u5916\uff0c\u4f46\u662f\u5404\u56fd\u91d1\u878d\u5e02\u573a\u7206\u8dcc\uff0c\u5df2\u7ecf\u5448\u73b0\u5371\u673a\u8fdb\u4e00\u6b65\u52a0\u6df1\u7684\u52bf\u5934\uff0c\u5370\u5c3c\u7b49\u5730\u751a\u81f3\u51fa\u73b0\u653f\u5c40\u4e0d\u7a33\u7684\u8ff9\u8c61\u3002</p><p>\u3000\u3000\u65e5\u672c\u7b49\u4e9a\u6d32\u5f3a\u56fd\u7684\u7ecf\u6d4e\u8870\u9000\u8d8b\u52bf\u4e5f\u65e5\u76ca\u660e\u663e\uff0c\u95fb\u7740\u8840\u8165\u805a\u96c6\u5230\u4e9a\u6d32\u91d1\u878d\u5e02\u573a\u4e0a\u7684\u56fd\u9645\u6e38\u8d44\u66f4\u662f\u8fbe\u5230\u60ca\u4eba\u7684\u89c4\u6a21\uff0c\u5148\u671f\u5728\u4e1c\u5357\u4e9a\u5404\u56fd\u7684\u91d1\u878d\u5e02\u573a\u66b4\u8650\u7684\u65bd\u5a01\uff0c\u6df1\u5ea6\u7684\u52a0\u5267\u8fd9\u4e9b\u56fd\u5bb6\u7684\u7ecf\u6d4e\u5d29\u6e83\u7684\u5c40\u52bf\u3002\u5728\u8fd9\u4e9b\u56fd\u5bb6\u653e\u5f03\u62b5\u6297\u4e4b\u540e\uff0c\u867d\u7136\u8fd9\u4e9b\u56fd\u5bb6\u7684\u8d27\u5e01\u8d2c\u503c\u5c06\u662f\u4e00\u4e2a\u957f\u671f\u7684\u8d8b\u52bf\uff0c\u4f46\u662f\u5927\u91cf\u7684\u7a7a\u5934\u7f3a\u4e4f\u63a5\u76d8\u8005\u5df2\u7ecf\u53d8\u5f97\u65e0\u5229\u53ef\u56fe\uff0c\u89c4\u6a21\u5e9e\u5927\u7684\u56fd\u9645\u6e38\u8d44\u5c06\u4e1c\u5357\u4e9a\u5404\u56fd\u90fd\u65bd\u66b4\u4e86\u4e00\u904d\u2014\u2014\u65b0\u52a0\u5761\u7ecf\u6d4e\u57fa\u7840\u5f3a\u52b2\uff0c\u62b5\u5fa1\u4f4f\u91d1\u878d\u98ce\u66b4\u7684\u66b4\u8650\u5439\u88ad\u2014\u2014\u4ed6\u4eec\u9700\u8981\u65b0\u7684\u72e9\u730e\u76ee\u6807\uff0c\u4f46\u662f\u9999\u6e2f\u80a1\u5e02\u7ecf\u8fc7\u4e3b\u52a8\u964d\u6e29\uff0c\u6052\u751f\u6307\u6570\u964d\u81f3\u4e00\u4e07\u4e24\u5343\u70b9\uff0c\u5728\u66fe\u7ecf\u53d1\u751f\u7684\u5386\u53f2\u4e0a\uff0c\u516b\u6708\u6700\u521d\u7684\u4e24\u4e2a\u661f\u671f\uff0c\u6052\u751f\u6307\u6570\u8fd8\u7ef4\u6301\u5728\u8fd1\u4e00\u4e07\u4e03\u5343\u70b9\u4ee4\u4eba\u751f\u754f\u7684\u9ad8\u5cf0\u4e0a\u2026\u2026</p></article>"}}}]);
