(window.webpackJsonp=window.webpackJsonp||[]).push([[753],{776:function(p){p.exports={chapterTitle:null,title:"\u7b2c59\u7ae0 \u6458\u6843\u5b50\u7684\u6b63\u6cf0\u96c6\u56e2",content:"<article><h2>\u7b2c59\u7ae0 \u6458\u6843\u5b50\u7684\u6b63\u6cf0\u96c6\u56e2</h2><p>\u3000\u3000\u5c0f\u53d4\u5f20\u77e5\u975e\u7684\u65b0\u516c\u53f8\u5b8f\u8fdc\u5b9e\u4e1a\u80a1\u4efd\u6709\u9650\u516c\u53f8\u7ecf\u8fc7\u4e00\u4e2a\u591a\u6708\u7684\u7b79\u5907\uff0c\u57289\u670815\u65e5\u8fd9\u5929\u6b63\u5f0f\u6302\u724c\uff0c\u5510\u5b66\u8c26\u5f53\u4e0a\u4ee3\u5e02\u957f\u4ee5\u6765\uff0c\u7b2c\u4e00\u6b21\u53c2\u52a0\u516c\u53f8\u7684\u526a\u5f69\u4eea\u5f0f\u3002\u65b0\u516c\u53f8\u6ce8\u518c\u8d44\u91d1\u4e00\u5343\u4e07\uff0c\u6d77\u5dde\u5e02\u5de5\u7a0b\u5efa\u7b51\u603b\u516c\u53f8\u3001\u4e1c\u793e\u53bf\u5de5\u7a0b\u5efa\u7b51\u516c\u53f8\u3001\u5e02\u5730\u8d28\u52d8\u5bdf\u9662\u3001\u6d77\u5dde\u5e02\u57ce\u5efa\u8bbe\u8ba1\u9662\u90fd\u53c2\u4e86\u80a1\uff0c\u4f5c\u4e3a\u65b0\u516c\u53f8\u7684\u4e3b\u8981\u53d1\u8d77\u4eba\uff0c\u5f20\u77e5\u975e\u4e2a\u4eba\u5360\u80a1\u4e0d\u5230\u4e09\u6210\uff0c\u4f46\u5728\u8fd9\u4e2a\u590f\u5929\u4e4b\u524d\uff0c\u4ed6\u8fd8\u6ca1\u6709\u60f3\u8fc7\u80fd\u8fd9\u4e48\u5feb\u638c\u63e1\u8fd9\u4e48\u5927\u89c4\u6a21\u7684\u516c\u53f8\u3002</p><p>\u3000\u3000\u5f20\u606a\u62c9\u7740\u5510\u5a67\u3001\u675c\u98de\u4e00\u8d77\u53c2\u52a0\u665a\u4e0a\u7684\u9152\u5bb4\uff0c\u89c1\u5c0f\u53d4\u7ea2\u5149\u6ee1\u9762\uff0c\u5012\u62c5\u5fc3\u8df3\u8dc3\u5f0f\u7684\u6269\u5f20\u5bf9\u4ed6\u6765\u8bf4\u672a\u5fc5\u662f\u4ef6\u597d\u4e8b\uff0c\u524d\u671f\u4e13\u6ce8\u4e8e\u5efa\u7b51\u5de5\u7a0b\uff0c\u592f\u5b9e\u516c\u53f8\u7684\u57fa\u7840\uff0c\u5c31\u6015\u4ed6\u7ed9\u8fd9\u79cd\u8df3\u8dc3\u5f0f\u6269\u5f20\u7684\u5feb\u611f\u51b2\u660f\u5934\u8111\uff0c\u5f3a\u884c\u6d89\u8db3\u5176\u4ed6\u9886\u57df\u3002</p><p>\u3000\u3000\u516c\u53f8\u7684\u540d\u79f0\u6ca1\u6709\u51a0\u4ee5\u5de5\u7a0b\u516c\u53f8\uff0c\u800c\u662f\u51a0\u4ee5\u5b9e\u4e1a\u516c\u53f8\uff0c\u53ef\u89c1\u9664\u4e86\u5c0f\u53d4\u4e4b\u5916\uff0c\u516c\u53f8\u7684\u5176\u4ed6\u80a1\u4e1c\u90fd\u6709\u8dc3\u8dc3\u6b32\u8bd5\u7684\u91ce\u5fc3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8fde\u7eed\u4e24\u5929\uff0c\u9488\u5bf9\u5168\u7701\u521a\u521a\u840c\u751f\u7684\u6570\u5b57\u624b\u673a\u5e02\u573a\uff0c\u5f20\u606a\u8d76\u51fa\u6574\u4f53\u63a8\u5e7f\u65b9\u6848\u7684\u521d\u7a3f\uff0c\u8fd9\u53ea\u662f\u4e4b\u524d\u7684\u65b9\u6848\u4e0a\u8fdb\u884c\u8865\u8ff0\uff0c\u6ca1\u6709\u592a\u5927\u7684\u5de5\u4f5c\u91cf\uff0c\u7ec6\u8282\u5904\u7531\u8521\u7eef\u5a1f\u5e26\u56de\u7701\u57ce\u6dfb\u52a0\u4fee\u6539\uff0c\u76db\u946b\u516c\u53f8\u7684\u603b\u90e8\u4e5f\u5728\u7701\u57ce\uff0c\u5f20\u606a\u4e0d\u4f1a\u53c2\u52a0\u5177\u4f53\u7684\u8c08\u5224\uff0c\u4f46\u662f\u7ed3\u679c\u80fd\u591f\u9884\u671f\u3002</p><p>\u3000\u3000\u76db\u946b\u516c\u53f8\u770b\u91cd\u6d77\u88d5\u516c\u53f8\u7684\u653f\u5546\u80cc\u666f\uff0c\u4e5f\u4ece\u6d77\u5dde\u529e\u4e8b\u5904\u770b\u5230\u6d77\u88d5\u505a\u5e02\u573a\u7684\u80fd\u529b\uff0c\u5176\u5b9e\u5728\u53f6\u5efa\u658c\u4ece\u5802\u59b9\u53f6\u5c0f\u6850\u90a3\u91cc\u770b\u5230\u6d77\u5dde\u7684\u5e02\u573a\u63a8\u5e7f\u65b9\u6848\u4ee5\u53ca\u5728\u6d77\u5dde\u65e5\u62a5\u4e0a\u4e09\u5929\u767b\u51fa\u7684\u5e7f\u544a\u6848\uff0c\u5c31\u60f3\u5c06\u5609\u4fe1\u516c\u53f8\u6362\u6389\uff1b\u6d77\u88d5\u65e0\u7591\u662f\u81ea\u5df1\u6b64\u65f6\u6700\u9700\u8981\u7684\u5408\u4f5c\u4f19\u4f34\uff0c\u4f46\u662f\u5728\u5708\u5b50\u91cc\u6df7\uff0c\u5c31\u4e0d\u80fd\u8086\u65e0\u5fcc\u60ee\u7684\u7834\u574f\u5546\u4e1a\u89c4\u5219\u3002</p><p>\u3000\u3000\u65e2\u7136\u5609\u4fe1\u516c\u53f8\u5bf9\u6570\u5b57\u624b\u673a\u5e02\u573a\u6ca1\u6709\u592a\u591a\u7684\u70ed\u60c5\uff0c\u529d\u8bf4\u4ed6\u4eec\u8ba9\u51fa\u4e00\u90e8\u5206\u5e02\u573a\u7ed9\u6d77\u88d5\uff0c\u95ee\u9898\u4e0d\u4f1a\u592a\u5927\uff0c\u8fd8\u8003\u8651\u5c06\u4e00\u7ebf\u57ce\u5e02\u60e0\u5c71\u7684\u5e02\u573a\u4ea4\u7ed9\u6d77\u88d5\u53bb\u505a\uff0c\u76db\u946b\u516c\u53f8\u5c31\u53ef\u4ee5\u4e13\u5fc3\u8d1f\u8d23\u5176\u4ed6\u7701\u5e02\u7684\u5e02\u573a\u3002</p><p>\u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u5c06\u4e1c\u6d77\u7684\u5e02\u573a\u5206\u6210\u4e24\u5757\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u5728\u7701\u57ce\uff0c\u4e00\u8fb9\u4e0e\u76db\u946b\u8c08\u5224\u4e89\u53d6\u66f4\u591a\u7684\u5e02\u573a\u4efd\u989d\uff0c\u4e00\u8fb9\u5c06\u6d77\u88d5\u516c\u53f8\u5728\u8c08\u5b9a\u533a\u57df\u5185\u7684\u76f8\u5173\u8d44\u4ea7\u4e0e\u503a\u52a1\u8fdb\u884c\u5265\u79bb\uff0c\u8fd8\u8981\u5bfb\u627e\u65b0\u7684\u5408\u4f5c\u65b9\uff0c\u6765\u7f13\u89e3\u65b0\u516c\u53f8\u7684\u8d22\u52a1\u538b\u529b\u3002</p><p>\u3000\u3000\u6d77\u5dde\u8fd9\u8fb9\u5728\u53d6\u5f97\u6b63\u5f0f\u7684\u4ee3\u7406\u6743\u4e4b\u540e\uff0c\u4e4b\u524d\u7b7e\u5b9a\u7684\u610f\u5411\u8d44\u91d1\u7edd\u5927\u90e8\u5206\u8f6c\u4e3a\u5408\u540c\u91d1\u989d\u6d41\u5411\u76db\u946b\u516c\u53f8\uff0c\u5dee\u4e0d\u591a\u63a5\u8fd1\u4e8c\u767e\u4e07\u7684\u8d44\u91d1\u91cf\uff0c\u53ea\u6709\u5c11\u90e8\u5206\u5546\u6237\u51fa\u4e8e\u79cd\u79cd\u539f\u56e0\u64a4\u6d88\u65e9\u5148\u7b7e\u5b9a\u7684\u610f\u5411\u4e66\u3002</p><p>\u3000\u3000\u867d\u7136\u65b0\u7684\u516c\u53f8\u8fd8\u6ca1\u6709\u6b63\u5f0f\u6210\u7acb\uff0c\u8521\u7eef\u5a1f\u7559\u5728\u7701\u57ce\u534f\u52a9\u8c22\u665a\u6674\u6709\u8bb8\u591a\u4e8b\u60c5\u8981\u505a\uff0c\u5468\u6e38\u4f5c\u4e3a\u65b0\u516c\u53f8\u7684\u526f\u603b\u7ecf\u7406\u5148\u5230\u6d77\u5dde\u534f\u52a9\u5de5\u4f5c\u3002\u4ed6\u662f\u968f\u5f90\u5fd7\u660e\u4e00\u8d77\u521b\u529e\u6d77\u88d5\u8d38\u6613\u80a1\u4efd\u516c\u53f8\u7684\u5143\u8001\uff0c\u8c22\u77bb\u5f53\u4e0a\u603b\u7ecf\u7406\u4e4b\u540e\uff0c\u4ed6\u88ab\u8c03\u79bb\u6d77\u88d5\u516c\u53f8\u7684\u6838\u5fc3\u7ba1\u7406\u5c42\uff0c\u6ca1\u6709\u5e72\u9884\u516c\u53f8\u7ecf\u8425\u65b9\u5411\u7684\u6743\u529b\uff0c\u8c22\u665a\u6674\u63a5\u624b\u516c\u53f8\uff0c\u4ed6\u8fd9\u4e9b\u88ab\u6392\u6324\u53c8\u575a\u6301\u6ca1\u6709\u79bb\u5f00\u516c\u53f8\u7684\u5143\u8001\uff0c\u81ea\u7136\u53d7\u5230\u91cd\u7528\uff0c\u53ea\u662f\u6d77\u88d5\u516c\u53f8\u79ef\u9014\u96be\u8fd4\uff0c\u5f88\u591a\u4eba\u5bf9\u516c\u53f8\u5df2\u7ecf\u6ca1\u6709\u592a\u5927\u7684\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u5468\u6e38\u4e09\u5341\u56db\u5c81\uff0c\u534e\u4e1c\u653f\u6cd5\u5927\u5b66\u56fd\u9645\u5173\u7cfb\u5b66\u9662\u6bd5\u4e1a\uff0c\u4ee5\u4ed6\u7684\u4e13\u4e1a\uff0c\u4f3c\u4e4e\u4ece\u653f\u6bd4\u7ecf\u5546\u66f4\u5408\u9002\uff0c\u6234\u7740\u539a\u539a\u7684\u73bb\u7483\u773c\u955c\uff0c\u8eab\u6750\u9ad8\u7626\uff0c\u773c\u775b\u7ec6\u5c0f\uff0c\u4f46\u662f\u773c\u795e\u5f88\u9510\u5229\u3002\u5468\u6e38\u521d\u6b21\u89c1\u5230\u5f20\u606a\u65f6\uff0c\u4e5f\u4e3a\u4ed6\u7684\u5e74\u8f7b\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u662f\u7cbe\u5f69\u7edd\u4f26\u7684\u7b56\u5212\u65b9\u6848\u4e0e\u7cbe\u51c6\u8be6\u7ec6\u7684\u5e02\u573a\u63a8\u5e7f\u8ba1\u5212\uff0c\u8ba9\u4ed6\u6536\u8d77\u8f7b\u89c6\u4e4b\u5fc3\uff0c\u5f53\u7136\uff0c\u4ed6\u66f4\u8ba4\u4e3a\u6d77\u5dde\u529e\u4e8b\u5904\u76ee\u524d\u4e3a\u6b62\u7684\u6210\u7ee9\uff0c\u4e3b\u8981\u662f\u8bb8\u601d\u4e0e\u4e0b\u9762\u4eba\u5458\u7684\u529f\u52b3\u3002</p><p>\u3000\u3000\u5f20\u606a\u6bd5\u7adf\u8fd8\u53ea\u662f\u8bfb\u9ad8\u4e2d\u7684\u5c11\u5e74\uff0c\u80fd\u529b\u53d7\u5230\u8d28\u7591\uff0c\u4e5f\u662f\u7406\u6240\u5f53\u7136\u7684\uff0c\u5728\u5468\u6e38\u62b5\u8fbe\u6d77\u5dde\u4e4b\u540e\uff0c\u5f20\u606a\u4fbf\u5c3d\u91cf\u51cf\u5c11\u5728\u529e\u4e8b\u5904\u9732\u9762\u7684\u673a\u4f1a\uff0c\u6bd5\u7adf\u901a\u8baf\u5e02\u573a\u53d1\u5c55\u7684\u521d\u671f\uff0c\u5e76\u6ca1\u6709\u5341\u5206\u590d\u6742\u7684\u5730\u65b9\uff0c\u76f8\u4fe1\u5468\u6e38\u8fd9\u6837\u7684\u4e13\u4e1a\u8425\u9500\u4eba\u58eb\uff0c\u53d7\u5230\u4e9b\u542f\u53d1\uff0c\u5c31\u80fd\u505a\u5f97\u5f88\u5b8c\u7f8e\u3002</p><p>\u3000\u3000\u8521\u7eef\u5a1f\u8d1f\u8d23\u65b0\u516c\u53f8\u7684\u8425\u8fd0\uff0c\u5468\u6e38\u8d1f\u8d23\u5e02\u573a\u63a8\u5e7f\uff0c\u5927\u7ea6\u4e00\u4e2a\u661f\u671f\u4e4b\u540e\uff0c\u65b0\u516c\u53f8\u7684\u8d22\u52a1\u603b\u76d1\u5355\u6885\u4e5f\u62b5\u8fbe\u6d77\u5dde\uff0c\u53c2\u52a0\u65b0\u516c\u53f8\u7684\u7b79\u5907\uff0c\u4e00\u4e2a\u624d\u4e8c\u5341\u4e03\u5c81\u7684\u804c\u4e1a\u5973\u6027\uff0c\u6234\u7740\u7ec6\u9ed1\u8fb9\u773c\u955c\uff0c\u7a84\u8138\u767d\u51c0\uff0c\u9887\u6709\u51e0\u5206\u77e5\u6027\u7f8e\uff0c\u5979\u662f\u8c22\u665a\u775b\u4ece\u5bb6\u65cf\u4f01\u4e1a\u6b63\u6cf0\u96c6\u56e2\u8bf7\u6765\u89e3\u51b3\u8d44\u4ea7\u91cd\u7ec4\u4e2d\u7684\u8d22\u52a1\u95ee\u9898\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u5230\u5979\u7684\u6765\u5386\uff0c\u77e5\u9053\u8c22\u665a\u6674\u8fd8\u662f\u4ece\u5bb6\u65cf\u5185\u90e8\u5bfb\u627e\u5408\u4f5c\u8005\u3002</p><p>\u3000\u3000\u540c\u65f6\uff0c\u4e1c\u6d77\u7701\u4e1c\u90e8\u5404\u5730\u533a\u529e\u4e8b\u5904\u7684\u7ecf\u7406\u4e0e\u4e1a\u52a1\u4e3b\u7ba1\u90fd\u5230\u6d77\u5dde\u529e\u4e8b\u5904\u53c2\u4e0e\u5de5\u4f5c\uff0c\u52a0\u4e0a\u5728\u6d77\u5dde\u5f53\u5730\u8058\u7528\u7684\u8d22\u52a1\u3001\u51fa\u7eb3\u3001\u603b\u52a1\u7b49\u8f85\u52a9\u6027\u804c\u5458\uff0c\u539f\u5148\u8fd8\u7b97\u5bbd\u655e\u7684\u529e\u516c\u5ba4\uff0c\u52a0\u4e0a\u5c06\u9694\u58c1\u76f8\u5f53\u89c4\u6a21\u7684\u529e\u516c\u5ba4\u627f\u79df\u4e0b\u6765\uff0c\u8fd8\u662f\u89c9\u5f97\u4eba\u6ee1\u4e3a\u60a3\u3002</p><p>\u3000\u3000\u5927\u7ea6\u7ecf\u8fc7\u4e24\u5468\u7684\u65f6\u95f4\uff0c\u65b0\u516c\u53f8\u7684\u6846\u67b6\u5c31\u51fa\u6765\u4e86\u3002\u8c22\u665a\u6674\u7684\u5bb6\u65cf\u4f01\u4e1a\u6b63\u6cf0\u96c6\u56e2\u63d0\u4f9b\u4e94\u767e\u4e07\u8d44\u91d1\u53c2\u4e0e\u65b0\u516c\u53f8\u7684\u53d1\u8d77\u7b79\u5efa\uff0c\u5360\u636e46\uff05\u7684\u80a1\u4efd\uff1b\u6d77\u88d5\u8d38\u6613\u5c06\u4e1c\u6d77\u7701\u4e1c\u534a\u7247\u7684\u6e20\u9053\u8d44\u6e90\u53ca\u4e8c\u767e\u4e07\u8d44\u91d1\u6ce8\u5165\u65b0\u516c\u53f8\uff0c\u5360\u636e34\uff05\u7684\u80a1\u4efd\uff0c\u5305\u62ec\u56db\u767e\u4e07\u7684\u9690\u6027\u503a\u52a1\u5c06\u7531\u65b0\u516c\u53f8\u6d88\u5316\uff1b\u8521\u7eef\u5a1f\u3001\u5468\u6e38\u3001\u5355\u6885\u7b49\u7ba1\u7406\u4eba\u5458\u51fa\u8d44\u4e94\u5341\u4e07\uff0c\u53605\uff05\u7684\u80a1\u4efd\uff1b\u4ee5\u6d77\u5dde\u5e02\u573a\u7684\u4efd\u989d\u5165\u80a1\uff0c\u5f20\u606a\u5b9e\u9645\u6240\u5f97\u3001\u6700\u7ec8\u6302\u5230\u8bb8\u601d\u540d\u4e0b\u7684\u80a1\u4efd\u53ea\u670915\uff05\u3002</p><p>\u3000\u3000\u65b0\u516c\u53f8\u547d\u540d\u4e3a\u6d77\u6cf0\u8d38\u6613\u80a1\u4efd\u6709\u9650\u516c\u53f8\uff0c\u7531\u8c22\u665a\u6674\u62c5\u4efb\u603b\u7ecf\u7406\uff0c\u5177\u4f53\u7684\u4e8b\u4e1a\u7531\u8521\u7eef\u5a1f\u3001\u5468\u6e38\u3001\u5355\u6885\u8d1f\u8d23\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u8fd9\u6837\u7684\u80a1\u6743\u5206\u914d\uff0c\u5f20\u606a\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\uff0c\u65b0\u516c\u53f8\u867d\u7136\u7f3a\u8d44\u91d1\uff0c\u4f46\u4e5f\u4e0d\u9700\u8981\u4e03\u767e\u4e07\u7684\u73b0\u91d1\uff0c\u6b63\u6cf0\u96c6\u56e2\u53ea\u662f\u5229\u7528\u4ed6\u4eec\u7684\u8d44\u91d1\u4f18\u52bf\uff0c\u5728\u65b0\u516c\u53f8\u91cc\u5360\u636e\u66f4\u591a\u7684\u80a1\u4efd\uff0c\u8bf4\u660e\u4ed6\u4eec\u5bf9\u6570\u5b57\u624b\u673a\u5e02\u573a\u7684\u524d\u666f\u4e5f\u5341\u5206\u770b\u597d\u3002\u5f20\u606a\u5fc3\u60f3\uff1a\u6b63\u6cf0\u7684\u5927\u96c6\u56e2\u80cc\u666f\uff0c\u4e5f\u80fd\u63d0\u4f9b\u989d\u5916\u7684\u652f\u6301\u5427\u3002\u4e5f\u53ea\u80fd\u8fd9\u6837\u5b89\u6170\u81ea\u5df1\uff0c\u4e0d\u80fd\u8ba9\u665a\u6674\u59d0\u5939\u5728\u4e2d\u95f4\u96be\u505a\u4eba\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u5bf9\u4e0d\u80fd\u7ed9\u4e88\u5f20\u606a\u627f\u8bfa\u768420\uff05\u80a1\u4efd\uff0c\u6709\u4e9b\u5185\u759a\u3002\u5728\u5206\u9500\u8d38\u6613\u4f53\u7cfb\u5185\uff0c\u8d44\u91d1\u7684\u4f5c\u7528\u76f8\u5bf9\u6765\u8bf4\u662f\u6b21\u8981\u7684\uff0c\u5e02\u573a\u80fd\u529b\u4e0e\u5bb9\u91cf\u624d\u662f\u7740\u91cd\u8003\u8651\u7684\u56e0\u7d20\uff0c\u5f20\u606a\u4e0d\u52a8\u7528\u4e00\u5206\u8d44\u91d1\u5c31\u4ece\u76db\u946b\u90a3\u91cc\u83b7\u5f97\u7279\u7ea6\u4ee3\u7406\u6743\uff0c\u4fbf\u662f\u5bf9\u4ed6\u5e02\u573a\u80fd\u529b\u7684\u80af\u5b9a\uff0c\u5b9e\u9645\u4e0a\uff0c\u6d77\u5dde\u5e02\u573a\u7684\u9884\u671f\u4efd\u989d\u5df2\u7ecf\u5360\u5230\u4e1c\u6d77\u7701\u4e1c\u534a\u7247\u5e02\u573a\u768415\uff05\uff0c\u4f5c\u4e3a\u8fd9\u4efd\u65b9\u6848\u7684\u6700\u521d\u7b56\u5212\u4eba\u4e0e\u6267\u884c\u4eba\uff0c\u7406\u7531\u5360\u636e\u66f4\u591a\u7684\u80a1\u4efd\u3002</p><p>\u3000\u3000\u5728\u6d77\u6cf0\u516c\u53f8\u6b63\u5f0f\u6210\u7acb\u4e4b\u524d\u7684\u4e00\u4e2a\u6708\u5185\uff0c\u6d77\u5dde\u5e02\u573a\u7684\u94fa\u8d27\u91cf\u5df2\u7ecf\u8fbe\u5230\u4e09\u767e\u4e07\u4e4b\u5de8\uff0c\u5dee\u4e0d\u591a\u662f\u5609\u4fe1\u516c\u53f8\u5728\u4e1c\u6d77\u7701\u897f\u534a\u7247\u533a\u57df\u7684\u94fa\u8d27\u91cf\u603b\u548c\uff0c\u76db\u946b\u516c\u53f8\u7279\u522b\u5956\u52b1\u989d\u5916\u7684\u8fd4\u5229\uff0c\u76db\u946b\u76f8\u4fe1\u5728\u6d77\u5dde\u5e02\u573a\u7684\u63a8\u52a8\u4e4b\u4e0b\uff0c\u5176\u4ed6\u5730\u65b9\u4e5f\u4f1a\u5f88\u5feb\u542f\u52a8\u8d77\u6765\u3002\u56e0\u4e3a\u6d77\u6cf0\u516c\u53f8\u8fd8\u6ca1\u6709\u771f\u6b63\u6210\u7acb\uff0c\u8c22\u665a\u6674\u5c06\u5341\u516b\u4e07\u7684\u5229\u6da6\uff0c\u90fd\u5212\u62e8\u7ed9\u4ed6\uff0c\u4f5c\u4e3a\u964d\u4f4e\u4ed6\u80a1\u4efd\u7684\u8865\u507f\u3002</p></article>"}}}]);
