(window.webpackJsonp=window.webpackJsonp||[]).push([[754],{777:function(p){p.exports={chapterTitle:null,title:"\u7b2c590\u7ae0 \u6c47\u7387\u635f\u5931",content:"<article><h2>\u7b2c590\u7ae0 \u6c47\u7387\u635f\u5931</h2><p>\u3000\u3000\u91d1\u878d\u98ce\u66b4\u7684\u9634\u4e91\u5728\u4e1c\u5357\u4e9a\u7684\u4f4e\u7a7a\u8086\u8650\uff0c\u91d1\u878d\u5de8\u9cc4\u5374\u5b8c\u5168\u6ca1\u6709\u6536\u624b\u7684\u610f\u613f\uff0c\u8702\u62e5\u5230\u4e9a\u6d32\u91d1\u878d\u5e02\u573a\u7684\u56fd\u9645\u6e38\u8d44\u89c4\u6a21\u4e5f\u662f\u65e5\u76ca\u5e9e\u5927\uff0c\u5df2\u7ecf\u5f88\u4e45\u6ca1\u6709\u8fd9\u6837\u7684\u76db\u5bb4\u673a\u4f1a\u4e86\uff0c\u90fd\u60f3\u7740\u8fc7\u6765\u7f8e\u98df\u4e00\u9910\u3002</p><p>\u3000\u3000\u5f53\u4e1c\u5357\u4e9a\u5404\u56fd\u653f\u5e9c\u7eb7\u7eb7\u653e\u5f03\u62b5\u6297\uff0c\u8fd9\u4e9b\u56fd\u5bb6\u4f5c\u4e3a\u98df\u7269\u6e90\u5df2\u7ecf\u4e0d\u80fd\u6ee1\u8db3\u91d1\u878d\u5de8\u9cc4\u7684\u80c3\u53e3\uff0c\u8fdb\u5165\u516b\u6708\u4e2d\u65ec\uff0c\u53c8\u5230\u4e86\u91cd\u65b0\u6311\u9009\u730e\u7269\u7684\u65f6\u673a\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u4e9a\u6d32\u5404\u56fd\u653f\u5e9c\u52a0\u5f3a\u8b66\u60d5\uff0c\u7eb7\u7eb7\u51fa\u624b\u9650\u5236\u5bf9\u672c\u56fd\u8d27\u5e01\u7684\u6295\u673a\u4ea4\u6613\u3001\u63d0\u9ad8\u6295\u673a\u4ea4\u6613\u6210\u672c\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u7684\u5bf9\u51b2\u57fa\u91d1\u6b64\u65f6\u5c31\u663e\u5f97\u6709\u4e9b\u5fae\u4e0d\u8db3\u9053\u4e86\uff0c\u4e5f\u6b63\u56e0\u4e3a\u6df9\u6ca1\u5728\u5e9e\u5927\u7684\u56fd\u9645\u6e38\u8d44\u5927\u6f6e\u4e4b\u4e2d\uff0c\u5728\u6cf0\u94e2\u4e0e\u5370\u5c3c\u76fe\u4e0a\u7f8e\u9910\u4e86\u4e00\u987f\uff0c\u4e4b\u540e\u5c31\u518d\u6ca1\u6709\u5148\u624b\u5efa\u4ed3\u7684\u673a\u4f1a\u4e86\uff0c\u53ea\u6709\u8ddf\u7740\u56fd\u9645\u6e38\u8d44\u4e3b\u529b\u50cf\u8757\u7fa4\u4e00\u6837\u5728\u4e9a\u6d32\u91d1\u878d\u5e02\u573a\u7684\u5929\u7a7a\u63a0\u8fc7\uff0c\u6709\u673a\u4f1a\u5c31\u4fef\u51b2\u4e0b\u53bb\u7f8e\u98df\u4e00\u9910\u3002</p><p>\u3000\u3000\u516b\u6708\u4e0a\u65ec\uff0c\u5f20\u606a\u7559\u5728\u6d77\u5dde\uff0c\u5728\u534a\u5c9b\u9152\u5e97\u906d\u9047\u90a3\u6837\u7684\u4e8b\u60c5\uff0c\u7ec8\u7a76\u4e0d\u80fd\u7b97\u6109\u5feb\uff0c\u4fbf\u4e00\u76f4\u7559\u5728\u6d77\u5dde\u966a\u5510\u5a67\uff0c\u9648\u5983\u84c9\u5047\u671f\u91cc\u4e5f\u56de\u5230\u6d77\u5dde\uff0c\u66f4\u591a\u65f6\u95f4\u662f\u4e09\u4e2a\u4eba\u8017\u5728\u4e00\u8d77\uff0c\u8c61\u5c71\u5357\u5d16\u4e0b\u7684\u690d\u7269\u56ed\u4e5f\u521d\u6210\u89c4\u6a21\uff0c\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u8fd9\u4e2a\u5b66\u671f\u4e5f\u8981\u6b63\u5f0f\u529e\u5b66\uff0c\u82b7\u5f64\u3001\u60dc\u5bb9\u3001\u60dc\u7fbd\u4ee5\u53ca\u9526\u6e56\u65d7\u4e0b\u9ad8\u7ea7\u804c\u5458\u7684\u5b50\u5973\u5c06\u662f\u9996\u6279\u5165\u5b66\u7684\u5b66\u751f\u3002</p><p>\u3000\u3000\u516b\u6708\u4e0a\u65ec\uff0c\u5f20\u606a\u3001\u5510\u5a67\u3001\u9648\u5983\u84c9\u4e09\u4e2a\u4eba\u5c31\u5728\u690d\u7269\u56ed\u4e0e\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u91cc\u53ae\u6df7\uff0c\u4e0d\u8fc7\u540e\u9762\u8ddf\u7740\u4e09\u4e2a\u5c0f\u5c3e\u5df4\uff0c\u7529\u90fd\u7529\u4e0d\u8131\uff0c\u5f20\u606a\u611f\u89c9\u81ea\u5df1\u6df1\u9677\u5973\u513f\u56fd\u91cc\u3002</p><p>\u3000\u3000\u7b49\u5510\u5a67\u987a\u5229\u901a\u8fc7\u9999\u6e2f\u5927\u5b66\u5230\u6d77\u5dde\u62db\u751f\u7684\u5de5\u4f5c\u4eba\u5458\u7684\u9762\u8bd5\u4e4b\u540e\uff0c\u4ed6\u624d\u518d\u5ea6\u8fd4\u56de\u9999\u6e2f\uff0c\u675c\u98de\u6bd4\u8f83\u547d\u82e6\uff0c\u4e00\u76f4\u7ed9\u4e22\u5728\u9999\u6e2f\u3002</p><p>\u3000\u3000\u5f20\u606a\u518d\u6b21\u5230\u9999\u6e2f\u65f6\uff0c\u9ad8\u8003\u6210\u7ee9\u4ee5\u53ca\u91cd\u70b9\u672c\u79d1\u7684\u5f55\u53d6\u7ebf\u4e5f\u90fd\u5df2\u7ecf\u51fa\u6765\uff0c\u4ed6\u4eec\u73ed\u7684\u6210\u7ee9\u5728\u5386\u754c\u91cd\u70b9\u6587\u79d1\u73ed\u4e2d\u7b97\u662f\u7a33\u5b9a\u53d1\u6325\u3002</p><p>\u3000\u3000\u591f\u4e0a\u4e1c\u5927\u6587\u79d1\u7ebf\u7684\u6709\u56db\u4eba\uff0c\u674e\u5b63\u3001\u8096\u6625\u94ed\u8fd8\u6709\u8bb8\u82e5\u8679\u90fd\u4e0d\u51fa\u610f\u5916\u7684\u8003\u4e0a\u4e1c\u5927\uff0c\u8fd8\u6709\u4e00\u4e2a\u5e73\u65e5\u91cc\u5f88\u6c89\u9ed8\u7684\u7537\u751f\u8003\u53d6\u4e86\u4e1c\u5927\u5916\u8bed\u7cfb\u2014\u2014\u5f20\u606a\u8fc4\u4eca\u90fd\u4e0d\u77e5\u9053\u90a3\u4e2a\u7537\u751f\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u597d\u50cf\u4e5f\u6ca1\u6709\u542c\u5230\u522b\u4eba\u63d0\u8d77\u8fc7\u4ed6\uff1b\u675c\u98de\u5dee\u4e86\u516d\u5206\uff0c\u4f46\u662f\u5728\u59d4\u57f9\u7ebf\u4e4b\u5185\uff1b\u8fd9\u662f\u4e00\u4e2a\u4ee4\u4ed6\u76f8\u5f53\u6ee1\u610f\u7684\u6210\u7ee9\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u53ea\u8981\u80fd\u628a\u4e1c\u5927\u7684\u5f55\u53d6\u901a\u77e5\u4e66\u62ff\u56de\u5bb6\uff0c\u5c31\u7b97\u5bf9\u4ed6\u5988\u6700\u5927\u7684\u4ea4\u5f85\uff1b\u52a0\u4e0a\u5f20\u606a\u8fd9\u540d\u201c\u4ee4\u4eba\u8be7\u5f02\u201d\u7684\u4fdd\u9001\u751f\u4e4b\u5916\u8fd8\u6709\u53e6\u4e00\u4e2a\u6587\u79d1\u73ed\u7684\u4e24\u540d\u59d4\u57f9\u751f\uff0c\u6d77\u5dde\u4e00\u4e2d\u5171\u6709\u516b\u540d\u6587\u79d1\u751f\u62ff\u5230\u4e1c\u5927\u7684\u5f55\u53d6\u901a\u77e5\u4e66\uff0c\u5f20\u606a\u4ed6\u4eec\u73ed\u8fd8\u6709\u4e00\u4eba\u8003\u53d6\u5317\u5927\u7ecf\u6d4e\u7ba1\u7406\u5b66\u9662\u3002</p><p>\u3000\u3000\u7701\u5185\u9ad8\u6821\u8d44\u6e90\u4e30\u5bcc\uff0c\u5f20\u606a\u4ed6\u4eec\u73ed\u5230\u5efa\u90ba\u8bfb\u5927\u5b66\u7684\u540c\u5b66\u5c31\u63a5\u8fd1\u4e00\u534a\uff0c\u8fd9\u6bb5\u65f6\u95f4\u6b63\u662f\u5168\u73ed\u540c\u5b66\u56db\u5904\u4e70\u9189\u7684\u5b63\u8282\uff0c\u675c\u98de\u5374\u7ed9\u5f20\u606a\u4e22\u5728\u9999\u6e2f\u8ddf\u53f6\u5efa\u658c\u4ed6\u4eec\u5728\u4e00\u8d77\u7ecf\u5386\u8fd9\u51e0\u5341\u5e74\u6765\u4e9a\u6d32\u7ecf\u6d4e\u53f2\u4e0a\u6700\u66b4\u8650\u3001\u5bf9\u4ed6\u4eec\u6765\u8bf4\u5374\u53c8\u6700\u5177\u6fc0\u60c5\u7684\u65f6\u523b\uff1b\u4ed6\u8001\u5934\u5b50\u675c\u5c0f\u5c71\u521a\u521a\u8c03\u5230\u5e02\u59d4\u7ec4\u7ec7\u90e8\u957f\u7684\u4f4d\u7f6e\u4e0a\uff0c\u5bf9\u4ed6\u4e5f\u662f\u4e0d\u95fb\u4e0d\u95ee\u3002</p><p>\u3000\u3000\u5c06\u8fd1\u4e8c\u5341\u5c81\u7684\u4eba\u751f\u66f4\u591a\u7f3a\u4e4f\u7684\u662f\u773c\u754c\u7684\u5f00\u62d3\u4ee5\u53ca\u5bf9\u4eba\u751f\u3001\u4e16\u754c\u3001\u793e\u4f1a\u6df1\u5165\u800c\u7ec6\u5fae\u7684\u601d\u8003\u4e0e\u89c2\u5bdf\uff0c\u867d\u7136\u675c\u98de\u8fd8\u4e00\u66fe\u575a\u6301\u8981\u6c42\u8981\u63a5\u53d7\u5fc3\u7406\u8f85\u5bfc\uff0c\u53ea\u662f\u6ca1\u6709\u4eba\u7406\u4f1a\u4ed6\uff0c\u4ed6\u4e5f\u5c31\u6ca1\u8138\u518d\u575a\u6301\u8981\u6c42\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u8fd4\u56de\u9999\u6e2f\u662f8\u670817\u65e5\uff0c\u7b2c\u4e00\u6ce2\u91d1\u878d\u98ce\u66b4\u6700\u72c2\u8650\u7684\u65f6\u523b\u5df2\u7ecf\u5439\u8fc7\u4e1c\u5357\u4e9a\u7684\u4e0a\u7a7a\uff0c\u4f46\u662f\u98ce\u66b4\u7684\u4f59\u6ce2\u8fd8\u5728\u5439\u8361\uff0c\u7fa4\u8757\u4f3c\u7684\u6e38\u8d44\u5374\u6ca1\u6709\u5c06\u6b8b\u6e23\u90fd\u653e\u5f03\u7684\u610f\u601d\uff0c\u6c47\u5e02\u3001\u671f\u8d27\u3001\u8bc1\u5238\u7b49\u91d1\u878d\u5e02\u573a\u5267\u70c8\u632f\u8361\uff0c\u4f3c\u4e4e\u8981\u5c06\u6700\u540e\u7684\u8d2a\u5a6a\u8005\u4e5f\u4e00\u5e76\u541e\u566c\u5f97\u9aa8\u8089\u4e0d\u5269\uff0c\u4e1c\u5357\u4e9a\u66fe\u7ecf\u7684\u56db\u5c0f\u864e\u5df2\u7136\u7ed9\u5543\u566c\u5f97\u53ea\u5269\u4e0b\u68ee\u68ee\u767d\u9aa8\u4e86\u3002</p><p>\u3000\u3000\u51d1\u5de7\uff0c\u5f20\u606a\u4e0e\u8c22\u5251\u5357\u3001\u8c22\u77bb\u3001\u5218\u660e\u8f89\u4e09\u4eba\u4e58\u4e1c\u65b9\u822a\u7684\u540c\u4e00\u73ed\u98de\u673a\u524d\u5f80\u9999\u6e2f\u3002</p><p>\u3000\u3000\u4e1c\u822a\u8fd9\u73ed\u822a\u73ed\u6ca1\u6709\u5934\u7b49\u8231\uff0c\u53ea\u6709\u7ecf\u6d4e\u8231\u4e0e\u5546\u52a1\u8231\uff0c\u770b\u7740\u8c22\u5251\u5357\u3001\u8c22\u77bb\u9887\u4e3a\u6194\u60b4\u63d0\u7740\u516c\u6587\u5305\u4ece\u8fc7\u9053\u5f80\u7ecf\u6d4e\u8231\u8d70\u53bb\uff0c\u5f20\u606a\u5634\u89d2\u542b\u7740\u4e00\u4e1d\u5fae\u7b11\uff0c\u4e5f\u4e0d\u662f\u8981\u6253\u62db\u547c\u7684\u610f\u601d\uff0c\u53ea\u662f\u8ba9\u8c22\u5251\u5357\u3001\u8c22\u77bb\u4e24\u4eba\u7684\u8138\u8272\u66f4\u52a0\u7684\u9634\u6c89\uff1b\u5218\u660e\u8f89\u66f4\u662f\u4f4e\u5934\u4e0d\u6562\u770b\u5f20\u606a\u4e00\u773c\u2014\u2014\u5750\u5728\u5f20\u606a\u8eab\u8fb9\u7684\u662f\u9876\u66ff\u5218\u660e\u8f89\u62c5\u4efb\u7231\u8fbe\u7535\u5b50\u5e02\u573a\u90e8\u9500\u552e\u5206\u90e8\u90e8\u957f\u8d56\u6d4e\u6625\uff0c\u4e00\u4e2a\u76f8\u5f53\u5e72\u7ec3\u7684\u9500\u552e\u5973\u5c06\uff0c\u5f53\u521d\u8fd8\u662f\u5218\u660e\u8f89\u4eb2\u81ea\u4ece\u676d\u5dde\u7684\u4e1c\u65b9\u901a\u4fe1\u5c06\u5979\u8058\u7528\u5230\u7231\u8fbe\u7535\u5b50\u5e02\u573a\u90e8\u7684\uff0c\u5148\u62c5\u4efb\u534e\u4e2d\u533a\u526f\u7ecf\u7406\uff0c\u540e\u62c5\u4efb\u534e\u4e1c\u533a\u7ecf\u7406\uff0c\u5218\u660e\u8f89\u8df3\u69fd\u53bb\u79d1\u738b\u65f6\uff0c\u8fd8\u505a\u8fc7\u5979\u7684\u5de5\u4f5c\uff0c\u88ab\u5979\u62d2\u7edd\u3002</p><p>\u3000\u3000\u6b64\u60c5\u6b64\u5f62\uff0c\u8ba9\u5218\u660e\u8f89\u4f55\u4ee5\u582a\u4e4b\uff1f\u552f\u6709\u4f4e\u5934\u9eef\u7136\u8d70\u8fc7\u53bb\uff1b\u624d\u8fc7\u53bb\u4e5d\u4e2a\u591a\u6708\u7684\u65f6\u95f4\u800c\u5df2\u3002</p><p>\u3000\u3000\u6cf0\u56fd\u7684\u7ecf\u6d4e\u5371\u673a\u4ece\u516d\u6708\u4e2d\u65ec\u5c31\u521d\u73b0\u7aef\u502a\uff0c\u4e03\u6708\u4e2d\u65ec\u5c31\u7a81\u7136\u5439\u88ad\u6574\u4e2a\u4e1c\u5357\u4e9a\u5730\u533a\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u7559\u7ed9\u79d1\u738b\u8c03\u6574\u6d77\u5916\u4e1a\u52a1\u7684\u65f6\u95f4\uff0c\u6b64\u65f6\u4ed3\u4fc3\u7f29\u51cf\uff0c\u53ea\u4f1a\u635f\u5931\u60e8\u91cd\uff0c\u5f53\u7136\uff0c\u4ed6\u4eec\u6210\u529f\u7684\u5c06\u6700\u5927\u7684\u635f\u5931\u79fb\u52a8\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u7684\u5934\u4e0a\uff0c\u4f46\u662f\u79d1\u738b\u5374\u5df2\u7ecf\u611f\u53d7\u5230\u5e93\u5b58\u79ef\u538b\u7684\u538b\u529b\u3002\u79d1\u738b\u789f\u673a\u4e03\u6708\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u9500\u91cf\u6bd4\u516d\u6708\u9510\u51cf\u4e24\u6210\uff0c\u8fd9\u8fd8\u4e0d\u8ba1\u5212\u4e1c\u5357\u4e9a\u5f53\u5730\u7ecf\u9500\u5546\u624b\u91cc\u7684\u5b58\u8d27\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5fc3\u91cc\u5f53\u7136\u90c1\u95f7\uff0c\u6709\u4e0e\u5f20\u606a\u4e58\u540c\u4e00\u73ed\u98de\u673a\u81ea\u5df1\u4e3a\u4e86\u7f29\u51cf\u5f00\u652f\u5374\u8981\u53bb\u5750\u7ecf\u6d4e\u8231\u7684\u90c1\u95f7\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u867d\u7136\u7231\u8fbe\u7535\u5b50\u4e8e7\u670811\u65e5\u624d\u53d1\u5e03\u7ecf\u6d4e\u5371\u673a\u9884\u8b66\u8c03\u6574\u4e1c\u5357\u4e9a\u5730\u533a\u4e1a\u52a1\u7684\u6d88\u606f\uff0c\u4f46\u662f\u4ed6\u4eec\u65e9\u5c31\u5728\u4e94\u6708\u5c31\u5f00\u59cb\u8c03\u6574\u4e1c\u5357\u4e9a\u7684\u4e1a\u52a1\u7ed3\u6784\uff0c\u524a\u51cf\u5e02\u573a\u5ba3\u4f20\u6295\u5165\u7684\u540c\u65f6\uff0c\u52a0\u5f3a\u4e86\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u6e20\u9053\u5546\u5efa\u8bbe\u4e0e\u5e93\u5b58\u7ba1\u7406\uff0c\u5728\u516d\u6708\u521d\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u6e20\u9053\u5546\u7eb7\u7eb7\u589e\u52a0\u8ba2\u5355\u7684\u60c5\u51b5\u4e0b\uff0c\u7231\u8fbe\u7535\u5b50\u5374\u505c\u6b62\u589e\u52a0\u5bf9\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u53d1\u8d27\u91cf\uff0c\u516d\u6708\u4e2d\u65ec\u751a\u81f3\u65ad\u7136\u653e\u5f03\u5370\u5c3c\u7684\u5e02\u573a\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u5bf9\u5e02\u573a\u72b6\u51b5\u9ad8\u77bb\u8fdc\u77a9\u7684\u9884\u89c1\u6027\uff0c\u4efb\u662f\u8c01\u5f53\u7231\u8fbe\u7535\u5b50\u7684\u7ade\u4e89\u5bf9\u624b\u5fc3\u91cc\u90fd\u4f1a\u6781\u5ea6\u90c1\u95f7\uff0c\u5374\u662f\u4e0d\u5f97\u4e0d\u53f9\u670d\u7684\uff0c\u4e5f\u8ba9\u79d1\u738b\u4f17\u4eba\u8fdb\u4e00\u6b65\u5931\u53bb\u5bf9\u6297\u7231\u8fbe\u7535\u5b50\u7684\u4fe1\u5fc3\uff1b\u5373\u4f7f\u7231\u8fbe\u7535\u5b50\u771f\u6709\u4ec0\u4e48\u4e0d\u6d4e\uff0c\u9526\u6e56\u8d26\u4e0a\u8fd8\u5b89\u9759\u7684\u8eba\u7740\u56db\u5341\u4ebf\u7684\u5de8\u8d44\u5462\u3002</p><p>\u3000\u3000\u867d\u7136\u9526\u6e56\u62ac\u8d77\u811a\u5e76\u6ca1\u6709\u8e29\u4ed6\u4eec\u7684\u610f\u601d\uff0c\u4f46\u662f\u6b64\u65f6\u53ea\u80fd\u770b\u7740\u522b\u4eba\u7684\u811a\u5e95\u677f\uff0c\u8c01\u7684\u5fc3\u60c5\u4f1a\u597d\u53d7\uff1f</p><p>\u3000\u3000\u8d56\u6d4e\u6625\u5750\u5728\u8fc7\u9053\u8fb9\u4e0a\u7684\u4f4d\u7f6e\uff0c\u5fcd\u4e0d\u4f4f\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u76f4\u5230\u8c22\u5251\u5357\u3001\u8c22\u77bb\u3001\u5218\u660e\u8f89\u8d70\u51fa\u5546\u52a1\u8231\u7684\u8fc7\u9053\uff0c\u8f7b\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\u3002</p><p>\u3000\u3000\u5979\u662f\u7b2c\u4e00\u6b21\u966a\u7740\u5927\u8001\u677f\u4e58\u98de\u673a\u51fa\u5dee\u53bb\u9999\u6e2f\u3002\u867d\u7136\u9526\u6e56\u540d\u4e49\u4e0a\u4e0e\u7231\u8fbe\u7535\u5b50\u4e0d\u518d\u6709\u8d22\u52a1\u4e0a\u7684\u8054\u7cfb\u2014\u2014\u8c22\u665a\u6674\u7684\u6d77\u88d5\u516c\u53f8\u624d\u662f\u7231\u8fbe\u7535\u5b50\u7684\u5b9e\u9645\u63a7\u5236\u4eba\uff0c\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u5185\u90e8\u7684\u4eba\u4ece\u6765\u90fd\u4e0d\u8ba4\u4e3a\u81ea\u5df1\u5df2\u7ecf\u8131\u79bb\u4e86\u9526\u6e56\u8fd9\u4e2a\u4f53\u7cfb\uff0c\u5728\u789f\u673a\u4e1a\u52a1\u4e0a\u4ecd\u7136\u63a5\u53d7\u7231\u8fbe\u96c6\u56e2\u8fd0\u8425\u90e8\u7684\u6307\u5bfc\u5373\u4e3a\u660e\u8bc1\u3002</p><p>\u3000\u3000\u5979\u73b0\u5728\u8fd8\u80fd\u60f3\u8d77\u5218\u660e\u8f89\u5f53\u521d\u9f13\u52a8\u5979\u8df3\u69fd\u53bb\u79d1\u738b\u65f6\u70ed\u60c5\u6d0b\u6ea2\u7684\u8bf4\u8f9e\uff0c\u53ea\u6015\u4ed6\u5f53\u521d\u4e5f\u6ca1\u6709\u5c11\u5bf9\u5176\u4ed6\u4eba\u8bf4\u8d77\uff0c\u90a3\u756a\u70ed\u60c5\u6d0b\u6ea2\u7684\u8bf4\u8f9e\u5df2\u6210\u7b11\u8c08\uff0c\u5012\u662f\u65e0\u4eba\u518d\u613f\u63d0\u8d77\uff0c\u60f3\u6765\u5218\u660e\u8f89\u4e5f\u8981\u7b97\u7231\u8fbe\u7535\u5b50\u521b\u4e1a\u7684\u5143\u8001\uff0c\u82e5\u662f\u4e0d\u79bb\u5f00\u9526\u6e56\uff0c\u50cf\u5218\u660e\u8f89\u90a3\u7ea7\u522b\u7684\u9ad8\u5c42\u7ba1\u7406\u4eba\u5458\uff0c\u8c01\u5728\u8fd9\u6b21\u589e\u53d1\u5e76\u8d2d\u91cc\u6240\u83b7\u5f97\u7684\u80a1\u6743\u5c11\u8fc7\u4e24\u4e09\u767e\u4e07\uff1f</p><p>\u3000\u3000\u201c\u79d1\u738b\u73b0\u5728\u7684\u5904\u5883\u6709\u4e9b\u56f0\u96be\u5427\uff01\u201d\u5f20\u606a\u89c1\u8d56\u6d4e\u6625\u56de\u5934\u5f20\u671b\uff0c\u968f\u610f\u8bf4\u4e86\u4e00\u95ee\u3002</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u8d56\u6d4e\u6625\u5e94\u4e86\u4e00\u58f0\uff0c\u795e\u7ecf\u7acb\u5373\u7ef7\u7d27\u8d77\u6765\uff0c\u5fc3\u60f3\u662f\u4e0d\u662f\u56de\u5934\u8fd9\u4e00\u770b\uff0c\u8ba9\u5927\u8001\u677f\u770b\u5728\u773c\u91cc\u4e86\u3002\u5176\u5b9e\u8ddf\u7740\u5927\u8001\u677f\u4e00\u8d77\u5750\u98de\u673a\u53bb\u9999\u6e2f\uff0c\u538b\u529b\u662f\u5f88\u5927\u7684\uff0c\u4e0d\u6653\u5f97\u7a81\u7136\u4f1a\u5192\u51fa\u4e00\u53e5\u4ec0\u4e48\u95ee\u9898\uff0c\u53cd\u5e94\u6162\u4e86\uff0c\u90fd\u611f\u89c9\u5bf9\u4e0d\u8d77\u81ea\u5df1\u62ff\u7684\u90a3\u4efd\u85aa\u6c34\uff0c\u5c0f\u5fc3\u7684\u9009\u62e9\u63aa\u8f9e\u5e94\u5bf9\uff0c\u8bf4\u9053\uff0c\u201c\u79d1\u738b\u4e0a\u534a\u5e74\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u6295\u5165\u90a3\u4e48\u5927\uff0c\u538b\u529b\u662f\u5f88\u5927\uff01\u5176\u4ed6\u7684\u6682\u4e14\u4e0d\u8bf4\uff0c\u4e1c\u5357\u4e9a\u5730\u533a\u8d27\u5e01\u5927\u5e45\u8d2c\u503c\uff0c\u5bf9\u789f\u673a\u5382\u5546\u6784\u6210\u6700\u76f4\u63a5\u7684\u538b\u529b\uff0c\u5c31\u662f\u5728\u8be5\u5730\u533a\u9500\u552e\u5546\u54c1\u4e5f\u8ddf\u7740\u5927\u5e45\u8d2c\u503c\u3002\u9488\u5bf9\u6cf0\u94e2\u3001\u5370\u5c3c\u76fe\u7684\u9500\u552e\u4ef7\u683c\u4e0d\u80fd\u63d0\u9ad8\uff0c\u6cf0\u94e2\u8d2c\u503c30\uff05\uff0c\u4e00\u53f0\u789f\u673a\u5728\u6cf0\u56fd\u9500\u552e\u7684\u7f8e\u5143\u4ef7\u683c\u3001\u4eba\u6c11\u5e01\u4ef7\u683c\u3001\u6e2f\u5143\u4ef7\u683c\u5b9e\u9645\u4e0a\u76f8\u5f53\u4e8e\u8981\u8d2c\u503c30\uff05\u3002\u73b0\u5728\u789f\u673a\u884c\u4e1a\u7684\u5e73\u5747\u6bdb\u5229\u6da6\u90fd\u6ca1\u6709\u523030\uff05\uff0c\u4ec5\u6c47\u7387\u635f\u5931\u5c31\u8db3\u4ee5\u8ba9\u4ed6\u4eec\u5934\u75bc\u4e86\uff1b\u8fd9\u65f6\u5019\u5927\u6982\u5c31\u80fd\u5f7b\u5e95\u7684\u4f53\u73b0\u5236\u9020\u6210\u672c\u4f18\u52bf\u4e86\u3002\u6211\u4eec\u53ef\u4ee5\u5927\u5e45\u5ea6\u8c03\u4f4e\u5bf9\u4e1c\u5357\u4e9a\u7684\u53d1\u8d27\u4ef7\u683c\uff0c\u4fdd\u8bc1\u7f8e\u5143\u7ed3\u7b97\u7684\u60c5\u51b5\u4e0b\uff0c\u4e1c\u5357\u4e9a\u7ecf\u9500\u5546\u4ecd\u6709\u4e00\u5b9a\u7684\u5fae\u8584\u5229\u6da6\u80fd\u652f\u6491\u4f4f\u7231\u8fbe\u789f\u673a\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u5e02\u573a\u2014\u2014\u4ed6\u4eec\u8ddf\u8fdb\u5c31\u8981\u4e8f\u635f\u8fd0\u8425\uff0c\u53ef\u80fd\u4e5f\u4f1a\u9009\u62e9\u5f7b\u5e95\u653e\u5f03\u4e1c\u5357\u4e9a\u5e02\u573a\uff0c\u8fd9\u65f6\u5019\u8fd8\u5f88\u96be\u63a8\u6d4b\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u65e0\u826f\u7684\u7b11\u4e86\u7b11\uff0c\u5f53\u521d\u53ef\u662f\u4ed6\u4e00\u5fc3\u8bf1\u4f7f\u79d1\u738b\u53bb\u52aa\u529b\u5f00\u53d1\u4e1c\u5357\u4e9a\u5730\u533a\u5e02\u573a\u7684\uff0c\u8fd9\u65f6\u5019\u518d\u8bf4\u4e00\u4e9b\u51b7\u5632\u70ed\u8bbd\u7684\u8bdd\uff0c\u5c31\u6709\u4e9b\u592a\u5c0f\u5fc3\u773c\u4e86\uff0c\u7b11\u7740\u8ddf\u8d56\u6d4e\u6625\u8bf4\uff0c\u201c\u5370\u5c3c\u90a3\u8fb9\u7684\u5e02\u573a\u5c31\u4e0d\u53bb\u7ba1\u4ed6\u7684\uff0c\u4efb\u5176\u8d25\u843d\uff0c\u6211\u4eec\u8981\u505a\u597d\u4e24\u70b9\uff0c\u4e00\u65b9\u9762\u7ee7\u7eed\u7ef4\u6301\u83f2\u5f8b\u5bbe\u4e0e\u9a6c\u6765\u897f\u4e9a\u7684\u5de5\u5382\uff0c\u589e\u52a0\u90a3\u4e24\u5bb6\u5de5\u5382\u5bf9\u5468\u8fb9\u5730\u65b9\u7684\u53d1\u8d27\u91cf\u4ee5\u5f25\u8865\u6c47\u7387\u635f\u5931\uff0c\u4e00\u65b9\u9762\uff0c\u5728\u8fd9\u6837\u8270\u96be\u7684\u65f6\u523b\u8981\u5c3d\u53ef\u80fd\u5e2e\u52a9\u4e1c\u5357\u4e9a\u7684\u90a3\u4e9b\u7ecf\u9500\u5546\u6e21\u8fc7\u53ef\u80fd\u4f1a\u7ef4\u6301\u4e00\u5e74\u751a\u81f3\u66f4\u4e45\u65f6\u95f4\u7684\u96be\u5173\u3002\u8fd8\u6709\u554a\uff0c\u8fd9\u6bb5\u65f6\u95f4\uff0c\u4e1c\u5357\u4e9a\u5404\u56fd\u7684\u95e8\u6249\u57fa\u672c\u662f\u7ed9\u653b\u5f00\u7684\uff0c\u8d81\u7740\u8fd9\u6bb5\u7279\u6b8a\u7684\u65f6\u671f\u5c06\u6570\u5b57\u624b\u673a\u5728\u8fd9\u4e9b\u5730\u533a\u7684\u9500\u552e\u8bb8\u53ef\u62ff\u5230\u624b\u6216\u8bb8\u4f1a\u7b80\u5355\u4e00\u4e9b\uff0c\u8fd9\u65b9\u9762\uff0c\u4f60\u4eec\u8981\u8ddf\u4e1c\u5357\u4e9a\u5f53\u5730\u7684\u7ecf\u9500\u5546\u8054\u5408\u8d77\u6765\u505a\u5de5\u4f5c\u2026\u2026\u201d</p></article>"}}}]);
