(window.webpackJsonp=window.webpackJsonp||[]).push([[755],{778:function(p){p.exports={chapterTitle:null,title:"\u7b2c591\u7ae0 \u51b0\u706b\u4e24\u91cd\u5929",content:"<article><h2>\u7b2c591\u7ae0 \u51b0\u706b\u4e24\u91cd\u5929</h2><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u4e0e\u4e1c\u5357\u4e9a\u5404\u56fd\u7684\u7ecf\u9500\u5546\u867d\u8bf4\u4ee5\u7f8e\u5143\u7ed3\u7b97\uff0c\u4f46\u662f\u5728\u4e1c\u5357\u4e9a\u5404\u56fd\u8d27\u5e01\u9646\u7eed\u8d2c\u503c\u4e4b\u540e\uff0c\u4e5f\u76f8\u5e94\u7684\u5927\u5e45\u8c03\u4f4e\u53d1\u8d27\u4ef7\u683c\uff0c\u4ee5\u4fdd\u8bc1\u90a3\u4e9b\u7ecf\u9500\u5546\u4e0e\u83f2\u5f8b\u5bbe\u3001\u9a6c\u6765\u897f\u4e9a\u7684\u5de5\u5382\u80fd\u6709\u5fae\u8584\u7684\u5229\u6da6\u4ee5\u7ef4\u6301\u5f53\u524d\u7684\u4e1a\u52a1\u3002</p><p>\u3000\u3000\u4ec5\u6cf0\u56fd\u7684\u5e02\u573a\uff0c\u6c47\u7387\u635f\u5931\u5c31\u8981\u63a5\u8fd130\uff05\uff0c\u7231\u8fbe\u7535\u5b50\u5f53\u7136\u4e0d\u4f1a\u767e\u5206\u767e\u7684\u8865\u8d34\u7ecf\u9500\u5546\u7684\u6c47\u7387\u635f\u5931\uff0c\u4f46\u662f\u5c06\u53d1\u8d27\u4ef7\u683c\u8c03\u4f4e20\uff05\uff0c\u76f8\u5f53\u4e8e\u627f\u62c5\u4e86\u4e09\u5206\u4e4b\u4e8c\u7684\u6c47\u7387\u635f\u5931\uff0c\u53c8\u63d0\u524d\u4e24\u4e2a\u6708\u4ee5\u524a\u51cf\u53d1\u8d27\u91cf\u7684\u5f62\u5f0f\u5f3a\u884c\u964d\u4f4e\u7ecf\u9500\u5546\u7684\u5e93\u5b58\uff0c\u5f53\u91d1\u878d\u98ce\u66b4\u5728\u4e1c\u5357\u4e9a\u7684\u4e0a\u7a7a\u8086\u8650\u7684\u65f6\u5019\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u7ecf\u9500\u5546\u8fdc\u6bd4\u5176\u4ed6\u789f\u673a\u5382\u5546\u7684\u7ecf\u9500\u5546\u6d3b\u5f97\u6ecb\u6da6\uff1b\u52d2\u7d27\u88e4\u8170\u5e26\u8fc7\u65e5\u5b50\u662f\u4e00\u56de\u4e8b\uff0c\u9877\u523b\u95f4\u6fd2\u4e34\u7834\u4ea7\u7684\u8fb9\u7f18\u5219\u662f\u53e6\u4e00\u79cd\u6781\u7aef\u7684\u4f53\u9a8c\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u7684\u8fd9\u4e9b\u60c5\u51b5\uff0c\u8c22\u5251\u5357\u65e9\u4ece\u79d1\u738b\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u9500\u552e\u6e20\u9053\u4e86\u89e3\u5230\uff0c\u4ece\u4e0a\u98de\u673a\u770b\u5230\u5f20\u606a\u60a0\u7136\u81ea\u5f97\u7684\u5750\u5728\u5546\u52a1\u8231\u91cc\uff0c\u4ed6\u4e00\u76f4\u90fd\u6c89\u9ed8\u5be1\u8a00\uff0c\u4e0b\u98de\u673a\u65f6\uff0c\u6545\u610f\u78e8\u8e6d\u7740\uff0c\u514d\u5f97\u518d\u4e0e\u5f20\u606a\u6709\u5c34\u5c2c\u7684\u78b0\u9762\uff0c\u66fe\u51e0\u4f55\u65f6\uff0c\u4ed6\u4e5f\u7ed9\u201c\u5929\u624d\u5c11\u5e74\u201d\u3001\u201c\u5e02\u573a\u5947\u8ff9\u4e4b\u624b\u201d\u7c7b\u4f3c\u7684\u8d5e\u8a89\u6df9\u6ca1\uff0c\u5982\u4eca\u518d\u8981\u63d0\u8d77\u6765\uff0c\u4f3c\u4e4e\u53ea\u662f\u60f9\u4eba\u803b\u7b11\u7684\u7b11\u8c08\uff1b\u81f3\u5c11\u4ed6\u5fc3\u91cc\u662f\u8fd9\u4e48\u60f3\u7684\u3002</p><p>\u3000\u3000\u8c22\u77bb\u3001\u5218\u660e\u8f89\u4e5f\u4e0e\u8c22\u5251\u5357\u4e00\u6837\u7684\u5fc3\u601d\uff0c\u4e0b\u98de\u673a\u540e\uff0c\u4e09\u4e2a\u4eba\u7ad9\u5728\u884c\u674e\u8f93\u9001\u67b6\u524d\uff0c\u80fd\u900f\u8fc7\u900f\u660e\u7684\u73bb\u7483\u770b\u5230\u5f20\u606a\u4e00\u884c\u529e\u5b8c\u624b\u7eed\u8d70\u8fdb\u63a5\u673a\u5927\u5385\uff0c\u4f3c\u4e4e\u6709\u6240\u611f\u5e94\u4f3c\u7684\uff0c\u5f20\u606a\u56de\u5934\u8f6c\u4ed6\u4eec\u7b11\u4e86\u7b11\uff0c\u53ea\u662f\u90a3\u4e00\u4e1d\u7b11\u5bb9\u662f\u6302\u5728\u5634\u5507\u7684\u4e00\u89d2\uff0c\u65e0\u8bba\u5982\u4f55\u90fd\u4e0d\u4f1a\u7406\u89e3\u6210\u5584\u610f\u7684\u62db\u547c\u3002</p><p>\u3000\u3000\u8c22\u77bb\u6c14\u607c\u7684\u8981\u5c06\u884c\u674e\u7bb1\u7838\u8fc7\u53bb\uff0c\u5982\u679c\u80fd\u5ffd\u89c6\u4e09\u56db\u5341\u7c73\u7684\u8ddd\u79bb\u4e0e\u4e2d\u95f4\u7684\u94a2\u5316\u73bb\u7483\u53ca\u5ddd\u6d41\u4e0d\u606f\u4e0b\u98de\u673a\u7684\u4eba\u7fa4\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u53d7\u7a9d\u56ca\u6c14\uff0c\u5c31\u7b97\u518d\u8282\u7ea6\u5f00\u652f\uff0c\u4e5f\u4e0d\u9700\u8981\u6211\u4eec\u5750\u4ec0\u4e48\u7ecf\u6d4e\u8231\uff1f\u201d</p><p>\u3000\u3000\u201c\u597d\u4e86\uff0c\u201d\u8c22\u5251\u5357\u8fd8\u80fd\u6709\u5e73\u9759\u7684\u58f0\u97f3\uff0c\u201c\u5c31\u7b97\u5750\u5230\u5546\u52a1\u8231\uff0c\u4f60\u5fc3\u91cc\u4f1a\u66f4\u597d\u53d7\u4e9b\uff1f\u201d\u4ed6\u8fd9\u65f6\u5019\u5df2\u7ecf\u770b\u89c1\u9648\u9759\u4e0e\u5b50\u5609\u7ad9\u5728\u63a5\u673a\u5927\u5385\u91cc\u3002</p><p>\u3000\u3000\u8c22\u77bb\u6487\u6487\u5634\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u7684\u786e\uff0c\u4e0e\u5f20\u606a\u4e00\u884c\u4eba\u540c\u5750\u4e00\u4e2a\u8231\u5ba4\u5185\uff0c\u5fc3\u60c5\u4f1a\u66f4\u538b\u6291\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u9759\u4e0e\u8c22\u5b50\u5609\u7ad9\u5728\u5bbd\u655e\u660e\u4eae\u7684\u63a5\u673a\u5927\u5385\u91cc\uff0c\u53f6\u5efa\u658c\u3001\u675c\u98de\u8ddf\u5728\u5979\u4eec\u540e\u9762\u8fdb\u4e86\u63a5\u673a\u5927\u5385\uff0c\u624d\u6653\u5f97\u5f20\u606a\u4e0e\u8c22\u5251\u5357\u4ed6\u4eec\u662f\u4e58\u540c\u4e00\u73ed\u98de\u673a\u5230\u9999\u6e2f\uff0c\u5f20\u606a\u4f3c\u4e4e\u80fd\u7406\u89e3\u5979\u7684\u5904\u5883\uff0c\u53ea\u662f\u70b9\u5934\u7b11\u4e86\u7b11\uff0c\u4e00\u884c\u4eba\u5c31\u4e0e\u53f6\u5efa\u658c\u3001\u675c\u98de\u79bb\u5f00\uff0c\u4e5f\u6ca1\u6709\u505c\u4e0b\u6765\u8ddf\u9648\u9759\u5bd2\u6684\u4e24\u53e5\uff1b\u9648\u9759\u4e5f\u53ea\u662f\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff0c\u5f88\u52c9\u5f3a\uff0c\u5012\u4e0d\u662f\u5bf9\u5f20\u606a\u6709\u4ec0\u4e48\u6028\u610f\uff0c\u53ea\u662f\u6015\u7b11\u5bb9\u843d\u5728\u8c22\u5251\u5357\u4ed6\u4eec\u773c\u91cc\u4f1a\u8ba9\u4ed6\u4eec\u7684\u611f\u89c9\u4e0d\u592a\u597d\u3002</p><p>\u3000\u3000\u4f55\u8c13\u51b0\u706b\u4e24\u91cd\u5929\uff0c\u5927\u6982\u80fd\u62ff\u6765\u55bb\u6307\u79d1\u738b\u4e0e\u7231\u8fbe\u7535\u5b50\u6b64\u65f6\u7684\u5904\u5883\uff0c\u6b64\u65f6\u7684\u4eb2\u5207\uff0c\u5927\u6982\u843d\u5728\u522b\u4eba\u7684\u773c\u91cc\uff0c\u4f1a\u6709\u4e9b\u4e0d\u5408\u65f6\u5b9c\u5427\u3002</p><p>\u3000\u3000\u5728\u5f20\u606a\u6389\u5934\u79bb\u5f00\u4e4b\u540e\uff0c\u9648\u9759\u5374\u53c8\u6709\u4e9b\u540e\u6094\uff0c\u5fc3\u60f3\u81ea\u5df1\u7684\u7b11\u5bb9\u4f1a\u4e0d\u4f1a\u592a\u52c9\u5f3a\u4e86\u4e9b\u3002</p><p>\u3000\u3000\u8c22\u5b50\u77bb\u4e00\u809a\u5b50\u6028\u6c14\uff0c\u6c14\u607c\u7684\u8bf4\u9053\uff1a\u201c\u62fd\u4ec0\u4e48\u561b\uff0c\u5f53\u4eba\u5bb6\u8ddf\u7a7a\u6c14\u4f3c\u7684\uff01\u201d\u4e0a\u4e00\u56de\u5728\u534a\u5c9b\u9152\u5e97\u91cc\uff0c\u4e5f\u662f\u7ed9\u5f20\u606a\u4e00\u8bed\u4e0d\u53d1\u7684\u4e22\u5728\u90a3\u91cc\uff0c\u5973\u5b69\u5b50\u603b\u53d7\u4e0d\u4e86\u522b\u4eba\u7684\u8f7b\u89c6\u3002\u4e0d\u8fc7\u7b49\u8c22\u5251\u5357\u3001\u8c22\u77bb\u4ed6\u4eec\u8d70\u8fc7\u6765\uff0c\u5979\u53c8\u8bf4\u9053\uff1a\u201c\u516d\u54e5\uff0c\u4f60\u4eec\u600e\u4e48\u8fd9\u4e48\u5012\u9709\uff0c\u8ddf\u90a3\u5bb6\u4f19\u4e58\u540c\u4e00\u73ed\u98de\u673a\uff1f\u201d\u8fd9\u662f\u6545\u610f\u5728\u622e\u8c22\u5251\u5357\u7684\u8138\uff0c\u8c22\u5251\u5357\u542c\u4e86\u5fc3\u91cc\u62bd\u62bd\u7684\uff0c\u53c8\u4e0d\u80fd\u5bf9\u5b50\u5609\u8bf4\u4ec0\u4e48\uff0c\u5fc3\u91cc\u66f4\u662f\u90c1\u6092\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u76f4\u63a5\u671d\u63a5\u673a\u5927\u5385\u5916\u8d70\u53bb\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u6ca1\u6709\u8fc7\u6765\u63a5\u673a\uff0c\u4e0d\u8fc7\u5c06\u4ed6\u7684\u90a3\u8f86\u6797\u80af\u6d3e\u6765\uff0c\u8ba9\u5728\u7ecf\u6d4e\u8231\u79ef\u7d2f\u8bb8\u591a\u90c1\u6092\u7684\u8c22\u5251\u5357\u3001\u8c22\u77bb\u7b49\u4eba\u5fc3\u91cc\u7a0d\u5fae\u597d\u53d7\u4e00\u4e9b\uff1b\u845b\u5bb6\u5728\u4e1c\u5357\u4e9a\u6709\u8bb8\u591a\u8d44\u4ea7\uff0c\u5206\u5bb6\u540e\uff0c\u4e5f\u662f\u845b\u660e\u4fe1\u8fd9\u4e00\u7cfb\u5206\u6389\u5927\u534a\uff0c\u98ce\u66b4\u88ad\u6765\uff0c\u4e5f\u4e0d\u53ef\u907f\u514d\u7684\u6b63\u906d\u53d7\u5de8\u5927\u7684\u635f\u5931\u3002</p><p>\u3000\u3000\u6bd4\u8f83\u8d77\u845b\u5bb6\u5e9e\u5927\u7684\u8d44\u4ea7\uff0c\u6d77\u5916\u516c\u53f8\u7684\u8fd9\u70b9\u635f\u5931\u5012\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u90a3\u4e9b\u5e9e\u5927\u7684\u8d85\u8fc7\u767e\u4ebf\u7684\u8d44\u4ea7\u8fd8\u90fd\u662f\u845b\u660e\u4fe1\u540d\u4e0b\u7684\uff0c\u845b\u836b\u5747\u5374\u662f\u5c06\u4ed6\u6240\u6709\u7684\u79c1\u623f\u94b1\u90fd\u6295\u8fdb\u4e86\u6d77\u5916\u516c\u53f8\uff0c\u504f\u504f\u5728\u91d1\u878d\u98ce\u66b4\u524d\u5915\u5c06\u6d77\u5916\u516c\u53f8\u7684\u63a7\u80a1\u63d0\u9ad8\u523085\uff05\u53c8\u6295\u8fdb\u8fd1\u4e24\u4ebf\u6e2f\u5143\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u5750\u8fdb\u8f66\u91cc\uff0c\u9648\u9759\u89c1\u66ae\u8272\u5df2\u669d\uff0c\u8bf4\u9053\uff1a\u201c\u5148\u53bb\u5bbe\u9986\u4f11\u606f\u4e00\u4e0b\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u53bb\u516c\u53f8\u2026\u2026\u201d\u8c22\u5251\u5357\u5f88\u5e73\u6de1\u7684\u8bf4\u9053\uff0c\u62b1\u80f8\u76b1\u7709\u6c89\u601d\uff0c\u6ca1\u6709\u8bf4\u592a\u591a\u8bdd\u7684\u610f\u613f\u3002\u9648\u9759\u4e5f\u5c31\u987a\u4ece\u4ed6\u7684\u610f\u601d\uff0c\u5429\u5490\u53f8\u673a\u76f4\u63a5\u5f00\u8f66\u53bb\u4e2d\u73af\u94f6\u53a6\u5927\u697c\u3002</p><p>\u3000\u3000\u201c\u845b\u836b\u5747\u662f\u4ec0\u4e48\u610f\u89c1\uff1f\u201d\u5f85\u8f66\u53a2\u4e0e\u53f8\u673a\u5ba4\u4e4b\u95f4\u7684\u9694\u677f\u5347\u8d77\u6765\uff0c\u8c22\u5251\u5357\u624d\u95ee\u9648\u9759\u8fd9\u8fb9\u7684\u60c5\u51b5\u3002</p><p>\u3000\u3000\u201c\u7231\u8fbe\u7535\u5b50\u6240\u91c7\u53d6\u7684\u63aa\u65bd\uff0c\u4f60\u4eec\u4e5f\u77e5\u9053\u2014\u2014\u845b\u836b\u5747\u7684\u610f\u89c1\uff0c\u81ea\u7136\u662f\u5c06\u6c47\u7387\u90e8\u5206\u7684\u635f\u5931\u76f4\u63a5\u8f6c\u5ac1\u5230\u672c\u90e8\u7684\u5934\u4e0a\u2026\u2026\u201d\u9648\u9759\u8bf4\u9053\u3002</p><p>\u3000\u3000\u79d1\u738b\u672c\u90e8\u4e0e\u6d77\u5916\u516c\u53f8\u4e4b\u95f4\u662f\u91c7\u53d6\u4eba\u6c11\u5e01\u7ed3\u7b97\uff0c\u6d77\u5916\u516c\u53f8\u4e0e\u4e1c\u5357\u4e9a\u5404\u56fd\u7684\u7ecf\u9500\u5546\u90fd\u662f\u91c7\u53d6\u5f53\u5730\u8d27\u5e01\u7ed3\u7b97\uff0c\u7531\u6d77\u5916\u516c\u53f8\u8d1f\u8d23\u5e01\u79cd\u5151\u6362\uff0c\u4e4b\u524d\u5927\u91cf\u7684\u5e93\u5b58\u5c31\u7ed9\u6d77\u5916\u516c\u53f8\u9020\u6210\u6570\u5343\u4e07\u7684\u6c47\u7387\u635f\u5931\uff0c\u5173\u952e\u6cf0\u94e2\u7b49\u4e1c\u5357\u4e9a\u8d27\u5e02\u8d2c\u503c\u5df2\u7ecf\u4e8b\u5b9e\uff0c\u79d1\u738b\u672c\u90e8\u4e0d\u964d\u4f4e\u53d1\u8d27\u4ef7\u683c\uff0c\u6d77\u5916\u516c\u53f8\u5c31\u8981\u5168\u989d\u627f\u62c5\u6c47\u7387\u635f\u5931\u3002\u6d77\u5916\u516c\u53f8\u622a\u7559\u7684\u6bdb\u5229\u6da6\u624d10\uff05\u5de6\u53f3\uff0c\u5c31\u7b97\u4e1c\u5357\u4e9a\u5404\u56fd\u7684\u8d27\u5e01\u4e0d\u518d\u7ee7\u7eed\u8d2c\u503c\uff0c\u6d77\u5916\u516c\u53f8\u8fd8\u8981\u627f\u53d7\u5927\u7ea630\uff05\u7684\u6c47\u7387\u635f\u5931\uff0c\u53ea\u6015\u7528\u4e0d\u4e86\u534a\u5e74\uff0c\u6d77\u5916\u516c\u53f8\u5c31\u8981\u6210\u8d1f\u8d44\u4ea7\u4e86\uff0c\u845b\u836b\u5747\u5f53\u7136\u4e0d\u4f1a\u5f53\u8fd9\u4e2a\u8089\u5934\u3002</p><p>\u3000\u3000\u201c\u4ed6\u60f3\u5f97\u5012\u7f8e\uff0c\u635f\u5931\u8f6c\u79fb\u7ed9\u6211\u4eec\uff0c\u79d1\u738b\u6bcf\u5f80\u6d77\u5916\u516c\u53f8\u53d1\u4e00\u4ef6\u8d27\uff0c\u5c31\u8981\u4e8f\u4e00\u767e\u591a\uff0c\u8fd8\u4e0d\u5982\u5c06\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\u5f7b\u5e95\u653e\u5f03\u6389\u2026\u2026\u201d\u8c22\u77bb\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u4ed6\u662f\u9876\u770b\u4e0d\u8d77\u845b\u836b\u5747\u8fd9\u79cd\u4eba\u7684\uff0c\u201c\u6211\u76f8\u4fe1\u5176\u4ed6\u789f\u673a\u5382\u5546\u4e5f\u4f1a\u505a\u540c\u6837\u7684\u9009\u62e9\uff0c\u8c01\u77e5\u9053\u4e1c\u5357\u4e9a\u7684\u7ecf\u6d4e\u4f1a\u8870\u9000\u5230\u4ec0\u4e48\u65f6\u5019\u624d\u4f1a\u6709\u8d77\u8272\uff1f\u201d</p><p>\u3000\u3000\u6d77\u5916\u516c\u53f8\u4e0d\u60f3\u627f\u62c5\u6c47\u7387\u635f\u5931\uff0c\u79d1\u738b\u672c\u8eab\u4e5f\u540c\u6837\u627f\u62c5\u4e0d\u8d77\u8fd9\u4e48\u5927\u7684\u6c47\u7387\u635f\u5931\u3002</p><p>\u3000\u3000\u9648\u9759\u6ca1\u6709\u8bf4\u8bdd\uff0c\u770b\u7740\u8c22\u5251\u5357\u3002</p><p>\u3000\u3000\u5f7b\u5e95\u653e\u5f03\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\uff0c\u4e4b\u524d\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u6295\u5165\u90fd\u8981\u6ce1\u6c64\uff0c\u5f53\u7136\u4e3b\u8981\u7684\u635f\u5931\u90fd\u7531\u6d77\u5916\u516c\u53f8\u627f\u62c5\u4e86\uff1b\u4ed6\u4eec\u51e0\u5bb6\u5728\u6d77\u5916\u516c\u53f8\u624d\u536015\uff05\u7684\u80a1\u4efd\uff0c\u5c31\u7b97\u6d77\u5916\u516c\u53f8\u7834\u4ea7\u5012\u95ed\uff0c\u4ed6\u4eec\u4e5f\u53ea\u662f\u4e0d\u8d5a\u4e0d\u8d54\uff0c\u5374\u662f\u8981\u5c06\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u5f7b\u5e95\u7684\u5f97\u7f6a\u5e72\u51c0\u3002</p><p>\u3000\u3000\u6b64\u65f6\u8fd8\u4e0d\u662f\u7edd\u88c2\u7684\u65f6\u5019\uff0c\u8c22\u5251\u5357\u63d0\u9192\u8c22\u77bb\u6ce8\u610f\u4ed6\u81ea\u5df1\u7684\u8bf4\u8f9e\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u610f\u6c14\u8bdd\uff0c\u4e0d\u8981\u968f\u4fbf\u8bf4\u51fa\u53e3\uff0c\u8fd9\u65f6\u5019\u5927\u5bb6\u8981\u4e92\u76f8\u6276\u6301\u6e21\u8fc7\u96be\u5173\u2026\u2026\u201d\u4e0d\u60f3\u5f7b\u5e95\u5f97\u7f6a\u845b\u6c0f\u7236\u5b50\u662f\u4e00\u65b9\u9762\uff0c\u8981\u662f\u5927\u5bb6\u90fd\u5f7b\u5e95\u7684\u653e\u5f03\u4e1c\u5357\u4e9a\u5e02\u573a\uff0c\u53ea\u6709\u7231\u8fbe\u7535\u5b50\u4e00\u5bb6\u8fd8\u80fd\u5728\u4e1c\u5357\u4e9a\u652f\u6491\u4f4f\uff0c\u5927\u5bb6\u653e\u5f03\u7684\u5e02\u573a\u65e0\u7591\u5c31\u90fd\u843d\u5165\u7231\u8fbe\u7535\u5b50\u7684\u624b\u4e2d\uff0c\u8c22\u5251\u5357\u4e0d\u4f1a\u8ba9\u7231\u8fbe\u7535\u5b50\u6361\u8fd9\u4e2a\u4fbf\u5b9c\u3002\u66f4\u4f55\u51b5\uff0c\u789f\u673a\u5382\u5546\u90fd\u64a4\u51fa\u4e1c\u5357\u4e9a\u5730\u533a\uff0c\u591a\u51fa\u6765\u7684\u4ea7\u80fd\u5c31\u8981\u5728\u56fd\u5185\u5e02\u573a\u91ca\u653e\uff0c\u8c22\u5251\u5357\u90fd\u4e0d\u6562\u60f3\u8c61\u90a3\u4e2a\u707e\u96be\u6027\u7684\u540e\u679c\u2014\u2014\u52bf\u5fc5\u4f1a\u6709\u4eba\u5148\u6253\u7834\u7231\u8fbe\u7535\u5b50\u5728\u56fd\u5185\u789f\u673a\u5e02\u573a\u5f3a\u52bf\u5efa\u7acb\u7684\u5747\u8861\u3002\u5c4a\u65f6\u4e0d\u8981\u8bf4\u4e1c\u5357\u4e9a\u5e02\u573a\u5206\u4e0d\u5230\u4e00\u70b9\u5229\u6da6\uff0c\u5c31\u8fde\u56fd\u5185\u7684\u5229\u6da6\u4e5f\u4f1a\u5927\u5e45\u5ea6\u7684\u524a\u51cf\uff1b\u79d1\u738b\u4eca\u5e74\u8fd8\u8981\u652f\u4ed8\u4e2d\u592e\u7535\u89c6\u53f0\u4e24\u4e2a\u4ebf\u7684\u5e7f\u544a\u8d39\u3002</p><p>\u3000\u3000\u4ed6\u8d76\u8fc7\u6765\uff0c\u5c31\u662f\u8981\u4e0e\u845b\u836b\u5747\u5546\u91cf\u6c47\u7387\u635f\u5931\u7684\u5206\u62c5\u95ee\u9898\uff1a\u90fd\u8981\u6d77\u5916\u516c\u53f8\u627f\u62c5\u7edd\u4e0d\u53ef\u80fd\uff1b\u90fd\u8981\u79d1\u738b\u672c\u90e8\u627f\u62c5\uff0c\u4e0d\u7528\u591a\u4e45\uff0c\u672c\u90e8\u4e5f\u4f1a\u5148\u6491\u4e0d\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u8981\u8c08\u51fa\u4e00\u4e2a\u5408\u9002\u7684\u6bd4\u4f8b\uff0c\u5c31\u7b97\u4e8f\u635f\u4e5f\u8981\u5c06\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\u6491\u4e0b\u53bb\uff0c\u633a\u8fc7\u8fd9\u4e00\u6bb5\u6700\u8270\u96be\u7684\u65f6\u523b\uff0c\u5c31\u662f\u67f3\u6697\u82b1\u660e\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u8bf4\u9053\uff1a\u201c\u5728\u4f60\u4eec\u524d\u9762\u4e0b\u98de\u673a\u7684\u90a3\u4e2a\u5bb6\u4f19\u53ef\u662f\u9884\u6d4b\u4e1c\u5357\u4e9a\u7206\u53d1\u7684\u7ecf\u6d4e\u8fd8\u4f1a\u8fdb\u4e00\u6b65\u7684\u8513\u5ef6\uff0c\u6301\u7eed\u7684\u65f6\u95f4\u4e5f\u4f1a\u76f8\u5f53\u7684\u957f\u2026\u2026\u201d\u53ea\u4e0d\u8fc7\u5979\u7684\u795e\u8272\u6709\u4e9b\u5e78\u707e\u4e50\u7978\uff0c\u8c22\u5251\u5357\u4e5f\u4e0d\u80fd\u8ddf\u5979\u8ba1\u8f83\u4ec0\u4e48\uff0c\u53ea\u5f53\u5979\u662f\u5584\u610f\u7684\u63d0\u9192\uff1b\u53ea\u4e0d\u8fc7\uff0c\u7231\u8fbe\u7535\u5b50\u53d1\u5e03\u7684\u516c\u544a\u4ee5\u53ca\u53f6\u81fb\u6c11\u7684\u6587\u7ae0\uff0c\u8c22\u5251\u5357\u90fd\u62ff\u8fc7\u6765\u4ed4\u7ec6\u7814\u7a76\u8fc7\uff0c\u5b50\u5609\u7684\u63d0\u9192\u8ba9\u4ed6\u542c\u6765\u8fd8\u662f\u76f8\u5f53\u7684\u523a\u8033\uff1a\u5979\u5927\u6982\u662f\u6c14\u6124\u4e4b\u524d\u8d26\u6237\u7ed9\u5979\u8001\u5b50\u51bb\u7ed3\u7684\u7f18\u6545\u5427\uff0c\u5bf9\u5bb6\u65cf\u91cc\u7684\u4e8b\u4e1a\u4e5f\u523b\u610f\u7684\u6f20\u4e0d\u5173\u5fc3\u3002</p><p>\u3000\u3000\u9648\u9759\u626f\u4e86\u626f\u5b50\u5609\u7684\u8863\u8896\uff0c\u4e0d\u8ba9\u5979\u80e1\u8bf4\u516b\u9053\uff0c\u4eca\u5929\u7684\u72b6\u51b5\uff0c\u5927\u5bb6\u8eab\u4e0a\u80cc\u8d1f\u7684\u538b\u529b\u90fd\u5f88\u5927\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9664\u4e86\u8c22\u5b50\u5609\u6ca1\u5fc3\u6ca1\u80ba\u4e4b\u5916\uff0c\u5e78\u707e\u4e50\u7978\u7684\u5927\u6709\u4eba\u5728\uff0c\u53f6\u5efa\u658c\u62b1\u5934\u9760\u7740\u5ea7\u6905\uff0c\u59ff\u52bf\u6781\u4e3a\u8212\u5766\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u79d1\u738b\u7684\u65e5\u5b50\u96be\u8fc7\u55bd\uff01\u201d\u53c8\u95ee\u5f20\u606a\uff0c\u201c\u4f60\u5728\u98de\u673a\u4e0a\u6709\u6ca1\u6709\u595a\u843d\u4eba\u5bb6\uff1f\u4f60\u5f53\u521d\u53ef\u662f\u8fde\u6050\u5413\u5e26\u8bf1\u9a97\u7684\u4fc3\u4f7f\u4eba\u5bb6\u5927\u529b\u5f00\u53d1\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\u554a\uff0c\u770b\u5427\uff0c\u679c\u7136\u6389\u8fdb\u4f60\u7684\u5751\u91cc\u4e86\uff0c\u4f60\u5c0f\u5b50\u4e00\u5b9a\u4e0d\u4f1a\u653e\u8fc7\u673a\u4f1a\u501f\u673a\u595a\u843d\u4e00\u4e0b\u4eba\u5bb6\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u54ea\u6709\u4f60\u8fd9\u4e48\u6ca1\u9053\u5fb7\uff0c\u201d\u5f20\u606a\u6b6a\u7740\u5634\u89d2\u8bf4\u9053\uff0c\u201c\u6211\u8fd8\u60f3\u7b11\u7740\u8ddf\u4eba\u5bb6\u6253\u62db\u547c\uff0c\u53ef\u60dc\u770b\u4ed6\u4eec\u7684\u8138\u8272\u4f3c\u4e4e\u66f4\u96be\u770b\u4e86\u2026\u2026\u201d\u8bf4\u5b8c\u5c31\u4e0e\u53f6\u5efa\u658c\u4e00\u8d77\u7b11\u4e86\u8d77\u6765\uff0c\u4e5f\u4e0d\u5c51\u518d\u8c08\u8bba\u79d1\u738b\uff0c\u5148\u5c06\u8d56\u6d4e\u6625\u9001\u5230\u7231\u8fbe\u7535\u5b50\u5728\u4e2d\u73af\u7684\u529e\u4e8b\u673a\u6784\u3002\u90a3\u91cc\u662f\u539f\u5609\u4fe1\u5b9e\u4e1a\u7684\u6240\u5728\u5730\uff0c\u5b9e\u65bd\u589e\u53d1\u5e76\u8d2d\u4e4b\u540e\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u603b\u90e8\u81ea\u7136\u8fd8\u7559\u5728\u6d77\u5dde\uff0c\u5085\u5bb6\u4fca\u63a5\u66ff\u82cf\u6d25\u4e1c\u51fa\u4efb\u7231\u8fbe\u7535\u5b50\u603b\u88c1\uff0c\u4eba\u4e5f\u53bb\u4e86\u6d77\u5dde\u8d74\u4efb\uff0c\u8fd9\u6837\u4e5f\u65b9\u4fbf\u6c9f\u901a\u9999\u96ea\u6d77\u7684\u4e1a\u52a1\uff0c\u9999\u6e2f\u8fd9\u91cc\u5c31\u6210\u7acb\u7231\u8fbe\u7535\u5b50\u7684\u6d77\u5916\u516c\u53f8\u5206\u90e8\uff0c\u8d8a\u79c0\u63a7\u80a1\u4e0e\u9526\u6e56\u5728\u9999\u6e2f\u7684\u4ea7\u54c1\u7814\u53d1\u4e2d\u5fc3\u4e5f\u90fd\u8fc1\u4e86\u8fc7\u53bb\uff0c\u89c4\u6a21\u5012\u662f\u6bd4\u4ee5\u524d\u8fd8\u6269\u5927\u4e86\u8bb8\u591a\u3002</p></article>"}}}]);
