(window.webpackJsonp=window.webpackJsonp||[]).push([[765],{788:function(p){p.exports={chapterTitle:null,title:"\u7b2c60\u7ae0 \u53e6\u8d77\u7089\u7076",content:"<article><h2>\u7b2c60\u7ae0 \u53e6\u8d77\u7089\u7076</h2><p>\u3000\u3000\u4e5d\u56db\u5e74\uff0c\u56fd\u5185\u94f6\u884c\u8fd8\u6ca1\u6709\u53d1\u884c\u4fe1\u7528\u5361\uff0c\u5f20\u606a\u770b\u7740\u5b58\u6298\u4e0a\u7684\u6570\u989d\uff0c\u8f7b\u8f7b\u5f39\u4e86\u5f39\uff0c\u9012\u7ed9\u8bb8\u601d\uff1a\u201c\u4f60\u62ff\u53bb\u8fd8\u503a\u5427\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u4e00\u6123\uff0c\u89c1\u5f20\u606a\u8138\u4e0a\u6302\u7740\u6d45\u6d45\u7684\u7b11\u5bb9\uff0c\u4e5d\u56db\u5e74\uff0c\u5341\u516b\u4e07\u5728\u522b\u4eba\u773c\u91cc\u7edd\u5bf9\u662f\u4e00\u7b14\u5de8\u989d\uff0c\u89c1\u4ed6\u4e00\u8138\u7684\u6ee1\u4e0d\u5728\u4e4e\uff0c\u4e5f\u96be\u602a\u4ed6\u80fd\u66ff\u81ea\u5df1\u501f\u6765\u5341\u4e8c\u4e07\u3002\u8bb8\u601d\u6ca1\u6709\u63a5\u8fc7\u6298\u5b58\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7684\u94b1\uff0c\u6211\u5728\u6d77\u6cf0\u6709\u9886\u5de5\u8d44\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u5979\u62ff\u624b\u6765\u63a8\uff0c\u987a\u624b\u63e1\u4f4f\u5979\u5a07\u67d4\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u4e3a\u6211\u6253\u5de5\uff0c\u6d77\u6cf0\u90a3\u8fb9\u53ea\u662f\u517c\u804c\uff0c\u4f60\u4eca\u540e\u4e09\u5e74\u7684\u4eba\u751f\u90fd\u662f\u6211\u7684\uff0c\u4f60\u600e\u4e48\u8fd8\u4e0d\u660e\u767d\uff1f\u201d\u76ef\u7740\u8bb8\u601d\u7edd\u7f8e\u8fd1\u4e4e\u90aa\u9b45\u7684\u773c\u775b\uff0c\u771f\u60f3\u94bb\u8fdb\u5979\u7684\u7075\u9b42\u6df1\u5904\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u9738\u9053\u7684\u6e29\u67d4\uff0c\u8ba9\u8bb8\u601d\u5fc3\u91cc\u6709\u79cd\u5f02\u6837\u7684\u60c5\u7eea\u5728\u8513\u5ef6\uff0c\u90a3\u79cd\u6e29\u99a8\u7684\u611f\u89c9\uff0c\u597d\u4e45\u672a\u66fe\u611f\u89c9\u5230\u4e86\uff0c\u6709\u4e9b\u8bb8\u7684\u751c\u871c\uff0c\u4e0d\u820d\u5f97\u5c06\u624b\u4ece\u4ed6\u5bbd\u539a\u7684\u624b\u638c\u91cc\u62bd\u51fa\u6765\uff0c\u53ea\u662f\u8ba9\u4ed6\u8fd9\u4e48\u76ef\u7740\uff0c\u8bb8\u601d\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u4f4e\u5934\u907f\u5f00\u4ed6\u707c\u70ed\u7684\u773c\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u62ff\u8fd9\u4e48\u591a\u94b1\u56de\u53bb\uff0c\u8ba9\u6211\u600e\u4e48\u89e3\u91ca\uff1f\u53ea\u8981\u6709\u4e2a\u597d\u5de5\u4f5c\uff0c\u6709\u4efd\u4e0d\u9519\u7684\u85aa\u6c34\uff0c\u5bb6\u91cc\u5df2\u7ecf\u653e\u5bbd\u5fc3\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u5012\u662f\u7684\uff0c\u4e4b\u524d\u7684\u5341\u4e8c\u4e07\uff0c\u4e00\u5b9a\u4e5f\u8ba9\u4f60\u5bb6\u91cc\u5f88\u4e0d\u5b89\uff0c\u201d\u5f20\u606a\u770b\u89c1\u8bb8\u601d\u4f4e\u4e0b\u5934\uff0c\u776b\u6bdb\u7684\u5f71\u5b50\u6620\u5728\u6e05\u6f88\u7684\u773c\u77b3\u4e0a\uff0c\u633a\u76f4\u7684\u9f3b\u6881\u4e0b\u662f\u5a07\u6da6\u7684\u7ea2\u5507\uff0c\u6709\u7740\u975e\u5e38\u7f8e\u7684\u66f2\u7ebf\uff0c\u201c\u665a\u4e0a\u8bf7\u665a\u6674\u53bb\u4f60\u5bb6\u5403\u996d\u5427\u3002\u201d\u6ca1\u7b49\u8bb8\u601d\u56de\u7b54\uff0c\u5f20\u606a\u5c31\u80af\u5b9a\u7684\u52a0\u4e86\u4e00\u53e5\uff0c\u201c\u5c31\u8fd9\u4e48\u5b9a\u4e86\uff0c\u4eca\u5929\u90fd\u628a\u82b7\u5f64\u618b\u529e\u516c\u5ba4\u91cc\u4e00\u5929\u4e86\uff0c\u5979\u4e00\u5b9a\u4f1a\u6e34\u671b\u51fa\u53bb\u73a9\u73a9\uff0c\u5c31\u53bb\u4f60\u5bb6\uff0c\u4f60\u5148\u5b89\u6392\u4e00\u4e0b\uff0c\u6211\u8ddf\u665a\u6674\u59d0\u8ddf\u8521\u59d0\u3001\u5468\u54e5\u8bf4\u53bb\u3002\u201d</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u4e0d\u5bb9\u62d2\u7edd\u7684\u63a8\u95e8\u8d70\u51fa\u529e\u516c\u5ba4\uff0c\u8bb8\u601d\u5fc3\u91cc\u751f\u51fa\u522b\u6837\u7684\u6e29\u67d4\uff0c\u53d1\u4e86\u4e00\u4f1a\u513f\u6123\uff0c\u5012\u662f\u62c5\u5fc3\u8c22\u665a\u6674\u4f1a\u4e0d\u4f1a\u53bb\uff0c\u6b63\u6536\u62fe\u684c\u4e0a\u7684\u6587\u4ef6\uff0c\u5c0f\u82b7\u5f64\u63a8\u95e8\u51b2\u4e86\u8fdb\u6765\uff0c\u4e24\u53ea\u5a07\u5ae9\u7684\u5c0f\u624b\u5174\u594b\u7684\u6bd4\u5212\uff0c\u8bb8\u601d\u6170\u5fc3\u7684\u4e00\u7b11\uff0c\u5c06\u5c0f\u82b7\u5f64\u62b1\u5230\u529e\u516c\u684c\u4e0a\uff0c\u8ba9\u5979\u770b\u7740\u81ea\u5df1\u6536\u62fe\u4e1c\u897f\u3002</p><p>\u3000\u3000\u65b0\u516c\u53f8\uff0c\u8bb8\u601d\u53ea\u62c5\u4efb\u603b\u7ecf\u7406\u7279\u522b\u52a9\u7406\u7684\u804c\u4f4d\uff0c\u5f20\u606a\u4e4b\u524d\u5c31\u660e\u786e\u63d0\u51fa\u4e0d\u8ba9\u5979\u5c06\u4e3b\u8981\u7cbe\u529b\u653e\u5728\u8fd9\u8fb9\uff0c\u8fd9\u91cc\u7684\u7ecf\u7406\u5ba4\u8981\u8ba9\u7ed9\u8521\u7eef\u5a1f\u4e0e\u5468\u6e38\uff0c\u53ea\u9700\u8981\u5728\u5927\u529e\u516c\u5ba4\u5360\u4e2a\u529e\u516c\u4f4d\u7f6e\u5c31\u8db3\u591f\u4e86\u3002</p><p>\u3000\u3000\u4e2d\u79cb\u8fc7\u540e\uff0c\u4e1c\u6d77\u7701\u4e1c\u90e8\u4e94\u4e2a\u5730\u7ea7\u5e02\u7684\u5e02\u573a\u5f00\u53d1\u5de5\u4f5c\u5df2\u7ecf\u6709\u5e8f\u7684\u8fdb\u884c\u7740\uff0c\u8c22\u665a\u6674\u4e4b\u524d\u5bf9\u8fd9\u7247\u7684\u5206\u9500\u6e20\u9053\u8fdb\u884c\u5927\u8c03\u6574\uff0c\u4eba\u4e8b\u8fdb\u884c\u5927\u6362\u8840\uff0c\u867d\u7136\u8fd9\u6b21\u662f\u8fdb\u5165\u65b0\u7684\u9886\u57df\uff0c\u53c8\u6709\u6d77\u5dde\u5e02\u573a\u7684\u793a\u8303\u4f5c\u7528\uff0c\u5f88\u5feb\u5c31\u6b65\u5165\u6b63\u8f68\u3002</p><p>\u3000\u3000\u848b\u8587\u3001\u6768\u4e91\u3001\u5468\u4e00\u5e73\u3001\u5218\u660e\u8f89\u5728\u65b0\u516c\u53f8\u4e5f\u91cd\u65b0\u627e\u5230\u81ea\u5df1\u7684\u4f4d\u7f6e\uff0c\u4ed6\u4eec\u7684\u80fd\u529b\u5f97\u5230\u80af\u5b9a\uff0c\u804c\u4f4d\u90fd\u76f8\u5f53\u7684\u4e0d\u9519\uff0c\u5468\u4e00\u5e73\u3001\u5218\u660e\u8f89\u7ee7\u7eed\u8d1f\u8d23\u6d77\u5dde\u7684\u5e02\u573a\u63a8\u5e7f\uff0c\u6768\u4e91\u8c03\u5230\u65b0\u6cf0\u529e\u4e8b\u5904\u5f53\u4efb\u526f\u7ecf\u7406\uff0c\u848b\u8587\u7ed9\u5468\u6e38\u5f53\u52a9\u624b\uff0c\u4f5c\u4e3a\u5bf9\u4ed6\u4eec\u524d\u671f\u5de5\u4f5c\u7684\u5956\u52b1\uff0c\u4ed6\u4eec\u7684\u5de5\u4f5c\u4fdd\u8bc1\u91d1\u6298\u5165\u516c\u53f8\u7684\u80a1\u4efd\u3002</p><p>\u3000\u3000\u65b0\u516c\u53f8\u6210\u7acb\u7684\u9152\u5bb4\u6628\u5929\u5df2\u7ecf\u5403\u8fc7\u4e86\uff0c\u4e0d\u8fc7\u804c\u5458\u5bf9\u516c\u53f8\u7684\u524d\u666f\u5341\u5206\u5174\u594b\uff0c\u7279\u522b\u5728\u65b0\u516c\u53f8\u6210\u7acb\u4e4b\u524d\uff0c\u8c22\u665a\u6674\u8ba9\u8d22\u52a1\u5c06\u62d6\u6b20\u5458\u5de5\u7684\u85aa\u8d44\u8db3\u989d\u8865\u53d1\uff0c\u4e00\u5230\u4e0b\u73ed\u7684\u65f6\u95f4\uff0c\u5927\u529e\u516c\u5ba4\u91cc\u7684\u540c\u4e8b\u5c31\u7ea6\u597d\u518d\u4e00\u8d77\u53bb\u559d\u9152\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u95ee\u8bb8\u601d\uff1a\u201c\u5c0f\u5f20\u7ecf\u7406\u8ddf\u4f60\u6709\u6ca1\u6709\u7a7a\uff0c\u4e00\u8d77\u53bb\u559d\u9152\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u64a9\u4e86\u64a9\u5934\u53d1\uff0c\u6734\u7d20\u7684\u804c\u4e1a\u6253\u626e\u5374\u4e0d\u63a9\u5979\u7684\u5929\u751f\u4e3d\u8d28\uff0c\u5c31\u8fd9\u52a8\u4f5c\uff0c\u5df2\u7ecf\u8ba9\u529e\u516c\u5ba4\u7684\u7537\u540c\u4e8b\u795e\u8ff7\u9b44\u6563\uff0c\u5218\u660e\u8f89\u6709\u5973\u670b\u53cb\uff0c\u77e5\u9053\u8bb8\u601d\u4e0e\u5f20\u606a\u5173\u7cfb\u4e0d\u540c\u4e00\u822c\uff0c\u76f8\u5904\u65f6\u95f4\u957f\u4e86\uff0c\u62b5\u6297\u529b\u5012\u662f\u5f3a\u4e9b\u3002</p><p>\u3000\u3000\u82b7\u5f64\u4f9d\u5728\u8bb8\u601d\u65c1\u8fb9\uff0c\u6012\u51b2\u51b2\u7684\u77aa\u7740\u5218\u660e\u8f89\uff0c\u76f4\u6447\u5934\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u634f\u4e86\u634f\u82b7\u5f64\u7684\u8138\u86cb\uff0c\u8bf4\u9053\uff1a\u201c\u77e5\u9053\u4e86\uff0c\u4e00\u5b9a\u4f1a\u5c06\u82b7\u5f64\u4e5f\u5e26\u4e0a\u7684\u3002\u201d</p><p>\u3000\u3000\u82b7\u5f64\u4ece\u5218\u660e\u8f89\u7684\u9b54\u624b\u6323\u624e\u51fa\u6765\uff0c\u5ae9\u5ae9\u7684\u5c0f\u8138\u7eef\u7ea2\uff0c\u663e\u7136\u4e3a\u81ea\u5df1\u4e0d\u80fd\u5f00\u53e3\u8bf4\u8bdd\u751f\u95f7\u6c14\uff0c\u8bb8\u601d\u5c06\u82b7\u5f64\u62b1\u8d77\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u7b54\u5e94\u9886\u82b7\u5f64\u56de\u5bb6\u53bb\u73a9\uff0c\u4e0d\u80fd\u8ddf\u4f60\u4eec\u4e00\u8d77\u53bb\u5403\u996d\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u8c22\u665a\u6674\u4ece\u7ecf\u7406\u5ba4\u51fa\u6765\uff0c\u5bf9\u8bb8\u601d\u8bf4\uff1a\u201c\u8521\u59d0\u665a\u4e0a\u6709\u5e94\u916c\uff0c\u5c31\u6211\u4eec\u56db\u4e2a\u4eba\u3002\u201d\u53c8\u5bf9\u5218\u660e\u8f89\u8bf4\uff0c\u201c\u60f3\u8bf7\u6211\u8ddf\u8bb8\u601d\uff0c\u6539\u5929\u8d4f\u4f60\u4e00\u4e2a\u673a\u4f1a\u3002\u201d</p><p>\u3000\u3000\u5218\u660e\u8f89\u7b11\u4e86\u7b11\uff0c\u8fde\u8bf4\uff1a\u201c\u5c0f\u5f20\u7ecf\u7406\u4e00\u5b9a\u8981\u8bb0\u5f97\uff0c\u6211\u5973\u670b\u53cb\u542c\u4e86\u516c\u53f8\u7684\u4e8b\u60c5\uff0c\u5bf9\u4f60\u53ef\u662f\u5d07\u62dc\u5f97\u5f88\uff0c\u5979\u5b66\u4eba\u529b\u8d44\u6e90\u7ba1\u7406\uff0c\u8fd8\u8981\u5411\u4f60\u8bf7\u6559\u5462\u3002\u201d</p><p>\u3000\u3000\u4e4b\u524d\u848b\u8587\u770b\u5230\u5510\u5b66\u8c26\u7684\u8f66\u6765\u63a5\u8c22\u665a\u6674\u8ddf\u5f20\u606a\uff0c\u5bf9\u516c\u53f8\u7684\u80cc\u666f\u90fd\u6709\u731c\u6d4b\uff0c\u5728\u77ed\u77ed\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u91cc\uff0c\u5c31\u7ec4\u5efa\u65b0\u7684\u516c\u53f8\uff0c\u5c06\u8fd1\u5343\u4e07\u7684\u6ce8\u518c\u8d44\u4ea7\uff0c\u7279\u522b\u662f\u5927\u80a1\u4e1c\u8fd8\u662f\u7701\u91cc\u7740\u540d\u7684\u6b63\u6cf0\u96c6\u56e2\uff0c\u5df2\u7ecf\u4e0d\u9700\u8981\u731c\u6d4b\u4e86\uff0c\u80cc\u666f\u7edd\u5bf9\u4e0d\u4e00\u822c\u3002</p><p>\u3000\u3000\u5f20\u606a\u6487\u4e86\u6487\u5634\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5012\u653e\u5fc3\u628a\u5973\u670b\u53cb\u5e26\u5230\u6211\u9762\u524d\u6765\uff0c\u4e0d\u6015\u5979\u79fb\u60c5\u522b\u604b\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u4ed6\u8fd8\u771f\u628a\u81ea\u5df1\u5f53\u6210\u5e74\u4eba\u4e86\uff0c\u5f20\u606a\u563f\u563f\u4e00\u7b11\uff0c\u8d70\u8fc7\u6765\u7275\u82b7\u5f64\u7684\u624b\uff0c\u8ddf\u7ad9\u8d77\u6765\u6253\u62db\u547c\u7684\u848b\u8587\u7b11\u4e86\u7b11\uff0c\u4fbf\u4e0e\u8bb8\u601d\u3001\u8c22\u665a\u6674\u4e00\u8d77\u51fa\u4e86\u529e\u516c\u5ba4\u3002</p><p>\u3000\u3000\u8ba9\u516c\u53f8\u7684\u522b\u514b\u5546\u52a1\u8f66\u9001\u5230\u6c99\u7530\uff0c\u5728\u8f66\u4e0a\uff0c\u5f20\u606a\u8ddf\u8c22\u665a\u6674\u63d0\u8d77\u5341\u4e8c\u4e07\u501f\u6b3e\u7684\u4e8b\u60c5\uff0c\u8ba9\u5979\u5e2e\u7740\u5706\u8c0e\uff0c\u8c22\u665a\u6674\u65e0\u5948\u7684\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u5c0f\u53d4\u4e5f\u771f\u6562\u62ff\u5341\u4e8c\u4e07\u7ed9\u4f60\u53bb\u6298\u817e\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u4e0d\u662f\u628a\u529e\u4e8b\u5904\u7ed9\u6211\u778e\u6298\u817e\uff1f\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u62cd\u62cd\u660e\u767d\u5982\u7389\u7684\u989d\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u521a\u521a\u8d70\u4e0a\u6b63\u8f68\uff0c\u4f60\u600e\u4e48\u5c31\u4e0d\u6298\u817e\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u5177\u4f53\u7684\u5de5\u4f5c\uff0c\u6211\u4e5f\u4e0d\u4f1a\u6bd4\u8521\u59d0\u3001\u5468\u603b\u8fd9\u4e9b\u804c\u4e1a\u7ecf\u7406\u4eba\u505a\u5f97\u66f4\u597d\uff0c\u6211\u60f3\u81ea\u5df1\u6ce8\u518c\u4e2a\u516c\u53f8\u73a9\u73a9\uff0c\u665a\u6674\u59d0\uff0c\u6d77\u88d5\u516c\u53f8\u4e5f\u5165\u80a1\u5427\u3002\u201d\u5f20\u606a\u5728\u8c22\u665a\u6674\u3001\u8bb8\u601d\u9762\u524d\u5012\u6ca1\u6709\u5fc5\u8981\u88c5\u6b63\u7ecf\uff0c\u91cd\u56de\u4e5d\u56db\u5e74\uff0c\u5012\u662f\u53d1\u73b0\u8bb8\u591a\u5c11\u5e74\u65f6\u6ca1\u6709\u5174\u8da3\uff0c\u8eab\u5b50\u57ae\u6389\u4f3c\u7684\u8737\u5728\u540e\u5ea7\u4e0a\uff0c\u8ba9\u82b7\u5f64\u67d4\u8f6f\u7684\u8eab\u4f53\u9760\u7740\u81ea\u5df1\uff0c\u8146\u7740\u8138\u770b\u7740\u665a\u6674\u3002</p><p>\u3000\u3000\u53ea\u6295\u5165\u4e24\u767e\u4e07\u7684\u8d44\u91d1\uff0c\u5c31\u5c06\u6d77\u88d5\u516c\u53f8\u5927\u534a\u7684\u9690\u6027\u503a\u52a1\u5265\u79bb\u5230\u65b0\u516c\u53f8\uff0c\u8c22\u665a\u6674\u8eab\u4e0a\u7684\u538b\u529b\u51cf\u8f7b\u4e86\u8bb8\u591a\uff0c\u6709\u8fd9\u6837\u7684\u601d\u8def\uff0c\u4f59\u4e0b\u7684\u90e8\u5206\uff0c\u62ff\u8d44\u4ea7\u8fdb\u884c\u6298\u51b2\uff0c\u4e5f\u4f1a\u5f88\u65b9\u4fbf\u7684\u89e3\u51b3\u6389\uff0c\u60f3\u5230\u516c\u53f8\u80fd\u5728\u81ea\u5df1\u624b\u91cc\u8d70\u4e0a\u6b63\u8f68\uff0c\u751a\u611f\u5fc3\u6170\u3002</p><p>\u3000\u3000\u867d\u7136\u5fd7\u660e\u65e0\u6cd5\u907f\u5f00\u8f66\u7978\uff0c\u5374\u8ba9\u81ea\u5df1\u4e0e\u82b7\u5f64\u9047\u5230\u5f20\u606a\uff0c\u4e5f\u662f\u4e0a\u5929\u7684\u7737\u987e\u4e0e\u8865\u507f\u5427\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u6c38\u8fdc\u60f3\u4e0d\u901a\u5f20\u606a\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u8111\u5b50\u91cc\u5012\u5e95\u88c5\u7740\u591a\u5c11\u4e1c\u897f\uff0c\u89c1\u4ed6\u63d0\u6ce8\u518c\u516c\u53f8\u7684\u4e8b\u60c5\uff0c\u7b11\u7740\u95ee\u4ed6\uff1a\u201c\u8be5\u4e0d\u4f1a\u53c8\u662f\u625b\u6d77\u88d5\u516c\u53f8\u5927\u65d7\u51fa\u53bb\u62db\u6447\u649e\u9a97\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4f1a\uff1f\u201d\u5f20\u606a\u5f88\u6b63\u7ecf\u7684\u5750\u76f4\u8eab\u5b50\uff0c\u201c\u624b\u91cc\u8fd9\u5341\u516b\u4e07\uff0c\u8bb8\u601d\u4e0d\u60f3\u4e00\u4e0b\u5b50\u62ff\u51fa\u53bb\u8fd8\u503a\uff0c\u6211\u8fd9\u4eba\u4e00\u5411\u4f4e\u8c03\u60ef\u4e86\uff0c\u7a81\u7136\u5927\u624b\u5927\u811a\u7684\u82b1\u94b1\uff0c\u8fd8\u4e0d\u77e5\u9053\u6211\u8001\u7238\u8001\u5988\u4f1a\u600e\u4e48\u60f3\uff0c\u62ff\u53bb\u6ce8\u518c\u516c\u53f8\u518d\u597d\u4e0d\u8fc7\u3002\u665a\u6674\u59d0\uff0c\u4f60\u8c03\u7ed9\u6d77\u6cf0\u516c\u53f8\u7684\u4e24\u53f0\u8f66\uff0c\u5c31\u6298\u5165\u65b0\u516c\u53f8\u7684\u8d44\u4ea7\uff0c\u5e73\u65f6\u79df\u7ed9\u6d77\u6cf0\u516c\u53f8\u7528\uff0c\u6ce8\u518c\u8d44\u4ea7\u9a6c\u9a6c\u864e\u864e\u5c31\u7b97\u4e00\u767e\u4e07\uff0c\u80a1\u6743\u5e73\u5206\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd8\u8bf4\u4e0d\u662f\u62db\u6447\u649e\u9a97\uff1f\u201d\u8bb8\u601d\u56de\u8fc7\u5934\u6765\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u8fd9\u6b21\u522b\u628a\u6211\u63a8\u51fa\u53bb\uff0c\u848b\u59d0\u5979\u4eec\u77e5\u9053\u6240\u8c13\u5de5\u4f5c\u4fdd\u8bc1\u91d1\u7684\u4e8b\u4e86\uff0c\u8fd8\u8bf4\u8981\u8ffd\u4f60\u8981\u7cbe\u795e\u635f\u5931\u8d39\u5462\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6320\u6320\u8111\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u4e0d\u51fa\u9762\uff0c\u6211\u8fd8\u5f97\u6293\u4e2a\u58ee\u4e01\u8fc7\u6765\uff0c\u518d\u8bf4\uff0c\u518d\u8bf4\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u601d\u7b11\u4e86\u7b11\uff0c\u5927\u6982\u653e\u677e\u5fc3\u60c5\u7684\u7f18\u6545\uff0c\u773c\u7738\u95f4\u6d41\u9732\u65e0\u9650\u7684\u59a9\u5a9a\uff0c\u5f20\u606a\u8d2a\u5a6a\u7684\u591a\u770b\u4e86\u4e24\u773c\uff0c\u5f85\u8c22\u665a\u6674\u8f6c\u8fc7\u5934\u6765\uff0c\u624d\u6536\u56de\u773c\u795e\u3002</p></article>"}}}]);
