(window.webpackJsonp=window.webpackJsonp||[]).push([[768],{791:function(p){p.exports={chapterTitle:null,title:"\u7b2c767\u7ae0 \u51fa\u56fd\u8003\u5bdf",content:"<article><h2>\u7b2c767\u7ae0 \u51fa\u56fd\u8003\u5bdf</h2><p>\u3000\u3000\u6797\u4e3d\u96ef\u8fd8\u60e6\u8bb0\u7740\u5979\u7684\u5de5\u4f5c\u804c\u8d23\uff0c\u5f20\u606a\u4e0e\u674e\u99a8\u4e88\u5230\u5c55\u53f0\u540e\u4e0d\u4e45\uff0c\u5979\u4e5f\u8d76\u4e86\u8fc7\u6765\uff0c\u79c1\u4e0b\u91cc\u544a\u8bc9\u5f20\u606a\uff1a\u201c\u6628\u5929\u662f\u5979\u7b2c\u4e00\u6b21\u53bb\u65b0\u5bbf\u7684\u9152\u5427\uff0c\u5f88\u96be\u582a\uff0c\u53c8\u62c5\u5fc3\u4f60\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5443\uff0c\u201d\u5f20\u606a\u8e59\u7740\u7709\u5934\u800c\u7b11\uff0c\u6709\u4e9b\u5c34\u5c2c\uff0c\u4ed6\u4e5f\u76f8\u4fe1\u90a3\u4e2a\u957f\u76f8\u4e0e\u5c0f\u6cfd\u5706\u6709\u51e0\u5206\u50cf\u7684\u4e0a\u6d77\u5973\u5b69\u5b50\u53ef\u80fd\u662f\u521d\u6b21\u4e0b\u6c34\uff0c\u5185\u5fc3\u6323\u624e\u5145\u6ee1\u77db\u76fe\uff0c\u624d\u4f1a\u8fd9\u4e48\u8106\u5f31\u7ed9\u8f7b\u8f7b\u4e00\u6233\u5c31\u6fd2\u4e34\u5d29\u6e83\uff0c\u95ee\u9053\uff0c\u201c\u5979\u4eba\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u60c5\u7eea\u6709\u4e9b\u4e0d\u597d\uff0c\u5979\u8bf7\u5047\u8981\u5148\u56de\u53bb\u2026\u2026\u201d\u6797\u4e3d\u96ef\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5373\u4f7f\u7535\u5b50\u5c55\u6709\u8bb8\u591a\u6b27\u7f8e\u65c5\u5ba2\uff0c\u4f46\u662f\u8eab\u9ad8\u63a5\u8fd1\u4e24\u7c73\u3001\u60e8\u767d\u76ae\u80a4\u7684\u827e\u9ed8\u8d70\u5728\u4eba\u7fa4\u8fd8\u662f\u5f88\u62a2\u773c\uff0c\u4ed6\u5148\u770b\u5230\u4e0e\u5f20\u606a\u5e76\u80a9\u7ad9\u7740\u7684\u674e\u99a8\u4e88\uff0c\u6123\u4e86\u4e00\u4e0b\uff0c\u56de\u8fc7\u795e\u6765\u624d\u66a7\u6627\u7684\u63fd\u8fc7\u5f20\u606a\u7684\u80a9\u8180\uff0c\u8be1\u5f02\u7684\u7b11\u7740\u8bf4\uff1a\u201c\u8fd8\u60f3\u7ea6\u4f60\u665a\u4e0a\u4e00\u8d77\u53bb\u6c60\u888b\u5462\uff0c\u201d\u6325\u4e86\u6325\u624b\uff0c\u8bf4\u9053\uff0c\u201c\u7b97\u4e86\uff0c\u7b97\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u6c60\u888b\uff0c\u4f20\u8bf4\u4e2d\u63f4\u52a9\u4ea4\u9645\u7684\u5723\u571f\uff0c\u5927\u53d4\u4e0e\u5973\u9ad8\u4e2d\u751f\u7684\u6c89\u6ca6\u4e4b\u5730\uff0c\u6c34\u624b\u670d\u3001\u683c\u5b50\u77ed\u88d9\u3001\u9ed1\u8272\u957f\u7edf\u889c\u7684\u5236\u670d\u805a\u96c6\u5730\u3002</p><p>\u3000\u3000\u827e\u9ed8\u8138\u4e0a\u8be1\u5f02\u800c\u66a7\u6627\u7684\u7b11\u5bb9\uff0c\u674e\u99a8\u4e88\u3001\u6797\u4e3d\u96ef\u770b\u4e86\u6c57\u6bdb\u90fd\u80fd\u7ad6\u8d77\u6765\uff0c\u5f20\u606a\u6478\u4e86\u6478\u9f3b\u5934\uff0c\u6709\u5fc3\u53bb\u957f\u957f\u89c1\u8bc6\uff0c\u53ea\u662f\u8fd9\u65f6\u4e0d\u662f\u5408\u9002\u7684\u65f6\u673a\uff0c\u62cd\u4e86\u62cd\u827e\u9ed8\u7684\u80a9\uff0c\u8ba9\u4ed6\u6709\u4ec0\u4e48\u4e8b\u60c5\u5f84\u76f4\u627e\u8096\u664b\u6210\u5546\u91cf\u53bb\uff0c\u4ed6\u8f6c\u8fc7\u8eab\u95ee\u6797\u4e3d\u96ef\uff1a\u201c\u4f60\u77e5\u9053\u8096\u5c0f\u59d0\u4f4f\u4ec0\u4e48\u5730\u65b9\uff0c\u6211\u5bf9\u5343\u53f6\u4e0d\u719f\u6089\uff0c\u4e5f\u6b63\u597d\u60f3\u7740\u51fa\u53bb\u8d70\u4e00\u8d70\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u96c1\u5b81\u53ef\u80fd\u8fd8\u5728\u66f4\u8863\u5ba4\u6ca1\u6709\u79bb\u5f00\uff0c\u6211\u80fd\u8bf7\u5979\u4e00\u8d77\u7ed9\u4f60\u5f53\u5bfc\u6e38\u7684\u2026\u2026\u201d\u6797\u4e3d\u96ef\u8bf4\u5b8c\u5c31\u5411\u5c55\u4f1a\u4e2d\u5fc3\u5de5\u4f5c\u4eba\u5458\u7684\u66f4\u8863\u5904\u8d70\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u671d\u674e\u99a8\u4e88\u644a\u644a\u624b\uff0c\u8fd8\u6ca1\u6709\u7b49\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u674e\u99a8\u4e88\u795e\u8272\u5a07\u67d4\u7684\u8bf4\u9053\uff1a\u201c\u4e5f\u597d\u60f3\u51fa\u53bb\u900f\u4e00\u53e3\u6c14\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u597d\u2026\u2026\u201d\u5f20\u606a\u4e0e\u666e\u901a\u7537\u4eba\u6beb\u65e0\u4e8c\u81f4\uff0c\u6709\u674e\u99a8\u4e88\u8fd9\u4e48\u4e2a\u7f8e\u4eba\u5e0c\u671b\u540c\u884c\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u62d2\u7edd\u7684\uff0c\u4fbf\u4e0e\u674e\u99a8\u4e88\u8ddf\u5728\u6797\u4e3d\u96ef\u7684\u540e\u9762\u671d\u5458\u5de5\u66f4\u8863\u5904\u8d70\u53bb\uff0c\u5085\u4fca\u8fb9\u8d70\u8fb9\u6253\u7535\u8bdd\u8054\u7cfb\u5916\u51fa\u7684\u8f66\u8f86\u3002</p><p>\u3000\u3000\u8d70\u5230\u66f4\u8863\u5ba4\u5916\u7684\u504f\u5385\uff0c\u8fd9\u91cc\u4e5f\u662f\u5de5\u4f5c\u4eba\u5458\u7684\u4f11\u606f\u533a\uff0c\u6709\u8bb8\u591a\u7a7f\u7740\u6027\u611f\u670d\u88c5\u7684\u4fc3\u9500\u5973\u90ce\u6216\u5750\u6216\u7ad9\u5728\u91cc\u9762\u4f11\u606f\u3002\u770b\u5230\u6797\u4e3d\u96ef\u5728\u524d\u9762\u5148\u9047\u5230\u6362\u4e0a\u73ab\u7ea2\u8272\u5462\u7ed2\u5916\u5957\u7684\u8096\u96c1\u5b81\u4e24\u4eba\u62c9\u7740\u624b\u8bf4\u8bdd\uff0c\u674e\u99a8\u4e88\u4f38\u624b\u70ed\u5207\u7684\u8981\u8ddf\u5979\u4eec\u6253\u62db\u547c\uff0c\u5374\u6709\u56db\u4e2a\u4eba\u4ece\u6a2a\u91cc\u63d2\u51fa\u6765\u5148\u5c06\u8096\u96c1\u5b81\u3001\u6797\u4e3d\u96ef\u7ed9\u62e6\u4f4f\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f02\u5e38\u8be7\u5f02\uff0c\u845b\u836b\u5747\u8d6b\u7136\u5c31\u5728\u90a3\u56db\u4eba\u4e4b\u4e2d\u3002\u867d\u7136\u65e9\u5c31\u77e5\u9053\u5609\u4fe1\u7535\u5b50\u6709\u6765\u53c2\u52a0\u7535\u5b50\u5c55\uff0c\u4f46\u662f\u845b\u836b\u5747\u8fd9\u6bb5\u65f6\u95f4\u4ee5\u6765\u4e00\u76f4\u662f\u79d1\u738b\u6d77\u5916\u516c\u53f8\u7684\u8d1f\u8d23\u4eba\u3002\u867d\u7136\u5609\u4fe1\u7535\u5b50\u662f\u845b\u6c0f\u540d\u4e0b\u7684\u4e0a\u5e02\u516c\u53f8\uff0c\u4f46\u662f\u845b\u836b\u5747\u4e2a\u4eba\u4e0e\u5609\u4fe1\u7535\u5b50\u5e76\u6ca1\u6709\u76f4\u63a5\u5173\u8054\uff0c\u4ed6\u662f\u56e0\u4e3a\u5176\u4ed6\u539f\u56e0\u51fa\u73b0\u5728\u8fd9\u91cc\uff0c\u53c8\u6216\u8bb8\u4ed6\u5df2\u7ecf\u56de\u5230\u5609\u4fe1\u7535\u5b50\u4efb\u804c\u8fd9\u6b21\u4ee3\u8868\u5609\u4fe1\u7535\u5b50\u5230\u4e1c\u4eac\u6765\u53c2\u5c55\uff1f</p><p>\u3000\u3000\u56db\u4e2a\u4eba\u4e2d\uff0c\u9664\u4e86\u4e00\u540d\u9752\u5e74\u4e0e\u845b\u836b\u5747\u5e74\u7eaa\u76f8\u5f53\u5916\uff0c\u53e6\u4e24\u4eba\u90fd\u662f\u5927\u8179\u4fbf\u4fbf\u7684\u4e2d\u5e74\u4eba\uff0c\u770b\u5230\u8fd9\u4e24\u4eba\uff0c\u5f20\u606a\u8111\u6d77\u91cc\u7acb\u5373\u60f3\u5230\u90a3\u4e9b\u51fa\u56fd\u8003\u5bdf\u7684\u5b98\u5458\u5f62\u8c61\uff0c\u4e0d\u6653\u5f97\u4ed6\u4eec\u7a81\u7136\u6321\u5728\u6797\u4e3d\u96ef\u3001\u8096\u96c1\u5b81\u9762\u524d\u505a\u4ec0\u4e48\u3002\u96be\u9053\u662f\u76f8\u8bc6\u7684\uff1f\u5f20\u606a\u62c9\u4e86\u4e00\u4e0b\u674e\u99a8\u4e88\uff0c\u6536\u5f80\u6b65\u5b50\u5c31\u7ad9\u5728\u540e\u9762\u770b\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u597d\uff0c\u8fd9\u4f4d\u845b\u5148\u751f\u662f\u9999\u6e2f\u4eba\uff0c\u6211\u4eec\u4e09\u4f4d\u90fd\u662f\u4f60\u4eec\u7684\u540c\u4e61\uff0c\u201d\u4e0e\u845b\u836b\u5747\u5e74\u7eaa\u76f8\u5f53\u7684\u90a3\u4e2a\u9752\u5e74\u8d70\u4e0a\u524d\uff0c\u4f38\u624b\u62e6\u4f4f\u8096\u96c1\u5b81\u3001\u6797\u4e3d\u96ef\uff0c\u4ee3\u66ff\u845b\u836b\u5747\u53ca\u5176\u4ed6\u4e24\u540d\u5927\u8179\u4fbf\u4fbf\u7684\u4e2d\u5e74\u4eba\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\u90fd\u662f\u5230\u4e1c\u4eac\u7559\u5b66\u7684\u5b66\u751f\u5427\uff1f\u521a\u624d\u6211\u4eec\u7530\u4e3b\u4efb\u542c\u5230\u4f60\u4eec\u8bf4\u5bb6\u4e61\u8bdd\uff0c\u611f\u89c9\u5f88\u4eb2\u5207\uff0c\u53c8\u542c\u8bf4\u4f60\u4eec\u5728\u4e1c\u4eac\u751f\u6d3b\u5f88\u4e0d\u5bb9\u6613\uff0c\u89c9\u5f97\u6709\u4e49\u52a1\u5e2e\u52a9\u4f60\u4eec\u4e00\u628a\u3002\u6211\u4eec\u56db\u4e2a\u4eba\u5bf9\u4e1c\u4eac\u4e0d\u719f\u6089\uff0c\u60f3\u8bf7\u4e8c\u4f4d\u7ed9\u6211\u4eec\u5f53\u4f34\u6e38\uff0c\u4e0d\u6653\u5f97\u4f60\u4eec\u9700\u8981\u591a\u5c11\u94b1\uff1f\u6211\u4eec\u6709\u56db\u4e2a\u4eba\uff0c\u6700\u597d\u80fd\u518d\u591a\u4e24\u4e2a\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u4f60\u4eec\u4e5f\u6e05\u695a\uff0c\u53ea\u8981\u73a9\u5f97\u5f00\u5fc3\uff0c\u6211\u4eec\u4e0d\u4f1a\u5728\u4e4e\u591a\u82b1\u4e9b\u94b1\u2026\u2026\u201d\u4ed6\u8bf4\u7684\u662f\u4e2d\u6587\uff0c\u4e00\u8138\u7684\u6c89\u7a33\uff0c\u7adf\u6ca1\u6709\u4e1d\u6beb\u7684\u4e0d\u5b89\u4e0e\u614c\u4e71\u3002\u845b\u836b\u5747\u4ed6\u4eec\u773c\u91cc\u53ea\u6709\u6797\u4e3d\u96ef\u3001\u8096\u96c1\u5b81\u4e24\u4e2a\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u90fd\u6ca1\u6709\u770b\u5230\u5f20\u606a\u4e0e\u674e\u99a8\u4e88\u4ece\u4ed6\u4eec\u8eab\u540e\u8d70\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u5927\u6982\u662f\u90a3\u4e2a\u59d3\u7530\u7684\u4e2d\u5e74\u4eba\u542c\u5230\u8096\u96c1\u5b81\u521a\u624d\u8ddf\u6797\u4e3d\u96ef\u8bf4\u9152\u5427\u966a\u9152\u7684\u4e8b\u60c5\uff0c\u8fd9\u624d\u6025\u8272\u7684\u627e\u4eba\u4e00\u8d77\u8ffd\u8fc7\u6765\uff0c\u7709\u5934\u8e59\u7740\uff0c\u5fc3\u60f3\u6797\u4e3d\u96ef\u4f1a\u8ba9\u4ed6\u4eec\u77e5\u96be\u800c\u9000\u7684\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u7ad9\u5f97\u7a0d\u540e\u4e00\u4e9b\uff0c\u4fa7\u8fc7\u5934\u4f4e\u58f0\u95ee\u8eab\u8fb9\u4e00\u540d\u897f\u88c5\u9769\u8986\u3001\u8179\u90e8\u5fae\u9f13\u7684\u4e2d\u5e74\u4eba\uff0c\u201c\u542c\u8bf4\u7530\u4e3b\u4efb\u559c\u6b22\u76ae\u80a4\u5ae9\u7684\u6f02\u4eae\u5973\u5b69\u5b50\uff0c\u5de6\u8fb9\u90a3\u4e2a\u5973\u5b69\u5b50\u4e0d\u9519\uff0c\u76ae\u80a4\u597d\uff0c\u817f\u4e5f\u957f\uff0c\u76f8\u4fe1\u8ba9\u4f60\u7530\u5385\u957f\u4f60\u5148\u9009\u4e00\u5b9a\u4f1a\u9009\u5979\uff0c\u201d\u53c8\u9876\u4e86\u9876\u5de6\u8fb9\u7684\u5934\u9876\u5fae\u79c3\u7684\u4e2d\u5e74\u4eba\uff0c\u201c\u9a6c\u5385\u957f\u4e0d\u4f1a\u8ddf\u7530\u4e3b\u4efb\u4e89\u5427\uff1f\u201d\u5934\u9876\u5fae\u79c3\u7684\u4e2d\u5e74\u4eba\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6307\u4e0d\u5b9a\u8001\u7530\u4e0d\u4e2d\u610f\u90a3\u4e2a\u957f\u817f\u7684\u8981\u8ddf\u6211\u4ea4\u6362\u5462\u2026\u2026\u201d\u4ed6\u4eec\u53ea\u5f53\u5728\u8fd9\u91cc\u5c31\u7b97\u6709\u542c\u5f97\u61c2\u4e2d\u6587\u7684\u4e5f\u90fd\u662f\u4e00\u4e9b\u5728\u4e1c\u4eac\u793e\u4f1a\u4f4e\u5c42\u6323\u624e\u7684\u7a77\u7559\u5b66\u751f\uff0c\u8bf4\u8bdd\u8086\u65e0\u5fcc\u60ee\uff0c\u8138\u4e0a\u8fd8\u6302\u7740\u7316\u7357\u7684\u7b11\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u4e0b\u610f\u8bc6\u7684\u56de\u5934\u77a5\u4e86\u4e00\u773c\uff0c\u770b\u5230\u7ad9\u5728\u4ed6\u4eec\u8eab\u540e\u7684\u674e\u99a8\u4e88\u3002\u674e\u99a8\u4e88\u542c\u5230\u4ed6\u7684\u8bdd\uff0c\u6b63\u6c14\u607c\u7684\u76ef\u7740\u4ed6\u7684\u540e\u8111\u52fa\u3002\u674e\u99a8\u4e88\u7684\u5bb9\u989c\u660e\u8273\u8000\u773c\uff0c\u773c\u795e\u660e\u6f88\uff0c\u89c6\u7ebf\u76f8\u5bf9\u4e4b\u4e0b\uff0c\u845b\u840c\u5747\u8fd9\u79cd\u7eb5\u6b32\u82b1\u4e1b\u7684\u8001\u624b\uff0c\u77a5\u4e86\u4e00\u773c\u5374\u5fc3\u614c\u614c\u7684\u5c06\u89c6\u7ebf\u60ca\u95ea\u5f00\uff0c\u6123\u662f\u6ca1\u770b\u5230\u4e0e\u674e\u99a8\u4e88\u5e76\u80a9\u800c\u7ad9\u7684\u5f20\u606a\u3002</p><p>\u3000\u3000\u4e0e\u845b\u836b\u5747\u5e76\u80a9\u7ad9\u7740\u7684\u90a3\u4e2a\u7ed9\u845b\u836b\u5747\u79f0\u7530\u4e3b\u4efb\u7684\u4e2d\u5e74\u4eba\u770b\u5230\u845b\u836b\u5747\u7684\u5f02\u5e38\uff0c\u56de\u8fc7\u5934\u6765\u770b\uff0c\u770b\u5230\u674e\u99a8\u4e88\uff0c\u6068\u4e0d\u80fd\u5c06\u773c\u775b\u6263\u51fa\u6765\u4e22\u674e\u99a8\u4e88\u8eab\u4e0a\u53bb\uff0c\u53ea\u5f53\u51fa\u73b0\u5728\u5458\u5de5\u4f11\u606f\u533a\u7684\u5973\u5b69\u5b50\u90fd\u662f\u4fc3\u9500\u5973\u90ce\uff0c\u6015\u4ed6\u7684\u8272\u53cb\u8ddf\u4ed6\u4e89\uff0c\u5bf9\u674e\u99a8\u4e88\u8eab\u8fb9\u7684\u5f20\u606a\u4e5f\u89c6\u82e5\u65e0\u7779\uff0c\u7adf\u7136\u6025\u8272\u7684\u4f38\u624b\u8fc7\u6765\u8981\u6293\u674e\u99a8\u4e88\u7684\u624b\u81c2\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u4e5f\u662f\u5230\u4e1c\u4eac\u6765\u7559\u5b66\u7684\u4e2d\u56fd\u5b66\u751f\u5427\uff0c\u6211\u4eec\u521a\u521a\u5230\u4e1c\u4eac\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u518d\u4f38\u624b\u5241\u4e86\u4f60\u7684\u72d7\u722a\u5b50\uff0c\u201d\u5f20\u606a\u4f38\u624b\u6293\u90a3\u4e2a\u7530\u4e3b\u4efb\u7684\u624b\u8155\uff0c\u731b\u7684\u4e00\u62c9\u987a\u624b\u626f\u7740\u4ed6\u7684\u8863\u9886\uff0c\u201c\u4e22\u4eba\u4e22\u51fa\u56fd\u95e8\u4e86\uff01\u201d\u53c8\u538c\u6076\u7684\u63a8\u5230\u4e00\u8fb9\u53bb\u3002\u674e\u99a8\u4e88\u5012\u662f\u6709\u4e9b\u5413\u7740\uff0c\u4e0b\u610f\u8bc6\u7684\u4f9d\u5728\u5f20\u606a\u7684\u8eab\u8fb9\u3002</p><p>\u3000\u3000\u90a3\u4e2a\u7530\u4e3b\u4efb\u8eab\u80a5\u4f53\u5f31\uff0c\u7ed9\u5f20\u606a\u4e00\u62c9\u4e00\u626f\uff0c\u5dee\u70b9\u6574\u4e2a\u4eba\u90fd\u6454\u5012\u5728\u5730\uff0c\u624b\u6491\u7740\u5730\u72fc\u72c8\u4e0d\u582a\u7684\u7ad9\u8d77\u6765\uff0c\u77aa\u7740\u5f20\u606a\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u968f\u4fbf\u5c31\u52a8\u624b\u52a8\u817f\u7684\uff0c\u8fd8\u8bb2\u4e0d\u8bb2\u7406\u4e86\uff1f\u4e0d\u8fc7\u5c31\u662f\u4ece\u56fd\u5185\u6765\u4e1c\u4eac\u7559\u5b66\u7684\u7a77\u5b66\u751f\uff0c\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\uff0c\u6211\u5c31\u662f\u95ee\u4e00\u95ee\uff0c\u6211\u6709\u505a\u4ec0\u4e48\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u77aa\u4e86\u4ed6\u4e00\u773c\uff1a\u201c\u778e\u4e86\u4f60\u7684\u72d7\u773c\u2026\u2026\u4ece\u56fd\u5185\u6765\u4e1c\u4eac\u7684\u7a77\u5b66\u751f\u5c31\u662f\u4f60\u4eec\u8fd9\u4e9b\u4ece\u56fd\u5185\u6765\u7684\u72d7\u5b98\u597d\u6b3a\u8d1f\u7684\uff1f\u201d\u8981\u4e0d\u662f\u6015\u52a8\u9759\u95f9\u5927\u4e86\u53cd\u800c\u4f1a\u7ed9\u90a3\u4e9b\u522b\u6709\u7528\u5fc3\u7684\u65e5\u672c\u5a92\u4f53\u80e1\u4e71\u7092\u4f5c\uff0c\u5f20\u606a\u80fd\u4e00\u811a\u8ddf\u7740\u8e39\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u8fd9\u624d\u56de\u5934\u770b\u5230\u5f20\u606a\uff0c\u6123\u5728\u90a3\u91cc\uff0c\u53e6\u4e00\u4e2a\u53eb\u9a6c\u5385\u957f\u7684\u4eba\u4e5f\u4e0d\u6562\u8ddf\u8eab\u5f3a\u4f53\u5065\u7684\u5f20\u606a\u4e89\u4ec0\u4e48\u610f\u6c14\uff0c\u53ea\u662f\u5c06\u7530\u4e3b\u4efb\u6400\u8d77\u6765\uff0c\u5012\u662f\u90a3\u4e2a\u4e3b\u52a8\u7ad9\u51fa\u6765\u5411\u6797\u4e3d\u96ef\u3001\u8096\u96c1\u5b81\u62c9\u76ae\u6761\u7684\u90a3\u4e2a\u9752\u5e74\u770b\u5230\u8fd9\u60c5\u5f62\uff0c\u8981\u8fc7\u6765\u8ddf\u5f20\u606a\u62fc\u547d\uff0c\u5374\u7ed9\u53ca\u65f6\u8d76\u8fc7\u6765\u7684\u5085\u4fca\u6293\u4f4f\u5de6\u80a9\u7a9d\u65e0\u6cd5\u52a8\u5f39\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u6307\u7740\u845b\u836b\u5747\uff0c\u538b\u7740\u55d3\u5b50\u8bad\u65a5\u9053\uff1a\u201c\u4e0d\u8981\u4e22\u4eba\u4e22\u5728\u8fd9\u91cc\uff0c\u845b\u5bb6\u4e5f\u662f\u6709\u5934\u6709\u8138\u8981\u8138\u9762\u7684\u3002\u201d</p><p>\u3000\u3000\u845b\u836b\u5747\u54ea\u91cc\u80fd\u60f3\u5230\u5728\u8fd9\u91cc\u4f1a\u9047\u5230\u5f20\u606a\u8fd9\u4e2a\u715e\u661f\uff1f\u81ea\u4ece\u5728\u9999\u6e2f\u4e2d\u94f6\u5927\u53a6\u524d\u7ed9\u5085\u4fca\u4e00\u638c\u6253\u843d\u4e24\u9897\u69fd\u7259\u4e4b\u540e\uff0c\u4ed6\u5fc3\u91cc\u5c31\u6709\u4e9b\u754f\u60e7\u770b\u5230\u5f20\u606a\u3002\u5f20\u606a\u8fd9\u5bb6\u4f19\u7b80\u76f4\u5c31\u662f\u4e00\u4e2a\u66b4\u53d1\u6237\uff0c\u662f\u90a3\u79cd\u4e00\u8a00\u4e0d\u5408\u5c31\u80fd\u52a8\u624b\u7684\u5bb6\u4f19\uff0c\u54ea\u6709\u4e16\u5bb6\u5b50\u5f1f\u7684\u6c14\u5ea6\uff0c\u8981\u662f\u5728\u8fd9\u91cc\u518d\u7ed9\u5f20\u606a\u7684\u4fdd\u9556\u6559\u8bad\u4e00\u987f\uff0c\u90a3\u624d\u53eb\u51a4\u6789\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u626f\u4e86\u626f\u90a3\u4e2a\u53eb\u7530\u4e3b\u4efb\u7684\u4e2d\u5e74\u4eba\uff0c\u8033\u8bed\u4e86\u4e24\u53e5\uff0c\u7530\u4e3b\u4efb\u53d1\u8bdd\u8ba9\u90a3\u4e2a\u7ed9\u5085\u4fca\u6293\u4f4f\u80a9\u7a9d\u65e0\u6cd5\u52a8\u5f39\u7684\u9752\u5e74\u606f\u4e8b\u5b81\u4eba\uff0c\u56db\u4e2a\u4eba\u5c31\u7070\u6e9c\u6e9c\u7684\u79bb\u5f00\u4e86\uff0c\u53ea\u662f\u4ed6\u4eec\u56db\u4e2a\u4eba\u79bb\u5f00\u65f6\u8fd8\u6709\u4e9b\u4e0d\u7518\u5fc3\uff0c\u8fd8\u56de\u5934\u6028\u6068\u7684\u77aa\u4e86\u4e24\u773c\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u629a\u7740\u989d\u5934\u8ddf\u674e\u99a8\u4e88\u65e0\u5948\u7684\u82e6\u7b11\u4e86\u4e24\u4e0b\uff0c\u674e\u99a8\u4e88\u62b1\u6b49\u7684\u7b11\u4e86\u7b11\uff0c\u5012\u662f\u4e3a\u5979\u5f15\u8d77\u8fd9\u4ef6\u7ea0\u7eb7\u800c\u5185\u759a\u3002\u770b\u7740\u8096\u96c1\u5b81\u3001\u6797\u4e3d\u96ef\u4f3c\u4e4e\u6709\u4e9b\u7ed9\u5413\u4f4f\u4e86\uff0c\u5f20\u606a\u624d\u5f3a\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u79cd\u4e8b\u4f60\u4eec\u4ee5\u524d\u6ca1\u6709\u9047\u5230\u8fc7\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u521a\u624d\u6211\u8ddf\u96c1\u5b81\u5728\u9910\u5385\u91cc\u5403\u996d\u65f6\uff0c\u90a3\u4e2a\u4e2d\u5e74\u4eba\u5c31\u6076\u5fc3\u7684\u7c98\u5728\u8fb9\u4e0a\uff0c\u6ca1\u60f3\u5230\u4ed6\u4eec\u662f\u4ece\u56fd\u5185\u6765\u7684\u3002\u201d\u6797\u4e3d\u96ef\u8bf4\u9053\uff0c\u201c\u4ed6\u53ef\u80fd\u542c\u5230\u6211\u4eec\u8bf4\u8bdd\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8096\u96c1\u5b81\u672c\u6765\u60c5\u7eea\u5c31\u4e0d\u7a33\u5b9a\uff0c\u53c8\u95f9\u51fa\u8fd9\u4e00\u51fa\uff0c\u8138\u8272\u66f4\u662f\u96be\u770b\u3002</p><p>\u3000\u3000\u867d\u7136\u95f9\u51fa\u4e9b\u52a8\u9759\uff0c\u4f46\u662f\u4e8b\u60c5\u5728\u5f88\u77ed\u7684\u65f6\u95f4\u91cc\u5c31\u5e73\u606f\u4e86\uff0c\u4e5f\u6ca1\u6709\u5f15\u8d77\u591a\u5c11\u4eba\u7684\u56f4\u89c2\uff0c\u5f20\u606a\u8e59\u7740\u7709\u5934\u8bf4\u9053\uff1a\u201c\u7b97\u4e86\uff0c\u6211\u4eec\u5148\u51fa\u53bb\u5427\u2026\u2026\u201d\u4e0e\u674e\u99a8\u4e88\u3001\u8096\u96c1\u5b81\u3001\u6797\u4e3d\u96ef\u5feb\u6b65\u5f80\u4f1a\u5c55\u4e2d\u5fc3\u5916\u8d70\u53bb\u3002\u4e0a\u8f66\u524d\uff0c\u907f\u5f00\u5176\u4ed6\u4eba\uff0c\u5f20\u606a\u8ddf\u5085\u4fca\u8bf4\uff1a\u201c\u90a3\u4e2a\u59d3\u7530\u7684\u4e2d\u5e74\u80d6\u5b50\uff0c\u6211\u6709\u4e9b\u5370\u8c61\uff0c\u662f\u6c5f\u5357\u7701\u7ecf\u8d38\u59d4\u7684\uff0c\u56de\u56fd\u540e\u4f60\u8bb0\u5f97\u8ba9\u4eba\u67e5\u4e00\u67e5\u2026\u2026\u201d\u8fd9\u4e8b\u60c5\u770b\u8d77\u6765\u4e5f\u7b80\u5355\uff0c\u5609\u4fe1\u7535\u5b50\u5728\u65e5\u672c\u505a\u4e1c\uff0c\u4ee5\u51fa\u56fd\u8003\u5bdf\u7684\u540d\u4e49\u8bf7\u6c5f\u5357\u7701\u91cc\u7684\u5b98\u5458\u5230\u4e1c\u4eac\u6765\u9b3c\u6df7\uff0c\u90fd\u4e0d\u6653\u5f97\u91d1\u5c71\u5e02\u7684\u7535\u5b50\u5de5\u4e1a\u5236\u9020\u57fa\u5730\u9879\u76ee\u8fdb\u884c\u5230\u54ea\u4e00\u6b65\uff0c\u770b\u4e0a\u53bb\u5609\u4fe1\u7535\u5b50\u6293\u5f97\u4e5f\u5f88\u7d27\u554a\u3002</p></article>"}}}]);
