(window.webpackJsonp=window.webpackJsonp||[]).push([[768],{791:function(p){p.exports={chapterTitle:null,title:"\u7b2c602\u7ae0 \u8981\u627f\u62c5\u7684\u98ce\u9669",content:"<article><h2>\u7b2c602\u7ae0 \u8981\u627f\u62c5\u7684\u98ce\u9669</h2><p>\u3000\u3000\u5bf9\u5404\u7701\u5e02\u56fd\u6295\u516c\u53f8\u7684\u6478\u5e95\u6e05\u67e5\u5de5\u4f5c\u63a5\u8fd1\u5c3e\u58f0\uff0c\u8d8a\u662f\u63a5\u8fd1\u5c3e\u58f0\uff0c\u6e05\u67e5\u51fa\u6765\u7684\u6570\u636e\u8d8a\u662f\u8ba9\u4eba\u80c6\u98a4\u5fc3\u60ca\uff0c\u4ec5\u5e7f\u56fd\u6295\u4e00\u5bb6\u67e5\u51fa\u6765\u7684\u5c31\u6709\u8fd1\u767e\u4ebf\u7684\u7f3a\u53e3\uff0c\u5168\u56fd\u5171\u6709\u51e0\u767e\u5bb6\u7a97\u53e3\u4fe1\u6258\u516c\u53f8\uff0c\u65e0\u6cd5\u60f3\u8c61\u4f1a\u6709\u591a\u5927\u7684\u6f0f\u6d1e\u9690\u85cf\u5728\u6697\u5904\u3002</p><p>\u3000\u3000\u51e0\u4f4d\u4e2d\u592e\u9886\u5bfc\u5305\u62ec\u4e3b\u7ba1\u91d1\u878d\u7ecf\u6d4e\u5de5\u4f5c\u7684\u526f\u603b\u7406\u8d75\u6d4e\u4e1c\u90fd\u7ed9\u8fd9\u6837\u7684\u7ed3\u679c\u5413\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u8fd9\u65f6\u5019\u8c01\u90fd\u6ca1\u6709\u81ea\u4fe1\u8bf4\u91d1\u878d\u98ce\u66b4\u79bb\u81ea\u5df1\u8fd8\u5f88\u8fdc\u3002</p><p>\u3000\u3000\u4e2d\u592e\u7684\u610f\u89c1\u662f\u5c06\u5ba1\u8ba1\u6e05\u67e5\u5de5\u4f5c\u7ee7\u7eed\u6df1\u5165\u4e0b\u53bb\uff0c\u8054\u5408\u8d22\u653f\u90e8\u3001\u5ba1\u8ba1\u5c40\u7b49\u90e8\u59d4\u6210\u7acb\u6e05\u67e5\u5c0f\u7ec4\uff0c\u8d75\u6d4e\u4e1c\u526f\u603b\u7406\u4eb2\u81ea\u6302\u5e05\uff0c\u627e\u5230\u5f90\u5b66\u5e73\u8981\u4ed6\u8fc7\u53bb\u5f53\u526f\u624b\u5177\u4f53\u8d1f\u8d23\u5ba1\u8ba1\u6e05\u67e5\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u9886\u5bfc\u5ba1\u8ba1\u6e05\u67e5\u5c0f\u7ec4\u5230\u5404\u4e2a\u7701\u5e02\u53bb\u6316\u6839\u7a76\u5e95\u603b\u4e0d\u80fd\u7b97\u662f\u4e00\u4ef6\u8ba8\u597d\u7684\u6d3b\uff0c\u4f46\u6bd5\u7adf\u662f\u51fa\u6765\u505a\u4e9b\u5b9e\u9645\u7684\u5de5\u4f5c\u3002\u5f53\u7136\uff0c\u8fd9\u5e76\u4e0d\u662f\u5f20\u606a\u5e0c\u671b\u770b\u5230\u7684\u7406\u60f3\u7ed3\u679c\uff0c\u4e0e\u5176\u51fa\u6765\u4e3b\u6301\u6e05\u67e5\u5de5\u4f5c\uff0c\u8fd8\u4e0d\u5982\u518d\u5b89\u9759\u7684\u7b49\u4e0a\u4e00\u5e74\uff0c\u5fc3\u60f3\u5f90\u5b66\u5e73\u53ea\u662f\u6e34\u671b\u51fa\u6765\u505a\u4e9b\u5b9e\u9645\u7684\u5de5\u4f5c\u5427\uff0c\u800c\u4ee5\u4ed6\u9c9c\u660e\u7684\u6027\u683c\uff0c\u603b\u505a\u4e0d\u4e86\u4e24\u9762\u90fd\u8ba8\u597d\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u51cc\u6668\u7684\u591c\u7a7a\u4eff\u4f5b\u88ab\u7740\u5149\u7684\u5b9d\u84dd\u8272\u8f7b\u7ef8\uff0c\u901a\u5b8c\u7535\u8bdd\uff0c\u5f20\u606a\u8ba9\u5085\u4fca\u5c06\u8f66\u505c\u5728\u5e08\u5927\u4e8c\u62db\u697c\u524d\u7684\u505c\u8f66\u573a\u4e0a\uff0c\u4e0e\u5085\u4fca\u5750\u5728\u8f66\u91cc\u5438\u4e86\u4e00\u4f1a\u513f\u70df\uff0c\u624d\u56de\u5230\u5c4b\u91cc\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9ad8\u79cb\u4e4b\u540e\uff0c\u5efa\u90ba\u7684\u5929\u6c14\u5df2\u7ecf\u4e0d\u90a3\u4e48\u9177\u70ed\u96be\u71ac\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u521b\u4e1a\u56ed\u533a\u4e00\u671f\u5de5\u7a0b\u63a5\u8fd1\u63a5\u8fd1\u5c3e\u58f0\uff0c\u516b\u6708\u4e09\u5341\u65e5\uff0c\u6a61\u6811\u56ed\u521b\u4e1a\u6295\u8d44\u4e2d\u5fc3\uff0c\u5728\u56ed\u533a\u4e3e\u884c\u63ed\u724c\u4eea\u5f0f\uff0c\u90ed\u677e\u5ef6\u3001\u67f3\u5fd7\u6210\u4e5f\u5e94\u9080\u53c2\u52a0\uff0c\u51fa\u5e2d\u63ed\u724c\u4eea\u5f0f\u7684\u8fd8\u6709\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u7b49\u7701\u5e02\u9886\u5bfc\u3002</p><p>\u3000\u3000\u5df2\u7ecf\u770b\u4e0d\u5230\u56db\u4e2a\u6708\u524d\u4e3d\u4e30\u5370\u67d3\u5382\u7684\u4e00\u70b9\u5f71\u5b50\uff0c\u7f57\u541b\u8be7\u5f02\u4e4b\u4f59\u6307\u7740\u9694\u58c1\u7684\u6f02\u67d3\u5382\u8ddf\u90ed\u677e\u5ef6\u3001\u67f3\u5fd7\u6210\u4ecb\u7ecd\u8bf4\uff1a\u201c\u4e94\u6708\u6211\u8fc7\u6765\u770b\u65f6\uff0c\u8fd9\u91cc\u5c31\u8ddf\u90a3\u8fb9\u7684\u6f02\u67d3\u5382\u6beb\u65e0\u4e8c\u81f4\uff0c\u8981\u8bf4\u6709\u533a\u522b\uff0c\u53ef\u80fd\u8fd8\u8981\u66f4\u7b80\u964b\u4e9b\u2026\u2026\u8fc7\u4e9b\u65e5\u5b50\uff0c\u90a3\u7247\u5382\u533a\u4e5f\u8981\u6539\u9020\u6210\u4e0e\u8fd9\u8fb9\u540c\u7b49\u89c4\u683c\u7684\u56ed\u533a\u3002\u201d</p><p>\u3000\u3000\u8fc7\u6765\u4e4b\u524d\uff0c\u5f20\u606a\u7279\u610f\u5b89\u6392\u8f66\u961f\u7a7f\u8fc7\u8fd8\u672a\u5168\u9762\u5b9e\u65bd\u6539\u9020\u7684\u6f02\u67d3\u5382\uff0c\u6f02\u67d3\u5382\u6b63\u8fdb\u884c\u7ebf\u7f06\u5730\u6c9f\u5de5\u7a0b\uff0c\u66f4\u663e\u5f97\u5382\u533a\u7684\u7834\u843d\u3002</p><p>\u3000\u3000\u4e24\u76f8\u6bd4\u8f83\uff0c\u6539\u5efa\u540e\u7684\u65b0\u56ed\u533a\u9887\u5177\u73b0\u4ee3\u56ed\u6797\u7684\u610f\u5473\uff0c\u56ed\u533a\u5185\u7ea2\u6a61\u6811\u7684\u6811\u53f6\u6b63\u5410\u9732\u521d\u79cb\u540e\u7684\u5fae\u7ea2\uff0c\u4e3b\u5e72\u9053\u4e24\u4fa7\u7684\u5899\u9762\u90fd\u7528\u5404\u7c7b\u77f3\u6750\u8d34\u9762\u6784\u753b\u51fa\u5947\u601d\u5999\u60f3\u7684\u56fe\u6848\uff0c\u8fd9\u4e9b\u5f62\u72b6\u602a\u5f02\u7684\u56fe\u6848\u8ba9\u4e00\u5217\u5217\u6574\u996c\u7684\u9ad8\u5927\u623f\u820d\u4e0d\u518d\u5355\u8c03\uff1b\u56ed\u533a\u4e4b\u5185\u8fd8\u6709\u4e00\u9053\u5375\u77f3\u94fa\u6210\u7684\u6d45\u6eaa\u4ece\u71d5\u5f52\u6e56\u5f15\u6765\u6e05\u6d41\u73af\u7ed5\u800c\u8fc7\uff0c\u5899\u89d2\u5c4b\u5c3e\u4e0d\u5e38\u6ce8\u610f\u5230\u7684\u5730\u65b9\uff0c\u5e38\u5e38\u4f1a\u6709\u4e00\u7566\u7fe0\u7af9\u63a9\u6620\uff0c\u5927\u7247\u7684\u8349\u9642\u8986\u76d6\u4e18\u5cad\u8d77\u4f0f\u72b6\u7684\u7a7a\u5730\u95f4\u3002</p><p>\u3000\u3000\u539f\u5148\u7c97\u964b\u7684\u5382\u623f\u5185\u90e8\u4e5f\u6574\u9970\u4e00\u65b0\uff0c\u7f51\u7edc\u5149\u7ea4\u7b49\u57fa\u7840\u786c\u4ef6\u4e00\u5e94\u5177\u9f50\uff0c\u53ea\u7b49\u8fdb\u5165\u56ed\u533a\u7684\u4f01\u4e1a\u81ea\u884c\u53d1\u6325\u521b\u610f\u8fdb\u884c\u5de5\u4f5c\u5185\u73af\u5883\u7684\u521b\u9020\u3002</p><p>\u3000\u3000\u201c\u786e\u5b9a\u4e0d\u4f1a\u62ff\u53bb\u5c55\u89c8\u9986\u4e4b\u7c7b\u7684\u7528\u9014\uff1f\u201d\u90ed\u677e\u5ef6\u5e26\u7740\u4e9b\u60ca\u8bb6\u5f00\u73a9\u7b11\u7684\u95ee\u4e86\u4e00\u53e5\uff0c\u4ed6\u7684\u786e\u65e0\u6cd5\u60f3\u8c61\u4e00\u5bb6\u6c11\u8425\u4f01\u4e1a\u4f1a\u6295\u8d44\u521b\u529e\u5b75\u5316\u56ed\uff0c\u8fd8\u5728\u56ed\u533a\u7684\u5efa\u8bbe\u4e0a\u4e0d\u8ba1\u8840\u672c\u7684\u6295\u5165\uff0c\u201c\u5317\u4eac\u4e2d\u5173\u6751\u641e\u4e86\u4e00\u4e2a\u521b\u4e1a\u5b75\u5316\u56ed\uff0c\u6211\u4e00\u65e9\u5c31\u53bb\u53c2\u89c2\u8fc7\u56ed\u533a\u5efa\u8bbe\u2026\u2026\u54e6\uff0c\u4e0d\u8981\u8bf4\u56fd\u5185\u7684\u5b75\u5316\u56ed\u533a\u4e86\uff0c\u4e9a\u6d32\u5730\u533a\uff0c\u6211\u8fd8\u6ca1\u6709\u89c1\u57fa\u5efa\u80fd\u6bd4\u5f97\u4e0a\u8fd9\u91cc\u7684\u5b75\u5316\u56ed\u533a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u4ec5\u4ec5\u662f\u786c\u4ef6\u5efa\u8bbe\u6bd4\u8f83\u51fa\u8272\u800c\u5df2\uff0c\u8981\u6210\u4e3a\u5404\u65b9\u9762\u6307\u6807\u90fd\u4f18\u5f02\u7684\u5b75\u5316\u56ed\u533a\uff0c\u8981\u8d70\u7684\u8def\u5b9e\u5728\u592a\u8fdc\u4e86\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u6a61\u6811\u56ed\u80fd\u5426\u6210\u529f\uff0c\u5e76\u4e0d\u53d6\u51b3\u4e8e\u5728\u786c\u4ef6\u8bbe\u65bd\u5efa\u8bbe\u4e0a\u6295\u8d44\u591a\u5c11\uff0c\u4f18\u60e0\u653f\u7b56\u3001\u6280\u672f\u652f\u6301\u3001\u878d\u8d44\u670d\u52a1\u3001\u521b\u4e1a\u6307\u5bfc\u7b49\u8f6f\u73af\u5883\u7684\u5efa\u8bbe\u624d\u662f\u5173\u952e\u3002</p><p>\u3000\u3000\u71d5\u5f52\u6e56\u7554\u7684\u90a3\u4e9b\u6c61\u67d3\u6e90\u5728\u4e09\u4e2a\u6708\u524d\u90fd\u5f7b\u5e95\u6e05\u9664\uff0c\u71d5\u5f52\u6e56\u60f3\u8981\u51ed\u501f\u81ea\u8eab\u7684\u51c0\u6c61\u80fd\u529b\u8fd8\u8981\u597d\u4e9b\u5e74\u624d\u80fd\u6062\u590d\u751f\u6001\u4f53\u7cfb\u7684\u5e73\u8861\u3002\u4e3a\u4e86\u914d\u5408\u56ed\u533a\u7684\u5efa\u8bbe\uff0c\u5efa\u90ba\u5e02\u653f\u5de5\u7a0b\u516c\u53f8\u6295\u8d44\u5343\u4e07\u5bf9\u71d5\u5f52\u6e56\u4e1c\u5317\u8fb9\u7684\u6e56\u57df\u8fdb\u884c\u4e3a\u671f\u4e09\u4e2a\u6708\u7684\u6e05\u6de4\u5904\u7406\uff0c\u5012\u662f\u8ba9\u8fd1\u5cb8\u7684\u6e56\u6c34\u4e00\u65f6\u95f4\u6e05\u6f88\u8d77\u6765\u3002</p><p>\u3000\u3000\u51fa\u4e86\u521b\u4e1a\u56ed\u4e3d\u4e30\u5382\u56ed\u533a\uff0c\u6cbf\u7740\u4eca\u590f\u65b0\u780c\u7684\u71d5\u5f52\u6e56\u7554\u7684\u77f3\u9a73\u5cb8\u5f80\u897f\u8d70\uff0c\u7ad9\u5728\u7ea2\u6a61\u6811\u7684\u8302\u5bc6\u7684\u6811\u836b\u4e0b\uff0c\u6307\u70b9\u7740\u4e1c\u4fa7\u5360\u5730\u89c4\u6a21\u6709\u4e94\u767e\u4ea9\u7684\u7e41\u5fd9\u5de5\u5730\u3002</p><p>\u3000\u3000\u4e1c\u9762\u7684\u6f02\u67d3\u5382\u533a\u6539\u5efa\u662f\u521b\u4e1a\u56ed\u533a\u7684\u4e8c\u671f\u5de5\u7a0b\uff0c\u4e0d\u8fc7\u6a61\u6811\u56ed\u8ba1\u5212\u5728\u5efa\u90ba\u7684\u6295\u8d44\u91cd\u70b9\u5374\u662f\u4f4d\u4e8e\u521b\u4e1a\u56ed\u533a\u897f\u4fa7\u7684\u5fae\u7535\u5b50\u57fa\u7840\u6280\u672f\u4e0e\u79fb\u52a8\u901a\u8baf\u6280\u672f\u4e24\u4e2a\u56fd\u5bb6\u7ea7\u5b9e\u9a8c\u5ba4\u4e0e\u4e00\u4e2a\u5305\u62ec\u6750\u6599\u5206\u6790\u3001\u96c6\u6210\u7535\u8def\u8bbe\u8ba1\u5f00\u53d1\u652f\u6301\u5728\u5185\u7684\u5fae\u7535\u5b50\u6280\u672f\u4e0e\u8f6f\u4ef6\u5f00\u53d1\u7efc\u5408\u670d\u52a1\u4e2d\u5fc3\uff0c\u4ec5\u8fd9\u4e24\u4e2a\u5b9e\u9a8c\u5ba4\u4e0e\u4e00\u4e2a\u670d\u52a1\u4e2d\u5fc3\u7684\u6295\u8d44\u91d1\u989d\u5c31\u5c06\u8d85\u8fc7\u4e00\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u897f\u9762\u7684\u5730\u5df2\u7ecf\u5e73\u6574\u8fc7\uff0c\u5de5\u7a0b\u961f\u90fd\u5df2\u7ecf\u4e0a\u4e86\u5de5\u5730\u5f00\u59cb\u6253\u5730\u57fa\u3002</p><p>\u3000\u3000\u4e09\u680b\u5b9e\u9a8c\u5ba4\u4e3b\u4f53\u5efa\u7b51\u5c31\u8981\u8038\u7acb\u5728\u71d5\u5f52\u6e56\u7554\uff0c\u4e3a\u4e86\u4f7f\u71d5\u5f52\u6e56\u7554\u7684\u98ce\u666f\u4e0d\u4f1a\u56e0\u4e3a\u9ad8\u5c42\u5efa\u7b51\u800c\u663e\u5f97\u7a81\u5140\uff0c\u79bb\u6e56\u6700\u8fdc\u7684\u4e00\u680b\u697c\u6807\u9ad8\u4e5f\u4e0d\u8fc7\u53bb\u4e8c\u5341\u56db\u7c73\uff0c\u4e34\u6e56\u7684\u4e24\u680b\u697c\u624d\u5341\u516d\u7c73\u4e0d\u5230\uff0c\u4ece\u8bbe\u8ba1\u56fe\u4e0a\u770b\uff0c\u90fd\u662f\u4f20\u7edf\u7684\u697c\u9601\u5efa\u7b51\u98ce\u683c\uff0c\u518d\u63a9\u6620\u4e9b\u8302\u5bc6\u800c\u9ad8\u5927\u7684\u7af9\u6811\uff0c\u5c31\u80fd\u4e0e\u71d5\u5f52\u6e56\u7684\u666f\u8272\u878d\u4e3a\u4e00\u4f53\u3002</p><p>\u3000\u3000\u73b0\u5728\u8fd8\u53ea\u80fd\u5728\u56fe\u7eb8\u4e0a\u60f3\u8c61\u672a\u6765\u8fd9\u4e2a\u7814\u53d1\u57fa\u5730\u7684\u58ee\u4e3d\u89c4\u6a21\uff0c\u90ed\u677e\u5ef6\u7709\u98de\u8272\u821e\uff0c\u4f5c\u4e3a\u534e\u4eba\uff0c\u5012\u662f\u975e\u5e38\u4e50\u610f\u770b\u5230\u5185\u5730\u7684\u4f01\u4e1a\u80fd\u6709\u8fd9\u4e48\u5927\u7684\u6c14\u9b44\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u6210\u5728\u5efa\u90ba\u7684\u8fd9\u51e0\u5929\u5374\u4e00\u76f4\u90fd\u6c89\u9ed8\u5be1\u8a00\uff0c\u770b\u5230\u9526\u6e56\u5728\u6280\u672f\u6269\u5f20\u7684\u7470\u4e3d\u613f\u666f\uff0c\u773c\u775b\u91cc\u95ea\u8fc7\u5149\u8292\uff0c\u66f4\u591a\u7684\u65f6\u5019\u662f\u9009\u62e9\u6c89\u9ed8\u3002</p><p>\u3000\u3000\u4e00\u5bb6\u4e4b\u524d\u8fd8\u53ea\u80fd\u751f\u4ea7\u5f71\u789f\u673a\u7684\u4f01\u4e1a\u8981\u6295\u8d44\u641e\u5fae\u7535\u5b50\u57fa\u7840\u5b9e\u9a8c\u5ba4\uff0c\u5728\u6280\u672f\u6269\u5f20\u4e0a\u7edd\u5bf9\u79f0\u5f97\u4e0a\u91ce\u5fc3\u52c3\u52c3\u3002</p><p>\u3000\u3000\u524d\u671f\u51e0\u4e2a\u4ebf\u7684\u6295\u8d44\uff0c\u4e5f\u4ec5\u4ec5\u662f\u94fa\u4e2a\u5e95\uff0c\u65e5\u540e\u7684\u7814\u53d1\u8d44\u91d1\u9700\u6c42\u66f4\u50cf\u4e00\u53ea\u80fd\u5438\u5e72\u6d77\u6c34\u7684\u6050\u6016\u6d77\u7ef5\u4f53\u3002\u9526\u6e56\u624b\u91cc\u634f\u7740\u56db\u5341\u4ebf\uff0c\u90fd\u672a\u5fc5\u80fd\u6491\u5230\u51fa\u6210\u679c\u7684\u90a3\u4e00\u5929\uff1b\u5e78\u597d\u9526\u6e56\u624b\u91cc\u634f\u7740\u4e00\u5f20\u5185\u5730\u6570\u5b57\u624b\u673a\u7684\u751f\u4ea7\u724c\u7167\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u9526\u6e56\u66f4\u591a\u7684\u6280\u672f\u673a\u5bc6\uff0c\u67f3\u5fd7\u6210\u4e5f\u4e0d\u4f1a\u6e05\u695a\uff0c\u4ed6\u53ea\u80fd\u4ece\u4e13\u4e1a\u7684\u773c\u5149\u53bb\u5206\u6790\u770b\u5230\u7684\u4e00\u5207\u3002</p><p>\u3000\u3000\u5728\u96c6\u6210\u7535\u8def\u751f\u4ea7\u8fdb\u516b\u5341\u5e74\u4ee3\u4e2d\u671f\u6765\u5206\u5212\u6210\u56db\u4e2a\u754c\u9650\u5206\u660e\u7684\u4ea7\u4e1a\u96c6\u7fa4\u4e4b\u540e\uff0c\u53f0\u79ef\u7535\u8fd1\u51e0\u5e74\u5728\u6676\u5706\u5236\u9020\u6280\u672f\u7814\u53d1\u4e0a\u6295\u5165\u7684\u7814\u53d1\u8d44\u91d1\uff0c\u6bcf\u5e74\u90fd\u8981\u4e0a\u4ebf\u7f8e\u5143\uff0c\u8fd9\u8fd8\u4e3b\u8981\u96c6\u4e2d\u5728\u6676\u5706\u5236\u9020\u524d\u6bb5\u5de5\u5e8f\u4e0a\u7684\u7814\u53d1\u4e0a\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u67f3\u5fd7\u6210\u8fd8\u662f\u76f8\u5f53\u4f69\u670d\u9526\u6e56\u5728\u6280\u672f\u6269\u5f20\u4e0a\u7684\u91ce\u5fc3\u4e0e\u51b3\u5fc3\u3002</p><p>\u3000\u3000\u94a6\u4f69\u5f52\u94a6\u4f69\uff0c\u67f3\u5fd7\u6210\u5fc3\u91cc\u6e05\u695a\u8d38\u7136\u5c06\u9879\u76ee\u79fb\u5230\u5185\u5730\u4ed6\u672c\u4eba\u8981\u627f\u53d7\u591a\u5927\u7684\u653f\u6cbb\u98ce\u9669\u3002</p><p>\u3000\u3000\u5728\u9a6c\u6765\u897f\u4e9a\u6295\u8d44\u5efa\u5382\u4e0e\u5728\u5185\u5730\u6295\u8d44\u5efa\u5382\u662f\u622a\u7136\u4e0d\u540c\u7684\u6982\u5ff5\u3002</p><p>\u3000\u3000\u4ece\u53bb\u5e74\u4ee5\u6765\uff0c\u6d77\u5ce1\u4e24\u5cb8\u7684\u5173\u7cfb\u5c31\u9677\u5165\u50f5\u6301\uff0c\u4e0d\u8981\u8bf4\u6676\u5706\u5236\u9020\u7b49\u6838\u5fc3\u6280\u672f\uff0c\u53f0\u6e7e\u5f53\u5c40\u751a\u81f3\u9650\u5236\u4e00\u822c\u975e\u6838\u5fc3\u7684\u7535\u5b50\u4fe1\u606f\u6280\u672f\u6d41\u5165\u5185\u5730\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u6210\u8981\u662f\u5c06\u4e00\u4e2a\u5b8c\u6574\u7684\u6676\u5706\u5236\u9020\u7684\u6280\u672f\u56e2\u961f\u5e26\u5230\u5185\u5730\uff0c\u4e0d\u4ec5\u4ec5\u662f\u9762\u4e34\u4e0e\u539f\u4e1c\u5bb6\u53f0\u79ef\u7535\u5173\u7cfb\u5f7b\u5e95\u7834\u88c2\u7684\u95ee\u9898\uff0c\u53ea\u6015\u6d77\u5ce1\u4e24\u5cb8\u5173\u7cfb\u7f13\u548c\u4e4b\u524d\u90fd\u65e0\u6cd5\u518d\u6b21\u8e0f\u8db3\u53f0\u6e7e\u7684\u571f\u5730\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u6210\u5012\u975e\u4e0d\u62e5\u62a4\u4e24\u5cb8\u7edf\u4e00\uff0c\u4f46\u662f\u8981\u4ed6\u4e2a\u4eba\u5192\u8fd9\u4e48\u5927\u7684\u653f\u6cbb\u98ce\u9669\uff0c\u5c31\u4e0d\u5bb9\u4ed6\u4e0d\u6df1\u601d\u719f\u8651\u3002\u4ece\u9999\u6e2f\u6709\u63a5\u89e6\u4ee5\u6765\uff0c\u4e00\u76f4\u5230\u4ee5\u8003\u5bdf\u4e1a\u52a1\u540d\u4e49\u62b5\u8fbe\u5efa\u90ba\u8fd9\u4e24\u5929\uff0c\u67f3\u5fd7\u6210\u4e00\u76f4\u5c31\u6ca1\u6709\u677e\u8fc7\u53e3\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u9526\u6e56\u4ece\u5fb7\u4eea\u5f15\u8fdb\u6280\u672f\u5728\u6d77\u5dde\u6b63\u6295\u8d44\u5174\u5efa\u4e00\u5ea7\u96c6\u6210\u7535\u8def\u82af\u7247\u5c01\u88c5\u6d4b\u8bd5\u5de5\u5382\uff0c\u8fd9\u5c31\u4e0d\u7528\u62c5\u4efb\u6676\u5706\u751f\u4ea7\u540e\u6bb5\u5de5\u5e8f\u7684\u95ee\u9898\uff0c\u5efa\u90ba\u5e02\u6570\u5b57\u957f\u5eca\u6784\u60f3\u7684\u65b0\u6218\u7565\u4e0e\u9526\u6e56\u6a61\u6811\u56ed\u8ba1\u5212\u5728\u5efa\u90ba\u7684\u5de8\u989d\u79d1\u7814\u6295\u8d44\u4ee5\u53ca\u5efa\u90ba\u5e02\u4e09\u5341\u591a\u6240\u9ad8\u7b49\u9662\u6821\u6240\u5f62\u6210\u7684\u6280\u672f\u8d44\u6e90\uff0c\u4e0d\u8003\u8651\u653f\u6cbb\u56e0\u7d20\uff0c\u5728\u5efa\u90ba\u6295\u8d44\u5efa\u5382\u7684\u6761\u4ef6\u7684\u786e\u6bd4\u5409\u9686\u5761\u66f4\u4f18\u8d8a\u3002</p><p>\u3000\u3000\u90ed\u677e\u5ef6\u7684\u987e\u8651\u6bd4\u8f83\u5c11\uff0c\u9a6c\u6765\u897f\u4e9a\u5f53\u5c40\u5e94\u4ed8\u5f53\u524d\u7684\u91d1\u878d\u98ce\u66b4\u8fd8\u6349\u895f\u89c1\u8098\uff0c\u5df2\u7ecf\u987e\u4e0d\u4e0a\u6676\u5706\u5382\u7684\u6295\u8d44\u9879\u76ee\u3002\u9664\u4e86\u67f3\u5fd7\u6210\u7684\u56e0\u7d20\u4e4b\u5916\uff0c\u90ed\u677e\u5ef6\u5012\u662f\u6709\u4e9b\u62c5\u5fc3\u65b0\u52a0\u5761\u65b9\u9762\u4f1a\u4e0d\u4f1a\u4e2d\u6b62\u4e4b\u524d\u7684\u5408\u4f5c\u610f\u5411\uff0c\u4e0d\u518d\u5411\u8be5\u9879\u76ee\u8f6c\u8ba9\u90e8\u5206\u6676\u5706\u5236\u9020\u6280\u672f\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u65f6\u5019\u53ea\u80fd\u8c08\u4e00\u4e9b\u5173\u4e8e\u5b9e\u9a8c\u5ba4\u5efa\u8bbe\u7684\u8bdd\u9898\u3002</p><p>\u3000\u3000\u201c\u5b9e\u9a8c\u5ba4\u80fd\u591f\u8fc8\u51fa\u7b2c\u4e00\u6b65\uff0c\u5173\u952e\u8fd8\u662f\u8981\u80fd\u62db\u8058\u5230\u6770\u51fa\u7684\u79d1\u7814\u4eba\u5458\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u9526\u6e56\u5728\u9999\u6e2f\u7684\u516c\u53f8\u6b63\u51c6\u5907\u5230\u4e1c\u5357\u4e9a\u6492\u5f00\u5927\u7f51\uff0c\u5c4a\u65f6\u8fd8\u5e0c\u671b\u67f3\u5148\u751f\u4ecb\u7ecd\u51e0\u540d\u4eba\u624d\u52a0\u76df\u9526\u6e56\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u67f3\u5fd7\u6210\u5e94\u4e86\u4e00\u58f0\uff0c\u5fc3\u91cc\u5c31\u7b97\u81ea\u5df1\u8981\u653e\u5f03\u6676\u5706\u6295\u8d44\u9879\u76ee\uff0c\u90a3\u4e9b\u5df2\u7ecf\u8131\u79bb\u53f0\u79ef\u7535\u7684\u5de5\u7a0b\u5e08\u603b\u8981\u5e2e\u7740\u5b89\u6392\u597d\u51fa\u8def\u624d\u5bf9\u5f97\u8d77\u4eba\u5bb6\uff0c\u65e2\u7136\u9526\u6e56\u5728\u6280\u672f\u7814\u53d1\u4e0a\u6295\u5165\u8fd9\u4e48\u5927\u7684\u8d44\u91d1\uff0c\u5f85\u9047\u5012\u4e5f\u672a\u5fc5\u4f1a\u5dee\uff0c\u5c31\u7b97\u6709\u4e9b\u4eba\u4e0d\u9002\u5408\u505a\u7814\u53d1\uff0c\u9526\u6e56\u4f53\u7cfb\u5185\u7684\u5de5\u5382\u4e5f\u9700\u8981\u5927\u91cf\u7684\u9ad8\u7ea7\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u3002</p><p>\u3000\u3000\u5f20\u606a\u662f\u770b\u51fa\u67f3\u5fd7\u6210\u5fc3\u91cc\u7684\u72b9\u8c6b\uff0c\u6253\u7b97\u5728\u4ed6\u72b9\u8c6b\u4e0d\u5b9a\u7684\u65f6\u5019\uff0c\u62a2\u7740\u5c06\u90a3\u6279\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u638c\u63e1\u5230\u624b\u91cc\uff1b\u4e1c\u5357\u4e9a\u5404\u56fd\u4ee5\u53ca\u53f0\u6e7e\u5730\u533a\u7684\u7ecf\u6d4e\u5f62\u52bf\u8fd8\u4f1a\u8fdb\u4e00\u6b65\u7684\u6076\u5316\uff0c\u81f3\u5c11\u8981\u8ba9\u67f3\u5fd7\u6210\u4e0e\u4ed6\u624b\u4e0b\u7684\u5de5\u7a0b\u5e08\u5728\u8131\u79bb\u53f0\u79ef\u7535\u4e4b\u540e\u6ca1\u6709\u592a\u591a\u522b\u7684\u9009\u62e9\u3002</p></article>"}}}]);
