(window.webpackJsonp=window.webpackJsonp||[]).push([[773],{796:function(p){p.exports={chapterTitle:null,title:"\u7b2c607\u7ae0 \u65b0\u751f\u62a5\u540d",content:"<article><h2>\u7b2c607\u7ae0 \u65b0\u751f\u62a5\u540d</h2><p>\u3000\u3000\u7ffb\u8eab\u9192\u6765\uff0c\u90fd\u4e0d\u6653\u5f97\u665a\u6674\u591c\u91cc\u4ec0\u4e48\u65f6\u5019\u8d70\u7684\uff0c\u603b\u4e4b\u9192\u8fc7\u6765\uff0c\u611f\u89c9\u4e0d\u5230\u665a\u6674\u591c\u91cc\u6709\u5728\u8fd9\u4e2a\u623f\u95f4\u5446\u8fc7\u7684\u75d5\u8ff9\u3002</p><p>\u3000\u3000\u4e5d\u6708\u4e94\u65e5\u3001\u516d\u65e5\u662f\u65b0\u751f\u62a5\u540d\u7684\u65f6\u95f4\uff0c\u6253\u7b97\u62d6\u5230\u660e\u65e5\u518d\u53bb\uff0c\u4e0d\u8fc7\u5510\u5a67\u8981\u966a\u7740\u9648\u5983\u84c9\u4e00\u8d77\u8fd4\u6821\uff0c\u5c31\u63d0\u524d\u5230\u4eca\u5929\u53bb\u3002</p><p>\u3000\u3000\u6d17\u8fc7\u6fa1\uff0c\u5f20\u606a\u7ad9\u5728\u9633\u53f0\u4e0a\u770b\u7740\u5929\u8fb9\u88f9\u5728\u96e8\u4e91\u91cc\u7684\u7470\u4e3d\u971e\u5149\u3002\u5510\u5a67\u9a91\u7740\u5979\u6854\u9ec4\u8272\u7684\u5c0f\u81ea\u884c\u8f66\u8fc7\u6765\uff0c\u81ea\u5df1\u6309\u4e86\u5bc6\u7801\u9501\u8fdb\u4e86\u9662\u5b50\uff0c\u5c06\u81ea\u884c\u8f66\u505c\u5728\u82b1\u575b\u524d\uff0c\u62ac\u5934\u770b\u89c1\u5f20\u606a\u4e0b\u8eab\u88f9\u7740\u6d74\u5dfe\u3001\u4e0a\u8eab\u8d64\u88f8\u7740\u3001\u5341\u5206\u62c9\u98ce\u7684\u7ad9\u5728\u9633\u5149\u4e0a\u770b\u971e\u5149\uff0c\u634f\u7740\u62f3\u5934\u671d\u4ed6\u793a\u5a01\u7684\u6325\u4e86\u6325\u3002</p><p>\u3000\u3000\u5510\u5a67\u549a\u549a\u549a\u7684\u8df3\u7740\u8dd1\u4e0a\u697c\uff1b\u5f85\u5979\u6253\u5f00\u95e8\uff0c\u5f20\u606a\u6b63\u6293\u7740\u6d74\u5dfe\u7684\u4e24\u8fb9\u7a81\u7136\u626f\u5f00\uff0c\u8fd8\u5c16\u53eb\u8d77\u6765\uff1a\u201c\u554a\uff01\u8d70\u5149\u4e86\uff01\u201d</p><p>\u3000\u3000\u5510\u5a67\u53ef\u6ca1\u6709\u5c16\u53eb\uff0c\u4e00\u811a\u98de\u8e39\u8fc7\u53bb\uff0c\u4eba\u79bb\u5f97\u8fdc\uff0c\u62d6\u978b\u5374\u98de\u51fa\u53bb\u6b63\u4e2d\u5f20\u606a\u7684\u88c6\u90e8\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5f20\u606a\u5403\u75db\u6342\u7740\u7ed9\u6253\u4e2d\u7684\u5730\u65b9\uff0c\u5510\u5a67\u4e00\u8138\u7d27\u5f20\u7684\u8dd1\u8fc7\u6765\u95ee\u4ed6\u6709\u6ca1\u6709\u4e8b\uff0c\u5f20\u606a\u6293\u5979\u67d4\u5ae9\u7684\u5c0f\u624b\u6309\u8fc7\u53bb\u8bf4\uff1a\u201c\u63c9\u63c9\u5c31\u6ca1\u4e8b\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5a67\u4e0d\u5c0f\u5fc3\u624b\u7ed9\u5f20\u606a\u62c9\u8fc7\u53bb\u89e6\u78b0\u4e86\u4e00\u4e0b\u5c31\u60ca\u7f9e\u7684\u62bd\u56de\u624b\uff0c\u5f20\u606a\u5f97\u610f\u7684\u9b3c\u7b11\uff0c\u5510\u5a67\u5374\u4e0d\u4f9d\u4e0d\u6320\u7684\u5bf9\u5f20\u606a\u53c8\u6376\u53c8\u8e22\u3002</p><p>\u3000\u3000\u665a\u6674\u51c6\u5907\u4e86\u65e9\u9910\uff0c\u5403\u8fc7\u65e9\u9910\uff0c\u5f20\u606a\u5c31\u5728\u5bb6\u91cc\u8ba9\u665a\u6674\u3001\u5510\u5a67\u5e2e\u7740\u6536\u62fe\u4e1c\u897f\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u5510\u5a67\u5979\u5988\u5988\u3001\u4ed6\u5927\u8205\u5988\u3001\u4ed6\u5c0f\u5a76\u4e5f\u90fd\u8dd1\u8fc7\u6765\u5e2e\u5fd9\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u9700\u8981\u6311\u9009\u54ea\u4e9b\u4e66\u3001\u54ea\u4e9b\u8d44\u6599\u8981\u5e26\u4e0a\uff0c\u4ed6\u8fd8\u8981\u6253\u7535\u8bdd\u901a\u77e5\u82cf\u6d25\u4e1c\u51e0\u4ef6\u4e8b\u60c5\uff0c\u7279\u522b\u662f\u6628\u5929\u5728\u8fdb\u516b\u9526\u73cd\u4e4b\u524d\u7b54\u5e94\u9001\u4e00\u53ea\u9650\u91cf\u7248\u7684\u624b\u673a\u7ed9\u9648\u9759\u5f53\u5f00\u5b66\u793c\u7269\u9001\u7ed9\u8c22\u5b50\u5609\uff0c\u5f20\u606a\u8ba9\u82cf\u6d25\u4e1c\u8bb0\u5f97\u5c06\u8fd9\u4ef6\u4e8b\u627e\u4eba\u529e\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\u5de5\u592b\uff0c\u7535\u8bdd\u4e0d\u65ad\uff0c\u597d\u50cf\u6240\u6709\u4eba\u90fd\u77e5\u9053\u4ed6\u5c31\u8981\u53bb\u5efa\u90ba\u4e0a\u5b66\u4e86\uff0c\u5f20\u606a\u5c06\u7535\u8bdd\u5939\u5728\u80a9\u8180\u4e0e\u8138\u988a\u4e4b\u95f4\u6536\u62fe\u4e1c\u897f\uff0c\u6536\u62fe\u5230\u6700\u540e\uff0c\u603b\u89c9\u5f97\u7f3a\u4e00\u4ef6\u4e1c\u897f\uff0c\u95ee\u665a\u6674\u3001\u5510\u5a67\uff0c\u5927\u5bb6\u53c8\u4ed4\u7ec6\u68c0\u67e5\u4e86\u4e00\u904d\uff0c\u6ca1\u6709\u53d1\u73b0\u7f3a\u4ec0\u4e48\u4e1c\u897f\uff0c\u4ed6\u5c0f\u5a76\u8fd8\u8bf4\uff1a\u201c\u7f3a\u4ec0\u4e48\u4e1c\u897f\u5230\u5efa\u90ba\u6ca1\u7684\u4e70\uff1f\u201d</p><p>\u3000\u3000\u5c06\u8863\u670d\u3001\u4e66\u7c4d\u3001\u8d44\u6599\u90fd\u6253\u5305\u4e22\u8fdb\u5546\u52a1\u8f66\u7684\u540e\u5907\u7bb1\u91cc\uff0c\u5f20\u606a\u624d\u60f3\u8d77\u6765\u7f3a\u7684\u662f\u5f55\u53d6\u901a\u77e5\u4e66\uff0c\u8fd9\u4e1c\u897f\u54ea\u91cc\u53ef\u4ee5\u5230\u5efa\u90ba\u73b0\u4e70\u554a\uff1f\u95ee\u5510\u5a67\uff0c\u5510\u5a67\u4ece\u5934\u5230\u5c3e\u90fd\u6ca1\u6709\u60f3\u5230\u8fd8\u6709\u8fd9\u56de\u4e8b\uff0c\u6253\u7535\u8bdd\u7ed9\u675c\u98de\uff0c\u675c\u98de\u6b63\u5728\u5bb6\u91cc\u6536\u62fe\u4e1c\u897f\uff0c\u65b0\u751f\u62a5\u540d\u4ed6\u5988\u8981\u8ddf\u7740\u8fc7\u53bb\u770b\u4e00\u4e0b\u624d\u653e\u5fc3\uff0c\u675c\u98de\u4e00\u8fb9\u8ddf\u5979\u5600\u5495\uff0c\u4e00\u8fb9\u544a\u8bc9\u5f20\u606a\u5f55\u53d6\u901a\u77e5\u4e66\u5e94\u8be5\u8fd8\u5728\u674e\u829d\u82b3\u90a3\u91cc\u3002</p><p>\u3000\u3000\u5f55\u53d6\u901a\u77e5\u4e66\u90fd\u662f\u5bc4\u5230\u5b66\u6821\u7531\u672c\u4eba\u53bb\u5b66\u6821\u53d6\u7684\uff0c\u5f20\u606a\u8fd9\u6bb5\u65f6\u95f4\u90fd\u4e0d\u5728\u6d77\u5dde\uff0c\u4e5f\u6ca1\u6709\u4eba\u5e2e\u4ed6\u60e6\u8bb0\u8fd9\u4e8b\uff0c\u5927\u6982\u5728\u675c\u98de\u3001\u5510\u5a67\u7684\u610f\u8bc6\u91cc\u4ece\u6765\u90fd\u4e0d\u8ba4\u8bc6\u8fd9\u5f20\u5f55\u53d6\u901a\u77e5\u4e66\u5bf9\u5f20\u606a\u6709\u4efb\u4f55\u7684\u610f\u4e49\uff0c\u4e5f\u5c31\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u6253\u7535\u8bdd\u5230\u5b66\u6821\u91cc\uff0c\u674e\u829d\u82b3\u4e0d\u5728\u529e\u516c\u5ba4\u91cc\uff0c\u9a6c\u5b50\u5584\u63a5\u5230\u7535\u8bdd\uff0c\u624d\u77e5\u9053\u9a6c\u5b50\u5584\u90fd\u5f80\u4ed6\u5bb6\u8dd1\u4e86\u597d\u51e0\u56de\u90fd\u6ca1\u6709\u902e\u5230\u4ed6\u4eba\uff1b\u4ed6\u5c31\u662f\u4e0d\u6253\u7535\u8bdd\uff0c\u9a6c\u5b50\u5584\u4e5f\u8fd8\u8981\u4eb2\u81ea\u518d\u8dd1\u4e00\u8d9f\u3002</p><p>\u3000\u3000\u4ece\u9a6c\u5b50\u5584\u624b\u91cc\u63a5\u8fc7\u5f55\u53d6\u901a\u77e5\u4e66\uff0c\u4f3c\u4e4e\u770b\u5230\u90a3\u4ee4\u4eba\u5411\u5f80\u7684\u5927\u5b66\u751f\u6d3b\u6f02\u8361\u800c\u6765\uff0c\u5f20\u606a\u5fae\u5fae\u7b11\u4e86\u8d77\u6765\uff0c\u4ed6\u770b\u7684\u5f88\u91cd\uff0c\u5176\u4ed6\u4eba\u5374\u6839\u672c\u6ca1\u6709\u5f53\u56de\u4e8b\u3002</p><p>\u3000\u3000\u8fd9\u8fb9\u6536\u62fe\u505c\u5f53\uff0c\u5c31\u6328\u4e2a\u53bb\u63a5\u9648\u5983\u84c9\u3001\u674e\u5b63\u3001\u8096\u6625\u94ed\u3001\u8bb8\u82e5\u8679\uff0c\u6700\u540e\u53bb\u63a5\u675c\u98de\uff1b\u675c\u98de\u4e00\u8fb9\u5f80\u8f66\u540e\u5907\u7bb1\u91cc\u585e\u884c\u674e\uff0c\u4e00\u8fb9\u6253\u53d1\u4ed6\u5988\uff1a\u201c\u4f60\u770b\u7740\u6211\u7238\u5c31\u884c\u4e86\uff0c\u54ea\u6709\u770b\u7740\u513f\u5b50\u7684\uff1f\u8fd9\u8f66\u5c31\u4e03\u4e2a\u5ea7\uff0c\u591a\u4f60\u4e00\u4e2a\u8fd8\u8981\u52a0\u90e8\u8f66\uff0c\u591a\u6d6a\u8d39\u554a\u3002\u201d\u575a\u51b3\u7684\u5c06\u4ed6\u5988\u585e\u5728\u8f66\u95e8\u5916\u4e0d\u8ba9\u4e0a\u8f66\uff0c\u5f85\u8f66\u5b50\u542f\u52a8\u8d77\u6765\uff0c\u624d\u65e0\u9650\u611f\u6168\u7684\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u6211\u7684\u5988\u554a\uff0c\u90fd\u5fcd\u53d7\u5979\u5341\u4e5d\u5e74\u4e86\uff0c\u6211\u5bb9\u6613\u5417\uff1f\u201d</p><p>\u3000\u3000\u5927\u5bb6\u54c8\u54c8\u5927\u7b11\uff0c\u9664\u4e86\u5f20\u606a\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u90fd\u6709\u8003\u4e0a\u5927\u5b66\u8c41\u7136\u8f7b\u677e\u7684\u7545\u5feb\u3002</p><p>\u3000\u3000\u9ad8\u901f\u8def\u6536\u8d39\u7ad9\u524d\u505c\u7740\u4e00\u8f86\u5b9d\u84dd\u8272\u7684\u83b2\u82b1\u8dd1\u8f66\uff0c\u675c\u98de\u559c\u6ecb\u6ecb\u7684\u62c9\u5f00\u8f66\u95e8\u8df3\u4e0a\u83b2\u82b1\u8dd1\u8f66\uff0c\u5f20\u606a\u8fd8\u4ee5\u4e3a\u76db\u590f\u4e0d\u966a\u4ed6\u4eec\u4e00\u8d77\u53bb\u7701\u57ce\u5462\uff0c\u6ca1\u60f3\u5230\u65e9\u5c31\u5728\u9ad8\u901f\u8def\u53e3\u7b49\u7740\uff0c\u96be\u602a\u675c\u98de\u6b7b\u6d3b\u4e0d\u80af\u4ed6\u5988\u8ddf\u7740\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8fd9\u4e09\u5e74\u6765\uff0c\u867d\u7136\u4e0d\u6b62\u4e00\u6b21\u7684\u7ecf\u8fc7\u3001\u9a7b\u8db3\u8fc7\u8fd9\u5ea7\u57ce\u5e02\uff0c\u8fd9\u4e00\u6b21\u7684\u611f\u89c9\u5374\u5927\u4e3a\u4e0d\u540c\uff0c\u671f\u5f85\u4e00\u79cd\u4e0d\u540c\u5bfb\u5e38\u7684\u751f\u6d3b\u5427\u3002</p><p>\u3000\u3000\u5728\u6b64\u4e4b\u524d\uff0c\u5f20\u606a\u5df2\u7ecf\u76f8\u5f53\u8ba4\u771f\u7684\u8b66\u544a\u8fc7\u9526\u6e56\u7684\u7ba1\u7406\u5c42\uff0c\u6ca1\u6709\u5929\u584c\u4e0b\u6765\u7684\u4e8b\u60c5\uff0c\u4e0d\u53ef\u4ee5\u968f\u4fbf\u6253\u4ed6\u7684\u624b\u673a\u5e72\u6270\u4ed6\u60a0\u95f2\u81ea\u5728\u7684\u5927\u5b66\u751f\u6d3b\u3002</p><p>\u3000\u3000\u4e0a\u5348\u5728\u6d77\u5dde\u803d\u6401\u4e86\u4e0d\u5c11\u65f6\u95f4\uff0c\u4e2d\u5348\u5341\u4e8c\u70b9\u624d\u8f66\u5b50\u624d\u4e0b\u9ad8\u901f\u516c\u8def\u8fdb\u5165\u5efa\u90ba\u5e02\u533a\uff0c\u624b\u673a\u9707\u52a8\u8d77\u6765\uff0c\u5f20\u606a\u638f\u51fa\u624b\u673a\u89c1\u662f\u5efa\u90ba\u7684\u7535\u8bdd\u53f7\u7801\uff0c\u8fd9\u53f7\u7801\u8fd8\u6709\u4e9b\u719f\u6089\uff0c\u521a\u8b66\u544a\u6ca1\u4e24\u5929\uff0c\u8c01\u8fd9\u65f6\u5019\u6253\u7535\u8bdd\u7ed9\u81ea\u5df1\uff1f\u65e0\u5948\u7684\u6447\u4e86\u6447\u5934\uff0c\u63a5\u901a\u7535\u8bdd\uff0c\u7535\u8bdd\u90a3\u5934\u5374\u4f20\u6765\u4ed6\u5988\u6881\u683c\u73cd\u7684\u58f0\u97f3\uff1a\u201c\u4f60\u4eec\u4e0b\u9ad8\u901f\u6ca1\u6709\uff0c\u5c0f\u5a67\u5988\u5988\u8bf4\u4f60\u4eec\u51fa\u53d1\u5feb\u4e24\u4e2a\u534a\u5c0f\u65f6\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5f20\u606a\u4e00\u60ca\u4e00\u6123\uff0c\u201c\u5988\uff0c\u4f60\u4eba\u600e\u4e48\u5728\u5efa\u90ba\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u7238\u4e5f\u5728\uff0c\u6211\u4eec\u6628\u5929\u8d76\u8fc7\u6765\u5c31\u542c\u8bf4\u4f60\u5c0f\u5b50\u521a\u8d70\uff0c\u5c31\u6ca1\u6709\u7406\u4f1a\u4f60\uff0c\u6211\u8ddf\u4f60\u7238\u591c\u91cc\u5728\u5efa\u90ba\u901b\u4e86\u534a\u5bbf\u2026\u2026\u201d</p><p>\u3000\u3000\u5f97\uff0c\u522b\u4eba\u628a\u7239\u5a18\u90fd\u7529\u6389\u4e86\uff0c\u81ea\u5df1\u5bb6\u7684\u7239\u5a18\u5374\u5728\u5efa\u90ba\u5b88\u682a\u5f85\u5154\uff0c\u603b\u4e0d\u80fd\u8fd9\u65f6\u5019\u5c06\u4e8c\u4f4d\u8d76\u56de\u65b0\u829c\u53bb\u5427\u3002\u624d\u60f3\u8d77\u6765\u663e\u793a\u7684\u7535\u8bdd\u662f\u5e08\u5927\u4e8c\u62db\u91cc\u7684\u53f7\u7801\u6bb5\uff0c\u4ed6\u7238\u3001\u4ed6\u5988\u8fd9\u65f6\u5019\u6b63\u5728\u5e08\u5927\u4e8c\u62db\u5462\u3002</p><p>\u3000\u3000\u5efa\u90ba\u4e3b\u57ce\u533a\u5728\u6c5f\u5357\uff0c\u53ea\u6709\u4e00\u5ea7\u5927\u6865\u6a2a\u8de8\u5c0f\u6c5f\u4e4b\u4e0a\u2014\u2014\u4e8c\u6865\u5de5\u7a0b\u867d\u7136\u5df2\u7ecf\u63d0\u524d\u542f\u52a8\uff0c\u6ca1\u6709\u4e24\u4e09\u5e74\u7684\u5de5\u592b\u53e6\u60f3\u5efa\u6210\u901a\u8f66\uff0c\u8fc7\u4e86\u5c0f\u6c5f\u5927\u6865\u90fd\u662f\u5efa\u90ba\u6bd4\u8f83\u7e41\u5fd9\u7684\u8def\u6bb5\uff0c\u82b1\u4e86\u56db\u5341\u591a\u5206\u949f\u624d\u8d76\u5230\u5e08\u5927\u4e8c\u62db\u3002</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u6539\u9020\u5de5\u7a0b\u4ece\u4e94\u6708\u5e95\u5c31\u8fdb\u5165\u5c01\u95ed\u65bd\u5de5\uff0c\u8fc4\u4eca\u8fc7\u53bb\u5c06\u8fd1\u4e09\u4e2a\u534a\u6708\u7684\u65f6\u95f4\u3002\u7d27\u8d76\u6162\u8d76\uff0c\u60f3\u5728\u4e5d\u6708\u4e4b\u524d\u5b8c\u5de5\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u4e0d\u8fc7\u516b\u5e62\u5341\u4e8c\u5c42\u9ad8\u7684\u9752\u5e74\u516c\u5bd3\u90fd\u5df2\u7ecf\u7ed3\u6784\u5c01\u9876\u5f00\u59cb\u5916\u4fa7\u9762\u88c5\u6f62\uff0c\u5185\u5df7\u5916\u8857\u3001\u5185\u5df7\u4e4b\u4e0a\u6709\u7a7f\u8857\u9a91\u697c\u7684\u590d\u5f0f\u5546\u5e97\u8857\u5916\u7acb\u9762\u88c5\u6f62\u5df2\u7ecf\u63a5\u8fd1\u5c3e\u58f0\u3002</p><p>\u3000\u3000\u8fd9\u6837\u7684\u5efa\u9020\u901f\u5ea6\uff0c\u5df2\u7ecf\u7b97\u662f\u80fd\u8d76\u4e0a\u6d77\u5dde\u5efa\u7b51\u4e1a\u7684\u8bb0\u5f55\u4e86\uff0c\u5f20\u606a\u4e5f\u65e0\u6cd5\u8868\u793a\u4e0d\u6ee1\u3002</p><p>\u3000\u3000\u867d\u8bf4\u65b0\u5efa\u7684\u5546\u5e97\u8857\u662f\u7531\u5185\u5df7\u5916\u8857\u7ec4\u6210\u7684\u590d\u5f0f\u8857\u9053\uff0c\u4f46\u662f\u540d\u79f0\u4f9d\u65e7\u4e0d\u53d8\u3002\u5f20\u606a\u4ed6\u4eec\u5728\u5b66\u5e9c\u5df7\u524d\u4e0b\u8f66\u65f6\uff0c\u5de5\u4eba\u6b63\u5f80\u5916\u8857\u7684\u4e24\u4fa7\u518d\u5404\u690d\u4e00\u6392\u6cd5\u56fd\u68a7\u6850\uff0c\u8fd9\u6837\uff0c\u52a0\u4e0a\u5916\u8857\u539f\u5148\u4fdd\u7559\u7684\u4e24\u6392\u9ad8\u5927\u68a7\u6850\u6811\uff0c\u5b66\u5e9c\u5df7\u5c31\u4f1a\u5f62\u6210\u6d53\u836b\u5bc6\u906e\u7684\u771f\u6b63\u7684\u6b65\u884c\u8857\u3002</p><p>\u3000\u3000\u8857\u9053\u7684\u53e6\u4e00\u7aef\u6b63\u5728\u94fa\u7684\u6d45\u9ec4\u8272\u95f4\u7eb9\u7684\u77f3\u6750\u8def\u9762\uff0c\u53ef\u60dc\u8fd9\u65f6\u5019\u5185\u5df7\u7981\u6b62\u8fdb\u5165\uff0c\u770b\u4e0d\u5230\u5185\u5df7\u7684\u60c5\u666f\uff0c\u516c\u5bd3\u697c\u90a3\u8fb9\u66f4\u662f\u8fdb\u4e0d\u53bb\uff0c\u5f20\u606a\u540e\u6765\u5012\u662f\u6709\u770b\u8fc7\u8bbe\u8ba1\u56fe\uff0c\u4e0d\u8fc7\u5149\u51ed\u60f3\u8c61\u529b\uff0c\u603b\u65e0\u6cd5\u60f3\u8c61\u771f\u6b63\u7684\u60c5\u666f\u662f\u4ec0\u4e48\u6837\u5b50\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e0e\u59bb\u5b50\u6881\u683c\u73cd\u7ad9\u5728\u4e00\u8fb9\u6b63\u611f\u6168\u5462\uff1a\u201c\u4e0a\u56de\u8fc7\u6765\u8fd8\u662f\u8001\u6837\u5b50\uff0c\u8fd9\u8fc7\u53bb\u6ca1\u6709\u51e0\u4e2a\u6708\u5427\u2026\u2026\u201d\u53c8\u95ee\u966a\u4e86\u4ed6\u4eec\u534a\u5929\u7684\u5f20\u77e5\u975e\uff0c\u201c\u8fd9\u91cc\u51e0\u65f6\u80fd\u5b8c\u5168\u5efa\u6210\uff1f\u201d</p><p>\u3000\u3000\u201c\u5546\u5e97\u8857\u534a\u4e2a\u6708\u5185\u5c31\u8981\u4ea4\u4ed8\u5546\u5bb6\u88c5\u6f62\uff0c\u516c\u5bd3\u697c\u90a3\u91cc\uff0c\u4e00\u4e2a\u6708\u4e4b\u540e\u4e5f\u8981\u540c\u65f6\u5f00\u59cb\u5185\u90e8\u88c5\u6f62\u3002\u90fd\u662f\u5f20\u606a\u90a3\u6df7\u7403\u5c06\u5de5\u671f\u8d76\u5f97\u8fd9\u4e48\u7d27\uff0c\u8bf4\u662f\u4ed6\u5728\u8fd9\u91cc\u8bfb\u4e66\uff0c\u4e0d\u80fd\u6ca1\u6709\u4e00\u5bb6\u597d\u7684\u5496\u5561\u5385\uff0c\u4e0d\u80fd\u6ca1\u6709\u4e00\u5bb6\u597d\u7684\u9152\u5427\uff0c\u4e0d\u80fd\u6ca1\u6709\u4e00\u5bb6\u597d\u7684\u9910\u9986\uff0c\u4f60\u8bf4\u8fd9\u6df7\u7403\u662f\u8fc7\u6765\u8bfb\u4e66\u7684\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u3001\u90b5\u81f3\u521a\u3001\u76db\u9752\u8fd8\u6709\u6881\u519b\u90fd\u5728\u8fb9\u4e0a\u966a\u7740\u3002</p><p>\u3000\u3000\u201c\u5f97\uff0c\u8981\u4e0d\u8981\u6211\u7b49\u4f1a\u513f\u518d\u8fc7\u6765\uff0c\u8ba9\u5c0f\u53d4\u4f60\u75db\u75db\u5feb\u5feb\u7684\u5148\u591a\u9a82\u4e24\u58f0\uff1f\u201d\u5f20\u606a\u4e0e\u5510\u5a67\u8d70\u8fc7\u6765\uff0c\u5176\u4ed6\u4eba\u90fd\u7ad9\u5728\u8f66\u90a3\u8fb9\u7b49\u7740\u3002\u76db\u590f\u4e0e\u675c\u98de\u5f00\u8dd1\u8f66\u4e00\u76f4\u90fd\u8dd1\u5728\u4ed6\u4eec\u524d\u9762\uff0c\u65e9\u5c31\u89c4\u89c4\u77e9\u77e9\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u83b2\u82b1\u8dd1\u8f66\u867d\u7136\u88ab\u55bb\u4e3a\u201c\u8d2b\u6c11\u8dd1\u8f66\u201d\uff0c\u4f46\u662f\u505c\u5728\u68a7\u6850\u6811\u4e0b\u8fd8\u633a\u4e4d\u773c\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u8f6c\u8fc7\u8eab\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u53ef\u662f\u4e24\u4e2a\u6708\u90fd\u6ca1\u6709\u56de\u6d77\u5dde\u4e86\uff0c\u591a\u5c11\u5fc3\u601d\u4e22\u5728\u8fd9\u91cc\uff0c\u62b1\u6028\u4e24\u53e5\u96be\u9053\u4e0d\u5e94\u8be5\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u7b11\u7740\uff0c\u6307\u7740\u8eab\u540e\u8f66\u65c1\u8fb9\u7684\u4e00\u5806\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u5feb\u8d70\u4e2a\u5730\u65b9\u5403\u996d\uff0c\u4f60\u4eec\u4e5f\u997f\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u5403\u8fc7\u4e86\uff0c\u4f60\u4eec\u81ea\u5df1\u627e\u5730\u65b9\u53bb\u5403\u996d\u3002\u201d\u5f20\u77e5\u884c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u4eec\u4fe9\u4e0d\u662f\u6765\u5efa\u90ba\u966a\u6211\u62a5\u540d\u7684\uff1f\u201d\u5f20\u606a\u8be7\u5f02\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u6c5f\u4e0a\u5143\u8bf4\u54ea\u6709\u81ea\u5df1\u513f\u5b50\u4e0a\u5927\u5b66\u505a\u8001\u5b50\u7684\u4e0d\u56de\u5bb6\u770b\u4e00\u773c\u7684\uff0c\u786c\u8981\u7ed9\u6211\u4e09\u5929\u7684\u5047\u671f\uff0c\u6211\u60f3\u60f3\u8fd9\u6bb5\u65f6\u95f4\u4e5f\u591f\u8f9b\u82e6\u7684\uff0c\u5c31\u62c9\u7740\u4f60\u5988\u51fa\u6765\u8d70\u8d70\uff0c\u6211\u4eec\u4e0b\u5348\u90fd\u6709\u5b89\u6392\u4e86\uff0c\u5c31\u4e0d\u966a\u4f60\u53bb\u62a5\u540d\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u65e0\u53ef\u5948\u4f55\u7684\u671d\u5510\u5a67\u644a\u4e86\u644a\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5e78\u4e8f\u4f60\u6765\u966a\u6211\uff0c\u4e0d\u7136\u6211\u8fde\u6b7b\u7684\u5fc3\u90fd\u6709\u3002\u201d\u5510\u5a67\u54af\u54af\u7684\u7b11\uff0c\u7b11\u7740\u5524\u5f20\u77e5\u884c\u3001\u6881\u683c\u73cd\u201c\u53d4\u53d4\u201d\u3001\u201c\u963f\u59e8\u201d\uff0c\u6ca1\u642d\u7406\u5f20\u606a\u53eb\u82e6\u3002</p><p>\u3000\u3000\u5f20\u606a\u95ee\u4ed6\u7238\uff1a\u201c\u4f60\u4eec\u4e0b\u5348\u6253\u7b97\u53bb\u54ea\u91cc\uff0c\u8981\u4e0d\u8981\u628a\u8f66\u7ed9\u4f60\u4eec\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u5750501\u5c31\u884c\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000501\u662f\u5efa\u90ba\u5e02\u7684\u65c5\u6e38\u516c\u4ea4\uff0c\u5f20\u606a\u89c1\u4ed6\u7238\u3001\u4ed6\u5988\u8fd9\u4e48\u6d12\u8131\u5c06\u81ea\u5df1\u4e22\u4e0b\u4e0d\u7ba1\uff0c\u4ed6\u4e5f\u5c31\u62c9\u7740\u5510\u5a67\u53c8\u5750\u56de\u8f66\u91cc\uff0c\u8ba9\u9a6c\u6d77\u9f99\u5c06\u8f66\u5f00\u5230\u4e1c\u5927\u6821\u95e8\u524d\uff0c\u90a3\u91cc\u8fd8\u6709\u51e0\u5bb6\u5c0f\u996d\u9986\uff0c\u80fd\u51d1\u5408\u7740\u5c06\u809a\u5b50\u586b\u9971\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u867d\u8bf4\u89c4\u5b9a\u65b0\u751f\u662f\u4e5d\u6708\u4e94\u65e5\u3001\u516d\u65e5\u4e24\u5929\u62a5\u540d\uff0c\u4f46\u662f\u56fd\u4eba\u90fd\u662f\u8d76\u65e9\u4e0d\u8d76\u665a\u7684\uff0c\u5dee\u4e0d\u591a\u90fd\u51d1\u5230\u4eca\u5929\u8d76\u6765\u62a5\u540d\u3002\u5df2\u7ecf\u662f\u4e0b\u5348\u4e00\u70b9\uff0c\u5c0f\u996d\u9986\u91cc\u6839\u672c\u5c31\u6ca1\u6709\u5ea7\u4f4d\uff0c\u53ea\u5f97\u5230\u5b66\u6821\u91cc\u9762\u5148\u8ba9\u9648\u5983\u84c9\u53bb\u5bbf\u820d\u62ff\u6765\u996d\u5361\u518d\u53bb\u6559\u5de5\u98df\u5802\u5403\u996d\u2014\u2014\u6559\u5de5\u98df\u5802\u4e0d\u7528\u81ea\u5907\u996d\u76c6\uff0c\u5c0f\u7092\u7684\u6c34\u5e73\u4e5f\u76f8\u5f53\u4e0d\u9519\uff0c\u5173\u952e\u6559\u5de5\u98df\u5802\u91cc\u6709\u7a7a\u8c03\u3002</p><p>\u3000\u3000\u65b0\u751f\u62a5\u540d\u7684\u539f\u56e0\uff0c\u6559\u5de5\u98df\u5802\u8fd9\u4e24\u5929\u4e5f\u63a5\u53d7\u73b0\u91d1\u4ea4\u6613\uff0c\u9648\u5983\u84c9\u7684\u996d\u5361\u7ba1\u4e0d\u4e0a\u7528\u5904\u3002\u8fd9\u91cc\u7684\u4eba\u867d\u7136\u591a\uff0c\u4f46\u662f\u7b49\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd8\u662f\u6709\u4f4d\u5b50\u591a\u51fa\u6765\u3002</p><p>\u3000\u3000\u5403\u8fc7\u4e2d\u996d\uff0c\u5dee\u4e0d\u591a\u5c31\u5230\u4e0b\u5348\u62a5\u540d\u7684\u65f6\u95f4\uff0c\u884c\u56ca\u90fd\u6392\u5728\u8f66\u4e0a\uff0c\u5404\u9662\u7cfb\u7684\u63a5\u5f85\u70b9\u90fd\u5e03\u7f6e\u5728\u4ece\u5357\u6821\u95e8\u8fdb\u6765\u7684\u5927\u9053\u4e24\u4fa7\u3002\u8def\u4e0a\u5230\u5904\u90fd\u7ad9\u7740\u4e24\u773c\u832b\u832b\u3001\u4e0d\u6653\u5f97\u5982\u4f55\u529e\u4e8b\u7684\u65b0\u751f\u4e0e\u65b0\u751f\u5bb6\u957f\uff0c\u5f20\u606a\u4ed6\u4eec\u5374\u8fc5\u901f\u627e\u5230\u56fd\u9645\u5546\u5b66\u9662\u7684\u63a5\u5f85\u70b9\u2014\u2014\u674e\u5b63\u662f\u6cd5\u5b66\u9662\u3001\u8bb8\u82e5\u8679\u662f\u5916\u8bed\u5b66\u9662\uff0c\u8096\u6625\u94ed\u8ddf\u5f20\u606a\u3001\u675c\u98de\u4e24\u4eba\u90fd\u662f\u56fd\u9645\u5546\u5b66\u9662\u7684\uff0c\u53ea\u662f\u4e13\u4e1a\u4e0d\u540c\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u90fd\u9009\u62e9\u7ecf\u6d4e\u5b66\uff0c\u8096\u6625\u94ed\u662f\u56fd\u9645\u91d1\u878d\u4e13\u4e1a\uff0c\u4e0e\u8c22\u5b50\u5609\u662f\u540c\u4e00\u4e2a\u4e13\u4e1a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u5b66\u6821\u91cc\u4e5f\u5e72\u8fc7\u65b0\u751f\u63a5\u5f85\u7684\u52fe\u5f53\uff0c\u4e09\u56db\u5f20\u684c\u5b50\u6446\u5728\u8def\u8fb9\uff0c\u5728\u4e24\u68f5\u68a7\u6850\u6811\u4e4b\u95f4\u7cfb\u7740\u201c\u56fd\u9645\u5546\u5b66\u9662\u6b22\u60a8\u201d\u7684\u6a2a\u5e45\uff0c\u9662\u5b66\u751f\u4f1a\u7684\u7537\u5973\u5e72\u90e8\u6216\u8005\u4e0e\u9662\u5b66\u751f\u4f1a\u7537\u5973\u5e72\u90e8\u6709\u52fe\u5f53\u7684\u5b66\u751f\uff0c\u5750\u5728\u684c\u5b50\u540e\u9762\u5b88\u682a\u5f85\u514d\uff0c\u901a\u5e38\u8fd8\u4f1a\u6709\u4e00\u4e24\u8f86\u4e09\u8f6e\u8f66\u5e2e\u7740\u8fd0\u9001\u884c\u674e\uff0c\u6ca1\u529e\u6cd5\uff0c\u6709\u4e9b\u65b0\u751f\u751a\u81f3\u8fde\u51ac\u5929\u7684\u88ab\u8925\u90fd\u642c\u4e86\u4e24\u5e8a\u8fc7\u6765\u3002</p><p>\u3000\u3000\u8981\u662f\u8dd1\u8fc7\u6765\u65b0\u751f\u662f\u7537\u7684\uff0c\u53cb\u60c5\u5ba2\u4e32\u7684\u63a5\u5f85\u5458\u662f\u7edd\u4e0d\u4f1a\u8d70\u51fa\u6765\u62db\u547c\u7684\u3001\u90a3\u4e9b\u5b66\u751f\u4f1a\u7684\u7537\u540c\u5b66\u4e5f\u4f1a\u6ca1\u7cbe\u6253\u5f69\u7684\uff1b\u8981\u662f\u4ed6\u4eec\u5c06\u9648\u5983\u84c9\u3001\u5510\u5a67\u3001\u76db\u590f\u90fd\u5f53\u6210\u8fd9\u4e00\u5c4a\u56fd\u5546\u9662\u7684\u65b0\u751f\u8bdd\u2014\u2014\u597d\u5427\uff0c\u5f20\u606a\u4ed6\u4eec\u773c\u775b\u521a\u7784\u5230\u201c\u56fd\u9645\u5546\u5b66\u9662\u201d\u7684\u6a2a\u5e45\u6709\u4e9b\u505c\u987f\u7684\u65f6\u5019\uff0c\u5019\u5728\u63a5\u5f85\u7684\u90a3\u73ed\u7537\u751f\u773c\u775b\u5c31\u5f00\u59cb\u5192\u5149\u4e86\uff0c\u5f53\u5f20\u606a\u4ed6\u4eec\u5728\u201c\u56fd\u9645\u5546\u5b66\u9662\u201d\u7684\u6a2a\u5e45\u4e0b\u505c\u4e0b\u6765\u65f6\uff0c\u4ed6\u4eec\u5c31\u5168\u6d8c\u51fa\u6765\u4e86\uff0c\u53ef\u60dc\u53ea\u662f\u56f4\u4f4f\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u76db\u590f\u800c\u5df2\uff0c\u786c\u751f\u751f\u7684\u5c06\u5f20\u606a\u3001\u675c\u98de\u8fd8\u6709\u8096\u6625\u94ed\u3001\u9a6c\u6d77\u9f99\u6324\u5230\u5916\u9762\u53bb\u4e86\u3002</p><p>\u3000\u3000\u684c\u5b50\u540e\u9762\u8fd8\u6709\u4e24\u4e2a\u957f\u76f8\u6e05\u79c0\u7684\u5973\u5b69\u5b50\u4e3a\u81ea\u5df1\u540c\u4f34\u7684\u884c\u5f84\u611f\u5230\u7f9e\u803b\uff0c\u4ece\u684c\u5b50\u540e\u9762\u8d70\u8fc7\u6765\u3001\u8fd8\u7ed5\u4e86\u4e00\u4e2a\u5708\u624d\u8d70\u5230\u5f20\u606a\u4ed6\u4eec\u9762\u524d\u62db\u5f85\u4ed6\u4eec\uff1a\u201c\u4f60\u4eec\u90fd\u662f\u5230\u56fd\u5546\u62a5\u9053\u7684\u65b0\u540c\u5b66\u5427\uff0c\u4f60\u4eec\u7684\u884c\u674e\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u3000\u3000\u8096\u6625\u94ed\u8fd8\u662f\u5355\u7eaf\u7684\u5c11\u5e74\uff0c\u6ca1\u89c1\u8fc7\u8fd9\u4ed7\u5f0f\uff0c\u6709\u4e9b\u53d1\u8499\uff0c\u5c0f\u58f0\u95ee\u5f20\u606a\uff1a\u201c\u8bfb\u5927\u5b66\u662f\u4e0d\u662f\u6bd4\u9ad8\u4e2d\u751f\u8fd8\u9965\u6e34\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6487\u4e86\u6487\u5634\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u7559\u4e0b\u6765\u7684\u90fd\u662f\u4e00\u7fa4\u6ca1\u51fa\u606f\u7684\uff0c\u6709\u672c\u4e8b\u7684\u5149\u68cd\u90fd\u6df7\u8fdb\u5e08\u5927\u6216\u8005\u97f3\u4e50\u5b66\u9662\u53bb\u63a5\u5f85\u65b0\u751f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u540c\u5b66\uff0c\u4f60\u8fd9\u8bdd\u5c31\u8bf4\u9519\u4e86\uff0c\u4eca\u5e74\u4e09\u6240\u5b66\u6821\u7684\u6821\u9886\u5bfc\u7279\u522b\u7167\u987e\u6211\u4eec\u8fd9\u4e9b\u63a5\u5f85\u65b0\u751f\u7684\u96be\u5904\uff0c\u5e08\u5927\u65b0\u751f\u62a5\u540d\u662f\u4e09\u53f7\u56db\u53f7\uff0c\u97f3\u4e50\u5b66\u9662\u65b0\u751f\u62a5\u540d\u662f\u4e03\u53f7\u516b\u53f7\u3002\u8981\u80fd\u5c06\u4e09\u6240\u5b66\u6821\u7684\u65b0\u751f\u63a5\u5f85\u73a9\u8f6c\u8fc7\u6765\uff0c\u90a3\u662f\u8981\u7b97\u771f\u672c\u4e8b\uff0c\u4e0d\u8fc7\u4eca\u5929\u51fa\u73b0\u5728\u8fd9\u91cc\u7684\uff0c\u4e5f\u672a\u5fc5\u662f\u6ca1\u51fa\u606f\u7684\u2026\u2026\u201d\u4e00\u4e2a\u5243\u7740\u5bf8\u5934\u3001\u8eab\u6750\u9ad8\u5927\u5c11\u8bf4\u6709\u4e00\u7c73\u516b\u4e94\u3001\u5367\u8695\u7709\u3001\u773c\u775b\u5374\u7565\u7ec6\u957f\u7684\u9752\u5e74\u8f6c\u8fc7\u8eab\u6765\u7ea0\u6b63\u5f20\u606a\u7684\u8bf4\u8f9e\u3002</p><p>\u3000\u3000\u770b\u5230\u8499\u4e50\uff0c\u5f20\u606a\u5dee\u70b9\u5c31\u7b11\u51fa\u58f0\uff0c\u8fd9\u4e2a\u5c71\u4e1c\u6c49\u5b50\u957f\u76f8\u51f6\u6076\uff0c\u5374\u662f\u4e2a\u56e0\u4e3a\u7ed9\u7f51\u604b\u6ca1\u4e00\u4e2a\u6708\u7684\u5973\u53cb\u7529\u4e86\u72c2\u559d\u4e24\u74f6\u5564\u9152\u559d\u51fa\u80c3\u51fa\u8840\u7684\u5bb6\u4f19\u3002\u8499\u4e50\u4ece\u5927\u4e8c\u8d77\u5c31\u662f\u56fd\u5546\u9662\u5b66\u751f\u4f1a\u4f1a\u957f\uff0c\u5f53\u521d\u81ea\u5df1\u5c31\u662f\u9760\u7740\u4ed6\u6df7\u8fdb\u5b66\u751f\u4f1a\u641e\u65b0\u751f\u63a5\u5f85\u7684\u3002</p><p>\u3000\u3000\u201c\u8499\u4e50\uff0c\u4e5d\u4e94\u5e02\u573a\u8425\u9500\u7684\uff0c\u4f60\u4eec\u662f\u2026\u2026\u201d\u8499\u4e50\u4f38\u51fa\u4ed6\u539a\u5b9e\u7684\u624b\u638c\u81ea\u6211\u4ecb\u7ecd\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\uff0c\u4ed6\uff0c\u675c\u98de\uff0c\u6211\u4eec\u4fe9\u90fd\u662f\u7ecf\u6d4e\u5b66\u4e13\u4e1a\uff0c\u4ed6\uff0c\u8096\u6625\u94ed\uff0c\u56fd\u9645\u91d1\u878d\u2026\u2026\u201d\u5f20\u606a\u53c8\u7b11\u7740\u6307\u5411\u7ed9\u56f4\u5728\u4eba\u7fa4\u91cc\u7684\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u76db\u590f\u4e09\u4e2a\u5973\u5b69\u5b50\uff0c\u201c\u5979\u4eec\uff0c\u662f\u6211\u4eec\u7684\u884c\u674e\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9760\uff0c\u201d\u542c\u5230\u4e09\u4e2a\u6c34\u7075\u7075\u7684\u5973\u5b69\u5b50\u90fd\u540d\u82b1\u6709\u4e3b\uff0c\u8c01\u90fd\u4f1a\u5fcd\u4e0d\u4f4f\u9a82\u4e86\u4e00\u58f0\uff0c\u8499\u4e50\u8f7b\u5550\u4e86\u4e00\u53e3\uff0c\u5c06\u8eab\u8fb9\u4e24\u4e2a\u6b63\u671d\u7740\u5510\u5a67\u732e\u6bb7\u52e4\u7684\u7537\u5b69\u5b50\u6252\u62e8\u5f00\uff0c\u201c\u7272\u53e3\u4eec\uff0c\u90fd\u662f\u6709\u4e3b\u7684\uff0c\u4e0d\u8981\u4e00\u4ed8\u4e8c\u5341\u5e74\u6ca1\u89c1\u8fc7\u6bcd\u732a\u7684\u6837\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5a67\u3001\u9648\u5983\u84c9\u8fd9\u624d\u90fd\u7ea2\u7740\u8138\u56de\u5230\u5f20\u606a\u8eab\u8fb9\uff0c\u4e0d\u6653\u5f97\u662f\u4e0d\u662f\u7ed9\u8bbd\u523a\u4e3a\u6bcd\u732a\u53c8\u65e0\u529b\u56de\u9a73\uff1b\u76db\u590f\u5012\u662f\u5766\u7136\u81ea\u82e5\uff0c\u56de\u5230\u675c\u98de\u8eab\u8fb9\u8fd8\u6390\u4e86\u4ed6\u4e00\u4e0b\uff1a\u201c\u77a7\u4f60\u4eec\u7684\u5fb7\u6027\uff01\u201d</p><p>\u3000\u3000\u675c\u98de\u59d4\u5c48\u7684\u62b1\u6028\u4e86\u4e00\u53e5\uff1a\u201c\u6328\u6211\u4eec\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u80fd\u597d\uff01\u201d\u76db\u590f\u6a2a\u4e86\u675c\u98de\u4e00\u773c\u3002</p><p>\u3000\u3000\u8499\u4e50\u9886\u7740\u5f20\u606a\u4ed6\u4eec\u79d1\u6559\u4f1a\u8bae\u5385\u90a3\u8fb9\u4ea4\u8d39\u529e\u7406\u624b\u7eed\uff0c\u8fd8\u6709\u4e00\u7fa4\u7272\u53e3\u4e0d\u7518\u5fc3\u60f3\u8ddf\u7740\uff0c\u90fd\u8ba9\u8499\u4e50\u77aa\u773c\u5236\u6b62\uff0c\u6709\u4eba\u5600\u5495\u9053\uff1a\u201c\u4e0d\u8fd8\u5f97\u5e2e\u7740\u642c\u884c\u674e\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u4eec\u4e5f\u8981\u7ed9\u6211\u5728\u8fd9\u91cc\u7b49\u7740\uff01\u201d</p><p>\u3000\u3000\u4e3b\u5e72\u9053\u90a3\u8fb9\u90fd\u4eba\u5c71\u4eba\u6d77\u4e86\uff0c\u62a5\u540d\u5904\u66f4\u662f\u4eba\u5934\u6512\u52a8\uff0c\u8499\u4e50\u673a\u8b66\uff0c\u770b\u7740\u54ea\u5904\u4eba\u5c11\uff0c\u5c31\u5e26\u7740\u5f20\u606a\u4ed6\u4eec\u5f80\u54ea\u91cc\u8dd1\uff0c\u52a0\u4e0a\u4ed6\u4eec\u6392\u961f\u7684\u4eba\u591a\uff0c\u5f53\u4e2d\u53c8\u52a0\u4e86\u4e24\u4e2a\u585e\uff0c\u5f53\u5176\u4ed6\u65b0\u751f\u8fd8\u832b\u7136\u7ad9\u5728\u62a5\u9053\u5927\u5385\u91cc\u4e0d\u77e5\u6240\u63aa\uff0c\u5f20\u606a\u4ed6\u4eec\u4e00\u4f1a\u513f\u65f6\u95f4\u5c31\u5c06\u6240\u6709\u7684\u624b\u7eed\u529e\u5b8c\u3002</p><p>\u3000\u3000\u201c\u9648\u5983\u84c9\u2026\u2026\u201d\u542c\u5230\u6709\u4eba\u5728\u558a\u9648\u5983\u84c9\uff0c\u5f20\u606a\u4e5f\u56de\u5934\u770b\u8fc7\u53bb\uff0c\u5374\u662f\u80e1\u91d1\u661f\u90a3\u5c0f\u5b50\u4ece\u5916\u9762\u8de8\u8fdb\u6765\uff0c\u4ed6\u4e00\u773c\u8ba4\u51fa\u9648\u5983\u84c9\u5c31\u558a\u5979\uff0c\u770b\u5230\u5f20\u606a\u56de\u8fc7\u5934\u6765\uff0c\u5c31\u4e0d\u81ea\u7136\u7684\u522b\u8fc7\u8138\u53bb\u3002</p><p>\u3000\u3000\u201c\u90a3\u5c0f\u5b50\uff0c\u4f60\u4eec\u8ba4\u8bc6\uff1f\u201d\u8499\u4e50\u770b\u4e86\u4e00\u773c\u80e1\u91d1\u661f\uff0c\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u4e0d\u8ba4\u8bc6\u3002\u201d\u5f20\u606a\u8038\u8038\u80a9\uff0c\u5efa\u90ba\u5e02\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\uff0c\u80e1\u5b97\u5e86\u65e0\u513f\u65e0\u5973\uff0c\u5c06\u80e1\u91d1\u661f\u5f53\u513f\u5b50\u770b\u5f85\u3002\u5934\u75bc\u8ddf\u8499\u4e50\u89e3\u91ca\u90a3\u5c42\u5173\u7cfb\uff0c\u8fd8\u8981\u5c06\u9648\u5983\u84c9\u626f\u8fdb\u6765\uff0c\u5f20\u606a\u5c31\u7d22\u6027\u8bf4\u4e0d\u8ba4\u8bc6\u3002</p><p>\u3000\u3000\u201c\u90a3\u5bb6\u4f19\u4e0d\u5730\u9053\uff0c\u4f60\u4eec\u8981\u770b\u597d\u81ea\u5df1\u7684\u884c\u674e\uff0c\u201d\u8499\u4e50\u597d\u5fc3\u63d0\u9192\u4e86\u4e00\u53e5\uff0c\u201c\u4ed6\u5728\u65b0\u6d66\u6821\u533a\u90a3\u5757\uff0c\u5c31\u7ea0\u7f20\u4e00\u4e2a\u5973\u5b69\uff0c\u5bb3\u90a3\u5973\u5b69\u5b50\u90fd\u6821\u5b66\u751f\u4f1a\u90fd\u4e0d\u6562\u8fdb\u3002\u201d\u53c8\u53ef\u60dc\u7684\u5482\u4e86\u5482\u5634\uff0c\u201c\u542c\u8bf4\u90a3\u5973\u5b69\u5b50\u662f\u65b0\u6d66\u6821\u533a\u7684\u6821\u82b1\uff0c\u53ef\u60dc\u90fd\u6ca1\u6709\u89c1\u7740\uff0c\u4f60\u8bf4\u8fd9\u6df7\u7403\u53ef\u4e0d\u53ef\u607c\u2026\u2026\u201d</p><p>\u3000\u3000\u8499\u4e50\u8fd9\u5bb6\u4f19\u662f\u607c\u80e1\u91d1\u661f\u5413\u5f97\u9648\u5983\u84c9\u4e0d\u6562\u8fdb\u5b66\u751f\u4f1a\u5427\uff1f\u4e0d\u7136\u4ed6\u4e5f\u6709\u673a\u4f1a\u8ba4\u8bc6\u9648\u5983\u84c9\u4e86\u3002\u5f20\u606a\u563f\u7136\u7b11\u7740\u770b\u4e86\u9648\u5983\u84c9\u4e00\u773c\uff0c\u9648\u5983\u84c9\u4fcf\u8138\u5fae\u7ea2\uff0c\u5c0f\u58f0\u7684\u5600\u5495\uff1a\u201c\u660e\u660e\u662f\u4eba\u5bb6\u5973\u5b69\u5b50\u4e0d\u60f3\u53bb\u2026\u2026\u201d\u58f0\u97f3\u4f4e\u5230\u53ea\u6709\u8fd1\u5904\u7684\u5f20\u606a\u3001\u5510\u5a67\u80fd\u542c\u5f97\u89c1\u3002</p><p>\u3000\u3000\u770b\u5230\u674e\u5b63\u3001\u8bb8\u82e5\u8679\u4ed6\u4eec\uff0c\u5927\u5bb6\u7684\u884c\u674e\u90fd\u5728\u4e00\u8d77\uff0c\u5f20\u606a\u4fbf\u7b49\u4ed6\u4eec\u4e00\u8d77\u5c06\u62a5\u540d\u624b\u7eed\u529e\u5b8c\uff0c\u8499\u4e50\u6bd4\u8f83\u6709\u8d23\u4efb\u5fc3\uff0c\u5148\u56de\u63a5\u5f85\u70b9\u53bb\uff0c\u8ba9\u5f20\u606a\u4ed6\u4eec\u529e\u7406\u624b\u7eed\u56de\u63a5\u5f85\u70b9\u627e\u4eba\u5e2e\u7740\u642c\u884c\u674e\u3002</p><p>\u3000\u3000\u5927\u5385\u91cc\u5f88\u70ed\uff0c\u5f20\u606a\u4ed6\u4eec\u627e\u5230\u80cc\u9634\u901a\u98ce\u7684\u5730\u65b9\u7b49\u7740\uff0c\u5f20\u606a\u8e72\u5728\u53f0\u9636\u4e0a\uff0c\u770b\u7740\u8fdb\u8fdb\u51fa\u51fa\u7684\u65b0\u751f\u4eec\uff0c\u4ed6\u6b64\u65f6\u7684\u611f\u89c9\u5f88\u6de1\uff0c\u611f\u89e6\u5374\u6df1\uff0c\u8fd8\u80fd\u60f3\u8d77\u524d\u4e16\u4e0e\u675c\u98de\u4e24\u4e2a\u4eba\u6123\u5934\u6123\u8111\u5728\u4eba\u7fa4\u4e4b\u4e2d\u7a7f\u68ad\u7684\u6a21\u6837\u3002</p><p>\u3000\u3000\u201c\u5514\u2026\u2026\u201d\u5f20\u606a\u542c\u5230\u8eab\u540e\u6709\u5431\u5431\u5514\u5514\u7684\u58f0\u97f3\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u4e00\u4e2a\u957f\u76f8\u86ee\u7075\u79c0\u7684\u5973\u5b69\u5b50\u7741\u7740\u773c\u775b\u770b\u7740\u81ea\u5df1\uff0c\u60f3\u6253\u62db\u547c\u53c8\u6709\u4e9b\u7f9e\u6da9\u7684\u6a21\u6837\uff0c\u58f0\u97f3\u90fd\u6709\u4e9b\u8d70\u5f62\uff0c\u81ea\u5df1\u7684\u8138\u5374\u5148\u6da8\u7ea2\u4e86\u3002</p><p>\u3000\u3000\u201c\u53eb\u6211\uff1f\u201d\u5f20\u606a\u6307\u4e86\u6307\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u53c8\u62ac\u5934\u770b\u4e86\u770b\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u675c\u98de\u4ed6\u4eec\uff0c\u786e\u5b9a\u4e0d\u662f\u53eb\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u201c\u5514\uff0c\u4f60\u4eec\u5b66\u6821\u641e\u51ac\u4ee4\u8425\u5230\u6211\u4eec\u6d2a\u81f4\u6797\u573a\u641e\u6d3b\u52a8\uff0c\u4f60\u8ddf\u6211\u7238\u7238\u4e0b\u8fc7\u68cb\uff0c\u6211\u8fd8\u6c8f\u8fc7\u5c71\u8336\u7ed9\u4f60\u559d\uff0c\u8fd8\u6709\u4ed6\u2026\u2026\u201d\u5973\u5b69\u5b50\u770b\u4e86\u675c\u98de\u4e00\u773c\uff0c\u201c\u6ca1\u4e8b\u5c31\u62ff\u77f3\u5934\u4e22\u6811\u68a2\u4e0a\u7684\u9e1f\u5de2\uff0c\u8b66\u544a\u4f60\u4e24\u56de\u4f60\u90fd\u6ca1\u6709\u5728\u542c\u2026\u2026\u201d\u5973\u5b69\u5b50\u597d\u4e0d\u5bb9\u6613\u9f13\u8db3\u52c7\u6c14\u4e00\u53e3\u6c14\u5c06\u6240\u6709\u7684\u8bdd\u90fd\u8bf4\u5b8c\u4e86\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u5f20\u606a\u60f3\u8d77\u6765\u4e86\uff0c\u6307\u7740\u5973\u5b69\u5b50\uff0c\u201c\u6c88\u2026\u2026\u201d\u201c\u6c88\u201d\u4e86\u534a\u5929\u6ca1\u8bb0\u8d77\u8fd9\u5973\u5b69\u5b50\u53eb\u4ec0\u4e48\uff0c\u8f6c\u4e86\u4e2a\u8bed\u8c03\u8bf4\uff0c\u201c\u6c88\u8001\u5e08\u7684\u5973\u513f\uff0c\u6797\u573a\u7684\u5c71\u8336\u662f\u5f88\u4e0d\u9519\u3002\u201d</p><p>\u3000\u3000\u9ad8\u4e8c\u5bd2\u5047\u7684\u51ac\u4ee4\u8425\uff0c\u5f20\u606a\u4ed6\u4eec\u90fd\u6709\u53c2\u52a0\uff0c\u90a3\u4e00\u5e74\u5c31\u662f\u6eaf\u5c0f\u6c5f\u800c\u4e0a\uff0c\u5c0f\u6c5f\u4e0a\u6e38\u7684\u6d2a\u81f4\u6797\u573a\u662f\u7ec8\u70b9\u7ad9\uff0c\u8fd8\u5728\u6d2a\u81f4\u6797\u573a\u4e0e\u6797\u573a\u4e2d\u5b66\u7684\u5b66\u751f\u4e00\u8d77\u641e\u8054\u8c0a\u6d3b\u52a8\uff1b\u5f20\u606a\u5f53\u65f6\u770b\u5230\u5c0f\u6c5f\u4e24\u5cb8\u4e71\u780d\u6ee5\u4f10\u7684\u73b0\u573a\u4e25\u91cd\uff0c\u4e34\u65f6\u51b3\u5b9a\u4e0e\u5468\u6e38\u3001\u5085\u4fca\u7559\u4e0b\u6765\u6478\u4e2a\u5e95\uff0c\u53c8\u4e0e\u6797\u573a\u68cb\u9986\u7684\u4e3b\u4eba\u76f8\u9047\uff0c\u5f20\u606a\u8fd8\u8bb0\u5f97\u68cb\u9986\u7684\u4e3b\u4eba\u540d\u53eb\u6c88\u7ea6\uff0c\u4e0b\u4e86\u4e00\u624b\u597d\u68cb\uff0c\u8fd8\u662f\u6797\u573a\u4e2d\u5b66\u7684\u6559\u5e08\uff0c\u4ed6\u5973\u513f\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u4e00\u65f6\u60f3\u4e0d\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u6c88\u7b71\uff0c\u6211\u8bb0\u5f97\u4f60\u540d\u5b57\u3002\u201d\u5510\u5a67\u8bb0\u6027\u597d\u3002</p><p>\u3000\u3000\u201c\u6211\u4e5f\u8bb0\u5f97\u4f60\u540d\u5b57\uff0c\u4f60\u53eb\u5510\u5a67\uff0c\u201d\u6c88\u7b71\u53cc\u624b\u8f7b\u63e1\u7740\u8eab\u524d\uff0c\u7075\u79c0\u7684\u773c\u775b\u8f6c\u7740\u770b\u5411\u9648\u5983\u84c9\uff0c\u201c\u4f60\u53eb\u9648\u5983\u84c9\uff0c\u4ed6\u53eb\u5f20\u606a\u3001\u4ed6\u53eb\u675c\u98de\u2026\u2026\u201d\u7b49\u7740\u8096\u6625\u94ed\u3001\u674e\u5b63\u3001\u8bb8\u82e5\u8679\u8fc7\u6765\uff0c\u53c8\u4e00\u4e1d\u4e0d\u5dee\u7684\u62a5\u51fa\u4ed6\u4eec\u7684\u540d\u5b57\uff0c\u53ea\u662f\u795e\u60c5\u95f4\u4e0d\u8131\u7eaf\u6734\u7684\u7f9e\u6da9\u3002</p><p>\u3000\u3000\u53ea\u8bb0\u5f97\u5f53\u65f6\u5927\u5bb6\u641e\u8054\u8c0a\u6d3b\u52a8\u65f6\u6709\u81ea\u62a5\u8fc7\u5bb6\u95e8\uff0c\u81ea\u6211\u4ecb\u7ecd\u90fd\u5f88\u7b80\u77ed\uff0c\u5927\u591a\u62a5\u4e2a\u540d\u5b57\u4e86\u4e8b\uff0c\u54ea\u91cc\u60f3\u5230\u6c88\u7b71\u80fd\u5c06\u6240\u6709\u4eba\u7684\u540d\u5b57\u90fd\u8bb0\u4f4f\uff1f\u675c\u98de\u9876\u4e86\u9876\u5f20\u606a\u7684\u540e\u80cc\uff0c\u4f7f\u5f97\u773c\u8272\uff0c\u793a\u610f\u8fd9\u5973\u5b69\u8bb0\u5fc6\u529b\u592a\u6050\u6016\u4e86\u3002</p></article>"}}}]);
