(window.webpackJsonp=window.webpackJsonp||[]).push([[774],{797:function(p){p.exports={chapterTitle:null,title:"\u7b2c608\u7ae0 \u4eab\u53d7\u5927\u5b66\u751f\u6d3b",content:"<article><h2>\u7b2c608\u7ae0 \u4eab\u53d7\u5927\u5b66\u751f\u6d3b</h2><p>\u3000\u3000\u6c88\u7b71\u51fa\u8272\u7684\u8bb0\u5fc6\u529b\u5413\u4e86\u5927\u5bb6\u4e00\u8df3\uff0c\u6ca1\u60f3\u5230\u524d\u5e74\u51ac\u5929\u6821\u9645\u8054\u8c0a\u5c31\u80fd\u8ba9\u5979\u8bb0\u4f4f\u5927\u5bb6\u7684\u540d\u5b57\uff0c\u5927\u5bb6\u90fd\u4e0d\u662f\u7f9e\u6da9\u7684\u4eba\uff0c\u675c\u98de\u770b\u5979\u624b\u91cc\u634f\u7740\u7eff\u5c01\u76ae\u7684\u65b0\u751f\u5c0f\u518c\u5b50\uff0c\u5934\u63a2\u8fc7\u53bb\u95ee\u5979\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e13\u4e1a\uff1f\u201d</p><p>\u3000\u3000\u201c\u8ddf\u4f60\u4eec\u4e00\u6837\uff0c\u7ecf\u6d4e\u5b66\u7cfb\uff0c\u5728\u82b1\u540d\u518c\u4e0a\u770b\u5230\u4f60\u4eec\u7684\u540d\u5b57\uff0c\u8fd8\u4ee5\u4e3a\u9047\u5230\u4e24\u4e2a\u540c\u540d\u540c\u59d3\u7684\u4eba\u5462\uff0c\u6ca1\u60f3\u5230\u4f60\u4eec\u4e5f\u90fd\u8003\u4e0a\u4e1c\u5927\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u90fd\u8003\u4e0a\u4e86\u2026\u2026\u201d\u5510\u5a67\u563b\u7b11\u7740\uff0c\u8bf4\u201c\u8003\u201d\u5b57\u65f6\uff0c\u97f3\u54ac\u91cd\u4e86\u4e00\u4e9b\uff0c\u8fd8\u77a5\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5f20\u606a\u8138\u76ae\u5b50\u539a\u5b9e\uff0c\u53ea\u662f\u8146\u7740\u8138\u7b11\u4e86\u7b11\uff0c\u5c31\u771f\u5f53\u81ea\u5df1\u4e5f\u662f\u8003\u4e0a\u7684\u3002</p><p>\u3000\u3000\u524d\u4e00\u4e16\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u8bfb\u7684\u662f\u56fd\u9645\u91d1\u878d\uff0c\u5949\u7740\u5154\u5b50\u4e0d\u5403\u7a9d\u8fb9\u8349\u7684\u539f\u5219\uff0c\u5230\u5927\u56db\u65f6\uff0c\u8fde\u81ea\u5df1\u540c\u4e00\u4e13\u4e1a\u7684\u5973\u5b69\u5b50\u90fd\u53eb\u4e0d\u5168\u540d\u5b57\uff0c\u66f4\u4e0d\u7528\u8bf4\u540c\u5c4a\u540c\u9662\u7cfb\u770b\u4e0a\u53bb\u6709\u4e9b\u7eaf\u6734\u7684\u6c88\u7b71\u4e86\uff1b\u4e0d\u8fc7\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u5012\u662f\u6709\u4e9b\u5370\u8c61\uff0c\u597d\u50cf\u5c31\u662f\u90a3\u4e2a\u4e00\u76f4\u5c06\u56fd\u5546\u9662\u4e5d\u4e03\u5c4a\u60df\u4e00\u7684\u4e00\u4e2a\u7279\u7b49\u5956\u5b66\u91d1\u540d\u989d\u9738\u5360\u4e86\u56db\u5e74\u7684\u5973\u5b69\u5b50\u3002</p><p>\u3000\u3000\u5012\u662f\u6ca1\u6709\u60f3\u5230\u4f1a\u6210\u4e00\u4e2a\u4e13\u4e1a\u7684\u540c\u5b66\u4e86\u3002</p><p>\u3000\u3000\u6c88\u7b71\u5bb9\u8c8c\u5012\u4e5f\u4e0d\u5dee\uff0c\u773c\u7738\u7075\u79c0\u3001\u9f3b\u76f4\u5507\u8584\u3001\u8138\u5f62\u7626\u524a\uff0c\u53ea\u662f\u8fd9\u65f6\u6ca1\u6709\u6d17\u8131\u6d53\u90c1\u7684\u4e61\u571f\u6c14\u606f\uff0c\u8fd8\u68b3\u7740\u4e24\u6839\u8fab\u5b50\uff0c\u4e00\u5de6\u4e00\u53f3\u642d\u5728\u80a9\u4e0a\uff0c\u8863\u7740\u7eaf\u6734\u4e9b\uff0c\u7d20\u82b1\u7684\u5316\u7ea4\u77ed\u8896\u886c\u886b\uff0c\u5728\u9633\u5149\u7167\u5c04\u4e0b\u8fd8\u6709\u4e9b\u900f\u660e\uff0c\u900f\u51fa\u91cc\u9762\u767d\u8272\u5185\u8863\u7684\u5e95\u5b50\u3002</p><p>\u3000\u3000\u770b\u5230\u8fd9\u4e2a\uff0c\u5f20\u606a\u5c31\u60f3\u8d77\u7b2c\u4e00\u6b21\u8ddf\u8bb8\u601d\u53bb\u9648\u5983\u84c9\u5bb6\u53bb\uff0c\u9648\u5983\u84c9\u5c31\u7a7f\u7740\u5316\u7ea4\u7684\u7761\u8863\uff0c\u5728\u706f\u5149\u4e0b\u6bd4\u8f83\u900f\uff0c\u5173\u952e\u5f53\u65f6\u5979\u91cc\u9762\u6ca1\u6709\u7a7f\u4ec0\u4e48\uff0c\u5f20\u606a\u5f53\u65f6\u5077\u770b\u4e86\u4e24\u773c\uff0c\u7ed9\u9648\u5983\u84c9\u53d1\u89c9\u540e\u7ed9\u72e0\u8e22\u4e86\u4e00\u811a\uff0c\u60f3\u5230\u8fd9\u91cc\uff0c\u5f20\u606a\u5c31\u4e0b\u610f\u8bc6\u7684\u770b\u4e86\u9648\u5983\u84c9\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u5929\u6c14\u708e\u70ed\uff0c\u9648\u5983\u84c9\u5149\u6d01\u5982\u7389\u7684\u989d\u5934\u6c81\u51fa\u7ec6\u81f4\u7684\u6c57\u73e0\uff0c\u8138\u988a\u5fae\u7ea2\uff0c\u89c1\u5f20\u606a\u7684\u773c\u775b\u7a81\u7136\u4ece\u6c88\u7b71\u8eab\u4e0a\u8f6c\u5230\u81ea\u5df1\u8eab\u8fb9\uff0c\u77ac\u65f6\u4e5f\u60f3\u5230\u5f53\u65f6\u8d70\u5149\u7684\u60c5\u5f62\uff0c\u8138\u988a\u6709\u4e9b\u53d1\u70eb\uff0c\u7075\u79c0\u660e\u8273\u7684\u773c\u7738\u5b50\u95ea\u5230\u522b\u5904\u3002</p><p>\u3000\u3000\u201c\u6c88\u8001\u5e08\u6709\u966a\u4f60\u6765\u62a5\u540d\u5427\uff0c\u4ed6\u4eba\u5728\u54ea\u91cc\uff1f\u201d\u5f20\u606a\u5bf9\u6c88\u7b71\u4ed6\u7238\u7238\u6c88\u7ea6\u7684\u68cb\u827a\u4ee5\u53ca\u4ed6\u63d0\u4f9b\u7684\u5f62\u72b6\u4e0d\u662f\u5f88\u597d\u770b\u7684\u5c71\u8336\u5370\u8c61\u6df1\u523b\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u7279\u522b\u559c\u6b22\u90a3\u5c71\u8336\uff0c\u5f20\u606a\u62ff\u4e86\u4e24\u5305\u56de\u6d77\u5dde\uff0c\u5927\u534a\u90fd\u7ed9\u8bb8\u9e3f\u4f2f\u8981\u4e86\u8fc7\u53bb\uff0c\u540e\u6765\u5f20\u606a\u5c31\u6ca1\u6709\u518d\u53bb\u4e0a\u6e38\u7684\u6797\u573a\uff0c\u5012\u662f\u8bb8\u9e3f\u4f2f\u8fd1\u4e24\u5e74\u6765\u56e0\u4e3a\u8425\u6797\u7684\u7f18\u6545\u8db3\u8ff9\u904d\u5e03\u5c0f\u6c5f\u6d41\u57df\uff0c\u5f20\u606a\u60f3\u8d77\u8981\u559d\u8fd9\u8336\uff0c\u8fd8\u8981\u627e\u8bb8\u9e3f\u4f2f\u8981\u3002</p><p>\u3000\u3000\u201c\u6211\u7238\u5f53\u7136\u8981\u7559\u5728\u5b66\u6821\u4e0a\u8bfe\u5462\uff0c\u6628\u5929\u591c\u91cc\uff0c\u6211\u8ddf\u540c\u5b66\u4e00\u8d77\u5750\u8239\u6765\u7684\u2026\u2026\u201d\u6c88\u7b71\u8bf4\u9053\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6c88\u7b71\u7684\u884c\u56ca\u653e\u5728\u63a5\u5f85\u70b9\u90a3\u91cc\uff0c\u5f20\u606a\u8ba9\u9a6c\u6d77\u9f99\u5c06\u88c5\u7740\u884c\u56ca\u7684\u8f66\u5f00\u5230\u63a5\u5f85\u70b9\u53bb\uff0c\u7b49\u674e\u5b63\u3001\u675c\u82e5\u8679\u529e\u5b8c\u624b\u7eed\uff0c\u5c31\u53bb\u63a5\u5f85\u70b9\u5e2e\u6c88\u7b71\u7684\u884c\u56ca\u4e00\u5e76\u88c5\u4e0a\u8f66\uff0c\u8499\u4e50\u90a3\u65f6\u4e0d\u5728\u63a5\u5f85\u70b9\uff0c\u4e5f\u5c31\u6ca1\u6709\u62db\u547c\uff0c\u4ed6\u4eec\u4e00\u7fa4\u4eba\u6d69\u6d69\u8361\u8361\u7684\u5f80\u5bbf\u820d\u533a\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u56fd\u5546\u9662\u7684\u5973\u751f\u5bbf\u820d\u697c\u662f\u4e8c\u820d\uff0c\u5148\u9001\u6c88\u7b71\u53bb\u4e8c\u820d\u3002</p><p>\u3000\u3000\u8bb8\u591a\u5bb6\u957f\u90fd\u5f00\u7740\u8f66\u9001\u5b50\u5973\u6765\u62a5\u9053\uff0c\u9a6c\u6d77\u9f99\u5f00\u7740\u5546\u52a1\u8f66\u8ddf\u5f20\u606a\u4ed6\u4eec\u540e\u9762\u5c31\u4e00\u70b9\u90fd\u4e0d\u8d77\u773c\u2014\u2014\u5f20\u606a\u4e4b\u524d\u575a\u6301\u8ba9\u76db\u590f\u5c06\u90a3\u8f86\u83b2\u82b1\u505c\u5728\u6821\u5916\uff0c\u9752\u5e74\u83b2\u82b1\u867d\u7136\u88ab\u55bb\u4e3a\u8d2b\u6c11\u8dd1\u8f66\uff0c\u4f46\u81f3\u5c11\u4e5f\u662f\u8f86\u5f88\u624e\u773c\u7684\u8dd1\u8f66\u2014\u2014\u8bb8\u82e5\u8679\u5c31\u8bfb\u5916\u8bed\u5b66\u9662\uff0c\u5bbf\u820d\u5728\u56fd\u5546\u9662\u5973\u751f\u7f29\u820d\u697c\u8fb9\u4e0a\uff0c\u4e0e\u9648\u5983\u84c9\u540c\u4e00\u680b\u697c\uff1b\u53c8\u5c06\u674e\u5b63\u3001\u8096\u6625\u94ed\u9001\u53bb\u5bbf\u820d\u3002\u4ed6\u4eec\u5230\u4e86\u5bbf\u820d\u4e4b\u540e\u8fd8\u8981\u5230\u7ba1\u7406\u5458\u90a3\u91cc\u9886\u88ab\u8fb1\u3001\u8138\u76c6\u3001\u996d\u76c6\u4e4b\u7c7b\u7684\u751f\u6d3b\u7528\u54c1\uff0c\u8ddf\u4ed6\u4eec\u90fd\u7ea6\u597d\u9ec4\u660f\u4e94\u70b9\u949f\u518d\u5230\u63a5\u5f85\u70b9\u78b0\u9762\uff0c\u5230\u65f6\u5019\u8981\u4ea4\u6362\u5bbf\u820d\u7535\u8bdd\u2014\u2014\u5927\u5b66\u91cc\u9664\u4e86\u540c\u5bbf\u820d\u7684\u540c\u5b66\uff0c\u5173\u7cfb\u597d\u7684\u4e5f\u6ca1\u6709\u591a\u5c11\u4e86\uff0c\u66f4\u4f55\u51b5\u5f20\u606a\u4e0e\u675c\u98de\u5c31\u4e0d\u4f1a\u4f4f\u5728\u9662\u7cfb\u5b89\u6392\u7684\u5bbf\u820d\u91cc\u3002</p><p>\u3000\u3000\u5c06\u4ed6\u4eec\u9001\u5230\u5bbf\u820d\uff0c\u5f20\u606a\u624d\u5f80\u7814\u7a76\u751f\u516c\u5bd3\u697c\u8d70\u53bb\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u5927\u5b66\u5efa\u6821\u8fd1\u767e\u5e74\uff0c\u5b66\u751f\u5bbf\u820d\u533a\u4e00\u518d\u6269\u5efa\uff0c\u5012\u662f\u6700\u521d\u5efa\u6821\u65f6\u5c31\u5efa\u6210\u7684\u4e00\u6279\u9752\u7816\u5c0f\u697c\u6700\u5177\u98ce\u683c\uff0c\u8d70\u8fdb\u53bb\u8fd8\u6709\u5431\u5440\u4f5c\u54cd\u7684\u6728\u697c\u68af\uff0c\u6b64\u65f6\u90fd\u6210\u4e3a\u5973\u751f\u5bbf\u820d\u697c\u3002</p><p>\u3000\u3000\u770b\u7740\u7a97\u5916\u6311\u6652\u7740\u7684\u7ea2\u7eff\u5185\u8863\uff0c\u989d\u5916\u7684\u8d4f\u5fc3\u60a6\u76ee\u3002</p><p>\u3000\u3000\u5973\u751f\u5bbf\u820d\u533a\u540e\u9762\u662f\u5ea7\u77ee\u5c71\uff0c\u4e1c\u5927\u4eba\u90fd\u620f\u79f0\u90a3\u662f\u5408\u6b22\u5c71\u3002\u7ad9\u5230\u5c71\u4e0a\uff0c\u53ef\u4ee5\u671b\u89c1\u71d5\u5f52\u6e56\u6c34\uff0c\u8981\u662f\u5bf9\u968f\u4fbf\u53ef\u89c1\u7684\u536b\u751f\u7eb8\u3001\u5076\u5c14\u4f1a\u6709\u907f\u5b55\u5957\u89c6\u82e5\u65e0\u7779\u7684\u8bdd\uff0c\u90a3\u91cc\u5e94\u8be5\u662f\u4e1c\u5927\u98ce\u666f\u6700\u4f73\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u5c71\u4e0b\u6709\u9632\u7a7a\u96a7\u9053\u7a7f\u8fc7\u53bb\uff0c\u7537\u751f\u5bbf\u820d\u533a\u4f4d\u4e8e\u5c71\u540e\uff0c\u9634\u68ee\u5e7d\u6697\u81ea\u4e0d\u7528\u8bf4\uff0c\u5173\u952e\u90fd\u662f\u516d\u4e03\u5341\u5e74\u4ee3\u9646\u7eed\u5efa\u8d77\u6765\u7684\u7b80\u6613\u697c\u623f\uff0c\u5750\u8fdb\u7537\u751f\u5bbf\u820d\u91cc\u8ddf\u4f4f\u8fdb\u8f6e\u8239\u4e94\u7b49\u8231\u7684\u611f\u89c9\u5dee\u4e0d\u591a\uff0c\u72ed\u4ec4\u9634\u6697\uff0c\u5f25\u6f2b\u96be\u4ee5\u8a00\u55bb\u7684\u53e4\u602a\u6c14\u5473\u3002\u591c\u91cc\u7761\u89c9\u8fd8\u8981\u5fcd\u53d7\u5176\u4ed6\u4e03\u4e2a\u4eba\u53ef\u80fd\u662f\u68a6\u8bdd\u3001\u53ef\u80fd\u662f\u547c\u565c\u3001\u8fd8\u53ef\u80fd\u662f\u90a3\u79cd\u6307\u7532\u5212\u8fc7\u73bb\u7483\u4f3c\u7684\u78e8\u7259\u58f0\u7684\u6298\u78e8\u3002</p><p>\u3000\u3000\u8fd8\u597d\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u4e0d\u9700\u8981\u5fcd\u53d7\u8fd9\u6837\u7684\u6298\u78e8\u3002</p><p>\u3000\u3000\u4e1c\u5927\u4e3a\u4e86\u6539\u5584\u5b66\u751f\u4f4f\u5bbf\u73af\u5883\uff0c\u5728\u5408\u6b22\u5c71\u7684\u4e1c\u8fb9\u5efa\u4e86\u4e24\u680b\u7814\u7a76\u751f\u516c\u5bd3\u697c\uff0c\u4f4f\u5bbf\u6761\u4ef6\u76f8\u5bf9\u8f83\u597d\uff0c\u897f\u697c\u90fd\u4f4f\u7537\u751f\uff0c\u4e1c\u697c\u6709\u4f4f\u7537\u751f\u3001\u6709\u4f4f\u5973\u751f\uff0c\u4e1c\u5927\u5bf9\u7814\u7a76\u751f\u7684\u7ba1\u7406\u4e0d\u662f\u90a3\u4e48\u6b7b\u677f\u3002</p><p>\u3000\u3000\u697c\u524d\u662f\u4e1c\u5927\u65b0\u5efa\u7684\u5851\u80f6\u7f51\u7403\u573a\u2014\u2014\u636e\u8bf4\u662f\u5efa\u90ba\u6700\u9ad8\u6863\u6b21\u7684\uff0c\u4e1c\u697c\u5916\u4fa7\u5c31\u662f\u4e1c\u5927\u7684\u9662\u5899\uff0c\u9662\u5899\u5916\u5c31\u662f\u65b0\u5efa\u4e2d\u7684\u9752\u5e74\u516c\u5bd3\u4e0e\u5b66\u5e9c\u5df7\uff0c\u4e1c\u5927\u65b0\u5efa\u7684\u6821\u4e1c\u95e8\u4e5f\u5c31\u5728\u7f51\u7403\u573a\u524d\u3002</p><p>\u3000\u3000\u5f20\u606a\u662f\u6253\u7b97\u7b49\u9752\u5e74\u516c\u5bd3\u5efa\u6210\u4e4b\u540e\uff0c\u4ed6\u5c31\u4e0e\u675c\u98de\u8fdb\u53bb\uff0c\u73b0\u5728\u4e5f\u53ea\u80fd\u5728\u7814\u7a76\u751f\u697c\u8fd9\u8fb9\u51d1\u5408\uff1b\u6c5f\u8fb9\u7684\u522b\u5885\u65e9\u5c31\u8ba9\u7ed9\u6881\u519b\u4e0e\u4ed6\u5973\u670b\u53cb\u5b59\u4e3d\u4f4f\u4e86\u3002</p><p>\u3000\u3000\u4e1c\u697c\u662f\u7537\u5973\u6df7\u5408\u697c\uff0c\u5973\u751f\u4f4f\u9ad8\u697c\u5c42\u7684\u56db\u697c\u3001\u4e94\u697c\u3001\u516d\u697c\uff0c\u5b66\u6821\u8fd9\u4e2a\u5b89\u6392\u662f\u5bf9\u7684\uff0c\u5c31\u7b97\u5982\u6b64\uff0c\u5c31\u7b97\u4e09\u697c\u3001\u56db\u697c\u95f4\u7684\u697c\u68af\u62d0\u89d2\u5899\u58c1\u4e0a\u5199\u7740\u8165\u7ea2\u7684\u51e0\u4e2a\u5927\u5b57\u201c\u4ee5\u4e0a\u662f\u5973\u751f\u5bbf\u820d\u533a\u201d\uff0c\u8fd8\u662f\u7ecf\u5e38\u6709\u7537\u751f\u8dd1\u9519\u697c\u5c42\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u8d70\u5230\u4e1c\u697c\u65f6\uff0c\u8d75\u9e4f\u5728\u697c\u4e0b\u7b49\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u201c\u8fd9\u70b9\u5c0f\u4e8b\u8981\u4f60\u8fc7\u6765\u8dd1\u4e00\u8d9f\uff1f\u201d\u5f20\u606a\u770b\u7740\u8d75\u9e4f\u95ee\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u65e0\u5c0f\u4e8b\u561b\uff0c\u5e73\u65f6\u53ef\u4ee5\u4e0d\u7ba1\uff0c\u5230\u9080\u529f\u7684\u65f6\u95f4\u603b\u4e0d\u80fd\u4e0d\u4eb2\u81ea\u8dd1\u4e00\u8d9f\u5427\uff1f\u201d\u8d75\u9e4f\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u771f\u4f1a\u62cd\u9a6c\u5c41\u2026\u2026\u201d\u5f20\u606a\u7b11\u9a82\u4e86\u4e00\u58f0\uff0c\u4ece\u8d75\u9e4f\u624b\u91cc\u5c06\u94a5\u5319\u63a5\u8fc7\u6765\uff0c\u5c31\u5c06\u8d75\u9e4f\u8d76\u4e86\u56de\u53bb\uff0c\u6a61\u6811\u56ed\u8ba1\u5212\u6b63\u662f\u5343\u5934\u4e07\u7eea\u7684\u9636\u6bb5\uff0c\u8d75\u9e4f\u662f\u8fd9\u91cc\u7684\u7b79\u5907\u8054\u7edc\u4eba\uff0c\u8f7b\u6613\u8131\u4e0d\u4e86\u8eab\u3002\u8d75\u9e4f\u4e0d\u4eb2\u81ea\u8fc7\u6765\uff0c\u5f20\u606a\u4e5f\u65e0\u6240\u8c13\uff0c\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u8dd1\u4e0e\u4e0d\u8dd1\uff0c\u6bd5\u7adf\u662f\u4e0d\u4e00\u6837\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u7684\u5bbf\u820d\u5c31\u5728\u4e8c\u697c\u6700\u897f\u4fa7\u671d\u9633\u7684\u623f\u95f4\uff0c\u8d70\u8fdb\u5bbf\u820d\uff0c\u5c31\u53d1\u73b0\u5bbf\u820d\u4e0e\u522b\u5904\u7684\u4e0d\u540c\u4e86\uff0c\u5510\u5a67\u5567\u5567\u53eb\u5947\u7684\u95ee\u9053\uff1a\u201c\u4e1c\u5927\u7814\u7a76\u751f\u516c\u5bd3\u7684\u786e\u4e0d\u9519\u5462\uff0c\u9648\u5983\u84c9\u5979\u4eec\u7684\u5bbf\u820d\u5c31\u5f88\u5dee\u52b2\uff01\u201d</p><p>\u3000\u3000\u5979\u4e2d\u5348\u65f6\u966a\u9648\u5983\u84c9\u56de\u5bbf\u820d\u62ff\u996d\u5361\u8fdb\u8fc7\u4e00\u820d\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u7b11\u7740\u8bf4\uff1a\u201c\u7814\u7a76\u751f\u516c\u5bd3\u597d\u662f\u597d\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u542c\u8bf4\u6709\u4e24\u4eba\u4e00\u95f4\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8981\u4e0d\u662f\u6015\u675c\u98de\u7684\u5fc3\u7075\u53d7\u5230\u4f24\u5bb3\uff0c\u6211\u771f\u60f3\u72e0\u72e0\u5fc3\u5c06\u4ed6\u4e22\u5341\u4e8c\u820d\u90a3\u91cc\uff0c\u6211\u4e00\u4e2a\u4eba\u4eab\u53d7\u8fd9\u623f\u95f4\u4f1a\u66f4\u6ecb\u6da6\u2026\u2026\u201d\u5f20\u606a\u8dd1\u8fc7\u53bb\u5c06\u540a\u9876\u7684\u98ce\u6247\u6253\u5f00\uff0c\u56db\u817f\u516b\u53c9\u7684\u8eba\u5230\u5e8a\u4e0a\uff1b\u521a\u521a\u62b1\u7740\u4e00\u5806\u4e1c\u897f\u4e0a\u6765\uff0c\u6c57\u6d41\u6d43\u80cc\uff0c\u6709\u5973\u5b69\u5b50\u5728\u573a\uff0c\u4e0d\u7136\u4ed6\u8ddf\u675c\u98de\u90fd\u53ef\u4ee5\u6253\u8d64\u818a\u4e86\u3002</p><p>\u3000\u3000\u5bbf\u820d\u7684\u9762\u79ef\u4e0d\u5927\uff0c\u8981\u6446\u4e0b\u4e24\u5957\u67da\u6728\u7684\u5e8a\u3001\u4e66\u684c\u3001\u8863\u6a71\uff0c\u4e5f\u5269\u4e0b\u4e0d\u4e86\u591a\u5927\u843d\u8db3\u7684\u7a7a\u95f4\uff0c\u4f46\u662f\u6bd4\u8f83\u7edf\u4e00\u7684\u56db\u4eba\u95f4\u5bbf\u820d\u5e03\u7f6e\uff0c\u8fd9\u91cc\u4e24\u5f20\u5e8a\u4e00\u6a2a\u4e00\u7ad6\uff0c\u5c06\u623f\u95f4\u5206\u5272\u6210\u4e24\u4e2a\u76f8\u5bf9\u72ec\u7acb\u7684\u5c0f\u5757\u533a\u57df\uff0c\u89c6\u611f\u4e0a\u8981\u4e30\u5bcc\u591a\u5f69\u8bb8\u591a\uff1b\u9760\u95e8\u7684\u4e66\u6a71\u4e0b\u8fd8\u6709\u4e00\u53ea\u534a\u4eba\u8eab\u7684\u51b0\u7bb1\u3002</p><p>\u3000\u3000\u675c\u98de\u6253\u5f00\u770b\u4e86\u770b\uff0c\u91cc\u9762\u5df2\u7ecf\u585e\u4e86\u4e00\u4e9b\u7f50\u88c5\u996e\u6599\u4e0e\u77ff\u6cc9\u6c34\uff0c\u611f\u6168\u4e86\u4e00\u58f0\uff1a\u201c\u8ddf\u7740\u5f20\u606a\u5c31\u662f\u4eab\u798f\uff01\u201d\u4ed6\u5148\u5c06\u624b\u91cc\u6367\u7740\u7684\u4e1c\u897f\u90fd\u4e22\u5e8a\u4e0a\uff0c\u6765\u4e0d\u53ca\u6574\u7406\uff0c\u5148\u62ff\u51fa\u996e\u6599\u5206\u53d1\u7ed9\u5927\u5bb6\uff0c\u9a6c\u6d77\u9f99\u5728\u540e\u9762\u5c06\u4e24\u53ea\u884c\u674e\u7bb1\u63d0\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5176\u4ed6\u623f\u95f4\u91cc\u7684\u536b\u751f\u95f4\u5f88\u7b80\u964b\uff0c\u5395\u6240\u8fd8\u90fd\u662f\u8e72\u5751\u52a0\u6447\u6643\u5bf9\u5f00\u7684\u6728\u95e8\u3002\u5f20\u606a\u4ed6\u4eec\u8fd9\u95f4\uff0c\u623f\u95f4\u867d\u7136\u90fd\u4e0d\u80fd\u52a0\u5927\uff0c\u5f53\u7136\u82b1\u4e86\u4e9b\u5c0f\u5de7\u5de5\u592b\uff0c\u6dcb\u6d74\u3001\u5395\u6240\u3001\u6d17\u6f31\u533a\u90fd\u7528\u9542\u82b1\u7684\u78e8\u7802\u94a2\u5316\u5761\u7483\u9694\u5f00\uff0c\u6dcb\u6d74\u533a\u5185\u662f\u78e8\u82b1\u7684\u5730\u78c1\u7816\uff0c\u9648\u5983\u84c9\u8fd9\u65f6\u5019\u624d\u6ce8\u610f\u5230\u623f\u95f4\u91cc\u662f\u4e0e\u5bb6\u4ff1\u4e00\u8272\u7684\u5730\u677f\uff0c\u5510\u5a67\u63a8\u5f00\u901a\u5f80\u9633\u53f0\u7684\u95e8\uff0c\u9633\u53f0\u4e0a\u6446\u7740\u7ea2\u9ed1\u76f8\u95f4\u7684\u73bb\u7483\u51e0\u4e0e\u4e24\u5f20\u85e4\u6905\uff0c\u5730\u9762\u4e5f\u7ecf\u8fc7\u989d\u5916\u7684\u5904\u7406\uff0c\u561f\u7740\u8138\u56de\u5934\u8ddf\u5f20\u606a\u8bf4\uff1a\u201c\u771f\u5962\u4f88\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u662f\u6015\u7a7a\u8c03\u5ba4\u5916\u673a\u592a\u624e\u773c\uff0c\u6211\u90fd\u60f3\u8ba9\u4eba\u5c06\u7a7a\u8c03\u88c5\u4e0a\uff0c\u5efa\u90ba\u7684\u590f\u5929\u771f\u662f\u591f\u8ba9\u4eba\u53d7\u7684\uff1b\u518d\u8bf4\u4e86\uff0c\u9633\u53f0\u6211\u4e5f\u6ca1\u5c01\u4e0a\u3002\u521a\u521a\u8d70\u8fc7\u6765\uff0c\u4f60\u6ca1\u6709\u770b\u89c1\u897f\u697c\u90a3\u91cc\u5c31\u6709\u5bbf\u820d\u6302\u7a7a\u8c03\u5916\u673a\u7684\u2026\u2026\u201d\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u662f\u8fc7\u6765\u4eab\u53d7\u5927\u5b66\u751f\u6d3b\uff0c\u53c8\u4e0d\u662f\u8fc7\u6765\u4f53\u9a8c\u5927\u5b66\u751f\u6d3b\u7684\uff0c\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u8ba9\u81ea\u5df1\u592a\u59d4\u5c48\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5a67\u7b11\u7740\u9a82\u5f20\u606a\u65e0\u803b\uff0c\u53c8\u5728\u5c4b\u5b50\u91cc\u8f6c\u4e86\u4e00\u5708\uff0c\u6328\u7740\u5f20\u606a\u5750\u5728\u5e8a\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u50cf\u8fd8\u7f3a\u4e9b\u4ec0\u4e48\u4e1c\u897f\uff0c\u6d17\u8863\u673a\u5c31\u6ca1\u6709\u554a\uff0c\u4f60\u4eec\u6253\u7b97\u81ea\u5df1\u6d17\u8863\u670d\u554a\uff1f\u201d</p></article>"}}}]);
