(window.webpackJsonp=window.webpackJsonp||[]).push([[776],{799:function(p){p.exports={chapterTitle:null,title:"\u7b2c775\u7ae0 \u4e8b\u6545\u5904\u7f6e",content:"<article><h2>\u7b2c775\u7ae0 \u4e8b\u6545\u5904\u7f6e</h2><p>\u3000\u3000\u5085\u4fca\u5728\u6025\u6551\u65b9\u9762\u6709\u7ecf\u9a8c\uff0c\u5f20\u606a\u5e2e\u4ed6\u5c06\u90a3\u4e2a\u7ed9\u6cd5\u62c9\u5229\u649e\u98de\u7684\u5973\u5b50\u8f7b\u7ffb\u8fc7\u6765\u8ba9\u5979\u5e73\u8eba\u5728\u8349\u5761\u4e0a\uff0c\u4f46\u662f\u5085\u4fca\u4e5f\u65e0\u6cd5\u7ed9\u8fd9\u5973\u5b50\u66f4\u591a\u7684\u5e2e\u52a9\uff0c\u53ea\u662f\u5c06\u5979\u7684\u8138\u4fa7\u8fc7\u6765\uff0c\u56db\u80a2\u653e\u5e73\uff0c\u6ce8\u610f\u4e0d\u8ba9\u5979\u53d1\u751f\u7a92\u606f\u3002\u5973\u5b50\u5634\u91cc\u4e0d\u65ad\u5410\u51fa\u8840\u6cab\uff0c\u6c89\u91cd\u800c\u6025\u4fc3\u7684\u547c\u5438\uff0c\u6709\u7740\u80ba\u91cc\u586b\u6ee1\u7802\u77f3\u4f3c\u7684\u5f02\u54cd\uff0c\u5438\u6c14\u65f6\u80f8\u53e3\u4f1a\u6df1\u6df1\u7684\u9677\u8fdb\u53bb\uff0c\u4eff\u4f5b\u80f8\u53e3\u7684\u9aa8\u5934\u5168\u90fd\u649e\u788e\u4e86\uff0c\u8138\u7ed9\u8840\u6c61\u810f\u4e86\uff0c\u4e24\u9b13\u5934\u53d1\u5df2\u7ecf\u82b1\u767d\uff0c\u662f\u4e2a\u8001\u5e74\u5987\u5973\uff0c\u770b\u4e0d\u6e05\u695a\u8138\uff0c\u53ea\u89c9\u5f97\u6709\u4e9b\u719f\u6089\uff0c\u53ef\u80fd\u5728\u9999\u6e2f\u9047\u89c1\u8fc7\u7684\u67d0\u4e2a\u8def\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u845b\u836b\u5747\u4e0e\u540c\u8f66\u7684\u9752\u5e74\u4e0b\u8f66\u8d70\u4e86\u8fc7\u6765\uff0c\u845b\u836b\u5747\u770b\u5230\u5085\u4fca\u6b63\u62a2\u6551\u88ab\u649e\u7684\u5987\u5973\uff0c\u8138\u8272\u53d8\u5f97\u60e8\u767d\uff0c\u4e0d\u6562\u770b\u5f20\u606a\u51f6\u72e0\u7684\u773c\u795e\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u56de\u9053\u8def\uff0c\u7126\u6025\u7684\u770b\u7740\u5c71\u4e0b\u7684\u65b9\u5411\u7b49\u6551\u62a4\u8f66\u8fc7\u6765\u3002\u4e0e\u845b\u836b\u5747\u540c\u8f66\u7684\u90a3\u4e2a\u9752\u5e74\u8d70\u5230\u5f20\u606a\u8eab\u8fb9\uff0c\u8d70\u8fd1\u4e9b\u63a2\u5934\u770b\u4e86\u770b\u8eba\u5728\u8349\u5761\u4e0a\u3001\u770b\u4e0a\u53bb\u5df2\u6ca1\u6709\u751f\u8fd8\u5e0c\u671b\u7684\u5987\u5973\uff0c\u62ff\u51fa\u624b\u673a\u6765\uff0c\u5f00\u59cb\u62d4\u6253\u7535\u8bdd\uff1a\u201c\u4e8c\u51b0\uff0c\u5feb\u5230\u6d45\u6c34\u6e7e\u5927\u9053\u6765\u770b\u70ed\u95f9\uff0c\u845b\u5927\u5c11\u628a\u4e2a\u5973\u7684\u649e\u6b7b\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7709\u5934\u76b1\u4e86\u8d77\u6765\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\u3002\u90a3\u9752\u5e74\u5374\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u8f6c\u8fc7\u8138\u7ee7\u7eed\u62d4\u7535\u8bdd\uff1a\u201c\u50bb\u9e1f\u2026\u2026\u554a\uff0c\u4f60\u5728\u63a5\u4e8c\u51b0\u7684\u7535\u8bdd\uff1f\u90a3\u8ba9\u4e8c\u51b0\u544a\u8bc9\u4f60\u5c31\u884c\uff0c\u4f60\u4eec\u5feb\u8fc7\u6765\uff0c\u6211\u518d\u7ed9\u5176\u4ed6\u4eba\u6253\u7535\u8bdd\uff0c\u5565\u65f6\u5019\u6709\u8fd9\u70ed\u95f9\u770b\u554a\uff1f\u201d</p><p>\u3000\u3000\u4e4b\u524d\u8fd8\u4ee5\u4e3a\u662f\u845b\u836b\u5747\u770b\u5230\u81ea\u5df1\u5403\u60ca\u5931\u795e\u624d\u63a7\u5236\u4e0d\u4f4f\u8f66\u5728\u5f2f\u9053\u9047\u9669\u915d\u6210\u5982\u6b64\u60b2\u7978\uff0c\u4f46\u662f\u770b\u5230\u8fd9\u9752\u5e74\u8ddf\u6ca1\u4e8b\u4eba\u4f3c\u7684\u53cd\u5e94\uff0c\u624d\u6653\u5f97\u4ed6\u4eec\u521a\u624d\u5728\u5f2f\u9053\u7684\u6025\u62d0\u4e0d\u8fc7\u662f\u4ed6\u4eec\u523b\u610f\u4e3a\u4e4b\u7684\u8868\u6f14\uff0c\u5374\u6ca1\u6709\u60f3\u5230\u5f2f\u9053\u540e\u8fd8\u6709\u4eba\u5728\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u6c14\u53c8\u607c\uff0c\u80f8\u8154\u91cc\u6c14\u8840\u7ffb\u817e\uff0c\u5f3a\u5fcd\u7740\u5fc3\u4e2d\u7684\u6012\u706b\u6ca1\u6709\u53d1\u4f5c\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5510\u5a67\u4e0e\u53f8\u673a\u8dd1\u8fc7\u6765\uff0c\u5510\u5a67\u8bf4\u9053\uff1a\u201c\u7535\u8bdd\u90fd\u6253\u4e86\uff0c\u8b66\u5bdf\u4e0e\u6025\u6551\u9a6c\u4e0a\u5c31\u5230\uff0c\u8fd8\u7ed9\u5b59\u53d4\u6253\u8fc7\u7535\u8bdd\u2026\u2026\u201d\u770b\u5230\u7ed9\u5085\u4fca\u906e\u4f4f\u5927\u534a\u4e2a\u8eab\u5b50\u7684\u5973\u5b50\uff0c\u8840\u6db2\u5728\u4f4e\u6d3c\u5730\u91cc\u90fd\u79ef\u6210\u4e00\u7247\uff0c\u5fc3\u810f\u7ed9\u624b\u4e00\u628a\u63ea\u4f4f\u4f3c\u7684\u96be\u53d7\uff0c\u90fd\u4e0d\u6562\u95ee\u60c5\u51b5\u600e\u4e48\u6837\u3002</p><p>\u3000\u3000\u90a3\u4e2a\u9752\u5e74\u672c\u6765\u5728\u901a\u7740\u7535\u8bdd\uff0c\u770b\u7740\u5510\u5a67\u8d70\u8fc7\u6765\uff0c\u5fcd\u4e0d\u4f4f\u5439\u8d77\u53e3\u54e8\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u4ed6\u5988\u6709\u6ca1\u6709\u4eba\u6027\uff1f\u201d\u5f20\u606a\u8fd9\u4e0b\u5b50\u518d\u4e5f\u6309\u8010\u4e0d\u4f4f\u5fc3\u4e2d\u7684\u6012\u706b\uff0c\u8f6c\u8fc7\u8eab\u4e00\u5df4\u638c\u7529\u8fc7\u53bb\uff0c\u72e0\u72e0\u7684\u62bd\u5728\u90a3\u9752\u5e74\u7684\u8138\u4e0a\uff0c\u8fde\u5e26\u7740\u5c06\u4ed6\u8d34\u7740\u8033\u6839\u7684\u624b\u673a\u6454\u5230\u9053\u8def\u53e6\u4e00\u4fa7\u7684\u4eba\u5de5\u5d16\u58c1\u4e0a\u53c8\u6eda\u843d\u8fc7\u6765\uff0c\u5374\u662f\u7231\u8fbe\u4e00\u6b3e\u5728\u9999\u6e2f\u9650\u91cf\u53d1\u552e\u7684i19\u81f3\u5c0a\u7248\u624b\u673a\u3002\u624b\u673a\u867d\u7136\u7ed9\u6454\u5f97\u8fd9\u4e48\u91cd\uff0c\u7adf\u7136\u6ca1\u6709\u6bc1\u574f\uff0c\u8eba\u5728\u8def\u9762\u4e0a\u7684\u624b\u673a\u8033\u673a\u91cc\u8fd8\u9690\u7ea6\u4f20\u51fa\u901a\u8bdd\u58f0\uff1a\u201c\u2026\u2026\u4f60\u4ed6\u5988\u600e\u4e48\u4e86\uff0c\u8be5\u4e0d\u4f1a\u4f60\u770b\u597d\u620f\u592a\u5174\u594b\u81ea\u5df1\u4e5f\u7ed9\u8f66\u649e\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6068\u610f\u96be\u6d88\uff0c\u4e00\u811a\u8e6c\u5728\u624b\u673a\u4e0a\uff0c\u5c06\u624b\u673a\u8e29\u762a\uff0c\u53c8\u5c06\u7ed9\u8e29\u762a\u7684\u624b\u673a\u8e22\u5f00\uff0c\u8fd9\u624d\u65ad\u4e86\u901a\u8bdd\u3002</p><p>\u3000\u3000\u90a3\u9752\u5e74\u8fd9\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u4ed6\u5e73\u65e5\u54ea\u91cc\u662f\u80af\u5403\u4e8f\u7684\u4e3b\uff0c\u53ea\u89c9\u5f97\u8138\u4e0a\u706b\u8fa3\u8fa3\u7684\u75db\uff0c\u4f38\u624b\u8fc7\u6765\u8981\u6293\u5f20\u606a\u7684\u8863\u9886\uff0c\u5374\u7ed9\u5f20\u606a\u8eab\u8fb9\u7684\u53f8\u673a\u62e6\u4f4f\u3002\u5f20\u606a\u62ac\u811a\u53c8\u8e39\u8fc7\u53bb\u4e00\u811a\uff0c\u8e39\u5728\u4ed6\u7684\u5927\u817f\u6839\u4e0a\u3002\u770b\u7740\u8fd9\u9752\u5e74\u6342\u4f4f\u817f\u6839\u75db\u5f97\u8e72\u4e0b\u53bb\uff0c\u5f20\u606a\u6ca1\u6709\u4e1d\u6beb\u7684\u601c\u60af\u4e4b\u5fc3\uff0c\u6068\u4e0d\u5f97\u518d\u8e39\u4e0a\u4e00\u811a\u624d\u80fd\u7a0d\u89e3\u5fc3\u4e2d\u7684\u6068\u610f\uff0c\u6012\u706b\u51b2\u51b2\u7684\u6307\u7740\u90a3\u4eba\u7684\u9f3b\u5b50\uff1a\u201c\u4f60\u4ed6\u5988\u7ed9\u6211\u5b89\u5206\u4e9b\uff0c\u4e0d\u8981\u4ee5\u4e3a\u591a\u51e0\u4e2a\u81ed\u94b1\u5c31\u53ef\u4ee5\u65e0\u6cd5\u65e0\u5929\uff0c\u53ef\u4ee5\u6f20\u89c6\u4ed6\u4eba\u7684\u751f\u547d\u3002\u201d</p><p>\u3000\u3000\u5728\u845b\u836b\u5747\u7684\u773c\u91cc\uff0c\u5f20\u606a\u8fd9\u4eba\u6709\u7740\u66b4\u53d1\u6237\u7684\u66b4\u5533\u813e\u6c14\uff0c\u4ed6\u6ca1\u6709\u80c6\u91cf\u4e0a\u524d\u5e2e\u4ed6\u7684\u670b\u53cb\uff0c\u62c9\u7740\u90a3\u9752\u5e74\u8d70\u5230\u4e00\u8fb9\uff0c\u62ff\u51fa\u624b\u673a\u80cc\u7740\u5f20\u606a\u6253\u8d77\u7535\u8bdd\u6765\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u5728\u6cd5\u5f8b\u4f53\u7cfb\u591a\u4e48\u4e25\u5bc6\u7684\u5730\u533a\uff0c\u90fd\u65e0\u6cd5\u963b\u6b62\u4eba\u5bf9\u4ed6\u4eba\u751f\u547d\u7684\u6f20\u89c6\uff0c\u6240\u8003\u8651\u53ea\u662f\u9700\u8981\u627f\u62c5\u7684\u540e\u679c\u800c\u5df2\uff0c\u5bf9\u4e8e\u6709\u94b1\u4eba\u6765\u8bf4\uff0c\u8fd9\u4f3c\u4e4e\u53c8\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u4e25\u91cd\u7684\u540e\u679c\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u4ece\u5c71\u4e0b\u9646\u7eed\u9a76\u4e0a\u6765\u51e0\u90e8\u8f66\u90fd\u505c\u5728\u8def\u8fb9\uff0c\u4eba\u56f4\u8fc7\u6765\u770b\u7a76\u7adf\u3002\u5b59\u5b85\u8ddd\u79bb\u8fd9\u5904\u5f2f\u9053\u5f88\u8fd1\uff0c\u5b59\u5c1a\u4e49\u592b\u5987\u3001\u5b59\u9759\u6aac\u3001\u5b59\u9759\u9999\u4e24\u59d0\u59b9\u4e5f\u6ca1\u6709\u5f00\u8f66\uff0c\u6ee1\u5934\u5927\u6c57\u7684\u8dd1\u4e86\u8fc7\u6765\u3002\u770b\u5230\u8def\u8fb9\u8349\u5761\u4e0a\u7684\u5973\u5b50\uff0c\u5b59\u5c1a\u4e49\u6014\u5728\u90a3\u91cc\uff0c\u4ed6\u59bb\u5b50\u96be\u8fc7\u7684\u6342\u4f4f\u8138\uff0c\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u59d0\u59b9\u51b2\u5230\u5085\u4fca\u8eab\u8fb9\uff0c\u62b9\u5f00\u5973\u5b50\u8138\u4e0a\u7684\u8840\u6c61\uff0c\u770b\u6e05\u90a3\u5973\u5b50\u7684\u76f8\u8c8c\uff0c\u5b59\u9759\u9999\u4e00\u8138\u9707\u60ca\uff0c\u6cea\u73e0\u5c31\u6ed1\u843d\u4e0b\u6765\uff0c\u65e0\u58f0\u7684\u62bd\u6ce3\u7740\uff0c\u5b59\u9759\u6aac\u8f6c\u8fc7\u8eab\u7259\u9f7f\u54ac\u8fdb\u5634\u5507\u91cc\uff0c\u773c\u775b\u91cc\u6740\u6c14\u51b2\u51b2\uff0c\u51b2\u5230\u845b\u836b\u5747\u8eab\u524d\uff0c\u4e00\u628a\u626f\u4f4f\u4ed6\u7684\u5934\u53d1\uff0c\u53d1\u75af\u4f3c\u7684\u53c8\u6293\u53c8\u8e22\uff1a\u201c\u4f60\u8d54\u6211\u7684\u963f\u59c6\u56de\u6765\uff0c\u4f60\u8d54\u6211\u7684\u963f\u59c6\u56de\u6765\u2026\u2026\u201d\u845b\u836b\u5747\u8138\u4e0a\u7acb\u5373\u7ed9\u6293\u51fa\u597d\u51e0\u9053\u8840\u75d5\uff0c\u819d\u76d6\u4e0a\u7ed9\u8e22\u4e86\u597d\u51e0\u811a\uff0c\u8fd9\u624d\u53cd\u5e94\u8fc7\u6765\u8981\u5c06\u5b59\u9759\u6aac\u63a8\u5f00\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u7ed9\u845b\u836b\u5747\u63a8\u7740\u540e\u9000\u4e86\u4e24\u6b65\uff0c\u811a\u8e29\u5728\u8def\u8fb9\u7684\u96e8\u6c34\u6c9f\u91cc\uff0c\u4e00\u4e2a\u8e09\u8dc4\u8dcc\u5012\u5728\u5730\uff0c\u5f20\u606a\u8df3\u8fc7\u53bb\u5c06\u5979\u4ece\u8349\u5730\u91cc\u6400\u8d77\u6765\uff0c\u770b\u5230\u5979\u53d1\u75af\u72b6\u7684\u8138\u86cb\u4e0a\u6cea\u75d5\u6ee1\u9762\uff0c\u624d\u60f3\u8d77\u8fd9\u4e2a\u88ab\u649e\u7684\u5987\u4eba\u5728\u5b59\u5b85\u91cc\u770b\u5230\u8fc7\uff0c\u770b\u5b59\u9759\u6aac\u53cd\u5e94\u5982\u6b64\u6fc0\u70c8\uff0c\u5e94\u8be5\u662f\u5979\u7684\u4eb2\u4eba\u5427\uff0c\u53ea\u662f\u6ca1\u6709\u542c\u5979\u63d0\u8d77\u6765\u3002</p><p>\u3000\u3000\u5c06\u5b59\u9759\u6aac\u6276\u8d77\u6765\uff0c\u5374\u6ca1\u6709\u6599\u5230\u5979\u6323\u624e\u51fa\u53bb\uff0c\u53c8\u671d\u845b\u836b\u5747\u6251\u8fc7\u6765\uff0c\u54ac\u7259\u5207\u9f7f\u7684\uff0c\u90a3\u773c\u795e\u4f3c\u4e4e\u8981\u5c06\u845b\u836b\u5747\u751f\u541e\u6d3b\u5265\u4e86\u624d\u7518\u5fc3\uff0c\u8fd8\u662f\u5979\u59d0\u59d0\u5b59\u9759\u9999\u8dd1\u8fc7\u6765\u5c06\u5979\u62b1\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u6551\u62a4\u8f66\u9a6c\u4e0a\u5c31\u6765\uff0c\u73e0\u59e8\u4e0d\u4f1a\u6709\u4e8b\u7684\u3002\u201d\u5b59\u9759\u6aac\u8fd9\u624d\u6251\u5012\u5728\u5979\u59d0\u59d0\u7684\u6000\u91cc\u75db\u54ed\u8d77\u6765\uff0c\u5510\u5a67\u770b\u4e86\u4e5f\u5f88\u4f24\u5fc3\uff0c\u6d41\u7740\u6cea\u8dd1\u8fc7\u53bb\u5b89\u629a\u5b59\u9759\u6aac\u3002</p><p>\u3000\u3000\u8b66\u8f66\u4e0e\u6025\u6551\u8f66\u968f\u540e\u8d76\u5230\uff0c\u5085\u4fca\u5c06\u4f24\u8005\u7684\u60c5\u51b5\u8ddf\u533b\u5e08\u7b80\u5355\u7684\u8bf4\u4e86\u4e00\u4e0b\uff0c\u53c8\u966a\u7740\u5728\u73b0\u573a\u8fdb\u884c\u7b80\u5355\u7684\u7d27\u6025\u6551\u62a4\u3002\u5b59\u5c1a\u4e49\u8d70\u5230\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u73e0\u5ac2\u662f\u9759\u6aac\u7684\u4fdd\u59c6\uff0c\u6211\u4eec\u521a\u5230\u9999\u6e2f\u65f6\uff0c\u6211\u8ddf\u9759\u6aac\u5979\u5988\u6bcf\u5929\u90fd\u8981\u5de5\u4f5c\u5f88\u957f\u65f6\u95f4\uff0c\u9759\u9999\u53c8\u5728\u56fd\u5916\u8bfb\u4e66\uff0c\u9759\u6aac\u521a\u5230\u9999\u6e2f\uff0c\u4e0d\u4f1a\u8bf4\u5e7f\u4e1c\u8bdd\uff0c\u4e5f\u4e0d\u4f1a\u8bf4\u82f1\u8bed\uff0c\u6027\u683c\u4e5f\u6709\u4e9b\u5b64\u5355\uff0c\u4eba\u4e5f\u4e0d\u5408\u7fa4\uff0c\u6211\u4eec\u8bf7\u73e0\u5ac2\u7ed9\u9759\u6aac\u5f53\u4fdd\u59c6\u966a\u5979\u6e21\u8fc7\u5728\u9999\u6e2f\u7684\u6700\u521d\u4e24\u5e74\uff0c\u540e\u6765\u4e5f\u4e00\u76f4\u8bf7\u73e0\u5ac2\u5728\u5bb6\u91cc\u5e2e\u5fd9\uff0c\u8fd9\u4e24\u5e74\u73e0\u5ac2\u56de\u5bb6\u4e86\uff0c\u73e0\u5ac2\u8981\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6700\u96be\u8fc7\u7684\u5c31\u662f\u9759\u6aac\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u9759\u6aac\u5728\u5979\u59d0\u6000\u91cc\u4f24\u5fc3\u6b32\u7edd\u7684\u6a21\u6837\uff0c\u5fc3\u91cc\u601c\u60dc\u5f97\u5f88\uff0c\u5374\u4e0d\u77e5\u9053\u600e\u4e48\u53bb\u5b89\u6170\u4ed6\uff0c\u53ea\u80fd\u9ed8\u7136\u7684\u770b\u7740\u5df2\u7ecf\u53d1\u751f\u7684\u4e00\u5207\uff0c\u5b59\u5c1a\u4e49\u867d\u7136\u6ca1\u8bf4\uff0c\u73e0\u5ac2\u5e94\u8be5\u662f\u88ab\u8bf7\u5230\u5b59\u5b85\u7528\u665a\u9910\u624d\u4f1a\u8fd9\u65f6\u5019\u8d70\u5728\u6d45\u6c34\u6e7e\u5927\u9053\u4e0a\u3002</p><p>\u3000\u3000\u5c31\u50cf\u4e00\u53ea\u811a\u8e29\u8fdb\u4e00\u6761\u6cb3\u6d41\uff0c\u867d\u7136\u4e0d\u81f3\u4e8e\u6539\u53d8\u6cb3\u6d41\u7684\u6d41\u5411\uff0c\u4f46\u662f\u65e0\u6570\u6d6a\u82b1\u3001\u6f29\u6da1\u5374\u53d1\u751f\u4e86\u6539\u53d8\u3002\u81ea\u5df1\u610f\u5916\u8e0f\u8db3\u8fd4\u56de\u4e5d\u56db\u5e74\uff0c\u5341\u56db\u5e74\u7684\u4eba\u751f\u91cd\u65b0\u6765\u8fc7\uff0c\u4e5f\u6539\u53d8\u4e86\u65e0\u6570\u4eba\u7684\u4eba\u751f\uff0c\u6709\u597d\u4e5f\u6709\u574f\u3002\u82e5\u662f\u845b\u836b\u5747\u5728\u8fc7\u5f2f\u9053\u524d\u4e00\u523b\u6ca1\u6709\u770b\u5230\u81ea\u5df1\uff0c\u8fd9\u4e00\u523b\u7684\u60b2\u5267\u6216\u8bb8\u4e0d\u4f1a\u53d1\u751f\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u662f\u81ea\u6028\u81ea\u827e\u7684\u4eba\uff0c\u56de\u5934\u770b\u4e86\u845b\u836b\u5747\u4e00\u773c\uff0c\u89c1\u4ed6\u5fff\u5fff\u4e0d\u5e73\u7684\u6342\u7740\u7ed9\u5b59\u9759\u6aac\u6293\u51fa\u51e0\u9053\u8840\u75d5\u7684\u8138\uff0c\u5fc3\u91cc\u7684\u6068\u610f\u72b9\u76db\u3002</p><p>\u3000\u3000\u7b80\u5355\u6551\u62a4\u540e\u4f24\u8005\u7ed9\u62ac\u8fdb\u6551\u62a4\u8f66\uff0c\u5085\u4fca\u8d70\u56de\u5230\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u53ea\u662f\u5fae\u5fae\u6447\u4e86\u6447\u5934\uff0c\u8868\u793a\u4f24\u8005\u53ef\u80fd\u6ca1\u6551\u4e86\u3002\u5f20\u606a\u56de\u5934\u671d\u845b\u836b\u5747\u770b\u53bb\uff0c\u8fd9\u65f6\u5019\u6709\u4e24\u90e8\u5954\u9a70\u8f7f\u8f66\u505c\u5728\u8def\u8fb9\uff0c\u6709\u4e24\u540d\u897f\u88c5\u9769\u8986\u7684\u4e2d\u5e74\u4eba\u8d70\u4e0b\u6765\uff0c\u7ad9\u5230\u845b\u836b\u5747\u7684\u8eab\u8fb9\uff0c\u8fd9\u4e24\u4e2a\u4e2d\u5e74\u4eba\uff0c\u5f20\u606a\u90fd\u6ca1\u6709\u89c1\u8fc7\uff0c\u4ed6\u4eec\u8d70\u56de\u5230\u8f7f\u8f66\u9644\u8fd1\uff0c\u795e\u60c5\u7d27\u6025\u7684\u5546\u91cf\u7740\u4ec0\u4e48\uff0c\u8f7f\u8f66\u91cc\u8fd8\u6709\u5176\u4ed6\u4eba\u5750\u7740\uff0c\u845b\u836b\u5747\u4e0e\u90a3\u4e24\u540d\u4e2d\u5e74\u4eba\u4e0d\u65f6\u4f4e\u4e0b\u5934\u6765\u8ddf\u8f66\u91cc\u7684\u4eba\u5546\u91cf\u3002\u5f20\u606a\u95ed\u7740\u773c\u775b\u90fd\u77e5\u9053\u8f66\u91cc\u8fd8\u5750\u7740\u8c01\uff0c\u4f5c\u4e3a\u5609\u4fe1\u96c6\u56e2\u7684\u8463\u4e8b\u957f\u517c\u603b\u88c1\u845b\u660e\u4fe1\u8fd9\u65f6\u5019\u62c5\u5fc3\u7684\u7adf\u7136\u8fd8\u662f\u6015\u7ed9\u5a92\u4f53\u8bb0\u8005\u77e5\u9053\u540e\u66b4\u5149\u3002</p><p>\u3000\u3000\u8981\u8fd9\u4e9b\u4eba\u5b66\u4f1a\u5c0a\u91cd\u4ed6\u4eba\u7684\u751f\u547d\uff0c\u8fd8\u4e0d\u5982\u671f\u5f85\u8001\u6bcd\u732a\u80fd\u722c\u4e0a\u6811\u3002</p><p>\u3000\u3000\u8b66\u5bdf\u5148\u52d8\u67e5\u4e86\u4e8b\u6545\u73b0\u573a\uff0c\u7b49\u52d8\u67e5\u8fc7\u4e8b\u6545\u73b0\u573a\u4e4b\u540e\uff0c\u624d\u627e\u8087\u4e8b\u8f66\u4e3b\u4e0e\u5f53\u4e8b\u4eba\u4e86\u89e3\u4e8b\u6545\u8be6\u60c5\u3002\u845b\u836b\u5747\u662f\u8087\u4e8b\u8f66\u4e3b\uff0c\u53d1\u751f\u8f66\u7978\u65f6\uff0c\u662f\u4ed6\u9a7e\u9a76\u8fd9\u8f86\u6cd5\u62c9\u5229\uff0c\u5f20\u606a\u4ed6\u4eec\u4e0e\u90a3\u4e2a\u540c\u8f66\u9752\u5e74\u90fd\u662f\u76ee\u51fb\u8bc1\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5085\u4fca\u4e3b\u52a8\u8d70\u8fc7\u53bb\u89e3\u91ca\u4ed6\u4eec\u6240\u76ee\u51fb\u7684\u4e8b\u5b9e\uff0c\u8fd9\u8d77\u8f66\u7978\u7684\u76f4\u63a5\u539f\u56e0\u5c31\u662f\u845b\u836b\u5747\u5728\u6d45\u6c34\u6e7e\u5927\u9053\u8d85\u901f\u884c\u9a76\u53c8\u5728\u5f2f\u9053\u5904\u73a9\u5371\u9669\u52a8\u4f5c\u917f\u51fa\u7684\u60e8\u5267\u3002</p><p>\u3000\u3000\u201c\u5f20\u5148\u751f\uff0c\u4f60\u4e5f\u662f\u6709\u8eab\u4efd\u6709\u5730\u4f4d\u7684\u4eba\uff0c\u867d\u7136\u4f60\u4e0e\u845b\u5bb6\u6709\u5f88\u591a\u4ec7\u6028\uff0c\u4f46\u662f\u5728\u8fd9\u573a\u5408\u6709\u4e9b\u8bdd\u662f\u4e0d\u80fd\u4e71\u8bf4\uff0c\u201d\u845b\u836b\u5747\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5374\u662f\u4ed6\u8eab\u8fb9\u7684\u4e2d\u5e74\u7537\u5b50\u7ad9\u51fa\u6765\u6307\u8d23\u5f20\u606a\uff0c\u53c8\u5bf9\u8b66\u5bdf\u8bf4\u9053\uff0c\u201c\u6211\u662f\u5f53\u4e8b\u4eba\u845b\u836b\u5747\u5148\u751f\u7684\u5f8b\u5e08\uff0c\u6211\u8ddf\u6211\u7684\u5f53\u4e8b\u4eba\u6c9f\u901a\u8fc7\uff0c\u4e86\u89e3\u5230\u8fd9\u8d77\u8f66\u7978\u7684\u8be6\u7ec6\uff0c\u8d77\u56e0\u662f\u6211\u7684\u5f53\u4e8b\u4eba\u5728\u5f2f\u9053\u5904\u9047\u5230\u9669\u60c5\uff0c\u7d27\u6025\u907f\u9669\u65f6\u6ca1\u6709\u6ce8\u610f\u5230\u5761\u9053\u8fd9\u8fb9\u6709\u4eba\u5728\u884c\u8d70\u2026\u2026\u5f2f\u9053\u5904\u7684\u62a4\u680f\u6709\u8f66\u8f6e\u6469\u64e6\u8fc7\u7684\u75d5\u8ff9\uff0c\u5c31\u662f\u7d27\u6025\u907f\u9669\u7684\u8bc1\u636e\uff0c\u6211\u521a\u624d\u770b\u8fc7\uff0c\u8bf7\u4f60\u4eec\u5168\u9762\u67e5\u8bc1\u3002\u201d</p><p>\u3000\u3000\u5371\u9669\u884c\u9a76\u4e0e\u7d27\u6025\u907f\u9669\u9020\u6210\u7684\u8f66\u7978\u662f\u622a\u7136\u4e0d\u540c\u7684\u4e24\u4e2a\u6982\u5ff5\uff0c\u5f20\u606a\u6ca1\u6709\u60f3\u5230\u845b\u836b\u5747\u9996\u5148\u60f3\u5230\u662f\u4fdd\u62a4\u81ea\u5df1\u8131\u79bb\u7f6a\u8d23\uff0c\u80f8\u81c6\u95f4\u7ed9\u4e00\u80a1\u96be\u4ee5\u8a00\u72b6\u7684\u6124\u6068\u5145\u585e\u7740\uff0c\u795e\u8272\u51b7\u5cfb\u7684\u76ef\u7740\u845b\u836b\u5747\uff1b\u845b\u836b\u5747\u5728\u5f20\u606a\u7684\u903c\u89c6\u4e0b\u4f4e\u4e0b\u5934\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u845b\u5bb6\u7684\u5f8b\u5e08\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u6653\u5f97\u6211\u7684\u8eab\u4efd\uff0c\u90a3\u5c31\u8bf7\u4f60\u7ee7\u7eed\u6627\u7740\u826f\u5fc3\u66ff\u845b\u5bb6\u64e6\u5c41\u80a1\u5427\uff0c\u8be5\u6709\u7684\u7f6a\u7f5a\uff0c\u4e0d\u662f\u4f60\u4eec\u60f3\u9003\u5c31\u9003\u5f97\u8fc7\u53bb\u7684\u2026\u2026\u201d\u8bed\u6c14\u51b0\u51b7\u7684\u4e22\u4e0b\u8fd9\u53e5\u8bdd\uff0c\u4ed6\u5c31\u5728\u8b66\u65b9\u7684\u8c03\u67e5\u8868\u683c\u4e0a\u7b7e\u4e0a\u5b57\u3002</p><p>\u3000\u3000\u201c\u8fd8\u662f\u8981\u63d0\u9192\u5f20\u5148\u751f\u4e00\u53e5\uff0c\u6709\u4e9b\u8bdd\u4e0d\u80fd\u4e71\u8bf4\uff0c\u5c31\u51ed\u4f60\u8fd9\u53e5\u8bdd\uff0c\u6211\u53ef\u4ee5\u544a\u4f60\u6050\u5413\u2026\u2026\u201d\u90a3\u4e2d\u5e74\u4eba\u4e0d\u4e3a\u5f20\u606a\u7684\u8bdd\u6240\u52a8\uff0c\u56de\u51fb\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u5f20\u606a\u634f\u7740\u62f3\u5934\uff0c\u5173\u8282\u54af\u54af\u4f5c\u54cd\uff0c\u8138\u8272\u5374\u6108\u53d1\u7684\u5e73\u9759\uff0c\u770b\u4e86\u505c\u5728\u4e0d\u8fdc\u5904\u7684\u5954\u9a70\u8f7f\u8f66\u4e00\u773c\uff0c\u4ed6\u77e5\u9053\u845b\u660e\u4fe1\u5c31\u5750\u5728\u90a3\u90e8\u8f66\u91cc\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u60f3\u5411\u5f53\u4e8b\u4eba\u4e86\u89e3\u60c5\u51b5\u2026\u2026\u201d\u8b66\u5bdf\u4f3c\u4e4e\u4e5f\u70e6\u6709\u94b1\u4eba\u7684\u4f5c\u6d3e\uff0c\u6325\u624b\u6b62\u4f4f\u90a3\u4e2d\u5e74\u4eba\u7ee7\u7eed\u8bf4\u8bdd\uff0c\u201c\u6211\u53ef\u6ca1\u6709\u542c\u51fa\u8fd9\u4f4d\u5148\u751f\u8bf4\u7684\u8bdd\u6709\u6050\u5413\u4f60\u7684\u610f\u601d\uff0c\u53ea\u662f\u5e0c\u671b\u4f60\u4eec\u4e0d\u8981\u9690\u7792\u8f66\u7978\u7684\u771f\u76f8\uff0c\u6211\u5e0c\u671b\u8087\u4e8b\u8f66\u4e3b\u80fd\u4eb2\u81ea\u56de\u7b54\u6211\u4eec\u7684\u95ee\u9898\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u7684\u5f53\u4e8b\u4eba\u6709\u6743\u4fdd\u6301\u6c89\u9ed8\uff0c\u6ca1\u6709\u6211\u7684\u966a\u540c\uff0c\u4ed6\u662f\u7edd\u4e0d\u4f1a\u56de\u7b54\u4f60\u4eec\u4efb\u4f55\u95ee\u9898\u7684\u3002\u73b0\u5728\u6211\u7684\u5f53\u4e8b\u4eba\u7ecf\u5728\u521a\u624d\u4e00\u7cfb\u5217\u7684\u9669\u60c5\uff0c\u5fc3\u7eea\u4e0d\u5b89\uff0c\u8fd9\u65f6\u5019\u4e5f\u6ca1\u6709\u529e\u6cd5\u914d\u5408\u4f60\u4eec\u7684\u8c03\u67e5\uff0c\u6211\u8bf7\u6c42\u6211\u7684\u5f53\u4e8b\u4e3b\u56de\u5bb6\u7b49\u5f85\u8b66\u65b9\u7684\u4f20\u8baf\uff0c\u201d\u90a3\u4e2d\u5e74\u4eba\u8f6c\u5934\u53c8\u5429\u5490\u845b\u836b\u5747\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u4e0d\u8981\u56de\u7b54\u8b66\u65b9\u7684\u4efb\u4f55\u95ee\u9898\uff0c\u5c31\u7b97\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4e5f\u4e00\u5b9a\u8981\u6211\u5728\u573a\u3002\u201d</p><p>\u3000\u3000\u9999\u6e2f\u6cd5\u5f8b\u7684\u4e25\u5bc6\uff0c\u66f4\u591a\u662f\u4fdd\u62a4\u6709\u94b1\u4eba\u7684\u7279\u6743\uff0c\u53ea\u8981\u8db3\u591f\u6709\u94b1\uff0c\u5c31\u53ef\u4ee5\u8bf7\u4e00\u5927\u5806\u5f8b\u5e08\u4e3a\u81ea\u5df1\u6d17\u8131\u7f6a\u540d\u3002</p><p>\u3000\u3000\u201c\u521a\u624d\u7684\u8bdd\uff0c\u6211\u4e0d\u662f\u8981\u6050\u5413\u4f60\u4eec\uff0c\u201d\u89c1\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u8fde\u627f\u62c5\u8d23\u4efb\u7684\u62c5\u5f53\u90fd\u6ca1\u6709\uff0c\u5f20\u606a\u51b7\u51b7\u7684\u4e22\u4e0b\u4e00\u53e5\u8bdd\uff0c\u5c06\u8c03\u67e5\u8868\u683c\u9012\u8fd8\u7ed9\u8b66\u5bdf\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u613f\u610f\u4f1a\u968f\u65f6\u914d\u5408\u4f60\u4eec\u7684\u8c03\u67e5\uff0c\u6709\u4e8b\u8bf7\u968f\u65f6\u8054\u7cfb\u6211\u4eec\u2026\u2026\u201d\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u4e0e\u5085\u4fca\u8f6c\u8eab\u671d\u5b59\u5c1a\u4e49\u592b\u5987\u8d70\u53bb\uff0c\u5b59\u9759\u6aac\u3001\u5b59\u9759\u9999\u5df2\u7ecf\u968f\u540c\u6551\u62a4\u8f66\u4e00\u8d77\u8d76\u5f80\u533b\u9662\u4e86\u3002</p><p>\u3000\u3000\u201c\u53ef\u80fd\u5df2\u6709\u7684\u8bc1\u636e\u4f1a\u8ba9\u845b\u836b\u5747\u53d7\u5230\u5e94\u6709\u7684\u60e9\u7f5a\u2026\u2026\u201d\u5f20\u606a\u4e5f\u6709\u4e9b\u60b2\u89c2\uff0c\u5173\u952e\u6cd5\u62c9\u5229\u8f66\u540e\u8f6e\u4e0e\u5f2f\u9053\u62a4\u680f\u6469\u64e6\u7684\u75d5\u8ff9\u5bf9\u845b\u836b\u5747\u6709\u5229\uff0c\u4ed6\u6240\u89c2\u5bdf\u5230\u7684\u4e1c\u897f\uff0c\u4f5c\u4e3a\u8bc1\u636e\u5728\u6cd5\u5f8b\u7684\u6709\u6548\u7a0b\u5ea6\u5f88\u5f31\u3002</p><p>\u3000\u3000\u770b\u7740\u8b66\u65b9\u5c31\u5728\u73b0\u573a\u5bf9\u845b\u836b\u5747\u505a\u9152\u7cbe\u65b9\u9762\u7684\u6d4b\u8bd5\uff0c\u9664\u975e\u6709\u660e\u663e\u7684\u8bc1\u636e\u80fd\u8bc1\u660e\u845b\u836b\u5747\u662f\u5371\u9669\u9a7e\u9a76\uff0c\u4e0d\u7136\u4e5f\u65e0\u6cd5\u7acb\u5373\u5c06\u4ed6\u62d8\u6355\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5b59\u5c1a\u4e49\u4e0d\u7518\u5fc3\u7684\u634f\u62f3\u9524\u7740\u81ea\u5df1\u7684\u624b\u5fc3\uff0c\u6c89\u9ed8\u7247\u54cd\uff0c\u624d\u8bf4\u9053\uff0c\u201c\u5148\u53bb\u533b\u9662\u518d\u8bf4\u5427\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5929\u6c14\u6e10\u6697\u4e0b\u6765\uff0c\u5c71\u5916\u80fd\u770b\u89c1\u6d77\u6c34\u7684\u4e00\u89d2\u4e0e\u6697\u84dd\u8272\u7684\u5929\u7a7a\u4ee5\u53ca\u9576\u7740\u6697\u91d1\u8272\u82b1\u8fb9\u7684\u6b8b\u4e91\uff0c\u8f66\u5b50\u521a\u9a76\u4e0b\u6d45\u6c34\u6e7e\u5927\u9053\uff0c\u5c31\u63a5\u5230\u5b59\u9759\u9999\u4ece\u533b\u9662\u91cc\u6253\u6765\u7684\u7535\u8bdd\uff0c\u73e0\u5ac2\u5728\u9001\u5f80\u533b\u9662\u7684\u9014\u4e2d\uff0c\u5fc3\u810f\u5c31\u505c\u6b62\u4e86\u5fc3\u8df3\uff0c\u5979\u73b0\u5728\u770b\u7740\u5b59\u9759\u6aac\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5b59\u5c1a\u4e49\u4f9d\u7136\u5148\u5f80\u533b\u9662\u8d76\u53bb\uff0c\u4e0e\u6b7b\u8005\u544a\u522b\u4e5f\u662f\u5bf9\u751f\u547d\u7684\u5c0a\u91cd\u3002</p><p>\u3000\u3000\u8d76\u5230\u533b\u9662\uff0c\u73e0\u5ac2\u7684\u5bb6\u4eba\u90fd\u5df2\u7ecf\u63a5\u5230\u6d88\u606f\u8d76\u5230\u90a3\u91cc\u3002\u73e0\u5ac2\u7684\u4e08\u592b\u5df2\u7ecf\u662f\u901d\u4e16\uff0c\u5979\u6709\u4e00\u4e2a\u5973\u513f\u3001\u4e00\u4e2a\u513f\u5b50\uff0c\u90fd\u4f4f\u5728\u5357\u533a\uff0c\u8b66\u65b9\u4e5f\u6709\u4e00\u540d\u8b66\u5458\u5728\u533b\u9662\u91cc\u3002\u5b59\u5c1a\u4e49\u592b\u5987\u8dd1\u8fc7\u53bb\u5b89\u6170\u73e0\u5ac2\u7684\u5bb6\u4eba\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u53bb\u627e\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u59d0\u59b9\uff0c\u9759\u6aac\u8138\u57cb\u5728\u5979\u59d0\u59d0\u7684\u6000\u91cc\u65f6\u800c\u53d1\u51fa\u4e00\u4e24\u58f0\u62bd\u6ce3\u3002\u5927\u6982\u542c\u5230\u5f20\u606a\u3001\u5510\u5a67\u7684\u811a\u6b65\u58f0\uff0c\u5b59\u9759\u6aac\u62ac\u8d77\u5934\u6765\uff0c\u773c\u775b\u54ed\u5f97\u7ea2\u80bf\uff0c\u5374\u72b9\u6709\u6ee1\u8154\u7684\u4e0d\u5fff\uff0c\u5bf9\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u6b21\u8981\u5e2e\u6211\uff0c\u4e00\u5bb6\u4e0d\u8981\u8ba9\u845b\u5bb6\u7236\u4ee5\u4e3a\u62ff\u94b1\u5c31\u80fd\u89e3\u51b3\u4e00\u5207\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u7b97\u65e0\u76f8\u5173\u7684\u4eba\uff0c\u6211\u4e5f\u4e0d\u53ef\u80fd\u89c6\u800c\u4e0d\u89c1\u7684\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5929\u4e0b\u7684\u4e0d\u5e73\u4e8b\u4eba\u4f55\u5176\u7684\u591a\uff0c\u5f20\u606a\u4e0d\u4f1a\u53bb\u7ba1\uff0c\u4e5f\u4e0d\u53ef\u80fd\u90fd\u7ba1\u8fc7\u6765\uff0c\u4f46\u65e2\u7136\u9047\u4e0a\u4e86\uff0c\u5374\u4e0d\u4f1a\u5750\u89c6\u4e0d\u7406\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u5510\u5a67\u966a\u7740\u5b59\u9759\u6aac\uff0c\u4ed6\u4e0e\u5085\u4fca\u8fdb\u53bb\u770b\u9057\u4f53\uff0c\u5b59\u5c1a\u4e49\u4e5f\u4e00\u8138\u6c89\u91cd\u7684\u7ad9\u5728\u90a3\u91cc\u3002\u5b59\u5c1a\u4e49\u7684\u59bb\u5b50\u845b\u660e\u73cf\u62ff\u624b\u7ee2\u64e6\u7740\u773c\u89d2\u7684\u6cea\u75d5\uff0c\u8d70\u51fa\u53bb\u627e\u5973\u513f\u9759\u6aac\u4e86\u3002</p><p>\u3000\u3000\u201c\u4e0e\u845b\u836b\u5747\u4e00\u8d77\u7684\u90a3\u4e2a\u5c0f\u5b50\u662f\u8c01\uff1f\u201d\u5f20\u606a\u62c9\u5b59\u5c1a\u4e49\u5230\u8fc7\u9053\u7684\u89d2\u843d\u8bf4\u8bdd\uff0c\u76ee\u524d\u7684\u5c40\u52bf\uff0c\u53ea\u6709\u4e0e\u845b\u836b\u5747\u540c\u8f66\u7684\u9752\u5e74\u7684\u8bc1\u8a00\u624d\u662f\u5173\u952e\uff0c\u5f53\u7136\u4e86\uff0c\u4f46\u662f\u60f3\u4ed6\u8bf4\u5b9e\u8bdd\u505a\u51fa\u4e0d\u5229\u845b\u836b\u5747\u7684\u8bc1\u8a00\u5f88\u56f0\u96be\u3002</p><p>\u3000\u3000\u201c\u9648\u529b\u9633\u7684\u513f\u5b50\u9648\u9a90\u2026\u2026\u201d\u5b59\u5c1a\u4e49\u8bf4\u9053\u3002</p><p>\u3000\u3000\u9999\u6e2f\u9648\u5bb6\u662f\u845b\u5bb6\u957f\u671f\u751f\u610f\u4e0a\u7684\u4f19\u4f34\uff0c\u66fe\u7ecf\u5728\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u6240\u6709\u7684\u4e0a\u5e02\u4f01\u4e1a\u90fd\u6301\u80a1\uff0c\u845b\u660e\u4fe1\u4e3a\u4e86\u538b\u5236\u845b\u5bb6\u5176\u4ed6\u6210\u5458\u5bf9\u4ed6\u7684\u53cd\u6297\uff0c\u52a0\u5f3a\u5bf9\u5609\u4fe1\u96c6\u56e2\u7684\u63a7\u5236\u529b\uff0c\u4fc3\u4f7f\u9648\u5bb6\u6210\u4e3a\u5609\u4fe1\u96c6\u56e2\u7684\u7b2c\u4e8c\u5927\u80a1\u4e1c\uff0c\u9648\u529b\u9633\u662f\u9999\u6e2f\u9648\u5bb6\u8fd9\u4e00\u4ee3\u7684\u4e3b\u4eba\uff0c\u4e5f\u662f\u5609\u4fe1\u96c6\u56e2\u7684\u8463\u4e8b\uff0c\u9648\u5bb6\u9664\u4e86\u5728\u5609\u4fe1\u96c6\u56e2\u6301\u80a1\u4e4b\u5916\uff0c\u8fd8\u6709\u5176\u4ed6\u5e9e\u5927\u7684\u5bb6\u4e1a\u3002</p><p>\u3000\u3000\u542c\u5b59\u5c1a\u4e49\u8bf4\u5230\u9648\u529b\u9633\uff0c\u5f20\u606a\u5927\u7ea6\u80fd\u731c\u5230\u9648\u9a90\u4e0e\u845b\u836b\u5747\u5e94\u8be5\u662f\u4ece\u5c0f\u4e00\u8d77\u73a9\u4e50\u7684\u72d0\u670b\u72d7\u515a\uff0c\u9648\u9a90\u5927\u6982\u4f1a\u8bb0\u6068\u5728\u8f66\u7978\u73b0\u573a\u6328\u4e86\u7684\u90a3\u4e00\u5df4\u638c\u3001\u4e24\u817f\u8ddf\u7684\u4e8b\u60c5\uff0c\u60f3\u4ed6\u7ad9\u51fa\u6765\u4f5c\u8bc1\uff0c\u51e0\u4e4e\u662f\u6ca1\u6709\u53ef\u80fd\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u5bf9\u6b64\u4e5f\u4e0d\u770b\u597d\uff0c\u5f20\u606a\u62ff\u51fa\u624b\u673a\uff0c\u72b9\u8c6b\u7740\uff0c\u5fc3\u60f3\u82e5\u662f\u8ba9\u65f6\u4efb\u9999\u6e2f\u4e2d\u8054\u529e\u526f\u4e3b\u4efb\u7684\u53f6\u81fb\u6c11\u76f4\u63a5\u5e72\u9884\u6b64\u4e8b\u6709\u4e9b\u4e0d\u5927\u5408\u9002\uff0c\u5c06\u624b\u673a\u62ff\u5728\u624b\u91cc\uff0c\u6ca1\u6709\u62e8\u51fa\u7535\u8bdd\uff0c\u4e0d\u60f3\u5c06\u9ebb\u70e6\u8f6c\u79fb\u5230\u522b\u4eba\u5934\u4e0a\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5916\u9762\u6709\u4e9b\u5435\u95f9\uff0c\u5f20\u606a\u4e0e\u5b59\u5c1a\u4e49\u8d70\u51fa\u53bb\u770b\uff0c\u845b\u660e\u4fe1\u7684\u5f8b\u5e08\u7ad9\u5728\u6b93\u5c38\u95f4\u5916\u9762\u7684\u95e8\u5385\u91cc\uff0c\u6b63\u4e0e\u73e0\u5ac2\u7684\u513f\u5b50\u3001\u5973\u513f\u8bf4\u8bdd\uff1a\u201c\u6211\u4ee3\u8868\u845b\u836b\u5747\u5148\u751f\u8fc7\u6765\u8d54\u793c\u9053\u6b49\uff0c\u5728\u8def\u4e0a\u624d\u77e5\u9053\u73e0\u5ac2\u8fc7\u4e16\u7684\u6d88\u606f\uff0c\u5f88\u9057\u61be\u3002\u76f8\u4fe1\u4f60\u4eec\u5df2\u7ecf\u542c\u8b66\u65b9\u8bf4\u8d77\u4e8b\u6545\u53d1\u751f\u7684\u7ecf\u8fc7\uff0c\u5373\u4f7f\u662f\u7d27\u6025\u907f\u9669\u9020\u6210\u7684\u4e8b\u6545\uff0c\u5bf9\u73e0\u5ac2\u7684\u8eab\u540e\u4e8b\uff0c\u845b\u836b\u5747\u5148\u751f\u4e5f\u613f\u610f\u627f\u62c5\u4e00\u5207\u7684\u8d23\u4efb\uff0c\u8fd9\u6837\u624d\u4f1a\u8ba9\u4ed6\u7684\u5fc3\u597d\u53d7\u4e9b\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u627e\u4e2a\u5730\u65b9\u5546\u8bae\u4e00\u4e0b\u73e0\u5ac2\u7684\u8eab\u540e\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u7684\u5fc3\u90fd\u8ba9\u72d7\u5403\u4e86\uff0c\u60f3\u548c\u89e3\uff0c\u505a\u68a6\u90fd\u6ca1\u95e8\uff01\u201d\u5b59\u9759\u6aac\u6307\u7740\u845b\u660e\u4fe1\u7684\u5f8b\u5e08\u5389\u58f0\u559d\u65a5\u9053\uff0c\u201c\u4f60\u7ed9\u6211\u51fa\u53bb\uff0c\u4e0d\u9700\u8981\u4f60\u4eec\u786c\u6324\u51fa\u6765\u7684\u9cc4\u9c7c\u773c\u6cea\u3002\u201d</p><p>\u3000\u3000\u201c\u5b59\u5c0f\u59d0\uff0c\u73e0\u5ac2\u901d\u4e16\u662f\u5927\u5bb6\u90fd\u4e0d\u60f3\u7684\u3002\u5927\u5bb6\u5fc3\u5e73\u6c14\u548c\u7684\u5c06\u4e8b\u60c5\u89e3\u51b3\u6389\uff0c\u8ba9\u5927\u5bb6\u7684\u751f\u6d3b\u90fd\u56de\u5230\u5404\u81ea\u7684\u8f68\u9053\u4e0a\u53bb\uff0c\u4e0d\u8981\u7ed9\u73e0\u5ac2\u7684\u901d\u4e16\u6253\u6270\u3002\u6211\u8ddf\u73e0\u5ac2\u4e5f\u89c1\u8fc7\u51e0\u6b21\u9762\uff0c\u6211\u60f3\u8fd9\u4e5f\u7b26\u5408\u73e0\u5ac2\u7684\u672c\u610f\u5427\uff0c\u201d\u90a3\u4e2d\u5e74\u4eba\u8bdd\u950b\u4e00\u8f6c\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u53e6\u5916\uff0c\u6211\u60f3\u95ee\u4e00\u4e0b\uff0c\u5b59\u5c0f\u59d0\u6709\u4ec0\u4e48\u7acb\u573a\u4ee3\u8868\u73e0\u5ac2\u7684\u4eb2\u751f\u513f\u5b50\u3001\u4eb2\u751f\u5973\u513f\u8bf4\u8bdd\uff1f\u5b59\u5c0f\u59d0\u5728\u8f66\u7978\u73b0\u5728\u5bf9\u845b\u5c11\u53c8\u6293\u53c8\u8e22\uff0c\u81f4\u4f7f\u845b\u5c11\u817f\u4e0a\u6de4\u9752\u591a\u5904\u3001\u8138\u4e0a\u4e5f\u7ed9\u6293\u51fa\u51e0\u9053\u8840\u75d5\uff0c\u4e8b\u4e8b\u90fd\u65a4\u65a4\u8ba1\u8f83\uff0c\u96be\u9053\u4e5f\u9700\u8981\u845b\u5c11\u53bb\u8b66\u5c40\u505a\u4f24\u60c5\u9274\u5b9a\uff1f\u201d</p><p>\u3000\u3000\u770b\u5230\u5b59\u9759\u6aac\u9677\u5165\u6293\u72c2\u7684\u72b6\u6001\uff0c\u845b\u660e\u73cf\u4e0e\u5b59\u9759\u9999\u7acb\u5373\u6212\u5907\u8d77\u6765\u62c5\u5fc3\u5979\u6251\u8fc7\u53bb\u54ac\u4eba\uff0c\u5b59\u9759\u6aac\u5374\u8f6c\u8fc7\u8eab\u4ece\u5f20\u606a\u624b\u91cc\u62a2\u8fc7\u624b\u673a\u671d\u845b\u5bb6\u5f8b\u5e08\u7838\u53bb\uff0c\u5f20\u606a\u6ca1\u6709\u963b\u6b62\u5979\uff0c\u800c\u662f\u95ea\u8fc7\u8eab\u6321\u4f4f\u5728\u573a\u8c03\u67e5\u4e8b\u6545\u7684\u8b66\u5458\u7684\u89c6\u7ebf\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u845b\u5bb6\u7684\u5f8b\u5e08\u60e8\u53eb\u4e86\u4e00\u58f0\uff0c\u6342\u7740\u5de6\u773c\u7a9d\u6307\u7740\u5b59\u9759\u6aac\uff0c\u53eb\u9053\uff0c\u201c\u8b66\u5bdf\u3001\u8b66\u5bdf\uff0c\u6709\u4eba\u62ff\u4e1c\u897f\u7838\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8c01\u5728\u7838\u4f60\uff1f\u201d\u5f20\u606a\u6321\u5230\u5b59\u9759\u6aac\u7684\u9762\u524d\uff0c\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u201c\u4f60\u82e5\u662f\u4e71\u6307\uff0c\u4e0d\u8981\u4ee5\u4e3a\u9664\u4e86\u845b\u660e\u4fe1\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u5c31\u8bf7\u4e0d\u8d77\u5f8b\u5e08\u3002\u6211\u53ef\u6ca1\u6709\u770b\u89c1\u8fd9\u5385\u91cc\u6709\u8c01\u7838\u4f60\u4e86\u3002\u201d\u5085\u4fca\u5728\u5f8b\u5e08\u53cd\u5e94\u8fc7\u6765\u4e4b\u524d\u5c06\u6454\u5230\u95e8\u5385\u89d2\u843d\u7684\u624b\u673a\u6361\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u8f6c\u8eab\u95ee\u7ad9\u5728\u8fc7\u9053\u53e3\u7684\u8b66\u5458\uff0c\u201c\u8b66\u5bdf\u5148\u751f\uff0c\u4f60\u770b\u5230\u6709\u8c01\u7838\u4ed6\u4e86\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u6ca1\u6709\u770b\u89c1\uff0c\u4e0d\u8fc7\u4f60\u82e5\u662f\u575a\u6301\u62a5\u8b66\uff0c\u6211\u4f1a\u53eb\u6211\u7684\u540c\u4e8b\u8fc7\u6765\u5c06\u4f60\u4eec\u90fd\u5e26\u56de\u8b66\u5458\u505a\u8c03\u67e5\u3002\u201d\u8b66\u5458\u516c\u4e8b\u516c\u529e\u7684\u5bf9\u845b\u660e\u4fe1\u7684\u5f8b\u5e08\u8bf4\u9053\uff0c\u5bf9\u4ed6\u5de6\u773c\u89d2\u7684\u6de4\u9752\u89c6\u82e5\u65e0\u7779\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u7684\u5f8b\u5e08\u4e5f\u4e0d\u60f3\u8282\u5916\u751f\u679d\uff0c\u53ea\u80fd\u5c06\u8fd9\u53e3\u6c14\u5fcd\u4e0b\uff0c\u4e0d\u7406\u4f1a\u5f20\u606a\u7b49\u4eba\uff0c\u8ddf\u73e0\u5ac2\u7684\u513f\u5b50\u3001\u5973\u513f\u8bf4\u9053\uff1a\u201c\u845b\u836b\u5747\u5148\u751f\u662f\u8bda\u5fc3\u8bda\u610f\u7684\u5bf9\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u60c5\u8868\u793a\u9057\u61be\uff0c\u73e0\u5ac2\u5df2\u7ecf\u6b7b\u4e86\uff0c\u8fd9\u5df2\u7ecf\u662f\u8c01\u90fd\u6ca1\u6cd5\u6539\u53d8\u7684\u4e8b\u5b9e\uff0c\u6211\u60f3\u6211\u4eec\u8fd8\u662f\u627e\u4e2a\u5730\u65b9\u8c08\u4e00\u8c08\u2026\u2026\u201d</p><p>\u3000\u3000\u4e3a\u4e86\u907f\u514d\u5b59\u9759\u6aac\u518d\u53d1\u4f5c\uff0c\u845b\u660e\u73cf\u8ddf\u9759\u9999\u8fd8\u8981\u5510\u5a67\u62c9\u7740\u5979\u79bb\u5f00\u95e8\u5385\u3002</p><p>\u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u8981\u662f\u53ef\u4ee5\uff0c\u6211\u8981\u6211\u5988\u80fd\u6d3b\u8fc7\u6765\u2026\u2026\u201d\u73e0\u5ac2\u7684\u5973\u513f\u6ca1\u6709\u4e3b\u610f\uff0c\u5012\u662f\u73e0\u5ac2\u7684\u513f\u5b50\u867d\u7136\u6447\u5934\u8bf4\u4e0d\uff0c\u4f46\u662f\u76ee\u5149\u6e38\u79bb\uff0c\u663e\u793a\u4ed6\u5fc3\u601d\u5df2\u52a8\uff0c\u6bd4\u8d77\u8ffd\u7a76\u845b\u836b\u5747\u7684\u7f6a\u8bad\uff0c\u4ed6\u6216\u8bb8\u66f4\u5173\u5fc3\u80fd\u5f97\u5230\u4e00\u5927\u7b14\u8d54\u507f\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u4e0e\u5b59\u5c1a\u4e49\u8d70\u51fa\u95e8\u5385\uff0c\u4ed6\u4eec\u6bd5\u7adf\u4e0d\u80fd\u4ee3\u66ff\u6b7b\u8005\u7684\u76f4\u7cfb\u4eb2\u5c5e\u8ffd\u7a76\u845b\u836b\u5747\u7684\u8d23\u4efb\u3002\u8d70\u5230\u95e8\u5916\uff0c\u5f20\u606a\u7a0d\u4f5c\u601d\u91cf\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e76\u4e0d\u60f3\u9488\u5bf9\u4efb\u4f55\u4eba\uff0c\u65e2\u7136\u845b\u660e\u4fe1\u7236\u5b50\u8fde\u627f\u62c5\u8d23\u4efb\u7684\u52c7\u6c14\u90fd\u6ca1\u6709\u2026\u2026\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u80fd\u4e0d\u80fd\u5148\u627e\u5bb6\u5a92\u4f53\u5c06\u8fd9\u4ef6\u4e8b\u66b4\u5149\u518d\u8bf4\u2026\u2026\u201d\u5fc3\u91cc\u60f3\u7740\uff1a\u8981\u4ec0\u4e48\u4e8b\u90fd\u4e0d\u505a\uff0c\u9759\u6aac\u5fc3\u91cc\u5927\u6982\u4f1a\u66f4\u96be\u53d7\u5427\u3002\u8fd9\u79cd\u5173\u5fc3\uff0c\u5f53\u7136\u4e0d\u4f1a\u5bf9\u5b59\u5c1a\u4e49\u8bf4\u51fa\u53e3\u3002</p></article>"}}}]);
