(window.webpackJsonp=window.webpackJsonp||[]).push([[777],{800:function(p){p.exports={chapterTitle:null,title:"\u7b2c610\u7ae0 \u5356\u6c14\u7403\u7684",content:"<article><h2>\u7b2c610\u7ae0 \u5356\u6c14\u7403\u7684</h2><p>\u3000\u3000\u5f20\u606a\u624b\u81c2\u4e0a\u7cfb\u7740\u516b\u53ea\u6c14\u7403\uff0c\u4e00\u8def\u4e0a\u9047\u5230\u597d\u51e0\u4e2a\u4eba\u8dd1\u8fc7\u8ddf\u4ed6\u4e70\u6c14\u7403\uff0c\u5f20\u606a\u626f\u7740\u4ed6\u7684\u77ed\u8896\u886c\u886b\u8863\u9886\u7ed9\u5510\u5a67\u770b\u8863\u9886\u5185\u4fa7\u7684\u9886\u6807\uff0c\u53eb\u5c48\u9053\uff1a\u201c\u6211\u4e00\u8eab\u7eaa\u68b5\u5e0c\uff0c\u54ea\u91cc\u50cf\u5356\u6c14\u7403\u7684\uff1f\u201d\u5510\u5a67\u7b11\u5c94\u6c14\uff0c\u80a0\u5b50\u90fd\u5feb\u63c9\u65ad\u4e86\u3002\u5f20\u606a\u8981\u5c06\u6c14\u7403\u5206\u7ed9\u5927\u5bb6\u4e00\u4eba\u624b\u91cc\u62ff\u4e00\u53ea\uff0c\u5510\u5a67\u53eb\u5927\u5bb6\u4e0d\u8981\u542c\u5f20\u606a\u7684\u3002\u56de\u53bb\u65f6\uff0c\u7ad9\u5728\u516c\u4ea4\u7ad9\u53f0\u524d\uff0c\u770b\u7740\u516c\u4ea4\u8f66\u8fc7\u6765\uff0c\u5f20\u606a\u6b63\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u907f\u514d\u6c14\u7403\u7ed9\u4eba\u7fa4\u6324\u7206\u6389\uff0c\u4eba\u8fd8\u6ca1\u6709\u4e0a\u8f66\uff0c\u5c31\u542c\u4e2d\u5e74\u5973\u53f8\u673a\u5750\u5728\u9a7e\u9a76\u5e2d\u4e0a\uff0c\u811a\u8e29\u7740\u6c14\u7f38\u76d6\uff0c\u6307\u7740\u6b63\u8981\u4e0a\u8f66\u7684\u4ed6\u5927\u58f0\u56b7\u9053\uff1a\u201c\u5356\u6c14\u7403\u7684\uff0c\u4f60\u4e0d\u8981\u4e0a\u6765\uff01\u8f66\u91cc\u4eba\u8fd9\u4e48\u591a\uff0c\u4f60\u4e0a\u8f66\u6765\uff0c\u6211\u8fd9\u8f66\u5b50\u8fd8\u88c5\u4e0d\u88c5\u5176\u4ed6\u4eba\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5510\u5a67\u5979\u4eec\u51e0\u4e2a\u5973\u5b69\u5b50\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\uff0c\u8fd9\u624d\u53c8\u4e0b\u8f66\u5e2e\u5f20\u606a\u5c06\u6c14\u7403\u5206\u8fc7\u53bb\uff0c\u516b\u4e2a\u4eba\uff0c\u6b63\u597d\u4e00\u4eba\u624b\u91cc\u7275\u7740\u4e00\u53ea\u6c14\u7403\uff0c\u5360\u4e86\u534a\u4e2a\u8f66\u53a2\u90fd\u4e0d\u6b62\u3002\u675c\u98de\u70b9\u5934\u54c8\u8170\u7684\u8ddf\u8180\u5927\u8170\u5706\u7684\u5973\u53f8\u673a\u6253\u62db\u547c\uff0c\u8fd8\u6295\u4e86\u4e24\u5f20\u5341\u5143\u7eb8\u5e01\u8fdb\u6295\u5e01\u7bb1\uff0c\u7b97\u662f\u7ed9\u6c14\u7403\u4e70\u8f66\u7968\u3002</p><p>\u3000\u3000\u5973\u53f8\u673a\u81ea\u5df1\u90fd\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u8bf4\u5356\u6c14\u7403\u7684\u5c0f\u4f19\u5b50\u54ea\u6709\u957f\u8fd9\u4e48\u5e05\u7684\uff1f\u201d</p><p>\u3000\u3000\u5230\u5e95\u662f\u5510\u5a67\u5979\u4eec\u51e0\u4e2a\u5973\u5b69\u5b50\u660e\u7738\u7693\u9f7f\u660e\u8273\u79c0\u7f8e\uff0c\u8fd9\u4e48\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u80fd\u5076\u5c14\u9047\u5230\u4e00\u4e24\u4eba\uff0c\u5df2\u7ecf\u5f88\u4e86\u4e0d\u5f97\u4e86\uff0c\u4e00\u4e0b\u5b50\u9047\u5230\u56db\u4e94\u4e2a\uff0c\u8f66\u53a2\u91cc\u90fd\u6000\u7591\u662f\u4e0d\u662f\u97f3\u4e50\u5b66\u9662\u8868\u6f14\u7cfb\u6216\u519b\u533a\u6b4c\u821e\u56e2\u7684\u5973\u5b69\u5b50\u5c3d\u5f97\u6f02\u4eae\u7684\u51fa\u6765\u51fa\u6e38\uff0c\u5012\u662f\u6ca1\u6709\u4eba\u4ecb\u610f\u4ed6\u4eec\u4e00\u4e0b\u5b50\u62ff\u4e86\u516b\u53ea\u6bd4\u4eba\u5934\u5927\u591a\u4e86\u7684\u5927\u6c14\u7403\u5230\u8f66\u53a2\u91cc\u6765\u3002</p><p>\u3000\u3000\u4e0b\u4e86\u8f66\uff0c\u82cf\u4e00\u5a77\u4e0e\u4f55\u5f26\u5148\u56de\u5bbf\u820d\uff0c\u5f20\u606a\u4ed6\u4eec\u5148\u53bb\u5e08\u5927\u4e8c\u62db\u91cc\u9762\uff0c\u4ed6\u7238\u5988\u4ece\u5916\u9762\u5c3d\u4e86\u6e38\u5174\u4e5f\u521a\u56de\u6765\u3002\u6881\u683c\u73cd\u770b\u5230\u5510\u5a67\uff0c\u5c31\u8bf4\u5979\u521a\u521a\u8ddf\u5979\u5988\u5988\u901a\u8fc7\u7535\u8bdd\uff0c\u8bf4\u8fd9\u6b21\u8981\u966a\u5979\u8fd8\u6709\u5979\u5988\u5988\u4e00\u8d77\u53bb\u9999\u6e2f\u3002</p><p>\u3000\u3000\u603b\u4e4b\u9999\u6e2f\u90a3\u91cc\u73b0\u5728\u6709\u848b\u8587\u8d1f\u8d23\u5b89\u6392\u4e00\u5207\uff0c\u591a\u5b89\u6392\u4e00\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u89c1\u5f97\u9ebb\u70e6\uff1b\u540e\u5929\u7684\u98de\u673a\uff0c\u63d0\u524d\u8865\u4e00\u5f20\u673a\u7968\u5c31\u884c\uff0c\u8fb9\u9632\u8bc1\u7b49\u8fc7\u5883\u624b\u7eed\u4e4b\u524d\u90fd\u6709\u529e\u7406\u3002</p><p>\u3000\u3000\u770b\u65f6\u95f4\u5dee\u4e0d\u591a\uff0c\u5927\u5bb6\u4e00\u8d77\u9001\u9648\u5983\u84c9\u56de\u5bbf\u820d\u3002\u5510\u5a67\u3001\u76db\u590f\u4e1d\u6beb\u611f\u89c9\u4e0d\u5230\u75b2\u60eb\uff0c\u5510\u5a67\u8fd8\u8981\u8dd1\u5230\u4e1c\u5927\u5c06\u6c14\u7403\u7cfb\u5230\u5973\u751f\u5bbf\u820d\u697c\u524d\u7684\u7bee\u7403\u573a\u94c1\u4e1d\u7f51\u5899\u4e0a\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u4e3b\u6559\u5b66\u697c\u524d\u7684\u5927\u64cd\u573a\uff0c\u5bc6\u5bc6\u9ebb\u9ebb\u7684\u94fa\u6ee1\u51c9\u5e2d\uff0c\u5bbf\u820d\u4e0d\u80fd\u7559\u4eba\uff0c\u8bb8\u591a\u65b0\u751f\u5c31\u5377\u7740\u94fa\u76d6\u966a\u5bb6\u957f\u7761\u5230\u64cd\u573a\u4e0a\u6765\uff0c\u770b\u8d77\u6765\u975e\u5e38\u7684\u70ed\u95f9\u3002</p><p>\u3000\u3000\u5973\u751f\u5bbf\u820d\u524d\u7684\u7bee\u7403\u573a\u4e5f\u662f\u5982\u6b64\uff1b\u5f20\u606a\u4ed6\u4eec\u521a\u5f80\u7bee\u7403\u573a\u8fb9\u4e0a\u7684\u94c1\u4e1d\u7f51\u7cfb\u6c14\u7403\u65f6\uff0c\u9634\u4e86\u534a\u5bbf\u7684\u5929\u7a7a\u7ec8\u4e8e\u567c\u54e9\u556a\u5566\u4e0b\u8d77\u9ec4\u8c46\u5927\u5c0f\u7684\u96e8\u6ef4\u6765\uff0c\u94fa\u7740\u8349\u5e2d\u51c6\u5907\u5728\u7bee\u7403\u573a\u4e0a\u8fc7\u591c\u7684\u65b0\u751f\u5bb6\u957f\u4eec\u63aa\u624b\u4e0d\u53ca\uff0c\u7eb7\u7eb7\u62b1\u8d77\u94fa\u76d6\u627e\u5730\u65b9\u907f\u96e8\u3002</p><p>\u3000\u3000\u503e\u523b\u95f4\uff0c\u96e8\u52bf\u5c31\u6025\u4e86\u8d77\u6765\uff1b\u5f20\u606a\u521a\u521a\u5e2e\u5510\u5a67\u5c06\u6c14\u7403\u7cfb\u597d\uff0c\u6402\u7740\u5979\u7684\u5c0f\u8170\u8e66\u8e66\u8df3\u8df3\u7684\u51b2\u5230\u8d70\u5eca\u4e0b\u907f\u96e8\u3002\u5510\u5a67\u638f\u51fa\u624b\u5e15\u6765\u5e2e\u5f20\u606a\u5c06\u989d\u5934\u7684\u96e8\u6c34\u64e6\u6389\uff0c\u4f9d\u5728\u5f20\u606a\u7684\u6000\u91cc\uff0c\u62ac\u5934\u770b\u7740\u4ed6\u8ff7\u4eba\u7684\u773c\u775b\uff0c\u6307\u7740\u8def\u706f\u4e0b\u7684\u6c14\u7403\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u5b8c\u86cb\u4e86\uff01\u201d</p><p>\u3000\u3000\u201c\u5618\uff0c\u5c0f\u58f0\u70b9\uff01\u201d\u5f20\u606a\u5c06\u5510\u5a67\u6402\u5728\u6000\u91cc\uff0c\u624b\u8f7b\u8f7b\u7684\u6342\u7740\u5979\u7ea2\u6da6\u6709\u7740\u6781\u7f8e\u7ebf\u6761\u7684\u5634\u5507\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u9635\u96e8\u4e0b\u5f97\u5f88\u5927\uff0c\u567c\u54e9\u556a\u5566\u7684\u7838\u5f97\u6c14\u7403\u4e71\u8f6c\u4e0d\u4f11\uff0c\u4e0d\u8fc7\u8fd8\u662f\u80fd\u52c9\u5f3a\u770b\u6e05\u695a\u6c14\u7403\u4e0a\u5199\u7740\u9192\u76ee\u5927\u5b57\u201c\u5f20\u606a\u662f\u4e2a\u5927\u8272\u72fc\u201d\uff0c\u5173\u952e\u5b64\u96f6\u96f6\u7684\u7cfb\u5728\u5973\u751f\u5bbf\u820d\u697c\u524d\u7684\u8def\u706f\u4e0b\u592a\u624e\u773c\u4e86\uff1b\u5f20\u606a\u4e70\u7684\u7b2c\u4e00\u53ea\u6c14\u7403\uff0c\u7ed9\u5510\u5a67\u753b\u4e86\u4e00\u4e2a\u7855\u5927\u7684\u611f\u53f9\u53f7\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u8d70\u5eca\u91cc\u907f\u6324\u6ee1\u907f\u96e8\u7684\u65b0\u751f\u5bb6\u957f\u8ddf\u521a\u521a\u5728\u7bee\u7403\u573a\u4e0a\u966a\u4f34\u5bb6\u957f\u7684\u5927\u4e00\u5973\u751f\u4eec\uff0c\u521a\u4e0b\u96e8\u91cc\u4e00\u9635\u614c\u4e71\uff0c\u8fd9\u65f6\u5019\u90fd\u6307\u7740\u8def\u706f\u4e0b\u7684\u6c14\u7403\u6807\u8bed\u8ba8\u8bba\u8fd9\u4e2a\u5f20\u606a\u5230\u5e95\u662f\u8c01\u5462\u3002</p><p>\u3000\u3000\u201c\u8fd9\u5c0f\u5b50\u8d3c\u906d\u4eba\u6068\u554a\uff0c\u5f97\u6709\u591a\u5c11\u5973\u5b69\u5b50\u6bc1\u8fd9\u5c0f\u5b50\u624b\u91cc\uff0c\u624d\u8ba9\u4eba\u72e0\u5fc3\u5c06\u8fd9\u6c14\u7403\u6807\u8bed\u90fd\u60ac\u6302\u5230\u5973\u751f\u5bbf\u820d\u697c\u6765\uff1f\u201d\u4e0d\u6653\u5f97\u4ece\u54ea\u91cc\u5192\u51fa\u4e00\u53e5\u4e1c\u5317\u7537\u4eba\u8154\u3002</p><p>\u3000\u3000\u5510\u5a67\u5728\u5f20\u606a\u6000\u91cc\u7b11\u5f97\u5a07\u8eaf\u4e71\u98a4\uff0c\u8fd8\u4e0d\u89e3\u610f\uff0c\u5c06\u9648\u5983\u84c9\u62c9\u8fc7\u6765\u6402\u7740\u800c\u7b11\uff0c\u9648\u5983\u84c9\u4e5f\u662f\u7b11\u5f97\u6ca1\u5f62\uff0c\u4e24\u4eba\u6402\u5728\u4e00\u56e2\u3002\u5f20\u606a\u7ad9\u5728\u5979\u4eec\u4fe9\u8eab\u8fb9\uff0c\u4e0b\u5df4\u538b\u7740\u5510\u5a67\u7684\u80a9\u8180\uff0c\u51d1\u5230\u9648\u5983\u84c9\u538b\u4f4e\u58f0\u8bf4\uff1a\u201c\u4ee5\u540e\u5343\u4e07\u5728\u4e1c\u5927\u4e0d\u8981\u8bf4\u8ba4\u8bc6\u6211\uff0c\u8ba4\u8bc6\u6211\u4e5f\u4e0d\u8981\u8bf4\u51fa\u6211\u7684\u540d\u5b57\uff0c\u8fd9\u540d\u5b57\u5728\u4e1c\u5927\u7b97\u662f\u5f7b\u5e95\u7684\u6bc1\u4e86\uff01\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u6342\u7740\u5507\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u53c8\u89c9\u5f97\u8fd9\u4e00\u773c\u6709\u4e9b\u66a7\u6627\u7684\u610f\u5473\uff0c\u8138\u988a\u5fae\u7ea2\uff0c\u522b\u8fc7\u5934\u53bb\u4e0d\u770b\u5f20\u606a\u3002</p><p>\u3000\u3000\u96e8\u52bf\u5f88\u5927\uff0c\u65b0\u751f\u5bb6\u957f\u53ea\u80fd\u8eb2\u5728\u5bbf\u820d\u8d70\u5eca\u3001\u8fc7\u9053\u91cc\u907f\u96e8\uff0c\u90a3\u4e9b\u5927\u4e00\u5973\u751f\u4eec\u4e5f\u4e0d\u80fd\u5c06\u5bb6\u957f\u4e22\u5f00\u4e0d\u7ba1\uff0c\u8fc7\u9053\u91cc\u6324\u6ee1\u4e86\u4eba\uff0c\u95f9\u70ed\u4e0d\u4f11\u3002\u90a3\u4e9b\u5927\u4e00\u7684\u5973\u751f\u4eec\u521a\u5230\u4e00\u4e2a\u5168\u65b0\u7684\u73af\u5883\uff0c\u6709\u79bb\u5bb6\u7684\u5174\u594b\uff0c\u4e5f\u6709\u79bb\u5bb6\u7684\u60b2\u4f24\uff0c\u7b11\u95f9\u7684\uff0c\u62b1\u7740\u7236\u6bcd\u54ed\u6ce3\u7684\u90fd\u6709\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u770b\u5f97\u4e00\u4f1a\u513f\uff0c\u5510\u5a67\u4fbf\u62c9\u9648\u5983\u84c9\u4e00\u8d77\u53bb\u5f20\u606a\u5bbf\u820d\u91cc\u53bb\u73a9\u3002\u8fd9\u91cc\u600e\u4e48\u770b\u591c\u91cc\u90fd\u4e0d\u53ef\u80fd\u4f1a\u5b89\u9759\u4e0b\u6765\u7684\u6837\u5b50\u3002\u9648\u5983\u84c9\u62c9\u7740\u5510\u5a67\u8dd1\u4e0a\u697c\uff0c\u62ff\u4e86\u4e24\u628a\u96e8\u4f1e\u4e0b\u6765\uff0c\u5f20\u606a\u559c\u6ecb\u6ecb\u7684\u60f3\u7740\u675c\u98de\u4e0e\u76db\u590f\u6253\u4e00\u628a\u4f1e\uff0c\u4ed6\u5c31\u8981\u8ddf\u5510\u5a67\u3001\u9648\u5983\u84c9\u6324\u4e00\u628a\u5c0f\u4f1e\uff0c\u4ed6\u7684\u5ff5\u5934\u8fd8\u521a\u521a\u5192\u51fa\u6765\uff0c\u5510\u5a67\u5c31\u4e22\u4e86\u4e00\u628a\u4f1e\u7ed9\u4ed6\uff1a\u201c\u4f60\u8ddf\u675c\u98de\u5408\u6253\u4e00\u628a\u4f1e\u2026\u2026\u201d\u4e09\u4e2a\u5973\u5b69\u5b50\u7d27\u7d27\u7684\u6324\u4f5c\u4e00\u56e2\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u4e00\u820d\u4e1c\u4fa7\u7684\u5546\u5e97\uff0c\u4e70\u4e86\u4e24\u724c\u6251\u514b\u724c\u8fd8\u8981\u4e00\u5927\u5305\u96f6\u98df\u8fd4\u56de\u5bbf\u820d\uff0c\u4e00\u76f4\u73a9\u5f97\u51cc\u6668\u4e24\u70b9\u949f\uff0c\u624d\u9001\u9648\u5983\u84c9\u56de\u5bbf\u820d\uff0c\u4ed6\u4eec\u5219\u53bb\u5e08\u5927\u4e8c\u62db\u53bb\u3002\u8fd9\u65f6\u5019\u96e8\u5df2\u7ecf\u6b47\u4e86\uff0c\u7bee\u7403\u573a\u662f\u6c34\u6ce5\u5730\u9762\uff0c\u53c8\u518d\u6b21\u94fa\u6ee1\u51c9\u5e2d\uff0c\u597d\u4e9b\u65b0\u751f\u5bb6\u957f\u90fd\u5df2\u7ecf\u5165\u7761\uff0c\u7ecf\u8fc7\u4e3b\u6559\u5b66\u6821\u524d\u7684\u5927\u64cd\u573a\u65f6\uff0c\u64cd\u573a\u4e0a\u6709\u79ef\u6c34\uff0c\u5f88\u591a\u5bb6\u957f\u5c31\u7761\u5728\u8def\u8fb9\u4e0a\u3002</p><p>\u3000\u3000\u8fd9\u4e0d\u8fc7\u662f\u767e\u6001\u4eba\u751f\u4e2d\u7684\u4e00\u5e55\u800c\u5df2\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e5d\u6708\u516d\u65e5\uff0c\u4f9d\u65e7\u662f\u65b0\u751f\u62a5\u540d\u7684\u65e5\u5b50\uff0c\u9648\u5983\u84c9\u4e0a\u5348\u6709\u8bfe\uff0c\u5f20\u606a\u5728\u5bbe\u9986\u7761\u5230\u8001\u665a\u624d\u8d77\u5e8a\uff0c\u770b\u4e86\u770b\u8155\u8868\uff0c\u90fd\u5feb\u5341\u70b9\uff0c\u6253\u624b\u673a\u7ed9\u5510\u5a67\uff0c\u5510\u5a67\u4e0e\u76db\u590f\u65e9\u5c31\u8d77\u5e8a\u966a\u7740\u4ed6\u7238\u5988\u8dd1\u5230\u6a61\u6811\u56ed\u521b\u4e1a\u56ed\u533a\u53bb\u53c2\u89c2\u4e86\u2014\u2014\u90a3\u91cc\u662f\u6a61\u6811\u56ed\u552f\u4e00\u5efa\u6210\u7684\u5730\u65b9\uff0c\u4e0d\u5230\u56db\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u7d27\u8d76\u6162\u8d76\uff0c\u8fd8\u53ea\u5efa\u6210\u4e00\u534a\u3002</p><p>\u3000\u3000\u5f20\u606a\u6293\u8d77\u6795\u5934\u5c06\u675c\u98de\u7ed9\u7838\u9192\uff0c\u4e24\u4eba\u6d17\u6ede\u8fc7\uff0c\u51fa\u4e86\u5bbe\u9986\u968f\u4fbf\u4e70\u4e9b\u5403\u7684\u4e1c\u897f\u5c31\u53bb\u627e\u5510\u5a67\u5979\u4eec\u3002\u7ad9\u5728\u4e1c\u534e\u5927\u9053\u4e0a\u90fd\u80fd\u770b\u89c1\u521b\u4e1a\u56ed\u533a\u7684\u5927\u95e8\uff0c\u6ca1\u6709\u51e0\u6b65\u8def\uff0c\u521b\u4e1a\u56ed\u533a\u4e0e\u5e08\u5927\u51e0\u4e4e\u662f\u95e8\u6328\u7740\u6328\u95e8\uff0c\u8dd1\u8fc7\u53bb\u624d\u51e0\u767e\u7c73\u7684\u8def\u3002</p><p>\u3000\u3000\u5ba4\u5916\u7684\u5929\u6c14\u4f9d\u65e7\u708e\u70ed\u4e0d\u582a\uff0c\u5f20\u606a\u770b\u5230\u5c0f\u53d4\u4ed6\u4eec\u966a\u7740\u7238\u5988\u5728\u56ed\u533a\u7684\u53c2\u89c2\uff0c\u8d70\u8fc7\u53bb\u542c\u5230\u4ed6\u4eec\u6b63\u8bf4\u8ba9\u4eba\u53bb\u627e\u4e00\u6761\u8239\u6765\u8f7d\u7740\u5927\u5bb6\u5230\u71d5\u5f52\u6e56\u4e0a\u653e\u821f\u9010\u6c34\u3002</p><p>\u3000\u3000\u8fd9\u5012\u662f\u597d\u5efa\u8bae\uff0c\u590f\u65e5\u708e\u708e\uff0c\u54ea\u91cc\u90fd\u4e0d\u53ca\u653e\u821f\u6e56\u6c34\u89e3\u6691\u3002</p><p>\u3000\u3000\u71d5\u5f52\u6e56\u662f\u6d3b\u6c34\uff0c\u6709\u6cb3\u4e0e\u5c0f\u6c5f\u6c9f\u901a\uff0c\u91cd\u6c61\u67d3\u6e90\u5f7b\u5e95\u5207\u65ad\u4e4b\u540e\u3001\u5e02\u653f\u6295\u5165\u4e00\u90e8\u5206\u4eba\u529b\u8d22\u529b\u8fdb\u884c\u6e05\u6c61\u6574\u6cbb\uff0c\u53c8\u7ecf\u8fc7\u6574\u4e2a\u96e8\u5b63\u7684\u51b2\u5237\uff0c\u6e56\u6c34\u5df2\u7136\u6e05\u6f88\u8d77\u6765\uff0c\u4e0d\u8fc7\u6e56\u5e95\u79ef\u6de4\u9644\u7740\u7684\u6c61\u67d3\u7269\u8d28\uff0c\u8fd8\u9700\u8981\u76f8\u5f53\u7684\u65f6\u95f4\u624d\u80fd\u81ea\u7136\u51c0\u5316\uff0c\u4e0d\u8fc7\u5012\u4e0d\u5f71\u54cd\u6b64\u65f6\u653e\u821f\u7684\u5fc3\u60c5\u3002</p><p>\u3000\u3000\u5750\u5e26\u906e\u9633\u9876\u84ec\u7684\u753b\u822b\u5f0f\u6e38\u8239\u9a76\u5230\u6e56\u4e2d\u5fc3\uff0c\u5efa\u90ba\u5e02\u6570\u5b57\u957f\u5eca\u6784\u60f3\uff0c\u4ee5\u6a61\u6811\u56ed\u8ba1\u5212\u4e3a\u6838\u5fc3\u7684\u71d5\u5f52\u6e56\u533a\u57df\u4e3a\u53d1\u5c55\u7684\u91cd\u5fc3\uff0c\u9a76\u5230\u6e56\u4e2d\u5fc3\uff0c\u5012\u662f\u66f4\u80fd\u4f53\u4f1a\u89c4\u5212\u5b8f\u56fe\uff0c\u542f\u52a8\u8fc4\u4eca\u624d\u5efa\u6210\u521b\u4e1a\u56ed\u533a\u7684\u4e00\u534a\uff0c\u5f20\u77e5\u884c\u95ee\u5f20\u606a\uff1a\u201c\u6709\u6ca1\u6709\u4ec0\u4e48\u4f01\u4e1a\u8fdb\u56ed\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u542c\u8bf4\u7533\u8bf7\u4e66\u5df2\u7ecf\u5806\u6ee1\u6848\u5934\u4e86\uff0c\u5ba1\u5bdf\u901a\u8fc7\u7684\u60c5\u51b5\uff0c\u6211\u8fd8\u6ca1\u6709\u5173\u5fc3\u8fc7\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u90a3\u4e9b\u6709\u4ef7\u503c\u7684\u5546\u4e1a\u8ba1\u5212\u3001\u521b\u4e1a\u65b9\u6848\uff0c\u901a\u8bfb\u4e00\u904d\u5c31\u8981\u82b1\u6389\u4e00\u4e2a\u4eba\u4e00\u5929\u7684\u65f6\u95f4\u3002\u8fd8\u8981\u4e3a\u65b9\u6848\u63d0\u4ea4\u4eba\u4e25\u683c\u4fdd\u5b88\u5546\u4e1a\u673a\u5bc6\uff0c\u8fd9\u4e25\u91cd\u9650\u5236\u5ba1\u5bdf\u7684\u8fdb\u5ea6\uff0c\u7b2c\u4e00\u6279\u8fdb\u56ed\u7684\u4f01\u4e1a\u80fd\u5728\u5341\u6708\u8fd9\u524d\u786e\u5b9a\u4e0b\u6765\uff0c\u5c31\u7b97\u4e0d\u9519\u4e86\uff0c\u8fd9\u65f6\u5019\u554a\uff0c\u6b63\u5f0f\u63ed\u724c\u8fd8\u6ca1\u6709\u5341\u5929\u7684\u5de5\u592b\u5462\uff0c\u4e0d\u7126\u6025\u2026\u2026\u201d</p><p>\u3000\u3000\u6881\u683c\u73cd\u62b1\u6028\u9053\uff1a\u201c\u90fd\u8bf4\u597d\u597d\u6b47\u51e0\u5929\uff0c\u6b47\u4e86\u6709\u4e00\u5929\uff0c\u53c8\u8c08\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u4f60\u4eec\u7237\u4fe9\u5230\u5e95\u7d2f\u4e0d\u7d2f\u554a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u9a6c\u4e0a\u95ed\u5634\uff0c\u603b\u4e4b\u4ed6\u53c8\u6ca1\u6709\u4ec0\u4e48\u597d\u5947\u5fc3\u3002</p><p>\u3000\u3000\u201c\u90a3\u8fb9\u5efa\u4ec0\u4e48\uff1f\u201d\u76db\u590f\u6307\u7740\u521b\u4e1a\u56ed\u533a\u897f\u533a\u4e34\u6e56\u7684\u4e00\u5904\u5728\u5efa\u5efa\u7b51\uff0c\u521b\u4e1a\u56ed\u897f\u533a\u662f\u5229\u7528\u539f\u6709\u7684\u6f02\u67d3\u5382\u5382\u623f\u6539\u5efa\uff0c\u90a3\u5904\u5efa\u7b51\u5374\u662f\u5b8c\u5168\u65b0\u5efa\u7684\u6837\u5b50\uff0c\u5728\u6e56\u8fb9\u8fd8\u6709\u6c34\u53f0\uff0c\u76db\u590f\u8bf4\u9053\uff0c\u201c\u597d\u50cf\u662f\u5ea7\u522b\u5885\u5462\uff0c\u600e\u4e48\u53ea\u5efa\u4e00\u5ea7\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5f20\u606a\u8116\u5b50\u6709\u4e9b\u50f5\u786c\uff0c\u8f6c\u8fc7\u5934\u95ee\u90b5\u81f3\u521a\uff0c\u201c\u90a3\u91cc\u5efa\u4ec0\u4e48\uff0c\u770b\u8bbe\u8ba1\u56fe\uff0c\u90fd\u6ca1\u6709\u6ce8\u610f\u8fd9\u89d2\u843d\u4e0a\u8fd8\u6709\u4e00\u5904\u8fd9\u4e48\u5c0f\u7684\u8bbe\u8ba1\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u9ad8\u5c42\u4e3b\u7ba1\u7684\u5bbf\u820d\u533a\uff0c\u6cbf\u6e56\u8fb9\u8fd8\u8981\u518d\u5efa\u51e0\u680b\u522b\u5885\u2026\u2026\u201d\u90b5\u81f3\u521a\u53cd\u5e94\u591f\u8fc5\u901f\uff0c\u201c\u606a\u5c11\u559c\u6b22\u7684\u8bdd\uff0c\u7ed9\u4f60\u7559\u4e00\u680b\uff1f\u201d</p><p>\u3000\u3000\u201c\u7b97\u4e86\uff0c\u79bb\u5b66\u6821\u592a\u8fdc\uff0c\u6ca1\u6709\u8dd1\u6765\u8dd1\u53bb\u7684\u5de5\u592b\u3002\u201d\u5f20\u606a\u6487\u7740\u5634\uff0c\u90a3\u6e56\u7554\u79bb\u4e1c\u5927\u5357\u95e8\u4e5f\u5c31\u4e00\u516c\u91cc\u7684\u6837\u5b50\uff0c\u9a91\u81ea\u884c\u8f66\uff0c\u600e\u4e48\u90fd\u4e0d\u4f1a\u89c9\u5f97\u8fdc\uff0c\u4ed6\u662f\u7741\u7740\u773c\u775b\u8bf4\u778e\u8bdd\u3002</p><p>\u3000\u3000\u8c01\u5efa\u8bae\u5230\u71d5\u5f52\u6e56\u4e0a\u6765\u653e\u821f\u9010\u6c34\u7684\uff1f\u771f\u662f\u4e00\u4e2a\u7cdf\u7cd5\u900f\u9876\u7684\u4e3b\u610f\u3002</p></article>"}}}]);
