(window.webpackJsonp=window.webpackJsonp||[]).push([[777],{800:function(p){p.exports={chapterTitle:null,title:"\u7b2c776\u7ae0 \u8981\u52a8\u624b",content:"<article><h2>\u7b2c776\u7ae0 \u8981\u52a8\u624b</h2><p>\u3000\u3000\u9999\u6e2f\u7684\u65b0\u95fb\u5a92\u4f53\u5927\u6982\u662f\u6700\u4e0d\u6015\u60f9\u5b98\u53f8\u7684\u5a92\u4f53\u4e86\uff0c\u53ea\u8981\u7206\u6599\u80fd\u5438\u5f15\u5e02\u6c11\u7684\u773c\u7403\u3001\u589e\u52a0\u62a5\u7eb8\u6216\u6742\u5fd7\u7684\u9500\u91cf\uff0c\u627e\u5230\u613f\u610f\u62a5\u9053\u201c\u845b\u6c0f\u5609\u4fe1\u96c6\u56e2\u7ee7\u627f\u4eba\u845b\u836b\u5747\u5371\u9669\u9a7e\u8f66\u81f4\u4eba\u6b7b\u4ea1\u3001\u4e8b\u540e\u53c8\u6781\u529b\u63a8\u8d23\u201d\u7684\u65b0\u95fb\u7684\u5a92\u4f53\u8bb0\u8005\u975e\u4f46\u5e76\u4e0d\u96be\uff0c\u8fd8\u5927\u6709\u4eba\u5728\u3002</p><p>\u3000\u3000\u867d\u7136\u5609\u4fe1\u96c6\u56e2\u5728\u9999\u6e2f\u4e1a\u5927\u52bf\u5927\uff0c\u5b59\u5c1a\u4e49\u5728\u9999\u6e2f\u4e5f\u975e\u6beb\u65e0\u5f71\u54cd\u529b\u4e4b\u4eba\uff0c\u7279\u522b\u662f\u8d8a\u79c0\u63a7\u80a1\u8fd1\u5e74\u6765\u8fde\u7eed\u5728\u5185\u5730\u6295\u8d44\u5de8\u989d\u3001\u6709\u6781\u5927\u5f71\u54cd\u529b\u7684\u9879\u76ee\uff0c\u5728\u9999\u6e2f\u7684\u58f0\u52bf\u5df2\u7ecf\u9690\u7136\u8ffd\u8d76\u4e0a\u845b\u6c0f\u5609\u4fe1\u96c6\u56e2\uff0c\u5b59\u5c1a\u4e49\u662f\u8d8a\u79c0\u63a7\u80a1\u7684\u8463\u4e8b\u957f\uff0c\u662f\u5728\u5a92\u4f53\u4e0e\u516c\u4f17\u9762\u524d\u4ee3\u8868\u8d8a\u79c0\u63a7\u80a1\u7684\u4eba\u7269\u3002\u4e0d\u7136\u5b59\u9759\u6aac\u521a\u624d\u62ff\u624b\u673a\u7838\u845b\u660e\u4fe1\u7684\u5f8b\u5e08\uff0c\u8b66\u5bdf\u600e\u4e48\u53ef\u80fd\u88c5\u4f5c\u6ca1\u770b\u89c1\u5462\uff1f</p><p>\u3000\u3000\u8b66\u5bdf\u5bf9\u4e8b\u6545\u7684\u52d8\u5b9a\u4ee5\u53ca\u6536\u96c6\u5230\u7684\u8bc1\u636e\u3001\u8bc1\u4eba\u7684\u8bc1\u8a00\uff0c\u770b\u4e0a\u53bb\u90fd\u50cf\u662f\u7d27\u6025\u907f\u9669\u6240\u5bfc\u81f4\u7684\u8f66\u7978\u4e8b\u6545\u3002\u5c31\u7b97\u5f20\u606a\uff0c\u8981\u4e0d\u662f\u4ed6\u770b\u5230\u4ece\u845b\u836b\u5747\u8f66\u7684\u9648\u9a90\u5728\u8f66\u7978\u53d1\u751f\u540e\u7684\u53cd\u5e94\uff0c\u4e5f\u4ee5\u4e3a\u845b\u836b\u5747\u5728\u5f2f\u9053\u5904\u7684\u6025\u62d0\u662f\u7d27\u6025\u907f\u9669\uff0c\u8fd9\u65f6\u5019\u662f\u6ca1\u6709\u7406\u7531\u4e0e\u8bc1\u636e\u8981\u6c42\u8b66\u65b9\u62d8\u6355\u4eba\u7684\uff0c\u5373\u4f7f\u95f9\u4e0a\u6cd5\u5ead\uff0c\u53cc\u65b9\u90fd\u6709\u80fd\u529b\u8058\u8bf7\u6709\u80fd\u529b\u7684\u5f8b\u5e08\uff0c\u6700\u7ec8\u7684\u7ed3\u679c\u90fd\u4e0d\u4f1a\u8ba9\u4eba\u6ee1\u610f\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u7684\u79c1\u4eba\u5f8b\u5e08\u6070\u597d\u4e0d\u5728\u9999\u6e2f\uff0c\u65e0\u6cd5\u8d76\u8fc7\u6765\uff0c\u901a\u8fc7\u7535\u8bdd\u8054\u7cfb\uff0c\u5b59\u5c1a\u4e49\u7684\u5f8b\u5e08\u5bf9\u8fd9\u5b98\u53f8\u4e5f\u76f8\u5f53\u4e0d\u770b\u597d\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6b21\u4e0d\u60f3\u8ba9\u4e8b\u60c5\u7684\u771f\u76f8\u6e6e\u706d\u5728\u6e38\u620f\u89c4\u5219\u4e4b\u4e2d\uff0c\u9664\u4e86\u901a\u8fc7\u6cd5\u5f8b\u7a0b\u5e8f\u4e4b\u5916\uff0c\u8fd8\u6709\u8bb8\u591a\u9014\u5f84\u80fd\u8ba9\u845b\u836b\u5747\u53d7\u5230\u5e94\u6709\u7684\u60e9\u7f5a\u3002</p><p>\u3000\u3000\u845b\u5bb6\u4e60\u60ef\u7528\u94b1\u89e3\u51b3\u4e00\u5207\u95ee\u9898\uff0c\u845b\u660e\u4fe1\u7684\u91d1\u94b1\u653b\u52bf\u8ba9\u73e0\u5ac2\u7684\u513f\u5b50\u9887\u4e3a\u52a8\u5fc3\uff0c\u5b59\u5c1a\u4e49\u4e0e\u5f20\u606a\u90fd\u770b\u5728\u773c\u91cc\u3002\u5b59\u5c1a\u4e49\u53ef\u4ee5\u627e\u6765\u5a92\u4f53\u66b4\u5149\u8fd9\u4e8b\uff0c\u4f46\u662f\u6ca1\u6709\u73e0\u5ac2\u513f\u5973\u7684\u914d\u5408\uff0c\u62a5\u9053\u7684\u529b\u5ea6\u4e0d\u4f1a\u5927\uff0c\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u4ed6\u4eec\u751a\u81f3\u90fd\u4e0d\u9700\u8981\u53cd\u51fb\uff0c\u8fd9\u6837\u7684\u62a5\u9053\u5c31\u4f1a\u7ed9\u6df9\u6ca1\u5728\u9999\u6e2f\u6bcf\u5929\u90fd\u4f1a\u6f2b\u5929\u98de\u4f20\u7684\u516b\u5366\u8c23\u8a00\u4e4b\u4e2d\uff0c\u4e0d\u4f1a\u5f62\u6210\u591a\u5927\u7684\u5f71\u54cd\u529b\uff0c\u5bf9\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u4e5f\u4e0d\u4f1a\u4ea7\u751f\u591a\u5927\u7684\u89e6\u52a8\u3002\u603b\u4e0d\u53ef\u80fd\u8ba9\u5f20\u606a\u7684\u540d\u5b57\u5728\u9999\u6e2f\u7684\u5a92\u4f53\u4e0a\u66b4\u5149\uff0c\u5b59\u5c1a\u4e49\u53ea\u80fd\u53bb\u627e\u73e0\u5ac2\u7684\u5973\u513f\u5546\u91cf\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u518d\u8d70\u56de\u53bb\uff0c\u845b\u660e\u4fe1\u7684\u5f8b\u5e08\u4e1a\u5df2\u79bb\u5f00\uff0c\u5c31\u7b97\u73e0\u5ac2\u7684\u513f\u5b50\u771f\u8d2a\u845b\u660e\u4fe1\u53ef\u80fd\u7ed9\u51fa\u7684\u5de8\u989d\u8d54\u507f\uff0c\u90fd\u4e0d\u53ef\u80fd\u60d8\u987e\u4eba\u4f26\u9053\u4e49\u8fd9\u65f6\u5019\u53bb\u8ddf\u845b\u660e\u4fe1\u7236\u5b50\u548c\u89e3\u53bb\uff0c\u518d\u8bf4\u548c\u89e3\u9700\u8981\u73e0\u5ac2\u7684\u513f\u5b50\u3001\u5973\u513f\u540c\u65f6\u8ba4\u53ef\u624d\u80fd\u751f\u6548\u3002</p><p>\u3000\u3000\u5373\u4f7f\u73e0\u5ac2\u7684\u513f\u5b50\u3001\u5973\u513f\u90fd\u540c\u610f\u548c\u89e3\uff0c\u5f20\u606a\u4e5f\u4e0d\u4f1a\u4efb\u8fd9\u4ef6\u4e8b\u5c31\u8fd9\u6837\u8fc7\u53bb\u3002\u5bf9\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u4e0d\u5e94\u8be5\u518d\u624b\u4e0b\u7559\u60c5\u4e86\uff0c\u5de8\u989d\u7684\u8d22\u5bcc\u638c\u63e1\u5728\u8fd9\u4e9b\u4eba\u7684\u624b\u4e2d\uff0c\u5bf9\u793e\u4f1a\u5e76\u65e0\u88e8\u76ca\u3002\u5f20\u606a\u4ece\u4e0d\u8ba4\u4e3a\u81ea\u5df1\u4f1a\u662f\u6551\u4e16\u4e3b\uff0c\u4f46\u662f\u5bf9\u770b\u4e0d\u987a\u773c\u7684\u4eba\u3001\u81ea\u59cb\u81f3\u7ec8\u90fd\u6709\u6069\u6028\u7684\u4eba\uff0c\u5c31\u6ca1\u6709\u5fc5\u8981\u518d\u505a\u6e05\u9ad8\u59ff\u6001\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8003\u8651\u7740\u8981\u63a8\u8fdf\u660e\u5929\u53bb\u5317\u4eac\u7684\u822a\u73ed\uff0c\u5b59\u5c1a\u4e49\u8bf4\u9053\uff1a\u201c\u4f60\u660e\u5929\u7ea6\u4e86\u90ae\u7535\u90e8\u7684\u6613\u4e91\u98de\u90e8\u957f\u89c1\u9762\uff0c\u4e0d\u8981\u63a8\u5ef6\u4e86\u3002\u773c\u4e0b\u9700\u8981\u505a\u7684\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u6211\u6765\u5b89\u6392\u5c31\u662f\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u60f3\u60f3\u4e5f\u662f\uff0c\u5230\u4ed6\u4eec\u8fd9\u5c42\u6b21\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u4e0d\u9700\u8981\u4eb2\u81ea\u51fa\u9762\uff0c\u65e0\u8bba\u4f55\u65f6\u4f55\u5730\u90fd\u53ef\u4ee5\u7528\u7535\u8bdd\u3001\u90ae\u4ef6\u8054\u7cfb\u3001\u8eb2\u5728\u5e55\u540e\u7b56\u5212\u6307\u6325\uff0c\u7559\u5728\u9999\u6e2f\u4e5f\u662f\u8003\u8651\u5b59\u9759\u6aac\u7684\u611f\u53d7\uff0c\u4f46\u662f\u60f3\u4e86\u60f3\uff0c\u53c8\u6ca1\u6709\u7acb\u573a\u975e\u8981\u7559\u5728\u9999\u6e2f\u4e0d\u53ef\u2014\u2014\u53bb\u5317\u4eac\u53bb\u8ddf\u6613\u4e91\u98de\u89c1\u9762\uff0c\u4e5f\u662f\u4e00\u4ef6\u91cd\u8981\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u5230\u6b93\u5c38\u95f4\u9694\u58c1\u7684\u623f\u95f4\uff0c\u845b\u660e\u73cf\u3001\u5b59\u9759\u9999\u3001\u5510\u5a67\u966a\u5b59\u9759\u6aac\u5728\u90a3\u91cc\u3002\u770b\u5230\u5f20\u606a\u8d70\u8fdb\u6765\uff0c\u5b59\u9759\u6aac\u6cea\u773c\u5a46\u5a11\u62ac\u773c\u770b\u7740\u4ed6\uff0c\u795e\u8272\u51c4\u695a\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u5979\u6709\u6bd4\u73e0\u5ac2\u5973\u513f\u3001\u513f\u5b50\u66f4\u5267\u70c8\u7684\u75db\u4e0e\u4f24\u5fc3\uff0c\u5fc3\u60f3\u6216\u8bb8\u6ca1\u6709\u4eba\u4e86\u89e3\u5979\u5e7c\u65f6\u968f\u7236\u6bcd\u521a\u5230\u9999\u6e2f\u65f6\u7684\u5b64\u5355\u4e0e\u695a\u695a\u53ef\u601c\u5427\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u53ea\u8981\u845b\u836b\u5747\u6ca1\u6709\u53d7\u5230\u5e94\u6709\u7684\u60e9\u7f5a\uff0c\u6211\u90fd\u4e0d\u4f1a\u653e\u624b\u4e0d\u7ba1\u7684\uff0c\u201d\u5f20\u606a\u5750\u5230\u5b59\u9759\u6aac\u7684\u5bf9\u9762\uff0c\u5fc3\u60f3\u8fd9\u6837\u7684\u8bdd\u5927\u6982\u8fd9\u65f6\u5019\u6700\u80fd\u5b89\u6170\u5979\u4e86\uff0c\u201c\u65e2\u7136\u73e0\u5ac2\u7684\u5bb6\u4eba\u90fd\u5728\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u5148\u56de\u53bb\u5546\u91cf\u4e00\u4e0b\uff0c\u770b\u600e\u4e48\u8ba8\u56de\u516c\u9053\u5408\u9002\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6e05\u695a\u5b59\u9759\u6aac\u7684\u6027\u683c\uff0c\u5979\u5373\u4f7f\u4f24\u5fc3\u6b32\u7edd\uff0c\u4e5f\u4f1a\u8ba4\u4e3a\u4e0e\u5176\u65e0\u8c13\u7684\u6c89\u6eba\u4e8e\u75db\u82e6\uff0c\u8fd8\u4e0d\u5982\u79ef\u6781\u7684\u8ba8\u56de\u516c\u9053\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8fd8\u662f\u545c\u54bd\u7740\uff0c\u8138\u988a\u4e0a\u7684\u6cea\u75d5\u672a\u6d88\uff0c\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5979\u59d0\u4e0e\u5510\u5a67\u6276\u5979\uff0c\u6ca1\u6709\u62d2\u7edd\uff0c\u7ad9\u4e86\u8d77\u6765\u5f80\u5916\u8d70\u3002</p><p>\u3000\u3000\u533b\u9662\u5916\u7684\u505c\u8f66\u573a\uff0c\u90a3\u4e24\u90e8\u4e8b\u540e\u51fa\u73b0\u5728\u8f66\u7978\u73b0\u573a\u7684\u5954\u9a70\u8f7f\u8f66\u8fd8\u505c\u5728\u90a3\u91cc\uff0c\u770b\u5230\u5f20\u606a\u4ed6\u4eec\u8d70\u51fa\u6765\uff0c\u524d\u9762\u90a3\u90e8\u8f7f\u8f66\u7684\u8f66\u95e8\u6253\u5f00\uff0c\u5609\u4fe1\u96c6\u56e2\u8463\u4e8b\u5c40\u4e3b\u5e2d\u845b\u660e\u4fe1\u4ece\u8f66\u91cc\u4e0b\u6765\uff0c\u6709\u4e24\u540d\u968f\u884c\u4eba\u5458\u8ddf\u7740\u4e0b\u6765\uff0c\u5176\u4ed6\u4eba\u90fd\u5750\u5728\u8f66\u91cc\u4e0d\u52a8\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e0e\u5b59\u9759\u9999\u5979\u4eec\u6400\u7740\u9759\u6aac\u7ee7\u7eed\u5f80\u4ed6\u4eec\u505c\u8f66\u7684\u5730\u65b9\u8d70\u53bb\uff0c\u6ca1\u6709\u505c\u987f\u3002\u5f20\u606a\u505c\u4e0b\u6765\uff0c\u62ac\u5934\u770b\u4e86\u4e00\u773c\u6697\u84dd\u8272\u7684\u591c\u7a7a\uff0c\u65b0\u6708\u5982\u94a9\u60ac\u6302\u5728\u8fdc\u5904\u68d5\u6988\u6811\u7684\u6797\u68a2\u3002</p><p>\u3000\u3000\u201c\u5728\u8fd9\u79cd\u5c0f\u4e8b\u60c5\u4e0a\u7ea0\u7f20\u4e0b\u53bb\uff0c\u4e0d\u77e5\u9053\u5bf9\u6211\u4eec\u53cc\u65b9\u6709\u4ec0\u4e48\u597d\u5904\uff1f\u201d\u845b\u660e\u4fe1\u8d70\u8fc7\u6765\u770b\u4e86\u4e00\u773c\u5b59\u5c1a\u4e49\u4e4b\u540e\u5c31\u5c06\u76ee\u5149\u505c\u5728\u5f20\u606a\u7684\u8138\u4e0a\uff0c\u201c\u60f3\u5fc5\u9526\u6e56\u4e0e\u8d8a\u79c0\u4e0d\u4f1a\u7f3a\u6cd5\u5f8b\u4e8b\u52a1\u4e0a\u7684\u987e\u95ee\uff0c\u8bf7\u95ee\u4f60\uff0c\u7ea0\u7f20\u4e0b\u53bb\u6709\u4ec0\u4e48\u610f\u4e49\uff1f\u201d</p><p>\u3000\u3000\u201c\u65e2\u7136\u5728\u6cd5\u5f8b\u4e0a\u4f60\u90fd\u6ca1\u6709\u4ec0\u4e48\u62c5\u5fc3\u7684\uff0c\u5e72\u561b\u7ad9\u5728\u8fd9\u91cc\uff1f\u201d\u5f20\u606a\u76ee\u5149\u51b7\u5cfb\u7684\u770b\u7740\u845b\u660e\u4fe1\uff0c\u5634\u89d2\u5374\u6302\u7740\u51b7\u7b11\uff0c\u201c\u4f60\u4e8f\u5fc3\u4e86\uff0c\u4f60\u5bb3\u6015\u6211\u4e86\uff01\u201d\u8f7b\u8511\u7684\u4e22\u4e0b\u4e00\u53e5\u8bdd\u5c31\u4e0e\u5b59\u5c1a\u4e49\u8f6c\u8eab\u79bb\u5f00\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u542c\u4e86\u4e00\u6014\uff0c\u4ed6\u4e0e\u5f20\u606a\u63a5\u89e6\u4e0d\u591a\uff0c\u4f46\u662f\u8fd9\u4e0d\u591a\u7684\u63a5\u89e6\u7ed9\u4ed6\u7559\u4e0b\u6df1\u523b\u7684\u5370\u8c61\uff0c\u518d\u8bf4\u5e9e\u5927\u7684\u9526\u6e56\u5c31\u6a2a\u4e98\u5728\u4ed6\u7684\u773c\u524d\u3002\u845b\u660e\u4fe1\u518d\u72c2\u5984\u81ea\u5927\uff0c\u8fd9\u65f6\u5019\u4e5f\u4e0d\u4f1a\u8ba4\u4e3a\u845b\u5bb6\u56db\u5206\u4e94\u88c2\u4e4b\u540e\u7684\u5609\u4fe1\u96c6\u56e2\u8fd8\u6bd4\u9526\u6e56\u66f4\u5f3a\u5927\u3001\u66f4\u6709\u6f5c\u529b\u3002\u4ed6\u5f53\u7136\u4e5f\u4e0d\u4f1a\u627f\u8ba4\u4ed6\u662f\u5728\u5bb3\u6015\u9526\u6e56\u3001\u5bb3\u6015\u7f14\u9020\u9526\u6e56\u7684\u8fd9\u4e2a\u9752\u5e74\uff0c\u4f46\u662f\u8fd9\u53e5\u8bdd\u4ece\u5f20\u606a\u5634\u91cc\u8f7b\u8511\u7684\u5410\u51fa\u6765\uff0c\u4ed6\u7684\u5fc3\u810f\u731b\u7684\u4e00\u6536\uff0c\u6709\u79cd\u7ed9\u523a\u4e2d\u8981\u5bb3\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5f85\u5f20\u606a\u4e0e\u5b59\u5c1a\u4e49\u5728\u5085\u4fca\u53ca\u968f\u884c\u4fdd\u9556\u7684\u73af\u62a4\u4e0b\u8d70\u51fa\u5341\u591a\u6b65\uff0c\u845b\u660e\u4fe1\u624d\u8272\u5389\u5185\u834f\u7684\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u6211\u90fd\u4e0d\u4f1a\u5141\u8bb8\u522b\u4eba\u5bf9\u836b\u5747\u4e0d\u5229\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8f6c\u56de\u5934\uff0c\u5634\u89d2\u9732\u51fa\u9b54\u9b3c\u822c\u90aa\u6076\u7684\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u8bf4\u8fd9\u79cd\u81ea\u6b3a\u6b3a\u4eba\u7684\u8bdd\u4e86\uff0c\u4f60\u77e5\u9053\u4f60\u81ea\u5df1\u5728\u62c5\u5fc3\u4ec0\u4e48\uff1f\u653e\u5fc3\u5427\uff0c\u6211\u4e0d\u4f1a\u8ba9\u4f60\u5931\u671b\u7684\u3002\u201d</p><p>\u3000\u3000\u845b\u660e\u4fe1\u542c\u4e86\u5f20\u606a\u8fd9\u8bdd\uff0c\u5fc3\u91cc\u6c14\u5f97\u6709\u5982\u706b\u711a\uff0c\u4f46\u5728\u968f\u884c\u7684\u4e0b\u5c5e\u9762\u524d\uff0c\u4ed6\u53c8\u4e0d\u5f97\u4e0d\u4fdd\u6301\u6de1\u5b9a\u51b7\u9759\uff0c\u8138\u8272\u9634\u6c89\u7684\u94bb\u56de\u8f66\u91cc\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u8c8c\u4f3c\u5e73\u606f\u4e86\uff0c\u4f46\u662f\u4e00\u5207\u7684\u8bc1\u636e\u3001\u6570\u636e\u90fd\u6307\u51fa\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5bf9\u5b9e\u4f53\u7ecf\u6d4e\u635f\u5bb3\u7684\u5f71\u54cd\u8fd8\u6ca1\u6709\u5f7b\u5e95\u7684\u66b4\u9732\u51fa\u6765\uff0c\u201d\u5750\u8fdb\u8f66\u91cc\uff0c\u5f20\u606a\u8ddf\u5b59\u5c1a\u4e49\u8bf4\u9053\uff0c\u201c\u4e00\u65e6\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5bf9\u5b9e\u4f53\u7ecf\u6d4e\u635f\u5bb3\u7684\u5f71\u54cd\u8868\u73b0\u51fa\u6765\uff0c\u4f1a\u53cd\u8fc7\u6765\u518d\u8fdb\u4e00\u6b65\u7684\u4fc3\u4f7f\u91d1\u878d\u5e02\u573a\u7684\u518d\u5ea6\u5267\u70c8\u52a8\u8361\uff0c\u53ea\u662f\u5177\u4f53\u7684\u65f6\u95f4\u5f88\u96be\u9884\u4f30\uff0c\u5e73\u9759\u4e86\u4e00\u6bb5\u65f6\u95f4\u7684\u91d1\u878d\u5de8\u9cc4\u4eec\u53c8\u5f00\u59cb\u8981\u8822\u8822\u6b32\u52a8\u3002\u8fd9\u6b21\u53bb\u5317\u4eac\uff0c\u6253\u7b97\u6709\u65f6\u95f4\u5c31\u627e\u53f6\u4e8c\u53d4\u8ba8\u8bba\u8fd9\u4e9b\u95ee\u9898\uff0c\u5b59\u5148\u751f\u4e0e\u6211\u4e00\u8d77\u53bb\u5317\u4eac\u5427\uff0c\u6211\u4eec\u8981\u5bf9\u5609\u4fe1\u5730\u4ea7\u3001\u5609\u4fe1\u7535\u5b50\u4e0b\u624b\uff0c\u5373\u5c06\u5230\u6765\u7684\u91d1\u878d\u5e02\u573a\u52a8\u8361\u5c06\u662f\u6211\u4eec\u7684\u826f\u673a\uff0c\u8fd9\u4e2a\u8981\u597d\u597d\u7814\u7a76\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u8fd9\u624d\u77e5\u9053\u5f20\u606a\u521a\u624d\u5bf9\u845b\u660e\u4fe1\u8bf4\u7684\u8bdd\uff0c\u5e76\u4e0d\u662f\u8bf4\u573a\u9762\u8bdd\uff0c\u800c\u771f\u51b3\u5fc3\u5bf9\u5609\u4fe1\u96c6\u56e2\u4e0b\u624b\u4e86\uff0c\u62c5\u5fe7\u7684\u95ee\u9053\uff1a\u201c\u4f1a\u4e0d\u4f1a\u5bf9\u9526\u6e56\u7684\u53d1\u5c55\u6709\u8d1f\u9762\u5f71\u54cd\uff1f\u201d</p><p>\u3000\u3000\u201c\u95ee\u9898\u4e0d\u5927\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5c31\u7b97\u6709\u4e9b\u5f71\u54cd\uff0c\u4e5f\u662f\u53ef\u4ee5\u627f\u53d7\u7684\u3002\u6211\u4eec\u8fd9\u6b21\u7684\u76ee\u6807\u5c31\u662f\u8981\u5c3d\u53ef\u80fd\u4ee5\u5c0f\u7684\u4ee3\u4ef7\u5728\u5609\u4fe1\u5730\u4ea7\u3001\u5609\u4fe1\u7535\u5b50\u53d6\u5f97\u8bdd\u8bed\u6743\uff0c\u8fd9\u4e5f\u5c06\u9650\u5236\u8c22\u5bb6\u3001\u845b\u660e\u4fe1\u4ed6\u4eec\u5229\u7528\u5609\u4fe1\u5730\u4ea7\u3001\u5609\u4fe1\u7535\u5b50\u6765\u5bf9\u6297\u9526\u6e56\u3002\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u77e5\u9053\u5f20\u606a\u4ece\u6765\u90fd\u4e0d\u4f1a\u4ecb\u610f\u9526\u6e56\u6709\u7ade\u4e89\u8005\u51fa\u73b0\uff0c\u751a\u81f3\u6c14\u5ea6\u5927\u5230\u4f1a\u53bb\u9f13\u52b1\u3001\u6276\u6301\u3001\u57f9\u517b\u672a\u6765\u7ade\u4e89\u8005\uff0c\u8fd9\u90fd\u662f\u4e0e\u5f20\u606a\u5927\u683c\u5c40\u7684\u7406\u5ff5\u6709\u5173\u3002\u56fd\u5185\u6ca1\u6709\u5f62\u6210\u4ea7\u4e1a\u7684\u5927\u683c\u5c40\uff0c\u5bf9\u9526\u6e56\u7684\u957f\u8fdc\u53d1\u5c55\u4e5f\u662f\u4e0d\u5229\u7684\u3002\u4f46\u662f\u4e0e\u8c22\u5bb6\u3001\u845b\u660e\u4fe1\u7684\u6069\u6028\u7531\u6765\u5df2\u4e45\uff0c\u8fd9\u79cd\u6069\u6028\u4e0d\u4ec5\u4ec5\u662f\u7ade\u4e89\uff0c\u800c\u662f\u4e00\u79cd\u76f4\u63a5\u7684\u5bf9\u6297\uff0c\u867d\u8bf4\u8c22\u5bb6\u4e00\u76f4\u7ed9\u5f20\u606a\u538b\u5236\u7740\u7ffb\u4e0d\u4e86\u8eab\uff0c\u4f46\u662f\u968f\u7740\u8c22\u5bb6\u4e0e\u5609\u4fe1\u96c6\u56e2\u7684\u8fdb\u4e00\u6b65\u878d\u5408\uff0c\u4e0e\u4e09\u661f\u7b49\u6d77\u5916\u7535\u5b50\u5de8\u5934\u7684\u8054\u5408\u8fdb\u4e00\u6b65\u52a0\u5f3a\uff0c\u90a3\u5c31\u6709\u53ef\u80fd\u5bf9\u9526\u6e56\u5f62\u6210\u4e00\u5b9a\u7684\u5a01\u80c1\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u70b9\u70b9\u8bf4\u9053\uff1a\u201c\u4ee5\u6b64\u4e3a\u76ee\u7684\u7684\u5e03\u5c40\uff0c\u4e5f\u662f\u5408\u9002\u7684\u3002\u201d\u4ed6\u4e0e\u845b\u660e\u4fe1\u7684\u6069\u6028\u4e5f\u662f\u7531\u6765\u5df2\u4e45\u7684\uff0c\u5230\u9999\u6e2f\u540e\u5728\u5609\u4fe1\u96c6\u56e2\u5185\u90e8\u53d7\u845b\u660e\u4fe1\u7684\u538b\u5236\u4e0e\u6392\u6324\uff0c\u751a\u81f3\u8fd8\u4f1a\u53d7\u5230\u8a00\u8bed\u4e0a\u7684\u8f7b\u8fb1\uff0c\u5cb3\u7236\u901d\u4e16\u540e\u5206\u914d\u9057\u4ea7\uff0c\u5bb6\u65cf\u77db\u76fe\u5f7b\u5e95\u66b4\u53d1\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u5411\u8f66\u7a97\u5916\uff0c\u5510\u5a67\u5979\u4eec\u4e0e\u5b59\u9759\u6aac\u5750\u5728\u5e76\u884c\u7684\u8f66\u4e0a\uff0c\u90a3\u8f66\u540e\u7a97\u5f00\u7740\uff0c\u5f20\u606a\u770b\u5230\u9759\u6aac\u695a\u695a\u53ef\u601c\u7684\u8138\u86cb\uff0c\u5979\u7ea2\u80bf\u540e\u4ecd\u4e0d\u5931\u79c0\u7f8e\u7684\u773c\u7738\u5931\u795e\u7684\u770b\u7740\u8f66\u7a97\u4e0b\u7684\u6ca5\u9752\u8def\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u51dd\u89c6\u5b59\u9759\u6aac\u592a\u4e45\uff0c\u770b\u4e86\u4e24\u773c\u53c8\u8f6c\u8fc7\u5934\u6765\u770b\u5411\u524d\u65b9\uff0c\u8f7b\u58f0\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u4e5f\u5bb9\u5fcd\u4ed6\u4eec\u592a\u4e45\u4e86\u3002\u201d</p></article>"}}}]);
