(window.webpackJsonp=window.webpackJsonp||[]).push([[778],{801:function(p){p.exports={chapterTitle:null,title:"\u7b2c777\u7ae0 \u5510\u5a67\u4e0e\u8bb8\u601d\u7684\u76f8\u9047",content:"<article><h2>\u7b2c777\u7ae0 \u5510\u5a67\u4e0e\u8bb8\u601d\u7684\u76f8\u9047</h2><p>\u3000\u3000\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u5f53\u7136\u65e0\u6cd5\u7406\u89e3\u5f20\u606a\u5bf9\u60d8\u987e\u4ed6\u4eba\u6027\u547d\u3001\u5371\u9a8c\u9a7e\u9a76\u884c\u4e3a\u7684\u75db\u6068\u3002\u5954\u9a70\u8f66\u5e73\u7a33\u7684\u884c\u9a76\u5728\u6d45\u6c34\u6e7e\u5927\u9053\u4e0a\uff0c\u845b\u660e\u4fe1\u7684\u8138\u8272\u9634\u6c89\uff0c\u7ed9\u4e00\u4e2a\u4e73\u81ed\u672a\u5e72\u7684\u5c0f\u5b50\u90a3\u6837\u6559\u8bad\uff0c\u4ed6\u600e\u4e48\u80fd\u591f\u65c1\u82e5\u65e0\u4e8b\u7684\u54bd\u4e0b\u8fd9\u53e3\u6c14\uff1f\u4e0e\u6b64\u540c\u65f6\uff0c\u4ed6\u8fd8\u62c5\u5fc3\u5f20\u606a\u4f1a\u505a\u51fa\u5bf9\u5609\u4fe1\u96c6\u56e2\u4e0d\u5229\u7684\u4e8b\u60c5\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u4ee5\u540e\u5f00\u8f66\u4e5f\u8981\u6ce8\u610f\u4e00\u4e9b\uff0c\u78b0\u5230\u3001\u649e\u5230\u8def\u4eba\u4e5f\u5c31\u7b97\u4e86\uff0c\u4f60\u5c31\u4e0d\u62c5\u5fc3\u4f60\u81ea\u5df1\uff1f\u6211\u771f\u641e\u4e0d\u6e05\u695a\u4f60\u4eec\u5e74\u8f7b\u4eba\uff0c\u4e3a\u4e86\u8ffd\u6c42\u523a\u6fc0\uff0c\u600e\u4e48\u53ef\u80fd\u4e0d\u6ce8\u610f\u81ea\u5df1\u7684\u5b89\u5371\uff1f\u201d\u845b\u660e\u4fe1\u6309\u8010\u4e0b\u5fc3\u91cc\u7684\u70e6\u71e5\uff0c\u770b\u4e86\u4e00\u773c\u5750\u5728\u8eab\u8fb9\u9ed8\u4e0d\u4f5c\u58f0\u7684\u513f\u5b50\uff0c\u8bed\u91cd\u5fc3\u957f\u7684\u8bf4\u9053\uff0c\u201c\u4f60\u4e5f\u662f\u5feb\u4e09\u5341\u5c81\u7684\u4eba\u4e86\uff0c\u8fd8\u8981\u6211\u8ddf\u4f60\u5988\u66ff\u4f60\u64cd\u5fc3\uff1f\u6211\u53ef\u662f\u6307\u671b\u4f60\u80fd\u63a5\u6211\u7684\u4f4d\u5b50\uff0c\u4f60\u4e0d\u8981\u8ba9\u6211\u5931\u671b\u2026\u2026\u201d\u845b\u660e\u4fe1\u8003\u8651\u4e00\u4e0b\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4e8b\u60c5\u6ca1\u6709\u5e73\u606f\u4e4b\u524d\uff0c\u6211\u8ba9\u5e73\u53d4\u505a\u4f60\u7684\u53f8\u673a\u3002\u201d</p><p>\u3000\u3000\u201c\u6cd5\u62c9\u5229\u7684\u5b89\u5168\u6027\u80fd\u975e\u5e38\u9ad8\uff0c\u53ea\u8981\u4e0d\u662f\u51b2\u4e0b\u60ac\u5d16\u5ced\u58c1\u3001\u6216\u8005\u5361\u5728\u94c1\u8def\u4e2d\u95f4\u7ed9\u706b\u8f66\u649e\u8fd9\u6837\u7684\u5927\u8f66\u7978\uff0c\u5c31\u7b97\u5f2f\u9053\u90a3\u91cc\u6211\u6ca1\u6709\u63a7\u5236\u597d\u8f66\u7529\u7ffb\u51fa\u53bb\uff0c\u4eba\u5750\u5728\u91cc\u9762\u4e5f\u4e0d\u4f1a\u6709\u591a\u5927\u7684\u95ee\u9898\uff0c\u9876\u591a\u4e00\u90e8\u8f66\u62a5\u9500\u800c\u5df2\uff0c\u201d\u845b\u836b\u5747\u8fa9\u89e3\u9053\uff0c\u201c\u8fd9\u6b21\u8f66\u7978\u6709\u6cd5\u62c9\u5229\u540e\u8f6e\u4e0e\u8def\u4fa7\u62a4\u680f\u6469\u64e6\u7684\u75d5\u8ff9\uff0c\u8bc1\u636e\u90fd\u5bf9\u6211\u4eec\u6709\u5229\uff0c\u4e5f\u4e0d\u9700\u8981\u62c5\u5fc3\u4ec0\u4e48\uff0c\u53ef\u60dc\u8981\u6362\u4e00\u53ea\u8f6e\u80ce\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u6709\u5fc3\u601d\u62c5\u5fc3\u8f66\u8f6e\u80ce\uff1f\u201d\u845b\u660e\u4fe1\u65e0\u5948\u7684\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u8981\u662f\u8fd9\u6b21\u8f66\u7978\u7ed9\u4eba\u522b\u6709\u7528\u5fc3\u7684\u6345\u5230\u5a92\u4f53\u53bb\u7092\u4f5c\uff0c\u4e0d\u4ec5\u8fd9\u65f6\u5019\u4f1a\u5bf9\u6211\u4eec\u845b\u5bb6\uff0c\u5bf9\u5609\u4fe1\u96c6\u56e2\u6709\u8d1f\u9762\u5f71\u54cd\uff0c\u65e5\u540e\u4f60\u60f3\u63a5\u66ff\u6211\u7684\u4f4d\u5b50\uff0c\u8981\u662f\u6709\u4eba\u65e7\u4e8b\u91cd\u63d0\uff0c\u4f60\u5c31\u51ed\u7a7a\u591a\u4e86\u4e00\u4e9b\u963b\u529b\u3002\u4f60\u8981\u76ee\u5149\u957f\u8fdc\u7684\u770b\u5f85\u8fd9\u4e9b\u95ee\u9898\uff0c\u6240\u4ee5\u6211\u4eec\u5e73\u65e5\u505a\u4e8b\u505a\u4eba\u90fd\u8981\u8c28\u5c0f\u614e\u5fae\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8f66\u5e73\u7a33\u7684\u884c\u9a76\u5728\u6d45\u6c34\u6e7e\u5927\u9053\u4e0a\uff0c\u591c\u7a7a\u4e00\u7247\u6697\u84dd\uff0c\u56de\u5934\u671b\u53bb\uff0c\u9053\u4fa7\u4e24\u4fa7\u7684\u8349\u5761\u7ed9\u5982\u6c34\u7684\u6708\u5149\u9540\u4e0a\u4e00\u5c42\u9690\u7ea6\u7684\u5149\u6cfd\u3002\u5f20\u606a\u665a\u4e0a\u5230\u5b59\u5b85\u6765\u672c\u662f\u8d74\u5bb4\u5e86\u795d\u5b59\u5bb6\u642c\u56de\u6545\u5c45\uff0c\u8c01\u80fd\u6599\u5230\u8def\u4e2d\u4f1a\u906d\u9047\u5982\u6b64\u60e8\u5267\uff1f\u8fd9\u6b21\u7684\u8f66\u7978\u60e8\u5267\u65e0\u7591\u53c8\u8ba9\u4e24\u8fb9\u7684\u5173\u7cfb\u5f7b\u5e95\u7834\u88c2\u5230\u8981\u52a8\u624b\u76f8\u5411\u624d\u7518\u5fc3\u7684\u7ed3\u679c\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u53eb\u6709\u94b1\u4eba\u7684\u7279\u6743\uff0c\u540c\u5728\u4e00\u6761\u9053\u8def\u4e0a\uff0c\u5750\u5728\u6cd5\u62c9\u5229\u8f66\u91cc\u7684\u4eba\u6bd4\u8def\u8fb9\u884c\u8d70\u7684\u4eba\u6709\u7740\u66f4\u591a\u7684\u5b89\u5168\u4fdd\u969c\uff0c\u8fd9\u5c31\u662f\u91d1\u94b1\u6240\u5f62\u6210\u7684\u7279\u6743\u4e4b\u4e00\u3002\u845b\u836b\u5747\u4e3a\u4ec0\u4e48\u559c\u6b22\u8086\u65e0\u5fcc\u60ee\u7684\u98da\u8f66\u3001\u5371\u9a8c\u9a7e\u9a76\uff0c\u4ed6\u77e5\u9053\u6cd5\u62c9\u5229\u80fd\u7ed9\u4ed6\u66f4\u591a\u7684\u5b89\u5168\u4fdd\u969c\uff0c\u6362\u4e00\u8f86\u97e9\u56fd\u73b0\u4ee3\u7684\u8f66\u5b50\u8ba9\u4ed6\u8bd5\u8bd5\uff0c\u4ed6\u8fd8\u6562\u4e0d\u6562\u5728\u5f2f\u9053\u4e0a\u90a3\u6837\u9a7e\u8f66\uff1f\u201d\u5f20\u606a\u538b\u4f4e\u7740\u58f0\u97f3\u4e5f\u65e0\u6cd5\u63a9\u9970\u4ed6\u5fc3\u91cc\u7684\u6124\u6068\uff0c\u8f66\u505c\u5728\u5b59\u5b85\u7684\u524d\u5ead\u9662\u91cc\uff0c\u4e00\u8fb9\u4e0e\u4e0b\u8f66\u6765\uff0c\u4e00\u8fb9\u50cf\u4e2a\u4ec7\u5bcc\u4e3b\u4e49\u8005\u90a3\u6837\u8ddf\u5b59\u5c1a\u4e49\u62b1\u6028\u7740\uff0c\u4ed6\u7ad9\u5728\u8f66\u95e8\u53e3\uff0c\u7b49\u7740\u5510\u5a67\u3001\u5b59\u9759\u6aac\u5979\u4eec\u4ece\u53e6\u4e00\u90e8\u8f66\u4e0b\u6765\uff0c\u51c6\u5907\u4e00\u8d77\u8fdb\u5c4b\u5b50\u91cc\u53bb\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u5750\u5728\u5979\u4eec\u90a3\u8f86\u8f66\u7684\u524d\u6392\uff0c\u5979\u5148\u4e0b\u8f66\uff0c\u773c\u795e\u50f5\u6ede\u7684\u6123\u5728\u90a3\u91cc\u3002\u770b\u5230\u5b59\u9759\u9999\u5982\u6b64\u7684\u53cd\u5e94\uff0c\u5f20\u606a\u4e0b\u610f\u8bc6\u7684\u56de\u5934\u770b\u53bb\uff0c\u8bb8\u601d\u6cea\u773c\u5a46\u5a11\u7684\u6b63\u7ad9\u5728\u5c4b\u6a90\u4e0b\u5f80\u8fd9\u8fb9\u770b\u7740\u3002\u5f20\u606a\u4e0d\u6e05\u695a\u8bb8\u601d\u4e3a\u4ec0\u4e48\u8981\u8d76\u8fc7\u6765\uff0c\u4ed6\u6761\u4ef6\u53cd\u5c04\u4f3c\u7684\u5c31\u60f3\u963b\u6b62\u5510\u5a67\u4e0b\u8f66\uff0c\u521a\u8981\u4f38\u51fa\u624b\uff0c\u5510\u5a67\u5df2\u7ecf\u6253\u5f00\u8f66\u95e8\u4e0b\u6765\u4e86\uff0c\u8138\u8fd8\u671d\u5411\u8f66\u91cc\u6b63\u51c6\u5907\u4e0e\u845b\u660e\u73cf\u4e00\u8d77\u6276\u5b59\u9759\u6aac\u4e0b\u8f66\u6765\uff0c\u968f\u65f6\u90fd\u4f1a\u8f6c\u8eab\u6765\uff0c\u8fd9\u65f6\u5019\u505a\u4ec0\u4e48\u63a9\u9970\u90fd\u8fdf\u4e86\uff0c\u5f20\u606a\u7684\u624b\u50f5\u6ede\u5728\u90a3\u91cc\uff0c\u4efb\u4ed6\u8111\u5b50\u5e73\u65e5\u518d\u597d\u4f7f\uff0c\u8fd9\u65f6\u5019\u4e5f\u662f\u4e00\u7247\u7a7a\u767d\uff0c\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u907f\u514d\u4e24\u5973\u5c34\u5c2c\u76f8\u89c1\u7684\u5c40\u9762\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u8bb8\u601d\u7684\u5173\u7cfb\uff0c\u5b59\u5bb6\u4e5f\u5c31\u5b59\u9759\u9999\u660e\u786e\u7684\u77e5\u9053\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u8001\u773c\u4e0d\u82b1\uff0c\u5f53\u7136\u80fd\u731c\u5230\u5f20\u606a\u4e0e\u8bb8\u601d\u7684\u5173\u7cfb\uff0c\u4f46\u662f\u8bb8\u601d\u5728\u5916\u4eba\u9762\u524d\u6bd5\u7adf\u514b\u5236\u7740\u4e0d\u8ddf\u5f20\u606a\u6709\u8fc7\u4e8e\u4eb2\u5bc6\u7684\u52a8\u4f5c\uff0c\u4ed6\u5c31\u53ef\u4ee5\u59cb\u7ec8\u88c5\u4f5c\u7cca\u6d82\uff0c\u770b\u5230\u8bb8\u601d\u7ad9\u5728\u5c4b\u6a90\u4e0b\uff0c\u4ed6\u53ea\u662f\u644a\u644a\u624b\u8868\u793a\u8be7\u5f02\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u51ed\u501f\u5c11\u5973\u7279\u6709\u7684\u654f\u611f\u80fd\u77e5\u9053\u66f4\u591a\uff0c\u5979\u6b64\u65f6\u6b63\u9677\u5165\u60b2\u4f24\u4e2d\u96be\u4ee5\u81ea\u62d4\uff0c\u7ed9\u5510\u5a67\u6400\u4e0b\u8f66\u6765\uff0c\u770b\u5230\u8bb8\u601d\u7ad9\u5230\u5c4b\u6a90\u4e0b\uff0c\u751a\u81f3\u90fd\u6ca1\u6709\u60f3\u5230\u5510\u5a67\u4e0e\u8bb8\u601d\u78b0\u9762\u540e\u7684\u5c34\u5c2c\u3002</p><p>\u3000\u3000\u56de\u6765\u662f\u5085\u4fca\u4eb2\u81ea\u66ff\u5f20\u606a\u5f00\u8f66\uff0c\u56e0\u4e3a\u5f20\u606a\u8981\u4e0e\u5b59\u5c1a\u4e49\u5728\u8f66\u91cc\u8ba8\u8bba\u673a\u5bc6\u7684\u4e8b\u60c5\uff0c\u4ed6\u53ea\u80fd\u5750\u5728\u8f66\u91cc\u770b\u7740\u4e8b\u60c5\u4f1a\u600e\u4e48\u53d1\u5c55\u3002</p><p>\u3000\u3000\u5b59\u5bb6\u4e5f\u5c31\u845b\u660e\u73cf\u7ed9\u8499\u5728\u9f13\u91cc\uff0c\u5979\u770b\u5230\u8bb8\u601d\u7ad9\u5728\u8d70\u6a90\u4e0b\uff0c\u660e\u4eae\u7684\u5eca\u706f\u5149\u8292\u7167\u51fa\u5979\u773c\u7738\u91cc\u6ee1\u542b\u6cea\u6c34\uff0c\u7ea2\u80bf\u7684\u773c\u775b\u5e76\u6ca1\u6709\u5f71\u54cd\u5979\u7684\u7edd\u7f8e\uff0c\u6ed1\u843d\u5230\u8138\u988a\u4e0a\u7684\u6cea\u6c34\u8fd8\u6ca1\u6709\u5e72\uff0c\u773c\u775b\u91cc\u7684\u6cea\u73e0\u63a7\u5236\u4e0d\u4f4f\u7684\u5f80\u4e0b\u6ed1\u843d\uff0c\u95ea\u70c1\u7740\u9690\u9690\u7684\u5149\u6cfd\uff0c\u89c1\u8bb8\u601d\u624b\u7d27\u7d27\u7684\u6293\u4f4f\u5c4b\u6a90\u524d\u7684\u6276\u62e6\uff0c\u6709\u7740\u5df2\u7ecf\u7528\u5c3d\u5168\u8eab\u529b\u6c14\u7684\u611f\u89c9\uff0c\u770b\u5230\u8bb8\u601d\u62b9\u53bb\u8138\u988a\u4e0a\u7684\u6cea\u6c34\u8f6c\u8eab\u8981\u8eb2\u8fdb\u5c4b\u91cc\u53bb\uff0c\u558a\u4f4f\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u8bb8\u601d\u4f60\u8fc7\u6765\u4e86\u554a\uff0c\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u60c5\u8ba9\u4f60\u4eb2\u81ea\u8d76\u8fc7\u6765\uff0c\u771f\u662f\u8fc7\u610f\u4e0d\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5230\u845b\u660e\u73cf\u8bf4\u51fa\u201c\u8bb8\u601d\u201d\u7684\u540d\u5b57\uff0c\u5510\u5a67\u7684\u8eab\u5b50\u5c31\u50f5\u76f4\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u90fd\u4e0d\u6562\u56de\u8fc7\u8eab\u6765\uff0c\u5979\u4e0d\u77e5\u9053\u81ea\u5df1\u8981\u4e0d\u8981\u8f6c\u8fc7\u8eab\u6765\u3002\u8fd8\u662f\u5b59\u9759\u9999\u8f7b\u8e22\u4e86\u5f20\u606a\u4e00\u4e0b\uff0c\u671d\u8bb8\u601d\u90a3\u8fb9\u5476\u5476\u5634\uff0c\u867d\u8bf4\u8bb8\u601d\u662f\u751f\u6027\u5584\u826f\u7684\u4eba\uff0c\u4f46\u662f\u4e3a\u5b59\u5bb6\u7684\u4fdd\u59c6\u9047\u8f66\u7978\u8fc7\u4e16\u5982\u6b64\u4f24\u5fc3\u7684\u54ed\u6ce3\uff0c\u591a\u5c11\u6709\u4e9b\u8ba9\u4eba\u610f\u5916\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u624d\u610f\u8bc6\u5230\u8bb8\u601d\u53ef\u80fd\u9047\u4e0a\u4ec0\u4e48\u4e8b\u60c5\u624d\u5982\u6b64\u4f24\u5fc3\u7684\u75db\u54ed\uff0c\u4e5f\u987e\u4e0d\u4e0a\u5510\u5a67\uff0c\u8dd1\u8fc7\u6765\u521a\u8981\u8bf4\u8bdd\uff0c\u8bb8\u601d\u6574\u4e2a\u4eba\u90fd\u5dee\u70b9\u762b\u5012\u5728\u5730\u4e0a\uff0c\u5f20\u606a\u53ca\u65f6\u62b1\u4f4f\u5979\u624d\u6ca1\u6709\u8ba9\u5979\u5012\u5728\u8fc7\u9053\u91cc\uff0c\u770b\u5979\u5982\u6b64\u6a21\u6837\uff0c\u5f20\u606a\u7684\u5fc3\u90fd\u5728\u62bd\u75db\uff0c\u4e5f\u987e\u4e0d\u4e0a\u65c1\u4eba\uff0c\u6402\u7740\u5979\uff1a\u201c\u600e\u4e48\u4e86\uff0c\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u4e0d\u63a5\u6211\u7684\u7535\u8bdd\uff0c\u4f60\u600e\u4e48\u4e0d\u63a5\u6211\u7684\u7535\u8bdd\u554a\uff1f\u542c\u8bf4\u4f60\u9047\u5230\u8f66\u7978\uff0c\u6211\u5c31\u4e00\u76f4\u6253\u4f60\u7684\u7535\u8bdd\uff0c\u6211\u4e0d\u60f3\u8fc7\u6765\uff0c\u53ef\u662f\u4f60\u600e\u4e48\u4e00\u76f4\u90fd\u4e0d\u63a5\u6211\u7684\u7535\u8bdd\u554a\uff1f\u201d\u8bb8\u601d\u773c\u7738\u91cc\u7684\u6cea\u6c34\u4e0d\u65ad\u7684\u6ed1\u843d\uff0c\u8981\u4ece\u5f20\u606a\u6000\u91cc\u6323\u624e\u8d77\u6765\uff0c\u5168\u8eab\u5374\u56e0\u4e3a\u8fc7\u5ea6\u7684\u62c5\u5fc3\u53d7\u6015\u800c\u6beb\u65e0\u529b\u6c14\uff0c\u62bd\u7740\u6c14\u544a\u8bc9\u5f20\u606a\u5979\u770b\u65b0\u95fb\u62a5\u9053\u4e86\u6d45\u6c34\u6e7e\u5927\u9053\u53d1\u751f\u8f66\u7978\uff0c\u65b0\u95fb\u5185\u5bb9\u5f88\u542b\u7cca\uff0c\u5979\u5f88\u62c5\u5fc3\u5f20\u606a\u7684\u5b89\u5371\u5c31\u60f3\u7ed9\u5f20\u606a\u6253\u7535\u8bdd\u95ee\u4e00\u4e0b\u5b89\u4e2a\u5fc3\uff0c\u4e24\u901a\u7535\u8bdd\u6ca1\u6709\u63a5\uff0c\u5979\u7684\u5fc3\u5c31\u4e71\u4e86\u3002\u6253\u5085\u4fca\u7684\u624b\u673a\uff0c\u624b\u673a\u5173\u673a\uff1b\u5979\u77e5\u9053\u5f20\u606a\u4e0e\u5510\u5a67\u591c\u91cc\u8981\u53bb\u5b59\u5b85\u5403\u665a\u996d\uff0c\u6253\u5b59\u9759\u9999\u7684\u624b\u673a\uff0c\u7535\u8bdd\u6ca1\u4eba\u63a5\uff0c\u6253\u5b59\u5c1a\u4e49\u7684\u624b\u673a\uff0c\u7535\u8bdd\u6ca1\u4eba\u63a5\uff0c\u6253\u7535\u8bdd\u5230\u5b59\u5b85\uff0c\u5b59\u5b85\u7684\u5973\u4f63\u4e5f\u4e0d\u77e5\u9053\u5177\u4f53\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u53ea\u662f\u544a\u8bc9\u5979\u6d45\u6c34\u9053\u53d1\u751f\u8f66\u7978\uff0c\u5b59\u5c1a\u4e49\u4e00\u5bb6\u4eba\u542c\u5230\u6d88\u606f\u5c31\u614c\u4e71\u7684\u5954\u8dd1\u7740\u51fa\u4e86\u95e8\uff0c\u90fd\u6ca1\u6765\u5f97\u53ca\u5c06\u624b\u673a\u62ff\u4e0a\u3002\u8fd9\u6837\u7684\u6d88\u606f\u8ba9\u5979\u9677\u5165\u5de8\u5927\u7684\u6050\u614c\u5f53\u4e2d\uff0c\u771f\u5c31\u4ee5\u4e3a\u662f\u5f20\u606a\u4ed6\u4eec\u51fa\u4e86\u8f66\u7978\uff0c\u53c8\u60ca\u53c8\u6050\u7684\u4e58\u8f66\u4ece\u5f71\u6e7e\u56ed\u8d76\u5230\u6d45\u6c34\u6e7e\u5927\u9053\uff0c\u8f66\u7978\u73b0\u573a\u9664\u4e86\u4e00\u4e9b\u8b66\u5bdf\u52d8\u5bdf\u540e\u7684\u9057\u8ff9\u4e0e\u4e00\u5c0f\u6ee9\u5e72\u6db8\u7684\u8840\u8ff9\uff0c\u4e5f\u6ca1\u6709\u4eba\u544a\u8bc9\u5979\u5230\u5e95\u600e\u4e48\u4e86\uff0c\u5979\u5c31\u6bd4\u5f20\u606a\u65e9\u51e0\u5206\u949f\u8d76\u5230\u5b59\u5b85\u3002</p><p>\u3000\u3000\u770b\u5230\u5f20\u606a\u6ca1\u4e8b\u4f3c\u7684\u4ece\u8f66\u91cc\u94bb\u51fa\u6765\uff0c\u8bb8\u601d\u5979\u5d29\u7d27\u5230\u6781\u70b9\u7684\u62c5\u60ca\u53d7\u6015\u4e00\u4e0b\u5b50\u5d29\u6e83\u6389\uff0c\u5185\u5fc3\u91cc\u53c8\u9661\u7136\u6d8c\u51fa\u5728\u8fd9\u79cd\u573a\u5408\u4e0e\u5510\u5a67\u89c1\u9762\u7684\u614c\u4e71\u4e0e\u5f3a\u70c8\u7684\u4e0d\u5b89\uff0c\u51e0\u79cd\u6781\u81f4\u7684\u60c5\u7eea\u4f53\u9a8c\u8ba9\u5979\u7ecf\u53d7\u4e0d\u4f4f\u6574\u4e2a\u4eba\u90fd\u5dee\u70b9\u57ae\u6389\uff0c\u5979\u5728\u5f20\u606a\u7684\u6000\u91cc\uff0c\u53ea\u662f\u7d6e\u7d6e\u53e8\u53e8\u7684\u53cd\u590d\u8bf4\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u63a5\u6211\u7535\u8bdd\u554a\uff0c\u4f60\u600e\u4e48\u4e0d\u63a5\u6211\u7535\u8bdd\u554a\uff1f\u201d</p><p>\u3000\u3000\u5085\u4fca\u624b\u6307\u6572\u6253\u7740\u65b9\u5411\u76d8\uff0c\u4ed6\u5728\u7ed9\u73e0\u5ac2\u505a\u6025\u6551\u65f6\uff0c\u624b\u673a\u4ece\u88e4\u515c\u91cc\u6ed1\u5230\u8840\u6cca\u91cc\uff0c\u7b49\u4ed6\u7a7a\u4e0b\u624b\u5c06\u624b\u673a\u6361\u8d77\u6765\u65f6\uff0c\u624b\u673a\u5df2\u7ecf\u62a5\u5e9f\u4e86\uff0c\u6240\u4ee5\u8bb8\u601d\u8054\u7cfb\u4e0d\u4e0a\u4ed6\u3002\u5b59\u5c1a\u4e49\u63a5\u5230\u5510\u5a67\u7684\u7535\u8bdd\u8bf4\u6709\u540d\u5987\u5973\u5728\u6d45\u6c34\u6e7e\u5927\u9053\u884c\u8d70\u7ed9\u845b\u836b\u5747\u5f00\u8f66\u649e\u5012\uff0c\u5b59\u5c1a\u4e49\u4e00\u5bb6\u7acb\u5373\u610f\u8bc6\u5230\u53ef\u80fd\u662f\u73e0\u5ac2\u88ab\u649e\uff0c\u54ea\u6709\u5fc3\u601d\u60f3\u7740\u5c06\u624b\u673a\u968f\u8eab\u62ff\u7740\uff1f\u6240\u4ee5\u8bb8\u601d\u8054\u7cfb\u4e0d\u4e0a\u5b59\u5c1a\u4e49\u4e00\u5bb6\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u6478\u4e86\u6478\u88e4\u515c\uff0c\u8fd9\u624d\u53d1\u89c9\u624b\u673a\u4e0d\u5728\uff0c\u4ece\u9047\u5230\u8f66\u7978\u8d77\uff0c\u4ed6\u90fd\u6ca1\u6709\u60f3\u5230\u8981\u6253\u7535\u8bdd\uff0c\u4e0d\u77e5\u9053\u843d\u54ea\u91cc\u4e86\u3002\u90a3\u65f6\u5019\u7ed9\u845b\u836b\u5747\u3001\u674e\u9a90\u7684\u884c\u4e3a\u6c14\u5f97\u8981\u55b7\u8840\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u6ce8\u610f\u5230\u624b\u673a\u4e22\u5931\u4e86\u3002\u770b\u7740\u8bb8\u601d\u56e0\u4e3a\u62c5\u60ca\u53d7\u6015\u3001\u652f\u6491\u4e0d\u4f4f\u7684\u5d29\u6e83\u6a21\u6837\uff0c\u5f20\u606a\u5fc3\u91cc\u6ee1\u662f\u601c\u60dc\uff0c\u53c8\u611f\u5230\u63ea\u5fc3\u7684\u75db\uff0c\u770b\u7740\u5979\u773c\u7738\u91cc\u5927\u6ef4\u7684\u6cea\u6c34\u6d8c\u51fa\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u54ed\u4e86\u8d77\u6765\uff0c\u62b9\u7740\u6cea\u6c34\u544a\u8bc9\u5979\uff1a\u201c\u6211\u6ca1\u6709\u4e8b\uff0c\u6211\u6ca1\u6709\u4e8b\uff0c\u6211\u4e0d\u662f\u597d\u597d\u7684\u5728\u8fd9\u91cc\uff0c\u4f60\u4e0d\u8981\u54ed\u4e86\u3002\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u5476\u7740\u5634\uff0c\u793a\u610f\u8ba9\u5176\u4ed6\u4eba\u90fd\u8ddf\u4ed6\u8fdb\u8fdb\u5c4b\u91cc\u53bb\uff0c\u5085\u4fca\u4e5f\u4ece\u8f66\u91cc\u4e0b\u6765\u8d70\u8fdb\u5c4b\u5b50\u91cc\u53bb\uff0c\u845b\u660e\u73cf\u4e4b\u524d\u90fd\u4e0d\u77e5\u9053\u5f20\u606a\u4e0e\u8bb8\u601d\u4e4b\u95f4\u6709\u8fd9\u4e48\u6df1\u7684\u611f\u60c5\uff0c\u8138\u4e0a\u8fd8\u6302\u7740\u4e9b\u8be7\u5f02\uff0c\u5b59\u9759\u9999\u8fc7\u6765\u4e0e\u5979\u4e00\u8d77\u6400\u9759\u6aac\u8fdb\u5c4b\u53bb\uff0c\u9759\u6aac\u7ed9\u8bb8\u601d\u8fd9\u4e00\u60f9\uff0c\u60f3\u5230\u4f24\u5fc3\u5904\uff0c\u53c8\u6d41\u6cea\u8d77\u6765\u3002\u5b59\u9759\u9999\u5fcd\u4e0d\u4f4f\u770b\u4e86\u80cc\u7740\u8eab\u5b50\u800c\u7ad9\u7684\u5510\u5a67\u4e00\u773c\uff0c\u5374\u89c1\u5979\u8138\u988a\u4e0a\u722c\u6ee1\u6676\u83b9\u7684\u6cea\u73e0\uff0c\u53ea\u662f\u5f3a\u5fcd\u7740\u6ca1\u6709\u62ac\u624b\u53bb\u64e6\u3002\u5b59\u9759\u9999\u72b9\u8c6b\u7740\uff0c\u4e0d\u77e5\u9053\u662f\u4e0d\u662f\u8981\u7559\u4e0b\u6765\u5e2e\u4ed6\u4eec\u4e09\u4e2a\u4eba\u5316\u89e3\u5f7c\u6b64\u95f4\u7684\u5c34\u5c2c\uff0c\u53c8\u60f3\u5979\u8fd9\u4e2a\u4eba\u53c2\u4e0e\u8fdb\u53bb\u4e5f\u4e0d\u5408\u9002\uff0c\u5c31\u4e5f\u8fdb\u4e86\u623f\u5b50\u3002</p><p>\u3000\u3000\u5176\u4ed6\u4eba\u90fd\u8fdb\u4e86\u5c4b\uff0c\u751a\u81f3\u5c06\u95e8\u5385\u843d\u5730\u73bb\u7483\u95e8\u90fd\u62c9\u4e0a\u7a97\u5e18\uff0c\u7559\u5f20\u606a\u4e0e\u8bb8\u601d\u3001\u5510\u5a67\u4e09\u4e2a\u4eba\u5728\u524d\u5ead\u7684\u6708\u5149\u7167\u8000\u4e0b\uff0c\u6c89\u9ed8\u4e86\u8bb8\u4e45\uff0c\u53ea\u6709\u65e0\u58f0\u7684\u54ed\u6ce3\u8fd8\u5728\u7ee7\u7eed\u3002</p></article>"}}}]);
