(window.webpackJsonp=window.webpackJsonp||[]).push([[779],{802:function(p){p.exports={chapterTitle:null,title:"\u7b2c778\u7ae0 \u8981\u662f\u53ef\u4ee5",content:"<article><h2>\u7b2c778\u7ae0 \u8981\u662f\u53ef\u4ee5</h2><p>\u3000\u3000\u5510\u5a67\u80cc\u7740\u8eab\u5b50\u7ad9\u5728\u5b59\u5b85\u524d\u5ead\u8349\u5761\u4e0a\u6ca1\u6709\u52c7\u6c14\u8f6c\u8fc7\u8eab\u53bb\u3002\u4e09\u4e2a\u4eba\u6c89\u9ed8\u4e86\u8bb8\u4e45\uff0c\u5510\u5a67\u624d\u62b9\u6389\u8138\u988a\u4e0e\u773c\u7751\u91cc\u7684\u6cea\u6c34\uff0c\u8fd8\u662f\u6ca1\u6709\u52c7\u6c14\u56de\u5934\uff0c\u53ea\u662f\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u7559\u5728\u8fd9\u91cc\u5427\uff0c\u6211\u5148\u56de\u5b66\u6821\u4e86\u2026\u2026\u201d\u5c31\u5934\u4e5f\u4e0d\u56de\u7684\u671d\u5916\u8d70\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u8bb8\u601d\u90a3\u771f\u631a\u7684\u3001\u6fd2\u4e34\u5d29\u6e83\u7684\u60c5\u611f\u89e6\u5fc3\u843d\u6cea\uff0c\u4f46\u662f\u6700\u8ba9\u4ed6\u5934\u75bc\u7684\u8fd8\u662f\u6536\u62fe\u773c\u4e0b\u7684\u5c40\u9762\u3002\u542c\u5230\u5510\u5a67\u8bf4\u8981\u8d70\u800c\u4e14\u771f\u7684\u62ac\u811a\u5c31\u5f80\u5916\u8d70\u53bb\uff0c\u4ed6\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u4e0d\u77e5\u9053\u600e\u4e48\u505a\u624d\u5408\u9002\u3002\u8bb8\u601d\u6323\u624e\u7740\u4ece\u4ed6\u7684\u6000\u91cc\u53c8\u7ad9\u8d77\u6765\uff0c\u7ecf\u5386\u5982\u6b64\u5f3a\u70c8\u7684\u60c5\u7eea\u9707\u8361\uff0c\u4f3c\u4e4e\u8ba9\u8eab\u4f53\u8017\u5c3d\u6240\u6709\u7684\u529b\u6c14\uff0c\u8fde\u6276\u7740\u5eca\u67f1\u7ad9\u8d77\u6765\u90fd\u611f\u89c9\u5f88\u4e4f\u529b\uff0c\u4fbf\u5750\u5230\u4e00\u8fb9\uff0c\u63a8\u7740\u5f20\u606a\u8ba9\u4ed6\u53bb\u8ffd\u5510\u5a67\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8be5\u6765\u7684\uff0c\u5c31\u662f\u62c5\u5fc3\u4f60\uff0c\u6211\u5750\u4e00\u4f1a\u513f\u5c31\u56de\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u8fd9\u65f6\u5019\u54ea\u91cc\u4f1a\u820d\u5f97\u5c06\u8bb8\u601d\u4e22\u5230\u4e00\u8fb9\uff0c\u4f46\u662f\u66f4\u4e0d\u80fd\u8ba9\u5510\u5a67\u5c31\u8fd9\u6837\u8d70\u7740\u56de\u6e2f\u5927\u3002\u5b59\u5c1a\u4e49\u3001\u5085\u4fca\u4ed6\u4eec\u8fdb\u53bb\u65f6\uff0c\u6709\u8f86\u5954\u9a70\u8f66\u6ca1\u6709\u7184\u706b\uff0c\u8f66\u5185\u706f\u8fd8\u4eae\u7740\uff0c\u5f20\u606a\u6400\u7740\u8bb8\u601d\u5750\u8fdb\u8f66\u91cc\uff0c\u5f00\u8f66\u8ffd\u4e86\u51fa\u53bb\uff0c\u4e5f\u4e0d\u7ba1\u5b59\u5c1a\u4e49\u4ed6\u4eec\u5728\u5c4b\u5b50\u91cc\u4f1a\u600e\u4e48\u60f3\u3002</p><p>\u3000\u3000\u5510\u5a67\u6602\u9996\u9614\u6b65\u7684\u5728\u8def\u706f\u4e0b\u8d70\u7740\uff0c\u5fc3\u91cc\u7ed9\u83ab\u540d\u7684\u60c5\u7eea\u5145\u585e\u7740\uff0c\u773c\u89d2\u6e7f\u6da6\uff0c\u5439\u7740\u51b0\u51b7\u7684\u591c\u98ce\uff0c\u867d\u7136\u5fc3\u91cc\u65e9\u5c31\u9ed8\u8ba4\u4e86\u8bb8\u601d\u7684\u5b58\u5728\uff0c\u4f46\u662f\u771f\u5230\u4e24\u4eba\u76f8\u89c1\u7684\u8fd9\u4e00\u6b65\uff0c\u90a3\u9053\u574e\u53c8\u5c82\u662f\u90a3\u4e48\u8f7b\u6613\u5c31\u80fd\u7ed5\u8fc7\u7684\uff1f</p><p>\u3000\u3000\u5510\u5a67\u89c9\u5f97\u81ea\u5df1\u5f88\u4e0d\u4e89\u6c14\uff0c\u600e\u4e48\u80fd\u54ed\u8d77\u6765\u5462\uff0c\u600e\u4e48\u5c31\u54ed\u8d77\u6765\uff1f\u4e00\u76f4\u90fd\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u907f\u514d\u4e0e\u8bb8\u601d\u5728\u67d0\u4e2a\u573a\u5408\u78b0\u9762\uff0c\u4e0a\u56de\u5df2\u7ecf\u591f\u72fc\u72c8\u4e86\uff0c\u4e3a\u4ec0\u4e48\u770b\u5230\u5979\u6fd2\u4e34\u5d29\u6e83\u7684\u8fb9\u7f18\uff0c\u5374\u80fd\u771f\u5207\u7684\u3001\u6709\u5982\u8eab\u53d7\u7684\u4f53\u4f1a\u5230\u5979\u5728\u7b49\u5f85\u65f6\u7684\u4f24\u5fc3\u4e0e\u7edd\u671b\uff1f\u56e0\u4e3a\u5982\u6b64\u7684\u611f\u53d7\uff0c\u6cea\u6c34\u5c31\u8ddf\u7740\u6b62\u4e0d\u4f4f\u7684\u6d41\u4e0b\u6765\u3002</p><p>\u3000\u3000\u867d\u8bf4\u662f\u865a\u60ca\u4e00\u573a\uff0c\u6362\u4f5c\u81ea\u5df1\u8981\u662f\u542c\u5230\u5f20\u606a\u51fa\u8f66\u7978\u7684\u6d88\u606f\u4e5f\u4e00\u5b9a\u4f1a\u627f\u53d7\u4e0d\u4f4f\u800c\u6fd2\u4e34\u5d29\u6e83\u7684\uff0c\u60f3\u8c61\u5230\u90a3\u60c5\u666f\uff0c\u4e5f\u4f1a\u60c5\u4e0d\u81ea\u7981\u7684\u7559\u4e0b\u4f24\u5fc3\u7684\u6cea\u6c34\u3002\u80fd\u611f\u53d7\u5230\u8bb8\u601d\u5bf9\u5f20\u606a\u771f\u631a\u7684\u611f\u60c5\uff0c\u5c31\u5728\u8fd9\u4e00\u523b\uff0c\u5fc3\u91cc\u5bf9\u8bb8\u601d\u8fd8\u5b58\u6709\u7684\u90a3\u4e9b\u654c\u610f\u5c31\u83ab\u660e\u7684\u5f7b\u5e95\u74e6\u89e3\u4e86\uff0c\u5979\u5bf9\u5f20\u606a\u6709\u7740\u4e0d\u5f31\u4e8e\u81ea\u5df1\u7684\u771f\u631a\u60c5\u611f\uff0c\u6709\u4ec0\u4e48\u7406\u7531\u8981\u6c42\u5979\u4ece\u5f20\u606a\u8eab\u8fb9\u79bb\u5f00\u5462\uff1f</p><p>\u3000\u3000\u8981\u662f\u53ef\u4ee5\uff0c\u5510\u5a67\u5b81\u53ef\u5e26\u6709\u654c\u610f\u7684\u9ed8\u8ba4\u8bb8\u601d\u7684\u5b58\u5728\uff0c\u6b64\u65f6\u5979\u4e0d\u582a\u9762\u5bf9\u5185\u5fc3\u7684\u81ea\u5df1\uff0c\u53ea\u60f3\u7740\u8fdc\u8fdc\u7684\u8d70\u5f00\uff0c\u5373\u4f7f\u611f\u89c9\u5230\u5f20\u606a\u5f00\u8f66\u8f7d\u7740\u8bb8\u601d\u8ffd\u4e86\u4e0a\u6765\uff0c\u5979\u4e5f\u5f3a\u8feb\u81ea\u5df1\u4e0d\u56de\u5934\u770b\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u4e0a\u8f66\u5427\uff0c\u4ece\u8fd9\u91cc\u8d70\u56de\u6e2f\u5927\u4e5f\u4e0d\u662f\u90a3\u56de\u4e8b\u2026\u2026\u201d\u5f20\u606a\u5934\u63a2\u51fa\u8f66\u7a97\uff0c\u529d\u5510\u5a67\u4e0a\u8f66\u3002</p><p>\u3000\u3000\u5510\u5a67\u8fde\u5934\u90fd\u6ca1\u6709\u6446\uff0c\u4e0d\u7528\u56de\u5934\u5979\u4e5f\u77e5\u9053\u8bb8\u601d\u4e5f\u5728\u8f66\u91cc\uff0c\u54ac\u7d27\u5634\u5507\u4e0d\u8ba9\u81ea\u5df1\u54ed\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u8ba9\u8bb8\u601d\u4e0b\u53bb\u8d70\u8def\uff0c\u4f60\u6765\u5750\u8f66\uff1f\u201d\u5f20\u606a\u7ee7\u7eed\u95ee\u9053\u3002</p><p>\u3000\u3000\u5510\u5a67\u4fa7\u8fc7\u5934\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u8fd8\u662f\u6ca1\u6709\u52c7\u6c14\u53bb\u770b\u5750\u5728\u540e\u6392\u7684\u8bb8\u601d\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u6211\u4e0b\u6765\u8d70\u8def\uff0c\u4f60\u8ddf\u8bb8\u601d\u5f00\u8f66\uff1f\u201d\u5f20\u606a\u7ee7\u7eed\u95ee\u9053\u3002</p><p>\u3000\u3000\u5510\u5a67\u731b\u7136\u7ad9\u4f4f\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u51b7\u51b0\u51b0\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4e0b\u6765\u8d70\u8def\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u8fd9\u59ae\u5b50\u5413\u4e86\u4e00\u8df3\uff0c\u5239\u4f4f\u8f66\uff0c\u542c\u8bdd\u7684\u6253\u5f00\u8f66\u95e8\u4e0b\u4e86\u8f66\uff0c\u8bb8\u601d\u4e5f\u8981\u5f00\u8f66\u95e8\u4e0b\u8f66\uff0c\u5510\u5a67\u8bf4\u9053\uff1a\u201c\u8ba9\u8fd9\u4e2a\u732a\u5934\u4e00\u4e2a\u4eba\u8d70\u8def\u3002\u201d\u8bb8\u601d\u5fc3\u91cc\u5bf9\u5510\u5a67\u603b\u89c9\u5f97\u6709\u79cd\u4e8f\u6b20\uff0c\u542c\u5510\u5a67\u8fd9\u4e48\u8bf4\uff0c\u5979\u4fbf\u7559\u5728\u8f66\u91cc\u3002</p><p>\u3000\u3000\u5c31\u8fd9\u6837\uff0c\u5f20\u606a\u8d70\u8def\u3001\u5510\u5a67\u5f00\u8f66\u8f7d\u7740\u8bb8\u601d\uff0c\u4e09\u4e2a\u4eba\u5728\u6d45\u6c34\u6e7e\u5927\u9053\u7f13\u7f13\u800c\u884c\u3002\u8d70\u5230\u9ec4\u660f\u53d1\u751f\u8f66\u7978\u7684\u5730\u70b9\uff0c\u5510\u5a67\u5c06\u8f66\u505c\u4e0b\u6765\u3002\u5f20\u606a\u770b\u7740\u8f66\u91cc\u7684\u5510\u5a67\uff0c\u4e0d\u77e5\u9053\u5979\u4e3a\u4ec0\u4e48\u8981\u505c\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5728\u6708\u5149\u4e0b\uff0c\u8349\u5730\u4e0a\u5e72\u6db8\u7684\u8840\u8ff9\u95ea\u7740\u5fae\u5f31\u7684\u5149\u8292\uff0c\u5510\u5a67\u51dd\u89c6\u7740\u8349\u5761\uff0c\u83ab\u660e\u7684\uff0c\u6cea\u6c34\u53c8\u6302\u5230\u8138\u4e0a\uff0c\u5f20\u606a\u53ea\u5f53\u5510\u5a67\u89c9\u5f97\u59d4\u5c48\uff0c\u5fc3\u614c\u614c\u7684\u90fd\u4e0d\u77e5\u9053\u5728\u8bb8\u601d\u9762\u524d\u5982\u4f55\u5b89\u6170\u5979\u3002\u5510\u5a67\u545c\u54bd\u7684\u8bf4\uff1a\u201c\u8981\u6ca1\u6709\u4e86\u4f60\uff0c\u4e00\u5207\u8fd8\u6709\u4ec0\u4e48\u610f\u4e49\uff1f\u60f3\u5230\u8fd9\u4e2a\u771f\u7684\u8ba9\u4eba\u5f88\u63ea\u5fc3\uff01\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u65e0\u58f0\u7684\u770b\u7740\u53d1\u751f\u8f66\u7978\u7684\u90a3\u7247\u8349\u5730\uff0c\u8bb8\u601d\u8d70\u4e0b\u4e86\u8f66\uff0c\u6276\u7740\u8f66\u95e8\u800c\u7acb\uff0c\u5f20\u606a\u6307\u7740\u8f66\u7978\u53d1\u751f\u7684\u5730\u65b9\uff0c\u8ddf\u5979\u89e3\u91ca\uff0c\u201c\u662f\u9759\u6aac\u7684\u59c6\u5988\u8d70\u5728\u8def\u4e0a\u7ed9\u8f66\u649e\u4e86\uff0c\u5728\u9001\u5f80\u533b\u9662\u62a2\u6551\u7684\u9014\u4e2d\u53bb\u4e16\u4e86\uff0c\u6211\u8ddf\u5510\u5a67\u7ecf\u8fc7\u8fd9\u91cc\uff0c\u4e00\u8d77\u8ddf\u7740\u8dd1\u5230\u533b\u9662\u2026\u2026\u201d\u5f20\u606a\u4e4d\u770b\u5230\u8bb8\u601d\u5373\u5c06\u5d29\u6e83\u3001\u7edd\u671b\u7684\u6837\u5b50\u65f6\uff0c\u4ed6\u4e5f\u5fc3\u614c\u610f\u4e71\uff0c\u5934\u8111\u91cc\u4e00\u7247\u7a7a\u767d\uff0c\u8fc7\u540e\u624d\u60f3\u8d77\u4ed6\u7684\u624b\u673a\u5728\u533b\u9662\u91cc\u7ed9\u5b59\u9759\u6aac\u62a2\u8fc7\u53bb\u7838\u4eba\u4e86\u3002\u53ef\u80fd\u7838\u4e2d\u845b\u660e\u4fe1\u5f8b\u5e08\u7684\u773c\u7a9d\u65f6\uff0c\u7535\u6c60\u649e\u677e\u8131\u4e86\uff0c\u53c8\u6454\u5230\u533b\u9662\u7684\u5730\u4e0a\uff0c\u88c5\u7535\u6c60\u7684\u4e00\u89d2\u6454\u53d8\u4e86\u5f62\uff0c\u6ca1\u529e\u6cd5\u88c5\u56de\u7535\u6c60\u7ee7\u7eed\u4f7f\u7528\uff0c\u4e5f\u6ca1\u6765\u5f97\u53ca\u66f4\u6362\u65b0\u624b\u673a\uff08\u4e0a\u4e00\u7ae0\u5173\u4e8e\u624b\u673a\u7684\u63cf\u8ff0\u6709\u4e9b\u9519\u8bef\uff0c\u8fd9\u91cc\u4fee\u6b63\u4e00\u4e0b\uff09\u3002</p><p>\u3000\u3000\u8bb8\u601d\u8f7b\u6342\u7740\u5634\u5507\uff0c\u6b49\u7136\u7684\u95ee\u9053\uff1a\u201c\u90a3\u6211\u521a\u624d\u4f1a\u4e0d\u4f1a\u6709\u4e9b\u5931\u793c\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u90fd\u8fd9\u6837\u4e86\uff0c\u8c01\u8fd8\u7ba1\u5f97\u4e86\u592a\u591a\uff1f\u4ed6\u5e2e\u5510\u5a67\u6253\u5f00\u8f66\u95e8\uff0c\u8ba9\u6cea\u6d41\u6ee1\u9762\u7684\u5979\u8d70\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u66fe\u8ddf\u4f60\u8bf4\u8d77\u8fc7\uff0c\u4e5d\u56db\u5e74\u590f\u5929\u6211\u505a\u8fc7\u4e00\u4e2a\u79bb\u5947\u800c\u771f\u5b9e\u7684\u68a6\uff0c\u5728\u68a6\u91cc\u6211\u5df2\u7ecf\u5ea6\u8fc7\u4e09\u5341\u5c81\u7684\u4eba\u751f\uff0c\u5c31\u662f\u56e0\u4e3a\u4e00\u573a\u8f66\u7978\u8ba9\u6211\u68a6\u4e2d\u7684\u4eba\u751f\u621b\u7136\u800c\u6b62\u2026\u2026\u5982\u679c\u68a6\u4e2d\u7684\u4eba\u4e5f\u6709\u611f\u60c5\u7684\u8bdd\uff0c\u4e0d\u77e5\u9053\u4ed6\u4eec\u4f1a\u600e\u4e48\u770b\u5f85\u6211\u7684\u79bb\u53bb\uff1f\u201d\u5f20\u606a\u5fc3\u60f3\u81ea\u5df1\u53ea\u6015\u6c38\u8fdc\u90fd\u6ca1\u6cd5\u641e\u6e05\u8fd9\u4e2a\u95ee\u9898\u4e86\uff0c\u7238\u5988\u3001\u675c\u98de\u4ee5\u53ca\u5468\u6653\u7490\u6216\u8bb8\u4f1a\u96be\u8fc7\uff0c\u96be\u8fc7\u4e00\u9635\u5b50\u4e5f\u5c31\u8fc7\u53bb\u4e86\uff0c\u521a\u8ba4\u8bc6\u7684\u5f20\u5a67\u6216\u8bb8\u4f1a\u89c9\u5f97\u6709\u4e9b\u60cb\u60dc\uff0c\u6b63\u6240\u8c13\u201c\u4eb2\u621a\u6216\u4f59\u60b2\uff0c\u4ed6\u4eba\u4ea6\u5df2\u6b4c\uff0c\u6b7b\u53bb\u4f55\u6240\u9053\uff0c\u6258\u4f53\u540c\u5c71\u963f\u201d\u3002</p><p>\u3000\u3000\u5510\u5a67\u4f9d\u5728\u5f20\u606a\u7684\u6000\u91cc\uff0c\u624b\u53cd\u8fc7\u6765\u6402\u7740\u4ed6\u7684\u8170\uff0c\u8138\u988a\u8d34\u5728\u4ed6\u7684\u80f8\u53e3\uff0c\u53c8\u63a2\u8fc7\u5934\u770b\u4e86\u7ad9\u5728\u5f20\u606a\u53e6\u4e00\u4fa7\u7684\u8bb8\u601d\u4e00\u773c\uff0c\u5782\u4e0b\u773c\u5e18\u770b\u7740\u8def\u7259\u4e0a\u7684\u6708\u5149\uff0c\u8f7b\u58f0\u7684\u8bf4\u9053\uff1a\u201c\u6211\u89c9\u5f97\u6211\u5e94\u8be5\u6068\u4f60\u624d\u662f\uff0c\u5374\u60f3\u5230\u8fd9\u5934\u732a\u771f\u6709\u4ec0\u4e48\u610f\u5916\uff0c\u6211\u4e5f\u4f1a\u6709\u8ddf\u4f60\u4e00\u6837\u7684\u4f24\u5fc3\u2026\u2026\u201d\u65e0\u7591\u662f\u627f\u8ba4\u8bb8\u601d\u5bf9\u5f20\u606a\u771f\u631a\u7684\u611f\u60c5\u5316\u89e3\u4e86\u5979\u5bf9\u8bb8\u601d\u7684\u654c\u610f\u3002</p><p>\u3000\u3000\u4e24\u4e2a\u5973\u4eba\u89c1\u9762\u5230\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6709\u4e0a\u6f14\u5200\u5149\u5251\u5f71\u7684\u4e00\u5e55\uff0c\u5f20\u606a\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u4f38\u624b\u60f3\u5c06\u8bb8\u601d\u4e5f\u6402\u5728\u6000\u91cc\uff0c\u8fd9\u5de6\u62e5\u53f3\u62b1\u7684\u7f8e\u68a6\u8fd8\u521a\u8d77\u5934\uff0c\u8170\u4e0b\u5374\u7ed9\u5510\u5a67\u72e0\u72e0\u7684\u6390\u4e86\u4e00\u4e0b\uff0c\u8bb8\u601d\u90a3\u8fb9\u4e5f\u8eb2\u5f00\u4e86\u3002\u5f20\u606a\u53ea\u80fd\u9000\u800c\u6c42\u53d6\u5176\u6b21\uff1a\u53ea\u8981\u76f8\u5b89\u65e0\u4e8b\u5c31\u597d\u3002</p><p>\u3000\u3000\u603b\u4e0d\u80fd\u4e00\u76f4\u7ad9\u5728\u8f66\u7978\u73b0\u573a\uff0c\u60f3\u60f3\u5fc3\u91cc\u5c31\u53d1\u6bdb\uff0c\u5f20\u606a\u5de6\u76fc\u53f3\u987e\u95ee\u8bb8\u601d\u3001\u5510\u5a67\uff1a\u201c\u6211\u4eec\u63a5\u4e0b\u6765\u53bb\u54ea\u91cc\uff1f\u4eca\u5929\u665a\u4e0a\u4f60\u4eec\u4fe9\u8c01\u90fd\u4e0d\u8981\u60f3\u7529\u6389\u6211\u2026\u2026\u201d\u65e0\u8bba\u8ddf\u8bb8\u601d\u5728\u4e00\u8d77\uff0c\u5c06\u5510\u5a67\u5f03\u4e4b\u4e0d\u987e\uff0c\u8fd8\u662f\u4e0e\u5510\u5a67\u5728\u4e00\u8d77\uff0c\u5c06\u8bb8\u601d\u5f03\u4e4b\u4e0d\u987e\uff0c\u90fd\u4f1a\u6709\u4e9b\u6b8b\u5fcd\u3002</p><p>\u3000\u3000\u5510\u5a67\u53c8\u62ac\u5934\u770b\u4e86\u4e00\u773c\u8bb8\u601d\uff0c\u81ea\u5df1\u4e0d\u60f3\u79bb\u5f00\u5f20\u606a\uff0c\u4e5f\u77e5\u9053\u5979\u5b9a\u7136\u4e5f\u4e0d\u613f\u8fd9\u6837\u7684\u591c\u665a\u6ca1\u6709\u5f20\u606a\u76f8\u4f34\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u95ee\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u53bb\u5f71\u6e7e\u56ed\u770b\u770b\uff0c\u6211\u4e00\u76f4\u60f3\u53bb\u6765\u7740\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u70b9\u70b9\u5934\uff0c\u5f20\u606a\u8f6c\u8eab\u6253\u5f00\u8f66\u95e8\uff0c\u521a\u8981\u94bb\u8fdb\u53bb\uff0c\u8863\u9886\u5c31\u7ed9\u5510\u5a67\u4ece\u540e\u9762\u6293\u4f4f\u3002</p><p>\u3000\u3000\u201c\u4f60\u8d70\u8def\uff0c\u6211\u4eec\u5750\u8f66\u3002\u201d</p><p>\u3000\u3000\u867d\u8bf4\u5f71\u6e7e\u56ed\u5728\u5730\u7406\u4e0a\u4e5f\u5c5e\u4e8e\u6d45\u6c34\u6e7e\u533a\u57df\uff0c\u8d70\u8def\u8fc7\u53bb\u5374\u6709\u4e94\u516c\u91cc\u7684\u8def\u7a0b\uff0c\u5f20\u606a\u82e6\u7b11\u7740\u8138\uff0c\u5510\u5a67\u5df2\u7ecf\u94bb\u8fdb\u8f66\u5c06\u8f66\u95e8\u4ece\u91cc\u9762\u9501\u4e0a\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5927\u6b65\u6d41\u661f\u8ddf\u7740\u8f66\u5b50\u671d\u5f71\u6e7e\u56ed\u8d70\u53bb\uff0c\u5510\u5a67\u4e5f\u662f\u94c1\u4e86\u5fc3\u8981\u6298\u817e\u4ed6\u4e00\u4e0b\uff0c\u786c\u662f\u8ba9\u4ed6\u82b1\u4e86\u63a5\u8fd1\u4e00\u4e2a\u5c0f\u65f6\u7684\u65f6\u95f4\u624d\u8d70\u5230\u5f71\u6e7e\u56ed\uff0c\u597d\u597d\u7684\u4e00\u53cc\u76ae\u978b\u5c31\u7ed9\u6bc1\u4e86\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u4e00\u5bb6\u4e4b\u524d\u5c31\u6682\u65f6\u5bc4\u5c45\u5728\u5f71\u6e7e\u56ed\uff0c\u8fd8\u6709\u8bb8\u591a\u4eba\u4f4f\u5728\u5f71\u6e7e\u56ed\uff0c\u848b\u8587\u8fd9\u4e9b\u8d8a\u79c0\u4e0e\u9526\u6e56\u9999\u6e2f\u5b50\u516c\u53f8\u7684\u9ad8\u7ea7\u7ba1\u7406\u4eba\u5458\u4e5f\u90fd\u4f4f\u5728\u5f71\u6e7e\u56ed\uff1b\u5b59\u9759\u9999\u5373\u4f7f\u4e0d\u8ddf\u7236\u6bcd\u4f4f\uff0c\u5979\u4e5f\u5355\u72ec\u4f4f\u5728\u5f71\u6e7e\u56ed\uff1b\u8bb8\u7ef4\u4e0e\u6c5f\u9edb\u513f\u8981\u4e0d\u662f\u521a\u5de7\u56de\u5185\u5730\u62cdM V\uff0c\u4e0d\u7136\u5979\u4eec\u4e5f\u4f4f\u5728\u5f71\u6e7e\u56ed\u3002</p><p>\u3000\u3000\u8fdb\u4e86\u8bb8\u601d\u5728\u5f71\u6e7e\u56ed\u91cc\u7684\u516c\u5bd3\uff0c\u4e09\u4e2a\u4eba\u5c31\u90fd\u5750\u5728\u5ba2\u5385\u91cc\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u8bdd\u9898\u597d\u804a\uff0c\u6c89\u9ed8\u4e86\u4e00\u4f1a\u513f\uff0c\u5f20\u606a\u5c31\u7ad9\u8d77\u6765\u53bb\u716e\u5496\u5561\u3002\u7aef\u7740\u70ed\u5496\u5561\u4e0a\u6765\uff0c\u4e09\u4e2a\u4eba\u5c31\u56f4\u7740\u5496\u5561\u51e0\u800c\u5750\uff0c\u8bb8\u601d\u9009\u4e86\u4e00\u4e2a\u8270\u6da9\u7684\u8bdd\u9898\uff0c\u4e5f\u5c31\u662f\u5c06\u4e5d\u56db\u5e74\u590f\u5929\u5510\u5b66\u8c26\u88ab\u7acb\u6848\u5ba1\u67e5\u4ee5\u53ca\u5979\u4e0e\u5f20\u606a\u8ba4\u8bc6\u524d\u540e\u7684\u8be6\u7ec6\u60c5\u51b5\u8bf4\u7ed9\u5510\u5a67\u542c\uff1a\u201c\u2026\u2026\u6211\u539f\u4ee5\u4e3a\u6211\u8981\u4e3a\u81ea\u5df1\u7684\u884c\u4e3a\u53d7\u5230\u5e94\u6709\u7684\u60e9\u7f5a\uff0c\u68c0\u5bdf\u9662\u53c8\u51fa\u4e4e\u610f\u6599\u7684\u6ca1\u6709\u8ffd\u7a76\u6211\u7684\u7f6a\u8d23\uff0c\u5373\u4f7f\u5982\u6b64\uff0c\u6211\u5fc3\u91cc\u4e00\u76f4\u90fd\u611f\u5230\u4e0d\u5b89\uff0c\u5fc3\u60c5\u4e5f\u4e0d\u5927\u597d\u3002\u9000\u8fd8\u6b3e\u662f\u4ed6\u5e2e\u6211\u57ab\u4e86\u5341\u4e8c\u4e07\uff0c\u6211\u53bb\u627e\u4ed6\uff0c\u4ed6\u8bf4\u8981\u62ff\u90a3\u5341\u4e8c\u4e07\u5c06\u6211\u4eca\u540e\u4e09\u5e74\u7684\u4eba\u751f\u4e70\u4e0b\u6765\uff0c\u6211\u6ca1\u6709\u529e\u6cd5\u62d2\u7edd\uff0c\u5c31\u7ed9\u4ed6\u6307\u4f7f\u4e1c\u5954\u897f\u8d70\u3001\u505a\u8fd9\u505a\u90a3\uff0c\u4e5f\u770b\u5230\u9526\u6e56\u50cf\u5947\u8ff9\u4e00\u6837\u51fa\u73b0\u5728\u9762\u524d\u3002\u5176\u5b9e\u4e5d\u56db\u5e74\u8fc7\u53bb\u4e4b\u540e\uff0c\u6211\u624d\u6e10\u6e10\u611f\u89c9\u5230\u81ea\u5df1\u8131\u80ce\u6362\u9aa8\u822c\u7684\u83b7\u5f97\u91cd\u751f\u3002\u6211\u90fd\u4e0d\u6562\u5962\u671b\u4ec0\u4e48\uff0c\u66f4\u6ca1\u6709\u5962\u671b\u80fd\u6c38\u8fdc\u7684\u7559\u5728\u4ed6\u7684\u8eab\u8fb9\u3002\u8fdc\u8fdc\u7684\u51dd\u671b\u4e5f\u597d\uff0c\u54ea\u6015\u53ea\u80fd\u7a7a\u5bc4\u4e00\u7f15\u601d\u5ff5\u4e5f\u597d\uff0c\u53ea\u8981\u80fd\u611f\u89c9\u5230\u4ed6\u7684\u5b58\u5728\uff0c\u53ef\u80fd\u662f\u591c\u91cc\u77ad\u671b\u540c\u4e00\u4e2a\u6697\u84dd\u8272\u7684\u591c\u7a7a\uff0c\u5c31\u6ca1\u6709\u522b\u7684\u5962\u6c42\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5f88\u4e0d\u6ee1\u610f\u8bb8\u601d\u4e0e\u5510\u5a67\u8c08\u8bdd\u65f6\u8bf4\u5230\u81ea\u5df1\u90fd\u62ff\u201c\u4ed6\u201d\u6765\u4ee3\u66ff\uff0c\u81ea\u5df1\u660e\u660e\u5c31\u5728\u5979\u4eec\u773c\u76ae\u5b50\u5e95\u4e0b\uff0c\u8fc7\u53bb\u7684\u8bdd\u9898\u603b\u662f\u6c89\u91cd\uff0c\u8bb8\u601d\u4e3b\u52a8\u4ea4\u5f85\u7684\u672a\u514d\u592a\u591a\u4e86\u4e00\u4e9b\uff0c\u5f20\u606a\u5728\u6728\u5236\u5496\u5561\u51e0\u4e0b\u8e22\u4e86\u8e22\u8bb8\u601d\u7684\u811a\uff0c\u5510\u5a67\u5374\u8f6c\u8fc7\u5934\u6765\u770b\u7740\u4ed6\uff1a\u201c\u4f60\u8e22\u6211\u811a\u505a\u4ec0\u4e48\uff1f\u8be5\u4e0d\u4f1a\u8fd8\u60f3\u7792\u6211\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u5f20\u606a\u4f4e\u4e0b\u5934\u4e00\u770b\uff0c\u8bb8\u601d\u8170\u4fa7\u7684\u811a\u53ef\u4e0d\u662f\u5510\u5a67\u4f38\u51fa\u6765\u5417\uff1f</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5510\u5a67\u7684\u624b\u673a\u54cd\u4e86\u8d77\u6765\uff0c\u5979\u6293\u8d77\u770b\u8fc7\u4e4b\u540e\u9012\u7ed9\u5f20\u606a\uff1a\u201c\u5085\u4fca\u7684\u7535\u8bdd\uff0c\u5e94\u8be5\u662f\u627e\u4f60\u7684\u3002\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5085\u4fca\u4e0d\u4f1a\u56e0\u4e3a\u4e00\u822c\u4e8b\u60c5\u5c06\u7535\u8bdd\u6253\u5230\u5510\u5a67\u7684\u624b\u673a\u4e0a\u627e\u81ea\u5df1\uff0c\u5f20\u606a\u63a5\u8fc7\u624b\u673a\uff0c\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\u3002</p></article>"}}}]);
