(window.webpackJsonp=window.webpackJsonp||[]).push([[78],{100:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u516d\u8282 \u518d\u89c1\u683c\u62c9\u5179\u7279",content:"<article><h2>\u7b2c\u4e09\u5341\u516d\u8282 \u518d\u89c1\u683c\u62c9\u5179\u7279</h2><p>\u5546\u961f\u8001\u677f\u5f00\u59cb\u553e\u6cab\u56db\u6e85\u5730\u8bb2\u89e3\u8d77\u6765\u3002\u539f\u6765\u81ea\u4ece\u9634\u9b42\u57ce\u56de\u5f52\u7269\u8d28\u754c\uff0c\u81f3\u4eca\u5df2\u7ecf\u5feb\u5230\u534a\u5e74\u2014\u2014\u8fd9\u5c0f\u534a\u5e74\u671f\u95f4\uff0c\u9634\u9b42\u57ce\u53ef\u6ca1\u6709\u95f2\u7740\uff0c\u800c\u662f\u56db\u5904\u5174\u98ce\u4f5c\u6d6a\u3002\u743c\u6069\u539f\u672c\u5728\u5deb\u5e08\u5b66\u6821\u91cc\uff0c\u5bf9\u5916\u754c\u6d88\u606f\u4e0d\u7075\u901a\uff0c\u5e76\u65e0\u4e86\u89e3\uff1b\u540e\u6765\u7a81\u7136\u88ab\u6254\u5230\u5927\u6c99\u6f20\u91cc\u6765\uff0c\u8db3\u8db3\u8d70\u4e86\u5927\u534a\u4e2a\u6708\u624d\u51fa\u6765\uff0c\u5bf9\u9634\u9b42\u57ce\u5230\u5e95\u5728\u5e72\u4ec0\u4e48\u66f4\u662f\u4e00\u65e0\u6240\u77e5\u3002\u5982\u4eca\u542c\u5546\u961f\u8001\u677f\u8bf4\uff0c\u539f\u6765\u81ea\u4ece\u9634\u9b42\u57ce\u4e00\u56de\u5f52\uff0c\u7acb\u523b\u5c31\u63d2\u624b\u6b63\u5728\u6fc0\u70c8\u8fdb\u884c\u7684\u827e\u5f17\u62c9\u65af\u5361\u5927\u6218\uff0c\u521d\u65f6\u4ed6\u4eec\u548c\u6df1\u6c34\u57ce\u8054\u624b\uff0c\u5f88\u5feb\u53c8\u76df\u7ea6\u7834\u88c2\uff0c\u9690\u9690\u654c\u5bf9\uff1b\u7d27\u63a5\u7740\uff0c\u9634\u9b42\u57ce\u53c8\u53d1\u5175\u8fdb\u5165\u8ff7\u65af\u5353\u8bfa\uff0c\u527f\u6740\u8d39\u6797\u9b54\u8475\u2014\u2014\u5982\u679c\u8fd9\u4e9b\u90fd\u8fd8\u4e0d\u80fd\u4ee4\u4eba\u60f4\u60f4\u4e0d\u5b89\u7684\u8bdd\uff0c\u90a3\u4e48\u6700\u65b0\u4f20\u6765\u7684\u6d88\u606f\u66f4\u52a0\u60ca\u4eba\u3002</p><p>\u5c31\u5728\u524d\u51e0\u5929\uff0c\u4e00\u4e2a\u6d88\u606f\u6d41\u4f20\u51fa\u6765\uff1a\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u4eec\uff0c\u53d1\u52a8\u4e86\u4e00\u4e2a\u5f3a\u5927\u7684\u9b54\u6cd5\uff0c\u4ed6\u4eec\u521b\u9020\u51fa\u906e\u5929\u853d\u65e5\u7684\u9634\u5f71\u7403\u4f53\uff0c\u51c6\u5907\u878d\u5316\u81f3\u9ad8\u51b0\u5ddd\u7684\u7ec8\u5e74\u79ef\u96ea\u3002\u81f3\u9ad8\u51b0\u5ddd\u4f4d\u4e8e\u57c3\u8bfa\u5965\u514b\u6c99\u6f20\u5317\u90e8\uff0c\u5982\u679c\u9634\u9b42\u57ce\u7684\u8fd9\u4e00\u75af\u72c2\u4e3e\u52a8\u6210\u529f\uff0c\u90a3\u4e48\u5927\u91cf\u7684\u6d2a\u6c34\u5c06\u4f1a\u6d8c\u5165\u6c99\u6f20\uff0c\u6539\u53d8\u5f53\u5730\u6c14\u5019\uff0c\u91cd\u73b0\u6614\u65e5\u7684\u6c83\u571f\u3002</p><p>\u201c\u4ec0\u2026\u2026\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u4f60\u521a\u624d\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u81f3\u9ad8\u51b0\u5ddd\u5730\u57df\u5343\u91cc\uff0c\u7ec8\u5e74\u51b0\u96ea\uff0c\u5982\u679c\u771f\u88ab\u6d88\u878d\uff0c\u5927\u9646\u5317\u90e8\u52bf\u5fc5\u4f1a\u53d1\u4e00\u573a\u6d69\u5927\u6d2a\u6c34\u3002\u9634\u9b42\u57ce\u53cd\u6b63\u662f\u6f02\u5728\u7a7a\u4e2d\uff0c\u6839\u672c\u65e0\u6240\u8c13\uff0c\u4f46\u5176\u4ed6\u8fd9\u4e9b\u5bb6\u56ed\u57ce\u90a6\u548c\u5c45\u6c11\uff0c\u5c82\u4e0d\u90fd\u8981\u9677\u5165\u6c6a\u6d0b\u3002</p><p>\u8fd9\u4e2a\u6d88\u606f\u4e00\u65e6\u6cc4\u6f0f\uff0c\u52bf\u5fc5\u8981\u5f15\u8d77\u5927\u9646\u5317\u90e8\u8bf8\u56fd\u7684\u4e00\u81f4\u653b\u51fb\u2026\u2026\u9634\u9b42\u57ce\u7684\u5927\u5965\u672f\u5e08\u4eec\uff0c\u5c45\u7136\u75af\u72c2\u81f3\u6b64\u4e48\uff1f\u8fd8\u662f\u8bf4\u4ed6\u4eec\u6709\u8db3\u591f\u7684\u81ea\u4fe1\uff0c\u5c31\u7b97\u4e0e\u5168\u5929\u4e0b\u4e3a\u654c\u90fd\u4e0d\u653e\u5728\u5fc3\u4e0a\uff1f</p><p>\u75af\u4e86\uff01</p><p>\u867d\u7136\u81ea\u5df1\u4e5f\u6765\u81ea\u9634\u9b42\u57ce\uff0c\u597d\u6b79\u90a3\u91cc\u7b97\u662f\u81ea\u5df1\u7684\u5bb6\u4e61\uff0c\u4f46\u743c\u6069\u4e5f\u4e0d\u5f97\u4e0d\u8bf4\uff0c\u9634\u9b42\u57ce\u7684\u9ad8\u5c42\u9886\u8896\u4eec\u771f\u662f\u53d1\u75af\u4e86\u3002\u878d\u5316\u81f3\u9ad8\u51b0\u5ddd\uff0c\u5176\u76ee\u7684\u81ea\u7136\u662f\u8981\u6539\u53d8\u5927\u6c99\u6f20\u7684\u73af\u5883\uff0c\u4e3a\u91cd\u5efa\u6614\u65e5\u8f89\u714c\u7684\u8010\u745f\u5e1d\u56fd\u505a\u51c6\u5907\uff0c\u4f46\u8fd9\u4e48\u73a9\u4e5f\u672a\u514d\u73a9\u5f97\u592a\u5927\u4e86\u70b9\u5427\uff0c\u4f1a\u6210\u4e3a\u4f17\u77e2\u4e4b\u7684\u7684\u3002</p><p>\u201c\u8fd9\u6d88\u606f\u662f\u771f\u7684\uff1f\u201d\u6885\u83f2\u65af\u4e5f\u5fae\u5fae\u76b1\u8d77\u7709\u5934\u3002</p><p>\u201c\u771f\u7684\uff01\u201d\u5546\u961f\u8001\u677f\u8fde\u8fde\u70b9\u5934\uff0c\u201c\u5df2\u7ecf\u786e\u8ba4\u4e86\u3002\u201d</p><p>\u6b63\u56e0\u4e3a\u786e\u8ba4\u4e86\u8fd9\u4e2a\u6d88\u606f\uff0c\u6240\u4ee5\u5317\u5730\u8bf8\u56fd\u6216\u57ce\u90a6\uff0c\u5df2\u7ecf\u5f00\u59cb\u7740\u624b\u5bf9\u4ed8\u9634\u9b42\u57ce\u3002\u4f4d\u4e8e\u57c3\u8bfa\u5965\u514b\u6c99\u6f20\u4e1c\u5357\u65b9\uff0c\u5f3a\u5927\u7684\u79d1\u7c73\u5c14\u738b\u56fd\uff0c\u5df2\u7ecf\u6d3e\u9063\u519b\u961f\u5bf9\u9634\u9b42\u57ce\u53d1\u52a8\u8fdb\u653b\u4e86\u3002</p><p>\u963f\u5fb7\u5df4\u57ce\u4f4d\u4e8e\u6c99\u6f20\u8fb9\u7f18\uff0c\u5c5e\u4e8e\u94f6\u6708\u8054\u90a6\uff08\u8d39\u4f26\u5317\u5730\u6700\u5f3a\u5927\u7684\u738b\u56fd\uff09\uff0c\u81ea\u7136\u4e5f\u53c2\u4e0e\u5176\u4e2d\u3002\u867d\u7136\u8fd8\u6ca1\u6709\u5bf9\u9634\u9b42\u57ce\u6b63\u5f0f\u5ba3\u6218\uff0c\u4f46\u6697\u4e2d\u5df2\u7ecf\u5728\u5389\u5175\u79e3\u9a6c\u3002\u63d0\u5c14\u6559\u4f1a\u4f5c\u4e3a\u5927\u9646\u4e0a\u52bf\u529b\u6700\u5f3a\u5927\u7684\u6559\u4f1a\u4e4b\u4e00\uff0c\u53c8\u4e00\u8d2f\u4ee5\u7ef4\u62a4\u5927\u9646\u7684\u516c\u6b63\u548c\u5e73\u4e3a\u5df1\u4efb\uff0c\u81ea\u7136\u4e5f\u63d2\u8db3\u5176\u4e2d\uff0c\u5982\u4eca\u5df2\u7ecf\u9690\u7136\u662f\u76df\u53cb\u5173\u7cfb\u3002</p><p>\u6885\u83f2\u65af\u662f\u63d0\u5c14\u6559\u4f1a\u7684\u5723\u6b66\u58eb\uff0c\u4f4d\u9636\u975e\u5e38\u9ad8\uff1b\u77ee\u4eba\u4eec\u786e\u5b9a\u4e86\u5979\u7684\u8eab\u4efd\u540e\uff0c\u81ea\u7136\u4e0d\u6562\u7559\u96be\uff0c\u8ba9\u5979\u987a\u987a\u5f53\u5f53\u5730\u8fdb\u6765\u4e86\u3002</p><p>\u8fd9\u81ea\u7136\u662f\u597d\u6d88\u606f\uff0c\u610f\u5473\u7740\u4ee5\u540e\u5728\u8def\u4e0a\u82e5\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6885\u83f2\u65af\u4eae\u51fa\u8eab\u4efd\u4fbf\u80fd\u83b7\u5f97\u4f18\u5f85\u2014\u2014\u4f46\u540c\u65f6\u4e5f\u662f\u574f\u6d88\u606f\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u7684\u8138\u8272\u90fd\u6709\u4e9b\u9634\u6c89\u4e0b\u6765\u3002\u5deb\u5e08\u662f\u62c5\u5fc3\u5018\u82e5\u6709\u4eba\u77e5\u9053\u81ea\u5df1\u6765\u81ea\u9634\u9b42\u57ce\uff0c\u53ea\u6015\u7acb\u523b\u8981\u88ab\u5f53\u4f5c\u5978\u7ec6\u6293\u8d77\u6765\uff1b\u5973\u5723\u6b66\u58eb\u62c5\u5fc3\u7684\u81ea\u7136\u662f\u9634\u9b42\u57ce\u90a3\u4e2a\u75af\u72c2\u8ba1\u5212\uff0c\u5018\u82e5\u6210\u529f\uff0c\u5c31\u662f\u4e00\u573a\u6d69\u52ab\u3002</p><p>\u5148\u4e0d\u53bb\u8003\u8651\u8fd9\u4e9b\u95ee\u9898\uff0c\u65e2\u7136\u5df2\u7ecf\u5230\u4e86\u963f\u5fb7\u5df4\u57ce\uff0c\u4e24\u4eba\u4e0e\u5546\u961f\u8001\u677f\u544a\u522b\u3002\u4e00\u8def\u6253\u542c\uff0c\u5230\u96c6\u5e02\u96c7\u4e86\u8f86\u56db\u8f6e\u9a6c\u8f66\uff0c\u518d\u914d\u4e00\u4e2a\u8f66\u592b\uff0c\u7ea6\u5b9a\u540e\u5929\u4e00\u65e9\u542f\u7a0b\uff0c\u628a\u4ed6\u4eec\u9001\u5230\u535a\u5f97\u4e4b\u95e8\u3002\u5f53\u505a\u5b8c\u8fd9\u4e00\u5207\uff0c\u5929\u4e5f\u5df2\u7ecf\u9ed1\u4e86\u3002</p><p>\u963f\u5fb7\u5df4\u57ce\u867d\u7136\u770b\u8d77\u6765\u50cf\u519b\u4e8b\u5821\u5792\u2014\u2014\u5b9e\u9645\u4e0a\u4e5f\u662f\u5821\u5792\uff0c\u4f46\u540c\u65f6\u4e5f\u662f\u5546\u4e1a\u8d38\u6613\u5927\u57ce\uff0c\u9152\u9986\u65c5\u5e97\u662f\u5f88\u591a\u7684\u3002\u77ee\u4eba\u53a8\u5e08\u505a\u7684\u996d\u83dc\uff0c\u53e3\u5473\u90fd\u6709\u4e9b\u7279\u522b\uff0c\u4e0d\u5408\u743c\u6069\u548c\u6885\u83f2\u65af\u7684\u80c3\u53e3\uff0c\u4f46\u5e8a\u94fa\u8fd8\u662f\u5f88\u67d4\u8f6f\u7684\u2014\u2014\u552f\u4e00\u7684\u95ee\u9898\u662f\uff0c\u5e8a\u6709\u70b9\u77ed\uff0c\u5927\u6982\u662f\u4f9d\u636e\u77ee\u4eba\u4eec\u81ea\u5df1\u7684\u4f53\u578b\u6765\u5236\u4f5c\u7684\uff0c\u6ca1\u6709\u8003\u8651\u5230\u4eba\u7c7b\u5ba2\u4eba\u7684\u9700\u6c42\u3002</p><p>\u4eca\u665a\u743c\u6069\u4e0d\u51c6\u5907\u518d\u8ba9\u7f8e\u4eba\u72ec\u5b88\u7a7a\u5e8a\uff0c\u4ed6\u8981\u6c42\u5973\u5723\u6b66\u58eb\u5c65\u884c\u5e94\u5c3d\u7684\u4e49\u52a1\uff0c\u6885\u83f2\u65af\u51b7\u7740\u8138\uff0c\u4f46\u5e76\u6ca1\u6709\u62d2\u7edd\uff0c\u53ea\u662f\u575a\u51b3\u4e0d\u80af\u6309\u7167\u743c\u6069\u7684\u8981\u6c42\u8131\u5149\u8863\u670d\uff0c\u4e00\u5b9a\u8981\u7a7f\u7740\u886c\u886b\u957f\u88e4\uff0c\u6263\u5b50\u6263\u5f97\u4e25\u4e25\u5b9e\u5b9e\uff0c\u6700\u540e\u5deb\u5e08\u53ea\u597d\u59a5\u534f\u3002\u743c\u6069\u4e00\u8fb9\u770b\u7740\u5979\u8dea\u5728\u81ea\u5df1\u80ef\u95f4\uff0c\u4fcf\u8138\u8d77\u4f0f\uff0c\u52aa\u529b\u670d\u4f8d\u81ea\u5df1\uff0c\u4e00\u8fb9\u8086\u610f\u73a9\u5f04\u5979\u7684\u80f8\u90e8\u3002\u201c\u53ef\u60dc\u5c0f\u4e86\u70b9\uff0c\u201d\u4ed6\u7565\u6709\u4e9b\u9057\u61be\uff0c\u201c\u6050\u6015\u4e0d\u9002\u5408\u73a9\u2026\u2026\u201d</p><p>\u518d\u4e00\u6b21\u5728\u6885\u83f2\u65af\u7684\u5c0f\u5634\u91cc\u6ee1\u8db3\u540e\uff0c\u743c\u6069\u62b1\u7740\u5979\u6c89\u6c89\u5730\u7761\u53bb\u3002</p><p>\u203b\u203b\u203b</p><p>\u6885\u83f2\u65af\u8737\u7f29\u7740\u8eab\u4f53\uff0c\u80cc\u8d34\u7740\u743c\u6069\u7684\u80f8\u819b\uff0c\u88ab\u4ed6\u63fd\u5728\u6000\u4e2d\uff0c\u611f\u89c9\u88ab\u6e29\u6696\u7684\u7537\u4eba\u6c14\u606f\u5305\u56f4\u3002\u5bf9\u4e8e\u5979\u6765\u8bf4\uff0c\u8fd9\u662f\u751f\u5e73\u7684\u7b2c\u4e00\u6b21\u7ecf\u5386\uff0c\u4e0d\u514d\u6709\u4e9b\u5f02\u6837\u548c\u53e4\u602a\uff0c\u5c24\u5176\u662f\u8fd9\u4e2a\u7537\u4eba\u5e76\u975e\u81ea\u5df1\u7684\u7231\u4eba\uff0c\u521a\u521a\u8fd8\u5728\u8086\u610f\u5730\u51cc\u8fb1\u7740\u81ea\u5df1\u3002</p><p>\u4e0d\u8fc7\uff0c\u8fd9\u4e48\u88ab\u4ece\u80cc\u540e\u73af\u62b1\u7740\uff0c\u6709\u79cd\u5f88\u5b89\u5fc3\u7684\u611f\u89c9\u5462\u3002</p><p>\u4ece\u5c0f\u7684\u7279\u6b8a\u7ecf\u5386\uff0c\u8ba9\u6885\u83f2\u65af\u7684\u60f3\u6cd5\u603b\u662f\u5f88\u7279\u522b\uff0c\u548c\u5176\u4ed6\u5973\u5b69\u4f3c\u4e4e\u683c\u683c\u4e0d\u5165\u3002\u5bf9\u4e8e\u743c\u6069\uff0c\u5979\u5e76\u6ca1\u6709\u591a\u5c11\u6124\u6012\uff0c\u4f46\u4e5f\u8c08\u4e0d\u4e0a\u6709\u4ec0\u4e48\u611f\u60c5\uff0c\u7eaf\u7cb9\u662f\u4e2a\u4ea4\u6613\u3002\u5979\u77e5\u9053\uff0c\u8fd9\u4e16\u754c\u4e0a\u6709\u4e9b\u4e8b\u60c5\u662f\u5fc5\u987b\u53bb\u505a\u7684\uff1b\u5979\u4e5f\u77e5\u9053\uff0c\u6216\u8005\u8bf4\u76f8\u4fe1\u8fd9\u4e16\u754c\u662f\u516c\u5e73\u7684\uff0c\u5f97\u5230\u597d\u5904\uff0c\u603b\u8981\u4ed8\u51fa\u4ee3\u4ef7\u3002\u743c\u6069\u5e2e\u5979\u53d6\u56de\u4e86\u9ece\u660e\u4e4b\u77f3\uff0c\u6362\u53d6\u4e86\u5bf9\u5979\u7684\u5360\u6709\uff0c\u8fd9\u5f88\u516c\u5e73\u3002\u6885\u83f2\u65af\u4ece\u6765\u5c31\u4e0d\u6307\u671b\u4e00\u4e2a\u840d\u6c34\u76f8\u9022\u7684\u964c\u751f\u4eba\u4f1a\u5927\u53d1\u5584\u5fc3\u5e2e\u52a9\u81ea\u5df1\u3002</p><p>\u6216\u8bb8\u5f88\u591a\u6f02\u4eae\u5973\u5b69\u90fd\u4f1a\u89c9\u5f97\u81ea\u5df1\u6709\u7279\u6743\uff0c\u90fd\u89c9\u5f97\u5468\u56f4\u7684\u4eba\u2014\u2014\u5c24\u5176\u662f\u7537\u4eba\u90fd\u5e94\u8be5\u4e89\u5148\u6050\u540e\u5730\u5e2e\u52a9\u81ea\u5df1\uff0c\u4e3a\u81ea\u5df1\u6548\u547d\uff0c\u4f46\u6885\u83f2\u65af\u7684\u8111\u888b\u91cc\uff0c\u4ece\u6ca1\u6709\u8fc7\u8fd9\u79cd\u60f3\u6cd5\u3002\u4ece\u5979\u8bb0\u4e8b\u8d77\uff0c\u5979\u5c31\u53ea\u8bb0\u5f97\u6bcf\u5929\u7684\u751f\u6d3b\u4e3b\u9898\u662f\u6740\u622e\u548c\u9003\u4ea1\u2014\u2014\u8fd9\u79cd\u68a6\u9b47\u4e00\u822c\u7684\u751f\u6d3b\u4e00\u76f4\u6301\u7eed\u4e86\u8fd1\u5341\u5e74\uff0c\u953b\u70bc\u51fa\u5979\u575a\u6bc5\u521a\u5f3a\u7684\u6027\u683c\uff0c\u76f4\u5230\u5979\u88ab\u5927\u4e3b\u6559\u770b\u4e2d\uff0c\u52a0\u5165\u63d0\u5c14\u6559\u4f1a\u3002\u5979\u575a\u5b88\u51c6\u5219\uff0c\u5d07\u5c1a\u5584\u826f\uff0c\u7ef4\u62a4\u79e9\u5e8f\uff0c\u606a\u5b88\u5f8b\u6cd5\uff0c\u4f46\u5979\u540c\u65f6\u4e5f\u76f8\u4fe1\u7b49\u4ef7\u4ea4\u6362\u7684\u9053\u7406\u3002</p><p>\u4e0d\u8fc7\uff0c\u4f3c\u4e4e\u603b\u662f\u6709\u4e9b\u9057\u61be\u5c31\u662f\u4e86\u3002</p><p>\u6885\u83f2\u65af\u662f\u4e2a\u5c11\u5973\uff0c\u4e0d\u662f\u4fee\u5973\uff0c\u5979\u4e5f\u66fe\u65e0\u6570\u6b21\u5730\u5e7b\u60f3\u8fc7\u81ea\u5df1\u5c06\u6765\u4f1a\u5bf9\u4ec0\u4e48\u6837\u7684\u7537\u5b50\u52a8\u5fc3\uff0c\u4f1a\u548c\u4ec0\u4e48\u6837\u7684\u4eba\u643a\u624b\u5171\u5ea6\u7ec8\u751f\u3002\u63d0\u5c14\u662f\u6b63\u4e49\u4e4b\u795e\uff0c\u5e76\u4e0d\u8bb2\u7a76\u7981\u6b32\uff0c\u63d0\u5c14\u7684\u5723\u6b66\u58eb\u5728\u7537\u5973\u60c5\u7231\u548c\u5a5a\u5ac1\u95ee\u9898\u4e0a\u662f\u5b8c\u5168\u81ea\u7531\u7684\u3002\u6885\u83f2\u65af\u957f\u5f97\u5f88\u6f02\u4eae\uff0c\u6240\u4ee5\u4e0d\u4e4f\u8ffd\u6c42\u8005\uff0c\u4f46\u90fd\u77e5\u96be\u800c\u9000\u4e86\uff0c\u56e0\u4e3a\u8fd9\u4f4d\u5973\u5723\u6b66\u58eb\u6ca1\u6709\u4e00\u4e2a\u80fd\u770b\u5f97\u4e0a\u773c\u3002</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u5979\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u559c\u6b22\u4ec0\u4e48\u6837\u7684\u7537\u5b50\u3002\u82e5\u8981\u8bf4\u82f1\u4fca\uff0c\u8ffd\u6c42\u8005\u4e2d\u4e0d\u4e4f\u7f8e\u7537\u5b50\uff1b\u82e5\u8981\u8bf4\u6743\u52bf\u5730\u4f4d\uff0c\u4e5f\u6709\u5f88\u591a\u8d35\u65cf\u5b50\u5f1f\u5411\u5979\u8868\u8fbe\u7231\u610f\uff1b\u82e5\u8981\u8bf4\u672c\u9886\uff0c\u5979\u4e5f\u66fe\u9047\u4e0a\u8fc7\u5f88\u591a\u58f0\u540d\u663e\u8d6b\u7684\u82f1\u96c4\u4eba\u7269\uff1b\u4f46\u770b\u6765\u770b\u53bb\uff0c\u4f3c\u4e4e\u603b\u662f\u6ca1\u6709\u80fd\u4ee4\u5979\u771f\u6b63\u52a8\u5fc3\u7684\u3002</p><p>\u743c\u6069\u81ea\u7136\u4e5f\u4e0d\u5305\u62ec\u5728\u5185\u3002</p><p>\u7b2c\u4e00\u773c\u770b\u5230\u743c\u6069\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u5c31\u51c6\u786e\u5730\u5224\u65ad\u51fa\u4e86\u4ed6\u7684\u8eab\u4efd\uff1a\u4e00\u4e2a\u521d\u51fa\u8305\u5e90\u7684\u5c0f\u5deb\u5e08\u3002\u8bba\u76f8\u8c8c\uff0c\u743c\u6069\u8fd8\u7b97\u4e0d\u9519\uff0c\u4f46\u4e5f\u6ca1\u5230\u5149\u5f69\u56db\u5c04\u7684\u5730\u6b65\uff1b\u8bba\u672c\u9886\uff0c\u6885\u83f2\u65af\u77e5\u9053\u4ed6\u751a\u81f3\u90fd\u63a5\u4e0d\u4e0b\u81ea\u5df1\u4e00\u5251\uff1b\u81f3\u4e8e\u6743\u52bf\u5730\u4f4d\uff0c\u90a3\u5c31\u66f4\u4e0d\u7528\u8bf4\uff0c\u5b64\u8eab\u4e00\u4eba\u7070\u5934\u571f\u8138\u5728\u6c99\u6f20\u91cc\u8270\u96be\u8dcb\u6d89\uff0c\u80fd\u6709\u591a\u9ad8\u7684\u8eab\u4efd\u3002</p><p>\u81f3\u4e8e\u743c\u6069\u540e\u6765\u7684\u8868\u73b0\uff0c\u90a3\u5c31\u66f4\u548c\u5176\u4ed6\u7537\u4eba\u6ca1\u4ec0\u4e48\u4e24\u6837\u3002\u5077\u770b\u5979\u6d17\u6fa1\uff0c\u88ab\u5979\u4e22\u8fdb\u6e56\u91cc\u4e86\uff1b\u5728\u57c3\u65af\u8003\u9762\u5bf9\u4eba\u9762\u72ee\u7684\u65f6\u5019\uff0c\u4ed6\u8eb2\u4e86\u8d77\u6765\uff0c\u76f4\u5230\u6700\u540e\u624d\u9732\u9762\u6551\u4e86\u81ea\u5df1\u4e00\u547d\u2014\u2014\u6885\u83f2\u65af\u5f88\u6e05\u695a\uff0c\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u81ea\u5df1\u662f\u4e2a\u6f02\u4eae\u5973\u5b69\uff0c\u5982\u679c\u4e0d\u662f\u56e0\u4e3a\u743c\u6069\u770b\u4e0a\u81ea\u5df1\uff0c\u90a3\u8fd9\u4e2a\u5deb\u5e08\u662f\u7edd\u5bf9\u4e0d\u4f1a\u53d1\u5584\u5fc3\u7684\u3002</p><p>\u7b80\u5355\u6765\u8bf4\uff0c\u5728\u6885\u83f2\u65af\u770b\u6765\uff0c\u743c\u6069\u5c31\u662f\u4e2a\u666e\u901a\u4eba\u3002</p><p>\u4f46\u4ed6\u8fd8\u662f\u6709\u70b9\u7279\u522b\u7684\u3002</p><p>\u5177\u4f53\u4ec0\u4e48\u5730\u65b9\u7279\u522b\uff0c\u6885\u83f2\u65af\u4e5f\u8bf4\u4e0d\u51fa\u6765\uff0c\u4f46\u81f3\u5c11\u5979\u77e5\u9053\uff0c\u5f88\u5c11\u6709\u4eba\u4f1a\u5192\u7740\u4e27\u547d\u7684\u5371\u9669\uff0c\u5e2e\u5979\u53bb\u593a\u56de\u9ece\u660e\u4e4b\u77f3\u3002</p><p>\u800c\u4e14\u4ed6\u786e\u5b9e\u6210\u529f\u4e86\u3002\u5728\u6885\u83f2\u65af\u88ab\u683c\u62c9\u5179\u7279\u5b9a\u4f4f\u4e4b\u540e\uff0c\u4ed6\u51fb\u6740\u4e86\u6240\u6709\u7684\u4eba\u9762\u72ee\uff0c\u51fb\u9000\u4e86\u6076\u9b54\uff0c\u5e2e\u5979\u53d6\u56de\u4e86\u9ece\u660e\u4e4b\u77f3\u3002\u867d\u7136\u743c\u6069\u77e5\u9053\u4e8b\u5b9e\u5e76\u975e\u5982\u6b64\uff0c\u6697\u4e2d\u6709\u4e2a\u4eba\u5728\u51fa\u624b\u76f8\u52a9\uff0c\u4f46\u5f53\u65f6\u6885\u83f2\u65af\u88ab\u6076\u9b54\u7684\u5f3a\u5927\u6cd5\u672f\u5b9a\u4f4f\u4e86\uff0c\u4e0d\u4f46\u538b\u5236\u4f4f\u4e86\u8eab\u4f53\u884c\u52a8\uff0c\u800c\u4e14\u51e0\u4e4e\u5c01\u9501\u4e86\u4e00\u5207\u611f\u5b98\uff0c\u5305\u62ec\u542c\u89c9\u3002\u6240\u4ee5\u5979\u5e76\u4e0d\u77e5\u60c5\uff0c\u4ee5\u4e3a\u662f\u743c\u6069\u72ec\u529b\u529e\u5230\u7684\u3002</p><p>\u5979\u5f53\u7136\u4e0d\u4f1a\u8ba4\u4e3a\u743c\u6069\u662f\u6df1\u85cf\u4e0d\u9732\u7684\u9ad8\u4eba\uff0c\u53ea\u4f1a\u89c9\u5f97\u8fd9\u5bb6\u4f19\u7684\u8fd0\u6c14\u4e0d\u9519\uff0c\u6216\u8005\u53c8\u662f\u51ed\u501f\u4ec0\u4e48\u5f3a\u5927\u7684\u9b54\u6cd5\u7269\u54c1\u594f\u529f\u3002\u4f46\u603b\u800c\u8a00\u4e4b\uff0c\u743c\u6069\u505a\u5230\u4e86\uff0c\u9ece\u660e\u4e4b\u77f3\u4ece\u4eba\u9762\u72ee\u90a3\u91cc\u53d6\u56de\u6765\u4e86\u3002</p><p>\u6240\u4ee5\u5982\u4eca\u81ea\u5df1\u6b63\u8eba\u5728\u4ed6\u6000\u91cc\u3002</p><p>\u6309\u7167\u534f\u8bae\uff0c\u81ea\u5df1\u8fd9\u4e00\u8f88\u5b50\uff0c\u4fbf\u8981\u4e3a\u743c\u6069\u6548\u547d\uff0c\u4ece\u76ee\u524d\u6765\u770b\uff0c\u672a\u5fc5\u4f1a\u6210\u4e3a\u59bb\u5b50\uff0c\u4f46\u81f3\u5c11\u662f\u5e8a\u4f34\u3002\u7537\u4eba\u603b\u662f\u8fd9\u6837\u5427\uff0c\u7eb5\u7136\u660e\u77e5\u4e00\u4e2a\u5973\u4eba\u5e76\u4e0d\u7231\u81ea\u5df1\uff0c\u5374\u4f9d\u65e7\u8981\u628a\u5979\u7262\u7262\u5360\u6709\u7740\u3002</p><p>\u6885\u83f2\u65af\u5bf9\u743c\u6069\u8c08\u4e0d\u4e0a\u6076\u611f\uff0c\u4e5f\u8bf4\u4e0d\u4e0a\u4ec0\u4e48\u597d\u611f\uff0c\u5728\u5979\u5fc3\u91cc\uff0c\u4e00\u76f4\u5c31\u53ea\u628a\u4ed6\u89c6\u4e3a\u4e00\u4e2a\u666e\u901a\u4eba\uff0c\u73b0\u5728\u4e5f\u8fd8\u662f\u5982\u6b64\u3002\u8fd9\u4e5f\u5c31\u662f\u5979\u5fc3\u4e2d\u90a3\u70b9\u9057\u61be\u7684\u6765\u6e90\u4e86\uff0c\u5982\u679c\u6709\u53ef\u80fd\u7684\u8bdd\uff0c\u6885\u83f2\u65af\u81ea\u7136\u8fd8\u662f\u5e0c\u671b\u80fd\u548c\u81ea\u5df1\u771f\u5fc3\u559c\u6b22\u7684\u4eba\u5171\u5ea6\u4e00\u751f\u7684\u3002</p><p>\u5c11\u5973\u6c89\u601d\u7740\uff0c\u5026\u610f\u6e10\u6e10\u88ad\u6765\uff0c\u8be5\u7761\u4e86\u3002\u8fd9\u6b21\u53bb\u535a\u5f97\u4e4b\u95e8\uff0c\u8def\u9014\u9065\u8fdc\uff0c\u660e\u5929\u8981\u53bb\u91c7\u8d2d\u8865\u7ed9\u3002\u51fa\u95e8\u5728\u5916\uff0c\u603b\u662f\u6709\u4e00\u5806\u9ebb\u70e6\u4e8b\u60c5\uff0c\u53ef\u60dc\u8fd9\u91cc\u662f\u77ee\u4eba\u57ce\u5e02\uff0c\u656c\u5949\u77ee\u4eba\u8bf8\u795e\uff0c\u6ca1\u6709\u63d0\u5c14\u7684\u6559\u4f1a\u5b58\u5728\uff0c\u5426\u5219\u6885\u83f2\u65af\u53ea\u8981\u4eae\u51fa\u8eab\u4efd\uff0c\u4ee5\u5979\u7684\u4f4d\u9636\uff0c\u81ea\u7136\u6709\u4eba\u7acb\u523b\u628a\u4e00\u5207\u90fd\u51c6\u5907\u597d\uff0c\u65e0\u9700\u64cd\u5fc3\u3002\u5982\u4eca\u8fd9\u4e9b\u7410\u788e\u4e8b\u52a1\uff0c\u5c31\u5f97\u81ea\u5df1\u4eb2\u81ea\u52a8\u624b\u4e86\u3002\u540e\u5929\u4e00\u65e9\uff0c\u5c31\u8981\u542f\u7a0b\u4e0a\u8def\uff0c\u597d\u5728\u6b64\u540e\u5c31\u662f\u5e73\u5766\u5927\u9053\u76f4\u901a\u535a\u5f97\u4e4b\u95e8\uff0c\u5e94\u8be5\u4e0d\u4f1a\u518d\u6709\u4ec0\u4e48\u610f\u5916\u53d8\u6545\u4e86\u3002</p><p>\u6b63\u5f53\u5979\u6452\u5f03\u8111\u4e2d\u6240\u6709\u4e71\u4e03\u516b\u7cdf\u7684\u5ff5\u5934\uff0c\u51c6\u5907\u5165\u7761\u7684\u65f6\u5019\uff0c\u7a81\u7136\u770b\u89c1\u4e00\u4e2a\u9ed1\u8272\u7684\u8eab\u5f71\u3002</p><p>\u90a3\u662f\u4e00\u4e2a\u9ad8\u5927\u82f1\u4fca\u7684\u7537\u4eba\uff0c\u6b63\u501a\u9760\u5728\u7a97\u524d\uff0c\u671d\u5979\u5fae\u5fae\u800c\u7b11\uff0c\u773c\u4e2d\u6cdb\u7740\u8be1\u5f02\u7684\u7eff\u5149\u3002\u4ed6\u7684\u8170\u95f4\u4f69\u7740\u4e00\u67c4\u94f6\u8272\u86c7\u5f62\u77ed\u5251\uff0c\u53f3\u624b\u6709\u610f\u65e0\u610f\u5730\u6469\u6332\u7740\u5251\u67c4\uff0c\u5373\u4fbf\u5728\u9ed1\u6697\u4e2d\uff0c\u6885\u83f2\u65af\u4e5f\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff1a\u90a3\u53ea\u9ed1\u8272\u7684\u624b\u638c\u4e0a\uff0c\u957f\u7740\u516d\u6839\u624b\u6307\u3002</p><p>\u4e4c\u9eef\u541b\u4e3b\u683c\u62c9\u5179\u7279\uff01</p></article>"}}}]);
