(window.webpackJsonp=window.webpackJsonp||[]).push([[780],{803:function(p){p.exports={chapterTitle:null,title:"\u7b2c779\u7ae0 \u574f\u6d88\u606f",content:"<article><h2>\u7b2c779\u7ae0 \u574f\u6d88\u606f</h2><p>\u3000\u3000\u5f20\u606a\u63a5\u8fc7\u5085\u4fca\u7684\u7535\u8bdd\uff0c\u624d\u77e5\u9053\u4ed6\u4e0e\u8bb8\u601d\u3001\u5510\u5a67\u79bb\u5f00\u4e4b\u540e\uff0c\u591c\u91cc\u5b59\u9759\u6aac\u7a81\u53d1\u9ad8\u70ed\uff0c\u4eba\u660f\u53a5\u8fc7\u53bb\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a\u59c6\u5988\u9047\u8f66\u7978\u8fc7\u4e16\u3001\u5b59\u9759\u6aac\u5979\u60b2\u4f24\u8fc7\u5ea6\u7684\u539f\u56e0\uff0c\u5b59\u5bb6\u7684\u79c1\u4eba\u533b\u751f\u5df2\u7ecf\u8d76\u5230\u5b59\u5b85\u6709\u4e00\u9635\u5b50\u4e86\uff0c\u5b59\u9759\u6aac\u5979\u4eba\u8fd8\u6ca1\u6709\u82cf\u9192\u8fc7\u6765\uff0c\u5b59\u5c1a\u4e49\u592b\u5987\u4e0e\u5b59\u9759\u9999\u7126\u6025\u5f97\u4e0d\u5f97\u4e86\uff0c\u5085\u4fca\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u6709\u5fc5\u8981\u8ddf\u5f20\u606a\u8a00\u8bed\u4e00\u58f0\u3002</p><p>\u3000\u3000\u201c\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff1f\u201d\u8bb8\u601d\u89c1\u5f20\u606a\u63a5\u8fc7\u7535\u8bdd\u7709\u5934\u8e59\u4e86\u8d77\u6765\uff0c\u5173\u5fc3\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u591c\u91cc\u53bb\u5b59\u5b85\u672c\u662f\u8981\u5546\u91cf\u600e\u4e48\u53bb\u5bf9\u4ed8\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\uff0c\u8ba9\u4ed6\u4eec\u4e3a\u4eca\u5929\u53d1\u751f\u7684\u8f66\u7978\u4ed8\u51fa\u5e94\u5f97\u7684\u4ee3\u4ef7\uff0c\u201d\u5f20\u606a\u4f38\u624b\u62b9\u4e86\u62b9\u7709\u68a2\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u5085\u4fca\u8fd8\u7559\u5728\u5b59\u5b85\uff0c\u4ed6\u6253\u7535\u8bdd\u544a\u8bc9\u6211\uff0c\u5b59\u9759\u6aac\u521a\u521a\u7a81\u53d1\u9ad8\u70ed\u660f\u53a5\u8fc7\u53bb\u2026\u2026\u533b\u751f\u521a\u8d76\u5230\u90a3\u91cc\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u8981\u4e0d\u8981\u7d27\uff1f\u4f60\u8fc7\u53bb\u770b\u4e00\u4e0b\u5427\uff0c\u201d\u8bb8\u601d\u62c5\u5fe7\u7684\u8bf4\u9053\uff0c\u201c\u4e5f\u4e0d\u80fd\u803d\u6401\u4e86\u6b63\u4e8b\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u79bb\u5f00\uff0c\u4f60\u4eec\u4fe9\u7559\u5728\u8fd9\u91cc\u4f1a\u4e0d\u4f1a\u62ff\u8d77\u83dc\u5200\u5c31\u5bf9\u780d\u8d77\u6765\uff1f\u201d\u5f20\u606a\u7ad9\u8d77\u8eab\u6765\uff0c\u89c1\u8bb8\u601d\u3001\u5510\u5a67\u90fd\u6ca1\u6709\u8981\u8ddf\u4ed6\u53bb\u5b59\u5b85\u770b\u4e00\u770b\u7684\u610f\u89c1\uff0c\u77e5\u9053\u5979\u4eec\u8fd9\u65f6\u5019\u53bb\u89c1\u5916\u4eba\u6709\u5fc3\u7406\u969c\u788d\uff0c\u5c31\u591a\u95ee\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u6eda\u4f60\u7684\u3002\u201d\u5510\u5a67\u6293\u8d77\u6c99\u53d1\u4e0a\u7684\u9760\u6795\u5c31\u671d\u5f20\u606a\u7838\u53bb\uff0c\u8bb8\u601d\u557c\u7b11\u7686\u975e\uff0c\u7ad9\u8d77\u6765\u63a8\u7740\u5f20\u606a\u5f80\u95e8\u5916\u8d70\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u65f6\u5019\u4f60\u8fd8\u6709\u5fc3\u601d\u5f00\u73a9\u7b11\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u9a7e\u8f66\u56de\u5230\u6d45\u6c34\u6e7e\u5927\u9053\u7684\u5b59\u5b85\uff0c\u4ed6\u5230\u90a3\u91cc\uff0c\u5b59\u9759\u6aac\u5df2\u7ecf\u82cf\u9192\u8fc7\u6765\u3002\u8d70\u5230\u5b59\u9759\u6aac\u7684\u623f\u95f4\u91cc\uff0c\u770b\u5230\u5979\u8eba\u5728\u5e8a\u4e0a\u6302\u540a\u6ef4\uff0c\u624d\u77ed\u77ed\u51e0\u4e2a\u5c0f\u65f6\u7684\u5de5\u592b\uff0c\u5979\u7cbe\u81f4\u7f8e\u4e3d\u7684\u8138\u86cb\u5df2\u7ecf\u6709\u7ed9\u4eba\u524a\u7626\u3001\u6194\u60b4\u7684\u611f\u89c9\uff0c\u5f80\u65e5\u6d41\u8f6c\u77b3\u5149\u7684\u7738\u5b50\u6ca1\u6709\u795e\u91c7\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u5c31\u7a81\u7136\u53d1\u9ad8\u70ed\u5462\uff1f\u201d\u5f20\u606a\u8d70\u51fa\u6765\u95ee\u5728\u4e00\u65c1\u62a4\u7406\u7684\u5b59\u9759\u9999\u3002</p><p>\u3000\u3000\u201c\u60c5\u7eea\u6ce2\u52a8\u592a\u5927\uff0c\u53ea\u8981\u5979\u60c5\u7eea\u80fd\u7a33\u5b9a\u4e0b\u6765\uff0c\u5c31\u4e0d\u4f1a\u6709\u4ec0\u4e48\u4e8b\u60c5\u4e86\u3002\u201d\u845b\u660e\u73cf\u5728\u65c1\u8bf4\u9053\uff0c\u201c\u8981\u662f\u6211\u6b7b\u4e86\uff0c\u90fd\u4e0d\u89c1\u5f97\u8fd9\u4e2b\u5934\u80fd\u6709\u8fd9\u6837\u7684\u4f24\u5fc3\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u65f6\u5019\u4f60\u8fd8\u6709\u5fc3\u601d\u8ba1\u8f83\u8fd9\u4e2a\uff1f\u201d\u5b59\u5c1a\u4e49\u6447\u4e86\u6447\u5934\uff0c\u62c9\u7740\u5f20\u606a\u5230\u4e66\u623f\u91cc\u53bb\u3002\u8fd9\u65f6\u5019\u4e5f\u6ca1\u6709\u4eba\u95ee\u4ed6\u8ddf\u8bb8\u601d\u3001\u5510\u5a67\u8d70\u540e\u7684\u60c5\u5f62\u3002</p><p>\u3000\u3000\u201c\u8bb0\u8005\u5df2\u7ecf\u5728\u91c7\u8bbf\u73e0\u5ac2\u7684\u5973\u513f\u4e86\uff0c\u73e0\u5ac2\u5973\u513f\u4e5f\u5c06\u63d0\u51fa\u845b\u836b\u5747\u5371\u9669\u9a7e\u8f66\u81f4\u4f7f\u73e0\u5ac2\u6b7b\u4ea1\u7684\u8bc9\u8bbc\u3002\u201d\u5b59\u5c1a\u4e49\u5728\u4e66\u623f\u91cc\u8ddf\u5f20\u606a\u8bf4\u8d77\u4ed6\u5df2\u7ecf\u5b89\u6392\u597d\u7684\u4e8b\u60c5\uff0c\u6345\u5230\u5a92\u4f53\u53c8\u8ffd\u8bc9\u845b\u836b\u5747\u5211\u4e8b\u8d23\u4efb\u7b49\u7b49\u63aa\u65bd\u4e0d\u8fc7\u662f\u4e00\u5207\u7684\u8bf1\u56e0\uff0c\u8981\u5c06\u771f\u6b63\u7684\u76ee\u7684\u2014\u2014\u4ee5\u5c3d\u91cf\u5c0f\u7684\u4ee3\u4ef7\u5728\u4e0a\u5e02\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u83b7\u5f97\u8bdd\u8bed\u6743\u2014\u2014\u906e\u63a9\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5b59\u5c1a\u4e49\u5728\u4e66\u623f\u5546\u91cf\u5230\u51cc\u6668\u56db\u70b9\u3002</p><p>\u3000\u3000\u73e0\u5ac2\u53bb\u4e16\uff0c\u5b59\u9759\u6aac\u53c8\u4f24\u5fc3\u8fc7\u5ea6\uff0c\u5b59\u5c1a\u4e49\u5c31\u4e0d\u80fd\u966a\u540c\u5f20\u606a\u4e00\u8d77\u53bb\u5317\u4eac\u4e86\uff0c\u65f6\u95f4\u5df2\u7ecf\u5f88\u665a\u4e86\uff0c\u4e58\u98de\u673a\u8fd8\u8981\u8d76\u65e9\uff0c\u5b59\u5c1a\u4e49\u4fbf\u8ba9\u5f20\u606a\u5148\u56de\u53bb\u4f11\u606f\u3002</p><p>\u3000\u3000\u79bb\u5f00\u65f6\uff0c\u5f20\u606a\u53c8\u53bb\u5b59\u9759\u6aac\u623f\u95f4\u770b\u4e86\u4e00\u773c\uff0c\u5b59\u9759\u6aac\u5df2\u7ecf\u7761\u4e86\u8fc7\u53bb\uff0c\u5979\u7684\u5bb9\u989c\u8ba9\u4eba\u770b\u4e86\u695a\u695a\u751f\u601c\uff0c\u8c01\u80fd\u60f3\u5230\u8fd9\u59ae\u5b50\u5e73\u65f6\u91cc\u5f20\u7259\u821e\u722a\uff0c\u7adf\u4e5f\u6709\u5982\u6b64\u67d4\u5f31\u7684\u65f6\u523b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u56de\u5230\u5f71\u6e7e\u56ed\uff0c\u4ed6\u4e34\u8d70\u65f6\u62ff\u4e86\u8bb8\u601d\u7684\u94a5\u5319\uff0c\u8fd9\u65f6\u5019\u76f4\u63a5\u5f00\u95e8\u8fdb\u53bb\uff0c\u8d77\u5c45\u5ba4\u91cc\u7684\u706f\u5149\u8fd8\u4eae\u7740\uff0c\u8bb8\u601d\u4e0e\u5510\u5a67\u4e24\u4e2a\u4eba\u90fd\u62b1\u4e86\u4e00\u4e2a\u9760\u6795\u6b6a\u7740\u8eab\u5b50\u7761\u5728\u6c99\u53d1\u4e0a\uff0c\u5165\u68a6\u540e\u7684\u8138\u86cb\u7ed9\u4eba\u9759\u8c27\u5a34\u96c5\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5510\u5a67\u767d\u7699\u7684\u8138\u86cb\u4e0a\u4e5f\u900f\u7740\u4e00\u5c42\u67d4\u548c\u7684\u5149\u6cfd\uff0c\u4e94\u5b98\u7cbe\u81f4\u52a8\u4eba\uff0c\u6e05\u79c0\u7684\u4e0b\u5df4\u6bd4\u4ee5\u5f80\u7565\u5706\u6da6\u4e00\u4e9b\uff0c\u8eab\u4e0a\u7684\u9752\u6da9\u9010\u6e10\u892a\u53bb\uff0c\u900f\u9732\u51fa\u66f4\u8ff7\u4eba\u7684\u98ce\u60c5\uff1b\u8bb8\u601d\u66f4\u662f\u6709\u7740\u98a0\u5012\u4f17\u751f\u7684\u7f8e\uff0c\u8138\u988a\u7684\u6cea\u75d5\u8fd8\u6ca1\u6709\u62b9\u6389\u3002</p><p>\u3000\u3000\u8f7b\u8f7b\u7684\u63a9\u4e0a\u95e8\uff0c\u5f20\u606a\u7ad9\u5728\u95e8\u8fb9\u770b\u7740\u8bb8\u601d\u4e0e\u5510\u5a67\u9759\u8c27\u5165\u7761\u7684\u753b\u9762\uff0c\u8f7b\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u91cd\u65b0\u6765\u4e16\u95f4\u8d70\u4e00\u906d\uff0c\u5374\u65e0\u6cd5\u53ea\u5bf9\u4e00\u4e2a\u5973\u4eba\u949f\u60c5\uff0c\u8981\u60f9\u8fd9\u4e9b\u7cbe\u7075\u822c\u5b58\u5728\u7684\u5973\u4eba\u4f24\u5fc3\uff0c\u771f\u662f\u9020\u5b7d\u3002\u5bb3\u6015\u5510\u5a67\u4f1a\u5c31\u6b64\u62c2\u8896\u79bb\u5f00\uff0c\u5fc3\u614c\u614c\u3001\u62c5\u60ca\u53d7\u6015\u7684\uff0c\u8fd9\u6216\u8bb8\u662f\u91cd\u56de\u4e5d\u56db\u5e74\u7684\u4e00\u4e9b\u4ee3\u4ef7\uff1f</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u60f3\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u4e0d\u60ca\u52a8\u4e24\u4eba\uff0c\u4e0d\u5c0f\u5fc3\u78b0\u5230\u5730\u677f\u4e0a\u7684\u98df\u54c1\u5305\u88c5\u888b\u53d1\u51fa\u54cd\u58f0\u3002\u5510\u5a67\u662f\u90a3\u79cd\u5165\u7761\u540e\u5c31\u5f88\u96be\u7ed9\u53eb\u9192\u7684\u4eba\uff0c\u8bb8\u601d\u7741\u5f00\u60fa\u677e\u7684\u773c\u7738\uff0c\u89c1\u662f\u5f20\u606a\u56de\u6765\u4e86\uff0c\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u95ee\u4ed6\uff1a\u201c\u9759\u6aac\u600e\u4e48\u6837\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6211\u8ddf\u5b59\u5148\u751f\u5546\u91cf\u4e8b\u60c5\u5230\u73b0\u5728\uff0c\u79bb\u5f00\u65f6\uff0c\u5979\u5df2\u7ecf\u7761\u7740\u4e86\uff0c\u201d\u5f20\u606a\u5750\u5230\u8bb8\u601d\u7684\u8eab\u8fb9\uff0c\u6293\u8d77\u5979\u7684\u624b\u5728\u624b\u5fc3\u91cc\u6162\u6162\u7684\u6469\u6332\u7740\uff0c\u671d\u7761\u5728\u6c99\u53d1\u5bf9\u9762\u7684\u5510\u5a67\u5476\u4e86\u5476\u5634\uff0c\u201c\u6211\u8d70\u540e\uff0c\u4f60\u4eec\u90fd\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u80fd\u8bf4\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5c31\u628a\u4f60\u8fd9\u51e0\u5e74\u505a\u8fc7\u7684\u4e8f\u5fc3\u4e8b\u90fd\u544a\u8bc9\u5979\u4e86\uff0c\u4f60\u79bb\u5f00\u4e0d\u4e45\u8fd8\u63a5\u5230\u665a\u6674\u7684\u7535\u8bdd\uff0c\u5979\u660e\u5929\u4e5f\u8981\u53bb\u5317\u4eac\u63a5\u82b7\u5f64\u56de\u6d77\u5dde\u4e0a\u5b66\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u4f1a\u5728\u6211\u8fd9\u91cc\u3002\u201d\u8bb8\u601d\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u5230\u5b59\u5b85\u540e\u624d\u91cd\u65b0\u6362\u4e86\u65b0\u624b\u673a\uff0c\u665a\u6674\u4e4b\u524d\u81ea\u7136\u8054\u7cfb\u4e0d\u4e0a\u4ed6\uff0c\u665a\u6674\u6253\u7535\u8bdd\u627e\u8bb8\u601d\u4e5f\u6b63\u5e38\uff0c\u53ea\u662f\u4eca\u5929\u5f20\u606a\u6709\u4e9b\u654f\u611f\u4e86\uff0c\u542c\u5230\u8bb8\u601d\u8bf4\u665a\u6674\u6253\u7535\u8bdd\u6765\uff0c\u4ed6\u5fc3\u795e\u4e00\u51db\uff0c\u5fc3\u810f\u90fd\u5feb\u63d0\u5230\u55d3\u5b50\u773c\u4e86\uff0c\u4e0b\u610f\u8bc6\u7684\u538b\u4f4e\u58f0\u97f3\uff0c\u5634\u5df4\u51d1\u5230\u8bb8\u601d\u7684\u8033\u8fb9\uff0c\u95ee\u9053\uff1a\u201c\u665a\u6674\u7684\u4e8b\u60c5\uff0c\u4f60\u6ca1\u6709\u544a\u8bc9\u5c0f\u5a67\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u54ea\u6562\u8bf4\u554a\uff1f\u201d\u8bb8\u601d\u7a0d\u6709\u4e9b\u5e7d\u6028\u7684\u8bf4\u9053\uff0c\u201c\u77a7\u4f60\u505a\u7684\u4e8b\u60c5\uff0c\u8fd8\u8981\u522b\u4eba\u66ff\u4f60\u63d0\u5fc3\u540a\u80c6\u7684\u3002\u201d</p><p>\u3000\u3000\u4e4b\u524d\u8ba1\u5212\u4e0e\u5b59\u5c1a\u4e49\u4e00\u8d77\u53bb\u5317\u4eac\u627e\u53f6\u81fb\u6c11\u8ba8\u8bba\u672a\u6765\u7684\u4e9a\u6d32\u7ecf\u6d4e\u5f62\u52bf\uff0c\u4ed6\u4eec\u8981\u501f\u52bf\u5728\u5609\u4fe1\u5730\u4ea7\u3001\u5609\u4fe1\u7535\u5b50\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\u83b7\u5f97\u8bdd\u8bed\u6743\uff0c\u5236\u8861\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u3002\u5b59\u9759\u6aac\u5982\u6b64\uff0c\u4f5c\u4e3a\u7236\u4eb2\u7684\u5b59\u5c1a\u4e49\u81ea\u7136\u4e0d\u80fd\u5b89\u5fc3\u79bb\u5f00\u9999\u6e2f\uff0c\u4e0d\u8fc7\u5f20\u606a\u4e4b\u524d\u5df2\u7ecf\u901a\u77e5\u7fdf\u4e39\u9752\u3001\u53f6\u5efa\u658c\u8fd8\u6709\u9648\u4fe1\u751f\u7b49\u4eba\u90fd\u8d76\u5230\u5317\u4eac\u6c47\u5408\u53c2\u4e0e\u8ba8\u8bba\uff0c\u8fd8\u7279\u610f\u8ba9\u9648\u4fe1\u751f\u8bf7\u8bb8\u9e3f\u4f2f\u4e00\u8d77\u5230\u5317\u4eac\u53bb\u3002\u665a\u6674\u6253\u7535\u8bdd\u8fc7\u6765\u7684\u610f\u601d\uff0c\u663e\u7136\u662f\u60f3\u5728\u5317\u4eac\u8ddf\u81ea\u5df1\u89c1\u9762\uff0c\u8fd9\u4e0b\u53ef\u597d\uff0c\u665a\u6674\u53c8\u4e0d\u53ef\u80fd\u5728\u5317\u4eac\u6ede\u7559\u51e0\u5929\uff0c\u81ea\u5df1\u53c8\u54ea\u91cc\u80fd\u62bd\u51fa\u7a7a\u6765\u53bb\u89c1\u5979\uff1f</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u63ea\u7709\u5934\u75bc\u7684\u6a21\u6837\uff0c\u8bb8\u601d\u8f7b\u8f7b\u6390\u4e86\u4ed6\u4e00\u4e0b\uff1a\u201c\u81ea\u4f5c\u5b7d\uff0c\u53ef\u6ca1\u6709\u4eba\u540c\u60c5\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u53ea\u8981\u4f60\u4e0d\u6068\u6211\u5c31\u884c\u4e86\u3002\u201d\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8146\u7740\u8138\u8981\u5c06\u8bb8\u601d\u6402\u5728\u6000\u91cc\u3002</p><p>\u3000\u3000\u8bb8\u601d\u624b\u6491\u7740\u5f20\u606a\u7684\u80f8\u53e3\uff0c\u4e0d\u8ba9\u4ed6\u5f97\u901e\u3002</p><p>\u3000\u3000\u6b63\u503c\u4e24\u4eba\u63a8\u63a8\u6402\u6402\u4e4b\u65f6\uff0c\u201c\u2026\u2026\u201d\u5510\u5a67\u627e\u4e86\u4e2a\u54c8\u6b20\uff0c\u7741\u5f00\u60fa\u677e\u7684\u773c\u7738\u9192\u4e86\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5510\u5a67\u770b\u5230\u5f20\u606a\u4e0e\u8bb8\u601d\u5750\u5728\u5bf9\u9762\u7684\u6c99\u53d1\u4e0a\uff0c\u8bf4\u9053\uff0c\u201c\u5728\u68a6\u91cc\u5c31\u770b\u5230\u4f60\u56de\u6765\u4e86\uff0c\u60f3\u8981\u9192\u8fc7\u6765\uff0c\u5728\u68a6\u91cc\u8fd8\u662f\u6323\u624e\u4e86\u597d\u4e00\u4f1a\u513f\u624d\u9192\u8fc7\u6765\u4e86\u2014\u2014\u4f60\u4ec0\u4e48\u65f6\u5019\u56de\u6765\u7684\uff1f\u201d</p><p>\u3000\u3000\u6709\u4e9b\u68a6\u5883\u4f1a\u771f\u5b9e\u5230\u8ba9\u4eba\u5982\u7f6e\u73b0\u5b9e\u7684\u4e16\u754c\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u201c\u8fd8\u6ca1\u6709\u51e0\u5206\u949f\uff0c\u770b\u4f60\u7761\u5f97\u8fd9\u4e48\u9999\uff0c\u5c31\u6ca1\u6709\u5435\u9192\u4f60\u3002\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u4ed6\u6253\u4e86\u54c8\u6b20\uff0c\u7761\u610f\u6cdb\u8d77\u6765\u3002\u8981\u8d76\u65e9\u6668\u7684\u98de\u673a\uff0c\u6ca1\u6709\u65f6\u95f4\u7761\u591a\u4e45\uff0c\u4f46\u662f\u4e0d\u7761\u89c9\uff0c\u767d\u5929\u4e00\u5b9a\u4f1a\u65e0\u7cbe\u6253\u91c7\u3002\u5f20\u606a\u5fc3\u91cc\u6709\u7740\u4e09\u4eba\u5927\u88ab\u540c\u5e8a\u7684\u68a6\u60f3\uff0c\u72b9\u8c6b\u4e86\u7247\u523b\uff0c\u89c9\u5f97\u8fd9\u65f6\u5019\u63d0\u8fd9\u6837\u7684\u8981\u6c42\u53ea\u6015\u6700\u7ec8\u4f1a\u662f\u9e21\u98de\u86cb\u6253\uff0c\u4e00\u4e2a\u4eba\u90fd\u635e\u4e0d\u7740\u3002\u4ed6\u4e5f\u5f88\u60f3\u6c14\u52bf\u51cc\u4eba\u7684\u95ee\u8bb8\u601d\u3001\u5510\u5a67\u201c\u4eca\u665a\u8c01\u966a\u6211\u7761\u89c9\u201d\u8fd9\u6837\u7684\u95ee\u9898\uff0c\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u89c9\u5f97\u8fd9\u65f6\u5019\u95ee\u8fd9\u6837\u7684\u95ee\u9898\u4e5f\u53ea\u4f1a\u81ea\u627e\u82e6\u5403\uff0c\u53ea\u5f97\u6293\u8d77\u4e00\u53ea\u9760\u6795\u8eab\u5b50\u6b6a\u5012\u5728\u6c99\u53d1\u4e0a\uff0c\u65e0\u5948\u53c8\u88c5\u53ef\u601c\u7684\u8bf4\u9053\uff1a\u201c\u5728\u4e0a\u98de\u673a\u524d\uff0c\u6211\u8981\u8d76\u7d27\u7761\u4e00\u89c9\uff0c\u8981\u662f\u8ddf\u6613\u4e91\u98de\u8c08\u8bdd\u65f6\u8fd8\u6253\u54c8\u6b20\uff0c\u4e22\u4eba\u5c31\u4e22\u5230\u5bb6\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u5c31\u5728\u6c99\u53d1\u4e0a\u7761\u5427\uff0c\u201d\u8bb8\u601d\u53c8\u5bf9\u5510\u5a67\u8bf4\u9053\uff0c\u201c\u4f60\u7761\u6211\u7684\u623f\u95f4\uff0c\u6211\u7761\u8bb8\u7ef4\u7684\u623f\u95f4\u53bb\u3002\u201d</p><p>\u3000\u3000\u53ef\u601c\u516e\u516e\u7684\u770b\u7740\u8bb8\u601d\u3001\u5510\u5a67\u5404\u81ea\u8fdb\u4e86\u5367\u5ba4\uff0c\u53ea\u5f97\u5012\u5934\u6b6a\u5728\u6c99\u53d1\u4e0a\u7761\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e03\u70b9\u949f\u51c6\u65f6\u6323\u624e\u7740\u8d77\u6765\uff0c\u5b8c\u5168\u6ca1\u6709\u7761\u8db3\uff0c\u5f20\u606a\u6253\u7740\u54c8\u6b20\u3001\u7741\u4e0d\u5f00\u773c\u5c31\u8981\u6478\u7d22\u7740\u8fdb\u6dcb\u6d74\u623f\u6d17\u6f31\uff0c\u819d\u76d6\u4e00\u4e0d\u5c0f\u5fc3\u649e\u5496\u5561\u684c\u89d2\u4e0a\u4e86\uff0c\u4e00\u75db\u4e4b\u4e0b\u6574\u4e2a\u4eba\u624d\u5b8c\u5168\u9192\u8fc7\u6765\u3002\u8bb8\u601d\u542c\u5230\u52a8\u9759\u8d70\u8fc7\u6765\uff0c\u770b\u5230\u5f20\u606a\u5728\u63c9\u819d\u76d6\uff0c\u8bf4\u4e86\u53e5\u201c\u6d3b\u8be5\u201d\u5c31\u5fd9\u5176\u4ed6\u4e8b\u60c5\u53bb\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u6d17\u6f31\u5b8c\u6bd5\uff0c\u63a8\u95e8\u8fdb\u4e86\u5367\u5ba4\uff0c\u5510\u5a67\u8fd8\u9759\u8c27\u7684\u62e5\u88ab\u7761\u7740\uff0c\u767d\u5f97\u6643\u773c\u7684\u80f3\u818a\u6a2a\u5728\u6de1\u84dd\u8272\u7684\u88ab\u5b50\u4e0a\uff0c\u5934\u53d1\u6563\u5f00\uff0c\u76d6\u4f4f\u5927\u534a\u5f20\u8138\uff0c\u5f20\u606a\u8d70\u8fc7\u53bb\uff0c\u8f7b\u8f7b\u7684\u62cd\u7740\u5979\u7684\u8138\uff0c\u5c06\u5979\u62cd\u9192\uff1a\u201c\u6211\u8981\u53bb\u4e58\u98de\u673a\u4e86\uff0c\u4f60\u518d\u7761\u4f1a\u513f\u5427\u3002\u201d</p><p>\u3000\u3000\u6628\u591c\u4e5f\u5c31\u7f62\u4e86\uff0c\u5927\u5bb6\u90fd\u60c5\u7eea\u6d53\u70c8\u3002\u60f3\u5230\u5f20\u606a\u8d70\u540e\uff0c\u5c31\u8981\u72ec\u81ea\u4e00\u4e2a\u4eba\u9762\u5bf9\u8bb8\u601d\uff0c\u5510\u5a67\u53c8\u6709\u4e9b\u5fc3\u865a\u4e86\uff0c\u770b\u5230\u8bb8\u601d\u4f9d\u95e8\u800c\u7ad9\u770b\u5411\u8fd9\u91cc\uff0c\u79c0\u53d1\u677e\u6563\u7684\u633d\u7740\uff0c\u5a34\u7136\u9759\u8c27\u3001\u6709\u7740\u98a0\u5012\u4f17\u751f\u7684\u7f8e\uff0c\u60f3\u8d77\u5979\u574e\u5777\u7684\u8eab\u4e16\uff0c\u5fc3\u91cc\u60f3\uff1a\u6211\u89c1\u72b9\u601c\uff0c\u4f55\u51b5\u732a\u5934\uff1f</p><p>\u3000\u3000\u5510\u5a67\u5fc3\u60f3\u8fd9\u65f6\u5019\u5306\u5fd9\u79bb\u5f00\uff0c\u5979\u5fc3\u91cc\u5927\u6982\u4f1a\u4ecb\u610f\u5427\uff0c\u4e5f\u5c31\u6ca1\u6709\u63d0\u51fa\u8ddf\u8981\u5f20\u606a\u4e00\u8d77\u79bb\u5f00\u5f71\u6e7e\u56ed\u516c\u5bd3\uff0c\u4f38\u4e86\u61d2\u8170\u53c8\u6293\u8d77\u88ab\u5b50\u76d6\u4f4f\u5927\u534a\u5f20\u8138\uff0c\u5a07\u67d4\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4e2a\u732a\u5934\uff0c\u4e0d\u8981\u6253\u5fe7\u6211\u7761\u89c9\uff0c\u6628\u5929\u7761\u5f97\u597d\u5c11\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5510\u5a67\u8fd9\u4e48\u8bf4\uff0c\u5f20\u606a\u7b97\u662f\u5f7b\u5e95\u653e\u5fc3\u4e0b\u6765\uff0c\u901a\u77e5\u5085\u4fca\u8fc7\u6765\u63a5\u4ed6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e5d\u516b\u5e74\uff0c\u98de\u673a\u4e0a\u5168\u7a0b\u7981\u7528\u624b\u673a\uff0c\u5f20\u606a\u5728\u98de\u673a\u4e0a\u53c8\u7761\u4e86\u4e00\u4f1a\u513f\uff0c\u4e0b\u98de\u673a\u65f6\u7cbe\u529b\u5c31\u6062\u590d\u8fc7\u6765\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u53f6\u5efa\u658c\u3001\u8bb8\u9e3f\u4f2f\u8fd8\u6709\u7fdf\u4e39\u9752\u7b49\u4eba\u65e9\u4e0a\u4ece\u5efa\u90ba\u4e58\u98de\u673a\u62b5\u8fbe\u5317\u4eac\uff0c\u6bd4\u5f20\u606a\u8981\u63d0\u524d\u5230\uff0c\u8fd8\u5728\u673a\u573a\u7b49\u4e86\u4ed6\u4eec\u6709\u4e00\u4e2a\u5c0f\u65f6\uff0c\u5317\u4eac\u5206\u516c\u53f8\u7684\u5468\u4e00\u5e73\u3001\u59da\u575a\u4e5f\u4eb2\u81ea\u5e26\u8f66\u5230\u673a\u573a\u6765\u63a5\u673a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0a\u98de\u673a\u4e4b\u524d\u7ed9\u665a\u6674\u6253\u8fc7\u7535\u8bdd\uff0c\u7535\u8bdd\u662f\u665a\u6674\u7684\u52a9\u7406\u5f20\u5ead\u63a5\u542c\u7684\uff0c\u665a\u6674\u5f53\u65f6\u4eba\u4e0d\u5728\u3002\u4e0a\u98de\u673a\u540e\u5c31\u5173\u95ed\u4e86\u624b\u673a\uff0c\u5c31\u7b97\u665a\u6674\u56de\u7535\u8bdd\u8fc7\u6765\uff0c\u4ed6\u4e5f\u6ca1\u6709\u529e\u6cd5\u63a5\u542c\u3002\u5728\u4f17\u4eba\u777d\u777d\u4e4b\u4e0b\uff0c\u5f20\u606a\u4e5f\u4e0d\u65b9\u4fbf\u8ddf\u665a\u6674\u8054\u7cfb\uff0c\u53ea\u662f\u5fc3\u91cc\u5728\u76d8\u7b97\u7740\u8fd9\u65f6\u5019\u5dee\u4e0d\u591a\u5feb\u5230\u665a\u6674\u767b\u673a\u7684\u65f6\u95f4\u4e86\u3002</p><p>\u3000\u3000\u4eca\u5929\u662f\u6b63\u6708\u5341\u4e94\u5143\u5bb5\u8282\uff0c\u5317\u4eac\u7684\u6c14\u6e29\u4f4e\u5230\u96f6\u4e0b\u4e94\u5ea6\uff0c\u4eba\u5728\u6696\u6c14\u5f00\u653e\u7684\u63a5\u673a\u5927\u5385\u91cc\uff0c\u8fd8\u6ca1\u6709\u4ece\u66ae\u6625\u8de8\u8fdb\u4e25\u51ac\u7684\u611f\u89c9\u3002\u8ddd\u7fdf\u4e39\u9752\u4e5f\u6709\u8fd1\u4e00\u4e2a\u6708\u6ca1\u6709\u89c1\u9762\u4e86\uff0c\u5176\u4ed6\u4eba\u66f4\u4e0d\u7528\u8bf4\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5c06\u6817\u8272\u7684\u98ce\u8863\u62ff\u5728\u624b\u91cc\uff0c\u7a7f\u7740\u9ebb\u7070\u8272\u7684\u5c0f\u897f\u88c5\uff0c\u91cc\u9762\u7a7f\u7740\u8377\u53f6\u72b6\u5927\u8936\u8fb9\u7684\u7c89\u8272\u886c\u886b\uff0c\u8170\u80a2\u5fae\u5fae\u6536\u8d77\uff0c\u51f8\u663e\u51fa\u5979\u6027\u611f\u8ff7\u4eba\u7684\u4f53\u6001\uff0c\u5bb9\u989c\u8273\u4e3d\u3001\u98ce\u59ff\u7ef0\u7ea6\u7684\u7ad9\u5728\u90a3\u91cc\u5438\u5f15\u8fc7\u5f80\u65c5\u5ba2\u7684\u76ee\u5149\uff0c\u770b\u5230\u5f20\u606a\u8d70\u5230\u63a5\u673a\u5927\u5385\uff0c\u4ece\u5bb9\u4e0d\u8feb\u7684\u8d70\u8fc7\u53bb\u5c06\u4ed6\u968f\u8eab\u62ff\u7740\u7684\u624b\u63d0\u5305\u63a5\u8fc7\u6765\u3002</p><p>\u3000\u3000\u7528\u7fdf\u4e39\u9752\u5f53\u52a9\u7406\uff0c\u5f20\u606a\u8fd8\u662f\u8981\u627f\u53d7\u76f8\u5f53\u7684\u5fc3\u7406\u538b\u529b\uff0c\u5c31\u8fde\u4ed6\u5988\u6881\u683c\u73cd\u90fd\u4e0d\u5927\u9ad8\u5174\u8138\u86cb\u8273\u7f8e\u6709\u5982\u5996\u5b7d\u3001\u8eab\u6750\u6027\u611f\u8fc7\u4e8e\u64a9\u4eba\u7684\u7fdf\u4e39\u9752\u4e0e\u4ed6\u8d70\u5f97\u592a\u8fd1\uff0c\u66f4\u4e0d\u7528\u8bf4\u7559\u5728\u8eab\u8fb9\u4e86\u3002\u773c\u4e0b\u53ea\u662f\u62ff\u65b0\u829c\u914d\u5957\u4ea7\u4e1a\u56ed\u533a\u7b79\u5212\u4ee5\u53ca\u7fdf\u4e39\u9752\u9700\u8981\u8131\u5c97\u5230\u4e1c\u5927\u5c31\u8bfb\u5de5\u5546\u7ba1\u7406\u7855\u58eb\u5f53\u501f\u53e3\u5c06\u5979\u7559\u5728\u8eab\u8fb9\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u8bb8\u9e3f\u4f2f\u4e5f\u4e0d\u89c9\u5f97\u8be7\u5f02\uff0c\u5f20\u606a\u8eab\u8fb9\u7684\u786e\u9700\u8981\u4e00\u4e2a\u80fd\u529b\u51fa\u4f17\u7684\u4eba\u5e2e\u7740\u5904\u7406\u65e5\u5e38\u4e8b\u52a1\uff0c\u4e0d\u7136\u5f20\u606a\u6bcf\u5929\u5149\u6d4f\u89c8\u90ae\u4ef6\u5c31\u8981\u7528\u592a\u591a\u7684\u65f6\u95f4\uff0c\u9664\u4e86\u7fdf\u4e39\u9752\uff0c\u8981\u627e\u5230\u5176\u4ed6\u5408\u9002\u7684\u4eba\u8fd8\u771f\u6709\u4e9b\u96be\u5ea6\u3002</p><p>\u3000\u3000\u5f20\u606a\u521a\u4e0b\u98de\u673a\u6765\uff0c\u8bb8\u9e3f\u4f2f\u5c31\u629b\u7ed9\u4ed6\u4e00\u4e2a\u574f\u6d88\u606f\uff1a\u201c\u6797\u4e1a\u516c\u53f8\u521a\u6536\u5230\u6c5f\u5357\u7701\u653f\u5e9c\u7684\u56de\u51fd\uff0c\u6c5f\u5357\u7701\u653f\u5e9c\u4ee5\u65b0\u5149\u8425\u6797\u8ba1\u5212\u4e0d\u7b26\u5408\u6c5f\u5357\u7701\u8ba1\u7ecf\u8d38\u59d4\u3001\u7701\u6797\u4e1a\u5385\u7b49\u6709\u5173\u90e8\u95e8\u5bf9\u5168\u7701\u6797\u4e1a\u7684\u89c4\u5212\u3001\u4ee5\u53ca\u6797\u6743\u9057\u7559\u95ee\u9898\u590d\u6742\u7b49\u4e8b\u9879\u4e3a\u7531\u5c06\u5bf9\u65b0\u5149\u6797\u4e1a\u8fdb\u5165\u6c5f\u5357\u7701\u5883\u5185\u8425\u6797\u5c06\u4e0d\u4e88\u653f\u7b56\u4e0a\u7684\u652f\u6301\u3002\u65b0\u5149\u6797\u4e1a\u8981\u7ee7\u7eed\u539f\u5148\u7684\u8ba1\u5212\uff0c\u53ea\u80fd\u627e\u5730\u65b9\u5e02\u53bf\u6709\u5173\u90e8\u95e8\u9010\u4e00\u8c08\u5224\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u771f\u662f\u4e2a\u574f\u6d88\u606f\u3002</p><p>\u3000\u3000\u4ed6\u539f\u8ba1\u5212\u7b49\u6797\u4e1a\u516c\u53f8\u4e0e\u6c5f\u5357\u7701\u653f\u5e9c\u6797\u4e1a\u5385\u7b49\u90e8\u95e8\u8c08\u59a5\u4e4b\u540e\uff0c\u5c31\u7acb\u5373\u5728\u4f4d\u4e8e\u5c0f\u6c5f\u4e0a\u6e38\u3001\u5c5e\u4e8e\u6c5f\u5357\u7701\u6240\u8f96\u7684\u6797\u6ee8\u3001\u65b0\u9633\u4e24\u53bf\u8fc5\u901f\u5f00\u5c55\u8425\u6797\u5de5\u4f5c\uff0c\u4e89\u53d6\u5728\u4e5d\u516b\u5e74\u96e8\u5b63\u6765\u4e34\u4e4b\u524d\uff0c\u8ba9\u901f\u751f\u6797\u8986\u76d6\u4f4f\u8fd9\u4e24\u53bf\u56e0\u4e71\u780d\u6ee5\u4f10\u800c\u4ea7\u751f\u7684\u5927\u91cf\u7684\u8352\u5c71\u7ae5\u5cad\u3002\u867d\u7136\u8fd9\u65f6\u5019\u62a2\u5728\u96e8\u5b63\u4e4b\u524d\u5927\u89c4\u6a21\u8425\u6797\u7b49\u5230\u767e\u5e74\u4e00\u9047\u7684\u6d2a\u6c34\u6765\u4e34\u4f1a\u906d\u53d7\u4e00\u5b9a\u7a0b\u5ea6\u7684\u635f\u5931\uff0c\u4f46\u662f\u5bf9\u7f13\u89e3\u5c0f\u6c5f\u6d41\u57df\u5728\u8baf\u671f\u7684\u538b\u529b\u4f1a\u6709\u4e00\u5b9a\u7684\u5e2e\u52a9\u3002</p><p>\u3000\u3000\u73b0\u5728\u8425\u6797\u8ba1\u5212\u906d\u5230\u6c5f\u5357\u7701\u6709\u5173\u90e8\u95e8\u7684\u5426\u51b3\uff0c\u867d\u8bf4\u8fd8\u53ef\u4ee5\u627e\u5730\u65b9\u53bf\u5e02\u9010\u4e00\u8c08\u5224\uff0c\u4f46\u662f\u96be\u5ea6\u8981\u589e\u52a0\u8bb8\u591a\uff0c\u8fd8\u8fdc\u4e0d\u5982\u4e00\u5f00\u59cb\u5c31\u76f4\u63a5\u627e\u53bf\u5e02\u8c08\u8fd9\u4e2a\u95ee\u9898\u7684\u597d\u3002</p><p>\u3000\u3000\u201c\u6c5f\u5357\u7701\u653f\u5e9c\u4e3a\u4ec0\u4e48\u8981\u62d2\u7edd\uff1f\u201d\u53f6\u5efa\u658c\u8131\u53e3\u95ee\u4e86\u4e00\u53e5\uff0c\u8bf4\u51fa\u53e3\u624d\u89c9\u8fd9\u53e5\u95ee\u8bdd\u771f\u662f\u591a\u4f59\uff0c\u90a3\u4e9b\u60f3\u963b\u788d\u9526\u6e56\u4e1a\u52a1\u6269\u5c55\u8fdb\u6c5f\u5357\u7701\u5883\u5185\u7684\u90a3\u4e00\u5c0f\u64ae\u4eba\uff0c\u5fc3\u91cc\u53ef\u6ca1\u6709\u4ec0\u4e48\u201c\u4ee5\u5927\u5c40\u4e3a\u91cd\u201d\u7684\u6982\u5ff5\uff0c\u8fdd\u80cc\u4ed6\u4eec\u5229\u76ca\u8bc9\u6c42\u7684\u90fd\u662f\u654c\u4eba\u3002\u867d\u7136\u6c5f\u5357\u7701\u5728\u56de\u51fd\u4e0a\u53ea\u8bf4\u660e\u201c\u4e0d\u4e88\u653f\u7b56\u4e0a\u7684\u652f\u6301\u201d\uff0c\u65e0\u7591\u5df2\u7ecf\u662f\u4e00\u79cd\u516c\u5f00\u7684\u4e0d\u6b22\u8fce\u6001\u5ea6\uff0c\u6c5f\u5357\u7701\u4e0b\u6765\u7684\u5e02\u53bf\u5c31\u7b97\u60f3\u5355\u72ec\u8ddf\u9526\u6e56\u5408\u4f5c\u5728\u8352\u5c71\u7ae5\u6797\u8425\u6797\u3001\u9020\u6797\uff0c\u4e5f\u4e0d\u4f1a\u7279\u522b\u7684\u79ef\u6781\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u8d77\u5728\u65e5\u672c\u4e1c\u4eac\u7535\u5b50\u5c55\u4e0a\u9047\u5230\u6c5f\u5357\u7701\u8ba1\u7ecf\u8d38\u59d4\u4e3b\u4efb\u7530\u5065\u7b49\u4eba\u7684\u4e8b\u60c5\uff0c\u7701\u8ba1\u7ecf\u8d38\u59d4\u53ef\u4ee5\u8bf4\u662f\u7701\u6709\u5173\u90e8\u95e8\u4e2d\u6700\u6709\u5b9e\u6743\u7684\u4e00\u4e2a\u90e8\u95e8\uff0c\u5f97\u7f6a\u7530\u5065\u7684\u786e\u4f1a\u6709\u4e9b\u9ebb\u70e6\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u7684\u59d1\u7236\u9648\u65b0\u6c11\u79bb\u5f00\u6c5f\u5357\u7701\u4e4b\u540e\uff0c\u9526\u6e56\u5728\u6c5f\u5357\u7701\u59d4\u7701\u653f\u5e9c\u9ad8\u5c42\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u6709\u52bf\u529b\u7684\u63f4\u529b\uff0c\u975e\u4f46\u6ca1\u6709\u63f4\u529b\uff0c\u8fd8\u6709\u50cf\u5468\u747e\u745c\u3001\u524d\u4e1c\u6d77\u7701\u6797\u4e1a\u5385\u5385\u957f\u7b49\u5bf9\u7acb\u4eba\u7269\u7684\u5b58\u5728\u3002\u8bb8\u9e3f\u4f2f\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u7530\u5065\u8fd9\u6837\u7684\u8df3\u6881\u5c0f\u4e11\u8df3\u51fa\u6765\u6363\u9b3c\u4e5f\u7684\u786e\u80fd\u7ed9\u9526\u6e56\u5728\u6c5f\u5357\u7701\u7684\u5de5\u4f5c\u5e26\u6765\u4e9b\u963b\u788d\uff0c\u8fd9\u4e2a\u7530\u5065\u8fd9\u6b21\u8fd8\u53ef\u80fd\u4f1a\u7ed9\u63d0\u540d\u62c5\u4efb\u526f\u7701\u957f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6837\u7684\u5b98\u5458\u5012\u662f\u5b98\u8fd0\u4ea8\u901a\u2026\u2026\u201d\u9648\u4fe1\u751f\u611f\u6168\u7684\u8bf4\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u5f20\u606a\u95ee\u8bb8\u9e3f\u4f2f\uff1a\u201c\u6c5f\u5357\u7701\u91cc\u6709\u54ea\u4e9b\u5b98\u5458\u662f\u503c\u5f97\u4fe1\u4efb\u7684\uff0c\u603b\u4e0d\u80fd\u9047\u5230\u4e00\u4e9b\u963b\u529b\uff0c\u5de5\u4f5c\u5c31\u505c\u6b62\u4e0d\u524d\u3002\u201d</p><p>\u3000\u3000\u4e00\u822c\u8bf4\u6765\uff0c\u65e2\u7136\u8425\u6797\u5de5\u4f5c\u5728\u6c5f\u5357\u7701\u9047\u5230\u963b\u788d\uff0c\u5c31\u53ef\u4ee5\u7ed5\u8fc7\u6c5f\u5357\u7701\uff0c\u5c06\u8425\u6797\u5de5\u4f5c\u6269\u5f20\u5230\u5176\u4ed6\u7701\u5e02\u53bb\u3002</p><p>\u3000\u3000\u56fd\u5bb6\u4ece\u516b\u5341\u5e74\u4ee3\u4ee5\u6765\u5c31\u9f13\u52b1\u5de5\u4e1a\u539f\u6599\u6797\u7684\u5efa\u8bbe\uff0c\u4f46\u662f\u7531\u4e8e\u6797\u6743\u6539\u9769\u7684\u5de5\u4f5c\u8fdf\u8fdf\u5f97\u4e0d\u5230\u63a8\u8fdb\u3001\u56fd\u5bb6\u5bf9\u5929\u7136\u6797\u8fd8\u6ca1\u6709\u5f00\u59cb\u4e25\u683c\u7684\u4fdd\u62a4\u3001\u79cd\u690d\u5de5\u4e1a\u539f\u6599\u6797\u7684\u6295\u8d44\u6548\u76ca\u8fd8\u8fdc\u6bd4\u4e0d\u4e0a\u780d\u4f10\u5929\u7136\u6797\uff0c\u5bfc\u81f4\u5de5\u4e1a\u539f\u6599\u6797\u5efa\u8bbe\u4ece\u6765\u90fd\u6ca1\u6709\u5f97\u5230\u8fc7\u5927\u53d1\u5c55\u3002</p><p>\u3000\u3000\u5728\u8fd9\u79cd\u80cc\u666f\u4e0b\uff0c\u65b0\u5149\u62ff\u51fa\u5de8\u8d44\u5f00\u59cb\u5927\u89c4\u6a21\u7684\u5de5\u4e1a\u539f\u6599\u6797\u8425\u9020\uff0c\u53ef\u80fd\u5728\u4e00\u4e9b\u5730\u65b9\u4f1a\u9047\u5230\u969c\u788d\uff0c\u4e5f\u4f1a\u53d7\u5230\u6709\u66f4\u591a\u7701\u5e02\u7684\u6b22\u8fce\u3002</p><p>\u3000\u3000\u5728\u4e1c\u6d77\u7701\u5883\u5185\u7684\u8425\u6797\u5de5\u4f5c\u8fdb\u5165\u5c3e\u58f0\u4e4b\u540e\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e0b\u5c5e\u7684\u65b0\u5149\u6797\u4e1a\u516c\u53f8\u5219\u5f00\u59cb\u8003\u8651\u5c06\u8425\u6797\u5de5\u4f5c\u63a8\u8fdb\u5230\u5176\u4ed6\u7701\u5e02\u53bb\uff0c\u9664\u4e86\u6c5f\u5357\u7701\u4e4b\u540e\uff0c\u6797\u4e1a\u516c\u53f8\u4e0e\u5176\u4ed6\u7701\u5e02\u4e5f\u5df2\u7ecf\u6709\u8fc7\u63a5\u89e6\u4e86\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u8003\u8651\u5230\u5f20\u606a\u8981\u8003\u8651\u6574\u4e2a\u9526\u6e56\u4f53\u7cfb\u7684\u7edf\u4e00\u8fd0\u4f5c\uff0c\u9526\u6e56\u5728\u56fd\u5185\u8fd8\u5b58\u5728\u7684\u7ade\u4e89\u8005\u4e5f\u6709\u5f80\u91d1\u5c71\u6c47\u96c6\u7684\u8d8b\u52bf\uff0c\u6c5f\u5357\u7701\u662f\u9526\u6e56\u59cb\u7ec8\u7ed5\u4e0d\u8fc7\u53bb\u7684\u969c\u788d\uff0c\u5176\u4ed6\u7701\u5e02\u7684\u5de5\u4f5c\u540c\u65f6\u4f1a\u63a8\u8fdb\uff0c\u4f46\u662f\u4e5f\u4e0d\u4f1a\u56e0\u4e3a\u4e00\u4e9b\u969c\u788d\u5c31\u653e\u5f03\u6c5f\u5357\u7701\u3002</p><p>\u3000\u3000\u201c\u542c\u8bf4\u4f60\u5728\u9999\u6e2f\u9047\u5230\u4e9b\u4e8b\u60c5\uff1f\u201d\u53f6\u5efa\u658c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u81ea\u7136\u662f\u4ece\u5b59\u9759\u9999\u90a3\u91cc\u77e5\u9053\u7684\u6d88\u606f\uff0c\u770b\u4ed6\u6324\u7709\u5f04\u773c\u7684\uff0c\u4f3c\u4e4e\u66f4\u60f3\u542c\u8bb8\u601d\u4e0e\u5510\u5a67\u76f8\u89c1\u7684\u60c5\u5f62\u3002</p><p>\u3000\u3000\u201c\u8bf7\u4f60\u4eec\u5230\u5317\u4eac\uff0c\u6b63\u662f\u8981\u8ba8\u8bba\u4e00\u4e9b\u4e8b\u60c5\uff0c\u201d\u5f20\u606a\u5f53\u7136\u4e0d\u4f1a\u6ee1\u8db3\u53f6\u5efa\u658c\u7684\u5fc3\u613f\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u8fd8\u662f\u5148\u4e0a\u8f66\u518d\u8bf4\uff0c\u4e0d\u8981\u7ad9\u5728\u63a5\u673a\u5927\u5385\u91cc\u5f53\u4eba\u5899\u4e86\u3002\u201d</p><p>\u3000\u3000\u4f17\u4eba\u5f80\u63a5\u673a\u5927\u5385\u5916\u8d70\u53bb\uff0c\u5317\u4eac\u5206\u516c\u53f8\u7684\u8f66\u5b50\u5c31\u505c\u5728\u63a5\u673a\u5927\u5385\u5916\u7684\u5019\u8f66\u533a\u57df\u3002\u5c31\u5728\u4f17\u4eba\u777d\u777d\u4e4b\u4e0b\uff0c\u5f20\u606a\u638f\u51fa\u624b\u673a\u7ed9\u665a\u6674\u6253\u7535\u8bdd\uff1a\u201c\u665a\u6674\u59d0\uff0c\u4f60\u5230\u5317\u4eac\u4e86\u6ca1\u6709\uff1f\u54e6\uff0c\u4f60\u8fd8\u5728\u673a\u573a\uff0c\u90a3\u7b49\u4f60\u5230\u5317\u4eac\u518d\u8bf4\u4e8b\u60c5\u5427\u3002\u201d</p><p>\u3000\u3000\u6d77\u5dde\u673a\u573a\u4eca\u5929\u6ca1\u6709\u5230\u5317\u4eac\u7684\u822a\u73ed\uff0c\u8c22\u665a\u6674\u662f\u4ece\u5efa\u90ba\u673a\u573a\u4e58\u98de\u673a\u8d76\u5230\u5317\u4eac\u6765\u3002\u9648\u4fe1\u751f\u7b49\u4eba\u5e76\u4e0d\u77e5\u9053\u8c22\u665a\u6674\u4f1a\u5230\u5317\u4eac\u6765\u3002\u8fd9\u6bb5\u65f6\u95f4\u6765\uff0c\u8c22\u665a\u6674\u5f88\u5c11\u5173\u5fc3\u516c\u53f8\u5177\u4f53\u7684\u4e8b\u52a1\uff0c\u4e00\u5fc3\u6251\u5728\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u91cc\uff0c\u4f46\u5979\u8fd8\u662f\u8d8a\u79c0\u4e0e\u9526\u6e56\u7684\u5927\u80a1\u4e1c\uff0c\u771f\u8981\u6709\u4ec0\u4e48\u5927\u8ba1\u5212\uff0c\u4e5f\u5e94\u8be5\u8bf7\u5979\u4e00\u8d77\u53c2\u4e0e\u5546\u8bae\u3002</p><p>\u3000\u3000\u4e5f\u53ea\u6709\u7fdf\u4e39\u9752\u7ad9\u5728\u5f20\u606a\u6709\u4e9b\u72d0\u7591\u7684\u51dd\u89c6\u7740\u4ed6\u7684\u80cc\u5f71\uff0c\u5979\u4e0e\u8c22\u665a\u6674\u53ea\u89c1\u8fc7\u51e0\u9762\uff0c\u4f46\u662f\u611f\u89c9\u654f\u9510\u7684\u5979\u603b\u89c9\u5f97\u5f20\u606a\u4e0e\u8c22\u665a\u6674\u4e4b\u95f4\u6709\u7740\u8bf4\u4e0d\u6e05\u9053\u4e0d\u660e\u7684\u5173\u7cfb\u3002</p></article>"}}}]);
