(window.webpackJsonp=window.webpackJsonp||[]).push([[780],{803:function(p){p.exports={chapterTitle:null,title:"\u7b2c613\u7ae0 \u7f51\u9875\u6e38\u620f",content:"<article><h2>\u7b2c613\u7ae0 \u7f51\u9875\u6e38\u620f</h2><p>\u3000\u3000\u8981\u5c06\u5341\u4e8c\u4efd\u5546\u4e1a\u8ba1\u5212\u4e66\u4ee5\u53ca\u76f8\u5e94\u7684\u5206\u6790\u8bc4\u4ef7\u62a5\u544a\u770b\u5b8c\uff0c\u4e0d\u662f\u4e00\u9879\u8f7b\u677e\u7684\u6d3b\u3002</p><p>\u3000\u3000\u9752\u8499\u8499\u7684\u5149\u4ece\u7a97\u5916\u900f\u8fdb\u6765\uff0c\u5f20\u606a\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u5750\u4e86\u4e00\u5bbf\u8170\u9178\u80cc\u75bc\uff0c\u675c\u98de\u8fd9\u5bb6\u4f19\u8bf4\u662f\u8981\u5e2e\u4ed6\u4e00\u8d77\u770b\u8d44\u6599\uff0c\u8eba\u5728\u5e8a\u4e0a\u6ca1\u80fd\u71ac\u591a\u4e45\u5c31\u7761\u7740\u4e86\uff0c\u8d44\u6599\u6ed1\u4e86\u4e00\u5730\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u8fc7\u53bb\uff0c\u5c06\u5730\u677f\u4e0a\u7684\u8d44\u6599\u68c0\u8d77\u6765\uff0c\u6574\u7406\u4e00\u4e0b\uff0c\u653e\u8fdb\u6587\u4ef6\u76d2\u91cc\u53bb\u3002</p><p>\u3000\u3000\u6311\u9009\u51fa\u6765\u7684\u5341\u4e8c\u4efd\u7533\u8bf7\u8d44\u6599\uff0c\u867d\u7136\u6216\u591a\u6216\u5c11\u6709\u4e9b\u5c0f\u95ee\u9898\uff0c\u4ec5\u4ece\u8d44\u6599\u4e0a\u6765\u770b\uff0c\u90fd\u662f\u503c\u5f97\u6276\u6301\u7684\u9879\u76ee\uff0c\u5f88\u591a\u5546\u4e1a\u8ba1\u5212\u4e66\u4e4b\u5916\u7684\u56e0\u7d20\u2014\u2014\u8fd9\u4e9b\u56e0\u7d20\u5f80\u5f80\u662f\u76f8\u5f53\u5173\u952e\u7684\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u8db3\u591f\u7684\u65f6\u95f4\u53bb\u9010\u4e00\u8003\u5bdf\uff0c\u4ed6\u8981\u6c42\u8d75\u6893\u7433\u6240\u9886\u5bfc\u7684\u56e2\u961f\u8981\u5c3d\u53ef\u80fd\u591a\u7684\u53bb\u63a5\u89e6\u90a3\u4e9b\u7533\u8bf7\u4eba\u2014\u2014\u5373\u4f7f\u5546\u4e1a\u8ba1\u5212\u4e66\u5199\u5f97\u4e0d\u90a3\u4e48\u52a8\u4eba\u7684\u7533\u8bf7\u4eba\u8bf4\u4e0d\u5b9a\u5728\u5176\u4ed6\u65b9\u9762\u6709\u7740\u8ba9\u4eba\u6b23\u8d4f\u7684\u9b45\u529b\u3002</p><p>\u3000\u3000\u6240\u4ee5\uff0c\u5f20\u606a\u8fd8\u662f\u5c06\u6700\u540e\u7684\u51b3\u5b9a\u6743\u7559\u7ed9\u9648\u4fe1\u751f\u4ed6\u4eec\u2014\u2014\u7b97\u662f\u5c06\u7403\u7ed9\u8e22\u4e86\u56de\u53bb\u3002</p><p>\u3000\u3000\u6253\u5f00\u7535\u8111\uff0c\u770b\u5230\u5510\u5a67\u51cc\u6668\u53d1\u6765\u7684\u90ae\u4ef6\uff0c\u9999\u6e2f\u7684\u5348\u591c\u5728\u4e0b\u96e8\uff0c\u5510\u5a67\u8bf4\u5979\u542c\u7740\u7a97\u5916\u6ef4\u7b54\u4e0d\u4f11\u7684\u96e8\u58f0\u96be\u4ee5\u5165\u7720\uff1b\u5374\u4e0d\u6653\u5f97\u8fd9\u65f6\u5019\u96e8\u6709\u6ca1\u6709\u505c\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u5510\u5a67\u56de\u5b8c\u90ae\u4ef6\uff0c\u4e00\u65f6\u95f4\u4e5f\u6ca1\u7761\u610f\uff0c\u6d4f\u89c8\u6606\u817e\u8d44\u8baf\u4e0a\u7684\u65b0\u95fb\uff0c\u7535\u8111\u5728\u7814\u7a76\u751f\u516c\u5bd3\u91cc\u80fd\u63a5\u5165\u6821\u56ed\u7f51\u3002\u51cc\u6668\u65f6\u5206\uff0c\u7f51\u901f\u8f83\u5feb\uff0c\u6362\u4f5c\u5e73\u65f6\uff0c\u5f20\u606a\u53ef\u6ca1\u6709\u5fc3\u60c5\u6162\u60a0\u60a0\u7684\u5728\u7f51\u4e0a\u6d4f\u89c8\u65b0\u95fb\uff0c\u4ed6\u8fd9\u51e0\u5e74\u5012\u662f\u91cd\u65b0\u4e60\u60ef\u9605\u8bfb\u62a5\u7eb8\u8d77\u6765\u4e86\u3002</p><p>\u3000\u3000\u7b49\u9752\u5e74\u516c\u5bd3\u5efa\u6210\uff0c\u9752\u5e74\u516c\u5bd3\u91cc\u4f1a\u94fa\u8bbe\u5149\u7ea4\uff0c\u4e0a\u7f51\u901f\u5ea6\u5c31\u4f1a\u5f97\u5230\u6539\u5584\u3002\u4e0d\u8fc7\u6b64\u65f6\u7684\u4e92\u8054\u7f51\u4e0e\u5341\u5e74\u4e4b\u540e\u8fd8\u65e0\u6cd5\u76f8\u63d0\u5e76\u8bba\uff0c\u7279\u522b\u56fd\u5185\u4e92\u8054\u7f51\u5efa\u8bbe\u521a\u521a\u8d77\u6b65\uff0c\u8fd8\u9650\u5236\u63a5\u5165\u6d77\u5916\u7f51\u7ad9\uff0c\u9664\u4e86\u6d4f\u89c8\u65b0\u95fb\u4e4b\u5916\uff0c\u4e5f\u6ca1\u6709\u7279\u522b\u503c\u5f97\u5f20\u606a\u6d41\u8fde\u5fd8\u8fd4\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u7684\u65b0\u95fb\u65f6\u6548\u6027\u5c31\u6709\u4e9b\u6ede\u540e\uff0c\u8fd9\u65b9\u9762\u9700\u8981\u52a0\u5f3a\u610f\u5473\u7740\u8981\u8fdb\u4e00\u6b65\u589e\u52a0\u5bf9\u6606\u817e\u65b0\u95fb\u4e2d\u5fc3\u7684\u5efa\u8bbe\uff0c\u4e0e\u4f20\u7edf\u65b0\u95fb\u5a92\u4f53\u5f00\u5c55\u66f4\u5e7f\u6cdb\u7684\u5408\u4f5c\uff0c\u90a3\u5c31\u8981\u6295\u5165\u66f4\u591a\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u662f\u4e2a\u4f1a\u70e7\u94b1\u7684\u5bb6\u4f19\uff0c\u9526\u6e56\u5e74\u521d\u5f80\u6606\u817e\u518d\u6b21\u6295\u5165\u7684\u4e24\u5343\u4e07\uff0c\u7528\u5230\u73b0\u5728\uff0c\u518d\u4e0d\u7528\u4e09\u4e94\u4e2a\u6708\u5c31\u5c06\u544a\u78ec\u3002</p><p>\u3000\u3000\u4e5d\u6708\u521d\uff0c\u516c\u7528\u8ba1\u7b97\u673a\u4e92\u8054\u7f51\u5b9e\u73b0\u4e86\u4e0e\u4e2d\u56fd\u79d1\u6280\u7f51\u3001\u4e2d\u56fd\u6559\u80b2\u548c\u79d1\u7814\u8ba1\u7b97\u673a\u7f51\u3001\u4e2d\u56fd\u91d1\u6865\u4fe1\u606f\u7f51\u7684\u4e92\u8054\u4e92\u901a\uff0c\u4ece\u6b64\u56fd\u5185\u4e92\u8054\u7f51\u5df2\u7ecf\u8fdb\u5165\u4e00\u4e2a\u7a7a\u524d\u6d3b\u8dc3\u7684\u65f6\u671f\u3002\u518d\u8fc7\u5341\u5e74\u6765\u56de\u987e\u56fd\u5185\u4e92\u8054\u7f51\u53d1\u5c55\u7684\u5386\u53f2\uff0c\u8fd9\u6bb5\u65f6\u95f4\u5e94\u8be5\u662f\u7ecf\u8fc7\u5546\u4e1a\u5316\u524d\u671f\u7684\u63a2\u7d22\u4e0e\u5f00\u62d3\u4e4b\u540e\u5728\u6b27\u7f8e\u4e92\u8054\u7f51\u70ed\u6f6e\u7684\u63a8\u52a8\u4e4b\u4e0b\u56fd\u5185\u4e92\u8054\u7f51\u8fce\u6765\u7684\u7b2c\u4e00\u6b21\u70ed\u6f6e\u3002</p><p>\u3000\u3000\u6240\u8c13\u70ed\u6f6e\uff0c\u53ea\u662f\u6bd4\u70e7\u94b1\u8c01\u66f4\u72e0\u7f62\u4e86\u3002\u81f3\u5c11\u5728\u4e92\u8054\u7f51\u771f\u6b63\u53d1\u5c55\u8d77\u6765\u4e4b\u524d\uff0c\u5728\u96f6\u96f6\u3001\u96f6\u4e00\u5e74\u4e4b\u524d\uff0c\u8fd8\u770b\u4e0d\u5230\u6709\u76f4\u63a5\u8d62\u5229\u7684\u673a\u4f1a\uff0c\u7f51\u7ad9\u7ecf\u8425\u5931\u8d25\u7684\u5178\u578b\u6848\u4f8b\u5927\u90fd\u662f\u90a3\u4e9b\u7f3a\u4e4f\u8db3\u591f\u8d44\u91d1\u652f\u6491\u3001\u6025\u4e8e\u8d62\u5229\u7684\u7f51\u7ad9\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u6606\u817e\u5177\u4f53\u7684\u8fd0\u8425\u4e0d\u4f1a\u76f4\u63a5\u5e72\u6d89\uff0c\u4f46\u662f\u8981\u9a6c\u5411\u4e1c\u4ed6\u4eec\u575a\u6301\u95e8\u6237\u7f51\u7ad9\u4e0e\u9488\u5bf9\u4e2a\u4eba\u7528\u6237\u514d\u8d39\u8fd0\u8425\u7684\u65b9\u9488\u3002\u81f3\u4e8e\u8fd0\u8425\u6240\u9700\u8981\u7684\u8d44\u91d1\uff0c\u5f20\u606a\u4e5f\u4e0d\u4f1a\u65e0\u9650\u5236\u7684\u5f80\u6606\u817e\u5728\u7ebf\u6295\u5165\u8d44\u91d1\uff0c\u4ed6\u65e9\u5df2\u7ecf\u660e\u786e\u544a\u8bc9\u9a6c\u5411\u4e1c\u4ed6\u4eec\u4e86\uff0c\u8fd9\u65f6\u5019\u8be5\u662f\u4ed6\u4eec\u5411\u5176\u4ed6\u6295\u8d44\u673a\u6784\u8fdb\u884c\u4e5e\u8ba8\u7684\u65f6\u5019\u4e86\uff0c\u6d77\u5916\u98ce\u9669\u6295\u8d44\u57fa\u91d1\u4e5f\u662f\u8fd9\u65f6\u5019\u6700\u5148\u8fdb\u5165\u56fd\u5185\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u2014\u2014\u6ca1\u6709\u6d77\u5916\u98ce\u6295\u673a\u6784\u52a0\u5165\uff0c\u5c31\u7b97\u5f20\u606a\u72e0\u5fc3\u5f80\u6606\u817e\u6295\u5165\u5341\u4ebf\u516b\u4ebf\uff0c\u4e5f\u65e0\u6cd5\u987a\u5229\u7684\u5728\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u3002</p><p>\u3000\u3000\u4e0d\u80fd\u4e0a\u5e02\uff0c\u96f6\u4e00\u5e74\u4e4b\u540e\u7684\u4e92\u8054\u7f51\u5bd2\u6f6e\u5c31\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\u6e21\u8fc7\uff0c\u95e8\u6237\u7f51\u7ad9\u7a33\u5b9a\u7684\u8425\u5229\u671f\u8981\u6ede\u7559\u5230\u96f6\u4e09\u5e74\u4e4b\u540e\uff0c\u5f20\u606a\u53ef\u4e0d\u5e0c\u671b\u6709\u90a3\u4e48\u5927\u7684\u4e00\u7b14\u8d44\u91d1\u957f\u65f6\u95f4\u7ed9\u5957\u5728\u6606\u817e\u91cc\u9762\u3002</p><p>\u3000\u3000\u5f53\u4e5d\u6708\u56fd\u5185\u4e92\u8054\u7f51\u771f\u6b63\u5b9e\u73b0\u4e92\u8054\u4e92\u901a\u4e4b\u524d\uff0c\u5c31\u5df2\u7ecf\u6709\u6d77\u5916\u98ce\u6295\u673a\u6784\u770b\u597d\u56fd\u5185\u7684\u8fd9\u5757\u5e02\u573a\uff0c\u56fd\u5185\u7684\u5341\u4e09\u4ebf\u4eba\u53e3\u7684\u65b0\u5174\u5e02\u573a\u4ee5\u53ca\u9ad8\u901f\u53d1\u5c55\u7684\u7ecf\u6d4e\u2014\u2014\u98ce\u9669\u6295\u8d44\u4e0d\u5c31\u662f\u770b\u597d\u8fd9\u4e9b\u524d\u666f\uff0c\u6295\u8d44\uff0c\u7136\u540e\u5c06\u8fd9\u4e9b\u524d\u666f\u5305\u88c5\u5ba3\u4f20\uff0c\u518d\u6253\u5305\u8f6c\u4e70\u7ed9\u6b27\u7f8e\u7684\u666e\u901a\u6295\u8d44\u4eba\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u4e5f\u5df2\u7ecf\u4e0e\u6d77\u5916\u6295\u8d44\u4eba\u63a5\u89e6\uff0c\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u6606\u817e\u5728\u7ebf\u7ecf\u8fc7\u4e00\u5e74\u5927\u529b\u7684\u53d1\u5c55\uff0c\u5728\u56fd\u5185\u95e8\u6237\u7f51\u7ad9\u90fd\u8fd8\u624d\u5177\u5907\u96cf\u5f62\u7684\u65f6\u523b\uff0c\u6606\u817e\u5728\u7ebf\u7684\u6210\u7ee9\u5df2\u7ecf\u8db3\u591f\u8000\u773c\u4e86\uff0c\u516d\u6708\u4e2d\u65ec\uff0c\u65e5\u5747\u6d41\u91cf\u5c31\u7a81\u7136\u767e\u4e07\u4eba\u6b21\u3002</p><p>\u3000\u3000\u8fd9\u5728\u4e5d\u4e03\u5e74\u5e74\u4e2d\uff0c\u662f\u4e00\u4e2a\u76f8\u5f53\u4e86\u4e0d\u5f97\u7684\u6570\u636e\u3002</p><p>\u3000\u3000\u65e5\u540e\u7684\u4e09\u5927\u95e8\u6237\u7f51\u7ad9\uff0c\u4e5f\u53ea\u6709\u65b0\u6d6a\u7684\u524d\u8eab\u5229\u65b9\u5728\u7ebf\u7684\u65e5\u5747\u6d41\u91cf\u7d27\u8ffd\u6606\u817e\u5728\u7ebf\u4e4b\u540e\uff0c\u4e8e\u516b\u6708\u4e0b\u65ec\u7a81\u7834\u767e\u4e07\u6d41\u91cf\uff0c\u800c\u90a3\u4e00\u523b\u7684\u6606\u817e\u5728\u7ebf\u5df2\u7ecf\u7a81\u7834\u4e24\u767e\u4e07\u7684\u65e5\u5747\u6d41\u91cf\u3002</p><p>\u3000\u3000\u773c\u4e0b\uff0c\u5c31\u5b9a\u5411\u589e\u53d1\u80a1\u4ef7\u4e0e\u4efd\u989d\u7684\u591a\u5c11\uff0c\u6606\u817e\u5728\u7ebf\u4e0e\u6d77\u5916\u6295\u8d44\u4eba\u8fd8\u6ca1\u6709\u8fbe\u6210\u4e00\u81f4\u7684\u534f\u8bae\u3002</p><p>\u3000\u3000\u9526\u6e56\u5c06\u5bf9\u6606\u817e\u7684\u6295\u8d44\u5e76\u5165\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u4e00\u8d77\u7ba1\u7406\u3002</p><p>\u3000\u3000\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u7684\u539f\u5219\u4e5f\u4ec5\u4ec5\u662f\u5bf9\u6280\u672f\u578b\u3001\u7814\u53d1\u578b\u4f01\u4e1a\u5728\u521d\u521b\u9636\u6bb5\u8fdb\u884c\u6276\u6301\uff0c\u4e00\u65e6\u8fd9\u4e9b\u4f01\u4e1a\u8d70\u4e0a\u53d1\u5c55\u58ee\u5927\u7684\u6b63\u8f68\uff0c\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u5c31\u4e0d\u518d\u6301\u7eed\u53c2\u4e0e\uff0c\u800c\u662f\u8981\u725f\u6c42\u5408\u7406\u9000\u51fa\u7684\u53ef\u80fd\uff0c\u62bd\u51fa\u8d44\u91d1\u652f\u6301\u66f4\u591a\u7684\u6280\u672f\u9879\u76ee\u8d70\u5411\u5546\u4e1a\u5316\u3002</p><p>\u3000\u3000\u5e74\u521d\u6606\u817e\u5728\u7ebf\u66f4\u6539\u4e3a\u80a1\u4efd\u5236\u6709\u9650\u516c\u53f8\u65f6\uff0c\u9526\u6e56\u5148\u540e\u6ce8\u8d44\u4e24\u5343\u4e94\u767e\u4e07\uff0c\u6301\u6709\u516b\u767e\u4e07\u80a1\uff0c\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u7b49\u4eba\u521b\u59cb\u4eba\u5171\u6301\u4e8c\u767e\u4e07\u80a1\u3002\u5f20\u606a\u5e0c\u671b\u4ee5\u4e0d\u4f4e\u4e8e\u6bcf\u80a1\u4e00\u7f8e\u5143\u7684\u4ef7\u683c\u589e\u53d1\u4e94\u767e\u4e07\u80a1\uff0c\u8fd9\u6b21\u82e5\u80fd\u52df\u96c6\u5230\u4e94\u767e\u591a\u4e07\u7f8e\u5143\u7684\u8d44\u91d1\uff0c\u5c31\u8db3\u4ee5\u652f\u6491\u5230\u7b49\u660e\u5e74\u5e74\u4e2d\u7684\u65f6\u95f4\u518d\u8fdb\u884c\u4e8c\u6b21\u589e\u53d1\u3002</p><p>\u3000\u3000\u9664\u4e86\u6311\u9009\u51fa\u6765\u7684\u8fd9\u5341\u4e8c\u4efd\u5546\u4e1a\u8ba1\u5212\u7533\u8bf7\uff0c\u5f20\u606a\u8fd8\u8ba9\u521b\u6295\u4e2d\u5fc3\u7684\u5de5\u4f5c\u4eba\u5458\u5c06\u6240\u6709\u7684\u7533\u8bf7\u90fd\u6253\u4e00\u4efd\u76ee\u5f55\u7ed9\u4ed6\u3002\u4ed6\u7565\u5e26\u7684\u6d4f\u89c8\u8fc7\uff0c\u5012\u662f\u6709\u5f88\u591a\u7f51\u7ad9\u8fd0\u8425\u9879\u76ee\u7684\u7533\u8bf7\u2014\u2014\u771f\u6b63\u7ed9\u4e88\u91cd\u89c6\u7684\u8fd8\u662f\u90a3\u4e9b\u5728\u7f51\u7edc\u6280\u672f\u4e0a\u6709\u8bc9\u6c42\u7684\u9879\u76ee\uff0c\u5341\u4e8c\u4efd\u5546\u4e1a\u8ba1\u5212\u91cc\u5c31\u6709\u4e00\u4efd\u662f\u5173\u4e8e\u53d1\u5c55\u7f51\u7edc\u5206\u7c7b\u4fe1\u606f\u641c\u7d22\u5f15\u64ce\u6280\u672f\u7684\u9879\u76ee\u7533\u8bf7\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u6ce8\u610f\u5230\u7ed9\u6dd8\u6c70\u6389\u7684\u76ee\u5f55\u540d\u5355\u91cc\u6709\u4e00\u4efd\u540d\u4e3a\u4e09\u56fd\u70c8\u8c6a\u4f20\u7684\u6587\u5b57\u6e38\u620f\u9879\u76ee\u7533\u8bf7\uff0c\u5f20\u606a\u8bb0\u5f97\u6700\u65e9\u63a5\u89e6\u7684\u6587\u5b57\u6e38\u620f\u662f\u91d1\u5eb8\u7fa4\u4fa0\u4f20\uff0c\u5f53\u65f6\u4e00\u7fa4\u4eba\u5bf9\u7740\u7a97\u53e3\u567c\u91cc\u556a\u5566\u7684\u6572\u6587\u5b57\u6572\u5f97\u6781\u6b22\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u5df2\u7ecf\u6ca1\u6709\u518d\u53bb\u63a5\u89e6\u6b64\u7c7b\u6587\u5b57\u6e38\u620f\u7684\u5ff5\u5934\uff0c\u4ed6\u5012\u662f\u5f88\u597d\u5947\u8fd9\u4e00\u7c7b\u7684\u6e38\u620f\u662f\u600e\u4e48\u5236\u4f5c\u8d77\u6765\u7684\uff0c\u4ed6\u7ed9\u8d75\u6893\u7433\u6253\u4e86\u4e00\u4e2a\u7535\u8bdd\uff0c\u53eb\u5979\u5c06\u9879\u76ee\u8d44\u6599\u8ba9\u9a6c\u6d77\u9f99\u9001\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0a\u5348\u7f8e\u7f8e\u7684\u7761\u4e86\u4e00\u89c9\uff0c\u4e0b\u5348\u5939\u7740\u4e09\u56fd\u70c8\u8c6a\u4f20\u7684\u6e38\u620f\u5f00\u53d1\u8d44\u6599\u5230\u5e74\u7ea7\u529e\u6df7\u65e5\u5b50\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u5404\u79cd\u539f\u56e0\u800c\u8bf7\u5047\u4e0d\u53c2\u52a0\u519b\u8bad\u7684\u65b0\u751f\u7b2c\u4e8c\u5929\u5c31\u9aa4\u589e\u4e86\u5341\u4e8c\u4eba\uff0c\u5012\u662f\u5904\u4e8e\u751f\u7406\u5468\u671f\u7684\u5973\u5b69\u5b50\u5c45\u591a\u3002\u5f20\u606a\u3001\u675c\u98de\u8fd8\u6709\u8c22\u5b50\u5609\u5c31\u8d1f\u8d23\u5728\u6d3b\u52a8\u5ba4\u91cc\u5199\u6d77\u62a5\u2014\u2014\u4e5f\u6ca1\u6709\u51e0\u5f20\u6d77\u62a5\u597d\u5199\uff0c\u5199\u5b8c\u6d77\u62a5\u4e4b\u540e\uff0c\u5f20\u8d34\u6d77\u62a5\u7684\u4e8b\u60c5\u5c31\u4ea4\u7ed9\u522b\u4eba\u53bb\u505a\uff0c\u5f20\u606a\u5c31\u62d6\u4e86\u4e00\u628a\u6905\u5b50\u5728\u7a97\u53e3\u770b\u4e09\u56fd\u70c8\u8c6a\u4f20\u7684\u6e38\u620f\u5f00\u53d1\u8d44\u6599\u3002</p><p>\u3000\u3000\u56fd\u5185\u7f51\u7edc\u7684\u5efa\u8bbe\u7684\u6ede\u540e\uff0c\u5728\u65e5\u97e9\u6b27\u7f8e\u5df2\u7ecf\u51fa\u73b0\u5e76\u9010\u6b65\u6d41\u884c\u8d77\u6765\u76842D\u7c7b\u7f51\u7edc\u6e38\u620f\uff0c\u8fd8\u65e0\u6cd5\u5728\u56fd\u5185\u7684\u4e92\u8054\u7f51\u571f\u58e4\u4e0a\u751f\u5b58\u2014\u2014\u4e0d\u8981\u62b1\u6028\u7f51\u901f\u4e86\uff0c\u56fd\u5185\u7edd\u5927\u591a\u6570\u8ba1\u7b97\u673a\u7684\u6027\u80fd\u751a\u81f3\u8fd8\u4e0d\u8db3\u4ee5\u652f\u63012D\u7c7b\u6e38\u620f\u7684\u786c\u4ef6\u9700\u6c42\uff0c\u5012\u662f\u4eca\u540e\u51e0\u5e74\u5229\u7528\u5149\u7ea4\u63a5\u5165\u4e92\u8054\u7f51\u7684\u7f51\u5427\u4ee5\u53ca\u8ba1\u7b97\u673a\u786c\u4ef6\u8bbe\u5907\u7684\u5feb\u901f\u5347\u7ea7\u624d\u5728\u56fd\u5185\u5927\u4e2d\u57ce\u5e02\u8fc5\u901f\u666e\u53ca\uff0c\u624d\u57f9\u80b2\u51fa\u4e00\u7247\u9002\u5b9c\u7684\u571f\u58e4\u3002</p><p>\u3000\u3000\u8d62\u5229\u524d\u666f\u9eef\u6de1\uff0c\u4ee5\u53ca\u6280\u672f\u53d1\u5c55\u7684\u65b9\u5411\u4e0d\u80fd\u5207\u5408\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u4e3b\u65e8\uff0c\u8fd9\u5c31\u662f\u4e09\u56fd\u70c8\u8c6a\u4f20\u7684\u9879\u76ee\u7533\u8bf7\u7b2c\u4e00\u6279\u5c31\u7ed9\u8d75\u6893\u7433\u5979\u4eec\u67aa\u6bd9\u6389\u7684\u539f\u56e0\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f88\u6e05\u695a\u56fd\u5185\u7f51\u7edc\u6e38\u620f\u7684\u53d1\u5c55\u8109\u80f3\uff0c\u7f51\u7edc\u6e38\u620f\u7684\u6210\u529f\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u53d6\u51b3\u4e8e\u8fd0\u8425\u4e0e\u8425\u9500\u7684\u6210\u529f\uff0c\u6e38\u620f\u81ea\u8eab\u7684\u7cbe\u826f\u5236\u4f5c\u53cd\u800c\u4e0d\u662f\u6700\u5173\u952e\u7684\u56e0\u7d20\u3002</p><p>\u3000\u3000\u6587\u5b57\u6e38\u620f\u7684\u8d62\u5229\u524d\u666f\u81ea\u7136\u9eef\u6de1\uff0c\u9879\u76ee\u8d44\u6599\u91cc\u5012\u662f\u63d0\u5230\u56fe\u5f62\u5316\u7684\u6982\u5ff5\u3002\u5f20\u606a\u611f\u89c9\u8ddf\u5341\u5e74\u540e\u76db\u884c\u7684\u7ecf\u8425\u7b56\u7565\u7c7b\u7f51\u9875\u6e38\u620f\u5dee\u4e0d\u591a\uff0c\u524d\u4e16\uff0c\u5f20\u606a\u6ca1\u4e8b\u4e5f\u559c\u6b22\u6ce1\u5728\u529e\u516c\u5ba4\u91cc\u73a9\u8fd9\u79cd\u7f51\u9875\u6e38\u620f\u3002</p><p>\u3000\u3000\u5229\u7528FLASH\u6280\u672f\u4e3a\u6e38\u620f\u5f15\u64ce\u7684\u7f51\u9875\u6e38\u620f\u504f\u91cd\u4e8e\u4f11\u95f2\uff0c\u662f\u57282D\u30013D\u7f51\u7edc\u6e38\u620f\u4e4b\u540e\u624d\u9010\u884c\u76db\u884c\u8d77\u6765\u7684\uff0c\u4f46\u662f\u5f20\u606a\u5e76\u4e0d\u8ba4\u4e3a\u7f51\u9875\u6e38\u620f\u4f1a\u6bd42D\u30013D\u7f51\u7edc\u6e38\u620f\u66f4\u665a\u51fa\u73b0\u3002\u8ba4\u771f\u8003\u8651\u4e00\u4e0b\uff0c\u5373\u4f7f\u7f51\u9875\u6e38\u620f\u4e0e2D\u6e38\u620f\u540c\u65f6\u51fa\u73b0\uff0c\u5728\u56fd\u58e4\u4e5d\u5341\u5e74\u4ee3\u672a\u5230\u4e8c000\u5e74\u4ee3\u521d\u671f\u7684\u4e92\u8054\u7f51\u80cc\u666f\u4e4b\u4e0b\uff0c\u8fd8\u662f2D\u7f51\u7edc\u6e38\u620f\u7684\u751f\u5b58\u4e0e\u53d1\u5c55\u7684\u7a7a\u95f4\u66f4\u5927\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf92D\u7f51\u6e38\u5df2\u7ecf\u6ca1\u6709\u5174\u8da3\uff0c\u5fc3\u91cc\u5f88\u662f\u72b9\u8c6b\uff1a\u638f\u4e00\u7b14\u94b1\u4e13\u95e8\u4e3a\u81ea\u5df1\u5f00\u53d1\u8fd9\u6b3e\u6e38\u620f\u4f1a\u4e0d\u4f1a\u592a\u5962\u4f88\u4e86\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5c06\u4e09\u56fd\u70c8\u8c6a\u4f20\u7684\u5f00\u53d1\u8d44\u6599\u4e22\u7ed9\u675c\u98de\uff1a\u201c\u4f60\u770b\u770b\u8fd9\u4e2a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u675c\u98de\u63a5\u8fc7\u6765\uff0c\u7ffb\u5f00\u5230\u6249\u9875\u4e0a\uff0c\u201c\u54a6\uff0c\u6211\u4eec\u5b66\u6821\u7684\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5f20\u606a\u8fd8\u6ca1\u6709\u7ec6\u770b\u9879\u76ee\u7533\u8bf7\u4eba\u7684\u8d44\u6599\uff0c\u4fa7\u8fc7\u5934\u624d\u770b\u5230\u9879\u76ee\u7533\u8bf7\u4eba\u7684\u540d\u5b57\u8fd8\u6709\u4e9b\u8033\u719f\uff0c\u7ffb\u5f00\u4e86\u6700\u540e\u9879\u76ee\u7533\u8bf7\u4eba\u7684\u8bf4\u660e\u9875\uff0c\u8fd8\u771f\u662f\u6784\u9020\u6821\u56ed\u7f51\u5185\u4e1c\u5927BBS\u7684\u90a3\u4f19\u4eba\uff0c\u675c\u98de\u8fd9\u4e24\u5929\u5df2\u7ecf\u6df7\u8fdb\u53bb\u4e86\u3002</p></article>"}}}]);
