(window.webpackJsonp=window.webpackJsonp||[]).push([[781],{804:function(p){p.exports={chapterTitle:null,title:"\u7b2c780\u7ae0 \u90e8\u957f\u4eba\u9009",content:"<article><h2>\u7b2c780\u7ae0 \u90e8\u957f\u4eba\u9009</h2><p>\u3000\u3000\u8d70\u51fa\u673a\u573a\u63a5\u673a\u5927\u5385\uff0c\u4f17\u4eba\u94bb\u8fdb\u5546\u52a1\u8f66\u91cc\uff0c\u5f20\u606a\u5c06\u6628\u5929\u5230\u5b59\u5c1a\u4e49\u5bb6\u7528\u9910\u4e4b\u524d\u770b\u5230\u7684\u8f66\u7978\u8bf4\u7ed9\u5927\u5bb6\u542c\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u5173\u8f66\u7978\u7684\u62a5\u9053\u5c06\u520a\u8f7d\u5230\u9999\u6e2f\u65b0\u5468\u520a\u4e0a\uff0c\u4eca\u5929\u9ec4\u660f\u4e4b\u524d\uff0c\u8fd9\u671f\u7684\u65b0\u5468\u520a\u5c31\u4f1a\u53d1\u884c\u3002\u5f53\u7136\uff0c\u6211\u4eec\u8fd9\u4e48\u505a\u5e76\u4e0d\u53ea\u662f\u60f3\u7ed9\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u4ee5\u538b\u529b\u2026\u2026\u201d\u5f20\u606a\u7a0d\u505c\u4e86\u4e00\u4e0b\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u8981\u505a\u7684\u66f4\u591a\u3002\u201d</p><p>\u3000\u3000\u5728\u66fe\u7ecf\u53d1\u751f\u8fc7\u7684\u5386\u53f2\u4e2d\uff0c\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u4e0d\u4ec5\u4f7f\u4e1c\u5357\u4e9a\u5404\u56fd\u53ca\u5730\u533a\u3001\u65e5\u672c\u3001\u97e9\u56fd\u90fd\u7ed9\u5377\u5165\u7ecf\u6d4e\u5371\u673a\u7684\u6df1\u6e0a\uff0c\u8fd8\u5c06\u62d6\u57ae\u4fc4\u7f57\u65af\u7684\u7ecf\u6d4e\uff0c\u6b27\u5dde\u3001\u5317\u7f8e\u7684\u7ecf\u6d4e\u4e5f\u5c06\u53d7\u5230\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u6ce2\u53ca\u3002</p><p>\u3000\u3000\u773c\u4e0b\uff0c\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u8fd8\u4ec5\u4ec5\u8086\u8650\u8fc7\u4e1c\u4e9a\u3001\u4e1c\u5357\u4e9a\u5404\u56fd\u53ca\u5730\u533a\u5c31\u8c8c\u4f3c\u6062\u590d\u4e86\u5e73\u9759\uff0c\u5404\u9879\u91d1\u878d\u6570\u636e\u90fd\u6709\u56de\u5347\u7684\u8ff9\u8c61\u3002</p><p>\u3000\u3000\u8fd9\u5e76\u4e0d\u662f\u5386\u53f2\u53d1\u751f\u4e86\u66f4\u6539\uff0c\u800c\u662f\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5bf9\u5b9e\u4f53\u7ecf\u6d4e\u7684\u635f\u5bb3\u8fd8\u6ca1\u6709\u5f7b\u5e95\u7684\u663e\u9732\u51fa\u6765\uff0c\u4e00\u65e6\u5b9e\u4f53\u7ecf\u6d4e\u6240\u53d7\u5230\u635f\u5bb3\u5f7b\u5e95\u7684\u66b4\u9732\u51fa\u6765\uff0c\u6b64\u65f6\u91d1\u878d\u5e02\u573a\u77ed\u6682\u7684\u53cd\u5f39\u5c06\u560e\u7136\u800c\u6b62\uff0c\u91d1\u878d\u5e02\u573a\u4f1a\u8fdb\u4e00\u6b65\u7684\u9677\u5165\u52a8\u8361\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e00\u76f4\u5728\u4e0e\u53f6\u81fb\u6c11\u3001\u53f6\u5efa\u658c\u4ea4\u6d41\u8fd9\u65b9\u9762\u7684\u89c1\u89e3\uff0c\u53f6\u81fb\u6c11\u4ece\u53bb\u5e74\u5e74\u4e2d\u88ab\u4e2d\u592e\u59d4\u4efb\u4e3a\u9999\u6e2f\u4e2d\u8054\u529e\u526f\u4e3b\u4efb\uff0c\u6700\u4e3b\u8981\u7684\u804c\u8d23\u5c31\u662f\u66ff\u9999\u6e2f\u5f53\u5c40\u51fa\u8c0b\u5212\u7b56\u6765\u9003\u8fc7\u91d1\u878d\u5371\u673a\u3002</p><p>\u3000\u3000\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u4ece\u5341\u4e8c\u6708\u4e2d\u65ec\u6682\u65f6\u6062\u590d\u5e73\u9759\u4e4b\u540e\uff0c\u53f6\u81fb\u6c11\u53c8\u6682\u65f6\u8fd4\u56de\u4ed6\u5728\u5916\u7ecf\u8d38\u90e8\u7684\u5c97\u4f4d\u4e0a\uff0c\u4f46\u662f\u4ed6\u517c\u7740\u7684\u9999\u6e2f\u4e2d\u8054\u529e\u526f\u4e3b\u4efb\u7684\u5934\u8854\u8fd8\u6ca1\u6709\u7ed9\u5265\u593a\uff0c\u56fd\u5185\u6709\u90e8\u5206\u76ee\u5149\u654f\u9510\u7684\u7ecf\u6d4e\u5b66\u5bb6\u770b\u6e05\u4e9a\u6d32\u98ce\u66b4\u53ea\u662f\u6682\u65f6\u5e73\u606f\u7684\u89c2\u70b9\uff0c\u4e00\u65e6\u5916\u754c\u6761\u4ef6\u9002\u5408\uff0c\u91d1\u878d\u5371\u673a\u5c31\u4f1a\u6709\u8fdb\u4e00\u6b65\u6f14\u5316\u3001\u6076\u5316\u3002\u53f6\u81fb\u6c11\u5c31\u662f\u8fd9\u4e00\u89c2\u70b9\u7684\u63d0\u51fa\u8005\uff0c\u5efa\u8bae\u56fd\u52a1\u9662\u5bf9\u4e1c\u4e9a\u53ca\u56fd\u5185\u7684\u7ecf\u6d4e\u653f\u7b56\u8981\u52a0\u500d\u7684\u8c28\u614e\u624d\u884c\u3002</p><p>\u3000\u3000\u4f17\u4eba\u5148\u8d76\u5230\u53f6\u81fb\u6c11\u5728\u5317\u4eac\u7684\u5bd3\u6240\u3002\u5f20\u606a\u8fd9\u6b21\u4e0e\u53f6\u81fb\u6c11\u89c1\u9762\uff0c\u4e0d\u4ec5\u4ec5\u662f\u8981\u4ea4\u6d41\u5bf9\u5b8f\u89c2\u7ecf\u6d4e\u53d1\u5c55\u7684\u770b\u6cd5\uff0c\u4e3b\u8981\u7684\u8fd8\u662f\u5982\u4f55\u501f\u52bf\u593a\u5f97\u9999\u6e2f\u4e0a\u5e02\u516c\u53f8\u5609\u4fe1\u5730\u4ea7\u3001\u5609\u4fe1\u7535\u5b50\u7684\u8bdd\u8bdd\u6743\u751a\u81f3\u63a7\u5236\u6743\uff0c\u8fd9\u624d\u8ba9\u9648\u4fe1\u751f\u3001\u8bb8\u9e3f\u4f2f\u3001\u53f6\u5efa\u658c\u4e00\u8d77\u5230\u5317\u4eac\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e09\u5341\u516d\u5c42\u7684\u5609\u4fe1\u5927\u53a6\u77d7\u7acb\u5728\u9999\u6e2f\u4e2d\u73af\u7684\u4e2d\u5fc3\uff0c\u662f\u4e3a\u5609\u4fe1\u96c6\u56e2\u5728\u9999\u6e2f\u7684\u6807\u5fd7\u5efa\u7b51\uff0c\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u6700\u91cd\u8981\u7684\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\uff0c\u4e5f\u5728\u5609\u4fe1\u5927\u53a6\u5185\u529e\u516c\u3002</p><p>\u3000\u3000\u8eab\u4e3a\u5609\u4fe1\u96c6\u56e2\u8463\u4e8b\u4f1a\u4e3b\u5e2d\u7684\u845b\u660e\u4fe1\u5e73\u65f6\u90fd\u5728\u5609\u4fe1\u5927\u53a6\u7b2c\u4e09\u5341\u516d\u5c42\u7684\u529e\u516c\u5ba4\u91cc\u5de5\u4f5c\uff0c\u5305\u62ec\u4f1a\u5ba2\u5385\u3001\u4f1a\u8bae\u5ba4\u3001\u5ba4\u5185\u9ad8\u5c14\u592b\u7ec3\u4e60\u5ba4\u3001\u529e\u516c\u5ba4\u5728\u5185\u7684\u6574\u6574\u4e00\u5c42\u697c\uff0c\u90fd\u4e3a\u845b\u660e\u4fe1\u4e13\u7528\u3002</p><p>\u3000\u3000\u5728\u68d5\u7ea2\u8272\u7684\u6a80\u6728\u529e\u516c\u5ba4\u4e0a\uff0c\u6446\u653e\u7740\u4e00\u672c\u6700\u65b0\u4e00\u671f\u7684\u9999\u6e2f\u65b0\u5468\u520a\uff0c\u8fd9\u671f\u65b0\u5468\u520a\u7684\u5c01\u9762\u8d6b\u7136\u662f\u6628\u5929\u6d45\u6c34\u6e7e\u5927\u9053\u8f66\u7978\u73b0\u573a\u7684\u7167\u7247\uff0c\u7ea2\u8272\u6807\u9898\u7684\u4e0b\u7f18\u90fd\u505a\u6ef4\u6c34\u72b6\u5904\u7406\uff0c\u4eff\u4f5b\u5728\u63a7\u8bc9\u8840\u6dcb\u6dcb\u7684\u60e8\u5267\uff0c\u4f46\u90fd\u4e0d\u6bd4\u201c\u845b\u59d3\u5bcc\u8c6a\u4e4b\u5b50\u5371\u9669\u9a7e\u8f66\u81f4\u4eba\u6b7b\u4ea1\uff0c\u79c1\u4e0b\u63a5\u89e6\u6b7b\u8005\u5bb6\u4eba\u6b32\u7528\u91d1\u94b1\u8131\u7f6a\u201d\u8fd9\u6837\u7684\u6807\u9898\u5185\u5bb9\u523a\u76ee\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u770b\u5230\u65b0\u5468\u520a\u91cc\u7684\u6587\u7ae0\uff0c\u80f8\u53e3\u7684\u6c14\u8840\u7ffb\u6d8c\uff0c\u6309\u94c3\u8ba9\u79d8\u4e66\u901a\u77e5\u6cd5\u52a1\u90e8\u7684\u9ad8\u7ea7\u987e\u95ee\u8d75\u5b50\u5f3a\u5230\u4ed6\u529e\u516c\u5ba4\u6765\u3002\u8d75\u5b50\u5f3a\u5c31\u662f\u6628\u5929\u4ee3\u8868\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u51fa\u9762\u7684\u4e2d\u5e74\u4eba\u5f8b\u5e08\uff0c\u8d75\u5b50\u5f3a\u5230\u6765\u4e4b\u540e\uff0c\u845b\u660e\u4fe1\u5c06\u90a3\u672c\u65b0\u5468\u520a\u6742\u5fd7\u9012\u5230\u4ed6\u7684\u624b\u91cc\uff0c\u8138\u8272\u9634\u6c89\u3001\u8bed\u6c14\u51b0\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u4e00\u5b9a\u8981\u544a\u5230\u65b0\u5468\u520a\u7834\u4ea7\u5173\u95e8\u2026\u2026\u201d</p><p>\u3000\u3000\u845b\u660e\u4fe1\u76db\u6012\u4e4b\u65f6\u7684\u8868\u73b0\u5c31\u662f\u8138\u8272\u9634\u6c89\u3001\u8bed\u6c14\u51b0\u51b7\uff0c\u8d75\u5b50\u5f3a\u770b\u8fc7\u8fd9\u7bc7\u62a5\u9053\uff0c\u867d\u8bf4\u5728\u6587\u7ae0\u4e2d\u76f4\u63a5\u70b9\u51fa\u845b\u836b\u5747\u7684\u540d\u5b57\uff0c\u8feb\u4f7f\u65b0\u5468\u520a\u6536\u56de\u62a5\u9053\u3001\u516c\u5f00\u9053\u6b49\u5bb9\u6613\uff0c\u4f46\u662f\u8981\u5c06\u65b0\u5468\u520a\u544a\u5230\u7834\u4ea7\u5173\u95e8\u8fd8\u662f\u4e0d\u5927\u53ef\u80fd\u3002\u8d75\u5b50\u5f3a\u6ca1\u6709\u529d\u963b\uff0c\u5148\u6253\u5b98\u53f8\u518d\u8bf4\uff0c\u80fd\u4e0d\u80fd\u8ba9\u65b0\u5468\u520a\u7834\u4ea7\u5173\u95e8\uff0c\u90a3\u662f\u53e6\u8bf4\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u7684\u6012\u706b\u4e0e\u5176\u8bf4\u662f\u7ed9\u65b0\u5468\u520a\u7684\u62a5\u9053\u523a\u6fc0\uff0c\u4e0d\u5982\u8bf4\u662f\u5f20\u606a\u6628\u5929\u5728\u533b\u9662\u524d\u7684\u8bdd\u8ba9\u4ed6\u9690\u9690\u5fc3\u60ca\uff0c\u65b0\u5468\u520a\u7684\u8fd9\u7bc7\u62a5\u9053\u4e0d\u6b63\u662f\u4ee3\u8868\u4ed6\u4eec\u4e0d\u4f1a\u653e\u624b\u7684\u610f\u56fe\u5417\uff1f</p><p>\u3000\u3000\u4ed6\u4eec\u7a76\u7adf\u60f3\u505a\u4ec0\u4e48\uff0c\u8fd9\u6837\u7684\u62a5\u9053\u5e76\u4e0d\u80fd\u6539\u53d8\u4ec0\u4e48\uff0c\u867d\u7136\u8ba9\u5609\u4fe1\u96c6\u56e2\u627f\u53d7\u4e00\u5b9a\u7684\u8206\u8bba\u538b\u529b\uff0c\u4f46\u5bf9\u8f66\u7978\u5ba1\u67e5\u5374\u4e0d\u4f1a\u6709\u591a\u5927\u7684\u5f71\u54cd\uff0c\u845b\u660e\u4fe1\u4e0d\u7531\u5728\u60f3\uff0c\u4ed6\u4eec\u7a76\u7adf\u60f3\u505a\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u5f88\u5feb\uff0c\u845b\u660e\u4fe1\u5c31\u731c\u6d4b\u5230\u5f20\u606a\u3001\u5b59\u5c1a\u4e49\u4ed6\u4eec\u7684\u610f\u56fe\u4e86\u3002</p><p>\u3000\u3000\u8d8a\u79c0\u63a7\u80a1\u5728\u4e0b\u5348\u56db\u70b9\u8bb8\u53ec\u5f00\u8bb0\u8005\u4f1a\u5ba3\u5e03\u5c06\u4ece\u96c6\u56e2\u8463\u4e8b\u957f\u5b59\u5c1a\u4e49\u3001\u96c6\u56e2\u8463\u4e8b\u845b\u660e\u4fe1\u624b\u91cc\u6536\u8d2d\u4ed6\u4eec\u6240\u6301\u76843\uff05\u5609\u4fe1\u7535\u5b50\u30012\uff05\u5609\u4fe1\u5730\u4ea7\u7684\u80a1\u7968\uff0c\u8d8a\u79c0\u5c06\u4ee5\u5b9a\u5411\u589e\u53d1\u80a1\u7968\u7684\u5f62\u5f0f\u652f\u4ed8\u6536\u8d2d\u6b3e\uff0c\u5e76\u8868\u793a\u5c06\u4ece\u516c\u5f00\u5e02\u573a\u6216\u5411\u7279\u5b9a\u7684\u4e2a\u4eba\u7ee7\u7eed\u6536\u8d2d\u5609\u4fe1\u7535\u5b50\u4e0e\u5609\u4fe1\u5730\u4ea7\u7684\u80a1\u7968\uff0c\u5e76\u8868\u793a\u7b2c\u4e00\u6b21\u589e\u6301\u662f\u5728\u8fd1\u4e24\u4e2a\u6708\u5185\uff0c\u589e\u6301\u5e45\u5ea6\u4e0d\u8d85\u8fc7\u603b\u80a1\u672c\u768410\uff05\uff0c\u4f46\u4e0d\u6392\u65a5\u65e5\u540e\u4f1a\u8fdb\u4e00\u6b65\u7684\u589e\u6301\u3002</p><p>\u3000\u3000\u8d8a\u79c0\u6beb\u4e0d\u63a9\u9970\u7684\u8868\u9732\u51fa\u5bf9\u5609\u4fe1\u7535\u5b50\u4e0e\u5609\u4fe1\u5730\u4ea7\u7684\u91ce\u5fc3\uff0c\u8f66\u7978\u4e0d\u8fc7\u662f\u4e2a\u8bf1\u56e0\u53bb\u4e86\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u7684\u76ee\u6807\u662f\u6574\u4e2a\u5609\u4fe1\u96c6\u56e2\uff0c\u201d\u845b\u836b\u5747\u770b\u5230\u65b0\u5468\u520a\u7684\u6587\u5b57\u62a5\u9053\u65f6\uff0c\u6ca1\u6709\u4ec0\u4e48\u614c\u5f20\u7684\uff0c\u53ea\u662f\u7ed9\u81ea\u5df1\u4e70\u4e86\u4e24\u526f\u963f\u739b\u5c3c\u7684\u58a8\u955c\u2014\u2014\u56e0\u4e3a\u65b0\u5468\u520a\u7684\u5185\u9875\u5c06\u4ed6\u7684\u7167\u7247\u4e5f\u520a\u767b\u51fa\u6765\uff0c\u4f46\u662f\u4ed6\u5728\u542c\u5230\u8d8a\u79c0\u63a7\u80a1\u7684\u516c\u5f00\u58f0\u660e\u4e4b\u540e\uff0c\u5c31\u6012\u6c14\u51b2\u51b2\u7684\u8d70\u5230\u5609\u4fe1\u5927\u53a6\u7684\u4e09\u5341\u516d\u5c42\uff0c\u201c\u4ed6\u4eec\u771f\u662f\u4e0d\u77e5\u9053\u6b7b\u6d3b\uff0c\u7adf\u7136\u5bf9\u5609\u4fe1\u96c6\u56e2\u52a8\u4e86\u89ca\u89ce\u4e4b\u5fc3\uff0c\u4ed6\u4eec\u5c31\u4e0d\u6015\u5403\u4e0d\u4e0b\u6491\u6b7b\uff1f\u201d</p><p>\u3000\u3000\u845b\u660e\u4fe1\u624b\u6478\u7740\u4e0b\u9894\uff0c\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\u9f0e\u76db\u65f6\uff0c\u603b\u5e02\u503c\u9ad8\u8fbe\u56db\u767e\u4ebf\uff0c\u7ee7\u7eed\u9057\u4ea7\u4e4b\u540e\uff0c\u845b\u660e\u4fe1\u540d\u4e0b\u6240\u6301\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\u7684\u80a1\u7968\u5c31\u8ba9\u4ed6\u6709\u767e\u4ebf\u8eab\u5bb6\u3002\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\uff0c\u867d\u8bf4\u9999\u6e2f\u5e94\u5bf9\u5f97\u5f53\uff0c\u4f46\u662f\u91d1\u878d\u5e02\u573a\u4e5f\u662f\u8fde\u906d\u632b\u6298\uff0c\u52a0\u4e0a\u9999\u6e2f\u697c\u5e02\u4ece\u53bb\u5e74\u6df1\u79cb\u8d77\u5c31\u6709\u5d29\u76d8\u8ff9\u8c61\uff0c\u4f7f\u5f97\u9999\u6e2f\u6574\u4f53\u5730\u4ea7\u80a1\u90fd\u906d\u53d7\u91cd\u632b\uff0c\u5609\u4fe1\u5730\u4ea7\u6b64\u65f6\u7684\u5e02\u503c\u751a\u81f3\u4e0d\u8db3\u6700\u9f0e\u76db\u65f6\u671f\u7684\u4e09\u5206\u4e4b\u4e00\uff0c\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u4ef7\u4e5f\u4e0b\u964d\u4e86\u8d85\u8fc7\u4e00\u534a\uff0c\u4e24\u5bb6\u516c\u53f8\u7684\u603b\u5e02\u503c\u751a\u81f3\u4e0d\u8db3\u4e24\u767e\u4ebf\u3002\u4e3a\u4e86\u89e3\u51b3\u5176\u4ed6\u7ecf\u8425\u4e1a\u52a1\u906d\u53d7\u91d1\u878d\u98ce\u66b4\u7684\u5f71\u54cd\uff0c\u845b\u660e\u4fe1\u4e00\u800c\u518d\u3001\u518d\u800c\u4e09\u7684\u51cf\u6301\u5bf9\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\u7684\u6301\u80a1\u3002\u845b\u660e\u4fe1\u6b64\u65f6\u62e5\u6709\u8fd9\u4e24\u5bb6\u516c\u53f8\u80a1\u7968\u7684\u5e02\u503c\u538b\u7f29\u5230\u56db\u5341\u4ebf\u5de6\u53f3\uff0c\u7ea6\u536022\uff05\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u8bc1\u5238\u5e02\u573a\u4e0d\u6ce2\u52a8\uff0c\u8d8a\u79c0\u53ea\u8981\u62ff\u51fa\u8d85\u8fc7\u56db\u5341\u4ebf\u7684\u8d44\u91d1\uff0c\u5c31\u80fd\u5c06\u4e24\u5bb6\u516c\u53f8\u7684\u63a7\u5236\u6743\u62a2\u5230\u624b\u91cc\u3002</p><p>\u3000\u3000\u5609\u4fe1\u96c6\u56e2\u7684\u6839\u672c\u5c31\u662f\u8fd9\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\uff0c\u4e00\u65e6\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u7684\u63a7\u5236\u6743\u4e0d\u5728\u624b\u91cc\uff0c\u5609\u4fe1\u96c6\u56e2\u4e5f\u5c31\u6210\u4e86\u7ed9\u67b6\u7a7a\u7684\u6846\u67b6\u800c\u5df2\u3002</p><p>\u3000\u3000\u53d7\u65b0\u5468\u520a\u62a5\u9053\u7684\u8d1f\u9762\u5f71\u54cd\uff0c\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u4e24\u5bb6\u516c\u53f8\u7684\u80a1\u4ef7\u90fd\u5927\u5e45\u5927\u4e0b\u632b\uff0c\u8d8a\u79c0\u63a7\u80a1\u6240\u53ec\u5f00\u7684\u8bb0\u8005\u4f1a\u5219\u5728\u56db\u70b9\u9999\u6e2f\u8bc1\u5238\u8054\u5408\u4ea4\u6613\u6240\u95ed\u5e02\u4e4b\u540e\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u865a\u6643\u4e00\u67aa\uff1f\u6216\u8bb8\u6839\u672c\u5c31\u6ca1\u6709\u6253\u7b97\u5bf9\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u63a7\u80a1\u52a8\u624b\uff0c\u518d\u8bf4\u4e86\uff0c\u5c31\u7b97\u8d8a\u79c0\u76f4\u63a5\u589e\u6301\u603b\u80a1\u672c10\uff05\u7684\u80a1\u7968\uff0c\u4e5f\u8981\u52a8\u7528\u4e8c\u4e09\u5341\u4ebf\u7684\u5de8\u8d44\uff0c\u6211\u770b\u4ed6\u4eec\u672a\u5fc5\u6709\u8fd9\u6837\u7684\u51b3\u5fc3\u4e0e\u8010\u5fc3\u3002\u201d\u845b\u836b\u5747\u8bf4\u9053\u3002</p><p>\u3000\u3000\u591a\u5fe7\u65e0\u7528\uff0c\u773c\u4e0b\u4e5f\u53ea\u80fd\u9759\u5f85\u8d8a\u79c0\u63a7\u80a1\u641e\u51fa\u4ec0\u4e48\u540d\u5802\u4e4b\u540e\uff0c\u624d\u80fd\u4e88\u4ee5\u53cd\u51fb\uff0c\u603b\u4e0d\u80fd\u56e0\u4e3a\u8d8a\u79c0\u7684\u4e00\u5219\u516c\u5f00\u58f0\u660e\u5c31\u4e71\u4e86\u624b\u811a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4ece\u53f6\u81fb\u6c11\u5728\u5317\u4eac\u7684\u5c45\u6240\u51fa\u6765\uff0c\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u5728\u968f\u884c\u4eba\u5458\u7684\u966a\u540c\u4e0b\u94bb\u8fdb\u6c7d\u8f66\uff0c\u524d\u5f80\u897f\u5355\u89c1\u90ae\u7535\u90e8\u515a\u7ec4\u526f\u4e66\u8bb0\u3001\u526f\u90e8\u957f\u6613\u4e91\u98de\u2014\u2014\u5176\u4ed6\u4eba\u5219\u7559\u5728\u53f6\u81fb\u6c11\u5bb6\u7528\u665a\u9910\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u6613\u4e91\u98de\u89c1\u9762\uff0c\u59da\u6587\u76db\u4e0e\u6613\u99a8\u2014\u2014\u59da\u6587\u76db\u540d\u4e49\u4e0a\u7684\u672a\u5a5a\u59bb\u8fd8\u7279\u610f\u5206\u522b\u4ece\u5efa\u90ba\u3001\u6df1\u5733\u98de\u56de\u5317\u4eac\u3002\u6613\u4e91\u98de\u8fd8\u7279\u610f\u9080\u8bf7\u4e0b\u5c5e\u7684\u90ae\u7535\u90e8\u5b98\u5458\u738b\u65ed\u741b\u5230\u5bb6\u91cc\u6765\u966a\u540c\uff0c\u53ea\u56e0\u4e3a\u738b\u65ed\u741b\u4e4b\u5973\u738b\u5f69\u94c3\u4e0e\u5f20\u606a\u8868\u5144\u6881\u6587\u6c5f\u786e\u5b9a\u4e86\u604b\u7231\u5173\u7cfb\uff0c\u53cc\u65b9\u5bb6\u957f\u8fd8\u7279\u610f\u5728\u53bb\u5e74\u5230\u6d77\u5dde\u89c1\u9762\u3002</p><p>\u3000\u3000\u5728\u5230\u6613\u4e91\u98de\u5bb6\u7684\u8def\u4e0a\uff0c\u5f20\u606a\u4e0e\u665a\u6674\u901a\u8fc7\u7535\u8bdd\uff0c\u5979\u5df2\u7ecf\u4e0e\u52a9\u624b\u5f20\u5ead\u98de\u5230\u5317\u4eac\u62b5\u8fbe\u5f90\u5b66\u5e73\u5bb6\uff0c\u4eca\u5929\u4e5f\u6ca1\u6709\u98de\u673a\u80fd\u5c06\u82b7\u5f64\u63a5\u56de\u6d77\u5dde\u53bb\uff0c\u8fd8\u8981\u5728\u5317\u4eac\u7559\u5bbf\u4e00\u591c\u3002</p><p>\u3000\u3000\u6613\u4e91\u98de\u5728\u5317\u4eac\u7684\u5c45\u6240\u662f\u680b\u4f4d\u4e8e\u957f\u5b89\u8857\u897f\u7aef\u7684\u56db\u5408\u9662\uff0c\u4e2d\u592e\u6709\u8bb8\u591a\u9ad8\u5b98\u90fd\u4f4f\u5728\u8fd9\u4e00\u7247\u7684\u56db\u5408\u9662\u91cc\uff0c\u6613\u4e91\u98de\u7684\u7ea7\u522b\u5728\u8fd9\u4e9b\u9ad8\u5b98\u5458\u90fd\u8981\u7b97\u7ea7\u522b\u4f4e\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d76\u5230\u65f6\u79bb\u7528\u9910\u65f6\u95f4\u8fd8\u65e9\uff0c\u6613\u4e91\u98de\u8bf7\u5927\u5bb6\u5230\u6539\u4e3a\u4e66\u623f\u7684\u4e1c\u53a2\u623f\u91cc\u5148\u5750\u4e0b\u804a\u4e00\u804a\u3002</p><p>\u3000\u3000\u201c\u4e24\u4f1a\u8fd8\u8981\u8fc7\u534a\u4e2a\u6708\u624d\u4f1a\u5728\u5317\u4eac\u53ec\u5f00\uff0c\u4e0d\u8fc7\u6709\u4e9b\u7ed3\u679c\u5927\u5bb6\u90fd\u5df2\u7ecf\u80fd\u770b\u5230\u4e86\u3002\u8d75\u6d4e\u4e1c\u526f\u603b\u7406\u6276\u6b63\u4e4b\u540e\uff0c\u56fd\u52a1\u9662\u673a\u6784\u6539\u5236\u5c31\u4f1a\u8fc5\u901f\u62c9\u5f00\uff0c\u73b0\u5728\u90ae\u7535\u90e8\u5185\u90e8\u90fd\u5728\u8bae\u8bba\u7eb7\u7eb7\uff0c\u8fd9\u4e00\u70b9\uff0c\u8001\u738b\u8eab\u5904\u5176\u5883\u3001\u662f\u6df1\u6709\u4f53\u4f1a\u7684\u3002\u201d\u6613\u4e91\u98de\u8bf7\u5927\u5bb6\u8d70\u8fdb\u4ed6\u7684\u4e66\u623f\uff0c\u5c31\u5f00\u95e8\u89c1\u5c71\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u90ae\u7535\u90e8\u5c06\u7535\u4fe1\u4e1a\u52a1\u5206\u62c6\u51fa\u6765\u4e0e\u7535\u5b50\u5de5\u4e1a\u90e8\u7b49\u90e8\u95e8\u5408\u5e76\u6210\u4fe1\u606f\u4ea7\u4e1a\u90e8\uff0c\u8fd9\u51e0\u4e4e\u5df2\u7ecf\u662f\u65e0\u6cd5\u9006\u8f6c\u7684\u4e8b\u5b9e\u4e86\uff0c\u73b0\u5728\u60f9\u4f17\u4eba\u731c\u6d4b\uff1a\u672a\u6765\u8c01\u4f1a\u662f\u4fe1\u606f\u4ea7\u4e1a\u90e8\u7684\u638c\u95e8\u4eba\uff1f</p><p>\u3000\u3000\u90ae\u7535\u90e8\u73b0\u5728\u90e8\u957f\u803f\u91cd\u9633\u867d\u7136\u6709\u8bb8\u591a\u7f3a\u70b9\uff0c\u521a\u610e\u81ea\u7528\u3001\u601d\u60f3\u4fdd\u5b88\u3001\u813e\u6c14\u6025\u71e5\uff0c\u4e0e\u8d75\u6d4e\u4e1c\u526f\u603b\u7406\u7684\u5173\u7cfb\u4e0d\u878d\u5408\uff0c\u5f53\u7136\u4e0d\u80fd\u5426\u8ba4\u4ed6\u5bf9\u56fd\u5185\u7535\u4fe1\u4e8b\u4e1a\u505a\u51fa\u7684\u5353\u8d8a\u8d21\u732e\uff0c\u662f\u5f53\u7136\u7684\u5019\u9009\u4eba\u4e4b\u4e00\u3002\u7535\u5b50\u5de5\u4e1a\u90e8\u6b64\u65f6\u7684\u90e8\u957f\u5e74\u9f84\u5230\u9650\uff0c\u53ef\u80fd\u5728\u4e24\u4f1a\u4e0a\u5c31\u8981\u9000\u5c45\u4e8c\u7ebf\uff1b\u6613\u4e91\u98de\u6b64\u65f6\u5c31\u5728\u90ae\u7535\u90e8\u5206\u7ba1\u7535\u4fe1\u4e1a\u52a1\uff0c\u4f46\u662f\u8981\u53bb\u4fe1\u606f\u4ea7\u4e1a\u90e8\u7684\u90e8\u957f\u8d44\u5386\u7a0d\u6d45\uff0c\u6700\u5927\u53ef\u80fd\u8fd8\u662f\u5230\u90ae\u7535\u90e8\u62c5\u4efb\u526f\u804c\uff1b\u6b64\u65f6\u8fd8\u4e0d\u80fd\u5fd8\u8bb0\u8ba1\u59d4\u5206\u7ba1\u4fe1\u606f\u4ea7\u4e1a\u7684\u526f\u4e3b\u4efb\u540c\u65f6\u517c\u4efb\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u9886\u5bfc\u5c0f\u7ec4\u7ec4\u957f\u845b\u5efa\u5fb7\u3002\u5728\u845b\u5efa\u5fb7\u4e0e\u803f\u91cd\u9633\u4e4b\u95f4\uff0c\u5f20\u606a\u5b81\u613f\u803f\u91cd\u9633\u6267\u638c\u4fe1\u606f\u4ea7\u4e1a\u90e8\u3002\u867d\u8bf4\u803f\u91cd\u9633\u5bf9\u9526\u6e56\u9887\u4e3a\u4e0d\u6ee1\uff0c\u53c8\u662f\u7535\u4fe1\u5784\u65ad\u601d\u7ef4\u7684\u575a\u5b9a\u62e5\u62a4\u8005\uff0c\u4f46\u662f\u6bd4\u8d77\u845b\u5efa\u5fb7\u8d77\u6765\uff0c\u803f\u91cd\u9633\u5c1a\u8bb2\u539f\u5219\u3001\u8bb2\u515a\u6027\u3002</p><p>\u3000\u3000\u6613\u4e91\u98de\u770b\u7740\u5f20\u606a\u7684\u53cd\u5e94\uff0c\u8bf4\u9053\uff1a\u201c\u803f\u91cd\u9633\u90e8\u957f\u5bf9\u6539\u5236\u540e\u7684\u90e8\u957f\u4e4b\u4f4d\u4e5f\u662f\u52bf\u5728\u5fc5\u5f97\uff0c\u4ed6\u751a\u81f3\u90fd\u5f00\u59cb\u62df\u5b9a\u62c5\u4efb\u8981\u804c\u7684\u53d1\u5c55\u89c4\u5212\uff0c\u5176\u4e2d\u4e00\u9879\u5c31\u662f\u7ec4\u5efa\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\u516c\u53f8\uff0c\u4f1a\u5c06\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u7b49\u7535\u5b50\u516c\u53f8\u53ca\u7814\u53d1\u673a\u6784\u90fd\u7f6e\u5165\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\u516c\u53f8\u65d7\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u5f20\u606a\u5438\u4e00\u53e3\u6c14\uff0c\u6240\u8c13\u7684\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\u6ca1\u6709\u5728\u66fe\u7ecf\u53d1\u751f\u8fc7\u7684\u5386\u53f2\u4e2d\u51fa\u73b0\u8fc7\uff0c\u5927\u6982\u662f\u9526\u6e56\u7684\u5b58\u5728\u4fc3\u4f7f\u803f\u91cd\u9633\u60f3\u7ec4\u5efa\u4e00\u4e2a\u56fd\u6709\u7684\u7535\u5b50\u4ea7\u4e1a\u7684\u91cd\u578b\u822a\u6bcd\u3002</p></article>"}}}]);
