(window.webpackJsonp=window.webpackJsonp||[]).push([[787],{810:function(p){p.exports={chapterTitle:null,title:"\u7b2c785\u7ae0 \u5973\u7814\u7a76\u751f",content:"<article><h2>\u7b2c785\u7ae0 \u5973\u7814\u7a76\u751f</h2><p>\u3000\u3000\u5f88\u591a\u4eba\u6709\u80fd\u529b\u3001\u6709\u60f3\u6cd5\uff0c\u4f46\u662f\u7f3a\u5c11\u673a\u9047\uff0c\u5f88\u53ef\u80fd\u6d51\u6d51\u5669\u5669\u4e00\u751f\uff0c\u4e00\u4e8b\u65e0\u6210\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u5927\u5b66\u867d\u662f\u56fd\u5185\u5c48\u6307\u53ef\u6570\u7684\u540d\u6821\uff0c\u4f46\u662f\u6709\u7740\u4e0e\u4e00\u822c\u4f01\u4e8b\u4e1a\u673a\u5173\u76f8\u540c\u7684\u4f53\u5236\u7279\u5f81\uff0c\u4f7f\u4eba\u8eab\u5904\u5176\u4e2d\u6d88\u78e8\u610f\u5fd7\uff0c\u7136\u540e\u6cef\u7136\u4f17\u4eba\uff0c\u4e0e\u5468\u56f4\u5728\u5b66\u751f\u65f6\u4ee3\u6df1\u6076\u75db\u7edd\u7684\u4eba\u7fa4\u6beb\u65e0\u533a\u522b\u3002</p><p>\u3000\u3000\u8fd9\u7c7b\u4eba\uff0c\u79e6\u521a\u770b\u5230\u592a\u591a\uff0c\u4e4b\u524d\u6709\u62b5\u6297\uff0c\u73b0\u5728\u7684\u4ed6\u4e5f\u6beb\u4e0d\u6000\u7591\u81ea\u5df1\u4e5f\u5c06\u6210\u4e3a\u8fd9\u7c7b\u4eba\u3002\u4ed6\u6709\u59bb\u5b50\uff0c\u5b69\u5b50\u5373\u4f7f\u51fa\u4e16\uff0c\u7236\u6bcd\u8eab\u4f53\u72b6\u51b5\u4e0d\u592a\u597d\uff0c\u611f\u89c9\u5f97\u5230\u8eab\u4e0a\u7684\u8d23\u4efb\u91cd\u5927\uff0c\u8d8b\u708e\u9644\u52bf\u3001\u52c7\u6c14\u5f00\u59cb\u4e27\u5931\uff0c\u4e4b\u524d\u8fc7\u4e8e\u5f3a\u70c8\u7684\u81ea\u4fe1\u5fc3\u5df2\u7ecf\u52a8\u6447\uff0c\u6b64\u65f6\u7684\u79e6\u521a\u6216\u8bb8\u518d\u5f80\u524d\u8de8\u4e00\u6b65\uff0c\u5c31\u6cef\u7136\u4f17\u4eba\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5c31\u50cf\u7a81\u7136\u6709\u9053\u95e8\u5728\u773c\u524d\u6253\u5f00\u4f3c\u7684\uff0c\u773c\u524d\u8c41\u7136\u5927\u4eae\uff0c\u90a3\u5373\u5c06\u7ed9\u6d88\u78e8\u7184\u706d\u7684\u610f\u5fd7\u9661\u7136\u95f4\u5c31\u6b7b\u7070\u590d\u71c3\u8d77\u6765\uff0c\u6d51\u8eab\u5145\u6ee1\u6597\u5fd7\u3002\u521b\u529e\u8fd9\u4e2a\u57f9\u8bad\u673a\u6784\u50cf\u4e00\u628a\u63ea\u8fc7\u6765\u4f3c\u7684\uff0c\u4e0d\u5bb9\u4ed6\u4e0d\u5c3d\u6700\u5927\u53ef\u80fd\u7684\u5c06\u7cbe\u529b\u6295\u5165\u8fdb\u6765\u3002</p><p>\u3000\u3000\u79e6\u521a\u77e5\u9053\u57f9\u8bad\u673a\u6784\u8981\u529e\u6210\u3001\u529e\u597d\uff0c\u9664\u4e86\u6821\u65b9\u7684\u6001\u5ea6\uff0c\u80fd\u4e0d\u80fd\u53d6\u5f97\u8ba1\u7b97\u673a\u3001\u4fe1\u606f\u5de5\u7a0b\u7b49\u76f8\u5173\u9662\u7cfb\u7684\u652f\u6301\u4e5f\u662f\u5173\u952e\u3002\u521b\u57df\u8981\u641e\u8ba1\u7b97\u673a\u7f51\u7edc\u57f9\u8bad\u5b66\u6821\uff0c\u9664\u4e86\u9700\u8981\u6302\u9760\u5230\u4e1c\u5927\u540d\u4e0b\uff0c\u4e5f\u6ca1\u6709\u53ef\u80fd\u7acb\u5373\u57f9\u517b\u4e00\u6279\u4f18\u79c0\u7684\u6559\u5e08\u961f\u4f0d\uff0c\u65e0\u975e\u8981\u4f9d\u8d56\u5468\u8fb9\u9ad8\u6821\u7684\u5e08\u8d44\u8d44\u6e90\uff0c\u800c\u4e1c\u5927\u8ba1\u7b97\u673a\u7cfb\u5c06\u662f\u4f9d\u8d56\u7684\u91cd\u70b9\u3002</p><p>\u3000\u3000\u5728\u5411\u5d14\u56fd\u6052\u6c47\u62a5\u4e4b\u540e\uff0c\u79e6\u521a\u5c31\u7acb\u5373\u627e\u5230\u6821\u957f\u529e\u516c\u5ba4\u4ee5\u53ca\u8ba1\u7b97\u673a\u7cfb\u7b49\u76f8\u5173\u9662\u7cfb\u7684\u9886\u5bfc\u8054\u7edc\u6b64\u4e8b\u3002\u65e0\u8bba\u662f\u6821\u65b9\u8fd8\u662f\u76f8\u5173\u9662\u7cfb\u7684\u9886\u5bfc\uff0c\u5728\u542c\u8bf4\u521b\u57df\u83b7\u5f97\u6a61\u6811\u56ed\u7684\u8d44\u52a9\u4e4b\u540e\u521b\u529e\u8fd9\u5bb6\u57f9\u8bad\u673a\u6784\uff0c\u90fd\u8fc5\u901f\u505a\u51fa\u56de\u5e94\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e1c\u6d77\u5927\u5b66\u91cc\uff0c\u5f20\u606a\u6700\u949f\u610f\u56fe\u4e66\u9986\u5e95\u5c42\u7684\u7efc\u5408\u9605\u89c8\u5ba4\uff0c\u5750\u5728\u9760\u7a97\u7684\u5ea7\u4f4d\u4e0a\uff0c\u7a97\u5916\u7b80\u6613\u6728\u67b6\u4e0a\u7684\u8461\u8404\u85e4\u8fd8\u6ca1\u6709\u53d1\u51fa\u65b0\u82bd\uff0c\u906e\u853d\u4e0d\u4e86\u7684\u5915\u9633\u5149\u4ece\u7a97\u5916\u900f\u5c04\u8fdb\u6765\u843d\u5728\u684c\u4e0a\u7684\u4e66\u4e0a\u3002</p><p>\u3000\u3000\u63a5\u5230\u7fdf\u4e39\u9752\u7684\u7535\u8bdd\uff0c\u5f20\u606a\u5408\u4e0a\u4e66\uff0c\u6709\u4e9b\u4e66\u8981\u8fd8\u56de\u4e66\u67b6\uff0c\u6709\u4e9b\u4e66\u4ed6\u662f\u4ece\u85cf\u4e66\u9986\u501f\u6765\u56de\u516c\u5bd3\u770b\u7684\uff0c\u8d70\u5230\u5a34\u9759\u800c\u5750\u7684\u9648\u5983\u84c9\u8eab\u8fb9\uff0c\u5f2f\u8170\u51d1\u5230\u5979\u8033\u8fb9\u8f7b\u58f0\u6253\u62db\u547c\uff1a\u201c\u665a\u4e0a\u8fd8\u6709\u4e8b\uff0c\u6211\u5148\u8d70\u4e86\uff1b\u672c\u6765\u7fdf\u59d0\u8981\u8bf7\u4f60\u5403\u996d\u7684\u3002\u201d\u9648\u5983\u84c9\u5ae3\u7136\u800c\u7b11\uff0c\u660e\u4eae\u7684\u7738\u5b50\u770b\u7740\u5f20\u606a\u8d70\u51fa\u9605\u89c8\u5ba4\u3002</p><p>\u3000\u3000\u56de\u5b57\u578b\u5efa\u7b51\u7684\u56fe\u4e66\u9986\u5929\u4e95\u5927\u5385\u91cc\uff0c\u7fdf\u4e39\u9752\u7a7f\u7740\u6de1\u7d2b\u7684\u683c\u5b50\u5916\u5957\uff0c\u91cc\u9762\u7a7f\u7740\u7684\u5957\u88d9\u7ed9\u957f\u5916\u5957\u906e\u4f4f\uff0c\u53ea\u770b\u89c1\u88f9\u7740\u8089\u8272\u4e1d\u889c\u5b50\u7684\u7ea4\u7ec6\u5c0f\u817f\u4e0e\u9c9c\u8273\u593a\u76ee\u7684\u7ea2\u8272\u9ad8\u8ddf\u978b\uff0c\u7ad9\u5728\u56fe\u4e66\u9986\u7684\u5929\u4e95\u5927\u5385\u91cc\uff0c\u660e\u8273\u800c\u593a\u76ee\uff0c\u6709\u79cd\u201c\u5317\u65b9\u6709\u4f73\u4eba\u3001\u9057\u4e16\u800c\u72ec\u7acb\u201d\u7684\u611f\u89c9\u3002\u8fd9\u4e16\u4eba\u7684\u7f8e\u5973\u4e0d\u5c11\uff0c\u8dd1\u5230\u97f3\u4e50\u5b66\u9662\u3001\u519b\u533a\u6b4c\u821e\u56e2\u3001\u5e08\u5927\u53bb\u4e00\u6293\u4e00\u628a\uff0c\u5374\u662f\u8fd9\u79cd\u72ec\u7279\u7684\u6c14\u8d28\u4e0e\u5353\u7ea6\u4e0d\u662f\u5e38\u4eba\u80fd\u6709\uff0c\u8ba9\u5979\u6709\u7740\u6c81\u5165\u4eba\u5fc3\u7684\u7f8e\u3002</p><p>\u3000\u3000\u201c\u8fd8\u4e0d\u77e5\u9053\u606a\u5c11\u662f\u8fd9\u4e48\u7231\u5b66\u4e60\u7684\u4e00\u4e2a\u4eba\u2026\u2026\u201d\u7fdf\u4e39\u9752\u4e4b\u524d\u5230\u4e1c\u5927\u6765\u8bfb\u4e1c\u5927MBA\u7684\u8003\u524d\u57f9\u73ed\uff0c\u4e0e\u5f20\u606a\u5728\u4e1c\u5927\u6709\u8fc7\u63a5\u89e6\uff0c\u4f46\u662f\u90fd\u6ca1\u50cf\u73b0\u5728\u8fd9\u822c\u6df1\u5165\u4ed6\u7684\u6821\u56ed\u751f\u6d3b\u4e4b\u4e2d\uff0c\u4ece\u56fe\u4e66\u9986\u9605\u89c8\u5ba4\u65e9\u6668\u5f00\u95e8\uff0c\u5f20\u606a\u62ff\u7740\u9762\u5305\u4e0e\u6c34\u8fdb\u53bb\u4e00\u76f4\u5750\u5230\u592a\u9633\u897f\u5782\uff0c\u8981\u6ca1\u6709\u5176\u4ed6\u4e8b\u60c5\u5e72\u6270\uff0c\u4ed6\u4f1a\u4e00\u76f4\u5750\u5230\u591c\u95f4\u95ed\u9986\u624d\u79bb\u5f00\u3002</p><p>\u3000\u3000\u201c\u4e1c\u5927\u6700\u8ba9\u6211\u7231\u7684\u5730\u65b9\u5c31\u662f\u8fd9\u5ea7\u56fe\u4e66\u9986\u4e86\u2026\u2026\u6211\u53c8\u4e0d\u53ef\u80fd\u5962\u4f88\u5230\u8981\u4e3a\u81ea\u5df1\u5efa\u4e00\u5ea7\u85cf\u4e66\u4e0a\u5343\u4e07\u518c\u7684\u56fe\u4e66\u9986\uff0c\u8fd9\u5927\u6982\u662f\u6211\u9009\u62e9\u8fd9\u56db\u5e74\u5728\u8fd9\u91cc\u53ae\u6df7\u7684\u4e3b\u8981\u539f\u56e0\u5427\u3002\u201d\u5f20\u606a\u7b11\u9053\uff0c\u4ed6\u5e76\u4e0d\u60f3\u8ddf\u522b\u4eba\u8bf4\u8d77\u4ed6\u85cf\u8eab\u4e1c\u5927\u6821\u56ed\u7684\u771f\u6b63\u539f\u56e0\uff0c\u201c\u5b66\u4e60\u4e0e\u601d\u8003\uff0c\u672c\u6765\u5c31\u662f\u6211\u63a8\u5378\u4e0d\u4e86\u7684\u8d23\u4efb\u2026\u2026\u6211\u8fd9\u4e48\u8bf4\uff0c\u4f1a\u4e0d\u4f1a\u8ba9\u4f60\u6709\u7279\u522b\u7684\u611f\u89e6\uff1f\u201d</p><p>\u3000\u3000\u201c\u5982\u6b64\u8ba4\u771f\u7684\u523b\u82e6\u5b66\u4e60\uff0c\u771f\u662f\u4e0d\u7b26\u5408\u4f60\u7ea8\u7ed4\u5b50\u5f1f\u7684\u5f62\u8c61\u554a\u3002\u201d\u7fdf\u4e39\u9752\u53d6\u7b11\u5f20\u606a\u9053\uff0c\u8981\u4f38\u624b\u5c06\u4ed6\u624b\u91cc\u7684\u4e66\u63a5\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u7f29\u4e86\u56de\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u5fc5\u8981\u8fd9\u4e48\u5ba2\u6c14\uff0c\u4f60\u8981\u505a\u7684\u5de5\u4f5c\u53ef\u6bd4\u5e2e\u6211\u63d0\u5305\u590d\u6742\u591a\u4e86\uff0c\u6211\u4e5f\u4e0d\u4e60\u60ef\u522b\u4eba\u770b\u6211\u7684\u773c\u795e\u592a\u602a\u5f02\u2026\u2026\u201d</p><p>\u3000\u3000\u5185\u90e8\u7684\u8f6c\u89d2\u6276\u68af\u5c31\u8bbe\u5728\u5929\u4e95\u5927\u5385\u91cc\uff0c\u6700\u9876\u7aef\u8986\u76d6\u7740\u906e\u9633\u73bb\u7483\u677f\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u524d\u80a9\u5411\u5927\u5385\u5916\u8d70\u53bb\uff0c\u5d14\u90c1\u66fc\u4e0e\u540c\u5b66\u8bf4\u8bf4\u7b11\u7b11\u4ece\u8f6c\u89d2\u697c\u68af\u8d70\u4e0b\u6765\uff0c\u5d14\u90c1\u66fc\u8ddf\u540c\u5b66\u6b63\u8ba8\u8bba\u95ee\u9898\uff0c\u80cc\u7740\u8eab\u5b50\u4e0b\u697c\uff0c\u6ca1\u6709\u770b\u5230\u5f20\u606a\uff0c\u5979\u540c\u5b66\u63d0\u9192\u5979\u5c0f\u5fc3\u522b\u649e\u5230\u4eba\u65f6\uff0c\u5979\u626d\u8fc7\u5934\u6765\u770b\uff0c\u89c1\u662f\u5f20\u606a\u505c\u4e0b\u6765\u7ad9\u5728\u697c\u68af\u53e3\u770b\u7740\u5979\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u8e29\u7a7a\u4e00\u9636\u697c\u68af\uff0c\u6574\u4e2a\u4eba\u671d\u5f20\u606a\u6000\u91cc\u649e\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u8eab\u624b\u4e5f\u591f\u654f\u6377\u7684\uff0c\u770b\u7740\u5d14\u90c1\u66fc\u671d\u4ed6\u6000\u91cc\u649e\u6765\uff0c\u8eab\u5b50\u53ca\u65f6\u95ea\u5f00\u3002\u5d14\u90c1\u66fc\u6ca1\u6709\u6293\u4f4f\u5230\u652f\u6491\u7269\uff0c\u62d0\u811a\u8e09\u8dc4\u8fde\u51b2\u4e86\u4e24\u6b65\uff0c\u7ec8\u6ca1\u80fd\u4fdd\u6301\u4f4f\u5e73\u8861\uff0c\u8dcc\u5012\u5728\u5730\u4e0a\uff0c\u624b\u91cc\u6367\u7740\u4e00\u5927\u5806\u4e66\u6d12\u4e86\u4e00\u5730\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u6ca1\u770b\u5230\u662f\u4f60\uff0c\u4f60\u4e0d\u4f1a\u602a\u6211\u53ca\u65f6\u95ea\u5f00\u5427\uff1f\u201d\u5f20\u606a\u7b11\u76c8\u76c8\u7684\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u6ee1\u9762\u901a\u7ea2\uff0c\u6628\u5929\u8fd8\u677f\u7740\u8138\u8bad\u8fd9\u5c0f\u5b50\uff0c\u4eca\u5929\u5c31\u5728\u8fd9\u5c0f\u5b50\u9762\u524d\u51fa\u4e86\u4e11\uff0c\u8fd8\u7ed9\u4ed6\u62ff\u8bdd\u595a\u843d\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u7684\u4e24\u4e2a\u540c\u5b66\u6025\u5fd9\u8d70\u8fc7\u6765\u5c06\u5979\u4ece\u51b0\u51b7\u7684\u78e8\u77f3\u5730\u9762\u4e0a\u6276\u8d77\u6765\uff0c\u4e00\u4e2a\u5973\u5b69\u5b50\u56de\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u62b1\u6028\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u600e\u4e48\u8fd9\u6837\uff0c\u770b\u5230\u4eba\u4ece\u697c\u68af\u4e0a\u8dcc\u4e0b\u6765\uff0c\u4e0d\u6276\u4e00\u628a\uff0c\u7adf\u7136\u95ea\u5f00\u4e86\uff1f\u201d\u770b\u5230\u5f20\u606a\u8fb9\u4e0a\u7684\u7fdf\u4e39\u9752\uff0c\u5012\u662f\u6014\u4e86\u4e00\u6014\u3002</p><p>\u3000\u3000\u770b\u7740\u5d14\u90c1\u66fc\u7ad9\u8d77\u6765\u63c9\u7740\u649e\u75bc\u7684\u819d\u76d6\uff0c\u770b\u8fc7\u6765\u7684\u773c\u795e\u53c8\u7f9e\u53c8\u607c\uff0c\u6bcd\u8001\u864e\u7684\u5c41\u80a1\u53ea\u53ef\u4ee5\u5076\u5c14\u6478\u4e00\u6478\uff0c\u4f46\u8981\u5728\u5979\u607c\u7f9e\u6210\u6012\u4e4b\u524d\u6e9c\u6389\uff0c\u626f\u4e86\u626f\u7fdf\u4e39\u9752\u7684\u8896\u7ba1\uff0c\u8fde\u5fd9\u8d70\u6389\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8fd8\u597d\u5947\u7684\u56de\u5934\u770b\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u662f\u8c01\u554a\uff0c\u8ddf\u4f60\u6709\u6df1\u4ec7\u5927\u6068\u554a\uff0c\u7adf\u7136\u8fd9\u4e48\u7f3a\u5fb7\u7684\u95ea\u5f00\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f55\u6b62\u6df1\u4ec7\u5927\u6068\u554a\uff0c\u5979\u7adf\u7136\u5bf9\u6211\u7b80\u76f4\u662f\u6b32\u9664\u4e4b\u800c\u540e\u5feb\u2026\u2026\u201d\u5f20\u606a\u544a\u8bc9\u7fdf\u4e39\u9752\u5d14\u90c1\u66fc\u5c31\u662f\u6628\u5929\u8ba9\u4ed6\u4e0e\u675c\u98de\u90fd\u611f\u5230\u5934\u75bc\u7684\u65b0\u4e0a\u4efb\u5e74\u7ea7\u4e3b\u4efb\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4f38\u624b\u63a9\u5507\u5927\u7b11\u8d77\u6765\uff0c\u6e05\u8106\u7684\u7b11\u58f0\u94f6\u94c3\u4f3c\u7684\u6d12\u843d\u5230\u56fe\u4e66\u9986\u7684\u5927\u5385\uff0c\u6b47\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u4e00\u624b\u6342\u7740\u8170\u773c\uff0c\u4e00\u624b\u6307\u7740\u5f20\u606a\uff1a\u201c\u6211\u770b\u4f60\u6700\u574f\u7684\u5730\u65b9\u5c31\u662f\u8fd9\u6837\u8086\u65e0\u5fcc\u60ee\u7684\u8c03\u620f\u5973\u5b69\u5b50\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u54ea\u6709\u554a\uff1f\u6211\u8eb2\u90fd\u8fd8\u6765\u4e0d\u53ca\uff0c\u54ea\u91cc\u6562\u53bb\u8c03\u620f\u5979\u554a\uff1f\u201d\u5f20\u606a\u65e0\u8f9c\u7684\u644a\u4e86\u644a\u624b\uff0c\u9876\u66ff\u5085\u4fca\u7684\u9a6c\u6d77\u9f99\u5728\u56fe\u4e66\u9986\u5927\u95e8\u5916\u7b49\u5f20\u606a\u3001\u7fdf\u4e39\u9752\uff0c\u770b\u5230\u4ed6\u4eec\u8d70\u51fa\u6765\uff0c\u5c31\u5148\u4f38\u624b\u5e2e\u4ed6\u4eec\u6253\u5f00\u8f7f\u8f66\u7684\u8f66\u95e8\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e24\u4e2a\u5973\u5b69\u5b50\u90fd\u662f\u5d14\u90c1\u66fc\u7684\u5ba4\u53cb\uff0c\u5979\u4eec\u626d\u8fc7\u5934\u770b\u7740\u5f20\u606a\u3001\u7fdf\u4e39\u9752\u94bb\u8fdb\u9ed1\u8272\u7684\u5c0f\u8f7f\u8f66\u91cc\uff0c\u8fd8\u6124\u6124\u4e0d\u5e73\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\u7537\u7684\u771f\u662f\u7684\uff0c\u767d\u957f\u4e86\u4e00\u526f\u597d\u770b\u7684\u8138\uff0c\u4e00\u70b9\u90fd\u4e0d\u89e3\u98ce\u60c5\uff0c\u4ee5\u540e\u8c01\u8fd8\u6562\u6545\u610f\u5f80\u4ed6\u6000\u91cc\u649e\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u522b\u82b1\u75f4\u554a\uff0c\u5c0f\u66fc\u51fa\u624b\u4ed6\u90fd\u80fd\u8eb2\u5f00\uff1b\u4f60\u8981\u649e\u8fc7\u53bb\uff0c\u4ed6\u4e0d\u4ec5\u4f1a\u95ea\u5f00\uff0c\u6307\u4e0d\u5b9a\u8fd8\u4f1a\u671d\u4f60\u5c41\u80a1\u4e0a\u8e39\u4e00\u811a\u2026\u2026\u4f60\u6ca1\u770b\u5230\u4ed6\u8eab\u8fb9\u7684\u90a3\u4e2a\u5973\u4eba\uff0c\u7b80\u76f4\u5c31\u662f\u5996\u5b7d\u554a\u3002\u201d\u53e6\u4e00\u4e2a\u5973\u5b69\u5b50\u6ee1\u773c\u8ff7\u79bb\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u5f97\u610f\u7684\u8d70\u5f00\uff0c\u5d14\u90c1\u66fc\u63c9\u7740\u8dcc\u7834\u76ae\u7684\u819d\u76d6\uff0c\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u542c\u89c1\u4e24\u4e2a\u6ca1\u4eba\u6027\u7684\u540c\u5b66\u5728\u90a3\u91cc\u80e1\u626f\uff0c\u597d\u50cf\u662f\u81ea\u5df1\u72af\u82b1\u75f4\u6545\u610f\u649e\u4e0a\u53bb\u4e00\u6837\uff0c\u5934\u90fd\u5927\u4e86\u4e24\u5708\uff0c\u89c1\u5979\u4eec\u90fd\u8fd8\u770b\u7740\u8f7f\u8f66\u79bb\u5f00\u7684\u65b9\u5411\uff0c\u5fff\u6068\u7684\u8bf4\u9053\uff1a\u201c\u522b\u53d1\u5446\u770b\u4e86\uff0c\u4ed6\u5c31\u662f\u6211\u8ddf\u4f60\u4eec\u8bf4\u7684\u5f20\u606a\uff0c\u6d77\u5dde\u56db\u5927\u6076\u5c11\u4e4b\u9996\uff0c\u770b\u5230\u4ed6\u51fa\u884c\u7684\u6c14\u6d3e\uff0c\u4ee5\u540e\u4e0d\u4f1a\u8bf4\u6211\u8ddf\u4f60\u4eec\u5728\u80e1\u5439\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u662f\u4ed6\uff01\u770b\u4e0a\u53bb\u86ee\u9633\u5149\u7684\u597d\u5e05\u5c0f\u4f19\u5b50\u554a\uff0c\u201d\u4e00\u4e2a\u5973\u5b69\u5b50\u8be7\u5f02\u7684\u8dd1\u5230\u5927\u95e8\u8fb9\u770b\u518d\u770b\u6d88\u901d\u5728\u62d0\u89d2\u7684\u8f7f\u8f66\u4e24\u773c\uff0c\u60c6\u6005\u7684\u8bf4\u9053\uff0c\u201c\u4ed6\u8981\u5165\u5ba4\u6765\u7ed1\u67b6\u6211\uff0c\u4f60\u4eec\u8bf4\u6211\u662f\u6323\u624e\u597d\uff0c\u8fd8\u662f\u4e0d\u6323\u624e\u597d\uff1f\u201d</p><p>\u3000\u3000\u53e6\u4e00\u4e2a\u5973\u5b69\u5b50\u8bf4\u9053\uff1a\u201c\u90fd\u6709\u4eba\u8bf4\u5973\u7814\u7a76\u751f\u662f\u706d\u7edd\u5e08\u592a\uff0c\u6211\u770b\u540d\u58f0\u5c31\u662f\u4ece\u4f60\u5f00\u59cb\u7ed9\u8d25\u574f\u7684\uff0c\u9700\u8981\u8fd9\u4e48\u9965\u6e34\u5417\uff1f\u7b49\u4f1a\u513f\u4e70\u4e24\u6839\u5c0f\u9ec4\u74dc\u9001\u7ed9\u4f60\uff0c\u4e0d\u8bb8\u63d0\u610f\u89c1\uff0c\u4e0d\u8bb8\u8981\u6c42\u4e24\u6839\u7ec6\u7684\u6362\u6839\u5927\u7684\uff0c\u63d0\u610f\u89c1\u9ec4\u74dc\u5c31\u5207\u7247\u2026\u2026\u201d\u7a81\u7136\u8bed\u6c14\u4e00\u8f6c\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u597d\u60f3\u5f53\u4e03\u53ea\u6c14\u7403\u7684\u5973\u4e3b\u89d2\u554a\uff0c\u5c31\u7b97\u53d7\u4f24\uff0c\u5c31\u7b97\u7ed9\u65e0\u60c5\u7684\u629b\u5f03\uff0c\u597d\u6b79\u4e5f\u8981\u8ddf\u8fd9\u4e48\u5e05\u3001\u8fd9\u4e48\u574f\u7684\u7537\u4eba\u8c08\u4e00\u573a\u604b\u7231\u554a\uff01\u201d</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u542c\u5230\u5ba4\u53cb\u7684\u8bdd\uff0c\u5dee\u70b9\u518d\u8dcc\u4e00\u8de4\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u5e76\u4e0d\u6e05\u695a\u5f53\u5e74\u7ed1\u67b6\u6848\u80cc\u540e\u7684\u7ec6\u8282\uff0c\u8b66\u5bdf\u6070\u597d\u662f\u5728\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u7b49\u95ef\u5165\u4e4b\u65f6\u51fa\u73b0\u5236\u6b62\u72af\u7f6a\u672c\u8eab\u5c31\u5e26\u6709\u5f88\u591a\u7684\u620f\u5267\u6027\uff0c\u540e\u6765\u5979\u54e5\u54e5\u8ffd\u67e5\u5230\u4e3e\u529e\u4eba\u9ec4\u67f1\u5168\u90a3\u91cc\u5c31\u518d\u4e5f\u67e5\u4e0d\u4e0b\u53bb\uff0c\u80fd\u660e\u663e\u770b\u51fa\u56f4\u7ed5\u7ed1\u67b6\u6848\u6709\u7740\u66f4\u591a\u6251\u6714\u8ff7\u79bb\u7684\u8ff7\u56e2\u3002\u5730\u65b9\u4e0a\u6709\u4e9b\u4e8b\u60c5\uff0c\u5c31\u7b97\u6709\u518d\u5927\u7684\u80fd\u8010\u90fd\u672a\u5fc5\u80fd\u76f4\u63a5\u63ed\u5f00\u76d6\u5b50\uff0c\u80fd\u770b\u5230\u80cc\u540e\u6709\u6743\u529b\u6597\u4e89\u7684\u75d5\u8ff9\u3002\u4ece\u90a3\u6b21\u4e8b\u4ef6\u4e4b\u540e\uff0c\u5d14\u90c1\u66fc\u5148\u56de\u5230\u5317\u4eac\u4f11\u606f\uff0c\u867d\u8bf4\u6d77\u5dde\u4e00\u4e2d\u5c06\u5979\u7684\u5de5\u4f5c\u8c03\u5230\u521d\u4e2d\u90e8\uff0c\u5979\u5176\u5b9e\u518d\u6ca1\u6709\u56de\u8fc7\u6d77\u5dde\u4e00\u5929\uff0c\u800c\u662f\u7ed9\u5979\u7236\u6bcd\u4ee5\u5f3a\u786c\u7684\u6001\u5ea6\u4e0e\u624b\u6bb5\u6263\u7559\u5728\u5317\u4eac\u7684\u5bb6\u4e2d\u5907\u8003\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u63ea\u7740\u5ba4\u53cb\u5e2e\u5979\u5c06\u6563\u4e86\u4e00\u5730\u7684\u4e66\u6361\u8d77\u6765\uff0c\u5728\u5979\u501f\u51fa\u6765\u7684\u90a3\u5806\u4e66\u4e2d\uff0c\u591a\u4e86\u4e00\u672c\u8584\u8584\u7684\u5c0f\u518c\u5b50\uff0c\u90a3\u591a\u534a\u662f\u5f20\u606a\u95ea\u8eab\u8df3\u5f00\u65f6\u65e0\u610f\u95f4\u6389\u4e0b\u6765\u7684\u3002\u5d14\u90c1\u66fc\u6b6a\u7740\u8111\u888b\u770b\u7740\u624b\u91cc\u7684\u5c0f\u518c\u5b50\uff0c\u7adf\u662f\u9ec4\u5b97\u7fb2\u7684\u300a\u660e\u5937\u5f85\u8bbf\u5f55\u300b\uff0c\u5fc3\u91cc\u8be7\u5f02\u4e07\u5206\uff1a\u8fd9\u5c0f\u5b50\u4f1a\u770b\u4ece\u56fe\u4e66\u9986\u91cc\u501f\u51fa\u8fd9\u79cd\u4e66\u770b\uff0c\u8fd8\u662f\u8bf4\u81ea\u5df1\u65e0\u610f\u4ece\u56fe\u4e66\u9986\u91cc\u8fde\u7740\u5176\u4ed6\u4e66\u4e00\u8d77\u62ff\u51fa\u6765\u7684\uff1f\u5d14\u90c1\u66fc\u66f4\u503e\u5411\u4e8e\u76f8\u4fe1\u540e\u4e00\u79cd\u53ef\u80fd\uff0c\u867d\u8bf4\u53ef\u80fd\u6027\u8981\u6bd4\u524d\u4e00\u79cd\u5c0f\u5f97\u591a\uff0c\u4eba\u7684\u601d\u7ef4\u60ef\u6027\u6216\u8005\u8bf4\u504f\u89c1\uff0c\u603b\u662f\u5f88\u96be\u626d\u8f6c\u8fc7\u6765\u7684\u3002</p><p>\u3000\u3000\u6700\u597d\u7684\u529e\u6cd5\u5c31\u662f\u62ff\u5230\u529e\u501f\u4e66\u7684\u5730\u65b9\u8f93\u5165\u6761\u5f62\u7801\u67e5\u4e00\u4e0b\u5c31\u77e5\u9053\u4e86\uff0c\u987a\u4fbf\u5c06\u8fd9\u672c\u4e66\u8fd8\u56de\u4e66\u5e93\u5c31\u53ef\u4ee5\u4e86\u3002</p></article>"}}}]);
