(window.webpackJsonp=window.webpackJsonp||[]).push([[79],{102:function(p){p.exports={chapterTitle:null,title:"\u7b2c79\u7ae0 \u7701\u57ce\u4e4b\u884c",content:"<article><h2>\u7b2c79\u7ae0 \u7701\u57ce\u4e4b\u884c</h2><p>\u3000\u3000\u7b49\u848b\u8587\u63a8\u95e8\u51fa\u53bb\uff0c\u5f20\u606a\u770b\u7740\u8bb8\u601d\uff0c\u5979\u8138\u4e0a\u7684\u8868\u60c5\u5f88\u602a\uff0c\u95ee\u5979\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u770b\u4e0d\u900f\u4f60\uff0c\u201d\u8bb8\u601d\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u7ffb\u624b\u4e3a\u4e91\u3001\u8986\u624b\u4e3a\u96e8\uff0c\u5927\u6982\u5c31\u662f\u8bf4\u4f60\u7684\u884c\u4e3a\uff0c\u4f46\u662f\u771f\u7684\u4e0d\u80fd\u8ddf\u4f60\u7684\u5e74\u7eaa\u8054\u7cfb\u8d77\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u628a\u9ebb\u70e6\u7559\u7ed9\u522b\u4eba\uff0c\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u7684\u4e8b\uff0c\u6211\u4eec\u53ef\u6ca1\u6709\u80fd\u529b\u5e94\u4ed8\u8fd9\u4e9b\u95ee\u9898\uff0c\u6240\u4ee5\u91c7\u53d6\u4e00\u4e9b\u624b\u6bb5\u4e5f\u662f\u6ca1\u6709\u529e\u6cd5\u7684\u4e8b\uff0c\u201d\u5f20\u606a\u6487\u6487\u5634\uff0c\u53ef\u4e0d\u60f3\u7ed9\u8bb8\u601d\u7559\u4e0b\u4ec0\u4e48\u4e0d\u597d\u7684\u5370\u8c61\uff0c\u201c\u53ef\u80fd\u662f\u592a\u81ea\u4fe1\u7684\u7f18\u6545\uff0c\u6211\u76f8\u4fe1\u81ea\u5df1\u80fd\u987a\u5229\u7684\u89e3\u51b3\u9020\u7eb8\u5382\u7684\u95ee\u9898\uff0c\u4e00\u5207\u90fd\u5728\u63a7\u5236\u4e4b\u4e2d\uff0c\u5c0f\u5c0f\u7684\u63d2\u66f2\uff0c\u4e0d\u4f1a\u6709\u4eba\u56e0\u6b64\u4f1a\u53d7\u5230\u4f24\u5bb3\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u8ba9\u8bb8\u601d\u89c9\u5f97\u4ed6\u6709\u4e9b\u5b69\u5b50\u6c14\uff0c\u5374\u613f\u610f\u76f8\u4fe1\u4ed6\uff0c\u6e29\u67d4\u7684\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u848b\u8587\u4e09\u70b9\u949f\u79bb\u5f00\u65b0\u6d77\u901a\u5927\u53a6\uff0c\u4e00\u76f4\u5230\u516d\u70b9\u949f\u624d\u6253\u7535\u8bdd\u56de\u529e\u516c\u5ba4\uff0c\u6c47\u62a5\u57ce\u5357\u533a\u653f\u5e9c\u7684\u60c5\u51b5\u3002\u5468\u5bcc\u660e\u5728\u7535\u8bdd\u91cc\u5c06\u57ce\u5357\u533a\u59d4\u4e66\u8bb0\u738b\u5c90\u6c5f\u72e0\u72e0\u8bad\u65a5\u4e86\u4e00\u987f\uff0c\u8ba9\u4ed6\u4eb2\u81ea\u8d1f\u8d23\u89e3\u51b3\u4e0a\u8bbf\u65b0\u5149\u9020\u7eb8\u5382\u804c\u5de5\u51b2\u51fb\u533a\u653f\u5e9c\u7684\u95ee\u9898\uff0c\u8fd9\u91cc\u5e02\u91cc\u81ea\u5165\u79cb\u4ee5\u6765\u9996\u6b21\u5bf9\u65b0\u5149\u9020\u7eb8\u5382\u7684\u804c\u5de5\u4e0a\u8bbf\u95ee\u9898\u8868\u660e\u7acb\u573a\uff1a\u804c\u5de5\u7684\u56f0\u96be\uff0c\u653f\u5e9c\u8981\u770b\u5728\u773c\u91cc\uff0c\u8bb0\u5728\u5fc3\u91cc\uff0c\u4f46\u662f\u5bf9\u4e8e\u804c\u5de5\u65e0\u9650\u5236\u7684\u7fa4\u8bbf\u95ee\u9898\uff0c\u8981\u4e25\u8083\u5bf9\u5f85\u3002\u5510\u5b66\u8c26\u968f\u540e\u7ed9\u5b8b\u57f9\u660e\u6253\u7535\u8bdd\uff0c\u80af\u5b9a\u57ce\u5357\u533a\u653f\u5e9c\u5173\u505c\u9020\u7eb8\u5382\u8001\u5382\u3001\u675c\u7edd\u57ce\u533a\u6c61\u67d3\u51b3\u5b9a\u7684\u6b63\u786e\u6027\uff0c\u8981\u6c42\u57ce\u5357\u533a\u653f\u5e9c\u575a\u6301\u539f\u5219\uff0c\u8010\u5fc3\u8bf4\u670d\u804c\u5de5\u7fa4\u4f17\uff0c\u5316\u89e3\u4e8b\u6001\u3002</p><p>\u3000\u3000\u6709\u5e02\u91cc\u4e00\u4e8c\u628a\u624b\u5bf9\u65b0\u5149\u9020\u7eb8\u5382\u804c\u5de5\u4e0a\u8bbf\u95ee\u9898\u8868\u6001\uff0c\u738b\u5c90\u6c5f\u4f1a\u540c\u5b8b\u57f9\u660e\uff0c\u5f53\u5373\u51b3\u5b9a\u5151\u73b0\u5b8b\u57f9\u660e\u6628\u5929\u5bf9\u65b0\u5149\u9020\u7eb8\u5382\u804c\u5de5\u7684\u627f\u8bfa\uff0c\u7531\u533a\u8d22\u653f\u8c03\u62e8\u4e8c\u5341\u4e07\uff0c\u4e34\u65f6\u89e3\u51b3\u65b0\u5149\u9020\u7eb8\u5382\u56f0\u96be\u804c\u5de5\u7684\u751f\u6d3b\u95ee\u9898\uff0c\u89e3\u51b3\u65b0\u5149\u9020\u7eb8\u5382\u804c\u5de5\u533b\u7597\u6551\u6cbb\u95ee\u9898\uff0c\u4f46\u5bf9\u4eca\u5929\u7ea0\u96c6\u804c\u5de5\u51b2\u51fb\u533a\u653f\u5e9c\u7684\u51e0\u540d\u804c\u5de5\u4ee5\u5e72\u6270\u793e\u4f1a\u6cbb\u5b89\u7684\u540d\u4e49\uff0c\u4e88\u4ee5\u884c\u653f\u62d8\u7559\uff0c\u5e76\u8981\u6c42\u65b0\u5149\u9020\u7eb8\u5382\u5bf9\u8fd9\u51e0\u540d\u804c\u5de5\u7ed9\u4e88\u884c\u653f\u5f00\u9664\u7684\u5904\u5206\u3002</p><p>\u3000\u3000\u57ce\u5357\u533a\u653f\u5e9c\u91c7\u53d6\u6069\u5a01\u5e76\u65bd\u7684\u624b\u6bb5\uff0c\u5f88\u5feb\u5c31\u5e73\u606f\u4e86\u65b0\u5149\u9020\u7eb8\u5382\u7684\u804c\u5de5\u7fa4\u8bbf\u98ce\u6ce2\uff0c\u8fd9\u4e3b\u8981\u53d6\u51b3\u4e8e\u5468\u5bcc\u660e\u4e0e\u5510\u5b66\u8c26\u7684\u8868\u6001\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u5728\u7535\u8bdd\u8ddf\u848b\u8587\u591a\u8bf4\u4ec0\u4e48\uff0c\u627f\u5305\u7ecf\u8425\u7684\u4e8b\u7531\u5979\u8ddf\u57ce\u5357\u533a\u653f\u5e9c\u63a5\u89e6\u3002</p><p>\u3000\u3000\u848b\u8587\u8fd9\u624d\u6e05\u695a\uff0c\u5f20\u606a\u867d\u7136\u53ea\u662f\u521a\u8bfb\u9ad8\u4e2d\u7684\u5c11\u5e74\uff0c\u5546\u4e1a\u4e0a\u7684\u5929\u8d4b\u81ea\u4e0d\u7528\u8bf4\uff0c\u5bf9\u6d77\u5dde\u5b98\u573a\u7684\u5f71\u54cd\u529b\uff0c\u4e5f\u662f\u4e0d\u5bb9\u5ffd\u89c6\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8bb8\u601d\u8d1f\u8d23\u516c\u53f8\u7684\u7b79\u5efa\uff0c\u848b\u8587\u8d1f\u8d23\u6d3d\u8c08\u627f\u5305\u7ecf\u8425\u65b0\u5149\u9020\u7eb8\u5382\u7684\u4e8b\u60c5\u3002\u8d2d\u8f66\u7684\u4e8b\u60c5\u901a\u8fc7\u76db\u946b\u516c\u53f8\u7684\u7279\u6b8a\u6e20\u9053\u53ef\u4ee5\u8282\u7ea6\u4e00\u5927\u7b14\u7684\u8d39\u7528\uff0c\u7279\u610f\u5728\u5341\u4e00\u6708\u7684\u7b2c\u4e8c\u4e2a\u5468\u672b\uff0c\u5f20\u606a\u62c9\u7740\u8bb8\u601d\u53bb\u7701\u57ce\u63d0\u8f66\u3002</p><p>\u3000\u3000\u4e4b\u524d\u7684\u4e00\u4e2a\u661f\u671f\u521a\u521a\u8fdb\u884c\u8fc7\u8fdb\u5165\u9ad8\u4e2d\u540e\u7684\u7b2c\u4e00\u6b21\u671f\u4e2d\u8003\u8bd5\uff0c\u5728\u8bb8\u601d\u7684\u50ac\u4fc3\u4e0b\uff0c\u5f20\u606a\u8fd8\u662f\u8ba4\u771f\u770b\u4e86\u51e0\u5929\u7684\u4e66\u3002\u5bf9\u4e8e\u6587\u79d1\u751f\u6765\u8bf4\uff0c\u9700\u8981\u80cc\u8bf5\u7684\u4e1c\u897f\u592a\u591a\uff0c\u5f20\u606a\u5e76\u4e0d\u5962\u671b\u80fd\u6709\u8ba9\u6240\u6709\u4eba\u90fd\u4f53\u9762\u7684\u6210\u7ee9\u3002</p><p>\u3000\u3000\u867d\u7136\u5165\u79cb\u7b2c\u4e8c\u4e2a\u6708\uff0c\u6c14\u6e29\u8fd8\u6ca1\u6709\u964d\u4e0b\u6765\u3002\u8bb8\u601d\u7a7f\u7740\u7d2b\u8272\u7684\u8d85\u77ed\u6b3e\u9488\u7ec7\u5f00\u886b\uff0c\u91cc\u9762\u662f\u9ed1\u8272\u62b9\u80f8\uff0c\u82a5\u8309\u7eff\u8272\u7684\u7d27\u8eab\u957f\u88e4\uff0c\u5496\u5561\u8272\u7684\u9ad8\u8ddf\u978b\uff0c\u7f00\u7740\u5927\u7c92\u7684\u6c34\u94bb\uff0c\u867d\u7136\u8fd8\u662f\u504f\u804c\u4e1a\u5316\u7684\u88c5\u675f\uff0c\u5374\u662f\u5f20\u606a\u8bb8\u4e45\u672a\u89c1\u7684\u6210\u719f\u6027\u611f\uff0c\u6ca1\u6709\u6234\u90a3\u4ed8\u5e73\u5149\u773c\u955c\uff0c\u4e00\u5934\u4e4c\u9ed1\u7684\u62ab\u80a9\u957f\u53d1\uff0c\u4fcf\u8138\u5982\u6625\uff0c\u79c0\u76f4\u7684\u9f3b\u6881\uff0c\u5a07\u8273\u7684\u7ea2\u5507\uff0c\u80f8\u90e8\u9ad8\u9ad8\u633a\u8d77\uff0c\u8584\u8584\u7684\u7d27\u8eab\u957f\u88e4\u5c06\u4fee\u957f\u7684\u7f8e\u817f\u7ef7\u7d27\uff0c\u773c\u775b\u770b\u7740\u5c31\u80fd\u611f\u89c9\u5230\u60ca\u4eba\u7684\u8bf1\u60d1\u5f39\u6027\uff0c\u5f20\u606a\u8d2a\u5a6a\u7684\u770b\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u53e3\u6c34\u5dee\u70b9\u6d41\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u778e\u770b\u4ec0\u4e48\uff1f\u201d\u8bb8\u601d\u4f38\u624b\u6321\u7740\u5f20\u606a\u7684\u773c\u775b\uff0c\u7b11\u9765\u59a9\u5a9a\uff0c\u201c\u8fd8\u4e0d\u5feb\u5e2e\u6211\u62ce\u4e1c\u897f\u3002\u201d</p><p>\u3000\u3000\u56e0\u4e3a\u8bf4\u8981\u53bb\u7701\u57ce\u770b\u770b\u665a\u6674\u59d0\u4e0e\u82b7\u5f64\uff0c\u8bb8\u601d\u7236\u4eb2\u51c6\u5907\u5f88\u591a\u6d77\u5dde\u5e02\u7684\u7279\u4ea7\uff0c\u5f53\u7136\u8fd8\u6709\u7ed9\u8bb8\u601d\u59b9\u59b9\u8bb8\u7ef4\u7684\u4e00\u4efd\u3002</p><p>\u3000\u3000\u201c\u6025\u4ec0\u4e48\uff0c\u597d\u4e45\u6ca1\u770b\u5230\u4f60\u8fd9\u4e48\u8ff7\u4eba\u7684\u6253\u626e\u4e86\uff0c\u8981\u4e0d\u6211\u4eec\u5728\u7701\u57ce\u7559\u4e00\u5929\uff0c\u63d0\u4e86\u8f66\uff0c\u901b\u8857\u4e5f\u65b9\u4fbf\uff1f\u201d\u8ba9\u8bb8\u601d\u8ff7\u5f97\u6709\u795e\u795e\u9b42\u98a0\u5012\uff0c\u5f20\u606a\u7a98\u8feb\u7684\u7b11\u4e86\u7b11\uff0c\u63a5\u8fc7\u8bb8\u601d\u624b\u91cc\u7684\u62c9\u6746\u884c\u674e\u7bb1\u3002</p><p>\u3000\u3000\u201c\u53bb\uff0c\u516c\u53f8\u7684\u4e8b\u60c5\u8fd9\u4e48\u591a\uff0c\u54ea\u6709\u5de5\u592b\u966a\u4f60\u7559\u5728\u7701\u57ce\u73a9\uff1f\u201d\u8bb8\u601d\u4ece\u624b\u63d0\u888b\u62ff\u51fa\u58a8\u955c\u6234\u4e0a\uff0c\u906e\u4f4f\u5927\u534a\u5f20\u8138\uff0c\u5374\u8ba9\u5a07\u8273\u7684\u7ea2\u5507\u3001\u4e30\u8174\u5ae9\u817b\u5982\u7389\u7684\u4e0b\u9894\u6108\u53d1\u7684\u8bf1\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u884c\u674e\u585e\u5230\u5ba2\u8f66\u7684\u884c\u91cc\u53a2\u91cc\uff0c\u4e24\u4eba\u4e0a\u4e86\u8f66\u3002\u4e34\u65f6\u5230\u8f66\u7ad9\u8d2d\u7968\u7684\u7f18\u6545\uff0c\u5ea7\u4f4d\u5728\u6700\u540e\u4e00\u6392\uff0c\u8ba9\u8bb8\u601d\u5750\u5728\u9760\u7a97\u7684\u91cc\u4fa7\u3002\u8f66\u7a97\u6253\u5f00\u5c11\u8bb8\uff0c\u98ce\u62c2\u52a8\u5979\u98d8\u98d8\u7684\u957f\u53d1\uff0c\u6709\u51e0\u4e1d\u7ec6\u53d1\u8d34\u5728\u5979\u5a07\u5ae9\u7684\u8138\u988a\u4e0a\uff0c\u5f20\u606a\u5c31\u8fd9\u6837\u76ee\u4e0d\u8f6c\u775b\u7684\u770b\u7740\uff0c\u60f3\u7740\u8981\u4e0d\u8981\u5e2e\u5979\u628a\u8138\u4e0a\u7684\u53d1\u4e1d\u6311\u5f00\u3002</p><p>\u3000\u3000\u8bb8\u601d\u80fd\u611f\u89c9\u5230\u5f20\u606a\u8d2a\u5a6a\u7684\u76ee\u5149\uff0c\u62ff\u5b09\u76ae\u8d56\u8138\u7684\u4ed6\u6ca1\u6709\u529e\u6cd5\uff0c\u60f3\u7740\u5f20\u606a\u4e5f\u53ea\u5bf9\u81ea\u5df1\u8fd9\u6837\uff0c\u5185\u5fc3\u6df1\u5904\u672a\u514d\u6ca1\u6709\u4e00\u4e1d\u4e1d\u7684\u751c\u871c\u611f\uff0c\u8138\u5fae\u5fae\u7ea2\u7740\uff0c\u624b\u6258\u7740\u4e0b\u5df4\uff0c\u8896\u7ba1\u633d\u5230\u624b\u8098\u5904\uff0c\u9732\u51fa\u7684\u624b\u81c2\u767d\u7699\u5982\u521d\u96ea\uff0c\u79c0\u957f\u7684\u7709\u5934\u5374\u4e00\u4ed8\u82e5\u6709\u6240\u601d\u7684\u6837\u5b50\u3002</p><p>\u3000\u3000\u201c\u662f\u4e0d\u662f\u5728\u60f3\u8bb8\u7ef4\u59d0\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5979\u597d\u4e45\u6ca1\u56de\u5bb6\u4e86\uff0c\u201d\u8bb8\u601d\u624b\u62b5\u7740\u592a\u9633\u7a74\uff0c\u626d\u8fc7\u5934\u770b\u7740\u5f20\u606a\uff0c\u201c\u5979\u5728\u7701\u57ce\u52a8\u7684\u624b\u672f\uff0c\u4e4b\u540e\u4e5f\u6ca1\u6709\u56de\u8fc7\u6d77\u5dde\uff0c\u5076\u5c14\u80fd\u901a\u4e0a\u7535\u8bdd\uff0c\u5979\u4f3c\u4e4e\u4e0d\u5927\u613f\u610f\u8ddf\u6211\u8bf4\u8bdd\u3002\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4f1a\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u8bb8\u7ef4\u59d0\u6216\u8bb8\u8ba4\u4e3a\u81ea\u5df1\u7ed9\u5bb6\u91cc\u5e26\u6765\u5f88\u91cd\u7684\u8d1f\u62c5\uff0c\u4f60\u4e0d\u662f\u8bf4\u5979\u5728\u5b66\u6821\u4e5f\u517c\u51e0\u4efd\u5de5\u4f5c\u5417\uff0c\u53ef\u80fd\u4e5f\u6210\u4e86\u5c0f\u8d22\u8ff7\uff0c\u6293\u7d27\u65f6\u95f4\u6323\u6bcf\u4e00\u5206\u94b1\uff0c\u6ca1\u6709\u65f6\u95f4\u56de\u6d77\u5dde\u4e5f\u662f\u6b63\u5e38\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5c31\u6015\u5979\u592a\u5014\u5f3a\u4e86\uff0c\u5979\u52a8\u624b\u672f\u4e4b\u540e\uff0c\u5c31\u6062\u590d\u4e86\u4e24\u4e2a\u6708\uff0c\u5c31\u575a\u6301\u51fa\u9662\u56de\u6821\u4e86\uff0c\u5c31\u6015\u5979\u7684\u8eab\u4f53\u5403\u4e0d\u6d88\u3002\u201d\u8bb8\u601d\u62c5\u5fe7\u7684\u8bf4\u3002</p><p>\u3000\u3000\u201c\u778e\u60f3\u6ca1\u6709\u7528\uff0c\u53bb\u7701\u57ce\u4e0d\u5c31\u770b\u5230\u5979\u4e86\u5417\uff1f\u201d\u5f20\u606a\u62b1\u5934\u9760\u7740\u6905\u80cc\uff0c\u201c\u672c\u6253\u7b97\u63d0\u4e86\u8f66\u5148\u966a\u4f60\u53bb\u901b\u5546\u573a\uff0c\u7b97\u4e86\uff0c\u5148\u966a\u4f60\u53bb\u770b\u8bb8\u7ef4\u59d0\u3002\u201d</p><p>\u3000\u3000\u5ba2\u8f66\u76f4\u63a5\u5f00\u5230\u7701\u57ce\u6c7d\u8fd0\u603b\u7ad9\uff0c\u521a\u4e0b\u8f66\u62ff\u4e86\u884c\u674e\uff0c\u8d70\u8fdb\u51fa\u7ad9\u901a\u9053\uff0c\u5c31\u770b\u89c1\u665a\u6674\u59d0\u4e0e\u53f6\u5c0f\u6850\u4e24\u4eba\u7275\u7740\u82b7\u5f64\u7ad9\u5728\u51fa\u7ad9\u8f66\u5f80\u91cc\u9762\u770b\uff0c\u65c1\u8fb9\u7ad9\u7740\u4e00\u540d\u8eab\u6750\u9ad8\u5927\u7684\u82f1\u4fca\u7537\u5b50\uff0c\u5927\u7ea6\u4e09\u5341\u56db\u4e94\u5c81\uff0c\u8138\u5f62\u4e0e\u53f6\u5c0f\u6850\u76f8\u4f3c\uff0c\u8d70\u5230\u51fa\u7ad9\u53e3\uff0c\u5c31\u542c\u53f6\u5c0f\u6850\u4ecb\u7ecd\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u7ecf\u7406\uff0c\u542c\u8bf4\u4f60\u8ddf\u8bb8\u52a9\u7406\u4eb2\u81ea\u5230\u7701\u57ce\u6765\u63d0\u8f66\uff0c\u53f6\u603b\u89c9\u5f97\u6709\u5fc5\u8981\u5230\u8fd9\u91cc\u6765\u63a5\u4f60\uff0c\u5e0c\u671b\u4f60\u4e0d\u8981\u89c9\u5f97\u5510\u7a81\u3002\u201d</p><p>\u3000\u3000\u76db\u946b\u8d38\u6613\u80a1\u4efd\u6709\u9650\u516c\u53f8\u603b\u7ecf\u7406\u53f6\u5efa\u658c\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u53f6\u603b\u6765\u63a5\u8ba9\u4eba\u60f6\u6050\uff0c\u53c8\u8ba9\u4eba\u5982\u6c90\u6625\u98ce\uff0c\u4e0d\u8fc7\u6211\u5f97\u8ddf\u5c0f\u516c\u4e3b\u5148\u6253\u8fc7\u62db\u547c\u2026\u2026\u201d\u5c06\u884c\u674e\u7bb1\u4ea4\u7ed9\u665a\u6674\u59d0\u7684\u53f8\u673a\uff0c\u4e00\u628a\u5c06\u82b7\u5f64\u62b1\u4e86\u8d77\u6765\uff0c\u4e3e\u7740\u5979\u5728\u7a7a\u4e2d\u72e0\u72e0\u7684\u7529\u4e86\u4e24\u5708\u3002\u82b7\u5f64\u54af\u54af\u76f4\u7b11\uff0c\u8146\u7740\u4e0b\u5df4\uff0c\u4e0d\u8ba9\u5f20\u606a\u4eb2\u5979\u3002\u5f20\u606a\u5728\u5979\u7c89\u5ae9\u7684\u8138\u4e0a\u72e0\u72e0\u8e6d\u4e86\u4e24\u4e0b\uff0c\u8ba9\u5979\u5750\u5728\u81ea\u5df1\u7684\u80a9\u5934\uff0c\u624b\u7ed5\u5230\u9888\u540e\uff0c\u6402\u4f4f\u5979\u7684\u8170\uff0c\u8ba9\u5979\u5750\u7ed3\u5b9e\uff0c\u624d\u7a7a\u51fa\u4e00\u53ea\u624b\u6765\u8ddf\u53f6\u5efa\u658c\u63e1\u624b\u3002</p><p>\u3000\u3000\u201c\u65e9\u5c31\u60f3\u8bf7\u4f60\u5230\u7701\u57ce\u6765\u73a9\uff0c\u53c8\u542c\u8c22\u603b\u8bf4\u4f60\u4e0d\u518d\u8d1f\u8d23\u6d77\u6cf0\u516c\u53f8\u7684\u4e1a\u52a1\uff0c\u4e00\u76f4\u5f88\u9057\u61be\uff0c\u7231\u7acb\u4fe1\u5df2\u7ecf\u786e\u5b9a\u63a8\u51fa\u4e2d\u6587\u6570\u5b57\u624b\u673a\u7684\u65f6\u95f4\uff0c\u6211\u5e0c\u671b\u4e2d\u6587\u6570\u5b57\u624b\u673a\u7684\u5e02\u573a\u63a8\u5e7f\u65b9\u6848\uff0c\u5f20\u7ecf\u7406\u4e00\u5b9a\u8981\u4eb2\u81ea\u64cd\u5200\uff0c\u624d\u80fd\u7ed9\u6211\u4eec\u66f4\u591a\u7684\u4fe1\u5fc3\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd8\u662f\u76f4\u63a5\u53eb\u6211\u5f20\u606a\u5427\uff0c\u6211\u6bd4\u53f6\u603b\u8981\u5c11\u592a\u591a\uff0c\u786c\u90a6\u90a6\u79f0\u8c13\uff0c\u8ba9\u4eba\u53ef\u4e0d\u8212\u670d\u3002\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u53f6\u5efa\u658c\u7684\u519b\u65b9\u80cc\u666f\u6bd4\u8f83\u6df1\u539a\uff0c\u6258\u76db\u946b\u6e20\u9053\u8d2d\u8fdb\u6765\u7684\u5965\u8fea\u8f66\uff0c\u81f3\u5c11\u8981\u8282\u7ea6\u5341\u4e94\u516d\u4e07\u7684\u8d39\u7528\u3002\u76ee\u524d\u6c11\u4f17\u5bf9\u6709\u4e9b\u6270\u4e71\u7ecf\u6d4e\u79e9\u5e8f\u7684\u505a\u6cd5\u62b5\u89e6\u5fc3\u5f88\u5f3a\uff0c\u8fd9\u79cd\u80cc\u666f\u7684\u4f01\u4e1a\u7ecf\u8fc7\u539f\u59cb\u7684\u8d44\u672c\u79ef\u7d2f\uff0c\u8f6c\u578b\u4e5f\u662f\u5fc5\u7136\u7684\u8d8b\u52bf\uff0c\u5f53\u7136\u641e\u51e0\u90e8\u8f66\u5b8c\u5168\u6ca1\u6709\u95ee\u9898\u3002\u975e\u4f46\u6ca1\u6709\u95ee\u9898\uff0c\u8fd9\u79cd\u8f66\u6bd4\u5730\u65b9\u4e0a\u724c\u7684\u8f66\u725bB\u5341\u500d\uff0c\u5076\u5c14\u95ef\u4e2a\u7ea2\u706f\u90fd\u662f\u5b88\u89c4\u77e9\u7684\u5178\u8303\uff0c\u8981\u60f3\u7740\u4e3b\u7f34\u7eb3\u8fc7\u8def\u8d39\u3001\u517b\u8def\u8d39\uff0c\u90a3\u7edd\u5bf9\u662f\u826f\u6c11\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e4b\u524d\u4e00\u76f4\u62d2\u7edd\u8ddf\u53f6\u5efa\u658c\u89c1\u9762\uff0c\u4e00\u662f\u62bd\u4e0d\u51fa\u65f6\u95f4\u53bb\u7701\u57ce\uff0c\u518d\u4e2a\u5c31\u662f\u76ee\u524d\u592a\u5e72\u6d89\u6d77\u6cf0\u516c\u53f8\u7684\u4e1a\u52a1\uff0c\u672a\u5fc5\u8ba8\u4eba\u559c\u6b22\uff0c\u8c22\u665a\u6674\u521a\u521a\u63a5\u624b\u6d77\u88d5\u516c\u53f8\uff0c\u8fd8\u9700\u8981\u4e0b\u9762\u4eba\u7684\u652f\u6301\u3002\u8fd9\u6b21\u5f97\u53f6\u5efa\u658c\u8fd9\u4e48\u5927\u7684\u4eba\u60c5\uff0c\u624d\u60f3\u7740\u4eb2\u81ea\u53bb\u7701\u57ce\u63d0\u8f66\uff0c\u987a\u4fbf\u62dc\u8bbf\u4e00\u4e0b\u53f6\u5efa\u658c\uff0c\u6ca1\u60f3\u5230\u4ed6\u4f1a\u6765\u8f66\u7ad9\u63a5\u81ea\u5df1\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u5c06\u6d17\u5c18\u5bb4\u5b89\u6392\u5728\u4e1c\u6d77\u5609\u5bcc\u5927\u9152\u5e97\uff0c\u5217\u5e2d\u7684\u8fd8\u6709\u76db\u946b\u8d38\u6613\u7684\u526f\u603b\u7ecf\u7406\u53f6\u5efa\u65b0\u3001\u5e02\u573a\u603b\u76d1\u90b5\u5fc3\u6587\uff0c\u90fd\u662f\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u9752\u5e74\u624d\u4fca\uff0c\u4e3a\u5f20\u606a\u6bd4\u60f3\u8c61\u4e2d\u5e74\u8f7b\u7684\u591a\u800c\u60ca\u8bb6\u7684\u540c\u65f6\uff0c\u66f4\u8be7\u5f02\u4e8e\u8bb8\u601d\u7684\u8273\u7f8e\u7edd\u4f26\uff0c\u4ed6\u4eec\u4e0e\u53f6\u5efa\u658c\u4e00\u6837\uff0c\u5c06\u5f20\u606a\u4e0e\u8bb8\u601d\u770b\u6210\u4e00\u5bf9\u604b\u4eba\uff0c\u81f3\u5c11\u80fd\u4fdd\u6301\u793c\u8282\u4e0a\u7684\u514b\u5236\u3002\u6b64\u5916\u8fd8\u6709\u6d77\u88d5\u516c\u53f8\u526f\u603b\u7ecf\u7406\u5434\u664b\u8363\u3002</p><p>\u3000\u3000\u9152\u5e2d\u4e0a\u7545\u804a\u6700\u591a\u7684\u8fd8\u662f\u6570\u5b57\u624b\u673a\u7684\u8bdd\u9898\u3002</p><p>\u3000\u3000\u5176\u5b9e\u5728\u4e5d\u56db\u5e74\uff0c\u65e0\u8bba\u662f\u6a21\u62df\u4fe1\u53f7\u673a\u5927\u54e5\u5927\uff0c\u8fd8\u662f\u6570\u5b57\u624b\u673a\uff0c\u90fd\u5e26\u7740\u5962\u4f88\u54c1\u7684\u7279\u5f81\uff0c\u4e00\u90e8\u624b\u673a\u52a8\u5219\u4e24\u4e09\u4e07\uff0c\u800c\u5728\u4e5d\u56db\u5e74\uff0c\u4eba\u5747\u5e74\u6536\u5165\u8fd8\u4e0d\u8db3\u4e94\u5343\u5143\uff0c\u53ef\u652f\u914d\u6536\u5165\u66f4\u4f4e\uff0c\u8fd9\u8ba9\u67d0\u4e9b\u4e13\u4e1a\u4eba\u58eb\u5f88\u4e0d\u770b\u597d\u56fd\u5185\u7684\u5e02\u573a\u5bb9\u91cf\uff0c\u4e5d\u4e09\u5e74\u5e95\uff0c\u90ae\u7535\u90e8\u95e8\u5bf9\u79fb\u52a8\u7535\u8bdd\u7684\u5e02\u573a\u5bb9\u91cf\u9884\u6d4b\u53ea\u7ed9\u51fa\u5341\u5e74\u7d2f\u79ef20\u4e07\u53ea\u7684\u4fdd\u5b88\u6570\u636e\uff0c\u8fd9\u4e2a\u73a9\u7b11\u5f88\u5927\uff0c\u8981\u77e5\u9053\u5230\u4e5d\u4e03\u5e74\u534a\u4e2a\u6708\u7684\u653e\u53f7\u6570\u91cf\u5c31\u8d85\u8fc7\u4e8c\u5341\u4e07\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u90fd\u53cd\u5e94\u51fa\u56fd\u5185\u901a\u8baf\u5546\u5bf9\u6570\u5b57\u5e02\u573a\u524d\u666f\u7684\u4fdd\u5b88\u8ba4\u8bc6\uff0c\u53f6\u5efa\u658c\u5728\u8fd9\u65b9\u9762\u7b97\u662f\u76f8\u5f53\u6709\u8d85\u524d\u610f\u8bc6\u4e0e\u5e02\u573a\u8fdc\u89c1\u7684\u4eba\uff0c\u4f46\u5728\u5bf9\u4eca\u540e\u5341\u51e0\u5e74\u6570\u5b57\u624b\u673a\u5e02\u573a\u53d1\u5c55\u4e00\u76ee\u4e86\u7136\u7684\u5f20\u606a\u9762\u524d\uff0c\u53f6\u5efa\u658c\u4e5f\u53ea\u6709\u6d17\u8033\u606d\u542c\u7684\u4efd\u3002</p><p>\u3000\u3000\u6570\u5b57\u7f51\u7edc\u6280\u672f\u7684\u6210\u719f\uff0c\u4ee5\u53ca\u5728\u6b27\u6d32\u5546\u7528\u5316\u7684\u6210\u529f\uff0c\u4e5d\u56db\u5e74\u56fd\u5185\u90ae\u653f\u90e8\u5df2\u7ecf\u51b3\u5b9a\u5927\u529b\u53d1\u5c55\u6570\u5b57\u901a\u8baf\u7f51\u7edc\uff0c\u5bf9\u5df2\u5efa\u6210\u7684\u6a21\u62df\u7f51\u91c7\u53d6\u9002\u5ea6\u5b8c\u5584\u7684\u653f\u7b56\uff0c\u5373\u5728\u4e1c\u90e8\u53d1\u8fbe\u5730\u533a\u5728\u4f18\u5316\u6a21\u62df\u901a\u4fe1\u7f51\u7edc\u7684\u57fa\u7840\u4e0a\u5c11\u91cf\u8865\u7f3a\uff0c\u4e2d\u897f\u90e8\u5730\u533a\u5219\u5b8c\u5168\u653e\u5f03\u5efa\u8bbe\u6a21\u62df\u7f51\uff0c\u96c6\u4e2d\u4e3b\u8981\u7cbe\u529b\u5efa\u8bbe\u6570\u5b57\u7f51\u3002</p><p>\u3000\u3000\u4e5d\u56db\u5e74\uff0c\u79fb\u52a8\u7535\u8bdd\u8fd8\u662f\u5962\u4f88\u54c1\uff0c\u4f46\u662f\u968f\u7740\u6570\u5b57\u901a\u8baf\u7f51\u7edc\u7684\u5efa\u6210\uff0c\u56fd\u9645\u5236\u9020\u5546\u5c06\u7eb7\u7eb7\u770b\u597d\u56fd\u5185\u7684\u5e02\u573a\u524d\u666f\uff0c\u4ea7\u54c1\u7ade\u4e89\u4f1a\u4f7f\u88f8\u673a\u4ef7\u683c\u4e0d\u65ad\u7684\u4e0b\u964d\uff0c\u7279\u522b\u662f\u4e5d\u4e03\u4e4b\u540e\uff0c\u56fd\u5185\u5236\u9020\u5546\u4ecb\u5165\u624b\u673a\u751f\u4ea7\u9886\u5bfc\uff0c\u4f7f\u5f97\u4f4e\u7aef\u624b\u673a\u7684\u4ef7\u683c\u6025\u5267\u8df3\u6c34\uff0c\u4e8c\u4e09\u767e\u5143\u4e00\u90e8\u7684\u624b\u673a\u4ef7\u683c\uff0c\u662f\u4e5d\u56db\u5e74\u6240\u4e0d\u80fd\u60f3\u8c61\u7684\uff1b\u53e6\u4e00\u65b9\u9762\u8054\u901a\u516c\u53f8\u4ecb\u5165\u901a\u8baf\u7f51\u7edc\u7684\u7ade\u4e89\uff0c\u4f7f\u5f97\u79fb\u52a8\u7535\u8bdd\u7684\u8d44\u8d39\u4e5f\u9010\u6b65\u4e0b\u8c03\uff0c\u624b\u673a\u4e0d\u9700\u8981\u51e0\u5e74\u5c31\u4f1a\u6210\u4e3a\u5927\u4f17\u5546\u54c1\u3002</p><p>\u3000\u3000\u7b49\u624b\u673a\u5e02\u573a\u771f\u6b63\u53d1\u5c55\u8d77\u6765\uff0c\u624b\u673a\u7684\u5229\u6da6\u4e5f\u4f1a\u4e00\u518d\u7684\u538b\u7f29\uff0c\u76ee\u524d\u7684\u603b\u5206\u5f0f\u591a\u7ea7\u5206\u9500\u6a21\u5f0f\uff0c\u5c31\u4f1a\u56e0\u4e3a\u9ad8\u6602\u6210\u672c\u7684\u56e0\u7d20\uff0c\u88ab\u624b\u673a\u5e02\u573a\u5f7b\u5e95\u7684\u6dd8\u6c70\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u6570\u5b57\u624b\u673a\u5e02\u573a\u9ad8\u77bb\u8fdc\u77a9\u7684\u8ba4\u8bc6\uff0c\u4ee4\u5217\u5e2d\u7684\u5e02\u573a\u8425\u9500\u9ad8\u624b\u6298\u670d\uff0c\u4f55\u51b5\u5f20\u606a\u672c\u8eab\u5728\u5e02\u573a\u8425\u9500\u65b9\u9762\u6709\u7740\u8d85\u8d8a\u522b\u4eba\u5341\u56db\u5e74\u7684\u7ecf\u9a8c\u4f18\u52bf\u3002\u4e00\u987f\u996d\u5403\u4e86\u4e09\u4e2a\u5c0f\u65f6\uff0c\u83dc\u90fd\u70ed\u4e86\u4e24\u4e09\u56de\uff0c\u53f6\u5efa\u658c\u4ed6\u4eec\u8fd8\u610f\u72b9\u672a\u5c3d\uff0c\u6293\u4f4f\u5f20\u606a\u7684\u624b\uff1a\u201c\u8981\u4e0d\u662f\u8c22\u603b\u4e0d\u653e\u4eba\uff0c\u6211\u6b7b\u6d3b\u4e5f\u8981\u628a\u4f60\u62c9\u76db\u946b\u6765\uff0c\u201d\u53f6\u5efa\u658c\u6307\u7740\u76db\u946b\u7684\u5e02\u573a\u603b\u76d1\u90b5\u5fc3\u6587\u8bf4\uff0c\u201c\u628a\u8fd9\u5c0f\u5b50\u8e22\u4e0b\u53bb\u7ed9\u4f60\u5f53\u52a9\u624b\u3002\u201d</p><p>\u3000\u3000\u90b5\u5fc3\u6587\u5fc3\u60a6\u8bda\u670d\uff0c\u4f55\u51b5\u5f20\u606a\u53c8\u4e0d\u4f1a\u771f\u5230\u76db\u946b\u6765\u6324\u6389\u81ea\u5df1\u7684\u4f4d\u7f6e\uff0c\u62a2\u8fc7\u6765\u63e1\u4f4f\u5f20\u606a\u7684\u624b\uff0c\u6ee1\u53e3\u8981\u5f20\u606a\u7b54\u5e94\u6307\u70b9\u4ed6\u7684\u5de5\u4f5c\u624d\u677e\u624b\u3002</p><p>\u3000\u3000\u8bb8\u601d\u770b\u5230\u8fd9\u6837\u7684\u573a\u9762\u8fd8\u662f\u5f88\u602a\u5f02\uff0c\u4fa7\u7740\u5934\uff0c\u770b\u7740\u8c22\u665a\u6674\u5077\u5077\u7684\u7b11\u7740\u3002\u4e0d\u662f\u8c22\u665a\u6674\u4e0d\u653e\u4eba\uff0c\u5f20\u606a\u8fd8\u8981\u5728\u6d77\u5dde\u4e00\u4e2d\u8bfb\u5b8c\u4e09\u5e74\u7684\u9ad8\u4e2d\u751f\u6daf\u3002</p><p>\u3000\u3000\u5403\u8fc7\u4e2d\u996d\uff0c\u5df2\u7ecf\u662f\u4e0b\u5348\u4e09\u70b9\u949f\uff0c\u65b0\u8f66\u7531\u76db\u946b\u7684\u53f8\u673a\u5f00\u8fc7\u6765\uff0c\u5c31\u505c\u5728\u4e1c\u6d77\u5609\u5bcc\u5927\u9152\u5e97\u7684\u505c\u8f66\u573a\u3002</p><p>\u3000\u3000\u9ed1\u8272\u70e4\u6f06\u8f66\u8eab\uff0c\u5965\u8fea\u8f66\u4e0d\u5f20\u626c\uff0c\u4e5d\u56db\u5e74\u5728\u56fd\u5185\u4e5f\u7b97\u76f8\u5f53\u4e0d\u9519\u7684\u8f66\uff0c\u64e6\u5f97\u4e0a\u9ad8\u6863\u8f7f\u8f66\u7684\u8fb9\uff0c\u5f53\u5f20\u606a\u770b\u5230\u8f66\u524d\u8eab\u7684\u8f66\u724c\uff0c\u6709\u4e9b\u4e0d\u77e5\u8bf4\u4ec0\u4e48\u597d\uff0c\u8f66\u724c\u767d\u5e95\u9ec4\u5b57\uff0c\u4ee5\u201cK3\u201d\u5f00\u5934\uff0c\u8981\u641e\u5230\u8fd9\u79cd\u5927\u519b\u533a\u7684\u519b\u724c\uff0c\u53f7\u7801\u8fd8\u6bd4\u8f83\u9760\u524d\uff0c\u6bd4\u8fd9\u90e8\u5965\u8fea\u8f66\u5c11\u82b1\u4e0d\u4e86\u591a\u5c11\u94b1\u3002\u5f20\u606a\u770b\u7740\u53f6\u5efa\u658c\uff0c\u505a\u4e86\u4e00\u4e2a\u96be\u4ee5\u7f6e\u4fe1\u7684\u624b\u59ff\uff1a\u201c\u53f6\u603b\uff0c\u8fd9\u4fbf\u5b9c\u6211\u53ef\u5360\u5927\u4e86\uff01\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u54c8\u54c8\u4e00\u7b11\uff0c\u62cd\u4e86\u62cd\u5f20\u606a\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u519b\u724c\u4e0d\u591a\u4e86\uff0c\u8fd9\u6b21\u6b63\u597d\u6709\u673a\u4f1a\uff0c\u987a\u4fbf\u5e2e\u4f60\u6302\u4e0a\u4e86\u3002\u201d</p><p>\u3000\u3000\u8fd9\u662f\u53f6\u5efa\u658c\u5bf9\u81ea\u5df1\u524d\u671f\u5de5\u4f5c\u7684\u5956\u52b1\uff0c\u4e5f\u662f\u4e00\u79cd\u7b3c\u7edc\uff0c\u5f20\u606a\u7b11\u7740\u8fb9\u53f9\u6c14\uff0c\u8fb9\u6447\u5934\uff0c\u660e\u5e74\u7231\u7acb\u4fe1\u4e2d\u6587\u6570\u5b57\u624b\u673a\u4e0a\u5e02\uff0c\u5236\u5b9a\u5e02\u573a\u63a8\u5e7f\u65b9\u6848\u7684\u5dee\u4e8b\u662f\u9003\u4e0d\u4e86\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u6cd5\u6539\u53d8\u81ea\u5df1\u8981\u7b49\u5230\u4e24\u5e74\u540e\u624d\u80fd\u62ff\u9a7e\u7167\u7684\u4e8b\u5b9e\uff0c\u53c8\u4e0d\u60f3\u5728\u53f6\u5efa\u658c\u7b49\u4eba\u9762\u524d\u9732\u4e86\u602f\uff0c\u63a8\u8bf4\u4e0e\u8c22\u665a\u6674\u3001\u5434\u664b\u8363\u8fd8\u6709\u4e8b\u60c5\u5546\u91cf\uff0c\u8ba9\u4ed6\u4eec\u4e58\u8f66\u5148\u79bb\u5f00\u9152\u5e97\u3002</p></article>"}}}]);
