(window.webpackJsonp=window.webpackJsonp||[]).push([[79],{102:function(p){p.exports={chapterTitle:null,title:"\u7b2c1066\u7ae0 \u987e\u6653\u6885\u53d1\u73b0\u5927\u79d8\u5bc6",content:"<article><h2>\u7b2c1066\u7ae0 \u987e\u6653\u6885\u53d1\u73b0\u5927\u79d8\u5bc6</h2><p>\u3000\u3000\u542c\u6210\u4f59\u4e1c\u8fd9\u4e48\u95ee\uff0c\u987e\u6653\u6885\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u6574\u5929\u8ddf\u7740\u5f20\u5e02\u957f\uff0c\u4f60\u90fd\u4e0d\u77e5\u9053\u5f20\u5e02\u957f\u53bb\u54ea\u91cc\u5316\u7f18\uff0c\u6211\u4ece\u54ea\u91cc\u77e5\u9053\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u987e\u59d0\u8fd9\u6b21\u53bb\u5efa\u90ba\uff0c\u5e94\u8be5\u6709\u9001\u4e8e\u7af9\u53bb\u4e1c\u5927\u5427\uff0c\u6709\u6ca1\u6709\u987a\u4fbf\u5230\u5f20\u606a\u5728\u5efa\u90ba\u7684\u8c6a\u5b85\u62dc\u8bbf\u4e00\u4e0b\uff1f\u201d\u6210\u4f59\u4e1c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u672c\u6765\u60f3\u53bb\u7684\uff0c\u201d\u987e\u6653\u6885\u5f53\u7136\u4e0d\u4f1a\u8bf4\u5979\u6628\u5929\u591c\u91cc\u5728\u7701\u4eba\u6c11\u533b\u9662\u9047\u5230\u5f20\u606a\u8fd8\u523b\u610f\u7684\u8eb2\u5f00\u4e86\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u7a81\u7136\u63a5\u5230\u7535\u8bdd\uff0c\u5306\u5306\u5403\u8fc7\u4e2d\u996d\u5c31\u5f80\u65b0\u829c\u8d76\uff0c\u628a\u8fd9\u4e8b\u7ed9\u5fd8\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0b\u56de\u6709\u673a\u4f1a\uff0c\u53bb\u770b\u8fc7\u4e86\uff0c\u5c31\u77e5\u9053\u6211\u8bf4\u7684\u8bdd\u4e0d\u662f\u5728\u8499\u987e\u59d0\u4f60\u3002\u201d</p><p>\u3000\u3000\u987e\u6653\u6885\u7b11\u4e86\u7b11\uff0c\u79c1\u4e0b\u91cc\u5927\u5bb6\u4e5f\u6709\u8ba8\u8bba\u5f20\u5bb6\u5728\u6d77\u5dde\u7684\u80cc\u666f\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u8bb3\u83ab\u5982\u6df1\u7684\uff0c\u53ea\u662f\u6d88\u606f\u6765\u6e90\u6bd4\u8f83\u6709\u9650\u7f62\u4e86\uff0c\u5979\u5fc3\u91cc\u60f3\u5f20\u5bb6\u5728\u6d77\u5dde\u5230\u5e95\u8981\u7b97\u600e\u6837\u7684\u571f\u8c6a\uff1f</p><p>\u3000\u3000\u6bd4\u8f83\u516c\u5f00\u7684\u6d88\u606f\uff0c\u4e09\u5e74\u524d\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u6539\u5236\u5356\u7ed9\u79c1\u4eba\uff0c\u6539\u5236\u540e\u7684\u5f85\u5bbe\u9986\u66f4\u540d\u4e3a\u4e16\u7eaa\u9526\u6e56\u5927\u9152\u5e97\uff0c\u80cc\u540e\u7684\u63a7\u80a1\u516c\u53f8\u5c31\u662f\u6d77\u5dde\u5e02\u6700\u5927\u7684\u9910\u996e\u96c6\u56e2\u4e16\u7eaa\u9526\u6e56\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u3002</p><p>\u3000\u3000\u65b0\u829c\u5e02\u4e2d\u5c42\u5e72\u90e8\u8fd9\u51e0\u5e74\u90fd\u516c\u8ba4\u4e16\u7eaa\u9526\u6e56\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u80cc\u540e\u7684\u8d22\u4e1c\u5c31\u662f\u5f20\u5bb6\uff0c\u8fd9\u79cd\u731c\u6d4b\u4e5f\u4e0d\u662f\u6beb\u65e0\u6839\u636e\u3002\u9996\u5148\u5f20\u77e5\u884c\u7684\u5ae1\u4eb2\u5802\u5144\u5f1f\u5f20\u77e5\u975e\u5c31\u662f\u4e16\u7eaa\u9526\u6e56\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u7684\u4e3b\u8981\u80a1\u4e1c\u2014\u2014\u7ffb\u770b\u5f53\u521d\u7684\u62db\u6807\u6587\u4ef6\u5c31\u6709\u6ce8\u660e\uff1b\u53e6\u5916\uff0c\u4e14\u4e0d\u8bba\u5f53\u65f6\u62db\u5f85\u5bbe\u9986\u4e09\u5343\u4e07\u7684\u51fa\u552e\u6b3e\u662f\u8d35\u662f\u8d31\uff0c\u603b\u4e4b\u8fd9\u4e09\u5343\u4e07\u7d27\u6025\u7528\u6765\u91cd\u4fee\u57ce\u9632\u6c5f\u5824\u7684\uff0c\u4e4b\u540e\u4e16\u7eaa\u9526\u6e56\u5927\u9152\u5e97\u53c8\u76f8\u7ee7\u4e24\u6b21\u5171\u5411\u57ce\u9632\u6c5f\u5824\u6350\u51fa\u4e00\u5343\u4e94\u767e\u4e07\u7684\u4fee\u5efa\u8d44\u91d1\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\u5e95\uff0c\u4e16\u7eaa\u9526\u6e56\u5927\u9152\u5e97\u53c8\u76f8\u5148\u51e0\u6b21\u5411\u65b0\u829c\u5e02\u804c\u4e1a\u6280\u672f\u57f9\u8bad\u4f53\u7cfb\u5171\u6350\u51fa\u4e00\u5343\u4e94\u767e\u4e07\u7684\u8d44\u91d1\uff0c\u5176\u4e2d\u5f20\u77e5\u884c\u5f53\u65f6\u91cd\u70b9\u6293\u65c5\u6e38\u4ea7\u4e1a\uff0c\u8fd9\u7b14\u8d44\u91d1\u4e2d\u6709\u76f8\u5f53\u4e00\u90e8\u5206\u662f\u7528\u4e8e\u65b0\u829c\u5f53\u5730\u65c5\u6e38\u804c\u4e1a\u4eba\u624d\u7684\u57f9\u517b\u2014\u2014\u6240\u4ee5\u6210\u4f59\u4e1c\u8fd9\u6b21\u53c8\u5f00\u73a9\u7b11\u8bf4\u5f20\u77e5\u884c\u4f1a\u5411\u4e16\u7eaa\u9526\u6e56\u5927\u9152\u5e97\u5316\u7f18\u3002</p><p>\u3000\u3000\u5dee\u4e0d\u591a\u80fd\u80af\u5b9a\u4e16\u7eaa\u9526\u6e56\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u80cc\u540e\u7684\u8d22\u4e1c\u5c31\u662f\u5f20\u5bb6\uff0c\u90a3\u51a0\u4ee5\u201c\u4e16\u7eaa\u9526\u6e56\u201d\u540d\u5934\u3001\u540c\u65f6\u53c8\u662f\u4ece\u6d77\u5dde\u8d77\u5bb6\u7684\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u5f00\u53d1\u96c6\u56e2\uff0c\u80cc\u540e\u7684\u8d22\u4e1c\u81ea\u7136\u4e5f\u5e94\u8be5\u662f\u5f20\u5bb6\u3002\u53e6\u5916\uff0c\u5f20\u77e5\u975e\u8fd8\u76f4\u63a5\u62c5\u4efb\u6d77\u5dde\u5e02\u89c4\u6a21\u6700\u5927\u7684\u6c11\u8425\u5de5\u7a0b\u5efa\u8bbe\u516c\u53f8\u5b8f\u8fdc\u5efa\u8bbe\u7684\u8463\u4e8b\u957f\u3001\u603b\u88c1\uff0c\u60f3\u6765\u5b8f\u8fdc\u5efa\u8bbe\u80cc\u540e\u7684\u8d22\u4e1c\u4e5f\u662f\u5f20\u5bb6\u3002</p><p>\u3000\u3000\u4ece\u516c\u5f00\u7684\u8d44\u6599\u6765\u770b\uff0c\u5f20\u5bb6\u5c31\u6a2a\u8de8\u9910\u996e\u5a31\u4e50\u3001\u5730\u4ea7\u3001\u5de5\u7a0b\u5efa\u8bbe\u4e09\u5927\u4ea7\u4e1a\uff0c\u65d7\u4e0b\u4e09\u5bb6\u96c6\u56e2\u516c\u53f8\u53c8\u90fd\u662f\u6d77\u5dde\u8fd9\u4e09\u5927\u884c\u4e1a\u7684\u7fd8\u695a\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u8bf4\u4e5f\u6ca1\u6cd5\u7ed9\u4eba\u76f4\u89c2\u7684\u5370\u8c61\uff0c\u987e\u6653\u6885\u8fd9\u6b21\u53bb\u5efa\u90ba\uff0c\u5750\u8f66\u7ed5\u7740\u5efa\u8bbe\u4e2d\u7684\u6e56\u4e1c\u5546\u5708\u8f6c\u4e86\u4e00\u5708\uff0c\u5c31\u5efa\u6210\u4e0e\u6b63\u5efa\u5de5\u7a0b\u7684\u89c4\u6a21\u5dee\u4e0d\u591a\u5c31\u62b5\u5f97\u4e0a\u534a\u4e2a\u65b0\u829c\u5e02\u4e2d\u5fc3\u4e86\uff0c\u7531\u6b64\u53ef\u89c1\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u96c6\u56e2\u7684\u8d44\u672c\u5b9e\u529b\u4e86\uff0c\u65b0\u829c\u5e02\u7684\u90a3\u51e0\u5bb6\u6700\u5927\u7684\u5730\u4ea7\u5f00\u53d1\u516c\u53f8\uff0c\u65e0\u8bba\u662f\u56fd\u6709\u7684\uff0c\u8fd8\u662f\u6c11\u8425\uff0c\u8fde\u7ed9\u4eba\u5bb6\u63d0\u978b\u7684\u8d44\u683c\u90fd\u6ca1\uff0c\u5f20\u5bb6\u4f5c\u4e3a\u80cc\u540e\u7684\u8d22\u4e1c\u4e5f\u786e\u5b9e\u80fd\u79f0\u5f97\u8d22\u5927\u6c14\u7c97\u3002</p><p>\u3000\u3000\u987e\u6653\u6885\u672c\u4e0d\u4f1a\u591a\u60f3\uff0c\u4f46\u662f\u770b\u5230\u5f20\u606a\u8863\u98df\u4f4f\u884c\u7684\u6d3e\u5934\uff0c\u5fcd\u4e0d\u4f4f\u5c31\u4f1a\u60f3\u8fd9\u65f6\u5019\u5927\u5bb6\u63a8\u6d4b\u6216\u8bb8\u8fd8\u53ea\u662f\u5f20\u5bb6\u9732\u51fa\u6d77\u9762\u7684\u51b0\u5c71\u4e00\u89d2\uff0c\u5979\u51d1\u5de7\u77e5\u9053\u4e0a\u56de\u5230\u5e02\u59d4\u5927\u9662\u62dc\u671b\u5f20\u77e5\u884c\u592b\u5987\u7684\u9752\u5e74\u7537\u5973\u662f\u5f20\u77e5\u884c\u7231\u4eba\u6881\u683c\u73cd\u7684\u4f84\u5b50\u6881\u6587\u6c5f\u4e0e\u5176\u672a\u5a5a\u59bb\u3002</p><p>\u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u987e\u6653\u6885\u5c06\u684c\u5b50\u4e0a\u7684\u7535\u8111\u663e\u793a\u5668\u8f6c\u5916\u8f6c\u4e86\u8f6c\uff0c\u70b9\u5f00\u7f51\u9875\u4e0a\u6709\u5173\u6606\u817e\u521b\u59cb\u7684\u4ecb\u7ecd\u65b0\u95fb\uff0c\u6307\u7740\u6881\u6587\u6c5f\u95ee\u6210\u4f59\u4e1c\uff1a\u201c\u770b\u7740\u4ed6\u89c9\u5f97\u9762\u719f\u4e0d\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u6210\u4f59\u4e1c\u773c\u775b\u77aa\u5f97\u6123\u5706\uff0c\u5634\u91cc\u5438\u7740\u51c9\u6c14\uff0c\u201c\u8fd9\u4e0d\u662f\u6881\u6587\u6c5f\u5417\uff1f\u4e0a\u56de\u770b\u5230\u4ed6\u65f6\uff0c\u4ed6\u8bf4\u4ed6\u4ece\u6e05\u534e\u5927\u5b66\u6bd5\u4e1a\u540e\uff0c\u5c31\u7559\u5728\u5317\u4eac\u505a\u666e\u901a\u6280\u672f\u5de5\u4f5c\u2026\u2026\u5976\u5976\u7684\uff0c\u6606\u817e\u5728\u7ebf\u7684\u9996\u5e2d\u6280\u672f\u5b98\u90a3\u4e5f\u53eb\u662f\u666e\u901a\u6280\u672f\u5de5\u4f5c\uff1f\u90a3\u4e00\u6b21\u6211\u5c31\u89c9\u5f97\u4ed6\u7684\u540d\u5b57\u542c\u4e0a\u53bb\u8033\u719f\uff0c\u987e\u59d0\u4f60\u662f\u600e\u4e48\u770b\u51fa\u6765\u7684\uff1f\u201d</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u73b0\u5728\u65b0\u95fb\u70ed\u5ea6\u6781\u9ad8\uff0c\u4e0d\u8fc7\u7206\u5149\u7387\u6700\u9ad8\u7684\u8fd8\u662f\u9996\u5e2d\u6267\u884c\u5b98\u9a6c\u5411\u4e1c\uff0c\u6709\u4e00\u4e2a\u4eba\u592a\u8000\u773c\uff0c\u5176\u4ed6\u4eba\u76f8\u886c\u4e4b\u4e0b\u5c31\u6ca1\u6709\u4ec0\u4e48\u5149\u8292\uff0c\u518d\u8bf4\u6881\u6587\u6c5f\u8a00\u884c\u6734\u7d20\uff0c\u5750\u5728\u90a3\u91cc\u4e5f\u4e0d\u58f0\u4e0d\u54cd\u7684\uff0c\u4e5f\u5f88\u5bb9\u6613\u7ed9\u5ffd\u89c6\u6389\u3002</p><p>\u3000\u3000\u201c\u6628\u5929\u542c\u4e8e\u7af9\u8ddf\u522b\u4eba\u5728\u90a3\u91cc\u804a\u4e92\u8054\u7f51\uff0c\u542c\u7740\u6881\u6587\u6c5f\u8fd9\u4e2a\u540d\u5b57\u8033\u719f\uff0c\u4e0a\u7f51\u4e00\u67e5\uff0c\u679c\u7136\u662f\u4ed6\u2026\u2026\u201d\u987e\u6653\u6885\u8bf4\u9053\uff0c\u8981\u4e0d\u662f\u6628\u5929\u542c\u513f\u5b50\u8ddf\u9648\u5b81\u5750\u5728\u75c5\u623f\u95f2\u804a\uff0c\u5979\u90fd\u8fd8\u4e0d\u77e5\u9053\u5f20\u77e5\u884c\u7684\u5185\u4f84\u7adf\u7136\u8fd8\u662f\u6606\u817e\u5728\u7ebf\u7684\u9996\u5e2d\u6280\u672f\u5b98\uff0c\u4e8f\u81ea\u5df1\u8fd8\u8fd9\u4e9b\u5929\u8fd8\u5929\u5929\u5728\u6606\u817e\u5728\u7ebf\u4e0a\u6d4f\u89c8\u65b0\u95fb\u3002</p><p>\u3000\u3000\u201c\u7b49\u7b49\uff0c\u7535\u8111\u501f\u6211\u7528\u4e00\u4e0b\uff0c\u201d\u6210\u4f59\u4e1c\u5c06\u952e\u76d8\u62ff\u5230\u684c\u4e0a\u53bb\uff0c\u76f4\u63a5\u5728\u6606\u817e\u5728\u7ebf\u7684\u7f51\u9875\u4e0a\u641c\u7d22\u6881\u6587\u6c5f\u7684\u65b0\u95fb\uff0c\u5e73\u65f6\u867d\u7136\u4e0d\u5927\u6ce8\u610f\uff0c\u5173\u4e8e\u6881\u6587\u6c5f\u7684\u65b0\u95fb\u5012\u4e5f\u4e0d\u5c11\uff0c\u6210\u4f59\u4e1c\u9690\u7ea6\u8bb0\u5f97\u6709\u4ecb\u7ecd\u6606\u817e\u5728\u7ebf\u521b\u59cb\u8d22\u5bcc\u7684\u65b0\u95fb\uff0c\u67e5\u627e\u5230\u70b9\u51fb\u5f00\u6765\uff0c\u6307\u7740\u65b0\u95fb\u7f51\u9875\uff0c\u95ee\u987e\u6653\u6885\uff0c\u201c\u5976\u5976\u7684\uff0c\u6881\u6587\u6c5f\u600e\u4e48\u770b\u4e5f\u4e0d\u50cf\u4ebf\u4e07\u5bcc\u7fc1\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0d\u662f\u624d\u4e00\u5343\u4e00\u767e\u591a\u4e07\u7f8e\u5143\u5427\uff0c\u79bb\u4ebf\u4e07\u5bcc\u7fc1\u8fd8\u5dee\u4e9b\u2026\u2026\u201d\u987e\u6653\u6885\u770b\u7740\u7f51\u9875\u7b11\u7740\u8bf4\uff0c\u5979\u4e0a\u56de\u5728\u5e02\u59d4\u5927\u9662\u91cc\u770b\u5230\u6881\u6587\u6c5f\u65f6\uff0c\u8fd8\u771f\u662f\u770b\u4e0d\u5230\u7a7f\u7740\u77ed\u8896\u886c\u886b\u8e72\u5728\u5730\u4e0a\u5e2e\u536b\u5ac2\u6361\u83dc\u7684\u9752\u5e74\u662f\u4e2a\u4ebf\u4e07\u5bcc\u7fc1\uff0c\u8981\u771f\u6bd4\u67b6\u52bf\uff0c\u771f\u662f\u6bd4\u5f20\u606a\u8981\u5dee\u4e0a\u5341\u4e07\u516b\u5343\u91cc\u3002</p><p>\u3000\u3000\u6709\u4e9b\u4eba\u4ee5\u4e3a\u5df2\u7ecf\u63a8\u6d4b\u51fa\u5f20\u5bb6\u7684\u5e95\u7ec6\u6765\u4e86\uff0c\u987e\u6653\u6885\u5374\u4ece\u8fd9\u5c0f\u5c0f\u7684\u4e00\u5904\u770b\u5230\u6f5c\u85cf\u5728\u6d77\u9762\u4e4b\u4e0b\u7684\u5f20\u5bb6\u8fdc\u6bd4\u5979\u4eec\u60f3\u8c61\u7684\u6765\u5f97\u8d22\u5927\u6c14\u7c97\u3001\u8fdc\u6bd4\u5979\u4eec\u60f3\u8c61\u7684\u6765\u5f97\u795e\u79d8\u3002</p><p>\u3000\u3000\u4e5f\u8bb8\u773c\u775b\u6240\u770b\u5230\u7684\u3001\u8033\u6735\u6240\u542c\u5230\u7684\u90fd\u662f\u8868\u8c61\uff0c\u5c31\u50cf\u5e02\u91cc\u4e2d\u5c42\u5e72\u90e8\u90fd\u5f88\u63a8\u5d07\u5f20\u77e5\u884c\uff0c\u4e5f\u516c\u8ba4\u5f20\u77e5\u884c\u6709\u4e2a\u7ea8\u7ed4\u513f\u5b50\uff0c\u987e\u6653\u6885\u5c31\u5728\u60f3\uff1a\u8fd9\u662f\u4e0d\u662f\u4e5f\u662f\u4e00\u79cd\u8868\u8c61\uff1f</p><p>\u3000\u3000\u60f3\u60f3\u6628\u5929\u7701\u4eba\u6c11\u533b\u9662\u91cc\u4e0b\u5c97\u5973\u804c\u5de5\u81ea\u6740\u7684\u4f20\u95fb\uff0c\u60f3\u60f3\u7701\u653f\u5e9c\u4eca\u5929\u4e00\u5927\u65e9\u5c31\u679c\u65ad\u7684\u8981\u6c42\u5404\u5730\u63d0\u9ad8\u4e0b\u5c97\u804c\u5de5\u7684\u6807\u51c6\uff0c\u5f20\u606a\u63ba\u5408\u5728\u91cc\u9762\uff0c\u4f3c\u4e4e\u6709\u4e9b\u592a\u51d1\u5de7\uff0c\u4f46\u662f\u51d1\u5de7\u7684\u4e8b\u60c5\u4e5f\u4e0d\u53ea\u8fd9\u4e48\u4e00\u6869\uff0c\u6700\u60ca\u5fc3\u52a8\u9b44\u7684\u4e5f\u4e0d\u662f\u8fd9\u4e00\u6869\u3002</p><p>\u3000\u3000\u6700\u60ca\u5fc3\u52a8\u9b44\u7684\u8fd8\u662f\u8981\u7b97\u4e09\u5e74\u524d\u5f20\u606a\u4e0e\u7f57\u5f52\u6e90\u513f\u5b50\u7f57\u6587\u4e3a\u5f53\u65f6\u8fd8\u662f\u5e02\u653f\u5e9c\u5bbe\u9986\u666e\u901a\u670d\u52a1\u5458\u7684\u6f02\u4eae\u5973\u5b69\u536b\u5170\u4e89\u98ce\u5403\u918b\u800c\u5927\u95f9\u5e02\u59d4\u5168\u59d4\u665a\u5bb4\u73b0\u573a\u4e00\u4e8b\uff0c\u60f3\u8d77\u8fd9\u4e8b\uff0c\u987e\u6653\u6885\u7981\u4e0d\u4f4f\u9677\u5165\u6c89\u601d\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u5979\u542c\u8bf4\u5f20\u606a\u76f4\u63a5\u62a1\u8d77\u5e38\u59d4\u9152\u684c\u4e0a\u7684\u9152\u74f6\u5c06\u7f57\u6587\u7838\u7ffb\u5728\u5730\uff0c\u7f57\u5f52\u6e90\u4e0e\u5f20\u77e5\u884c\u5728\u9152\u684c\u4e0a\u5dee\u70b9\u4e5f\u8981\u5927\u6253\u51fa\u624b\uff0c\u7ed3\u679c\u4f7f\u521a\u5f00\u4e86\u5934\u7684\u5e02\u5168\u59d4\u665a\u5bb4\u4e0d\u6b22\u800c\u6563\u3002</p><p>\u3000\u3000\u5c31\u662f\u56e0\u4e3a\u6b64\u4e8b\uff0c\u5f20\u77e5\u884c\u521d\u5230\u65b0\u829c\u5c31\u4e0e\u7f57\u5f52\u6e90\u7ed3\u4e0b\u6b7b\u4ec7\u3002\u7f57\u5f52\u6e90\u662f\u5730\u65b9\u5b9e\u529b\u6d3e\uff0c\u4f46\u662f\u5f20\u77e5\u884c\u662f\u7a7a\u964d\u6d3e\uff0c\u5927\u5bb6\u8fd8\u7b49\u7740\u770b\u597d\u620f\uff0c\u6ca1\u60f3\u5230\u77db\u76fe\u6fc0\u5316\u4e4b\u540e\u6839\u672c\u5c31\u6ca1\u6709\u4ec0\u4e48\u7f13\u51b2\u7684\u65f6\u95f4\uff0c\u5f20\u77e5\u884c\u5f53\u591c\u5c31\u544a\u5047\u56de\u6d77\u5dde\uff0c\u5927\u5bb6\u90fd\u76f8\u4fe1\u4ed6\u662f\u53bb\u642c\u63f4\u5175\uff0c\u4f46\u662f\u8c01\u4e5f\u6ca1\u6709\u60f3\u5230\u7b2c\u4e8c\u5929\u7701\u957f\u5f90\u5b66\u5e73\u5c31\u51fa\u73b0\u5728\u65b0\u829c\uff0c\u7ad9\u5728\u57ce\u9632\u6c5f\u5824\u4e0a\u8feb\u7740\u5f53\u65f6\u7684\u5e02\u59d4\u4e66\u8bb0\u6c5f\u4e0a\u5143\u4e0e\u5e02\u957f\u5218\u7235\u7834\u5824\u68c0\u67e5\uff0c\u76f4\u63a5\u5c06\u7f57\u5f52\u6e90\u7236\u5b50\u9001\u8fdb\u5927\u7262\u5224\u4e86\u6b7b\u7f13\u3002</p><p>\u3000\u3000\u5982\u6b64\u5f3a\u786c\u7684\u59ff\u6001\uff0c\u624d\u8ba9\u5f20\u77e5\u884c\u521d\u5230\u65b0\u829c\u5c31\u7ad9\u7a33\u811a\u8ddf\uff0c\u4f46\u662f\u65b0\u829c\u8bb8\u591a\u673a\u5173\u5e72\u90e8\u5bf9\u6b64\u5374\u9887\u4e3a\u5fae\u8f9e\uff0c\u4ee5\u4e3a\u5f20\u77e5\u884c\u4e0e\u7f57\u5f52\u6e90\u7684\u6076\u6597\u662f\u72d7\u54ac\u72d7\u3002\u867d\u7136\u5c06\u7f57\u5f52\u6e90\u8fd9\u6761\u6076\u72d7\u9001\u8fdb\u7262\u7b3c\u91cc\uff0c\u4f46\u662f\u6709\u4e9b\u673a\u5173\u5e72\u90e8\u5bf9\u5f20\u77e5\u884c\u7684\u6212\u5fc3\u8fd8\u662f\u5f88\u9ad8\uff0c\u5c31\u50cf\u5f20\u77e5\u884c\u5728\u65b0\u829c\u6574\u987f\u632f\u5174\u767d\u9152\u4ea7\u4e1a\u65f6\uff0c\u7d20\u6765\u6e05\u9ad8\u7684\u9648\u80b2\u9009\u62e9\u4e86\u56de\u907f\u3002</p><p>\u3000\u3000\u5f85\u4e91\u6c60\u9152\u4e1a\u5e26\u7740\u65b0\u829c\u767d\u9152\u4ea7\u4e1a\u6574\u4f53\u5d1b\u8d77\uff0c\u5f20\u77e5\u884c\u81f3\u5c11\u5728\u65b0\u829c\u83b7\u5f97\u201c\u80fd\u540f\u201d\u7684\u8bc4\u4ef7\uff0c\u4e0d\u8fc7\u8d1f\u9762\u8bc4\u4ef7\u4f9d\u65e7\u4e0d\u5c11\u3002</p><p>\u3000\u3000\u673a\u5173\u5e72\u90e8\u5bf9\u5f20\u77e5\u884c\u771f\u6b63\u7684\u6539\u89c2\uff0c\u5927\u6982\u8fd8\u662f\u53bb\u5e74\u590f\u5b63\u90a3\u573a\u767e\u5e74\u4e00\u9047\u7684\u7279\u5927\u6d2a\u6c34\u5427\u3002</p><p>\u3000\u3000\u5c0f\u6c5f\u6d41\u57df\u6574\u4e2a\u590f\u5b63\u5171\u5f62\u6210\u4e5d\u6b21\u6d2a\u5cf0\uff0c\u4e00\u6b21\u6bd4\u4e00\u6b21\u51f6\u731b\uff0c\u65b0\u829c\u4f4d\u4e8e\u5c0f\u6c5f\u7684\u4e2d\u6bb5\u8170\u4e0a\uff0c\u53d7\u5230\u6d2a\u5cf0\u51b2\u51fb\u7684\u80fd\u91cf\u6700\u5f3a\uff0c\u5b9e\u5728\u96be\u4ee5\u60f3\u8c61\u8981\u4e0d\u662f\u4e5d\u516d\u5e74\u53ca\u65f6\u7834\u4e86\u7f57\u5f52\u6e90\u6848\u3001\u4e5d\u4e03\u5e74\u91cd\u4fee\u4e86\u57ce\u9632\u6c5f\u5824\uff0c\u65b0\u829c\u8fd9\u5ea7\u57ce\u5e02\u4f1a\u5728\u4e5d\u516b\u5e74\u9762\u4e34\u600e\u6837\u7684\u706d\u9876\u4e4b\u707e\uff01</p><p>\u3000\u3000\u91d1\u5c71\u6e56\u6e83\u5824\uff0c\u5bfc\u81f4\u8fd1\u5343\u519b\u6c11\u4f24\u4ea1\uff0c\u9020\u6210\u8fd1\u767e\u4ebf\u7684\u7ecf\u6d4e\u635f\u5931\uff0c\u91d1\u5c71\u6e56\u79bb\u91d1\u5c71\u5e02\u57ce\u533a\u8fd8\u6709\u51e0\u5341\u516c\u91cc\u7684\u8ddd\u79bb\uff0c\u4f46\u662f\u65b0\u829c\u5e02\u7684\u57ce\u533a\u5c31\u5728\u57ce\u9632\u6c5f\u5824\u4e4b\u540e\uff0c\u53ef\u4ee5\u8bf4\u5824\u7834\u57ce\u4ea1\uff0c\u4e0d\u8981\u8bf4\u4f24\u4ea1\u4e0a\u5343\uff0c\u4e0a\u4e07\u90fd\u6709\u53ef\u80fd\u3002</p><p>\u3000\u3000\u987e\u6653\u6885\u5fc3\u91cc\u60f3\uff1a\u8ba4\u771f\u7684\u53bb\u5206\u6790\uff0c\u7f57\u5f52\u6e90\u6848\u7684\u7834\u83b7\u4e0e\u57ce\u9632\u6c5f\u5824\u7684\u91cd\u5efa\uff0c\u5f20\u606a\u4e0e\u7f57\u6587\u4e89\u98ce\u5403\u918b\u662f\u76f4\u63a5\u7684\u5bfc\u706b\u7d22\uff0c\u800c\u4e14\u53cc\u65b9\u77db\u76fe\u6fc0\u5316\u540e\uff0c\u7701\u957f\u5f90\u5b66\u5e73\u7b2c\u4e8c\u5929\u5c31\u5230\u65b0\u829c\u5f3a\u8feb\u5f53\u65f6\u7684\u5e02\u59d4\u4e66\u8bb0\u6c5f\u4e0a\u5143\u7834\u5824\uff0c\u8fd9\u4e5f\u8868\u660e\u5f20\u77e5\u884c\u4ed6\u4eec\u5bf9\u57ce\u9632\u6c5f\u5824\u5b58\u5728\u95ee\u9898\u6709\u5341\u8db3\u7684\u628a\u63e1\uff0c\u5c31\u7b97\u5f20\u77e5\u884c\u4e00\u6765\u65b0\u829c\u5c31\u60f3\u7740\u70e7\u628a\u706b\u7ad9\u7a33\u811a\u8ddf\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u76f4\u63a5\u5c06\u706b\u70e7\u5230\u7f57\u5f52\u6e90\u7684\u5934\u4e0a\u2014\u2014\u518d\u52a0\u5f90\u5b66\u5e73\u5728\u7f57\u5f52\u6e90\u6848\u540e\u53c8\u8fc5\u901f\u7684\u5927\u529b\u6574\u987f\u5168\u7701\u7684\u6c34\u5229\u4f53\u7cfb\uff0c\u8ba9\u6574\u4ef6\u4e8b\u60c5\u770b\u4e0a\u53bb\u5c31\u662f\u4e00\u573a\u7cbe\u5fc3\u8bbe\u8ba1\u7684\u9884\u8c0b\u3002</p><p>\u3000\u3000\u8fd9\u5c31\u662f\u7cbe\u5fc3\u8bbe\u8ba1\u7684\u9884\u8c0b\uff0c\u987e\u6653\u6885\u7a81\u7136\u60f3\u901a\u4e86\u8fd9\u70b9\u3002</p><p>\u3000\u3000\u201c\u987e\u59d0\u60f3\u5230\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8fd9\u4e48\u5174\u594b\uff0c\u773c\u775b\u90fd\u6bd4\u7535\u8111\u5c4f\u8fd8\u4eae\u3002\u201d\u6210\u4f59\u4e1c\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\uff0c\u4f20\u771f\u673a\u54cd\u4e86\u8d77\u6765\uff0c\u987e\u6653\u6885\u8bf4\u9053\uff1a\u201c\u53ef\u80fd\u6c11\u653f\u5c40\u5728\u53d1\u4f20\u771f\uff0c\u5f20\u5e02\u957f\u5728\u7b49\u8fd9\u4e2a\u5462\u3002\u201d\u6709\u4e9b\u4e1c\u897f\u6bd5\u7adf\u662f\u731c\u6d4b\uff0c\u800c\u4e14\u9690\u85cf\u5728\u80cc\u540e\u7684\u79d8\u5bc6\u592a\u60ca\u4eba\u4e86\uff0c\u987e\u6653\u6885\u4e5f\u4e0d\u80fd\u8ddf\u6210\u4f59\u4e1c\u778e\u626f\uff0c\u5979\u7ad9\u8d77\u6765\u5230\u4f20\u771f\u673a\u8fb9\u4e0a\u7b49\u7740\u4f20\u771f\u53d1\u8fc7\u6765\uff0c\u5fc3\u91cc\u5374\u5728\u60f3\uff1a\u8fd9\u4e48\u8bf4\u6765\uff0c\u5f20\u606a\u4e0e\u7f57\u5f52\u6e90\u513f\u5b50\u7f57\u6587\u4e89\u98ce\u5403\u918b\u6839\u672c\u5c31\u662f\u8fd9\u4e2a\u9884\u8c0b\u4e2d\u7cbe\u5fc3\u8bbe\u8ba1\u7684\u4e00\u73af\uff0c\u4e00\u5207\u7684\u4e00\u5207\u5c31\u662f\u5f90\u5b66\u5e73\u60f3\u6574\u987f\u5168\u7701\u6c34\u5229\u7cfb\u7edf\u5374\u6ca1\u6709\u5408\u9002\u7684\u501f\u53e3\uff0c\u65b0\u829c\u53ea\u662f\u4ed6\u7684\u7a81\u7834\u53e3\uff1f\u4e5f\u5c31\u662f\u8bf4\u5f20\u606a\u4e09\u5e74\u524d\u5728\u65b0\u829c\u7684\u8868\u73b0\u90fd\u662f\u7cbe\u5fc3\u8bbe\u8ba1\u7684\u3001\u6709\u9884\u8c0b\u7684\u5047\u8c61\uff1f</p></article>"}}}]);
