(window.webpackJsonp=window.webpackJsonp||[]).push([[790],{813:function(p){p.exports={chapterTitle:null,title:"\u7b2c788\u7ae0 \u5206\u624b",content:"<article><h2>\u7b2c788\u7ae0 \u5206\u624b</h2><p>\u3000\u3000\u7adf\u7136\u4e0e\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u4e58\u540c\u4e00\u73ed\u98de\u673a\u5230\u5efa\u90ba\uff0c\u771f\u8ba9\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u59d0\u59b9\u597d\u53d7\u7684\uff1b\u5176\u5b9e\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u4eec\u5fc3\u91cc\u4e5f\u4e0d\u597d\u53d7\uff0c\u4ed6\u4eec\u5e76\u4e0d\u60f3\u7ed9\u9526\u6e56\u90a3\u8fb9\u7684\u4eba\u77e5\u9053\u4ed6\u4eec\u9009\u62e9\u8fd9\u4e2a\u65f6\u95f4\u5230\u5185\u5730\u6765\u3002\u91d1\u5c71\u4e0e\u9999\u6e2f\u4e4b\u95f4\u7684\u822a\u7ebf\u6bcf\u661f\u671f\u624d\u6709\u5468\u4e00\u3001\u5468\u4e94\u4e24\u73ed\u5f80\u8fd4\u7684\u822a\u73ed\uff0c\u845b\u660e\u4fe1\u8d76\u7740\u8981\u5230\u5185\u5730\u6765\uff0c\u53ea\u662f\u4e58\u98de\u673a\u62b5\u8fbe\u5efa\u90ba\u540e\u518d\u8f6c\u4e58\u6c7d\u8f66\u2014\u2014\u91d1\u5c71\u7535\u5b50\u5de5\u4e1a\u56ed\u6709\u4e13\u8f66\u5230\u673a\u573a\u6765\u63a5\u4ed6\u4eec\uff0c\u56e0\u4e3a\u6ca1\u6709\u6253\u7b97\u5728\u5efa\u90ba\u505c\u7559\uff0c\u6240\u4ee5\u8c22\u7edd\u8c22\u6c49\u9756\u6216\u8c22\u5251\u5357\u5230\u673a\u573a\u6765\u63a5\u673a\uff0c\u5f20\u606a\u770b\u5728\u773c\u91cc\uff0c\u81ea\u7136\u5c31\u731c\u51fa\u845b\u5bb6\u7236\u5b50\u53ea\u662f\u9014\u5f84\u5efa\u90ba\u3002</p><p>\u3000\u3000\u5f20\u606a\u7a7f\u7740\u6df1\u68d5\u8272\u7684\u76ae\u5939\u514b\u7ad9\u5728\u5bbd\u655e\u660e\u4eae\u7684\u63a5\u673a\u5927\u5385\u91cc\uff0c\u845b\u5bb6\u7236\u5b50\u770b\u5230\u4ed6\u672a\u5c1d\u6ca1\u6709\u5148\u9000\u56de\u53bb\u8eb2\u4e00\u4f1a\u513f\u518d\u51fa\u6765\u7684\u51b2\u52a8\u3002\u5f53\u7136\uff0c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u8111\u6d77\u4e2d\u5f98\u5f8a\u7684\u5ff5\u5934\u800c\u5df2\uff0c\u773c\u4e0b\u53ea\u6709\u786c\u7740\u5934\u76ae\u4ece\u5f20\u606a\u7684\u773c\u76ae\u5b50\u5e95\u4e0b\u7a7f\u8fc7\u63a5\u673a\u5927\u5385\uff0c\u6487\u7740\u5934\u4e0d\u770b\uff0c\u4f9d\u65e7\u80fd\u611f\u89c9\u5230\u5f20\u606a\u671b\u6765\u7684\u51b7\u51bd\u7684\u76ee\u5149\u2014\u2014\u8fd9\u65f6\u5019\u53cc\u65b9\u8fde\u865a\u4f2a\u7684\u5ba2\u5957\u90fd\u61d2\u5f97\u505a\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u845b\u660e\u4fe1\u7236\u5b50\u5728\u968f\u884c\u4eba\u5458\u7684\u7c07\u62e5\u4e0b\u51fa\u4e86\u63a5\u673a\u5927\u5385\uff0c\u624d\u6413\u4e86\u6413\u624b\uff0c\u8d70\u8fc7\u6765\u5e2e\u5b59\u9759\u6aac\u3001\u5b59\u9759\u9999\u62ff\u884c\u674e\uff0c\u89c1\u5979\u4eec\u7a7f\u7684\u90fd\u6bd4\u8f83\u5355\u8584\uff0c\u8bf4\u9053\uff1a\u201c\u9999\u6e2f\u6625\u610f\u6b63\u6d53\uff0c\u5efa\u90ba\u7684\u51ac\u5929\u8fd8\u6ca1\u6709\u8fc7\u53bb\u5462\uff0c\u5929\u6c14\u9884\u62a5\u4eca\u5929\u665a\u4e0a\u8fd8\u4f1a\u4e0b\u5c0f\u96ea\uff0c\u201d\u5c06\u76ae\u8863\u8131\u4e0b\u6765\u9012\u7ed9\u5b59\u9759\u6aac\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u611f\u89c9\u5230\u5c31\u4f60\u9700\u8981\u7167\u987e\u554a\u2026\u2026\u201d\u89c1\u5b59\u9759\u6aac\u6ca1\u6709\u62d2\u7edd\uff0c\u5e2e\u5979\u62ab\u80a9\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e2e\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u59d0\u59b9\u63a8\u7740\u884c\u674e\u8f66\u51fa\u63a5\u673a\u5927\u5385\uff0c\u9a6c\u6d77\u9f99\u53ca\u5176\u4ed6\u968f\u5458\u5c06\u8f66\u505c\u5728\u4e09\u53f7\u51fa\u53e3\u5904\u7b49\u5019\uff0c\u4e24\u8f86\u8f66\uff0c\u4e00\u8f86\u8f7f\u8f66\uff0c\u4e00\u8f86\u5546\u52a1\u8f66\u3002\u5916\u9762\u7684\u6c14\u6e29\u5f88\u4f4e\uff0c\u5f20\u606a\u8ba9\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u59d0\u59b9\u540c\u4ed6\u5750\u8f7f\u8f66\uff0c\u521a\u5750\u4e0a\u8f66\u5c31\u63a5\u5230\u53f6\u5efa\u658c\u7684\u7535\u8bdd\uff0c\u7531\u4e8e\u8981\u7167\u987e\u5230\u5b59\u9759\u6aac\u7684\u5fc3\u60c5\uff0c\u665a\u996d\u5c31\u5728\u9752\u5e74\u516c\u5bd3\u91cc\u968f\u610f\u51c6\u5907\uff0c\u5f20\u606a\u8fc7\u6765\u63a5\u673a\uff0c\u53f6\u5efa\u658c\u4e0e\u675c\u98de\u8fd8\u6709\u9648\u5983\u84c9\u5c31\u5728\u516c\u5bd3\u91cc\u51c6\u5907\u996d\u83dc\uff0c\u4e5f\u6ca1\u6709\u8bf7\u5176\u4ed6\u4eba\u8fc7\u6765\u3002\u5f20\u606a\u5728\u7535\u8bdd\u91cc\u8ddf\u53f6\u5efa\u658c\u80e1\u626f\u4e86\u4e24\u53e5\uff0c\u5c31\u5c06\u7535\u8bdd\u9012\u7ed9\u540e\u6392\u7684\u5b59\u9759\u9999\u3002</p><p>\u3000\u3000\u8f66\u5230\u5b66\u5e9c\u5df7\uff0c\u6697\u84dd\u8272\u7684\u591c\u7a7a\u679c\u771f\u5c31\u98d8\u8d77\u96ea\u82b1\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4fe9\u4eca\u5929\u7684\u63a5\u5f85\u89c4\u683c\u771f\u662f\u9ad8\u4e86\uff0c\u201d\u53f6\u5efa\u658c\u6253\u5f00\u95e8\u65f6\u7ad9\u5728\u95e8\u53e3\u7cfb\u7740\u56f4\u88d9\u7aef\u7740\u9752\u82b1\u5927\u74f7\u7897\u5728\u6253\u86cb\u82b1\uff0c\u8ddf\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u59d0\u59b9\u5f00\u73a9\u7b11\u8bf4\uff0c\u201c\u7b49\u4f1a\u513f\u8ba9\u5f20\u606a\u7ed9\u4f60\u4eec\u4e5f\u7092\u4e24\u4e2a\u83dc\uff0c\u8fd9\u987f\u996d\u53ef\u4ee5\u8bf4\u662f\u5343\u91d1\u4e0d\u6362\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e25\u91cd\u6000\u7591\u53f6\u5efa\u658c\u4e0e\u675c\u98de\u7684\u53a8\u827a\uff0c\u4ed6\u95ee\u7fdf\u4e39\u9752\uff1a\u201c\u4f60\u7684\u6c34\u624b\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e5f\u5c31\u7ed9\u4f60\u6253\u4e0b\u624b\u3002\u201d\u7fdf\u4e39\u9752\u4e0d\u597d\u610f\u601d\u7684\u627f\u8ba4\u81ea\u5df1\u7684\u53a8\u827a\u6c34\u51c6\u4e0d\u8fc7\u5173\u3002</p><p>\u3000\u3000\u201c\u5f97\uff0c\u5f97\uff0c\u5f97\uff0c\u4f60\u4eec\u5c31\u7b49\u7740\u5403\u73b0\u6210\u7684\u5427\u3002\u201d\u5f20\u606a\u6325\u4e86\u6325\u624b\uff0c\u4ed6\u5c06\u53f6\u5efa\u658c\u8eab\u4e0a\u7684\u56f4\u88d9\u89e3\u4e0b\u6765\uff0c\u4eb2\u81ea\u8fdb\u53a8\u623f\u505a\u83dc\u53bb\uff0c\u8fd8\u597d\u6709\u9648\u5983\u84c9\u5e2e\u4ed6\u6253\u4e0b\u624b\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u542c\u5b59\u9759\u9999\u8bf4\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u8ddf\u5979\u4eec\u540c\u673a\u62b5\u8fbe\u9999\u6e2f\uff0c\u5c31\u5728\u53a8\u623f\u95e8\u53e3\u8ddf\u5f20\u606a\u6253\u5c94\uff1a\u201c\u770b\u6765\u6211\u4eec\u641e\u7684\u4e9b\u5c0f\u52a8\u9759\uff0c\u8fd8\u662f\u8d77\u5230\u6405\u6d51\u6c34\u7684\u76ee\u6807\u2026\u2026\u845b\u660e\u4fe1\u8fd9\u8001\u5bb6\u4f19\uff0c\u8f7b\u6613\u4e0d\u632a\u52a8\u3002\u201d</p><p>\u3000\u3000\u201c\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u3001\u5bb6\u7528\u7535\u5668\uff0c\u53bb\u5e74\u4e0b\u534a\u5e74\u7684\u51fa\u53e3\u53d7\u5230\u91cd\u632b\uff0c\u4eca\u5e74\u90fd\u672a\u5fc5\u80fd\u6062\u590d\u8fc7\u6765\uff0c\u5185\u5730\u5e02\u573a\u5374\u5728\u4e0d\u65ad\u7684\u589e\u6da8\u4e2d\uff0c\u4f60\u4ee5\u4e3a\u845b\u660e\u4fe1\u80fd\u6709\u591a\u5c11\u9009\u62e9\uff1f\u201d\u5f20\u606a\u63a5\u8fc7\u9648\u5983\u84c9\u6d17\u51c0\u7684\u80e1\u841d\u535c\u653e\u5230\u83dc\u6848\u4e0a\u5207\u4e01\uff0c\u8fd8\u4e0d\u95ee\u8ddf\u53f6\u5efa\u658c\u80e1\u626f\u3002</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u4ed6\u4eec\u8fd9\u6b21\u5230\u5185\u5730\u6765\u4f1a\u6709\u4ec0\u4e48\u52a8\u4f5c\uff1f\u201d\u53f6\u5efa\u658c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5609\u4fe1\u7535\u5b50\u8981\u8d70\u51fa\u6682\u65f6\u7684\u56f0\u5883\uff0c\u4e00\u65b9\u9762\u53ef\u4ee5\u52a0\u5f3a\u4e0e\u4e09\u661f\u7b49\u6d77\u5916\u7535\u5b50\u5382\u5546\u7684\u5408\u4f5c\uff0c\u52a0\u5f3a\u9488\u5bf9\u5185\u5730\u5e02\u573a\u7684\u7535\u5b50\u4ea7\u54c1\u7684\u4ee3\u5de5\u4e1a\u52a1\uff1b\u540c\u65f6\uff0c\u6211\u4eec\u4e5f\u4e0d\u8981\u5fd8\u8bb0\u4e86\uff0c\u53bb\u5e74\u4e5d\u6708\uff0c\u5609\u4fe1\u7535\u5b50\u8fde\u540c\u6b63\u6cf0\u96c6\u56e2\u4e00\u5171\u6ce8\u518c\u4e86\u56db\u5bb6\u80fd\u591f\u65e0\u507f\u4f7f\u79d1\u738b\u5546\u6807\u7684\u516c\u53f8\u3002\u79d1\u738b\u9ad8\u79d1\u5b9e\u9645\u53d7\u9648\u5bb6\u5584\u7684\u5927\u96c5\u96c6\u56e2\u63a7\u5236\uff0c\u8fd8\u6709\u4e09\u5bb6\u51a0\u4ee5\u79d1\u738b\u540d\u8854\u7684\u516c\u53f8\u6709\u4e00\u5bb6\u7b79\u5212\u7740\u505a\u767d\u7535\uff0c\u4f46\u662f\u8fc4\u4eca\u4e3a\u6b64\u90fd\u6ca1\u6709\u53d1\u5c55\u51fa\u4ec0\u4e48\u6838\u5fc3\u4e1a\u52a1\uff0c\u800c\u4eca\u5e74\u79d1\u738b\u5728\u5e7f\u544a\u4e0a\u7684\u6295\u5165\u9884\u8ba1\u4e0d\u4f1a\u4f4e\u4e8e\u4e00\u4ebf\u4e94\u5343\u4e07\uff0c\u4f60\u8bf4\u4ed6\u4eec\u60f3\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u5609\u4fe1\u7535\u5b50\u624b\u4e2d\u62e5\u6709\u8bb8\u591a\u7535\u5b50\u4ea7\u54c1\u7684\u5b8c\u6574\u4ea7\u54c1\u6280\u672f\u4e0e\u751f\u4ea7\u7ebf\uff0c\u53ea\u662f\u4ed6\u4eec\u4e4b\u524d\u7684\u5e02\u573a\u5b9a\u4f4d\uff0c\u9650\u5236\u4ed6\u4eec\u4e13\u6ce8\u4e8e\u4ee3\u5de5\u4e1a\u52a1\u7684\u53d1\u5c55\uff0c\u5728\u54c1\u724c\u5efa\u8bbe\u65b9\u9762\u7684\u6295\u5165\u6781\u5fae\uff0c\u4e5f\u6ca1\u6709\u4e0e\u7ec8\u7aef\u5e02\u573a\u8fde\u63a5\u7684\u8425\u9500\u4f53\u7cfb\u3002\u6362\u4f5c\u662f\u6211\u7684\u8bdd\uff0c\u5e76\u4e0d\u9700\u8981\u6539\u53d8\u5609\u4fe1\u7535\u5b50\u7684\u5e02\u573a\u5b9a\u4f4d\u4e0e\u7ecf\u8425\u601d\u8def\uff0c\u4f46\u662f\u4e0e\u79d1\u738b\u7535\u5668\u8054\u5408\u8d77\u6765\uff0c\u5229\u7528\u79d1\u738b\u5546\u6807\u54c1\u724c\u7684\u58f3\u5b50\uff0c\u5374\u80fd\u66f4\u52a0\u6df1\u5165\u7684\u3001\u66f4\u52a0\u76f4\u63a5\u7684\u8fdb\u5165\u5185\u5730\u6d88\u8d39\u7c7b\u7535\u5b50\u3001\u5bb6\u7535\u5e02\u573a\u3002\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u867d\u7136\u5f88\u4e0d\u8ba8\u4eba\u559c\u6b22\uff0c\u53ef\u60dc\u554a\uff0c\u4ed6\u4eec\u5e76\u4e0d\u662f\u5984\u81ea\u5c0a\u5927\u3001\u72c2\u5984\u65e0\u77e5\u7684\u8822\u4eba\uff0c\u201d\u5f20\u606a\u5482\u5482\u5634\uff0c\u201c\u4ed6\u4eec\u5728\u91d1\u5c71\u7b79\u5efa\u7684\u7535\u5b50\u5de5\u4e1a\u5236\u9020\u57fa\u5730\uff0c\u4e5f\u5e76\u975e\u6b3a\u4e0a\u7792\u4e0b\u7684\u4e00\u7eb8\u7a7a\u6587\u2026\u2026\u201d\u9526\u6e56\u83b7\u5f97\u957f\u8db3\u53d1\u5c55\u7684\u540c\u65f6\uff0c\u5bf9\u624b\u4eec\u5e76\u975e\u611a\u8822\u5230\u5728\u539f\u5730\u8e0f\u8df3\u4e0d\u524d\uff0c\u76f8\u53cd\u7684\uff0c\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u3001\u8c22\u610f\u4ee5\u53ca\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7b49\u4eba\u90fd\u6709\u76f8\u5f53\u7684\u7ecf\u6d4e\u5934\u8111\uff0c\u5728\u9526\u6e56\u7684\u523a\u6fc0\u4e0b\u6709\u7740\u6bd4\u65e2\u5b9a\u5386\u53f2\u8f68\u9053\u66f4\u51fa\u8272\u7684\u53d1\u5c55\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u7ade\u4e89\u5bf9\u624b\u592a\u611a\u8822\uff0c\u53c8\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u53f6\u5efa\u658c\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u6211\u6ca1\u6709\u4f60\u7684\u51cc\u4e91\u58ee\u5fd7\uff0c\u6211\u5012\u662f\u5f88\u671f\u5f85\u9047\u5230\u7684\u5bf9\u624b\u4e2a\u4e2a\u90fd\u8822\u7b28\u5982\u732a\uff0c\u5c31\u4e0d\u7528\u6211\u8017\u592a\u591a\u7684\u8111\u5b50\u4e86\uff0c\u201d\u5f20\u606a\u8bf4\u8bdd\u7684\u5de5\u592b\u5df2\u7ecf\u5c06\u80e1\u841d\u535c\u3001\u9999\u8339\u3001\u9e21\u8089\u90fd\u5207\u6210\u4e01\uff0c\u53c8\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u8ddf\u53f6\u5efa\u658c\u8bf4\u9053\uff0c\u201c\u4f60\u4e0d\u662f\u8fd9\u51e0\u5929\u8981\u53bb\u91d1\u5c71\u5417\uff1f\u6211\u8ddf\u4f60\u4e00\u8d77\u8fc7\u53bb\u3002\u201d</p><p>\u3000\u3000\u201c\u662f\u5417\uff1f\u90a3\u518d\u597d\u4e0d\u8fc7\u4e86\u3002\u4f60\u77e5\u9053\u7684\uff0c\u5468\u747e\u745c\u73b0\u5728\u662f\u91d1\u5c71\u7684\u4e00\u628a\u624b\uff0c\u5979\u662f\u7edd\u4e0d\u4f1a\u6b22\u8fce\u6211\u4eec\u8fc7\u53bb\u7684\u3002\u6211\u4e00\u4e2a\u4eba\u8fc7\u53bb\u8fd8\u6709\u4e9b\u80c6\u602f\uff0c\u4f60\u966a\u6211\u4e00\u8d77\u8fc7\u53bb\uff0c\u4e24\u4eba\u76f8\u4e92\u58ee\u58ee\u80c6\u5b50\uff0c\u201d\u53f6\u5efa\u658c\u63f6\u63c4\u7684\u7b11\u7740\u8bf4\uff0c\u201c\u8981\u4e0d\u6211\u4eec\u63d0\u524d\u8fc7\u53bb\uff0c\u8d81\u7740\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u4ed6\u4eec\u90fd\u6ca1\u6709\u79bb\u5f00\u91d1\u5c71\uff0c\u6211\u4eec\u4e00\u8d77\u8fc7\u53bb\u51d1\u51d1\u70ed\u95f9\uff1f\u201d</p><p>\u3000\u3000\u8981\u4e0d\u662f\u9648\u5983\u84c9\u63d0\u9192\uff0c\u5f20\u606a\u5dee\u70b9\u5c06\u767d\u7cd6\u5f53\u6210\u76d0\u52a0\u5230\u83dc\u91cc\u53bb\uff0c\u9a6c\u4e0a\u5c06\u4e00\u8fb9\u4e71\u626f\u5206\u4ed6\u5fc3\u7684\u53f6\u5efa\u658c\u8d76\u8d70\uff0c\u4e0e\u9648\u5983\u84c9\u914d\u5408\u5c06\u4e00\u684c\u83dc\u505a\u4e86\u51fa\u6765\u3002</p><p>\u3000\u3000\u6ca1\u6709\u5176\u4ed6\u4eba\uff0c\u5c31\u53f6\u5efa\u658c\u3001\u675c\u98de\u3001\u9648\u5983\u84c9\u3001\u7fdf\u4e39\u9752\u3001\u9a6c\u6d77\u9f99\u4e0e\u5f20\u606a\u3001\u5b59\u9759\u6aac\u3001\u5b59\u9759\u9999\u516b\u4e2a\u4eba\uff0c\u56f4\u7740\u4e00\u684c\u83dc\uff0c\u5403\u5b8c\u5c31\u6563\u4e86\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u966a\u53f6\u5efa\u658c\u8d70\u4e86\uff0c\u675c\u98de\u591c\u91cc\u8fd8\u6709\u4e8b\u60c5\u8981\u5904\u7406\uff0c\u8fd8\u8981\u53bb\u4ed6\u7684\u521b\u57df\u516c\u53f8\uff0c\u7fdf\u4e39\u9752\u8fd8\u6709\u5f20\u606a\u5429\u5490\u5979\u6574\u7406\u7684\u8d44\u6599\u5f85\u6574\u7406\uff0c\u4e5f\u5148\u56de\u697c\u4e0b\u516c\u5bd3\u4e86\u3002\u5c31\u5f20\u606a\u62c9\u7740\u9648\u5983\u84c9\u966a\u5b59\u9759\u6aac\u5750\u5230\u697c\u9876\u56ed\u6797\u7684\u8336\u5ba4\u91cc\u804a\u5929\u3002</p><p>\u3000\u3000\u201c\u660e\u660e\u8bf4\u662f\u966a\u6211\u56de\u5efa\u90ba\u6563\u5fc3\u7684\uff0c\u4eba\u521a\u5230\u5efa\u90ba\u5c31\u5fc3\u9b42\u4e0d\u5b81\u7684\u6e9c\u8d70\u4e86\uff0c\u201d\u5b59\u9759\u6aac\u62ab\u7740\u5916\u5957\u8737\u5750\u5728\u8336\u5ba4\u7684\u957f\u6905\u4e0a\u62b1\u6028\u5979\u59d0\u59d0\u5b59\u9759\u9999\u521a\u5230\u5efa\u90ba\u4eba\u5c31\u8ddf\u53f6\u5efa\u658c\u8d70\u4e86\uff0c\u201c\u771f\u6000\u7591\u5979\u966a\u6211\u56de\u5efa\u90ba\u7684\u5c45\u5fc3\u3002\u201d</p><p>\u3000\u3000\u201c\u90fd\u662f\u4e9b\u6ca1\u4eba\u6027\u7684\u5bb6\u4f19\uff0c\u201d\u5f20\u606a\u89c1\u5b59\u9759\u6aac\u8fd8\u6709\u5fc3\u601d\u53d1\u8fd9\u6837\u7684\u7262\u9a9a\uff0c\u5fc3\u60f3\u5979\u7684\u60c5\u7eea\u8981\u6bd4\u5728\u9999\u6e2f\u65f6\u597d\u591a\u4e86\uff0c\u8bf4\u9053\uff0c\u201c\u8981\u4e0d\u4e00\u8d77\u5230\u8857\u4e0a\u8f6c\u8f6c\u53bb\uff1f\u8fd9\u91cc\u4e0d\u5c06\u78b3\u7089\u70e7\u8d77\u6765\uff0c\u7a7a\u5750\u5728\u8fd9\u91cc\u611f\u89c9\u6709\u4e9b\u51b7\u554a\u3002\u201d\u592a\u5b89\u9759\u5bf9\u5b59\u9759\u6aac\u672a\u5fc5\u662f\u597d\uff0c\u8fd8\u4e0d\u5982\u70ed\u70ed\u95f9\u95f9\u7684\u627e\u4e9b\u4e8b\u60c5\u5c06\u5979\u7684\u601d\u7ef4\u5c94\u5230\u5176\u4ed6\u5730\u65b9\u53bb\u66f4\u597d\u4e9b\uff0c\u53bb\u5b59\u9759\u6aac\u76841978\u4e5f\u6bd4\u5750\u8fd9\u91cc\u5f3a\u3002</p><p>\u3000\u3000\u5929\u7a7a\u8fd8\u98d8\u7740\u7a00\u758f\u7684\u96ea\u82b1\uff0c\u5b59\u9759\u6aac\u8fd8\u6ca1\u6709\u5c06\u884c\u674e\u7bb1\u6253\u5f00\uff0c\u5979\u5c31\u62ab\u7740\u5f20\u606a\u7684\u76ae\u8863\u5fa1\u5bd2\uff0c\u8ddf\u7740\u5f20\u606a\u3001\u9648\u5983\u84c9\u4e00\u8d77\u51fa\u4e86\u9752\u5e74\u516c\u5bd3\uff0c\u5f80\u5b66\u5e9c\u5df7\u8d70\u53bb\uff0c\u5728\u8def\u4e0a\u8ddf\u5b59\u9759\u6aac\u8bf4\u8d77\u675c\u98de\u4ed6\u4eec\u6b63\u7b79\u5efa\u8ba1\u7b97\u673a\u7f51\u7edc\u57f9\u8bad\u5b66\u6821\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u6f2b\u4e0d\u7ecf\u5fc3\u7684\u5728\u5b66\u5e9c\u5df7\u91cc\u8d70\u7740\uff0c\u5230\u5df7\u5b50\u53e3\uff0c\u770b\u89c1\u5e2d\u82e5\u7433\u624b\u62b9\u7740\u8138\u988a\u5f80\u8fd9\u8fb9\u8d70\uff0c\u773c\u775b\u91cc\u6cea\u5149\u95ea\u70c1\uff0c\u5728\u8def\u706f\u5149\u4e0b\uff0c\u4e00\u526f\u4f24\u5fc3\u6b32\u7edd\u7684\u6837\u5b50\uff0c\u800c\u5728\u8857\u9053\u7684\u53e6\u4e00\u4fa7\uff0c\u738b\u6d77\u7c9f\u6b63\u4f4e\u5934\u94bb\u8fdb\u4e00\u90e8\u505c\u9760\u5728\u8def\u8fb9\u7684\u8f7f\u8f66\u91cc\u3002</p><p>\u3000\u3000\u5435\u67b6\u6291\u6216\u5206\u624b\uff1f</p><p>\u3000\u3000\u770b\u5230\u8fd9\u573a\u666f\uff0c\u5f20\u606a\u8111\u5b50\u91cc\u53ea\u6d6e\u73b0\u51fa\u4e24\u4e2a\u5ff5\u5934\u3002\u4e0e\u5b59\u9759\u6aac\u3001\u9648\u5983\u84c9\u9762\u9762\u76f8\u89d1\uff0c\u8003\u8651\u7740\u662f\u4e0d\u662f\u8eb2\u5f00\u4e00\u4e0b\uff0c\u7136\u540e\u901a\u77e5\u8499\u4e50\u6765\u6536\u62fe\u6b8b\u5c40\uff1f</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u770b\u5230\u5f20\u606a\u4ed6\u4eec\uff0c\u8f6c\u8fc7\u8eab\u5b50\u4e0d\u8ba9\u4ed6\u4eec\u770b\u5230\u5979\u54ed\u6ce3\u7684\u6837\u5b50\uff0c\u8981\u5c06\u8138\u988a\u4e0a\u7684\u6cea\u6c34\u62b9\u6389\uff0c\u80a9\u5934\u8038\u52a8\u7740\uff0c\u5f88\u4f24\u5fc3\u7684\u6837\u5b50\u3002</p><p>\u3000\u3000\u201c\u53d1\u751f\u4ec0\u4e48\u4e8b\u4e86\uff0c\u201d\u5f20\u606a\u603b\u4e0d\u80fd\u8bf4\u89c6\u800c\u4e0d\u89c1\u5427\uff0c\u7ed5\u5230\u5e2d\u82e5\u7433\u9762\u524d\uff0c\u5f00\u53e3\u95ee\u9053\uff0c\u201c\u662f\u4e0d\u662f\u7ed9\u81ed\u6d41\u6c13\u6b3a\u8d1f\u4e86\uff1f\u6211\u9a6c\u4e0a\u6253\u7535\u8bdd\u8499\u4e50\uff0c\u8ba9\u4ed6\u5e26\u4e24\u5144\u5f1f\u8df3\u51fa\u6765\u7ed9\u4f60\u51fa\u6c14\u3002\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u770b\u60c5\u5f62\u591a\u534a\u662f\u4e24\u4eba\u521a\u5206\u624b\uff0c\u5f20\u606a\u8fd8\u6545\u610f\u5f80\u5e2d\u82e5\u7433\u7684\u4f24\u53e3\u4e0a\u6492\u76d0\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u5fc3\u6ca1\u80ba\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u611f\u60c5\u7684\u4e8b\u60c5\u5916\u4eba\u6700\u96be\u63d2\u5634\uff0c\u4ed6\u6709\u5fc3\u5229\u7528\u738b\u6d77\u7c9f\u4e3a\u68cb\u5b50\uff0c\u53c8\u62c5\u5fc3\u4f1a\u5c06\u5e2d\u82e5\u7433\u5377\u8fdb\u53bb\uff0c\u770b\u5230\u4ed6\u4eec\u4e24\u4eba\u5206\u624b\uff0c\u81ea\u7136\u662f\u6700\u7406\u60f3\u7684\u7ed3\u679c\u3002\u4ed6\u4e5f\u80fd\u9884\u6599\u5230\u8fd9\u4e2a\u7ed3\u679c\uff0c\u738b\u6d77\u7c9f\u5fc3\u91cc\u85cf\u7740\u9b3c\u3001\u754f\u4ed6\u5982\u864e\uff0c\u738b\u6d77\u7c9f\u53c8\u5982\u4f55\u4e0d\u6015\u5e2d\u82e5\u7433\u65e0\u610f\u95f4\u5c06\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u7684\u771f\u76f8\u6cc4\u9732\u7ed9\u6797\u96ea\u3001\u845b\u5efa\u5fb7\u7b49\u4eba\u77e5\u9053\uff1f\u8fd9\u6bb5\u65f6\u95f4\u4ee5\u6765\u738b\u6d77\u7c9f\u751a\u81f3\u4e0d\u6562\u5230\u5b66\u5e9c\u5df7\u6765\u3001\u4e0d\u6562\u4e0e\u5e2d\u82e5\u7433\u591a\u63a5\u89e6\uff0c\u8fd9\u5df2\u7ecf\u610f\u5473\u7740\u4e24\u4eba\u4e4b\u95f4\u8d70\u5230\u5c3d\u5934\u4e86\u3002</p><p>\u3000\u3000\u6700\u96be\u582a\u7684\u6216\u8bb8\u662f\u81ea\u5df1\u96be\u582a\u7684\u65f6\u5019\u7ed9\u719f\u6089\u7684\u4eba\u770b\u5230\uff0c\u5e2d\u82e5\u7433\u4f24\u5fc3\u7684\u62b9\u7740\u6cea\u6c34\uff0c\u5012\u662f\u8d8a\u62b9\u8d8a\u591a\uff0c\u62bd\u6ce3\u7740\uff0c\u65e0\u6cd5\u505c\u6b62\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5de6\u53f3\u8f6c\u7740\u5934\u8ddf\u5b59\u9759\u6aac\u3001\u9648\u5983\u84c9\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u8ba9\u6211\u5148\u8d70\uff1f\u8981\u662f\u8ba9\u522b\u4eba\u8bef\u4f1a\u6211\u662f\u81ed\u6d41\u6c13\u5c31\u60e8\u4e86\uff0c\u6211\u7684\u540d\u58f0\u5728\u4e1c\u5927\u5df2\u7ecf\u591f\u5dee\u52b2\u4e86\uff0c\u6ca1\u5fc5\u8981\u96ea\u4e0a\u52a0\u971c\u3002\u201d</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u7834\u557c\u800c\u7b11\uff0c\u9f3b\u6d95\u5dee\u70b9\u90fd\u55b7\u51fa\u6765\uff0c\u8fd9\u4e0b\u5b50\u5979\u90fd\u4e0d\u597d\u610f\u518d\u54ed\u4e0b\u53bb\u4e86\u3002</p></article>"}}}]);
