(window.webpackJsonp=window.webpackJsonp||[]).push([[794],{817:function(p){p.exports={chapterTitle:null,title:"\u7b2c626\u7ae0 \u6350\u8d60",content:"<article><h2>\u7b2c626\u7ae0 \u6350\u8d60</h2><p>\u3000\u3000\u770b\u7740\u4f55\u5f26\u8f6c\u8eab\u5f80\u6821\u95e8\u91cc\u8d70\u53bb\uff0c\u5f20\u606a\u60f3\u8d77\u6765\u5728\u8f66\u4e0a\u90fd\u5fd8\u4e86\u95ee\u5979\u7b491978\u91cd\u65b0\u5f00\u4e1a\u4f1a\u4e0d\u4f1a\u518d\u53bb\u6253\u5de5\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u30001978\uff0c\u672a\u66fe\u53d1\u751f\u8fc7\u7684\u56de\u5fc6\uff0c\u4eba\u662f\u7269\u5df2\u975e\uff0c\u60f3\u5230\u8fd9\u4e9b\uff0c\u5f20\u606a\u8f7b\u8f7b\u7684\u5482\u4e86\u5482\u5634\uff0c\u4ed6\u5c31\u5728\u5b66\u5e9c\u5df7\u524d\u4e0b\u8f66\uff0c\u8ba9\u5085\u4fca\u5c06\u8f66\u5f00\u5230\u521b\u4e1a\u56ed\u533a\u5185\u7684\u505c\u8f66\u573a\u91cc\u53bb\u3002</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u4e00\u5929\u6bd4\u4e00\u5929\u66f4\u6210\u6a21\u6837\uff0c\u5012\u662f\u9752\u5e74\u516c\u5bd3\u90a3\u91cc\u6025\u4e0d\u5f97\uff0c\u5f20\u606a\u4fbf\u4fe1\u6b65\u5728\u5b66\u5e9c\u5df7\u7684\u5916\u8857\u4e0a\u95f2\u901b\u3002</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u5e97\u94fa\u8fd8\u521a\u521a\u8fdb\u5165\u62db\u5546\u9636\u6bb5\uff0c\u5916\u8857\u7684\u5e95\u5c42\u5e97\u94fa\u57fa\u672c\u4e0a\u90fd\u8865\u507f\u7ed9\u539f\u6765\u7684\u5546\u6237\u4e86\uff0c\u8fd9\u4e9b\u5e97\u94fa\u5177\u4f53\u4e0a\u4e4b\u524d\u90fd\u6709\u627f\u79df\u7684\u5546\u6237\uff0c\u90fd\u8d76\u4e0d\u53ca\u62ff\u5230\u5546\u94fa\u8fdb\u884c\u88c5\u6f62\u53ef\u4ee5\u65e9\u65e5\u8425\u4e1a\u2014\u2014\u867d\u7136\u79bb\u5b66\u5e9c\u5df7\u6b63\u5f0f\u5f00\u5f20\u8fd8\u6709\u4e00\u6bb5\u65f6\u95f4\uff0c\u4e0d\u8fc7\u56db\u6392\u6cd5\u56fd\u68a7\u6850\u6d53\u836b\u906e\u853d\u7684\u77f3\u94fa\u5927\u8857\u4e0a\u6709\u8bb8\u591a\u8f67\u9a6c\u8def\u7684\u5b66\u751f\u7537\u5973\u6e38\u8d70\uff0c\u8857\u5fc3\u82b1\u575b\u7684\u6728\u5236\u56f4\u6905\u4e5f\u4e09\u4e09\u4e24\u4e24\u7684\u5750\u7740\u4e9b\u4eba\uff0c\u5012\u662f\u672a\u6765\u7684\u7e41\u8363\u666f\u8c61\u53ef\u4ee5\u60f3\u8c61\u4e86\u3002</p><p>\u3000\u3000\u60f3\u8d77\u8d75\u9633\u8bf4\u8fc7\u7684\u8bdd\uff0c\u5f20\u606a\u5728\u8857\u5fc3\u82b1\u575b\u8fb9\u7684\u56f4\u51f3\u4e0a\u5750\u4e0b\u6765\uff0c\u62e8\u6253\u4ed6\u7238\u7238\u7684\u7535\u8bdd\uff0c\u8bf4\u53ca\u5468\u672b\u53bb\u60e0\u5c71\u7684\u4e8b\u60c5\uff1a\u201c\u8d75\u9633\u597d\u50cf\u77e5\u9053\u4e9b\u5185\u5e55\u6d88\u606f\uff0c\u7701\u91cc\u8003\u8651\u7740\u5c06\u4f60\u5f80\u4e0a\u63d0\u4e00\u63d0\u5462\uff0c\u6211\u80fd\u4e0d\u80fd\u63d0\u524d\u606d\u559c\u4f60\u5440\uff1f\u201d</p><p>\u3000\u3000\u201c\u5012\u4e5f\u4e0d\u5173\u5fc3\u8fd9\u4e2a\uff0c\u624b\u91cc\u7684\u4e8b\u60c5\u4e00\u5806\uff0c\u80fd\u505a\u5b8c\u5c31\u7b97\u4e0d\u9519\u4e86\uff0c\u518d\u8bf4\u65b0\u829c\u8fd9\u8fb9\u6709\u610f\u89c1\u7684\u4eba\u633a\u591a\uff0c\u7701\u91cc\u8fd8\u8981\u8003\u8651\u65b0\u829c\u8fd9\u8fb9\u7684\u610f\u89c1\uff0c\u6211\u5012\u6ca1\u6709\u70ed\u5fc3\u2026\u2026\u6bd4\u8d77\u8fd9\u4e2a\uff0c\u6211\u5012\u5e0c\u671b\u4f60\u5c0f\u5b50\u6709\u7a7a\u5230\u65b0\u829c\u6765\u8f6c\u4e00\u8f6c\uff0c\u4f60\u8fd8\u8bb0\u5f97\u65b0\u829c\u6709\u4f60\u7684\u5bb6\u4e0d\uff1f\u201d</p><p>\u3000\u3000\u63a5\u4e0b\u6765\u5c31\u662f\u5341\u4e00\u4e86\uff0c\u867d\u7136\u56fd\u52a1\u9662\u8981\u5230\u4e5d\u516b\u5e74\u624d\u6b63\u5f0f\u5236\u8ba2\u56fd\u5e86\u3001\u4e94\u4e00\u957f\u5047\u523a\u6fc0\u7ecf\u6d4e\u7684\u653f\u7b56\uff0c\u4f46\u662f\u4e1c\u5927\u4eca\u5e74\u5341\u4e00\u7684\u5047\u671f\u5c31\u957f\u8fbe\u4e94\u5929\u534a\uff0c\u4e0d\u6653\u5f97\u5510\u5a67\u600e\u4e48\u5b89\u6392\uff0c\u8981\u662f\u53bb\u9999\u6e2f\u7684\u8bdd\uff0c\u7b97\u4e00\u7b97\u65f6\u95f4\uff0c\u90fd\u4e0d\u4e00\u5b9a\u80fd\u6709\u65f6\u95f4\u8d76\u53bb\u65b0\u829c\u4f4f\u4e24\u5929\uff0c\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u90fd\u662f\u7236\u6bcd\u6765\u770b\u671b\u5c0f\u5b69\u7684\uff0c\u8981\u4e0d\u6211\u5728\u4e1c\u5927\u8fd9\u8fb9\u7b49\u4f60\u4eec\u6765\u770b\u6211\uff1f\u4f60\u4eec\u53c8\u6ca1\u6709\u5230\u4e03\u8001\u516b\u5341\u9700\u8981\u6211\u53bb\u63a2\u671b\u7684\u5e74\u7eaa\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u6ed1\u5934\uff0c\u201d\u5f20\u77e5\u884c\u5728\u7535\u8bdd\u90a3\u5934\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4e0d\u60f3\u6765\u65b0\u829c\u5c31\u7b97\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u7238\uff0c\u6709\u4e2a\u4e8b\u60c5\u60f3\u8ddf\u4f60\u8bf4\u4e00\u4e0b\uff0c\u201d\u5f20\u606a\u63d0\u5230\u6b63\u4e8b\u4e0a\uff0c\u201c\u4e1c\u5357\u4e9a\u7684\u91d1\u878d\u98ce\u66b4\u4e00\u65f6\u534a\u4f1a\u4e5f\u5e73\u606f\u4e0d\u4e86\uff0c\u7ecf\u6d4e\u8fd8\u4f1a\u7ee7\u7eed\u75b2\u8f6f\u4e0b\u6765\u3002\u518d\u75b2\u8f6f\u4e0b\u53bb\uff0c\u4e2d\u592e\u5c31\u4f1a\u51fa\u53f0\u4e00\u7cfb\u5217\u7684\u7ecf\u6d4e\u523a\u6fc0\u653f\u7b56\uff0c\u4e0d\u63d0\u4f1a\u51fa\u53f0\u653f\u7b56\u9f13\u52b1\u65c5\u6e38\u4e1a\u53d1\u5c55\u7684\u53ef\u80fd\u6027\uff0c\u8fd9\u51e0\u5e74\u5185\u5730\u7684\u7ecf\u6d4e\u4e0a\u6765\uff0c\u56fd\u5185\u7684\u65c5\u6e38\u4e1a\u7684\u786e\u4e5f\u662f\u4e00\u5e74\u6bd4\u4e00\u5e74\u5174\u65fa\u53d1\u8fbe\u4e86\uff0c\u6211\u8fd8\u662f\u5f88\u671f\u5f85\u65b0\u829c\u5728\u7238\u7238\u4f60\u624b\u91cc\u80fd\u6210\u529f\u7684\u8f6c\u53d8\u6210\u65c5\u6e38\u578b\u7684\u82b1\u56ed\u57ce\u5e02\u2026\u2026\u65e2\u7136\u7701\u91cc\u90fd\u6709\u5728\u8003\u8651\u8ba9\u4f60\u518d\u52a8\u4e00\u52a8\u4e86\uff0c\u6211\u4eec\u4e5f\u6ca1\u6709\u5fc5\u8981\u6309\u5175\u4e0d\u52a8\u3002\u6211\u4eec\u4e5f\u4e0d\u641e\u522b\u7684\u52a8\u4f5c\uff0c\u6211\u60f3\u901a\u8fc7\u5c0f\u53d4\u90a3\u91cc\u7ed9\u65b0\u829c\u62e8\u4e24\u5343\u4e07\u8fc7\u53bb\uff0c\u4ee5\u6350\u8d60\u7684\u540d\u4e49\uff0c\u5176\u4e2d\u4e00\u5343\u4e07\u641e\u4e2a\u521d\u7ea7\u6280\u672f\u57f9\u8bad\u5b66\u6821\uff0c\u65b0\u829c\u8bb8\u591a\u4eba\u51fa\u6765\u6253\u5de5\uff0c\u6ca1\u6709\u7ecf\u8fc7\u4e00\u5b9a\u7684\u6280\u672f\u57f9\u8bad\uff0c\u53ea\u80fd\u4ece\u4e8b\u6700\u5e95\u5c42\u7684\u5de5\u79cd\uff0c\u8fd9\u4e00\u5343\u4e07\u5c31\u7528\u8fd9\u4e0a\u9762\uff1b\u8fd8\u6709\u4e00\u5343\u4e07\uff0c\u7238\uff0c\u4f60\u89c9\u5f97\u82b1\u54ea\u91cc\u5408\u9002\u5c31\u82b1\u54ea\u91cc\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e00\u5343\u4e07\u554a\u2026\u2026\u505a\u5176\u4ed6\u4e8b\u60c5\u6216\u8bb8\u662f\u4e0d\u591f\u7684\uff0c\u6211\u770b\u90fd\u7ed9\u65b0\u829c\u7684\u65c5\u6e38\u5b66\u6821\u5427\uff0c\u65b0\u829c\u771f\u8981\u53d1\u5c55\u65c5\u6e38\u7ecf\u6d4e\uff0c\u8f6f\u786c\u4ef6\u8bbe\u65bd\u90fd\u8981\u8ddf\u4e0a\uff0c\u786c\u4ef6\u8bbe\u65bd\u6295\u8d44\uff0c\u4e00\u5343\u4e07\u662f\u8fdc\u8fdc\u4e0d\u591f\u7684\uff0c\u8f6f\u4ef6\u8bbe\u65bd\u4e5f\u8981\u8ddf\u4e0a\uff0c\u8fd9\u4e00\u5343\u4e07\u62ff\u7ed9\u65c5\u6e38\u5b66\u6821\u5408\u9002\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7238\uff0c\u4f60\u8be5\u4e0d\u4f1a\u65e9\u5c31\u5728\u6253\u6211\u7684\u4e3b\u610f\uff0c\u6211\u521a\u63d0\u51fa\u7ed9\u94b1\uff0c\u4f60\u5c31\u60f3\u5230\u94b1\u600e\u4e48\u7528\uff1f\u201d</p><p>\u3000\u3000\u201c\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u9700\u8981\u6253\u4f60\u7684\u4e3b\u610f\uff1f\u201d\u5f20\u77e5\u884c\u5728\u7535\u8bdd\u7b11\u7740\u9a82\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u90a3\u5c31\u7b97\u4e86\uff0c\u65b0\u829c\u53d1\u5c55\u65c5\u6e38\u7ecf\u6d4e\uff0c\u521d\u6b65\u8981\u6295\u5165\u591a\u5c11\u8d44\u91d1\u624d\u591f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c11\u8bf4\u2026\u2026\u201d\u5f20\u77e5\u884c\u521a\u8981\u8131\u53e3\u62a5\u7740\u6570\u636e\uff0c\u53c8\u53ca\u65f6\u6536\u4f4f\u5634\uff0c\u201c\u8fd9\u4e2a\u95ee\u9898\u8fd8\u6ca1\u6709\u8ba4\u771f\u8003\u8651\u8fc7\uff0c\u4ec0\u4e48\u65f6\u5019\u7b49\u4f60\u60f3\u8d77\u65b0\u829c\u8fd8\u6709\u4e00\u5bb6\u65f6\u518d\u8c08\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u884c\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u76f4\u63a5\u7ed9\u5c0f\u53d4\u6302\u7535\u8bdd\uff0c\u9694\u4e24\u5929\u5c31\u5c06\u94b1\u6c47\u5230\u4ed6\u516c\u53f8\u8d26\u4e0a\u2026\u2026\u201d</p><p>\u3000\u3000\u901a\u8fc7\u5851\u9020\u4e91\u6c60\u9152\u4e1a\u5e26\u52a8\u65b0\u829c\u767d\u9152\u53ca\u76f8\u5173\u4ea7\u4e1a\u7684\u5d1b\u8d77\uff0c\u5e76\u4e0d\u9700\u8981\u641e\u592a\u5927\u7684\u82b1\u62db\uff0c\u53ea\u8981\u6301\u7eed\u7684\u8fd0\u8425\u4e0b\u53bb\uff0c\u65b0\u829c\u5e02\u91cc\u6bcf\u5e74\u56e0\u4e3a\u767d\u9152\u4ea7\u4e1a\u7684\u5d1b\u8d77\u800c\u65b0\u589e\u7684\u8d22\u653f\u6536\u5165\u5c31\u8981\u63a5\u8fd1\u4e24\u4e2a\u4ebf\u3002\u5c06\u4eca\u540e\u51e0\u5e74\u7684\u8d22\u653f\u6536\u5165\u589e\u6da8\u900f\u652f\u51fa\u6765\u641e\u65c5\u6e38\u57fa\u5efa\u8bbe\u65bd\uff0c\u52a0\u4e0a\u5efa\u90ba\u5230\u65b0\u829c\u7684\u9ad8\u901f\u516c\u8def\u5df2\u7ecf\u63d0\u524d\u5f00\u5de5\u3001\u5efa\u6210\u4e4b\u540e\uff0c\u5efa\u90ba\u5230\u65b0\u829c\u5c31\u6ca1\u6709\u5fc5\u8981\u518d\u4ece\u5317\u9762\u7ed5\u4e00\u4e2a\u5f2f\uff0c\u8def\u7a0b\u7f29\u77ed\u5230\u4e00\u4e2a\u534a\u5c0f\u65f6\uff0c\u52a0\u4e0a\u660e\u5e74\u5c31\u4f1a\u6b63\u5f0f\u63d0\u51fa\u9ec4\u91d1\u5468\u6982\u5ff5\uff0c\u8fd9\u4e9b\u90fd\u662f\u5bf9\u65b0\u829c\u6b64\u65f6\u5927\u529b\u53d1\u5c55\u65c5\u6e38\u4e1a\u6781\u4e3a\u6709\u5229\u7684\u56e0\u7d20\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u6302\u7535\u8bdd\u7ed9\u5c0f\u53d4\uff0c\u5c0f\u53d4\u8fd9\u4e9b\u5929\u7ec8\u4e8e\u4ece\u5efa\u90ba\u8131\u8eab\u56de\u6d77\u5dde\u4e86\uff0c\u8ddf\u5c0f\u53d4\u8bf4\u4e86\u8981\u901a\u8fc7\u4e16\u7eaa\u9526\u6e56\u5c06\u4e24\u5343\u4e07\u6350\u8d60\u7ed9\u65b0\u829c\u5e02\u91cc\u3002</p><p>\u3000\u3000\u201c\u5976\u5976\u7684\uff0c\u4e16\u7eaa\u9526\u6e56\u8fc7\u53bb\u4e00\u5e74\u5728\u65b0\u829c\u635e\u5230\u7684\u94b1\u90fd\u6ca1\u6709\u516b\u767e\u4e07\u5462\uff0c\u524d\u4e00\u6b21\u4e3a\u57ce\u9632\u6c5f\u5824\u7684\u4e8b\u60c5\u5c31\u6350\u4e86\u4e00\u56de\u94b1\uff0c\u8fd9\u6b21\u53c8\u6350\u4e24\u5343\u4e07\uff0c\u522b\u4eba\u90fd\u4ee5\u4e3a\u6211\u4eec\u4e16\u7eaa\u9526\u6e56\u94b1\u591a\u4eba\u50bb\u5462\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u4e48\u62b1\u6028\uff0c\u597d\u50cf\u8fd9\u94b1\u662f\u4f60\u51fa\u7684\u4f3c\u7684\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e0d\u662f\u6ca1\u6cd5\u5b50\u8ddf\u90a3\u4e9b\u4e0d\u660e\u771f\u76f8\u7684\u7fa4\u4f17\u89e3\u91ca\u5417\uff1f\u201d\u5f20\u77e5\u975e\u5728\u7535\u8bdd\u90a3\u91cc\u723d\u6717\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u597d\u4e86\uff0c\u6211\u77e5\u9053\u8fd9\u56de\u4e8b\uff0c\u56de\u5934\u8ddf\u8001\u90b5\u3001\u76db\u9752\u4ed6\u4eec\u77e5\u4f1a\u4e00\u58f0\u3002\u5177\u4f53\u4ec0\u4e48\u4e8b\u60c5\u4f60\u76f4\u63a5\u627e\u7fdf\u4e39\u9752\u4e0d\u5c31\u884c\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u554a\uff0c\u90a3\u4e2a\u59ff\u8272\u8273\u4e3d\u3001\u6027\u611f\u59a9\u5a9a\u7684\u5973\u4eba\u554a\uff0c\u5f20\u606a\u62ff\u7740\u624b\u673a\u6572\u4e86\u6572\u7709\u5934\uff0c\u60f3\u7740\u7fdf\u4e39\u9752\u8eab\u4e0a\u603b\u6709\u4e00\u79cd\u5176\u4ed6\u5973\u4eba\u4e0d\u53ca\u7684\u6210\u719f\u4e30\u8174\u7684\u9b45\u60d1\u97f5\u5473\uff0c\u4e5f\u7684\u786e\u597d\u4e45\u90fd\u6ca1\u6709\u8054\u7cfb\u5979\u4e86\uff0c\u6bd5\u7adf\u65b0\u829c\u7684\u4e16\u7eaa\u9526\u6e56\u5927\u9152\u5e97\u53c8\u4e0d\u662f\u9526\u6e56\u65d7\u4e0b\u7684\u4e1a\u52a1\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u5fc5\u8981\u64cd\u90a3\u4efd\u95f2\u5fc3\uff0c\u8fd8\u597d\u624b\u673a\u91cc\u5b58\u6709\u5d14\u4e39\u9752\u7684\u8054\u7cfb\u65b9\u5f0f\uff0c\u62e8\u7535\u8bdd\u8fc7\u53bb\uff0c\u542c\u5230\u5c31\u662f\u536b\u5170\u6e05\u751c\u723d\u5fc3\u7684\u58f0\u97f3\u3002</p><p>\u3000\u3000\u536b\u5170\u4e5f\u6ca1\u6709\u7b49\u5f20\u606a\u5f00\u58f0\uff0c\u5c31\u76f4\u63a5\u8bf4\uff1a\u201c\u4f60\u627e\u7fdf\u603b\u662f\u5427\uff0c\u7fdf\u603b\u73b0\u5728\u6709\u4e8b\uff0c\u4e00\u4f1a\u513f\u8ba9\u7fdf\u603b\u7ed9\u4f60\u6253\u56de\u53bb\u884c\u4e0d\u884c\uff0c\u662f\u4e0d\u662f\u8fd9\u4e2a\u53f7\u7801\uff1f\u201d</p><p>\u3000\u3000\u201c\u5443\uff0c\u7fdf\u603b\u73b0\u5728\u6709\u4ec0\u4e48\u4e8b\u60c5\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u536b\u5170\u5728\u7535\u8bdd\u90a3\u5934\u96c0\u8dc3\u7684\u53eb\u4e86\u8d77\u6765\uff0c\u90fd\u5fd8\u4e86\u8981\u5148\u8ddf\u5f20\u606a\u6253\u58f0\u62db\u547c\uff0c\u4e5f\u4e0d\u6342\u8bed\u7b52\uff0c\u5c31\u5927\u558a\uff0c\u201c\u606a\u5c11\u7684\u7535\u8bdd\uff0c\u4f60\u6709\u6ca1\u6709\u8131\u8863\u88f3\u5462\uff0c\u6211\u5c06\u7535\u8bdd\u62ff\u8fdb\u6765\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u63a5\u7740\u542c\u89c1\u95e8\u5f00\u542f\u3001\u51b2\u6dcb\u5934\u5f80\u5916\u55b7\u6c34\u7684\u58f0\u97f3\uff0c\u83ab\u975e\u7fdf\u4e39\u9752\u8fd9\u65f6\u5019\u5728\u6d17\u6fa1\uff1f</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7684\u58f0\u97f3\u603b\u662f\u63c9\u8fdb\u4e86\u8bb8\u591a\u5a9a\u60d1\u7684\u751c\u67d4\uff0c\u4e0d\u6653\u5979\u8ddf\u522b\u4eba\u8bf4\u8bdd\u662f\u4e0d\u662f\u8fd9\u6837\uff0c\u542c\u8d77\u6765\u5fc3\u91cc\u9165\u9165\u7684\uff1a\u201c\u606a\u5c11\u8fd9\u65f6\u5019\u600e\u4e48\u4f1a\u60f3\u8d77\u6211\u6765\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u5728\u60f3\u7fdf\u4e39\u9752\u5728\u7535\u8bdd\u90a3\u5934\u662f\u5c06\u8131\u672a\u8131\u3001\u88d9\u886b\u534a\u63a9\u8fd8\u662f\u5a07\u8eaf\u8d64\u88f8\uff1f</p><p>\u3000\u3000\u201c\u6709\u4ef6\u4e8b\u60c5\u60f3\u8bf7\u4f60\u5e2e\u5fd9\uff0c\u4e00\u65f6\u534a\u4f1a\u4e5f\u8bf4\u4e0d\u6e05\u695a\uff0c\u8981\u4e0d\u7b49\u4f60\u6d17\u5b8c\u6fa1\u4f60\u518d\u6253\u8fd9\u4e2a\u7535\u8bdd\u7ed9\u6211\uff1f\u201d</p><p>\u3000\u3000\u201c\u606a\u5c11\u5728\u7701\u57ce\u4e0d\uff1f\u201d\u7fdf\u4e39\u9752\u5728\u7535\u8bdd\u90a3\u5934\u95ee\u3002</p><p>\u3000\u3000\u201c\u5728\u554a\uff0c\u521a\u521a\u4ece\u5916\u9762\u56de\u5efa\u90ba\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u6d17\u6fa1\u662f\u51c6\u5907\u9a6c\u4e0a\u5c31\u53bb\u7701\u57ce\uff0c\u8981\u6709\u4ec0\u4e48\u6307\u793a\uff0c\u7b49\u6211\u5230\u7701\u57ce\uff0c\u606a\u5c11\u60a8\u518d\u7ed9\u6211\u4e0b\u8fbe\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6837\u554a\uff0c\u90a3\u884c\uff0c\u201d\u4e5f\u4e0d\u6653\u5f97\u7fdf\u4e39\u9752\u5468\u65e5\u4e0b\u5348\u8d76\u5230\u7701\u57ce\u6765\u4f1a\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4ece\u65b0\u829c\u91cc\u5750\u8f66\u8fc7\u6765\u8981\u56db\u4e2a\u5c0f\u65f6\uff0c\u201c\u4f60\u8fc7\u6765\u76f4\u63a5\u5230\u4e1c\u5927\u5357\u95e8\u6253\u6211\u7535\u8bdd\uff0c\u6211\u8bf7\u4f60\u4eec\u5403\u665a\u996d\u2026\u2026\u201d</p><p>\u3000\u3000\u6302\u4e86\u7535\u8bdd\uff0c\u5f20\u606a\u7ad9\u8d77\u6765\u5f80\u5bbf\u820d\u8d70\u53bb\uff1b\u524d\u4e9b\u5929\u5c06\u81ea\u884c\u8f66\u4e22\u5973\u751f\u5bbf\u820d\u697c\u524d\u7b2c\u4e8c\u5929\u518d\u53bb\u627e\u5c31\u5931\u4e86\u8e2a\u5f71\uff0c\u73b0\u5728\u53ea\u80fd\u6b65\u884c\u8d70\u56de\u5bbf\u820d\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u79cb\u540e\u5468\u65e5\u4e0b\u5348\u7684\u9633\u5149\u548c\u7199\uff0c\u4ece\u71d5\u5f52\u6e56\u9762\u4e0a\u5439\u6765\u7684\u5fae\u98ce\u4e5f\u6ca1\u6709\u4e4b\u524d\u53e4\u602a\u7684\u523a\u9f3b\u5473\u9053\uff0c\u8d70\u5728\u6d53\u5bc6\u7684\u68a7\u6850\u6811\u836b\u4e0b\uff0c\u770b\u7740\u5076\u5c14\u7ecf\u8fc7\u9753\u4e3d\u7684\u9752\u6625\u5973\u751f\uff0c\u8fd9\u6837\u7684\u6b65\u884c\u7684\u786e\u7b97\u4e0d\u4e0a\u592a\u96be\u53d7\u3002</p><p>\u3000\u3000\u6821\u56ed\u7684\u4e1c\u7247\u662f\u751f\u6d3b\u533a\uff0c\u672c\u79d1\u751f\u5bbf\u820d\u3001\u7814\u7a76\u751f\u5bbf\u820d\u53ca\u516c\u5bd3\u3001\u56db\u4e2a\u5b66\u751f\u98df\u5802\u3001\u6559\u5de5\u98df\u5802\u3001\u5b66\u751f\u6d74\u5ba4\u3001\u5f00\u6c34\u623f\u3001\u6821\u56ed\u5546\u5e97\u3001\u7f51\u7403\u573a\u3001\u5ba4\u5185\u4f53\u80b2\u9986\u90fd\u96c6\u4e2d\u5728\u8fd9\u4e00\u7247\uff0c\u4e0d\u8fc7\u6700\u96c6\u4e2d\u7684\u8fd8\u662f\u5408\u6b22\u5c71\u7684\u4e1c\u9e93\uff0c\u6700\u5927\u7684\u4e24\u4e2a\u98df\u5802\u3001\u6d74\u5ba4\u3001\u5f00\u6c34\u623f\u8fd8\u6709\u4e1c\u5927\u6700\u957f\u7684\u6d77\u62a5\u5899\u90fd\u96c6\u4e2d\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u8d70\u5230\u6d77\u62a5\u5899\u90a3\u91cc\uff0c\u6b63\u662f\u5f00\u6c34\u623f\u4f9b\u6c34\u7684\u65f6\u95f4\uff0c\u5f88\u591a\u5b66\u751f\u90fd\u63d0\u7740\u6c34\u58f6\u7ad9\u5728\u6d77\u62a5\u5899\u524d\u641c\u7d22\u6709\u7528\u6216\u6709\u8da3\u7684\u4fe1\u606f\uff0c\u821e\u4f1a\u4e4b\u7c7b\u7684\u5b66\u751f\u7ec4\u7ec7\u901a\u77e5\u90fd\u4f1a\u8d34\u5230\u6d77\u62a5\u5899\u4e0a\uff0c\u5f20\u606a\u4e5f\u95f2\u9002\u7684\u7ad9\u5728\u6d77\u62a5\u5899\u524d\u6d4f\u89c8\uff0c\u6d77\u62a5\u5899\u4e0a\u5f20\u8d34\u4e86\u8bb8\u591a\u5b66\u751f\u7ec4\u7ec7\u62db\u65b0\u7684\u6d77\u62a5\uff0c\u90fd\u662f\u6821\u62a5\u3001\u56e2\u62a5\u3001\u6821\u7535\u89c6\u53f0\u3001\u6821\u7535\u53f0\u3001\u5b66\u751f\u4f1a\u3001\u767b\u5c71\u534f\u4f1a\u3001\u821e\u8e48\u534f\u4f1a\u7b49\u7b49\u8bf8\u5982\u6b64\u7c7b\u7684\u5b66\u751f\u7ec4\u7ec7\u3002</p><p>\u3000\u3000\u5f20\u606a\u524d\u4e16\u5728\u4e1c\u5927\u4e0d\u60f3\u53d7\u522b\u4eba\u7684\u95f2\u6c14\uff0c\u5c31\u81ea\u5df1\u7ec4\u7ec7\u4e86\u4e00\u4e2a\u56f4\u68cb\u534f\u4f1a\uff0c\u53ea\u8981\u62db\u6536\u7684\u5b66\u751f\u4f1a\u5458\u8d85\u8fc7\u4e00\u5b9a\u7684\u4eba\u6570\u3001\u7ec4\u7ec7\u7684\u4e3b\u9898\u53c8\u8db3\u591f\u7684\u79ef\u6781\u5411\u4e0a\uff0c\u6821\u56e2\u59d4\u57fa\u672c\u4e0a\u90fd\u4f1a\u4e88\u4ee5\u901a\u8fc7\u5e76\u63d0\u4f9b\u4e00\u4e2a\u6d3b\u52a8\u7684\u573a\u6240\uff0c\u8fd8\u6709\u4e00\u5b9a\u7684\u6d3b\u52a8\u7ecf\u8d39\uff0c\u4e0d\u8fc7\u5f20\u606a\u7684\u76ee\u7684\u8fd8\u662f\u4ece\u62db\u5f85\u7684\u534f\u4f1a\u4f1a\u5458\u90a3\u91cc\u9a97\u4e9b\u4f1a\u5458\u8d39\u5927\u5bb6\u4e00\u8d77\u5403\u5403\u559d\u559d\u5ea6\u8fc7\u6109\u5feb\u7684\u5927\u5b66\u65f6\u5149\u3002</p></article>"}}}]);
