(window.webpackJsonp=window.webpackJsonp||[]).push([[795],{818:function(p){p.exports={chapterTitle:null,title:"\u7b2c793\u7ae0 \u51fa\u53e3\u6076\u6c14",content:"<article><h2>\u7b2c793\u7ae0 \u51fa\u53e3\u6076\u6c14</h2><p>\u3000\u3000\u5728\u8d75\u6709\u4f26\u7684\u6781\u529b\u9080\u8bf7\u4e0b\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u7b54\u5e94\u968f\u8d75\u6709\u4f26\u53bb\u91d1\u5c71\u56fd\u9645\u4f1a\u5c55\u4e2d\u5fc3\u8d70\u4e00\u8d70\uff0c\u90a3\u91cc\u662f\u7535\u5b50\u4ea7\u4e1a\u53d1\u5c55\u8bba\u575b\u7684\u4e3b\u4f1a\u573a\u3002</p><p>\u3000\u3000\u91d1\u5c71\u7684\u57ce\u5e02\u5efa\u8bbe\u8981\u843d\u540e\u4e8e\u5efa\u90ba\uff0c\u8ddf\u60e0\u5c71\u76f8\u6bd4\u8fd8\u6709\u4e9b\u5dee\u8ddd\uff0c\u65b0\u843d\u6210\u7684\u56fd\u9645\u4f1a\u5c55\u4e2d\u5fc3\u7531\u4e94\u5ea7\u5927\u578b\u4f1a\u5c55\u5efa\u7b51\u6784\u6210\uff0c\u5ea7\u843d\u5728\u91d1\u5c71\u5e02\u57ce\u897f\u7684\u90ca\u533a\uff0c\u4e94\u5ea7\u4f1a\u5c55\u5efa\u7b51\u73af\u62b1\u7740\u4e00\u5ea7\u6570\u4e07\u5e73\u65b9\u7c73\u7684\u4e2d\u5fc3\u5e7f\u573a\uff0c\u6c14\u6d3e\u975e\u51e1\u3002</p><p>\u3000\u3000\u8d75\u6709\u4f26\u7279\u610f\u8ba9\u8f66\u5f00\u5230\u4f1a\u5c55\u4e2d\u5fc3\u5357\u8fb9\u7684\u4e00\u5ea7\u767e\u5341\u7c73\u9ad8\u7684\u5c71\u4e0a\uff0c\u7ad9\u5728\u5c71\u9876\u7684\u89c2\u666f\u53f0\u51ed\u680f\u5f80\u4e0b\u770b\uff0c\u89c6\u89c9\u4e0a\u7684\u611f\u89e6\u8981\u66f4\u5f3a\u70c8\u4e00\u4e9b\uff0c\u53ef\u60dc\u5c71\u7684\u5317\u9762\uff0c\u9664\u4e86\u8fd9\u5ea7\u6c14\u6d3e\u975e\u51e1\u7684\u4f1a\u5c55\u4e2d\u5fc3\u4e4b\u5916\uff0c\u5468\u8fb9\u5c31\u6ca1\u6709\u4ec0\u4e48\u50cf\u6837\u7684\u5efa\u7b51\u4e86\uff0c\u8fd8\u96f6\u96f6\u6563\u6563\u7684\u6563\u843d\u7740\u4e00\u4e9b\u6ca1\u6709\u62c6\u8fc1\u7684\u6c11\u5c45\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u5728\u4f1a\u5c55\u4e2d\u5fc3\u7684\u4e1c\u8fb9\u6709\u4e00\u5904\u6b63\u65bd\u5de5\u4e2d\u7684\u5927\u578b\u5de5\u5730\uff0c\u518d\u5f80\u4e1c\u7565\u8fc7\u4e00\u4e9b\u6c11\u5c45\u5c31\u662f\u91d1\u5c71\u5e02\u7684\u4e3b\u57ce\u533a\u4e86\u3002</p><p>\u3000\u3000\u201c\u8fd9\u91cc\u5c06\u662f\u91d1\u5c71\u5e02\u65b0\u57ce\u533a\u7684\u4e2d\u5fc3\uff0c\u5e02\u59d4\u3001\u5e02\u653f\u5e9c\u4ee5\u53ca\u4e0b\u5c5e\u7684\u884c\u653f\u673a\u6784\u90fd\u8981\u4ece\u4ea4\u901a\u62e5\u6324\u7684\u57ce\u4e2d\u533a\u8fc1\u51fa\u6765\uff0c\u201d\u8d75\u6709\u4f26\u8ddf\u5f20\u606a\u4ecb\u7ecd\u4f1a\u5c55\u4e2d\u5fc3\u5468\u8fb9\u65b0\u57ce\u533a\u7684\u89c4\u5212\u84dd\u56fe\uff0c\u6709\u7740\u6307\u70b9\u6c5f\u5c71\u7684\u6c14\u6982\uff0c\u4ed6\u6307\u7740\u4e1c\u9762\u90a3\u5904\u6b63\u65bd\u5de5\u4e2d\u7684\u5927\u578b\u5de5\u5730\uff0c\u8bf4\u9053\uff0c\u201c\u90a3\u91cc\u5c31\u662f\u65b0\u7684\u5e02\u653f\u5927\u697c\u53ca\u5e7f\u573a\u5de5\u7a0b\u2026\u2026\u8fd9\u4e2a\u9879\u76ee\u65e9\u5c31\u7531\u6211\u7684\u524d\u4efb\u63d0\u51fa\u6765\u4e86\uff0c\u7531\u4e8e\u79cd\u79cd\u539f\u56e0\u4e00\u76f4\u62d6\u7740\u6ca1\u6709\u53bb\u5b9e\u65bd\uff0c\u6211\u5fc3\u91cc\u5c31\u60f3\uff0c\u65b0\u57ce\u533a\u8981\u5438\u5f15\u5f00\u53d1\u5546\u8fc7\u6765\u7684\u6295\u8d44\uff0c\u5149\u6709\u8fd9\u4f1a\u5c55\u4e2d\u5fc3\u8fd8\u4e0d\u591f\uff0c\u5f00\u53d1\u5546\u7684\u773c\u775b\u90fd\u76ef\u7740\u65b0\u5e02\u653f\u5927\u697c\u7684\u5efa\u8bbe\uff0c\u5c31\u662f\u7838\u9505\u5356\u94c1\u4e5f\u8981\u5c06\u65b0\u5e02\u653f\u5927\u697c\u5148\u5efa\u8d77\u6765\uff0c\u8fd9\u624d\u6709\u4eca\u5929\u7684\u8fdb\u5ea6\u2026\u2026\u201d</p><p>\u3000\u3000\u4ee5\u89c4\u6a21\u7684\u884c\u653f\u673a\u6784\u8fc1\u79fb\u6765\u805a\u96c6\u65b0\u57ce\u533a\u7684\u4eba\u6c14\uff0c\u4ee5\u6b64\u4fc3\u8fdb\u65b0\u57ce\u533a\u7684\u5730\u4ea7\u6295\u8d44\uff0c\u62c9\u52a8\u65b0\u57ce\u533a\u571f\u5730\u4ef7\u683c\u3001\u623f\u4ea7\u4ef7\u683c\u6025\u901f\u4e0a\u5347\uff0c\u8fd9\u4e0d\u8fc7\u4eca\u540e\u5341\u5e74\u56fd\u5185\u57ce\u5e02\u5efa\u8bbe\u7684\u60ef\u7528\u4f0e\u4fe9\u7f62\u4e86\uff0c\u5f20\u606a\u5bf9\u6b64\u4e0d\u4e88\u7f6e\u8bc4\uff0c\u5341\u5206\u6709\u8010\u5fc3\u7684\u5f53\u4e00\u540d\u503e\u542c\u8005\uff0c\u503e\u542c\u8d75\u6709\u4f26\u6292\u53d1\u4ed6\u62c5\u4efb\u91d1\u5c71\u5e02\u957f\u4e4b\u540e\u7684\u62b1\u8d1f\u3002\u53f6\u5efa\u658c\u5076\u5c14\u4f1a\u63d2\u51e0\u53e5\u8bdd\uff0c\u8eab\u4e3a\u4e16\u7eaa\u9526\u6e56\u7684\u5927\u80a1\u4e1c\u3001\u5bf9\u5730\u4ea7\u6295\u8d44\u6709\u7740\u6781\u6d53\u539a\u5174\u8da3\u7684\u4ed6\uff0c\u81ea\u7136\u559c\u6b22\u5728\u57ce\u5e02\u5efa\u8bbe\u4e0a\u6709\u5927\u624b\u7b14\u3001\u6709\u5927\u6c14\u9b44\u7684\u5b98\u5458\u3002</p><p>\u3000\u3000\u7ad9\u5728\u5c71\u9876\uff0c\u8f6c\u8eab\u5f80\u5357\u8fdc\u773a\uff0c\u80fd\u770b\u89c1\u6b63\u6cf0\u96c6\u56e2\u4e0e\u5609\u4fe1\u7535\u5b50\u8054\u5408\u6295\u8d44\u5e76\u63a8\u52a8\u7684\u7535\u5b50\u5de5\u4e1a\u5236\u9020\u57fa\u5730\u9879\u76ee\u7684\u5de5\u5730\u3002\u6d51\u6d4a\u6d69\u8361\u7684\u996e\u9a6c\u6cb3\u5c31\u4ee5\u9752\u5c71\u4e3a\u7b14\u4e66\u5199\u7684\u201c\u4e00\u637a\u201d\u6a2a\u4e98\u5728\u5de5\u5730\u4e0e\u89c4\u5212\u4e2d\u7684\u65b0\u57ce\u533a\u4e4b\u95f4\uff0c\u996e\u9a6c\u6cb3\u7684\u897f\u5357\u662f\u91d1\u5c71\u7684\u9ad8\u65b0\u533a\uff0c\u4e1c\u5317\u662f\u65b0\u57ce\u533a\uff0c\u6709\u5ea7\u65b0\u5efa\u7684\u516c\u8def\u5927\u6865\u8fde\u63a5\u7740\u996e\u9a6c\u6cb3\u7684\u4e24\u5cb8\u571f\u5730\u3002\u8fd9\u5ea7\u5c71\u4e0d\u591f\u9ad8\uff0c\u52a0\u4e0a\u524d\u65b9\u6709\u8fde\u4f0f\u4e0d\u5b9a\u7684\u5c71\u5cad\u6321\u7740\uff0c\u66f4\u8fdc\u5904\u7684\u91d1\u5c71\u6e56\u53ea\u80fd\u770b\u5230\u9ec4\u7eff\u95f4\u67d3\u7684\u4e00\u7247\u6c34\u5929\u4e4b\u8272\u3002</p><p>\u3000\u3000\u5f20\u606a\u6307\u7740\u90a3\u8fb9\u7684\u5de5\u5730\uff0c\u95ee\u8d75\u6709\u4f26\uff1a\u201c\u91d1\u5c71\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u9009\u5740\u4e5f\u5728\u90a3\u8fb9\uff1f\u201d</p><p>\u3000\u3000\u201c\u5728\u7535\u5b50\u56ed\u7684\u5317\u9762\uff0c\u4e0e\u7535\u5b50\u56ed\u7d27\u6328\u7740\uff0c\u8fc7\u4e00\u6bb5\u65f6\u95f4\u5c31\u52a8\u5de5\u4e86\uff0c\u201d\u8d75\u6709\u4f26\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u90a3\u91cc\u662f\u521a\u5e76\u5165\u9ad8\u65b0\u533a\u7684\u571f\u5730\uff0c\u4e0e\u89c4\u5212\u4e2d\u7684\u65b0\u57ce\u533a\u7d27\u6328\u7740\uff0c\u9526\u6e56\u82e5\u80fd\u8fd1\u671f\u5185\u51b3\u5b9a\u6295\u8d44\u91d1\u5c71\uff0c\u53ef\u4ee5\u5728\u6cb3\u8fb9\u62ff\u4e00\u5757\u5730\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u76ee\u5149\u51dd\u7740\u6a2a\u4e98\u5728\u5927\u5730\u6000\u62b1\u91cc\u7684\u996e\u9a6c\u6cb3\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u53f6\u5efa\u658c\u5728\u4e00\u65c1\u5c0f\u58f0\u8ddf\u5468\u6e38\u8bf4\u8bdd\uff1a\u201c\u6cb3\u6c34\u7684\u989c\u8272\u6709\u4e9b\u4e0d\u5927\u5bf9\u52b2\u2026\u2026\u201d</p><p>\u3000\u3000\u6d4a\u9ec4\u7684\u6cb3\u9762\u4e0a\u6cdb\u7740\u4e00\u7247\u7247\u7684\u767d\u8272\u6d6e\u6cab\uff0c\u996e\u9a6c\u6cb3\u4e0a\u6e38\u6709\u4f01\u4e1a\u5728\u8086\u65e0\u5fcc\u60ee\u7684\u6392\u6c61\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u770b\u8d75\u6709\u4f26\u542c\u4e86\u53f6\u5efa\u658c\u8fd9\u8bdd\u4f1a\u6709\u4ec0\u4e48\u53cd\u5e94\uff0c\u8f6c\u5934\u770b\u53bb\uff0c\u8d75\u6709\u4f26\u6b63\u770b\u5411\u6765\u65f6\u7684\u5761\u9053\uff0c\u6709\u56db\u90e8\u8f7f\u8f66\u6b63\u6cbf\u7740\u5761\u5ea6\u4e0d\u5927\u7684\u6ce5\u8def\u5f00\u8fc7\u6765\u3002\u90a3\u56db\u90e8\u8f66\u9a76\u5230\u8fd1\u5904\u624d\u6ce8\u610f\u5230\u5c71\u4e0a\u6709\u4eba\uff0c\u5728\u79bb\u5f20\u606a\u4ed6\u4eec\u8fd8\u6709\u56db\u4e94\u5341\u7c73\u7684\u5730\u65b9\u505c\u4e0b\u6765\uff0c\u5468\u747e\u745c\u3001\u845b\u5efa\u5fb7\u3001\u8c22\u6c49\u9756\u3001\u845b\u660e\u4fe1\u3001\u6797\u96ea\u7b49\u4eba\u4f9d\u6b21\u4e0b\u8f66\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f53\u7136\u80fd\u660e\u767d\u5468\u747e\u745c\u4e3a\u4ec0\u4e48\u8981\u5e26\u7740\u4eba\u5230\u8fd9\u5c71\u5c97\u4e0a\u6765\u3002\u8c01\u638c\u63e1\u7740\u4e00\u5ea7\u57ce\u5e02\uff0c\u90fd\u4f1a\u6709\u5411\u5916\u4eba\u70ab\u8000\u4e0e\u63cf\u7ed8\u672a\u6765\u53d1\u5c55\u84dd\u56fe\u7684\u5fc3\u601d\u3002\u5bf9\u4e8e\u5e02\u59d4\u4e66\u8bb0\u6216\u5e02\u957f\u800c\u8a00\uff0c\u8fd9\u5ea7\u57ce\u5e02\u5927\u6982\u5c31\u662f\u4ed6\u4eec\u6700\u5962\u534e\u7684\u753b\u677f\u4e86\uff0c\u53c8\u4e0d\u662f\u4ec0\u4e48\u7f9e\u4e8e\u89c1\u4eba\u7684\u5fc3\u601d\u3002\u53ef\u60dc\u540c\u4e00\u5f20\u753b\u677f\u8981\u7ed9\u4e24\u4e2a\u5fc3\u601d\u5404\u5f02\u3001\u4e89\u7740\u62ff\u753b\u7b14\u7684\u4eba\u53bb\u753b\u3002</p><p>\u3000\u3000\u80ae\u810f\u7684\u5b98\u573a\u6216\u8bb8\u5145\u6ee1\u7740\u5c14\u865e\u6211\u8bc8\u3001\u9634\u8c0b\u7b97\u8ba1\u3001\u52fe\u5fc3\u534a\u89d2\uff0c\u5f53\u9762\u6495\u7834\u8138\u7684\u60c5\u5f62\u5012\u662f\u5c11\u89c1\uff0c\u5468\u747e\u745c\u770b\u5230\u8d75\u6709\u4f26\u966a\u540c\u5f20\u606a\u7ad9\u5728\u5c71\u9876\uff0c\u867d\u7136\u79bb\u56db\u4e94\u5341\u7c73\u5c31\u4e0b\u4e86\u8f66\uff0c\u4e0b\u8f66\u540e\u8fd8\u662f\u671d\u8fd9\u8fb9\u8d70\u6765\uff0c\u8fdc\u8fdc\u7684\u5c31\u558a\uff1a\u201c\u8d75\u5e02\u957f\u4e5f\u5e26\u7740\u5ba2\u4eba\u8fc7\u6765\u770b\u65b0\u57ce\u533a\u7684\u53d1\u5c55\u89c4\u5212\u554a\uff1f\u90a3\u6b63\u597d\uff0c\u6211\u8eab\u8fb9\u8fd9\u51e0\u4f4d\u5ba2\u4eba\uff0c\u4e5f\u52b3\u70e6\u8d75\u5e02\u957f\u60a8\u518d\u8d39\u795e\u7ed9\u4ed6\u4eec\u8bb2\u89e3\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u8d75\u6709\u4f26\u542c\u4e86\u5468\u747e\u745c\u8c8c\u4f3c\u8bf7\u6c42\u3001\u5b9e\u9645\u6307\u4f7f\u7684\u8bdd\uff0c\u5fc3\u91cc\u9690\u6709\u4e0d\u5feb\uff0c\u6ca1\u6709\u8868\u73b0\u5728\u8138\u4e0a\uff0c\u8138\u4e0a\u8fd8\u5806\u51fa\u7b11\u5bb9\u6765\uff0c\u5f80\u524d\u8fce\u4e86\u51e0\u6b65\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u540e\u7684\u65b0\u57ce\u533a\u5efa\u8bbe\uff0c\u5468\u4e66\u8bb0\u4f60\u662f\u603b\u7b56\u5212\u4eba\u3001\u603b\u8bbe\u8ba1\u5e08\uff0c\u6211\u6765\u8bb2\u89e3\u4e0d\u662f\u55a7\u5bbe\u593a\u4e3b\u5417\uff1f\u201d</p><p>\u3000\u3000\u5468\u747e\u745c\u8138\u8272\u7565\u6c89\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4f60\u5148\u8bf4\uff0c\u6211\u6765\u8865\u5145\u597d\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u539f\u5730\u542c\u4e86\u4ed6\u4eec\u8fd9\u4e24\u4e09\u53e5\u5bf9\u8bdd\uff0c\u53ea\u662f\u6de1\u6de1\u4e00\u7b11\uff0c\u5fc3\u60f3\u5468\u747e\u745c\u8fd8\u662f\u9890\u6307\u6c14\u4f7f\u3001\u4e8b\u4e8b\u60f3\u538b\u4eba\u4e00\u5934\u7684\u813e\u6c14\u3002\u5728\u60e0\u5c71\u65f6\uff0c\u5979\u662f\u5e02\u957f\uff0c\u8d75\u9633\u662f\u5e02\u59d4\u4e66\u8bb0\uff0c\u5c31\u662f\u8fd9\u6837\uff0c\u5979\u7684\u813e\u6c14\u5df2\u7ecf\u5f88\u8ba9\u8d75\u9633\u5934\u75bc\u4e86\uff1b\u5728\u91d1\u5c71\uff0c\u5979\u662f\u4e66\u8bb0\uff0c\u8d75\u6709\u4f26\u662f\u5e02\u957f\uff0c\u771f\u6709\u8d75\u6709\u4f26\u597d\u53d7\u7684\u3002</p><p>\u3000\u3000\u773c\u770b\u8d75\u6709\u4f26\u4e0b\u4e0d\u4e86\u53f0\u9762\uff0c\u65c1\u8fb9\u5206\u7ba1\u5de5\u4e1a\u7684\u526f\u5e02\u957f\u8096\u4e3a\u519b\u60f3\u8981\u7ad9\u51fa\u6765\u6253\u5706\u573a\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u5728\u4ed6\u8eab\u540e\u7709\u5934\u4e00\u626c\uff0c\u8f7b\u7b11\u7740\u8bf4\uff1a\u201c\u521a\u624d\u542c\u8d75\u5e02\u957f\u8bf4\u4e86\u5f88\u4e45\uff0c\u6b63\u60f3\u542c\u5468\u4e66\u8bb0\u60a8\u8865\u5145\u5462\uff01\u201d\u6446\u51fa\u4e00\u4ed8\u6d17\u8033\u606d\u7684\u6a21\u6837\u5fae\u4fa7\u7740\u8111\u888b\u3002</p><p>\u3000\u3000\u5468\u747e\u745c\u770b\u5230\u8d75\u6709\u4f26\u966a\u540c\u9526\u6e56\u4f17\u4eba\u5728\u5c71\u9876\uff0c\u5fc3\u4e2d\u5c31\u6709\u4e9b\u4e0d\u60a6\uff0c\u518d\u8ba9\u5f20\u606a\u4e00\u64a9\u62e8\uff0c\u80f8\u81c6\u95f4\u7684\u6c14\u8840\u7ffb\u6d8c\uff0c\u76ee\u5149\u8ddf\u5200\u5b50\u4f3c\u7684\u5250\u8fc7\u6765\uff0c\u770b\u5230\u5f20\u606a\u8138\u4e0a\u8c8c\u4f3c\u65e0\u8f9c\u7684\u5fae\u7b11\uff0c\u53c8\u4e0d\u80fd\u771f\u7684\u5c31\u5f53\u573a\u53d1\u4f5c\u3002</p><p>\u3000\u3000\u8d75\u6709\u4f26\u5012\u662f\u5fc3\u60c5\u5927\u597d\uff0c\u7b97\u662f\u51fa\u4e86\u4e00\u53e3\u6076\u6c14\u3002\u4ed6\u505a\u597d\u8ddf\u8fd9\u4e2a\u86ee\u5a46\u5b50\u957f\u671f\u6297\u6218\u7684\u51c6\u5907\uff0c\u5f53\u7136\u6e05\u695a\u9526\u6e56\u4e0e\u8c22\u5bb6\u4e4b\u95f4\u96be\u4ee5\u5316\u89e3\u7684\u6069\u6028\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u64a9\u62e8\u4eba\u7684\u529f\u592b\u5012\u662f\u4e86\u5f97\uff0c\u4e5f\u4e0d\u542d\u58f0\u8bf4\u8bdd\uff0c\u53ea\u662f\u51b7\u773c\u65c1\u89c2\u5468\u747e\u745c\u4f1a\u4e0d\u4f1a\u771f\u8981\u8865\u5145\u7684\u8bf4\u4e24\u53e5\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u3001\u5468\u6e38\u542c\u4e86\u8fd9\u8bdd\u89e3\u6c14\u3002\u7fdf\u4e39\u9752\u7ad9\u5728\u5f20\u606a\u8eab\u8fb9\uff0c\u770b\u7740\u4ed6\u8138\u4e0a\u65e0\u8f9c\u7684\u6d45\u7b11\uff0c\u53cc\u624b\u62b1\u5728\u80f8\u524d\uff0c\u4e00\u624b\u62ac\u8d77\u6258\u7740\u4e0b\u9894\uff0c\u4e00\u6839\u624b\u6307\u538b\u7740\u7c89\u6da6\u7684\u5634\u5507\uff0c\u538b\u6291\u7740\u7b11\u610f\uff1b\u53ea\u6709\u8bb8\u9e3f\u4f2f\u9887\u4e3a\u65e0\u5948\u7684\u6447\u5934\uff1a\u90fd\u8bf4\u51a4\u5bb6\u5b9c\u89e3\u4e0d\u5b9c\u7ed3\uff0c\u5f20\u606a\u8fd9\u6a21\u6837\u662f\u786c\u8ddf\u5bf9\u65b9\u6760\u4e0a\u4e86\u3002</p><p>\u3000\u3000\u522b\u4eba\u90fd\u60f3\u4e0d\u5230\uff0c\u8fd9\u65f6\u5019\u4f1a\u662f\u8c22\u6c49\u9756\u7ad9\u51fa\u6765\u5316\u89e3\u53cc\u65b9\u7684\u52bf\u82e5\u6c34\u706b\u7684\u5bf9\u5cd9\uff0c\u4ed6\u62fe\u6b65\u8d70\u5230\u5c71\u9876\uff0c\u770b\u7740\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u606a\u5c11\u8fd9\u4e48\u5173\u5fc3\u91d1\u5c71\u5e02\u65b0\u57ce\u533a\u7684\u5efa\u8bbe\u89c4\u5212\uff0c\u662f\u6709\u610f\u6765\u91d1\u5c71\u6295\u8d44\u55bd\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u624b\u63d2\u5728\u76ae\u5939\u514b\u7684\u5916\u515c\u91cc\uff0c\u770b\u7740\u8c22\u6c49\u9756\uff0c\u8bf4\u9053\uff1a\u201c\u6216\u8bb8\u5427\uff0c\u4e0d\u8fc7\u7ad9\u5728\u8fd9\u91cc\u770b\u98ce\u666f\u5012\u4e5f\u4e0d\u9519\uff0c\u5c31\u662f\u996e\u9a6c\u6cb3\u6cb3\u6c34\u7684\u989c\u8272\u602a\u5f02\u4e86\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u4ec5\u4ec5\u8d75\u6709\u4f26\u5728\u573a\uff0c\u987e\u53ca\u5230\u8d75\u6709\u4f26\u7684\u989c\u9762\uff0c\u5f20\u606a\u4e0d\u4f1a\u76f4\u63a5\u8bf4\u7834\u996e\u9a6c\u6cb3\u4e0e\u91d1\u5c71\u6e56\u7684\u6c61\u67d3\u73b0\u72b6\uff0c\u4f46\u662f\u5468\u747e\u745c\u4e5f\u4e00\u5e76\u8fc7\u6765\u4e86\uff0c\u5c31\u6ca1\u6709\u90a3\u4e48\u591a\u7684\u987e\u5fcc\u4e86\uff0c\u8eab\u4e3a\u91d1\u5c71\u7684\u5e02\u59d4\u4e66\u8bb0\u3001\u5e02\u957f\uff0c\u8fd9\u79cd\u4e8b\u603b\u8981\u4ed6\u4eec\u62c5\u5f85\u7684\u3002</p><p>\u3000\u3000\u996e\u9a6c\u6cb3\u6d4a\u9ec4\u4e2d\u6cdb\u7740\u767d\u6cab\u7684\u6cb3\u6c34\u523a\u773c\u7684\u6a2a\u4e98\u5728\u9762\u524d\uff0c\u79bb\u6cb3\u9053\u8fd9\u4e48\u8fdc\uff0c\u7a7a\u6c14\u91cc\u4e5f\u82e5\u9690\u82e5\u73b0\u7684\u6f02\u7740\u523a\u6fc0\u7684\u5473\u9053\uff0c\u8c22\u6c49\u9756\u5fae\u5fae\u8e59\u7709\u5934\u3002\u5468\u747e\u745c\u542c\u4e86\u4e5f\u5fae\u5fae\u8e59\u7709\uff0c\u5979\u8e59\u7709\u662f\u56e0\u4e3a\u89c9\u5f97\u5f20\u606a\u8fd9\u65f6\u5019\u6307\u51fa\u996e\u9a6c\u6cb3\u6c61\u67d3\u7684\u95ee\u9898\u65e2\u86ee\u6a2a\u53c8\u591a\u7ba1\u95f2\u4e8b\uff0c\u5012\u4e5f\u540e\u6094\u6ca1\u6709\u5b89\u6392\u597d\uff0c\u81f3\u5c11\u8ba9\u4e0a\u6e38\u6392\u6c61\u7684\u51e0\u5bb6\u5382\u8fd9\u51e0\u5929\u514b\u5236\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u8d75\u6709\u4f26\u8bf4\u9053\uff1a\u201c\u4e0a\u6e38\u662f\u6709\u51e0\u5bb6\u5382\u5b50\u4f1a\u65f6\u4e0d\u65f6\u7684\u5077\u6392\u2026\u2026\u201d\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u8fd9\u54ea\u91cc\u662f\u201c\u5077\u6392\u201d\uff1f\u4ed6\u5982\u6b64\u8bf4\u65e0\u975e\u4e5f\u662f\u63a9\u8033\u76d7\u94c3\u7f62\u4e86\uff0c\u56de\u5934\u8ddf\u4ed6\u7684\u79d8\u4e66\u957f\u89e3\u73cd\u534e\u8bf4\u9053\uff0c\u201c\u4f60\u901a\u77e5\u73af\u4fdd\u5c40\u6d3e\u4eba\u8fc7\u53bb\u770b\u4e00\u770b\uff0c\u5230\u5e95\u662f\u54ea\u51e0\u5bb6\u5382\u5b50\u5728\u5077\u6392\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6668\u66e6\u7eb8\u4e1a\u5c31\u5728\u996e\u9a6c\u6cb3\u7684\u4e0a\u6e38\u91d1\u5c71\u6e56\u8fb9\uff0c\u6211\u65d7\u4e0b\u7684\u65b0\u5149\u7eb8\u4e1a\u60f3\u8981\u8fdb\u5165\u6c5f\u5357\u7684\u5e02\u573a\uff0c\u6668\u66e6\u7eb8\u4e1a\u53ef\u662f\u6211\u4eec\u6700\u5927\u7684\u7ade\u4e89\u5bf9\u624b\uff0c\u6211\u5012\u662f\u5f88\u671f\u5f85\u8d75\u5e02\u957f\u80fd\u6d3e\u4eba\u72e0\u72e0\u7684\u67e5\u4e00\u67e5\u4ed6\u4eec\u7684\u6392\u6c61\u95ee\u9898\u3002\u201d</p><p>\u3000\u3000\u6709\u7ecf\u9a8c\u7684\u4eba\u770b\u6cb3\u6c34\u7684\u989c\u8272\u5c31\u77e5\u9053\u662f\u6709\u5de5\u5382\u5728\u6392\u653e\u672a\u8fbe\u6807\u7684\u5236\u6d46\u5de5\u4e1a\u5e9f\u6c34\uff0c\u4f46\u662f\u4f5c\u4e3a\u6c5f\u5357\u7701\u6700\u5927\u7684\u3001\u65d7\u4e0b\u62e5\u6709\u4e00\u5bb6\u4e0a\u5e02\u516c\u53f8\u7684\u7eb8\u4e1a\u96c6\u56e2\uff0c\u6668\u66e6\u7eb8\u4e1a\u7684\u6392\u6c61\u95ee\u9898\u4e00\u76f4\u6301\u7eed\u5230\u5341\u5e74\u4e4b\u540e\u90fd\u6ca1\u6709\u5f97\u5230\u6709\u6548\u7684\u89e3\u51b3\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u7684\u8425\u6797\u5de5\u4f5c\u63a8\u8fdb\u5230\u6c5f\u5357\u7701\u53d7\u963b\uff0c\u6709\u5f88\u591a\u590d\u6742\u7684\u56e0\u7d20\uff0c\u4f46\u7edd\u5bf9\u4e5f\u5305\u542b\u4e86\u6668\u66e6\u7eb8\u4e1a\u7684\u56e0\u7d20\u3002\u65b0\u5149\u7eb8\u4e1a\u63a8\u52a8\u7684\u662f\u6797\u7eb8\u4e00\u4f53\u5316\u7684\u5927\u5de5\u4e1a\u4f53\u7cfb\uff0c\u4e00\u65e6\u8ba9\u65b0\u5149\u7eb8\u4e1a\u5e03\u5c40\u5b8c\u6210\uff0c\u6668\u66e6\u7eb8\u4e1a\u5728\u65b0\u5149\u9762\u524d\u7684\u7ade\u4e89\u529b\u5c31\u5f88\u663e\u5f97\u5f88\u5f31\u5c0f\u800c\u53ef\u601c\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u7684\u8fd9\u4e48\u76f4\u767d\uff0c\u8d75\u6709\u4f26\u3001\u5468\u747e\u745c\u5012\u4e0d\u597d\u63a5\u8bdd\u4e86\uff0c\u65e0\u8bba\u662f\u5468\u747e\u745c\u4e5f\u597d\uff0c\u8d75\u6709\u4f26\u4e5f\u597d\uff0c\u90fd\u6ca1\u6709\u8db3\u591f\u7684\u52c7\u6c14\u4e0e\u51b3\u5fc3\u5728\u81ea\u5df1\u6839\u57fa\u672a\u7a33\u7684\u65f6\u5019\u62ff\u73af\u4fdd\u95ee\u9898\u5361\u4f4f\u8ba9\u5e74\u8425\u6536\u63a5\u8fd1\u4e09\u5341\u4ebf\u3001\u6bcf\u5e74\u4e0a\u7f34\u5229\u7a0e\u516d\u4e03\u4ebf\u7684\u5927\u578b\u56fd\u6709\u4f01\u4e1a\u6668\u66e6\u7eb8\u4e1a\u505c\u4ea7\u6574\u987f\u3002</p><p>\u3000\u3000\u5468\u747e\u745c\u5979\u4eec\u81ea\u7136\u518d\u6ca1\u6709\u5728\u5c71\u5c97\u4e0a\u6307\u70b9\u6c5f\u5c71\u7684\u5174\u8da3\uff0c\u5f88\u5feb\u5c31\u53c8\u6cbf\u539f\u8def\u4e0b\u5c71\u4e86\uff0c\u5728\u6b64\u8fc7\u7a0b\u5f53\u4e2d\uff0c\u8ba1\u59d4\u526f\u4e3b\u4efb\u3001\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u9886\u5bfc\u5c0f\u7ec4\u7ec4\u957f\u845b\u5efa\u5fb7\u5012\u6ca1\u6709\u542d\u58f0\u3002\u5f20\u606a\u6ce8\u610f\u5230\u4ed6\u4e0e\u6797\u96ea\u7684\u773c\u795e\u7686\u662f\u4e0d\u5584\uff0c\u738b\u6d77\u7c9f\u4e0e\u4ed6\u4eec\u7ad9\u5728\u4e00\u8d77\uff0c\u773c\u795e\u95ea\u70c1\u3002</p><p>\u3000\u3000\u5feb\u5230\u4e0b\u5348\u4f1a\u573a\u5f00\u653e\u7684\u65f6\u95f4\uff0c\u8d75\u6709\u4f26\u3001\u9648\u4fe1\u751f\u7b49\u4eba\u8fd8\u8981\u8d76\u5230\u5c71\u811a\u4e0b\u7684\u4f1a\u5c55\u4e2d\u5fc3\u53c2\u52a0\u4e0b\u5348\u7684\u6d3b\u52a8\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u907f\u5f00\u5a92\u4f53\u8bb0\u8005\u7684\u76ee\u5149\uff0c\u5c31\u4e0d\u80fd\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e0b\u4e0e\u9648\u4fe1\u751f\u3001\u8d75\u6709\u4f26\u6216\u5468\u747e\u745c\u8fd9\u4e9b\u80fd\u5438\u5f15\u4f17\u4eba\u76ee\u5149\u7684\u516c\u4f17\u4eba\u7269\u516c\u7136\u8d70\u5f97\u592a\u8fd1\uff0c\u4ed6\u6ca1\u6709\u8ddf\u8d75\u6709\u4f26\u3001\u9648\u4fe1\u751f\u4ed6\u4eec\u4e00\u8d77\u53bb\u4f1a\u8bae\u73b0\u573a\uff0c\u63d0\u51fa\u5728\u65b0\u57ce\u533a\u3001\u9ad8\u65b0\u57ce\u9644\u8fd1\u8f6c\u4e00\u8f6c\u3002</p><p>\u3000\u3000\u7b49\u8d75\u6709\u4f26\u4e0e\u9648\u4fe1\u751f\u7b49\u4eba\u79bb\u5f00\uff0c\u5f20\u606a\u5728\u5468\u6e38\u3001\u8bb8\u9e3f\u4f2f\u7b49\u4eba\u7684\u966a\u540c\u4e0b\uff0c\u4e58\u8f66\u8d76\u5230\u996e\u9a6c\u6cb3\u8fb9\uff0c\u7ad9\u5728\u6cb3\u8fb9\uff0c\u6cb3\u6c34\u91cc\u4f20\u51fa\u6765\u7684\u5473\u9053\u66f4\u523a\u9f3b\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5468\u6e38\u8bf4\u9053\uff1a\u201c\u50cf\u6668\u66e6\u7eb8\u4e1a\u8fd9\u4e48\u5927\u7684\u751f\u4ea7\u89c4\u6a21\uff0c\u8981\u60f3\u6392\u6c34\u8fbe\u6807\uff0c\u6bcf\u5e74\u6295\u5165\u7684\u6cbb\u6c61\u6210\u672c\u5c11\u8bf4\u4e5f\u8981\u516d\u5343\u4e07\u2026\u2026\u4ed6\u4eec\u8fd9\u6837\u5149\u660e\u6b63\u5927\u7684\u6392\u6c61\uff0c\u7ed9\u73af\u4fdd\u5c40\u5904\u7f5a\u80fd\u6709\u591a\u5c11\uff1f\u9876\u5929\u6bcf\u5e74\u7ed9\u7f5a\u4e00\u4e24\u5343\u4e07\uff0c\u5927\u5bb6\u90fd\u4f1a\u7b97\u8fd9\u7b14\u7ecf\u6d4e\u8d26\u3002\u5404\u5730\u7684\u73af\u4fdd\u5c40\u90fd\u5728\u641e\u521b\u6536\uff0c\u73af\u4fdd\u5c40\u5927\u6982\u4e5f\u5728\u652f\u6301\u4ed6\u4eec\u4e00\u76f4\u8fd9\u4e48\u6392\u4e0b\u53bb\u5427\u3002\u6211\u4eec\u7684\u8425\u6797\u5de5\u4f5c\u5728\u6c5f\u5357\u7701\u53d7\u963b\uff0c\u4e5f\u4e0e\u6668\u66e6\u7eb8\u4e1a\u6709\u5f88\u5927\u7684\u5173\u7cfb\u3002\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u8bf4\u9053\uff1a\u201c\u4f5c\u4e3a\u4e00\u5bb6\u5927\u578b\u7684\u56fd\u6709\u4f01\u4e1a\uff0c\u672c\u6765\u5c31\u662f\u57f9\u80b2\u4e2d\u9ad8\u7ea7\u5b98\u5458\u7684\u6e29\u5e8a\uff0c\u4e5f\u4f7f\u6668\u66e6\u7eb8\u4e1a\u4e0e\u5730\u65b9\u5b98\u573a\u5f62\u6210\u76d8\u6839\u9519\u8282\u7684\u5229\u76ca\u5173\u8054\uff0c\u6c5f\u5357\u7701\u526f\u7701\u957f\u53f6\u795d\u540c\u3001\u5b89\u987a\u5e02\u5e02\u957f\u6c5f\u949f\u5e73\u7b49\u591a\u540d\u6c5f\u5357\u7701\u5185\u7684\u5b9e\u6743\u4eba\u7269\u90fd\u662f\u51fa\u8eab\u6668\u66e6\u7eb8\u4e1a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u62bf\u7740\u5634\u62ac\u5934\u770b\u7740\u5929\u7a7a\u504f\u659c\u7684\u767d\u65e5\uff0c\u53c8\u5782\u4e0b\u5934\u7b11\u7740\u8bf4\uff1a\u201c\u8fd8\u662f\u5f53\u540d\u7ea8\u7ed4\u5b50\u5f1f\u5bb9\u6613\u4e9b\u3002\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u8981\u5bb9\u6613\u591a\u4e86\uff0c\u201d\u8bb8\u9e3f\u4f2f\u7b11\u7740\u5e94\u9053\uff0c\u201c\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u8981\u662f\u4e0d\u9009\u62e9\u4e00\u6761\u8270\u96be\u7684\u9053\u8def\u53bb\u8d70\uff0c\u9526\u6e56\u4e5f\u4e0d\u53ef\u80fd\u6709\u4eca\u5929\u7684\u6a21\u6837\uff0c\u4f46\u662f\u8d70\u5230\u8fd9\u4e00\u6b65\uff0c\u9526\u6e56\u4e5f\u5fc5\u987b\u9009\u62e9\u4e00\u6761\u8270\u96be\u7684\u9053\u8def\u53bb\u8d70\uff0c\u624d\u80fd\u771f\u6b63\u7684\u5c06\u5c40\u9762\u8d70\u5f00\u2026\u2026\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u6709\u4e9b\u2018\u5929\u5c06\u964d\u5927\u4efb\u2019\u7684\u610f\u5473\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4f4e\u5934\u6cbf\u7740\u6cb3\u5824\u5f80\u4e1c\u5357\u8d70\u53bb\uff0c\u5fc3\u91cc\u76d8\u65cb\u7740\u201c\u5929\u5c06\u964d\u5927\u4efb\u201d\u8fd9\u53e5\u8bdd\uff0c\u83ab\u660e\u5176\u5999\u7684\u91cd\u56de\u4e5d\u56db\u5e74\uff0c\u603b\u4e0d\u81f3\u4e8e\u53ea\u662f\u4e3a\u4e86\u505a\u4e00\u4e2a\u7ea8\u7ed4\u5b50\u5f1f\u5427\uff1f\u524d\u65b9\u5230\u5e95\u6709\u600e\u4e48\u7684\u5bbf\u547d\u5728\u7b49\u5019\u81ea\u5df1\uff1f\u8003\u8651\u8fd9\u4e9b\u95ee\u9898\uff0c\u591a\u5c11\u6709\u4e9b\u6c89\u91cd\uff0c\u5374\u662f\u4e00\u76f4\u4ee5\u6765\u90fd\u76d8\u4e98\u5728\u8111\u6d77\u91cc\u6325\u4e4b\u4e0d\u53bb\u7684\u7591\u60d1\u3002</p><p>\u3000\u3000\u6cbf\u6cb3\u5cb8\u8d70\u4e86\u8bb8\u591a\uff0c\u6cb3\u6c34\u91cc\u4f20\u6765\u7684\u523a\u9f3b\u7684\u5473\u9053\u8d8a\u6765\u8d8a\u6d53\uff0c\u90fd\u80fd\u770b\u89c1\u6668\u66e6\u7eb8\u4e1a\u5382\u623f\u7684\u5f71\u5b50\uff0c\u5f20\u606a\u7ad9\u4f4f\u811a\uff0c\u770b\u7740\u811a\u4e0b\u5728\u6cdb\u9ec4\u7684\u6cb3\u6c34\uff0c\u5f20\u606a\u8f6c\u8fc7\u8eab\uff0c\u5bf9\u4f17\u4eba\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53bb\u4f1a\u5c55\u770b\u770b\u2026\u2026\u4e0b\u5348\u4e0d\u662f\u6709\u845b\u5efa\u5fb7\u7684\u53d1\u8a00\u5417\uff1f\u5012\u8981\u770b\u770b\u4ed6\u72d7\u5634\u91cc\u80fd\u5410\u51fa\u4ec0\u4e48\u8c61\u7259\u6765\u3002\u201d</p><p>\u3000\u3000\u4e24\u90e8\u8f66\u90fd\u8ddf\u5728\u540e\u9762\uff0c\u5f20\u606a\u4ed6\u4eec\u4e0b\u4e86\u6cb3\u5824\u94bb\u8fdb\u8f66\uff0c\u8f6c\u773c\u5c31\u8d76\u5230\u4f1a\u5c55\u4e2d\u5fc3\u3002\u5f20\u606a\u4ed6\u4eec\u6ca1\u6709\u5165\u573a\u8bc1\uff0c\u8fd8\u662f\u6253\u7535\u8bdd\u8bf7\u8d75\u6709\u4f26\u7684\u79d8\u4e66\u9648\u660e\u5e06\u51fa\u6765\u9886\u4ed6\u4eec\u8fdb\u53bb\uff0c\u6b63\u8d76\u4e0a\u845b\u5efa\u5fb7\u5728\u53f0\u4e0a\u53d1\u8a00\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u8d70\u8fdb\u4f1a\u573a\u7684\u65f6\u5019\uff0c\u845b\u5efa\u5fb7\u8138\u4e0a\u5fae\u6709\u8be7\u5f02\uff0c\u6f14\u8bb2\u7a0d\u7a0d\u505c\u4e86\u4e24\u4e09\u79d2\u949f\uff0c\u60f9\u5f97\u4f1a\u573a\u91cc\u7684\u5217\u5e2d\u4ee3\u8868\u90fd\u56de\u5934\u770b\u8fc7\u6765\uff0c\u90fd\u6709\u597d\u4e9b\u719f\u4eba\u770b\u5230\u5f20\u606a\u51fa\u73b0\u5728\u4f1a\u573a\u90fd\u9732\u51fa\u8be7\u5f02\u7684\u795e\u8272\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u7b11\u7740\u8bf4\uff1a\u201c\u845b\u5efa\u5fb7\u6216\u8bb8\u4ee5\u4e3a\u6211\u4eec\u662f\u4e13\u7a0b\u6765\u542c\u4ed6\u53d1\u8a00\uff0c\u4e0d\u8981\u7ed9\u4ed6\u592a\u591a\u7684\u538b\u529b\u624d\u597d\u2026\u2026\u8981\u4e0d\u4f60\u629b\u4e2a\u5a9a\u773c\u4e0a\u53bb\uff1f\u8981\u80fd\u8ba9\u845b\u5efa\u5fb7\u5728\u53f0\u4e0a\u51fa\u4e11\uff0c\u4eca\u5929\u665a\u996d\u6211\u8bf7\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u53bb\uff0c\u6211\u7684\u5a9a\u773c\u5c31\u8fd9\u4e48\u5ec9\u4ef7\uff1f\u201d\u7fdf\u4e39\u9752\u7b11\u7738\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u987e\u76fc\u95f4\u98ce\u60c5\u65e0\u7aef\uff0c\u8ba9\u5f20\u606a\u8d76\u7d27\u5750\u4f4d\u5b50\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u4e5f\u5c31\u505c\u987f\u4e86\u7247\u523b\uff0c\u53c8\u7ee7\u7eed\u4ed6\u521a\u624d\u7684\u6f14\u8bb2\uff1a</p><p>\u3000\u3000\u201c\u2026\u2026\u968f\u7740\u4fe1\u606f\u5316\u5efa\u8bbe\u7684\u53d1\u5c55\uff0c\u5bf9\u5404\u884c\u5404\u4e1a\u7684\u5f71\u54cd\u4e5f\u8d8a\u6765\u8d8a\u5927\uff0c\u4eba\u4eec\u5728\u5efa\u8bbe\u8fc7\u7a0b\u4e2d\u4e5f\u5728\u4e0d\u65ad\u7684\u63a2\u7d22\u4e0e\u63a2\u8ba8\uff1a\u5982\u4f55\u8ba9\u4fe1\u606f\u5316\u4e0e\u81ea\u5df1\u6240\u5904\u7684\u884c\u4e1a\u66f4\u878d\u6d3d\u7684\u63a5\u5408\u8d77\u6765\uff1f\u6211\u56fd\u7535\u5b50\u653f\u52a1\u5efa\u8bbe\u624d\u8d77\u6b65\u4e0d\u4e45\uff0c\u9664\u4e86\u521d\u671f\u7684\u7f51\u7edc\u5efa\u8bbe\u4e0e\u7b80\u5355\u7684\u4fe1\u606f\u53d1\u5e03\u4e4b\u5916\uff0c\u6211\u4eec\u8fd8\u8981\u8fbe\u6210\u6709\u6548\u6574\u5408\u653f\u5e9c\u5185\u5916\u90e8\u8d44\u6e90\u4e3a\u4e3b\u4f53\u3001\u653f\u5e9c\u76d1\u7ba1\u80fd\u529b\u548c\u670d\u52a1\u6c34\u5e73\u83b7\u5f97\u63d0\u9ad8\u7684\u76ee\u6807\u2026\u2026\u76ee\u524d\u5728\u8be5\u6280\u672f\u9886\u57df\u5904\u4e8e\u524d\u6cbf\u7684\u4ea7\u54c1\u5bf9\u5404\u79cd\u6570\u636e\u5e93\u548c\u4e1a\u52a1\u6570\u636e\u51fa\u73b0\u7684\u5f02\u5e38\u60c5\u51b5\u5206\u6790\u548c\u5904\u7406\u5df2\u7ecf\u80fd\u8ba9\u4eba\u611f\u5230\u6ee1\u610f\u4e86\uff0c\u5982\u5728\u5efa\u90ba\u5927\u6570\u636e\u91cf\u7684\u5b9e\u9645\u5904\u7406\u4e2d\u7ecf\u8fc7\u8003\u9a8c\u7684\u2018\u91d1\u7c9f\u2019\u57ce\u5e02\u6570\u636e\u4e2d\u5fc3\u57fa\u7840\u4fe1\u606f\u5904\u7406\u7cfb\u7edf\uff0c\u5c31\u662f\u6280\u672f\u5904\u4e8e\u524d\u6cbf\u7684\u4ea7\u54c1\u2026\u2026\u76ee\u524d\u5404\u5730\u65b9\u76f8\u5173\u804c\u80fd\u90e8\u95e8\u90fd\u5728\u52a0\u5feb\u4fe1\u606f\u5316\u7684\u6b65\u4f10\uff0c\u540c\u65f6\u5bf9\u5efa\u8bbe\u9879\u76ee\u4e2d\u7684\u57fa\u7840\u6570\u636e\u91c7\u96c6\u4e0e\u6574\u5408\u76f8\u5f53\u91cd\u89c6\uff0c\u5bf9\u4e8e\u76f8\u5e94\u7684\u7cfb\u7edf\u8f6f\u4ef6\u63d0\u4f9b\u5546\u53ca\u5176\u4ea7\u54c1\u548c\u670d\u52a1\u63d0\u51fa\u4e86\u4e25\u683c\u7684\u8981\u6c42\u3002\u4e0d\u4ec5\u8981\u80fd\u80dc\u4efb\u5404\u79cd\u57ce\u5e02\u57fa\u7840\u6570\u636e\u7684\u91c7\u96c6\u548c\u6574\u5408\u5de5\u4f5c\uff0c\u6709\u6781\u9ad8\u7684\u6570\u636e\u8d28\u91cf\uff0c\u4ee5\u540e\u8fd8\u53ef\u4e0e\u57ce\u5e02\u6570\u636e\u4e2d\u5fc3\u7684\u6570\u636e\u4ea4\u6362\u5e73\u53f0\u4e00\u8d77\u6346\u7ed1\u4f7f\u7528\uff0c\u5177\u6709\u5f88\u5f3a\u7684\u73af\u5883\u9002\u5e94\u6027\u4ee5\u53ca\u7075\u6d3b\u7684\u529f\u80fd\u6269\u5c55\u6027\u3002\u4ece\u800c\u5e2e\u52a9\u653f\u5e9c\u90e8\u95e8\u5efa\u7acb\u7edf\u4e00\u7684\u6570\u636e\u4e2d\u5fc3\uff0c\u4f7f\u5404\u5730\u533a\u5404\u90e8\u95e8\u7684\u4e1a\u52a1\u6570\u636e\u80fd\u6709\u89c4\u8303\u7684\u4e92\u8054\u4e92\u901a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd8\u4ee5\u4e3a\u4ed6\u8981\u4e3a\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\u7684\u7ec4\u5efa\u9020\u52bf\u5462\uff0c\u6ca1\u60f3\u5230\u8fd8\u662f\u4e00\u5fc3\u6251\u5728\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u4e0a\u2026\u2026\u201d\u53f6\u5efa\u658c\u4f4e\u58f0\u8bf4\u9053\u3002</p><p>\u3000\u3000\u2018\u91d1\u7c9f\u2019\u5c31\u662f\u91d1\u9f0e\u4e0e\u6d77\u7c9f\u7684\u5408\u79f0\uff0c\u6240\u8c13\u7684\u201c\u57ce\u5e02\u6570\u636e\u4e2d\u5fc3\u57fa\u7840\u4fe1\u606f\u5904\u7406\u7cfb\u7edf\u201d\u5176\u5b9e\u4e3b\u8981\u8fd8\u662f\u91d1\u9f0e\u7684\u6280\u672f\u3002\u738b\u6d77\u7c9f\u62ff\u51fa\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u7acb\u9879\u7b56\u5212\u4e4b\u540e\uff0c\u845b\u5efa\u5fb7\u3001\u6797\u96ea\u3001\u80e1\u5b97\u5e86\u7b49\u4eba\u5c31\u51b3\u5b9a\u4ee5\u6d77\u7c9f\u79d1\u6280\u4e3a\u6838\u5fc3\u8fd0\u8425\u76f8\u5173\u9879\u76ee\uff0c\u5c06\u4e2d\u79d1\u9662\u8f6f\u4ef6\u7814\u7a76\u6240\u4ee5\u53ca\u4e1c\u6d77\u7701\u79d1\u6280\u5385\u4e0b\u5c5e\u7684\u5b9e\u529b\u6700\u5f3a\u7684\u8f6f\u4ef6\u96c6\u56e2\u91d1\u9f0e\u79d1\u6280\u7684\u90e8\u5206\u6280\u672f\u8d44\u4ea7\u4e0e\u4eba\u529b\u8d44\u6e90\u90fd\u4ee5\u5408\u8d44\u7684\u540d\u4e49\u5e76\u5165\u6d77\u7c9f\u79d1\u6280\uff0c\u5c06\u6d77\u7c9f\u79d1\u6280\u4ece\u4e00\u5bb6\u6240\u6709\u5458\u5de5\u4eba\u6570\u90fd\u4e0d\u6ee1\u4e8c\u5341\u7684\u5c0f\u516c\u53f8\u5728\u77ed\u77ed\u51e0\u4e2a\u6708\u7684\u65f6\u95f4\u91cc\u786c\u751f\u751f\u7684\u5439\u80a5\u6210\u4e1c\u6d77\u5730\u533a\u6700\u5177\u5b9e\u529b\u7684\u8f6f\u4ef6\u516c\u53f8\u3002\u201c\u91d1\u7c9f\u57ce\u5e02\u6570\u636e\u4e2d\u57fa\u7840\u4fe1\u606f\u5904\u7406\u7cfb\u7edf\u201d\u81ea\u7136\u4e5f\u5c31\u6210\u4e86\u6d77\u7c9f\u79d1\u6280\u5f53\u524d\u7684\u62f3\u5934\u4ea7\u54c1\uff0c\u5728\u80e1\u5b97\u5e86\u7b49\u4eba\u7684\u8fd0\u4f5c\u4e0b\uff0c\u5728\u8096\u660e\u5efa\u7684\u652f\u6301\uff0c\u8be5\u4fe1\u606f\u5904\u7406\u7cfb\u7edf\u8fd0\u7528\u4e8e\u5efa\u90ba\u5e02\u7684\u7535\u5b50\u653f\u52a1\u5de5\u7a0b\u5efa\u8bbe\u3002</p><p>\u3000\u3000\u8be5\u7cfb\u7edf\u5efa\u90ba\u5e02\u8fd8\u6b63\u5728\u8c03\u8bd5\u4e2d\uff0c\u5bf9\u4e8e\u5177\u4f53\u7684\u6027\u80fd\uff0c\u5f20\u606a\u624b\u5934\u8fd8\u6ca1\u6709\u76f8\u5173\u7684\u8bc4\u6d4b\u62a5\u544a\uff0c\u845b\u5efa\u5fb7\u8fd9\u65f6\u5019\u8c08\u7535\u5b50\u653f\u52a1\u3001\u8c08\u57ce\u5e02\u6570\u636e\u4e2d\u5fc3\u57fa\u7840\u4fe1\u606f\u5904\u7406\u7cfb\u7edf\uff0c\u81ea\u7136\u662f\u4e3a\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9020\u52bf\uff0c\u6bd5\u7adf\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u8fd0\u8425\u4e0d\u53ef\u80fd\u5b8c\u5168\u5efa\u5728\u6ca1\u6709\u4e00\u70b9\u57fa\u7840\u7684\u7a7a\u4e2d\u697c\u9601\u4e4b\u4e2d\uff0c\u800c\u7535\u5b50\u653f\u52a1\u7cfb\u7edf\u6070\u597d\u53c8\u662f\u4e0b\u4e00\u4e2a\u8f6f\u4ef6\u5e02\u573a\u7684\u70ed\u70b9\u3002</p><p>\u3000\u3000\u6d77\u7c9f\u79d1\u6280\u82e5\u80fd\u5c06\u4e24\u7701\u4e3b\u8981\u5730\u5e02\u7684\u7535\u5b50\u653f\u52a1\u5de5\u7a0b\u62ff\u5230\u624b\uff0c\u5012\u662f\u80fd\u652f\u6491\u4f4f\u4e00\u6bb5\u65f6\u95f4\uff0c\u4e0d\u7528\u62c5\u5fc3\u5728\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u5efa\u6210\u4e4b\u524d\u5c31\u7ed9\u88ab\u4eba\u6233\u7834\u8001\u5e95\u3002</p></article>"}}}]);
