(window.webpackJsonp=window.webpackJsonp||[]).push([[796],{819:function(p){p.exports={chapterTitle:null,title:"\u7b2c628\u7ae0 \u6709\u53ef\u80fd\u505a\u6821\u53cb",content:"<article><h2>\u7b2c628\u7ae0 \u6709\u53ef\u80fd\u505a\u6821\u53cb</h2><p>\u3000\u3000\u201c\u2026\u2026\u5929\u8fb9\u4f4e\u60ac\uff0c\u6668\u5149\u91cc\u90a3\u9897\u84dd\u661f\u7684\u5e7d\u5149/\u5524\u9192\u4e86\u4f60\u6211\u5fc3\u4e2d\uff0c\u4e00\u7f15\u4e0d\u6b7b\u7684\u5fe7\u4f24\u3002\u9732\u6e7f\u7684\u767e\u5408\u3001\u73ab\u7470\u68a6\u91cc\u9038\u51fa\u4e00\u4e1d\u56f0\u5026\uff1b\u5475\uff0c\u4eb2\u7231\u7684\uff0c\u53ef\u522b\u68a6\u90a3\u6d41\u661f\u7684\u95ea\u8000\uff0c\u4e5f\u522b\u68a6\u90a3\u84dd\u661f\u7684\u5e7d\u5149\u5728\u6ef4\u9732\u4e2d\u4f4e\u5f8a\uff1a\u4f46\u613f\u6211\u4eec\u5316\u4f5c\u6d6a\u5c16\u4e0a\u7684\u767d\u9e1f\uff1a\u6211\u548c\u4f60\uff01\u201d\u9648\u5983\u84c9\u7279\u610f\u8dd1\u5230\u4e66\u67b6\u90a3\u91cc\u627e\u6765\u53f6\u829d\u7684\u8bd7\u9009\u96c6\u6765\u4f4e\u8bed\u7684\u8f7b\u8bfb\u90a3\u9996\u300a\u767d\u9e1f\u300b\uff0c\u60f3\u8d77\u5510\u5a67\u8bf4\u8d77\u5f20\u606a\u96e8\u4e2d\u8ddf\u5979\u5ff5\u8bd7\u7684\u4e8b\u60c5\uff0c\u5634\u89d2\u6302\u7740\u6d45\u7b11\uff0c\u4e0d\u60f3\u5f71\u54cd\u4ed6\u4eba\uff0c\u8bf4\u8bdd\u8fd8\u8981\u5c0f\u5de7\u7cbe\u81f4\u7684\u8793\u9996\u8fd8\u8981\u51d1\u5230\u684c\u5b50\u4e2d\u95f4\uff0c\u201c\u4f60\u600e\u4e48\u8bb0\u5f97\u82f1\u6587\u539f\u8bd7\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u62cd\u4e86\u62cd\u8170\u95f4\uff0c\u7b11\u4e86\u7b11\uff0c\u8111\u888b\u51d1\u8fc7\u53bb\u8ddf\u9648\u5983\u84c9\u4f4e\u8bed\u8bf4\uff1a\u201c\u6211\u5c31\u60f3\u554a\uff0c\u6ca1\u6709\u4e24\u628a\u5237\u5b50\u5230\u4e1c\u5927\u600e\u4e48\u80fd\u5403\u5f97\u5f00\uff1f\u53ef\u601c\u554a\uff0c\u4e24\u628a\u5237\u5b50\u6839\u672c\u5c31\u6ca1\u6709\u673a\u4f1a\u821e\u4e00\u821e\uff0c\u6076\u540d\u5df2\u7ecf\u4f20\u904d\u4e1c\u5927\uff0c\u6211\u73b0\u5728\u57fa\u672c\u4e0a\u4e0d\u4f1a\u8ddf\u522b\u4eba\u627f\u8ba4\u6211\u53eb\u5565\u540d\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u9648\u5983\u84c9\u8f7b\u7b11\u7740\uff0c\u60f3\u8d77\u6765\u5f20\u606a\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u5403\u665a\u996d\uff0c\u95ee\u4ed6\uff0c\u201c\u6211\u8fc7\u6765\u65f6\uff0c\u4f60\u597d\u50cf\u90fd\u5750\u597d\u4e45\u4e86\uff0c\u6ca1\u89c1\u4f60\u51fa\u53bb\u5403\u665a\u996d\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5728\u5f00\u6c34\u623f\u90a3\u91cc\u9047\u5230\u4f60\u4e4b\u540e\u5c31\u56de\u4e86\u4e00\u4e0b\u5bbf\u820d\uff0c\u201d\u5f20\u606a\u6478\u4e86\u6478\u809a\u5b50\uff0c\u6765\u4e4b\u524d\u5c31\u5f80\u809a\u5b50\u586b\u4e86\u4e24\u5757\u9762\u5305\uff0c\u8fd9\u65f6\u5019\u7684\u786e\u4e5f\u997f\u4e86\uff0c\u7b11\u7740\u8ddf\u9648\u5983\u84c9\u89e3\u91ca\u8bf4\uff0c\u201c\u591c\u91cc\u6709\u4eba\u8981\u4ece\u65b0\u829c\u8d76\u8fc7\u6765\uff0c\u603b\u8981\u7b49\u4eba\u5bb6\u8fc7\u6765\u4e00\u8d77\u5403\u665a\u996d\u624d\u884c\uff0c\u8fd9\u624d\u7b49\u5230\u73b0\u5728\uff0c\u201d\u770b\u4e86\u770b\u8868\uff0c\u90fd\u516b\u70b9\u949f\uff0c\u201c\u4e5f\u5e94\u8be5\u5feb\u5230\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5f\u662f\u5de7\uff0c\u773c\u775b\u521a\u79fb\u5230\u684c\u89d2\u7684\u624b\u673a\u4e0a\uff0c\u624b\u673a\u5c31\u9707\u52a8\u8d77\u6765\uff0c\u62ff\u8d77\u6765\u679c\u7136\u662f\u7fdf\u4e39\u9752\u7684\u7535\u8bdd\uff0c\u5f20\u606a\u8dd1\u5230\u9605\u89c8\u5ba4\u5916\u7684\u8fc7\u9053\u4e0a\u63a5\u7535\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u5475\uff0c\u4f60\u4eba\u5230\u4e1c\u5927\u5357\u95e8\u4e86\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u5443\uff0c\u6211\u5df2\u7ecf\u5230\u6821\u5185\u4e86\uff0c\u6b63\u5728\u7d2b\u971e\u6e56\u8fd9\u8fb9\u529e\u5165\u4f4f\u624b\u7eed\u5462\u3002\u201d</p><p>\u3000\u3000\u7d2b\u971e\u6e56\u662f\u4e1c\u5927\u6821\u5185\u897f\u5357\u89d2\u4e0a\u7684\u5c0f\u6e56\uff0c\u6821\u5185\u62db\u5f85\u6240\u5c31\u5728\u6e56\u7684\u897f\u9762\uff0c\u5f20\u606a\u4e0d\u6653\u5f97\u7fdf\u4e39\u9752\u4e3a\u4ec0\u4e48\u5230\u5efa\u90ba\u6765\u529e\u4e8b\u8981\u4f4f\u8fdb\u4e1c\u5927\u7684\u62db\u5f85\u5bbe\u9986\uff0c\u4fbf\u8bf4\u4ed6\u8fc7\u53bb\u627e\u5979\u3002\u6302\u4e86\u7535\u8bdd\u8dd1\u56de\u9605\u89c8\u5ba4\u8981\u5c06\u684c\u4e0a\u4e00\u5806\u4e66\u5f52\u67b6\uff0c\u9648\u5983\u84c9\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u4e8b\u5148\u53bb\u4e86\uff0c\u4e66\u6211\u5e2e\u4f60\u653e\u56de\u67b6\u5b50\u4e0a\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u884c\uff0c\u8c22\u8c22\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u7b49\u5230\u73b0\u5728\u7684\u786e\u4e5f\u997f\u614c\u4e86\uff0c\u5f20\u606a\u60f3\u521a\u624d\u4e0e\u7fdf\u4e39\u9752\u78b0\u9762\u627e\u4e2a\u5730\u65b9\u5403\u996d\u53bb\uff0c\u8dd1\u5230\u7d2b\u971e\u6e56\u62db\u5f85\u5bbe\u9986\uff0c\u9694\u7740\u73bb\u7483\u95e8\uff0c\u770b\u89c1\u7fdf\u4e39\u9752\u5750\u5728\u62db\u5f85\u6240\u63a5\u5f85\u5927\u5385\u7684\u6c99\u53d1\u4e0a\uff0c\u706f\u5149\u4e0b\u6765\u767d\u7699\u7684\u8138\u59a9\u5a9a\u800c\u8273\u4e3d\uff0c\u7a7f\u7740\u767d\u8272\u534a\u8896\u659c\u895f\u886c\u886b\uff0c\u8170\u95f4\u7cfb\u7740\u9ed1\u8272\u7684\u5bbd\u8170\u5e26\uff0c\u8eab\u5b50\u5fae\u4fef\u7740\uff0c\u773c\u775b\u4f3c\u4e4e\u51dd\u89c6\u7740\u7d2b\u8272\u7684\u9ad8\u8ddf\u978b\u5c16\u6765\u6253\u53d1\u7b49\u5f85\u7684\u65e0\u804a\u65f6\u95f4\uff0c\u6ca1\u770b\u5230\u6709\u5176\u4ed6\u4eba\uff0c\u5f20\u606a\u5012\u662f\u5947\u602a\u4e86\uff0c\u8fd8\u4ee5\u4e3a\u536b\u5170\u4f1a\u8ddf\u5979\u5f62\u5f71\u4e0d\u79bb\u5462\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u4e0a\u53f0\u9636\uff0c\u7fdf\u4e39\u9752\u624d\u770b\u5230\u4ed6\u7ad9\u8d77\u6765\uff0c\u5fae\u5f20\u7740\u5a07\u8273\u7684\u5634\u5507\uff0c\u90a3\u4e9b\u6b32\u9732\u8fd8\u655b\u7684\u6b23\u559c\u795e\u8272\uff0c\u603b\u4f1a\u8ba9\u4eba\u770b\u4e86\u5fc3\u91cc\u8212\u5766\uff0c\u8fd9\u65f6\u5019\u624d\u770b\u89c1\u5979\u4e0b\u8eab\u7a7f\u7740\u66f2\u7ebf\u5706\u6da6\u7684\u84dd\u8272\u77ed\u88d9\uff0c\u6574\u4e00\u4e2a\u6027\u611f\u8273\u8272\u7684\u804c\u4e1a\u4e3d\u4eba\u6a21\u6837\uff0c\u867d\u7136\u4e0a\u8863\u80f8\u524d\u7f00\u6709\u8936\u76b1\uff0c\u4e0d\u8fc7\u8fd8\u662f\u80fd\u770b\u51fa\u80f8\u524d\u7684\u6ce2\u6d9b\u6c79\u6d8c\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u53ea\u6709\u4f60\u4e00\u4e2a\u4eba\uff1f\u201d\u5f20\u606a\u5947\u602a\u7684\u95ee\u5979\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u516c\u53f8\u7684\u8f66\u56de\u5efa\u90ba\uff0c\u8ba9\u53f8\u673a\u76f4\u63a5\u9001\u6211\u5230\u8fd9\u8fb9\uff0c\u6ca1\u6709\u5176\u4ed6\u4eba\u4e86\uff0c\u96be\u9053\u606a\u5c11\u8fd8\u671f\u5f85\u522b\u7684\u4eba\uff1f\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7b11\u8d77\u6765\u59a9\u5a9a\u8bf1\u4eba\uff0c\u5f20\u606a\u53ea\u662f\u6487\u6487\u5634\uff0c\u8fd9\u4e2a\u5973\u4eba\u5728\u65b0\u829c\u53ef\u662f\u8c01\u90fd\u4e0d\u6562\u62db\u60f9\u7684\u3002</p><p>\u3000\u3000\u201c\u6211\u80fd\u671f\u5f85\u8c01\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u6211\u809a\u5b50\u5feb\u997f\u762a\u4e86\uff0c\u8d76\u7d27\u627e\u5730\u65b9\u5403\u996d\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u82e5\u8bf4\u5408\u6b22\u5c71\u4e1c\u9e93\u662f\u4e1c\u5927\u7684\u4e3b\u8981\u751f\u6d3b\u533a\uff0c\u90a3\u7d2b\u971e\u6e56\u5468\u56f4\u5c31\u662f\u4e1c\u5927\u7684\u9ad8\u6863\u751f\u6d3b\u533a\uff0c\u4e0d\u4ec5\u6821\u5185\u7684\u62db\u5f85\u5bbe\u9986\u4f4d\u4e8e\u6b64\u5904\uff0c\u56fd\u9645\u4ea4\u6d41\u5b66\u9662\u7684\u5916\u4e8b\u670d\u52a1\u4e2d\u5fc3\u4e5f\u4f4d\u4e8e\u6b64\u5904\u2014\u2014\u4e1c\u5927\u5b66\u751f\u901a\u5e38\u5c06\u5916\u4e8b\u670d\u52a1\u4e2d\u5fc3\u79f0\u4e3a\u7559\u5b66\u751f\u516c\u5bd3\uff0c\u7559\u5b66\u751f\u516c\u5bd3\u5e94\u8be5\u662f\u4e1c\u5927\u6821\u5185\u4f4f\u5bbf\u6761\u4ef6\u6700\u597d\u7684\uff0c\u4f46\u662f\u5f20\u606a\u4e0d\u60f3\u8ddf\u90a3\u4e9b\u9b3c\u5b50\u6df7\u5728\u4e00\u8d77\uff0c\u6240\u4ee5\u5b81\u53ef\u9ebb\u70e6\u4e9b\uff0c\u4e5f\u8981\u5c06\u5bbf\u820d\u63d2\u8fdb\u7814\u7a76\u751f\u516c\u5bd3\u91cc\u3002</p><p>\u3000\u3000\u62db\u5f85\u5bbe\u9986\u65c1\u8fb9\u5c31\u6709\u4e00\u5bb6\u76f8\u5f53\u4e0d\u9519\u7684\u897f\u9910\u5385\uff0c\u4e3b\u8981\u505a\u7559\u5b66\u751f\u7684\u751f\u610f\uff0c\u8bb8\u591a\u4e1c\u5927\u7684\u6559\u5e08\u4e5f\u90fd\u559c\u6b22\u5230\u8fd9\u91cc\u6765\u7528\u9910\u3002</p><p>\u3000\u3000\u897f\u9910\u5385\u8fd9\u65f6\u5019\u8fd8\u4e0d\u4f1a\u7ed3\u675f\u4e00\u5929\u7684\u8425\u4e1a\uff0c\u5f20\u606a\u9886\u7740\u7fdf\u4e39\u9752\u8d70\u8fdb\u90a3\u5bb6\u897f\u9910\u5385\uff0c\u8fd8\u6709\u597d\u4e9b\u4eba\u5728\u90a3\u91cc\u7528\u9910\uff0c\u770b\u5230\u5468\u747e\u73ba\u3001\u8c22\u6c49\u9756\u3001\u8c22\u610f\u3001\u8c22\u5b50\u5609\u8fd8\u6709\u5d14\u56fd\u6052\u3001\u9b4f\u4e1c\u5f3a\u5750\u5728\u90a3\u91cc\u7528\u9910\uff0c\u62ff\u7740\u5200\u53c9\u804a\u5929\u6b63\u5f00\u5fc3\u3002</p><p>\u3000\u3000\u4eca\u5929\u5468\u672b\uff0c\u8c22\u6c49\u9756\u4eba\u5728\u5efa\u90ba\u62c9\u7740\u8c22\u610f\u5230\u5b66\u6821\u6765\u770b\u4ed6\u5973\u513f\u987a\u4fbf\u8bf7\u56fd\u5546\u9662\u7684\u9662\u957f\u4ee5\u53ca\u8c22\u5b50\u5609\u7684\u5e74\u7ea7\u4e3b\u4efb\u9b4f\u4e1c\u5f3a\u4e00\u8d77\u5403\u987f\u996d\uff0c\u5012\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u7684\u4e8b\u60c5\u2014\u2014\u8c22\u5b50\u5609\u4e00\u526f\u4e56\u5de7\u7684\u6a21\u6837\uff0c\u8fd8\u771f\u50cf\u4e2a\u4e56\u4e56\u5973\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u4f3c\u4e4e\u975e\u5e38\u6709\u9ed8\u5951\u7684\u5c06\u89c6\u7ebf\u4ece\u5bf9\u65b9\u7684\u8eab\u4e0a\u79fb\u5f00\uff0c\u53ea\u6709\u9b4f\u4e1c\u5f3a\u8138\u4e0a\u6709\u4e9b\u8be7\u5f02\u770b\u7740\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u8d70\u8fdb\u6765\uff0c\u5d14\u56fd\u6052\u90fd\u4e0d\u8ba4\u8bc6\u5f20\u606a\uff0c\u53ea\u662f\u7fdf\u4e39\u9752\u7684\u5bb9\u989c\u8fd9\u822c\u7684\u660e\u8273\uff0c\u89c6\u7ebf\u5fcd\u4e0d\u4f4f\u5728\u5979\u8138\u4e0a\u53ca\u52a8\u4eba\u7684\u66fc\u5999\u8eab\u6750\u4e0a\u591a\u505c\u7559\u4e86\u4e00\u4e24\u79d2\u624d\u79fb\u5f00\uff1b\u9b4f\u4e1c\u5f3a\u9644\u5230\u5d14\u56fd\u6052\u8033\u8fb9\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u5d14\u56fd\u6052\u624d\u518d\u5ea6\u8be7\u5f02\u7684\u6253\u91cf\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u6b64\u89c6\u82e5\u65e0\u7779\uff0c\u4e5f\u662f\u5b66\u751f\u6253\u5de5\u7684\u4f8d\u5e94\u751f\u8dd1\u8fc7\u6765\u62db\u5f85\u5b89\u6392\u5ea7\u4f4d\uff0c\u5f20\u606a\u8d70\u5230\u9910\u5385\u7684\u53e6\u4e00\u89d2\u4e0e\u7fdf\u4e39\u9752\u5750\u4e0b\u6765\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u3001\u8c22\u610f\u90fd\u662f\u4e1c\u6d77\u5546\u754c\u7684\u540d\u6d41\u4eba\u7269\uff0c\u4f55\u51b5\u53c8\u662f\u9526\u6e56\u5bf9\u7acb\u9635\u8425\u7684\u4eba\u7269\uff0c\u7fdf\u4e39\u9752\u81ea\u7136\u8ba4\u5f97\uff0c\u5979\u4e0d\u8ba4\u8bc6\u8c22\u5b50\u5609\uff0c\u4e5f\u4e0d\u8ba4\u8bc6\u5468\u747e\u73ba\uff0c\u81ea\u7136\u5c31\u597d\u5947\u4ed6\u4eec\u4fe9\u4e3a\u4ec0\u4e48\u5750\u5728\u4e1c\u5927\u6821\u5185\u7684\u9910\u5385\u91cc\u3002</p><p>\u3000\u3000\u89c1\u7fdf\u4e39\u9752\u7f8e\u4e3d\u7684\u773c\u775b\u6709\u6291\u4e0d\u4f4f\u7684\u597d\u5947\uff0c\u5f20\u606a\u7b11\u7740\u89e3\u91ca\u8c22\u5b50\u5609\u3001\u5468\u747e\u73ba\u7684\u8eab\u4efd\uff0c\u7fdf\u4e39\u9752\u7b11\u7740\u8bf4\uff1a\u201c\u606a\u5c11\u5c31\u4f7f\u4e9b\u624b\u6bb5\uff0c\u5c06\u4eba\u5bb6\u59d1\u5a18\u7684\u9b42\u7ed9\u52fe\u8fc7\u6765\uff0c\u770b\u8c22\u6c49\u9756\u5bf9\u4f60\u8fd8\u4f1a\u6709\u4ec0\u4e48\u8138\u8272\uff1f\u201d</p><p>\u3000\u3000\u201c\u53bb\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u8fd8\u9700\u8981\u6211\u4eb2\u81ea\u6765\u727a\u7272\u8272\u76f8\uff1f\u201d\u53c8\u95ee\u7fdf\u4e39\u9752\uff0c\u201c\u4f60\u5230\u5efa\u90ba\u6765\u6709\u4ec0\u4e48\u4e8b\uff0c\u9700\u8981\u5750\u4f4f\u6211\u4eec\u5b66\u6821\u7684\u62db\u5f85\u5bbe\u9986\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u4e0d\u662f\u60f3\u8ddf\u606a\u5c11\u60a8\u6328\u5f97\u8fd1\u4e9b\uff1f\u201d</p><p>\u3000\u3000\u201c\u62dc\u6258\uff0c\u201d\u5f20\u606a\u5408\u638c\u6c42\u9976\uff0c\u201c\u6211\u4e00\u4e2a\u9752\u6625\u70ed\u8840\u5c11\u5e74\u7ecf\u5f97\u8d77\u4f60\u8fd9\u4e48\u52fe\u5f15\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u624d\u4e0d\u4fe1\u606a\u5c11\u4f1a\u662f\u90a3\u79cd\u4eba\uff0c\u201d\u7fdf\u4e39\u9752\u5a9a\u7136\u4e00\u7b11\uff0c\u5c06\u989d\u524d\u7684\u53d1\u4e1d\u64a9\u5230\u6676\u83b9\u5254\u900f\u7684\u8033\u540e\uff0c\u59ff\u6001\u8fd8\u771f\u662f\u5f02\u5e38\u7684\u6027\u611f\u64a9\u4eba\uff0c\u201c\u5728\u6211\u770b\u6765\uff0c\u606a\u5c11\u662f\u4f1a\u5c06\u5973\u4eba\u7684\u9b42\u513f\u3001\u9b44\u513f\u3001\u9aa8\u8089\u90fd\u5403\u8fdb\u809a\u5b50\u91cc\u4e0d\u4f1a\u5410\u6e23\u7684\u90a3\u79cd\u4eba\uff0c\u6240\u4ee5\u6211\u624d\u4e0d\u628a\u536b\u5170\u90a3\u5c0f\u59ae\u5b50\u5e26\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5f88\u65e0\u8bed\uff0c\u8eab\u8fb9\u7684\u8fd9\u4e48\u591a\u4e3d\u4eba\u91cc\uff0c\u5c31\u7b97\u5f88\u957f\u65f6\u95f4\u6ca1\u6709\u89c1\u9762\uff0c\u4e5f\u5c31\u7fdf\u4e39\u9752\u6562\u62ff\u8bdd\u76f4\u63a5\u6233\u81ea\u5df1\uff0c\u770b\u5979\u98ce\u60c5\u65e0\u8fb9\u7684\u6a21\u6837\uff0c\u770b\u4e0a\u53bb\u8273\u4e3d\u65e0\u53cc\uff0c\u8bf1\u60d1\u5f97\u9910\u5385\u91cc\u7684\u4f8d\u5e94\u751f\u4e22\u9b42\u843d\u9b44\uff0c\u5f20\u606a\u5f88\u60f3\u7ad9\u8d77\u6765\u544a\u8beb\u7ad9\u5728\u4e00\u8fb9\u7684\u4e24\u540d\u4f8d\u5e94\u751f\u7fdf\u4e39\u9752\u7edd\u5bf9\u662f\u4ed6\u4eec\u60f9\u4e0d\u8d77\u7684\u5371\u9669\u89d2\u8272\uff1b\u8fd8\u771f\u4e0d\u6562\u8ddf\u7fdf\u4e39\u9752\u901e\u5634\u820c\u4e4b\u5229\uff0c\u7b11\u4e86\u7b11\uff0c\u7b97\u662f\u8ba4\u8f93\uff0c\u5c06\u4e00\u672c\u83dc\u5355\u9012\u5230\u5979\u9762\u524d\u5404\u81ea\u70b9\u9910\u3002</p><p>\u3000\u3000\u70b9\u8fc7\u9910\u4e4b\u540e\uff0c\u7fdf\u4e39\u9752\u6b63\u8bf4\u51fa\u5979\u8fd9\u6b21\u6765\u5efa\u90ba\u7684\u76ee\u7684\uff1a\u201c\u5341\u6708\u4e0a\u65ec\u8981\u53c2\u52a0\u4e1c\u5927\u7ecf\u8d38\u73ed\u7684\u5165\u5b66\u8003\u8bd5\uff0c\u8fd9\u6b21\u8fc7\u6765\u662f\u53c2\u52a0\u4e1c\u5927\u4e3e\u529e\u7684\u8003\u524d\u57f9\u8bad\u73ed\uff0c\u8be5\u8f6e\u5230\u606a\u5c11\u4f60\u8bf4\u6709\u4ec0\u4e48\u4e8b\u627e\u6211\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5f20\u606a\u5f20\u4e86\u5f20\u5634\uff0c\u8bf4\u9053\uff0c\u201c\u90a3\u54b1\u4eec\u4ee5\u540e\u5c31\u662f\u6821\u53cb\u4e86\u3002\u201d\u5176\u5b9e\u60f3\u5230\u7fdf\u4e39\u9752\u6765\u5f53\u81ea\u5df1\u7684\u6821\u53cb\uff0c\u8fd8\u662f\u7a0d\u7a0d\u611f\u5230\u6709\u4e00\u4e9b\u5934\u75bc\u3002</p><p>\u3000\u3000\u7ecf\u8d38\u73ed\u662f\u4e1c\u5927\u6625\u5b63MBA\u7684\u4fd7\u79f0\uff0c\u5341\u6708\u4e0a\u65ec\u53c2\u52a0\u7edf\u4e00\u8003\u8bd5\uff0c\u660e\u5929\u4e00\u6708\u4e4b\u540e\u624d\u5165\u5b66\uff0c\u591a\u4e3a\u5728\u804c\u4eba\u5458\u6240\u9009\uff0c\u5165\u5b66\u8003\u8bd5\u6bd4\u79cb\u5b63MBA\u7b80\u5355\u4e00\u4e9b\uff0c\u5012\u662f\u6ca1\u6709\u60f3\u5230\u7fdf\u4e39\u9752\u6709\u5fc3\u7a81\u7834\u81ea\u5df1\u6765\u8bfbMBA\u3002\u4e1c\u5927\u56fd\u5546\u9662\u4e0e\u7814\u7a76\u751f\u9662\u8054\u5408\u4e3e\u529e\u7684MBA\u73ed\u5f00\u59cb\u4e8e\u4e5d\u56db\u5e74\uff0c\u4e5f\u662f\u56fd\u5185\u6700\u6709\u4ef7\u503c\u7684MBA\u57f9\u8bad\u57fa\u5730\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u201c\u8fd8\u4e0d\u6653\u5f97\u80fd\u4e0d\u80fd\u8003\u4e0a\u5462\uff0c\u597d\u4e9b\u5e74\u4e0d\u8bfb\u4e66\u4e86\uff0c\u201d\u7fdf\u4e39\u9752\u95ee\u9053\uff0c\u201c\u4ec0\u4e48\u4e8b\u60c5\uff0c\u606a\u5c11\u60a8\u8fd8\u6ca1\u6709\u8bf4\u5462\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u9ad8\u4e2d\u5c31\u8f8d\u5b66\u8d70\u4e0a\u90a3\u6761\u66f2\u6298\u7684\u4eba\u751f\u9053\u8def\uff0c\u867d\u8bf4MBA\u62db\u8003\u5f88\u6ce8\u91cd\u8003\u751f\u7684\u5de5\u4f5c\u7ecf\u9a8c\uff0c\u4f46\u662f\u5979\u80fd\u6709\u4fe1\u5fc3\u62a5\u8003\u4e1c\u5927\u7684MBA\uff0c\u8bf4\u5979\u8fd9\u4e9b\u5e74\u6ca1\u6709\u78b0\u8fc7\u4e66\u672c\uff0c\u5f20\u606a\u4e5f\u4e0d\u4fe1\uff0c\u4e0d\u8fc7\u4e8b\u60c5\u5c31\u4e0d\u9002\u5408\u8fd9\u65f6\u5019\u9ebb\u70e6\u7fdf\u4e39\u9752\u53bb\u505a\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u7b97\u4e86\uff0c\u90a3\u4ef6\u4e8b\u6211\u8fd8\u662f\u53e6\u627e\u522b\u4eba\u53bb\u529e\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u606a\u5c11\u7684\u4e8b\u60c5\u662f\u7d27\u7740\u8981\u5b8c\u6210\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u7b97\u662f\u5427\u201d\u5f20\u606a\u5c06\u4ed6\u5e0c\u671b\u901a\u8fc7\u4e16\u7eaa\u9526\u6e56\u5927\u9152\u5e97\u6350\u8d60\u4e24\u5343\u4e07\u7684\u4e8b\u60c5\u4f4e\u8bed\u8bf4\u7ed9\u7fdf\u4e39\u9752\u542c\uff0c\u201c\u539f\u672c\u6253\u7b97\u8ba9\u4f60\u5e2e\u52a9\u5c06\u8fd9\u4e24\u7b14\u94b1\u82b1\u51fa\u53bb\uff0c\u8fd9\u65f6\u5019\u603b\u4e0d\u80fd\u803d\u6401\u4f60\u7684\u65f6\u95f4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u606a\u5c11\u4fe1\u4efb\u6211\u5417\uff1f\u201d\u7fdf\u4e39\u9752\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u4fe1\u4efb\u4e0d\u4fe1\u4efb\u7684\uff0c\u4e8b\u60c5\u505a\u597d\u4e86\uff0c\u5c31\u6709\u4fe1\u4efb\uff1b\u4e8b\u60c5\u505a\u7838\u4e86\uff0c\u591a\u5927\u7684\u4fe1\u4efb\u4e5f\u8981\u6253\u6298\u6263\u4e86\u2026\u2026\u201d\u5f20\u606a\u5f00\u73a9\u7b11\u8bf4\u3002</p><p>\u3000\u3000\u201c\u90a3\u6211\u7ed9\u606a\u5c11\u4f60\u63a8\u8350\u4e00\u4e2a\u4eba\uff0c\u4ece\u4e8b\u804c\u4e1a\u6280\u672f\u6559\u80b2\u597d\u4e9b\u5e74\uff0c\u662f\u65b0\u829c\u5e02\u9ad8\u7ea7\u804c\u4e1a\u4e2d\u5b66\u7684\u6821\u957f\uff0c\u5728\u65b0\u829c\u5fb7\u9ad8\u671b\u91cd\uff0c\u60f3\u5fc5\u5f20\u526f\u5e02\u957f\u4e5f\u6709\u542c\u8bf4\u8fc7\u3002\u521d\u7ea7\u804c\u4e1a\u6280\u672f\u57f9\u8bad\u7684\u5de5\u4f5c\uff0c\u53ef\u4ee5\u501f\u7528\u65b0\u829c\u804c\u9ad8\u7684\u573a\u5730\u3001\u5e08\u8d44\u529b\u91cf\u53bb\u8fdb\u884c\uff1b\u8fd9\u7b14\u8d44\u91d1\u6350\u7ed9\u65b0\u829c\u5e02\uff0c\u5c31\u8ba9\u5e02\u91cc\u6210\u7acb\u4e13\u95e8\u8d26\u6237\u7ba1\u7406\u5c31\u53ef\u4ee5\u4e86\u3002\u53e6\u5916\uff0c\u65c5\u6e38\u5b66\u6821\u7684\u4e8b\u60c5\uff0c\u4e5f\u53ef\u4ee5\u8fd9\u4e48\u64cd\u4f5c\uff0c\u4e8b\u60c5\u5c31\u7b80\u5355\u591a\u4e86\uff0c\u4e13\u95e8\u8d26\u6237\u7684\u8d44\u91d1\u6709\u5f20\u526f\u5e02\u957f\u76ef\u7740\uff0c\u603b\u4e0d\u4f1a\u6709\u51fa\u591a\u5927\u7684\u4e71\u5b50\u3002\u201d</p></article>"}}}]);
