(window.webpackJsonp=window.webpackJsonp||[]).push([[796],{819:function(p){p.exports={chapterTitle:null,title:"\u7b2c794\u7ae0 \u52a3\u8d28\u7ade\u4e89",content:"<article><h2>\u7b2c794\u7ae0 \u52a3\u8d28\u7ade\u4e89</h2><p>\u3000\u3000\u5468\u747e\u745c\u8d39\u4e86\u4e00\u756a\u5fc3\u673a\u5728\u91d1\u5c71\u4e3e\u529e\u7535\u5b50\u4ea7\u4e1a\u53d1\u5c55\u8bba\u575b\uff0c\u8fd8\u662f\u4e3a\u7535\u5b50\u5de5\u4e1a\u5236\u9020\u57fa\u5730\u2014\u2014\u91d1\u5c71\u7535\u5b50\u5de5\u4e1a\u56ed\u9020\u52bf\uff0c\u5927\u4f1a\u540e\u534a\u6bb5\u7684\u8bae\u9898\u4e3b\u8981\u96c6\u4e2d\u91d1\u5c71\u7535\u5b50\u56ed\u7684\u53d1\u5c55\u4e0a\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u4fe1\u606f\u4ea7\u4e1a\u5206\u5de5\u5177\u6709\u5782\u76f4\u7ed3\u6784\u7279\u5f81\uff0c\u7f8e\u56fd\u3001\u65e5\u672c\u3001\u6b27\u6d32\u7b49\u53d1\u8fbe\u56fd\u5bb6\u4e0e\u5730\u533a\u51ed\u501f\u5176\u8d44\u91d1\u3001\u6280\u672f\u548c\u54c1\u724c\u7684\u4f18\u52bf\uff0c\u4e3b\u8981\u4ece\u4e8b\u7cfb\u7edf\u96c6\u6210\u548c\u9ad8\u79d1\u6280\u4ea7\u54c1\u7684\u5f00\u53d1\u4e0e\u9500\u552e\uff0c\u4e3b\u5bfc\u7740\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u8d8b\u52bf\u548c\u683c\u5c40\uff0c\u5904\u4e8e\u4ef7\u503c\u94fe\u7684\u9ad8\u7aef\uff0c\u8fd9\u662f\u65e0\u5eb8\u7f6e\u7591\u7684\u4e8b\u5b9e\u3002\u5185\u5730\u7684\u4f18\u52bf\u5728\u54ea\u91cc\uff1f\u4e0d\u9700\u8981\u7f9e\u4e8e\u627f\u8ba4\uff0c\u5185\u5730\u7684\u4f18\u52bf\u5c31\u662f\u5ec9\u4ef7\u7684\u52b3\u52a8\u529b\u3002\u4ee5\u5ec9\u4ef7\u7684\u52b3\u52a8\u529b\u4e3a\u57fa\u7840\uff0c\u5229\u7528\u6d77\u5916\u7535\u5b50\u5382\u5546\u6210\u719f\u7684\u6280\u672f\uff0c\u53d1\u5c55\u52b3\u52a8\u5bc6\u96c6\u578b\u52a0\u5de5\u3001\u7ec4\u88c5\u548c\u5236\u9020\u4e1a\uff0c\u624d\u662f\u5185\u5730\u73b0\u9636\u6bb5\u5728\u4fe1\u606f\u4ea7\u4e1a\u56fd\u9645\u5316\u5206\u5de5\u4e2d\u6240\u80fd\u627e\u5230\u7684\u5730\u4f4d\uff0c\u6b63\u662f\u6709\u8fd9\u6837\u7684\u6e05\u9192\u8ba4\u77e5\uff0c\u5728\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u3001\u91d1\u5c71\u5e02\u59d4\u3001\u5e02\u653f\u5e9c\u7684\u9f0e\u529b\u652f\u6301\uff0c\u5609\u4fe1\u7535\u5b50\u8054\u5408\u6b63\u6cf0\u96c6\u56e2\u5728\u91d1\u5c71\u6295\u5165\u5de8\u8d44\u5efa\u8bbe\u91d1\u5c71\u7535\u5b50\u5de5\u4e1a\u56ed\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u662f\u845b\u660e\u4fe1\u4e3a\u91d1\u5c71\u7535\u5b50\u5de5\u4e1a\u56ed\u5b9a\u4f4d\u7684\u4e00\u6bb5\u8bb2\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u539f\u6253\u7b97\u5728\u4e0b\u5348\u7684\u6d3b\u52a8\u7ed3\u675f\u4e4b\u524d\u5077\u5077\u6e9c\u51fa\u53bb\uff0c\u4ed6\u7684\u5ff5\u5934\u8fd8\u6ca1\u6709\u7b49\u5b9e\u65bd\uff0c\u8d75\u6709\u4f26\u7684\u79d8\u4e66\u9648\u660e\u5e06\u8dd1\u8fc7\u6765\u544a\u8bc9\u4ed6\uff0c\u4e0b\u5348\u51fa\u5e2d\u6d3b\u52a8\u7684\u6c5f\u5357\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\u6881\u4f1f\u53d1\u5e0c\u671b\u5728\u665a\u4e0a\u7684\u9152\u4f1a\u80fd\u6709\u4e0e\u4ed6\u4ea4\u8c08\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u6ca1\u6709\u72c2\u5984\u5230\u8fde\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\u7684\u9080\u8bf7\u90fd\u7f6e\u4e4b\u4e0d\u7406\u7684\u5730\u6b65\uff0c\u867d\u7136\u4ed6\u4e0e\u674e\u8fdc\u6e56\u4e4b\u95f4\u66fe\u7ecf\u53d1\u751f\u7684\u5bf9\u6297\u5728\u9ad8\u5c42\u7684\u5708\u5b50\u91cc\u5df2\u7ecf\u4e0d\u662f\u4ec0\u4e48\u79d8\u95fb\u3002</p><p>\u3000\u3000\u665a\u4e0a\u9152\u4f1a\u4e5f\u5b89\u6392\u5728\u4f1a\u5c55\u4e2d\u5fc3\u5185\u8fdb\u884c\uff0c\u6362\u4e86\u4e00\u4e2a\u7a0d\u5c0f\u4e00\u4e9b\u7684\u573a\u9986\u3002\u867d\u7136\u91d1\u5c71\u7535\u5b50\u4ea7\u4e1a\u53d1\u5c55\u8bba\u575b\u5438\u5f15\u4e86\u56fd\u5185\u4f17\u591a\u5a92\u4f53\u7684\u5173\u6ce8\uff0c\u8bb8\u591a\u8bb0\u8005\u4e91\u96c6\u5728\u4f1a\u573a\u5185\u5916\uff0c\u4f46\u662f\u665a\u4e0a\u7684\u9152\u4f1a\u5e76\u6ca1\u6709\u9080\u8bf7\u8fd9\u4e9b\u8bb0\u8005\u53c2\u52a0\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u6881\u4f1f\u53d1\u7684\u4f1a\u9762\u5b89\u6392\u5728\u4e00\u4e2a\u5355\u72ec\u7684\u5bb4\u4f1a\u5385\u91cc\uff0c\u5012\u4e5f\u4e0d\u662f\u53ea\u6709\u5f20\u606a\u4e0e\u6881\u4f1f\u53d1\u4e24\u4e2a\u4eba\uff0c\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\u5468\u747e\u745c\u3001\u5e02\u957f\u8d75\u6709\u4f26\u3001\u56fd\u5bb6\u8ba1\u59d4\u526f\u4e3b\u4efb\u845b\u5efa\u5fb7\u3001\u5609\u4fe1\u96c6\u56e2\u8463\u4e8b\u5c40\u4e3b\u5e2d\u845b\u660e\u4fe1\u3001\u6b63\u6cf0\u96c6\u56e2\u8463\u4e8b\u957f\u8c22\u6c49\u9756\u7b49\u4eba\u90fd\u5728\u573a\u3002\u770b\u5230\u8fd9\u4e9b\u4eba\u7269\u5728\u573a\uff0c\u53f6\u5efa\u658c\u7ad9\u5728\u95e8\u53e3\uff0c\u4fa7\u5934\u8ddf\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u662f\u8981\u4e3e\u529e\u4e00\u4e2a\u771f\u6b63\u7684\u7535\u5b50\u4ea7\u4e1a\u53d1\u5c55\u7684\u5cf0\u4f1a\u8bba\u575b\u554a\uff01\u201d</p><p>\u3000\u3000\u6881\u4f1f\u53d1\u662f\u4e2a\u8eab\u6750\u9b41\u68a7\u7684\u9ad8\u4e2a\u5b50\uff0c\u79c3\u4e86\u524d\u534a\u7247\u7684\u8111\u95e8\u6cb9\u5149\u53d1\u4eae\uff0c\u5f20\u606a\u5728\u53f6\u5efa\u658c\u3001\u9648\u4fe1\u751f\u3001\u8bb8\u9e3f\u4f2f\u7684\u966a\u540c\u4e0b\u8d70\u8fdb\u5bb4\u4f1a\u5385\uff0c\u4ed6\u5c31\u5927\u6b65\u8d70\u8fc7\u6765\u4e3b\u52a8\u63e1\u4f4f\u5f20\u606a\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u56fd\u5185\u7684\u4fe1\u606f\u4ea7\u4e1a\uff0c\u4f60\u4e0e\u845b\u5efa\u5fb7\u4e3b\u4efb\u90fd\u662f\u6700\u91cd\u91cf\u7ea7\u7684\u4eba\u7269\u4e86\uff0c\u91d1\u5c71\u4e3e\u529e\u8fd9\u6b21\u8bba\u575b\uff0c\u80fd\u9080\u8bf7\u5230\u4f60\u4eec\u4fe9\u4eba\u5230\u573a\uff0c\u5c31\u7b97\u662f\u83b7\u5f97\u5f88\u5927\u7684\u6210\u529f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c82\u6562\u8ddf\u845b\u4e3b\u4efb\u76f8\u63d0\u5e76\u8bba\uff1f\u6881\u7701\u957f\u62ac\u4e3e\u6211\u4e86\u3002\u201d\u5f20\u606a\u8c26\u900a\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u4e0d\u8fc7\u662f\u8eab\u5c45\u7279\u6b8a\u7684\u4f4d\u5b50\uff0c\u8eab\u4e3a\u56fd\u5bb6\u8ba1\u59d4\u526f\u4e3b\u4efb\u53c8\u517c\u4efb\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u9886\u5bfc\u5c0f\u7ec4\u7ec4\u957f\uff0c\u4ed6\u5728\u4e1a\u5185\u7684\u5f71\u54cd\u529b\u662f\u4ed6\u6240\u5904\u7684\u5b98\u4f4d\u8d4b\u4e88\u7684\uff0c\u603b\u6709\u4eba\u8d70\u8336\u51c9\u7684\u4e00\u5929\uff1b\u800c\u5f20\u606a\u4e0d\u4ec5\u7edf\u6cbb\u7740\u6574\u4e2a\u9526\u6e56\u4f53\u7cfb\uff0c\u8fd8\u5728\u80cc\u540e\u63a8\u52a8\u4e2d\u6676\u5fae\u82af\u7684\u6676\u5706\u5382\u9879\u76ee\u3001\u63a8\u52a8\u5efa\u90ba\u5e02\u6570\u5b57\u957f\u5eca\u6784\u60f3\u4ea7\u4e1a\u6218\u7565\u7684\u542f\u52a8\u3001\u63a8\u52a8\u6570\u5b57\u624b\u673a\u4ea7\u4e1a\u96c6\u7fa4\u5728\u5efa\u90ba\u3001\u6d77\u5dde\u5b55\u80b2\u751f\u6839\u3002\u542c\u6881\u4f1f\u53d1\u5c06\u5f20\u606a\u4e0e\u845b\u5efa\u5fb7\u4e4b\u6d41\u7684\u4eba\u7269\u76f8\u63d0\u5e76\u8bba\uff0c\u53f6\u5efa\u658c\u8fd8\u89c9\u5f97\u76f8\u5f53\u4e0d\u516c\u5e73\u5462\u3002</p><p>\u3000\u3000\u6881\u4f1f\u53d1\u53ef\u4e0d\u7ba1\u53f6\u5efa\u658c\u5728\u65c1\u8fb9\u4f1a\u600e\u4e48\u60f3\uff0c\u4ed6\u5206\u522b\u4e0e\u53f6\u5efa\u658c\u3001\u9648\u4fe1\u751f\u3001\u8bb8\u9e3f\u4f2f\u63e1\u8fc7\u624b\u4e4b\u540e\uff0c\u53c8\u95ee\u5f20\u606a\uff1a\u201c\u6211\u7279\u610f\u9080\u8bf7\u4f60\u8fc7\u6765\uff0c\u662f\u60f3\u542c\u542c\u4f60\u5bf9\u91d1\u5c71\u53d1\u5c55\u4fe1\u606f\u4ea7\u4e1a\u7684\u770b\u6cd5\uff0c\u6709\u4f26\u5e02\u957f\u521a\u521a\u8ddf\u6211\u8bf4\u8d77\u6765\uff0c\u9526\u6e56\u4e5f\u6709\u610f\u5728\u91d1\u5c71\u6295\u8d44\uff1f\u201d</p><p>\u3000\u3000\u91d1\u5c71\u53ea\u662f\u4eab\u53d7\u526f\u7701\u7ea7\u5f85\u9047\u7684\u7701\u4f1a\u57ce\u5e02\uff0c\u4e0e\u526f\u7701\u7ea7\u884c\u653f\u7f16\u5236\u7684\u57ce\u5e02\u4e0d\u540c\uff0c\u5728\u7ecf\u6d4e\u53d1\u5c55\u4e0a\uff0c\u8fd8\u53d7\u5230\u7701\u91cc\u7684\u6307\u5bfc\u4e0e\u5236\u7ea6\uff0c\u6881\u4f1f\u53d1\u627e\u5f20\u606a\u95ee\u4ed6\u5bf9\u91d1\u5c71\u53d1\u5c55\u4fe1\u606f\u4ea7\u4e1a\u7684\u770b\u6cd5\uff0c\u4e0d\u7b97\u662f\u903e\u8d8a\u3002</p><p>\u3000\u3000\u6c5f\u5357\u7701\u53bb\u5e74\u4e0b\u534a\u5e74\u5b98\u573a\u5927\u53d8\u52a8\uff0c\u9664\u4e86\u7701\u59d4\u4e66\u8bb0\u5c1a\u5b66\u4e49\u4e4b\u5916\uff0c\u7701\u5e38\u59d4\u6210\u5458\u6362\u4e86\u4e94\u4eba\uff0c\u8fd9\u5728\u56fd\u5185\u4e5f\u662f\u6781\u4e3a\u7f55\u89c1\u7684\u5b98\u573a\u5730\u9707\uff0c\u6881\u4f1f\u53d1\u773c\u4e0b\u8fd8\u662f\u4ee3\u7701\u957f\uff0c\u8fd8\u8981\u7b49\u4e09\u6708\u5e95\u7684\u7701\u4eba\u5927\u4f1a\u8bae\u53ec\u5f00\u4e4b\u540e\uff0c\u624d\u80fd\u5c06\u201c\u4ee3\u201d\u5b57\u53bb\u6389\u3002</p><p>\u3000\u3000\u201c\u6211\u624d\u8584\u5b66\u6d45\uff0c\u9526\u6e56\u80fd\u7a0d\u6709\u6210\u5c31\uff0c\u9664\u4e86\u9526\u6e56\u7ba1\u7406\u5c42\u7684\u52aa\u529b\u4e4b\u5916\uff0c\u66f4\u5927\u7684\u56e0\u7d20\u4e5f\u662f\u65f6\u8fd0\u4f7f\u7136\uff0c\u4eca\u5929\u5728\u4f1a\u573a\u4e0a\u53d1\u8a00\u7684\u8bf8\u4f4d\u90fd\u8ba9\u6211\u53d7\u76ca\u532a\u6d45\uff0c\u8981\u8bf4\u6709\u770b\u6cd5\uff0c\u4e5f\u90fd\u662f\u6d45\u8584\u7684\u3001\u8bf4\u51fa\u6765\u4f1a\u8ba9\u5927\u5bb6\u54c2\u7136\u800c\u7b11\u7684\u3002\u201d\u5f20\u606a\u4e0d\u4ea2\u4e0d\u5351\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u5c31\u662f\u8bf4\u6709\u60f3\u6cd5\u55bd\uff0c\u201d\u845b\u660e\u4fe1\u7aef\u7740\u9ad8\u811a\u73bb\u7483\u676f\u6084\u7136\u8d70\u8fd1\uff0c\u624b\u8f7b\u8f7b\u6447\u6643\u7740\u73bb\u7483\u676f\u91cc\u7684\u6bb7\u7ea2\u9152\u6db2\uff0c\u201c\u6211\u4e5f\u5f88\u60f3\u542c\u542c\u4f60\u7684\u2018\u6d45\u8584\u2019\u7684\u770b\u6cd5\u2026\u2026\u201d\u845b\u660e\u4fe1\u867d\u7136\u4e0d\u6562\u8f7b\u89c6\u5f20\u606a\uff0c\u4f46\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u8981\u62ff\u8a00\u8bed\u523a\u4e00\u4e0b\u4ed6\uff0c\u8bf4\u5230\u201c\u6d45\u8584\u201d\u4e8c\u5b57\u65f6\uff0c\u8fd8\u7279\u610f\u52a0\u91cd\u4e86\u4e00\u4e0b\u8bed\u6c14\u3002</p><p>\u3000\u3000\u72ec\u7acb\u5bb4\u4f1a\u5385\u91cc\u7684\u4f17\u4eba\u6ce8\u610f\u529b\u65e9\u5c31\u7ed9\u5438\u5f15\u5230\u8fd9\u91cc\u3002</p><p>\u3000\u3000\u201c\u82e5\u662f\u8ba9\u6211\u4e3b\u6301\u5609\u4fe1\u7535\u5b50\uff0c\u5c31\u4e0d\u4f1a\u8ba9\u5609\u4fe1\u7535\u5b50\u5728\u4eca\u5929\u7684\u4f1a\u573a\u53d1\u51fa\u5982\u6b64\u5984\u81ea\u83f2\u8584\u7684\u58f0\u97f3\u2026\u2026\u201d\u5f20\u606a\u54c2\u7136\u4e00\u7b11\uff0c\u845b\u660e\u4fe1\u62ff\u8a00\u8bed\u523a\u4ed6\uff0c\u4ed6\u81ea\u7136\u8981\u53cd\u51fb\u56de\u53bb\u3002</p><p>\u3000\u3000\u8bf4\u8005\u65e0\u5fc3\uff0c\u542c\u8005\u6709\u610f\uff0c\u542c\u4e86\u5f20\u606a\u7684\u8bdd\uff0c\u8c22\u6c49\u9756\u5fc3\u91cc\u4e00\u8df3\uff1a\u83ab\u975e\u5f20\u606a\u771f\u6709\u610f\u67d3\u6307\u5609\u4fe1\u7535\u5b50\uff0c\u8fd9\u51e0\u5929\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u4e0a\uff0c\u5609\u4fe1\u7535\u5b50\u80a1\u4ef7\u7684\u5f02\u52a8\uff0c\u6216\u8bb8\u4e0d\u662f\u9526\u6e56\u7684\u865a\u5f20\u58f0\u52bf\uff1f</p><p>\u3000\u3000\u201c\u5984\u81ea\u83f2\u8584\uff0c\u201d\u845b\u660e\u4fe1\u7709\u5934\u5fae\u626c\uff0c\u201c\u4f55\u8c13\u5984\u81ea\u83f2\u8584\uff1f\u201d</p><p>\u3000\u3000\u6881\u4f1f\u53d1\u7b49\u4eba\u4e5f\u662f\u7709\u5934\u5fae\u8e59\uff0c\u5f20\u606a\u53ef\u4e0d\u4ec5\u4ec5\u662f\u6279\u8bc4\u5609\u4fe1\u7535\u5b50\u554a\uff0c\u8010\u5fc3\u542c\u5f20\u606a\u8bf4\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u201c\u6539\u9769\u5f00\u653e\u4e4b\u521d\uff0c\u4e2d\u56fd\u7684\u4fe1\u606f\u4ea7\u4e1a\u5c31\u4ece\u6700\u521d\u7684\u52a0\u5de5\u8d38\u6613\u5165\u624b\uff0c\u4f9d\u9760\u52b3\u52a8\u529b\u7684\u5ec9\u4ef7\u4f18\u52bf\u6765\u53c2\u4e0e\u4fe1\u606f\u4ea7\u4e1a\u7684\u56fd\u9645\u5316\u5206\u5de5\u3002\u90fd\u5341\u591a\u5e74\u7684\u65f6\u5149\u8fc7\u53bb\u4e86\uff0c\u5609\u4fe1\u7535\u5b50\u76ee\u5149\u8fd8\u53ea\u76ef\u7740\u6574\u6761\u4ea7\u4e1a\u4ef7\u503c\u94fe\u7684\u6700\u4f4e\u7aef\uff0c\u4e0d\u662f\u5984\u81ea\u83f2\u8584\u662f\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u8a00\u8f9e\u5c16\u9510\uff0c\u4f83\u4f83\u800c\u8c08\uff0c\u201c\u901a\u8fc7\u4e0d\u65ad\u7684\u63d0\u9ad8\u5de5\u4eba\u7684\u52b3\u52a8\u5f3a\u5ea6\u4e0e\u5ef6\u957f\u52b3\u52a8\u65f6\u95f4\u3001\u538b\u5236\u5de5\u4eba\u85aa\u8d44\u7b49\u79cd\u79cd\u624b\u6bb5\uff0c\u901a\u8fc7\u538b\u67de\u5de5\u4eba\u7684\u52b3\u52a8\u5269\u4f59\u4ef7\u503c\u6765\u83b7\u5f97\u53ef\u89c2\u5229\u6da6\u7684\u786e\u662f\u4e00\u6761\u8def\uff0c\u4f46\u662f\u8fd9\u79cd\u5e02\u573a\u7ade\u4e89\u6c38\u8fdc\u662f\u79cd\u52a3\u8d28\u7684\u5e02\u573a\u7ade\u4e89\uff0c\u96be\u9053\u845b\u5148\u751f\u60a8\u5c31\u770b\u4e0d\u5230\u4e00\u6761\u4e0e\u4e4b\u4e0d\u540c\u7684\u9053\u8def\u53ef\u4ee5\u9009\u62e9\u53bb\u8d70\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u6697\u6697\u611f\u6168\uff0c\u60f3\u4e0d\u5230\u5f20\u606a\u7684\u8a00\u8f9e\u8fd8\u771f\u662f\u5c16\u9510\u554a\uff0c\u8fd9\u5df4\u638c\u53ef\u4e0d\u4ec5\u4ec5\u53ea\u62bd\u5728\u845b\u660e\u4fe1\u4e00\u4eba\u7684\u8138\u4e0a\u3002\u91d1\u5c71\u7535\u5b50\u5de5\u4e1a\u56ed\u662f\u6c5f\u5357\u7701\u3001\u91d1\u5c71\u5e02\u91cd\u70b9\u63a8\u8fdb\u7684\u5927\u578b\u9ad8\u65b0\u4ea7\u4e1a\u9879\u76ee\u3002\u9664\u4e86\u845b\u660e\u4fe1\u8138\u9762\u9752\u7ea2\u4e4b\u5916\uff0c\u8c22\u6c49\u9756\u3001\u5468\u747e\u745c\u7b49\u4eba\u8138\u8272\u4e5f\u90fd\u4e0d\u597d\u770b\uff0c\u845b\u5efa\u5fb7\u7709\u5934\u626c\u7740\u7785\u5411\u5f20\u606a\uff0c\u6216\u8bb8\u5bf9\u5f20\u606a\u7684\u72c2\u5984\u6709\u6240\u8f7b\u89c6\u5427\uff0c\u5012\u662f\u6881\u4f1f\u53d1\u542c\u4e86\u4e4b\u540e\u82e5\u6709\u6240\u601d\u7684\u6a21\u6837\uff0c\u4e0d\u6e05\u695a\u4ed6\u5185\u5fc3\u7684\u771f\u5b9e\u611f\u53d7\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u542c\u5230\u5f20\u606a\u8fd9\u901a\u8bdd\uff0c\u5fc3\u91cc\u5012\u662f\u75db\u5feb\uff0c\u4e0d\u8fc7\u4e5f\u6697\u6697\u62c5\u5fe7\uff0c\u6307\u671b\u522b\u4eba\u6c14\u5ea6\u8fc7\u4eba\u3001\u80fd\u6709\u5bb9\u4eba\u4e4b\u91cf\u6c38\u8fdc\u662f\u4e00\u79cd\u5371\u9669\u7684\u8d4c\u535a\uff0c\u4f46\u662f\u6ca1\u6709\u8fd9\u6837\u7684\u5192\u9669\uff0c\u683c\u5c40\u5c31\u5f88\u96be\u6253\u5f00\u3002\u5468\u747e\u745c\u3001\u845b\u5efa\u5fb7\u3001\u845b\u660e\u4fe1\u8fd9\u4e9b\u4eba\u5012\u4e5f\u7b97\u4e86\uff0c\u5df2\u7ecf\u6760\u4e0a\u4e86\uff0c\u5c31\u6ca1\u6709\u9000\u7f29\u7684\u4f59\u5730\uff0c\u4f46\u662f\u6881\u4f1f\u53d1\u3001\u8d75\u6709\u4f26\u542c\u4e86\u8fd9\u756a\u8bdd\u5fc3\u91cc\u4f1a\u6709\u4ec0\u4e48\u611f\u53d7\uff1f</p><p>\u3000\u3000\u5f20\u606a\u610f\u6001\u72b9\u8db3\uff0c\u9648\u4fe1\u751f\u8fd9\u65f6\u5019\u5374\u662f\u53bb\u770b\u7ad9\u5728\u5f20\u606a\u8eab\u540e\u7684\u8bb8\u9e3f\u4f2f\u3002</p><p>\u3000\u3000\u9526\u6e56\u7684\u9053\u8def\u8fd8\u8981\u7ee7\u7eed\u8d70\u4e0b\u53bb\uff0c\u52bf\u5fc5\u8981\u7275\u6d89\u8fdb\u66f4\u590d\u6742\u3001\u66f4\u9ad8\u5c42\u6b21\u7684\u6597\u4e89\u4e2d\u53bb\uff0c\u8fd9\u662f\u56fd\u5185\u4ea7\u4e1a\u53d1\u5c55\u7684\u7279\u8d28\uff0c\u4e0e\u5b98\u65b9\u7684\u7ea0\u7f20\u8981\u4f34\u968f\u9526\u6e56\u65e5\u540e\u53d1\u5c55\u7684\u6bcf\u4e00\u6b65\u524d\u884c\uff0c\u9526\u6e56\u80cc\u540e\u3001\u5f20\u606a\u8eab\u8fb9\u5c31\u6ce8\u5b9a\u9700\u8981\u8bb8\u9e3f\u4f2f\u8fd9\u6837\u6709\u7740\u7279\u6b8a\u7ecf\u5386\u4e0e\u89c1\u8bc6\u7684\u667a\u56ca\u578b\u4eba\u7269\u5b58\u5728\u3002</p><p>\u3000\u3000\u4f17\u4eba\u5fc3\u601d\u5404\u5f02\uff0c\u8fd9\u5bb4\u4f1a\u5385\u91cc\u9661\u7136\u5c31\u9759\u9ed8\u4e0b\u6765\uff0c\u5f20\u606a\u795e\u8272\u7a0d\u655b\uff0c\u89c1\u6881\u4f1f\u53d1\u82e5\u6709\u6240\u601d\u3001\u4e0d\u542d\u58f0\uff0c\u4ed6\u8fd8\u662f\u671d\u8138\u8272\u9752\u767d\u7684\u845b\u660e\u4fe1\u8bf4\u9053\uff1a\u201c\u6216\u8bb8\u6211\u5e74\u8f7b\u6c14\u76db\u72c2\u5984\u4e86\uff0c\u4e5f\u5c31\u8fd9\u70b9\u6d45\u8584\u4e4b\u89c1\u8bf4\u51fa\u6765\u4f9b\u845b\u603b\u4f60\u54c2\u7136\u4e00\u7b11\u3002\u201d</p><p>\u3000\u3000\u845b\u660e\u4fe1\u518d\u50bb\u4e5f\u77e5\u9053\u5f20\u606a\u8fd9\u8bdd\u662f\u8bf4\u7ed9\u6881\u4f1f\u53d1\u542c\u7684\uff0c\u5fc3\u91cc\u6c14\u4e0d\u8fc7\u7ed9\u5f20\u606a\u8fd9\u4e48\u6559\u8bad\uff0c\u504f\u504f\u53c8\u65e0\u6cd5\u53cd\u9a73\u5f20\u606a\u7684\u72c2\u5984\u4e4b\u8a00\uff0c\u8bf4\u4ed6\u72c2\u5984\u5427\uff0c\u9526\u6e56\u5149\u8292\u95ea\u70c1\u7684\u5cd9\u7acb\u5728\u773c\u524d\uff0c\u53ea\u80fd\u9f3b\u8154\u91cc\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u8868\u8fbe\u5fc3\u91cc\u7684\u4e0d\u5fff\u3002</p><p>\u3000\u3000\u201c\u8fd9\u756a\u8bdd\u53d1\u804b\u9707\u8069\u554a\uff01\u503c\u5f97\u6df1\u601d\uff0c\u201d\u542c\u5230\u845b\u660e\u4fe1\u7684\u51b7\u54fc\uff0c\u6881\u4f1f\u53d1\u8fd9\u624d\u611f\u6168\u8bf4\u4e86\u4e00\u53e5\uff0c\u63a5\u4e0b\u6765\u8bed\u6c14\u53c8\u8f6c\uff0c\u201c\u845b\u5148\u751f\u7684\u52a1\u5b9e\u601d\u8def\u2026\u2026\u4e5f\u662f\u503c\u5f97\u501f\u9274\u601d\u8003\u7684\uff0c\u8981\u4e0d\u662f\u6015\u803d\u6401\u5927\u5bb6\u7528\u9910\uff0c\u6211\u5012\u662f\u5e0c\u671b\u5728\u8fd9\u91cc\u80fd\u597d\u597d\u7684\u8fa9\u4e00\u8fa9\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u521a\u53d1\u8868\u610f\u89c1\uff0c\u5c31\u641e\u5f97\u706b\u836f\u5473\u5f25\u6f2b\uff0c\u8c01\u4f1a\u6ca1\u4e8b\u7ad9\u51fa\u6765\u8fa9\u8bba\uff1f\u518d\u8bf4\u6881\u4f1f\u53d1\u4e5f\u4e0d\u53ef\u80fd\u5e0c\u671b\u845b\u660e\u4fe1\u4e0e\u5f20\u606a\u5f53\u573a\u4e89\u5f97\u9762\u7ea2\u8033\u8d64\u3002\u521a\u624d\u7684\u8bdd\u9898\u90fd\u6ca1\u6cd5\u7ee7\u7eed\u4e0b\u53bb\uff0c\u4f17\u4eba\u53ea\u6709\u5404\u81ea\u627e\u98df\u7269\u586b\u8fdb\u809a\u5b50\u91cc\uff0c\u559d\u9152\u7684\u5fc3\u60c5\u4e5f\u90fd\u51b7\u6de1\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e00\u884c\u4eba\u5f88\u5feb\u5c31\u544a\u8f9e\u79bb\u5f00\uff0c\u7fdf\u4e39\u9752\u3001\u5468\u6e38\u7b49\u4eba\u5728\u5927\u5bb4\u4f1a\u5385\u91cc\u5012\u662f\u6e38\u5203\u6709\u4f59\u7684\u8ddf\u4e0e\u4f1a\u4ee3\u8868\u4ea4\u9645\uff0c\u770b\u5f20\u606a\u4ed6\u4eec\u5f88\u5feb\u5c31\u4ece\u5c0f\u5bb4\u4f1a\u5385\u91cc\u51fa\u6765\uff0c\u4e5f\u5c31\u8ddf\u7740\u4e00\u8d77\u79bb\u5f00\u3002</p><p>\u3000\u3000\u8d70\u5728\u4f1a\u5c55\u4e2d\u5fc3\u4e94\u5ea7\u4f1a\u5c55\u5efa\u7b51\u73af\u62b1\u7684\u5bbd\u9614\u5e7f\u573a\u4e0a\uff0c\u5f20\u606a\u624d\u95ee\u8bb8\u9e3f\u4f2f\uff1a\u201c\u6881\u4f1f\u53d1\u6709\u6ca1\u6709\u53ef\u80fd\u7ed9\u90a3\u756a\u8bdd\u89e6\u52a8\uff1f\u201d</p><p>\u3000\u3000\u201c\u6881\u4f1f\u53d1\u5b66\u8005\u51fa\u8eab\uff0c\u4ece\u4ed6\u4ee5\u5f80\u7684\u5c65\u5386\u6765\u770b\uff0c\u4ed6\u672c\u4eba\u8fd8\u662f\u6bd4\u8f83\u503e\u5411\u4e8e\u5927\u5200\u9614\u65a7\u4f3c\u7684\u6539\u9769\uff0c\u5948\u4f55\u56fd\u5185\u7684\u73b0\u5b9e\uff0c\u5f88\u5c11\u80fd\u5bb9\u5fcd\u8fc7\u4e8e\u6fc0\u70c8\u7684\u53d8\u9769\uff0c\u6881\u4f1f\u53d1\u53c8\u662f\u90a3\u79cd\u5f88\u806a\u660e\u80fd\u751f\u5b58\u5728\u5939\u7f1d\u91cc\u7684\u4eba\u7269\uff0c\u6240\u4ee5\u624d\u4f1a\u7ed9\u4eba\u9762\u76ee\u6a21\u6837\u7684\u611f\u89c9\uff0c\u201d\u8bb8\u9e3f\u4f2f\u5206\u6790\u9053\uff0c\u201c\u4f46\u662f\u4f60\u6709\u6ca1\u6709\u6ce8\u610f\u5230\u4ed6\u6700\u540e\u7684\u90a3\u53e5\u8bdd\uff1f\u90a3\u53e5\u8bdd\u8fd8\u662f\u5b89\u6170\u845b\u660e\u4fe1\u7684\u6210\u5206\u5c45\u591a\uff0c\u4e00\u4e24\u79d2\u7684\u505c\u987f\uff0c\u662f\u4ed6\u5728\u5bfb\u627e\u5408\u9002\u7684\u5b89\u6170\u8bdd\uff0c\u4ed6\u6c89\u9ed8\u7684\u90a3\u4f1a\u513f\u5de5\u592b\uff0c\u5e94\u8be5\u5b8c\u5168\u662f\u7ed9\u4f60\u7684\u90a3\u756a\u8bdd\u5360\u636e\u4e86\u8111\u6d77\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u770b\u4eba\u7ec6\u81f4\u5165\u5fae\uff0c\u6709\u7740\u641c\u80a0\u522e\u809a\u7684\u6df1\u5165\u660e\u4e86\uff0c\u542c\u8bb8\u9e3f\u4f2f\u8fd9\u4e48\u8bf4\uff0c\u5f20\u606a\u4e5f\u662f\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u5c06\u6881\u4f1f\u53d1\u89e6\u6012\u4e86\uff0c\u6211\u4eec\u771f\u8981\u5939\u7740\u5c3e\u5df4\u5728\u91d1\u5c71\u505a\u4eba\u4e86\u2026\u2026\u53ea\u662f\u4e0d\u77e5\u9053\u4ed6\u8fd8\u6709\u6ca1\u6709\u5174\u8da3\u542c\u6211\u7684\u2018\u72c2\u5984\u2019\u4e4b\u8a00\u3002\u201d</p><p>\u3000\u3000\u8d70\u5230\u5e7f\u573a\u8fb9\u7f18\u7684\u505c\u8f66\u5904\uff0c\u5f20\u606a\u521a\u8981\u94bb\u8fdb\u8f66\u91cc\uff0c\u7fdf\u4e39\u9752\u62ff\u7740\u624b\u673a\u4ece\u53e6\u4e00\u90e8\u8f66\u91cc\u4e0b\u6765\uff1a\u201c\u6881\u7701\u957f\u79d8\u4e66\u7684\u7535\u8bdd\u2026\u2026\u201d\u5c06\u624b\u673a\u4ece\u8f66\u7a97\u91cc\u9012\u7ed9\u5f20\u606a\uff0c\u4e0e\u5f20\u606a\u540c\u8f66\u7684\u53f6\u5efa\u658c\u3001\u9648\u4fe1\u751f\u3001\u8bb8\u9e3f\u4f2f\u3001\u5468\u6e38\u90fd\u5c06\u76ee\u5149\u8f6c\u76ef\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u6211\u4eec\u73b0\u5728\u56de\u5bbe\u9986\u2026\u2026\u4ec0\u4e48\u5bbe\u9986\uff1f\u201d\u5f20\u606a\u6342\u7740\u8bdd\u7b52\u95ee\u9648\u4fe1\u751f\uff0c\u201c\u6211\u4eec\u4f4f\u4ec0\u4e48\u5bbe\u9986\uff1f\u201d</p><p>\u3000\u3000\u201c\u5929\u5357\u5927\u9152\u5e97\u3002\u201d\u9648\u4fe1\u751f\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5929\u5357\u5927\u9152\u5e97\uff0c\u6211\u4eec\u4f4f\u5929\u5357\u5927\u9152\u5e97\uff0c\u201d\u5f20\u606a\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u53c8\u5bf9\u7740\u624b\u673a\u8bf4\u9053\uff0c\u201c\u6211\u8fd9\u65f6\u5019\u4f1a\u53bb\u996e\u9a6c\u6cb3\u8fb9\u8d70\u4e00\u8d70\uff0c\u591c\u8272\u8fd8\u672a\u6df1\u561b\uff0c\u884c\uff0c\u8fc7\u4f1a\u513f\u518d\u8054\u7edc\u2026\u2026\u201d\u6302\u4e86\u7535\u8bdd\u5bf9\u9648\u4fe1\u751f\u7b49\u4eba\u8bf4\u9053\uff0c\u201c\u6881\u4f1f\u53d1\u8fd8\u8981\u5148\u5e94\u916c\u8fc7\u845b\u5efa\u5fb7\u3001\u845b\u660e\u4fe1\u7b49\u4eba\u624d\u80fd\u8131\u8eab\u51fa\u6765\uff0c\u5927\u6982\u8fd8\u8981\u7b49\u4e0a\u4e00\u5c0f\u65f6\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u6211\u4eec\u5148\u56de\u5bbe\u9986\u2026\u2026\u201d\u8bb8\u9e3f\u4f2f\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u53f6\u54e5\u7559\u4e0b\u6765\u5c31\u884c\u3002\u201d</p><p>\u3000\u3000\u9664\u4e86\u9a6c\u6d77\u9f99\u3001\u7fdf\u4e39\u9752\u4e0e\u4e24\u540d\u4fdd\u536b\u53ca\u53f8\u673a\uff0c\u5176\u4ed6\u4eba\u90fd\u5148\u56de\u5bbe\u9986\u4f11\u606f\uff0c\u5f20\u606a\u521a\u521a\u624d\u5728\u5bb4\u4f1a\u5385\u91cc\u6559\u8bad\u4e86\u845b\u660e\u4fe1\u7b49\u4eba\u4e00\u987f\uff0c\u51fa\u4e8e\u591a\u65b9\u9762\u7684\u8003\u8651\uff0c\u6881\u4f1f\u53d1\u4e5f\u4e0d\u60f3\u8ba9\u845b\u660e\u4fe1\u7b49\u4eba\u77e5\u9053\u4ed6\u53c8\u6025\u5207\u7740\u518d\u627e\u5f20\u606a\u4f1a\u9762\u3002</p><p>\u3000\u3000\u767d\u5929\u6392\u6c61\u5982\u6b64\u4e25\u91cd\uff0c\u591c\u91cc\u7684\u60c5\u51b5\u66f4\u53ef\u4ee5\u60f3\u8c61\uff0c\u53f6\u5efa\u658c\u751a\u4e8e\u8981\u62ff\u51fa\u6bdb\u5dfe\u6342\u7740\u9f3b\u5b50\u966a\u5f20\u606a\u7ad9\u5728\u996e\u9a6c\u6cb3\u5927\u6865\u4e0a\uff0c\u8f66\u5b50\u90fd\u505c\u5728\u8f83\u8fdc\u7684\u8def\u57fa\u4e0b\u9762\u3002</p><p>\u3000\u3000\u5927\u6982\u8fc7\u4e86\u534a\u5c0f\u65f6\uff0c\u6881\u4f1f\u53d1\u79d8\u4e66\u7684\u7535\u8bdd\u5c31\u6253\u4e86\u8fc7\u6765\u3002\u591c\u91cc\uff0c\u516c\u8def\u4e0a\u7684\u8f66\u6781\u5c11\uff0c\u770b\u7740\u4e00\u90e8\u8f66\u4ece\u4f1a\u5c55\u4e2d\u5fc3\u5f00\u8fc7\u6765\uff0c\u4e00\u4f1a\u513f\u5c31\u9a76\u5230\u773c\u524d\uff0c\u6881\u4f1f\u53d1\u8d70\u4e0b\u8f66\u6765\uff0c\u4ed6\u6325\u4e86\u6325\u624b\uff0c\u8ba9\u8f66\u5b50\u7ee7\u7eed\u5f80\u524d\u5f00\uff0c\u505c\u5728\u7a0d\u8fdc\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u6765\u542c\u4f60\u521a\u624d\u6240\u8bf4\u7684\u53e6\u5916\u53ef\u9009\u62e9\u7684\u9053\u8def\uff0c\u201d\u6881\u4f1f\u53d1\u8d70\u5230\u8fd1\u5904\uff0c\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u4f60\u9009\u62e9\u5728\u8fd9\u91cc\u8c08\u8bdd\uff0c\u60f3\u5fc5\u4e5f\u6709\u4ec0\u4e48\u8bdd\u8981\u544a\u8bc9\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6bd4\u8d77\u91d1\u5c71\u7535\u5b50\u5de5\u4e1a\u56ed\uff0c\u6668\u66e6\u7eb8\u4e1a\u4ee5\u727a\u7272\u751f\u6001\u73af\u5883\u6765\u83b7\u5f97\u66f4\u591a\u7684\u5229\u6da6\uff0c\u6240\u9009\u62e9\u8d70\u7684\u662f\u4e00\u6761\u66f4\u52a3\u8d28\u7684\u7ade\u4e89\u4e4b\u8def\u2026\u2026\u201d\u5f20\u606a\u80cc\u98ce\u7ad9\u7740\uff0c\u8fd9\u6837\u6cb3\u6c34\u4e2d\u6f02\u6d6e\u7684\u523a\u9f3b\u6c14\u5473\u5c31\u4e0d\u4f1a\u76f4\u63a5\u7a9c\u8fdb\u9f3b\u5b54\u91cc\u545b\u5f97\u592a\u96be\u53d7\u3002</p><p>\u3000\u3000\u201c\u6668\u66e6\u7eb8\u4e1a\u7684\u60c5\u51b5\u590d\u6742\u5230\u6cbb\u4e5f\u65e0\u6cd5\u4e0b\u624b\u53bb\u6cbb\u7684\u7a0b\u5ea6\uff0c\u201d\u6881\u4f1f\u53d1\u53bb\u5e74\u79cb\u540e\u5230\u6c5f\u5357\u6765\u62c5\u4efb\u4ee3\u7701\u957f\uff0c\u201c\u4e5f\u6216\u8bb8\u662f\u7701\u653f\u5e9c\u7684\u51b3\u5fc3\u4e0d\u591f\u2026\u2026\u53e6\u5916\u91d1\u5c71\u5e02\u91cc\u7684\u6001\u5ea6\u4e5f\u4e0d\u4e00\u81f4\uff0c\u8ba9\u95ee\u9898\u66f4\u52a0\u68d8\u624b\u3002\u201d</p><p>\u3000\u3000\u4e0d\u63d0\u6668\u66e6\u7eb8\u4e1a\u6bcf\u5e74\u4e0a\u7f34\u7684\u4e94\u516d\u4ebf\u7684\u5229\u7a0e\uff0c\u6668\u66e6\u7eb8\u4e1a\u4e0e\u6c5f\u5357\u7701\u5b98\u573a\u7684\u5173\u7cfb\u590d\u6742\uff0c\u8fd8\u6709\u5c31\u662f\u6668\u66e6\u6709\u7740\u8fd1\u4e07\u540d\u7684\u5458\u5de5\u3002\u6240\u8c13\u6001\u5ea6\u4e0d\u4e00\u81f4\uff0c\u5b9e\u9645\u662f\u8bf4\u5305\u5e87\u6668\u66e6\u7684\u5b98\u5458\u5c45\u591a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u5b66\u8005\u4e3a\u5b98\uff0c\u6c14\u5ea6\u3001\u60f3\u6cd5\u90fd\u662f\u6709\u7684\uff0c\u77bb\u524d\u987e\u540e\u4e5f\u6bd4\u5176\u4ed6\u4eba\u591a\u4e9b\uff0c\u8fd9\u5927\u6982\u4e5f\u662f\u6881\u4f1f\u53d1\u4ece\u653f\u8fd9\u4e9b\u5e74\u6765\u7ed9\u4eba\u9762\u76ee\u6a21\u6837\u7684\u4e3b\u8981\u539f\u56e0\uff0c\u4f46\u662f\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u4f17\u591a\u7701\u90e8\u7ea7\u7684\u9ad8\u5b98\uff0c\u7ed9\u4eba\u6027\u683c\u9c9c\u660e\u611f\u89c9\u7684\u5b98\u5458\u4e5f\u5c31\u90a3\u4e48\u51e0\u4eba\u3002</p></article>"}}}]);
