(window.webpackJsonp=window.webpackJsonp||[]).push([[797],{820:function(p){p.exports={chapterTitle:null,title:"\u7b2c629\u7ae0 \u540c\u5b66\u5c11\u5e74",content:"<article><h2>\u7b2c629\u7ae0 \u540c\u5b66\u5c11\u5e74</h2><p>\u3000\u3000\u6709\u719f\u6089\u60c5\u51b5\u7684\u4eba\u603b\u662f\u597d\u529e\u4e8b\uff0c\u65e2\u7136\u7fdf\u4e39\u9752\u63a8\u8350\u4eba\u9009\uff0c\u53c8\u4e0d\u4f1a\u803d\u6401\u5979\u591a\u5c11\u65f6\u95f4\uff0c\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u884c\uff0c\u8fd9\u4ef6\u4e8b\u8fd8\u662f\u8981\u8bf7\u4f60\u5e2e\u5fd9\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u634b\u7740\u9b13\u65c1\u7684\u9752\u4e1d\uff0c\u5e7d\u7136\u7684\u7b11\u4e86\u7b11\uff0c\u4e0d\u81ea\u89c9\u7684\u90a3\u4e9b\u9500\u9b42\u8680\u9aa8\u7684\u5a9a\u6001\u655b\u53bb\u4e0d\u5c11\uff0c\u4f46\u662f\u59a9\u5a9a\u4e2d\u900f\u9732\u4f18\u96c5\u7684\u6c14\u606f\uff0c\u5374\u5206\u5916\u6709\u53e6\u5916\u4e00\u79cd\u9b45\u60d1\u7684\u9b54\u529b\uff0c\u5f20\u606a\u770b\u4e86\u5fae\u5fae\u4e00\u6014\uff0c\u95ee\u5979\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u524d\u540e\u7ed9\u522b\u4eba\u7684\u611f\u89c9\u4e0d\u4e00\u6837\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u6709\u5417\uff1f\u201d\u7fdf\u4e39\u9752\u53c8\u5a9a\u7136\u7684\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u53c8\u6062\u590d\u8fc7\u6765\u4e86\u3002\u201d\u8ba4\u8bc6\u7684\u8fd9\u4e48\u591a\u4e3d\u4eba\u4e4b\u4e2d\uff0c\u60df\u6709\u7fdf\u4e39\u9752\u80fd\u5c06\u5973\u4eba\u81ea\u8db3\u7684\u9b45\u529b\u53d1\u6325\u5230\u6781\u81f4\uff0c\u6709\u7740\u5165\u9aa8\u7684\u98ce\u60c5\uff0c\u8fd9\u5927\u6982\u8ddf\u5979\u7684\u4eba\u751f\u7ecf\u5386\u6709\u83ab\u5927\u7684\u5173\u7cfb\uff1b\u8bb8\u601d\u662f\u5e7d\u7136\u7efd\u653e\u7684\u7f8e\uff1b\u665a\u6674\u7684\u98ce\u60c5\u4e5f\u662f\u6536\u655b\u800c\u4f18\u96c5\u7684\u3002</p><p>\u3000\u3000\u201c\u54c8\u54c8\u2026\u2026\u201d\u7fdf\u4e39\u9752\u6342\u7740\u5507\u8f7b\u7b11\u8d77\u6765\uff0c\u201c\u606a\u5c11\u770b\u5973\u4eba\u4e5f\u771f\u662f\u4ed4\u7ec6\uff0c\u6211\u90fd\u4e0d\u6653\u5f97\u6211\u6709\u54ea\u91cc\u4e0d\u540c\u3002\u201d\u5979\u5f53\u7136\u80fd\u610f\u8bc6\u7684\u90a3\u4e00\u77ac\u95f4\u5fc3\u5c16\u7684\u98a4\u52a8\uff0c\u53ea\u662f\u4e0d\u613f\u610f\u5728\u8fd9\u4e2a\u5c0f\u7537\u4eba\u9762\u524d\u627f\u8ba4\u7f62\u4e86\uff0c\u8bf4\u4ed6\u662f\u90a3\u79cd\u80fd\u5c06\u5973\u4eba\u7684\u9b42\u513f\u3001\u9b44\u513f\u3001\u9aa8\u8089\u90fd\u541e\u8fdb\u53bb\u8fde\u6e23\u90fd\u4e0d\u4f1a\u5410\u51fa\u6765\u7684\u4eba\uff0c\u5012\u4e5f\u4e0d\u662f\u968f\u4fbf\u8bf4\u8bf4\uff0c\u7fdf\u4e39\u9752\u8fdb\u5165\u793e\u4f1a\u63a5\u89e6\u7684\u89d2\u8272\u4e0d\u8ba1\u5176\u6570\uff0c\u8c01\u80fd\u5728\u4e00\u4e2a\u590f\u5b63\u7684\u65f6\u95f4\u7ed9\u5979\u8fd9\u4e48\u5f3a\u70c8\u800c\u590d\u6742\u7684\u51b2\u51fb\uff1f\u5012\u771f\u662f\u6015\u536b\u5170\u90a3\u59ae\u5b50\u6df1\u9677\u5176\u4e2d\u5462\uff0c\u50cf\u5f20\u606a\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u53c8\u600e\u4e48\u4f1a\u4e3a\u5979\u4eec\u8fd9\u6837\u7684\u5c0f\u89d2\u8272\u7559\u8fde\u7247\u523b\uff1f</p><p>\u3000\u3000\u5f20\u606a\u662f\u997f\u6025\u4e86\uff0c\u70b9\u4e86\u6d53\u6c64\u3001\u610f\u5927\u5229\u9762\u3001\u8304\u6c41\u725b\u6252\u9001\u4e0a\u6765\uff0c\u5c31\u57cb\u5934\u82e6\u5e72\u8d77\u6765\uff0c\u7fdf\u4e39\u9752\u8fd8\u662f\u8981\u4fdd\u6301\u5973\u4eba\u7684\u4f18\u96c5\u59ff\u6001\uff0c\u5403\u5230\u534a\u9014\uff0c\u684c\u89d2\u7684\u624b\u673a\u9707\u52a8\u8d77\u6765\uff0c\u662f\u675c\u98de\u4e0e\u8499\u4e50\u4ece\u7f51\u5427\u6362\u73ed\u56de\u6765\uff0c\u95ee\u4ed6\u5728\u54ea\u91cc\uff0c\u5f20\u606a\u4fbf\u8ba9\u4ed6\u4eec\u76f4\u63a5\u5230\u8fd9\u8fb9\u7684\u897f\u9910\u5385\u6765\u3002</p><p>\u3000\u3000\u201c\u5475\uff0c\u6211\u8fd8\u8bf4\u8c01\u80fd\u8ba9\u5f20\u606a\u7684\u683c\u8c03\u63d0\u9ad8\u5230\u897f\u9910\u5385\u5462\uff0c\u539f\u6765\u662f\u7fdf\u59d0\u8fc7\u6765\u4e86\u554a\u2026\u2026\u201d\u675c\u98de\u5728\u9910\u5385\u5916\u5c31\u770b\u5230\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u5750\u5728\u91cc\u9762\u7528\u9910\uff0c\u5e2e\u8499\u4e50\u4ecb\u7ecd\uff0c\u201c\u8499\u4e50\uff0c\u6211\u4eec\u56fd\u5546\u9662\u5927\u4e09\u7684\u5b66\u957f\uff0c\u8fd9\u662f\u7fdf\u59d0\uff0c\u8fd8\u8bf4\u62c9\u4f60\u4e00\u8d77\u53bb\u65b0\u829c\u73a9\u624d\u80fd\u89c1\u5230\u7fdf\u59d0\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u62db\u624b\u8ba9\u5524\u4f8d\u5e94\u751f\u62ff\u83dc\u5355\u8fc7\u6765\u7ed9\u675c\u98de\u4ed6\u4eec\u70b9\u9910\uff0c\u4ed6\u4eec\u521a\u4ece\u7f51\u5427\u6362\u73ed\u56de\u6765\uff0c\u5e94\u8be5\u8fd8\u6ca1\u6709\u627e\u5730\u65b9\u5403\u591c\u5bb5\u3002</p><p>\u3000\u3000\u8499\u4e50\u770b\u5230\u8273\u4e3d\u56db\u5c04\u7684\u7fdf\u4e39\u9752\u90fd\u6709\u4e9b\u53d1\u8499\uff0c\u50cf\u9648\u5983\u84c9\u3001\u5510\u5a67\u3001\u5b59\u9759\u6aac\u7b49\u4eba\uff0c\u7f8e\u5219\u7f8e\u77e3\uff0c\u4f46\u90fd\u53ea\u80fd\u5f52\u5165\u5c0f\u7f8e\u4eba\u7684\u8303\u7574\u4e4b\u5185\uff0c\u5979\u4eec\u8eab\u4e0a\u7684\u7f8e\u662f\u542b\u82de\u5f85\u653e\u7684\uff0c\u6709\u7740\u6c81\u4eba\u5fc3\u813e\u7684\u9752\u6625\u6c14\u606f\uff0c\u90fd\u4e0d\u53ca\u7fdf\u4e39\u9752\u7684\u8273\u4e3d\u4ee5\u53ca\u5165\u9aa8\u7684\u98ce\u60c5\u7ed9\u4eba\u5f88\u5f3a\u70c8\u7684\u51b2\u51fb\u529b\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u6700\u8ba9\u8499\u4e50\u50bb\u773c\u7684\uff0c\u5f20\u606a\u8fd9\u5bb6\u4f19\u5e73\u65f6\u63a5\u89e6\u7684\u5973\u6027\u600e\u4e48\u4e2a\u4e2a\u90fd\u662f\u5929\u59ff\u56fd\u8272\uff1f</p><p>\u3000\u3000\u201c\u50bb\u4e86\u5427\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u5e2e\u8499\u4e50\u62c9\u51fa\u6905\u5b50\u8ba9\u4ed6\u5750\u81ea\u5df1\u8eab\u8fb9\u3002</p><p>\u3000\u3000\u201c\u5443\uff0c\u201d\u8499\u4e50\u4e3a\u81ea\u5df1\u7684\u5931\u795e\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff0c\u8ddf\u7740\u675c\u98de\u8f7b\u5524\u4e86\u58f0\uff0c\u201c\u7fdf\u59d0\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u98de\u5750\u5728\u7fdf\u4e39\u9752\u7684\u8eab\u8fb9\uff0c\u5750\u4e0b\u4e4b\u540e\u624d\u53d1\u73b0\u9910\u5385\u53e6\u4e00\u4fa7\u7684\u8c22\u6c49\u9756\u3001\u8c22\u610f\u3001\u8c22\u5b50\u5609\u4ed6\u4eec\uff0c\u8be7\u5f02\u7684\u8f7b\u547c\u4e86\u4e00\u58f0\uff1a\u201c\u554a\uff0c\u201d\u6307\u4e86\u6307\u5f20\u606a\u7684\u8eab\u540e\uff0c\u95ee\u4ed6\uff0c\u201c\u4f60\u8fdb\u6765\u6709\u6ca1\u6709\u770b\u89c1\u4ed6\u4eec\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u90fd\u4e0d\u56de\u5934\u770b\uff0c\u70b9\u5934\u7b11\u7740\u8bf4\uff1a\u201c\u55ef\uff0c\u4ed6\u4eec\u5148\u5c31\u5728\u8fd9\u91cc\u7528\u9910\u3002\u201d</p><p>\u3000\u3000\u8499\u4e50\u4e0d\u89e3\u7684\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u4ed6\u4e0d\u8ba4\u8bc6\u8c22\u6c49\u9756\u3001\u8c22\u610f\uff0c\u56fd\u5546\u9662\u9662\u957f\u5d14\u56fd\u6052\u4ee5\u53ca\u4ed6\u7684\u7ecf\u6d4e\u5b66\u8bfe\u7684\u5bfc\u5e08\uff0c\u4ed6\u603b\u4e0d\u80fd\u5ffd\u89c6\u4e0d\u89c1\uff1a\u201c\u554a\uff0c\u5d14\u9662\u957f\u3001\u5468\u6559\u6388\u4e5f\u5728\u8fd9\u91cc\u7528\u9910\uff0c\u201d\u8fd8\u6015\u5f20\u606a\u4ed6\u4eec\u4e0d\u8ba4\u8bc6\u5468\u747e\u73ba\uff0c\u201c\u5468\u747e\u73ba\u662f\u7ecf\u6d4e\u5b66\u7684\u6559\u6388\uff0c\u4f60\u4eec\u5b66\u7ecf\u6d4e\u7684\uff0c\u94c1\u5b9a\u8981\u8fc7\u4ed6\u7684\u624b\uff0c\u8981\u4e0d\u8981\u4e00\u8d77\u8fc7\u53bb\u8ddf\u4ed6\u4eec\u6253\u58f0\u62db\u547c\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8fc7\u53bb\u5c31\u884c\u2026\u2026\u201d\u4ed6\u4e0e\u675c\u98de\u53ef\u4ee5\u88c5\u817c\u8146\u3001\u88c5\u964c\u4e0d\u76f8\u8bc6\uff0c\u4e0d\u8fc7\u8499\u4e50\u8fde\u7eed\u4e24\u5c4a\u90fd\u662f\u56fd\u5546\u9662\u5b66\u751f\u4f1a\u4e3b\u5e2d\uff0c\u9047\u5230\u5d14\u56fd\u6052\u90fd\u4e0d\u53bb\u6253\u58f0\u62db\u547c\u5c31\u4e0d\u592a\u77e5\u4eba\u60c5\u4e16\u6545\u4e86\u3002</p><p>\u3000\u3000\u8499\u4e50\u8d70\u4e86\u8fc7\u53bb\uff0c\u5f20\u606a\u4ece\u675c\u98de\u773c\u795e\u91cc\u5c31\u80fd\u770b\u89c1\u6709\u4eba\u8ddf\u7740\u8499\u4e50\u8d70\u56de\u6765\uff0c\u5e94\u8be5\u662f\u9b4f\u4e1c\u5f3a\u5427\uff0c\u4e0d\u6653\u5f97\u8c22\u6c49\u9756\u3001\u8c22\u610f\u6709\u6ca1\u6709\u5728\u770b\u8fd9\u8fb9\uff0c\u5f20\u606a\u62ff\u8d77\u684c\u89d2\u7684\u9c9c\u6a59\u6c41\u559d\u4e86\u4e00\u58f0\uff0c\u5374\u88c5\u4f5c\u65e0\u610f\u7684\u4fa7\u8fc7\u5934\u6765\uff0c\u770b\u7740\u9b4f\u4e1c\u5f3a\uff0c\u5fae\u5e26\u8be7\u5f02\u7684\u8bf4\uff1a\u201c\u5475\uff0c\u9b4f\u4e3b\u4efb\u4e5f\u5728\u8fd9\u91cc\u5403\u996d\u554a\uff1f\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u63a9\u5507\u7b11\u4e86\u7b11\uff0c\u597d\u50cf\u5f20\u606a\u521a\u624d\u771f\u6ca1\u6709\u770b\u89c1\u90a3\u4e00\u684c\u7684\u4eba\u4f3c\u7684\u3002</p><p>\u3000\u3000\u201c\u5d14\u9662\u957f\u5728\u90a3\u91cc\uff0c\u4f60\u4eec\u4e5f\u8fc7\u53bb\u6253\u58f0\u62db\u547c\u5427\uff1f\u201d\u9b4f\u4e1c\u5f3a\u4e0d\u6562\u591a\u770b\u5d14\u4e39\u9752\uff0c\u89c6\u7ebf\u653e\u5728\u5f20\u606a\u4e0e\u675c\u98de\u7684\u8138\u4e0a\u3002</p><p>\u3000\u3000\u9b4f\u4e1c\u5f3a\u80fd\u8fd9\u4e48\u8dd1\u8fc7\u6765\uff0c\u8981\u662f\u8896\u624b\u4e0d\u7406\uff0c\u5c31\u6709\u4e9b\u4e0d\u8fd1\u4eba\u60c5\uff0c\u5f20\u606a\u5c06\u819d\u4e0a\u7684\u9910\u5dfe\u53d6\u4e0b\u6765\u4e22\u684c\u4e0a\uff0c\u8ddf\u7fdf\u4e39\u9752\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u6211\u4eec\u8fc7\u53bb\u4e00\u4e0b\u5c31\u56de\u6765\u3002\u201d\u88c5\u4f5c\u7eaf\u6d01\u65e0\u5bb3\u53c8\u817c\u8146\u7684\u6a21\u6837\u8ddf\u7740\u9b4f\u4e1c\u5f3a\u8d70\u8fc7\u53bb\uff0c\u9b4f\u4e1c\u5f3a\u4ecb\u7ecd\u5d14\u56fd\u6052\u201c\u8fd9\u662f\u5d14\u9662\u957f\u2026\u2026\u201d\u5c31\u8ddf\u7740\u558a\u4e00\u58f0\u201c\u5d14\u9662\u957f\u2026\u2026\u201d\u9b4f\u4e1c\u5f3a\u4ecb\u7ecd\u5468\u747e\u73ba\uff0c\u5c31\u8ddf\u7740\u5524\u4e00\u58f0\u201c\u5468\u6559\u6388\u2026\u2026\u201d</p><p>\u3000\u3000\u8499\u4e50\u7ed9\u8499\u5728\u9f13\u91cc\uff0c\u4e5f\u770b\u4e0d\u51fa\u4ec0\u4e48\u5f02\u5e38\uff1b\u5d14\u56fd\u6052\u5012\u662f\u5e0c\u671b\u4e0e\u5f20\u606a\u4e00\u8d77\u8fdb\u6765\u7684\u90a3\u4e2a\u6210\u719f\u8273\u4e3d\u7684\u5973\u5b50\u80fd\u4e00\u8d77\u8fc7\u6765\u6253\u58f0\u62db\u547c\uff0c\u4ec5\u4ec5\u662f\u5f20\u606a\u4e0e\u675c\u98de\u8fc7\u6765\uff0c\u4e5f\u5c31\u9894\u9996\u5f88\u51b7\u6de1\u7684\u5e94\u4e86\u4e00\u58f0\u3002</p><p>\u3000\u3000\u675c\u98de\u5012\u662f\u6709\u4e9b\u6c89\u4e0d\u4f4f\u6c14\u7684\u6253\u91cf\u8c22\u6c49\u9756\u4ed6\u4eec\u8138\u4e0a\u7684\u795e\u8272\uff1b\u8c22\u5b50\u5609\u62ff\u7740\u5200\u53c9\u8f7b\u8f7b\u7684\u523a\u7740\u5ae3\u7ea2\u7684\u5634\u5507\uff0c\u6e05\u51bd\u7684\u76ee\u5149\u4e00\u4f1a\u513f\u505c\u5728\u5f20\u606a\u7684\u8138\u4e0a\uff0c\u4e00\u4f1a\u513f\u53c8\u4ece\u5979\u7238\u3001\u5979\u54e5\u4ee5\u53ca\u5468\u747e\u73ba\u7684\u8138\u4e0a\u626b\u8fc7\uff0c\u4f3c\u4e4e\u4e00\u70b9\u90fd\u610f\u8bc6\u4e0d\u5230\u4ed6\u4eec\u7684\u5c34\u5c2c\uff0c\u5634\u89d2\u7684\u6d45\u7b11\u4e5f\u592a\u660e\u663e\u4e86\uff0c\u4f3c\u4e4e\u6362\u7740\u5730\u65b9\u90fd\u80fd\u5927\u7b11\u8d77\u6765\u3002\u72e1\u9ee0\u7684\u773c\u775b\u8f6e\u4e86\u4e00\u5708\uff0c\u5ffd\u53c8\u8da3\u5473\u5341\u8db3\u7684\u5e2e\u4ed6\u7238\u3001\u4ed6\u54e5\u4ecb\u7ecd\u8d77\u5f20\u606a\u3001\u675c\u98de\u8d77\u6765\uff1a\u201c\u7238\u3001\u54e5\uff0c\u5f20\u540c\u5b66\u3001\u675c\u540c\u5b66\u662f\u6211\u4eec\u56fd\u5546\u9662\u6709\u540d\u7684\u5e74\u5c11\u53c8\u591a\u91d1\u7684\u5e05\u54e5\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u6c49\u9756\u3001\u8c22\u610f\u4e00\u76f4\u4fdd\u6301\u6de1\u7136\u7684\u795e\u8272\u8fd9\u65f6\u5019\u4e5f\u7981\u4e0d\u4f4f\u50f5\u6ede\u4e86\u4e00\u4e0b\uff0c\u5dee\u70b9\u90fd\u8981\u5d29\u6e83\u6389\uff1b\u5f20\u606a\u5012\u6ca1\u6709\u60f3\u5230\u8c22\u5b50\u5609\u4f1a\u8fd9\u65f6\u5019\u4f1a\u9677\u5bb3\u5979\u81ea\u5df1\u7684\u8001\u5b50\uff0c\u4ed6\u4e5f\u6709\u4e00\u79cd\u63aa\u624b\u4e0d\u53ca\u7684\u611f\u89c9\uff0c\u70b9\u5934\u5f88\u52c9\u5f3a\u7684\u7b11\u4e86\u7b11\uff0c\u5927\u5bb6\u8138\u4e0a\u7684\u7b11\u5bb9\u5047\u5f97\u5c31\u8ddf\u62ff\u7eb8\u8d34\u4e0a\u53bb\u4f3c\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u675c\u98de\u7070\u5934\u571f\u8138\u7684\u8d70\u4e86\u56de\u53bb\uff0c\u4e0d\u60f3\u8ba9\u8c22\u5b50\u5609\u6709\u770b\u620f\u7684\u826f\u597d\u611f\u89c9\uff0c\u53cd\u6b63\u675c\u98de\u3001\u8499\u4e50\u90fd\u8fd8\u6ca1\u6709\u5f00\u59cb\u70b9\u9910\uff0c\u62db\u624b\u8ba9\u4f8d\u5e94\u751f\u8fc7\u6765\u4e70\u5355\uff0c\u9003\u4e5f\u4f3c\u7684\u79bb\u5f00\u897f\u9910\u5385\uff1b\u4e5f\u4e0d\u7ba1\u9b4f\u4e1c\u5f3a\u4ed6\u4eec\u770b\u5230\u4f1a\u600e\u4e48\u60f3\u3002\u4ed6\u4eec\u7231\u600e\u4e48\u662f\u4ed6\u4eec\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u51fa\u4e86\u897f\u9910\u5385\uff0c\u7fdf\u4e39\u9752\u5012\u662f\u4e50\u4e0d\u53ef\u5431\uff0c\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\u3001\u5a9a\u773c\u6a2a\u98de\uff0c\u6307\u7740\u5f20\u606a\u8bf4\uff1a\u201c\u4f60\u4eec\u771f\u80fd\u88c5\u554a\uff0c\u4f60\u4eec\u7ee7\u7eed\u88c5\u4e0b\u53bb\u554a\uff1b\u90a3\u5c0f\u59d1\u5a18\uff0c\u6211\u559c\u6b22\u3002\u201d\u5979\u4e5f\u5728\u8fb9\u4e0a\u770b\u4e86\u4e00\u51fa\u597d\u620f\u3002</p><p>\u3000\u3000\u201c\u518d\u88c5\u5c31\u8981\u5d29\u6e83\u4e86\u3002\u201d\u5f20\u606a\u4e5f\u62ff\u8c22\u5b50\u5609\u6ca1\u6709\u529e\u6cd5\uff0c\u8fd9\u59ae\u5b50\u8fde\u5979\u8001\u5b50\u90fd\u4e0d\u7559\u60c5\u9762\u7684\u9677\u5bb3\u3002</p><p>\u3000\u3000\u8499\u4e50\u4e08\u4e8c\u548c\u5c1a\u6478\u4e0d\u7740\u5934\u8111\uff1a\u201c\u600e\u4e48\uff0c\u4f60\u4eec\u662f\u4e0d\u662f\u90fd\u8ba4\u8bc6\uff0c\u600e\u4e48\u53c8\u88c5\u4f5c\u4e00\u526f\u964c\u4e0d\u76f8\u8bc6\u7684\u6a21\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u5927\u5bb6\u662f\u51a4\u5bb6\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8ddf\u8499\u4e50\u89e3\u91ca\u8bf4\uff0c\u201c\u4e0d\u8fc7\u60f3\u5fc5\u4ed6\u4eec\u6ca1\u6709\u52c7\u6c14\u5728\u522b\u4eba\u9762\u524d\u627f\u8ba4\u6211\u4eec\u662f\u4ed6\u4eec\u7684\u51a4\u5bb6\uff0c\u6211\u53c8\u4e0d\u80fd\u5728\u897f\u9910\u5385\u8ddf\u4ed6\u4eec\u6380\u684c\u5b50\u5927\u6253\u51fa\u624b\uff0c\u5927\u5bb6\u53ea\u6709\u88c5\u4f5c\u964c\u4e0d\u76f8\u8bc6\u55bd\u3002\u201d</p><p>\u3000\u3000\u8fd9\u6837\u7684\u89e3\u91ca\uff0c\u8499\u4e50\u8fd8\u662f\u542c\u4e0d\u660e\u767d\uff0c\u4e0d\u8fc7\u5927\u5b66\u91cc\u7684\u670b\u53cb\u6709\u4e00\u79cd\u597d\uff0c\u4e0d\u4f1a\u5228\u6839\u95ee\u5e95\u7684\u8ffd\u95ee\u5f7c\u6b64\u7684\u5bb6\u5ead\u80cc\u666f\uff0c\u66f4\u591a\u662f\u6027\u60c5\u76f8\u6295\u7684\u5728\u4ea4\u5f80\u3002</p><p>\u3000\u3000\u4fe1\u6b65\u5f80\u6559\u5de5\u98df\u5802\u8d70\u53bb\u2014\u2014\u53ea\u5f97\u53bb\u90a3\u5728\u7ee7\u7eed\u70b9\u83dc\u7528\u9910\uff0c\u5728\u8def\u4e0a\uff0c\u5f20\u606a\u8ddf\u675c\u98de\u3001\u8499\u4e50\u8bf4\u5230\u7fdf\u4e39\u9752\u51fa\u73b0\u5728\u4e1c\u5927\u6821\u56ed\u91cc\u7684\u539f\u56e0\uff0c\u53c8\u95ee\u81ea\u5df1\u8d70\u7684\u4e24\u5929\uff0c\u4ed6\u4eec\u8ba8\u8bba\u5f00\u7f51\u5427\u7684\u4e8b\u60c5\u6709\u4ec0\u4e48\u521d\u6b65\u7684\u7ed3\u8bba\u6ca1\u6709\u3002</p><p>\u3000\u3000\u201c\u6709\u5fc3\u601d\u5728\u8fd9\u9644\u8fd1\u5f00\u7f51\u5427\u7684\u4eba\u4e0d\u4f1a\u5c11\uff0c\u6bd5\u7adf\u5317\u95e8\u7535\u8111\u623f\u7684\u706b\u7206\u573a\u666f\u8bb8\u591a\u4eba\u90fd\u770b\u5728\u773c\u91cc\uff0c\u4e0d\u8fc7\u8fd9\u65f6\u5019\u7f51\u7edc\u7aef\u63a5\u5165\u7684\u8d39\u7528\u5f88\u9ad8\uff0c\u52a0\u4e0a\u6bd5\u7adf\u8fd8\u662f\u65b0\u51fa\u73b0\u7684\u4e8b\u7269\uff0c\u4f7f\u5f97\u8bb8\u591a\u4eba\u90fd\u72b9\u8c6b\u4e0d\u51b3\u2014\u2014\u8fd9\u4e9b\u90fd\u53ea\u662f\u65f6\u95f4\u95ee\u9898\u5427\uff0c\u6211\u4e0e\u8499\u4e50\u5408\u8ba1\u7740\uff0c\u8fd9\u65f6\u5019\u8981\u628a\u672a\u6765\u5c31\u4f1a\u6d8c\u5165\u8fd9\u4e2a\u884c\u4e1a\u7684\u7ade\u4e89\u7f51\u5427\u4e5f\u8003\u8651\u8fdb\u6765\u2026\u2026\u201d\u675c\u98de\u8bf4\u9053\uff0c\u201c\u8499\u4e50\u4e4b\u524d\u5c31\u6709\u8fc7\u5c06\u5b66\u6821\u8ba1\u7b97\u4e2d\u5fc3\u673a\u623f\u7684\u7a7a\u95f2\u65f6\u95f4\u627f\u5305\u4e0b\u6765\u7684\u5ff5\u5934\uff0c\u8fd9\u4e2a\u53ef\u80fd\u6027\u4e5f\u5c0f\uff0c\u4e00\u6765\u5404\u9662\u7cfb\u7684\u8ba1\u7b97\u673a\u8bfe\u7a0b\u5b89\u6392\u5f88\u7d27\uff0c\u5269\u4f59\u7684\u7a7a\u95f2\u65f6\u95f4\u592a\u96f6\u788e\uff0c\u53e6\u5916\u8ba1\u7b97\u4e2d\u5fc3\u7684\u8001\u5e08\u4e5f\u90fd\u662f\u89c1\u7f1d\u63d2\u9488\u7684\u4e3b\uff0c\u6709\u597d\u5904\u4e0d\u4f1a\u7559\u7ed9\u5916\u4eba\uff1b\u53e6\u5916\uff0c\u56fe\u4e66\u9986\u4e5f\u6709\u7b79\u5efa\u7535\u5b50\u9605\u89c8\u5ba4\u7684\u8ba1\u5212\u3002\u6628\u5929\u6211\u4eec\u62bd\u65f6\u95f4\u51fa\u6765\u505a\u4e86\u4e00\u4e0b\u8c03\u67e5\uff0c\u4e5f\u8ba4\u771f\u8003\u8651\u4e86\u4e00\u4e0b\uff0c\u8003\u8651\u5230\u672a\u6765\u5b66\u5e9c\u5df7\u5546\u94fa\u7684\u79df\u91d1\uff0c\u5728\u5b66\u5e9c\u5df7\u5f00\u7f51\u5427\u4e0e\u5728\u9a6c\u83b2\u8857\u6216\u5176\u4ed6\u5730\u65b9\u5f00\u7f51\u5427\u7684\u6210\u672c\u5dee\u5f02\u5de8\u5927\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u5728\u5b66\u5e9c\u5df7\u5f00\u7f51\u5427\u4e0d\u80fd\u5c06\u76ee\u5149\u505c\u7559\u5728\u4ec5\u4ec5\u662f\u63d0\u4f9b\u4e00\u4e2a\u4e0a\u7f51\u3001\u73a9\u7535\u8111\u6e38\u620f\u670d\u52a1\u573a\u6240\u7684\u72ed\u9698\u5ff5\u5934\u4e0a\uff0c\u53ef\u80fd\u9700\u8981\u4e00\u5f00\u59cb\u5c31\u8003\u8651\u5230\u6d88\u8d39\u5bf9\u8c61\u7684\u5206\u5c42\u95ee\u9898\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u7f51\u5427\u4f5c\u4e3a\u4e00\u4e2a\u4ea7\u4e1a\u4f1a\u5f88\u5feb\u7684\u5174\u8d77\u5e76\u4e14\u4f1a\u8fc5\u901f\u5c31\u8fdb\u5165\u5176\u4ea7\u4e1a\u7684\u9ec4\u91d1\u65f6\u671f\uff0c\u8fd9\u4e2a\u9ec4\u91d1\u65f6\u671f\u4e5f\u5c31\u4e03\u516b\u5e74\u7684\u65f6\u95f4\uff0c\u4ed6\u5c31\u6015\u675c\u98de\u3001\u8499\u4e50\u8fd9\u65f6\u5019\u7684\u89c6\u91ce\u53d7\u9650\u3001\u8003\u8651\u7684\u683c\u5c40\u592a\u8fc7\u72ed\u9698\u4e86\uff0c\u542c\u5230\u675c\u98de\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u4eec\u7b97\u662f\u521d\u6b65\u627e\u5230\u8fd9\u4e2a\u884c\u4e1a\u7684\u95e8\u5f84\u4e86\uff0c\u675c\u98de\u8ddf\u4ed6\u6df7\u4e86\u8fd9\u51e0\u5e74\uff0c\u773c\u754c\u6bd5\u7adf\u8981\u9ad8\u8fc7\u5176\u4ed6\u4eba\u3002</p></article>"}}}]);
