(window.webpackJsonp=window.webpackJsonp||[]).push([[798],{821:function(p){p.exports={chapterTitle:null,title:"\u7b2c796\u7ae0 \u4e09\u6708",content:"<article><h2>\u7b2c796\u7ae0 \u4e09\u6708</h2><p>\u3000\u3000\u91d1\u5c71\u7535\u5b50\u4ea7\u4e1a\u8bba\u575b\u4f1a\u671f\u4ec5\u4e3a\u4e24\u5929\uff0c\u9664\u4e86\u9996\u5148\u7684\u5f00\u5e55\u5f0f\u4e0e\u9ad8\u5cf0\u8bba\u575b\u4e4b\u5916\uff0c\u6b21\u65e5\u5219\u5728\u56fd\u9645\u4f1a\u5c55\u4e2d\u5fc3\u5927\u7efc\u5408\u9986\u4e0e\u4f1a\u5c55\u4e2d\u5fc3\u5927\u996d\u5e97\u4e3e\u529e\u7535\u5b50\u653f\u52a1\u4e0e\u8f6f\u4ef6\u4ea7\u4e1a\u3001\u91d1\u5c71\u7535\u5b50\u5de5\u4e1a\u56ed\u3001\u4e92\u8054\u7f51\u4f20\u5a92\u53d1\u5c55\u4e0e\u793e\u4f1a\u5f71\u54cd\u7b49\u5341\u4e2a\u4e13\u9898\u8bba\u575b\u4f1a\u8bae\uff0c\u540c\u65f6\u6c5f\u5357\u7701\u653f\u5e9c\u3001\u91d1\u5c71\u5e02\u653f\u5e9c\u8fd8\u5c06\u5728\u56fd\u9645\u4f1a\u5c55\u4e2d\u5fc3\u8fdb\u884c\u9879\u76ee\u7b7e\u7ea6\u4eea\u5f0f\u3001\u94f6\u884c-\u4f01\u4e1a\u5bf9\u63a5\u3001\u79d1\u7814\u673a\u6784-\u4f01\u4e1a\u5bf9\u63a5\u7b49\u7ecf\u8d38\u6d3b\u52a8\u3002</p><p>\u3000\u3000\u94a2\u7ed3\u6784\u5c4b\u9876\u7eb5\u6a2a\u4ea4\u9519\u5728\u4f1a\u5c55\u573a\u9986\u7684\u4e0a\u65b9\uff0c\u5efa\u7b51\u98ce\u683c\u7c97\u72b7\uff0c\u4f46\u662f\u4e5d\u5341\u5e74\u4ee3\u672b\u5230\u4e8c\u5341\u4e00\u4e16\u7eaa\u521d\u671f\uff0c\u56fd\u5185\u7684\u4f1a\u5c55\u4e2d\u5fc3\uff0c\u51e0\u4e4e\u90fd\u662f\u8fd9\u4e48\u5efa\u7b51\u98ce\u683c\uff0c\u770b\u591a\u4e86\u5c31\u4f1a\u611f\u89c9\u817b\u5473\uff0c\u4f1a\u604d\u7136\u7701\u5f97\u8fd9\u79cd\u6a21\u4eff\u897f\u65b9\u4f1a\u5c55\u5efa\u7b51\u7684\u98ce\u683c\u5e76\u6ca1\u6709\u81ea\u5df1\u7684\u751f\u547d\u529b\u5728\u5185\u3002</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\u9664\u4e86\u8d70\u9a6c\u89c2\u82b1\u7684\u6d4f\u89c8\u5404\u4e13\u9898\u8bba\u575b\u4f1a\u8bae\u7684\u72b6\u51b5\uff0c\u4f1a\u5c55\u4e2d\u5fc3\u7684\u5efa\u7b51\u5e76\u6ca1\u6709\u4ec0\u4e48\u5730\u65b9\u8ba9\u5f20\u606a\u6d41\u8fde\u5fd8\u8fd4\u7684\u3002</p><p>\u3000\u3000\u5728\u7b2c\u4e8c\u5929\u7684\u7ecf\u8d38\u6d3b\u52a8\u4e2d\uff0c\u9648\u4fe1\u751f\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u4e0e\u91d1\u5c71\u5e02\u653f\u5e9c\u7b7e\u7f72\u6295\u8d44\u4e24\u4ebf\u989d\u5ea6\u7684\u91d1\u5c71\u6a61\u6811\u56ed\u7814\u53d1\u4e2d\u5fc3\u7684\u6295\u8d44\u9879\u76ee\u610f\u5411\u4e66\uff0c\u800c\u6d77\u7c9f\u79d1\u6280\u8054\u5408\u6b63\u6cf0\u96c6\u56e2\u3001\u5609\u4fe1\u7535\u5b50\u5bf9\u91d1\u5c71\u8f6f\u4ef6\u56ed\u4ee5\u53ca\u7535\u5b50\u5de5\u4e1a\u56ed\u7684\u4e8c\u671f\u5de5\u7a0b\u610f\u5411\u6295\u8d44\u8d44\u91d1\u603b\u989d\u8fbe\u5230\u4e09\u5341\u4ebf\uff0c\u8ddf\u4ed6\u4eec\u6bd4\u8d77\u6765\uff0c\u9526\u6e56\u8fd9\u6b21\u7684\u8868\u73b0\u7684\u786e\u6ca1\u6709\u592a\u5927\u7684\u98ce\u5149\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u9047\u5230\u4eca\u5929\u4f9d\u65e7\u5217\u5e2d\u8bba\u575b\u6d3b\u52a8\u3001\u8eb2\u5728\u53f0\u4e0b\u88c5\u542c\u4f17\u7684\u5f20\u606a\u5fcd\u4e0d\u4f4f\u8981\u595a\u843d\u4ed6\u4e24\u53e5\uff1a\u201c\u6628\u5929\u542c\u4f60\u8a00\u8f9e\u6177\u6168\u6fc0\u6602\uff0c\u8fd8\u4ee5\u4e3a\u9526\u6e56\u8fd9\u6b21\u80fd\u6709\u591a\u5927\u7684\u624b\u7b14\u2026\u2026\u624d\u4e24\u4e2a\u4ebf\u800c\u5df2\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u672c\u4e0d\u60f3\u7406\u4f1a\u4ed6\uff0c\u4f46\u89c1\u4ed6\u8dbe\u9ad8\u6c14\u626c\u7684\u6a21\u6837\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u60f3\u8981\u523a\u6fc0\u4ed6\u4e00\u4e24\u53e5\uff1a\u201c\u7b7e\u7f72\u7684\u53ea\u662f\u610f\u5411\u4e66\uff0c\u6240\u8c13\u7684\u610f\u5411\u8d44\u91d1\u7a76\u7adf\u80fd\u6709\u591a\u5c11\u843d\u5728\u5b9e\u5904\uff0c\u8c01\u4e5f\u8bf4\u4e0d\u597d\u3002\u5199\u51fa\u6765\u53ea\u662f\u8ba9\u653f\u5e9c\u62a5\u544a\u597d\u770b\u4e00\u4e9b\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u8ba9\u91d1\u5c71\u5e02\u7684\u653f\u5e9c\u62a5\u544a\u770b\u4e0a\u53bb\u633a\u7f8e\uff1f\u4e24\u4e2a\u4ebf\uff0c\u53ea\u662f\u6628\u5929\u591c\u91cc\u65e0\u804a\u968f\u4fbf\u60f3\u51fa\u6765\u7684\u4e00\u4e2a\u6570\u5b57\u800c\u5df2\uff0c\u6211\u6562\u80af\u5b9a\u7684\u544a\u8bc9\u4f60\uff0c\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u4eca\u5929\u5609\u4fe1\u7535\u5b50\u7684\u4e70\u5165\u8d44\u91d1\u5c31\u4e0d\u6b62\u4e24\u4e2a\u4ebf\uff01\u201d</p><p>\u3000\u3000\u542c\u5f20\u606a\u518d\u6b21\u63d0\u53ca\u5609\u4fe1\u7535\u5b50\uff0c\u845b\u836b\u5747\u9634\u6674\u4e0d\u5b9a\u7684\u770b\u7740\u4ed6\uff0c\u6ca1\u6709\u518d\u591a\u8bf4\u4ec0\u4e48\uff0c\u5c31\u8f6c\u8eab\u79bb\u5f00\u4e86\u3002</p><p>\u3000\u3000\u770b\u7740\u845b\u836b\u5747\u65e0\u8da3\u7684\u79bb\u5f00\uff0c\u53f6\u5efa\u658c\u6487\u5634\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5efa\u90ba\u4ec5\u4e2d\u6676\u5fae\u82af\u9879\u76ee\u5c31\u6709\u4e00\u767e\u4ebf\u7684\u6295\u8d44\u8d44\u91d1\u843d\u5230\u5b9e\u5904\uff0c\u8fd9\u5c0f\u5b50\u6709\u4ec0\u4e48\u503c\u5f97\u8dbe\u9ad8\u6c14\u626c\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u662f\u5b9e\u5728\u627e\u4e0d\u5230\u673a\u4f1a\u595a\u843d\u6211\u4eec\u554a\uff01\u201d\u7fdf\u4e39\u9752\u7b11\u7740\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4e0d\uff0c\u4e0d\uff0c\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u662f\u5c06\u91d1\u5c71\u5f53\u6210\u81ea\u5df1\u7684\u5730\u76d8\u4e86\uff0c\u624d\u4f1a\u6709\u5982\u6b64\u7684\u59ff\u6001\u3002\u201d</p><p>\u3000\u3000\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u53f6\u5efa\u658c\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u771f\u8981\u505a\u6210\u4e86\uff0c\u4e0d\u77e5\u9053\u4ed6\u4eec\u7684\u8138\u8272\u4f1a\u6709\u591a\u96be\u770b\u554a\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u7684\u8bdd\uff0c\u845b\u836b\u5747\u521d\u65f6\u5e76\u6ca1\u6709\u592a\u653e\u5728\u5fc3\u4e0a\uff0c\u76f4\u5230\u4e0b\u5348\u56db\u70b9\u8bb8\uff0c\u5609\u4fe1\u7535\u5b50\u5728\u9999\u6e2f\u7684\u603b\u90e8\u6253\u6765\u7535\u8bdd\u544a\u8bc9\u4ed6\u4eec\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u7968\u4eca\u5929\u5728\u516c\u5f00\u5e02\u573a\u7684\u4ea4\u6613\u989d\u6bd4\u8d77\u5f80\u65e5\u6709\u4e86\u660e\u663e\u7684\u589e\u6da8\uff0c\u8fd9\u624d\u89c9\u5f97\u52bf\u6001\u6bd4\u4ed6\u4eec\u60f3\u8c61\u4e2d\u8981\u4e25\u91cd\u5f97\u591a\u3002</p><p>\u3000\u3000\u53d7\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5f71\u54cd\uff0c\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u4e5f\u9677\u5165\u6c89\u5bc2\u7684\u4e25\u51ac\u4e4b\u4e2d\uff0c\u4ea4\u6613\u4e0d\u6d3b\u8dc3\u3001\u4ea4\u6613\u91cf\u6301\u7eed\u4f4e\u9761\uff0c\u5609\u4fe1\u7535\u5b50\u80a1\u7968\u7684\u65e5\u4ea4\u6613\u989d\u8fd1\u65e5\u5374\u6301\u7eed\u7684\u589e\u6da8\u2014\u2014\u9526\u6e56\u7684\u610f\u56fe\u662f\u8d8a\u6765\u8d8a\u660e\u663e\u4e86\u3002</p><p>\u3000\u3000\u5468\u747e\u745c\u8fd8\u8981\u8d76\u53bb\u673a\u573a\u4e3a\u4e00\u4e9b\u91cd\u8981\u7684\u5609\u5bbe\u9001\u884c\uff0c\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u3001\u8c22\u6c49\u9756\u3001\u8c22\u6c49\u660e\u3001\u8c22\u5251\u5357\u7b49\u4eba\u90fd\u6709\u4e9b\u614c\u795e\uff0c\u5728\u7535\u5b50\u4ea7\u4e1a\u53d1\u5c55\u8bba\u575b\u95ed\u5e55\u4e4b\u540e\uff0c\u5c31\u53c8\u7d27\u6025\u805a\u5230\u91d1\u5c71\u5927\u9152\u5e97\u7684\u8c6a\u534e\u5957\u623f\u91cc\u78cb\u5546\u3002</p><p>\u3000\u3000\u867d\u7136\u80fd\u4f30\u6d4b\u9526\u6e56\u73b0\u6709\u7684\u8d44\u91d1\u5b58\u91cf\u4e0d\u8db3\u4ee5\u5bf9\u5609\u4fe1\u7535\u5b50\u6784\u6210\u5a01\u80c1\uff0c\u4f46\u662f\u4ee5\u9526\u6e56\u4eca\u65e5\u7684\u89c4\u6a21\u4e0e\u5730\u4f4d\uff0c\u5c31\u7b97\u4ece\u4f4e\u9761\u7684\u56fd\u9645\u91d1\u878d\u5e02\u573a\u878d\u8d44\u767e\u516b\u5341\u4ebf\u7684\u8d44\u91d1\uff0c\u8c01\u4e5f\u4e0d\u6562\u80af\u5b9a\u7684\u8bf4\u8fd9\u662f\u4e0d\u53ef\u80fd\u53d1\u751f\u7684\u4e8b\u3002</p><p>\u3000\u3000\u82e5\u662f\u771f\u8ba9\u9526\u6e56\u4ece\u516c\u5f00\u5e02\u573a\u593a\u53bb\u5609\u4fe1\u7535\u5b50\u7684\u63a7\u5236\u6743\uff0c\u8c22\u6c49\u9756\u90fd\u4e0d\u6562\u60f3\u8c61\u53d1\u751f\u8fd9\u4ef6\u4e8b\u60c5\u7684\u540e\u679c\uff0c\u91d1\u5c71\u7535\u5b50\u5de5\u4e1a\u56ed\u81ea\u7136\u4f1a\u987a\u52bf\u6210\u4e86\u9526\u6e56\u7684\u56ca\u4e2d\u4e4b\u7269\uff0c\u4ed6\u4eec\u7684\u4e00\u5207\u52aa\u529b\uff0c\u5374\u7ed9\u9526\u6e56\u505a\u4e86\u5ac1\u8863\uff0c\u90a3\u5c82\u4e0d\u662f\u6210\u4e86\u5929\u5927\u7684\u7b11\u8bdd\uff1f\u6b63\u6cf0\u96c6\u56e2\u4e0e\u5609\u4fe1\u7535\u5b50\u7684\u5173\u8054\u8d8a\u6765\u8d8a\u5bc6\u5207\uff0c\u82e5\u8ba9\u9526\u6e56\u593a\u53bb\u5609\u4fe1\u7535\u5b50\u7684\u63a7\u5236\u6743\uff0c\u4ed6\u4eec\u60f3\u8981\u8fdc\u8fdc\u7684\u8eb2\u5f00\u9526\u6e56\u90fd\u4e0d\u53ef\u80fd\uff0c\u5927\u6982\u4f1a\u6c38\u8fdc\u7684\u7ed9\u9526\u6e56\u9a91\u5728\u5934\u4e0a\u4e0d\u53ef\u3002</p><p>\u3000\u3000\u5609\u4fe1\u5730\u4ea7\u4e0e\u5609\u4fe1\u7535\u5b50\u662f\u5609\u4fe1\u96c6\u56e2\u4e24\u5927\u652f\u67f1\uff0c\u845b\u660e\u4fe1\u7edd\u4e0d\u4f1a\u5bb9\u5fcd\u4ed6\u4eba\u5bf9\u5609\u4fe1\u7535\u5b50\u6709\u67d3\u6307\u4e4b\u5fc3\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u4e00\u53ea\u75af\u72d7\uff01\u201d\u845b\u660e\u4fe1\u538b\u7740\u58f0\u97f3\u5728\u5486\u54ee\uff0c\u4ed6\u60f3\u4e0d\u660e\u767d\u72d9\u51fb\u5609\u4fe1\u7535\u5b50\u5bf9\u9526\u6e56\u6709\u591a\u5c11\u5b9e\u8d28\u6027\u7684\u597d\u5904\uff0c\u4f46\u662f\u4ed6\u4eec\u786e\u5b9e\u5728\u505a\u4e86\u3002</p><p>\u3000\u3000\u201c\u9999\u6e2f\u80a1\u5e02\u6bd5\u7adf\u4e0d\u6bd4\u5185\u5730\uff0c\u76d1\u7ba1\u8981\u4e25\u5389\u5f97\u591a\uff0c\u4ed6\u4eec\u603b\u4e0d\u80fd\u4efb\u610f\u5984\u4e3a\u5427\uff1f\u201d\u8c22\u6c49\u660e\u8fdf\u7591\u7684\u8bf4\u9053\uff0c\u4ed6\u5bf9\u8bc1\u5238\u5e02\u573a\u7684\u60c5\u51b5\u4e0d\u751a\u719f\u6089\uff0c\u51ed\u76f4\u89c2\u7684\u611f\u89c9\u8ba4\u4e3a\u9526\u6e56\u65e0\u6cd5\u5728\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u4efb\u610f\u5984\u4e3a\u3002</p><p>\u3000\u3000\u201c\u4e25\u5389\u662f\u4e00\u65b9\u9762\uff0c\u4f46\u9999\u6e2f\u91d1\u878d\u5e02\u573a\u53c8\u662f\u4e00\u4e2a\u9ad8\u5ea6\u81ea\u7531\u5316\u7684\u5e02\u573a\uff0c\u52a0\u4e0a\u8fd1\u671f\u5185\u56fd\u9645\u91d1\u878d\u6e38\u8d44\u9891\u9891\u51fa\u6ca1\u9999\u6e2f\u80a1\u5e02\uff0c\u4ed6\u4eec\u5b8c\u5168\u53ef\u4ee5\u63a9\u76d6\u4ed6\u4eec\u7684\u6536\u8d2d\u610f\u56fe\u2026\u2026\u4ed6\u4eec\u53ef\u4ee5\u5206\u6563\u5438\u5165\uff0c\u518d\u9009\u62e9\u5408\u9002\u7684\u65f6\u673a\u5408\u5e76\u53d1\u52a8\u8981\u7ea6\u6536\u8d2d\uff0c\u6211\u4eec\u80fd\u591f\u7528\u6765\u53cd\u51fb\u7684\u624b\u6bb5\u5374\u6bd4\u8f83\u5355\u4e00\u3002\u201d\u845b\u660e\u4fe1\u8bf4\u9053\u3002</p><p>\u3000\u3000\u773c\u4e0b\u7684\u60c5\u5f62\uff0c\u5373\u4f7f\u65e0\u6cd5\u786e\u5207\u7684\u63a8\u6d4b\u9526\u6e56\u7684\u610f\u56fe\uff0c\u4ed6\u4eec\u5374\u8981\u8ba4\u8ba4\u771f\u771f\u7684\u6253\u4e00\u573a\u9632\u5fa1\u6218\uff0c\u8fd9\u7684\u786e\u8ba9\u845b\u660e\u4fe1\u591f\u90c1\u95f7\u7684\u3002\u6700\u8ba9\u4ed6\u62c5\u5fc3\u7684\u8fc7\u51e0\u65e5\u5c31\u8981\u516c\u5e03\u7684\u4e5d\u4e03\u5e74\u5ea6\u8d22\u52a1\u62a5\u544a\uff0c\u5927\u5e45\u4e0b\u6ed1\u7684\u76c8\u5229\u6570\u636e\u4f1a\u4fc3\u4f7f\u5927\u6279\u7684\u6295\u8d44\u8005\u653e\u5f03\u624b\u91cc\u7684\u80a1\u7968\uff0c\u4ece\u800c\u8ba9\u9526\u6e56\u4ee5\u66f4\u4f4e\u5ec9\u7684\u6210\u672c\u6536\u8d2d\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u7968\u3002</p><p>\u3000\u3000\u5c06\u80a1\u4ef7\u7ef4\u6301\u5728\u4e00\u4e2a\u76f8\u5bf9\u9ad8\u4f4d\uff0c\u662f\u72d9\u51fb\u5bf9\u624b\u7684\u6700\u4f73\u65b9\u6cd5\uff0c\u53ef\u4ee5\u8feb\u4f7f\u5bf9\u624b\u56e0\u4e3a\u8fc7\u9ad8\u7684\u6210\u672c\u800c\u653e\u5f03\u5e76\u8d2d\u3002\u9664\u4e86\u5609\u4fe1\u96c6\u56e2\u52a8\u7528\u8d44\u91d1\u8fdb\u5165\u5e02\u573a\u8fdb\u884c\u516c\u5f00\u589e\u6301\u4e4b\u5916\uff0c\u91c7\u63aa\u884c\u4e4b\u6709\u6548\u7684\u63aa\u65bd\u63d0\u9ad8\u672a\u6765\u7684\u76c8\u5229\u9884\u671f\u3001\u589e\u5f3a\u6295\u8d44\u8005\u7684\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5fc3\u60f3\u8fd9\u65f6\u5019\u6070\u662f\u63d0\u51fa\u79d1\u738b\u4e0e\u5609\u4fe1\u7535\u5b50\u8fdb\u884c\u66f4\u5bc6\u5207\u5408\u4f5c\u7684\u65f6\u5019\u4e86\u3002</p><p>\u3000\u3000\u5609\u4fe1\u7535\u5b50\u5bf9\u79d1\u738b\u7684\u6301\u80a1\u53ea\u6bd4\u6b63\u6cf0\u96c6\u56e2\u7565\u5c11\uff0c\u63a5\u8fd150\uff05\uff0c\u53d1\u6325\u79d1\u738b\u7684\u54c1\u724c\u4f18\u52bf\u4e5f\u7b26\u5408\u5609\u4fe1\u7535\u5b50\u7684\u5229\u76ca\uff0c\u5bf9\u5609\u4fe1\u7535\u5b50\u662f\u4e00\u4e2a\u5229\u597d\u6d88\u606f\u3002\u53e6\u5916\uff0c\u79d1\u738b\u6d77\u5916\u516c\u53f8\u845b\u836b\u5747\u4e2a\u4eba\u6301\u6709\u8d85\u8fc780\uff05\u7684\u80a1\u4efd\uff0c\u53d1\u6325\u79d1\u738b\u7684\u54c1\u724c\u4f18\u52bf\uff0c\u66f4\u7b26\u5408\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u4e2a\u4eba\u7684\u5229\u76ca\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u5f53\u591c\u6ca1\u6709\u79bb\u5f00\u91d1\u5c71\uff0c\u8fd8\u662f\u4e0e\u8d75\u6709\u4f26\u5728\u5929\u5357\u5927\u9152\u5e97\u5171\u8fdb\u665a\u9910\uff0c\u5728\u9152\u5e2d\u540e\uff0c\u5f20\u606a\u5c06\u9526\u6e56\u6709\u610f\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u8ba1\u5212\u544a\u8bc9\u8d75\u6709\u4f26\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u4e0d\u53ef\u80fd\u4f1a\u8fd9\u4e48\u5feb\u5c31\u7ed9\u4e88\u7b54\u590d\uff0c\u5c31\u7b97\u6881\u4f1f\u6cd5\u6ca1\u6709\u7ed9\u4e88\u7b54\u590d\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u9526\u6e56\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u8ba1\u5212\u8ba9\u4ed6\u4e0d\u52a8\u5fc3\uff0c\u800c\u662f\u8be5\u8ba1\u5212\u80cc\u540e\u8574\u85cf\u7684\u653f\u6cbb\u98ce\u9669\u4ee5\u53ca\u53ef\u80fd\u5f15\u8d77\u7684\u5de8\u5927\u4e89\u8bae\u8ba9\u4ed6\u72b9\u8c6b\u4e0d\u51b3\u3001\u62ff\u4e0d\u5b9a\u6ce8\u610f\u3002</p><p>\u3000\u3000\u8d75\u6709\u4f26\u662f\u80fd\u4fc3\u8fdb\u8be5\u6536\u8d2d\u8ba1\u5212\u8fdb\u884c\u4e0b\u53bb\u7684\u5173\u952e\u4eba\u7269\uff0c\u6668\u66e6\u7eb8\u4e1a\u867d\u7136\u7701\u5c5e\u4f01\u4e1a\uff0c\u4f46\u6bd5\u7adf\u4f4d\u4e8e\u91d1\u5c71\u5e02\u7684\u5730\u76d8\uff0c\u5bf9\u6d41\u7ecf\u91d1\u5c71\u5e02\u897f\u90ca\u7684\u996e\u9a6c\u6cb3\u9020\u6210\u5927\u9762\u79ef\u7684\u6c61\u67d3\uff0c\u5bf9\u996e\u9a6c\u6cb3\u4e24\u5cb8\u7684\u5c45\u6c11\u9020\u6210\u8bf8\u591a\u7684\u4e0d\u4fbf\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u635f\u5bb3\u5230\u4e24\u5cb8\u5c45\u6c11\u7684\u8eab\u4f53\u5065\u5eb7\uff0c\u8d75\u6709\u4f26\u53ef\u4ee5\u4ece\u73af\u4fdd\u6267\u6cd5\u7684\u89d2\u5ea6\u65bd\u52a0\u538b\u529b\u3002</p><p>\u3000\u3000\u542c\u5230\u9526\u6e56\u6709\u610f\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u8ba1\u5212\uff0c\u8d75\u6709\u4f26\u76f8\u5f53\u5403\u60ca\uff0c\u4e5d\u516b\u5e74\u79c1\u6709\u8d44\u672c\u6b32\u56fe\u6536\u8d2d\u5927\u578b\u56fd\u6709\u96c6\u56e2\uff0c\u5728\u56fd\u5185\u90fd\u6ca1\u6709\u5148\u4f8b\uff0c\u867d\u8bf4\u4e2d\u592e\u9f13\u52b1\u5730\u65b9\u5728\u7ecf\u6d4e\u4f53\u5236\u6539\u9769\u4e0a\u521b\u65b0\u3001\u5927\u80c6\u5f80\u524d\u8fc8\uff0c\u8c01\u80fd\u80af\u5b9a\u8fd9\u4e00\u6b65\u8fc8\u4e0b\u53bb\u4e0d\u662f\u5730\u96f7\u9635\u3001\u4e0d\u662f\u4e07\u4e08\u6df1\u6e0a\uff1f\u738b\u6709\u4f26\u65cb\u5373\u60f3\u5230\u6536\u8d2d\u8ba1\u5212\u5b9e\u65bd\u4e4b\u540e\u5bf9\u4ed6\u7684\u597d\u5904\uff1a\u4ed6\u4e0e\u5468\u747e\u745c\u4e4b\u95f4\u7684\u7ade\u4e89\u5173\u952e\u8fd8\u5728\u4e8e\u8c01\u80fd\u66f4\u5feb\u7684\u505a\u51fa\u5b9e\u7ee9\u3001\u5728\u91d1\u5c71\u7684\u515a\u653f\u5e72\u90e8\u7fa4\u4f53\u91cc\u5f62\u6210\u66f4\u5927\u7684\u5f71\u54cd\u529b\u3001\u624e\u4e0b\u66f4\u575a\u5b9e\u7684\u6839\u57fa\u3002</p><p>\u3000\u3000\u867d\u7136\u80fd\u9884\u6599\u5230\u9526\u6e56\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u963b\u529b\uff0c\u4f46\u662f\u4e00\u65e6\u6536\u8d2d\u6210\u529f\uff0c\u5f62\u52bf\u5c06\u4f1a\u9661\u7136\u8f6c\u53d8\u6210\u5bf9\u4ed6\u4eec\u6709\u5229\u3002</p><p>\u3000\u3000\u81f3\u4e8e\u5982\u4f55\u7ed9\u6668\u66e6\u7eb8\u4e1a\u65bd\u52a0\u538b\u529b\u5c31\u592a\u7b80\u5355\u4e86\u3002</p><p>\u3000\u3000\u7ed9\u5927\u9762\u79ef\u6c61\u67d3\u7684\u996e\u9a6c\u6cb3\u4e24\u5cb8\u7684\u5c45\u6c11\uff0c\u5bf9\u6668\u66e6\u7eb8\u4e1a\u7684\u60c5\u7eea\u53ef\u4ee5\u8bf4\u662f\u6068\u4e4b\u5165\u9aa8\u4e86\uff0c\u4e0d\u4ec5\u996e\u9a6c\u6cb3\u4e24\u5cb8\u5927\u91cf\u7684\u7530\u5730\u4e0e\u5730\u4e0b\u6c34\u88ab\u6c61\u67d3\uff0c\u56e0\u6c61\u67d3\u81f4\u4f7f\u4e24\u5cb8\u5c45\u6c11\u7684\u75be\u75c5\u53d1\u751f\u7387\u4e5f\u8981\u8fdc\u8fdc\u9ad8\u4e8e\u5176\u4ed6\u5730\u533a\uff0c\u5728\u5f53\u5730\u5c45\u6c11\u5df2\u7ecf\u9020\u6210\u6050\u614c\u7684\u60c5\u7eea\uff0c\u5927\u90e8\u5206\u519c\u6c11\u5c06\u4e24\u5cb8\u79cd\u51fa\u6765\u7684\u7cae\u98df\u90fd\u62ff\u5230\u5e02\u573a\u4e0a\u9500\u552e\uff0c\u81ea\u5df1\u518d\u4ece\u5e02\u573a\u53e6\u5916\u8d2d\u4e70\u7cae\u98df\u5403\uff0c\u6709\u6761\u4ef6\u7684\u4f4f\u6237\u751a\u81f3\u4ece\u6668\u66e6\u7eb8\u4e1a\u5de5\u5382\u4e0b\u6e38\u7684\u4e24\u5cb8\u5730\u533a\u8fc1\u8d70\u3002\u5728\u8fc7\u53bb\uff0c\u7701\u5e02\u4e24\u7ea7\u653f\u5e9c\u7684\u773c\u775b\u53ea\u770b\u5230\u6668\u66e6\u7eb8\u4e1a\u6240\u521b\u9020\u7684\u7ecf\u6d4e\u6548\u76ca\uff0c\u5bf9\u6c61\u67d3\u7ed9\u996e\u9a6c\u6cb3\u4e24\u5cb8\u5c45\u6c11\u9020\u6210\u7684\u4f24\u5bb3\u89c6\u800c\u4e0d\u89c1\uff0c\u4e0d\u4ec5\u89c6\u800c\u4e0d\u89c1\uff0c\u751a\u81f3\u5bf9\u90a3\u4e9b\u62b5\u6297\u6668\u66e6\u7eb8\u4e1a\u7684\u58f0\u97f3\u2014\u2014\u65e0\u8bba\u662f\u4e0a\u8bbf\u4e5f\u597d\u3001\u96c6\u4f53\u9759\u5750\u4e5f\u597d\u2014\u2014\u90fd\u4e25\u5389\u7684\u538b\u5236\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u91d1\u5c71\u5e02\u53ea\u8981\u96be\u5f97\u7684\u987a\u4ece\u4e00\u4e0b\u6c11\u610f\uff0c\u5c31\u80fd\u7ed9\u9526\u6e56\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u63d0\u4f9b\u7edd\u4f73\u7684\u52a9\u529b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u30003\u6708\u521d\uff0c\u56fd\u9645\u7ecf\u5408\u7ec4\u7ec7\u53d1\u8868\u62a5\u544a\u6307\u51fa\uff0c\u5c3d\u7ba1\u4e9a\u6d32\u5730\u533a\u53d1\u751f\u91d1\u878d\u5371\u673a\uff0c\u4f46\u662f\u4e5d\u4e03\u5e74\u56fd\u9645\u8d44\u672c\u5e02\u573a\u878d\u8d44\u603b\u989d\u8d85\u8fc7\u4e00\u4e07\u4e03\u5343\u4ebf\u7f8e\u5143\uff0c\u521b\u4e0b\u5386\u53f2\u6700\u9ad8\u7eaa\u5f55\uff1b\u7f8e\u56fd\u5546\u52a1\u90e8\u540c\u65f6\u5ba3\u5e03\u4e5d\u4e03\u7684\u7f8e\u56fd\u7ecf\u6d4e\u589e\u957f3.8\uff05\uff0c\u589e\u5e45\u4e3a\u8fc7\u53bb9\u5e74\u4e4b\u6700\u3002\u6240\u6709\u7684\u8ff9\u8c61\u4f3c\u4e4e\u90fd\u4e0d\u8db3\u4ee5\u8bf4\u660e\u5168\u7403\u7ecf\u6d4e\u6709\u88ab\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u62d6\u7d2f\u4e4b\u5fe7\uff0c\u4e9a\u6d32\u5404\u56fd\u53ca\u5730\u533a\u7684\u91d1\u878d\u5e02\u573a\u5728\u7ecf\u5386\u957f\u8fbe\u5927\u534a\u5e74\u65f6\u7684\u5267\u70c8\u4e0b\u632b\u4e4b\u540e\u5f00\u59cb\u8c8c\u4f3c\u6062\u590d\u6027\u7684\u4e0a\u6da8\uff0c\u9999\u6e2f\u6052\u751f\u6307\u6570\u4e5f\u4ece\u6700\u4f4e\u4e0d\u8db3\u4e5d\u5343\u70b9\u7684\u4f4e\u70b9\u53cd\u5f39\uff0c\u4e0a\u5e02\u516c\u53f8\u4e1a\u7ee9\u4e0b\u6ed1\u7684\u62c5\u5fe7\u4e5f\u7ed9\u63a9\u76d6\u5728\u865a\u5047\u7684\u6062\u590d\u6027\u7e41\u8363\u4e4b\u4e2d\uff0c\u4ea4\u6613\u91cf\u4e5f\u6301\u7eed\u56de\u5347\u3002</p><p>\u3000\u3000\u5728\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u5609\u4fe1\u96c6\u56e2\u6839\u672c\u5c31\u65e0\u6cd5\u5224\u65ad\u9526\u6e56\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u72d9\u51fb\u529b\u91cf\u5230\u5e95\u6709\u591a\u5927\uff0c\u4ed6\u4eec\u53ea\u80fd\u4f9d\u7167\u65e2\u5b9a\u7684\u9632\u5fa1\u7b56\u7565\u589e\u6301\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u4efd\uff0c\u5c06\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u63a7\u80a1\u6bd4\u4f8b\u63d0\u9ad8\u5230\u4e00\u4e2a\u76f8\u5bf9\u5b89\u5168\u7684\u6570\u503c\u4e0a\uff0c\u5374\u6d88\u8017\u4e86\u5609\u4fe1\u96c6\u56e2\u5927\u91cf\u7684\u73b0\u91d1\u3002</p><p>\u3000\u3000\u63a5\u4e0b\u6765\u534a\u4e2a\u591a\u6708\u7684\u65f6\u95f4\uff0c\u5728\u5317\u4eac\u53ec\u5f00\u7684\u5168\u56fd\u4eba\u5927\u4e5d\u5c4a\u4f1a\u8bae\u6210\u4e3a\u5168\u56fd\u4e43\u81f3\u5168\u4e16\u754c\u77a9\u76ee\u7684\u7126\u70b9\u3002\u65b0\u4e00\u5c4a\u653f\u5e9c\u7684\u603b\u7406\u3001\u526f\u603b\u7406\u3001\u56fd\u52a1\u59d4\u5458\u3001\u90e8\u957f\u90fd\u5728\u8fd9\u6b21\u4f1a\u8bae\u9009\u4e3e\u4ea7\u751f\uff0c\u8d75\u6d4e\u4e1c\u51fa\u4efb\u65b0\u4e00\u5c4a\u653f\u5e9c\u603b\u7406\uff0c\u4e3b\u6301\u4e2d\u592e\u653f\u5e9c\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u4e24\u4f1a\u4e0a\u9488\u5bf9\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u7684\u73b0\u72b6\uff0c\u63d0\u51fa\u201c\u786e\u4fdd\u4eca\u5e74\u7684\u7ecf\u6d4e\u53d1\u5c55\u901f\u5ea6\u8981\u8fbe\u52308\uff05\uff0c\u901a\u8d27\u81a8\u80c0\u7387\u5c0f\u4e8e3\uff05\u3001\u4eba\u6c11\u5e01\u4e0d\u8d2c\u503c\u201d\u7684\u7ecf\u6d4e\u76ee\u6807\uff0c\u4e0e\u6b64\u7ecf\u6d4e\u76ee\u6807\u7684\u63d0\u51fa\u76f8\u914d\u5957\u7684\u63aa\u65bd\u5219\u662f\u5305\u62ec\u52a0\u5f3a\u94c1\u8def\u3001\u516c\u8def\u3001\u519c\u7530\u6c34\u5229\u3001\u5e02\u653f\u3001\u73af\u4fdd\u7b49\u65b9\u9762\u7684\u57fa\u7840\u8bbe\u65bd\u5efa\u8bbe\u3001\u52a0\u5f3a\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u7684\u5efa\u8bbe\u3001\u52a0\u5f3a\u73b0\u6709\u4f01\u4e1a\u7684\u6280\u672f\u6539\u9020\u7b49\u5b8f\u89c2\u8c03\u6574\u63aa\u65bd\u4ee5\u523a\u6fc0\u7ecf\u6d4e\u7ee7\u7eed\u7684\u5f3a\u52b2\u589e\u6da8\u3002</p><p>\u3000\u3000\u4e0e\u666e\u901a\u6c11\u4f17\u606f\u606f\u76f8\u5173\u7684\u3001\u611f\u53d7\u4e5f\u6700\u4e3a\u6df1\u523b\u7684\uff0c\u65e0\u8fc7\u4e8e\u8fd9\u6b21\u4f1a\u8bae\u63d0\u51fa\u7684\u4f4f\u623f\u5236\u5ea6\u6539\u9769\u4e0e\u533b\u7597\u5236\u5ea6\u6539\u9769\u3002</p><p>\u3000\u3000\u4f4f\u623f\u5236\u5ea6\u6539\u9769\u915d\u917f\u5df2\u6709\u4e09\u5e74\u4e4b\u4e45\uff0c\u5c06\u73b0\u884c\u7684\u798f\u5229\u5206\u623f\u653f\u7b56\u6539\u4e3a\u8d27\u5e01\u5316\u3001\u5546\u54c1\u5316\u7684\u4f4f\u623f\u653f\u7b56\uff0c\u4fc3\u4f7f\u4f4f\u623f\u5efa\u8bbe\u6210\u4e3a\u7ecf\u6d4e\u65b0\u7684\u589e\u957f\u70b9\u3002\u867d\u7136\u5728\u4e24\u4f1a\u4e0a\u6ca1\u6709\u660e\u786e\u63d0\u51fa\u8981\u5c06\u623f\u5730\u4ea7\u4e1a\u4f5c\u4e3a\u56fd\u6c11\u7ecf\u6d4e\u7684\u652f\u67f1\u4ea7\u4e1a\uff0c\u4f46\u662f\u4ec5\u4f4f\u623f\u5236\u5ea6\u6539\u9769\u7684\u63d0\u51fa\uff0c\u5c31\u8db3\u4ee5\u8ba9\u5168\u56fd\u7684\u623f\u5730\u4ea7\u5546\u6b22\u6b23\u96c0\u8dc3\u4e86\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u3001\u5f20\u77e5\u975e\u77e5\u9053\u76f8\u5173\u6d88\u606f\u4e4b\u540e\uff0c\u751a\u81f3\u6253\u7b97\u5927\u6446\u5bb4\u5e2d\uff0c\u60f3\u9f13\u52a8\u5efa\u90ba\u5e02\u6700\u5148\u8ddf\u968f\u4e2d\u592e\u653f\u5e9c\u5236\u5b9a\u6709\u5173\u53d6\u6d88\u798f\u5229\u5206\u623f\u7684\u8be6\u7ec6\u653f\u7b56\uff0c\u5c06\u5927\u91cf\u57ce\u9547\u804c\u5de5\u7684\u4f4f\u623f\u9700\u6c42\u5b8c\u5168\u63a8\u5411\u5e02\u573a\uff0c\u71d5\u5f52\u6e56\u4e1c\u5cb8\u5546\u5708\u9879\u76ee\u4e2d\u8fd1\u4e00\u534a\u7684\u5546\u54c1\u4f4f\u5b85\u9879\u76ee\u5c06\u4ece\u8be5\u653f\u7b56\u4e2d\u76f4\u63a5\u83b7\u76ca\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u6b64\u65e9\u6709\u9884\u671f\uff0c\u5bf9\u6b64\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u7684\u6216\u574f\u7684\u770b\u6cd5\uff0c\u4f46\u662f\u5404\u5730\u533a\u7684\u623f\u5730\u4ea7\u4f55\u65f6\u80fd\u4ece\u4f4f\u623f\u5236\u5ea6\u6539\u9769\u4e2d\u76f4\u63a5\u83b7\u76ca\uff0c\u8fd8\u8981\u770b\u5730\u65b9\u653f\u5e9c\u4f55\u65f6\u79ef\u6781\u7684\u91c7\u53d6\u76f8\u914d\u5957\u7684\u653f\u7b56\u63aa\u65bd\u6765\u4fc3\u4f7f\u5730\u65b9\u57ce\u9547\u804c\u5de5\u7684\u4f4f\u623f\u4f53\u5236\u6539\u9769\u3002</p><p>\u3000\u3000\u5bf9\u9526\u6e56\u5f71\u54cd\u8f83\u5927\u7684\u5c31\u662f\u4e5d\u5c4a\u4eba\u5927\u4f1a\u8bae\u63d0\u51fa\u52a0\u5f3a\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u7684\u5efa\u8bbe\uff0c\u53e6\u5916\uff0c\u4e5d\u5c4a\u5168\u56fd\u4eba\u5927\u4f1a\u8bae\u8fd8\u901a\u8fc7\u4e2d\u592e\u653f\u5e9c\u673a\u6784\u6539\u9769\u65b9\u6848\uff0c\u5c06\u4e00\u4e0b\u5b50\u7f29\u51cf\u7684\u90e8\u59d4\u5171\u6709\u5341\u4e00\u4e2a\u4e4b\u591a\uff0c\u5728\u7535\u5b50\u5de5\u4e1a\u90e8\u4e0e\u90ae\u7535\u90e8\u7535\u4fe1\u90e8\u95e8\u7684\u57fa\u7840\u4e0a\u7ec4\u5efa\u4fe1\u606f\u4ea7\u4e1a\u90e8\uff0c\u803f\u91cd\u9633\u6beb\u65e0\u610f\u5916\u7684\u51fa\u4efb\u65b0\u7ec4\u5efa\u4fe1\u606f\u4ea7\u4e1a\u90e8\u7684\u90e8\u957f\u3002</p><p>\u3000\u3000\u867d\u7136\u8fd9\u6b21\u6ca1\u6709\u516c\u5f00\u900f\u9732\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\u7ec4\u5efa\u7684\u4fe1\u606f\uff0c\u4f46\u662f\u803f\u91cd\u9633\u5728\u65b0\u4efb\u90e8\u957f\u4eae\u76f8\u8bb0\u8005\u4f1a\u4e0a\u5173\u4e8e\u7535\u4fe1\u90e8\u95e8\u653f\u4f01\u5206\u79bb\u7684\u53d1\u8a00\u4e2d\u6697\u793a\u4e86\u76f8\u5173\u4fe1\u606f\uff0c\u52a0\u4e0a\u8fd9\u5c4a\u4e2d\u592e\u653f\u5e9c\u63d0\u51fa\u5bf9\u52a0\u5f3a\u53d1\u5c55\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u53d1\u5c55\u4e0e\u52a0\u5f3a\u56fd\u6709\u8d44\u4ea7\u5728\u6838\u5fc3\u4ea7\u4e1a\u4e2d\u5730\u4f4d\u7b49\u76f8\u5173\u8981\u6c42\uff0c\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\u7684\u7ec4\u5efa\uff0c\u5df2\u7ecf\u65e0\u6cd5\u907f\u514d\u4e86\u3002</p></article>"}}}]);
