(window.webpackJsonp=window.webpackJsonp||[]).push([[804],{827:function(p){p.exports={chapterTitle:null,title:"\u7b2c635\u7ae0 \u62db\u6536\u65b0\u4eba",content:"<article><h2>\u7b2c635\u7ae0 \u62db\u6536\u65b0\u4eba</h2><p>\u3000\u3000\u201c\u8499\u4e50\uff0c\u4e0d\u662f\u6211\u4e0d\u7ed9\u4f60\u9762\u5b50\u554a\uff0c\u6211\u5c31\u770b\u4e00\u773c\uff0c\u4e0d\u5408\u610f\u6389\u5934\u5c31\u8d70\u2026\u2026\u6211\u4eec\u5728\u8fd9\u5730\u76d8\u4e0a\u53ae\u6df7\u90fd\u7b2c\u4e09\u4e2a\u5e74\u5934\u4e86\uff0c\u4f60\u8981\u5b89\u6392\u4e2a\u7406\u4e8b\u3001\u90e8\u957f\u4e4b\u7c7b\u7684\u5934\u8854\uff0c\u8fd8\u53ef\u4ee5\u8003\u8651\u8003\u8651\uff1b\u8ba9\u6211\u4eec\u8fdb\u53bb\u5f53\u65b0\u4eba\uff0c\u6211\u8fd8\u4e0d\u5982\u5728\u4e66\u6cd5\u534f\u4f1a\u7ee7\u7eed\u8017\u7740\uff1b\u4f60\u4e5f\u6653\u5f97\u7ec3\u4e66\u6cd5\u7684\u5973\u5b69\u5b50\uff0c\u901a\u5e38\u8bf4\u6765\u6c14\u8d28\u90fd\u53ef\u4ee5\u2026\u2026\u201d</p><p>\u3000\u3000\u671d\u5317\u7684\u5c4b\u5b50\uff0c\u4e0a\u5348\u7684\u9633\u5149\u7167\u5c04\u4e0d\u5230\uff0c\u8fd9\u65f6\u5019\uff0c\u5c0f\u7ea2\u697c\u91cc\u7684\u4eba\u5f88\u5c11\uff0c\u4e0b\u5348\u3001\u665a\u4e0a\u624d\u662f\u5b66\u751f\u7ec4\u7ec7\u6d3b\u8dc3\u7684\u65f6\u5019\uff0c\u9634\u51c9\u800c\u9759\u8c27\uff0c\u9648\u5983\u84c9\u60a0\u7136\u7684\u770b\u7740\u5f20\u606a\u5199\u6d77\u62a5\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u7684\u6bdb\u7b14\u5b57\u4e5f\u8fd9\u4e48\u6f02\u4eae\uff0c\u5979\u4e0d\u6653\u5f97\u5f20\u606a\u8fd9\u4e9b\u5929\u5c31\u62ff\u5199\u6d77\u62a5\u7ec3\u5b57\u5462\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u542c\u7740\u95e8\u5916\u55a7\u56b7\u7684\u58f0\u97f3\uff0c\u597d\u4e9b\u4eba\u671d\u8fd9\u8fb9\u8d70\u8fc7\u6765\uff0c\u9648\u5983\u84c9\u64a9\u8d77\u773c\u5e18\uff0c\u7741\u7740\u6e05\u4eae\u7684\u7738\u5b50\u5fae\u8bb6\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\u3002</p><p>\u3000\u3000\u201c\u8499\u4e50\u9a97\u4eba\u56de\u6765\u4e86\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u8ddf\u9648\u5983\u84c9\u89e3\u91ca\uff0c\u53ea\u5728\u65b0\u751f\u62a5\u5230\u90a3\u5929\uff0c\u9648\u5983\u84c9\u89c1\u8fc7\u8499\u4e50\u4e00\u9762\uff0c\u521a\u624d\u90a3\u4f1a\u513f\uff0c\u5f20\u606a\u5c06\u675c\u98de\u4e0e\u8499\u4e50\u7684\u6253\u7b97\u8ddf\u5979\u4ecb\u7ecd\u4e86\u4e00\u4e0b\uff0c\u5fc3\u60f3\u9648\u5983\u84c9\u5728\u4e1c\u5927\u6821\u56ed\u91cc\u6df1\u5c45\u7b80\u51fa\u7684\uff0c\u5927\u6982\u662f\u4e0d\u60f3\u522b\u4eba\u6253\u6270\u5979\u5b81\u9759\u7684\u751f\u6d3b\u5427\u3002</p><p>\u3000\u3000\u201c\u8499\u4e50\uff0c\u6211\u4e5f\u4e0d\u662f\u6211\u8bf4\u4f60\uff0c\u4f60\u90fd\u5728\u56fd\u5546\u9662\u6df7\u4e24\u5c4a\u5b66\u751f\u4f1a\u4e3b\u5e2d\u4e86\uff0c\u5e2d\u5e08\u592a\u90fd\u6ca1\u6709\u6b63\u773c\u77a7\u8fc7\u4f60\uff1b\u4f60\u641e\u8fd9\u4e9b\uff0c\u51fa\u518d\u5927\u7684\u98ce\u5934\uff0c\u80fd\u62b5\u4ec0\u4e48\u7528\uff1f\u5973\u4eba\u7684\u5fc3\u601d\uff0c\u4e00\u5b9a\u671d\u7740\u522b\u7684\u65b9\u9762\uff0c\u4f60\u5c31\u662f\u5341\u5934\u725b\u90fd\u62c9\u4e0d\u56de\u6765\u3002\u5144\u5f1f\u6211\u6c42\u6c42\u4f60\u4e86\uff0c\u6362\u5bf9\u8c61\u5427\uff01\u6211\u8ddf\u8bf4\uff0c\u516c\u5171\u5173\u7cfb\u5b66\u9662\u7684\u90a3\u9648\u5983\u84c9\u59ff\u8272\u771f\u4e0d\u9519\uff0c\u90a3\u59ff\u8272\uff0c\u5e2d\u82e5\u7433\u4e5f\u5c31\u914d\u505a\u4eba\u5bb6\u901a\u623f\u4e2b\u5934\uff0c\u4e1c\u5927\u8303\u56f4\u5185\uff0c\u4e5f\u5c31\u6c5f\u9edb\u513f\u80fd\u8ddf\u4eba\u5bb6\u6bd4\u4e00\u6bd4\uff1b\u542c\u8bf4\u6821\u5b66\u751f\u4f1a\u65b0\u7684\u725b\u53c9\u4eba\u7269\u80e1\u91d1\u661f\u7785\u4e0a\u4eba\u5bb6\u3002\u4e0d\u8fc7\u5462\uff0c\u8c01\u7406\u6821\u5b66\u751f\u4f1a\u7684\u90a3\u73ed\u9e1f\u4eba\uff1f\u5144\u5f1f\u4eec\u5c31\u5fcd\u75db\u5c06\u8fd9\u6837\u7684\u6781\u54c1\u8d27\u8272\u8ba9\u7ed9\u4f60\u4e86\uff0c\u665a\u4e0a\u5e2e\u4f60\u53bb\u56fe\u4e66\u9986\u5835\u5979\uff0c\u4e3a\u4e86\u4f60\uff0c\u6211\u4eec\u5144\u5f1f\u51e0\u4e2a\u8c41\u51fa\u53bb\u4e86\uff0c\u626e\u6d41\u6c13\u6328\u4f60\u4e09\u62f3\u4e24\u817f\u90fd\u6210\u2026\u2026\u201d</p><p>\u3000\u3000\u95e8\u5916\u7684\u4eba\u8fd8\u5728\u80e1\u626f\uff0c\u9648\u5983\u84c9\u542c\u80e1\u626f\u5230\u81ea\u5df1\u5934\u4e0a\uff0c\u62bf\u7740\u5634\uff0c\u7c89\u96d5\u7389\u7422\u7684\u8138\u988a\u4e0a\u6cdb\u8d77\u7eef\u7ea2\uff0c\u53c8\u662f\u597d\u6c14\u53c8\u662f\u597d\u7b11\u3002</p><p>\u3000\u3000\u5f20\u606a\u653e\u4e0b\u624b\u4e2d\u7b14\uff0c\u76ef\u7740\u95e8\u53e3\uff0c\u5c31\u60f3\u770b\u8499\u4e50\u9886\u8fc7\u6765\u7684\u90a3\u4f19\u4eba\u770b\u5230\u9648\u5983\u84c9\u5c31\u6d3b\u751f\u751f\u7684\u7ad9\u5728\u8fd9\u91cc\u4f1a\u6709\u4ec0\u4e48\u8868\u60c5\u3002</p><p>\u3000\u3000\u201c\u8499\u4e50\uff0c\u4f60\u5c31\u522b\u5728\u4e00\u68f5\u6811\u4e0a\u540a\u6b7b\u4e86\u2026\u2026\u201d\u8d70\u5728\u6700\u524d\u9762\u7684\u4eba\u540e\u9000\u7740\u62ff\u5c41\u80a1\u62f1\u5f00\u95e8\uff0c\u4ed6\u8fd8\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u7ed9\u540e\u9762\u4e00\u4e2a\u4eba\u6342\u4f4f\u5634\uff0c\u5934\u7ed9\u786c\u6273\u8fc7\u6765\uff0c\u770b\u5230\u9648\u5983\u84c9\u8584\u6012\u5fae\u7f9e\u7684\u4fcf\u8138\uff0c\u773c\u775b\u77aa\u7684\u8d3c\u5706\uff0c\u50bb\u4e86\u51e0\u79d2\uff0c\u7a81\u7136\u6491\u5706\u4e86\u55d3\u95e8\u9ad8\u558a\u4e86\u4e00\u53e5\uff0c\u201c\u8001\u5a46\uff0c\u5feb\u51fa\u6765\u770b\u4e0a\u5e1d\u554a\uff01\u201d\u5c16\u9510\u7684\u53eb\u58f0\u5c06\u5835\u5728\u95e8\u53e3\u7684\u51e0\u4e2a\u5c0f\u5b50\u90fd\u60ca\u9192\u8fc7\u6765\u3002</p><p>\u3000\u3000\u51e0\u4e2a\u4eba\u60ca\u9192\u8fc7\u6765\u5c31\u662f\u62fd\u7740\u8499\u4e50\u5f80\u89d2\u843d\u91cc\u62d6\uff0c\u62f3\u811a\u76f8\u52a0\uff1a\u201c\u8499\u4e50\u4f60\u8fd9\u5c0f\u5b50\uff0c\u5144\u5f1f\u4eec\u4e00\u76f4\u90fd\u5ff5\u7740\u8981\u4ec0\u4e48\u597d\u5904\u90fd\u5148\u7559\u7ed9\u4f60\uff0c\u4f60\u5c0f\u5b50\u8fd9\u4e48\u4e0d\u5730\u9053\uff0c\u6316\u4e00\u4e2a\u5927\u5751\u8fc7\u6765\u62c9\u5144\u5f1f\u4eec\u8df3\u8fdb\u53bb\uff0c\u5feb\u8bf4\uff0c\u4f60\u5c0f\u5b50\u600e\u4e48\u5c06\u6821\u82b1\u62d0\u9a97\u8fc7\u6765\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u51a4\u6789\u554a\uff0c\u6211\u90fd\u4e0d\u6653\u5f97\u5979\u662f\u9648\u5983\u84c9\u2026\u2026\u201d\u8499\u4e50\u4ece\u51e0\u4e2a\u5c0f\u5b50\u7684\u7ea0\u7f20\u91cc\u6323\u624e\u7740\u5c06\u5934\u4f38\u51fa\u6765\uff0c\u95ee\u5f20\u606a\uff0c\u201c\u8fd9\u662f\u4f60\u7684\u884c\u674e\u8fd8\u662f\u675c\u98de\u90a3\u6df7\u5c0f\u5b50\u7684\u884c\u674e\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u884c\u674e\u4e0d\u884c\u674e\u7684\uff1f\u201d\u9648\u5983\u84c9\u4e0d\u89e3\u7684\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u65b0\u751f\u63a5\u5f85\u65f6\uff0c\u8499\u4e50\u5c31\u89c1\u8fc7\u9648\u5983\u84c9\u4e00\u9762\uff0c\u542c\u5f20\u606a\u6307\u7740\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u76db\u590f\u542b\u6df7\u7684\u8bf4\u90fd\u662f\u6709\u4e3b\u7684\u884c\u674e\uff0c\u4e5f\u6ca1\u6709\u7ec6\u95ee\u8c01\u662f\u8c01\u7684\u5973\u670b\u53cb\uff0c\u8fd9\u6bb5\u65f6\u95f4\uff0c\u770b\u5230\u5b59\u9759\u6aac\u3001\u7fdf\u4e39\u9752\u51fa\u73b0\u5728\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u5012\u6ca1\u6709\u518d\u89c1\u5230\u9648\u5983\u84c9\u3001\u5510\u5a67\u5979\u4eec\uff0c\u4e5f\u6ca1\u6709\u65f6\u95f4\u8ddf\u4ee5\u524d\u7684\u4e00\u822c\u635f\u53cb\u5728\u6821\u56ed\u91cc\u8e72\u70b9\u6d4f\u89c8\u65b0\u664b\u7684\u6821\u56ed\u7f8e\u8272\uff0c\u4e00\u76f4\u542c\u522b\u4eba\u5ff5\u53e8\u4e1c\u5927\u7684\u65b0\u6821\u82b1\u9648\u5983\u84c9\uff0c\u5f53\u7136\u4e0d\u6653\u5f97\u539f\u6765\u65e9\u5c31\u89c1\u8fc7\u9648\u5983\u84c9\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u5f20\u606a\u6253\u4e86\u6123\uff0c\u81ea\u7136\u4e0d\u4f1a\u8ddf\u9648\u5983\u84c9\u89e3\u91ca\u8fd9\u4e9b\uff0c\u53ea\u662f\u7b11\u7740\u8ddf\u8499\u4e50\u8bf4\uff0c\u201c\u5979\u554a\uff0c\u5979\u8ba9\u675c\u98de\u7ed9\u9a97\u8fdb\u6765\u7684\uff0c\u6211\u90fd\u4e0d\u6653\u5f97\u5979\u662f\u4e0d\u662f\u6709\u4e3b\u2026\u2026\u201d</p><p>\u3000\u3000\u90a3\u4e9b\u5c0f\u5b50\u9f50\u56de\u5934\u76ef\u7740\u9648\u5983\u84c9\u770b\u4e86\u4e00\u773c\uff0c\u53c8\u7a81\u7136\u5bf9\u8499\u4e50\u8f6c\u53d8\u4e86\u6001\u5ea6\uff0c\u5351\u8eac\u5c48\u819d\u7684\u8ba8\u597d\u4ed6\uff1a\u201c\u8001\u5927\uff0c\u4ee5\u540e\u5144\u5f1f\u5c31\u8ddf\u4f60\u6df7\u4e86\uff0c\u4ec0\u4e48\u7406\u4e8b\u3001\u90e8\u957f\uff0c\u54b1\u90fd\u4e0d\u5962\u6c42\u4e86\uff0c\u4f60\u5c31\u5b89\u6392\u6211\u5728\u9648\u7f8e\u5973\u624b\u4e0b\u6253\u6742\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u8499\u4e50\u62ff\u634f\u7740\u59ff\u6001\u6765\uff0c\u6574\u4e86\u6574\u7ed9\u8fd9\u4e9b\u5c0f\u5b50\u62fd\u7740\u6b6a\u4e03\u6b6a\u516b\u7684\u8863\u9886\uff0c\u201c\u6211\u4eec\u534f\u4f1a\u5c31\u662f\u62db\u65b0\u4eba\u4e5f\u662f\u6709\u8981\u6c42\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8001\u5927\uff0c\u4f60\u5c31\u4e0d\u8981\u8e6c\u9f3b\u5b50\u4e0a\u8138\u4e86\uff0c\u6211\u4eec\u90fd\u5351\u8eac\u5c48\u819d\u7684\u6c42\u4f60\u6536\u4e0b\u6211\u4eec\u5f53\u5c0f\u5f1f\u4e86\uff0c\u8981\u4e0d\u8981\u6211\u4eec\u4e0b\u8dea\u2026\u2026\u6211\u4eec\u771f\u6b63\u8dea\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8499\u4e50\u7b11\u7740\u8bf4\u5e2e\u5f20\u606a\u3001\u9648\u5983\u84c9\u4ecb\u7ecd\u4ed6\u9886\u8fc7\u6765\u7684\u4eba\u3002</p><p>\u3000\u3000\u6709\u4e24\u4e2a\u8fd8\u662f\u866b\u4ff1\u4e50\u90e8\u7684\u6210\u5458\uff0c\u53ea\u4e0d\u8fc7\u8ddf\u8499\u4e50\u4e00\u6837\uff0c\u90fd\u56e0\u4e3a\u4e0d\u662f\u5b66\u8ba1\u7b97\u673a\u51fa\u8eab\uff0c\u4e00\u4e2a\u662f\u6cd5\u5b66\u9662\u7684\u5927\u4e09\u5b66\u751f\u8463\u8dc3\u534e\uff0c\u4e00\u4e2a\u662f\u65b0\u95fb\u4f20\u64ad\u5b66\u9662\u7684\u5927\u4e8c\u5b66\u751f\u65f6\u5b66\u658c\u2014\u2014\u521a\u624d\u5728\u8fc7\u9053\u91cc\u56b7\u56b7\u6700\u5927\u58f0\u7684\u5c31\u662f\u4ed6\uff0c\u866b\u4ff1\u4e50\u90e8\u771f\u6b63\u8981\u5f00\u59cb\u5f00\u53d1\u6e38\u620f\u5e76\u5411\u6a61\u6811\u56ed\u521b\u6295\u4e2d\u5fc3\u9012\u4ea4\u878d\u8d44\u7533\u8bf7\u65f6\uff0c\u5c06\u4ed6\u4eec\u8fd9\u4e9b\u975e\u6280\u672f\u4eba\u5458\u5254\u9664\u5728\u5916\u3002</p><p>\u3000\u3000\u8463\u8dc3\u534e\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u4fe9\u6ca1\u6709\u53d7\u4ec0\u4e48\u60c5\u4f24\uff0c\u53ea\u662f\u4e0d\u5fff\u81ea\u5df1\u7684\u4ef7\u503c\u7ed9\u5ffd\u89c6\u6389\uff0c\u53c8\u4e0e\u8499\u4e50\u7684\u5173\u7cfb\u4ea4\u597d\uff0c\u89c1\u8499\u4e50\u5f7b\u5e95\u8131\u79bb\u866b\u4ff1\u4e50\u90e8\uff0c\u4ed6\u4eec\u4fe9\u4e5f\u5c31\u5f88\u5c11\u518d\u4e0aBBS\uff0c\u82e5\u662f\u534a\u6b63\u5f0f\u7684\u8131\u79bb\u4e86\u866b\u4ff1\u4e50\u90e8\u3002</p><p>\u3000\u3000\u8463\u8dc3\u534e\u3001\u65f6\u5b66\u658c\uff0c\u8fd8\u6709\u53e6\u4e24\u4e2a\u4eba\uff0c\u5f20\u606a\u4e5f\u90fd\u8ba4\u5f97\uff1b\u53ea\u662f\u4ed6\u4eec\u90fd\u4e0d\u8ba4\u5f97\u5f20\u606a\u7f62\u4e86\u3002</p><p>\u3000\u3000\u8499\u4e50\u4ecb\u7ecd\u5230\u5f20\u606a\u7684\u540d\u5b57\u65f6\uff0c\u8fd9\u4e9b\u4eba\u90fd\u4e0b\u610f\u8bc6\u7684\u62bf\u7740\u5634\uff0c\u514b\u5236\u7740\u6ca1\u8ba9\u201c\u539f\u6765\u662f\u8fd9\u9e1f\u4eba\u201d\u8fd9\u53e5\u8bdd\u4ece\u5634\u5df4\u91cc\u8e66\u51fa\u6765\uff0c\u90fd\u7b11\u7740\u8bf4\uff1a\u201c\u4e45\u4ef0\uff0c\u4e45\u4ef0\uff0c\u771f\u662f\u4e45\u4ef0\u4e86\uff0c\u4e1c\u5927\u5c11\u8bf4\u6709\u4e00\u534a\u7684\u5973\u751f\u90fd\u8ba4\u8bc6\u4f60\u4e86\uff1b\u8fd8\u4ee5\u4e3a\u4f60\u521a\u8fdb\u4e1c\u5927\u4ee5\u540e\u7684\u751f\u6daf\u4f1a\u5f88\u75db\u82e6\u5462\uff0c\u6ca1\u60f3\u5230\u4f60\u8001\u6d3b\u5f97\u6bd4\u6211\u4eec\u6ecb\u6da6\u591a\u4e86\u2026\u2026\u201d\u8fdb\u6765\u770b\u5230\u5f20\u606a\u5199\u6d77\u62a5\u3001\u9648\u5983\u84c9\u8896\u7ba1\u633d\u7740\u3001\u96ea\u767d\u6676\u83b9\u7684\u7d20\u624b\u5e2e\u4ed6\u538b\u7740\u7eb8\uff0c\u90fd\u8981\u7fa1\u6155\u6b7b\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u62bf\u7740\u5634\u7b11\uff0c\u77e5\u9053\u8fd9\u4e9b\u4eba\u201c\u4e45\u4ef0\u201d\u5f20\u606a\u8fd8\u662f\u5973\u751f\u5bbf\u820d\u697c\u524d\u6c14\u7403\u6548\u5e94\u7684\u5e7f\u6cdb\u5f71\u54cd\u3002</p><p>\u3000\u3000\u8499\u4e50\u5c06\u8fd9\u4e9b\u5c0f\u5b50\u4ece\u529e\u516c\u684c\u524d\u6252\u5f00\uff0c\u8ba4\u771f\u7ed9\u4ed6\u4eec\u4ecb\u7ecd\u60c5\u51b5\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u5c0f\u5b50\u53eb\u675c\u98de\uff0c\u534f\u4f1a\u7684\u7406\u4e8b\uff0c\u6682\u65f6\u53ea\u6709\u6211\u4eec\u4e09\u4e2a\u4eba\uff0c\u5bab\u4e66\u8bb0\u8fd8\u6709\u5c31\u662f\u6211\u4eec\u56fd\u5546\u9662\u7684\u79e6\u521a\u62c5\u4efb\u534f\u4f1a\u7684\u6307\u5bfc\u8001\u5e08\uff0c\u8fd9\u4e48\u8bf4\uff0c\u4f60\u4eec\u5927\u6982\u5c31\u77e5\u9053\u534f\u4f1a\u7684\u5206\u91cf\u4e86\uff0c\u4e0d\u662f\u4e0d\u7ed9\u4f60\u5206\u5b98\u8d50\u7235\uff0c\u4e3b\u8981\u662f\u6211\u4eec\u534f\u4f1a\u7684\u6027\u8d28\u8ddf\u5176\u4ed6\u5b66\u751f\u7ec4\u7ec7\u6709\u7740\u4e0d\u4e00\u6837\u7684\u5730\u65b9\u2026\u2026\u201d\u521a\u8981\u8be6\u7ec6\u4ecb\u7ecd\u60c5\u51b5\uff0c\u542c\u89c1\u8fc7\u9053\u91cc\u6709\u4eba\u558a\uff1a\u201c\u8499\u4e50\u3001\u8499\u4e50\u2026\u2026\u201d</p><p>\u3000\u3000\u8499\u4e50\u5e94\u4e86\u4e00\u58f0\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u6ca1\u89c1\u4eba\u8fdb\u6765\u3002\u5f20\u606a\u7ad9\u7684\u4f4d\u7f6e\u79bb\u95e8\u8fd1\uff0c\u6253\u5f00\u95e8\u63a2\u5934\u5f80\u8fc7\u9053\u91cc\u770b\u4e86\u770b\uff0c\u8fc7\u9053\u91cc\u7a7a\u65e0\u4e00\u4eba\uff0c\u6487\u6487\u5634\uff0c\u5f00\u73a9\u7b11\u8bf4\uff1a\u201c\u9047\u5230\u9b3c\u4e86\uff1f\u201d\u5173\u4e0a\u95e8\u793a\u610f\u8499\u4e50\u7ee7\u7eed\u7ed9\u5927\u5bb6\u4ecb\u7ecd\u60c5\u51b5\uff0c\u53c8\u542c\u89c1\u6709\u4eba\u5728\u8fc7\u9053\u91cc\u558a\u8499\u4e50\uff0c\u6253\u5f00\u95e8\u770b\u89c1\u5e2d\u82e5\u7433\u4e0e\u90a3\u5929\u79bb\u5f00\u7f51\u7403\u573a\u8f7d\u5979\u53bb\u6559\u5de5\u98df\u5802\u3001\u4e5f\u662f\u866b\u4ff1\u4e50\u90e8\u6210\u5458\u4e4b\u4e00\u7684\u9ad8\u4e2a\u5b50\u7537\u751f\u4ece\u9694\u58c1\u5b66\u751f\u4f1a\u7684\u529e\u516c\u5ba4\u91cc\u8d70\u51fa\u6765\uff0c\u539f\u6765\u4ed6\u4eec\u542c\u5230\u8499\u4e50\u7684\u5e94\u58f0\uff0c\u4ee5\u4e3a\u8499\u4e50\u5728\u5b66\u751f\u4f1a\u529e\u516c\u5ba4\u91cc\uff0c\u5c31\u8dd1\u5230\u5b66\u751f\u4f1a\u529e\u516c\u5ba4\u91cc\u627e\u4ed6\u4eec\u53bb\u4e86\u3002</p><p>\u3000\u3000\u201c\u8499\u4e50\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u627e\u9519\u5730\u65b9\u4e86\u2026\u2026\u201d\u5f20\u606a\u63a2\u5934\u8ddf\u5e2d\u82e5\u7433\u8bf4\u4e86\u4e00\u53e5\uff0c\u62db\u547c\u4ed6\u4eec\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u770b\u5230\u5f20\u606a\u5728\u8fd9\u91cc\uff0c\u6709\u4e9b\u610f\u601d\uff0c\u4ed6\u4e0a\u5348\u6ca1\u6709\u53bb\u5e74\u7ea7\u529e\uff0c\u90fd\u6ca1\u6709\u4eba\u5199\u6d77\u62a5\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u6253\u5f00\u95e8\u8ba9\u5e2d\u82e5\u7433\u8ddf\u90a3\u4e2a\u7537\u7684\u8fdb\u6765\uff0c\u770b\u7740\u8499\u4e50\u8fd9\u5c0f\u5b50\u4e0d\u4e89\u6c14\u7684\u7d27\u5f20\u8d77\u6765\uff0c\u6487\u5634\u7b11\u4e86\u7b11\uff1a\u5e73\u65f6\u518d\u6f47\u6d12\u7684\u4e00\u4e2a\u4eba\uff0c\u4e5f\u603b\u6709\u653e\u4e0d\u5f00\u7684\u65f6\u5019\u3002</p><p>\u3000\u3000\u660e\u77e5\u9053\u5e2d\u82e5\u7433\u8ddf\u90a3\u4e2a\u7537\u7684\u4e3a\u4ec0\u4e48\u627e\u8fc7\u6765\uff0c\u5f20\u606a\u8fd8\u662f\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u4f60\u4eec\u8fc7\u6765\u627e\u8499\u4e50\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u90a3\u4e2a\u7537\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u6ca1\u6709\u7406\u4f1a\u4ed6\uff0c\u76f4\u63a5\u95ee\u8499\u4e50\uff1a\u201c\u4f60\u5230\u5e95\u5728\u641e\u4ec0\u4e48\u9b3c\uff0c\u4e3a\u4ec0\u4e48\u6211\u4eec\u4eca\u5929\u65e9\u4e0a\u63a5\u5230\u901a\u77e5\u8bf4\u90a3\u4efd\u9879\u76ee\u4e66\u5728\u4f60\u624b\u91cc\uff1f\u201d\u8bed\u6c14\u6709\u4e9b\u4e0d\u8010\u70e6\uff0c\u4f3c\u4e4e\u7ed9\u5192\u72af\u4e86\u4e00\u6837\u3002</p><p>\u3000\u3000\u201c\u90b5\u5f6c\uff0c\u4ec0\u4e48\u9879\u76ee\u4e66\uff0c\u4f60\u600e\u4e48\u4e00\u8fc7\u6765\u5c31\u8bf4\u6ca1\u5934\u6ca1\u8111\u7684\u8bdd\uff1f\u201d\u65f6\u5b66\u658c\u4e4b\u524d\u4e5f\u662f\u866b\u4ff1\u4e50\u90e8\u6210\u5458\uff0c\u4e5f\u53c2\u4e0e\u4e86\u6700\u521d\u7684\u6e38\u620f\u8bbe\u5b9a\uff0c\u8ba4\u8bc6\u5e2d\u82e5\u7433\u4ee5\u53ca\u90a3\u4e2a\u4e00\u8d77\u8fdb\u6765\u7684\u7537\u751f\u3002</p><p>\u3000\u3000\u201c\u88c5\u4ec0\u4e48\u849c\uff0c\u4f60\u4eec\u641e\u51fa\u6765\u7684\u9b3c\uff0c\u4e0d\u6653\u5f97\u662f\u4ec0\u4e48\u9879\u76ee\u4e66\uff1f\u201d\u4e0e\u5e2d\u82e5\u7433\u8fdb\u6765\u7684\u90a3\u4e2a\u7537\u751f\u8bed\u6c14\u6709\u4e9b\u51b2\uff0c\u5f53\u521d\u5c06\u5176\u4ed6\u975e\u6280\u672f\u4eba\u5458\u6392\u9664\u5728\u5916\uff0c\u5c31\u6653\u5f97\u4ed6\u4eec\u5fc3\u91cc\u4e0d\u75db\u5feb\uff0c\u65e9\u4e0a\u63a5\u5230\u6a61\u6811\u56ed\u521b\u6295\u4e2d\u5fc3\u7684\u7535\u8bdd\u901a\u77e5\u8bf4\u300a\u4e09\u56fd\u70c8\u8c6a\u4f20\u300b\u7684\u6e38\u620f\u5f00\u4e66\u9879\u76ee\u4e66\u843d\u5728\u8499\u4e50\u624b\u91cc\uff0c\u5f88\u4e00\u809a\u5b50\u7684\u4e0d\u89e3\uff0c\u8dd1\u8fc7\u6765\u53c8\u770b\u8499\u4e50\u8ddf\u5176\u4ed6\u4e24\u4e2a\u8131\u79bb\u866b\u4ff1\u4e50\u90e8\u7684\u6210\u5458\u5728\u8fd9\u91cc\u805a\u9996\uff0c\u96be\u514d\u8981\u60f3\u5230\u522b\u5904\u53bb\u3002</p><p>\u3000\u3000\u201c\u65f6\u5b66\u658c\u4ed6\u4eec\u7684\u786e\u4e0d\u77e5\u9053\u60c5\u51b5\uff0c\u201d\u5f20\u606a\u5f80\u524d\u7ad9\u4e86\u4e00\u6b65\uff0c\u201c\u6211\u6765\u8ddf\u4f60\u89e3\u91ca\u662f\u600e\u4e48\u56de\u6765\u2014\u2014\u4ece\u4f60\u4eec\u7684\u9879\u76ee\u4e66\u91cc\u9762\uff0c\u770b\u4e0d\u5230\u6709\u56e2\u961f\u5408\u4f5c\u7684\u7cbe\u795e\uff0c\u6a61\u6811\u56ed\u90a3\u8fb9\u4e0d\u8ba4\u4e3a\u53ea\u662f\u4e00\u4e9b\u5b66\u8ba1\u7b97\u673a\u51fa\u8eab\u7684\u5c31\u80fd\u5c06\u4e00\u4e2a\u6e38\u620f\u5f00\u53d1\u7684\u5546\u4e1a\u9879\u76ee\u505a\u597d\u2014\u2014\u6240\u4ee5\uff0c\u4f60\u4eec\u7684\u9879\u76ee\u4e66\u88ab\u5224\u5b9a\u662f\u6ca1\u6709\u591a\u5c11\u5546\u4e1a\u4ef7\u503c\u7684\uff0c\u4e00\u65e9\u5c31\u7ed9\u6a61\u6811\u56ed\u90a3\u8fb9\u67aa\u6bd9\u6389\u4e86\uff0c\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5f20\u606a\u7a0d\u7a0d\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u770b\u4e86\u5e2d\u82e5\u7433\u4e00\u773c\uff0c\u53c8\u6beb\u4e0d\u5ba2\u6c14\u7684\u3001\u5e26\u7740\u6559\u8bad\u7684\u8bed\u6c14\u8ddf\u7ad9\u5728\u5979\u8eab\u8fb9\u7684\u9ad8\u4e2a\u5b50\u7537\u7684\u8bf4\u9053\uff0c\u201c\u5f88\u9057\u61be\u6ca1\u6709\u53ca\u65f6\u901a\u77e5\u4f60\u8ddf\u5176\u4ed6\u866b\u4ff1\u4e50\u90e8\u7684\u6210\u5458\u3002\u81f3\u4e8e\u9879\u76ee\u4e66\u4e3a\u4ec0\u4e48\u5728\u8499\u4e50\u624b\u91cc\uff1f\u6211\u6765\u544a\u8bc9\u4f60\uff0c\u8499\u4e50\u4e0e\u6211\u4eec\u641e\u7684\u5546\u4e1a\u8ba1\u5212\u4ece\u6a61\u6811\u56ed\u62ff\u5230\u4e00\u5343\u4e07\u7684\u6295\u8d44\u6b3e\uff0c\u8499\u4e50\u4ee5\u4e3a\u866b\u4ff1\u4e50\u90e8\u7684\u6e38\u620f\u5f00\u53d1\u8ba1\u5212\u8fd8\u6709\u633d\u6551\u7684\u4f59\u5730\uff0c\u4e3b\u52a8\u8981\u6c42\u5c06\u9879\u76ee\u63a5\u8fc7\u6765\u3002\u6240\u4ee5\uff0c\u4f60\u4eec\u7684\u878d\u8d44\u7533\u8bf7\u5df2\u7ecf\u4ece\u6a61\u6811\u56ed\u8f6c\u6211\u4eec\u624b\u91cc\u3002\u81f3\u4e8e\u6211\u4eec\u662f\u8c01\uff1f\u6211\u6765\u544a\u8bc9\u4f60\u3002\u65f6\u95f4\u5f88\u4ed3\u4fc3\uff0c\u6628\u5929\u624d\u8ddf\u5bab\u4e66\u8bb0\u63d0\u51fa\u521b\u4f1a\u5ba1\u8bf7\uff0c\u95e8\u53e3\u7684\u724c\u5b50\u8fd8\u6ca1\u6709\u6302\u51fa\u6765\uff0c\u8fd9\u91cc\u4ee5\u540e\u662f\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u7684\u5730\u76d8\uff0c\u65b0\u6210\u7acb\u7684\u534f\u4f1a\u3002\u6211\u4eec\u4ece\u6a61\u6811\u56ed\u62ff\u4e00\u5343\u4e07\u7684\u8d44\u91d1\uff0c\u4e3b\u8981\u5c31\u662f\u652f\u6301\u5b66\u751f\u521b\u4e1a\uff0c\u6709\u4e9b\u6a61\u6811\u56ed\u8ba4\u4e3a\u6ca1\u6709\u591a\u5c11\u5546\u4e1a\u4ef7\u503c\u7684\u9879\u76ee\uff0c\u6211\u4eec\u4e5f\u4f1a\u8003\u8651\u4ece\u6276\u6301\u5b66\u751f\u521b\u4e1a\u7684\u89d2\u5ea6\u4e88\u4ee5\u652f\u6301\u3002\u5f88\u5bf9\u4e0d\u8d77\uff0c\u6628\u5929\u521a\u62ff\u9879\u76ee\u4e66\uff0c\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u8ddf\u4f60\u4eec\u8054\u7edc\uff0c\u770b\u4f60\u7684\u8bed\u6c14\uff0c\u4f3c\u4e4e\u5f88\u53d7\u59d4\u5c48\uff0c\u4f60\u5982\u679c\u80fd\u4ee3\u8868\u866b\u4ff1\u4e50\u90e8\u7684\u5176\u4ed6\u6210\u5458\uff0c\u73b0\u5728\u5c31\u53ef\u4ee5\u5c06\u9879\u76ee\u4e66\u62ff\u56de\u53bb\u3001\u64a4\u51fa\u7533\u8bf7\u2026\u2026\u4f60\u53eb\u90b5\u5f6c\u662f\u5427\uff1f\u4f60\u4e5f\u4e0d\u662f\u8fd9\u4e2a\u9879\u76ee\u4e66\u6307\u5b9a\u7684\u8054\u7edc\u4eba\u554a\uff0c\u4f60\u4e5f\u6ca1\u6709\u8d44\u683c\u63d0\u51fa\u64a4\u56de\u8981\u6c42\u3002\u201d\u56de\u5934\u95ee\u8499\u4e50\uff0c\u201c\u8054\u7edc\u662f\u8c01\uff1f\u597d\u50cf\u8bb0\u5f97\u53eb\u65bd\u65b0\u98de\u7684\u5bb6\u4f19\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d</p><p>\u3000\u3000\u8ba9\u5f20\u606a\u5c45\u9ad8\u4e34\u4e0b\u7684\u6559\u8bad\u4e86\u4e00\u901a\uff0c\u90b5\u5f6c\u7684\u6c14\u7130\u987f\u65f6\u7184\u706d\uff0c\u6709\u4e9b\u96be\u4ee5\u7f6e\u4fe1\u7684\u770b\u7740\u8499\u4e50\uff0c\u4ed6\u4eec\u600e\u4e48\u53ef\u80fd\u4ece\u6a61\u6811\u56ed\u521b\u6295\u4e2d\u5fc3\u62ff\u5230\u8fd9\u4e48\u591a\u94b1\uff1f</p><p>\u3000\u3000\u6709\u5f20\u606a\u5e2e\u4ed6\u6321\u4e86\u8fd9\u4e00\u4e0b\uff0c\u8499\u4e50\u56de\u8fc7\u795e\u6765\uff0c\u89c6\u7ebf\u4ece\u5e2d\u82e5\u7433\u7684\u773c\u775b\u4e0a\u79fb\u5f00\uff0c\u5bf9\u90b5\u5f6c\u8bf4\uff1a\u201c\u4f60\u53bb\u627e\u65bd\u65b0\u98de\u4ed6\u4eec\u8fc7\u6765\u8ddf\u6211\u8c08\u2014\u2014\u77aa\u7740\u773c\u775b\u3001\u634b\u7740\u8896\u5b50\uff0c\u8fd9\u91cc\u6ca1\u4eba\u6b20\u4f60\u94b1\u3002\u201d\u8bed\u6c14\u591a\u5c11\u6709\u4e9b\u4e0d\u5ba2\u6c14\u3002\u5f53\u521d\u5c31\u662f\u8fd9\u5c0f\u5b50\u575a\u6301\u7740\u5c06\u5176\u4ed6\u975e\u6280\u672f\u4eba\u5458\u5254\u9664\u51fa\u53bb\u7684\uff0c\u53c8\u662f\u8fd9\u5c0f\u5b50\u6574\u5929\u90fd\u7ea0\u7f20\u7740\u5e2d\u82e5\u7433\uff0c\u867d\u7136\u8bf4\u5e2d\u82e5\u7433\u53e6\u6709\u7537\u53cb\uff0c\u4f46\u662f\u8499\u4e50\u5bf9\u4ed6\u591a\u5c11\u4f1a\u89c9\u5f97\u6709\u4e9b\u4e0d\u723d\uff0c\u53ea\u662f\u521a\u624d\u770b\u5230\u5e2d\u82e5\u7433\u4e00\u8d77\u8fdb\u6765\uff0c\u8fd8\u6ca1\u6709\u51c6\u5907\u600e\u4e48\u53bb\u9762\u5bf9\u5e2d\u82e5\u7433\uff0c\u6709\u4e9b\u65b9\u5bf8\u5927\u4e71\u7684\u4e0d\u5b89\u4e0e\u7d27\u5f20\uff0c\u8fd9\u65f6\u5019\u597d\u591a\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u653e\u5728\u80cc\u540e\u7ed9\u8499\u4e50\u7ad6\u4e86\u4e2a\u5927\u62c7\u6307\uff0c\u5c31\u5e0c\u671b\u4ed6\u80fd\u5f3a\u52bf\u4e00\u4e9b\uff0c\u8fd9\u6837\u624d\u80fd\u9547\u4f4f\u5c40\u9762\u3002</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u4e5f\u89c9\u5f97\u5f88\u4e0d\u53ef\u601d\u8bae\uff0c\u89c1\u8499\u4e50\u8981\u626f\u7834\u8138\u5c06\u90b5\u5f6c\u8d76\u51fa\u53bb\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u65e9\u4e0a\u63a5\u5230\u6a61\u6811\u56ed\u7684\u7535\u8bdd\u901a\u77e5\uff0c\u5c31\u8ddf\u65bd\u65b0\u98de\u4ed6\u4eec\u4e00\u8d77\u6ee1\u6821\u56ed\u7684\u627e\u4f60\uff0c\u6211\u8ddf\u90b5\u5f6c\u542c\u8bf4\u4f60\u5728\u5c0f\u7ea2\u697c\u8fd9\u8fb9\uff0c\u65bd\u65b0\u98de\u4ed6\u4eec\u4e00\u4f1a\u513f\u5c31\u4f1a\u8d76\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u8fc7\u9053\u91cc\u4f20\u6765\u6025\u51b2\u51b2\u7684\u811a\u6b65\u58f0\uff0c\u65f6\u5b66\u658c\u8d70\u5230\u95e8\u53e3\u5f80\u770b\u4e86\u770b\uff0c\u62db\u547c\u65bd\u65b0\u98de\u4ed6\u4eec\uff1a\u201c\u5728\u8fd9\u91cc\u3002\u201d\u8d76\u7740\u675c\u98de\u4e5f\u4ece\u5916\u9762\u8d76\u56de\u6765\uff0c\u8d70\u8fdb\u6765\uff0c\u8be7\u5f02\u7684\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u624d\u51fa\u53bb\u4e0d\u5230\u4e24\u4e2a\u5c0f\u65f6\uff0c\u8499\u4e50\u4f60\u5c0f\u5b50\u90fd\u9a97\u8fdb\u6765\u8fd9\u4e48\u591a\u4eba\u4e86\u2026\u2026\u201d\u770b\u5230\u5e2d\u82e5\u7433\u4e5f\u7ad9\u5728\u91cc\uff0c\u8bf4\u9053\uff0c\u201c\u54a6\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u5c0f\u5b50\u4e0d\u6562\u8ddf\u5e2d\u5e08\u59d0\u8bf4\u8bdd\u4e86\u5462\uff0c\u6ca1\u60f3\u5230\u4f60\u6709\u80c6\u5b50\u5c06\u5e2d\u5e08\u59d0\u4e5f\u62d0\u8fdb\u6765\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4e4b\u524d\u7684\u6c14\u6c1b\u591a\u5c11\u6709\u4e9b\u5251\u62d4\u5f29\u5f20\uff0c\u8ba9\u675c\u98de\u8fd9\u4e00\u6405\u548c\uff0c\u5c31\u7ed9\u51b2\u6de1\u4e86\u3002</p></article>"}}}]);
