(window.webpackJsonp=window.webpackJsonp||[]).push([[806],{829:function(p){p.exports={chapterTitle:null,title:"\u7b2c803\u7ae0 \u6597\u5bcc",content:"<article><h2>\u7b2c803\u7ae0 \u6597\u5bcc</h2><p>\u3000\u3000\u201c\u665a\u6674\u59d0\u65e2\u7136\u4e0d\u5728\uff0c\u90a3\u6211\u5c31\u5148\u8d70\u4e86\u2026\u2026\u201d\u611f\u89c9\u5230\u7fdf\u4e39\u9752\u62d2\u4eba\u5343\u91cc\u7684\u6001\u5ea6\uff0c\u5f20\u606a\u5fc3\u91cc\u6cdb\u8d77\u4e9b\u5931\u843d\uff0c\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u5fc5\u8981\u4e3a\u521a\u624d\u7684\u8bef\u4e2d\u526f\u8f66\u89e3\u91ca\u4ec0\u4e48\u4e86\uff0c\u5c31\u8ba9\u5979\u5f53\u6210\u68a6\u91cc\u7684\u6625\u60c5\u5f97\u4e86\uff0c\u6ca1\u6709\u7b49\u7fdf\u4e39\u9752\u5728\u623f\u95f4\u91cc\u56de\u7b54\uff0c\u5c31\u4e0b\u4e86\u697c\u3002</p><p>\u3000\u3000\u542c\u7740\u5f20\u606a\u4e0b\u697c\u7684\u811a\u6b65\u58f0\u4ee5\u53ca\u5f00\u95e8\u5173\u95e8\u7684\u58f0\u97f3\uff0c\u7fdf\u4e39\u9752\u5728\u95e8\u540e\u762b\u8f6f\u7684\u5750\u4e86\u4e0b\u6765\uff0c\u5750\u4e86\u7247\u523b\u53c8\u575a\u5f3a\u7684\u7ad9\u4e86\u8d77\u6765\uff0c\u8d70\u5230\u7a97\u524d\u770b\u7740\u5f20\u606a\u8d70\u51fa\u516c\u5bd3\u697c\u2014\u2014\u4ed6\u7684\u8f66\u505c\u5728\u697c\u524d\u7684\u7a7a\u5730\u4e0a\u3002</p><p>\u3000\u3000\u867d\u7136\u5fc3\u91cc\u767e\u5473\u9648\u6742\uff0c\u4f46\u662f\u7fdf\u4e39\u9752\u5e76\u4e0d\u662f\u90a3\u79cd\u62d6\u6ce5\u5e26\u6c34\u7684\u5973\u4eba\uff0c\u4f3c\u4e4e\u7529\u7529\u624b\u5c31\u80fd\u5c06\u521a\u624d\u7684\u610f\u5916\u629b\u4e4b\u8111\u540e\uff0c\u4e5f\u53ea\u80fd\u5f53\u6210\u610f\u5916\u6765\u5904\u7406\u4e86\uff0c\u51dd\u671b\u7740\u697c\u4e0b\u7684\u7537\u4eba\u94bb\u8fdb\u8f66\u91cc\uff0c\u867d\u7136\u60c5\u77e5\u81ea\u5df1\u65e0\u6cd5\u81ea\u62e8\u7684\u6df1\u9677\u8fdb\u53bb\uff0c\u4e5f\u4e0d\u60f3\u4ee5\u8fd9\u79cd\u65b9\u5f0f\u5f7b\u5e95\u7684\u7f34\u68b0\u6ca6\u9677\u3002\u8bf4\u5230\u5e95\uff0c\u81ea\u5df1\u53ea\u662f\u4ed6\u9501\u5728\u5fc3\u623f\u91cc\u7684\u90a3\u53ea\u9b54\u9b3c\u7f62\u4e86\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e16\u754c\u4f1a\u4e0d\u4f1a\u8fd8\u80fd\u6709\u4e00\u4e2a\u5973\u4eba\u5c06\u4ed6\u5f7b\u5934\u5f7b\u5c3e\u7684\u541e\u566c\u5f97\u5e72\u51c0\uff0c\u800c\u4e0d\u8ba9\u4ed6\u5fc3\u5934\u7684\u9b54\u9b3c\u8dd1\u51fa\u6765\u7978\u5bb3\u5176\u4ed6\u5973\u4eba\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8eab\u5b50\u4f9d\u5728\u7a97\u4e0a\uff0c\u60f3\u8d77\u65b0\u829c\u590f\u5b63\u4e0b\u96e8\u9ec4\u660f\u4e2d\u90a3\u4e2a\u6491\u7740\u4f1e\u7a7f\u7740\u767d\u8fde\u8863\u88d9\u7684\u5973\u5b69\u5b50\uff0c\u4e3a\u4f55\u5f20\u606a\u7ed9\u8fd9\u4e2a\u964c\u751f\u7684\u5973\u5b69\u5b50\u7cfb\u978b\u5e26\u65f6\u7adf\u6709\u8ba9\u4eba\u6709\u5982\u6b64\u60b2\u4f24\u96be\u6291\u7684\u611f\u89c9\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5750\u5728\u8f66\u91cc\u62ac\u5934\u770b\u4e86\u770b\u697c\u4e0a\uff0c\u6ca1\u6709\u4eae\u706f\uff0c\u4e5f\u770b\u4e0d\u89c1\u7fdf\u4e39\u9752\u5c31\u4f9d\u5728\u7a97\u5b50\u8fb9\u770b\u7740\u4e0b\u9762\uff0c\u62ac\u624b\u770b\u4e86\u770b\u8868\uff0c\u5df2\u7ecf\u662f\u51cc\u6668\u4e24\u70b9\u4e86\uff0c\u5efa\u90ba\u6b64\u65f6\u7684\u591c\u989d\u5916\u7684\u5bc2\u9759\uff0c\u5750\u5728\u8f66\u91cc\uff0c\u4f3c\u4e4e\u53ea\u542c\u89c1\u5916\u9762\u6709\u866b\u5b50\u5728\u53eb\u3002\u5bbd\u655e\u7684\u9a6c\u8def\u4e0a\u5076\u5c14\u80fd\u770b\u5230\u51cc\u6668\u8fd8\u5728\u5916\u9762\u901b\u8361\u7684\u884c\u4eba\uff0c\u8fd9\u4e9b\u884c\u4eba\u6216\u8bb8\u662f\u901b\u6b22\u5230\u51cc\u6668\uff0c\u4e5f\u6216\u8bb8\u662f\u60c5\u4fa3\u4e0d\u820d\u5f97\u5206\u5f00\u53c8\u6ca1\u6709\u52c7\u6c14\u53bb\u5bbe\u9986\u5f00\u623f\u95f4\uff0c\u4e5f\u6709\u6324\u5728\u5899\u89d2\u91cc\u907f\u98ce\u7684\u6d41\u6d6a\u6c49\uff0c\u6216\u8bb8\u4e5f\u6709\u50cf\u81ea\u5df1\u8fd9\u6837\u7ed9\u5973\u4eba\u62d2\u7edd\u540e\u72fc\u72c8\u79bb\u5f00\u7684\u4eba\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5728\u5979\u7565\u6709\u4e9b\u98ce\u5c18\u5473\u7684\u98ce\u60c5\u5a9a\u8273\u4e4b\u4e0b\u85cf\u7740\u4ed6\u4eba\u7edd\u5c11\u80fd\u8ba4\u8bc6\u5230\u7684\u51fa\u5c18\u4e0d\u67d3\u4e0e\u575a\u97e7\u4e0d\u62e8\u3002</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u6709\u7740\u66f4\u591a\u51cc\u6668\u8fd8\u4e0d\u80af\u6d88\u6563\u7684\u4eba\u7fa4\uff0c\u9ad8\u6821\u7684\u5b66\u751f\u4eec\u6709\u7740\u65e0\u5c3d\u65e0\u529b\u7684\u7cbe\u529b\u53bb\u5b09\u95f9\u6b22\u4e50\uff0c\u66f4\u559c\u6b22\u6c89\u9759\u800c\u6df1\u6c89\u7684\u591c\u8272\uff0c\u5f20\u606a\u8bb0\u5f97\u524d\u5c18\u5f80\u4e16\u91cc\u7684\u81ea\u5df1\u4e5f\u662f\u8fd9\u822c\uff0c\u901a\u5bb5\u8fbe\u65e6\u7684\u7741\u7740\u773c\u775b\uff0c\u5c06\u7761\u7720\u65f6\u95f4\u7559\u5230\u7b2c\u4e8c\u5929\u7684\u8bfe\u5802\u4e0a\u3002</p><p>\u3000\u3000\u8fdb\u5165\u56db\u6708\uff0c\u5b66\u5e9c\u5df7\u5df2\u7ecf\u4e0d\u6b62\u521b\u57df\u4e00\u5bb6\u7f51\u5427\u4e86\u3002\u7531\u4e8e\u5b66\u5e9c\u5df7\u9ad8\u6602\u7684\u5e97\u79df\u4e0e\u867d\u7136\u6298\u4e86\u534a\u4ef7\u8fd8\u8fdc\u4e0d\u80fd\u7b97\u4fbf\u5b9c\u7684\u7f51\u8def\u8d39\u7528\uff0c\u4e5d\u516b\u5e74\u5728\u5b66\u5e9c\u5df7\u7ecf\u8425\u7f51\u5427\u6ce8\u5b9a\u8981\u8d70\u4e2d\u9ad8\u7aef\u8def\u7ebf\uff0c\u98ce\u4e91\u7f51\u5427\u5c31\u5f00\u5728\u521b\u57df\u7684\u8857\u5bf9\u9762\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8f66\u505c\u5230\u5c0f\u533a\u91cc\uff0c\u6b64\u65f6\u7684\u4ed6\u6ca1\u6709\u4e00\u661f\u7684\u7761\u610f\uff0c\u867d\u7136\u6709\u4e9b\u997f\uff0c\u4ed6\u53ea\u662f\u8d70\u5230\u5df7\u5b50\u53e3\u7684\u82cf\u679c\u4e8c\u5341\u56db\u5c0f\u65f6\u4fbf\u5229\u5e97\u4e70\u4e86\u4e00\u5305\u70df\uff0c\u770b\u5230\u8054\u534e\u4fbf\u5229\u5c31\u5728\u5e97\u5bf9\u9762\uff0c\u6487\u7740\u5634\u5fcd\u4e0d\u4f4f\u60f3\u8981\u7b11\u8d77\u6765\uff0c\u6b8b\u9177\u7684\u5546\u4e1a\u7ade\u4e89\u753b\u5377\u5c31\u8fd9\u4e48\u6d3b\u751f\u751f\u7684\u94fa\u5c55\u5728\u81ea\u5df1\u7684\u9762\u524d\u3002</p><p>\u3000\u3000\u80cc\u8fc7\u98ce\u5c06\u70df\u70b9\u7740\uff0c\u5f20\u606a\u5c31\u8e72\u5728\u521b\u57df\u7f51\u5427\u95e8\u524d\u7684\u53f0\u9636\u4e0a\u60f3\u7740\u4e8b\u60c5\uff0c\u4e5f\u770b\u7740\u8857\u5bf9\u9762\u7684\u98ce\u4e91\u7f51\u5427\u706f\u706b\u901a\u660e\uff0c\u4e0d\u8fc7\u6b64\u65f6\u5728\u98ce\u4e91\u7f51\u5427\u91cc\u901a\u5bb5\u4e0a\u7f51\u7684\u4eba\u8fdc\u6ca1\u6709\u521b\u57df\u591a\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u8e72\u5728\u5916\u9762\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u7a7f\u7740\u73ab\u7ea2\u8272\u77ed\u98ce\u8863\u7684\u4f55\u5f26\u7ad9\u5728\u7f51\u5427\u95e8\u53e3\u6b63\u770b\u7740\u81ea\u5df1\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e5f\u4f1a\u901a\u5bb5\u4e0a\u7f51\uff1f\u201d\u5f20\u606a\u5947\u602a\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u6211\u5c31\u4e0d\u80fd\u901a\u5bb5\u4e0a\u7f51\u4e86\uff1f\u201d\u4f55\u5f26\u5947\u602a\u7684\u770b\u7740\u5f20\u606a\uff0c\u6307\u4e86\u6307\u8eab\u540e\u843d\u5730\u73bb\u7483\u7a97\u5185\u7684\u5927\u5802\uff0c\u4e0a\u7f51\u7684\u5973\u5b69\u5b50\u8fd8\u5f88\u4e0d\u5c11\uff0c\u53c8\u89e3\u91ca\u8bf4\u9053\uff0c\u201c\u7ed9\u82cf\u4e00\u5a77\u62c9\u8fc7\u6765\u7684\uff0c\u4e0d\u8fc7\u5979\u8db4\u684c\u5b50\u4e0a\u7761\u7740\u4e86\uff0c\u6211\u51fa\u6765\u900f\u53e3\u6c14\u2026\u2026\u4f60\u8e72\u5728\u8fd9\u91cc\u4e5f\u662f\u900f\u4e00\u53e3\u6c14\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\u3002\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u5f80\u8fb9\u4e0a\u632a\u4e86\u4e00\u6b65\uff0c\u7ed9\u4f55\u5f26\u8ba9\u4e86\u4e00\u4e2a\u7a7a\u4f4d\uff0c\u522b\u4eba\u5f88\u96be\u60f3\u8c61\u4f55\u5f26\u8fd9\u4e48\u4e00\u4e2a\u6709\u7740\u74f7\u5668\u8d28\u611f\u7cbe\u81f4\u5bb9\u989c\u7684\u5973\u5b69\u5b50\u4f1a\u4e0d\u987e\u5f62\u8c61\u7684\u8ddf\u5f20\u606a\u8e72\u5728\u8def\u7259\u4e0a\uff0c\u5979\u8fd8\u5c31\u771f\u8e72\u4e0b\u6765\u4e86\uff0c\u5b66\u5f20\u606a\u90a3\u6837\u51dd\u89c6\u7740\u8857\u5bf9\u9762\uff0c\u5979\u80fd\u611f\u89c9\u5230\u5f20\u606a\u5e76\u6ca1\u6709\u5728\u4e13\u6ce8\u7684\u770b\u8857\u5bf9\u9762\u7684\u98ce\u4e91\u7f51\u5427\u5185\u7684\u60c5\u5f62\uff0c\u6216\u8bb8\u5728\u770b\u8857\u9053\u4e0a\u65b9\u7684\u7a7a\u6c14\u5427\u3002</p><p>\u3000\u3000\u201c\u8bfa\uff01\u201d\u5f20\u606a\u5c06\u9999\u70df\u9012\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u4f55\u5f26\u5fae\u5fae\u4e00\u60ca\uff0c\u53c8\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u8fd8\u60f3\u5c06\u6dd1\u5973\u5370\u8c61\u5047\u88c5\u5230\u5e95\u5462\uff0c\u4f60\u4e0d\u8981\u8fd9\u4e48\u8d64\u88f8\u88f8\u7684\u5c06\u6211\u63ed\u7a7f\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u7ed9\u81ea\u5df1\u53c8\u63a5\u4e86\u4e00\u6839\u70df\uff0c\u5c06\u70df\u76d2\u4e0e\u6253\u706b\u673a\u653e\u4e00\u8fb9\u7684\u8def\u7259\u4e0a\u3002</p><p>\u3000\u3000\u201c\u62bd\u70df\u5f88\u6709\u8da3\u5417\uff1f\u201d\u4f55\u5f26\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u65e2\u4e0d\u4f1a\u6709\u8da3\uff0c\u4e5f\u4e0d\u662f\u90a3\u4e48\u65e0\u8da3\uff0c\u6709\u65f6\u5019\u53ea\u662f\u4e60\u60ef\u800c\u5df2\u3002\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u8c01\u8bf4\u6dd1\u5973\u5c31\u4e0d\u53ef\u4ee5\u5438\u70df\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u90a3\u4e9b\u5438\u70df\u7684\u7537\u4eba\u8bf4\u7684\u3002\u201d\u4f55\u5f26\u72e1\u9ee0\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u8fd8\u4ee5\u4f60\u4f1a\u4e00\u672c\u6b63\u7ecf\u7684\u8bf4\u5973\u5b69\u5b50\u62bd\u70df\u4e0d\u597d\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u53c8\u5c06\u70df\u76d2\u4e0e\u6253\u706b\u673a\u62ff\u4e86\u8d77\u6765\u9012\u7ed9\u4f55\u5f26\u3002</p><p>\u3000\u3000\u201c\u4e0d\u7528\u4e00\u652f\uff0c\u5148\u8bd5\u4e00\u53e3\u8bd5\u8bd5\u2026\u2026\u201d\u4f55\u5f26\u6307\u7740\u5f20\u606a\u5634\u5507\u53fc\u7684\u9999\u70df\uff0c\u201c\u514d\u5f97\u6d6a\u8d39\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u70df\u9012\u7ed9\u5979\uff0c\u770b\u7740\u5979\u5e26\u7740\u4e9b\u65b0\u5947\u4e0e\u754f\u7f29\u7684\u795e\u60c5\u5c06\u8fc7\u6ee4\u5634\u62bf\u5728\u7c89\u6da6\u7684\u4e24\u74e3\u5507\u95f4\uff0c\u80c6\u98a4\u5fc3\u60ca\u7684\u5438\u4e86\u4e00\u53e3\uff0c\u53c8\u731b\u7740\u545b\u4e86\u51fa\u6765\u2026\u2026\u4e0e\u524d\u5c18\u5f80\u4e16\u91cc\u7684\u8bb0\u5fc6\u4e00\u6837\uff0c\u4f55\u5f26\u5254\u900f\u5982\u521d\u96ea\u7684\u8138\u988a\u545b\u51fa\u4e00\u62b9\u6f6e\u7ea2\uff0c\u773c\u6cea\u4e5f\u7ed9\u545b\u4e86\u51fa\u6765\uff0c\u63aa\u624b\u4e0d\u53ca\uff0c\u8eab\u5b50\u540e\u6291\uff0c\u4e00\u5c41\u80a1\u5750\u8def\u7259\u4e0a\u2026\u2026</p><p>\u3000\u3000\u4f55\u5f26\u54b3\u55fd\u4e86\u597d\u51e0\u4e0b\u624d\u7f13\u8fc7\u6c14\u6765\uff0c\u4e0a\u5f53\u53d7\u9a97\u7684\u5c06\u70df\u8fd8\u7ed9\u5f20\u606a\uff1a\u201c\u545b\u6b7b\u6211\u4e86\uff0c\u539f\u6765\u62bd\u70df\u4e00\u70b9\u90fd\u4e0d\u597d\u73a9\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5927\u7b11\u7740\u5c06\u70df\u63a5\u4e86\u8fc7\u6765\uff0c\u5c31\u8fd9\u4e48\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u81ea\u5df1\u8ddf\u5979\u8ba4\u8bc6\u4e86\u5341\u591a\u5e74\uff0c\u7ecf\u5386\u51e0\u591a\u60c5\u52ab\u4e4b\u540e\uff0c\u5c06\u4e0e\u5979\u4e4b\u95f4\u7684\u53cb\u8c0a\u73cd\u60dc\u7684\u5f53\u6210\u4e2a\u5b9d\u4f3c\u7684\uff0c\u4eba\u751f\u91cd\u8d70\u4e00\u56de\uff0c\u6709\u4e9b\u770b\u4f3c\u5076\u7136\u7684\u4e1c\u897f\u4f9d\u65e7\u4e0d\u4f1a\u504f\u79bb\u5b83\u5e94\u6709\u7684\u8f68\u8ff9\uff0c\u6216\u8bb8\u8fd9\u672c\u8eab\u5c31\u662f\u66f4\u63a5\u8fd1\u672c\u8d28\u3001\u66f4\u575a\u5b9a\u7684\u4e1c\u897f\u3002\u5f20\u606a\u8fd9\u65f6\u5019\u5fc3\u91cc\u7684\u8584\u9634\u624d\u6d88\u6563\u6389\uff0c\u62c9\u7740\u4f55\u5f26\u7684\u7ea4\u5ae9\u624b\u5c06\u5979\u4ece\u8def\u7259\u4e0a\u62c9\u8d77\u6765\uff0c\u6307\u7740\u7f51\u5427\u91cc\uff0c\u95ee\u5979\uff1a\u201c\u966a\u6211\u559d\u676f\u5496\u5561\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u8bdd\u7684\u8bed\u6c14\u8ddf\u5e38\u4eba\u4e0d\u4e00\u6837\u5462\uff0c\u901a\u5e38\u4e0d\u662f\u8bf4\u2018\u8bf7\u4f60\u559d\u676f\u5496\u5561\u2019\u5417\uff1f\u201d\u4f55\u5f26\u62cd\u4e86\u62cd\u5c41\u80a1\uff0c\u5c06\u7070\u5c18\u62cd\u6389\u3002</p><p>\u3000\u3000\u201c\u4f60\u66f4\u63a5\u53d7\u54ea\u79cd\u8bf4\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u201c\u90fd\u65e0\u6cd5\u6240\u8c13\uff0c\u6211\u53c8\u4e0d\u662f\u7279\u522b\u65a4\u65a4\u8ba1\u8f83\u7684\u4eba\u3002\u201d\u4f55\u5f26\u7b11\u7740\u8ddf\u5f20\u606a\u8d70\u5230\u7f51\u5427\u91cc\uff0c\u5728\u4f11\u95f2\u5496\u5561\u5385\u5f0f\u7684\u4f11\u606f\u533a\u57df\u5750\u4e0b\u6765\uff0c\u770b\u7740\u5f20\u606a\u4ece\u7efc\u5408\u5427\u53f0\u90a3\u91cc\u7aef\u4e86\u4e24\u8fb9\u5496\u5561\u8fc7\u6765\u3002</p><p>\u3000\u3000\u8fd8\u6ca1\u6709\u7b49\u5f20\u606a\u5c06\u5496\u5561\u653e\u4e0b\u6765\uff0c\u6709\u4e09\u4e2a\u9752\u5e74\u4ece\u8857\u5bf9\u9762\u7684\u98ce\u4e91\u7f51\u5427\u8d70\u8fc7\u6765\uff0c\u95ef\u5230\u4f55\u5f26\u7684\u9762\u524d\uff0c\u795e\u60c5\u6fc0\u52a8\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u62d2\u7edd\u6211\u5c31\u662f\u56e0\u4e3a\u8fd9\u4e2a\u762a\u4e09\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u8fd9\u9752\u5e74\u4e5f\u50cf\u662f\u90a3\u79cd\u5e74\u8f7b\u5e05\u6c14\u591a\u91d1\u7684\u4e8c\u4e16\u7956\uff0c\u4f46\u662f\u81ea\u5df1\u5230\u5e95\u6709\u54ea\u70b9\u6bd4\u4ed6\u66f4\u50cf\u762a\u4e09\u7684\u5730\u65b9\uff1f\u5f20\u606a\u4e0d\u52a8\u58f0\u8272\u7684\u770b\u7740\u6c14\u52bf\u6c79\u6c79\u7684\u7ad9\u5728\u4f55\u5f26\u9762\u524d\u7684\u4e09\u4e2a\u9752\u5e74\uff0c\u9886\u5934\u7684\u90a3\u4e2a\u4e8c\u4e16\u7956\u773c\u775b\u770b\u7740\u4f55\u5f26\uff0c\u624b\u5374\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u5f20\u606a\u5f80\u8fb9\u4e0a\u632a\u4e86\u534a\u6b65\uff0c\u4ed6\u4e0d\u662f\u5f88\u9ad8\u5174\u9f3b\u5b50\u7ed9\u4eba\u6307\u7740\uff1b\u5176\u4ed6\u4e24\u4e2a\u5c0f\u9752\u5e74\u5219\u773c\u775b\u5145\u6ee1\u7740\u654c\u89c6\u4e0e\u8f7b\u8511\u7684\u773c\u795e\u76ef\u7740\u81ea\u5df1\u3002</p><p>\u3000\u3000\u7f8e\u5973\u5c31\u662f\u62db\u60f9\u4e8b\u975e\u7684\u7978\u6839\uff0c\u5f20\u606a\u5bf9\u8fd9\u70b9\u662f\u6df1\u6709\u4f53\u4f1a\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u8981\u8ba4\u771f\u644a\u7b97\u4e0b\u6765\uff0c\u5e74\u5c11\u591a\u91d1\u7684\u4e8c\u4e16\u7956\u52a0\u4e0a\u6709\u80c6\u5305\u4e8c\u5976\u7684\u6709\u94b1\u4eba\u603b\u6570\u76ee\u4e0d\u4f1a\u6bd4\u5c1a\u5c11\u59ff\u8272\u7684\u7f8e\u5973\u66f4\u591a\uff0c\u8d44\u6e90\u5e94\u8be5\u8db3\u591f\u5206\u914d\u4e86\uff0c\u5c1a\u6709\u5927\u91cf\u7684\u8d44\u6e90\u6d41\u843d\u5230\u7c89\u7ea2\u53d1\u5eca\u8fd9\u79cd\u4f4e\u6863\u573a\u6240\u53bb\u2014\u2014\u4f46\u662f\u603b\u514d\u4e0d\u4e86\u4f1a\u6709\u51e0\u53cc\u773c\u775b\u76ef\u5230\u4e00\u53ea\u9505\u91cc\uff0c\u4e89\u6267\u4e5f\u5c31\u96be\u514d\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4e16\u95f4\uff0c\u9664\u4e86\u6743\u52bf\u3001\u91d1\u94b1\uff0c\u8fd8\u6709\u4ec0\u4e48\u6bd4\u5973\u4eba\u66f4\u80fd\u6311\u8d77\u7537\u4eba\u95f4\u7684\u6597\u4e89\uff1f</p><p>\u3000\u3000\u201c\u6211\u5929\u5929\u53bb1978\u6367\u4f60\u7684\u573a\uff0c\u4f60\u5374\u4e3a\u8fd9\u79cd\u762a\u4e09\u62d2\u7edd\u6211\uff1f\u201d\u90a3\u9752\u5e74\u7a76\u7adf\u8981\u51fa\u79bb\u6124\u6012\u4e86\u3002</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u5f20\u606a\u5fcd\u4e0d\u4f4f\u8981\u63d0\u9192\u8fd9\u5c0f\u5b50\u4e00\u53e5\uff0c\u201c\u4f55\u5f26\u57281978\u6253\u5de5\uff0c\u85aa\u6c34\u662f\u6bcf\u5c0f\u65f6\u5341\u5143\uff0c\u4f60\u6367\u573a\u4e5f\u597d\uff0c\u4e0d\u6367\u573a\u4e5f\u597d\uff0c\u5979\u4ece1978\u62ff\u5230\u94b1\u65e2\u4e0d\u4f1a\u591a\u4e00\u5206\uff0c\u4e5f\u4e0d\u4f1a\u5c11\u4e00\u5206\uff0c\u4f3c\u4e4e\u770b\u4e0d\u51fa\u5979\u975e\u8981\u4e3a\u6b64\u627f\u60c5\u7b54\u5e94\u8ddf\u4f60\u4ea4\u5f80\u2026\u2026\u201d\u89c1\u90a3\u5c0f\u5b50\u773c\u775b\u77aa\u5f97\u6e9c\u5706\uff0c\u53c8\u7b11\u7740\u89e3\u91ca\u9053\uff0c\u201c\u6211\u53ea\u662f\u63d2\u4e00\u53e5\u89e3\u91ca\u4e00\u4e0b\uff0c\u514d\u5f97\u4f60\u8bef\u4f1a1978\u662f\u4ec0\u4e48\u4e0d\u5e72\u51c0\u7684\u573a\u5b50\uff0c\u4f60\u8ddf\u4f55\u5f26\u6709\u4ec0\u4e48\u7ea0\u7eb7\uff0c\u4f60\u4eec\u7ee7\u7eed\u8c08\u3002\u201d</p><p>\u3000\u3000\u770b\u5230\u5f20\u606a\u8fd9\u4ed8\u6d8e\u8138\u6b7b\u732a\u4e0d\u6015\u5f00\u6c34\u70eb\u7684\u6a21\u6837\uff0c\u90a3\u5c0f\u5b50\u706b\u5192\u4e09\u4e08\uff0c\u5dee\u70b9\u6c14\u70b8\u4e86\u809a\uff1a\u201c\u4f60\u4e0d\u62ff\u955c\u7167\u7167\u81ea\u5df1\uff0c\u4f60\u6709\u4ec0\u4e48\u8d44\u683c\u8ffd\u6c42\u4f55\u5f26\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u8bf4\u8d44\u683c\u554a\uff0c\u201d\u5f20\u606a\u6307\u7740\u90a3\u5c0f\u5b50\u634b\u8d77\u8896\u7ba1\u7684\u624b\u8155\u6234\u7740\u7684\u624b\u8868\uff0c\u201c\u4f60\u6234\u7684\u90a3\u5757\u8868\u662f\u5361\u5730\u4e9a\u5427\uff1f\u4f60\u8bf4\u7684\u8d44\u683c\u662f\u6307\u8fd9\u4e2a\u5417\uff1f\u201d</p><p>\u3000\u3000\u90a3\u9752\u5e74\u7709\u5934\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u7b97\u4f60\u8bc6\u8d27\uff01\u201d</p><p>\u3000\u3000\u201c\u6211\u8fd9\u91cc\u6709\u4e00\u5757\u6c5f\u8bd7\u4e39\u987f\uff0c\u5982\u679c\u8bf4\u4f60\u8bf4\u7684\u8d44\u683c\u662f\u6307\u8fd9\u4e2a\u7684\u8bdd\uff1f\u201d\u5f20\u606a\u4ece\u5916\u5957\u53e3\u888b\u91cc\u638f\u51fa\u8868\u5728\u90a3\u5c0f\u5b50\u9762\u524d\u626c\u4e86\u626c\uff0c\u8d70\u5230\u7f51\u5427\u95e8\u53e3\uff0c\u671d\u8def\u4e2d\u592e\u7838\u8fc7\u6765\uff0c\u770b\u4e86\u6ca1\u770b\uff0c\u5c31\u8d70\u4e86\u56de\u6765\uff0c\u644a\u4e86\u644a\u624b\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4e0d\u9700\u8981\u4ec0\u4e48\u8d44\u683c\u4e5f\u80fd\u50cf\u670b\u53cb\u4e00\u6837\u8bf7\u4f55\u5f26\u559d\u676f\u5496\u5561\u2026\u2026\u201d</p><p>\u3000\u3000\u90a3\u5c0f\u5b50\u7edd\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u80fd\u4ece\u5916\u5957\u53e3\u888b\u91cc\u638f\u51fa\u4e00\u5757\u6c5f\u8bd7\u4e39\u987f\u51fa\u6765\uff0c\u8138\u8272\u96be\u770b\u5f97\u62e7\u4e00\u62e7\u5c31\u80fd\u62e7\u51fa\u4e00\u6ee9\u6c34\u4e0b\u6765\uff0c\u5c06\u624b\u8868\u6458\u4e0b\u6765\u66ff\u7ed9\u4ed6\u8eab\u8fb9\u7684\u9752\u5e74\uff1a\u201c\u5e2e\u6211\u7838\u4e86\uff01\u201d\u90a3\u9752\u5e74\u4e00\u8138\u4e3a\u96be\uff0c\u5341\u591a\u4e07\u7684\u4e00\u5757\u8868\u8bf4\u7838\u5c31\u7838\uff0c\u5c31\u7b97\u4e0d\u662f\u81ea\u5df1\u7684\u4e5f\u5fc3\u75bc\u3002</p><p>\u3000\u3000\u201c\u5feb\u53bb\u7838\uff0c\u4e00\u5757\u8868\u800c\u5df2\uff0c\u6211\u7238\u62bd\u5c49\u91cc\u6bd4\u8fd9\u597d\u7684\u8868\u6709\u4e03\u516b\u5757\uff0c\u8868\u592a\u591a\u4e86\u4e5f\u9ebb\u70e6\u2026\u2026\u201d\u90a3\u5c0f\u5b50\u5c31\u50cf\u7ed9\u5f20\u606a\u8d76\u5230\u67b6\u5b50\u4e0a\u7684\u9e2d\u5b50\uff0c\u54ea\u91cc\u80fd\u54bd\u4e86\u4e0b\u8fd9\u53e3\u6c14\uff1f\u4ed6\u540c\u4f34\u5c06\u4ed6\u52a8\u4e86\u706b\u6c14\uff0c\u4e0d\u53bb\u7838\u53ea\u6015\u4ed6\u4f1a\u8fc1\u6012\u5230\u81ea\u5df1\u5934\u4e0a\u6765\uff0c\u53ea\u5f97\u62ff\u7740\u8868\u4e00\u628a\u7838\u5927\u8857\u4e0a\u3002</p><p>\u3000\u3000\u7531\u4e8e\u662f\u5468\u672b\uff0c\u675c\u98de\u6ca1\u6709\u53bb\u4f11\u606f\uff0c\u542c\u5230\u8bf4\u6709\u4eba\u95f9\u4e8b\uff0c\u5c31\u4e0e\u8499\u4e50\u98de\u5feb\u7684\u4e0b\u4e86\u697c\uff0c\u770b\u5230\u8fd9\u4e00\u5e55\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u8981\u5927\u7b11\u8d77\u6765\uff0c\u5728\u697c\u68af\u53e3\u544a\u8bc9\u8499\u4e50\uff1a\u201c\u8fd9\u7272\u53e3\u592a\u9634\u4e86\uff0c\u4ed6\u90a3\u5757\u8868\u662f\u4e09\u5341\u5757\u94b1\u7684\u5730\u644a\u8d27\u2026\u2026\u201d\u4ed6\u7684\u8bdd\u6ca1\u505c\uff0c\u90a3\u8fb9\u5f20\u606a\u53c8\u4ece\u53e3\u888b\u91cc\u638f\u4e86\u8f66\u94a5\u5319\u644a\u684c\u5b50\u4e0a\uff1a\u201c\u6211\u8fd8\u6709\u4e00\u8f86\u5954\u9a70600\u2026\u2026\u201d</p></article>"}}}]);
