(window.webpackJsonp=window.webpackJsonp||[]).push([[808],{831:function(p){p.exports={chapterTitle:null,title:"\u7b2c638\u7ae0 \u767e\u4eba\u884c\u52a8",content:"<article><h2>\u7b2c638\u7ae0 \u767e\u4eba\u884c\u52a8</h2><p>\u3000\u3000\u591c\u91cc\uff0c\u53c8\u4e0e\u53f6\u81fb\u6c11\u53d6\u5f97\u8054\u7cfb\u3002</p><p>\u3000\u3000\u53f0\u6e7e\u5f53\u5c40\u4e00\u5411\u90fd\u5bf9\u65b0\u53f0\u5e01\u91c7\u53d6\u4e3b\u52a8\u8d2c\u503c\u7684\u59ff\u6001\uff0c\u4f46\u5728\u91d1\u878d\u98ce\u66b4\u4ece\u6cf0\u56fd\u6700\u5148\u7206\u53d1\u4e4b\u540e\uff0c\u53f0\u6e7e\u5f53\u5c40\u5c31\u8f6c\u53d8\u59ff\u6001\uff0c\u5bf9\u65b0\u53f0\u5e01\u6c47\u7387\u79ef\u6781\u56fa\u5b88\u8d77\u6765\uff0c\u5728\u6628\u5929\u4e4b\u524d\u7684\u534a\u4e2a\u6708\u65f6\uff0c\u65b0\u53f0\u5e01\u6c47\u7387\u672a\u964d\u5206\u6beb\uff0c\u7ed9\u91d1\u878d\u5e02\u573a\u4f20\u4e86\u4e00\u4e2a\u76f8\u5bf9\u79ef\u6781\u7684\u4fe1\u53f7\uff0c\u4f46\u662f\u6beb\u65e0\u9884\u5146\u7684\u5f03\u5b88\uff0c\u8fd9\u5176\u4e2d\u9690\u85cf\u7684\u610f\u5473\u5c31\u6df1\u523b\u4e86\u3002</p><p>\u3000\u3000\u6709\u53ef\u80fd\u53f0\u6e7e\u5f53\u5c40\u7ed9\u56fd\u9645\u8d27\u5e01\u6295\u8d44\u8005\u8bbe\u4e0b\u9677\u9631\uff0c\u4e5f\u6709\u53ef\u80fd\u6709\u4e9b\u653f\u6cbb\u6027\u7684\u56e0\u7d20\u5728\u5185\u3002</p><p>\u3000\u3000\u6709\u4e9b\u8bdd\u4e0d\u4f1a\u5bf9\u5c3c\u514b\u674e\u68ee\u6216\u845b\u660e\u5fb7\u8bf4\uff0c\u4f46\u662f\u8981\u4e0e\u53f6\u81fb\u6c11\u8fdb\u6c9f\u901a\uff0c\u4e86\u89e3\u6052\u751f\u80a1\u6307\u7684\u505a\u7a7a\u538b\u529b\u3002</p><p>\u3000\u3000\u65b0\u53f0\u5e01\u5f03\u5b88\u662f\u4e2a\u5bfc\u706b\u7d22\uff0c\u538b\u529b\u4f1a\u4f20\u9012\u5230\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u3001\u97e9\u56fd\u3001\u65e5\u672c\u7b49\u5730\u7684\u91d1\u878d\u5e02\u573a\u4e0a\uff0c\u5f20\u606a\u60df\u6050\u65e5\u97e9\u91d1\u878d\u5e02\u573a\u4e0a\u7684\u98ce\u66b4\u4e0d\u591f\u731b\u70c8\uff0c\u5f53\u7136\u5c31\u65e0\u6cd5\u6307\u671b\u9999\u6e2f\u80fd\u4e1d\u6beb\u65e0\u635f\uff0c\u4f46\u662f\u8981\u575a\u51b3\u6253\u6d88\u56fd\u9645\u7092\u5bb6\u5bf9\u9999\u6e2f\u7684\u89ca\u89ce\uff0c\u8981\u9884\u9632\u4ed6\u4eec\u5927\u89c4\u6a21\u79fb\u5e08\u9999\u6e2f\u3002</p><p>\u3000\u3000\u65b0\u53f0\u5e01\u9009\u62e9\u5728\u4e8c\u5341\u56db\u65e5\u5f03\u5b88\uff0c\u7684\u786e\u4ee4\u6e2f\u5e9c\u5f53\u5c40\u6781\u5176\u72fc\u72c8\u3002</p><p>\u3000\u3000\u8fd8\u6709\u56db\u5929\uff0c\u5c31\u662f\u5f53\u6708\u4e0e\u5b63\u6708\u6052\u751f\u6307\u6570\u671f\u8d27\u5408\u7ea6\u7684\u4ea4\u5272\u671f\uff0c\u8fd9\u51e0\u5929\u672c\u6765\u662f\u91d1\u7ba1\u5c40\u53cd\u51fb\u524d\u671f\u8fdb\u5165\u6e2f\u80a1\u505a\u7a7a\u8d44\u672c\u7684\u6709\u5229\u65f6\u673a\u2014\u2014\u4ece\u6628\u5929\u8d77\uff0c\u5f62\u52bf\u5c31\u9aa4\u7136\u6076\u52a3\u8d77\u6765\uff0c\u53cd\u51fb\u7684\u5927\u597d\u5f62\u52bf\u5df2\u7ecf\u4e0d\u518d\uff0c\u6e2f\u80a1\u8fd8\u53ef\u80fd\u5728\u6700\u540e\u56db\u5929\u5267\u70c8\u6076\u5316\u3002</p><p>\u3000\u3000\u53f0\u6e7e\u5f53\u5c40\u8fd9\u662f\u4ee5\u90bb\u4e3a\u58d1\uff1b\u4e0d\u4ec5\u6e2f\u80a1\u3001\u6052\u751f\u6307\u6570\u671f\u8d27\uff0c\u5916\u6c47\u5e02\u573a\u7684\u538b\u529b\u90fd\u500d\u589e\u3002</p><p>\u3000\u3000\u53f6\u81fb\u6c11\u8fd9\u4e9b\u4e2d\u8054\u529e\u7684\u5b98\u5458\u4ee5\u53ca\u6e2f\u5e9c\u5f53\u5c40\u7684\u5b98\u5458\uff0c\u4eca\u591c\u6ce8\u5b9a\u662f\u96be\u7720\u4e4b\u591c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u53ea\u8981\u4e0d\u50cf\u524d\u4e16\u90a3\u4e48\u7684\u60ca\u60f6\u5931\u63aa\u5c31\u53ef\u4ee5\u4e86\uff0c\u6bd5\u7adf\u6e2f\u80a1\u6307\u6570\u8fd9\u6bb5\u65f6\u95f4\u6765\u4e3b\u52a8\u9000\u6f6e\uff0c\u6ca1\u6709\u79ef\u7d2f\u591a\u5f3a\u70c8\u7684\u505a\u7a7a\u529b\u91cf\uff0c\u751a\u81f3\u6709\u8d44\u91d1\u6709\u7740\u53cd\u5f39\u505a\u591a\u7684\u51b2\u52a8\uff0c\u603b\u4e4b\u6e2f\u5e9c\u5f53\u5c40\u8981\u6709\u7740\u8ba9\u6052\u751f\u6307\u6570\u957f\u671f\u4e0b\u6ed1\u7684\u51c6\u5907\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5bf9\u9999\u6e2f\u7ecf\u6d4e\u7a33\u5b9a\u4e0e\u7e41\u8363\u7684\u8ffd\u6c42\uff0c\u4e0d\u662f\u8981\u4fdd\u6301\u534a\u5e74\u524d\u72c2\u70ed\u72b6\u51b5\uff0c\u591a\u5728\u62a5\u7aef\u62a5\u9053\u90a3\u4e9b\u5df2\u7ecf\u53d1\u751f\u91d1\u878d\u98ce\u8679\u3001\u7ecf\u6d4e\u5371\u673a\u6b63\u8513\u5ef6\u7684\u56fd\u5bb6\u4e0e\u5730\u533a\u7684\u65b0\u95fb\uff0c\u6216\u8bb8\u5bf9\u6e2f\u4eba\u8ba4\u6e05\u5f53\u524d\u7684\u72b6\u51b5\u4f1a\u6709\u5e2e\u52a9\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6e05\u6668\u8fd8\u5728\u4e0b\u96e8\uff0c\u8fdc\u5904\u6d77\u9762\u4e0a\u7684\u6c7d\u7b1b\u58f0\u6773\u6773\u4f20\u6765\uff0c\u4f3c\u4e4e\u7ed9\u96e8\u58f0\u51b2\u6de1\u4e86\u3002\u76db\u590f\u8fd8\u8981\u8d76\u56de\u5b66\u6821\u53bb\u4e0a\u7b2c\u4e00\u5802\u8bfe\uff0c\u5728\u6e05\u6668\u7684\u96e8\u58f0\u4ee5\u53ca\u60a0\u8fdc\u7684\u6c7d\u7b1b\u58f0\u4e2d\uff0c\u5f20\u606a\u5728\u7761\u68a6\u95f4\u65f6\u6d6e\u65f6\u6c89\uff0c\u76f4\u5230\u5510\u5a67\u94bb\u8fdb\u88ab\u7a9d\u91cc\uff0c\u624d\u6e05\u9192\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5510\u5a67\u7a7f\u7740\u7ef8\u8d28\u7684\u7761\u88d9\uff0c\u88f8\u9732\u7684\u808c\u80a4\u6bd4\u7ef8\u8d28\u7761\u88d9\u8fd8\u8981\u817b\u6ed1\u51e0\u5206\u3002</p><p>\u3000\u3000\u5f20\u606a\u6273\u8fc7\u5510\u5a67\u7684\u8138\uff0c\u7cbe\u81f4\u5c0f\u5de7\u7684\u8138\u4e0a\u8fd8\u6302\u7740\u60fa\u677e\u7684\u7761\u610f\uff0c\u773c\u775b\u4e0d\u613f\u610f\u7741\u5f00\uff0c\u776b\u6bdb\u5728\u8f7b\u98a4\uff0c\u8eab\u5b50\u4e0b\u610f\u8bc6\u7684\u5f80\u5f20\u606a\u7684\u6000\u91cc\u94bb\uff0c\u597d\u4e0d\u5bb9\u6613\u7b49\u5230\u76db\u590f\u6e05\u6668\u79bb\u5f00\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u6367\u7740\u5510\u5a67\u79c0\u7740\u4e73\u767d\u8272\u5149\u6cfd\u7684\u8138\u86cb\u8981\u53bb\u543b\u5979\uff0c\u5374\u7ed9\u5979\u62ff\u624b\u5835\u4f4f\u5634\uff0c\u53cc\u6670\u900f\u660e\u7684\u624b\uff0c\u5341\u6307\u5982\u7389\uff0c\u5510\u5a67\u7741\u5f00\u60fa\u677e\u7f8e\u4e3d\u7684\u773c\u7738\u5b50\uff0c\u5a07\u55d4\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u90fd\u6ca1\u6709\u6f31\u5634\u2026\u2026\u201d\u88ab\u5b50\u6ed1\u5728\u8170\u9645\uff0c\u7761\u88d9\u4e0b\u6ed1\u51fa\u5927\u7247\u96ea\u767d\u7684\u808c\u80a4\uff0c\u8fd8\u9732\u51fa\u4e00\u7247\u767d\u5f97\u8000\u773c\u7684\u767d\u4e73\uff0c\u5510\u5a67\u89c1\u5f20\u606a\u7684\u773c\u795e\u770b\u7740\u81ea\u5df1\u7684\u80f8\u53e3\uff0c\u4f38\u624b\u628a\u9886\u53e3\u5f80\u4e0a\u62c9\u4e86\u62c9\uff0c\u5a07\u55d4\u7684\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u662f\u8fc7\u6765\u501f\u4f60\u7684\u80f8\u53e3\u7761\u4e2a\u56de\u7b3c\u89c9\u2026\u2026\u56de\u7b3c\u89c9\u6700\u8212\u670d\u4e86\uff01\u201d\u633a\u4e86\u633a\u80f8\u812f\uff0c\u6175\u61d2\u65e0\u6bd4\u7684\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u7738\u5b50\u53c8\u957f\u53c8\u5a9a\uff0c\u8ba9\u4eba\u53e3\u5e72\u820c\u71e5\uff0c\u5979\u53c8\u8f6c\u8fc7\u8eab\u6765\uff0c\u534a\u8db4\u5728\u5f20\u606a\u7684\u80f8\u53e3\uff0c\u51dd\u7740\u4ed6\uff0c\u8f7b\u58f0\u7684\u8bf4\uff1a\u201c\u4f60\u7684\u80f8\u53e3\u4f1a\u968f\u65f6\u8ba9\u6211\u94bb\u8fc7\u6765\u7761\u4e00\u4e0b\u8212\u670d\u7684\u56de\u7b3c\u89c9\u5417\uff1f\u201d</p><p>\u3000\u3000\u5c0f\u59ae\u5b50\u7ec8\u4e8e\u957f\u5927\u4e86\uff0c\u5f20\u606a\u5f3a\u70c8\u611f\u89c9\u5230\u80f8\u524d\u8f6f\u8089\u7684\u5f39\u6027\u4e0e\u5c11\u5973\u6e05\u5e7d\u7684\u4f53\u9999\uff0c\u4e24\u4eba\u5149\u4e2b\u4e2b\u7684\u811a\u5728\u88ab\u7a9d\u91cc\u53ae\u6df7\u7740\uff0c\u56db\u80a2\u7ea0\u7f20\uff0c\u624b\u4ece\u7761\u88d9\u91cc\u94bb\u8fdb\uff0c\u642d\u5728\u5979\u8f6f\u5f39\u817b\u6ed1\u7684\u81c0\u74e3\u4e0a\uff0c\u6469\u6332\u7740\uff0c\u63c9\u6413\u7740\uff0c\u770b\u7740\u5510\u5a67\u7cbe\u81fb\u767d\u7699\u7684\u8138\u5e9e\u6e10\u6e10\u6d6e\u8d77\u9189\u4eba\u7684\u9161\u7ea2\uff0c\u7f8e\u4e3d\u7684\u77b3\u5149\u7115\u53d1\u51fa\u8ff7\u79bb\u8bf1\u4eba\u7684\u795e\u91c7\u3002</p><p>\u3000\u3000\u5c06\u7ef8\u8d28\u7684\u7761\u88d9\u5265\u4e0b\u6765\uff0c\u96ea\u767d\u67d3\u7740\u8f7b\u7ea2\u7684\u9c9c\u5ae9\u8089\u4f53\u6676\u83b9\u5254\u900f\uff0c\u5728\u6668\u5149\u4e2d\u71a0\u71a0\u751f\u8f89\uff0c\u6700\u4e3a\u8ff7\u4eba\u7684\u773c\u7738\u5b50\u6d41\u6cc4\u7740\u65e0\u7aef\u9752\u6625\u7684\u59a9\u5a9a\uff0c\u5f20\u606a\u8f7b\u8f7b\u6258\u8d77\u5979\u7684\u81c0\u74e3\uff0c\u8981\u5c06\u5979\u68c9\u8d28\u5185\u88e4\u892a\u4e0b\u6765\uff0c\u4ed6\u8eab\u4f53\u67d0\u4e2a\u575a\u786c\u7684\u90e8\u4f4d\u90fd\u5fcd\u4e0d\u4f4f\u6709\u4e9b\u98a4\u6296\u4e86\u3002</p><p>\u3000\u3000\u624b\u673a\u5728\u5e8a\u5934\u67dc\u4e0a\u9707\u52a8\u8d77\u6765\uff0c\u5f20\u606a\u81ea\u7136\u542c\u82e5\u672a\u95fb\uff0c\u5510\u5a67\u957f\u53c8\u5a9a\u3001\u6d41\u6cc4\u7740\u8ff7\u79bb\u5149\u8292\u7684\u7738\u5b50\u5374\u6e05\u4eae\u8d77\u6765\uff0c\u4fa7\u9996\u770b\u4e86\u4e00\u773c\u5e8a\u5934\u67dc\u4e0a\u575a\u6301\u4e0d\u61c8\u9707\u52a8\u7684\u624b\u673a\uff0c\u6293\u8d77\u8695\u4e1d\u88ab\u88f9\u8d77\u5a07\u5ae9\u7684\u8eab\u4f53\uff0c\u8ba9\u5f20\u606a\u5148\u63a5\u7535\u8bdd\u2014\u2014\u5f20\u606a\u6068\u4e0d\u5f97\u8981\u5c06\u624b\u673a\u7838\u5230\u7a97\u5b50\u5916\u53bb\uff0c\u53ea\u5f97\u5148\u722c\u8d77\u6765\u63a5\u7535\u8bdd\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5728\u5efa\u90ba\u6253\u6765\u7684\u7535\u8bdd\uff0c\u4ed6\u4eec\u6628\u5929\u591c\u91cc\u8d76\u56de\u5efa\u90ba\uff0c\u9a6c\u4e0a\u5c31\u5230\u767b\u4e0a\u98de\u5f80\u9999\u6e2f\u7684\u98de\u673a\u3002</p><p>\u3000\u3000\u9e21\u6bdb\u849c\u76ae\u7684\u5c0f\u4e8b\uff0c\u5f20\u606a\u8fd9\u4e0b\u5b50\u679c\u65ad\u7684\u5c06\u624b\u673a\u5173\u6389\uff0c\u8ddf\u5510\u5a67\u8bf4\uff1a\u201c\u6211\u4eec\u7ee7\u7eed\uff1f\u201d</p><p>\u3000\u3000\u5510\u5a67\u5a07\u7b11\u7740\uff0c\u7565\u5e26\u7f9e\u6da9\u7684\u8bf4\u9053\uff1a\u201c\u597d\u4e0d\u5bb9\u6613\u624d\u9f13\u8db3\u7684\u52c7\u6c14\uff0c\u8fd9\u65f6\u5019\u6ca1\u6709\u4e86\u2026\u2026\u6211\u8fd8\u8981\u8d76\u7740\u53bb\u5b66\u6821\u4e0a\u8bfe\u5462\u3002\u201d\u773c\u7738\u5b50\u7785\u5230\u5f20\u606a\u7684\u8eab\u4f53\uff0c\u53c8\u60ca\u7f9e\u7684\u5c06\u8138\u8f6c\u5f00\uff0c\u5c06\u5f20\u606a\u7684\u5185\u88e4\u5c06\u4ed6\u8eab\u4e0a\u4e22\u8fc7\u53bb\uff0c\u201c\u597d\u4e11\uff0c\u5feb\u7a7f\u8d77\u6765\u3002\u201d\u5979\u4eba\u5148\u8eb2\u5230\u88ab\u5b50\u91cc\u7a78\u7a78\u7c0c\u7c0c\u7684\u5c06\u7761\u88d9\u7a7f\u597d\u3002</p><p>\u3000\u3000\u53c8\u4e0d\u80fd\u771f\u72e0\u5fc3\u5c06\u624b\u673a\u7838\u6389\uff0c\u5f20\u606a\u53ea\u5f97\u5148\u7a7f\u8d77\u5185\u88e4\u7a7f\u8d77\u6765\uff0c\u53cd\u6b63\u8fd9\u6bb5\u65f6\u95f4\u8981\u8017\u5728\u9999\u6e2f\uff0c\u65f6\u95f4\u6709\u7684\u662f\uff0c\u94bb\u5230\u88ab\u7a9d\u91cc\uff0c\u8ba9\u5510\u5a67\u4f9d\u7740\u81ea\u5df1\u7684\u6000\u91cc\u770b\u7740\u7a97\u5916\u7684\u98ce\u96e8\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5148\u9001\u5510\u5a67\u5b66\u6821\uff0c\u7136\u540e\u53bb\u673a\u573a\u63a5\u8096\u664b\u6210\u3001\u9648\u4fe1\u751f\u3001\u8c2d\u4e91\u677e\u7b49\u4eba\u3002</p><p>\u3000\u3000\u8096\u664b\u6210\u5c31\u6bd4\u8f83\u597d\uff0c\u98de\u673a\u516d\u70b9\u949f\u5c31\u4ece\u4e1c\u4eac\u8d77\u98de\uff0c\u4ed6\u5c31\u6ca1\u6709\u60f3\u7740\u6253\u7535\u8bdd\u60ca\u52a8\u8fd9\u8fb9\u4eba\u7684\u7f8e\u68a6\uff0c\u770b\u5230\u9648\u4fe1\u751f\uff0c\u5f20\u606a\u6068\u4e0d\u5f97\u4e0a\u53bb\u8e39\u4ed6\u4e24\u811a\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u6ee1\u9762\u5fae\u7b11\u7740\u4e0e\u968f\u884c\u7684\u5de5\u4f5c\u4eba\u5458\u966a\u8c2d\u4e91\u677e\u592b\u5987\u8d70\u5411\u63a5\u673a\u5927\u5385\uff0c\u90fd\u4e0d\u6653\u5f97\u54ea\u91cc\u6709\u5f97\u7f6a\u5f20\u606a\u3002</p><p>\u3000\u3000\u8096\u664b\u6210\u7684\u98de\u673a\u5148\u62b5\u8fbe\u542f\u5fb7\u673a\u573a\uff0c\u5148\u4e0e\u5f20\u606a\u3001\u5468\u6e38\u3001\u848b\u8587\u6c47\u5408\u540e\uff0c\u5728\u63a5\u673a\u5927\u5385\u91cc\u7b49\u5019\u9648\u4fe1\u751f\u4ed6\u4eec\u7684\u98de\u673a\u3002</p><p>\u3000\u3000\u8c2d\u4e91\u677e\u8fd9\u51e0\u5929\u5df2\u7ecf\u4e86\u89e3\u5230\u9526\u6e56\u6280\u672f\u6269\u5f20\u7684\u7ec6\u8282\uff0c\u6a61\u6811\u56ed\u8ba1\u5212\u4e3a\u6838\u5fc3\uff0c\u4f46\u662f\u6a61\u6811\u56ed\u8ba1\u5212\u8981\u6210\u719f\u8d77\u6765\uff0c\u9700\u8981\u4e00\u5b9a\u7684\u65f6\u95f4\uff0c\u5e76\u4e0d\u80fd\u5b8c\u8db3\u9526\u6e56\u4f53\u7cfb\u6b64\u65f6\u5bf9\u6280\u672f\u7814\u53d1\u7684\u6e34\u6c42\uff0c\u5728\u97e9\u56fd\u6c49\u57ce\u3001\u65e5\u672c\u4e1c\u4eac\u4ee5\u53ca\u9999\u6e2f\u7b49\u5730\u6210\u7acb\u4ea7\u54c1\u7814\u53d1\u4e2d\u5fc3\uff0c\u5728\u7f8e\u56fd\u7845\u8c37\uff0c\u5728\u65e5\u672c\u7b51\u6ce2\u5e02\u6210\u7acb\u6280\u672f\u5b9e\u9a8c\u57fa\u5730\uff0c\u90fd\u662f\u4e3a\u4e86\u6ee1\u8db3\u6b64\u65f6\u7684\u6280\u672f\u9700\u8981\u3002</p><p>\u3000\u3000\u968f\u540c\u9648\u4fe1\u751f\u4e00\u8d77\u62b5\u8fbe\u9999\u6e2f\u7684\uff0c\u8fd8\u6709\u6768\u4e91\u7b49\u4eba\u3002</p><p>\u3000\u3000\u8981\u8003\u8651\u7b79\u5efa\u9526\u6e56\u7684\u5927\u8425\u9500\u4f53\u7cfb\uff0c\u6ca1\u6709\u5fc5\u8981\u53e6\u8d77\u7089\u7076\uff0c\u53ea\u9700\u8981\u5c06\u7231\u8fbe\u7535\u5b50\u7684\u8425\u9500\u8d44\u6e90\u5265\u79bb\u51fa\u6765\uff0c\u8fdb\u884c\u6574\u5408\uff0c\u8fd0\u8425\u90e8\u65d7\u4e0b\u5305\u62ec\u5185\u5730\u5728\u5185\u7684\u65b0\u5174\u5730\u533a\u5927\u8425\u9500\u4f53\u7cfb\u5c31\u80fd\u521d\u6b65\u5efa\u7acb\u8d77\u6765\u3002</p><p>\u3000\u3000\u6768\u4e91\u8fc7\u6765\uff0c\u662f\u8981\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u4e0e\u9999\u6e2f\u7684\u80a1\u6c11\u8fdb\u884c\u6c9f\u901a\uff0c\u8981\u4ee5\u5408\u9002\u7684\u6761\u4ef6\u8fdb\u884c\u5265\u79bb\uff0c\u4ee5\u514d\u5f15\u8d77\u9999\u6e2f\u4e2d\u5c0f\u80a1\u4e1c\u7684\u6fc0\u70c8\u53cd\u5bf9\u3002</p><p>\u3000\u3000\u5728\u5f20\u606a\u770b\u6765\uff0c\u65b0\u5174\u5730\u533a\u7684\u5e02\u573a\u4e0e\u53d1\u8fbe\u5730\u533a\u7684\u5e02\u573a\uff0c\u6027\u8d28\u4e0a\u6709\u7740\u622a\u7136\u4e0d\u540c\u7684\u533a\u522b\uff0c\u54c1\u724c\u8fd0\u8425\u3001\u5e02\u573a\u8fd0\u4f5c\u4ee5\u53ca\u8425\u9500\u4f53\u7cfb\u7684\u7b79\u5efa\u4e5f\u6709\u7740\u6781\u5927\u7684\u4e0d\u540c\uff0c\u6240\u4ee5\u624d\u5c06\u9526\u6e56\u7684\u4e1a\u52a1\u5212\u4e3a\u4e24\u5757\uff0c\u4e00\u5757\u5f52\u8fd0\u8425\u90e8\u8d1f\u8d23\uff0c\u4e00\u5757\u5f52\u6d77\u5916\u8fd0\u8425\u90e8\u8d1f\u8d23\uff0c\u8425\u9500\u4f53\u7cfb\u7684\u7b79\u5efa\u4e5f\u662f\u5206\u5f00\u6765\u4e86\u3002</p><p>\u3000\u3000\u6768\u4e91\u6b64\u65f6\u8fd8\u8981\u771f\u6b63\u7ba1\u7406\u6d77\u5916\u5e02\u573a\u7684\u80fd\u529b\uff0c\u8fd9\u91cc\u9762\u5f88\u5927\u7684\u4e0d\u540c\u5c31\u662f\u6587\u5316\u8ba4\u77e5\u4e0a\u7684\u5dee\u5f02\uff0c\u50cf\u4ee5\u5f80\u63a8\u5e7f\u5f71\u789f\u673a\u7684\u90a3\u5957\u628a\u620f\uff0c\u5728\u65e5\u97e9\u6b27\u7f8e\u7b49\u5730\u7684\u5e02\u573a\u5c31\u7edd\u5bf9\u884c\u4e0d\u901a\u3002</p><p>\u3000\u3000\u5728\u63a5\u673a\u5927\u5385\u91cc\uff0c\u5f20\u606a\u4eb2\u81ea\u7ed9\u8c2d\u4e91\u677e\u592b\u5987\u4ecb\u7ecd\u5927\u9879\u76ee\u90e8\u7684\u53e6\u4e00\u4e2a\u6210\u5458\uff0c\u6d77\u5916\u8fd0\u8425\u90e8\u7684\u603b\u8d1f\u8d23\u4eba\u8096\u664b\u6210\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u7231\u8fbe\u96c6\u56e2\u6d77\u5916\u8fd0\u8425\u90e8\u7684\u603b\u8d1f\u8d23\u4eba\uff0c\u8096\u664b\u6210\u8fd9\u6bb5\u65f6\u95f4\u9664\u4e86\u8d1f\u8d23\u534f\u52a9\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u7b79\u5efa\u97e9\u56fd\u3001\u65e5\u672c\u7684\u7814\u53d1\u4e2d\u5fc3\u4e0e\u6280\u672f\u5b9e\u9a8c\u5ba4\u4e4b\u5916\uff0c\u8fd8\u8d1f\u8d60\u7b79\u5efa\u6d77\u5916\u8425\u9500\u4f53\u7cfb\u3002</p><p>\u3000\u3000\u5728\u6b64\u4e4b\u524d\uff0c\u8c2d\u4e91\u677e\u542c\u5230\u9526\u6e56\u4f53\u7cfb\u5185\u8fd8\u6709\u6d77\u5916\u8fd0\u8425\u90e8\uff0c\u5c31\u611f\u5230\u5f88\u5947\u602a\uff0c\u603b\u4e0d\u81f3\u4e8e\u7ba1\u7406\u51e0\u4e2a\u7814\u53d1\u4e2d\u5fc3\u3001\u51e0\u95f4\u5b9e\u9a8c\u90e8\u9700\u8981\u4e13\u95e8\u6210\u7acb\u4e00\u4e2a\u5927\u7684\u90e8\u95e8\u3002</p><p>\u3000\u3000\u9526\u6e56\u4f53\u7cfb\u5185\uff0c\u76ee\u524d\u7684\u4ea7\u54c1\u5f88\u5355\u4e00\uff0c\u7231\u8fbe\u7535\u5b50\u65d7\u4e0b\u7684\u5f71\u789f\u673a\u6839\u672c\u5c31\u4e0d\u9002\u5408\u5728\u6d77\u5916\u8fd0\u8425\u90e8\u7684\u8f96\u7ba1\u5e02\u573a\u91cc\u63a8\u5e7f\u2014\u2014\u91cd\u89c6\u77e5\u8bc6\u4ea7\u6743\u7684\u5730\u533a\uff0c\u8d85\u7ea7VCD\u6839\u672c\u5c31\u6ca1\u6709\u7247\u6e90\u3002\u7231\u8fbe\u6559\u80b2\u7535\u5b50\u65d7\u4e0b\u7684\u7535\u5b50\u8bcd\u5178\u662f\u4e2d\u6587\u7248\u672c\u82f1\u8bed\u5b66\u4e60\u4ea7\u54c1\uff0c\u5c31\u7b97\u6709\u5176\u4ed6\u884d\u751f\u4ea7\u54c1\uff0c\u5728\u6d77\u5916\u5e02\u573a\u4e5f\u6ca1\u6709\u592a\u5f3a\u7684\u7ade\u4e89\u529b\u3002\u7231\u8fbe\u901a\u8baf\u7535\u5b50\u65d7\u4e0b\u7684\u7b2c\u4e00\u6b3e\u624b\u673a\u4e00\u518d\u5ef6\u8fdf\u4e0a\u5e02\u65f6\u95f4\uff0c\u8c2d\u4e91\u677e\u770b\u5230\u7231\u8fbe\u5373\u5c06\u4e0a\u5e02\u7684\u4e09\u6b3e\u624b\u673a\u6837\u673a\uff0c\u4ece\u4ea7\u54c1\u8bbe\u8ba1\u3001\u6280\u672f\u6027\u80fd\u4e0a\u6765\u770b\uff0c\u5012\u8ba4\u4e3a\u53ef\u4ee5\u63a8\u5411\u6d77\u5916\u5e02\u573a\uff0c\u4f46\u662f\u5185\u5730\u7684\u7b2c\u4e00\u70ae\u8fd8\u6ca1\u6709\u6253\u51fb\uff0c\u5728\u6beb\u65e0\u77e5\u540d\u5ea6\u7684\u6d77\u5916\u5e02\u573a\uff0c\u5148\u63a8\u624b\u673a\u4ea7\u54c1\uff0c\u591a\u5c11\u6709\u4e9b\u820d\u672c\u9010\u672b\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u76f4\u5230\u5728\u6d77\u5dde\u6b63\u5f0f\u7b7e\u7f72\u8fd4\u56de\u5efa\u90ba\u9886\u5bfc\u5fae\u7535\u5b50\u57fa\u7840\u5b9e\u9a8c\u5ba4\u7684\u5408\u4f5c\u534f\u8bae\uff0c\u8c2d\u4e91\u677e\u592b\u5987\u624d\u770b\u5230\u9526\u6e56\u73cd\u85cf\u8bb8\u4e45\u7684\u4e00\u628a\u5229\u5668\uff1a\u4ee3\u53f7\u4e3aM-1\u7684\u7535\u5b50\u97f3\u4e50\u64ad\u653e\u5668\u7684\u6837\u673a\u3002</p><p>\u3000\u3000ESS\u5df2\u7ecf\u4e3a\u7535\u5b50\u97f3\u4e50\u64ad\u653e\u5668\u5f00\u53d1\u51fa\u4e13\u95e8\u7684\u97f3\u9891\u89e3\u7801\u82af\u7247\uff0c\u4ece\u5fb7\u4eea\u83b7\u5f97\u624b\u673a\u7535\u6e90\u82af\u7247\u7ba1\u7406\u6280\u672f\u7a0d\u52a0\u6539\u8fdb\uff0c\u540c\u6837\u9002\u7528\u4e8e\u7535\u5b50\u97f3\u4e50\u64ad\u653e\u5668\uff0c\u800c\u65e9\u5728\u4e00\u5e74\u524d\uff0c\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u5c31\u81ea\u884c\u5f00\u53d1\u51fa\u7535\u5b50\u97f3\u4e50\u64ad\u653e\u5668\u7684\u63a7\u5236\u82af\u7247\uff0c\u5229\u7528\u4e1c\u829d\u5f00\u53d1\u51fa\u7684\u95ea\u5b58\u4f5c\u4e3a\u50a8\u5b58\u4ecb\u8d28\uff0c\u9526\u6e56\u5df2\u7ecf\u5f00\u53d1\u51fa\u50a8\u5b58\u5bb9\u91cf\u5206\u522b\u4e3a32M\u300164M\u4e24\u6b3e\u7535\u5b50\u97f3\u4e50\u64ad\u653e\u5668\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u9526\u6e56\uff0c\u7535\u5b50\u97f3\u4e50\u64ad\u653e\u5668\u5df2\u7ecf\u7b97\u662f\u8f83\u4e3a\u6210\u719f\u7684\u6280\u672f\uff0c\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6709\u62ff\u51fa\u6765\uff0c\u4e00\u65b9\u9762\u8981\u7b49\u6d77\u5916\u8425\u9500\u4f53\u7cfb\u5efa\u7acb\u8d77\u6765\uff0c\u4e00\u65b9\u9762\u5c31\u662f\u8981\u7ee7\u7eed\u6df1\u5ea6\u7814\u53d1\uff0c\u7ed9\u5176\u4ed6\u7684\u6d77\u5916\u7535\u5b50\u5382\u5546\u8bbe\u7f6e\u66f4\u591a\u7684\u4e13\u5229\u969c\u788d\uff0c\u8bbe\u7f6e\u5176\u4ed6\u6d77\u5916\u7535\u5b50\u5382\u5546\u7ed5\u4e0d\u8fc7\u53bb\u7684\u4e13\u5229\u969c\u788d\u3002</p><p>\u3000\u3000\u9526\u6e56\u76ee\u524d\u4e0d\u7f3a\u8d44\u91d1\uff0c\u56db\u5341\u4ebf\u7684\u5de8\u8d44\u8fd8\u80fd\u6d88\u8017\u4e24\u5e74\uff0c\u4ece\u7b2c\u4e09\u4ee3\u789f\u673a\u89e3\u7801\u82af\u7247\u4e0a\u6bcf\u4e2a\u6708\u8fd8\u80fd\u83b7\u5f97\u8d85\u8fc7\u5343\u4e07\u7f8e\u91d1\u7684\u5206\u7ea2\uff0c\u8fd9\u7b14\u8d44\u91d1\u8db3\u4ee5\u652f\u6491\u9526\u6e56\u5728\u6d77\u5916\u7684\u8fd0\u4f5c\uff1b\u9526\u6e56\u7f3a\u4e4f\u4e00\u6b3e\u53ef\u4ee5\u64ac\u5f00\u6d77\u5916\u5e02\u573a\u3001\u4e0e\u6d77\u5916\u7535\u5b50\u5382\u5546\u53eb\u677f\u7684\u5229\u5668\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\u7684\u6280\u672f\u5f88\u591a\u90fd\u662f\u878d\u4f1a\u8d2f\u901a\u7684\uff0c\u97f3\u9891\u5904\u7406\u82af\u7247\u6280\u672f\uff0c\u7535\u5b50\u97f3\u4e50\u64ad\u653e\u5668\u9700\u8981\uff0c\u624b\u673a\u540c\u6837\u4e5f\u9700\u8981\uff0c\u7231\u8fbe\u5373\u5c06\u63a8\u51fa\u7684\u7b2c\u4e00\u6b3e\u624b\u673a\u751a\u81f3\u53ea\u9700\u8981\u6b21\u4e00\u7ea7\u7684\u97f3\u9891\u5904\u7406\u82af\u7247\u6280\u672f\uff0c\u5c31\u80fd\u591f\u6bd4\u5185\u5730\u5e02\u573a\u4e0a\u7684\u624b\u673a\u6709\u7740\u66f4\u4f18\u79c0\u7684\u97f3\u8d28\u8868\u73b0\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u8033\u673a\u6a21\u5757\u3001\u6676\u5c4f\u3001\u7535\u6e90\u63a7\u5236\u3001\u5b57\u5e93\u82af\u7247\u7b49\u7b49\u6280\u672f\uff0c\u65e0\u4e0d\u662f\u53ef\u4ee5\u4e92\u76f8\u501f\u7528\u7684\u3002\u5c06\u638c\u4e0a\u7535\u8111\u3001\u6570\u7801\u6444\u76f8\u3001\u7535\u5b50\u97f3\u4e50\u3001\u7535\u5b50\u89c6\u9891\u7b49\u6d88\u8d39\u7535\u5b50\u6280\u672f\u6700\u7ec8\u96c6\u6210\u5230\u624b\u673a\u4ea7\u54c1\u4e0a\uff0c\u4e5f\u662f\u5927\u52bf\u6240\u8d8b\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u662f\u6a61\u6811\u56ed\u8ba1\u5212\u80fd\u4e3a\u9526\u6e56\u4e13\u653b\u6d88\u8d39\u7535\u5b50\u5e02\u573a\u63d0\u4f9b\u6280\u672f\u4fdd\u969c\u7684\u57fa\u7840\u6240\u5728\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u662f\u9526\u6e56\u7684\u91ce\u5fc3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u6700\u8fd1\u5728\u8003\u8651\u600e\u6837\u53bb\u63a8\u8fdb\u6a61\u6811\u56ed\u76ee\u524d\u5de5\u4f5c\u7684\u95ee\u9898\uff0c\u201d\u5750\u5230\u8f66\u4e0a\uff0c\u5f20\u606a\u56de\u8fc7\u5934\u5bf9\u9648\u4fe1\u751f\u8bf4\uff0c\u4e03\u5ea7\u7684\u5546\u52a1\u8f66\uff0c\u65b9\u4fbf\u5927\u5bb6\u90fd\u6324\u8fdb\u4e00\u8f86\u8f66\u5b50\u91cc\u8ba8\u8bba\u4e8b\u60c5\uff0c\u8fd9\u65f6\u5019\u4e5f\u4e0d\u518d\u62ff\u8c2d\u4e91\u677e\u5f53\u5916\u4eba\uff0c\u201c\u6211\u60f3\u641e\u4e2a\u767e\u4eba\u884c\u52a8\u6765\u5438\u5f15\u6d77\u5916\u534e\u4eba\u79d1\u5b66\u5bb6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u4e00\u76f4\u90fd\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u7269\u8272\u4f18\u79c0\u7684\u534e\u4eba\u79d1\u5b66\u5bb6\u4e0e\u5de5\u7a0b\u5e08\uff0c\u9648\u4fe1\u751f\u8fd8\u662f\u7b2c\u4e00\u6b21\u542c\u5f20\u606a\u8bf4\u53ca\u201c\u767e\u4eba\u884c\u52a8\u201d\u8fd9\u4e2a\u8bcd\uff0c\u6b63\u895f\u5371\u5750\u3001\u6d17\u8033\u606d\u542c\u3002</p><p>\u3000\u3000\u201c\u7ecf\u6d4e\u5371\u673a\u5411\u4e9a\u6d32\u5404\u4e2a\u5730\u533a\u8513\u5ef6\u5df2\u7ecf\u662f\u5927\u52bf\u6240\u8d8b\uff0c\u65e0\u6cd5\u633d\u56de\u4e86\uff0c\u65e5\u97e9\u3001\u4e1c\u5357\u4e9a\u7684\u8bb8\u591a\u9ad8\u79d1\u6280\u672f\u516c\u53f8\u4f1a\u6709\u4e00\u9635\u5b50\u96be\u8fc7\u7684\u65f6\u95f4\uff0c\u516c\u53f8\u96c7\u5458\u4eba\u4eba\u81ea\u5371\uff0c\u90a3\u4e5f\u4eba\u4e4b\u5e38\u60c5\uff0c\u4f46\u662f\u4eba\u603b\u662f\u6709\u60f0\u6027\u7684\uff0c\u80fd\u4e0d\u632a\u7a9d\u5c31\u4e0d\u632a\u7a9d\uff0c\u4f55\u51b5\u8de8\u56fd\u8f6c\u79fb\u5de5\u4f5c\u66f4\u9ebb\u70e6\uff0c\u95ee\u9898\u66f4\u591a\u3002\u6211\u60f3\u6211\u4eec\u53ef\u4ee5\u66f4\u4e3b\u52a8\u4e00\u4e9b\uff0c\u66f4\u79ef\u6781\u7684\u5c06\u6ede\u7559\u5728\u8fd9\u4e9b\u5730\u533a\u7684\u6d77\u5916\u534e\u4eba\u79d1\u5b66\u5bb6\u53ca\u5de5\u7a0b\u5e08\u62c9\u56de\u5230\u56fd\u5185\u6765\u3002\u4e0d\u8fc7\u4ee5\u6a61\u6811\u56ed\u7684\u80fd\u529b\uff0c\u53ea\u80fd\u641e\u4e2a\u5c0f\u89c4\u6a21\u7684\u767e\u4eba\u8ba1\u5212\u2026\u2026\u5bf9\u4e8e\u53c2\u4e0e\u6a61\u6811\u56ed\u5efa\u8bbe\u8ba1\u5212\u7684\u6d77\u5916\u534e\u4eba\u79d1\u5b66\u5bb6\u53ca\u5de5\u7a0b\u5e08\uff0c\u5206\u4e09\u4e2a\u7b49\u7ea7\uff0c\u79d1\u5b66\u5bb6\u4e00\u7ea7\uff0c\u6a61\u6811\u56ed\u4e00\u6b21\u6027\u8865\u52a9\u4e00\u767e\u4e07\uff1b\u4e3b\u4efb\u5de5\u7a0b\u5e08\uff0c\u4e00\u6b21\u6027\u8865\u52a9\u4e94\u5341\u4e07\uff1b\u4f18\u79c0\u7684\u9752\u5e74\u6280\u672f\u4eba\u624d\uff0c\u4e00\u6b21\u6027\u8865\u52a9\u4e8c\u5341\u4e07\u3002\u5728\u6c38\u4e45\u5c45\u7559\u6743\u3001\u6237\u7c4d\u3001\u5b50\u5973\u4e0a\u5b66\u65b9\u9762\uff0c\u6211\u4eec\u53ef\u4ee5\u5411\u5efa\u90ba\u5e02\u7533\u8bf7\u4e00\u5207\u53ef\u80fd\u7684\u4f18\u60e0\u6761\u4ef6\u2026\u2026\u201d\u5f20\u606a\u5c06\u4e00\u4e9b\u60f3\u6cd5\u8bf4\u51fa\u6765\uff0c\u201c\u62ff\u51fa\u56db\u5343\u4e07\u5230\u516d\u5343\u4e07\u7684\u8d44\u91d1\u51fa\u6765\uff0c\u5e2e\u52a9\u8fd9\u4e9b\u4f18\u79c0\u7684\u534e\u4eba\u79d1\u5b66\u5bb6\u3001\u5de5\u7a0b\u5e08\u56de\u5230\u56fd\u5185\u5b89\u5bb6\u5c31\u4e1a\u2026\u2026\u4f60\u770b\u8fd9\u4e48\u505a\uff0c\u80fd\u4e0d\u80fd\u63a8\u52a8\u4e00\u4e0b\u5b9e\u9645\u7684\u5de5\u4f5c\u8fdb\u7a0b\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6837\u554a\uff01\u201d\u9648\u4fe1\u751f\u4e00\u5411\u77e5\u9053\u5f20\u606a\u5728\u5b89\u629a\u4eba\u624d\u4e0a\u5f88\u6177\u6168\uff0c\u4ece\u8fd9\u4e0a\u9762\u4e5f\u53ef\u4ee5\u770b\u51fa\u5f20\u606a\u5bf9\u6280\u672f\u6269\u5f20\u7684\u6e34\u671b\uff0c\u6ca1\u6709\u9876\u5c16\u7684\u79d1\u5b66\u5bb6\u4e0e\u5de5\u7a0b\u5e08\uff0c\u6240\u6709\u7684\u6280\u672f\u6269\u5f20\u90fd\u662f\u5e7b\u60f3\uff0c\u867d\u7136\u9526\u6e56\u65e9\u5c31\u5f00\u53d1\u7535\u5b50\u97f3\u4e50\u64ad\u653e\u5668\u7684\u96cf\u5f62\uff0c\u4f46\u662f\u8fd8\u662f\u5c06ESS\u6536\u5165\u65d7\u4e0b\u4e4b\u540e\uff0c\u624d\u4f7f\u76f8\u5173\u6280\u672f\u6210\u719f\u8d77\u6765\u7684\u3002\u4e3a\u4ec0\u4e48\u7231\u8fbe\u7684\u624b\u673a\u8981\u4e00\u62d6\u518d\u62d6\uff0c\u4e00\u65b9\u9762\u662f\u5bf9\u4ea7\u54c1\u8d28\u91cf\u7684\u4e25\u683c\u8981\u6c42\uff0c\u53e6\u4e00\u65b9\u9762\u6070\u6070\u4e5f\u8bf4\u660e\u9526\u6e56\u5728\u76f8\u5173\u6280\u672f\u4e0a\u8fd8\u4e0d\u591f\u6210\u719f\u3002\u9526\u6e56\u6b64\u65f6\u4ec5\u4ec5\u638c\u63e1\u7684\u662f\u4e8c\u6d41\u7684\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u5c06\u4ece\u5fb7\u4eea\u624b\u91cc\u62ff\u8fc7\u6765\u7684\u4e8c\u6d41\u624b\u673a\u5236\u9020\u6280\u672f\u5b8c\u5168\u6d88\u5316\uff0c\u8fd8\u662f\u4f9d\u8d56\u4ece\u5fb7\u4eea\u4e00\u540c\u8f6c\u8fc7\u6765\u7684\u4e00\u6279\u5de5\u7a0b\u6280\u672f\u4eba\u5458\uff0c\u8981\u7f29\u77ed\u4e0e\u6700\u65b0\u624b\u673a\u6280\u672f\u4e4b\u95f4\u7684\u5dee\u8ddd\uff0c\u8981\u6478\u7d22\u7740\u8e0f\u5165\u624b\u673a\u6838\u5fc3\u57fa\u5e26\u82af\u7247\u6280\u672f\u7684\u95e8\u69db\uff0c\u8fd8\u8981\u597d\u51e0\u5ea7\u9ad8\u5927\u9669\u5cfb\u7684\u5c71\u5cf0\u8981\u6500\u8d8a\uff0c\u201c\u8fd9\u4e48\u505a\u80af\u5b9a\u5bf9\u5de5\u4f5c\u6709\u5f88\u5927\u7684\u63a8\u52a8\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u884c\u554a\uff0c\u6211\u7b49\u4f1a\u513f\u7ed9\u76f4\u63a5\u7ed9\u7f57\u541b\u4e66\u8bb0\u901a\u7535\u8bdd\uff0c\u770b\u5e02\u91cc\u7ed9\u591a\u5c11\u4f18\u60e0\u7684\u653f\u7b56\uff0c\u4f60\u4eec\u5c3d\u5feb\u62df\u4e2a\u7ec6\u5219\u51fa\u6765\uff0c\u5b9e\u65bd\u554a\uff0c\u5c31\u4ece\u8c2d\u5148\u751f\u5f00\u59cb\uff0c\u4e5f\u5305\u62ec\u4e4b\u524d\u5df2\u7ecf\u7b54\u5e94\u5230\u6a61\u6811\u56ed\u5de5\u4f5c\u7684\u6280\u672f\u4eba\u5458\u2026\u2026\u201d\u5f20\u606a\u53c8\u671d\u8c2d\u4e91\u677e\u7b11\u7740\u8bf4\uff0c\u201c\u8c2d\u5148\u751f\uff0c\u54b1\u4eec\u4ee5\u540e\u5c31\u7b97\u7ed1\u5230\u4e00\u68f5\u6811\u4e0a\u7684\u8682\u86b1\u4e86\uff0c\u4f60\u53ef\u4ee5\u5e2e\u6a61\u6811\u56ed\u591a\u62d0\u4e9b\u4eba\u8fc7\u6765\uff0c\u4e5f\u65b9\u4fbf\u4f60\u5230\u5efa\u90ba\u540e\u5f00\u5c55\u5de5\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u8c2d\u4e91\u677e\u54c8\u54c8\u4e00\u7b11\uff0c\u4ed6\u9886\u5bfc\u65b0\u52a0\u5761\u56fd\u7acb\u5927\u5b66\u4e0e\u5bcc\u58eb\u901a\u8054\u5408\u6210\u7acb\u7684\u7535\u5b50\u6280\u672f\u5b9e\u9a8c\u5ba4\uff0c\u5e74\u85aa\u6298\u5408\u6210\u4eba\u6c11\u5e01\u8fd8\u4e0d\u5230\u4e00\u767e\u4e07\uff0c\u5012\u4e0d\u662f\u8d2a\u56fe\u4e00\u767e\u4e07\u7684\u8865\u52a9\u91d1\uff0c\u800c\u662f\u611f\u6168\u5f20\u606a\u8fd9\u4e48\u4e00\u4e2a\u9752\u5e74\u6563\u8d22\u60dc\u624d\u7684\u9b44\u529b\u3002</p><p>\u3000\u3000\u5728\u56fd\u5185\uff0c\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u7684\u85aa\u8d44\u5f88\u6709\u9650\uff0c\u800c\u4e14\u56fd\u5185\u79d1\u7814\u673a\u6784\u7684\u4f53\u7cfb\uff0c\u91cd\u884c\u653f\u3001\u8f7b\u6280\u672f\uff0c\u6280\u672f\u4eba\u5458\u7684\u4ef7\u503c\u5f97\u4e0d\u5230\u5e94\u6709\u7684\u5c0a\u91cd\uff1b\u4e0d\u61c2\u6280\u672f\u7684\u884c\u653f\u4eba\u5458\u638c\u63e1\u79d1\u7814\u7684\u65b9\u5411\u4e0e\u8d22\u6743\uff0c\u52a0\u4e0a\u56fd\u5185\u79d1\u7814\u57fa\u7840\u7684\u8584\u5f31\uff0c\u4f7f\u5f97\u6280\u672f\u4eba\u5458\u7684\u624d\u80fd\u5f97\u4e0d\u5230\u5e94\u6709\u7684\u53d1\u6325\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u79cd\u539f\u56e0\u662f\u8bb8\u591a\u4f18\u79c0\u6280\u672f\u4eba\u5458\u7eb7\u7eb7\u5f80\u5916\u8dd1\u7684\u539f\u56e0\uff0c\u4e0e\u7231\u56fd\u4e0d\u7231\u56fd\uff0c\u5173\u7cfb\u5012\u662f\u4e0d\u5927\u3002</p></article>"}}}]);
