(window.webpackJsonp=window.webpackJsonp||[]).push([[810],{833:function(p){p.exports={chapterTitle:null,title:"\u7b2c807\u7ae0 \u4e13\u9898\u8bb2\u5ea7",content:"<article><h2>\u7b2c807\u7ae0 \u4e13\u9898\u8bb2\u5ea7</h2><p>\u3000\u3000\u5f20\u606a\u5230\u6559\u5de5\u98df\u5802\u4e0d\u7b97\u65e9\u4e86\uff0c\u7a00\u7a00\u843d\u843d\u7684\u6ca1\u591a\u5c11\u4eba\uff0c\u5468\u747e\u73ba\u5750\u5728\u98df\u5802\u7684\u4e00\u89d2\u7528\u9910\uff0c\u966a\u4ed6\u4e00\u8d77\u7684\uff0c\u8fd8\u6709\u4e09\u540d\u4e2d\u5e74\u7537\u5b50\uff0c\u5f20\u606a\u90fd\u4e0d\u8ba4\u8bc6\uff0c\u4e0d\u662f\u56fd\u5546\u9662\u7684\u6559\u6388\uff0c\u770b\u4e0a\u53bb\u8eab\u4efd\u9887\u4e3a\u4e0d\u4fd7\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u5c31\u5f80\u90a3\u8fb9\u770b\u4e86\u4e00\u773c\uff0c\u5c31\u5230\u7a97\u53e3\u70b9\u4e86\u4e00\u6837\u7092\u83dc\uff0c\u5750\u5230\u4e00\u8fb9\u7684\u5ea7\u4f4d\u4e0a\u8fb9\u7b49\u8fb9\u770b\u624b\u91cc\u7684\u8d44\u6599\u3002\u5468\u747e\u73ba\u4e5f\u5c31\u770b\u4e86\u5f20\u606a\u4e24\u773c\uff0c\u5c31\u6536\u56de\u89c6\u7ebf\u4e0d\u52a8\u58f0\u97f3\u7684\u8ddf\u8eab\u8fb9\u4eba\u8c08\u8bdd\uff0c\u867d\u7136\u4e0e\u5f20\u606a\u9694\u5f97\u8fdc\uff0c\u4f46\u8fd8\u662f\u4e0b\u610f\u8bc6\u7684\u538b\u4f4e\u4e86\u58f0\u97f3\u3002\u5468\u747e\u73ba\u7684\u7ec6\u5fae\u5f02\u5e38\u5012\u5f15\u8d77\u5750\u5728\u4ed6\u5bf9\u9762\u7684\u4e2d\u5e74\u7537\u5b50\u7684\u8b66\u60d5\uff0c\u90a3\u4e2d\u5e74\u7537\u4eba\u56de\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u6ca1\u770b\u51fa\u5f20\u606a\u6709\u4f55\u51fa\u5947\u4e4b\u5904\uff0c\u53ea\u5f53\u662f\u5468\u747e\u73ba\u5728\u4e1c\u5927\u7684\u5b66\u751f\uff0c\u5fc3\u60f3\u73b0\u5728\u7684\u5b66\u751f\u4e0d\u542d\u4e00\u58f0\u5c31\u81ea\u987e\u81ea\u7684\u5750\u5230\u4e00\u8fb9\u8fd8\u771f\u662f\u4e0d\u61c2\u793c\u8c8c\uff0c\u4e5f\u5c31\u6ca1\u5f80\u6df1\u5904\u53bb\u60f3\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4e0b\u5348\u7684\u4e13\u9898\u8bb2\u5ea7\u4f1a\u63d0\u5230\u7a00\u571f\u6750\u6599\uff0c\u5f20\u606a\u5bf9\u8fd9\u79cd\u6cdb\u6cdb\u800c\u8bba\u7684\u8bb2\u5ea7\u5374\u6ca1\u6709\u592a\u5927\u7684\u5174\u8da3\u3002\u7a00\u571f\u91d1\u5c5e\u5728\u53d1\u5149\u6750\u6599\u53ca\u7535\u5b50\u5143\u5668\u4ef6\u65b9\u9762\u7684\u5e94\u7528\u7814\u7a76\uff0c\u6cf0\u5fd2\u5149\u7535\u6280\u672f\u7814\u7a76\u9662\u6709\u4e00\u5b9a\u7684\u57fa\u7840\uff0c\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u7684\u76f8\u5173\u57fa\u7840\u7814\u7a76\u5de5\u4f5c\u4e5f\u6b63\u5728\u5f00\u5c55\uff0c\u7a00\u571f\u8d44\u6e90\u5f00\u53d1\u7b49\u65b9\u9762\u7684\u5185\u5bb9\uff0c\u6b63\u5982\u7fdf\u4e39\u9752\u6240\u5efa\u8bae\u7684\u90a3\u6837\uff0c\u5f20\u606a\u53ef\u4ee5\u8bf7\u5404\u6743\u5a01\u7684\u4e13\u4e1a\u4eba\u58eb\u89e3\u7b54\u2014\u2014\u542c\u7ecf\u6d4e\u5b66\u8005\u8bf4\u7a00\u571f\u8d44\u6e90\u5f00\u53d1\uff0c\u591a\u5c11\u6709\u7740\u7ed9\u9694\u9774\u6320\u75d2\u7684\u611f\u89c9\u3002\u8fd9\u65f6\u5019\u770b\u5230\u5468\u747e\u73ba\u4e2d\u5348\u966a\u540c\u5ba2\u4eba\u5728\u6559\u5de5\u98df\u5802\u91cc\u7528\u9910\uff0c\u5f20\u606a\u60f3\u8d77\u6765\u4e0b\u5348\u5e76\u6ca1\u6709\u4ec0\u4e48\u7d27\u8981\u505a\u7684\u4e8b\u60c5\uff0c\u53bb\u542c\u4e00\u542c\u5012\u4e5f\u65e0\u59a8\u3002</p><p>\u3000\u3000\u5f20\u606a\u5939\u7740\u8d44\u6599\u8d70\u51fa\u6559\u5de5\u98df\u5802\u65f6\uff0c\u5468\u747e\u73ba\u4ed6\u4eec\u8fd8\u672a\u79bb\u5f00\uff0c\u770b\u5230\u5750\u5728\u5468\u747e\u73ba\u5bf9\u9762\u7684\u4e2d\u5e74\u7537\u5b50\u6709\u4e9b\u9762\u719f\u7684\u611f\u89c9\uff0c\u867d\u7136\u5468\u747e\u73ba\u966a\u4e09\u4e2a\u4e2d\u5e74\u4eba\u5750\u5728\u4e00\u8d77\uff0c\u4f46\u662f\u65c1\u8fb9\u8fd8\u5750\u7740\u51e0\u4eba\u5e94\u8be5\u662f\u8ddf\u4ed6\u4eec\u4e00\u8d77\uff0c\u53f8\u673a\u6216\u8005\u4fdd\u9556\u7684\u6a21\u6837\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u51fa\u6559\u5de5\u98df\u5802\uff0c\u7279\u610f\u7ed5\u5230\u5357\u8fb9\u6559\u5de5\u98df\u5802\u9644\u8fd1\u67d0\u4e00\u53ef\u505c\u8f66\u7684\u5730\u65b9\u770b\u4e86\u4e00\u773c\uff0c\u505c\u7740\u4e00\u90e8\u5954\u9a70\u3001\u4e00\u90e8\u608d\u9a6c\uff0c\u5954\u9a70\u6302\u7684\u662f\u91d1\u5c71\u7684\u8f66\u724c\uff0c\u608d\u9a6c\u6302\u7684\u5374\u662f\u6c5f\u5357\u7701\u519b\u533a\u7684\u8f66\u724c\u3002\u90a3\u4e09\u4e2a\u4e2d\u5e74\u7537\u5b50\u7684\u6765\u5934\u8fd8\u6709\u4e9b\u4e0d\u51e1\u3002\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\uff0c\u6c11\u7528\u608d\u9a6c\u4ea7\u91cf\u5f88\u6709\u9650\uff0c\u56fd\u5185\u6781\u4e3a\u7f55\u89c1\uff0c\u8981\u8bf4\u6c5f\u5357\u7701\uff0c\u4e5f\u5c31\u7ae0\u5dde\u7684\u79c1\u8425\u77ff\u4e3b\u559c\u6b22\u8fd9\u79cd\u5c71\u5730\u6027\u80fd\u6781\u4f73\u7684\u8d8a\u91ce\u8f66\u3002\u90fd\u8bf4\u201c\u8c08\u7b11\u6709\u9e3f\u5112\u3001\u5f80\u6765\u65e0\u767d\u4e01\u201d\uff0c\u5230\u4e86\u5468\u747e\u73ba\u8fd9\u91cc\u5219\u53ef\u8c13\u662f\u201c\u8c08\u7b11\u6709\u2018\u9e3f\u5112\u2019\u3001\u5f80\u6765\u7686\u5b98\u5546\u201d\u4e86\u3002</p><p>\u3000\u3000\u5728\u53bb\u542c\u8bb2\u5ea7\u4e4b\u524d\uff0c\u5f20\u606a\u5c06\u51e0\u4efd\u63d0\u7eb2\u5951\u9886\u4ecb\u7ecd\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u73b0\u72b6\u7684\u8d44\u6599\u770b\u4e86\u4e00\u904d\uff0c\u624d\u4e0b\u697c\u53bb\u5de5\u5b57\u697c\u7684\u9636\u68af\u6559\u5ba4\u53bb\u542c\u8bb2\u5ea7\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u5f20\u606a\u4e2a\u4eba\u5bf9\u5468\u747e\u73ba\u6068\u4e4b\u5165\u9aa8\u4e5f\u7f62\uff0c\u5468\u747e\u73ba\u5728\u4e1c\u5927\u8fd8\u662f\u9887\u5177\u53f7\u53ec\u529b\u7684\uff0c\u5f20\u606a\u64e6\u7740\u65f6\u95f4\u8d76\u5230\u9636\u68af\u6559\u5ba4\uff0c\u542c\u8bb2\u5ea7\u7684\u4eba\u90fd\u5df2\u7ecf\u6324\u5230\u95e8\u5916\u4e86\u3002\u5f20\u606a\u6324\u8fdb\u53bb\u6559\u5ba4\uff0c\u8fde\u8fc7\u9053\u91cc\u90fd\u7ad9\u6ee1\u4e86\u4eba\uff0c\u4e5f\u4e0d\u6653\u5f97\u675c\u98de\u6709\u6ca1\u6709\u5148\u8fc7\u6765\uff0c\u5012\u662f\u5148\u770b\u5230\u5750\u5728\u7b2c\u4e09\u6392\u7684\u9648\u5983\u84c9\u671d\u4ed6\u62db\u624b\u3002\u8fd9\u79cd\u5c42\u6b21\u7684\u8bb2\u5ea7\u5bf9\u9648\u5983\u84c9\u589e\u957f\u89c1\u8bc6\u8fd8\u662f\u6709\u5e2e\u52a9\u7684\uff0c\u9648\u5983\u84c9\u5012\u662f\u7ecf\u5e38\u5728\u5468\u672b\u7684\u65f6\u5019\u62c9\u5bbf\u820d\u540c\u5b66\u8fc7\u6765\u542c\uff0c\u800c\u4e14\u5f88\u79ef\u6781\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u4e0e\u5979\u540c\u5b66\u6324\u5728\u4e00\u8d77\uff0c\u5f20\u606a\u5750\u4e86\u8fdb\u53bb\uff0c\u4e5f\u96be\u514d\u8981\u4e0e\u9648\u5983\u84c9\u7684\u8eab\u4f53\u8d34\u6328\u5728\u4e00\u8d77\uff0c\u8fd9\u524d\u540e\u5de6\u53f3\u7686\u662f\u7fa1\u8273\u800c\u5fcc\u6068\u7684\u773c\u795e\uff0c\u7279\u522b\u662f\u539f\u5148\u4e0e\u9648\u5983\u84c9\u6328\u7740\u5750\u7684\u90a3\u4e2a\u7537\u751f\uff0c\u5fc3\u91cc\u8fd8\u6b63\u72b9\u8c6b\u7740\u8981\u600e\u4e48\u8ddf\u9648\u5983\u84c9\u642d\u8baa\uff0c\u9648\u5983\u84c9\u7ed9\u5f20\u606a\u8ba9\u5ea7\u4f4d\uff0c\u4ed6\u6123\u662f\u5835\u5728\u90a3\u91cc\u4e0d\u52a8\u5f39\uff0c\u770b\u7740\u5f20\u606a\u4ece\u7b2c\u4e8c\u6392\u7ffb\u8fc7\u63d2\u5230\u4ed6\u8ddf\u9648\u5983\u84c9\u4e4b\u95f4\uff0c\u770b\u5f20\u606a\u7684\u773c\u795e\u4f3c\u80fd\u4ece\u4ed6\u8eab\u4e0a\u6316\u4e00\u5757\u8089\u4e0b\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u8fc7\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u6709\u4eba\u62ff\u7b14\u5728\u6345\u81ea\u5df1\u7684\u540e\u80cc\uff0c\u5f20\u606a\u56de\u5934\u770b\u89c1\u8c22\u5b50\u5609\u5750\u5728\u4ed6\u4fa7\u540e\u9762\uff0c\u8fd9\u65f6\u5019\u6559\u5ba4\u91cc\u4eba\u6324\u7740\u6324\u4eba\u3001\u95f9\u54c4\u54c4\u7684\uff0c\u8fd8\u771f\u6ca1\u6709\u6ce8\u610f\u5230\u8c22\u5b50\u5609\u5c31\u5750\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u201c\u4f60\u80fd\u8fc7\u6765\uff0c\u6211\u4e0d\u80fd\u8fc7\u6765\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8ddf\u8c22\u5b50\u5609\u9017\u8da3\u3002</p><p>\u3000\u3000\u201c\u8fd9\u662f\u6211\u5bb6\u8001\u5934\u5b50\u89c4\u5b9a\u7684\u529f\u8bfe\uff0c\u4e0d\u7136\u6211\u624d\u6ca1\u6709\u8fd9\u95f2\u5de5\u592b\u6765\u542c\u8bb2\u5ea7\u5462\uff0c\u201d\u8c22\u5b50\u5609\u72e1\u9ee0\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u4e0d\u8fc7\u4f60\u8fc7\u6765\u4e86\uff0c\u5c82\u4e0d\u662f\u8ba9\u67d0\u4eba\u6ca1\u6709\u52c7\u6c14\u7ad9\u5230\u53f0\u4e0a\u8bb2\u4e0b\u53bb\uff1f\u201d\u5979\u4f3c\u4e4e\u4e5f\u5f88\u4e50\u610f\u770b\u5230\u5f20\u606a\u80fd\u5f53\u4f17\u6233\u5468\u747e\u73ba\u7684\u77ed\u5904\uff0c\u4e00\u4ed8\u552f\u6050\u5929\u4e0b\u4e0d\u4e71\u7684\u6a21\u6837\uff0c\u5b8c\u5168\u4e0d\u4f1a\u8003\u8651\u5230\u5468\u747e\u73ba\u8bb8\u591a\u65f6\u95f4\u662f\u5728\u7ed9\u5979\u8c22\u5bb6\u7684\u6b63\u6cf0\u96c6\u56e2\u505a\u5229\u76ca\u4ee3\u8a00\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ff\u8fd9\u79cd\u4e0d\u6309\u5e38\u7406\u51fa\u724c\u7684\u5973\u5b69\u5b50\u6ca1\u6709\u529e\u6cd5\uff0c\u9648\u5983\u84c9\u56de\u8fc7\u5934\u6765\uff0c\u7ed9\u5f20\u606a\u6307\u51fa\u675c\u98de\u3001\u8499\u4e50\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u5750\u5728\u6559\u5ba4\u6700\u540e\uff0c\u4e5f\u662f\u4e09\u4e2a\u4eba\u6324\u4e24\u4eba\u7684\u5ea7\u4f4d\uff0c\u663e\u7136\u611f\u89c9\u4e0a\u8981\u6bd4\u5f20\u606a\u5dee\u8fdc\u4e86\u3002</p><p>\u3000\u3000\u5230\u4e86\u65f6\u95f4\uff0c\u5148\u662f\u5468\u747e\u73ba\u7684\u52a9\u6559\u8d70\u8fdb\u6765\uff0c\u4ece\u95e8\u53e3\u80fd\u770b\u89c1\u5468\u747e\u73ba\u4e0e\u4e2d\u5348\u5728\u6559\u5de5\u98df\u5802\u4e00\u8d77\u7528\u9910\u7684\u4e09\u540d\u4e2d\u5e74\u7537\u5b50\u7ad9\u5728\u5916\u9762\u8bf4\u8bdd\uff0c\u8fd8\u6709\u56fd\u5546\u9662\u7684\u53e6\u4e00\u540d\u6559\u6388\u966a\u4ed6\u4eec\u7ad9\u5728\u6559\u5ba4\u5916\u3002\u5468\u747e\u73ba\u7684\u52a9\u6559\u671d\u5f20\u606a\u4ed6\u4eec\u8fd9\u8fb9\u8d70\u8fc7\u6765\uff0c\u5012\u4e0d\u662f\u8ba4\u8bc6\u5f20\u606a\uff0c\u800c\u662f\u5b89\u6392\u6b63\u5bf9\u7740\u6f14\u8bb2\u53f0\u7684\u7b2c\u4e09\u6392\u7a7a\u51fa\u51e0\u4e2a\u5ea7\u4f4d\u7ed9\u6765\u5bbe\u5750\u2026\u2026\u90a3\u4e2a\u5bf9\u5f20\u606a\u6ee1\u662f\u6028\u6c14\u7684\u3001\u8138\u4e0a\u957f\u6ee1\u9752\u6625\u75d8\u7684\u7537\u5b69\u5b50\u4e0e\u5916\u9762\u4e09\u540d\u5b66\u751f\u7ed9\u8bf7\u4e86\u51fa\u53bb\u7ad9\u5230\u8fc7\u9053\u4e0a\uff0c\u90a3\u540d\u52a9\u6559\u53c8\u8fc7\u6765\u62c9\u5f20\u606a\u7684\u8863\u8896\uff1a\u201c\u8fd9\u4f4d\u540c\u5b66\uff0c\u5bf9\u4e0d\u8d77\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c0f\u82cf\u8001\u5e08\uff0c\u4f60\u53eb\u5468\u4f2f\u4f2f\u81ea\u5df1\u8fdb\u6765\uff0c\u770b\u4ed6\u6709\u6ca1\u6709\u8138\u80fd\u8bf7\u8fd9\u4f4d\u540c\u5b66\u7ad9\u5728\u8fc7\u9053\u4e0a\u542c\u4ed6\u4eec\u505a\u4e13\u9898\u62a5\u544a\uff1f\u201d\u8c22\u5b50\u5609\u620f\u8c11\u7684\u6253\u8da3\u5468\u747e\u73ba\u7684\u52a9\u6559\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u8fd9\u65f6\u5019\u7ad9\u5728\u95e8\u53e3\u8f6c\u5934\u770b\u89c1\u5f20\u606a\u8d6b\u7136\u5750\u5728\u6559\u5ba4\u91cc\uff0c\u8fd8\u6b63\u8981\u7ed9\u4ed6\u7684\u52a9\u6559\u62c9\u5230\u8fc7\u9053\u4e0a\u53bb\uff0c\u4ed6\u5fd9\u62db\u624b\u558a\u4ed6\u7684\u52a9\u6559\u8fc7\u53bb\uff1a\u201c\u5c0f\u82cf\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5634\u89d2\u6302\u7740\u4e00\u4e1d\u5fae\u7b11\uff0c\u770b\u7740\u5468\u747e\u73ba\u7684\u52a9\u6559\u53c8\u8d70\u56de\u6559\u5ba4\uff0c\u4e0d\u8fc7\u4ed6\u8fd9\u6b21\u662f\u5230\u7b2c\u4e00\u6392\u9760\u95e8\u7684\u4e00\u4fa7\u6e05\u7a7a\u5ea7\u4f4d\uff0c\u8c22\u5b50\u5609\u5012\u662f\u8d81\u673a\u5750\u5230\u524d\u6392\u6765\u2014\u2014\u8fd9\u4e0b\u5f20\u606a\u66f4\u906d\u4eba\u6068\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5468\u747e\u73ba\u7b49\u4eba\u624d\u8d70\u8fdb\u6559\u5ba4\u91cc\u6765\uff0c\u5148\u662f\u56fd\u5546\u9662\u7684\u6c88\u6d4e\u6559\u6388\u505a\u201c\u56fd\u5185\u65b0\u6750\u6599\u4ea7\u4e1a\u7ecf\u6d4e\u672a\u6765\u53d1\u5c55\u4e0e\u53ef\u884c\u6027\u6a21\u5f0f\u7814\u7a76\u201d\u7684\u62a5\u544a\u3002</p><p>\u3000\u3000\u65b0\u6750\u6599\u4ea7\u4e1a\u662f\u672a\u6765\u6700\u5177\u53d1\u5c55\u6f5c\u529b\u7684\u4e09\u5927\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u4e4b\u4e00\uff0c\u6c88\u6d4e\u6559\u6388\u5217\u4e3e\u4e86\u56fd\u5185\u65b0\u6750\u6599\u4ea7\u4e1a\u4e0e\u56fd\u9645\u5148\u8fdb\u6c34\u5e73\u4e4b\u95f4\u7684\u5de8\u5927\u843d\u5dee\uff0c\u867d\u7136\u6ca1\u6709\u7279\u522b\u65b0\u9c9c\u7684\u4e1c\u897f\uff0c\u4f46\u662f\u4ec5\u7f57\u5217\u8fd9\u4e9b\u843d\u540e\u7684\u73b0\u72b6\u4e5f\u8db3\u4ee5\u50ac\u4eba\u594b\u8fdb\u4e86\uff0c\u542c\u5f97\u6559\u5ba4\u91cc\u8bf8\u591a\u5b66\u751f\u70ed\u8840\u6cb8\u817e\uff0c\u6068\u4e0d\u80fd\u9a6c\u4e0a\u5c31\u4e3a\u4e2d\u534e\u4e4b\u5d1b\u8d77\u800c\u8bfb\u4e66\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u5f88\u5947\u602a\u7c7b\u4f3c\u7684\u8bb2\u5ea7\u4e0d\u627e\u6750\u6599\u7cfb\u7684\u6559\u6388\u8fc7\u6765\uff0c\u800c\u8981\u56fd\u5546\u9662\u7684\u4eba\u5728\u8fd9\u91cc\u5192\u5145\u5168\u624d\uff1f</p><p>\u3000\u3000\u5f20\u606a\u7b49\u7740\u542c\u5468\u747e\u73ba\u5bf9\u7a00\u571f\u6750\u6599\u53d1\u5c55\u6709\u4ec0\u4e48\u9ad8\u89c1\uff0c\u6ca1\u6709\u63d0\u524d\u79bb\u5f00\uff0c\u4ece\u515c\u91cc\u638f\u51fa\u638c\u4e0a\u7535\u8111\u770b\u81ea\u5df1\u7684\u8d44\u6599\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u6ce8\u610f\u5230\u5f20\u606a\u624b\u91cc\u7684\u638c\u4e0a\u7535\u8111\u662f\u6ca1\u6709\u4ec0\u4e48\u54c1\u724c\u6807\u8bc6\u7684\u767d\u673a\uff0c\u4e0d\u662f\u9526\u6e56\u5df2\u7ecf\u63a8\u5411\u5e02\u573a\u7684\u7535\u5b50\u4ea7\u54c1\uff0c\u90a3\u5e94\u8be5\u662f\u9526\u6e56\u5f00\u53d1\u51fa\u6765\u7684\u65b0\u54c1\uff0c\u8fd9\u53ea\u638c\u4e0a\u7535\u8111\u7684\u952e\u76d8\u533a\u4e0e\u9526\u6e56\u5df2\u7ecf\u63a8\u5411\u5e02\u573a\u7684\u7535\u5b50\u8bcd\u5178\u4e0d\u540c\uff0c\u6ca1\u6709\u91c7\u53d6\u5168\u952e\u76d8\u6a21\u5f0f\uff0c\u800c\u91c7\u53d6\u4e0e\u624b\u673a\u76f8\u7c7b\u4f3c\u7684\u6570\u5b57\u952e\u76d8\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u6570\u5b57\u952e\u600e\u4e48\u8f93\u5165\u4e2d\u6587\uff1f\u201d\u8c22\u5b50\u5609\u5fc3\u601d\u4e5f\u4e0d\u5728\u53f0\u4e0a\u505a\u62a5\u544a\u7684\u6c88\u6d4e\u8eab\u4e0a\uff0c\u638c\u4e0a\u7535\u8111\u8981\u8f93\u5165\u4fe1\u606f\uff0c\u4e0d\u53ef\u80fd\u6ca1\u6709\u4e2d\u6587\u8f93\u5165\u529f\u80fd\uff0c\u9664\u975e\u8fd9\u6b3e\u638c\u4e0a\u7535\u8111\u4e0d\u662f\u5f00\u53d1\u7ed9\u56fd\u5185\u5e02\u573a\u4f7f\u7528\u7684\uff0c\u8c22\u5b50\u5609\u5c0f\u5de7\u7684\u8111\u888b\u60c5\u4e0d\u81ea\u7981\u7684\u51d1\u4e86\u8fc7\u53bb\uff0c\u638c\u4e0a\u7535\u8111\u7684\u754c\u9762\u662f\u4e2d\u6587\u754c\u9762\u4e0d\u5047\uff0c\u7591\u60d1\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5fae\u5f20\u7740\u5634\uff0c\u8be7\u5f02\u7684\u538b\u7740\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u5f00\u53d1\u51fa\u6570\u5b57\u952e\u76d8\u4e2d\u6587\u8f93\u5165\u6280\u672f\u4e86\uff1f\u201d\u624b\u673a\u4e5f\u662f\u6807\u51c6\u7684\u6570\u5b57\u952e\u76d8\uff0c\u96be\u602a\u5979\u8981\u60ca\u8bb6\u4e86\u3002</p><p>\u3000\u3000\u201c\u5443\uff0c\u5fd8\u8bb0\u8981\u5728\u4f60\u9762\u524d\u4fdd\u5bc6\u4e86\uff0c\u201d\u5f20\u606a\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u5fc3\u60f3\u8c22\u5b50\u5609\u6b64\u65f6\u5bf9\u624b\u673a\u6280\u672f\u7684\u7406\u89e3\u5e94\u8be5\u6bd4\u7edd\u5927\u591a\u6570\u4e1a\u5185\u4eba\u66f4\u6df1\u523b\u5427\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u7b97\u662f\u5427\u2026\u2026\u4e0d\u8fc7\u8fd9\u6b3e\u638c\u4e0a\u7535\u8111\u7684\u6280\u672f\u8fd8\u4e0d\u591f\u6210\u719f\uff0c\u4e5f\u5c31\u5148\u9020\u51fa\u4e00\u53ea\u767d\u673a\u7ed9\u6211\u5728\u7528\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u618b\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u518d\u63a8\u4e00\u6b3e\u65b0\u624b\u673a\uff0c\u8be5\u4e0d\u4f1a\u63a5\u4e0b\u6765\u63a8\u51fa\u7684\u624b\u673a\u5168\u90e8\u90fd\u79fb\u690d\u6700\u65b0\u7684\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\uff1f\u201d\u8c22\u5b50\u5609\u8f7b\u8f7b\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u620f\u8c11\u7684\u7b11\u7740\u8bf4\uff0c\u201c\u54e6\u54e6\uff01\u524d\u4e9b\u5929\u9648\u9759\u53bb\u53c2\u52a0\u534e\u590f\u7535\u5b50\u5185\u90e8\u53ec\u5f00\u7684\u4e00\u4e2a\u4f1a\u8bae\uff0c\u845b\u5efa\u5fb7\u81ea\u4ee5\u4e3a\u51e0\u53ea\u80a5\u7f8a\u62f4\u6210\u4e00\u8d77\u5c31\u80fd\u53d8\u6210\u8001\u864e\uff0c\u8fd8\u5728\u4f1a\u4e0a\u5439\u5618\u4eca\u5e74\u8981\u5728\u56fd\u4ea7\u624b\u673a\u4e0a\u5f7b\u5e95\u5c06\u9526\u6e56\u7684\u6c14\u7130\u6253\u538b\u4e0b\u53bb\uff0c\u771f\u4e0d\u77e5\u9053\u4f60\u4eec\u4eae\u51fa\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u4e4b\u540e\u4ed6\u4eec\u7684\u8138\u8272\u4f1a\u6709\u591a\u96be\u770b\uff1f\u201d</p><p>\u3000\u3000\u4f5c\u4e3a\u638c\u4e0a\u7535\u8111\uff0c\u5f20\u606a\u8fd8\u662f\u503e\u5411\u4e8e\u91c7\u7528\u5168\u952e\u76d8\u7684\u6a21\u5f0f\uff0c\u800c\u4ed6\u624b\u91cc\u7684\u8fd9\u53ea\u6570\u5b57\u952e\u638c\u4e0a\u7535\u8111\u5f00\u53d1\u51fa\u6765\uff0c\u7eaf\u7cb9\u662f\u4e3a\u4e86\u65b9\u4fbf\u4ed6\u4eb2\u81ea\u6d4b\u8bd5\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u3002</p><p>\u3000\u3000\u5176\u5b9e\u9526\u6e56\u4ece\u5fb7\u4eea\u624b\u4e2d\u5168\u76d8\u63a5\u53d7\u9664\u57fa\u5e26\u82af\u7247\u4e4b\u5916\u7684\u6570\u5b57\u624b\u673a\u6280\u672f\u65f6\uff0c\u4e5f\u5305\u62ec\u4e86\u62fc\u97f3\u4e0e\u5b57\u5f62\u4e24\u79cd\u6570\u5b57\u952e\u8f93\u5165\u4e2d\u6587\u4e13\u5229\u6280\u672f\uff0c\u4f46\u90a3\u662f\u6700\u57fa\u7840\u7684\u4e2d\u6587\u8f93\u5165\uff0c\u6709\u7740\u4e25\u91cd\u7684\u7f3a\u9677\uff0c\u5b57\u5e93\u7684\u5bb9\u91cf\u4e5f\u6781\u4e3a\u6709\u9650\uff0c\u6240\u4ee5\u5f20\u606a\u5e76\u6ca1\u6709\u8ba9\u6700\u521d\u7684i19\u3001i08\u4e24\u6b3e\u624b\u673a\u5e26\u4e2d\u6587\u8f93\u5165\u529f\u80fd\uff0c\u518d\u8bf4\u622a\u6b62\u5230\u73b0\u5728\uff0c\u5728\u56fd\u5185\u5e02\u573a\u51fa\u73b0\u6240\u6709\u4e2d\u6587\u6570\u5b57\u624b\u673a\u90fd\u6ca1\u6709\u4e2d\u6587\u8f93\u5165\u529f\u80fd\u3002</p><p>\u3000\u3000\u6700\u521d\u662fESS\uff0c\u540e\u6765\u662f\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u4e13\u95e8\u6210\u4e86\u4e00\u4e2a\u5f00\u53d1\u5c0f\u7ec4\u5728\u5fb7\u4eea\u7684\u4e13\u5229\u6280\u672f\u57fa\u7840\u5f00\u53d1\u771f\u6b63\u610f\u4e49\u4e0a\u7684\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\uff0c\u5b9e\u73b0\u62fc\u97f3\u4e0e\u7b14\u753b\u8f93\u5165\u7684\u667a\u80fd\u5316\u3002</p><p>\u3000\u3000\u5927\u6982\u6ca1\u6709\u8c01\u80fd\u6bd4\u5f20\u606a\u66f4\u9002\u5408\u53bb\u505a\u65b0\u4ea7\u54c1\u7684\u8bd5\u7528\u5de5\u4f5c\uff0c\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u7684\u5f00\u53d1\u6bd4\u8f83\u987a\u5229\uff0c\u4f46\u662f\u5f20\u606a\u7684\u8981\u6c42\u66f4\u82db\u523b\uff0c\u6240\u4ee5\u62d6\u7740\u4e00\u76f4\u6ca1\u6709\u80fd\u591f\u5c06\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u62ff\u51fa\u6765\uff0c\u4e5f\u81f4\u4f7f\u9526\u6e56\u5728\u63a8\u5728\u53bb\u5e74\u5341\u6708\u63a8\u51fai19\u3001i08\u4e24\u6b3e\u624b\u673a\u4e4b\u540e\u8fc7\u53bb\u534a\u5e74\u90fd\u6ca1\u6709\u518d\u63a8\u51fa\u65b0\u6b3e\u624b\u673a\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u91cc\u6e05\u695a\u7f8e\u56fd\u3001\u52a0\u62ff\u5927\u90fd\u6709\u516c\u53f8\u5728\u5f00\u53d1\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\uff0c\u6469\u6258\u7f57\u62c9\u516c\u53f8\u751a\u81f3\u5728\u53bb\u5e74\u4ece\u5176\u4ed6\u516c\u53f8\u8d2d\u5165\u4e2d\u6587\u8f93\u5165\u4e13\u5229\u6280\u672f\u8fdb\u884c\u8fdb\u4e00\u6b65\u7684\u4fee\u6539\u4e0e\u5b8c\u5584\uff0c\u4f9d\u7167\u5386\u53f2\u7684\u8f68\u8ff9\uff0c\u6469\u6258\u7f57\u62c9\u4f1a\u5c06\u5728\u4eca\u5e74\u7684\u67d0\u4e2a\u65f6\u95f4\u2014\u2014\u5f20\u606a\u4e5f\u8bb0\u5f97\u5177\u4f53\u7684\u65f6\u95f4\u2014\u2014\u5728\u56fd\u5185\u5e02\u573a\u6700\u5148\u63a8\u51fa\u5e26\u6709\u4e2d\u6587\u8f93\u5165\u529f\u80fd\u7684\u624b\u673a\u3002\u9526\u6e56\u5e76\u4e0d\u9700\u8981\u8ddf\u6469\u6258\u7f57\u62c9\u4e89\u7740\u8c01\u80fd\u6700\u5148\u63a8\u51fa\u5e26\u4e2d\u6587\u8f93\u5165\u529f\u80fd\u7684\u624b\u673a\uff0c\u53ea\u8981\u9526\u6e56\u63a8\u51fa\u7684\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u80fd\u9065\u9065\u9886\u5148\u90a3\u4e9b\u56fd\u5916\u8f6f\u4ef6\u5f00\u53d1\u5546\uff0c\u5c31\u5b8c\u5168\u80fd\u518d\u9e23\u60ca\u4eba\u3002</p><p>\u3000\u3000\u5c31\u7b97\u5176\u4ed6\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6709\u663e\u5c71\u9732\u6c34\uff0c\u6709\u7740\u524d\u4e16\u8bb0\u5fc6\u7684\u5f20\u606a\u5374\u5f53\u7136\u77e5\u9053\u90a3\u4e9b\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u65e9\u671f\u7684\u81f4\u547d\u7f3a\u9677\uff1a\u62fc\u97f3\u8f93\u5165\u91cd\u7801\u7387\u592a\u9ad8\uff0c\u7b14\u753b\u8f93\u5165\u5bf9\u7b14\u753b\u987a\u5e8f\u6709\u4e25\u683c\u8981\u6c42\uff0c\u65e9\u671f\u51e0\u4e4e\u6ca1\u6709\u8054\u60f3\u8bcd\u6c47\u529f\u80fd\u2026\u2026\u9526\u6e56\u7684\u6280\u672f\u5728\u8fd1\u534a\u5e74\u7684\u65f6\u95f4\u91cc\u5df2\u7ecf\u5c3d\u6700\u5927\u9650\u5ea6\u7684\u514b\u670d\u4e86\u4e0a\u8ff0\u7684\u7f3a\u9677\u3002</p><p>\u3000\u3000\u7231\u8fbei19\u624b\u673a\u91c7\u7528\u7684\u84dd\u5c4f\u4e0e\u590d\u5f0f\u97f3\u8f68\u6280\u672f\u8fc4\u4eca\u6ca1\u6709\u7ed9\u5176\u4ed6\u7535\u5b50\u5382\u5546\u7ed5\u8fc7\u9526\u6e56\u7684\u4e13\u5229\u5f00\u53d1\u51fa\u66ff\u4ee3\u6280\u672f\uff0c\u9526\u6e56\u4ece\u5e74\u540e\u6084\u7136\u5c06i19\u51fa\u8d27\u91cf\u63d0\u9ad8\u5230\u6bcf\u6708\u56db\u4e07\u53ea\uff0c\u552e\u4ef7\u4ec5\u6bd4\u53bb\u5e74\u5341\u5e74\u7565\u964d\u4e24\u767e\u5143\uff0ci08\u624b\u673a\u7684\u51fa\u8d27\u91cf\u5df2\u7ecf\u8d85\u8fc7\u5341\u4e8c\u4e07\u53ea\u3002\u8c22\u5b50\u5609\u6562\u80af\u5b9a\u9526\u6e56\u968f\u7740\u6280\u672f\u8d8a\u6765\u8d8a\u6210\u719f\uff0c\u5236\u9020\u6210\u672c\u9010\u6e10\u964d\u4f4e\uff0c\u53ef\u80fd\u4ec5i19\u8fd9\u4e00\u6b3e\u624b\u673a\u5c31\u7ed9\u9526\u6e56\u6bcf\u4e2a\u6708\u5e26\u53bb\u8d85\u4ebf\u5143\u7684\u51c0\u5229\uff0c\u8981\u662f\u9526\u6e56\u518d\u6700\u5148\u63a8\u51fa\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\uff0c\u534e\u590f\u60f3\u5728\u56fd\u4ea7\u624b\u673a\u4e0a\u538b\u5236\u9526\u6e56\uff0c\u65e0\u7591\u662f\u767d\u65e5\u505a\u68a6\u3002\u53ea\u6015\u9526\u6e56\u5c06\u6709\u5b9e\u529b\u5728\u9ad8\u7aef\u624b\u673a\u5e02\u573a\u53bb\u64bc\u52a8\u6469\u6258\u7f57\u62c9\u4e0e\u7231\u7acb\u4fe1\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u624b\u91cc\u5e26\u6570\u5b57\u952e\u7684\u638c\u4e0a\u7535\u8111\uff0c\u8c22\u5b50\u5609\u771f\u662f\u773c\u70ed\u5f97\u5f88\uff0c\u4e3a\u65b9\u4fbf\u4e0e\u5f20\u606a\u5c0f\u58f0\u8bf4\u8bdd\uff0c\u5979\u534a\u7247\u8eab\u5b50\u8ddf\u79c0\u7f8e\u7684\u5c0f\u8111\u888b\u90fd\u8db4\u684c\u5b50\uff0c\u8111\u888b\u8fd8\u5c3d\u53ef\u80fd\u51d1\u5230\u5f20\u606a\u8138\u524d\uff0c\u4eae\u82e5\u9ed1\u6f06\u7684\u773c\u73e0\u5b50\u5728\u773c\u7a9d\u5b50\u91cc\u8f6c\u6765\u8f6c\u53bb\uff0c\u76ee\u5149\u5728\u5f20\u606a\u7684\u8138\u4e0a\u4e0e\u4ed6\u624b\u91cc\u90a3\u53ea\u638c\u4e0a\u7535\u8111\u4e0a\u626b\u6765\u626b\u53bb\uff0c\u8fd9\u6a21\u6837\u4eff\u4f5b\u5728\u8bfe\u5802\u4e0a\u8c08\u60c5\u8bf4\u7b11\u3001\u5c06\u53f0\u4e0a\u8bb2\u5e08\u629b\u4e4b\u8111\u540e\u7684\u5b66\u751f\u604b\u4eba\u4f3c\u7684\u2014\u2014\u5f53\u7136\u4e86\uff0c\u5728\u8bb2\u53f0\u4e0a\u7684\u8001\u5e08\u82e5\u8981\u770b\u4e0b\u9762\u6709\u8fd9\u6837\u7684\u5b66\u751f\uff0c\u5fc3\u91cc\u662f\u76f8\u5f53\u706b\u5927\u7684\uff0c\u4e0d\u8fc7\u56fd\u5546\u9662\u6559\u6388\u6c88\u6d4e\u4e5f\u8ba4\u8bc6\u4e0e\u5468\u747e\u73ba\u6e0a\u6e90\u9887\u6df1\u7684\u8c22\u5b50\u5609\uff0c\u4e5f\u77e5\u9053\u8c22\u5b50\u5609\u662f\u7701\u5185\u5f88\u6709\u540d\u6c14\u7684\u8c22\u5bb6\u6b63\u6cf0\u96c6\u56e2\u7684\u5c0f\u516c\u4e3b\uff0c\u5fc3\u91cc\u6709\u6c14\u5374\u53ea\u80fd\u89c6\u82e5\u65e0\u7779\u7684\u5728\u53f0\u4e0a\u7ee7\u7eed\u4ed6\u7684\u8bdd\u9898\u3002\u4ed6\u4e0d\u8ba4\u8bc6\u5f20\u606a\uff0c\u5f53\u7136\u542c\u8fc7\u201c\u5f20\u606a\u201d\u8fd9\u4e2a\u540d\u5b57\uff0c\u4f46\u662f\u6ca1\u6709\u89c1\u8fc7\u5f20\u606a\u7684\u8138\uff0c\u53ea\u662f\u5947\u602a\u8fd9\u5c0f\u4f19\u5b50\u662f\u8c01\uff0c\u7adf\u7136\u83b7\u5f97\u8c22\u5bb6\u5c0f\u516c\u4e3b\u7684\u9752\u7750\uff1f</p><p>\u3000\u3000\u5468\u747e\u73ba\u4e5f\u6ce8\u610f\u5230\u540e\u6392\u7684\u5f02\u5e38\uff0c\u9887\u4e3a\u5c34\u5c2c\uff0c\u4ed6\u6653\u5f97\u8c22\u5b50\u5609\u6301\u80a1\u7684\u79d1\u738b\u9ad8\u79d1\u83b7\u5f97\u9526\u6e56\u7684\u9f0e\u7acb\u652f\u6301\u624d\u83b7\u5f97\u4e0e\u56db\u5927\u56fd\u4ea7\u624b\u673a\u5382\u540c\u5e2d\u7684\u7279\u6b8a\u5730\u4f4d\uff0c\u79d1\u738b\u9ad8\u79d1\u8d34\u724c\u751f\u4ea7\u8054\u4fe1\u54c1\u724c\u7684\u624b\u673a\uff0c\u53c8\u81ea\u884c\u7ec4\u7ec7\u9500\u552e\uff0c\u6708\u9500\u91cf\u4ece\u5e74\u521d\u5c31\u987a\u5229\u7684\u7a81\u7834\u4e00\u4e07\u4ef6\uff0c\u5728\u5f53\u524d\u624b\u673a\u4ea7\u4e1a\u7684\u9ec4\u91d1\u65f6\u4ee3\uff0c\u5b9e\u96be\u60f3\u8c61\u79d1\u738b\u9ad8\u79d1\u6536\u5272\u7684\u66b4\u5229\u2014\u2014\u867d\u7136\u8fdc\u4e0d\u80fd\u8ddf\u9526\u6e56\u76f8\u63d0\u5e76\u8bba\uff0c\u4f46\u662f\u5728\u56fd\u5185\u7edd\u5927\u591a\u6570\u7684\u7535\u5b50\u5382\u5546\u4e2d\uff0c\u79d1\u738b\u9ad8\u79d1\u8981\u8ba9\u4eba\u773c\u7ea2\u5f97\u751f\u75c5\u3002</p><p>\u3000\u3000\u82e5\u518d\u6709\u6c11\u8425\u4f01\u4e1a\u80fd\u4ece\u4fe1\u606f\u4ea7\u4e1a\u90e8\u83b7\u5f97\u624b\u673a\u751f\u4ea7\u724c\u7167\uff0c\u65e0\u7591\u79d1\u738b\u9ad8\u79d1\u5c06\u6392\u5728\u524d\u5217\uff0c\u8fd8\u5265\u593a\u4e86\u79d1\u738b\u83b7\u5f97\u624b\u673a\u751f\u4ea7\u724c\u7167\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u5728\u79d1\u738b\u9ad8\u79d1\u6301\u80a1\uff0c\u4f7f\u5f97\u8c22\u5bb6\u539f\u5148\u62e7\u6210\u4e00\u56e2\u7684\u5173\u7cfb\u66f4\u5f97\u5fae\u5999\u800c\u590d\u6742\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u54ea\u91cc\u80fd\u60f3\u5230\u8c22\u5b50\u5609\u773c\u91cc\u5168\u662f\u5f20\u606a\u624b\u91cc\u7684\u90a3\u53ea\u638c\u4e0a\u7535\u8111\u767d\u673a\uff1f\u4ed6\u53ea\u770b\u5230\u8c22\u5b50\u5609\u5bf9\u5f20\u606a\u4e00\u4ed8\u4eb2\u70ed\u7684\u6a21\u6837\uff0c\u5fc3\u91cc\u611f\u89c9\u9887\u4e3a\u602a\u5f02\uff0c\u8981\u662f\u8c22\u5bb6\u8fd9\u4e2a\u8c01\u4e5f\u964d\u670d\u4e0d\u4e86\u7684\u5c0f\u9b54\u5934\u5bf9\u5f20\u606a\u5fc3\u751f\u60c5\u612b\uff0c\u5c82\u4e0d\u662f\u7edd\u5927\u7684\u8bbd\u523a\uff1f</p><p>\u3000\u3000\u5468\u747e\u73ba\u7684\u4e2d\u5e74\u7537\u5b50\u6ce8\u610f\u8981\u5468\u747e\u73ba\u9891\u9891\u56de\u5934\u770b\u90a3\u4e2a\u4e2d\u5348\u5728\u6559\u5de5\u98df\u5802\u9047\u5230\u7684\u90a3\u4e2a\u5c0f\u9752\u5e74\uff0c\u542c\u8bb2\u5ea7\u91cc\u8ddf\u5973\u5b69\u5b50\u8c03\u60c5\uff0c\u8fd8\u771f\u662f\u6ca1\u6709\u793c\u8c8c\u554a\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u7684\u52a9\u6559\u770b\u5f20\u606a\u7684\u773c\u795e\u91cc\u5374\u5145\u6ee1\u4e86\u654c\u610f\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u54ea\u77e5\u9053\u522b\u4eba\u600e\u4e48\u60f3\uff0c\u5c31\u7b97\u77e5\u9053\uff0c\u5979\u591a\u534a\u4e5f\u61d2\u5f97\u7406\u4f1a\uff0c\u6307\u4e86\u6307\u5f20\u606a\u624b\u91cc\u7684\u638c\u4e0a\u7535\u8111\uff1a\u201c\u6211\u80fd\u7528\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u9012\u8fc7\u53bb\uff0c\u8fd8\u8981\u51d1\u8fc7\u8111\u888b\u6559\u8c22\u5b50\u5609\u600e\u4e48\u64cd\u4f5c\uff0c\u9648\u5983\u84c9\u7684\u6ce8\u610f\u529b\u4e5f\u7ed9\u5438\u5f15\u8fc7\u6765\uff0c\u5927\u6982\u4e5f\u89c9\u5f97\u8bb2\u5ea7\u6709\u4e9b\u4e4f\u5473\uff0c\u5c31\u6258\u7740\u8111\u888b\u770b\u5f20\u606a\u5c0f\u58f0\u4ecb\u7ecd\u638c\u4e0a\u7535\u8111\u7684\u64cd\u4f5c\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u5c31\u5173\u5fc3\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6cd5\u7684\u64cd\u4f5c\uff0c\u9526\u6e56\u7684\u4e2d\u6587\u8f93\u5165\u6280\u672f\u667a\u80fd\u5316\u76f8\u5f53\u9ad8\uff0c\u52a0\u4e0a\u8c22\u5b50\u5609\u5bf9\u6700\u57fa\u7840\u7684\u4e2d\u6587\u8f93\u5165\u4e5f\u6709\u6240\u4e86\u89e3\uff0c\u4e0a\u624b\u5c31\u4f1a\uff0c\u4e13\u5fc3\u81f4\u5fd7\u7684\u201c\u73a9\u201d\u8d77\u6765\uff0c\u4e5f\u6839\u672c\u4e0d\u53bb\u6ce8\u610f\u8fd9\u65f6\u5019\u6362\u5468\u747e\u73ba\u5230\u53f0\u4e0a\u505a\u62a5\u544a\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u7684\u8bb2\u5ea7\u5e76\u6ca1\u6709\u5c40\u9650\u4e8e\u7a00\u571f\u6750\u6599\u8fd9\u4e2a\u5f88\u72ed\u7a84\u7684\u8303\u56f4\uff0c\u800c\u662f\u4e00\u5f00\u59cb\u5c31\u5ef6\u4f38\u5230\u6574\u4e2a\u7a00\u571f\u4ea7\u4e1a\u7684\u53d1\u5c55\u95ee\u9898\u3002\u5f20\u606a\u7684\u6ce8\u610f\u529b\u5f88\u5feb\u7ed9\u5468\u747e\u73ba\u5438\u5f15\u8fc7\u6765\uff0c\u56e0\u4e3a\u5468\u747e\u73ba\u7684\u62a5\u544a\u5f88\u7b26\u5408\u672a\u6765\u5341\u5e74\u5185\u7a00\u571f\u4ea7\u4e1a\u7684\u53d1\u5c55\u8d8b\u52bf\uff1a\u91c7\u77ff\u6743\u5411\u5730\u65b9\u653e\u677e\uff0c\u7a00\u571f\u5f00\u91c7\u4e0e\u51b6\u70bc\u5206\u79bb\u884c\u4e1a\u5141\u8bb8\u79c1\u6709\u8d44\u672c\u8fdb\u5165\uff0c\u5141\u8bb8\u5916\u8d44\u8fdb\u5165\u7a00\u571f\u77ff\u6df1\u52a0\u5de5\u4e0e\u5de5\u4e1a\u5e94\u7528\u5f00\u53d1\u9886\u57df\uff0c\u4fc3\u8fdb\u5e02\u573a\u5316\u7ade\u4e89\uff0c\u653e\u677e\u5bf9\u5916\u7a00\u571f\u4ea7\u54c1\u7684\u51fa\u53e3\u9650\u989d\uff0c\u5229\u7528\u597d\u7a00\u571f\u8d44\u6e90\u7684\u4f18\u52bf\uff0c\u5efa\u7acb\u5168\u7403\u6700\u5927\u7684\u7a00\u571f\u6750\u6599\u57fa\u5730\uff0c\u964d\u4f4e\u5bf9\u7a00\u571f\u77ff\u4f01\u8d44\u6e90\u7a0e\u7684\u5f81\u6536\uff0c\u51cf\u5c11\u51fa\u53e3\u5173\u7a0e\u7684\u5f81\u6536\uff0c\u4fc3\u8fdb\u56fd\u5185\u7a00\u571f\u4ea7\u54c1\u52a0\u5165\u5230\u56fd\u9645\u5e02\u573a\u7684\u7ade\u4e89\u4e4b\u4e2d\u2026\u2026\u8fd9\u4e9b\u6709\u52a9\u4e2d\u56fd\u53c2\u5165\u4e16\u8d38\u7ec4\u7ec7\u7684\u8c08\u5224\u3002</p><p>\u3000\u3000\u542c\u5230\u8fd9\u4e9b\uff0c\u5f20\u606a\u51e0\u4e4e\u6000\u7591\u5468\u747e\u73ba\u4e8b\u5b9e\u4e0a\u53c2\u4e0e\u5230\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u7684\u5236\u5b9a\uff0c\u6b63\u662f\u5bbd\u677e\u7684\u4ea7\u4e1a\u653f\u7b56\uff0c\u4f7f\u5f97\u56fd\u5185\u7684\u7a00\u571f\u8d44\u6e90\u7279\u522b\u662f\u7ecf\u6d4e\u3001\u519b\u4e8b\u4ef7\u503c\u6781\u9ad8\u7684\u91cd\u7a00\u571f\u8d44\u6e90\u65e5\u540e\u6210\u4e3a\u65e5\u97e9\u7b49\u56fd\u5ec9\u4ef7\u63a0\u593a\u7684\u5bf9\u8c61\u2014\u2014\u5468\u747e\u73ba\u7684\u786e\u6709\u8fd9\u4e2a\u5f71\u54cd\u529b\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ee1\u5fc3\u72d0\u7591\u7684\u770b\u4e86\u524d\u6392\u4e0e\u5468\u747e\u73ba\u4e00\u8d77\u7684\u4e09\u4e2a\u4e2d\u5e74\u7537\u5b50\uff0c\u867d\u7136\u56fd\u5185\u7684\u5927\u591a\u6570\u7a00\u571f\u8d44\u6e90\u90fd\u96c6\u4e2d\u5728\u5185\u8499\u53e4\u767d\u4e91\u9102\u535a\uff0c\u4f46\u662f\u7ecf\u6d4e\u4ef7\u503c\u6700\u9ad8\u7684\u91cd\u7a00\u571f\u8d44\u6e90\u5374\u53c8\u96c6\u4e2d\u5728\u7ae0\u5dde\u5468\u8fb9\u53ca\u897f\u5357\u67d0\u4e9b\u5730\u533a\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u5f53\u7136\u4e0d\u6653\u5f97\u5f20\u606a\u5728\u8003\u8651\u4ec0\u4e48\uff0c\u5979\u5c06\u638c\u4e0a\u7535\u8111\u9012\u7ed9\u5f20\u606a\uff0c\u5f20\u606a\u5fc3\u601d\u4e0d\u5728\u8fd9\u91cc\uff0c\u63a5\u8fc7\u638c\u4e0a\u7535\u8111\u5c31\u8981\u585e\u8fdb\u515c\u91cc\uff0c\u8c22\u5b50\u5609\u6321\u4f4f\uff0c\u8ba9\u4ed6\u770b\u5c4f\u5e55\uff0c\u539f\u6765\u8c22\u5b50\u5609\u5728\u5c4f\u5e55\u4e0a\u5f55\u5165\u4e00\u884c\u6587\u5b57\uff1a\u201c\u9526\u6e56\u6253\u7b97\u72ec\u4eab\u6570\u5b57\u952e\u76d8\u4e2d\u6587\u8f93\u5165\u6cd5\u6280\u672f\u8fd8\u662f\u6709\u53ef\u80fd\u3001\u6709\u6761\u4ef6\u7684\u5bf9\u5176\u4ed6\u5382\u5546\u8f6c\u8ba9\uff1f\u201d</p><p>\u3000\u3000\u8c22\u5b50\u5609\u8fd9\u6bb5\u65f6\u95f4\u7684\u5fc3\u795e\u9b42\u9b44\u90fd\u94bb\u5165\u624b\u673a\u91cc\uff0c\u5f53\u7136\u6e05\u695a\u624b\u673a\u4e2d\u6587\u8f93\u5165\u6280\u672f\u5728\u672a\u6765\u6570\u5b57\u624b\u673a\u5e02\u573a\u4e0a\u7684\u4ef7\u503c\uff0c\u5979\u62c5\u5fc3\u9526\u6e56\u4e3a\u4e86\u8fdb\u4e00\u6b65\u6269\u5927\u5728\u56fd\u4ea7\u624b\u673a\u4e2d\u4f18\u52bf\u5730\u4f4d\u72ec\u4eab\u8fd9\u9879\u6280\u672f\u2014\u2014\u4e5d\u516b\u5e74\u56fd\u5185\u53ef\u6ca1\u6709\u4ec0\u4e48\u53cd\u5784\u65ad\u6cd5\u3002</p></article>"}}}]);
