(window.webpackJsonp=window.webpackJsonp||[]).push([[813],{836:function(p){p.exports={chapterTitle:null,title:"\u7b2c810\u7ae0 \u5927\u56fd\u6218\u7565",content:"<article><h2>\u7b2c810\u7ae0 \u5927\u56fd\u6218\u7565</h2><p>\u3000\u3000\u201c\u7a00\u571f\u662f17\u79cd\u9567\u7cfb\u91d1\u5c5e\u7269\u7684\u7edf\u79f0\uff0c\u53c8\u6709\u8f7b\u7a00\u571f\u4e0e\u91cd\u7a00\u571f\u4e4b\u5206\uff0c\u5728\u519b\u4e8b\u3001\u7535\u5b50\u3001\u65b0\u6750\u6599\u7b49\u9886\u57df\u6709\u7740\u6781\u5176\u5e7f\u6cdb\u7684\u5e94\u7528\uff0c\u4ee5\u56fd\u5185\u773c\u4e0b\u7684\u79d1\u6280\u683c\u5c40\uff0c\u8fd8\u65e0\u6cd5\u66f4\u6df1\u523b\u7684\u7406\u89e3\u7a00\u571f\u5728\u9ad8\u65b0\u79d1\u6280\u4e2d\u7684\u91cd\u8981\u4f5c\u7528\uff0c\u53ef\u4ee5\u8bf4\u662f\u672a\u6765\u9ad8\u65b0\u79d1\u6280\u5fc5\u4e0d\u53ef\u5c11\u7684\u5de5\u4e1a\u539f\u6599\uff0c\u968f\u540e\u79d1\u6280\u7684\u53d1\u5c55\uff0c\u9700\u6c42\u91cf\u4f1a\u65e5\u76ca\u589e\u6da8\u2026\u2026\u53ef\u80fd\u6211\u4eec\u6bcf\u4e2a\u4eba\u4ece\u5c0f\u5b66\u5f00\u59cb\u5c31\u7ed9\u704c\u8f93\u6211\u56fd\u7684\u7a00\u571f\u5de5\u4e1a\u50a8\u91cf\u662f\u4f55\u7b49\u7684\u4e30\u5bcc\uff0c\u4e0e\u4e00\u4e9b\u7a00\u6709\u91d1\u5c5e\u4e00\u8d77\uff0c\u662f\u6211\u56fd\u552f\u4e00\u5728\u8d44\u6e90\u50a8\u91cf\u4e0a\u5360\u636e\u5784\u65ad\u6027\u5730\u4f4d\u7684\u77ff\u4ea7\u8d44\u6e90\u3002\u9093\u516c\u5728\u4e5d\u4e8c\u5e74\u5c31\u8bf4\u8fc7\uff0c\u2018\u4e2d\u4e1c\u6709\u77f3\u6cb9\uff0c\u4e2d\u56fd\u6709\u7a00\u571f\u2019\uff0c\u4e0d\u63d0\u8fc7\u5ea6\u5f00\u53d1\u4f1a\u4e25\u91cd\u7834\u574f\u751f\u6001\u73af\u5883\uff0c\u4f7f\u5f97\u7a00\u571f\u77ff\u4ea7\u5728\u56fd\u9645\u5e02\u573a\u65e0\u6cd5\u83b7\u5f97\u4e0e\u5176\u4ef7\u503c\u76f8\u7b26\u7684\u5229\u76ca\uff0c\u8fd9\u4e48\u91cd\u8981\u7684\u8d44\u6e90\u5176\u5b9e\u5e94\u8be5\u53d1\u6325\u51fa\u66f4\u91cd\u8981\u7684\u4f5c\u7528\u2026\u2026\u201d\u51fa\u4e86\u9636\u68af\u6559\u5ba4\uff0c\u5f20\u606a\u5c31\u4e0e\u675c\u98de\u3001\u9648\u5983\u84c9\u4ed6\u4eec\u76f4\u63a5\u53bb\u6559\u5de5\u98df\u5802\u5403\u665a\u996d\uff0c\u675c\u98de\u3001\u8499\u4e50\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u5bf9\u7a00\u571f\u4ea7\u4e1a\u7684\u77e5\u8bc6\u5f88\u53ef\u601c\uff0c\u5f20\u606a\u4e0d\u5f97\u4e0d\u5145\u5f53\u8d77\u79d1\u666e\u8bb2\u89e3\u5458\u7684\u89d2\u8272\uff0c\u5750\u5728\u5ea7\u4f4d\u4e0a\u7b49\u4e0a\u83dc\u7684\u5f53\u513f\uff0c\u8ddf\u4ed6\u4eec\u8bb2\u89e3\u8d77\u7a00\u571f\u7684\u6218\u7565\u4ef7\u503c\uff0c\u201c\u5982\u679c\u4e2d\u4e1c\u80fd\u7ec4\u6210\u4e00\u4e2a\u5f3a\u6709\u529b\u7684\u653f\u5e9c\uff0c\u90a3\u5c06\u5168\u4e16\u754c\u6700\u5f3a\u5927\u7684\u56fd\u5bb6\uff0c\u6574\u4e2a\u5de5\u4e1a\u4f53\u7cfb\u90fd\u4f9d\u8d56\u77f3\u6cb9\u800c\u751f\u5b58\uff0c\u4e2d\u4e1c\u53c8\u62e5\u6709\u5168\u7403\u6700\u591a\u7684\u77f3\u6cb9\u5de5\u4e1a\u50a8\u91cf\uff0c\u5982\u4eca\u4e2d\u4e1c\u5404\u56fd\u53ea\u80fd\u79f0\u5f97\u4e0a\u5bcc\uff0c\u7edd\u79f0\u4e0d\u4e0a\u5f3a\uff0c\u8fd8\u4e0d\u65f6\u53d1\u52a8\u51e0\u573a\u6218\u4e89\uff0c\u5f53\u7136\uff0c\u4e4b\u6240\u4ee5\u4f1a\u53d1\u751f\u6218\u4e89\uff0c\u591a\u534a\u4e5f\u662f\u56e0\u4e3a\u77f3\u6cb9\u7684\u56e0\u7d20\u5728\u80cc\u540e\u9a71\u52a8\u2026\u2026\u8fd9\u4e48\u8bf4\uff0c\u662f\u4e0d\u662f\u80fd\u6bd4\u8f83\u5bb9\u6613\u7406\u89e3\u7a00\u571f\u7684\u6218\u7565\u4ef7\u503c\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u5c31\u8ddf\u675c\u98de\u4ed6\u4eec\u6cdb\u6cdb\u800c\u8c08\uff0c\u66f4\u8be6\u7ec6\u7684\u8fd8\u8981\u7b49\u7fdf\u4e39\u9752\u5728\u4e13\u5bb6\u7684\u534f\u52a9\u4e0b\u6574\u7406\u51fa\u4e00\u4efd\u8be6\u7ec6\u7684\u62a5\u544a\u51fa\u6765\u3002\u5403\u8fc7\u665a\u996d\uff0c\u5f20\u606a\u5148\u56de\u516c\u5bd3\uff0c\u5728\u6821\u56ed\u7684\u4fbf\u9053\uff0c\u8fb9\u8d70\u8fb9\u7ed9\u665a\u6674\u901a\u7535\u8bdd\uff0c\u665a\u6674\u4e0b\u5348\u5c31\u5230\u5efa\u90ba\uff0c\u5728\u7535\u8bdd\u91cc\u544a\u8bc9\u665a\u6674\u4e0b\u5348\u7684\u4e8b\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u7b97\u662f\u5e2e\u4f60\u51fa\u4e86\u4e00\u53e3\u6076\u6c14\uff0c\u8981\u4e0d\u662f\u4ed6\u81ea\u5df1\u9001\u4e0a\u67aa\u53e3\u6765\uff0c\u8fd8\u771f\u6ca1\u6709\u673a\u4f1a\u62bd\u4ed6\u7684\u8138\u2014\u2014\u4ed6\u603b\u662f\u85cf\u5728\u5e55\u540e\u3002\u201d</p><p>\u3000\u3000\u201c\u5b66\u6821\u91cc\u4f1a\u4e0d\u4f1a\u6709\u4e9b\u9ebb\u70e6\uff0c\u6bd5\u7adf\u600e\u4e48\u770b\u90fd\u4e0d\u50cf\u4e56\u5b66\u751f\u7684\u6837\u5b50\uff1f\u201d\u665a\u6674\u5728\u7535\u8bdd\u91cc\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u201c\u4e0d\u6e05\u695a\uff0c\u6216\u8bb8\u5468\u747e\u73ba\u6ca1\u6709\u8138\u53bb\u544a\u72b6\u3002\u201d\u5468\u747e\u73ba\u53ea\u80fd\u6253\u843d\u7259\u5e26\u7740\u8840\u5f80\u809a\u5b50\u91cc\u54bd\uff0c\u4f46\u662f\u5f20\u606a\u4e0d\u77e5\u9053\u5468\u747e\u73ba\u7684\u52a9\u6559\u5df2\u7ecf\u64c5\u81ea\u4e3b\u5f20\u544a\u72b6\u5230\u9662\u91cc\u53bb\u4e86\uff0c\u5176\u5b9e\u5f20\u606a\u66f4\u5e0c\u671b\u5468\u747e\u73ba\u80fd\u5c06\u4e8b\u60c5\u95f9\u5927\u3002\u4e8b\u60c5\u8d8a\u95f9\u5927\uff0c\u58f0\u97f3\u4e5f\u5c31\u8d8a\u54cd\u4eae\uff0c\u4e5f\u80fd\u5f15\u8d77\u66f4\u591a\u4eba\u7684\u5173\u6ce8\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u4f60\u4e00\u76f4\u90fd\u5728\u8ddf\u8c01\u901a\u7535\u8bdd\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8d70\u5230\u516c\u5bd3\u697c\u524d\uff0c\u7fdf\u4e39\u9752\u98ce\u98ce\u706b\u706b\u7684\u8d70\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u5443\uff0c\u665a\u6674\u59d0\u7684\u7535\u8bdd\uff0c\u201d\u5f20\u606a\u6307\u4e86\u6307\u624b\u673a\uff0c\u95ee\u7fdf\u4e39\u9752\uff0c\u201c\u6709\u4ec0\u4e48\u6025\u4e8b\u627e\u6211\uff1f\u201d</p><p>\u3000\u3000\u201c\u9646\u6587\u592b\u6253\u4e0d\u901a\u4f60\u7684\u7535\u8bdd\uff0c\u5c31\u6253\u5230\u5085\u4fca\u90a3\u91cc\uff0c\u5085\u4fca\u901a\u77e5\u4e86\u6211\uff0c\u4ed6\u53bb\u5b66\u6821\u627e\u4f60\u53bb\u4e86\u2026\u2026\u674e\u8fdc\u6e56\u60f3\u89c1\u4f60\u3002\u201d\u7fdf\u4e39\u9752\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4e0e\u675c\u98de\u4ed6\u4eec\u51fa\u5de5\u5b57\u697c\u5c31\u53bb\u6559\u5de5\u98df\u5802\u5403\u996d\uff0c\u8fd9\u65f6\u5019\u624d\u662f\u6b63\u7528\u665a\u9910\u7684\u65f6\u95f4\uff0c\u674e\u8fdc\u6e56\u76f8\u7ea6\u5171\u8fdb\u665a\u9910\u554a\uff01\u5f20\u606a\u63c9\u4e86\u63c9\u809a\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u53ef\u60dc\u4e86\uff0c\u521a\u586b\u9971\u809a\u5b50\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u597d\u4e45\u6ca1\u6709\u5728\u79c1\u4e0b\u573a\u5408\u4e0e\u674e\u8fdc\u6e56\u89c1\u9762\u4e86\uff0c\u5373\u4f7f\u53bb\u5e74\u671f\u672b\u56e0\u4f5c\u5f0a\u4e8b\u60c5\u674e\u8fdc\u6e56\u4e3b\u52a8\u8868\u8fbe\u4e86\u5584\u610f\uff0c\u4e4b\u540e\u4e5f\u6ca1\u6709\u89c1\u9762\u7684\u673a\u4f1a\uff0c\u8fd9\u6b21\u96be\u5f97\u674e\u8fdc\u6e56\u76f8\u9080\uff0c\u5f20\u606a\u5f53\u7136\u4e0d\u6562\u6020\u6162\uff0c\u53ea\u662f\u4e0d\u6e05\u695a\u674e\u8fdc\u6e56\u7a81\u7136\u627e\u4ed6\u8fc7\u53bb\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u8c08\u3002</p><p>\u3000\u3000\u201c\u5728\u6559\u5ba4\u91cc\u5c06\u56fd\u5185\u77e5\u540d\u7684\u5b66\u8005\u6c14\u5410\u8840\u53ef\u4e0d\u662f\u4ec0\u4e48\u503c\u5f97\u63d0\u5021\u7684\u4e8b\u60c5\u2026\u2026\u201d\u752b\u89c1\u9762\uff0c\u674e\u8fdc\u6e56\u5c31\u9762\u5e26\u7b11\u610f\u7684\u6279\u8bc4\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u5443\uff0c\u201d\u5f20\u606a\u6478\u4e86\u6478\u8111\u888b\uff0c\u89c1\u674e\u8fdc\u6e56\u7684\u8bdd\u91cc\u5e76\u65e0\u8d23\u5907\u4e4b\u610f\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u6d88\u606f\u4f20\u51fa\u6765\u8fd8\u771f\u5feb\uff0c\u53ea\u662f\u4e00\u65f6\u56fe\u5634\u5feb\uff0c\u8fc7\u540e\u89c1\u5468\u747e\u73ba\u90a3\u6837\uff0c\u5c31\u540e\u6094\u4e86\uff0c\u8fd8\u5f88\u5185\u759a\uff0c\u60f3\u7740\u627e\u673a\u4f1a\u4e0a\u95e8\u53bb\u8d54\u793c\u9053\u6b49\u5462\u3002\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u5fae\u5fae\u4e00\u7b11\uff0c\u674e\u8fdc\u6e56\u575a\u6301\u8981\u8bf7\u5f20\u606a\u5403\u996d\uff0c\u4ed6\u521a\u521a\u53bb\u5b89\u6392\u665a\u9910\uff0c\u5c0f\u5305\u53a2\u91cc\u5c31\u674e\u8fdc\u6e56\u3001\u5f20\u606a\u3001\u4ed6\u8fd8\u6709\u7fdf\u4e39\u9752\u56db\u4e2a\u4eba\uff0c\u5176\u4ed6\u968f\u884c\u4eba\u5458\u90fd\u5750\u5728\u5916\u9762\u3002\u5f20\u606a\u771f\u8981\u4e0a\u95e8\u8d54\u793c\u9053\u6b49\u80fd\u518d\u628a\u5468\u747e\u73ba\u6c14\u5410\u8840\uff0c\u4fa7\u5934\u544a\u8bc9\u5f20\u606a\u662f\u5d14\u56fd\u6052\u5c06\u9ec4\u660f\u65f6\u7684\u4e8b\u60c5\u544a\u8bc9\u4ed6\u7684\u3002\u5f20\u606a\u5728\u6559\u5ba4\u91cc\u5f53\u7740\u56db\u767e\u591a\u5b66\u751f\u4e0e\u6559\u5e08\u7684\u9762\u524d\u516c\u7136\u8bad\u65a5\u5468\u747e\u73ba\u662f\u5229\u76ca\u9636\u5c42\u7684\u8d70\u72d7\u3001\u4ee3\u8a00\u4eba\uff0c\u62ff\u4eba\u5bb6\u94b1\u8d22\u800c\u6ca1\u6709\u81ea\u5df1\u7684\u7acb\u573a\uff0c\u5c06\u5468\u747e\u73ba\u6c14\u5f97\u5410\u8840\uff0c\u8fd9\u4e8b\u60c5\u5176\u5b9e\u5f88\u68d8\u624b\uff0c\u81f3\u5c11\u9662\u91cc\u8bb8\u591a\u6559\u6388\u542c\u4e86\u90fd\u6709\u5154\u6b7b\u72d0\u60b2\u4e4b\u611f\uff0c\u4e0d\u5c0a\u91cd\u5e08\u957f\u53c8\u504f\u504f\u662f\u5b66\u6821\u91cc\u6700\u5927\u7684\u5fcc\u8bb3\u3002\u5d14\u56fd\u6052\u662f\u806a\u660e\u4eba\uff0c\u611f\u89c9\u5230\u4e8b\u60c5\u68d8\u624b\u65f6\uff0c\u5c31\u5c06\u7403\u8e22\u5230\u9646\u6587\u592b\u8fd9\u91cc\u6765\uff0c\u53bb\u5e74\u671f\u672b\u9646\u6587\u592b\u80fd\u4e3a\u4f5c\u5f0a\u4e8b\u4ef6\u6253\u7535\u8bdd\uff0c\u90a3\u5c31\u4e00\u5b9a\u80fd\u5728\u8fd9\u4ef6\u4e8b\u60c5\u5e2e\u4ed6\u62ff\u4e3b\u610f\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u5c0f\u58f0\u5c06\u7f18\u7531\u544a\u8bc9\u5f20\u606a\uff0c\u5f20\u606a\u624d\u77e5\u9053\u539f\u6765\u4e8b\u60c5\u5df2\u7ecf\u6345\u5230\u9662\u91cc\u4e86\uff0c\u90a3\u80af\u5b9a\u6709\u4eba\u8fdd\u80cc\u4e86\u5468\u747e\u73ba\u7684\u610f\u613f\uff0c\u4e0d\u7136\u4ee5\u5468\u747e\u73ba\u7684\u57ce\u5e9c\u5c31\u662f\u843d\u4e86\u7259\u4e5f\u8981\u541e\u81ea\u5df1\u809a\u5b50\u91cc\u53bb\u3002\u8fd9\u4e00\u6345\uff0c\u5c31\u6345\u5230\u674e\u8fdc\u6e56\u8fd9\u91cc\u6765\u4e86\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u7701\u6ca1\u6709\u4ec0\u4e48\u4e0a\u89c4\u6a21\u7684\u7a00\u571f\u4ea7\u4e1a\uff0c\u96be\u5f97\u6709\u8fd9\u4e48\u597d\u7684\u673a\u4f1a\uff0c\u665a\u4e0a\u53c8\u6ca1\u6709\u5e94\u916c\uff0c\u674e\u8fdc\u6e56\u4fbf\u7ea6\u5f20\u606a\u51fa\u6765\uff0c\u9664\u4e86\u7a00\u571f\u4ea7\u4e1a\u4e4b\u5916\uff0c\u8fd8\u662f\u6709\u5f88\u591a\u8bdd\u9898\u53ef\u4ee5\u6c9f\u901a\u7684\u2014\u2014\u518d\u8bf4\u4ed6\u7684\u653f\u6cbb\u7406\u60f3\u4e5f\u4e0d\u4ec5\u4ec5\u5c40\u9650\u4e8e\u4e1c\u6d77\u3002</p><p>\u3000\u3000\u201c\u8fd9\u5e94\u8be5\u662f\u56fd\u52a1\u9662\u7a00\u571f\u529e\u59d4\u6258\u7ed9\u5468\u747e\u73ba\u7684\u8bfe\u9898\u5427\uff0c\u4e5f\u4e0d\u4ec5\u4ec5\u53ea\u6709\u5468\u747e\u73ba\u4e00\u540d\u5b66\u8005\uff0c\u53ef\u80fd\u5468\u747e\u73ba\u8d1f\u8d23\u7684\u8bfe\u9898\u66f4\u5b8f\u89c2\u4e00\u4e9b\uff0c\u201d\u674e\u8fdc\u6e56\u521a\u5f00\u59cb\u8fd8\u662f\u626f\u5230\u7a00\u571f\u7684\u8bdd\u9898\uff0c\u4ecb\u7ecd\u8d77\u66f4\u6df1\u5c42\u6b21\u7684\u80cc\u666f\uff0c\u201c\u4e2d\u592e\u4e3a\u52a0\u5165\u4e16\u8d38\u7ec4\u7ec7\u6b63\u52a0\u5f3a\u4e0e\u5404\u6210\u5458\u56fd\u7684\u8c08\u5224\uff0c\u7a00\u571f\u4ee5\u53ca\u4e00\u4e9b\u7a00\u6709\u91d1\u5c5e\u4ea7\u4e1a\u7684\u5f00\u653e\u6070\u6070\u662f\u4e16\u8d38\u4e00\u4e9b\u6210\u5458\u56fd\u63d0\u51fa\u4e00\u4e9b\u6761\u4ef6\uff0c\u56fd\u52a1\u9662\u5404\u90e8\u59d4\u624d\u6709\u76f8\u5173\u8bfe\u9898\u4e0b\u6765\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u521a\u7740\u624b\u53bb\u6536\u641c\u76f8\u5173\u8d44\u6599\uff0c\u6709\u4e9b\u5185\u90e8\u8d44\u6599\uff0c\u7684\u786e\u662f\u4ed6\u63a5\u89e6\u4e0d\u5230\u7684\u3002\u674e\u8fdc\u6e56\u7684\u8bdd\u6070\u6070\u4e5f\u8bc1\u660e\u5468\u747e\u73ba\u662f\u5f71\u54cd\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u7684\u5173\u952e\u4eba\u7269\uff0c\u770b\u6765\u4eca\u5929\u5f53\u4f17\u62bd\u4ed6\u7684\u8138\u8fd8\u662f\u8d70\u5bf9\u4e86\u4e00\u6b65\u68cb\u3002\u5fc5\u987b\u4ee5\u9488\u950b\u76f8\u5bf9\u7684\u59ff\u6001\u8feb\u4f7f\u5468\u747e\u73ba\u4e0d\u6562\u8f7b\u6613\u7684\u5411\u56fd\u52a1\u9662\u7a00\u571f\u529e\u629b\u51fa\u4ed6\u7684\u89c2\u70b9\uff0c\u8fd9\u4e8b\u60c5\u5c31\u6709\u66f4\u591a\u7684\u8f6c\u673a\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u5f53\u7136\u4e5f\u597d\u5947\u5f20\u606a\u4eca\u5929\u4e3a\u4ec0\u4e48\u4f1a\u5728\u7a00\u571f\u8fd9\u4e2a\u8bdd\u9898\u4e0a\u4f1a\u6709\u5982\u6b64\u5f3a\u786c\u7684\u59ff\u6001\uff0c\u89e3\u91ca\u8fc7\u66f4\u6df1\u5c42\u6b21\u7684\u80cc\u666f\u4e4b\u540e\uff0c\u5c31\u7b49\u7740\u5f20\u606a\u89e3\u91ca\u3002</p><p>\u3000\u3000\u201c\u5728\u8fd9\u91cc\u4e5f\u4e0d\u8bf4\u4ec0\u4e48\u751f\u6001\u7834\u574f\u7684\u95ee\u9898\uff0c\u4e0d\u4ec5\u4ec5\u7a00\u571f\uff0c\u8fd8\u6709\u4e00\u4e9b\u5176\u4ed6\u7a00\u6709\u91d1\u5c5e\uff0c\u9526\u6e56\u65d7\u4e0b\u7684\u7814\u7a76\u673a\u6784\u9884\u6d4b\u672a\u6765\u4f1a\u6709\u66f4\u5e7f\u6cdb\u7684\u7528\u9014\u3002\u5728\u53d1\u5149\u6750\u6599\u65b9\u9762\uff0c\u6cf0\u5fd2\u5149\u7535\u6280\u672f\u7814\u7a76\u9662\u8ba4\u4e3a\u4f1a\u6709\u4e00\u79cd\u65b0\u578b\u7684\u8282\u80fd\u7535\u5b50\u706f\u4f1a\u5f97\u5230\u63a8\u5e7f\uff0c\u751a\u81f3\u53ef\u4ee5\u66ff\u4ee3\u4e16\u754c\u5f53\u524d\u7684\u4e3b\u8981\u706f\u5177\uff0c\u8fd9\u79cd\u706f\u5177\u6240\u91c7\u7528\u7684\u53d1\u5149\u6750\u6599\u5bf9\u7a00\u571f\u91d1\u5c5e\u6709\u6781\u5927\u7684\u6d88\u8017\u2026\u2026\u5f53\u7136\uff0c\u76f8\u5173\u6280\u672f\u53ef\u80fd\u8fd8\u9700\u8981\u5341\u5e74\u3001\u516b\u5e74\u7684\u65f6\u95f4\u624d\u80fd\u591f\u6210\u719f\uff0c\u4f46\u662f\u8003\u8651\u4e00\u9879\u4ea7\u4e1a\u653f\u7b56\uff0c\u6211\u4eec\u7684\u76ee\u5149\u96be\u9053\u4e0d\u5e94\u8be5\u6bd4\u5341\u5e74\u3001\u516b\u5e74\u66f4\u957f\u8fdc\uff1f\u201d\u5f20\u606a\u5148\u4ece\u9526\u6e56\u81ea\u8eab\u7684\u5229\u76ca\u89d2\u5ea6\u53bb\u89e3\u91ca\u4e3a\u4ec0\u4e48\u4ed6\u4eca\u5929\u4e0b\u5348\u8981\u53bb\u6405\u5c40\u7684\u539f\u56e0\uff0c\u201c\u53e6\u5916\uff0c\u65e5\u3001\u97e9\u7b49\u5c11\u6570\u56fd\u5bb6\u638c\u63e1\u7684\u8584\u819c\u6db2\u6676\u5c4f\u6280\u672f\u53ef\u80fd\u4f1a\u5728\u5168\u7403\u8303\u56f4\u5185\u6d41\u884c\uff0c\u6211\u4eec\u63a8\u6d4b\u8fd9\u79cd\u8584\u819c\u6db2\u6676\u5c4f\u6280\u672f\u4f7f\u7528\u5230\u4e00\u79cd\u56fd\u5185\u50a8\u5907\u5360\u6709\u5784\u65ad\u5730\u4f4d\u7684\u94df\u91d1\u5c5e\u3002\u8fd9\u79cd\u94df\u91d1\u5c5e\u5168\u7403\u50a8\u5907\u90fd\u6781\u5c11\uff0c\u9664\u4e86\u5728\u9ad8\u7aef\u519b\u4e8b\u4e0a\u7684\u5c11\u91cf\u5e94\u7528\u4e4b\u5916\uff0c\u76ee\u524d\u5728\u6c11\u7528\u5de5\u4e1a\u6280\u672f\u4e0a\u8fd8\u6ca1\u6709\u4ec0\u4e48\u5177\u4f53\u7684\u5e94\u7528\uff0c\u6240\u4ee5\u4f7f\u5f97\u552e\u4ef7\u8f83\u4f4e\u3002\u82e5\u662f\u6211\u4eec\u7684\u63a8\u6d4b\u6ca1\u6709\u9519\uff0c\u4e00\u65e6\u8584\u819c\u6db2\u6676\u5c4f\u6280\u672f\u63a8\u5e7f\u5f00\u6765\uff0c\u5bf9\u94df\u91d1\u5c5e\u7684\u9700\u6c42\u4f1a\u5927\u5e45\u4e0a\u5347\u3002\u773c\u4e0b\u56fd\u5185\u94df\u91d1\u5c5e\u51fa\u53e3\u603b\u503c\u624d\u4e00\u767e\u56db\u4e94\u5341\u4e07\u7f8e\u5143\uff0c\u7b80\u76f4\u662f\u5c06\u5b9d\u5f53\u6210\u5783\u573e\u5728\u5356\u3002\u6211\u8003\u8651\u5411\u6709\u5173\u90e8\u95e8\u63d0\u51fa\u7533\u8bf7\uff0c\u7531\u9526\u6e56\u52a8\u7528\u8d44\u91d1\u5bf9\u94df\u91d1\u5c5e\u8fdb\u884c\u50a8\u5907\u2026\u2026\u82e5\u6709\u53ef\u80fd\uff0c\u9526\u6e56\u751a\u81f3\u5e0c\u671b\u80fd\u63a7\u5236\u94df\u91d1\u5c5e\u5bf9\u5916\u6c11\u7528\u51fa\u53e3\u7684\u6e20\u9053\uff0c\u8fd9\u53ef\u4ee5\u4f5c\u4e3a\u9526\u6e56\u65e5\u540e\u5bf9\u6297\u65e5\u3001\u97e9\u7535\u5b50\u4f01\u4e1a\u4e00\u4e2a\u91cd\u8981\u7b79\u7801\u3002\u201d</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u5bf9\u5177\u4f53\u7684\u6280\u672f\u95ee\u9898\u4e0d\u662f\u5f88\u4e86\u89e3\uff0c\u94df\u91d1\u5c5e\u65e2\u7136\u76ee\u524d\u53ea\u5728\u9ad8\u7aef\u519b\u4e8b\u9886\u57df\u6709\u5e94\u7528\uff0c\u9526\u6e56\u8981\u50a8\u5907\u94df\u91d1\u5c5e\uff0c\u81ea\u7136\u8981\u8ddf\u56fd\u5bb6\u76f8\u5173\u90e8\u95e8\u6253\u597d\u62db\u547c\uff0c\u53ef\u80fd\u50a8\u5907\u57fa\u5730\u8fd8\u8981\u53d7\u5230\u56fd\u5bb6\u6709\u5173\u90e8\u95e8\u7684\u76d1\u7ba1\u3002\u5f53\u7136\uff0c\u9526\u6e56\u8981\u60f3\u63a7\u5236\u94df\u91d1\u5c5e\u5bf9\u5916\u7684\u51fa\u53e3\u6e20\u9053\uff0c\u6709\u4e00\u4e2a\u66f4\u9002\u5408\u7684\u4eba\u7269\u80fd\u63d0\u4f9b\u6781\u5927\u7684\u52a9\u529b\uff0c\u90a3\u5c31\u662f\u5916\u7ecf\u8d38\u90e8\u7684\u53f6\u81fb\u6c11\u3002</p><p>\u3000\u3000\u5f20\u606a\u80fd\u8ddf\u81ea\u5df1\u8bf4\u5f97\u8fd9\u4e48\u900f\u5f7b\uff0c\u674e\u8fdc\u6e56\u5f88\u6ee1\u610f\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u60c5\uff0c\u7684\u786e\u9700\u8981\u4e0b\u9762\u53cd\u5e94\u4e0a\u53bb\uff0c\u6709\u4e9b\u5b66\u8005\u53d7\u5229\u76ca\u56e2\u4f53\u7684\u5f71\u54cd\u6df1\u4e9b\uff0c\u4e5f\u5f88\u96be\u6709\u72ec\u7acb\u3001\u6301\u6b63\u7684\u6001\u5ea6\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u53c8\u8bf4\u9053\uff1a\u201c\u4e2d\u56fd\u5728\u5341\u5e74\u3001\u4e8c\u5341\u5e74\u7684\u65f6\u95f4\u91cc\u90fd\u4f1a\u4fdd\u6301\u9ad8\u901f\u589e\u957f\uff0c\u6211\u4eec\u6709\u8fd9\u4e2a\u4fe1\u5fc3\u2026\u2026\u4f46\u662f\uff0c\u4ec5\u4ec5\u8ba1\u7b97\u6bcf\u5e748\uff05\u7684\u7ecf\u6d4e\u589e\u957f\u901f\u7387\uff0c\u5341\u5e74\u4e4b\u540e\uff0c\u4e2d\u56fd\u5c31\u5c06\u6210\u4e3a\u5168\u7403\u6700\u5927\u7684\u7ecf\u6d4e\u4f53\u4e4b\u4e00\uff0c\u5bf9\u77f3\u6cb9\u3001\u94c1\u77ff\u77f3\u7b49\u5de5\u4e1a\u539f\u6599\u7684\u9700\u6c42\u4e5f\u5c06\u6025\u5267\u589e\u6da8\uff0c\u5c06\u4e25\u91cd\u4f9d\u8d56\u5bf9\u6d77\u5916\u8d44\u6e90\u7684\u8fdb\u53e3\u3002\u5728\u5c06\u6765\uff0c\u56fd\u4e0e\u56fd\u4e4b\u95f4\uff0c\u7279\u522b\u662f\u5927\u56fd\u4e0e\u5927\u56fd\u4e4b\u95f4\uff0c\u80fd\u6e90\u4e0e\u5de5\u4e1a\u539f\u6599\u51b2\u7a81\u5c06\u65e5\u76ca\u660e\u663e\u3002\u9664\u4e86\u5341\u4e09\u4ebf\u4eba\u53e3\u7684\u5e02\u573a\u3001\u5ec9\u4ef7\u7684\u52b3\u52a8\u529b\u4e4b\u5916\uff0c\u9664\u4e86\u56fd\u5185\u5360\u636e\u5784\u65ad\u6027\u7684\u7a00\u571f\u53ca\u5176\u4ed6\u7a00\u6709\u91d1\u5c5e\u8d44\u6e90\u53ef\u4ee5\u4e3a\u5c06\u6765\u7684\u7ecf\u6d4e\u6469\u64e6\u63d0\u4f9b\u66f4\u591a\u3001\u66f4\u6709\u529b\u7684\u6597\u4e89\u624b\u8155\u4e4b\u5916\uff0c\u56fd\u5185\u5b9e\u9645\u4e0a\u518d\u6ca1\u6709\u4e00\u4ef6\u53ef\u80fd\u62ff\u51fa\u624b\u7684\u3001\u80fd\u591f\u4e0e\u5176\u4ed6\u5927\u56fd\u6297\u8861\u7684\u7ecf\u6d4e\u5229\u5668\u4e86\u2014\u2014\u5927\u56fd\u6218\u7565\u9700\u65e9\u7b79\u5212\uff01\u201d\u5f20\u606a\u8bf4\u5230\u8fd9\u91cc\u7a0d\u7f13\u4e86\u7f13\uff0c\u770b\u4e86\u674e\u8fdc\u6e56\u4e00\u773c\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u7279\u522b\u662f\u65e5\u3001\u97e9\u4e24\u56fd\u7a00\u571f\u5de5\u4e1a\u50a8\u5907\u6781\u5fae\uff0c\u6b27\u6d32\u5404\u56fd\u7684\u7a00\u571f\u5de5\u4e1a\u50a8\u5907\u4e5f\u5c11\uff0c\u5176\u9ad8\u65b0\u4ea7\u4e1a\u4f53\u7cfb\u5bf9\u7a00\u571f\u7684\u4f9d\u8d56\u6027\u53c8\u6781\u5f3a\uff0c\u63a7\u5236\u7a00\u571f\u5411\u65e5\u3001\u97e9\u7b49\u56fd\u7684\u51fa\u53e3\uff0c\u751a\u81f3\u53ef\u4ee5\u8feb\u4f7f\u65e5\u3001\u97e9\u7b49\u56fd\u4e0e\u7a00\u571f\u76f8\u5173\u7684\u9ad8\u65b0\u4ea7\u4e1a\u5411\u4e2d\u56fd\u8f6c\u79fb\uff0c\u4e2d\u56fd\u786e\u5b9e\u6ca1\u6709\u5fc5\u8981\u5728\u7a00\u571f\u4ea7\u4e1a\u7684\u5f00\u653e\u95ee\u9898\u592a\u8fc7\u6025\u5207\u4e86\u2026\u2026\u201d</p></article>"}}}]);
