(window.webpackJsonp=window.webpackJsonp||[]).push([[815],{838:function(p){p.exports={chapterTitle:null,title:"\u7b2c812\u7ae0 \u5510\u82f1\u57f9",content:"<article><h2>\u7b2c812\u7ae0 \u5510\u82f1\u57f9</h2><p>\u3000\u3000\u8c22\u5b50\u5609\u5634\u91cc\u5bf9\u5468\u747e\u73ba\u4ece\u6765\u90fd\u6ca1\u6709\u534a\u5206\u656c\u610f\uff0c\u53ea\u4e0d\u8fc7\u6709\u4e9b\u65f6\u5019\u5f53\u9762\u8981\u88c5\u4e56\u4e56\u5973\u3002</p><p>\u3000\u3000\u8c22\u610f\u542c\u4e86\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u96be\u602a\u5468\u747e\u73ba\u542c\u5230\u4ed6\u59bb\u5b50\u8ddf\u52a9\u6559\u5c06\u4e8b\u60c5\u6345\u5230\u5b66\u6821\u91cc\u53bb\u4f1a\u6709\u8fd9\u4e48\u5927\u7684\u53cd\u5e94\u3002\u867d\u7136\u8fd8\u4e0d\u6e05\u695a\u5f20\u606a\u7a76\u7adf\u56e0\u4e3a\u4ec0\u4e48\u4e8b\u60c5\u5728\u8bb2\u5802\u4e0a\u516c\u7136\u7f9e\u8fb1\u5468\u747e\u73ba\uff0c\u552f\u4e00\u80fd\u80af\u5b9a\u7684\uff0c\u5f20\u606a\u7edd\u4e0d\u53ef\u80fd\u5728\u6ca1\u6709\u6293\u4f4f\u5468\u747e\u73ba\u75db\u811a\u7684\u60c5\u51b5\u65e0\u7f18\u65e0\u6545\u7684\u5c31\u66b4\u8d77\u653b\u51fb\u2014\u2014\u4e8b\u60c5\u95f9\u5927\uff0c\u53ea\u662f\u5c06\u5468\u747e\u73ba\u7684\u75db\u811a\u66b4\u9732\u5230\u66f4\u591a\u4eba\u7684\u9762\u524d\u3002</p><p>\u3000\u3000\u8c22\u610f\u4e0d\u52a8\u58f0\u8272\u7684\u8d70\u56de\u75c5\u623f\uff0c\u5728\u533b\u751f\u3001\u62a4\u536b\u4e03\u624b\u516b\u811a\u7684\u5fd9\u788c\u4e0b\uff0c\u5468\u747e\u73ba\u7684\u60c5\u51b5\u6682\u65f6\u7a33\u5b9a\u4e0b\u6765\uff0c\u533b\u751f\u5429\u5490\u8fd9\u65f6\u5019\u8981\u5468\u747e\u73ba\u9700\u8981\u7edd\u5bf9\u7684\u9759\u517b\uff0c\u4e0d\u80fd\u518d\u53d7\u4ec0\u4e48\u523a\u6fc0\uff0c\u5df2\u7ecf\u6709\u8111\u6ea2\u8840\u521d\u53d1\u7684\u8ff9\u8c61\uff0c\u8840\u538b\u518d\u9661\u7136\u5347\u9ad8\uff0c\u7edd\u4e0d\u4f1a\u6709\u4ec0\u4e48\u597d\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u770b\u7740\u5468\u747e\u73ba\u670d\u836f\u7761\u53bb\uff0c\u8c22\u610f\u5c31\u544a\u8f9e\u79bb\u53bb\uff0c\u8c22\u5bb6\u65e9\u5df2\u4e0d\u662f\u8363\u8fb1\u4e0e\u5171\u7684\u4e00\u4e2a\u6574\u4f53\u4e86\uff0c\u8c22\u610f\u751a\u81f3\u6ca1\u6709\u95ee\u5728\u8bb2\u5802\u7a76\u7adf\u662f\u4e3a\u4ec0\u4e48\u800c\u8d77\u4e89\u6267\u2014\u2014\u4ed6\u60f3\u77e5\u9053\u4e8b\u60c5\u7684\u539f\u7531\u81ea\u6709\u5176\u4ed6\u7684\u9014\u5f84\uff0c\u5728\u5468\u747e\u73ba\u9762\u524d\u5b81\u53ef\u59cb\u7ec8\u88c5\u4f5c\u7cca\u6d82\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e0e\u674e\u8fdc\u6e56\u5206\u5f00\u4e4b\u540e\uff0c\u5f20\u606a\u94bb\u8fdb\u8f66\u91cc\uff0c\u653e\u677e\u7684\u540e\u6291\u7740\u9760\u5728\u5ea7\u6905\u4e0a\uff0c\u624b\u6367\u7740\u809a\u5b50\uff0c\u611f\u6168\u7684\u8bf4\u9053\uff1a\u201c\u539f\u6765\u8fde\u7740\u5403\u4e24\u6b21\u665a\u996d\uff0c\u611f\u89c9\u5e76\u4e0d\u597d\u53d7\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5728\u8fc7\u6765\u4e4b\u540e\uff0c\u5c31\u966a\u675c\u98de\u4ed6\u4eec\u4e00\u8d77\u5728\u6559\u5de5\u98df\u5802\u5403\u8fc7\u665a\u996d\u4e86\uff0c\u8fd9\u65f6\u5019\u53c8\u8ddf\u674e\u8fdc\u6e56\u4e00\u8d77\u8868\u73b0\u5f97\u4e1d\u6beb\u6ca1\u6709\u52c9\u5f3a\u7684\u6a21\u6837\u518d\u5ea6\u7528\u9910\u5403\u6491\u7740\u4e86\u3002</p><p>\u3000\u3000\u201c\u81ea\u4f5c\u81ea\u53d7\uff0c\u53ef\u6ca1\u6709\u4eba\u4f1a\u540c\u60c5\u4f60\u3002\u201d\u7fdf\u4e39\u9752\u5ae3\u7136\u7b11\u7740\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5085\u4fca\u5750\u5728\u526f\u9a7e\u9a76\u4f4d\u63a5\u7535\u8bdd\uff0c\u63a5\u8fc7\u7535\u8bdd\uff0c\u8f6c\u8fc7\u5934\u6765\u544a\u8bc9\u5f20\u606a\uff1a\u201c\u90a3\u8f86\u608d\u9a6c\u7684\u8f66\u4e3b\u60c5\u51b5\u67e5\u5230\u4e00\u4e9b\uff0c\u8f66\u5b50\u5f52\u91d1\u5c71\u534e\u7a00\u6709\u9650\u516c\u53f8\u540d\u4e0b\u6240\u6709\u3002\u8fd9\u5bb6\u516c\u53f8\u5957\u7740\u7a00\u571f\u65b0\u6750\u6599\u5f00\u53d1\u7684\u540d\u5934\uff0c\u5176\u5b9e\u662f\u5bb6\u79c1\u8425\u7684\u8d38\u6613\u516c\u53f8\u2026\u2026\u534e\u7a00\u7684\u8001\u603b\u53eb\u5510\u82f1\u57f9\uff0c\u5510\u82f1\u57f9\u662f\u90a3\u8f86\u608d\u9a6c\u8f66\u7684\u8f66\u4e3b\uff0c\u4ed6\u662f\u7ae0\u5dde\u4eba\uff0c\u636e\u8bf4\u662f\u5728\u7ae0\u5dde\u53d1\u5bb6\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u8d38\u6613\u516c\u53f8\uff0c\u4f1a\u4e0d\u4f1a\u662f\u7ae0\u5dde\u76d7\u91c7\u7a00\u571f\u77ff\u6d41\u5411\u5e02\u573a\u6e20\u9053\u4e4b\u4e00\uff1f\u201d\u7fdf\u4e39\u9752\u95ee\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u8d77\u90a3\u4e2a\u603b\u89c9\u5f97\u6709\u4e9b\u9762\u719f\u7684\u4e2d\u5e74\u7537\u5b50\uff0c\u539f\u6765\u4e5f\u662f\u7ae0\u5dde\u5510\u5bb6\u7684\uff0c\u8ddf\u6628\u5929\u7ea0\u7f20\u4f55\u5f26\u7684\u90a3\u4e2a\u5510\u5fe0\u5e94\u8be5\u6709\u7740\u8840\u7f18\u5173\u7cfb\uff0c\u957f\u7684\u6709\u4e9b\u50cf\uff0c\u90fd\u662f\u72ed\u957f\u7684\u8138\u5f62\uff0c\u770b\u4e0a\u53bb\u6709\u4e9b\u9634\u67d4\u2014\u2014\u8fd9\u624d\u4f1a\u89c9\u5f97\u90a3\u4e2a\u4e2d\u5e74\u7537\u5b50\u8138\u719f\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u53bb\u89c1\u674e\u8fdc\u6e56\u4e4b\u540e\uff0c\u5c31\u8ba9\u5085\u4fca\u53bb\u67e5\u608d\u9a6c\u8f66\u4e3b\u7684\u5e95\u7ec6\u3002\u9664\u975e\u50cf\u5f20\u606a\u7279\u610f\u7684\u53bb\u63a9\u9970\uff0c\u4e0d\u7136\u67e5\u4e00\u90e8\u91d1\u5c71\u8f66\u724c\u7684\u608d\u9a6c\u8f66\u4e3b\u8fd8\u662f\u76f8\u5f53\u7684\u7b80\u5355\u2014\u2014\u608d\u9a6c\u8fd9\u79cd\u8f66\u5728\u6c5f\u5357\u7701\u5c31\u6ca1\u6709\u51e0\u8f86\uff0c\u7a0d\u4e00\u6253\u542c\u5c31\u80fd\u77e5\u9053\u8fd9\u90e8\u608d\u9a6c\u8f66\u4e3b\u7684\u8eab\u4efd\u3002</p><p>\u3000\u3000\u770b\u6765\u7ae0\u5dde\u5510\u5bb6\u5df2\u7ecf\u5f62\u6210\u76d7\u91c7\u3001\u51b6\u70bc\u3001\u9500\u552e\u4e09\u8005\u4e8e\u4e00\u4f53\u7684\u7f51\u7edc\u4e86\uff0c\u5510\u82f1\u57f9\u638c\u63e1\u7740\u8fd9\u4e2a\u7f51\u7edc\u7684\u5bf9\u5916\u9500\u552e\u53e3\u5b50\uff0c\u5e94\u8be5\u662f\u7ae0\u5dde\u5510\u5bb6\u7684\u91cd\u8981\u4eba\u7269\u3002</p><p>\u3000\u3000\u201c\u8981\u5f7b\u5e95\u6253\u65ad\u5468\u747e\u73ba\u7684\u7259\u9f7f\uff0c\u4f3c\u4e4e\u53ef\u4ee5\u662f\u8fd9\u4e2a\u5510\u82f1\u57f9\u5165\u624b\u2026\u2026\u201d\u7fdf\u4e39\u9752\u53c8\u8bf4\u9053\uff0c\u201c\u5468\u747e\u73ba\u53d7\u56fd\u52a1\u9662\u7a00\u571f\u529e\u7684\u59d4\u6258\u7814\u7a76\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u7684\u540c\u65f6\u53c8\u4e0e\u534e\u7a00\u7684\u4eba\u8fc7\u5f80\u4ece\u5bc6\uff0c\u4ed6\u600e\u4e48\u4e5f\u6d17\u4e0d\u5e72\u51c0\u81ea\u5df1\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e5f\u53ea\u80fd\u8ba9\u4ed6\u63d0\u524d\u9000\u4f11\u800c\u5df2\uff0c\u201d\u5f20\u606a\u5fae\u5fae\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u5f53\u7136\u8981\u505a\u5230\u8fd9\u70b9\uff0c\u4e5f\u4e0d\u90a3\u4e48\u5bb9\u6613\uff0c\u59d3\u5510\u7684\u5728\u7ae0\u5dde\u7ecf\u8425\u591a\u5e74\uff0c\u6211\u4eec\u624b\u91cc\u53c8\u6ca1\u6709\u534e\u7a00\u8d29\u5356\u7684\u5c31\u662f\u76d7\u91c7\u7684\u7a00\u571f\u77ff\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u5f53\u683d\u8d43\u597d\u4e86\uff0c\u968f\u4fbf\u627e\u4e00\u5bb6\u5a92\u4f53\u5c06\u8fd9\u4e8b\u6345\u51fa\u53bb\uff0c\u5468\u747e\u73ba\u4ed6\u81ea\u5df1\u90fd\u767e\u53e3\u83ab\u8fa9\u2026\u2026\u5373\u4f7f\u6ca1\u6709\u771f\u51ed\u5b9e\u636e\uff0c\u4e0a\u9762\u7684\u4eba\u96be\u9053\u773c\u775b\u771f\u662f\u778e\u4e86\uff0c\u5bf9\u7ae0\u5dde\u7a00\u571f\u8d44\u6e90\u7684\u76d7\u91c7\u73b0\u72b6\u4e00\u70b9\u90fd\u4e0d\u4e86\u89e3\uff1f\u201d\u7fdf\u4e39\u9752\u5f88\u5947\u602a\u5f20\u606a\u7684\u6001\u5ea6\uff0c\u201c\u8fd8\u662f\u8bf4\u4f60\u6839\u672c\u5c31\u60f3\u5c06\u7ae0\u5dde\u76d7\u91c7\u56e2\u4f19\u4e00\u7a9d\u7ed9\u7aef\u4e86\uff0c\u6240\u4ee5\u624d\u9700\u8981\u786e\u51ff\u7684\u8bc1\u636e\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\u5f97\u7f6a\u4e86\u4f60\u4e5f\u771f\u662f\u5012\u9709\uff0c\u201d\u5f20\u606a\u671d\u7740\u7fdf\u4e39\u9752\u54c8\u54c8\u4e00\u7b11\uff0c\u4e5f\u4e0d\u53bb\u6b63\u9762\u56de\u7b54\u5979\u7684\u95ee\u9898\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4ece\u5510\u82f1\u57f9\u8fd9\u6761\u7ebf\u6316\u4e0b\u53bb\u4e5f\u662f\u4e00\u6761\u8def\uff0c\u6211\u4eec\u73b0\u5728\u8981\u591a\u7ebf\u5e76\u8fdb\u2026\u2026\u201d\u9664\u4e86\u5510\u82f1\u57f9\u4e4b\u5916\uff0c\u4e2d\u5348\u5728\u6559\u5de5\u98df\u5802\u966a\u5468\u747e\u73ba\u7528\u9910\u7684\u8fd8\u6709\u53e6\u5916\u4e24\u4e2a\u4e2d\u5e74\u4eba\uff0c\u9664\u4e86\u608d\u9a6c\u8f66\u5916\uff0c\u8fd8\u6709\u6302\u7740\u91d1\u5c71\u8f66\u724c\u7684\u5954\u9a70\uff0c\u8fd9\u79cd\u5229\u76ca\u94fe\u7684\u94c1\u4e09\u89d2\u901a\u5e38\u8fd8\u8981\u5305\u62ec\u5403\u91cc\u6252\u5916\u7684\u653f\u5e9c\u5b98\u5458\u3002</p><p>\u3000\u3000\u8f66\u5230\u5b66\u5e9c\u5df7\u7684\u5df7\u5b50\u53e3\uff0c\u5f20\u606a\u5148\u4e0b\u4e86\u8f66\uff0c\u4ed6\u501f\u53e3\u809a\u5b50\u5403\u4e86\u592a\u6491\u9700\u8981\u4e0b\u6765\u8d70\u52a8\u8d70\u52a8\uff0c\u7fdf\u4e39\u9752\u731c\u4ed6\u8981\u53bb\u627e\u8c22\u665a\u6674\uff0c\u4e5f\u6ca1\u6709\u6233\u7a7f\u4ed6\uff0c\u5fc3\u91cc\u5374\u6709\u4e9b\u5f02\u6837\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u6628\u5929\u591c\u91cc\u7325\u4eb5\u8fc7\u7fdf\u4e39\u9752\u4e4b\u540e\uff0c\u5f20\u606a\u518d\u770b\u5230\u5979\u5fc3\u91cc\u603b\u6709\u4e9b\u53d1\u865a\uff0c\u5148\u4e0b\u8f66\u4e5f\u662f\u907f\u514d\u56de\u516c\u5bd3\u4e0e\u7fdf\u4e39\u9752\u5355\u72ec\u76f8\u5904\u2014\u2014\u4e0d\u7136\u603b\u8981\u804a\u5de5\u4f5c\u5230\u5f88\u665a\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u4e8b\u559c\u6b22\u5728\u5b66\u5e9c\u5df7\u95f2\u901b\uff0c\u8fd9\u65f6\u7684\u591c\u8272\u8fd8\u4e0d\u6df1\uff0c\u6708\u6717\u661f\u7a00\uff0c\u8def\u706f\u5149\u4ea4\u9519\u8f89\u6620\u3002\u8fd9\u65f6\u5019\u8fd8\u5728\u5b66\u5e9c\u5df7\u6d41\u8fde\u5f80\u8fd4\u7684\u5b66\u751f\u5f88\u591a\uff0c\u80fd\u770b\u5230\u6bd4\u767d\u5929\u66f4\u591a\u957f\u76f8\u5a07\u7f8e\u7684\u5973\u5b69\u5b50\uff0c\u6216\u8bb8\u90a3\u4e9b\u5973\u5b69\u5b50\u5bb9\u8c8c\u4e0a\u7684\u67d0\u4e9b\u7f3a\u9677\u5728\u591c\u8272\u4f1a\u53d8\u5f97\u4e0d\u518d\u5206\u660e\u3001\u523a\u773c\u2014\u2014\u9152\u3001\u591c\u8272\u4ee5\u53ca\u60c5\u6b32\u751a\u81f3\u4f1a\u8ba9\u5973\u5b69\u5b50\u5bb9\u8c8c\u4e0a\u7684\u67d0\u4e9b\u7f3a\u9677\u53d8\u5f97\u53ef\u7231\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6015\u5728\u665a\u6674\u9762\u524d\u9732\u4e86\u9a6c\u811a\uff0c\u72b9\u8c6b\u7740\u8981\u4e0d\u8981\u9a6c\u4e0a\u53bb\u627e\u5979\uff0c\u4ed6\u5750\u5728\u5b66\u5e9c\u5df7\u5916\u8857\u7684\u8857\u5fc3\u94c1\u827a\u957f\u51f3\u4e0a\u8003\u8651\u8fd9\u4e2a\u95ee\u9898\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u5510\u603b\u60f3\u8bf7\u4f60\u8fc7\u53bb\u804a\u4e00\u804a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u662f\u4eca\u5929\u51fa\u73b0\u5728\u90a3\u4e2a\u4e2d\u5e74\u7537\u4eba\u2014\u2014\u4e5f\u5c31\u662f\u534e\u7a00\u516c\u53f8\u5510\u82f1\u57f9\u8eab\u8fb9\u7684\u4eba\uff0c\u4e09\u5341\u591a\u5c81\uff0c\u4eba\u9ad8\u9a6c\u5927\u7684\u3002\u90a3\u4eba\u677f\u7740\u8138\uff0c\u76ee\u5149\u5f88\u51b7\uff0c\u7ad9\u5728\u94c1\u827a\u957f\u6905\u80cc\u540e\u62cd\u5f20\u606a\u7684\u80a9\u8180\uff0c\u8981\u5f20\u606a\u8ddf\u7740\u4ed6\u8d70\u4e00\u8d9f\uff0c\u8bed\u6c14\u6709\u4e9b\u4e0d\u5584\u3002</p><p>\u3000\u3000\u90a3\u8f86\u608d\u9a6c\u8f66\u5c31\u505c\u5728\u4e0d\u8fdc\u5904\u7684\u8def\u4fa7\uff0c\u53f8\u673a\u7684\u5ea7\u4f4d\u4e0a\u7a7a\u7740\u2014\u2014\u5f20\u606a\u5fc3\u60f3\u8eab\u540e\u8fd9\u4eba\u662f\u5510\u82f1\u57f9\u7684\u53f8\u673a\uff0c\u6291\u6216\u517c\u7740\u4fdd\u9556\u2014\u2014\u526f\u9a7e\u9a76\u4f4d\u4e0a\u8fd8\u5750\u7740\u4e00\u4eba\uff0c\u770b\u4e0d\u6e05\u695a\u8138\uff0c\u608d\u9a6c\u8f66\u540e\u9762\u7684\u8f66\u7a97\u6253\u5f00\u4e00\u534a\uff0c\u5510\u82f1\u57f9\u9732\u51fa\u534a\u5f20\u8138\u770b\u5411\u8fd9\u8fb9\uff0c\u5728\u8def\u706f\u5149\u4e0b\u7684\u8138\u8272\u9634\u67d4\u3002</p><p>\u3000\u3000\u5f20\u606a\u772f\u8d77\u773c\u775b\u770b\u7740\u5750\u5728\u8f66\u91cc\u7684\u5510\u82f1\u57f9\uff0c\u770b\u7740\u4ed6\u53c8\u8f6c\u8fc7\u8138\u53bb\uff0c\u8fd9\u65f6\u5019\u786e\u8ba4\u4ed6\u5e76\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u771f\u5b9e\u8eab\u4efd\uff0c\u60f3\u8d77\u4ed6\u4eca\u5929\u5728\u6559\u5ba4\u91cc\u7740\u5e26\u5a01\u80c1\u610f\u5473\u7684\u8bdd\uff0c\u5927\u6982\u662f\u5f00\u8f66\u7ecf\u8fc7\u6b64\u65f6\u770b\u5230\u4e0d\u8bc6\u62ac\u4e3e\u7684\u81ea\u5df1\u5750\u5728\u8fd9\u91cc\uff0c\u624d\u4e34\u65f6\u8d77\u610f\u505c\u4e0b\u8f66\u6765\u627e\u4e8b\u6765\u7684\u5427\uff1f\u56e0\u4e3a\u5728\u8bb2\u5802\u4e0a\u201c\u6363\u4e71\u201d\u7684\u7f18\u6545\uff0c\u800c\u8981\u5c06\u81ea\u5df1\u53eb\u8fc7\u53bb\u201c\u804a\u4e00\u804a\u201d\uff0c\u603b\u4e0d\u8be5\u6709\u4ec0\u4e48\u597d\u4e8b\u53d1\u751f\u3002\u4e5f\u771f\u96be\u4e3a\u4ed6\u4e86\uff0c\u90fd\u56db\u5341\u597d\u51e0\u7684\u4e2d\u5e74\u4eba\u4e86\uff0c\u8fd8\u662f\u8fd9\u79cd\u6c89\u4e0d\u4f4f\u6c14\u60f3\u60f9\u7978\u7684\u6027\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u7709\u5934\u5fae\u5fae\u76b1\u4e86\u76b1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u8ba4\u8bc6\u4f60\u4eec\u5510\u603b\uff0c\u5982\u679c\u4ed6\u60f3\u804a\u5929\uff0c\u5982\u679c\u4ed6\u8fd8\u77e5\u9053\u4e9b\u793c\u8c8c\uff0c\u8bf7\u4ed6\u4e0b\u8f66\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u5510\u603b\u597d\u5fc3\u8bf7\u4f60\uff0c\u4f60\u4e0d\u8981\u7cdf\u8e4b\u4e86\u4ed6\u7684\u597d\u610f\uff01\u201d\u5510\u82f1\u57f9\u7684\u53f8\u673a\u538b\u7740\u55d3\u5b50\uff0c\u8eab\u5b50\u503e\u8fc7\u6765\uff0c\u633d\u8d77\u8896\u5b50\uff0c\u9732\u51fa\u624b\u81c2\u4e0a\u7684\u4e00\u6bb5\u523a\u9752\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u771f\u62c5\u5fc3\u5510\u82f1\u57f9\u4f1a\u627e\u9000\u4f0d\u7684\u7279\u79cd\u519b\u4eba\u7ed9\u81ea\u5df1\u5f53\u53f8\u673a\uff0c\u89c1\u8fd9\u5bb6\u4f19\u9732\u51fa\u624b\u81c2\u4e0a\u7684\u523a\u9752\uff0c\u77e5\u9053\u591a\u534a\u662f\u8ddf\u7740\u5510\u5bb6\u4e00\u8d77\u6df7\u51fa\u9053\u7684\u9752\u76ae\u6d41\u6c13\uff0c\u771f\u5f53\u81ea\u5df1\u662f\u666e\u901a\u7684\u5927\u5b66\u751f\u597d\u552c\u5f04\uff0c\u7709\u5934\u4e00\u626c\uff0c\u51b7\u773c\u76ef\u7740\u4ed6\uff1a\u201c\u4f60\u4eec\u60f3\u505a\u4ec0\u4e48\uff1f\u5728\u8fd9\u91cc\uff0c\u4f60\u4eec\u6562\u505a\u4ec0\u4e48\uff1f\u201d\u4e22\u4e0b\u4e00\u53e5\u8bdd\uff0c\u7ad9\u8d77\u6765\u8eab\u8d70\u5f00\u3002</p><p>\u3000\u3000\u8fd9\u91cc\u8f66\u4e0a\u53c8\u4e0b\u6765\u4e00\u4e2a\u4eba\uff0c\u4e09\u5341\u5c81\u5de6\u53f3\uff0c\u770b\u6837\u5b50\u4e5f\u662f\u5510\u82f1\u57f9\u7684\u4e0b\u5c5e\uff0c\u4e0e\u5510\u82f1\u57f9\u7684\u53f8\u673a\u8ddf\u5728\u5f20\u606a\u7684\u540e\u9762\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u4e5f\u660e\u767d\u5728\u4eba\u6765\u4eba\u5f80\u7684\u5b66\u5e9c\u5df7\u7ed9\u524d\u9762\u90a3\u5c0f\u5b50\u4e00\u4e9b\u6559\u8bad\u4e5f\u6709\u4e9b\u62db\u773c\u4e86\uff0c\u60f3\u8ddf\u7740\u5f20\u606a\u540e\u9762\u8d70\u5230\u4eba\u5c11\u7684\u5730\u65b9\u731b\u7684\u6765\u4e24\u4e0b\u5c31\u8131\u8eab\u8dd1\u5f00\u3002\u5510\u82f1\u57f9\u4e5f\u4e0d\u60f3\u9519\u8fc7\u770b\u597d\u620f\u7684\u673a\u4f1a\uff0c\u867d\u7136\u4e0d\u4f1a\u4eb2\u81ea\u52a8\u624b\uff0c\u4f46\u4e5f\u8fdc\u8fdc\u7f00\u5728\u540e\u9762\u770b\u620f\u3002</p><p>\u3000\u3000\u8d70\u5230\u9760\u8fd1\u9752\u5e74\u516c\u5bd3\u5165\u53e3\u7684\u5730\u65b9\uff0c\u8fd9\u6bb5\u8def\u884c\u4eba\u5f88\u5c11\uff0c\u8def\u706f\u95f4\u9694\u6bd4\u8f83\u7a00\uff0c\u540e\u9762\u7684\u56db\u680b\u516c\u5bd3\u697c\u5927\u591a\u6ca1\u6709\u542f\u7528\uff0c\u663e\u5f97\u8fd9\u6bb5\u8def\u6709\u4e9b\u6697\uff0c\u5f20\u606a\u505c\u4e0b\u811a\u6b65\uff0c\u8f6c\u8eab\u770b\u7740\u8ddf\u7740\u540e\u9762\u7684\u4e24\u4e2a\u5bb6\u4f19\uff1b\u201c\u4f60\u4eec\u7a76\u7adf\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ea\u662f\u8ba9\u4f60\u77e5\u9053\u6709\u4e9b\u8bdd\u662f\u4e0d\u80fd\u4e71\u8bf4\u7684\uff0c\u4eca\u5929\u8981\u8ba9\u4f60\u957f\u4e9b\u8bb0\u6027\u2026\u2026\u201d\u5510\u82f1\u57f9\u7684\u53f8\u673a\u538b\u7740\u58f0\u97f3\uff0c\u50cf\u732b\u770b\u9f20\u4f3c\u7684\u76ef\u7740\u5f20\u606a\uff0c\u5c06\u5916\u5957\u8131\u4e0b\uff0c\u9732\u51fa\u90fd\u662f\u523a\u9752\u7684\u80f3\u818a\uff0c\u4ed6\u770b\u5230\u4fdd\u5b89\u5ba4\u6709\u4eba\u63a2\u51fa\u5934\u5f80\u5916\u770b\uff0c\u4ed6\u8fd8\u4ee5\u4e3a\u8fd9\u4e9b\u5c0f\u533a\u7684\u4fdd\u5b89\u90fd\u662f\u6ca1\u7528\u7684\u8f6f\u86cb\uff0c\u518d\u8bf4\u53c8\u4e0d\u662f\u5728\u5c0f\u533a\u91cc\u9762\uff0c\u671d\u4fdd\u5b89\u5ba4\u77aa\u4e86\u4e00\u773c\uff1a\u201c\u770b\u4ec0\u4e48\u770b\uff0c\u6ca1\u89c1\u8fc7\u9ed1\u793e\u4f1a\u6253\u4eba\u554a\uff1f\u201d\u4ed6\u8eab\u8fb9\u7684\u540c\u4f34\u6361\u8d77\u538b\u9053\u77f3\uff0c\u770b\u4e24\u540d\u4fdd\u536b\u4ece\u4fdd\u5b89\u5ba4\u8d70\u51fa\u6765\uff0c\u671d\u4ed6\u4eec\u626c\u4e86\u626c\uff0c\u8fd9\u65f6\u5019\u9aa4\u7136\u89c9\u5f97\u817f\u7a9d\u7a9d\u5904\u4e00\u9635\u5267\u75db\uff0c\u8eab\u5b50\u63a7\u5236\u4e0d\u4f4f\u7684\u524d\u8dcc\uff0c\u4f46\u662f\u5728\u4ed6\u8dcc\u5012\u4e4b\u524d\uff0c\u62ff\u538b\u9053\u77f3\u7684\u80a9\u7a9d\u5904\u53c8\u5403\u4e86\u4e00\u62f3\uff0c\u611f\u89c9\u80a9\u7a9d\u7ed9\u8fd9\u4e00\u62f3\u51e0\u4e4e\u6253\u788e\u6389\u3002\u5510\u82f1\u57f9\u7684\u53f8\u673a\u4e0d\u660e\u767d\u4e3a\u4ec0\u4e48\u4f1a\u4ece\u540e\u9762\u7a81\u7136\u95ea\u51fa\u6765\u4e00\u4e2a\u4eba\u4e09\u62f3\u4e24\u817f\u5c31\u5c06\u4ed6\u7684\u540c\u4f19\u6253\u8db4\u4e0b\uff0c\u6ca1\u7b49\u4ed6\u6709\u4ec0\u4e48\u53cd\u5e94\uff0c\u7855\u5927\u7684\u62f3\u5934\u5c31\u76f4\u5954\u4ed6\u7684\u9f3b\u6881\u7838\u6765\u2026\u2026\u4e24\u540d\u4fdd\u536b\u4e5f\u90fd\u4e0a\u524d\u5e2e\u5fd9\uff0c\u5c06\u4e24\u4e2a\u5bb6\u4f19\u4e00\u628a\u63ea\u4f4f\uff0c\u4e0d\u8ba9\u4ed6\u4eec\u52a8\u5f39\u3002</p><p>\u3000\u3000\u5510\u82f1\u57f9\u8fdc\u8fdc\u7684\u770b\u7740\uff0c\u54ea\u91cc\u60f3\u5230\u4e8b\u60c5\u4f1a\u53d8\u6210\u8fd9\u6837\uff0c\u770b\u7740\u51fa\u624b\u679c\u65ad\u7684\u6253\u4eba\u8005\u671d\u4ed6\u8fd9\u8fb9\u770b\u8fc7\u6765\uff0c\u4ed6\u5fc3\u865a\u7684\u8f6c\u8fc7\u8eab\u5f80\u56de\u8d70\uff0c\u8fb9\u8d70\u8fb9\u56de\u5934\u770b\u90a3\u4eba\u6709\u6ca1\u6709\u8ffd\u8fc7\u6765\uff0c\u4e5f\u987e\u4e0d\u4e0a\u7ba1\u90a3\u4e24\u4e2a\u7ed9\u4eba\u5bb6\u4e09\u62f3\u4e24\u817f\u6253\u8db4\u4e0b\u6765\u3001\u53c8\u7ed9\u6293\u4f4f\u7684\u4e0b\u5c5e\uff0c\u597d\u5728\u6ca1\u4eba\u8ffd\u4e0a\u6765\uff0c\u8d70\u5230\u4eba\u591a\u70ed\u95f9\u7684\u5b66\u5e9c\u5df7\uff0c\u4ed6\u624d\u7a0d\u7a0d\u5b89\u5fc3\uff0c\u5fc3\u91cc\u5374\u7eb3\u95f7\uff1a\u90a3\u4e2a\u5bb6\u4f19\u8eab\u624b\u8fd9\u4e48\u597d\uff0c\u600e\u4e48\u4f1a\u5c31\u7a81\u7136\u5192\u51fa\u6765\u4e86\uff1f\u8fd9\u65f6\u5019\u4ed6\u53c8\u5173\u5fc3\u8d77\u4ed6\u90a3\u4e24\u4e2a\u4e0b\u5c5e\u6765\uff0c\u6700\u6070\u5f53\u7684\u505a\u6cd5\u5c31\u662f\u5148\u62a5\u8b66\uff0c\u514d\u5f97\u4e24\u4e2a\u4e0b\u5c5e\u5728\u8fd9\u4eba\u751f\u5730\u4e0d\u719f\u7684\u5730\u65b9\u7ed9\u4eba\u5bb6\u72e0\u72e0\u6253\u4e86\u4e00\u987f\u518d\u9001\u5230\u6d3e\u51fa\u6240\u53bb\u3002</p><p>\u3000\u3000\u4ed6\u638f\u624b\u673a\u65f6\u53d1\u73b0\u624b\u673a\u5fd8\u8f66\u91cc\u4e86\uff0c\u8d70\u5230\u4ed6\u608d\u9a6c\u8f66\u524d\uff0c\u90a3\u91cc\u5df2\u7ecf\u6709\u4e24\u4e2a\u7a7f\u7740\u5236\u670d\u7684\u8b66\u5bdf\u5728\u7b49\u4ed6\u4e86\uff0c\u4e0d\u7531\u4ed6\u5206\u8bf4\uff0c\u76f4\u63a5\u4e00\u5de6\u4e00\u53f3\u5c06\u4ed6\u6441\u7ffb\u5728\u5730\u4e0a\u62ff\u624b\u94d0\u80cc\u94d0\u8d77\u6765\u3002</p></article>"}}}]);
