(window.webpackJsonp=window.webpackJsonp||[]).push([[817],{840:function(p){p.exports={chapterTitle:null,title:"\u7b2c646\u7ae0 \u79cb\u5b63\u653b\u52bf",content:"<article><h2>\u7b2c646\u7ae0 \u79cb\u5b63\u653b\u52bf</h2><p>\u3000\u3000\u98ce\u8d77\u9752\u840d\u4e4b\u672b\uff0c\u4fbf\u662f\u7ecf\u5e38\u767b\u5f55\u6606\u817e\u8d44\u8baf\u7684\u7f51\u6c11\u4e5f\u5f88\u5c11\u6ce8\u610f\u5230\u8d44\u8baf\u4e3b\u9875\u53f3\u4e0b\u89d2\u572810\u67082\u65e5\u8fd9\u5929\u63d0\u4f9b\u7231\u8fbei19\u624b\u673a\u7684\u5e7f\u544a\u89c6\u9891\u4e0b\u8f7d\u3002</p><p>\u3000\u300010\u67083\u65e5\uff0c\u6606\u817e\u8d44\u8baf\u589e\u8bbe\u6d88\u8d39\u7535\u5b50\u8d44\u8baf\u5b50\u680f\u76ee\uff0ci19\u624b\u673a\u6d4b\u8bc4\u62a5\u544a\u5373\u4e3a\u6d88\u8d39\u7535\u5b50\u8d44\u8baf\u7f51\u9875\u53f3\u4e0a\u53f3\u5c01\u9762\u63a8\u8350\u9605\u8bfb\u7684\u9192\u76ee\u4f4d\u7f6e\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u6606\u817e\u5728\u7ebf\u6240\u6709\u7684\u4e3b\u6863\u76ee\u7f51\u9875\u90fd\u63d2\u5165i19\u6781\u84dd\u97f3\u8272\u9b45\u60d1\u7684\u9759\u6001\u5e7f\u544a\u3002</p><p>\u3000\u3000\u753b\u9762\u4e0a\uff0c\u6781\u81f4\u9b45\u529b\u7684i19\u6c5f\u9edb\u513f\u6676\u83b9\u5254\u900f\u7684\u6307\u5c16\u65cb\u8f6c\uff0c\u6c5f\u9edb\u513f\u60ca\u7f9e\u602f\u559c\u7684\u51dd\u89c6\u7740\u6cdb\u7740\u91d1\u5c5e\u5149\u6cfd\u7684\u84dd\u5c4fi19\u3002</p><p>\u3000\u3000\u8fd9\u6837\u7684\u9759\u6b62\u753b\u9762\u540c\u6837\u520a\u767b\u4e8e\u56fd\u5185\u4e3b\u8981\u9ad8\u7aef\u6742\u5fd7\u5a92\u4f53\u7684\u9192\u76ee\u4f4d\u7f6e\u4e0a\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u914d\u5408i19\u7684\u63a8\u51fa\uff0c\u76db\u946b\u73af\u7403\u7535\u5668\u65d7\u4e0b\u7684\u5efa\u90ba\u65b0\u5e02\u8857\u65d7\u8230\u5e97\u4e5f\u62d6\u5ef6\u4e8e10\u67083\u65e5\u3001\u56fd\u5e86\u8282\u4e4b\u540e\u7684\u7b2c\u4e00\u4e2a\u5468\u672b\u624d\u9686\u91cd\u63a8\u51fa\u624b\u673a\u4e13\u5356\u4e1a\u52a1\u3002</p><p>\u3000\u3000\u9664\u4e86\u76db\u946b\u5728\u5efa\u90ba\u5404\u7c7b\u5a92\u4f53\u4e3a\u5176\u63a8\u51fa\u624b\u673a\u4e13\u67dc\u6240\u5927\u529b\u558a\u51fa\u7684\u5406\u559d\u4e4b\u5916\uff0c\u76db\u946b\u7684\u65b0\u5e02\u8857\u65d7\u8230\u5e97\u5185\u51e0\u4e4e\u60ac\u6302\u6ee1i19\u3001i08\u7684\u5e7f\u544a\u5f69\u65d7\uff0c\u5f69\u7535\u533a\u90fd\u7edf\u4e00\u64ad\u653ei19\u7684\u6d77\u6ee9\u5e7f\u544a\u89c6\u9891\uff0c\u4eff\u4f5b\u6574\u4e2a\u5e97\u5802\u4e4b\u5185\u90fd\u662f\u6c5f\u9edb\u513f\u8ff7\u4eba\u7684\u7b11\u9765\u4e0ei19\u7edd\u9b45\u7684\u8eab\u5f62\uff1b\u62ff\u90b5\u5fc3\u6587\u7684\u8bdd\u8bf4\uff0c\u4ed6\u5c06\u76db\u946b\u516c\u53f8\u6700\u5177\u59ff\u8272\u7684\u4e24\u4e2a\u5973\u5b69\u5b50\u6307\u6d3e\u5230i19\u3001i08\u624b\u673a\u4e13\u533a\u5f53\u82e6\u529b\u4e86\u3002</p><p>\u3000\u3000\u90b5\u5fc3\u6587\u5728\u7535\u8bdd\u8ddf\u5f20\u606a\u9080\u529f\uff0c\u5f20\u606a\u56de\u9053\uff1a\u201c\u884c\uff0c\u4f60\u4eec\u7684\u8f9b\u82e6\uff0c\u6211\u7684\u660e\u767d\uff0c\u6211\u56de\u5efa\u90ba\u5355\u72ec\u8bf7\u90a3\u4e24\u4e2a\u5973\u5b69\u5b50\u5403\u996d\u2026\u2026\u201d</p><p>\u3000\u3000\u90b5\u5fc3\u6587\u8840\u6c14\u5728\u80f8\u53e3\u7ffb\u6eda\u4e86\u4e00\u4f1a\u513f\uff0c\u5f53\u771f\u4e0d\u6562\u62ff\u76db\u946b\u4e24\u679d\u9c9c\u82b1\u53bb\u586b\u5f20\u606a\u7684\u864e\u53e3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5efa\u90ba\u65b0\u5e02\u8857\u65d7\u8230\u5e97\u5f00\u8bbe\u5927\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u8425\u4e1a\u989d\u7834\u5929\u91cf\u7684\u7a81\u7834\u5341\u4e8c\u4ebf\uff0c\u5168\u5e74\u6709\u671b\u7a81\u7834\u4e8c\u5341\u4ebf\uff0c\u8fd9\u662f\u4e4b\u524d\u4ee4\u4eba\u65e0\u6cd5\u60f3\u8c61\u7684\u4e00\u4e2a\u6570\u636e\uff0c\u4ee4\u76db\u946b\u4e0a\u4e0b\u58eb\u6c14\u5927\u6da8\u3002</p><p>\u3000\u3000\u9664\u4e86\u6269\u5f20\u4e0a\u6d77\u4e2d\u5c71\u516c\u56ed\u95e8\u5e97\u3001\u676d\u5dde\u89e3\u653e\u8def\u95e8\u5e97\u7684\u89c4\u6a21\u6539\u9020\u6210\u5927\u578b\u65d7\u8230\u5e97\u4e4b\u5916\uff0c\u8fd8\u5728\u4e03\u6708\u3001\u516b\u6708\u8fde\u7eed\u5728\u6210\u90fd\u6625\u7199\u8def\u3001\u5317\u4eac\u897f\u5355\u7838\u4e0b\u91cd\u91d1\u5f00\u8bbe\u65d7\u8230\u5e97\uff0c\u5b9e\u65bd\u91cd\u70b9\u5546\u5708\u65d7\u8230\u5e97\u6269\u5f20\u7684\u65b9\u5f0f\u3002\u4e5d\u6708\u83b7\u5f97\u8d8a\u79c0\u4e24\u4e2a\u4ebf\u6e2f\u5143\u7684\u6ce8\u8d44\u4e4b\u540e\uff0c\u53c8\u7b79\u5212\u5f00\u8bbe\u5e7f\u5dde\u5929\u6cb3\u65d7\u8230\u5e97\u3001\u91cd\u5e86\u89e3\u653e\u7891\u65d7\u8230\u5e97\u3001\u6b66\u6c49\u6c49\u53e3\u65d7\u8230\u5e97\u3002</p><p>\u3000\u3000\u76f8\u6bd4\u76db\u946b\u7684\u5168\u56fd\u533a\u57df\u7684\u91cd\u70b9\u5546\u5708\u5f00\u8bbe\u65d7\u8230\u5e97\u7684\u5f3a\u52bf\u6269\u5f20\u6a21\u5f0f\uff0c\u53d7\u8d44\u91d1\u9650\u5236\uff0c\u6d77\u6cf0\u773c\u4e0b\u53ea\u80fd\u56fa\u5de9\u7701\u5185\u5df2\u6709\u7684\u5bb6\u7535\u8fde\u9501\u5e02\u573a\u4efd\u989d\u3002</p><p>\u3000\u3000\u8c01\u80fd\u60f3\u5230\u4e00\u6b65\u4e4b\u5dee\uff0c\u5c31\u9661\u7136\u8ba9\u76db\u946b\u62c9\u5f00\u8fd9\u4e48\u5927\u7684\u8ddd\u79bb\uff1f</p><p>\u3000\u3000\u6d77\u6cf0\u5728\u7701\u5185\u7684\u5341\u4e09\u5bb6\u95e8\u5e97\uff0c\u5e74\u8425\u4e1a\u989d\u8981\u4e0a\u4e8c\u5341\u4ebf\u90fd\u5f88\u52c9\u5f3a\u3002</p><p>\u3000\u3000\u8fd9\u6b21\uff0c\u76db\u946b\u53c8\u7387\u5148\u5728\u65d7\u4e0b\u4e94\u5bb6\u65d7\u8230\u5e97\u589e\u8bbe\u624b\u673a\u4e13\u5356\u533a\u57df\uff0c\u5f00\u59cb\u5c06\u624b\u673a\u3001\u5bfb\u547c\u673a\u7b49\u901a\u8baf\u4ea7\u54c1\u7eb3\u5165\u5bb6\u7535\u8fde\u9501\u5356\u573a\uff0c\u4e3a\u5373\u5c06\u5230\u6765\u7684\u79fb\u52a8\u901a\u8baf\u9ec4\u91d1\u7406\u4ee3\u505a\u51fa\u5206\u4e00\u676f\u7fb9\u7684\u51c6\u5907\u3002</p><p>\u3000\u3000\u4e3a\u652f\u6301\u76db\u946b\u8fd9\u65f6\u5019\u63a8\u51fa\u624b\u673a\u4e13\u5356\uff0c\u5185\u5730\u5e02\u573a\uff0c\u7231\u8fbe\u7684i19\u624b\u673a\u8fd1\u671f\u5185\u53ea\u5728\u76db\u946b\u65d7\u4e0b\u7684\u4e0a\u6d77\u4e2d\u5c71\u516c\u56ed\u65d7\u8230\u5e97\u3001\u676d\u5dde\u89e3\u653e\u8def\u65d7\u8230\u5e97\u3001\u6210\u90fd\u6625\u7199\u8def\u65d7\u8230\u5e97\u3001\u5efa\u90ba\u65b0\u5e02\u8857\u65d7\u8230\u5e97\u3001\u5317\u4eac\u897f\u5355\u65d7\u8230\u5e97\u7b49\u4e94\u5bb6\u65d7\u8230\u5e97\u63a8\u51fa\uff0c\u5176\u4ed6\u5730\u533a\u53ea\u63a5\u53d7\u9884\u8ba2\u9500\u552e\u670d\u52a1\u3002</p><p>\u3000\u3000\u8fd9\u4e3b\u8981\u4e5f\u662fi19\u6b64\u65f6\u7684\u4ea7\u80fd\u4e5f\u4e0d\u8db3\u4ee5\u652f\u6491\u5168\u65b9\u9762\u7684\u5e02\u573a\u94fa\u8d27\uff0c\u5185\u5730\u5e02\u573a\u6682\u65f6\u53ea\u5411\u76db\u946b\u4e00\u5bb6\u4ee3\u7406\u5546\u653e\u8d27\uff0c\u5012\u662f\u9ed1\u767d\u5c4f\u3001\u5355\u5f26\u4e50\u8272\u7684\u4e2d\u6863\u4ef7\u4f4d\u673ai08\u6b64\u6b21\u4f1a\u5168\u7ebf\u4e0a\u67b6\u3002</p><p>\u3000\u3000\u4e24\u6b3e\u624b\u673a\u5728\u5e7f\u544a\u7b56\u7565\u4e0a\u6709\u6240\u5f88\u5927\u7684\u4e0d\u540c\u3002</p><p>\u3000\u3000i19\u5728\u5185\u5730\u7684\u5e7f\u544a\u4e3b\u8981\u662f\u6709\u91cd\u70b9\u7684\u9488\u5bf9\u9ad8\u7aef\u7528\u6237\u7fa4\u6295\u653e\uff0c\u4ee5\u6606\u817e\u5728\u7ebf\u7684\u8d44\u8baf\u5e7f\u544a\u3001\u6742\u5fd7\u5e7f\u544a\u3001\u592e\u89c6\u8d22\u7ecf90\u5206\u680f\u76ee\u540e\u768415\u79d2\u7535\u89c6\u5e7f\u544a\u4e3a\u91cd\u70b9\uff0c\u5e7f\u544a\u7684\u70c8\u5ea6\u53ea\u662f\u5b9a\u5411\u7684\u91ca\u653e\u3002\u5728\u5e73\u9762\u5a92\u4f53\u4e2d\uff0c\u6742\u5fd7\u6700\u80fd\u9488\u5bf9\u6027\u7684\u8986\u76d6\u9ad8\u7aef\u624b\u673a\u7528\u6237\u7fa4\uff0c\u4ece\u6750\u8d28\u4e0e\u5370\u5237\u6548\u679c\uff0c\u6742\u5fd7\u4e5f\u6bd4\u62a5\u7eb8\u7b49\u5176\u4ed6\u5a92\u4f53\u66f4\u80fd\u6811\u7acbi19\u6781\u81f4\u9b45\u529b\u7684\u54c1\u8d28\u4e0e\u5f62\u8c61\u3002</p><p>\u3000\u3000i08\u5728\u5185\u5730\u7684\u5e7f\u544a\u529b\u5ea6\u5c31\u8981\u731b\u70c8\u8bb8\u591a\uff0c10\u67083\u65e5\u8fd9\u4e00\u5929\u540c\u65f6\u767b\u5165\u5185\u5730\u4e3b\u8981\u5927\u4e2d\u57ce\u5e02\u7684\u7535\u89c6\u5e7f\u544a\u3001\u5404\u7c7b\u4f20\u7edf\u5a92\u4f53\u3001\u6237\u5916\u5e7f\u544a\u7684\u5e73\u53f0\uff0c\u4f3c\u4e4e\u8981\u7231\u8fbe\u4e94\u516d\u4e2a\u6708\u618b\u5c48\u7740\u7684\u529b\u91cf\u4e00\u4e0b\u5b50\u90fd\u91ca\u653e\u51fa\u6765\u3002</p><p>\u3000\u3000\u7231\u8fbe\u624b\u673a\u7684\u79cb\u5b63\u653b\u52bf\u5c31\u4eff\u4f5b\u79cb\u51ac\u8349\u539f\u4e0a\u7684\u4e00\u661f\u91ce\u706b\u501f\u7740\u98ce\u52bf\u5c31\u731b\u70c8\u7684\u71c3\u70e7\u8d77\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e4b\u524d\uff0c\u7231\u8fbe\u4e0e\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u56db\u5bb6\u516c\u53f8\u8fdb\u519b\u624b\u673a\u4ea7\u4e1a\uff0c\u662f\u56fd\u5185\u7535\u5b50\u4ea7\u4e1a\u4e00\u9879\u96f6\u7684\u7a81\u7834\uff0c\u5f88\u53d7\u56fd\u5185\u5a92\u4f53\u7684\u91cd\u89c6\uff0c\u52a0\u4e0a\u7231\u8fbe\u662f\u5f88\u4f1a\u5728\u5e02\u573a\u4e0a\u9020\u52bf\uff0c\u5a92\u4f53\u5bf9\u6b64\u7684\u8ddf\u8e2a\u62a5\u9053\u7edc\u7ece\u4e0d\u7edd\u3002</p><p>\u3000\u3000\u5728\u83b7\u5f97\u90ae\u7535\u90e8\u7684\u624b\u673a\u751f\u4ea7\u8bb8\u53ef\u724c\u7167\u4e4b\u540e\uff0c\u8054\u4fe1\u6700\u5148\u63a8\u51fa\u81ea\u5df1\u7684\u624b\u673a\u4ea7\u54c1\uff0c\u51ed\u501f\u8054\u4fe1\u4e4b\u524d\u9500\u552e\u5bfb\u547c\u673a\u3001\u65e0\u7ef3\u7535\u8bdd\u7b49\u901a\u8baf\u7535\u5b50\u4ea7\u54c1\u642d\u5efa\u8d77\u6765\u7684\u9500\u552e\u5e73\u53f0\uff0c\u4e03\u6708\u8054\u4fe1\u624b\u673a\u9500\u91cf\u5c31\u7a81\u7834\u4e24\u4e07\u53f0\uff0c\u52a0\u4e0a\u8054\u4fe1\u624b\u673a5688\u5143\u7684\u5e02\u573a\u552e\u4ef7\uff0c\u4e30\u539a\u7684\u5229\u6da6\u51e0\u4e4e\u8ba9\u5176\u4ed6\u6ca1\u6709\u62ff\u5230\u624b\u673a\u751f\u4ea7\u724c\u7167\u7684\u7535\u5b50\u5382\u5546\u6320\u5fc3\u6320\u80ba\u3002</p><p>\u3000\u3000\u4e4b\u540e\u8054\u8baf\u3001\u4e1c\u5174\u90fd\u76f8\u7ee7\u7eed\u63a8\u51fa\u624b\u673a\u4ea7\u54c1\uff0c\u9500\u91cf\u90fd\u76f8\u5f53\u559c\u4eba\uff0c\u56fd\u5185\u5a92\u4f53\u90fd\u5927\u58f0\u75be\u547c\u56fd\u4ea7\u624b\u673a\u7684\u65f6\u4ee3\u5373\u5c06\u5230\u6765\u3002</p><p>\u3000\u3000\u800c\u9020\u52bf\u6700\u6c79\u6d8c\u3001\u76f4\u63a5\u4ece\u56fd\u9645\u534a\u5bfc\u4f53\u9886\u519b\u5de8\u5934\u5fb7\u4eea\u624b\u4e2d\u6536\u8d2d\u624b\u673a\u5236\u9020\u6280\u672f\u7684\u7231\u8fbe\u96c6\u56e2\uff0c\u624d\u5bc4\u4e88\u4e86\u56fd\u5185\u5a92\u4f53\u4e0e\u6f5c\u5728\u7528\u6237\u7fa4\u7684\u6700\u5927\u671f\u5f85\u3002</p><p>\u3000\u3000\u4e4b\u524d\u5c31\u6709\u5a92\u4f53\u9884\u6d4b\u7231\u8fbe\u6700\u5feb\u516b\u6708\u521d\u5c31\u80fd\u63a8\u51fa\u81ea\u5df1\u7684\u624b\u673a\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u867d\u7136\u7231\u8fbe\u4ece\u5fb7\u4eea\u6536\u8d2d\u6210\u5957\u7684\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u4f46\u662f\u60f3\u5728\u7b2c\u4e00\u6b3e\u624b\u673a\u4e0a\u90fd\u91c7\u7528\u81ea\u5df1\u751f\u4ea7\u7684\u914d\u4ef6\u662f\u7edd\u4e0d\u53ef\u80fd\u7684\uff0c\u90a3\u81f3\u5c11\u9700\u8981\u4e00\u5230\u4e24\u5e74\u7684\u7b79\u5907\u671f\u624d\u591f\uff0c\u82cf\u6d25\u4e1c\u4e5f\u65e9\u5c31\u5411\u5a92\u4f53\u516c\u5e03\u7231\u8fbe\u7684\u7ec4\u7ec7\u751f\u4ea7\u65b9\u6848\u3002</p><p>\u3000\u3000\u82af\u7247\u90e8\u5206\u7531\u7231\u8fbe\u81ea\u4e3b\u8bbe\u8ba1\uff0c\u59d4\u6258\u5fb7\u4eea\u4ee3\u5de5\uff0c\u7231\u8fbe\u5fae\u7535\u5b50\u521a\u521a\u8bd5\u751f\u4ea7\uff0c\u4e3a\u786e\u4fdd\u9996\u6279\u624b\u673a\u7684\u8d28\u91cf\uff0c\u82af\u7247\u7684\u5c01\u88c5\u4e0e\u6d4b\u8bd5\u6682\u65f6\u8fd8\u4e0d\u4f1a\u62ff\u5230\u56fd\u5185\u6765\u505a\uff0c\u4e24\u5e74\u524d\u5c31\u6536\u8d2d\u6d77\u5dde\u7535\u5b50\u5143\u5668\u4ef6\u5382\u7ecf\u8fc7\u4e24\u5e74\u7684\u6301\u7eed\u6295\u5165\uff0c\u7535\u8def\u677f\u6280\u672f\u5df2\u7ecf\u5b8c\u5168\u6210\u719f\u3002</p><p>\u3000\u3000\u7231\u8fbe\u5728\u8fc7\u53bb\u4e24\u5e74\u65f6\u95f4\u91cc\u5728\u7cbe\u5bc6\u6a21\u5177\u5236\u9020\u6280\u672f\u4e0a\u7684\u6295\u5165\u4e5f\u5f88\u5927\uff0c\u4e3a\u4e86\u6c42\u5f97\u7231\u8fbe\u7b2c\u4e00\u6b3e\u624b\u673a\u5728\u5916\u89c2\u8bbe\u8ba1\u6709\u5927\u7684\u7a81\u7834\uff0c\u5f20\u606a\u65e9\u5728\u5e74\u524d\u5c31\u8ba9\u7231\u8fbe\u65d7\u4e0b\u7684\u7cbe\u5bc6\u6a21\u5177\u7535\u5b50\u6709\u9650\u516c\u53f8\u4ee5\u53ca\u76f8\u5173\u5b9e\u9a8c\u5ba4\u5c31\u5f00\u59cb\u4e3a\u673a\u58f3\u4e0e\u5185\u90e8\u586b\u5145\u6750\u6599\u505a\u51c6\u5907\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u90e8\u5206\u7684\u5de5\u4f5c\u4e5f\u662f\u4e3a\u65e5\u540e\u7684\u7535\u5b50\u97f3\u4e50\u64ad\u653e\u5668\u53caU\u76d8\u505a\u51c6\u5907\u3002</p><p>\u3000\u3000\u4e8c\u6d41\u7535\u6c60\u6280\u672f\u4e0d\u6210\u719f\uff0c\u8981\u843d\u540e\u5e02\u573a\u540c\u7c7b\u4ea7\u54c1\u8bb8\u591a\uff0c\u7ec4\u5efa\u5de5\u5382\u751f\u4ea7\u7684\u4e8c\u6b21\u5145\u653e\u7535\u7535\u6c60\uff0c\u4e5f\u53ea\u662f\u4f9b\u5e94\u7231\u8fbe\u7684\u4f4e\u7aef\u7535\u5b50\u4ea7\u54c1\u6216\u5411\u5176\u4ed6\u5382\u5546\u4f9b\u8d27\u3002</p><p>\u3000\u3000\u6676\u5c4f\u6280\u672f\u4e0d\u6210\u719f\uff0c\u89e3\u6670\u5ea6\u3001\u9971\u548c\u5ea6\u3001\u8017\u7535\u91cf\u90fd\u4e0d\u80fd\u6ee1\u8db3i19\u624b\u673a\u7684\u9ad8\u7aef\u54c1\u8d28\u8981\u6c42\uff0c\u7ec4\u5efa\u5de5\u5382\u751f\u4ea7\u7684\u6676\u5c4f\u5012\u662f\u4f1a\u7528\u5728\u9ed1\u767d\u5c4f\u624b\u673ai08\u4e0a\uff0c\u4ee5\u53ca\u789f\u673a\u4e0a\u7684\u6676\u5c4f\u663e\u793a\u4e5f\u53ef\u4ee5\u91c7\u7528\u7231\u8fbe\u6676\u5c4f\u7535\u5b50\u751f\u4ea7\u7684\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u7531\u4e8e\u7231\u8fbe\u65d7\u4e0b\u5de8\u5927\u7684\u789f\u673a\u9500\u91cf\uff0c\u80fd\u8ba9\u7231\u8fbe\u6676\u5c4f\u7535\u5b50\u7684\u9500\u91cf\u89c4\u6a21\u4e00\u4e0b\u5b50\u5c31\u80fd\u7a81\u7834\u5343\u4e07\u5143\u7684\u76c8\u4e8f\u5e73\u8861\u7ebf\uff0c\u7535\u5b50\u8bcd\u5178\u3001i08\u624b\u673a\u7b49\u4ea7\u54c1\u7684\u9500\u91cf\u8fd8\u4e0d\u51c6\u786e\u9884\u77e5\uff0c\u4f46\u662f\u53ef\u4ee5\u80af\u5b9a\u6676\u5c4f\u5de5\u5382\u7b2c\u4e00\u5e74\u5c31\u80fd\u5b9e\u73b0\u76c8\u5229\u3002</p><p>\u3000\u3000\u8fd9\u5c31\u662f\u5782\u76f4\u5316\u751f\u4ea7\u7ec4\u7ec7\u7684\u5de8\u5927\u4f18\u52bf\uff0c\u5c06\u6700\u5927\u7684\u9650\u5ea6\u7684\u5316\u89e3\u5f00\u53d1\u65b0\u4ea7\u54c1\u7684\u5e02\u573a\u98ce\u9669\uff0c\u4e5f\u80fd\u5206\u51fa\u4e00\u90e8\u5206\u5229\u6da6\u53bb\u652f\u6301\u540e\u7eed\u7684\u6280\u672f\u5f00\u53d1\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6709\u6307\u671b\u6676\u5c4f\u5de5\u5382\u81ea\u8eab\u4ea7\u751f\u7684\u5229\u6da6\u80fd\u5b8c\u5168\u652f\u6491\u540e\u7eed\u6280\u672f\u7684\u5f00\u53d1\uff0c\u90a3\u53ef\u80fd\u8981\u4e00\u767e\u5e74\u624d\u80fd\u8ffd\u8d76\u4e0a\u65e5\u97e9\u5148\u8fdb\u7684\u6676\u5c4f\u6280\u672f\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7b2c\u4e00\u6b3e\u624b\u673a\u91cc\u76f8\u5f53\u591a\u7684\u914d\u4ef6\u8fd8\u8981\u5411\u6d77\u5916\u5382\u5546\u5b9a\u5236\uff0c\u8fd9\u4e5f\u662f\u4e00\u4e9b\u4e13\u4e1a\u5a92\u4f53\u6700\u521d\u5c31\u9884\u6d4b\u5230\u7684\uff0c\u6240\u4ee5\u624d\u9884\u6d4b\u7231\u8fbe\u7684\u7b2c\u4e00\u6b3e\u624b\u673a\u6700\u5feb\u4f1a\u5728\u516b\u6708\u521d\u4e0a\u5e02\u586b\u8865\u56fd\u4ea7\u624b\u673a\u7684\u7a7a\u767d\uff0c\u9884\u6d4b\u65f6\u95f4\u53ea\u6bd4\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u624b\u673a\u5382\u5546\u7a0d\u8fdf\u3002</p><p>\u3000\u3000\u5012\u662f\u6ca1\u6709\u60f3\u5230\u516b\u6708\u521d\uff0c\u7231\u8fbe\u7684\u624b\u673a\u8fd8\u662f\u6084\u7136\u65e0\u58f0\u4e0d\u89c1\u8e2a\u5f71\uff0c\u53ea\u662f\u5927\u5f20\u65d7\u9f13\u7684\u542f\u52a8\u6a61\u6811\u56ed\u8ba1\u5212\u3002\u76f4\u5230\u516b\u6708\u4e2d\u65ec\uff0c\u7231\u8fbe\u624d\u53d1\u51fa\u98ce\u58f0\uff0c\u5c06\u9996\u6b3e\u624b\u673a\u7684\u4e0a\u5e02\u65f6\u95f4\u5b9a\u4e8e\u4e5d\u6708\u4e2d\u4e0b\u65ec\uff0c\u8fd8\u9080\u8bf7\u4e00\u4e9b\u4e13\u4e1a\u5a92\u4f53\u6d3e\u4eba\u53c2\u52a0\u7231\u8fbe\u624b\u673a\u7684\u5185\u90e8\u8bc4\u6d4b\u3002</p><p>\u3000\u3000\u4e00\u76f4\u7b49\u5230\u4e5d\u6708\u4e8c\u5341\u65e5\uff0c\u7231\u8fbe\u518d\u6b21\u5ba3\u5e03\u63a8\u5ef6\u7b2c\u4e00\u6b3e\u624b\u673a\u4e0a\u5e02\u65f6\u95f4\uff0c\u671f\u5f85\u4e4b\u5fc3\u6cb8\u817e\u8bb8\u591a\u7684\u5e02\u573a\u6b64\u65f6\u5bf9\u7231\u8fbe\u4e5f\u4e0d\u7531\u7684\u4ea7\u751f\u4e00\u4e1d\u7684\u4fe1\u5fc3\u52a8\u6447\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\uff0c\u8096\u745e\u6c11\u3001\u5468\u5174\u4e1c\u7b49\u4eba\u624d\u5bf9\u7231\u8fbe\u751f\u51fa\u8f7b\u89c6\u4e4b\u5fc3\u3002</p><p>\u3000\u3000\u8054\u4fe1\u624b\u673a\u4e0a\u5e02\u4e09\u4e2a\u6708\u4ee5\u6765\uff0c\u8fde\u7eed\u4e24\u4e2a\u6708\u7684\u51c0\u5229\u6da6\u7a81\u7834\u4e24\u5343\u4e07\uff0c\u7231\u8fbe\u6ca1\u6709\u7279\u6b8a\u7684\u539f\u56e0\u6ca1\u6709\u7406\u7531\u4e00\u518d\u5ef6\u8fdf\u4e0a\u5e02\u7684\u65f6\u95f4\u653e\u5f03\u553e\u624b\u53ef\u5f97\u7684\u66b4\u5229\u3002</p><p>\u3000\u3000\u76f4\u5230\u5728\u4e5d\u6708\u4e09\u5341\u4e00\u65e5\u5728\u9999\u6e2f\u542f\u5fb7\u673a\u573a\uff0c\u4fe1\u5fc3\u66b4\u6ee1\u60f3\u5230\u9999\u6e2f\u5bfb\u4f5c\u5408\u4f5c\u8005\u5f00\u62d3\u6d77\u5916\u5e02\u573a\u7684\u8096\u745e\u6c11\u3001\u5468\u5174\u6c11\u624d\u7ed9\u5f20\u606a\u800d\u5b9d\u62ff\u51fa\u6765\u7684i19\u6837\u54c1\u673a\u7529\u4e86\u4e00\u8bb0\u54cd\u4eae\u7684\u8033\u5149\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u53ef\u4ee5\u8bf4\u662f\u4ed3\u60f6\u8fd4\u56de\u5317\u4eac\u3002</p><p>\u3000\u3000\u7231\u8fbe\u85cf\u533f\u4e94\u516d\u6708\u4e4b\u4e45\uff0c\u539f\u6765\u53ea\u662f\u529b\u6c42\u5728\u7b2c\u4e00\u6b3e\u624b\u673a\u4e0a\u53d6\u5f97\u5916\u89c2\u8bbe\u8ba1\u7684\u7a81\u7834\u3002</p><p>\u3000\u3000\u5f53\u7231\u8fbe\u5728\u56fd\u5e86\u8282\u540e\u7b2c\u4e00\u4e2a\u5468\u672b\u6b63\u5f0f\u63ed\u5f00i19\u624b\u673a\u7684\u9762\u7eb1\u4e4b\u65f6\uff0c\u6574\u4e2a\u5e02\u573a\u60ca\u8273\u4e4b\u4f59\u90fd\u4e3a\u6b64\u6cb8\u817e\u8d77\u6765\u3002</p></article>"}}}]);
