(window.webpackJsonp=window.webpackJsonp||[]).push([[819],{842:function(p){p.exports={chapterTitle:null,title:"\u7b2c816\u7ae0 \u53c8\u662f\u4e09\u4e95",content:"<article><h2>\u7b2c816\u7ae0 \u53c8\u662f\u4e09\u4e95</h2><p>\u3000\u3000\u4e0d\u60f3\u6253\u8349\u60ca\u86c7\uff0c\u968f\u5468\u747e\u73ba\u3001\u5510\u82f1\u57f9\u51fa\u73b0\u5728\u6559\u5de5\u98df\u5802\u7684\u53e6\u4e24\u4e2a\u4e2d\u5e74\u4eba\u7684\u8eab\u4efd\u4e00\u76f4\u5230\u56db\u6708\u4e0b\u65ec\u7684\u4e8c\u5341\u516d\u65e5\u624d\u8c03\u67e5\u6e05\u695a\u3002\u90a3\u4e24\u4e2a\u4e2d\u5e74\u4eba\u90fd\u662f\u65e5\u672c\u6700\u5927\u7684\u7a00\u571f\u4ea7\u4e1a\u516c\u53f8\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u77ff\u4e1a\u516c\u53f8\u9a7b\u534e\u529e\u4e8b\u673a\u6784\u7684\u9ad8\u7ea7\u804c\u5458\uff0c\u5176\u4e2d\u4e00\u4eba\u540d\u53eb\u6c60\u4f50\u82f1\u5bff\uff0c\u662f\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u4ece\u65e5\u672c\u6d3e\u9063\u5230\u4e2d\u56fd\u7684\u8d1f\u8d23\u4eba\uff0c\u53e6\u4e00\u4eba\u662f\u4e2d\u56fd\u4eba\uff0c\u539f\u662f\u91d1\u5c71\u77ff\u4e1a\u7814\u7a76\u6240\u7684\u7814\u7a76\u5458\uff0c\u7ed9\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u6316\u5230\u9a7b\u534e\u673a\u6784\u91cc\u62c5\u4efb\u9ad8\u7ea7\u804c\u5458\u3002\u7531\u4e8e\u7ae0\u5dde\u662f\u56fd\u5185\u6700\u4e3b\u8981\u7684\u91cd\u7a00\u571f\u77ff\u8d44\u6e90\u7684\u6240\u5728\u5730\uff0c\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u77ff\u4e1a\u516c\u53f8\u9a7b\u534e\u529e\u4e8b\u673a\u6784\u8bbe\u5728\u91d1\u5c71\uff0c\u800c\u975e\u5bfb\u5e38\u610f\u4e49\u7684\u5916\u8d44\u5206\u652f\u673a\u6784\u805a\u96c6\u5730\u5317\u4eac\u6216\u8005\u4e0a\u6d77\uff0c\u90a3\u8f86\u91d1\u5c71\u8f66\u724c\u7684\u5954\u9a70\u8f66\u5373\u4e3a\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u77ff\u4e1a\u516c\u53f8\u9a7b\u534e\u529e\u4e8b\u673a\u6784\u6240\u6709\u3002</p><p>\u3000\u3000\u81f3\u6b64\uff0c\u56e0\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u8c03\u6574\u800c\u51fa\u73b0\u7684\u5229\u76ca\u94fe\u5927\u4f53\u6d6e\u73b0\u5728\u773c\u524d\uff0c\u91d1\u5c71\u534e\u7a00\u4ee3\u8868\u7ae0\u5dde\u6700\u5927\u7684\u7a00\u571f\u77ff\u76d7\u91c7\u52bf\u529b\u5510\u5bb6\uff0c\u4e00\u65e6\u7a00\u571f\u77ff\u5f00\u91c7\u4e0e\u5206\u79bb\u4ea7\u4e1a\u5411\u6c11\u8425\u8d44\u672c\u653e\u5f00\uff0c\u5510\u5bb6\u4ee5\u53ca\u5176\u4ed6\u76d7\u91c7\u5546\u5bf9\u7ae0\u5dde\u53ca\u5176\u4ed6\u5730\u533a\u7684\u7a00\u571f\u77ff\u8d44\u6e90\u7684\u75af\u72c2\u5f00\u91c7\u5c06\u4ece\u5730\u4e0b\u516c\u5f00\u7684\u8d70\u5230\u5730\u4e0a\uff0c\u8fd8\u4f1a\u53d8\u5f97\u66f4\u52a0\u7684\u75af\u72c2\uff0c\u7a00\u571f\u77ff\u6df1\u52a0\u5de5\u9886\u57df\u5411\u5916\u8d44\u5f00\u653e\uff0c\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u7b49\u6d77\u5916\u5382\u5546\u5c06\u53ef\u4ee5\u76f4\u63a5\u5728\u56fd\u5185\u8bbe\u7acb\u5de5\u5382\u6536\u50a8\u7a00\u571f\u77ff\u8f6c\u79fb\u5230\u56fd\u5916\uff0c\u4e3a\u4ed6\u4eec\u63a0\u593a\u4e2d\u56fd\u7684\u7a00\u571f\u8d44\u6e90\u63d0\u4f9b\u66f4\u52a0\u4fbf\u6377\u7684\u9014\u5f84\u3002</p><p>\u3000\u3000\u201c\u53c8\u662f\u4e09\u4e95\uff0c\u53c8\u662f\u6c60\u4f50\u5bb6\u7684\u4eba\u2026\u2026\u201d\u5f20\u606a\u62ff\u5230\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u9a7b\u91d1\u5c71\u529e\u4e8b\u673a\u6784\u7684\u8d44\u6599\uff0c\u4e0d\u7531\u7684\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u4ed6\u65e9\u5c31\u6e05\u695a\u65e5\u672c\u4e09\u4e95\u8d22\u9600\u4ece\u516b\u5341\u5e74\u4ee3\u4ee5\u6765\u5c31\u9010\u6b65\u52a0\u5f3a\u5bf9\u4e2d\u56fd\u7ecf\u6d4e\u7684\u6e17\u900f\uff0c\u5012\u662f\u6ca1\u6709\u60f3\u5230\u5df2\u4e0e\u4e09\u4e95\u72ed\u8def\u76f8\u9022\u591a\u6b21\uff0c\u4ece\u6700\u65e9\u7684\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u4e0e\u4e09\u4e95\u65d7\u4e0b\u7684\u65b0\u65e5\u94c1\u8c08\u5224\uff0c\u4ee5\u53ca\u540e\u6765\u4e09\u4e95\u8d22\u9600\u7684\u6210\u5458\u4f01\u4e1a\u7d22\u5c3c\u5728\u789f\u673a\u673a\u82af\u5236\u9020\u9886\u57df\u6e83\u8d25\uff0c\u518d\u5230\u773c\u4e0b\u7684\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u8c03\u6574\uff0c\u4e09\u4e95\u7684\u8eab\u5f71\u8fd8\u771f\u662f\u65e0\u5904\u4e0d\u5728\u3002</p><p>\u3000\u3000\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u77ff\u4e1a\u7684\u89c4\u6a21\u6709\u9650\uff0c\u770b\u4e0a\u53bb\u4e0d\u80fd\u8ddf\u4e09\u4e95\u77ff\u4e1a\u3001\u65b0\u65e5\u94c1\u3001\u7d22\u5c3c\u8fd9\u6837\u7684\u65d7\u4e0b\u5927\u6210\u5458\u4f01\u4e1a\u76f8\u63d0\u5e76\u8bba\uff0c\u4f46\u662f\u9274\u4e8e\u7279\u79cd\u91d1\u5c5e\u5728\u73b0\u4ee3\u5de5\u4e1a\u4e2d\u7684\u91cd\u8981\u6027\uff0c\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u77ff\u4e1a\u5728\u4e09\u4e95\u4f53\u7cfb\u5185\u7684\u5730\u4f4d\u76f8\u5f53\u91cd\u8981\u3002\u53c8\u7531\u4e8e\u7279\u79cd\u91d1\u5c5e\u8d44\u6e90\u5728\u4e2d\u56fd\u7684\u5206\u5e03\u76f8\u6bd4\u8f83\u5176\u4ed6\u5730\u533a\u800c\u8a00\u663e\u5f97\u5f02\u5e38\u7684\u4e30\u5bcc\uff0c\u6240\u4ee5\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u77ff\u4e1a\u9a7b\u534e\u673a\u6784\u7684\u7ea7\u522b\u76f8\u5f53\u9ad8\uff0c\u52a0\u4e0a\u56fd\u5185\u5305\u62ec\u7a00\u571f\u5728\u5185\u7684\u7a00\u6709\u91d1\u5c5e\u4ea7\u4e1a\u653f\u7b56\u8c03\u6574\u8f6c\u5411\u7684\u654f\u611f\uff0c\u8fd9\u624d\u4f1a\u6709\u4e09\u4e95\u8d22\u56e2\u80cc\u540e\u5bb6\u65cf\u7684\u4eba\u4eb2\u81ea\u51fa\u9762\u5230\u4e2d\u56fd\u4e3b\u6301\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u4e0d\u6653\u5f97\u8fd9\u4f4d\u6c60\u4f50\u82f1\u5bff\u4e0e\u4e0a\u6b21\u5728\u4e1c\u4eac\u7535\u5b50\u5c55\u770b\u5230\u6c60\u4f50\u79c0\u85cf\u662f\u4ec0\u4e48\u5173\u7cfb\uff0c\u770b\u60c5\u5f62\uff0c\u6c60\u4f50\u79c0\u85cf\u7684\u5730\u4f4d\u4f3c\u4e4e\u66f4\u9ad8\u4e00\u4e9b\uff0c\u66f4\u63a5\u8fd1\u5ae1\u7cfb\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u8981\u662f\u60f3\u5230\u80cc\u540e\u6709\u53cc\u9510\u5229\u7684\u6bd2\u773c\u5728\u76ef\u7740\u4ed6\u4eec\uff0c\u5927\u6982\u7edd\u4e0d\u4f1a\u5982\u6b64\u7c97\u5fc3\u5927\u610f\u7684\u8d70\u5728\u4e00\u8d77\u5427\uff0c\u201d\u7fdf\u4e39\u9752\u7684\u7b11\u50cf\u4e00\u628a\u5f00\u5203\u7684\u5200\uff0c\u950b\u5229\u95ea\u5149\uff0c\u5728\u4f17\u591a\u4eba\u4e2d\uff0c\u5979\u7684\u786e\u662f\u5f53\u5f20\u606a\u52a9\u624b\u7684\u5408\u9002\u4eba\u9009\uff0c\u611f\u89c9\u654f\u9510\u800c\u4e14\u4eba\u4e8b\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u8bf4\u9053\uff0c\u201c\u53ea\u6709\u5c06\u8fd9\u6837\u7684\u7ebf\u7d22\u516c\u5e03\u4e8e\u4f17\uff0c\u5468\u747e\u73ba\u7edd\u5bf9\u5403\u4e0d\u4e86\u8981\u515c\u7740\u8d70\u3002\u201d</p><p>\u3000\u3000\u201c\u4e00\u68cd\u5b50\u5c06\u5468\u747e\u73ba\u6253\u6b7b\uff0c\u53ef\u4e0d\u662f\u6211\u7684\u76ee\u7684\uff0c\u201d\u5f20\u606a\u8eba\u5728\u697c\u9876\u82b1\u56ed\u7684\u8336\u5ba4\u91cc\u85e4\u6905\u4e0a\uff0c\u770b\u7740\u7eb5\u6a2a\u4ea4\u9519\u7684\u6728\u6881\u4e4b\u5916\u7684\u84dd\u8272\u7684\u5929\u7a7a\uff0c\u201c\u79c1\u6028\u5f53\u7136\u8981\u4e86\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u4f1a\u4e0e\u4e09\u4e95\u6709\u66f4\u5927\u7684\u6069\u6028\u8981\u7ed3\u3002\u4ece\u4e2d\u7530\u82f1\u5bff\u8fd9\u6761\u7ebf\u7d22\u53ef\u4ee5\u6df1\u6316\u4e0b\u53bb\uff0c\u4e09\u4e95\u8981\u60f3\u5728\u80cc\u540e\u63a8\u52a8\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u7684\u8c03\u6574\uff0c\u7edd\u4e0d\u4f1a\u53ea\u60f3\u5230\u8981\u5229\u7528\u5b66\u754c\u7684\u5f71\u54cd\u529b\uff0c\u8981\u662f\u80fd\u786e\u51ff\u6293\u4f4f\u4ed6\u4eec\u5728\u4e2d\u56fd\u8fdb\u884c\u5546\u4e1a\u72af\u7f6a\u7684\u8bc1\u636e\uff0c\u4e0d\u4ec5\u80fd\u963b\u6b62\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u8fdb\u884c\u4e0d\u6070\u5f53\u7684\u8f6c\u5411\uff0c\u8fd8\u80fd\u4e25\u91cd\u6253\u51fb\u4e09\u4e95\u5bf9\u4e2d\u56fd\u7a00\u571f\u4ea7\u4e1a\u5df2\u6709\u7684\u6e17\u900f\u2026\u2026\u8fd9\u79cd\u4e8b\u60c5\uff0c\u867d\u8bf4\u770b\u4e0a\u53bb\u8ddf\u9526\u6e56\u65e0\u5173\uff0c\u6211\u8fd8\u662f\u5f88\u4e50\u610f\u53bb\u505a\u7684\u2026\u2026\u201d\u867d\u7136\u8fd9\u4e48\u8bf4\u7740\uff0c\u4f46\u662f\u5f20\u606a\u5fc3\u91cc\u6e05\u695a\u8fd9\u4e0d\u662f\u7b80\u5355\u7684\u4e8b\u60c5\uff0c\u4e09\u4e95\u8d22\u9600\u5bf9\u4e2d\u56fd\u7684\u7ecf\u6d4e\u6e17\u900f\u5176\u5b9e\u4ece\u4e03\u5341\u5e74\u4ee3\u672a\u671f\u4e2d\u65e5\u5173\u7cfb\u590d\u82cf\u671f\u5c31\u5f00\u59cb\u4e86\uff0c\u4e3b\u7ba1\u56fd\u5185\u7ecf\u6d4e\u4ea7\u4e1a\u653f\u7b56\u7684\u5b98\u5458\u91cc\u6709\u76f8\u5f53\u591a\u7684\u4e00\u6279\u4eba\u6709\u7740\u7559\u65e5\u7684\u80cc\u666f\uff0c\u5728\u5fc3\u7406\u4e0a\u503e\u5411\u65e5\u672c\u90a3\u4e5f\u662f\u9884\u6599\u4e4b\u4e2d\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u4f60\u662f\u8981\u5c06\u4e09\u4e95\u5f53\u6210\u9526\u6e56\u6f5c\u5728\u7684\u5bf9\u624b\uff1f\u201d\u7fdf\u4e39\u9752\u7591\u60d1\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u4e09\u4e95\u662f\u4e2a\u5e9e\u7136\u5927\u7269\uff0c\u8981\u8bf4\u9526\u6e56\u5c06\u4e09\u4e95\u5f53\u6210\u6f5c\u5728\u7684\u5bf9\u624b\uff0c\u8ba9\u522b\u4eba\u542c\u4e86\u4f1a\u7b11\u8bdd\u6211\u4eec\u592a\u81ea\u4e0d\u91cf\u529b\uff0c\u4e0d\u8fc7\u4ee5\u540e\u5229\u76ca\u51b2\u7a81\u7684\u5730\u65b9\u4f1a\u8d8a\u6765\u8d8a\u591a\u7684\uff0c\u7d22\u5c3c\u3001\u677e\u4e0b\u3001\u590f\u666e\u7b49\u53ef\u90fd\u662f\u4e09\u4e95\u65d7\u4e0b\u7684\u6210\u5458\u4f01\u4e1a\u554a\uff0c\u5c31\u8fde\u4e1c\u829d\u4e5f\u8ddf\u4e09\u4e95\u6709\u7740\u5bc6\u5207\u7684\u5173\u7cfb\u3002\u7a00\u571f\u5c06\u6765\u7684\u4e3b\u8981\u5e94\u7528\u8d8b\u52bf\u96c6\u4e2d\u5728\u519b\u5de5\u3001\u7535\u5b50\u3001\u65b0\u6750\u6599\u5236\u9020\u7b49\u9886\u57df\uff0c\u8fd9\u4e9b\u4ea7\u4e1a\u53c8\u662f\u4e09\u4e95\u4f53\u7cfb\u65d7\u4e0b\u4f01\u4e1a\u53d1\u5c55\u7684\u91cd\u5fc3\u6240\u5728\uff0c\u8981\u80fd\u8fd9\u6b21\u7ed9\u4e88\u4e09\u4e95\u91cd\u632b\uff0c\u5bf9\u5176\u672a\u6765\u7684\u4ea7\u4e1a\u5e03\u5c40\u591a\u5c11\u4f1a\u4ea7\u751f\u4e9b\u5f71\u54cd\u2026\u2026\u201d</p><p>\u3000\u3000\u4e09\u4e95\u8d22\u9600\u5df2\u7ecf\u6784\u6210\u4e00\u4e2a\u5e9e\u5927\u7684\u9aa8\u5e72\u5de5\u4e1a\u4f53\u7cfb\uff0c\u4ece\u6574\u4e2a\u5de5\u4e1a\u4f53\u7cfb\u7684\u57fa\u7840\u7814\u7a76\u3001\u8d44\u6e90\u63a0\u593a\u3001\u7ecf\u6d4e\u6e17\u900f\u5230\u5b8c\u6574\u7684\u5de5\u4e1a\u5316\u5236\u9020\uff0c\u4e09\u4e95\u53ef\u4ee5\u8bf4\u662f\u65e0\u6240\u4e0d\u5305\uff0c\u4e09\u4e95\u4e4b\u5f3a\u5927\u53ef\u4ee5\u8bf4\u5df2\u7ecf\u6df1\u5165\u5230\u65e5\u672c\u8fd9\u4e2a\u56fd\u5185\u7684\u9aa8\u9ad3\u91cc\u4e86\uff0c\u4e09\u4e95\u4e0e\u5176\u4ed6\u51e0\u5bb6\u65e5\u7cfb\u8d22\u9600\u53ef\u4ee5\u8bf4\u6491\u8d77\u65e5\u672c\u7ecf\u6d4e\u7684\u6574\u4e2a\u9aa8\u67b6\u3002\u5c31\u7b97\u6ca1\u6709\u4f53\u5236\u4e0a\u7684\u9650\u5236\uff0c\u4e09\u4e95\u8d22\u9600\u4e5f\u662f\u9526\u6e56\u5229\u7528\u77ed\u77ed\u51e0\u5341\u5e74\u7684\u65f6\u95f4\u6240\u65e0\u6cd5\u8ffd\u8d76\u7684\uff0c\u4f46\u662f\u6709\u4e00\u70b9\u65e9\u5c31\u80fd\u80af\u5b9a\u7684\uff0c\u9526\u6e56\u5728\u7535\u5b50\u9886\u57df\uff0c\u7279\u522b\u662f\u6d88\u8d39\u7c7b\u7535\u5b50\u9886\u57df\uff0c\u4e0e\u4e09\u4e95\u8d22\u9600\u7684\u6469\u64e6\u4f1a\u8d8a\u6765\u8d8a\u5927\uff0c\u5176\u5b9e\u4e5f\u5c06\u5728\u4e09\u4e95\u8d22\u9600\u6700\u6838\u5fc3\u7684\u4ea7\u4e1a\u9886\u57df\u5411\u8fd9\u5934\u5e9e\u7136\u5927\u7269\u6311\u6218\u3002</p><p>\u3000\u3000\u4e09\u661f\u8fd8\u53ea\u662f\u4e00\u5bb6\u7535\u5b50\u4f01\u4e1a\uff0c\u4e09\u4e95\u8d22\u9600\u65d7\u4e0b\u6210\u5458\u4f01\u4e1a\u5374\u805a\u96c6\u4e86\u65e5\u672c\u6700\u4f18\u79c0\u7684\u51e0\u5bb6\u7535\u5b50\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7279\u522b\u559c\u6b22\u770b\u5f20\u606a\u6307\u70b9\u6c5f\u5c71\u7684\u6a21\u6837\uff0c\u867d\u7136\u6ca1\u6709\u6ca6\u9677\uff0c\u867d\u7136\u8fd8\u80fd\u51fa\u6de4\u6ce5\u800c\u4e0d\u67d3\uff0c\u4f46\u662f\u7531\u4e8e\u7279\u6b8a\u7684\u7ecf\u5386\uff0c\u8ba9\u5979\u5bf9\u6743\u52bf\u4e5f\u6709\u4e00\u79cd\u8fd1\u4e4e\u81ea\u6211\u4fdd\u62a4\u672c\u80fd\u7684\u6e34\u671b\uff0c\u6bd4\u8d77\u5f53\u773c\u524d\u8fd9\u4e2a\u7537\u4eba\u7684\u60c5\u4eba\uff0c\u7fdf\u4e39\u9752\u66f4\u5e0c\u671b\u80fd\u6210\u4e3a\u4ed6\u7684\u52a9\u624b\u7559\u5728\u4ed6\u7684\u8eab\u8fb9\uff1b\u6709\u65f6\u5019\u4e5f\u4f1a\u60f3\uff0c\u5373\u4f7f\u5f53\u60c5\u4eba\u4e5f\u65e0\u6240\u8c13\u5427\uff0c\u4e5f\u4e0d\u4f1a\u56e0\u4e3a\u5f20\u606a\u8eab\u8fb9\u5df2\u7ecf\u6709\u4e86\u5176\u4ed6\u5973\u4eba\u800c\u8ba1\u8f83\uff0c\u4f46\u662f\u603b\u4e0d\u80fd\u4e0d\u660e\u4e0d\u767d\u7684\u5c31\u6ca6\u9677\u5427\uff1f</p><p>\u3000\u3000\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u89c1\u7fdf\u4e39\u9752\u6709\u4e9b\u8d70\u795e\uff0c\u4fa7\u8fc7\u5934\u7684\u770b\u7740\u5979\u3002</p><p>\u3000\u3000\u201c\u6211\u5728\u60f3\u4f60\u5fc3\u5b58\u9ad8\u8fdc\uff0c\u773c\u5149\u65e9\u5c31\u4e0d\u653e\u5728\u56fd\u5185\uff0c\u534e\u590f\u7535\u5b50\u5bf9\u9526\u6e56\u53ef\u662f\u4e25\u9635\u4ee5\u5f85\u554a\uff1f\u201d\u7fdf\u4e39\u9752\u5ae3\u7136\u4e00\u7b11\uff0c\u5c06\u5fc3\u95f4\u7684\u60c5\u7eea\u6ef4\u6c34\u4e0d\u6f0f\u7684\u62b9\u4e86\u5e72\u51c0\uff0c\u7ee7\u7eed\u521a\u624d\u7684\u8bdd\u9898\uff0c\u201c\u534e\u590f\u65d7\u4e0b\u7684\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u516c\u53f8\u53ef\u4ee5\u6253\u7b97\u4e94\u6708\u8981\u8054\u5408\u4e00\u5171\u63a8\u51fa\u516d\u6b3e\u65b0\u624b\u673a\u554a\uff0c\u770b\u770b\uff0c\u4ed6\u4eec\u8fd8\u662f\u8ffd\u54ac\u5f97\u7d27\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u591a\u9ad8\u8fdc\u7684\u76ee\u6807\uff0c\u4e5f\u662f\u8981\u4e00\u6b65\u4e00\u6b65\u7684\u53bb\u5b9e\u73b0\uff0c\u6218\u7565\u76ee\u6807\u4e5f\u662f\u8981\u5206\u89e3\u5728\u6218\u672f\u7684\u5c42\u6b21\u4e00\u6b65\u4e00\u6b65\u7684\u53bb\u89e3\u51b3\uff0c\u4e0d\u80fd\u786e\u4fdd\u5728\u56fd\u5185\u4f01\u4e1a\u4e2d\u7684\u5730\u4f4d\uff0c\u53c8\u8c08\u8bba\u53c2\u4e0e\u56fd\u9645\u7ade\u4e89\u4e2d\u53bb\uff1f\u201d\u5f20\u606a\u7ffb\u770b\u4e86\u4e00\u4e0b\u624b\u8fb9\u7684\u8bb0\u5f55\u672c\uff0c\u8ddf\u7fdf\u4e39\u9752\u8bf4\uff0c\u201c\u4f60\u95ee\u9648\u4fe1\u751f\uff0c\u6211\u4eec\u7684\u65b0\u6b3e\u624b\u673a\u80fd\u4e0d\u80fd\u63d0\u524d\u63a8\u51fa\uff1f\u6211\u603b\u770b\u4e0d\u5f97\u845b\u5efa\u5fb7\u8fd9\u4eba\u592a\u5f97\u610f\u2026\u2026\u201d</p><p>\u3000\u3000\u867d\u7136\u4fe1\u606f\u4ea7\u4e1a\u90e8\u4e0b\u4e86\u5f88\u5927\u7684\u51b3\u5fc3\u7ec4\u5efa\u534e\u590f\u7535\u5b50\u96c6\u56e2\uff0c\u4f46\u662f\u9526\u6e56\u5f53\u524d\u7684\u6218\u672f\u76ee\u6807\u5c31\u662f\u8981\u5c06\u534e\u590f\u7535\u5b50\u96c6\u56e2\u7684\u654c\u610f\u7ed9\u5316\u89e3\u6389\uff0c\u8fd9\u53ef\u4e0d\u662f\u8981\u53bb\u6447\u5c3e\u4e5e\u601c\u53bb\u6c42\u845b\u5efa\u5fb7\u6d88\u9664\u5bf9\u9526\u6e56\u7684\u654c\u610f\uff0c\u800c\u662f\u8981\u901a\u8fc7\u5e02\u573a\u624b\u6bb5\u5c06\u534e\u590f\u7535\u5b50\u6253\u5f97\u6ca1\u6709\u813e\u6c14\uff0c\u8ba9\u534e\u590f\u7535\u5b50\u81ea\u5df1\u4e0d\u6562\u5bf9\u9526\u6e56\u4ea7\u751f\u654c\u610f\uff0c\u5c31\u50cf\u73b0\u5728\u7684\u79d1\u738b\uff0c\u591a\u597d\u7684\u813e\u6c14\uff01</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5c06\u4e8b\u60c5\u8bb0\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u5176\u4ed6\u4e8b\u60c5\uff0c\u6211\u5c31\u5148\u4e0b\u53bb\u4e86\uff0c\u4f60\u665a\u4e0a\u53c2\u52a0\u9152\u4f1a\u6253\u7b97\u7a7f\u4ec0\u4e48\u793c\u670d\uff0c\u6211\u5e2e\u4f60\u51c6\u5907\u597d\uff0c\u8fc7\u4e00\u4e2a\u5c0f\u65f6\u6211\u518d\u6765\u63a5\u4f60\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7a7f\u7740\u85cf\u9752\u8272\u7684\u5957\u88c5\uff0c\u8f6c\u8eab\u79bb\u5f00\u8eab\u59ff\u6447\u66f3\uff0c\u7a84\u7a84\u7684\u8170\u80a2\u4e0e\u7ed9\u5957\u88d9\u5305\u88f9\u5f97\u6d51\u5706\u7684\u81c0\u90e8\u4ee5\u6781\u5176\u5938\u5f20\u53c8\u6027\u611f\u7684\u66f2\u7ebf\u8854\u63a5\u7740\uff0c\u4e1d\u889c\u5305\u88f9\u7684\u5c0f\u817f\uff0c\u6697\u7ea2\u8272\u7684\u9ad8\u8ddf\u978b\uff0c\u6643\u5f97\u4eba\u5fc3\u9189\u795e\u8ff7\uff0c\u5f20\u606a\u772f\u7740\u773c\u775b\u770b\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u5012\u662f\u7fdf\u4e39\u9752\u5728\u94bb\u8fdb\u697c\u68af\u95f4\u65f6\u56de\u7738\u6a2a\u76fc\uff0c\u624d\u8ba9\u5f20\u606a\u5c06\u5fc3\u601d\u6536\u62fe\u8d77\u6765\u3002</p><p>\u3000\u3000\u524d\u5c18\u5f80\u4e16\u91cc\u8fc7\u6df1\u7684\u6c89\u6eba\u4e8e\u60c5\u6b32\uff0c\u53c8\u8fdc\u8fdc\u6ca1\u6709\u5230\u52d8\u7834\u7ea2\u7c89\u9ab7\u9ac5\u7684\u7a0b\u5ea6\uff0c\u5982\u4eca\u5bf9\u6f02\u4eae\u7684\u5973\u4eba\u62b5\u6297\u529b\u5c31\u5f31\u4e86\uff0c\u5bf9\u60c5\u611f\u4e5f\u6ca1\u6709\u4e13\u4e00\u7684\u6001\u5ea6\uff0c\u66f4\u591a\u8ba9\u5f20\u606a\u7684\u987e\u5fcc\u5927\u6982\u662f\u4e0d\u60f3\u8ba9\u5fc3\u7231\u7684\u5973\u4eba\u4f24\u5fc3\u5427\u3002</p><p>\u3000\u3000\u665a\u4e0a\u7684\u9152\u4f1a\u662f\u4e1c\u6d77\u7701\u4e0e\u6c5f\u5357\u7701\u8f6c\u53d8\u533a\u57df\u7ecf\u6d4e\u7ade\u4e89\u601d\u8def\u9996\u6b21\u63a5\u89e6\u540e\u7684\u9152\u4f1a\uff0c\u6c5f\u5357\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u4ee3\u7701\u957f\u6881\u4f1f\u6cd5\u4e0e\u91d1\u5c71\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u5e02\u957f\u8d75\u6709\u4f26\u4eb2\u81ea\u7387\u961f\u5230\u5efa\u90ba\u6765\u8003\u5bdf\u5b66\u4e60\uff0c\u5e76\u4e0e\u4e1c\u6d77\u7701\u59d4\u3001\u7701\u653f\u5e9c\u4ee5\u53ca\u5efa\u90ba\u5e02\u59d4\u3001\u5e02\u653f\u5e9c\u7684\u5b98\u5458\u8fdb\u884c\u63a5\u89e6\u3002</p><p>\u3000\u3000\u957f\u671f\u4ee5\u6765\uff0c\u4e24\u7701\u7279\u522b\u662f\u91d1\u5c71\u4e0e\u5efa\u90ba\u3001\u60e0\u5c71\u5b58\u5728\u6fc0\u70c8\u7684\u7ecf\u6d4e\u7ade\u4e89\uff0c\u5f20\u606a\u4ece\u4eca\u5e74\u521d\u5c31\u79ef\u6781\u63a8\u52a8\u4e24\u5730\u653f\u5e9c\u8f6c\u53d8\u533a\u57df\u7ade\u4e89\u7684\u601d\u8def\uff0c\u52a0\u5f3a\u533a\u57df\u7ecf\u6d4e\u5408\u4f5c\uff0c\u5c06\u5c0f\u6c5f\u6d41\u57df\u4f5c\u4e3a\u6574\u4f53\u7684\u4e00\u4e2a\u533a\u57df\u7ecf\u6d4e\u4f53\u6765\u770b\u5f85\uff0c\u5171\u540c\u4fc3\u8fdb\u6574\u4f53\u533a\u57df\u7ecf\u6d4e\u7684\u53d1\u5c55\u3002\u4e00\u65b9\u9762\u6c5f\u5357\u7701\u5b98\u5458\u53bb\u5e74\u79cb\u540e\u8c03\u52a8\u5f88\u5927\uff0c\u65b0\u4e0a\u4efb\u7684\u6881\u4f1f\u6cd5\u3001\u8d75\u6709\u4f26\u7b49\u4eba\u5bf9\u4e1c\u6d77\u7701\u5e76\u65e0\u6210\u89c1\uff0c\u53e6\u4e00\u65b9\uff0c\u6c5f\u5357\u3001\u4e1c\u6d77\u7684\u5b98\u5458\u4e5f\u5728\u533a\u57df\u7ecf\u6d4e\u7ade\u4e89\u65b9\u9762\u6709\u6240\u53cd\u601d\uff0c\u52a0\u4e0a\u5f20\u606a\u5728\u80cc\u540e\u79ef\u6781\u63a8\u52a8\uff0c\u624d\u6709\u4e86\u8fd9\u6b21\u8f83\u5927\u89c4\u6a21\u7684\u4e24\u5730\u5b98\u5458\u4ea4\u6d41\u4e0e\u63a5\u89e6\u3002\u65e2\u7136\u50cf\u6881\u4f1f\u6cd5\u3001\u8d75\u6709\u4f26\u4ee5\u53ca\u4e1c\u6d77\u8fd9\u8fb9\u7684\u9676\u664b\u3001\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u7b49\u4e3b\u8981\u5b98\u5458\u90fd\u51fa\u9762\uff0c\u8fd9\u6b21\u63a5\u89e6\u4e5f\u662f\u8c08\u51fa\u4e86\u5f88\u5927\u7684\u6210\u679c\uff0c\u7b7e\u7f72\u4e86\u300a\u52a0\u5f3a\u4e24\u7701\u533a\u57df\u4ea4\u901a\u5408\u4f5c\u534f\u8bae\u300b\uff0c\u5c06\u9996\u5148\u5728\u91d1\u5c71\u3001\u60e0\u5c71\u3001\u5efa\u90ba\u4e09\u5ea7\u57ce\u5e02\u4e4b\u95f4\u52a0\u5f3a\u9ad8\u901f\u516c\u8def\u3001\u5c0f\u6c5f\u822a\u9053\u3001\u94c1\u8def\u8fd0\u8f93\u3001\u98de\u673a\u822a\u73ed\u7b49\u5404\u9879\u4ea4\u901a\u57fa\u7840\u8bbe\u65bd\u7684\u5efa\u8bbe\uff0c\u5b9e\u73b0\u4e09\u5ea7\u57ce\u5e02\u4e4b\u95f4\u7684\u65e0\u7f1d\u5bf9\u63a5\u3002</p></article>"}}}]);
