(window.webpackJsonp=window.webpackJsonp||[]).push([[82],{104:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u8282 \u77ee\u4eba\u7f8e\u5973",content:"<article><h2>\u7b2c\u56db\u5341\u8282 \u77ee\u4eba\u7f8e\u5973</h2><p>\u6839\u636e\u5730\u56fe\u663e\u793a\uff0c\u6b64\u53bb\u4e00\u8def\u4e0a\u4f1a\u7ecf\u8fc7\u6851\u8fbe\u5df4\u3001\u94f6\u6708\u57ce\uff08\u94f6\u6708\u8054\u90a6\u7684\u9996\u90fd\uff09\u3001\u827e\u5c14\u6c83\u5fb7\u3001\u96c5\u5854\u3001\u7ea2\u677e\u9547\u7b49\u8f83\u5927\u7684\u57ce\u9547\uff0c\u5230\u8fbe\u6df1\u6c34\u57ce\u3002\u7136\u540e\u518d\u4ece\u6df1\u6c34\u57ce\u8f6c\u9053\u5357\u4e0b\uff0c\u4e00\u8def\u76f4\u8fbe\u535a\u5f97\u4e4b\u95e8\uff0c\u4e5f\u5c31\u662f\u6b64\u884c\u7684\u76ee\u7684\u5730\u4e86\u3002</p><p>\u6885\u83f2\u65af\u662f\u8981\u9001\u9ece\u660e\u4e4b\u77f3\u5230\u535a\u5f97\u4e4b\u95e8\uff0c\u904f\u5236\u6d88\u9664\u90a3\u91cc\u7684\u5927\u761f\u75ab\u3002\u5176\u5b9e\u65e0\u8bba\u662f\u5965\u672f\u8fd8\u662f\u795e\u672f\uff0c\u90fd\u6709\u8fdc\u8ddd\u79bb\u8de8\u8d8a\u7a7a\u95f4\u4f20\u9001\u7684\u6cd5\u672f\uff0c\u53ea\u8981\u627e\u5230\u8db3\u591f\u5f3a\u7684\u5deb\u5e08\u6216\u8005\u7267\u5e08\u6765\u65bd\u6cd5\u3002\u6885\u83f2\u65af\u7684\u540c\u50da\u4e2d\uff0c\u5176\u5b9e\u5c31\u6709\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u5f00\u4e00\u4e2a\u4f20\u9001\u95e8\uff0c\u8ba9\u4eba\u5e26\u70b9\u4e1c\u897f\u8fc7\u53bb\uff0c\u539f\u672c\u5e76\u4e0d\u4e3a\u96be\uff0c\u65e0\u5948\u4ed6\u4eec\u8fd9\u6b21\u8981\u9001\u7684\u662f\u9ece\u660e\u4e4b\u77f3\u3002</p><p>\u4ece\u672c\u8d28\u4e0a\u6765\u8bf4\uff0c\u4f20\u9001\u95e8\u5c31\u662f\u6682\u65f6\u521b\u9020\u51fa\u4e00\u4e2a\u7a7a\u95f4\u901a\u9053\uff0c\u80fd\u8ba9\u4e00\u5b9a\u7684\u7269\u4f53\u901a\u8fc7\u3002\u666e\u901a\u7684\u4e1c\u897f\u4fbf\u7f62\u4e86\uff0c\u9ece\u660e\u4e4b\u77f3\u662f\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u521b\u9020\u7684\u795e\u5668\uff0c\u5176\u4e2d\u8574\u542b\u7684\u529b\u91cf\u592a\u5f3a\uff0c\u4e00\u65e6\u8fdb\u5165\u4f20\u9001\u95e8\uff0c\u4f1a\u7acb\u523b\u8ba9\u7a7a\u95f4\u901a\u9053\u77ac\u95f4\u5d29\u6e83\u6389\uff0c\u5230\u65f6\u5019\u53cd\u800c\u9ebb\u70e6\u900f\u9876\u3002</p><p>\u8981\u77e5\u9053\uff0c\u5c31\u8fde\u683c\u62c9\u5179\u7279\u8fd9\u79cd\u529b\u91cf\u53ef\u4ee5\u5339\u654c\u795e\u7947\u7684\u6df1\u6e0a\u6076\u9b54\u9886\u4e3b\uff0c\u60f3\u62ff\u5230\u9ece\u660e\u4e4b\u77f3\uff0c\u90fd\u5f97\u6d3e\u9063\u6295\u5f71\u9644\u4f53\u51e1\u4eba\uff0c\u4eb2\u81ea\u8fc7\u6765\u8dd1\u4e00\u8d9f\u3002\u5426\u5219\u6076\u9b54\u76f4\u63a5\u5728\u6df1\u6e0a\u4e2d\u6253\u5f00\u4e00\u4e2a\u4f4d\u9762\u901a\u9053\uff0c\u8ba9\u4eba\u9762\u72ee\u9996\u9886\u5c06\u9ece\u660e\u4e4b\u77f3\u4e22\u8fdb\u53bb\u5c31\u597d\u4e86\uff0c\u4f55\u5fc5\u8fd9\u4e48\u9ebb\u70e6\u3002</p><p>\u6559\u4f1a\u6ca1\u529e\u6cd5\uff0c\u53ea\u5f97\u6d3e\u9063\u6885\u83f2\u65af\u7b49\u4eba\u4eb2\u81ea\u8dd1\u8fd9\u4e00\u8d9f\u3002</p><p>\u81f3\u4e8e\u743c\u6069\uff0c\u4ed6\u662f\u8981\u53bb\u535a\u5f97\u4e4b\u95e8\u9001\u4fe1\uff0c\u627e\u4e00\u4e2a\u53eb\u505a\u62c9\u6c83\u514b\u7684\u8001\u5deb\u5e08\u2014\u2014\u9ebb\u70e6\u5728\u4e8e\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u81ea\u5df1\u90fd\u641e\u4e0d\u6e05\u695a\u8fd9\u4e2a\u62c9\u6c83\u514b\u4f4f\u5728\u54ea\u91cc\u3002\u4ece\u5730\u56fe\u4e0a\u770b\uff0c\u535a\u5f97\u4e4b\u95e8\u662f\u4e2a\u975e\u5e38\u5927\u7684\u57ce\u5e02\uff0c\u8fd9\u8ba9\u743c\u6069\u5230\u54ea\u91cc\u5927\u6d77\u635e\u9488\u53bb\uff0c\u603b\u4e0d\u6210\u6ee1\u5927\u8857\u8d34\u5bfb\u4eba\u542f\u4e8b\uff0c\u8fd9\u4e16\u754c\u4e0a\u53c8\u6ca1\u6709\u5e7f\u64ad\u5587\u53ed\u4e4b\u7c7b\u7684\u4e1c\u897f\u3002</p><p>\u5c24\u5176\u662f\u77e5\u9053\u535a\u5f97\u4e4b\u95e8\u6b63\u5728\u7206\u53d1\u65e0\u6cd5\u904f\u5236\u7684\u5927\u761f\u75ab\uff0c\u5df2\u7ecf\u6b7b\u4eba\u65e0\u6570\u540e\uff0c\u743c\u6069\u7684\u8111\u888b\u5c31\u66f4\u75bc\u4e86\u3002\u5929\u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u4eba\u6709\u65e6\u5915\u7978\u798f\uff0c\u761f\u75ab\u8fd9\u4e1c\u897f\u4e0d\u662f\u666e\u901a\u7684\u75be\u75c5\uff0c\u51f6\u9669\u65e0\u6bd4\u3002\u743c\u6069\u81ea\u5ea6\u6ca1\u6709\u91d1\u521a\u4e0d\u574f\u4e4b\u8eaf\uff0c\u8981\u4ed6\u8fd9\u65f6\u5019\u8dd1\u5230\u75ab\u533a\u53bb\uff0c\u5fc3\u4e2d\u5b9e\u5728\u5fd0\u5fd1\u4e0d\u5b89\u3002\u8981\u4e0d\u662f\u6709\u9ece\u660e\u4e4b\u77f3\u8fd9\u4e2a\u62a4\u8eab\u7b26\u5728\uff0c\u4ed6\u90fd\u8981\u8003\u8651\u662f\u4e0d\u662f\u5148\u9017\u7559\u4e00\u6bb5\u65f6\u95f4\uff0c\u7b49\u761f\u75ab\u8fc7\u4e86\u518d\u53bb\u535a\u5f97\u4e4b\u95e8\u3002</p><p>\u4ed6\u6ca1\u6709\u544a\u8bc9\u6885\u83f2\u65af\u81ea\u5df1\u6b64\u884c\u7684\u76ee\u7684\uff0c\u56e0\u4e3a\u90a3\u52bf\u5fc5\u5c31\u8981\u6cc4\u9732\u81ea\u5df1\u6765\u81ea\u9634\u9b42\u57ce\u7684\u4e8b\u60c5\uff0c\u53ea\u8bf4\u81ea\u5df1\u5de6\u53f3\u65e0\u4e8b\uff0c\u966a\u5979\u53bb\u535a\u5f97\u4e4b\u95e8\u628a\u4efb\u52a1\u5b8c\u6210\u3002</p><p>\u6885\u83f2\u65af\u5e76\u4e0d\u591a\u95ee\uff0c\u5979\u5fc3\u4e2d\u5e94\u8be5\u662f\u6709\u6240\u7591\u8651\u7684\uff0c\u4f46\u65e2\u7136\u743c\u6069\u4e0d\u8bf4\uff0c\u5979\u4e5f\u5c31\u4e0d\u7ba1\u3002</p><p>\u7b97\u7b97\u8def\u7a0b\uff0c\u5c31\u7b97\u662f\u5e73\u5766\u5546\u9053\u9a6c\u8f66\u5954\u9a70\uff0c\u5927\u7ea6\u4e5f\u5f97\u8981\u8d70\u8fd1\u4e00\u4e2a\u6708\uff0c\u4f55\u51b5\u8def\u4e0a\u8bf4\u4e0d\u51c6\u53c8\u6709\u4ec0\u4e48\u803d\u6401\uff0c\u8865\u7ed9\u81ea\u7136\u662f\u4e0d\u80fd\u532e\u4e4f\u7684\u3002\u6885\u83f2\u65af\u53bb\u57ce\u4e2d\u4e70\u4e86\u98df\u7269\u3001\u6c34\uff0c\u4ee5\u53ca\u5979\u81ea\u5df1\u7684\u6362\u6d17\u8863\u670d\uff0c\u7528\u5305\u88f9\u5305\u7740\u5e26\u4e86\u56de\u6765\u3002</p><p>\u201c\u90fd\u5f04\u5b8c\u4e86\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u55ef\u3002\u201d</p><p>\u7b80\u77ed\u7684\u4ea4\u8c08\u540e\uff0c\u4e24\u4eba\u4e0d\u518d\u8bf4\u8bdd\u3002\u81ea\u4ece\u6628\u665a\u7684\u4e8b\u60c5\u540e\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\u53d8\u5f97\u66f4\u52a0\u5fae\u5999\u8d77\u6765\u3002\u82e5\u8bf4\u662f\u670b\u53cb\uff0c\u4f3c\u4e4e\u53c8\u8bf4\u4e0d\u4e0a\uff1b\u82e5\u8bf4\u662f\u654c\u4eba\uff0c\u540c\u6837\u4e5f\u6ca1\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff1b\u82e5\u8bf4\u662f\u60c5\u4eba\uff0c\u5374\u66f4\u591a\u662f\u8089\u4f53\u5173\u7cfb\uff0c\u5f88\u96be\u8bf4\u6709\u4ec0\u4e48\u7231\u60c5\uff1b\u4f46\u82e5\u8981\u8bf4\u4ec5\u4ec5\u662f\u5e8a\u4f34\uff0c\u6216\u8005\u8bf4\u662f\u4e3b\u4eba\u4e0e\u5974\u96b6\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u5bf9\u52b2\u3002</p><p>\u8fd9\u79cd\u5fae\u5999\u4e2d\u7565\u5e26\u5c34\u5c2c\u7684\u6c14\u6c1b\uff0c\u4e24\u4eba\u81ea\u7136\u4e5f\u90fd\u6e05\u695a\u611f\u53d7\u5230\u4e86\uff0c\u4f46\u4ed6\u4eec\u90fd\u662f\u806a\u660e\u4eba\uff0c\u4e92\u76f8\u9ed8\u5951\u5730\u56de\u907f\u8fd9\u4e2a\u8bdd\u9898\uff0c\u5c3d\u53ef\u80fd\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u7ef4\u6301\u7740\u67d0\u79cd\u6682\u65f6\u7684\u5e73\u8861\u3002</p><p>\u6885\u83f2\u65af\u8fdb\u95e8\u65f6\uff0c\u6b63\u770b\u89c1\u743c\u6069\u5728\u7814\u7a76\u9ece\u660e\u4e4b\u77f3\u3002\u5979\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8d70\u8fc7\u6765\uff0c\u7528\u5305\u88f9\u5c06\u5b83\u5305\u8d77\uff0c\u624e\u7d27\uff0c\u653e\u5728\u5e8a\u4e0a\u3002</p><p>\u201c\u6211\u4e3a\u4ec0\u4e48\u78b0\u4e0d\u5230\u5b83\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u9ece\u660e\u4e4b\u77f3\u53ea\u4f1a\u88ab\u5584\u826f\u7684\u5fc3\u89e6\u6478\u5230\uff0c\u201d\u6885\u83f2\u65af\u80cc\u5bf9\u7740\u743c\u6069\uff0c\u6574\u7406\u7740\u521a\u4e70\u6765\u7684\u4e1c\u897f\uff0c\u201c\u800c\u90aa\u6076\u5219\u4f1a\u88ab\u5b83\u4f24\u5bb3\u3002\u201d</p><p>\u201c\u90a3\u6211\u5462\uff1f\u201d</p><p>\u201c\u4f60\u5904\u4e8e\u4e2d\u95f4\u72b6\u6001\uff0c\u6240\u4ee5\u4f60\u4e0d\u4f1a\u88ab\u5b83\u4f24\u5bb3\uff0c\u4f46\u4e5f\u78b0\u4e0d\u5230\u5b83\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u4e8e\u4f60\u6765\u8bf4\uff0c\u5b83\u7b49\u4e8e\u4e0d\u5b58\u5728\u3002\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u8fd9\u4e2a\u8bc4\u4ef7\u542c\u8d77\u6765\uff0c\u5514\uff0c\u5012\u8fd8\u9a6c\u9a6c\u864e\u864e\u3002\u4ed6\u81ea\u77e5\u4e0d\u662f\u4e2a\u5584\u826f\u7684\u4eba\uff0c\u4f46\u5374\u4e5f\u4e0d\u613f\u610f\u88ab\u8bc4\u4ef7\u4e3a\u90aa\u6076\u2014\u2014\u81f3\u5c11\u4e0d\u613f\u610f\u88ab\u6885\u83f2\u65af\u8fd9\u4e48\u8bc4\u4ef7\u3002</p><p>\u5982\u679c\u53ef\u80fd\u7684\u8bdd\uff0c\u4ed6\u8fd8\u662f\u5e0c\u671b\u80fd\u5728\u5973\u5723\u6b66\u58eb\u5fc3\u4e2d\u7559\u4e0b\u4e00\u4e2a\u6bd4\u8f83\u6b63\u9762\u7684\u5f62\u8c61\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u6211\u51fa\u53bb\u4e70\u70b9\u4e1c\u897f\u3002\u201d\u743c\u6069\u8bf4\u3002\u4e24\u4eba\u5fc5\u987b\u6709\u4e00\u4e2a\u7559\u5b88\uff0c\u770b\u7740\u9ece\u660e\u4e4b\u77f3\uff0c\u53e6\u5916\u4e00\u4e2a\u81ea\u7531\u6d3b\u52a8\uff0c\u8fd9\u662f\u4e8b\u5148\u8bf4\u597d\u7684\u3002\u5982\u4eca\u6885\u83f2\u65af\u5df2\u7ecf\u56de\u6765\uff0c\u4ed6\u4e5f\u8be5\u53bb\u4e70\u70b9\u81ea\u5df1\u7684\u79c1\u4eba\u7528\u54c1\u4e86\uff0c\u81f3\u5c11\u50cf\u521a\u624d\u60f3\u5230\u7684\uff0c\u5f04\u51e0\u4ef6\u65b0\u5deb\u5e08\u888d\u3002</p><p>\u201c\u55ef\u3002\u201d\u6885\u83f2\u65af\u5e94\u4e86\u4e00\u58f0\u3002</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u8d70\u51fa\u65c5\u5e97\uff0c\u6309\u7167\u8001\u677f\u6307\u70b9\u7684\u8def\u7ebf\uff0c\u627e\u5230\u4e86\u5e02\u573a\u3002\u4ed6\u8fd9\u7b97\u662f\u7b2c\u4e00\u6b21\u5230\u5916\u9762\u7684\u5927\u57ce\u5e02\u6765\uff0c\u4e0d\u514d\u6709\u4e9b\u597d\u5947\uff0c\u4f46\u770b\u4e86\u4e00\u4f1a\uff0c\u5374\u53d1\u73b0\u600e\u4e48\u611f\u89c9\u548c\u9634\u9b42\u57ce\u6709\u51e0\u5206\u8fd1\u4f3c\u3002\u4e8b\u5b9e\u4e0a\uff0c\u743c\u6069\u4e0d\u77e5\u9053\uff0c\u963f\u5fb7\u5df4\u57ce\u548c\u9634\u9b42\u57ce\u4e00\u6837\uff0c\u4e5f\u662f\u4e2a\u519b\u4e8b\u5316\u7ba1\u7406\u7684\u5730\u65b9\uff0c\u98ce\u683c\u81ea\u7136\u6709\u4e9b\u96f7\u540c\u3002</p><p>\u4ed6\u7c97\u7565\u7edf\u8ba1\u4e86\u4e00\u4e0b\uff0c\u53d1\u73b0\u8def\u4e0a\u770b\u89c1\u7684\u884c\u4eba\u4e2d\uff0c\u5927\u7ea6\u767e\u5206\u4e4b\u4e5d\u5341\u4ee5\u4e0a\u90fd\u662f\u77ee\u4eba\uff0c\u5927\u6982\u53ea\u6709\u4e0d\u5230\u767e\u5206\u4e4b\u5341\u7684\u4eba\u7c7b\uff0c\u800c\u4e14\u5927\u591a\u4e00\u770b\u5c31\u4e0d\u662f\u672c\u5730\u4eba\uff0c\u800c\u662f\u5982\u4ed6\u4e00\u6837\u7684\u5916\u5730\u6765\u5ba2\uff0c\u8fd9\u662f\u4e00\u5ea7\u540d\u526f\u5176\u5b9e\u7684\u77ee\u4eba\u57ce\u3002</p><p>\u77ee\u4eba\u57ce\u81ea\u7136\u8fd8\u662f\u6709\u77ee\u4eba\u7684\u7279\u8272\u7684\uff0c\u6700\u5178\u578b\u7684\u5c31\u662f\u9053\u8def\u4e24\u65c1\u7684\u623f\u5b50\uff0c\u4e2a\u4e2a\u90fd\u77ee\u5c0f\u3001\u65b9\u6b63\u3001\u6566\u5b9e\uff0c\u800c\u4e14\u5c31\u50cf\u5bc6\u5bc6\u9ebb\u9ebb\u7684\u706b\u67f4\u76d2\uff08\u5f53\u7136\u8fd9\u4e2a\u4e16\u754c\u6ca1\u6709\u706b\u67f4\u76d2\u8fd9\u79cd\u4e1c\u897f\uff09\u91cd\u53e0\u5792\u79ef\u5728\u4e00\u8d77\uff0c\u4e2d\u95f4\u5f80\u5f80\u6ca1\u6709\u4e00\u4e1d\u7a7a\u9699\u3002\u4ee5\u4eba\u7c7b\u7684\u773c\u5149\u6765\u770b\uff0c\u8fd9\u79cd\u5c45\u4f4f\u73af\u5883\uff0c\u5b9e\u5728\u662f\u592a\u62e5\u6324\u592a\u538b\u6291\u4e86\uff0c\u4f46\u8fd9\u4e9b\u77ee\u4eba\u5374\u7199\u7199\u6518\u6518\uff0c\u81ea\u5f97\u5176\u4e50\uff0c\u5168\u7136\u4e0d\u89c9\u6709\u4ec0\u4e48\u4e0d\u4fbf\u3002</p><p>\u540c\u65f6\u8981\u5f3a\u8c03\u4e00\u70b9\u7684\u662f\uff0c\u77ee\u4eba\u4eec\u6216\u8bb8\u662f\u953b\u9020\u9ad8\u624b\uff0c\u4f46\u7edd\u5bf9\u4e0d\u662f\u73af\u4fdd\u5148\u950b\u3002\u743c\u6069\u8f6c\u60a0\u4e86\u5c0f\u534a\u4e2a\u57ce\uff0c\u5c45\u7136\u5c31\u6ca1\u770b\u89c1\u4e00\u68f5\u6811\uff0c\u5f53\u7136\u4e5f\u6ca1\u6709\u9c9c\u82b1\u8349\u5730\u3002\u4ed6\u62ac\u8d77\u5934\uff0c\u770b\u89c1\u5341\u51e0\u6839\u70df\u56f1\u9ad8\u8038\uff0c\u5410\u51fa\u6eda\u6eda\u6d53\u70df\u548c\u706b\u7130\uff0c\u53ee\u53ee\u5f53\u5f53\u7684\u91d1\u5c5e\u649e\u51fb\u58f0\u9690\u9690\u4f20\u6765\uff0c\u539f\u672c\u7406\u5f53\u851a\u84dd\u7684\u5929\u7a7a\u90fd\u6709\u4e9b\u53d1\u9ed1\u4e86\u3002</p><p>\u8fd9\u60c5\u5f62\u8ba9\u4ed6\u60f3\u8d77\u524d\u4e16\u5728\u5730\u7403\u4e0a\uff0c\u53d7\u5230\u5de5\u4e1a\u5316\u4e25\u91cd\u6c61\u67d3\u7684\u57ce\u5e02\u3002\u6ca1\u60f3\u5230\u7a7f\u8d8a\u5230\u8fd9\u5947\u5e7b\u4e16\u754c\u6765\uff0c\u5c45\u7136\u8fd8\u80fd\u770b\u5230\u8fd9\u79cd\u7c7b\u4f3c\u7684\u60c5\u666f\u3002\u770b\u8d77\u6765\uff0c\u963f\u5fb7\u5df4\u57ce\u5e94\u8be5\u662f\u94f6\u6708\u8054\u90a6\u7684\u4e00\u5ea7\u5de5\u4e1a\u91cd\u9547\u4e86\uff0c\u5012\u771f\u662f\u8ba9\u8fd9\u4e9b\u77ee\u4eba\u4eec\u53d1\u6325\u6240\u957f\u3002</p><p>\u53e6\u5916\u8ba9\u4ed6\u5f88\u4f24\u5fc3\u7684\u4e00\u70b9\u662f\uff1a\u4ed6\u53d1\u73b0\u5973\u77ee\u4eba\u679c\u7136\u662f\u957f\u80e1\u5b50\u7684\u3002</p><p>\u4ed6\u524d\u4e16\u770b\u8fc7\u4e0d\u5c11\u5947\u5e7b\u5c0f\u8bf4\uff0c\u91cc\u9762\u90fd\u63d0\u5230\u77ee\u4eba\u3002\u5f88\u591a\u4e66\u91cc\u90fd\u8bf4\uff1a\u77ee\u4eba\u4e2d\u7684\u5973\u6027\uff0c\u548c\u7537\u6027\u4e00\u6837\u5b54\u6b66\u6709\u529b\uff0c\u8170\u5927\u8180\u5706\uff0c\u8eab\u6750\u77ee\u80d6\u81c3\u80bf\uff0c\u800c\u4e14\u8fd8\u957f\u80e1\u5b50\u3002</p><p>\u4e0d\u540c\u7684\u79cd\u65cf\u6709\u4e0d\u540c\u7684\u5ba1\u7f8e\u89c2\uff0c\u5bf9\u6b64\u743c\u6069\u5f53\u7136\u53ef\u4ee5\u7406\u89e3\u3002\u6216\u8bb8\u77ee\u4eba\u7537\u6027\u8ba4\u4e3a\u80e1\u5b50\u8d8a\u957f\u7684\u5973\u77ee\u4eba\u8d8a\u53ef\u7231\uff0c\u5bf9\u6b64\u743c\u6069\u4e5f\u6beb\u65e0\u610f\u89c1\u3002\u4f46\u4ed6\u662f\u4e2a\u8d27\u771f\u4ef7\u5b9e\u7684\u4eba\u7c7b\uff0c\u4e07\u4e07\u63a5\u53d7\u4e0d\u4e86\u77ee\u4eba\u7684\u8fd9\u79cd\u5ba1\u7f8e\u89c2\u3002\u5018\u82e5\u4e0a\u8ff0\u63cf\u5199\u5c5e\u5b9e\uff0c\u90a3\u4e5f\u5c31\u662f\u8bf4\uff1a\u81f3\u5c11\u4ee5\u4eba\u7c7b\u7684\u5ba1\u7f8e\u6807\u51c6\u6765\u8bc4\u4ef7\uff0c\u77ee\u4eba\u4e2d\u662f\u7edd\u5bf9\u6ca1\u6709\u7f8e\u5973\u4e86\u2014\u2014\u6216\u8005\u8bf4\u5f97\u66f4\u5e72\u8106\u70b9\uff0c\u77ee\u4eba\u4e2d\u53ea\u6709\u4e11\u5973\u3002</p><p>\u5bf9\u4e8e\u4e00\u540d\u4ee5\u5efa\u7acb\u540e\u5bab\u4e3a\u5fd7\u5411\u4e4b\u4e00\u7684\u6deb\u8d3c\u6765\u8bf4\uff0c\u8fd9\u6d88\u606f\u672a\u514d\u592a\u4ee4\u4eba\u6cae\u4e27\u4e86\u4e9b\u3002</p><p>\u4f46\u4ed6\u540c\u65f6\u8fd8\u8bb0\u5f97\u53e6\u5916\u67d0\u672c\u975e\u5e38\u975e\u5e38\u8457\u540d\u7684\u5947\u5e7b\u5c0f\u8bf4\u91cc\uff0c\u6709\u622a\u7136\u76f8\u53cd\u7684\u770b\u6cd5\uff0c\u8bf4\u5973\u77ee\u4eba\u867d\u7136\u77ee\u5c0f\u4e86\u4e9b\uff0c\u5374\u90fd\u4e2a\u4e2a\u524d\u51f8\u540e\u7fd8\uff0c\u4e30\u4e73\u80a5\u81c0\uff0c\u8eab\u6750\u73b2\u73d1\u6709\u81f4\uff0c\u4eff\u4f5b\u5c0f\u5de7\u7684\u7f8e\u5973\u96d5\u50cf\u3002\u6240\u8c13\u957f\u80e1\u5b50\u4e91\u4e91\uff0c\u7eaf\u719f\u8c23\u4f20\u3002</p><p>\u743c\u6069\u4e5f\u4e0d\u77e5\u9053\u8be5\u76f8\u4fe1\u54ea\u4e00\u79cd\u8bf4\u6cd5\uff0c\u5f53\u7136\u4ed6\u5185\u5fc3\u662f\u5e0c\u671b\u540e\u4e00\u79cd\u3002\u9634\u9b42\u57ce\u91cc\u662f\u6ca1\u6709\u77ee\u4eba\u7684\uff0c\u56fe\u4e66\u9986\u91cc\u7684\u8bb0\u8f7d\u5012\u662f\u548c\u524d\u4e00\u79cd\u8bf4\u6cd5\u4e00\u81f4\uff0c\u8ba9\u743c\u6069\u5f88\u5931\u671b\uff0c\u4f46\u5230\u5e95\u6ca1\u6709\u4eb2\u773c\u89c1\u5230\uff0c\u5fc3\u91cc\u8fd8\u5b58\u4e86\u70b9\u4fa5\u5e78\u5fc3\u7406\u3002</p><p>\u5982\u4eca\u773c\u89c1\u4e3a\u5b9e\uff0c\u8fd9\u70b9\u4fa5\u5e78\u5fc3\u7406\u8f70\u7136\u7834\u706d\u4e86\u3002</p><p>\u201c\u4ed6\u5988\u7684\uff0c\u4e3a\u4ec0\u4e48\u6211\u660e\u660e\u8bb0\u5f97\u90a3\u672c\u5c0f\u8bf4\u91cc\u8bf4\u5973\u77ee\u4eba\u90fd\u662f\u7f8e\u5973\u7684\u3002\u90a3\u4e66\u53eb\u4ec0\u4e48\u540d\u5b57\u6765\u7740\uff0c\u5982\u679c\u6709\u671d\u4e00\u65e5\u80fd\u56de\u5230\u5730\u7403\uff0c\u4e00\u5b9a\u627e\u4ed6\u7b97\u8d26\u2026\u2026\u5443\uff0c\u4e0d\u5bf9\uff0c\u60f3\u8d77\u6765\u4e86\u2026\u2026\u201d</p><p>\u6012\u53d1\u51b2\u51a0\u7684\u743c\u6069\u7a81\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u90a3\u672c\u5c0f\u8bf4\u7684\u540d\u5b57\uff0c\u597d\u50cf\u662f\u53eb\u4ec0\u4e48\u963f\u91cc\u5e03\u8fbe\u5e74\u4ee3\u8bb0\u2026\u2026\u5982\u679c\u6ca1\u8bb0\u9519\u7684\u8bdd\uff0c\u4f3c\u4e4e\u662f\u672c18X\u7684\u5c11\u513f\u4e0d\u5b9c\u8bfb\u7269\uff0c\u4f5c\u8005\u662f\u4e2a\u80d6\u5b50\u2026\u2026</p><p>\u7b97\u4e86\uff0c\u4e0d\u8ddf\u90a3\u4e2a\u6b7b\u80d6\u5b50\u8ba1\u8f83\u3002</p></article>"}}}]);
