(window.webpackJsonp=window.webpackJsonp||[]).push([[820],{843:function(p){p.exports={chapterTitle:null,title:"\u7b2c817\u7ae0 \u51fa\u4e8b\u4e86",content:"<article><h2>\u7b2c817\u7ae0 \u51fa\u4e8b\u4e86</h2><p>\u3000\u3000\u9152\u5bb4\u9009\u5728\u7701\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u6885\u56ed\u9152\u5e97\u8fdb\u884c\uff0c\u53cc\u65b9\u90fd\u662f\u4e24\u5730\u7684\u653f\u5e9c\u5b98\u5458\uff0c\u5f20\u606a\u5dee\u4e0d\u591a\u5230\u9152\u4f1a\u7ed3\u675f\u624d\u8d76\u5230\u6885\u56ed\u9152\u5e97\u53bb\u3002</p><p>\u3000\u3000\u6885\u56ed\u662f\u6c11\u56fd\u521d\u671f\u7684\u5efa\u7b51\uff0c\u66fe\u662f\u56fd\u6c11\u515a\u5b98\u5458\u7684\u79c1\u5b85\uff0c\u89e3\u653e\u540e\u5c31\u4e00\u76f4\u662f\u4e1c\u6d77\u7701\u59d4\u3001\u7701\u653f\u5e9c\u7684\u62db\u5f85\u5bbe\u9986\u3002</p><p>\u3000\u3000\u6885\u56ed\u80cc\u6795\u7740\u6885\u82b1\u5c71\uff0c\u5efa\u90ba\u6885\u82b1\u5c71\u867d\u4e0d\u53ca\u60e0\u5c71\u7684\u5341\u91cc\u9999\u96ea\u6d77\uff0c\u4f46\u5230\u8d4f\u6885\u5b63\u8282\u4e5f\u662f\u597d\u53bb\u5904\u3002\u6b64\u65f6\u7684\u6885\u5c71\u82b1\u9664\u4e86\u7a00\u758f\u6a2a\u659c\u7684\u679d\u6860\u4e4b\u5916\uff0c\u6ca1\u6709\u4ec0\u4e48\u770b\u5904\uff0c\u5012\u662f\u6cbf\u8def\u8fc7\u6765\u6ee1\u5c71\u6ee1\u4e18\u7684\u6843\u82b1\u751a\u662f\u5a07\u8273\u3002\u5f20\u606a\u3001\u7fdf\u4e39\u9752\u4e0e\u5468\u6e38\u6c47\u5408\u540e\u5728\u6885\u56ed\u504f\u9662\u53a2\u623f\u6539\u6210\u7684\u5385\u91cc\u7a0d\u7b49\u4e86\u7247\u523b\uff0c\u9676\u664b\u6ca1\u6709\u51fa\u5e2d\u665a\u4e0a\u7684\u9152\u5bb4\uff0c\u674e\u8fdc\u6e56\u3001\u6881\u4f1f\u6cd5\u3001\u7f57\u541b\u3001\u8d75\u6709\u4f26\u8fd8\u6709\u9646\u6587\u592b\u7b49\u4eba\u5c31\u4ece\u90a3\u8fb9\u7684\u9152\u5e2d\u79bb\u5f00\u8d76\u5230\u8fd9\u91cc\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u6881\u4f1f\u6cd5\u3001\u8d75\u6709\u4f26\u90fd\u662f\u7b2c\u4e8c\u6b21\u89c1\u9762\uff0c\u4e0d\u8fc7\u5f7c\u6b64\u95f4\u53ef\u6ca1\u6709\u4ec0\u4e48\u751f\u758f\u7684\u5730\u65b9\uff0c\u5f00\u59cb\u8c08\u4e86\u5f88\u591a\u533a\u57df\u7ecf\u6d4e\u5408\u4f5c\u7684\u8bdd\u9898\u3002</p><p>\u3000\u3000\u5728\u5730\u56fe\u4e0a\u5448\u5012\u52fe\u5f62\u7684\u5c0f\u6c5f\u4e0b\u6e38\u6d41\u6bb5\uff0c\u805a\u96c6\u4e86\u4e24\u7701\u6700\u4e3b\u8981\u7684\u4e94\u5ea7\u57ce\u5e02\uff0c\u4e1c\u6d77\u7684\u5efa\u90ba\u3001\u60e0\u5c71\u3001\u6d77\u5dde\uff0c\u6c5f\u5357\u7684\u91d1\u5c71\u3001\u957f\u4ead\uff0c\u8fd9\u4e94\u5ea7\u57ce\u5e02\u8ddd\u79bb\u5f88\u65e9\uff0c\u957f\u4ead\u4e0e\u5efa\u90ba\u76f8\u8ddd\u6700\u8fdc\uff0c\u4e5f\u4e0d\u8fc7\u53ea\u76f8\u8ddd\u4e8c\u767e\u516d\u5341\u516c\u91cc\uff0c\u533a\u57df\u8054\u5408\u76f8\u4e92\u4fc3\u8fdb\u7684\u53d1\u5c55\u6f5c\u529b\u8fdc\u8f83\u5404\u81ea\u4e3a\u9635\u3001\u76f8\u4e92\u62c6\u53f0\u8981\u5f3a\u8bb8\u591a\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e00\u6b65\u4e00\u4e2a\u811a\u5370\u8d70\u5230\u4eca\u5929\uff0c\u4ece\u6700\u521d\u7684\u6d77\u5dde\u4e00\u9685\u5230\u5411\u4e1c\u6d77\u7701\u6700\u91cd\u8981\u7684\u5efa\u90ba\u3001\u60e0\u5c71\u4e24\u5ea7\u57ce\u5e02\u8fdb\u884c\u4ea7\u4e1a\u5e03\u5c40\uff0c\u5982\u4eca\u53c8\u5f00\u59cb\u5728\u56fd\u5185\u5404\u4e3b\u8981\u57ce\u5e02\u5448\u5e45\u5c04\u72b6\u7684\u8fdb\u884c\u6280\u672f\u6295\u8d44\uff0c\u4e5f\u4e0d\u6392\u9664\u4f1a\u5728\u5176\u4ed6\u5730\u533a\u6295\u8d44\u5efa\u8bbe\u5236\u9020\u57fa\u5730\uff0c\u4f46\u662f\u5f20\u606a\u59cb\u7ec8\u5c06\u5c0f\u6c5f\u6d41\u57df\u89c6\u4e3a\u9526\u6e56\u7684\u6839\u57fa\u6240\u5728\u2014\u2014\u65e2\u975e\u5c40\u9650\u4e8e\u6d77\u5dde\uff0c\u4e5f\u975e\u5c40\u9650\u4e8e\u4e1c\u6d77\uff0c\u800c\u662f\u6574\u4e2a\u5c0f\u6c5f\u6d41\u57df\u3002\u6216\u8bb8\u4e4b\u524d\u7684\u6295\u8d44\u90fd\u96c6\u4e2d\u5728\u5efa\u90ba\u3001\u60e0\u5c71\u3001\u5efa\u90ba\u4e09\u5730\uff0c\u4f46\u662f\u9526\u6e56\u4eca\u540e\u4e5f\u4f1a\u8003\u8651\u5411\u91d1\u5c71\u3001\u957f\u4ead\u751a\u81f3\u7ae0\u5dde\u8fdb\u884c\u9002\u5f53\u7684\u6295\u8d44\u503e\u659c\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8981\u662f\u4e24\u7701\u8fd8\u5c40\u9650\u5728\u72ed\u9698\u7684\u533a\u57df\u7ecf\u6d4e\u7ade\u4e89\u4e4b\u4e2d\u65e0\u6cd5\u81ea\u62d4\uff0c\u9526\u6e56\u5411\u91d1\u5c71\u6216\u957f\u4ead\u8fdb\u884c\u5927\u89c4\u6a21\u7684\u4ea7\u4e1a\u6295\u8d44\uff0c\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u7b49\u4eba\u5373\u4f7f\u4e0d\u4f1a\u8868\u793a\u51fa\u6765\u4ec0\u4e48\u4e0d\u6ee1\uff0c\u5fc3\u91cc\u591a\u534a\u4f1a\u6709\u82a5\u8482\uff0c\u6b64\u65f6\u80fd\u4ece\u533a\u57df\u7ecf\u6d4e\u5408\u4f5c\u7684\u89d2\u5ea6\u53bb\u8003\u8651\u95ee\u9898\uff0c\u5bf9\u9526\u6e56\u7684\u4ea7\u4e1a\u5e03\u5c40\u5f53\u7136\u80fd\u66f4\u5bb9\u6613\u63a5\u53d7\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6b63\u5f0f\u8ddf\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u63d0\u53ca\u9526\u6e56\u6b63\u7b79\u5212\u7740\u52a8\u7528\u5de8\u8d44\u6536\u8d2d\u91d1\u5c71\u7684\u6668\u66e6\u7eb8\u4e1a\u96c6\u56e2\uff0c\u5c06\u4ee5\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u4e3a\u6954\u673a\uff0c\u5c55\u5f00\u5728\u6c5f\u5357\u7701\u7684\u4ea7\u4e1a\u5e03\u5c40\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u4ed6\u4eec\u5fc3\u91cc\u6e05\u695a\u6c11\u8425\u673a\u6784\u6536\u8d2d\u5927\u578b\u56fd\u4f01\u5728\u5f53\u524d\u56fd\u5185\u6240\u80fd\u906d\u9047\u7684\u963b\u529b\u80fd\u6709\u591a\u5927\uff0c\u4e5f\u6e05\u695a\u9526\u6e56\u7684\u5fd7\u5411\u9ad8\u8fdc\uff0c\u65e0\u6cd5\u8981\u6c42\u9526\u6e56\u5c06\u81ea\u5df1\u7684\u624b\u811a\u6346\u4f4f\u53ea\u5c40\u9650\u5728\u4e1c\u6d77\u53d1\u5c55\uff0c\u5728\u6881\u4f1f\u6cd5\u3001\u8d75\u6709\u4f26\u7684\u9762\u524d\uff0c\u5bf9\u9526\u6e56\u610f\u56fe\u6536\u8d2d\u5f53\u7136\u8868\u793a\u652f\u6301\u3002</p><p>\u3000\u3000\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u524d\u671f\u7b79\u5907\u5de5\u4f5c\u662f\u4ee5\u9a6c\u6765\u897f\u4e9a\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u65d7\u4e0b\u7684\u4e91\u6e90\u7eb8\u4e1a\u4e0e\u6668\u66e6\u7eb8\u4e1a\u8fdb\u884c\u5408\u8d44\u8c08\u5224\u7684\u540d\u4e49\u8fdb\u884c\u7684\uff0c\u5df2\u7ecf\u8fdb\u884c\u4e86\u5c06\u8fd1\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u3002\u6668\u66e6\u7eb8\u4e1a\u7684\u7ba1\u7406\u5c42\u90fd\u4e0d\u6e05\u695a\u4e8b\u60c5\u7684\u771f\u76f8\uff0c\u5c31\u662f\u4ee5\u5408\u8d44\u540d\u4e49\u8fdb\u884c\u7684\u63a5\u89e6\uff0c\u4e5f\u90fd\u5904\u4e8e\u76f8\u5bf9\u4e25\u683c\u7684\u4fdd\u5bc6\u72b6\u6001\u3002\u6c5f\u5357\u7701\u5c31\u4e5f\u51e0\u4e2a\u4eba\u77e5\u9053\u80cc\u540e\u662f\u9526\u6e56\u5bf9\u6668\u66e6\u7eb8\u4e1a\u6709\u7740\u5174\u8da3\uff0c\u8fd9\u4e48\u505a\uff0c\u4e5f\u662f\u5e0c\u671b\u80fd\u51cf\u8f7b\u524d\u671f\u7b79\u5907\u5de5\u4f5c\u53ef\u80fd\u9047\u5230\u7684\u963b\u529b\u3002</p><p>\u3000\u3000\u524d\u671f\u7684\u7b79\u5907\u5de5\u4f5c\u662f\u4e3a\u4e86\u5bf9\u6668\u66e6\u7eb8\u4e1a\u7684\u8d22\u52a1\u3001\u7ecf\u8425\u72b6\u51b5\u80fd\u6709\u4e00\u4e2a\u6bd4\u8f83\u6df1\u5165\u3001\u5168\u9762\u7684\u4e86\u89e3\uff0c\u65b9\u4fbf\u9526\u6e56\u4e3a\u5168\u8d44\u6536\u8d2d\u62a5\u4ef7\uff0c\u6b64\u65f6\u5468\u6e38\u968f\u5f20\u606a\u8fc7\u6765\u5c31\u662f\u4ee3\u8868\u65b0\u5149\u7eb8\u4e1a\u6b63\u5f0f\u5411\u6881\u4f1f\u6cd5\u9012\u4ea4\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u8981\u7ea6\u4e66\u3002</p><p>\u3000\u300022\u4ebf\u7684\u62a5\u4ef7\uff0c\u5e76\u6ca1\u6709\u4f4e\u4e8e\u6881\u4f1f\u6cd5\u672c\u4eba\u5bf9\u6668\u66e6\u7eb8\u4e1a\u7684\u4f30\u4ef7\uff0c\u751a\u81f3\u6bd4\u6881\u4f1f\u6cd5\u7684\u9884\u671f\u8981\u9ad8\u8bb8\u591a\u3002</p><p>\u3000\u3000\u6668\u66e6\u7eb8\u4e1a\u867d\u7136\u8fd8\u6709\u76f8\u5f53\u4e0d\u9519\u7684\u76c8\u5229\uff0c\u4f46\u662f\u76c8\u5229\u5b8c\u5168\u662f\u4ee5\u727a\u7272\u5468\u8fb9\u751f\u6001\u73af\u5883\u800c\u5f97\u6765\u7684\uff0c\u6263\u9664\u751f\u6001\u73af\u5883\u6210\u672c\uff0c\u6668\u66e6\u7eb8\u4e1a\u8fd8\u5c06\u4ea7\u751f\u76f8\u5f53\u7684\u4e8f\u635f\u3002\u9526\u6e56\u4ee5\u6b64\u62a5\u4ef7\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\uff0c\u8fd8\u627f\u8bfa\u54cd\u5e94\u56fd\u5bb6\u653f\u7b56\u5173\u95ed\u6c61\u67d3\u4e25\u91cd\u7684\u8349\u6d46\u751f\u4ea7\u7ebf\uff0c\u8fd8\u5c06\u540e\u7eed\u6295\u5165\u5de8\u8d44\u5bf9\u751f\u4ea7\u7ebf\u4e0e\u5de5\u4e1a\u5e9f\u6c34\u5904\u7406\u8bbe\u65bd\u8fdb\u884c\u5168\u9762\u7684\u6280\u672f\u6539\u9020\u4e0e\u5347\u7ea7\uff0c\u4fdd\u8bc1\u5f7b\u5e95\u89e3\u51b3\u6668\u66e6\u7eb8\u4e1a\u7684\u6c61\u67d3\u95ee\u9898\uff0c\u751a\u81f3\u6ca1\u6709\u5728\u7a0e\u6536\u65b9\u9762\u63d0\u989d\u5916\u7684\u8981\u6c42\u4ee5\u514d\u51cf\u5c11\u5730\u65b9\u8d22\u653f\u7684\u6536\u5165\u3002</p><p>\u3000\u3000\u4e00\u65e6\u5f7b\u5e95\u89e3\u51b3\u6668\u66e6\u7eb8\u4e1a\u8fd9\u4e2a\u996e\u9a6c\u6cb3\u6d41\u57df\u7684\u4e3b\u8981\u6c61\u67d3\u6e90\uff0c\u5c45\u6c11\u7684\u751f\u6d3b\u73af\u5883\u5f97\u5230\u6539\u5584\u90a3\u662f\u4e00\u5b9a\u7684\uff0c\u53e6\u5916\uff0c\u6d41\u57df\u5185\u7684\u519c\u6797\u4e1a\u3001\u6e14\u4e1a\u3001\u822a\u8fd0\u4e1a\u4ee5\u53ca\u65c5\u6e38\u4e1a\u7b49\u4ea7\u4e1a\u90fd\u4f1a\u590d\u82cf\uff0c\u8fd9\u5176\u4e2d\u7684\u7ecf\u6d4e\u8d26\uff0c\u6881\u4f1f\u6cd5\u90fd\u4f1a\u7b97\uff0c\u4f46\u662f\u5230\u8fd9\u65f6\u5019\uff0c\u4ed6\u4ecd\u7136\u6709\u7740\u5176\u4ed6\u7684\u62c5\u5fe7\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5927\u6982\u662f\u8fc4\u4eca\u4e3a\u6b62\u56fd\u5185\u89c4\u6a21\u6700\u5927\u7684\u4e00\u6869\u6c11\u4f01\u6536\u8d2d\u56fd\u6709\u8d44\u4ea7\u6848\uff0c\u963b\u529b\u4e0d\u5bb9\u5c0f\u89c6\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u4e5f\u9887\u4e3a\u8ba4\u540c\u6881\u4f1f\u6cd5\u7684\u62c5\u5fe7\uff0c\u56fd\u5185\u6c11\u4f01\u6536\u8d2d\u5730\u65b9\u56fd\u6709\u8d44\u4ea7\u5c61\u6709\u53d1\u751f\uff0c\u4f46\u662f\u89c4\u6a21\u3001\u91d1\u989d\u90fd\u6781\u5176\u6709\u9650\uff0c\u53ef\u4ee5\u89c6\u4e3a\u5730\u65b9\u641e\u56fd\u4f01\u4f53\u5236\u6539\u9769\u7684\u8bd5\u70b9\u3002\u8fd9\u4e48\u5927\u89c4\u6a21\u7684\u6c11\u4f01\u6536\u8d2d\u56fd\u6709\u8d44\u4ea7\u6848\uff0c\u5f88\u53ef\u80fd\u4f1a\u5f15\u53d1\u5927\u4e89\u8bae\u3002\u8981\u4e48\u6881\u4f1f\u6cd5\u6210\u4e3a\u63a8\u52a8\u56fd\u6709\u4f53\u5236\u6539\u9769\u7684\u5148\u950b\u4eba\u7269\uff0c\u4fc3\u4f7f\u6536\u8d2d\u6848\u83b7\u5f97\u901a\u8fc7\uff1b\u8981\u4e48\u6536\u8d2d\u6848\u88ab\u5426\u51b3\uff0c\u90a3\u5bf9\u6881\u4f1f\u6cd5\u7684\u653f\u6cbb\u751f\u6daf\u7684\u5f71\u54cd\u5c06\u4e0d\u5bb9\u5ffd\u89c6\u3002\u53cd\u800c\u9526\u6e56\u5728\u653f\u6cbb\u4e0a\u53d7\u5230\u7684\u5f71\u54cd\u8981\u5c0f\u5f97\u591a\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u8fd9\u4efd\u65b9\u6848\u963b\u529b\u592a\u5927\uff0c\u65b0\u5149\u7eb8\u4e1a\u8fd8\u5907\u6709\u4e00\u4efd\u65b9\u6848\uff0c\u201d\u5f20\u606a\u5012\u662f\u5145\u5206\u8003\u8651\u5230\u6881\u4f1f\u6cd5\u7684\u987e\u8651\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u65b0\u5149\u7eb8\u4e1a\u53ef\u4ee5\u4e0e\u9a6c\u6765\u897f\u4e9a\u7684\u4e91\u6e90\u7eb8\u4e1a\u5408\u8d44\u6210\u7acb\u6d77\u5916\u516c\u53f8\uff0c\u518d\u7531\u6d77\u5916\u516c\u53f8\u5411\u6668\u66e6\u7eb8\u4e1a\u6ce8\u8d4418\u4ebf\u4ee5\u6362\u53d651\uff05\u7684\u80a1\u4efd\uff0c\u5176\u4ed6\u76f8\u5173\u6761\u4ef6\u53ea\u505a\u7ec6\u8282\u4e0a\u7684\u8c03\u6574\uff0c\u5e76\u65e0\u5927\u7684\u53d8\u66f4\u2026\u2026\u201d</p><p>\u3000\u3000\u5408\u8d44\u7684\u8bdd\uff0c\u6668\u66e6\u7eb8\u4e1a\u7684\u8d44\u4ea7\u53ea\u88ab\u4f30\u7b9718\u4ebf\u4e0d\u5230\uff0c\u5168\u8d44\u6536\u8d2d\u5374\u9ad8\u8fbe22\u4ebf\uff0c4\u4e2a\u4ebf\u7684\u5dee\u503c\u662f\u9526\u6e56\u4e3a\u5168\u8d44\u6536\u8d2d\u652f\u4ed8\u7684\u6ea2\u4ef7\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u63a5\u8fc7\u5468\u6e38\u9012\u8fc7\u53bb\u7684\u53e6\u4e00\u4efd\u5408\u8d44\u8981\u7ea6\u4e66\uff0c\u7ffb\u770b\u7740\uff0c\u5fc3\u91cc\u6743\u8861\u6536\u8d2d\u4e0e\u5408\u8d44\u4e4b\u95f4\u7684\u5229\u76ca\u5dee\u5f02\u4e0e\u963b\u529b\u5927\u5c0f\u3002\u663e\u7136\u4ee5\u6d77\u5916\u516c\u53f8\u7684\u540d\u4e49\u8fdb\u884c\u5408\u8d44\uff0c\u9047\u5230\u7684\u963b\u529b\u8981\u5c0f\u5f97\u591a\uff0c\u4f46\u662f\u5bf9\u5f53\u524d\u6c5f\u5357\u7701\u653f\u5e9c\u5bf9\u8d44\u91d1\u7684\u9965\u6e34\u5e76\u65e0\u7f13\u89e3\u3002\u56e0\u4e3a\u5408\u8d44\u7684\u8bdd\uff0c\u8d44\u5165\u768418\u4ebf\u73b0\u91d1\u4f1a\u622a\u7559\u5728\u6668\u66e6\u7eb8\u4e1a\u5185\u90e8\u7528\u4e8e\u4f01\u4e1a\u81ea\u8eab\u7684\u53d1\u5c55\uff0c\u6c5f\u5357\u7701\u653f\u5e9c\u62ff\u4e0d\u5230\u4e00\u5206\u94b1\uff1b\u8981\u662f\u7ed9\u9526\u6e56\u5168\u8d44\u6536\u8d2d\uff0c\u5c31\u662f\u7701\u91cc\u4ee522\u4ebf\u7684\u4ef7\u683c\u5c06\u6668\u66e6\u7eb8\u4e1a\u7684\u6240\u6709\u8d44\u4ea7\u90fd\u51fa\u552e\u7ed9\u9526\u6e56\uff0c\u7701\u91cc\u62ff\u5230\u73b0\u91d1\u518d\u4e5f\u4e0d\u5e72\u6d89\u6668\u66e6\u7eb8\u4e1a\u7684\u4e8b\u52a1\u3002</p><p>\u3000\u3000\u770b\u7740\u6881\u4f1f\u6cd5\u773c\u775b\u6d41\u8f6c\u7740\u8fdf\u7591\u4e0d\u5b9a\u7684\u5fc3\u601d\uff0c\u5f20\u606a\u8bdd\u950b\u8f7b\u8f7b\u4e00\u8f6c\uff0c\u8bf4\u9053\uff1a\u201c\u6881\u7701\u957f\u4eca\u5929\u53ef\u53ea\u80fd\u5e26\u4e00\u4efd\u65b9\u6848\u56de\u53bb\uff0c\u4e0d\u6653\u5f97\u6881\u7701\u957f\u60a8\u4f1a\u505a\u4ec0\u4e48\u9009\u62e9\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ea\u80fd\u5e26\u4e00\u4efd\u56de\u53bb\uff1f\u201d\u6881\u4f1f\u6cd5\u8be7\u5f02\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u4ed6\u53ef\u662f\u60f3\u7740\u5c06\u4e24\u4efd\u65b9\u6848\u5e26\u56de\u53bb\u4ed4\u7ec6\u7684\u6743\u8861\u5229\u5f0a\uff0c\u751a\u81f3\u53ef\u4ee5\u5c06\u4e24\u4efd\u65b9\u6848\u62ff\u5230\u7701\u653f\u5e9c\u529e\u516c\u4f1a\u8bae\u6216\u8005\u7701\u5e38\u59d4\u4f1a\u8bae\u53bb\u8ba8\u8bba\uff0c\u6ca1\u60f3\u5230\u9526\u6e56\u53ea\u80af\u63d0\u4f9b\u4e00\u4efd\u65b9\u6848\u4f9b\u6c5f\u5357\u7701\u51b3\u7b56\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u7684\u4e3b\u8981\u9886\u5bfc\u9664\u4e86\u4ed6\u80fd\u770b\u5230\u4e24\u4efd\u65b9\u6848\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u53ea\u80fd\u770b\u5230\u4ed6\u5728\u8fd9\u91cc\u505a\u51fa\u9009\u62e9\u7684\u4e00\u4efd\u65b9\u6848\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u95ee\u4e86\u4e00\u53e5\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u5e76\u4e0d\u60f3\u5728\u6668\u66e6\u7eb8\u4e1a\u7684\u4e8b\u60c5\u4e0a\u82b1\u592a\u957f\u7684\u65f6\u95f4\uff0c\u4e00\u65e6\u6668\u66e6\u7eb8\u4e1a\u8fd9\u6761\u8def\u8d70\u4e0d\u901a\uff0c\u6211\u4eec\u4e5f\u5e0c\u671b\u80fd\u5c3d\u5feb\u7684\u9009\u62e9\u5176\u4ed6\u9053\u8def\u6765\u52a0\u901f\u65b0\u5149\u7eb8\u4e1a\u7684\u53d1\u5c55\u3002\u201d\u5f20\u606a\u89e3\u91ca\u9053\u3002</p><p>\u3000\u3000\u7684\u786e\uff0c\u63d0\u4f9b\u4e24\u4efd\u65b9\u6848\u4f1a\u8ba9\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u5728\u8fdb\u884c\u51b3\u7b56\u65f6\u6d6a\u8d39\u592a\u591a\u7684\u65f6\u95f4\u7528\u4e8e\u6743\u8861\u4e24\u4efd\u65b9\u6848\u7684\u5229\u5f0a\uff0c\u8981\u662f\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u8981\u6c42\u4e24\u4efd\u65b9\u6848\u540c\u65f6\u5f00\u542f\u8c08\u5224\uff0c\u6700\u7ec8\u518d\u7531\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u51b3\u7b56\uff0c\u5f20\u606a\u5927\u6982\u8fde\u54ed\u7684\u5fc3\u601d\u90fd\u6709\u2014\u2014\u8fd9\u6837\u7684\u8c08\u5224\uff0c\u6ca1\u6709\u4e00\u5e74\u534a\u8f7d\u7684\u95f2\u5de5\u592b\u80af\u5b9a\u8c08\u4e0d\u4e0b\u6765\u3002\u5c31\u50cf\u5f53\u521d\u4e09\u661f\u4e0e\u9999\u96ea\u6d77\u7684\u5408\u8d44\u8c08\u5224\u8fdb\u884c\u4e86\u4e00\u5e74\u534a\uff0c\u6700\u540e\u8fd8\u8981\u9526\u6e56\u6a2a\u63d2\u4e00\u817f\u5c06\u4e8b\u60c5\u6405\u9ec4\u4e86\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u8981\u6c42\u6881\u4f1f\u6cd5\u8fd9\u65f6\u5019\u5c31\u505a\u51fa\u51b3\u5b9a\u4e8c\u8005\u53d6\u4e00\uff0c\u5f20\u606a\u7684\u6001\u5ea6\u4e5f\u591f\u5f3a\u786c\u7684\uff0c\u7f57\u541b\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u501f\u7740\u4e0e\u674e\u8fdc\u6e56\u6362\u773c\u795e\u7684\u5f53\u513f\u6253\u91cf\u4e86\u4ed6\u4e00\u773c\uff0c\u89c1\u674e\u8fdc\u6e56\u8138\u4e0a\u5e76\u65e0\u82a5\u8482\uff0c\u5fc3\u60f3\uff1a\u5f20\u606a\u5728\u8981\u4e0d\u8981\u6025\u7740\u6295\u8d44\u5236\u6d46\u5de5\u5382\u7684\u95ee\u9898\u4e0e\u674e\u8fdc\u6e56\u4e4b\u95f4\u8d77\u7684\u77db\u76fe\uff0c\u5927\u6982\u4e5f\u968f\u7740\u65f6\u95f4\u800c\u51b2\u6de1\u6e10\u65e0\u4e86\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u6027\u5b50\u9887\u4e3a\u5f3a\u52bf\uff0c\u522b\u4eba\u4ee5\u5f3a\u52bf\u7684\u59ff\u6001\u51fa\u73b0\u5728\u4ed6\u9762\u524d\uff0c\u5bb9\u6613\u8d77\u51b2\u7a81\uff1b\u6881\u4f1f\u6cd5\u6027\u5b50\u5bbd\u548c\uff0c\u5bf9\u5f20\u606a\u7684\u8981\u6c42\u867d\u7136\u89c9\u5f97\u6709\u4e9b\u8be7\u5f02\uff0c\u5012\u4e5f\u4e0d\u4f1a\u5f15\u8d77\u53cd\u611f\u3002\u5f53\u7136\uff0c\u4e5f\u4e0e\u9526\u6e56\u65e5\u76ca\u91cd\u8981\u7684\u5730\u4f4d\u6709\u5173\u3002</p><p>\u3000\u3000\u8d75\u6709\u4f26\u5728\u6668\u66e6\u7eb8\u4e1a\u7684\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e5f\u65e0\u6cd5\u5e2e\u6881\u4f1f\u6cd5\u62ff\u4e3b\u610f\uff0c\u4ed6\u751a\u81f3\u5728\u914d\u5408\u9526\u6e56\u5bf9\u6668\u66e6\u7684\u6536\u8d2d\uff0c\u53ea\u662f\u4e0d\u4f1a\u5728\u6881\u4f1f\u6cd5\u9762\u524d\u8868\u9732\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8fd9\u6b21\u4e24\u5730\u7684\u653f\u5e9c\u5b98\u5458\u63a5\u89e6\uff0c\u5f20\u606a\u8d76\u8fc7\u6765\u51d1\u70ed\u95f9\uff0c\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u4e3a\u6668\u66e6\u7eb8\u4e1a\u7684\u4e8b\u60c5\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u8d81\u7740\u674e\u7701\u957f\u3001\u6881\u7701\u957f\u4e24\u4eba\u90fd\u5728\uff0c\u6709\u4e2a\u60c5\u51b5\u8981\u8ddf\u4f60\u4eec\u901a\u62a5\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u8fd8\u6b63\u5934\u75bc\u5728\u9526\u6e56\u63d0\u4ea4\u7684\u4e24\u4efd\u65b9\u6848\u95f4\u96be\u4ee5\u51b3\u7b56\uff0c\u8fd9\u65f6\u5019\u53c8\u542c\u5f20\u606a\u90d1\u91cd\u5176\u4e8b\u7684\u8bf4\u8981\u901a\u62a5\u5176\u4ed6\u60c5\u51b5\uff0c\u7cbe\u795e\u7a0d\u632f\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u614e\u91cd\u5176\u4e8b\u7684\u8bf4\u4ec0\u4e48\u4e8b\u60c5\uff0c\u603b\u8ba9\u6211\u6709\u83ab\u660e\u7684\u62c5\u5fe7\u2026\u2026\u201d</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u5fae\u5fae\u4e00\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u80fd\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u201c\u5012\u4e5f\u4e0d\u662f\u591a\u4e25\u91cd\u7684\u4e8b\u60c5\uff0c\u53ea\u662f\u89c9\u5f97\u8fd9\u4e2a\u60c5\u51b5\u6709\u5fc5\u8981\u8ba9\u4e24\u4f4d\u7701\u957f\u77e5\u9053\u4e00\u4e0b\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u4e1c\u6d77\u5927\u5b66\u6559\u6388\u5468\u747e\u73ba\u53d7\u56fd\u52a1\u9662\u7a00\u571f\u529e\u59d4\u6258\uff0c\u5bf9\u7a00\u571f\u7b49\u7a00\u6709\u91d1\u5c5e\u4ea7\u4e1a\u653f\u7b56\u8c03\u6574\u8fdb\u884c\u5b8f\u89c2\u7ecf\u6d4e\u9762\u7684\u8c03\u67e5\u4e0e\u8003\u7814\uff0c\u5df2\u6709\u53ef\u9760\u7684\u6d88\u606f\u8bc1\u5b9e\uff0c\u5468\u747e\u73ba\u8fd9\u6bb5\u65f6\u95f4\u4e0e\u91d1\u5c71\u534e\u7a00\u3001\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u7684\u7ba1\u7406\u9ad8\u5c42\u6765\u5f80\u4ece\u5bc6\u3002\u6216\u8bb8\u6881\u7701\u957f\u8fd8\u4e0d\u5927\u6e05\u695a\uff0c\u91d1\u5c71\u534e\u7a00\u662f\u7ae0\u5dde\u76d7\u91c7\u7a00\u571f\u77ff\u8d44\u6e90\u6d41\u5411\u5e02\u573a\u6700\u4e3b\u8981\u7684\u6e20\u9053\u2026\u2026\u201d</p><p>\u3000\u3000\u4e1c\u6d77\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u77ff\u4ea7\uff0c\u6c5f\u5357\u7ae0\u5dde\u5374\u4ee5\u77ff\u4e1a\u4e3a\u6700\u4e3b\u8981\u7684\u652f\u67f1\u4ea7\u4e1a\u3002\u4f5c\u4e3a\u5168\u56fd\u6700\u4e3b\u8981\u7684\u4e24\u4e2a\u7a00\u571f\u77ff\u8d44\u6e90\u6240\u5728\u5730\u4e4b\u4e00\uff0c\u6c5f\u5357\u7701\u5728\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u8c03\u6574\u7684\u95ee\u9898\u6709\u7740\u76f8\u5f53\u7684\u8bdd\u8bed\u6743\u3002</p><p>\u3000\u3000\u4ece\u5730\u65b9\u7ecf\u6d4e\u7684\u89d2\u5ea6\u6765\u8bf4\uff0c\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u4ee5\u5468\u747e\u73ba\u7684\u89c2\u70b9\u8fdb\u884c\u8c03\u6574\uff0c\u5141\u8bb8\u6c11\u8d44\u3001\u5916\u8d44\u8fdb\u5165\uff0c\u80fd\u58ee\u5927\u7ae0\u5dde\u7684\u77ff\u4e1a\u4ea7\u4e1a\u89c4\u6a21\uff0c\u5bf9\u53d1\u5c55\u6c5f\u5357\u3001\u7ae0\u5dde\u7684\u5730\u65b9\u7ecf\u6d4e\u53d1\u5c55\u66f4\u6709\u5229\u4e00\u4e9b\uff1b\u5f53\u7136\u8fd9\u4e2a\u6709\u5229\u8fd8\u5c40\u9650\u5728\u56db\u4e94\u5e74\u7684\u671f\u9650\u4e4b\u5185\uff0c\u5c40\u9650\u5728\u5355\u7eaf\u7684GDP\u7ecf\u6d4e\u589e\u957f\u901f\u6210\u5ea6\u4e0a\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u80fd\u77e5\u9053\u5355\u7eaf\u7684\u8ffd\u6c42\u7ecf\u6d4e\u5feb\u901f\u6210\u589e\u957f\u5e76\u975e\u662f\u5bf9\u5168\u7701\u6c11\u4f17\u6700\u8d1f\u8d23\u4efb\u7684\u4e00\u79cd\u6001\u5ea6\uff0c\u5c31\u50cf\u6668\u66e6\u7eb8\u4e1a\u7684\u6536\u8d2d\u6848\u4e00\u6837\uff0c\u652f\u6301\u9526\u6e56\u5168\u8d44\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u624d\u662f\u6700\u6070\u5f53\u7684\u65b9\u5f0f\uff0c\u4f46\u662f\u8fd9\u6d89\u53ca\u5230\u4e0e\u65e7\u6709\u7ecf\u6d4e\u3001\u653f\u6cbb\u4f53\u5236\u7684\u67d0\u4e9b\u51b2\u7a81\uff0c\u4ece\u800c\u4f1a\u663e\u5f97\u56f0\u96be\u91cd\u91cd\u96be\u4ee5\u524d\u884c\u2026\u2026</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8d77\u521d\u6709\u4e9b\u4e0d\u660e\u767d\u5f20\u606a\u4f1a\u5c06\u7a00\u571f\u7684\u95ee\u9898\u9009\u62e9\u8fd9\u4e2a\u65f6\u673a\u5411\u6881\u4f1f\u6cd5\u63d0\u51fa\u6765\uff0c\u89c1\u6881\u4f1f\u6cd5\u9677\u5165\u6c89\u601d\u4e4b\u4e2d\uff0c\u624d\u77e5\u9053\u5f20\u606a\u8fd9\u662f\u5175\u884c\u9669\u62db\uff0c\u8981\u4e48\u6881\u4f1f\u6cd5\u90fd\u9009\u62e9\u9000\u7f29\u3001\u4fdd\u5b88\uff0c\u8981\u4e48\u5c31\u8868\u73b0\u51fa\u4e00\u79cd\u653f\u6cbb\u5bb6\u7684\u6c14\u9b44\u4ee5\u4e00\u79cd\u66f4\u8d1f\u8d23\u4efb\u3001\u6562\u62c5\u5f53\u7684\u6001\u5ea6\u5bf9\u6c5f\u5357\u7701\u7684\u7ecf\u6d4e\u4f53\u7cfb\u8fdb\u884c\u6df1\u5ea6\u7684\u6cbb\u7406\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u89c1\u773c\u4e0b\u60c5\u5f62\uff0c\u5c31\u77e5\u9053\u9526\u6e56\u5bf9\u6881\u4f1f\u6cd5\u7814\u7a76\u751a\u6df1\uff0c\u624d\u5728\u8fd9\u79cd\u573a\u5408\u63a5\u8fde\u629b\u51fa\u4e24\u9053\u96be\u9898\uff0c\u53e6\u5916\uff0c\u5f20\u606a\u8fd9\u5c0f\u5b50\u4f3c\u4e4e\u4e5f\u4e0d\u4ecb\u610f\u522b\u4eba\u8ba4\u4e3a\u4ed6\u4f1a\u516c\u62a5\u79c1\u4ec7\u5c06\u9526\u6e56\u4e0e\u5468\u5bb6\u7684\u6069\u6028\u7275\u6d89\u8fdb\u6765\u3002</p><p>\u3000\u3000\u8d75\u6709\u4f26\u5728\u7a00\u571f\u4ea7\u4e1a\u7684\u95ee\u9898\u4e00\u6837\u6ca1\u6709\u529e\u6cd5\u5e2e\u6881\u4f1f\u6cd5\u62ff\u4e3b\u610f\uff0c\u4f46\u91d1\u5c71\u534e\u7a00\u771f\u82e5\u662f\u7ae0\u5dde\u76d7\u91c7\u77ff\u8d44\u6e90\u6d41\u5165\u5e02\u573a\u7684\u4e3b\u8981\u6e20\u9053\uff0c\u91d1\u5c71\u5e02\u653f\u5e9c\u5bf9\u91d1\u5c71\u534e\u7a00\u8fd8\u662f\u6709\u7ba1\u8f96\u6743\u7684\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u662f\u5468\u747e\u745c\u7684\u54e5\u54e5\uff0c\u53d7\u56fd\u52a1\u9662\u7a00\u571f\u529e\u59d4\u6258\u5374\u4e0e\u4e09\u4e95\u7a00\u79cd\u91d1\u5c5e\u53ca\u91d1\u5c71\u534e\u7a00\u7684\u7ba1\u7406\u9ad8\u5c42\u4ea4\u5f80\u751a\u5bc6\uff0c\u8d75\u6709\u4f26\u542c\u5230\u8fd9\u6d88\u606f\u5012\u662f\u9887\u4e3a\u5174\u594b\uff0c\u8fd9\u6d88\u606f\u8981\u4f20\u51fa\u53bb\uff0c\u5bf9\u5468\u747e\u745c\u4e5f\u662f\u4e25\u5389\u7684\u6253\u51fb\u5427\uff1f</p><p>\u3000\u3000\u201c\u6709\u4ec0\u4e48\u8bc1\u636e\u6ca1\u6709\uff1f\u201d\u6881\u4f1f\u6cd5\u5f53\u7136\u6e05\u695a\u8fd9\u80cc\u540e\u6d89\u53ca\u5230\u9519\u7efc\u590d\u6742\u7684\u7275\u8fde\u5173\u7cfb\u6ca1\u6709\u4e00\u4e24\u5929\u7684\u65f6\u95f4\u90fd\u89e3\u91ca\u4e0d\u6e05\u695a\u3002</p><p>\u3000\u3000\u201c\u91d1\u5c71\u534e\u7a00\u8001\u603b\u5510\u82f1\u57f9\u534a\u4e2a\u6708\u5728\u4e1c\u5927\u6307\u4f7f\u4e0b\u5c5e\u4f24\u5bb3\u4e1c\u5927\u67d0\u5b66\u751f\u65f6\u7ed9\u516c\u5b89\u673a\u5173\u62d8\u6355\uff0c\u76f4\u5230\u4eca\u5929\u624d\u51fa\u62d8\u7559\u6240\u2026\u2026\u5510\u82f1\u57f9\u7ed9\u516c\u5b89\u673a\u5173\u505a\u7684\u7b14\u5f55\u91cc\u4ea4\u5f85\u4e86\u4ed6\u56db\u6708\u5341\u4e8c\u65e5\u3001\u5341\u4e09\u65e5\u4e24\u5929\u5230\u5efa\u90ba\u540e\u7684\u884c\u7a0b\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6240\u6709\u7684\u52fe\u901a\u3001\u62db\u547c\u3001\u6c42\u60c5\u5230\u4e86\u5efa\u90ba\u9ad8\u65b0\u533a\u516c\u5b89\u5206\u5c40\u6cbb\u5b89\u79d1\u90a3\u91cc\u90fd\u7ed9\u6321\u4e86\u56de\u53bb\uff0c\u5510\u82f1\u57f9\u786c\u662f\u5728\u62d8\u7559\u6240\u91cc\u7ed9\u5173\u62bc\u4e86\u534a\u4e2a\u6708\u624d\u7ed9\u653e\u51fa\u6765\uff0c\u72e0\u72e0\u7684\u53d7\u4e86\u4e00\u756a\u82e6\uff0c\u8138\u90fd\u7626\u7ec6\u957f\u4e86\u3002</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u4e1c\u534e\u62d8\u7559\u6240\u5bf9\u5510\u82f1\u57f9\u62d8\u8db3\u4e86\u5341\u4e94\u5929\u7684\u65f6\u95f4\uff0c\u4e00\u5206\u4e00\u79d2\u90fd\u4e0d\u5dee\uff0c\u786c\u662f\u6328\u5230\u4e8c\u5341\u516b\u65e5\u591c\u91cc\u4e5d\u70b9\u624d\u8ba9\u4ed6\u51fa\u62d8\u7559\u6240\u7684\u5927\u95e8\u3002</p><p>\u3000\u3000\u5510\u82f1\u57f9\u518d\u50bb\u4e5f\u77e5\u9053\u6709\u4eba\u5728\u80cc\u540e\u6574\u4ed6\uff0c\u4f46\u662f\u5374\u6478\u4e0d\u7740\u5934\u8111\uff0c\u66f4\u60f3\u4e0d\u51fa\u5efa\u90ba\u4ed6\u6709\u5f97\u7f6a\u8fc7\u8c01\u8ba9\u4eba\u5bb6\u8fd9\u4e48\u6574\u4ed6\u3002\u4ed6\u66f4\u6ca1\u6709\u60f3\u5230\u4ed6\u5728\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\u505a\u51fa\u7684\u90a3\u4efd\u4ea4\u5f85\u884c\u7a0b\u7684\u7b14\u5f55\uff0c\u5728\u4ed6\u884c\u7a0b\u4e2d\u51fa\u73b0\u7684\u4eba\u7269\u8eab\u4efd\u4e00\u4e00\u5f97\u5230\u8bc1\u5b9e\u4e4b\u540e\uff0c\u5c06\u662f\u4e00\u628a\u523a\u6740\u5468\u747e\u73ba\u7684\u5229\u5668\uff1b\u4ed6\u751a\u81f3\u8fd8\u4ee5\u4e3a\u5f20\u606a\u53ea\u662f\u5728\u8bb2\u5802\u4e0a\u5bf9\u5468\u747e\u73ba\u4e0d\u591f\u5c0a\u91cd\u3001\u5e26\u7740\u6bdb\u523a\u7684\u4e1c\u5927\u5b66\u751f\u3002</p><p>\u3000\u3000\u4e0e\u5510\u82f1\u57f9\u4e00\u540c\u7ed9\u653e\u51fa\u6765\u7684\uff0c\u81ea\u7136\u8fd8\u6709\u4ed6\u4e24\u540d\u4e0b\u5c5e\u3002\u5510\u82f1\u57f9\u8d70\u51fa\u62d8\u7559\u6240\u7684\u94c1\u95e8\uff0c\u770b\u5230\u4ed6\u5927\u54e5\u5510\u82f1\u80b2\u4e0e\u4f84\u5b50\u5510\u5fe0\u5728\u968f\u884c\u4eba\u5458\u7684\u966a\u540c\u4e0b\u7ad9\u5728\u62d8\u7559\u6240\u7684\u95e8\u5916\u7b49\u7740\uff0c\u8be7\u5f02\u7684\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u600e\u4e48\u8fc7\u6765\u4e86\uff1f\u6709\u6ca1\u6709\u5c06\u6211\u7684\u8f66\u53d6\u51fa\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u5148\u4e0a\u8f66\u518d\u8bf4\u2026\u2026\u201d\u5510\u82f1\u80b2\u9634\u6c89\u7740\u8138\uff0c\u62db\u547c\u5510\u82f1\u57f9\u4e0a\u8f66\uff0c\u8ba9\u4ed6\u513f\u5b50\u5510\u5fe0\u4e0e\u968f\u884c\u4eba\u5458\u5750\u540e\u9762\u7684\u8f66\u3002</p><p>\u3000\u3000\u201c\u4f60\u5728\u5efa\u90ba\u6709\u5f97\u7f6a\u8fc7\u8c01\u6ca1\u6709\uff1f\u201d\u94bb\u8fdb\u5954\u9a70600\u91cc\uff0c\u5510\u82f1\u80b2\u5c06\u9694\u97f3\u677f\u653e\u4e0b\u6765\uff0c\u8fd8\u662f\u4e0b\u610f\u8bc6\u7684\u538b\u7740\u55d3\u5b50\u95ee\u5510\u82f1\u57f9\u3002</p><p>\u3000\u3000\u201c\u6211\u54ea\u91cc\u77e5\u9053\uff0c\u6211\u7ed9\u5173\u5728\u91cc\u9762\u534a\u4e2a\u6708\uff0c\u8111\u888b\u90fd\u6293\u7834\u4e86\uff0c\u6123\u662f\u6ca1\u6709\u60f3\u5230\u7eb0\u6f0f\u51fa\u5728\u54ea\u91cc\uff1f\u603b\u4e0d\u4f1a\u6709\u4eba\u6545\u610f\u8ddf\u6211\u4eec\u8fc7\u4e0d\u53bb\u5427\uff1f\u201d\u5510\u82f1\u57f9\u6076\u72e0\u72e0\u7684\u8bf4\u9053\uff0c\u201c\u8981\u662f\u8ba9\u6211\u77e5\u9053\u8c01\u6545\u610f\u6574\u6211\u5728\u62d8\u7559\u6240\u91cc\u5173\u4e86\u5341\u4e94\u5929\uff0c\u6ca1\u6709\u4ed6\u597d\u770b\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u672c\u6ca1\u6709\u6253\u7b97\u8fc7\u6765\u63a5\u4f60\uff0c\u4e0d\u8fc7\u7ae0\u5dde\u90a3\u8fb9\u51fa\u4e8b\u4e86\uff0c\u201d\u5510\u82f1\u80b2\u7709\u5934\u6df1\u6df1\u7684\u9501\u7740\uff0c\u201c\u5c0f\u90b5\u4eca\u5929\u4e0a\u5348\u5728\u897f\u5858\u770b\u5230\u4e09\u4e2a\u4eba\u8d3c\u5934\u8d3c\u8111\u7684\u6253\u63a2\u77ff\u4e0a\u7684\u60c5\u51b5\uff0c\u5c31\u5c06\u8fd9\u4e09\u4e2a\u4eba\u7ed9\u6263\u4e0b\u6765\u4e86\uff0c\u4f60\u77e5\u9053\u8fd9\u4e09\u4e2a\u4eba\u662f\u4ec0\u4e48\u8eab\u4efd\uff1f\u201d</p><p>\u3000\u3000\u201c\u8bb0\u8005\u8fd8\u662f\u4e0a\u5934\u7684\u8c03\u67e5\u5458\uff1f\u201d\u5510\u82f1\u57f9\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u662f\u8bb0\u8005\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u62ff\u94b1\u585e\u4f4f\u4ed6\u4eec\u7684\u5634\u5457\uff0c\u201d\u5510\u82f1\u57f9\u8f7b\u9119\u7684\u5550\u4e86\u4e00\u53e3\uff0c\u4ed6\u8fd9\u4e9b\u5929\u5728\u62d8\u7559\u6240\u4e5f\u53d7\u4e86\u5f88\u591a\u7684\u6076\u6c14\uff0c\u201c\u8fd9\u4e9b\u8bb0\u8005\u771f\u4ed6\u5988\u53ef\u6068\uff0c\u6bcf\u56de\u4e0b\u6765\u90fd\u8981\u6765\u6253\u70b9\u91ce\u98df\uff0c\u4ee5\u4e3a\u6211\u4eec\u5f00\u77ff\u7684\u8ddf\u80fd\u5f00\u94f6\u884c\u7684\u6bd4\u3002\u201d</p><p>\u3000\u3000\u201c\u4e8b\u60c5\u6ca1\u90a3\u4e48\u7b80\u5355\uff0c\u90a3\u4e09\u4e2a\u4eba\u6ca1\u6709\u8981\u94b1\uff0c\u6001\u5ea6\u5f88\u56a3\u5f20\uff0c\u5f53\u65f6\u60f3\u4ece\u5c0f\u90b5\u624b\u91cc\u62a2\u56de\u76f8\u673a\u8ddf\u80f6\u5377\uff0c\u5c0f\u90b5\u52a8\u624b\u6559\u8bad\u4e86\u4ed6\u4eec\uff0c\u641c\u4e86\u90a3\u4e09\u4e2a\u4eba\u7684\u8eab\uff0c\u662f\u65b0\u534e\u793e\u603b\u793e\u4e0e\u6c5f\u5357\u5206\u793e\u7684\u8bb0\u8005\uff0c\u6211\u8d76\u8fc7\u53bb\uff0c\u5c0f\u90b5\u5df2\u7ecf\u5c06\u4eba\u5bb6\u6253\u5f97\u4e0d\u6210\u4eba\u5f62\uff0c\u4ec0\u4e48\u6761\u4ef6\u90fd\u53ef\u4ee5\u7b54\u5e94\uff0c\u4f46\u662f\u8fd9\u4e09\u4e2a\u4eba\u6123\u662f\u5a01\u903c\u5229\u8bf1\u90fd\u4e0d\u7ba1\u7528\uff0c\u73b0\u5728\u653e\u4e5f\u4e0d\u662f\uff0c\u5173\u4e5f\u4e0d\u662f\u2026\u2026\u201d\u5510\u82f1\u80b2\u7684\u7709\u5934\u9501\u7740\uff0c\u201c\u90a3\u4e09\u4e2a\u4eba\u662f\u4f60\u88ab\u5173\u540e\u7b2c\u56db\u5929\u5230\u7ae0\u5dde\u7684\uff0c\u6211\u770b\u8fd8\u662f\u8ddf\u4f60\u88ab\u5173\u6709\u5173\u7cfb\uff0c\u8fd9\u624d\u8d76\u8fc7\u6765\uff0c\u60f3\u8ddf\u4f60\u5546\u8bae\u770b\u770b\u662f\u54ea\u91cc\u51fa\u4e86\u7eb0\u6f0f\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u4e0d\u662f\u53bb\u627e\u5468\u747e\u73ba\uff1f\u201d\u5510\u82f1\u57f9\u4e5f\u77e5\u9053\u4e8b\u60c5\u6709\u4e9b\u4e25\u91cd\u4e86\u3002</p><p>\u3000\u3000\u201c\u5468\u747e\u73ba\u6211\u63a5\u89e6\u4e0d\u591a\uff0c\u4f46\u662f\u4e5f\u77e5\u9053\u4ed6\u662f\u53ea\u8001\u72d0\u72f8\uff0c\u8981\u662f\u8fd9\u4e8b\u60c5\u7ed9\u4ed6\u77e5\u9053\uff0c\u6211\u4eec\u8981\u62ff\u591a\u5c11\u94b1\u624d\u80fd\u5c06\u4ed6\u62c9\u5230\u540c\u4e00\u6761\u6218\u58d5\u91cc\uff1f\u201d\u5510\u82f1\u80b2\u66f4\u6000\u7591\u5468\u747e\u73ba\u4f1a\u9996\u5148\u5c06\u4ed6\u4eec\u7ed9\u5356\u4e86\uff0c\u201c\u800c\u4e14\u8fd9\u4e8b\u8ddf\u4f60\u5728\u5efa\u90ba\u88ab\u5173\u8054\u7cfb\u8d77\u6765\u6709\u4e9b\u8e4a\u8df7\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u5c0f\u90b5\u6293\u4eba\u6709\u6ca1\u6709\u4eba\u770b\u89c1\uff1f\u201d\u5510\u82f1\u57f9\u7a81\u7136\u8138\u5f62\u51f6\u6076\u7684\u95ee\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u6709\u4e9b\u5750\u7acb\u4e0d\u5b89\uff0c\u5979\u54e5\u5d14\u90c1\u6052\u6df1\u5165\u5230\u7ae0\u5dde\u91c7\u8bbf\u7a00\u571f\u77ff\u8d44\u6e90\u7ed9\u76d7\u91c7\u7684\u73b0\u72b6\u6bcf\u5929\u665a\u4e0a\u90fd\u8981\u8ddf\u5979\u7535\u8bdd\u8054\u7cfb\u4ee5\u62a5\u5e73\u5b89\uff0c\u5979\u4e5f\u77e5\u9053\u8fd9\u79cd\u6697\u8bbf\u6709\u4e00\u5b9a\u7684\u5371\u9669\u6027\uff0c\u6bd5\u7adf\u91c7\u8bbf\u7ed3\u679c\u516c\u5e03\u51fa\u6765\u4f1a\u4e25\u91cd\u6253\u51fb\u76d7\u77ff\u8005\u7684\u5229\u76ca\u3002\u4eca\u5929\u90fd\u5230\u591c\u91cc\u4e5d\u70b9\u591a\u4e86\uff0c\u5979\u8fd8\u6ca1\u6709\u63a5\u5230\u5979\u54e5\u54e5\u7684\u7535\u8bdd\uff0c\u6709\u4e9b\u62c5\u5fc3\u3002</p></article>"}}}]);
