(window.webpackJsonp=window.webpackJsonp||[]).push([[822],{845:function(p){p.exports={chapterTitle:null,title:"\u7b2c650\u7ae0 \u518d\u9732\u950b\u8292",content:"<article><h2>\u7b2c650\u7ae0 \u518d\u9732\u950b\u8292</h2><p>\u3000\u3000\u4ece\u4f01\u4e1a\u53d1\u5c55\u7684\u957f\u8fdc\u773c\u5149\u4ee5\u53ca\u54c1\u724c\u5efa\u8bbe\u7684\u89d2\u5ea6\u6765\u770b\uff0c\u968f\u610f\u7ed9\u5176\u4ed6\u5382\u5546\u8d34\u724c\u7edd\u4e0d\u80fd\u7b97\u660e\u667a\u7684\u884c\u4e3a\uff0c\u4f46\u662f\u5bf9\u4e8e\u90a3\u4e9b\u90e8\u59d4\u76f4\u8f96\u4f01\u4e1a\uff0c\u7ba1\u7406\u8005\u7684\u5229\u76ca\u4e0e\u4f01\u4e1a\u7684\u5229\u76ca\u5e76\u4e0d\u603b\u662f\u4e00\u81f4\u7684\uff0c\u66f4\u4f55\u51b5\u8054\u4fe1\u662f\u5c5e\u4e8e\u90ae\u7535\u90e8\u76f4\u8f96\u7684\uff0c\u8054\u8baf\u4e0e\u4e1c\u5174\u662f\u5c5e\u4e8e\u7535\u5b50\u5de5\u4e1a\u90e8\u76f4\u8f96\u7684\uff0c\u8fd9\u4e24\u8fb9\u5c31\u6709\u8f83\u6df1\u7684\u77db\u76fe\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u95ee\u9898\uff0c\u5f20\u606a\u4e0e\u82cf\u6d25\u4e1c\u3001\u9648\u4fe1\u751f\u3001\u4e01\u69d0\u7b49\u4eba\u901a\u8fc7\u7535\u8bdd\u6c9f\u901a\u8fc7\uff0c\u57fa\u672c\u4e0a\u80fd\u80af\u5b9a\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e0d\u592a\u53ef\u80fd\u4f1a\u7a7f\u540c\u4e00\u6761\u88e4\u5b50\u62b5\u5236\u9526\u6e56\u671d\u5411\u624b\u673a\u7ec4\u914d\u4ef6\u4f9b\u5e94\u5546\u7684\u65b9\u5411\u8fc8\u8fdb\uff0c\u8d34\u724c\u7684\u53e3\u5b50\u4ece\u4ed6\u4eec\u90a3\u91cc\u6839\u672c\u5c31\u4e0d\u53ef\u80fd\u5835\u4f4f\u3002</p><p>\u3000\u3000\u53e6\u4e00\u65b9\uff0c\u8d34\u724c\u5382\u5546\u80fd\u4ece\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u516c\u53f8\u90a3\u91cc\u62ff\u5230\u8d34\u724c\u6388\u6743\uff0c\u9526\u6e56\u5411\u4ed6\u4eec\u4f9b\u5e94\u624b\u673a\u7ec4\u914d\u4ef6\uff0c\u6839\u672c\u5c31\u4e0d\u7528\u7ba1\u90ae\u7535\u90e8\u90a3\u8fb9\u4f1a\u6709\u4ec0\u4e48\u6001\u5ea6\u3002</p><p>\u3000\u3000\u8fd9\u7684\u786e\u662f\u9526\u6e56\u6b63\u5f0f\u8fc8\u5165\u624b\u673a\u7ec4\u914d\u4ef6\u4f9b\u5e94\u5546\u7684\u673a\u4f1a\uff0c\u9526\u6e56\u4e5f\u4e0d\u5e94\u8be5\u653e\u5f03\u8fd9\u6837\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u6563\u5f00\u5728\u5404\u5730\uff0c\u8ba8\u8bba\u4e00\u4ef6\u4e8b\u60c5\uff0c\u8fd8\u8981\u5206\u5f00\u6765\u6253\u7535\u8bdd\uff0c\u9ebb\u70e6\u5f97\u7d27\uff1b\u7b49\u6a61\u6811\u56ed\u5185\u7684\u9ad8\u901f\u7f51\u5efa\u6210\uff0c\u5728\u5efa\u90ba\u5c31\u53ef\u4ee5\u53ec\u5f00\u591a\u65b9\u89c6\u9891\u4f1a\u8bae\u3002</p><p>\u3000\u3000\u4e03\u516b\u901a\u7535\u8bdd\u6253\u5b8c\uff0c\u5f20\u606a\u90fd\u89c9\u5f97\u5934\u53d1\u9ebb\uff0c\u771f\u4e0d\u662f\u4eba\u53d7\u7684\u7d2f\uff1b\u8fd8\u8981\u6709\u8bb8\u601d\u4f53\u8d34\u7740\u5c06\u5fae\u51c9\u7684\u6307\u809a\u6309\u5728\u4ed6\u7684\u592a\u9633\u7a74\u4e0a\u8f7b\u8f7b\u63c9\u7740\u3002</p><p>\u3000\u3000\u201c\u79d1\u738b\u9ad8\u79d1\u8981\u6210\u4e3a\u624b\u673a\u8d34\u724c\u5546\uff0c\u5927\u5bb6\u90fd\u4e50\u89c1\u5176\u6210\u554a\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u82cf\u6d25\u4e1c\u4ed6\u4eec\u6253\u7535\u8bdd\u8ba8\u8bba\uff0c\u8bb8\u601d\u5c31\u5b89\u9759\u7684\u7ad9\u5728\u5f20\u606a\u7684\u8eab\u8fb9\u3002</p><p>\u3000\u3000\u201c\u90fd\u662f\u91ce\u5fc3\u52c3\u52c3\u7684\u5bb6\u4f19\uff01\u201d\u5f20\u606a\u7b11\u7740\u8bc4\u4ef7\uff0c\u201c\u82cf\u6d25\u4e1c\u8fd8\u63d0\u51fa\u8981\u5c06\u5355\u789f\u789f\u673a\u7684\u90e8\u5206\u751f\u4ea7\u59d4\u6258\u65b0\u5143\u7535\u5668\u4ee3\u5de5\uff0c\u8fd9\u6837\u505a\u5c31\u662f\u8981\u4e3a\u624b\u673a\u7ec4\u4ef6\u7684\u751f\u4ea7\u632a\u51fa\u6765\u66f4\u591a\u7684\u8d44\u6e90\u51fa\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5355\u789f\u789f\u673a\u76ee\u524d\u662f\u7231\u8fbe\u7535\u5b50\u65d7\u4e0b\u7684\u4f4e\u7aef\u4ea7\u54c1\uff0c\u65b0\u5143\u7535\u5668\u7684\u751f\u4ea7\u6761\u4ef6\u4e5f\u76f8\u5f53\u6210\u719f\uff0c\u59d4\u6258\u4ed6\u4eec\u4ee3\u5de5\u751f\u4ea7\u5355\u789f\u789f\u673a\uff0c\u95ee\u9898\u5012\u4e0d\u4f1a\u592a\u5927\uff0c\u4e5f\u5c31\u4e0d\u9700\u8981\u76f2\u76ee\u7684\u6269\u5f20\u751f\u4ea7\uff0c\u6bd5\u7adf\u4e2d\u9ad8\u7ea7\u6280\u672f\u5de5\u4eba\u4e0e\u719f\u7ec3\u5de5\u4eba\u90fd\u4e0d\u662f\u80fd\u51ed\u7a7a\u80fd\u53d8\u51fa\u6765\u7684\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u6276\u6301\u65b0\u5143\u4e5f\u7b97\u662f\u4e0d\u9057\u4f59\u529b\uff0c\u9664\u4e86\u5c06\u6d77\u5dde\u7535\u5b50\u5de5\u4e1a\u7684\u57fa\u7840\u505a\u624e\u5b9e\u4e4b\u5916\uff0c\u4f60\u6700\u521d\u7684\u5fc3\u601d\u4e0d\u4e5f\u8fd8\u5305\u62ec\u6709\u6307\u671b\u7740\u80fd\u501f\u7528\u5230\u65b0\u5143\u7684\u8d44\u6e90\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u8fd9\u6837\uff0c\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u6402\u7740\u8bb8\u601d\u7ea4\u76c8\u7684\u8170\u80a2\uff0c\u4e5f\u4e0d\u5fd8\u8bf4\u4e00\u4e24\u53e5\u751c\u871c\u7684\u8bdd\uff0c\u201c\u8fd8\u662f\u4f60\u6700\u4e86\u89e3\u6211\uff0c\u9526\u6e56\u65e2\u5b9a\u7684\u7b56\u7565\u5c31\u662f\u8981\u4e0d\u65ad\u7684\u5f80\u9ad8\u7aef\u53d1\u5c55\uff0c\u4f46\u603b\u4e0d\u80fd\u65e0\u9650\u5236\u7684\u6269\u5927\u751f\u4ea7\uff0c\u6240\u4ee5\u8981\u5c06\u4e00\u4e9b\u4e2d\u4f4e\u7aef\u7684\u4e1a\u52a1\u5206\u5272\u51fa\u53bb\uff0c\u6700\u7ec8\u53ea\u638c\u63e1\u6838\u5fc3\u7684\u6280\u672f\u4e5f\u53ef\u4ee5\uff0c\u8fd9\u6837\u624d\u80fd\u5f62\u6210\u8f83\u7a33\u5b9a\u7684\u91d1\u5b57\u5854\u5f0f\u7684\u4ea7\u4e1a\u7fa4\u7ed3\u6784\u3002\u6211\u4eec\u7684\u789f\u673a\u4e1a\u52a1\uff0c\u76ee\u524d\u5df2\u7ecf\u53ef\u4ee5\u9010\u6b65\u7684\u5b9e\u73b0\u8fd9\u5c42\u8f6c\u5316\u4e86\u3002\u624b\u673a\u4e1a\u52a1\u8fd8\u521a\u521a\u5f00\u59cb\uff0c\u8fd8\u9700\u8981\u4e00\u6bb5\u65f6\u95f4\u7684\u57f9\u80b2\u671f\uff0c\u4e0d\u6653\u5f97\u8fc7\u4e00\u4e24\u5e74\uff0c\u79d1\u738b\u9ad8\u79d1\u6709\u6ca1\u6709\u6761\u4ef6\u6210\u4e3a\u9526\u6e56\u7684\u4ee3\u5de5\u5382\u5546\uff0c\u773c\u4e0b\u5012\u4e0d\u6025\u7740\u8003\u8651\u8fd9\u4e9b\uff0c\u201d\u5f20\u606a\u6276\u7740\u8bb8\u601d\u7684\u8170\u80a2\uff0c\u5c06\u6e29\u9999\u67d4\u7389\u7684\u5a07\u8eaf\u62c9\u8fdb\u81ea\u5df1\u7684\u6000\u91cc\uff0c\u8ba9\u5979\u5750\u81ea\u5df1\u7684\u5927\u817f\u4e0a\uff0c\u611f\u53d7\u7740\u5979\u4e30\u6ee1\u81c0\u90e8\u7684\u6e29\u70ed\u4e0e\u5f39\u6027\uff0c\u201c\u9526\u6e56\u8981\u6210\u4e3a\u624b\u673a\u7ec4\u914d\u4ef6\u4f9b\u5e94\u5546\uff0c\u8981\u907f\u514d\u8d34\u724c\u5382\u5546\u62ff\u5230\u9526\u6e56\u7684\u624b\u673a\u7ec4\u914d\u4ef6\u4e4b\u540e\u751f\u4ea7\u51fa\u4e0e\u7231\u8fbe\u624b\u673a\u540c\u8d28\u5316\u7684\u4ea7\u54c1\uff0c\u5c31\u9700\u8981\u8bbe\u8ba1\u8bb8\u591a\u4e0d\u540c\u7684\u624b\u673a\u65b9\u6848\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u662f\u4e00\u76f4\u62c5\u5fc3\u4ea7\u54c1\u8bbe\u8ba1\u4e2d\u5fc3\u7684\u5f00\u53d1\u4eba\u5458\u6df7\u65e5\u5b50\u5417\uff1f\u8fd9\u4e0b\u5c31\u4e0d\u7528\u62c5\u5fc3\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\u2026\u2026\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u53cd\u9a73\u8bb8\u601d\u7684\u5632\u8bbd\uff0c\u201c\u6211\u662f\u90a3\u4e48\u5c0f\u6c14\u7684\u4eba\u5417\uff1f\u517b\u7740\u4ed6\u4eec\uff0c\u53ea\u6015\u4ed6\u4eec\u81ea\u5df1\u4e5f\u6e34\u671b\u53d1\u6325\u81ea\u5df1\u7684\u4ef7\u503c\u5427\u3002\u201d</p><p>\u3000\u3000\u7231\u8fbe\u8981\u7a33\u5b9a\u7684\u63a8\u51fa\u6709\u5f71\u54cd\u529b\u7684\u3001\u5e02\u573a\u5206\u5c42\u6e05\u6670\u7684\u4ea7\u54c1\uff0c\u6bcf\u534a\u5e74\u63a8\u51fa\u4e00\u6b3e\u65b0\u7684\u624b\u673a\u4e3a\u8f83\u5408\u9002\u7684\u5468\u671f\u3002</p><p>\u3000\u3000\u4e00\u822c\u8bf4\u6765\uff0c\u7ecf\u9a8c\u4e30\u5bcc\u7684\u624b\u673a\u8bbe\u8ba1\u516c\u53f8\uff0c\u5916\u89c2\u8bbe\u8ba1\u9700\u89813\u30015\u5929\uff0c\u7ed3\u6784\u8bbe\u8ba1\u53ca\u7535\u8def\u677f\u8bbe\u8ba1\u9700\u898110\u5929\uff0c\u5f00\u6a21\u3001\u7ebf\u8def\u677f\u6253\u6837\uff0c\u7b2c\u4e00\u6b21\u8bd5\u6a21\u3001\u7ebf\u8def\u677f\u8c03\u8bd5\u9700\u898120\u5929\uff0c\u7b2c\u4e8c\u6b21\u8bd5\u6a21\u9700\u89815\u5929\uff0c\u4e24\u6b21\u6574\u673a\u8c03\u8bd55\u5929\uff0c\u5b8c\u6574\u7684\u624b\u673a\u8bbe\u8ba1\u5468\u671f\u56db\u5341\u4e94\u5929\uff0c\u7b2c\u56db\u5341\u4e94\u5929\u5c31\u8981\u62ff\u51fa\u6837\u673a\u6765\u3002</p><p>\u3000\u3000\u8fd9\u662f\u5f20\u606a\u5bf9\u4e03\u516b\u5e74\u540e\u56fd\u5185\u624b\u673a\u8bbe\u8ba1\u516c\u53f8\u7684\u8ba4\u8bc6\uff0c\u9526\u6e56\u5728\u5317\u4eac\u3001\u9999\u6e2f\u3001\u4e1c\u4eac\u3001\u6c49\u57ce\u90fd\u8bbe\u6709\u4ea7\u54c1\u5f00\u53d1\u4e2d\u5fc3\uff0c\u4e2d\u5fc3\u90fd\u6709\u624b\u673a\u8bbe\u8ba1\u5f00\u53d1\u5c0f\u7ec4\uff0c\u8fd9\u65f6\u5019\u8fd8\u662f\u4e5d\u4e03\u5e74\uff0c\u5f20\u606a\u5bf9\u4ed6\u4eec\u7684\u8981\u6c42\u662f\u81f3\u5c11\u6bcf\u4e09\u4e2a\u6708\u5f00\u53d1\u51fa\u4e00\u6b3e\u6210\u719f\u7684\u5546\u7528\u624b\u673a\u4ea7\u54c1\u65b9\u6848\u3002</p><p>\u3000\u3000\u65d7\u4e0b\u56db\u5bb6\u4ea7\u54c1\u5f00\u53d1\u4e2d\u5fc3\uff0c\u4ee5\u540e\u5728\u5efa\u90ba\u8fd8\u8981\u6210\u7acb\u4e00\u5bb6\u4ea7\u54c1\u8bbe\u8ba1\u4e2d\u5fc3\uff0c\u534a\u5e74\u5c11\u8bf4\u4e5f\u8981\u5f00\u53d1\u5341\u6b3e\u6210\u719f\u7684\u624b\u673a\u4ea7\u54c1\u65b9\u6848\u3002</p><p>\u3000\u3000\u534a\u5e74\u65f6\u95f4\uff0c\u5341\u6b3e\u65b9\u6848\u91cc\uff0c\u7231\u8fbe\u53ea\u91c7\u7528\u4e00\u5230\u4e24\u6b3e\uff0c\u5f53\u7136\u4e0d\u80fd\u5145\u5206\u6316\u6398\u8fd9\u51e0\u4ea7\u54c1\u5f00\u53d1\u4e2d\u5fc3\u7684\u6f5c\u529b\uff0c\u6240\u4ee5\u8bb8\u601d\u8bf4\u4ed6\u4e00\u76f4\u62c5\u5fc3\u5f00\u53d1\u4eba\u5458\u4f1a\u6574\u5929\u6df7\u65e5\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0b\u5df4\u78d5\u5728\u8bb8\u601d\u7684\u9999\u5ae9\u67d4\u6ed1\u7684\u7410\u9aa8\u4e0a\uff0c\u6ce8\u89c6\u7740\u7a97\u5916\u7684\u96e8\u4e1d\uff0c\u4e0a\u5348\u8fd8\u662f\u6674\u6717\u6717\u7684\u5929\uff0c\u8f6c\u77ac\u95f4\u5c31\u5377\u6765\u6f2b\u5929\u7684\u4e4c\u4e91\u4e0b\u8d77\u96e8\u6765\uff0c\u90fd\u4e0d\u6653\u5f97\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u901b\u8857\u4f1a\u4e0d\u4f1a\u7ed9\u96e8\u6dcb\u5230\uff0c\u6709\u5085\u4fca\u8ddf\u5c0f\u516c\u53f8\u7684\u53f8\u673a\u8ddf\u7740\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u592a\u72fc\u72c8\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u7684\u6280\u672f\u50a8\u5907\u6709\u9650\uff0c\u8981\u4ee5\u9526\u6e56\u73b0\u6709\u7684\u6280\u672f\u57fa\u7840\u4e0a\u3001\u91c7\u7528\u9526\u6e56\u6709\u6761\u4ef6\u751f\u4ea7\u7684\u7ec4\u914d\u4ef6\uff0c\u8ba9\u4ea7\u54c1\u8bbe\u8ba1\u4e2d\u5fc3\u7684\u53bb\u5f00\u53d1\u65b0\u7684\u624b\u673a\u4ea7\u54c1\u65b9\u6848\uff0c\u5c31\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5\u4e86\u3002\u6211\u4eec\u7684\u6280\u672f\u5e95\u8574\u8fd8\u4e0d\u591f\uff0c\u6280\u672f\u50a8\u5907\u4e5f\u4e0d\u591a\uff0c\u4ea7\u54c1\u65b9\u6848\u53d8\u6765\u53d8\u53bb\uff0c\u4e5f\u53d8\u4e0d\u51fa\u4ec0\u4e48\u65b0\u82b1\u6837\u51fa\u6765\u3002\u9526\u6e56\u8981\u80fd\u534a\u5e74\u63a8\u51fa\u4e00\u6b3e\u5728\u6027\u80fd\u4e0a\u6709\u8f83\u5927\u7a81\u7834\u7684\u624b\u673a\u4ea7\u54c1\uff0c\u6211\u5c31\u5f88\u6ee1\u8db3\u4e86\u3002\u8fd9\u4e2a\u9636\u6bb5\uff0c\u4ea7\u54c1\u8bbe\u8ba1\u4e2d\u5fc3\u7684\u65b9\u6848\uff0c\u4e3b\u8981\u8fd8\u662f\u8981\u4f53\u73b0\u5728\u5de5\u4e1a\u8bbe\u8ba1\u4e0a\uff0c\u6027\u80fd\u7684\u540c\u8d28\u5316\uff0c\u5927\u6982\u662f\u8fd9\u4e2a\u9636\u6bb5\u65e0\u6cd5\u907f\u514d\u7684\u2026\u2026\u6211\u4eec\u4e5f\u65e0\u6cd5\u5b9e\u73b0\u5168\u7403\u5316\u91c7\u8d2d\u6218\u7565\u4ee5\u5b9e\u73b0\u4ea7\u54c1\u7684\u591a\u6837\u5316\uff0c\u8de8\u533a\u57df\u91c7\u8d2d\u7684\u6210\u672c\u592a\u9ad8\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u6211\u4eec\u8ddf\u6d77\u5916\u5382\u5546\u76f8\u6bd4\uff0c\u5728\u6210\u672c\u5c31\u5360\u4e0d\u4e86\u591a\u5c11\u4f18\u52bf\uff0c\u800c\u56fd\u5185\u7684\u4ea7\u4e1a\u94fe\u6781\u4e0d\u5b8c\u5584\uff0c\u8fd9\u624d\u662f\u6211\u4e0b\u51b3\u5fc3\u8981\u9526\u6e56\u8d70\u5782\u76f4\u5316\u53d1\u5c55\u6a21\u5f0f\u7684\u5173\u952e\u539f\u56e0\uff0c\u4e00\u5207\u8fd8\u521a\u521a\u8d77\u6b65\u2026\u2026\u201d\u5f20\u606a\u8ddf\u8bb8\u601d\u89e3\u91ca\u9526\u6e56\u6b64\u65f6\u7684\u5904\u5883\uff0c\u201c\u8981\u662f\u9648\u9759\u8fd9\u65f6\u5019\u5c31\u8ddf\u9526\u6e56\u8981\u6c42\u4f9b\u5e94\u5979\u4eec\u624b\u673a\u7ec4\u4ef6\uff0c\u4e5f\u53ea\u80fd\u5148\u4f9b\u5e94\u5979\u4eeci08\u624b\u673a\u7684\u534a\u6210\u54c1\u7ec4\u88c5\u6a21\u5757\u7a0d\u52a0\u6539\u53d8\u800c\u5df2\uff0c\u773c\u4e0b\u53ea\u80fd\u4fdd\u4f4fi19\u624b\u673a\u7684\u5e02\u573a\u5730\u4f4d\u4e0d\u88ab\u64bc\u52a8\u5c31\u53ef\u4ee5\u4e86\uff1bi08\uff0c\u5bf9\u7231\u8fbe\u6765\u8bf4\uff0c\u4e5f\u662f\u60f3\u8981\u5c3d\u5feb\u6dd8\u6c70\u7684\u673a\u578b\u2026\u2026\u201d</p><p>\u3000\u3000\u9526\u6e56\u8981\u6210\u4e3a\u56fd\u5185\u5e02\u573a\u4e3b\u8981\u7684\u624b\u673a\u65b9\u6848\u4e0e\u7ec4\u4ef6\u4f9b\u5e94\u5546\u8fd8\u8981\u4e00\u6bb5\u6f2b\u957f\u7684\u8def\u8981\u8d70\uff0c\u4e3b\u8981\u8fd8\u662f\u8c22\u5b50\u5609\u63d0\u51fa\u624b\u673a\u8d34\u724c\u7684\u5546\u4e1a\u6a21\u5f0f\u592a\u65e9\u4e86\u4e00\u4e9b\uff0c\u90fd\u6ca1\u6709\u7ed9\u9526\u6e56\u7559\u4e0b\u6280\u672f\u50a8\u5907\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u5f20\u606a\u603b\u4e0d\u80fd\u5c06\u8fd9\u5c42\u7406\u7531\u6302\u5728\u5634\u8fb9\uff0c\u65e2\u7136\u8c22\u5b50\u5609\u5f88\u6709\u6295\u673a\u773c\u5149\u7684\u63d0\u51fa\u624b\u673a\u8d34\u724c\u7684\u8fd0\u8425\u6a21\u5f0f\uff0c\u9526\u6e56\u4e5f\u53ea\u6709\u63d0\u524d\u8ddf\u7740\u6478\u7740\u77f3\u5934\u8fc7\u6cb3\u3002</p><p>\u3000\u3000\u542c\u5f20\u606a\u8bf4\u5230\u7231\u8fbei19\u624b\u673a\uff0c\u5f20\u606a\u8fd9\u4e24\u5929\u6362\u4e86\u4e00\u6b3ei19\u624b\u673a\uff0c\u5c31\u6401\u5728\u529e\u516c\u684c\u7684\u684c\u89d2\u4e0a\uff0c\u8bb8\u601d\u5c06\u624b\u673a\u62ff\u5728\u624b\u91cc\u7ec6\u7ec6\u628a\u73a9\uff0c\u4e0e\u4e0a\u5e02\u7684\u4e09\u6b3ei19\u4e0d\u540c\uff0c\u8499\u7740\u6781\u8584\u7684\u9e8b\u76ae\uff0c\u624b\u611f\u6781\u4f73\uff0c\u5496\u5561\u8272\uff0c\u6cdb\u7740\u76ae\u9769\u7684\u5149\u6cfd\u3002</p><p>\u3000\u3000\u7cbe\u5bc6\u6a21\u5177\u516c\u53f8\u5bf9\u673a\u58f3\u9644\u9e87\u76ae\u8fdb\u884c\u6e17\u5851\u5904\u7406\u7684\u5de5\u827a\u8fd8\u5f88\u4e0d\u6210\u719f\uff0c\u6839\u672c\u65e0\u6cd5\u8fdb\u884c\u89c4\u6a21\u751f\u4ea7\uff0c\u5f20\u606a\u8fd9\u53ea\u624b\u673a\u662f\u8bd5\u9a8c\u54c1\uff0c\u81ea\u7136\u662f\u505a\u5f97\u5c3d\u5584\u5c3d\u7f8e\uff0c\u4e5f\u5c5e\u4e8ei19\u589e\u5f3a\u7248\u7cfb\u5217\u3002</p><p>\u3000\u3000\u4e0d\u8981\u8bf4i19\u589e\u5f3a\u7248\u7cfb\u5217\u4e86\uff0c\u4e0a\u5e02\u9500\u552e\u7684\u7231\u8fbei19\u666e\u901a\u7248\uff0c\u84dd\u5c4f\u4e0e\u548c\u5f26\u4e50\u97f3\u5c31\u662f\u5176\u5353\u5c14\u4e0d\u7fa4\u7684\u3001\u8ba9\u5176\u4ed6\u624b\u673a\u5382\u5546\u4e00\u65f6\u8fd8\u671b\u5c18\u83ab\u53ca\u7684\u7279\u8d28\u3002</p><p>\u3000\u3000\u84dd\u5c4f\u6280\u672f\u662f\u91c7\u7528\u84dd\u5149\u4e8c\u6781\u7ba1\u4e3aST\u6db2\u6676\u5c4f\u7684\u80cc\u666f\u5149\u6e90\uff0c\u4f4e\u529f\u8017\u7684\u84dd\u5149\u4e8c\u6781\u7ba1\u76ee\u524d\u4e5f\u53ea\u6709\u6cf0\u5fd2\u5149\u7535\u6280\u672f\u7814\u7a76\u9662\u7b49\u6781\u5c11\u6570\u51e0\u5bb6\u673a\u6784\u624d\u638c\u63e1\u6210\u719f\u7684\u5546\u7528\u751f\u4ea7\u6280\u672f\uff08\u6bd4\u8f83\u524d\u6587\uff0c\u8fd9\u91cc\u505a\u4e00\u4e2a\u5c0f\u5c0f\u7684\u4fee\u6539\uff09\uff1b\u548c\u5f26\u4e50\u97f3\u662f\u901a\u8fc7\u590d\u5f0f\u97f3\u8f68\u6280\u672f\u5b9e\u73b0\u7684\uff0c\u4e5f\u662f\u9526\u6e56\u81ea\u8eab\u6280\u672f\u4e0a\u7684\u7a81\u7834\u3002</p><p>\u3000\u3000\u53ea\u8981\u8fd9\u4e24\u9879\u6280\u672f\u4e0d\u7ed9\u5176\u4ed6\u624b\u673a\u5382\u5546\u590d\u5236\uff0c\u7231\u8fbei19\u624b\u673a\u57fa\u672c\u5c31\u80fd\u786e\u4fdd\u5185\u5730\u9ad8\u7aef\u5e02\u573a\u7684\u5730\u4f4d\u4e0d\u88ab\u52a8\u6447\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u542c\u8bf4\u4f60\u5c06\u9c9c\u82b1\u7248\u624b\u673a\u8fd8\u9001\u4e86\u4e00\u53ea\u7ed9\u5983\u84c9\u2026\u2026\u201d\u8bb8\u601d\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5510\u5a67\u4e0e\u5979\u4eb2\u70ed\u7740\u5462\uff0c\u8bf4\u5b9e\u8bdd\uff0c\u90a3\u53ea\u624b\u673a\u9001\u51fa\u53bb\uff0c\u6211\u4e5f\u633a\u8089\u75bc\u7684\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u6ca1\u80c6\u5b50\u8bf4\u624b\u673a\u4e0a\u7684\u82b1\u5349\u56fe\u6848\u662f\u63cf\u6479\u4f60\u7684\u624b\u7ed8\u4f5c\u54c1\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5979\u4eec\u8fd8\u559c\u6b22\uff1f\u201d\u8bb8\u601d\u62c5\u5fc3\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4eba\u89c1\u4eba\u7231\u7684\u624b\u673a\uff0c\u4e0d\u7136\u848b\u59d0\u4f1a\u591a\u5634\u5230\u4f60\u8fd9\u8fb9\u8bf4\u4e0d\u505c\uff1f\u201d</p><p>\u3000\u3000\u4e01\u69d0\u90a3\u8fb9\u4e0d\u4f1a\u591a\u5634\u591a\u820c\uff0c\u5173\u952e\u662f\u6628\u5929\u9648\u5983\u84c9\u624b\u91cc\u7684\u624b\u673a\u7ed9\u848b\u8587\u770b\u89c1\u4e86\uff0c\u4eba\u89c1\u4eba\u7231\u7684\u624b\u673a\uff0c\u8c01\u770b\u4e86\u90fd\u7a00\u5947\uff0c\u5f53\u7136\u662f\u848b\u8587\u8ddf\u8bb8\u601d\u8bf4\u7684\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5a07\u67d4\u7684\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u559c\u6b22\u5c31\u597d\uff0c\u4f60\u4ee5\u540e\u5c3d\u53ef\u4ee5\u8bbe\u8ba1\u4e9b\u7cbe\u5de7\u7684\u4e1c\u897f\u8ba8\u597d\u5973\u5b69\u5b50\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u6709\u90a3\u5fc3\u60c5\u3001\u6709\u90a3\u65f6\u95f4\uff0c\u6211\u90fd\u5e0c\u671b\u80fd\u53d8\u6210\u6574\u5428\u6574\u5428\u7684\u949e\u7968\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u624d\u4e0d\u4fe1\u4f60\u4f1a\u6389\u94b1\u773c\u91cc\u53bb\u4e86\u2026\u2026\u201d\u8bb8\u601d\u5a07\u7b11\u7740\u8bf4\uff0c\u770b\u7740\u65f6\u95f4\u5230\u4e2d\u5348\u4e86\uff0c\u4e0e\u5f20\u606a\u4e00\u8d77\u5230\u5199\u5b57\u697c\u4e8c\u5341\u5c42\u7684\u9910\u5385\u7528\u9910\uff0c\u7528\u8fc7\u9910\uff0c\u5f20\u606a\u8fd8\u53bb\u966a\u5510\u5a67\u5979\u4eec\u53bb\u901b\u8857\uff0c\u62ff\u5f20\u606a\u81ea\u5df1\u7684\u8bdd\u6765\u8bf4\uff0c\u4ed6\u7684\u4eba\u751f\u662f\u7ed9\u5207\u5272\u6210\u4e00\u5757\u4e00\u5757\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e2d\u5348\uff0c\u521a\u4e0b\u8fc7\u4e00\u9635\u96e8\uff0c\u653e\u6674\u4e4b\u540e\uff0c\u9999\u6e2f\u7684\u5929\u6c14\u9661\u7136\u51c9\u723d\u8d77\u6765\uff0c\u6709\u7740\u5165\u79cb\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u9999\u6e2f\u4eba\u6765\u8bf4\uff0c\u8fd9\u6837\u7684\u611f\u89c9\u4e0d\u4f1a\u6301\u4e45\uff0c\u6216\u8bb8\u660e\u5929\u5c31\u53c8\u4f1a\u6062\u590d\u708e\u708e\u590f\u5929\u7684\u5b63\u5019\uff0c\u4f46\u5bf9\u4e8e\u5510\u5a67\u3001\u9648\u5983\u84c9\u5979\u4eec\u6765\u8bf4\uff0c\u6b8a\u4e3a\u96be\u5f97\uff0c\u5927\u4e2d\u5348\u5c31\u4e0d\u7528\u8eb2\u5728\u5145\u6ee1\u51b7\u6c14\u7684\u623f\u95f4\u91cc\u4e86\uff0c\u5c06\u5f20\u606a\u4ece\u516c\u53f8\u91cc\u5524\u51fa\u6765\u62fd\u7740\u4e00\u8d77\u53bb\u95f2\u901b\u9999\u6e2f\u7684\u8857\u5934\u3002</p><p>\u3000\u3000\u8981\u4e58\u665a\u95f4\u7684\u98de\u673a\u56de\u5efa\u90ba\uff0c\u5bf9\u5510\u5a67\u3001\u9648\u5983\u84c9\u6765\u8bf4\uff0c\u8fd9\u90fd\u662f\u6700\u540e\u534a\u5929\u7684\u5047\u671f\u4e86\u3002</p><p>\u3000\u3000\u8d70\u5230\u65b0\u4e16\u754c\u767e\u8d27\u5546\u5e97\u524d\u9762\uff0c\u770b\u5230\u524d\u9762\u642d\u8bbe\u7740\u4e00\u4e2a\u7528\u84dd\u8272\u5e03\u5e54\u642d\u8bbe\u7684\u5c55\u53f0\uff0c\u5c55\u53f0\u524d\u56f4\u805a\u4e86\u8bb8\u591a\u4eba\u4e3e\u7740\u624b\u5728\u5934\u9876\u5de6\u53f3\u6446\u52a8\u7740\uff0c\u5c55\u53f0\u97f3\u54cd\u91cc\u4f20\u51fa\u6c5f\u9edb\u513f\u7684\u6b4c\u58f0\uff0c\u5510\u5a67\u5174\u594b\u7684\u8bf4\uff1a\u201c\u662f\u6c5f\u9edb\u513f\u4eb2\u81ea\u51fa\u6765\u505a\u5ba3\u4f20\u5417\uff0c\u6211\u8fd8\u6ca1\u6709\u542c\u5979\u73b0\u573a\u5531\u8fc7\u6b4c\u5462\uff1f\u201d\u4e00\u5de6\u4e00\u53f3\u633d\u7740\u5f20\u606a\u3001\u9648\u5983\u84c9\u7684\u80f3\u818a\u5c31\u5f80\u5c55\u53f0\u524d\u8d70\u53bb\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7684i08\u624b\u673a\u4e0d\u4f1a\u51fa\u73b0\u5728\u7684\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u7684\u5e02\u573a\u4e0a\u9500\u552e\uff0c\u4f46\u662f\u4f1a\u5c06i19\u624b\u673a\u4f5c\u4e3a\u8fdb\u519b\u6d77\u5916\u5e02\u573a\u7684\u8bd5\u91d1\u77f3\u5148\u8981\u5728\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u6253\u5f00\u5c40\u9762\u3002</p><p>\u3000\u3000\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u8fd9\u4e24\u5ea7\u57ce\u5e02\u6240\u6295\u5165\u7684\u5e7f\u544a\u70c8\u5ea6\u90fd\u8981\u5f3a\u8fc7\u5185\u5730\u5e02\u573a\u8bb8\u591a\uff0c\u7ecf\u8fc7\u4e09\u5929\u5f3a\u70c8\u5ea6\u7684\u6295\u653e\u5e7f\u544a\u4e4b\u540e\uff0c\u4eca\u5929\u4f1a\u5728\u65b0\u4e16\u754c\u767e\u8d27\u5546\u5e97\u642d\u8bbe\u5c55\u53f0\u5f00\u59cb\u4e3a\u671f\u4e09\u5929\u7684\u6781\u84dd\u97f3\u8272\u9b45\u60d1\u5de1\u5c55\u6d3b\u52a8\u3002</p><p>\u3000\u3000\u867d\u7136\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u4e24\u5730\u8fd9\u6bb5\u65f6\u95f4\u90fd\u53d7\u5230\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u6ce2\u53ca\uff0c\u4f46\u8fd9\u4e24\u5ea7\u57ce\u5e02\u7684\u4eba\u5747\u8d2d\u4e70\u80fd\u529b\u8981\u8fdc\u8fdc\u9ad8\u4e8e\u5185\u5730\u3002i19\u624b\u673a6888\u5143\u7684\u5b9a\u4ef7\uff0c\u5728\u5185\u5730\u8981\u5f52\u5165\u5962\u4f88\u54c1\uff0c\u5728\u9999\u6e2f\u3001\u65b0\u52a0\u5761\uff0c\u666e\u901a\u5e02\u6c11\u4e5f\u90fd\u80fd\u63a5\u53d7\u3002</p><p>\u3000\u3000\u8fd9\u4e09\u5929\u65f6\u95f4\uff0c\u4e0d\u4ec5\u5728\u5185\u5730\uff0c\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u90fd\u6380\u8d77i19\u7684\u65cb\u98ce\uff0c\u91d1\u878d\u98ce\u66b4\u6bd5\u7adf\u8fd8\u5f71\u54cd\u5230\u8fd9\u4e24\u5ea7\u57ce\u5e02\u7684\u90e8\u5206\u4eba\u7fa4\uff0c\u7edd\u5927\u90e8\u5206\u5e02\u6c11\u90fd\u7eb7\u7eb7\u8868\u793a\u751f\u6d3b\u672a\u53d7\u5e72\u6270\u3002</p><p>\u3000\u3000\u7531\u4e8ei19\u5728\u5185\u5730\u5e02\u573a\u53ea\u5728\u76db\u946b\u73af\u7403\u7535\u5668\u7684\u65d7\u8230\u5e97\u624b\u673a\u4e13\u533a\u6709\u552e\uff0c\u6240\u4ee5\u5f20\u606a\u51e0\u4e4e\u662f\u6bcf\u5929\u90fd\u80fd\u77e5\u9053i19\u5728\u5185\u5730\u7684\u9500\u552e\u6570\u636e\u3002\u624d\u4e0a\u5e02\u4e09\u5929\uff0c\u76db\u946b\u73af\u7403\u7535\u5668\u4e94\u95e8\u65d7\u8230\u5e97\u6bcf\u5929\u9650\u989d\u4f9b\u5e94\u7684\u516b\u767e\u53eai19\u624b\u673a\u90fd\u9500\u552e\u4e00\u7a7a\uff0c\u5176\u4ed6\u5730\u533a\u76ee\u524d\u8fd8\u53ea\u63a5\u53d7\u7535\u8bdd\u9884\u5b9a\uff0c\u9884\u5b9a\u6570\u91cf\u4e5f\u63a5\u8fd1\u4e24\u5343\u53ea\u3002</p><p>\u3000\u3000\u8981\u662f\u4ea7\u91cf\u80fd\u63d0\u4e0a\u6765\uff0ci19\u7684\u9500\u91cf\u672a\u5fc5\u4f1a\u6bd4i18\u5dee\uff0c\u773c\u4e0b\u53ea\u80fd\u4fdd\u8bc1\u4e09\u4e07\u53ea\u7684\u6708\u4ea7\u91cf\uff0c\u518d\u591a\uff0c\u4ea7\u54c1\u8d28\u91cf\u5c31\u65e0\u6cd5\u4fdd\u8bc1\u4e86\u3002</p><p>\u3000\u3000i18\u624b\u673a\u7684\u4ea7\u80fd\u8f83\u5927\uff0c\u4e0a\u5e02\u524d\u5168\u7ebf\u94fa\u8d27\uff0c\u4e09\u5929\u5177\u4f53\u7684\u6570\u636e\u8fd8\u6ca1\u6709\u8be6\u7ec6\u7684\u603b\u7ed3\u51fa\u6765\uff0c\u4e0d\u8fc7\u5927\u578b\u57ce\u5e02\u5df2\u7ecf\u6709\u65ad\u8d27\u8ffd\u52a0\u8ba2\u5355\u7684\u62a5\u5355\u3002</p><p>\u3000\u3000\u5c31\u76ee\u524d\u7684\u826f\u597d\u5f00\u5c40\u6765\u770b\uff0c\u7231\u8fbe\u7b2c\u4e00\u4e2a\u6708\u7684\u4e24\u6b3e\u624b\u673a\u5728\u5185\u5730\u5e02\u573a\u7684\u51fa\u8d27\u603b\u91cf\u8d85\u8fc7\u5341\u4e07\u53ea\u4e0d\u4f1a\u5b58\u5728\u591a\u5927\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u5185\u5730\u5e02\u573a\u6bcf\u6708\u603b\u51fa\u8d27\u91cf\u5341\u4e07\u53ea\uff0c\u4ee5\u5e02\u573a\u552e\u4ef7\u8ba1\u7b97\uff0c\u6708\u8425\u4e1a\u989d\u5c31\u63a5\u8fd1\u4e94\u4e2a\u4ebf\uff0c\u629b\u5f00\u4ee3\u7406\u5546\u7684\u5229\u6da6\u4e0e\u8d39\u7528\uff0c\u7231\u8fbe\u7684\u8425\u6536\u4e5f\u4e0d\u4f1a\u4f4e\u4e8e\u56db\u4ebf\u4e94\u5343\u4e07\u2014\u2014\u7231\u8fbe\u96c6\u56e2\u6241\u5e73\u5316\u7684\u4ee3\u7406\u8425\u9500\u4f53\u7cfb\uff0c\u4f7f\u5f97\u8425\u9500\u6210\u672c\u964d\u5230\u6700\u4f4e\uff0c\u7559\u5728\u4ee3\u7406\u5546\u7684\u5229\u6da6\u7a7a\u95f4\u770b\u4e0a\u53bb\u4e0d\u5927\uff0c\u4f46\u662f\u7231\u8fbe\u7684\u51fa\u8d27\u91cf\u6781\u9ad8\uff0c\u4f7f\u5f97\u4ee3\u7406\u5546\u7684\u5229\u6da6\u603b\u989d\u76f8\u5f53\u53ef\u89c2\u3002</p><p>\u3000\u3000\u597d\u5728\u4e5d\u4e03\u5e74\u8fd8\u53ea\u5269\u4e0b\u4e0d\u5230\u4e09\u4e2a\u6708\uff0c\u4e5f\u597d\u5728\u7231\u8fbe\u7684\u789f\u673a\u4e1a\u52a1\u5df2\u7ecf\u5e76\u5165\u4e0a\u5e02\u516c\u53f8\uff0c\u4e0e\u9526\u6e56\u5728\u8d22\u52a1\u4e0a\u6ca1\u6709\u76f4\u63a5\u7684\u5173\u8fde\uff0c\u4e0d\u7136\u4eca\u5e74\u7535\u5b50\u767e\u5f3a\u7684\u7b2c\u4e00\u540d\u5c31\u8981\u8ba9\u4e00\u5bb6\u5d1b\u8d77\u624d\u4e09\u5e74\u7684\u540e\u8d77\u4e4b\u79c0\u4e86\u3002\u90ae\u7535\u90e8\u76f4\u8f96\u7684\u4f01\u4e1a\u4e22\u8138\u4e22\u60ef\u4e86\uff0c\u8981\u662f\u7535\u5b50\u5de5\u4e1a\u90e8\u5c06\u7535\u5b50\u767e\u5f3a\u72b6\u5143\u7684\u540d\u989d\u7ed9\u4e22\u4e86\uff0c\u8138\u9762\u4e0a\u603b\u4e0d\u4f1a\u597d\u770b\u3002</p><p>\u3000\u3000\u8fd9\u4fbf\u662f\u9526\u6e56\u505a\u5e02\u573a\u7684\u80fd\u529b\uff0c\u4e00\u4e0b\u5b50\u4ece\u8fd9\u4e2a\u5e02\u573a\u62a2\u5360\u7684\u4efd\u989d\u5c31\u8d85\u8fc7\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u7684\u603b\u548c\uff0c\u90fd\u4e0d\u6653\u5f97\u8fd9\u4e09\u5bb6\u56fd\u4f01\u7684\u8001\u603b\u6b64\u65f6\u4f1a\u6709\u600e\u6837\u7684\u60f3\u6cd5\u3002</p><p>\u3000\u3000\u9526\u6e56\u5728\u624b\u673a\u4e1a\u52a1\u6295\u5165\u662f\u5927\u2014\u2014\u4e09\u4e94\u5343\u4e07\u7f8e\u5143\u5728\u9526\u6e56\u773c\u91cc\u4f3c\u4e4e\u4e0d\u5f53\u56de\u4e8b\uff0c\u4e0d\u8fc7\u9526\u6e56\u7684\u6536\u5165\u66f4\u53ef\u89c2\uff0c\u773c\u4e0b\u5916\u754c\u8fd8\u65e0\u6cd5\u63a8\u6d4b\u9526\u6e56\u7684\u5236\u9020\u6210\u672c\uff0c\u4ee5\u76ee\u524d\u7684\u8d8b\u52bf\u770b\uff0c\u53ea\u8981i19\u7684\u706b\u7206\u5c40\u9762\u7ef4\u6301\u534a\u5e74\uff0c\u9526\u6e56\u5c31\u5e94\u8be5\u80fd\u6536\u56de\u524d\u671f\u6295\u5165\u7684\u5de8\u989d\u6295\u8d44\u3002</p><p>\u3000\u3000\u5012\u4e0d\u6653\u5f97\u9526\u6e56\u518d\u6b21\u7684\u8f89\u714c\u7684\u6210\u529f\uff0c\u4f1a\u4e0d\u4f1a\u518d\u6b21\u75af\u72c2\u5438\u5f15\u66f4\u591a\u7684\u6295\u673a\u8d44\u672c\u8fdb\u5165\u8fd9\u4e2a\u884c\u4e1a\uff1f</p><p>\u3000\u3000\u8fd9\u51e0\u4e4e\u662f\u80af\u5b9a\u7684\uff0c\u90ae\u7535\u90e8\u5bf9\u624b\u673a\u884c\u4e1a\u5b9e\u884c\u51c6\u5165\u5236\uff0c\u9664\u4e86\u8981\u6c42\u90ae\u7535\u90e8\u518d\u6b21\u53d1\u653e\u624b\u673a\u751f\u4ea7\u724c\u7167\u4ee5\u6765\uff0c\u8d34\u724c\u4ee3\u5de5\u5305\u9500\u7684\u5546\u4e1a\u6a21\u5f0f\u5c06\u662f\u90a3\u4e9b\u6295\u673a\u8d44\u672c\u8fdb\u5165\u8fd9\u4e2a\u884c\u4e1a\u7684\u7ec8\u5357\u6377\u5f84\u3002</p><p>\u3000\u3000\u6bd4\u8d77\u90a3\u4e9b\uff0c\u5f20\u606a\u66f4\u5728\u610fi19\u5728\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u5e02\u573a\u4e0a\u8fd9\u4e09\u5929\u7684\u8868\u73b0\uff0c\u62ffi19\u5bf9\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u7684\u5e02\u573a\u8fdb\u884c\u5f00\u8352\uff0c\u662f\u9526\u6e56\u7684\u8bd5\u91d1\u884c\u52a8\u3002</p><p>\u3000\u3000\u5728\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u7684\u901a\u8baf\u5e02\u573a\uff0c\u5e02\u6c11\u6709\u7740\u66f4\u591a\u3001\u66f4\u597d\u7684\u9009\u62e9\uff0c\u6d77\u5916\u624b\u673a\u5382\u5546\u5bf9\u8fd9\u4e24\u4e2a\u5e02\u573a\u7684\u6295\u653e\u53d7\u9650\u5236\u8f83\u5c11\uff0c\u8fd9\u91cc\u4e5f\u662f\u7ade\u4e89\u76f8\u5bf9\u6210\u719f\u3001\u8d2d\u5356\u529b\u4e5f\u7edd\u4e0d\u5dee\u4e8e\u6b27\u7f8e\u53d1\u8fbe\u5730\u533a\u7684\u5e02\u573a\uff0c\u5e02\u573a\u4e0a\u7684\u624b\u673a\u90fd\u662f\u6d77\u5916\u624b\u673a\u5382\u5546\u4e00\u6d41\u7684\u4ea7\u54c1\uff0ci19\u624b\u673a\u5728\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u7684\u5e02\u573a\u624d\u9762\u4e34\u771f\u6b63\u6709\u538b\u529b\u7684\u7ade\u4e89\u3002</p><p>\u3000\u3000\u5e78\u597d\uff0c\u8fd9\u4e09\u5929\u5728\u9999\u6e2f\u7684\u9500\u552e\u6570\u636e\u8fd8\u4ee4\u5f20\u606a\u6ee1\u610f\uff0c\u5728\u5404\u5927\u7535\u8baf\u4e13\u8425\u95e8\u5e97\u90fd\u8fdb\u5165\u9500\u552e\u699c\u524d\u4e94\u7684\u4f4d\u5b50\uff0c\u6210\u7ee9\u4e5f\u4e0d\u53ef\u80fd\u7279\u522b\u597d\uff0c\u4e3b\u8981\u662f\u7231\u8fbe\u5728\u9999\u6e2f\u8fd8\u6ca1\u6709\u5efa\u7acb\u8d77\u591a\u5c11\u7684\u54c1\u724c\u77e5\u540d\u5ea6\u4e0e\u8ba4\u540c\u5ea6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u8d70\u5230\u5c55\u53f0\u524d\uff0c\u6c5f\u9edb\u513f\u7684\u6b4c\u58f0\u5df2\u7ecf\u6d88\u5931\u542c\u4e0d\u89c1\uff0c\u6c5f\u9edb\u513f\u4eba\u4e5f\u4e0d\u5728\u5c55\u53f0\u4e0a\uff0c\u8fd9\u65f6\u5019\u662f\u516b\u540d\u7a7f\u7740\u84dd\u8272\u76ae\u9769\u77ed\u88d9\u3001\u84dd\u8272\u7f51\u773c\u4e1d\u889c\u7684\u5c11\u5973\u5728\u53f0\u4e0a\u8868\u6f14\u84dd\u8272\u65f6\u88c5\u79c0\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u597d\u53ef\u60dc\uff0c\u521a\u5531\u5b8c\u4e86\u3002\u201d\u5510\u5a67\u4e0d\u65e0\u60cb\u60dc\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u65e0\u6240\u8c13\uff0c\u7ad9\u5728\u53f0\u4e0b\u770b\u7740\u53f0\u4e0a\u7684\u7f8e\u4e3d\u5c11\u5973\uff0c\u88d9\u88fe\u98de\u626c\uff0c\u96ea\u80a4\u5149\u6da6\u7684\u4e30\u8174\u5927\u817f\u82e5\u9690\u82e5\u73b0\uff0c\u8fde\u91cc\u9762\u7a7f\u7684\u9ed1\u8272\u5b89\u5168\u88e4\u4e5f\u82e5\u9690\u82e5\u73b0\uff1b\u5728\u5185\u5730\uff0c\u4e0d\u8981\u8bf4\u84dd\u8272\u7f51\u773c\u4e1d\u889c\u4e86\uff0c\u8fde\u540a\u5e26\u88d9\u5728\u8857\u5934\u90fd\u770b\u4e0d\u89c1\u3002</p><p>\u3000\u3000\u521a\u521a\u7ed9\u8d76\u5230\u9999\u6e2f\u62c5\u4efb\u9999\u6e2f\u9500\u552e\u516c\u53f8\u8d1f\u8d23\u4eba\u7684\u8d75\u6d77\u821f\u770b\u5230\u5f20\u606a\u3001\u5510\u5a67\u7ecf\u8fc7\u53f0\u4e0b\u5f20\u671b\uff0c\u8fd8\u4ee5\u4e3a\u5927\u8001\u677f\u7279\u610f\u5728\u5de1\u89c6\u6d3b\u52a8\u7ec4\u7ec7\u60c5\u51b5\uff0c\u6025\u5fd9\u6253\u8d77\u7cbe\u795e\u4ece\u5c55\u53f0\u80cc\u540e\u7684\u7b80\u6613\u66f4\u8863\u5ba4\u91cc\u6e9c\u8fc7\u6765\uff1a\u201c\u606a\u5c11\u8fc7\u6765\u4e86\uff0c\u8981\u4e0d\u8981\u8fdb\u53bb\u6307\u5bfc\u4e00\u4e0b\u6211\u4eec\u7684\u5de5\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6709\u4ec0\u4e48\u597d\u6307\u5bfc\u7684\uff1f\u201d\u5f20\u606a\u89c1\u5510\u5a67\u3001\u9648\u5983\u84c9\u5012\u662f\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u60f3\u5230\u5c55\u53f0\u540e\u9762\u7684\u66f4\u8863\u5ba4\u53bb\u770b\u770b\uff0c\u624d\u8ddf\u8d75\u6d77\u821f\u8d70\u4e86\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u6b63\u5728\u91cc\u9762\u6362\u8863\u670d\uff0c\u53ef\u60dc\u7ed9\u5979\u5988\u4e0e\u8bb8\u7ef4\u62ff\u5851\u6599\u5e03\u56f4\u7740\uff0c\u5934\u9732\u51fa\u5728\u5916\u9762\uff0c\u5999\u66fc\u7684\u8eab\u5b50\u53ea\u900f\u7740\u6de1\u6de1\u7684\u5f71\u5b50\uff0c\u4e5f\u4e0d\u6653\u5f97\u6709\u6ca1\u6709\u7a7f\u8863\u670d\uff0c\u603b\u4e0d\u80fd\u5f53\u8fd9\u4e48\u591a\u4eba\u7684\u9762\u63a2\u8fc7\u5934\u53bb\u770b\u4e24\u773c\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u4f60\u600e\u4e48\u8fc7\u6765\u4e86\uff1f\u542c\u8bf4\u4f60\u8fd9\u51e0\u5929\u4e00\u76f4\u5728\u9999\u6e2f\u5462\u3002\u201d\u6c5f\u9edb\u513f\u7ad9\u5851\u6599\u5e03\u90a3\u5934\uff0c\u6b23\u559c\u7684\u770b\u7740\u5f20\u606a\u6380\u5f00\u95e8\u5e18\u8d70\u8fdb\u6765\uff0c\u5185\u5fc3\u91cc\u6df1\u5904\u53c8\u6d8c\u51fa\u4e00\u4e1d\u60ca\u7f9e\u7684\u611f\u89e6\uff0c\u867d\u7136\u6709\u5e03\u5e54\u906e\u7740\uff0c\u4f46\u5979\u5374\u6653\u5f97\u81ea\u5df1\u5728\u5e03\u5e54\u8fd9\u8fb9\u53ea\u7a7f\u7740\u4e09\u70b9\u5f0f\uff0c\u8868\u6f14\u670d\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u88c5\u4e0a\u5462\uff0c\u4fcf\u8138\u4eff\u4f5b\u67d3\u4e0a\u4e00\u5c42\u8f7b\u7ea2\u4f3c\u7684\uff0c\u683c\u5916\u7684\u5a07\u8273\uff0c\u53c8\u4e0e\u8ddf\u5728\u5f20\u606a\u540e\u9762\u8d70\u8fdb\u6765\u7684\u5510\u5a67\u3001\u9648\u5983\u84c9\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u4e0e\u8bb8\u7ef4\u5bb6\u591a\u5e74\u90bb\u91cc\uff0c\u5173\u7cfb\u81ea\u7136\u4eb2\u70ed\uff0c\u8d70\u8fc7\u53bb\u8ddf\u8bb8\u7ef4\u4eb2\u70ed\u7684\u8bf4\u8bdd\uff0c\u8fd8\u5e2e\u5979\u62fd\u7740\u5e03\u5e54\u5e2e\u6c5f\u9edb\u513f\u906e\u7740\u6362\u8863\u670d\u3002</p><p>\u3000\u3000\u201c\u6628\u5929\u8ddf\u6211\u5988\u6253\u7535\u8bdd\u91cc\u65f6\u624d\u542c\u8bf4\u4f60\u56fd\u5e86\u5230\u9999\u6e2f\u6765\u73a9\u4e86\uff0c\u90fd\u4e0d\u6653\u5f97\u600e\u4e48\u8054\u7cfb\u4f60\uff1f\u201d\u8bb8\u7ef4\u8ddf\u9648\u5983\u84c9\u8bf4\u7740\u8bdd\uff0c\u53c8\u4fa7\u8fc7\u5934\u6765\u95ee\u5f20\u606a\uff0c\u201c\u4f60\u4eec\u600e\u4e48\u4e0d\u4f4f\u5230\u5f71\u6e7e\u56ed\u6765\uff1f\u6211\u4eec\u90a3\u91cc\u7684\u623f\u95f4\u90fd\u7a7a\u7740\u5462\uff0c\u5230\u9999\u6e2f\u6765\u6709\u5fc5\u8981\u4f4f\u9152\u5e97\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u5443\uff01\u201d\u5f20\u606a\u773c\u775b\u95ea\u70c1\u7740\uff0c\u8bb8\u601d\u5979\u4eec\u90fd\u4f4f\u5f71\u6e7e\u56ed\uff0c\u4ed6\u6709\u51e0\u4e2a\u80c6\u5b50\u5e26\u7740\u5510\u5a67\u4e5f\u4f4f\u8fdb\u5f71\u6e7e\u56ed\u9ad8\u7ea7\u9152\u5e97\u516c\u5bd3\u53bb\uff0c\u5f53\u771f\u75af\u4e86\u4e0d\u6210\uff1f\u80e1\u626f\u9053\uff0c\u201c\u4e0d\u80fd\u8ba9\u4f60\u4eec\u627e\u5230\u501f\u53e3\u5c06\u5de5\u4f5c\u7684\u4e8b\u60c5\u7ed9\u803d\u6401\u4e86\u3002\u201d</p><p>\u3000\u3000\u5510\u5a67\u8d70\u8fc7\u53bb\u5e2e\u6c5f\u9edb\u513f\u5979\u5988\u674e\u6708\u8339\u6293\u7740\u5e03\u5e54\uff0c\u8ddf\u8bb8\u7ef4\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u7b49\u5983\u84c9\u4e0b\u4e00\u6b21\u8fc7\u6765\uff0c\u6211\u4eec\u518d\u4e00\u8d77\u4f4f\u5230\u5f71\u6e7e\u56ed\u53bb\u597d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u8bdd\u8bf4\u5f97\u5f20\u606a\u5fc3\u54af\u5654\u4e00\u8df3\uff0c\u62ac\u773c\u8981\u770b\u5510\u5a67\u8bf4\u8fd9\u4e48\u662f\u63f6\u63c4\u8fd8\u662f\u6709\u5176\u4ed6\u610f\u5473\uff0c\u5510\u5a67\u8138\u5374\u8f6c\u5411\u5e03\u5e54\u540e\u7684\u6c5f\u9edb\u513f\uff0c\u5fae\u8bb6\u7684\u8d5e\u9053\uff1a\u201c\u9edb\u513f\u59d0\uff0c\u4f60\u597d\u6f02\u4eae\u554a\uff01\u201d</p><p>\u3000\u3000\u770b\u7740\u6c5f\u9edb\u513f\u5a07\u7f9e\u7684\u6a21\u6837\uff0c\u5f20\u606a\u5f3a\u5fcd\u7740\u8fc7\u53bb\u5e2e\u5fd9\u7684\u51b2\u52a8\uff0c\u5b89\u9759\u7684\u5750\u5728\u4e00\u65c1\u4e0d\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u6781\u84dd\u97f3\u8272\u9b45\u60d1\u5de1\u5c55\u6d3b\u52a8\u7531\u9999\u6e2f\u9500\u552e\u516c\u53f8\u7ec4\u7ec7\uff0c\u5b59\u9759\u9999\u7684\u4e16\u7eaa\u534e\u97f3\u516c\u53f8\u914d\u5408\uff0c\u914d\u5408i19\u7684\u5e02\u573a\u5ba3\u4f20\uff0c\u5148\u5728\u9999\u6e2f\u4e3e\u529e\u4e09\u5929\uff0c\u63a5\u4e0b\u6765\u53bb\u65b0\u52a0\u5761\uff0c\u518d\u53bb\u9a6c\u6765\u897f\u4e9a\u7684\u5409\u9686\u5761\u4e0e\u69df\u57ce\uff0c\u7136\u800c\u624d\u8fd4\u56de\u56fd\u5185\u914d\u5408\u76db\u946b\u65d7\u4e0b\u4e3e\u529e\u7c7b\u4f3c\u7684\u5de1\u5c55\u6d3b\u52a8\u3002</p><p>\u3000\u3000\u5750\u4e86\u7247\u523b\u5c31\u8981\u56de\u9152\u5e97\u53bb\uff0c\u8981\u8d76\u516d\u70b9\u534a\u7684\u98de\u673a\u56de\u5efa\u90ba\uff0c\u800c\u76db\u590f\u5c06\u4e58\u98de\u673a\u4e94\u70b9\u534a\u949f\u62b5\u8fbe\u542f\u5fb7\u673a\u573a\uff0c\u6240\u4ee5\u4ed6\u4eec\u8981\u8d76\u5728\u4e94\u70b9\u534a\u949f\u4e4b\u524d\u5148\u53bb\u673a\u573a\u7b49\u76db\u590f\u3002</p></article>"}}}]);
