(window.webpackJsonp=window.webpackJsonp||[]).push([[824],{847:function(p){p.exports={chapterTitle:null,title:"\u7b2c821\u7ae0 \u591c\u884c",content:"<article><h2>\u7b2c821\u7ae0 \u591c\u884c</h2><p>\u3000\u3000\u5510\u82f1\u57f9\u8d3c\u4ed6\u5988\u7684\u90c1\u95f7\uff0c\u521a\u4ece\u62d8\u7559\u6240\u51fa\u6765\u4e00\u4e2a\u665a\u4e0a\u7684\u65f6\u95f4\u90fd\u4e0d\u5230\uff0c\u53c8\u7ed9\u8702\u62e5\u800c\u8fdb\u7684\u8b66\u5bdf\u7ed9\u6b7b\u6b7b\u7684\u6309\u5728\u9152\u5e97\u623f\u95f4\u7684\u8c6a\u534e\u5927\u5e8a\u4e0a\uff0c\u5fc3\u91cc\u90c1\u95f7\u5f97\u5927\u53eb\uff1a\u8fd9\u4ed6\u5988\u7684\u8fd8\u662f\u4e94\u661f\u7ea7\u6d89\u5916\u9152\u5e97\u5417\uff0c\u62db\u4e2a\u5993\u90fd\u6709\u8b66\u5bdf\u6765\u6293\uff1f</p><p>\u3000\u3000\u4e24\u4e2a\u5c0f\u59d0\u4e5f\u9887\u4e3a\u610f\u5916\uff0c\u60ca\u6050\u7684\u62ff\u5e8a\u5355\u906e\u4f4f\u96ea\u767d\u7684\u8089\u4f53\u3002</p><p>\u3000\u3000\u5510\u82f1\u57f9\u7ed9\u53cd\u94d0\u7740\u5e26\u51fa\u623f\u95f4\uff0c\u770b\u5230\u4ed6\u5927\u54e5\u5510\u82f1\u80b2\u4e5f\u53d7\u5230\u540c\u6837\u7684\u5f85\u9047\uff0c\u4ed6\u618b\u4e86\u4e00\u809a\u5b50\u706b\uff0c\u51b2\u7740\u63ea\u4f4f\u4ed6\u80f3\u818a\u7684\u8b66\u5bdf\u5927\u58f0\u56b7\u9053\uff1a\u201c\u662f\u8c01\u4ed6\u5988\u7684\u5728\u6574\u6211\u4eec\uff0c\u6709\u79cd\u9732\u4e2a\u5934\u51fa\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u4f55\u7eaa\u4e91\u56de\u8fc7\u5934\u6765\u770b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u60f3\u8ba9\u6574\u4e2a\u9152\u5e97\u7684\u5ba2\u4eba\u90fd\u51fa\u6765\u770b\u5230\u4f60\u8fd9\u6a21\u6837\uff0c\u8bf7\u7ee7\u7eed\u56b7\u56b7\u2026\u2026\u4f60\u4eec\u53ea\u80fd\u81ea\u8ba4\u9709\u4e86\uff0c\u6b63\u8d76\u4e0a\u6211\u4eec\u5c40\u91cc\u641e\u626b\u9ec4\u6253\u975e\u4e13\u9879\u6574\u6cbb\uff0c\u4e0d\u8981\u8bf4\u4e94\u661f\u7ea7\u9152\u5e97\u4e86\uff0c\u4f60\u4eec\u5728\u5bb6\u91cc\u5bbf\u5a3c\uff0c\u53ea\u8981\u6709\u4eba\u4e3e\u62a5\uff0c\u6211\u4eec\u4e00\u6837\u51b2\u8fdb\u53bb\u902e\u4eba\uff1b\u4f60\u518d\u4e71\u8bf4\uff0c\u6211\u4e0d\u4f1a\u4ecb\u610f\u7ed9\u4f60\u52a0\u6761\u8bfd\u8c24\u7684\u7f6a\u540d\u3002\u201d</p><p>\u3000\u3000\u5510\u82f1\u80b2\u4e0a\u8eab\u53ea\u7a7f\u7740\u4e00\u4ef6\u80cc\u5fc3\uff0c\u72fc\u72c8\u4e0d\u582a\uff0c\u4ed6\u5f53\u7136\u80fd\u660e\u767d\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u7edd\u4e0d\u662f\u4e13\u9879\u6574\u6cbb\u8fd9\u4e48\u7b80\u5355\uff0c\u518d\u8bf4\u4e86\uff0c\u626b\u9ec4\u6253\u975e\u7684\u4e13\u9879\u6574\u6cbb\u4ec0\u4e48\u65f6\u5019\u4f1a\u626b\u5230\u4e94\u661f\u7ea7\u6d89\u5916\u9152\u5e97\u6765\uff1f\u4ed6\u7a0d\u5b9a\u4e86\u5b9a\u795e\uff0c\u770b\u5f97\u51fa\u4f55\u7eaa\u4e91\u662f\u9886\u961f\u7684\uff0c\u8bf4\u9053\uff1a\u201c\u80fd\u4e0d\u80fd\u8ba9\u6211\u5148\u6253\u4e2a\u7535\u8bdd\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u6253\u4e2a\u7535\u8bdd\u627e\u4e2a\u5927\u4eba\u7269\u5c31\u53ef\u4ee5\u5f53\u4f60\u4eec\u4eca\u5929\u665a\u4e0a\u4ec0\u4e48\u574f\u4e8b\u90fd\u6ca1\u6709\u505a\uff1f\u201d\u4f55\u7eaa\u4e91\u76ee\u5149\u51b7\u5cfb\u7684\u76ef\u7740\u5510\u82f1\u80b2\uff0c\u53c8\u538b\u7740\u55d3\u5b50\u5429\u5490\u624b\u4e0b\u7684\u8b66\u5458\uff0c\u201c\u5c06\u4ed6\u4eec\u62bc\u56de\u5c40\u91cc\u53bb\uff0c\u4e0d\u8981\u542c\u4ed6\u4eec\u5e9f\u8bdd\u3002\u201d</p><p>\u3000\u3000\u7ed9\u5206\u522b\u62bc\u4e0a\u4e24\u90e8\u8b66\u8f66\uff0c\u5510\u82f1\u80b2\u867d\u7136\u5bf9\u5efa\u90ba\u7684\u60c5\u51b5\u4e0d\u662f\u5f88\u719f\uff0c\u542c\u7740\u8f66\u7a97\u5916\u6865\u4e0b\u7684\u6c5f\u6c34\u58f0\uff0c\u501f\u7740\u6726\u6726\u4eae\u7684\u5929\u5149\uff0c\u770b\u5230\u8f66\u5b50\u9a76\u4e0a\u4e00\u5ea7\u5927\u6865\uff0c\u53c8\u8d77\u4e86\u7591\u5fc3\uff0c\u8b66\u60d5\u7684\u95ee\u5750\u5728\u4ed6\u8eab\u8fb9\u7684\u8b66\u5458\uff1a\u201c\u4f60\u4eec\u662f\u54ea\u91cc\u7684\u8b66\u5bdf\uff1f\u60f3\u628a\u6211\u4eec\u5e26\u5230\u54ea\u91cc\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u9ad8\u65b0\u533a\u5206\u5c40\uff0c\u600e\u4e48\u4e86\uff0c\u9ad8\u65b0\u533a\u5206\u5c40\u7684\u5c31\u4e0d\u80fd\u5230\u65b0\u5e02\u8857\u6267\u52e4\u4e86\uff1f\u201d\u8b66\u5bdf\u8f7b\u8511\u7684\u770b\u7740\u5510\u82f1\u80b2\u3002</p><p>\u3000\u3000\u5510\u82f1\u80b2\u6709\u7740\u5927\u96be\u4e34\u5934\u7684\u9884\u611f\uff0c\u5510\u82f1\u57f9\u4e0a\u56de\u5728\u5b66\u5e9c\u5df7\u88ab\u6263\uff0c\u4e5f\u662f\u9ad8\u65b0\u533a\u5206\u5c40\u4e0b\u5c5e\u7684\u8857\u9053\u6d3e\u51fa\u6240\u51fa\u9762\u6293\u4eba\uff0c\u4e0d\u8fc7\u90a3\u662f\u9ad8\u65b0\u533a\u7684\u8303\u56f4\uff1b\u8fd9\u6b21\u4ed6\u4eec\u660e\u660e\u662f\u5728\u5efa\u90ba\u7684\u5e02\u4e2d\u5fc3\uff0c\u53c8\u662f\u9ad8\u65b0\u533a\u5206\u5c40\u7684\u8b66\u5bdf\u8d76\u8fc7\u6765\u6293\u4eba\uff0c\u9b3c\u624d\u4f1a\u4fe1\u6ca1\u6709\u4eba\u5728\u80cc\u666f\u6307\u4f7f\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u4eba\u662f\u8c01\uff0c\u6709\u4ec0\u4e48\u610f\u56fe\uff0c\u5bbf\u5a3c\u62db\u5993\u6839\u672c\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u90a3\u4e2a\u4eba\u4e3a\u4ec0\u4e48\u8981\u4e00\u518d\u7684\u64a9\u62e8\u81ea\u5df1\uff1f\u5510\u82f1\u80b2\u53c8\u62c5\u5fc3\u8d77\u897f\u5858\u7684\u60c5\u51b5\u6765\uff0c\u4e0d\u7531\u4ed6\u4e0d\u5c06\u51fa\u73b0\u5728\u897f\u5858\u9547\u7684\u4e09\u540d\u65b0\u534e\u793e\u8bb0\u8005\u4e0e\u81ea\u5df1\u773c\u4e0b\u7684\u56f0\u5883\u8054\u7cfb\u8d77\u6765\uff0c\u53ef\u60dc\u4ed6\u4eba\u7ed9\u63a7\u5236\u4e4b\u540e\uff0c\u4e0d\u8981\u8bf4\u63a5\u89e6\u624b\u673a\u4e86\uff0c\u4ed6\u4eec\u7ed9\u5e26\u79bb\u5efa\u90ba\u5927\u9152\u5e97\u91cc\uff0c\u751a\u81f3\u90fd\u6ca1\u6709\u60ca\u52a8\u5230\u4f4f\u5728\u697c\u4e0b\u7684\u4e0b\u5c5e\u3002</p><p>\u3000\u3000\u5510\u82f1\u80b2\u773c\u775b\u76ef\u7740\u5750\u5728\u8b66\u8f66\u524d\u5ea7\u7684\u4f55\u7eaa\u4e91\uff0c\u4ed6\u7684\u624b\u673a\u5c31\u662f\u4f55\u7eaa\u4e91\u624b\u91cc\uff0c\u5df2\u7ecf\u6709\u597d\u51e0\u4e2a\u7535\u8bdd\u62e8\u4e86\u8fdb\u6765\u5374\u7ed9\u4f55\u7eaa\u4e91\u6390\u6389\u3002</p><p>\u3000\u3000\u8b66\u8f66\u8fc7\u4e86\u5c0f\u6c5f\u5927\u6865\uff0c\u4f55\u7eaa\u4e91\u624d\u732b\u620f\u8001\u9f20\u7684\u8f6c\u56de\u5934\uff0c\u671d\u5510\u82f1\u80b2\u626c\u4e86\u626c\u624b\u91cc\u7684\u624b\u673a\uff0c\u8138\u8272\u5e26\u7740\u4e00\u4e1d\u5632\u8bbd\u7684\u51b7\u7b11\uff1a\u201c\u7ae0\u5dde\u8fde\u7eed\u6253\u4e86\u5341\u51e0\u4e2a\u7535\u8bdd\u8fdb\u6765\u3002\u4e0d\u77e5\u9053\u7ae0\u5dde\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff0c\u8fd9\u5927\u6e05\u65e9\u7684\u8981\u6765\u6253\u5fe7\u4f60\u7684\u7f8e\u68a6\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u8fd8\u80fd\u7b97\u662f\u7f8e\u68a6\u5417\uff1f\u201d\u5510\u82f1\u80b2\u7ed9\u4e24\u540d\u8b66\u5458\u5939\u5750\u5728\u4e2d\u95f4\uff0c\u4e3e\u8d77\u53cc\u624b\uff0c\u4eae\u4e86\u4eae\u660e\u6643\u6643\u7684\u624b\u94d0\uff0c\u5f3a\u88c5\u9547\u5b9a\u7684\u8bf4\u9053\uff0c\u201c\u75db\u5feb\u7684\u8bf4\u5427\uff0c\u6211\u4eec\u5728\u5efa\u90ba\u5230\u5e95\u662f\u5f97\u7f6a\u8c01\u4e86\uff0c\u6211\u4eec\u600e\u6837\u505a\u624d\u80fd\u7b97\u8d54\u8fc7\u793c\u3001\u9053\u8fc7\u6b49\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u89e3\u91ca\u591a\u5c11\u904d\uff0c\u6211\u4eec\u8fd9\u662f\u5949\u516c\u6267\u6cd5\uff0c\u4f60\u4ee5\u4e3a\u6211\u4eec\u5728\u9152\u5e97\u5019\u5230\u8fd9\u5927\u6e05\u65e9\u662f\u6ca1\u6709\u4e8b\u505a\uff0c\u5c31\u662f\u4e3a\u4e86\u6574\u4f60\uff1f\u4f60\u4e5f\u592a\u628a\u81ea\u5df1\u5f53\u56de\u4e8b\u4e86\uff01\u201d\u4f55\u7eaa\u4e91\u8fd8\u5728\u4e00\u672c\u6b63\u7ecf\u7684\u8ddf\u5510\u82f1\u80b2\u8bf4\u8bdd\uff0c\u4ed6\u515c\u91cc\u7684\u624b\u673a\u54cd\u4e86\uff0c\u63a5\u4e86\u624b\u673a\uff0c\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u4fe9\u8fd8\u7b97\u5b89\u9759\uff0c\u4f60\u5429\u5490\u7684\u4e8b\uff0c\u54ea\u91cc\u6562\u51fa\u7eb0\u6f0f\uff1f\u4f60\u73b0\u5728\u5c31\u8981\u8d76\u53bb\u91d1\u5c71\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5c06\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u4e24\u5144\u5f1f\u63a7\u5236\u4f4f\uff0c\u897f\u5858\u9547\u7684\u52bf\u529b\u5c31\u6210\u4e86\u4e00\u76d8\u6563\u6c99\uff0c\u6ca1\u6709\u4eba\u7ad9\u51fa\u6765\u4e3b\u6301\u5c40\u9762\uff0c\u867d\u7136\u9a6c\u6d77\u9f99\u4ed6\u4eec\u79bb\u5f00\u897f\u5858\u9547\u65f6\u7ed9\u4e00\u4f19\u4eba\u5728\u8def\u4e0a\u8bbe\u8def\u969c\u56f4\u4f4f\uff0c\u6b66\u8b66\u9e23\u67aa\u4e4b\u540e\uff0c\u90a3\u4e9b\u4eba\u5c31\u5982\u9e1f\u517d\u6563\u4e86\uff0c\u9a6c\u6d77\u9f99\u4ed6\u4eec\u8fd9\u65f6\u5019\u5df2\u7ecf\u51fa\u4e86\u897f\u5858\u9547\u7684\u8303\u56f4\uff0c\u5c31\u7b97\u7ae0\u5dde\u8b66\u65b9\u9ad8\u5c42\u51fa\u4e86\u86c0\u866b\uff0c\u4e5f\u4e0d\u53ef\u80fd\u6709\u80c6\u91cf\u516c\u7136\u6d3e\u51fa\u8b66\u529b\u56f4\u5835\u6b66\u8b66\u7684\u8f66\u8f86\u2014\u2014\u5c31\u7b97\u6709\u80c6\u91cf\u4e5f\u8981\u6240\u6709\u7684\u8b66\u5bdf\u90fd\u542c\u547d\u624d\u884c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e0c\u671b\u6c5f\u5357\u7701\u5385\u80fd\u76f4\u63a5\u4ecb\u5165\u6b64\u6848\uff0c\u81f3\u5c11\u4e5f\u8981\u7ed9\u6c5f\u5357\u7701\u653f\u5e9c\u3001\u7ed9\u6881\u4f1f\u6cd5\u7559\u51e0\u5206\u989c\u9762\uff0c\u5317\u4eac\u5d14\u6587\u6bc5\u90a3\u91cc\u786e\u5b9a\u4eba\u6ca1\u6709\u751f\u547d\u5371\u9669\uff0c\u4e5f\u4e0d\u53cd\u5bf9\u4ea4\u7ed9\u6c5f\u5357\u7701\u5385\u5904\u7406\u6b64\u6848\uff0c\u8fd9\u65f6\u5019\u65b0\u534e\u793e\u603b\u793e\u53ca\u6c5f\u5357\u5206\u793e\u90fd\u60ca\u52a8\u4e86\uff0c\u65b0\u534e\u793e\u8bb0\u8005\u7ed9\u5730\u65b9\u52bf\u529b\u975e\u6cd5\u62d8\u7981\u7684\u4e8b\u60c5\uff0c\u8fd9\u4e9b\u5e74\u7edd\u5c11\u89c1\uff0c\u6c5f\u5357\u5206\u793e\u7684\u8d1f\u8d23\u4eba\u5df2\u7ecf\u4e0e\u6c5f\u5357\u7701\u516c\u5b89\u5385\u5385\u957f\u8c2d\u4ee5\u91d1\u968f\u8b66\u961f\u4e00\u8d77\u5728\u91d1\u7ae0\u56fd\u9053\u7ebf\u7684\u91d1\u5c71\u6536\u8d39\u7ad9\u5904\u7b49\u5f85\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u6025\u4e0d\u53ef\u8010\u7684\u8981\u8d76\u53bb\u91d1\u5c71\u786e\u8ba4\u4ed6\u54e5\u5b89\u7136\u65e0\u7fa1\uff0c\u6700\u65e9\u7684\u957f\u9014\u73ed\u8f66\u8981\u7b49\u5230\u65e9\u4e0a\u516b\u70b9\uff0c\u706b\u8f66\u8fd8\u6709\u665a\u2014\u2014\u5979\u5988\u5988\u8fd8\u6709\u5979\u5ac2\u5b50\u8fd8\u662f\u5728\u4eba\u7ed9\u6551\u51fa\u6765\u4e4b\u540e\u624d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f1a\u5750\u6700\u65e9\u7684\u73ed\u673a\u4ece\u5317\u4eac\u8d76\u53bb\u91d1\u5c71\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u7740\u5efa\u90ba\u5e02\u8b66\u65b9\u8fd8\u53ef\u4ee5\u5ad6\u5a3c\u62db\u5993\u7684\u540d\u4e49\u5c06\u5510\u82f1\u80b2\u3001\u5510\u82f1\u57f9\u4e24\u5144\u5f1f\u5173\u62bc\u5341\u4e94\u5929\uff0c\u6700\u597d\u80fd\u5728\u5341\u4e94\u5929\u4e4b\u95f4\u5bf9\u7ae0\u5dde\u6848\u60c5\u7684\u4fa6\u67e5\u80fd\u53d6\u5f97\u7a81\u7834\u6027\u7684\u8fdb\u5c55\uff0c\u90a3\u6837\u5c31\u53ef\u4ee5\u8ba9\u5510\u82f1\u80b2\u3001\u5510\u82f1\u57f9\u4e24\u5144\u5f1f\u4e00\u76f4\u7559\u5728\u91cc\u9762\uff0c\u4e0d\u81f3\u4e8e\u653e\u4ed6\u4eec\u51fa\u53bb\u518d\u8282\u5916\u5f00\u679d\u3002\u5f20\u606a\u4e5f\u8981\u8d76\u53bb\u91d1\u5c71\u4e3a\u8fd9\u4ef6\u6848\u5b50\u518d\u8ddf\u6881\u4f1f\u6cd5\u89c1\u4e00\u9762\uff0c\u6bd5\u7adf\u5728\u91d1\u5c71\u53ea\u80fd\u4f9d\u8d56\u6881\u4f1f\u6cd5\u6765\u63a8\u52a8\u6848\u60c5\u7684\u4fa6\u67e5\uff0c\u8981\u4e0d\u7136\u7ed9\u67d0\u4e9b\u6709\u5fc3\u4eba\u62d6\u4e0a\u4e00\u62d6\u8bf4\u4e0d\u5b9a\u5c31\u8ba9\u4e3b\u8981\u6848\u72af\u6709\u673a\u4f1a\u9003\u5230\u56fd\u5916\u53bb\u3002</p><p>\u3000\u3000\u5085\u4fca\u8d76\u56de\u5b66\u5e9c\u5df7\uff0c\u5f20\u606a\u5c31\u901a\u77e5\u4ed6\u9a6c\u4e0a\u53bb\u91d1\u5c71\uff0c\u867d\u8bf4\u4e00\u5bbf\u6ca1\u7761\uff0c\u5728\u8f66\u4e0a\u6b63\u597d\u80fd\u8865\u7761\u4e00\u89c9\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u542c\u5f20\u606a\u8bf4\u8981\u9001\u5979\u53bb\u91d1\u5c71\uff0c\u5c31\u8ddf\u7740\u4e0b\u4e86\u697c\uff0c\u8fd9\u65f6\u5019\u5929\u5df2\u7ecf\u6726\u6726\u4eae\uff0c\u8fdc\u5904\u7684\u71d5\u5f52\u6e56\u6c34\u4e4b\u662f\u6d6e\u7740\u4e00\u5c42\u9752\u5e7d\u7684\u5149\uff0c\u53ea\u662f\u770b\u5230\u505c\u5728\u7f51\u5427\u95e8\u524d\u6709\u4e9b\u7b28\u5934\u7b28\u8111\u7684\u5954\u9a70\u8f66\u6709\u4e9b\u8be7\u5f02\u3002</p><p>\u3000\u3000\u5085\u4fca\u4ece\u524d\u5ea7\u4e0b\u6765\u6253\u5f00\u8f66\u95e8\u8bf7\u5d14\u90c1\u66fc\u4e0a\u8f66\uff0c\u5d14\u90c1\u66fc\u77ee\u8eab\u4e4b\u9645\uff0c\u770b\u5230\u540e\u8f66\u53a2\u91cc\u7fdf\u4e39\u9752\u90a3\u5f20\u8ba9\u7edd\u5927\u591a\u6570\u5973\u4eba\u770b\u4e86\u90fd\u4f1a\u5fc3\u751f\u5fc3\u5992\u5fcc\u7684\u8138\u86cb\uff0c\u5fae\u5fae\u4e00\u6014\uff0c\u56de\u8fc7\u5934\u8981\u770b\u5f20\u606a\uff0c\u5f20\u606a\u5df2\u7ecf\u8f6c\u5230\u53e6\u4e00\u9762\u4e0a\u8f66\u4e86\u3002</p><p>\u3000\u3000\u201c\u5c0f\u5d14\u4e3b\u4efb\u4f60\u597d\uff0c\u6211\u662f\u5de5\u5546\u7ba1\u7406\u7855\u58eb\u6625\u5b63\u73ed\u7684\u7fdf\u4e39\u9752\uff0c\u6211\u4eec\u7b97\u662f\u6821\u53cb\uff0c\u5f88\u65e9\u5c31\u542c\u5f20\u606a\u63d0\u8d77\u8fc7\u4f60\uff0c\u8fd9\u662f\u7b2c\u4e00\u6b21\u89c1\u9762\uff0c\u201d\u7fdf\u4e39\u9752\u76c8\u76c8\u4e00\u7b11\u4f38\u51fa\u624b\u8ba9\u5d14\u90c1\u66fc\u642d\u7740\u5750\u8fdb\u6765\uff0c\u56de\u5934\u770b\u89c1\u5f20\u606a\u5728\u5979\u8eab\u4fa7\u5df2\u7ecf\u95ed\u76ee\u517b\u795e\u4e86\uff0c\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u5f20\u606a\u8fd9\u5b69\u5b50\u603b\u4e0d\u8ba9\u4eba\u7701\u5fc3\uff0c\u5c0f\u5d14\u4e3b\u4efb\u4e00\u5b9a\u4e5f\u5f88\u4e3a\u4ed6\u82e6\u607c\u5427\uff1f\u201d</p><p>\u3000\u3000\u542c\u7fdf\u4e39\u9752\u5728\u5d14\u90c1\u66fc\u9762\u524d\u5192\u5145\u81ea\u5df1\u7684\u957f\u8f88\uff0c\u5f20\u606a\u53c8\u4e0d\u80fd\u7741\u5f00\u773c\u775b\u77aa\u5979\uff0c\u53ea\u6709\u968f\u5979\u4e71\u8bf4\u53bb\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u4e09\u5e74\u524d\u5c31\u6076\u540d\u6ee1\u6d77\u5dde\u7684\u5b66\u751f\u8eab\u4e0a\u8fd8\u771f\u85cf\u7740\u592a\u591a\u672a\u89e3\u7684\u8ff7\uff0c\u7fdf\u4e39\u9752\u662f\u90a3\u79cd\u8bf4\u4e0d\u4e0a\u51b7\u8273\u53c8\u8bf4\u4e0d\u4e0a\u59a9\u5a9a\u7684\u5973\u4eba\uff0c\u98a6\u7b11\u4e4b\u95f4\u6709\u7740\u6e29\u5a49\u7684\u98ce\u60c5\uff0c\u5939\u6742\u7740\u592a\u591a\u590d\u6742\u7684\u6c14\u8d28\uff0c\u5374\u8ba9\u5979\u5a07\u8273\u7684\u5bb9\u989c\u66f4\u5177\u8ff7\u4eba\u7684\u9b45\u529b\u3002\u5d14\u90c1\u66fc\u5e73\u65e5\u5bf9\u81ea\u5df1\u7684\u5bb9\u8c8c\u86ee\u6709\u81ea\u4fe1\uff0c\u8fd9\u65f6\u5019\u5374\u5728\u60f3\u8fd9\u4e2a\u5973\u4eba\u8ddf\u5f20\u606a\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f</p><p>\u3000\u3000\u5954\u9a70\u8f66\u51fa\u5b66\u5e9c\u5df7\u65f6\uff0c\u5df7\u5b50\u53e3\u53c8\u6709\u4e00\u8f86\u9ed1\u8272\u7684\u522b\u514b\u5546\u52a1\u8f66\u8ddf\u4e0a\u6765\uff0c\u8981\u4e0d\u662f\u542c\u5230\u5085\u4fca\u5750\u5728\u524d\u6392\u62ff\u7740\u5bf9\u8bb2\u673a\u5728\u8054\u7edc\uff0c\u5d14\u90c1\u66fc\u8fd8\u4e0d\u77e5\u9053\u524d\u9762\u7684\u53e6\u4e00\u8f86\u9ed1\u8272\u5546\u52a1\u8f66\u8ddf\u4ed6\u4eec\u4e5f\u662f\u4e00\u8d77\u7684\u3002\u62c5\u60ca\u53d7\u6015\u4e86\u4e00\u591c\uff0c\u77e5\u9053\u4eba\u7ed9\u6551\u51fa\u6765\uff0c\u8fd8\u5728\u8d76\u5f80\u91d1\u5c71\u7684\u8def\u4e0a\uff0c\u5d14\u90c1\u66fc\u5012\u4e0d\u7528\u518d\u5fc3\u614c\u4e86\uff0c\u51fa\u5efa\u90ba\u5e02\u533a\u91cc\uff0c\u4e5f\u5728\u8f66\u4e0a\u6c89\u6c89\u7684\u7761\u53bb\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u518d\u9192\u8fc7\u6765\u65f6\uff0c\u5929\u5df2\u7ecf\u5927\u4eae\u4e86\uff0c\u6b63\u884c\u9a76\u5728\u524d\u5f80\u91d1\u5c71\u4e0a\u7684\u56fd\u9053\u4e0a\uff0c\u4e5f\u4e0d\u77e5\u9053\u8fd8\u6709\u591a\u5c11\u65f6\u95f4\u624d\u80fd\u8d76\u5230\u91d1\u5c71\uff0c\u9694\u677f\u653e\u4e0b\u6765\uff0c\u770b\u4e0d\u5230\u524d\u6392\u7684\u60c5\u5f62\uff0c\u7fdf\u4e39\u9752\u5934\u6795\u7740\u5f20\u606a\u7684\u80a9\u8180\u800c\u7761\uff0c\u624b\u8fd8\u633d\u7740\u4ed6\u7684\u80f3\u818a\uff0c\u5f20\u606a\u8138\u8d34\u7740\u8f66\u7a97\u4e5f\u6b63\u7761\u5f97\u9999\uff0c\u770b\u8fd9\u9759\u8c27\u5165\u7761\u7684\u60c5\u5f62\u4e24\u4eba\u5012\u50cf\u662f\u4e00\u5bf9\u60c5\u4fa3\uff0c\u4e0d\u8fc7\u7fdf\u4e39\u9752\u8fd9\u5973\u4eba\u770b\u4e0a\u53bb\u8981\u6210\u719f\u4e00\u4e9b\uff0c\u6bd5\u7adf\u4e1c\u5927\u5de5\u5546\u7ba1\u7406\u7855\u58eb\u73ed\u7684\u5b66\u751f\u5f88\u591a\u662f\u529f\u6210\u540d\u5c31\u7684\u4f01\u4e1a\u5bb6\u6216\u8005\u516c\u53f8\u7ba1\u7406\u9ad8\u5c42\u3002\u89c1\u5f20\u606a\u8eab\u5b50\u5fae\u52a8\uff0c\u5d14\u90c1\u66fc\u53c8\u95ed\u76ee\u7761\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u89c9\u5f97\u534a\u7247\u8180\u5b50\u53d1\u9ebb\uff0c\u6709\u4e00\u7f15\u9752\u4e1d\u98d8\u5230\u5634\u91cc\uff0c\u9f3b\u7aef\u8426\u7ed5\u7740\u5e7d\u5e7d\u7684\u53d1\u9999\uff0c\u4fa7\u5934\u770b\u89c1\u7fdf\u4e39\u9752\u9759\u8c27\u7684\u7761\u5f97\u6b63\u9999\uff0c\u50cf\u5a74\u513f\u4f3c\u7684\uff0c\u7761\u89c9\u559c\u6b22\u5f80\u522b\u4eba\u8eab\u4e0a\u62f1\uff0c\u80f3\u818a\u7ed9\u5979\u50cf\u6551\u547d\u7a3b\u8349\u4f3c\u7684\u7d27\u6402\u5728\u6000\u91cc\uff0c\u5f20\u606a\u5373\u4f7f\u662f\u6b63\u4eba\u541b\u5b50\uff0c\u4e5f\u4e0d\u7531\u4f1a\u60f3\u90a3\u5929\u665a\u4e0a\u770b\u5230\u7684\u90a3\u5bf9\u767d\u5f97\u6643\u773c\u7684\u4e30\u4e73\u3002</p><p>\u3000\u3000\u5efa\u90ba\u56db\u6708\u672b\u7684\u6c14\u6e29\u5df2\u7ecf\u6709\u4e9b\u708e\u70ed\u4e86\uff0c\u7fdf\u4e39\u9752\u7a7f\u7740\u7c89\u767d\u8272\u7684\u5343\u8936\u9886\u886c\u886b\uff0c\u67d4\u8f6f\u7684\u9762\u6599\u8d34\u5728\u5979\u4f53\u6001\u5999\u66fc\u7684\u8eab\u4e0a\uff0c\u66f4\u663e\u5f97\u51f9\u51f8\u6709\u81f4\u3001\u66f2\u7ebf\u73b2\u73d1\uff0c\u4e0b\u8eab\u662f\u70df\u7070\u8272\u7684\u5f39\u6027\u957f\u88e4\uff0c\u5c06\u4e30\u8174\u4fee\u957f\u7684\u5927\u817f\u7ef7\u7d27\uff0c\u770b\u4e0a\u53bb\u4f3c\u4e4e\u5c31\u80fd\u611f\u89c9\u5230\u6709\u60ca\u4eba\u5f39\u6027\uff0c\u66f4\u4f55\u51b5\u8fd8\u8ddf\u5979\u7684\u4e30\u80f8\u4e00\u8d77\u7d27\u8d34\u7740\u81ea\u5df1\u7684\u8eab\u4f53\uff1f\u968f\u7740\u8f66\u8eab\u7684\u8f66\u98a4\uff0c\u6709\u7740\u50cf\u6625\u66ae\u6e56\u6c34\u5728\u5fae\u98ce\u4e0b\u7684\u8f7b\u6f3e\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c9\u5f97\u5f97\u60f3\u4e00\u4e9b\u715e\u98ce\u666f\u7684\u4e8b\u60c5\u6765\u5206\u6563\u6ce8\u610f\u529b\uff0c\u53c8\u627e\u4e0d\u5230\u4e00\u4ef6\u5916\u5957\u76d6\u5728\u5927\u817f\u4e0a\uff0c\u771f\u662f\u4e22\u4eba\u554a\uff0c\u5173\u952e\u5d14\u90c1\u66fc\u8fd8\u5750\u5728\u540e\u8f66\u53a2\u91cc\u3002</p><p>\u3000\u3000\u4f3c\u4e4e\u8ddf\u5f20\u606a\u6545\u610f\u4f5c\u5bf9\u4f3c\u7684\uff0c\u8f66\u8f6e\u8f67\u8fc7\u4e00\u5757\u788e\u77f3\u4e4b\u7c7b\u7684\u786c\u7269\uff0c\u8f66\u8eab\u6296\u52a8\u4e86\u4e00\u4e0b\uff0c\u51e0\u4e4e\u7b97\u662f\u7f20\u7740\u5f20\u606a\u8eab\u5b50\u5728\u7761\u89c9\u7684\u7fdf\u4e39\u9752\u5f80\u524d\u4e00\u6ed1\uff0c\u60ca\u9192\u4e4b\u65f6\u624b\u4e0b\u610f\u8bc6\u7684\u6491\u5728\u5f20\u606a\u7684\u4e24\u817f\u4e4b\u95f4\uff0c\u4e00\u65f6\u6ca1\u6709\u53cd\u5e94\u8fc7\u6765\uff0c\u53ea\u770b\u89c1\u5f20\u606a\u5472\u7259\u54a7\u5634\u4e00\u4ed8\u5403\u75db\u7684\u6a21\u6837\uff0c\u95ee\u9053\uff1a\u201c\u5230\u91d1\u5c71\u4e86\uff1f\u201d\u4fc4\u800c\u624d\u611f\u89c9\u5230\u6491\u9519\u5730\u65b9\u4e86\uff0c\u4e00\u62b9\u6f6e\u7ea2\u98de\u4e0a\u8138\u988a\uff0c\u60ca\u614c\u7684\u6536\u56de\u624b\uff0c\u5374\u5f3a\u88c5\u9547\u5b9a\u7684\u9644\u5230\u5f20\u606a\u8033\u6735\u5632\u8bbd\uff1a\u201c\u4f60\u4eec\u7537\u4eba\u6574\u5929\u90fd\u5728\u60f3\u4ec0\u4e48\u4e1c\u897f\u554a\uff1f\u201d</p></article>"}}}]);
