(window.webpackJsonp=window.webpackJsonp||[]).push([[824],{847:function(p){p.exports={chapterTitle:null,title:"\u7b2c652\u7ae0 \u591c\u5f52\u5efa\u90ba",content:"<article><h2>\u7b2c652\u7ae0 \u591c\u5f52\u5efa\u90ba</h2><p>\u3000\u3000\u63a5\u5230\u5b59\u5c1a\u4e49\u4ece\u53f0\u6e7e\u6253\u8fc7\u6765\u7684\u7535\u8bdd\uff0c\u5f20\u606a\u5148\u8ba9\u53f6\u5efa\u658c\u7684\u53f8\u673a\u5148\u9001\u9648\u5983\u84c9\u56de\u5b66\u6821\uff0c\u591c\u8272\u5df2\u6df1\uff0c\u5728\u98de\u673a\u4e0a\u7528\u8fc7\u7b80\u9910\uff0c\u867d\u8bf4\u53ea\u7565\u586b\u4e9b\u809a\u5b50\uff0c\u5012\u4e5f\u7b97\u4e0d\u4e0a\u9965\u80a0\u8f98\u8f98\u3002\u5c06\u4e8b\u60c5\u7b80\u5355\u8ddf\u53f6\u5efa\u658c\u8bf4\u4e86\u4e00\u4e0b\uff0c\u6253\u7535\u8bdd\u7ed9\u59da\u6587\u76db\uff0c\u8ba9\u4ed6\u901a\u77e5\u5efa\u90ba\u5e02\u59d4\u5e38\u59d4\u3001\u526f\u5e02\u957f\u738b\u7ef4\u5747\uff0c\u7b49\u7740\u4e00\u8d77\u8fc7\u53bb\u627e\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u6c47\u62a5\u60c5\u51b5\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u5929\u57ce\u6cb3\u8def\uff0c\u4e0e\u59da\u6587\u76db\u3001\u6731\u5c0f\u519b\u6c47\u5408\uff0c\u59da\u6587\u76db\u4ece\u4ed6\u7684\u8f66\u91cc\u4e0b\u6765\uff0c\u94bb\u8fdb\u5f20\u606a\u7684\u8f66\u91cc\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u671d\u53f6\u5efa\u658c\u54c8\u4e86\u4e00\u53e3\u6c14\uff0c\u95ee\u4ed6\u6709\u6ca1\u6709\u95fb\u51fa\u9152\u5473\u6765\uff0c\u521a\u521a\u966a\u6295\u8d44\u5546\u5728\u9152\u5e97\u559d\u9152\uff0c\u559d\u5230\u534a\u9014\u63a5\u5230\u5f20\u606a\u7684\u7535\u8bdd\uff0c\u4e07\u4e8b\u90fd\u4e0d\u53ca\u6676\u5706\u5382\u6295\u8d44\u9879\u76ee\u91cd\u8981\uff0c\u62c9\u7740\u6731\u5c0f\u519b\u966a\u793c\u9000\u5bb4\u5e2d\uff0c\u8fc7\u6765\u8ddf\u5f20\u606a\u6c47\u5408\u3002\u59da\u6587\u76db\u51fa\u9152\u5e97\u91cc\uff0c\u62ff\u51b7\u6c34\u51b2\u8fc7\u8138\uff0c\u8fd8\u6f31\u8fc7\u53e3\uff0c\u8fd8\u662f\u62c5\u5fc3\u6ee1\u8eab\u9152\u6c14\u7ed9\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u7559\u4e0b\u574f\u5370\u8c61\u3002</p><p>\u3000\u3000\u7701\u59d4\u5927\u9662\u65b0\u6885\u82d1\u4f4d\u4e8e\u5efa\u90ba\u5e02\u4e1c\u5357\u7247\uff0c\u65b0\u542f\u7528\u7684\u5e02\u59d4\u5927\u9662\u5012\u662f\u8981\u523b\u610f\u4e0e\u7701\u59d4\u5927\u9662\u62c9\u5f00\u4e00\u4e9b\u8ddd\u79bb\u4f3c\u7684\uff0c\u5efa\u5230\u6c5f\u7554\u72ee\u5b50\u5c71\u9644\u8fd1\uff0c\u4e0e\u5f20\u606a\u4e4b\u524d\u5728\u6c5f\u7554\u7684\u522b\u5885\u4e0d\u8fdc\u2014\u2014\u738b\u7ef4\u5747\u4e5f\u4f4f\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u8d76\u5230\u65f6\uff0c\u7f57\u541b\u4e0e\u738b\u7ef4\u5747\u5df2\u7ecf\u5728\u5ead\u9662\u7684\u6728\u6805\u680f\u95e8\u524d\u7fd8\u9996\u4ee5\u5f85\u4e86\u8bb8\u4e45\u4f3c\u7684\uff0c\u738b\u7ef4\u5747\u5f00\u53e3\u5c31\u95ee\uff1a\u201c\u600e\u4e48\u8fd9\u4e48\u957f\u65f6\u95f4\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u6211\u4eec\u592a\u7126\u6025\u4e86\u2026\u2026\u201d\u7f57\u541b\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u738b\u7ef4\u5747\u7b11\u4e86\u8d77\u6765\uff0c\u4ed6\u8fd9\u4e48\u95ee\u4e5f\u6ca1\u6709\u8981\u8d23\u602a\u7684\u610f\u601d\uff0c\u7684\u786e\u662f\u592a\u7740\u6025\u4e86\u3002</p><p>\u3000\u3000\u6731\u5c0f\u519b\u4e0e\u5085\u4fca\u7b49\u4eba\u90fd\u7559\u5728\u8f66\u91cc\u7b49\u5019\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u3001\u59da\u6587\u76db\u4e0b\u8f66\u966a\u7f57\u541b\u3001\u738b\u7ef4\u8fdb\u4e86\u623f\u95f4\u8c08\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u8981\u771f\u80fd\u5c06\u6676\u5706\u5382\u9879\u76ee\u62ff\u4e0b\u6765\uff0c\u8fd9\u5c06\u662f\u56fd\u5185\u5728\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u9886\u57df\u7684\u91cd\u5927\u7a81\u7834\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u5efa\u90ba\u5e02\u641e\u7684\u6570\u5b57\u957f\u5eca\u6784\u60f3\uff0c\u6a61\u6811\u56ed\u8ba1\u5212\u8981\u8fc7\u4e9b\u5e74\u624d\u80fd\u53d1\u6325\u51fa\u4f5c\u7528\u6765\uff0c\u6572\u4e0b\u6676\u5706\u5382\u6295\u8d44\u9879\u76ee\u5374\u662f\u7acb\u7aff\u89c1\u5f71\u7684\u5de8\u5927\u6210\u679c\u3002</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u7684\u6570\u5b57\u957f\u5eca\u6784\u60f3\u4e0e\u4e1c\u6d77\u7701\u7684\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\uff0c\u867d\u7136\u5728\u5730\u65b9\u4e0a\u5f00\u59cb\u6709\u8bd5\u70b9\u7684\u5b9e\u65bd\u4e86\uff0c\u4f46\u662f\u6bd5\u7adf\u8fd8\u6ca1\u6709\u5f97\u5230\u4e2d\u592e\u7684\u6b63\u5f0f\u6279\u590d\uff0c\u8fd9\u4e24\u4e2a\u65b0\u6218\u7565\u8ba1\u5212\uff0c\u4e00\u65f6\u8fd8\u65e0\u6cd5\u5f97\u5230\u56fd\u5bb6\u653f\u7b56\u7684\u5927\u529b\u652f\u6301\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u62ff\u4e0b\u6676\u5706\u5382\u6295\u8d44\u9879\u76ee\uff0c\u5bf9\u4fc3\u8fdb\u4e2d\u592e\u6b63\u5f0f\u6279\u590d\u4e1c\u6d77\u7701\u4e0e\u5efa\u90ba\u5e02\u7684\u4e24\u4e2a\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u65b0\u6218\u7565\u8ba1\u5212\u6709\u76f4\u63a5\u7684\u63a8\u52a8\u4f5c\u7528\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u4e00\u4e9b\u60c5\u51b5\u8ddf\u7f57\u541b\u8be6\u7ec6\u6c47\u62a5\u4e86\u4e00\u4e0b\uff0c\u67f3\u5fd7\u6210\u80fd\u677e\u53e3\u7b54\u5e94\u5230\u5efa\u90ba\u6765\u4e3b\u6301\u6676\u5706\u5382\u6295\u8d44\u9879\u76ee\u7684\u786e\u662f\u8fd1\u671f\u6700\u5927\u7684\u8fdb\u5c55\u3002</p><p>\u3000\u3000\u773c\u4e0b\u5168\u7403\u7684\u7535\u5b50\u4ea7\u4e1a\u6b63\u8fce\u6765\u884c\u4e1a\u7684\u4f4e\u8c37\u671f\uff0c\u4f7f\u5f97\u6295\u8d44\u6676\u5706\u5382\u7684\u5efa\u8bbe\u8d39\u7528\u6bd4\u4e4b\u524d\u7684\u9884\u7b97\u8981\u4f4e\u8bb8\u591a\uff0c\u4f46\u662f\u8981\u4e0a\u4e00\u6761\u516b\u82f1\u5bf8\u7684\u751f\u4ea7\u7ebf\uff0c\u5c11\u8bf4\u4e5f\u4e0d\u80fd\u4f4e\u4e8e8\u4ebf\u7f8e\u5143\u3002\u8d8a\u79c0\u624b\u4e2d\u7684\u8d44\u91d1\u624d\u4e09\u4ebf\u591a\u70b9\uff0c\u4e0d\u7ba1\u4ee5\u4ec0\u4e48\u540d\u4e49\uff0c\u8fd8\u9700\u8981\u7b79\u5efa\u8fd15\u4ebf\u7f8e\u5143\u7684\u5de8\u8d44\uff0c\u8fd9\u5927\u6982\u662f\u56fd\u5185\u91d1\u878d\u5e02\u573a\u8fd1\u671f\u6700\u5927\u7684\u4e00\u7b14\u878d\u8d44\u4e86\u3002</p><p>\u3000\u3000\u201c\u5e02\u91cc\uff0c\u738b\u526f\u5e02\u957f\u4eb2\u81ea\u51fa\u9762\u63a8\u52a8\u4e00\u5207\u5de5\u4f5c\uff0c\u9ec4\u526f\u5e02\u957f\u3001\u5c0f\u59da\u5c31\u7ed9\u738b\u526f\u5e02\u957f\u5f53\u52a9\u624b\uff0c\u8981\u521b\u9020\u4e00\u5207\u4fbf\u5229\u6761\u4ef6\uff1b\u660e\u5929\u738b\u526f\u5e02\u957f\u660e\u5929\u8ddf\u6211\u53bb\u627e\u9676\u664b\u4e66\u8bb0\u3001\u674e\u7701\u957f\u5355\u72ec\u6c47\u62a5\u4e00\u4e0b\u8fd9\u4ef6\u4e8b\uff0c\u4e2d\u592e\u90e8\u59d4\u4ee5\u53ca\u878d\u8d44\u65b9\u9762\u7684\u4e8b\u60c5\uff0c\u6211\u6765\u51fa\u9762\u8ddf\u5404\u65b9\u9762\u6253\u62db\u547c\uff1b\u8fd9\u4e48\u5927\u7684\u9879\u76ee\uff0c\u5bf9\u56fd\u5185\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u5e03\u5c40\u7684\u610f\u4e49\u91cd\u5927\uff0c\u8ba1\u59d4\u4e5f\u4f1a\u6d3e\u4eba\u79ef\u6781\u53c2\u4e0e\uff0c\u4e5f\u9700\u8981\u4ed6\u4eec\u51fa\u9762\u534f\u8c03\u4e00\u4e9b\u4e8b\u60c5\u3002\u4e0d\u8fc7\u8fd9\u4e9b\u4e8b\u60c5\u8fd8\u662f\u8981\u7b49\u67f3\u5fd7\u6210\u987a\u5229\u62b5\u8fbe\u5efa\u90ba\u4e4b\u540e\u518d\u5927\u5c55\u65d7\u9f13\u7684\u8fdb\u884c\u2026\u2026\u201d\u7f57\u541b\u5174\u594b\u7684\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u6015\u7f57\u541b\u8981\u5c06\u6240\u6709\u7684\u529f\u52b3\u90fd\u63fd\u4e0b\u6765\uff0c\u90a3\u81ea\u7136\u4f1a\u5f92\u589e\u8bb8\u591a\u7684\u963b\u529b\uff0c\u542c\u4ed6\u8bf4\u660e\u5929\u5c31\u62c9\u7740\u738b\u7ef4\u5747\u627e\u9676\u664b\u3001\u674e\u8fdc\u6e56\u6c47\u62a5\u6b64\u4e8b\uff0c\u90a3\u81ea\u7136\u662f\u518d\u597d\u4e0d\u8fc7\uff0c\u6bd5\u7adf\u8fd9\u4e9b\u4e8b\u8fd8\u662f\u8131\u4e0d\u5f00\u7701\u91cc\u7684\u652f\u6301\u3002</p><p>\u3000\u3000\u8fd8\u8ba8\u8bba\u4e86\u4e00\u4e9b\u4e8b\u60c5\uff0c\u6a61\u6811\u56ed\u7684\u201c\u767e\u4eba\u8ba1\u5212\u201d\uff0c\u4f5c\u4e3a\u5efa\u90ba\u5e02\u6570\u5b57\u957f\u5eca\u6784\u60f3\u7684\u6709\u673a\u7ec4\u6210\u90e8\u5206\uff0c\u5e02\u91cc\u5f53\u7136\u8981\u7ed9\u4e88\u8d23\u65e0\u65c1\u8d37\u7684\u652f\u6301\uff0c\u67f3\u5fd7\u6210\u7387\u4f17\u5230\u8fbe\u5efa\u90ba\u540e\u540c\u6837\u4e5f\u4eab\u7528\u201c\u767e\u4eba\u8ba1\u5212\u201d\u6240\u63d0\u4f9b\u7684\u4e00\u5207\u4f18\u60e0\u653f\u7b56\uff0c\u5956\u52b1\u8d44\u91d1\u90e8\u5206\u81ea\u7136\u8981\u4ece\u9879\u76ee\u91cc\u76f4\u63a5\u62ff\u94b1\u3002</p><p>\u3000\u3000\u53e6\u5916\uff0c\u8fd8\u8ba8\u8bba\u4e86\u4e00\u4e9b\u5e02\u91cc\u76f4\u63a5\u63a8\u52a8\u6570\u5b57\u957f\u5eca\u6784\u60f3\u5efa\u8bbe\u8fdb\u7a0b\u7684\u65b0\u63aa\u65bd\uff0c\u4e00\u65b9\u9762\u52a0\u5f3a\u5efa\u90ba\u5e02\u7684\u7f51\u7edc\u57fa\u7840\u8bbe\u65bd\u5efa\u8bbe\uff0c\u9996\u5148\u8981\u786e\u4fdd\u71d5\u5f52\u6e56\u5730\u533a\u7684\u7f51\u7edc\u5728\u8fd1\u671f\u5185\u5168\u9762\u5347\u7ea7\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u8d44\u8d39\u4e5f\u4f1a\u5927\u5e45\u4e0b\u8c03\uff0c\u4ece\u524d\u671f\u5438\u8840\u6027\u8d28\u7684\u8d44\u8d39\u653f\u7b56\u53d8\u66f4\u4e3a\u5168\u9762\u652f\u6301\u7684\u65b9\u9488\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u70b9\uff0c\u675c\u98de\u4ed6\u4eec\u8981\u5728\u5b66\u5e9c\u5df7\u5f00\u8bbe\u7f51\u5427\u5012\u662f\u76f4\u63a5\u53d7\u76ca\u7684\uff0c\u540c\u7b49\u5e26\u5bbd\u7684\u7f51\u7edc\u5149\u7ea4\u63a5\u5165\u8d39\u7528\uff0c\u5c11\u8bf4\u4e5f\u8981\u51cf\u514d\u8fd1\u534a\u3002</p><p>\u3000\u3000\u5728\u7f57\u541b\u5bb6\u5750\u4e86\u4e00\u4e2a\u591a\u5c0f\u65f6\uff0c\u624d\u544a\u8f9e\u51fa\u6765\uff0c\u738b\u7ef4\u5747\u4e5f\u4e00\u9053\u8d70\u4e86\u51fa\u6765\uff0c\u4e0a\u8f66\u4e4b\u524d\uff0c\u5f20\u606a\u627e\u5230\u673a\u4f1a\u8ddf\u738b\u7ef4\u5747\u8bf4\u8d77\u7f51\u5427\u7684\u4e8b\u60c5\uff0c\u5012\u4e0d\u662f\u8981\u4e3a\u675c\u98de\u4ed6\u4eec\u8ddf\u738b\u7ef4\u5747\u6253\u62db\u547c\u2014\u2014\u8fd9\u4e8b\u8fd8\u4e0d\u9700\u8981\u76f4\u63a5\u627e\u738b\u7ef4\u5747\uff0c\u800c\u662f\u60f3\u4ece\u7f51\u5427\u4ea7\u4e1a\u5174\u8d77\u4e4b\u65f6\u5c31\u7eb3\u5165\u6b63\u89c4\u7684\u7ba1\u7406\u6e20\u9053\uff0c\u4e0d\u8981\u62d6\u4e0a\u4e09\u56db\u5e74\u7b49\u51fa\u4e86\u5927\u95ee\u9898\u518d\u641e\u5927\u6574\u987f\u3002</p><p>\u3000\u3000\u53e6\u5916\uff0c\u7f51\u5427\u8425\u4e1a\u6267\u7167\u7684\u53d1\u653e\u662f\u7b14\u4e0d\u5c0f\u7684\u6536\u5165\uff0c\u5bf9\u4e8e\u7f51\u5427\u4e1a\u4e3b\uff0c\u8fd9\u7b14\u8d39\u7528\u662f\u514d\u4e0d\u4e86\uff0c\u4e0e\u5176\u7ed9\u5c0f\u4e00\u64ae\u4eba\u4e2d\u9971\u79c1\u56ca\u6216\u8005\u6d41\u5165\u67d0\u4e9b\u90e8\u95e8\u7684\u5c0f\u91d1\u5e93\u5185\uff0c\u8fd8\u4e0d\u5982\u76f4\u63a5\u7eb3\u5165\u5e02\u8d22\u653f\u9884\u7b97\u4f53\u7cfb\u5bf9\u5168\u5e02\u7f51\u7edc\u5efa\u8bbe\u8fdb\u884c\u8ffd\u52a0\u6295\u8d44\u3002</p><p>\u3000\u3000\u4ece\u5e02\u59d4\u5927\u9662\u51fa\u6765\uff0c\u8fd8\u7279\u610f\u7ed5\u5230\u5b54\u5e99\u5e7f\u573a\u90a3\u91cc\u5403\u5bb5\u591c\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u4ed6\u4eec\u5c31\u5728\u98de\u673a\u4e0a\u5403\u8fc7\u4e1c\u65b9\u822a\u7a7a\u516c\u53f8\u63d0\u4f9b\u7684\u897f\u9910\uff0c\u91cf\u90fd\u5f88\u5c11\uff0c\u4ed6\u4eec\u8fd8\u7279\u5730\u5c11\u5403\u4e86\u4e9b\uff0c\u6253\u7b97\u4e0b\u4e86\u98de\u673a\u6311\u95f4\u7f8e\u98df\u5e97\u9971\u9910\u4e00\u987f\uff0c\u6ca1\u60f3\u5230\u4e34\u65f6\u63a5\u5230\u5b59\u5c1a\u4e49\u7684\u65f6\u5019\u518d\u8dd1\u8fc7\u7ed9\u7f57\u541b\u6c47\u62a5\u60c5\u51b5\u4e00\u76f4\u62d6\u5230\u73b0\u5728\uff0c\u5927\u5bb6\u90fd\u9965\u80a0\u8f98\u8f98\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5403\u8fc7\u5bb5\u591c\u56de\u5230\u4e1c\u5927\u5357\u95e8\u5df2\u7ecf\u662f\u51cc\u6668\u4e00\u70b9\u4e86\uff0c\u5f20\u606a\u5c31\u5728\u6821\u5357\u95e8\u524d\u4e0b\u4e86\u8f66\uff0c\u4ece\u5357\u95e8\u8fdb\u4e86\u5b66\u6821\u3002\u867d\u7136\u662f\u51cc\u6668\u4e00\u70b9\u591a\uff0c\u4e0d\u8fc7\u6821\u56ed\u91cc\u8fd8\u662f\u6709\u4eba\u5728\u6e38\u8361\u3002</p><p>\u3000\u3000\u56fd\u5e86\u8282\uff0c\u4e1c\u5927\u8fde\u7eed\u653e\u4e86\u4e94\u5929\u534a\u7684\u957f\u5047\uff0c\u7b97\u662f\u63d0\u524d\u4eab\u53d7\u9ec4\u91d1\u5468\u7684\u5f85\u9047\uff0c\u6709\u4e9b\u9752\u5e74\u7537\u5973\u63a5\u7740\u5047\u671f\u76f8\u805a\uff0c\u96be\u514d\u6709\u4e00\u4e24\u5bf9\u820d\u4e0d\u5f97\u5206\u5f00\u7684\uff0c\u7ecf\u8fc7\u5357\u95e8\u5185\u7684\u6885\u6797\u65f6\uff0c\u5c31\u542c\u5230\u91cc\u9762\u6709\u597d\u4e9b\u8bf4\u8bdd\u58f0\uff0c\u5408\u6b22\u5c71\u90a3\u8fb9\u66f4\u662f\u5982\u6b64\u3002</p><p>\u3000\u3000\u867d\u7136\u8fdb\u5165\u91d1\u79cb\u5341\u6708\u3001\u79cb\u9ad8\u6c14\u723d\uff0c\u591c\u91cc\u7684\u79cb\u51c9\u5012\u4e0d\u662f\u4e0d\u80fd\u6328\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u4eff\u4f5b\u4e00\u9053\u95e8\u5206\u9694\u5f00\u4e24\u4e2a\u4e16\u754c\uff0c\u8d70\u8fdb\u6821\u56ed\uff0c\u5f20\u606a\u5c31\u611f\u89c9\u6d51\u8eab\u8f7b\u677e\u3002</p><p>\u3000\u3000\u8499\u4e50\u4e0e\u675c\u98de\u90fd\u5728\u5bbf\u820d\uff0c\u5f20\u606a\u6253\u5f00\u95e8\uff0c\u770b\u7740\u4e24\u4eba\u6b63\u5750\u5728\u7535\u8111\u524d\u804a\u5929\uff0c\u95ee\u4ed6\u4eec\uff1a\u201c\u8fd9\u4e48\u665a\u8fd8\u6ca1\u6709\u7761\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u624d\u4e00\u70b9\u949f\u5417\uff1f\u201d\u8499\u4e50\u770b\u4e86\u770b\u7535\u8111\u5c4f\u5e55\u4e0a\u7684\u65f6\u95f4\u663e\u793a\uff0c\u201c\u6211\u4eec\u4ec0\u4e48\u65f6\u5019\u4e00\u70b9\u949f\u4e4b\u524d\u7761\u8fc7\u89c9\uff1f\u53e6\u5916\uff0c\u4e0d\u662f\u8fd8\u8981\u7b49\u4f60\u56de\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4f60\u4eec\u77e5\u9053\u6211\u4ec0\u4e48\u65f6\u5019\u4f1a\u56de\u6765\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u201c\u9047\u5230\u9648\u5983\u84c9\u4e86\uff0c\u201d\u675c\u98de\u8bf4\u9053\uff0c\u4ece\u7535\u8111\u91cc\u8c03\u51fa\u5de5\u4f5c\u8bb0\u5f55\uff0c\u5c06\u7b14\u8bb0\u672c\u7684\u5c4f\u5e55\u8f6c\u5411\u5f20\u606a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u90fd\u662f\u6211\u4eec\u8fd1\u671f\u505a\u7684\u4e8b\u60c5\uff0c\u4f60\u6765\u770b\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5d\u6708\u4e8c\u5341\u56db\u65e5\u5f20\u606a\u79bb\u5f00\u5efa\u90ba\u53bb\u9999\u6e2f\uff0c\u73b0\u5728\u5df2\u7ecf\u662f\u5341\u6708\u516d\u65e5\u51cc\u6668\u4e86\uff0c\u675c\u98de\u4e0e\u8499\u4e50\u4ed6\u4eec\u4e00\u76f4\u5728\u7b79\u5907\u516c\u53f8\u4e0e\u521b\u534f\u7684\u4e8b\u60c5\u3002\u5f20\u606a\u62c9\u4e86\u4e00\u5f20\u51f3\u5b50\u5750\u4e0b\u6765\uff0c\u770b\u675c\u98de\u5728\u7535\u8111\u4e0a\u8bb0\u5f55\u4e0b\u6765\u8fd9\u4e9b\u5929\u7684\u5de5\u4f5c\u60c5\u51b5\u3002\u7ecf\u8fc7\u4ed6\u4eec\u8fd9\u4e9b\u5929\u7684\u7b79\u5907\uff0c\u516c\u53f8\u4e0e\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u7684\u8109\u7edc\u5df2\u7ecf\u9010\u6e10\u6e05\u6670\u8d77\u6765\uff0c\u4e5f\u6b63\u5f0f\u6ce8\u518c\u4e86\u521b\u57df\u6295\u8d44\u7ba1\u7406\u6709\u9650\u516c\u53f8\u3002\u675c\u98de\u4ed6\u4eec\u62df\u5199\u4e86\u5b8c\u6574\u7684\u5546\u4e1a\u8ba1\u5212\uff0c\u7531\u6a61\u6811\u56ed\u521b\u4e1a\u6295\u8d44\u4e2d\u5fc3\u5411\u521b\u57df\u6295\u8d44\u7ba1\u7406\u6709\u9650\u516c\u53f8\u6ce8\u8d44\u4e00\u5343\u4e07\u63a7\u80a170\uff05\uff0c\u675c\u98de\u4e0e\u8499\u4e50\u8fd9\u4e24\u4e2a\u73b0\u9636\u6bb5\u7684\u4e3b\u521b\u4eba\u5404\u6301\u80a125\uff05\u30015\uff05\uff0c\u5f85\u516c\u53f8\u80fd\u7a33\u5b9a\u8d62\u5229\u4e4b\u540e\uff0c\u6709\u53e6\u5916\u7684\u878d\u8d44\u6e20\u9053\uff0c\u675c\u98de\u4e0e\u8499\u4e50\u8fd9\u4e9b\u521b\u59cb\u4eba\u53ef\u4ee5\u518d\u4ece\u5176\u4ed6\u5730\u65b9\u878d\u8d44\u4ece\u6a61\u6811\u56ed\u521b\u6295\u4e2d\u5fc3\u90a3\u91cc\u5c06\u516c\u53f8\u7684\u80a1\u4efd\u4e70\u56de\u53bb\uff1b\u73b0\u9636\u6bb5\u8981\u4e0d\u52a8\u7528\u7279\u6b8a\u7684\u5173\u7cfb\uff0c\u662f\u65e0\u6cd5\u4ece\u5176\u4ed6\u6e20\u9053\u8d37\u53d6\u5343\u4e07\u5143\u7684\u5de8\u8d44\u7684\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u5728\u56fd\u5185\uff0c\u4e00\u5343\u4e07\u7684\u786e\u8981\u7b97\u5f97\u4e0a\u5de8\u989d\u4e86\u3002</p><p>\u3000\u3000\u9664\u4e86\u4e4b\u524d\u6572\u5b9a\u7684\u9aa8\u5e72\u4eba\u5458\u4e4b\u5916\uff0c\u521b\u534f\u8fd8\u5229\u7528\u56fd\u5e86\u5047\u671f\u7684\u65f6\u95f4\u8fdb\u884c\u5927\u89c4\u6a21\u7684\u62db\u65b0\u6d3b\u52a8\uff0c\u5c06\u521b\u534f\u7684\u5404\u90e8\u95e8\u6846\u67b6\u6491\u8d77\u6765\u3002\u7f51\u5427\u4e0e\u7f51\u7edc\u8bbe\u8ba1\u5927\u8d5b\u4e5f\u90fd\u5728\u56fd\u5e86\u8282\u4e4b\u524d\u6b63\u5f0f\u5c55\u5f00\u4e86\uff0c\u9646\u7eed\u6709\u4f5c\u54c1\u4e0a\u7f34\u8fc7\u6765\uff0c\u53ea\u5f85\u5047\u671f\u7ed3\u675f\u5c31\u6b63\u5f0f\u8fdb\u5165\u8bc4\u5ba1\u9636\u6bb5\uff0c\u6311\u9009\u6700\u4f73\u65b9\u6848\u6216\u8005\u8bf4\u5c06\u6700\u4f18\u79c0\u7684\u51e0\u5957\u65b9\u6848\u7efc\u5408\u8d77\u6765\u91cd\u65b0\u8bbe\u8ba1\u4e4b\u540e\uff0c\u5c31\u53ef\u4ee5\u6b63\u5f0f\u8fdb\u5165\u7f51\u5427\u7684\u6b63\u5f0f\u5b9e\u65bd\u9636\u6bb5\u4e86\u3002</p><p>\u3000\u3000\u675c\u98de\u7b11\u7740\u8ddf\u5f20\u606a\u8bf4\uff1a\u201c\u65f6\u5b66\u658c\u5e94\u8be5\u53ef\u4ee5\u53bb\u505a\u7537\u516c\u5173\u4e86\uff0c\u8fd9\u6b21\u62db\u65b0\uff0c\u786c\u662f\u7ed9\u4ed6\u9a97\u4e86\u597d\u4e9b\u65e0\u77e5\u53c8\u7f8e\u8c8c\u7684\u5c11\u5973\u8fdb\u6765\uff0c\u8fd9\u5bb6\u4f19\u8fd8\u8dd1\u5230\u97f3\u4e50\u5b66\u9662\u4e0e\u5e08\u5927\u9a97\u4e86\u4e03\u516b\u4e2a\u5973\u5b69\u5b50\u8fc7\u6765\uff0c\u4f60\u8bf4\u600e\u4e48\u529e\uff1f\u5927\u5bb6\u90fd\u89c9\u5f97\u6709\u4e9b\u4e0d\u5bf9\u52b2\uff0c\u53ef\u662f\u603b\u4e0d\u80fd\u5c06\u97f3\u4e50\u5b66\u9662\u4e0e\u5e08\u5927\u7684\u6f02\u4eae\u5973\u5b69\u5b50\u5f80\u5916\u63a8\u5427\uff1f\u660e\u5929\u4f60\u4e5f\u53bb\u770b\u770b\uff0c\u770b\u4e86\uff0c\u4f60\u4e5f\u4e0d\u820d\u5f97\u5f80\u5916\u63a8\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5f20\u606a\u5f20\u4e86\u5f20\u5634\uff0c\u4ed6\u7684\u786e\u6700\u7ec8\u60f3\u7740\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u80fd\u8d85\u8d8a\u5b66\u6821\u4e0e\u5b66\u6821\u4e4b\u95f4\u7684\u969c\u788d\uff0c\u6210\u4e3a\u8de8\u8d8a\u5b66\u6821\u7684\u5b66\u751f\u521b\u4e1a\u7ec4\u7ec7\uff0c\u81f3\u5c11\u8981\u5c06\u71d5\u5f52\u6e56\u5730\u533a\u7684\u9ad8\u6821\u7fa4\u8054\u7cfb\u8d77\u6765\uff0c\u5012\u662f\u6ca1\u6709\u60f3\u5230\u65f6\u5b66\u658c\u5728\u521b\u534f\u6210\u7acb\u8fd8\u6ca1\u6709\u51e0\u5929\u7684\u5f53\u513f\u5c31\u5df2\u7ecf\u7387\u5148\u8d70\u51fa\u8fd9\u4e00\u6b65\u4e86\u3002</p><p>\u3000\u3000\u65f6\u5b66\u658c\u662f\u866b\u4ff1\u4e50\u90e8\u7684\u524d\u6210\u5458\uff0c\u6cd5\u5b66\u9662\u7684\u5927\u4e09\u5b66\u751f\uff0c\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u5916\u8054\u90e8\u7684\u8d1f\u8d23\u4eba\u3002</p></article>"}}}]);
