(window.webpackJsonp=window.webpackJsonp||[]).push([[826],{849:function(p){p.exports={chapterTitle:null,title:"\u7b2c654\u7ae0 \u5b9e\u9a8c\u5ba4\u9aa8\u67b6",content:"<article><h2>\u7b2c654\u7ae0 \u5b9e\u9a8c\u5ba4\u9aa8\u67b6</h2><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u6df7\u5728\u9676\u664b\u3001\u674e\u8fdc\u6e56\u3001\u845b\u5efa\u5fb7\u3001\u6613\u4e91\u98de\u3001\u7f57\u541b\u3001\u8096\u660e\u5efa\u90a3\u4e9b\u4e2d\u8001\u5e74\u961f\u4f0d\u91cc\u53bb\u7ed9\u90ed\u677e\u5ef6\u3001\u67f3\u5fd7\u6210\u3001\u5b59\u5c1a\u4e49\u4e00\u884c\u4eba\u63a5\u673a\uff0c\u4ed6\u7b97\u7740\u65f6\u95f4\uff0c\u6253\u7b97\u9a91\u8f66\u53bb\u6a61\u6811\u56ed\u7ed9\u8c2d\u4e91\u677e\u592b\u5987\u63a5\u98ce\u3002</p><p>\u3000\u3000\u968f\u8c2d\u4e91\u677e\u62b5\u8fbe\u5efa\u90ba\u7684\u8fd8\u6709\u4ed6\u4f5c\u4e3a\u5fae\u7535\u5b50\u6280\u672f\u57fa\u7840\u5b9e\u9a8c\u5ba4\u4ece\u65b0\u52a0\u5761\u3001\u9a6c\u6765\u897f\u4e9a\u7b49\u5730\u5ef6\u8bf7\u8fc7\u6765\u7684\u56db\u540d\u5de5\u7a0b\u5e08\uff0c\u90fd\u662f\u5728\u5fae\u7535\u5b50\u6280\u672f\u7814\u53d1\u9886\u57df\u6709\u6240\u6210\u5c31\u7684\u7cbe\u82f1\u4eba\u58eb\uff0c\u4e5f\u662f\u6a61\u6811\u56ed\u201c\u767e\u4eba\u884c\u52a8\u201d\u8ba1\u5212\u8981\u62db\u63fd\u7684\u534e\u4eba\u79d1\u5b66\u5bb6\u4e0e\u5de5\u7a0b\u5e08\u3002</p><p>\u3000\u3000\u65e2\u7136\u59d4\u4efb\u8c2d\u4e91\u677e\u4e3a\u5fae\u7535\u5b50\u6280\u672f\u57fa\u7840\u5b9e\u9a8c\u5ba4\u7684\u9996\u5e2d\u79d1\u5b66\u5bb6\uff0c\u5c31\u8981\u59d4\u4ee5\u7ec4\u5efa\u79d1\u7814\u56e2\u961f\u7684\u6743\u529b\uff0c\u53ea\u8981\u662f\u8c2d\u4e91\u677e\u8ba4\u53ef\u7684\u4eba\u9009\uff0c\u5728\u9526\u6e56\u3001\u5728\u6a61\u6811\u56ed\u65b9\u9762\u53ea\u8981\u8d70\u4e00\u4e0b\u8058\u8bf7\u7684\u5f62\u5f0f\u3002\u5f53\u7136\uff0c\u8c2d\u4e91\u677e\u4e5f\u5145\u5206\u5f81\u8be2\u4e86\u9526\u6e56\u8fd9\u8fb9\u7684\u610f\u89c1\uff0c\u7279\u522b\u662f\u5f81\u8be2\u4e86\u5bf9\u4e9a\u6d32\u5fae\u7535\u5b50\u6280\u672f\u7814\u53d1\u9886\u57df\u4e86\u89e3\u9887\u6df1\u7684\u9648\u4fe1\u751f\u3001\u4e01\u69d0\u7684\u610f\u89c1\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u4eba\u90fd\u662f\u672a\u6765\u9526\u6e56\u6280\u672f\u7814\u53d1\u4f53\u7cfb\u7684\u9aa8\u5e72\uff0c\u9700\u8981\u4ed6\u4eec\u5c06\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u9aa8\u9abc\u7ed9\u6491\u8d77\u6765\uff0c\u5f20\u606a\u4e0d\u4f1a\u541d\u60dc\u4e00\u9762\u90fd\u4e0d\u9732\u7684\u3002</p><p>\u3000\u3000\u65f6\u95f4\u6025\u8feb\u4e86\u4e9b\uff0c\u624d\u7ed9\u8c2d\u4e91\u677e\u4e0d\u5230\u4e8c\u5341\u5929\u7684\u65f6\u95f4\uff0c\u4e0d\u8fc7\u8c2d\u4e91\u677e\u5728\u9886\u57df\u5185\u5e7f\u6cdb\u800c\u6df1\u539a\u7684\u4eba\u8109\u53d1\u6325\u4e86\u5de8\u5927\u7684\u4f5c\u7528\uff0c\u8fd9\u56db\u4eba\u8fd8\u662f\u9996\u6279\u7684\u4eba\u9009\uff0c\u8fd8\u6709\u4e03\u516b\u4eba\u603b\u8981\u7b49\u7ed3\u675f\u524d\u96c7\u4e3b\u59d4\u6258\u7684\u5de5\u4f5c\u624d\u53ef\u80fd\u9646\u7eed\u5230\u5efa\u90ba\u6765\u5c31\u804c\u3002</p><p>\u3000\u3000\u6709\u8c2d\u4e91\u677e\uff0c\u6709\u8c2d\u4e91\u677e\u4ece\u4e1c\u5357\u4e9a\u5ef6\u63fd\u6765\u7684\u5341\u591a\u540d\u9ad8\u7ea7\u7814\u53d1\u4eba\u5458\uff0c\u9526\u6e56\u8fd9\u51e0\u5e74\u4e5f\u57f9\u517b\u4e86\u4e00\u6279\u6280\u672f\u9aa8\u5e72\uff0c\u8c03\u62e8\u4e00\u6279\u4eba\u8fc7\u6765\u7ed9\u8fd9\u4e9b\u9ad8\u7ea7\u7814\u53d1\u4eba\u5458\u5145\u5f53\u52a9\u624b\uff0c\u4e5f\u53ef\u4ee5\u6df1\u5165\u57f9\u517b\uff0c\u518d\u4e0e\u4e1c\u5927\u7b49\u9ad8\u6821\u8bd5\u884c\u53cc\u5bfc\u5e08\u7684\u7814\u7a76\u751f\u57f9\u517b\u673a\u5236\u3002\u6bcf\u4e2a\u9ad8\u7ea7\u7814\u53d1\u4eba\u5458\u914d\u4e24\u540d\u52a9\u624b\uff0c\u518d\u914d\u4e24\u5230\u56db\u540d\u7814\u7a76\u751f\uff0c\u5dee\u4e0d\u591a\u5c31\u80fd\u5c06\u5fae\u7535\u5b50\u57fa\u7840\u6280\u672f\u5b9e\u9a8c\u5ba4\u7684\u6846\u67b6\u7ed9\u6491\u8d77\u6765\u3002</p><p>\u3000\u3000\u80fd\u6709\u4e24\u5230\u4e09\u5e74\u7684\u65f6\u95f4\uff0c\u5b9e\u9a8c\u5ba4\u7684\u6280\u672f\u56e2\u961f\u5c31\u80fd\u521d\u6b65\u7684\u6210\u719f\u8d77\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6a61\u6811\u56ed\u7684\u7b2c\u4e8c\u521b\u4e1a\u56ed\u5f0f\u4e3b\u4f53\u6539\u5efa\u5de5\u7a0b\u4e5f\u5df2\u7ecf\u5230\u5c3e\u58f0\u4e86\uff1b\u4e3a\u4e86\u533a\u522b\u4e1c\u8fb9\u6539\u5efa\u5230\u5c3e\u58f0\u7684\u7b2c\u4e8c\u521b\u4e1a\u56ed\uff0c\u5185\u90e8\u4eba\u5458\u901a\u5e38\u5c06\u8fd9\u91cc\u79f0\u4e3a\u4e3d\u4e30\u56ed\uff0c\u4e1c\u8fb9\u7684\u521b\u4e1a\u56ed\u79f0\u4e3a\u71d5\u6c34\u56ed\uff0c\u9a91\u8f66\u5230\u4e3d\u4e30\u56ed\u524d\uff0c\u6b63\u8d76\u4e0a\u8d75\u9e4f\u3001\u8d75\u6893\u7433\u9886\u7740\u8c2d\u4e91\u677e\u592b\u5987\u4e0e\u4e00\u773c\u770b\u4e0a\u53bb\u5c31\u50cf\u534e\u4fa8\u5de5\u7a0b\u5e08\u6253\u626e\u7684\u51e0\u4e2a\u4eba\u4ece\u4e3d\u4e30\u56ed\u8d70\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u8fd9\u662f\u53bb\u54ea\u91cc\uff1f\u201d\u5f20\u606a\u811a\u652f\u7740\u5730\u95ee\u8d75\u9e4f\u4ed6\u4eec\uff0c\u5f20\u606a\u8fc7\u6765\u4e4b\u524d\uff0c\u4e5f\u6ca1\u6709\u8ddf\u4ed6\u4eec\u6253\u62db\u547c\uff0c\u81ea\u5df1\u6390\u7740\u65f6\u95f4\u8fc7\u6765\u7684\u3002</p><p>\u3000\u3000\u201c\u8c2d\u8001\u5e08\u8bf4\u662f\u8981\u5148\u53bb\u71d5\u6c34\u56ed\u770b\u4e00\u770b\u2026\u2026\u201d\u8d75\u9e4f\u8dd1\u8fc7\u6765\u5c06\u5f20\u606a\u8de8\u4e0b\u7684\u81ea\u884c\u8f66\u63a5\u8fc7\u6765\uff0c\u603b\u4e0d\u80fd\u8ba9\u5f20\u606a\u63a8\u7740\u81ea\u884c\u8f66\u8ddf\u4ed6\u4eec\u5f80\u71d5\u6c34\u56ed\u53bb\u3002</p><p>\u3000\u3000\u5b9e\u9a8c\u5ba4\u90fd\u662f\u65b0\u5efa\u7684\u5efa\u7b51\uff0c\u8d76\u5728\u5341\u6708\u524d\u5b8c\u6210\u4e3b\u4f53\u5de5\u7a0b\u5df2\u7ecf\u662f\u76f8\u5f53\u4e0d\u9519\u4e86\uff0c\u79bb\u771f\u6b63\u5efa\u6210\u3001\u57fa\u7840\u8bbe\u65bd\u5b89\u88c5\u5b8c\u6bd5\uff0c\u8fd8\u9700\u8981\u4e00\u4e9b\u65f6\u95f4\u3002\u867d\u7136\u5341\u6708\u4efd\u52a0\u5927\u521b\u6295\u9879\u76ee\u7684\u5ba1\u6279\u529b\u5ea6\uff0c\u5341\u6708\u901a\u8fc7\u5ba1\u6279\u7684\u9879\u76ee\u4e5f\u4e0d\u8fc7\u516b\u4e2a\uff0c\u79bb\u586b\u6ee1\u4e3d\u4e30\u56ed\u8fd8\u9700\u8981\u76f8\u5f53\u957f\u7684\u4e00\u6bb5\u65f6\u95f4\uff1b\u71d5\u6c34\u56ed\u5efa\u6210\u4e4b\u540e\uff0c\u5148\u5c06\u6a61\u6811\u56ed\u7b79\u5907\u529e\u516c\u5ba4\u642c\u8fc7\u53bb\uff0c\u8c2d\u4e91\u677e\u7b49\u5148\u62db\u52df\u5230\u7684\u79d1\u5b66\u5bb6\u3001\u5de5\u7a0b\u5e08\uff0c\u4e5f\u4f1a\u5148\u5c06\u5de5\u4f5c\u5730\u70b9\u653e\u5728\u71d5\u6c34\u56ed\u8fd9\u8fb9\uff0c\u7b49\u57fa\u7840\u5b9e\u9a8c\u5ba4\u5efa\u6210\u4e4b\u540e\uff0c\u518d\u5c06\u8fd9\u91cc\u632a\u51fa\u6765\u7ed9\u540e\u7eed\u7684\u521b\u4e1a\u4f01\u4e1a\u5165\u9a7b\u3002</p><p>\u3000\u3000\u8c2d\u4e91\u677e\u7ed9\u5f20\u606a\u4ecb\u7ecd\u4ed6\u8eab\u8fb9\u3001\u968f\u4ed6\u5230\u5efa\u90ba\u6765\u53c2\u4e0e\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u534e\u4eba\u5de5\u7a0b\u5e08\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u56de\u65b0\u52a0\u5761\uff0c\u6211\u5f88\u96be\u62ff\u8a00\u8bed\u8ddf\u4ed6\u4eec\u63cf\u8ff0\u606a\u5c11\u5e74\u5c11\u6709\u4e3a\uff0c\u8fd8\u597d\u9526\u6e56\u53c8\u505a\u51fa\u8fd9\u756a\u8ba9\u4eba\u77a0\u76ee\u7ed3\u820c\u7684\u52a8\u4f5c\u6765\uff0c\u90fd\u4e0d\u7528\u6211\u89e3\u91ca\u4ec0\u4e48\u4e86\u3002\u6211\u4e0e\u67f3\u5fd7\u6210\u4e5f\u6709\u7f18\u89c1\u8fc7\u51e0\u9762\uff0c\u6ca1\u60f3\u5230\u90fd\u7ed9\u4f60\u62c9\u5230\u5efa\u90ba\u6765\u4e86\u2026\u2026\u600e\u4e48\u5728\u673a\u573a\u6ca1\u6709\u770b\u5230\u606a\u5c11\u4f60\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u53bb\u51d1\u90a3\u4e2a\u70ed\u95f9\u505a\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u542c\u8c2d\u4e91\u677e\u8bf4\u6676\u5706\u5382\u9879\u76ee\u7684\u4e8b\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u4eca\u5929\u672c\u5e94\u8be5\u662f\u4e01\u69d0\u6765\u63a5\u5f85\u4f60\u4eec\u7684\uff0c\u4e0d\u8fc7\u4ed6\u7ed9\u6211\u8d76\u5230\u65e5\u672c\u53bb\u4e86\uff0c\u5012\u662f\u82cf\u6d25\u4e1c\u80fd\u5728\u665a\u996d\u4e4b\u524d\u8d76\u8fc7\u6765\u3002\u4f60\u4eec\u4e0d\u5148\u53bb\u7ed9\u5b89\u6392\u4f4f\u7684\u5730\u65b9\u4f11\u606f\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u4e01\u69d0\u662f\u9526\u6e56\u6574\u4e2a\u6280\u672f\u7814\u53d1\u4f53\u7cfb\u7684\u603b\u8d1f\u8d23\u4eba\u3002</p><p>\u3000\u3000\u201c\u5728\u98de\u673a\u4e0a\u5750\u4e86\u4e94\u4e2a\u5c0f\u65f6\uff0c\u4e5f\u8981\u6d3b\u52a8\u4e00\u4e0b\u817f\u811a\uff0c\u5c31\u5148\u53bb\u71d5\u6c34\u56ed\u770b\u770b\uff0c\u5148\u4e86\u89e3\u4e00\u4e0b\u5de5\u4f5c\u73af\u5883\u4e5f\u597d\u3002\u201d\u8c2d\u4e91\u677e\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u4e0d\u5acc\u522b\u4eba\u592a\u656c\u4e1a\uff0c\u5c31\u966a\u7740\u8c2d\u4e91\u677e\u7b49\u4eba\u5f80\u71d5\u6c34\u56ed\u53bb\u53c2\u89c2\uff0c\u4e5f\u5728\u8def\u4e0a\u5c06\u6676\u5706\u5382\u9879\u76ee\u7684\u4e00\u4e9b\u8be6\u60c5\u89e3\u91ca\u7ed9\u8c2d\u4e91\u677e\u542c\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u6210\u8131\u79bb\u53f0\u79ef\u7535\u5230\u5efa\u90ba\u8d1f\u8d23\u7b79\u5efa\u6676\u5706\u5382\u7684\u4e8b\u60c5\u662f\u8fd1\u671f\u4e1a\u5185\u6700\u8f70\u52a8\u7684\u4e00\u9879\u6d88\u606f\uff0c\u5728\u5f53\u524d\u7684\u5c40\u52bf\u4e0b\uff0c\u5bf9\u4e9a\u6d32\u51e0\u5bb6\u6676\u5706\u5236\u9020\u4f01\u4e1a\u90fd\u6709\u4e00\u5b9a\u7684\u51b2\u51fb\uff0c\u6bd5\u7adf\u53c8\u591a\u4e86\u4e00\u4e2a\u6f5c\u5728\u7684\u7ade\u4e89\u5bf9\u624b\uff0c\u6709\u4e9b\u4e8b\u60c5\u5c31\u4e5f\u4e0d\u9700\u8981\u4f5c\u4e3a\u7edd\u5bc6\u5bf9\u6240\u6709\u4eba\u90fd\u79d8\u800c\u4e0d\u5ba3\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u6253\u7b97\u5f15\u8fdb0.35\u5fae\u7c73\u7684\u6676\u5706\u5236\u9020\u5de5\u827a\uff0c\u4e0d\u8fc7\u8fd90.35\u5fae\u7c73\u7ea7\u522b\u7684\u96c6\u6210\u7535\u8def\u8bbe\u8ba1\uff0c\u9664\u4e86\u9526\u6e56\u8fd9\u4e24\u5e74\u5efa\u4e86\u4e9b\u57fa\u7840\uff0c\u56fd\u5185\u51e0\u4e4e\u662f\u7a7a\u767d\uff0c\u6211\u5f88\u671f\u5f85\u5b9e\u9a8c\u5ba4\u80fd\u5c06\u76f8\u5173\u5de5\u4f5c\u5e26\u52a8\u8d77\u6765\uff0c\u4e24\u4e09\u5e74\u540e\uff0c\u80fd\u57f9\u517b\u51fa\u4e00\u6279\u9ad8\u6c34\u51c6\u7684IC\u5de5\u7a0b\u5e08\u6765\u2026\u2026\u201d\u5f20\u606a\u8fb9\u8d70\u8fb9\u8ddf\u8c2d\u4e91\u8bf4\u3002</p><p>\u3000\u3000\u201c\u6211\u770b\u9526\u6e56\u5728\u672a\u6765\u4e24\u4e09\u5e74\u7684\u53d1\u5c55\uff0c\u5c31\u53ef\u80fd\u5c06\u8fd9\u4e2a\u6676\u5706\u5382\u5582\u9971\u2026\u2026\u201d\u8c2d\u4e91\u677e\u8bf4\u9053\u3002</p><p>\u3000\u3000\u8c2d\u4e91\u677e\u9884\u8ba1\u7b49\u5efa\u90ba\u7684\u6676\u5706\u5382\u5efa\u6210\uff0c\u56fd\u9645\u4e0a\u7684\u4e3b\u6d41\u6676\u5706\u5382\u5dee\u4e0d\u591a\u90fd\u8981\u6362\u4e0a0.18\u5fae\u7c73\u751a\u81f3\u66f4\u9ad8\u6c34\u5e73\u7684\u5de5\u827a\uff0c\u5efa\u90ba\u7684\u6676\u5706\u5382\u6070\u80fd\u6ee1\u8db3\u9526\u6e56\u4f4e\u7aef\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\u5bf9\u82af\u7247\u7684\u9700\u8981\uff0c\u9ad8\u7aef\u4ea7\u54c1\u6240\u9700\u8981\u7684\u82af\u7247\u8fd1\u51e0\u5e74\u8fd8\u662f\u8981\u59d4\u6258\u5fb7\u4eea\u8fd9\u4e9b\u4f01\u4e1a\u4ee3\u5de5\u7684\uff0c\u4e0d\u53ef\u5426\u8ba4\uff0c\u4e2d\u4f4e\u7aef\u7684\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\u5728\u65b0\u5174\u5e02\u573a\u6709\u7740\u5de8\u5927\u7684\u5e02\u573a\u6f5c\u529b\u3002\u8fd9\u4e00\u5757\uff0c\u6070\u6070\u662f\u9526\u6e56\u672a\u6765\u4e24\u4e09\u5e74\u7684\u4e3b\u653b\u65b9\u5411\uff0c\u5373\u4f7f\u9526\u6e56\u63a8\u51fa\u7684\u4e24\u6b3e\u624b\u673a\uff0c\u8d70\u91cf\u80fd\u7684\u8fd8\u662f\u4f4e\u7aef\u7684i08\u624b\u673a\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5f20\u606a\u8fd8\u6ca1\u6709\u8ddf\u8c2d\u4e91\u677e\u8ba8\u8bba\u7ed9\u8d34\u724c\u624b\u673a\u5382\u5546\u4f9b\u5e94\u7ec4\u4ef6\u7684\u53ef\u80fd\uff0c\u8fd9\u4e9b\u624b\u673a\u7ec4\u4ef6\uff0c\u6070\u6070\u4e5f\u5bf9\u4f4e\u7aef\u96c6\u6210\u7535\u8def\u82af\u7247\u4f1a\u4ea7\u751f\u6781\u5927\u7684\u9700\u6c42\u3002</p><p>\u3000\u3000\u8c8c\u4f3c0.35\u5fae\u7c73\u7684\u6280\u672f\u5728\u4e5d\u4e03\u5e74\uff0c\u8fd8\u4e0d\u80fd\u7b97\u662f\u4f4e\u7aef\u6280\u672f\u3002</p><p>\u3000\u30000.35\u5fae\u7c73\u7ea7\u522b\u7684\u82af\u7247\u8bbe\u8ba1\uff0c\u5728\u56fd\u5185\u51e0\u4e4e\u662f\u7a7a\u767d\uff0c\u8981\u662f\u56fd\u5185\u5e02\u573a\u6ca1\u6709\u8fd9\u79cd\u7a0b\u5ea6\u7684\u82af\u7247\u8bbe\u8ba1\u516c\u53f8\uff0c\u6676\u5706\u5382\u5efa\u6210\u4e4b\u540e\uff0c\u5c31\u8981\u53bb\u56fd\u9645\u5e02\u573a\u4e89\u53d6\u5ba2\u6237\uff0c\u7ade\u4e89\u65e0\u7591\u4f1a\u5f88\u6b8b\u9177\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u6210\u6700\u7ec8\u4e0b\u51b3\u5fc3\u8fc7\u6765\u8d1f\u8d23\u6676\u5706\u5382\u9879\u76ee\uff0c\u4e5f\u662f\u4ece\u5b59\u5c1a\u4e49\u5230\u53f0\u6e7e\u65f6\u7ed9\u4ed6\u5e26\u53bb\u7684\u51e0\u6837\u5c0f\u793c\u54c1\u4e2d\u770b\u5230\u9526\u6e56\u5728\u6280\u672f\u4e0a\u7684\u771f\u6b63\u5b9e\u529b\u4ee5\u53ca\u672a\u6765\u7684\u5e02\u573a\u6f5c\u529b\u3002\u7684\u786e\uff0c\u4ec5\u4ec5\u662f\u4e00\u6761\u516b\u82f1\u5bf8\u7684\u7ebf\uff0c\u9526\u6e56\u5728\u672a\u6765\u4e24\u4e09\u5e74\u5c31\u53ef\u80fd\u5c06\u6676\u5706\u5382\u5582\u9971\uff0c\u4ed6\u5230\u5efa\u90ba\u6765\u7b79\u5efa\u6676\u5706\u5382\u9879\u76ee\uff0c\u538b\u529b\u65e0\u7591\u4f1a\u5c11\u5f88\u591a\u3002</p><p>\u3000\u3000\u5f85\u6676\u5706\u5382\u9879\u76ee\u5efa\u8bbe\u5b8c\u6210\uff0c\u9526\u6e56\u7684\u5782\u76f4\u751f\u4ea7\u4f53\u7cfb\u624d\u7b97\u6709\u4e86\u4e00\u4e2a\u96cf\u5f62\uff0c\u4e4b\u540e\u8fd8\u9700\u8981\u82b1\u5927\u529b\u6c14\u624d\u8ba9\u8fd9\u4e2a\u4f53\u7cfb\u4e30\u6ee1\u8d77\u6765\u3002</p><p>\u3000\u3000\u4e0e\u6b27\u7f8e\u5b8c\u5584\u7684\u6280\u672f\u5e02\u573a\u4e0d\u540c\uff0c\u56fd\u5185\u7684\u7535\u5b50\u6280\u672f\u6c34\u5e73\u843d\u540e\u592a\u591a\uff0c\u9526\u6e56\u8981\u662f\u53ea\u4e13\u6ce8\u505a\u7ec8\u7aef\u4ea7\u54c1\uff0c\u4e00\u6d41\u7684\u4ea7\u54c1\u6240\u9700\u8981\u7684\u5143\u914d\u4ef6\u51e0\u4e4e\u90fd\u8981\u53bb\u56fd\u9645\u5e02\u573a\u91c7\u8d2d\uff0c\u56fd\u5bb6\u4e0e\u56fd\u5bb6\u4e4b\u95f4\u7684\u5173\u7a0e\u58c1\u5792\u3001\u6280\u672f\u58c1\u5792\uff0c\u9664\u4e86\u62fc\u547d\u6324\u538b\u5de5\u4eba\u7684\u52b3\u52a8\u529b\u3001\u964d\u4f4e\u4eba\u529b\u8d44\u6e90\u6210\u672c\u4e4b\u5916\uff0c\u4e0e\u90a3\u4e9b\u6d77\u5916\u7535\u5b50\u5de5\u4e1a\uff0c\u9526\u6e56\u51e0\u4e4e\u5c31\u6ca1\u6709\u4efb\u4f55\u4f18\u52bf\u53ef\u8a00\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u662f\u9526\u6e56\u5fc5\u987b\u8981\u81ea\u529b\u66f4\u751f\u7b79\u5efa\u4e00\u4e2a\u5b8c\u5584\u7684\u5782\u76f4\u7684\u751f\u4ea7\u4f53\u7cfb\u7684\u5173\u952e\u56e0\u7d20\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u76f8\u4fe1\u968f\u7740\u65f6\u95f4\u7684\u63a8\u79fb\uff0c\u4eba\u4eec\u80fd\u9010\u6e10\u7684\u610f\u8bc6\u5230\u8fd9\u4e2a\u5782\u76f4\u5316\u7684\u751f\u4ea7\u4f53\u7cfb\u5728\u65b0\u5174\u5e02\u573a\u4f1a\u95ea\u8000\u51fa\u8d8a\u6765\u8d8a\u8000\u773c\u7684\u5149\u8292\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u4e0b\u5348\u4e94\u70b9\u949f\u624d\u4e58\u8f66\u8d76\u5230\u5efa\u90ba\u4e0e\u8c2d\u4e91\u677e\u7b49\u4eba\u89c1\u9762\uff0c\u660e\u5929\u8ba1\u59d4\u526f\u4e3b\u4efb\u845b\u5efa\u5fb7\u4e00\u884c\u4eba\u8981\u6765\u53c2\u89c2\u6a61\u6811\u56ed\uff0c\u8981\u6709\u4e00\u4e2a\u91cd\u91cf\u7ea7\u7684\u4eba\u7269\u51fa\u9762\u63a5\u5f85\uff0c\u5bb6\u91cc\u5c31\u5269\u82cf\u6d25\u4e1c\u4e86\uff1b\u5468\u6e38\u53c8\u4e0d\u662f\u7231\u8fbe\u96c6\u56e2\u4f53\u7cfb\u7684\uff0c\u4ed6\u51fa\u9762\u5c31\u6709\u4e9b\u4e0d\u4f26\u4e0d\u7c7b\u3002</p><p>\u3000\u3000\u9752\u5e74\u516c\u5bd3\u6b63\u6293\u7d27\u65f6\u95f4\u88c5\u4fee\u524d\u9762\u56db\u680b\u697c\uff0c\u4f46\u662f\u7b49\u88c5\u4fee\u7ed3\u675f\uff0c\u4e5f\u4e0d\u662f\u4e00\u4e24\u5929\u7684\u5de5\u592b\u3002\u5f20\u606a\u8ba9\u4eba\u5c06\u71d5\u5f52\u6e56\u897f\u5cb8\u7684\u4e1c\u534e\u5bbe\u9986\u7684\u4e00\u680b\u9644\u697c\u7ed9\u5305\u4e0b\u6765\uff0c\u5b89\u6392\u67f3\u5fd7\u6210\u53ca\u968f\u884c\u4eba\u5458\u5165\u4f4f\u2014\u2014\u6676\u5706\u5382\u7b79\u5907\u529e\u516c\u5904\u4e5f\u653e\u5728\u71d5\u6c34\u56ed\uff0c\u603b\u4e4b\u67f3\u5fd7\u6210\u4ed6\u4eec\u8fd9\u65f6\u5019\u8981\u63a5\u53d7\u8ba1\u59d4\u3001\u7701\u3001\u5e02\u7b49\u653f\u5e9c\u90e8\u95e8\u7684\u70ed\u60c5\u62db\u5f85\uff1b\u4e5f\u5b89\u6392\u8c2d\u4e91\u677e\u7b49\u4eba\u6682\u65f6\u4f4f\u8fdb\u4e1c\u534e\u5bbe\u9986\uff0c\u5148\u8bf7\u8c2d\u4e91\u677e\u7b49\u4eba\u53bb\u4e1c\u534e\u5bbe\u9986\u4f11\u606f\u4e00\u4e0b\uff0c\u665a\u996d\u5c31\u5b89\u6392\u5728\u4e1c\u534e\u5bbe\u9986\u3002</p><p>\u3000\u3000\u5728\u53bb\u4e1c\u534e\u5bbe\u9986\u7684\u8def\u4e0a\uff0c\u82cf\u6d25\u4e1c\u8ddf\u5f20\u606a\u8bf4\u8d77\u5bf9\u79d1\u738b\u9ad8\u79d1\u4f9b\u5e94\u624b\u673a\u7ec4\u914d\u4ef6\u7684\u8003\u8651\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e0d\u80fd\u4e00\u4e0b\u5b50\u6269\u4ea7\u592a\u591a\uff0c\u8fd9\u6837\u5bf9\u9526\u6e56\u6574\u4e2a\u7ba1\u7406\u4f53\u7cfb\u7684\u51b2\u51fb\u4f1a\u6bd4\u8f83\u5927\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u4e00\u65e6\u6211\u4eec\u5927\u8086\u7684\u8fdb\u5165\u624b\u673a\u7ec4\u914d\u4ef6\u4f9b\u5e94\u5e02\u573a\uff0c\u4f1a\u5bf9\u56fd\u5916\u7684\u624b\u673a\u7ec4\u914d\u5382\u4f9b\u5e94\u5382\u5546\u9020\u6210\u76f8\u5f53\u5927\u7684\u51b2\u51fb\u3002\u8fd1\u4e24\u5e74\u5185\uff0c\u624b\u673a\u7ec4\u914d\u4ef6\u7684\u5173\u7a0e\u58c1\u5792\u4f1a\u7ed9\u6211\u4eec\u63d0\u4f9b\u4e00\u5c42\u4fdd\u62a4\uff0c\u8fd8\u6709\u5c31\u662f\u4f4e\u5ec9\u7684\u4eba\u529b\u8d44\u6e90\u6210\u672c\u4e5f\u662f\u6211\u4eec\u7684\u4f18\u52bf\uff0c\u4f46\u662f\u4e00\u65e6\u6d77\u5916\u624b\u673a\u7ec4\u914d\u4ef6\u5382\u5546\u8fdb\u5165\u56fd\u5185\u5927\u91cf\u5efa\u5382\uff0c\u5c31\u4f1a\u8fc5\u901f\u7684\u5316\u89e3\u6211\u4eec\u7684\u8fd9\u4e24\u9879\u4f18\u52bf\uff0c\u5982\u679c\u5728\u8fd9\u6bb5\u65f6\u95f4\u91cc\uff0c\u6211\u4eec\u4e0d\u80fd\u5f25\u8865\u6280\u672f\u4e0a\u7684\u52a3\u52bf\uff0c\u540e\u671f\u7684\u7ade\u4e89\uff0c\u53cd\u800c\u4f1a\u9677\u5165\u52a3\u52bf\u3002\u6211\u4eec\u8981\u5c3d\u53ef\u80fd\u8ba9\u8fd9\u4e2a\u8fdb\u7a0b\u53d8\u957f\uff0c\u8d62\u53d6\u66f4\u591a\u7684\u65f6\u95f4\uff0c\u6bd5\u7adf\u6a61\u6811\u56ed\u8ba1\u5212\u8981\u53d1\u6325\u4f5c\u7528\u603b\u9700\u8981\u4e00\u6bb5\u65f6\u95f4\u7684\u2026\u2026\u6211\u60f3\u5bf9\u79d1\u738b\u9ad8\u79d1\u7684\u4f9b\u534e\u4ef7\u683c\u8981\u7ef4\u6301\u5728\u4e00\u4e2a\u76f8\u5bf9\u8f83\u9ad8\u7684\u6c34\u5e73\u4e4b\u4e0a\uff0c\u9010\u6b65\u7684\u6709\u8ba1\u5212\u7684\u8fdb\u5165\u624b\u673a\u7ec4\u914d\u4ef6\u4f9b\u5e94\u5e02\u573a\u3002\u201d</p></article>"}}}]);
