(window.webpackJsonp=window.webpackJsonp||[]).push([[827],{850:function(p){p.exports={chapterTitle:null,title:"\u7b2c824\u7ae0 \u519b\u65b9\u603b\u662f\u85cf\u5728\u80cc\u540e",content:"<article><h2>\u7b2c824\u7ae0 \u519b\u65b9\u603b\u662f\u85cf\u5728\u80cc\u540e</h2><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u6cb3\u5824\u4e0a\u7ed9\u5d14\u90c1\u66fc\u56de\u7535\u8bdd\uff0c\u4e0d\u6e05\u695a\u5979\u4e3a\u4ec0\u4e48\u5728\u8fd9\u65f6\u5019\u6253\u7535\u8bdd\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u521a\u542c\u8bf4\u5efa\u90ba\u6668\u62a5\u793e\u7684\u8bb0\u8005\u4e5f\u5148\u56de\u91d1\u5c71\u4e86\uff0c\u4e0d\u77e5\u9053\u4f60\u8fd8\u80fd\u4e0d\u80fd\u8054\u7cfb\u4e0a\uff0c\u6211\u54e5\u8fd8\u6709\u65b0\u534e\u793e\u7684\u8521\u4e91\u98de\u793e\u957f\u60f3\u627e\u5efa\u90ba\u6668\u62a5\u793e\u7684\u8bb0\u8005\u8bf4\u4e9b\u4e8b\uff0c\u201d\u5d14\u90c1\u66fc\u5728\u7535\u8bdd\u91cc\u8bf4\u9053\uff0c\u201c\u6211\u54e5\u4e5f\u8981\u5f53\u9762\u8bf4\u58f0\u8c22\u8c22\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u90a3\u6211\u5e2e\u4f60\u4eec\u8054\u7cfb\u4e00\u4e0b\u2026\u2026\u201d\u5f20\u606a\u5148\u6302\u4e86\u7535\u8bdd\uff0c\u671d\u7fdf\u4e39\u9752\u644a\u644a\u624b\uff0c\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u4f60\u731c\u6d4b\u9519\u8bef\uff0c\u8fd8\u662f\u6307\u671b\u4e0d\u4e0a\u4ed6\u4eec\u73b0\u5728\u80fd\u611f\u6069\u6234\u5fb7\u3002\u5373\u4f7f\u65b0\u534e\u793e\u526f\u793e\u957f\u8521\u4e91\u98de\u591a\u534a\u77e5\u9053\u6211\u7684\u8eab\u4efd\uff0c\u4f46\u662f\u4ed6\u4eec\u6ca1\u6709\u5c31\u8fd9\u4e2a\u95ee\u9898\u8bf4\u5230\u4e00\u8d77\u53bb\u2014\u2014\u4ed6\u4eec\u627e\u6211\u662f\u4e3a\u4e86\u8054\u7cfb\u7b80\u5fd7\u5eb7\uff0c\u4f60\u73b0\u5728\u80fd\u731c\u5230\u4ed6\u4eec\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u5d14\u56fd\u6052\u4ed6\u4eec\u5728\u7ae0\u5dde\u91c7\u8bbf\u7684\u7b2c\u4e00\u624b\u6750\u6599\u5728\u4eba\u88ab\u6263\u62bc\u540e\u81ea\u7136\u4e5f\u88ab\u90a3\u4e9b\u4eba\u9500\u6bc1\u4e86\uff0c\u4ed6\u4eec\u60f3\u501f\u7528\u5eb7\u5fd7\u5eb7\u624b\u91cc\u7684\u7b2c\u4e00\u624b\u8d44\u6599\uff1f\u201d\u5d14\u90c1\u66fc\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u591a\u534a\u5982\u6b64\u2026\u2026\u201d\u5f20\u606a\u70b9\u70b9\u5934\u8bf4\u9053\u3002</p><p>\u3000\u3000\u8003\u8651\u5230\u8fd9\u7c7b\u65b0\u95fb\u9898\u6750\u7684\u654f\u611f\u6027\uff0c\u5373\u4f7f\u5141\u8bb8\u901a\u8fc7\u65b0\u95fb\u5a92\u4f53\u62ab\u9732\uff0c\u6309\u7167\u8981\u6c42\u90fd\u9700\u8981\u4ee5\u65b0\u534e\u793e\u901a\u7a3f\u7684\u5f62\u5f0f\u520a\u8f7d\uff1b\u82e5\u4e0d\u80fd\u516c\u5f00\u62ab\u9732\uff0c\u4e5f\u53ef\u4ee5\u901a\u8fc7\u65b0\u534e\u5185\u53c2\u7684\u5f62\u5f0f\uff0c\u5c06\u5728\u7ae0\u5dde\u6697\u8bbf\u5230\u7684\u7b2c\u4e00\u624b\u6750\u6599\u53cd\u5e94\u5230\u4e2d\u592e\u9ad8\u5c42\u3002\u5efa\u90ba\u6668\u62a5\u7684\u5f71\u54cd\u529b\u6bd5\u7adf\u8fd8\u5c40\u9650\u5728\u534e\u4e1c\u5730\u533a\uff0c\u53d1\u8868\u5728\u65b0\u534e\u5185\u53c2\u4e0a\uff0c\u5bf9\u89e3\u51b3\u95ee\u9898\u66f4\u6709\u5e2e\u52a9\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f53\u521d\u6d3e\u51fa\u4e09\u6279\u4eba\u624b\u5230\u7ae0\u5dde\u6697\u8bbf\uff0c\u5c31\u662f\u60f3\u6536\u96c6\u7b2c\u4e00\u624b\u6750\u6599\u901a\u8fc7\u5404\u79cd\u6e20\u9053\u4f20\u9012\u5230\u4e2d\u592e\u9ad8\u5c42\uff0c\u5c3d\u6700\u5927\u7684\u53ef\u80fd\u6765\u5f71\u54cd\u8fd9\u6b21\u7684\u7a00\u571f\u53ca\u7a00\u6709\u91d1\u5c5e\u4ea7\u4e1a\u653f\u7b56\u4e0d\u81f3\u4e8e\u62d0\u5230\u6b6a\u8def\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u6240\u6709\u7684\u6b63\u5f0f\u9014\u5f84\u91cc\uff0c\u901a\u8fc7\u65b0\u534e\u5185\u53c2\u4e0a\u8fbe\u5929\u542c\uff0c\u65e0\u7591\u662f\u6700\u4f73\u7684\u9014\u5f84\uff1a\u8521\u4e91\u98de\u3001\u5d14\u90c1\u6052\u4ed6\u4eec\u6025\u7740\u627e\u7b80\u5fd7\u5eb7\uff0c\u90a3\u80af\u5b9a\u4e5f\u4e0d\u60f3\u8f7b\u6613\u653e\u8fc7\u7ae0\u5dde\u3002</p><p>\u3000\u3000\u7b80\u5fd7\u5eb7\u7ad9\u5728\u6cb3\u5824\u4e0a\u7684\u8fdc\u5904\u6342\u7740\u9f3b\u5b50\u5728\u6b23\u8d4f\u8fd9\u66ae\u8272\u4e0b\u7684\u996e\u9a6c\u6cb3\uff0c\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u5927\u6b65\u8d70\u8fc7\u6765\uff0c\u8ddf\u4ed6\u8bf4\u9053\uff1a\u201c\u65b0\u534e\u793e\u526f\u793e\u957f\u8521\u4e91\u98de\u60f3\u89c1\u4f60\uff0c\u591a\u534a\u4e3a\u4e86\u7ae0\u5dde\u79c1\u77ff\u8fdd\u7981\u7684\u4e8b\u60c5\uff0c\u6211\u5b89\u6392\u8f66\u9001\u4f60\u8fc7\u53bb\uff1f\u201d</p><p>\u3000\u3000\u7b80\u5fd7\u5eb7\u4e0e\u6731\u5c0f\u519b\u662f\u540c\u5b66\uff0c\u6bd5\u4e1a\u540e\u8fdb\u5165\u65b0\u534e\u793e\u4e1c\u6d77\u5206\u793e\u5de5\u4f5c\uff0c\u4e0e\u6731\u5c0f\u519b\u4e00\u8d77\u5c06\u9999\u96ea\u6d77\u5408\u8d44\u6848\u7684\u6750\u6599\u8d8a\u7ea7\u6345\u5230\u7701\u91cc\uff0c\u90a3\u6b21\u4e8b\u60c5\u4e4b\u540e\uff0c\u6731\u5c0f\u519b\u7ed9\u8e22\u51fa\u7701\u7ecf\u8d38\u59d4\uff0c\u7b80\u5fd7\u5eb7\u5219\u4e0d\u518d\u662f\u65b0\u534e\u793e\u7684\u8bb0\u8005\u3002\u5012\u662f\u8fd9\u51e0\u5e74\u65f6\u95f4\u4ee5\u6765\uff0c\u8fdb\u5165\u5efa\u90ba\u6668\u62a5\u793e\u4e4b\u540e\u7684\u7b80\u5fd7\u5eb7\u65f6\u5e38\u80fd\u91c7\u8bbf\u9526\u6e56\u7684\u72ec\u5bb6\u65b0\u95fb\uff0c\u5df2\u7ecf\u662f\u4e1c\u6d77\u9887\u5177\u540d\u6c14\u7684\u8d22\u7ecf\u8bb0\u8005\uff0c\u5f20\u606a\u5e73\u65e5\u4e0d\u63a5\u89e6\u65b0\u95fb\u5a92\u4f53\u7684\u8bb0\u8005\uff0c\u4f46\u662f\u7b80\u5fd7\u5eb7\u4f8b\u5916\uff0c\u9526\u6e56\u8bb8\u591a\u9700\u8981\u5411\u5a92\u4f53\u62ab\u9732\u7684\u6d88\u606f\uff0c\u4e5f\u662f\u9996\u5148\u4f1a\u60f3\u5230\u7b80\u5fd7\u5eb7\u3002</p><p>\u3000\u3000\u5b89\u6392\u7684\u8f66\u8f86\u5f00\u5230\u6cb3\u5824\u8fd9\u8fb9\u6765\u63a5\u7b80\u5fd7\u5eb7\u53bb\u533b\u9662\u89c1\u8521\u4e91\u98de\u3001\u5d14\u56fd\u6052\u7b49\u4eba\uff0c\u770b\u7740\u8f7f\u8f66\u79bb\u5f00\uff0c\u7fdf\u4e39\u9752\u53c8\u8f6c\u8eab\u770b\u7740\u66ae\u8272\u4e0b\u6d51\u6d4a\u7684\u6cb3\u6c34\uff0c\u8fd1\u5cb8\u5904\u6709\u6b7b\u9c7c\u7ffb\u7740\u767d\u8272\u7684\u809a\u76ae\u98d8\u5728\u6c34\u9762\u4e0a\u3002\u4ece\u6668\u66e6\u7eb8\u4e1a\u5206\u6563\u5728\u6cb3\u8fb9\u7684\u6392\u6c61\u53e3\u4e0b\u6e38\uff0c\u9c7c\u867e\u80fd\u5b58\u6d3b\u8fd8\u771f\u5c31\u662f\u5947\u8ff9\u4e86\uff0c\u5927\u91cf\u542b\u70e7\u78b1\u7684\u5de5\u4e1a\u6c61\u6c34\u6d78\u8680\u5230\u4e24\u5cb8\u7684\u571f\u5730\u4e0e\u5730\u4e0b\u6c34\uff0c\u81f4\u4f7f\u4e24\u5cb8\u5f62\u6210\u5927\u7247\u7684\u76d0\u78b1\u5730\u3002</p><p>\u3000\u3000\u201c\u542f\u52a8\u8ddf\u6668\u66e6\u7eb8\u4e1a\u7684\u8c08\u5224\uff0c\u4f60\u4e0d\u4f1a\u5e0c\u671b\u8fd9\u91cc\u5f53\u6210\u4e3b\u8981\u8c08\u5224\u5730\u70b9\u5427\uff1f\u201d\u7fdf\u4e39\u9752\u7a81\u7136\u60f3\u5230\u4e00\u4e2a\u95ee\u9898\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u963b\u529b\u6709\u591a\u5927\uff0c\u4e0d\u7ba1\u6700\u540e\u7684\u6536\u8d2d\u80fd\u4e0d\u80fd\u6210\u529f\uff0c\u4e0e\u6c5f\u5357\u7701\u653f\u5e9c\u4ee5\u53ca\u6668\u66e6\u7eb8\u4e1a\u7684\u8c08\u5224\u8fd8\u662f\u4f1a\u542f\u52a8\uff0c\u4e0d\u7136\u5c31\u771f\u7684\u5c06\u6881\u4f1f\u6cd5\u8fd9\u4e2a\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u4ee3\u7701\u957f\u5f53\u6446\u9970\u4e86\u3002</p><p>\u3000\u3000\u201c\u6668\u66e6\u7eb8\u4e1a\u90a3\u4e48\u5927\u7684\u5382\u533a\uff0c\u7ba1\u7406\u697c\u5efa\u5728\u79bb\u6cb3\u5cb8\u6700\u8fdc\u7684\u5730\u65b9\uff1b\u7701\u91cc\u7684\u9886\u5bfc\uff0c\u591a\u534a\u6ca1\u6709\u4eb2\u81ea\u611f\u53d7\u5230\u6668\u66e6\u7eb8\u4e1a\u7684\u6c61\u67d3\u6709\u591a\u91cd\u3002\u53ea\u662f\u8ba9\u4ed6\u4eec\u547c\u5438\u4e00\u4e0b\u8fd9\u91cc\u7a7a\u6c14\uff0c\u8fd8\u6ca1\u6709\u8981\u4ed6\u4eec\u559d\u8fd9\u91cc\u6c34\u3001\u4ece\u8fd9\u5730\u91cc\u957f\u51fa\u6765\u7684\u7c73\uff0c\u96be\u9053\u4f1a\u5f88\u96be\u4e3a\u4ed6\u4eec\u5417\uff1f\u201d\u5f20\u606a\u4fa7\u7740\u5934\u95ee\u7fdf\u4e39\u9752\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u62bf\u7740\u5634\u5507\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e8e\u90a3\u4e9b\u5531\u9ad8\u8c03\u8bf4\u4e3a\u4e86\u7ecf\u6d4e\u53d1\u5c55\u5927\u5c40\u53ef\u4ee5\u727a\u7272\u5176\u4ed6\u4e00\u5207\u5229\u76ca\u7684\u5bb6\u4f19\u6765\u8bf4\uff0c\u5c06\u4ed6\u4eec\u4ece\u8fd9\u91cc\u4e22\u4e0b\u6765\uff0c\u4ed6\u4eec\u5927\u6982\u5c31\u4f1a\u6539\u53e3\u4e86\u3002\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u6211\u4eec\u6ca1\u6709\u5fc5\u8981\u7ad9\u5728\u8fd9\u91cc\u547c\u5438\u8fd9\u7a7a\u6c14\u3002\u201d</p><p>\u3000\u3000\u201c\u52a0\u5f3a\u4e00\u4e0b\u51b3\u5fc3\u4e5f\u597d\u3002\u201d\u8bb8\u9e3f\u4f2f\u5728\u66ae\u8272\u4e0b\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8c2d\u5929\u6ca1\u60f3\u5230\u8fd9\u51e0\u5e74\u6765\u7684\u56f0\u5883\u4f1a\u5728\u4eca\u5929\u9047\u5230\u8f6c\u673a\uff0c\u4ed6\u6ca1\u6709\u7b80\u5355\u5230\u5934\u8111\u53d1\u70ed\u5c31\u5931\u53bb\u601d\u8003\u7684\u80fd\u529b\u3002\u8fd9\u65f6\u5019\u6c81\u56ed\u6b63\u5728\u5b89\u6392\u665a\u4e0a\u7684\u9152\u5e2d\uff0c\u8c2d\u5929\u501f\u53e3\u53bb\u7763\u4fc3\u4e00\u4e0b\u6682\u65f6\u79bb\u5f00\u3002\u4ed6\u8d70\u4e0b\u6cb3\u5824\u65f6\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u66ae\u8272\u4e0b\u6cb3\u5824\u4e0a\u7684\u4f17\u4eba\u591a\u5c11\u6709\u4e9b\u7a81\u5140\u3002\u8c2d\u5929\u638f\u51fa\u624b\u673a\u7ed9\u7701\u653f\u5e9c\u91cc\u7684\u670b\u53cb\u6253\u7535\u8bdd\uff1a\u201c\u4e16\u7eaa\u9526\u6e56\u4e0e\u65b0\u5149\u7eb8\u4e1a\u662f\u4e0d\u662f\u6709\u4ec0\u4e48\u8054\u7cfb\uff1f\u201d</p><p>\u3000\u3000\u201c\u4eca\u5929\u8fd9\u91cc\u53ef\u771f\u662f\u70ed\u95f9\u4e86\uff0c\u5c0f\u9053\u6d88\u606f\u6f2b\u5929\u98de\uff0c\u4e16\u7eaa\u9526\u6e56\u8ddf\u65b0\u5149\u7eb8\u4e1a\u5177\u4f53\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u6211\u4e0d\u662f\u5927\u6e05\u695a\uff0c\u4f46\u6709\u5173\u7cfb\u662f\u80af\u5b9a\u7684\uff0c\u4f60\u83ab\u975e\u4ee5\u4e3a\u4f60\u90a3\u7834\u5e97\u6709\u4e86\u8f6c\u673a\uff1f\u7b49\u7740\u5427\uff0c\u70ed\u95f9\u5f52\u70ed\u95f9\uff0c\u4e8b\u60c5\u6ca1\u90a3\u4e48\u5bb9\u6613\uff0c\u51e0\u4e2a\u5927\u8001\u677f\u90fd\u6ca1\u6709\u8868\u6001\u5462\u3002\u4e0d\u8868\u6001\u5c31\u662f\u610f\u5473\u7740\u6c89\u9ed8\uff0c\u4f60\u53ef\u4e0d\u8981\u5c06\u8fd9\u6c89\u9ed8\u5f53\u6210\u9ed8\u8ba4\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u8c2d\u5929\u4e0d\u60f3\u542c\u670b\u53cb\u7f57\u55e6\uff0c\u80e1\u626f\u4e86\u51e0\u53e5\u5c31\u6302\u4e86\u7535\u8bdd\u3002\u4ed6\u867d\u7136\u8fd8\u4e0d\u77e5\u9053\u5f20\u606a\u5177\u4f53\u7684\u8eab\u4efd\uff0c\u8fd9\u65f6\u5019\u591a\u534a\u80fd\u731c\u5230\u4e9b\u4e1c\u897f\u3002\u72ec\u529b\u63a8\u52a8\u6536\u8d2d\u6848\u7684\u7701\u59d4\u526f\u4e66\u8bb0\u6881\u4f1f\u6cd5\u4e2d\u5348\u51fa\u73b0\u5728\u8fd9\u91cc\u8ddf\u8fd9\u4e9b\u4eba\u4f1a\u9762\uff0c\u73b0\u5728\uff0c\u76db\u946b\u96c6\u56e2\u7684\u5927\u8001\u677f\u53f6\u5efa\u658c\u4ee5\u8463\u4e8b\u7684\u8eab\u4efd\u4ee3\u8868\u4e16\u7eaa\u9526\u6e56\u53c8\u63d0\u51fa\u63a5\u624b\u6c81\u56ed\uff0c\u4e16\u7eaa\u9526\u6e56\u53c8\u4e0e\u6b64\u65f6\u6536\u8d2d\u6848\u7684\u65b0\u5149\u7eb8\u4e1a\u6709\u7740\u8bf4\u4e0d\u6e05\u9053\u4e0d\u660e\u7684\u5173\u8fde\uff0c\u8c2d\u5929\u5f53\u7136\u80fd\u731c\u5230\u4e9b\u4e1c\u897f\u3002</p><p>\u3000\u3000\u8c2d\u5929\u773c\u4e0b\u9762\u4e34\u591a\u79cd\u9009\u62e9\uff0c\u5c31\u662f\u54ac\u7740\u7259\u575a\u6301\u4e0b\u53bb\uff0c\u7b49\u5f85\u6700\u5927\u7684\u8f6c\u673a\u3002\u5f53\u7136\u6536\u8d2d\u6848\u9047\u5230\u7684\u963b\u529b\u4f1a\u96be\u4ee5\u60f3\u8c61\uff0c\u5c31\u7b97\u6668\u66e6\u7eb8\u4e1a\u7ed9\u65b0\u5149\u6536\u8d2d\uff0c\u65b0\u5149\u4e5f\u672a\u5fc5\u4f1a\u5f7b\u5e95\u89e3\u51b3\u996e\u9a6c\u6cb3\u7684\u6c61\u67d3\u95ee\u9898\u2014\u2014\u8fd9\u79cd\u7834\u4e8b\u5f88\u591a\uff0c\u8bf4\u4e0d\u5b9a\u4e3a\u4e86\u591a\u8d5a\u4e9b\u5229\u6da6\uff0c\u5bf9\u996e\u9a6c\u6cb3\u4f1a\u53d8\u672c\u52a0\u5389\u7684\u6392\u653e\u6c61\u6c34\uff0c\u90a3\u5c06\u6c81\u56ed\u4ee5\u5408\u9002\u7684\u4ef7\u4f4d\u5168\u76d8\u8131\u624b\u4fbf\u662f\u6700\u7a33\u59a5\u7684\u9009\u62e9\u3002\u4e09\u5e74\u524d\u5728\u6c81\u56ed\u6295\u4e0b\u81ea\u5df1\u4eba\u751f\u6700\u5927\u7684\u4e00\u7b14\u8d4c\u6ce8\uff0c\u8c2d\u5929\u5c31\u5c06\u6240\u6709\u5fc3\u8840\u90fd\u6295\u4e86\u8fdb\u6765\uff0c\u5168\u76d8\u8131\u624b\u591a\u5c11\u6709\u4e9b\u4e0d\u7518\u5fc3\u3002\u7279\u522b\u662f\u5f52\u8fd8\u94f6\u884c\u8d37\u6b3e\u4e4b\u540e\uff0c\u624b\u91cc\u4e0d\u4f1a\u5269\u4e0b\u591a\u5c11\u94b1\uff0c\u8fd8\u8981\u518d\u91cd\u65b0\u9009\u62e9\u4eba\u751f\u7684\u65b0\u8d77\u70b9\u3002</p><p>\u3000\u3000\u8c2d\u5929\u7565\u68c0\u67e5\u8fc7\u665a\u4e0a\u7684\u83dc\u80b4\uff0c\u5c31\u53c8\u8dd1\u56de\u6cb3\u5824\u901a\u77e5\u5f20\u606a\u3001\u53f6\u5efa\u658c\u4ed6\u4eec\u7528\u9910\uff0c\u8fdc\u8fdc\u542c\u89c1\u53f6\u5efa\u658c\u8ddf\u5f20\u606a\u3001\u8bb8\u9e3f\u4f2f\u5728\u8bf4\u201c\u56fd\u5185\u9ad8\u7aef\u519b\u4e8b\u9886\u57df\u5728\u5fae\u7535\u5b50\u8fd9\u4e00\u5757\u7684\u57fa\u7840\u5f88\u8584\u5f31\uff0c\u73b0\u5728\u5c31\u8fde\u51fa\u53e3\u5df4\u57fa\u65af\u5766\u7684\u5bfc\u5f39\u7cfb\u7edf\u5728\u51fa\u53e3\u524d\u90fd\u8981\u6574\u5957\u7684\u6362\u4e0a\u6b27\u7f8e\u7684\u7535\u5b50\u7cfb\u7edf\u2026\u2026\u201d\u4e4b\u7c7b\u7684\u8bdd\uff0c\u770b\u5230\u8c2d\u5929\u8d70\u8fd1\uff0c\u5c31\u6536\u4f4f\u53e3\u3002</p><p>\u3000\u3000\u8c2d\u5929\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u5728\u9ad8\u8c08\u9614\u8bba\u5f53\u524d\u7684\u519b\u4e8b\uff0c\u6ca1\u6709\u60f3\u5230\u522b\u5904\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u9152\u5e2d\u90fd\u51c6\u5907\u597d\u4e86\uff0c\u521a\u624d\u591a\u6709\u5510\u7a81\uff0c\u665a\u4e0a\u8fd9\u5e2d\u9152\u7b97\u662f\u7ed9\u6211\u8868\u8fbe\u6b49\u610f\u7684\u673a\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u8ddf\u7740\u4e0b\u4e86\u6cb3\u5824\u56de\u5230\u6c81\u56ed\u7684\u5c0f\u5bb4\u4f1a\u5385\u91cc\uff0c\u5728\u95e8\u7a97\u7d27\u95ed\u7684\u5ba4\u5185\uff0c\u547c\u5438\u4e0a\u611f\u89c9\u8981\u987a\u7545\u4e9b\u3002\u9664\u4e86\u8c2d\u5929\u4e4b\u5916\uff0c\u90fd\u662f\u5185\u90e8\u4eba\u5458\u2014\u2014\u6606\u817e\u5728\u7ebf\u65b0\u95fb\u90e8\u95e8\u7684\u8bb0\u8005\u4e5f\u4e0d\u80fd\u7b97\u5916\u4eba\uff0c\u9152\u5e2d\u6ca1\u6709\u5e38\u89c1\u7684\u70ed\u95f9\u55a7\u56a3\u3002</p><p>\u3000\u3000\u9152\u5e2d\u540e\uff0c\u53f6\u5efa\u658c\u627e\u5f20\u606a\u3001\u8bb8\u9e3f\u4f2f\u8c08\u521a\u624d\u5728\u6cb3\u5824\u4e0a\u6ca1\u6709\u8c08\u5b8c\u7684\u8bdd\u9898\uff1a\u201c\u56fd\u5185\u53c8\u6709\u54ea\u5bb6\u50cf\u9526\u6e56\u8fd9\u4e48\u5927\u7684\u624b\u7b14\u5728\u5fae\u7535\u5b50\u57fa\u7840\u7814\u7a76\u9886\u57df\u8fdb\u884c\u5982\u6b64\u89c4\u6a21\u7684\u6295\u8d44\uff1f\u7279\u522b\u662f\u4ece\u56fd\u5916\u5438\u5f15\u5927\u6279\u91cf\u7684\u9ad8\u7ea7\u4eba\u624d\u56de\u6765\uff0c\u5373\u4f7f\u56fd\u5bb6\u6709\u5fc3\u5f15\u5bfc\u6d77\u5916\u7559\u5b66\u4eba\u58eb\u8fd4\u56de\u56fd\u5185\uff0c\u4f46\u662f\u4f53\u7cfb\u5185\u73b0\u6709\u7684\u4eba\u624d\u5236\u5ea6\u9650\u5236\u4e86\u5f15\u8fdb\u7684\u89c4\u6a21\u4e0e\u5f15\u8fdb\u4eba\u624d\u7684\u6574\u6b21\u5c42\u6b21\uff0c\u6211\u770b\u603b\u88c5\u5907\u90e8\u7684\u5d14\u6587\u6bc5\u4e2d\u5c06\u672a\u5fc5\u5bf9\u9526\u6e56\u4e00\u70b9\u90fd\u4e0d\u5173\u5fc3\u3002\u8981\u662f\u4e0d\u4fe1\uff0c\u6211\u4eec\u73b0\u5728\u5c31\u6253\u7535\u8bdd\u7ed9\u5c0f\u6850\u4ed6\u7238\uff0c\u770b\u519b\u65b9\u662f\u4e0d\u662f\u5bf9\u9526\u6e56\u6709\u7279\u522b\u7684\u5173\u6ce8\uff1f\u4e4b\u6240\u4ee5\u6ca1\u6709\u660e\u786e\u7684\u8868\u6001\uff0c\u6211\u770b\u5404\u65b9\u9762\u8fd8\u662f\u5728\u7b49\u4e2d\u6676\u5fae\u82af\u7684\u9879\u76ee\u5efa\u6210\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u662f\u798f\u662f\u7978\uff1f\u201d\u5f20\u606a\u95ee\u8bb8\u9e3f\u4f2f\u3002</p><p>\u3000\u3000\u201c\u4efb\u4f55\u4e8b\u60c5\u90fd\u6709\u4e24\u9762\u6027\uff0c\u5229\u5f0a\u6743\u8861\u800c\u5df2\uff0c\u201d\u8bb8\u9e3f\u4f2f\u8bf4\u9053\uff0c\u201c\u4eba\u5bb6\u4e0d\u6025\u7740\u611f\u6069\u6234\u5fb7\u4e5f\u662f\u6709\u8003\u8651\u7684\uff0c\u4e5f\u6015\u6211\u4eec\u8fd9\u8fb9\u722c\u7aff\u5b50\u4e0a\u554a\u3002\u6d41\u6c34\u672a\u5fc5\u6709\u60c5\uff0c\u843d\u82b1\u5374\u662f\u6709\u610f\uff0c\u5bf9\u4ed6\u4eec\u6765\u8bf4\uff0c\u5f15\u8fdb\u6c11\u8425\u4f01\u4e1a\u53c2\u4e0e\u5230\u519b\u4e8b\u7814\u7a76\u9886\u57df\uff0c\u7a81\u7834\u7684\u6b65\u4f10\u53ef\u4e0d\u6bd4\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u6765\u5f97\u5c0f\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0a\u5348\u5df2\u7ecf\u5728\u533b\u9662\u81ea\u62a5\u5bb6\u95e8\u4e86\uff0c\u96be\u4e0d\u6210\u5d14\u6587\u6bc5\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u8ddf\u5728\u91d1\u5c71\u7684\u5bb6\u4eba\u8054\u7cfb\u8fc7\uff1f\u518d\u8bf4\u65b0\u534e\u793e\u526f\u793e\u957f\u8521\u4e91\u98de\u4e5f\u4e0d\u5e94\u8be5\u662f\u4e2a\u8fdf\u949d\u7684\u4e3b\uff0c\u4ed6\u4eec\u624d\u4e0d\u4fe1\u65b0\u534e\u793e\u526f\u793e\u957f\u7ea7\u522b\u7684\u4eba\u7269\u6ca1\u6709\u8d44\u683c\u63a5\u89e6\u5230\u9526\u6e56\u7684\u6838\u5fc3\u8d44\u6599\u3002</p><p>\u3000\u3000\u4efb\u4f55\u4e8b\u60c5\u5728\u6c34\u843d\u77f3\u51fa\u4e4b\u524d\u90fd\u4f1a\u663e\u5f97\u6251\u6714\u8ff7\u79bb\uff0c\u5f20\u606a\u5fc3\u91cc\u4e5f\u6ca1\u6709\u4ec0\u4e48\u6025\u8e81\uff0c\u996d\u8981\u4e00\u53e3\u4e00\u53e3\u7684\u5403\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u7b80\u5fd7\u5eb7\u62bd\u4e86\u7a7a\u9699\u6253\u7535\u8bdd\u8fc7\u6765\u6c47\u62a5\u8521\u4e91\u98de\u627e\u4ed6\u8fc7\u53bb\u7684\u7f18\u7531\uff0c\u4e0d\u4ec5\u8521\u4e91\u98de\u4ee5\u65b0\u534e\u793e\u6c5f\u5357\u5206\u793e\u7684\u8d1f\u8d23\u4eba\u5728\u573a\uff0c\u8fd8\u5c06\u6881\u4f1f\u6cd5\u4ee5\u53ca\u7701\u7eaa\u59d4\u3001\u7701\u516c\u5b89\u5385\u7684\u8d1f\u8d23\u4eba\u90fd\u8bf7\u5230\u573a\u3002\u8521\u4e91\u98de\u662f\u4e0b\u51b3\u5fc3\u8981\u5c06\u7ae0\u5dde\u7684\u76d6\u5b50\u63ed\u5f00\u6765\uff0c\u624d\u5c06\u6881\u4f1f\u6cd5\u53ca\u6c5f\u5357\u7701\u76f8\u5173\u90e8\u95e8\u8d1f\u8d23\u4eba\u8bf7\u8fc7\u53bb\u4e00\u8d77\u542c\u7b80\u5fd7\u5eb7\u3001\u5d14\u90c1\u6052\u4ed6\u4eec\u6c47\u62a5\u7ae0\u5dde\u79c1\u77ff\u73b0\u72b6\u7684\u7b2c\u4e00\u624b\u6750\u6599\uff0c\u800c\u4e14\u660e\u786e\u8868\u793a\u4f1a\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u4ee5\u9ad8\u7ea7\u522b\u7684\u5185\u53c2\u5f62\u5f0f\u8f6c\u4ea4\u5230\u6709\u5173\u4e2d\u592e\u9886\u5bfc\u7684\u624b\u91cc\u3002</p><p>\u3000\u3000\u8521\u4e91\u98de\u5982\u6b64\u5f3a\u786c\u7684\u59ff\u6001\u8868\u660e\u4ed6\u65e0\u6cd5\u5bb9\u5fcd\u5730\u65b9\u4e0a\u548c\u7a00\u6ce5\u7684\u62ff\u4e9b\u5c0f\u9c7c\u5c0f\u867e\u5f53\u66ff\u7f6a\u7f8a\u6577\u884d\u4e86\u4e8b\uff0c\u6881\u4f1f\u6cd5\u501f\u673a\u8981\u6c42\u7701\u516c\u5b89\u5385\u8fde\u591c\u52a0\u5f3a\u5bf9\u4e09\u540d\u6d89\u5acc\u975e\u6cd5\u62d8\u7981\u7684\u5acc\u7591\u72af\u7684\u5ba1\u8baf\u3002\u5c0f\u9c7c\u5c0f\u867e\u5f80\u5f80\u6700\u4f73\u7684\u7a81\u7834\u53e3\uff0c\u53ea\u8981\u5c06\u706b\u70e7\u5230\u88ab\u5efa\u90ba\u8b66\u65b9\u5173\u62bc\u7684\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u5144\u5f1f\u4e24\u4eba\u8eab\u4e0a\uff0c\u5c31\u53ef\u4ee5\u8fc5\u901f\u7684\u5c06\u6b64\u6848\u6df1\u6316\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u201c\u53ef\u80fd\u5d14\u6587\u6bc5\u5c06\u519b\u4e5f\u6709\u5728\u5173\u6ce8\u56fd\u5185\u7a00\u571f\u8d44\u6e90\u7684\u73b0\u72b6\u3002\u201d\u5f20\u606a\u4e0d\u6e05\u695a\u5d14\u6587\u6bc5\u662f\u4ece\u4f55\u65f6\u5f00\u59cb\u5173\u6ce8\u56fd\u5185\u7a00\u571f\u8d44\u6e90\u73b0\u72b6\uff0c\u4ece\u4eca\u5929\u7684\u60c5\u51b5\u6765\u770b\uff0c\u53ef\u80fd\u4ec5\u4ec5\u662f\u5d14\u6587\u6bc5\u4e0d\u65b9\u4fbf\u76f4\u63a5\u7ad9\u51fa\u6765\u8868\u6001\uff0c\u8521\u4e91\u98de\u7684\u5f3a\u786c\u59ff\u6001\u80cc\u540e\u5fc5\u6709\u6743\u8861\uff0c\u4e0d\u4f1a\u4ec5\u4ec5\u662f\u60f3\u7ed9\u5d14\u90c1\u6052\u5728\u5185\u7684\u4e09\u540d\u65b0\u534e\u793e\u8bb0\u8005\u8ba8\u516c\u9053\u3002</p></article>"}}}]);
