(window.webpackJsonp=window.webpackJsonp||[]).push([[829],{852:function(p){p.exports={chapterTitle:null,title:"\u7b2c657\u7ae0 \u5929\u65f6\u5730\u5229",content:"<article><h2>\u7b2c657\u7ae0 \u5929\u65f6\u5730\u5229</h2><p>\u3000\u300010\u670815\u65e5\u4e0a\u5348\uff0c\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u3001\u7701\u957f\u674e\u8fdc\u6e56\u3001\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u3001\u5efa\u90ba\u5e02\u59d4\u5e38\u59d4\u3001\u526f\u5e02\u957f\u738b\u7ef4\u5747\u3001\u9ad8\u65b0\u533a\u6295\u8d44\u4fc3\u8fdb\u5c40\u5c40\u957f\u59da\u6587\u76db\u7b49\u4eba\u4e0e\u56fd\u5bb6\u8ba1\u59d4\u526f\u4e3b\u4efb\u845b\u5efa\u5fb7\u3001\u90ae\u7535\u90e8\u526f\u90e8\u957f\u6613\u4e91\u98de\u7b49\u4eba\u966a\u540c\u67f3\u5fd7\u6210\u3001\u90ed\u677e\u5ef6\u7b49\u4eba\u4e58\u8f66\u5230\u6c5f\u5317\u65b0\u6d66\u53c2\u89c2\u4e86\u9ad8\u65b0\u533a\u3002</p><p>\u3000\u3000\u5efa\u90ba\u9ad8\u65b0\u533a\u8bbe\u7acb\u8fd9\u4e9b\u5e74\u6765\uff0c\u53d1\u5c55\u6709\u4e9b\u6ede\u540e\uff0c\u7f57\u541b\u5c31\u62ff\u8fd9\u4e00\u70b9\u5c06\u5e02\u957f\u8096\u660e\u5efa\u538b\u5f97\u62ac\u4e0d\u8d77\u5934\u6765\uff0c\u6240\u4ee5\u8096\u660e\u5efa\u501f\u6545\u6ca1\u6709\u5217\u5e2d\u4eca\u5929\u7684\u6d3b\u52a8\u3002\u8fd9\u4e2a\u5df2\u7ecf\u4e0d\u518d\u91cd\u8981\uff0c\u4eca\u5929\u7f57\u541b\u8bf7\u5927\u5bb6\u8fc7\u6765\uff0c\u662f\u770b\u4e00\u4e0b\u5e02\u91cc\u89c4\u5212\u7ed9\u6676\u5706\u5382\u9879\u76ee\u7684\u5efa\u8bbe\u7528\u5730\u3002</p><p>\u3000\u3000\u4eca\u5929\u624d\u662f\u67f3\u5fd7\u6210\u4ed6\u4eec\u62b5\u8fbe\u5efa\u90ba\u7684\u7b2c\u4e8c\u5929\uff0c\u79bb\u67f3\u5fd7\u6210\u5728\u65b0\u52a0\u5761\u6b63\u5f0f\u5ba3\u5e03\u63a5\u53d7\u5efa\u5382\u59d4\u6258\u4e5f\u4e0d\u8fc7\u624d\u51e0\u5929\u7684\u5de5\u592b\uff0c\u5efa\u90ba\u5e02\u91cc\u5c31\u5c06\u9879\u76ee\u7528\u5730\u7ed9\u5708\u4e86\u51fa\u6765\uff0c\u4ece\u8fd9\u91cc\u5c31\u53ef\u4ee5\u77e5\u9053\u5efa\u90ba\u5e02\u662f\u591a\u4e48\u6e34\u671b\u5f15\u8fdb\u8fd9\u4e2a\u6676\u5706\u5382\u9879\u76ee\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e03\u5341\u5e74\u4ee3\u4ee5\u6765\uff0c\u56fd\u5185\u6709\u8fc7\u4e09\u6b21\u5927\u89c4\u6a21\u7684\u5f15\u8fdb\u6676\u5706\u5236\u9020\u9879\u76ee\uff0c\u4e5f\u5f15\u8fdb\u4e86\u51e0\u6761\u751f\u4ea7\u7ebf\uff0c\u4f46\u662f\u6ca1\u80fd\u7ed9\u56fd\u5185\u7535\u5b50\u4fe1\u606f\u6280\u672f\u4ea7\u4e1a\u7684\u53d1\u5c55\u63d0\u51fa\u4e00\u4e2a\u57fa\u77f3\u6027\u7684\u5e73\u53f0\u3002</p><p>\u3000\u3000\u6bcf\u4e00\u6b21\u7684\u5f15\u8fdb\uff0c\u603b\u8981\u5f97\u5230\u4e00\u4e9b\u6559\u8bad\u3002\u6bd4\u5982\u8bf4\uff0c\u53ea\u5f15\u8fdb\u8bbe\u5907\u6ca1\u6709\u5f15\u8fdb\u76f8\u5e94\u7684\u5236\u9020\u5de5\u827a\uff0c\u6bd4\u5982\u8bf4\uff0c\u5438\u53d6\u524d\u6b21\u6559\u8bad\u5f15\u8fdb\u8bbe\u5907\u4e0e\u5236\u9020\u5de5\u827a\uff0c\u5374\u6ca1\u6709\u5b8c\u6574\u7684\u6280\u672f\u7ba1\u7406\u56e2\u961f\uff0c\u6bd4\u5982\u8bf4\uff0c\u5efa\u8bbe\u671f\u62d6\u5ef6\u4e86\u4e03\u5e74\u7b49\u5de5\u5382\u5efa\u8bbe\u6210\u5f15\u8fdb\u7684\u6240\u8c13\u5148\u8fdb\u5de5\u827a\u5374\u5df2\u7ecf\u7ed9\u6dd8\u6c70\u4e86\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u6709\u591a\u5927\u7684\u56f0\u96be\uff0c\u5c31\u7b97\u6709\u524d\u4e09\u6b21\u7684\u7ecf\u9a8c\u6559\u8bad\uff0c\u6676\u5706\u5382\u9879\u76ee\u8fd8\u662f\u8981\u4e0a\uff0c\u8d75\u6d4e\u4e1c\u526f\u603b\u7406\u4e5f\u591a\u6b21\u8868\u793a\u5c31\u7b97\u7838\u9505\u5356\u94c1\u4e5f\u8981\u518d\u4e0a\u6676\u5706\u5382\u9879\u76ee\u3002</p><p>\u3000\u3000\u82f1\u7279\u5c14\u57285x486\u65f6\u4ee3\u5c31\u91c7\u75280.35\u5fae\u7c73\u5de5\u827a\u751f\u4ea7\u8ba1\u7b97\u673a\u5fae\u5904\u7406\u5668\u82af\u7247\uff0c\u8fc4\u4eca\u5df2\u6709\u56db\u4e94\u5e74\u7684\u65f6\u95f4\u8fc7\u53bb\uff0c\u867d\u7136\u591a\u5bb6\u4e3b\u6d41\u6676\u5706\u5382\u5546\u90fd\u8981\u7740\u624b\u5efa\u8bbe\u76840.25\u5fae\u7c73\u7684\u65b0\u5382\uff0c\u4f46\u662f0.35\u5fae\u7c73\u7684\u5236\u9020\u5de5\u827a\u4ecd\u662f\u5f53\u524d\u7684\u4e3b\u6d41\u6280\u672f\u3002</p><p>\u3000\u3000\u73b0\u5b9e\u7684\u56f0\u96be\uff0c\u5236\u9020\u5de5\u827a\u3001\u5efa\u8bbe\u8d44\u91d1\u3001\u6280\u672f\u56e2\u961f\u8fd9\u4e09\u5ea7\u5927\u5c71\u53ef\u4ee5\u8bf4\u90fd\u5df2\u7ecf\u7ffb\u8d8a\u8fc7\u53bb\u4e86\uff0c\u5269\u4e0b\u7684\u5c31\u662f\u5236\u9020\u8bbe\u5907\u7684\u91c7\u8d2d\u3002</p><p>\u3000\u3000\u53d7\u5230\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u6d89\u53ca\uff0c\u7535\u5b50\u4ea7\u4e1a\u906d\u53d7\u91cd\u632b\uff0c\u4e5f\u4f7f\u96c6\u6210\u7535\u8def\u5236\u9020\u8bbe\u5907\u4f9b\u5e94\u5546\u5927\u53d7\u5f71\u54cd\uff0c\u8feb\u5207\u9700\u8981\u5f00\u53d1\u65b0\u7684\u7528\u6237\uff1b\u8fd9\u6070\u6070\u662f\u56fd\u5185\u5efa\u8bbe\u6676\u5706\u5382\u7a81\u7834\u6b27\u7f8e\u53d1\u8fbe\u56fd\u5bb6\u8bbe\u5907\u5c01\u9501\u7684\u6700\u4f73\u65f6\u673a\u3002\u52a0\u4e0a\u6709\u4e00\u4e9b\u96c6\u6210\u7535\u8def\u5236\u9020\u5546\u53d7\u5230\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u5f71\u54cd\u8ba1\u5212\u7f29\u51cf\u4ea7\u80fd\uff0c\u90a3\u4e9b\u7740\u624b\u5efa\u8bbe0.25\u5fae\u7c73\u65b0\u5382\u7684\u96c6\u6210\u7535\u8def\u5236\u9020\u5546\u6709\u5c06\u90e8\u5206\u8bbe\u5907\u6dd8\u6c70\u6389\u7684\u6b32\u671b\u3002\u52a0\u4e0a\u56fd\u5185\u4e4b\u524d\u5c31\u6709\u4e09\u6b21\u6210\u529f\u5f15\u8fdb\u6676\u5706\u5236\u9020\u8bbe\u5907\u7684\u7ecf\u9a8c\uff0c\u65b0\u52a0\u5761\u56fd\u5bb6\u6295\u8d44\u7ba1\u7406\u5c40\u4f5c\u4e3a\u8be5\u9879\u76ee\u7684\u7b2c\u4e8c\u5927\u51fa\u8d44\u4eba\uff0c\u4ee5\u53ca\u4e2d\u592e\u653f\u5e9c\u591a\u6b21\u516c\u5f00\u8868\u793a\u4eba\u6c11\u5e01\u575a\u6301\u4e0d\u8d2c\u503c\uff0c\u8fd9\u5c06\u5bf9\u4e9a\u6d32\u7ecf\u6d4e\u6062\u590d\u7a33\u5b9a\u53d1\u6325\u51fa\u5de8\u5927\u4f5c\u7528\u3002\u5728\u8fd9\u4e9b\u79cd\u79cd\u6709\u5229\u7684\u56e0\u7d20\u4e0b\uff0c\u5f15\u8fdb\u6676\u5706\u5236\u9020\u8bbe\u5907\u5012\u4e0d\u518d\u662f\u96be\u4ee5\u8de8\u8d8a\u7684\u969c\u788d\u3002</p><p>\u3000\u3000\u5728\u8fd9\u79cd\u80cc\u666f\u4e0b\uff0c\u53c8\u62e5\u6709\u5728\u56fd\u5185\u5efa\u8bbe\u6676\u5706\u5382\u9879\u76ee\u5929\u65f6\u5730\u5229\u7684\u826f\u673a\uff0c\u5efa\u90ba\u65e0\u8bba\u591a\u4e48\u8feb\u5207\u7684\u8868\u793a\u51fa\u5bf9\u6676\u5706\u5382\u9879\u76ee\u7684\u6e34\u671b\u90fd\u4e0d\u8fc7\u5206\u3002</p><p>\u3000\u3000\u65e9\u572810\u67088\u65e5\uff0c\u5728\u4e00\u5207\u90fd\u624d\u521a\u521a\u9732\u51fa\u7709\u76ee\u4e4b\u65f6\uff0c\u7f57\u541b\u5c31\u53ec\u5f00\u5e02\u59d4\u5e38\u59d4\u4f1a\u8bae\u8feb\u4e0d\u53ca\u5f85\u7684\u5c06\u9879\u76ee\u5efa\u8bbe\u7528\u5730\u7ed9\u5708\u51fa\u6765\uff0c\u6210\u7acb\u4e86\u4ee5\u7f57\u541b\u4eb2\u81ea\u6302\u5e05\u7684\u6676\u5706\u5382\u5efa\u8bbe\u5de5\u4f5c\u9886\u5bfc\u5c0f\u7ec4\u6765\u63a8\u8fdb\u76f8\u5173\u7b79\u5907\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u56fd\u52a1\u9662\u5bf9\u6b64\u66f4\u4e3a\u8feb\u5207\u3002\u5728\u5b59\u5c1a\u4e49\u3001\u90ed\u677e\u5ef6\u3001\u67f3\u5fd7\u6210\u4e00\u884c\u4eba\u8fd8\u5728\u65b0\u52a0\u5761\u4e0e\u65b0\u52a0\u5761\u5f53\u5c40\u6d3d\u8c08\u65b0\u52a0\u5761\u5f53\u5c40\u5165\u8d44\u6676\u5706\u5382\u9879\u76ee\u7684\u540c\u65f6\uff0c\u7f57\u541b\u5e26\u7740\u6750\u6599\u8fdb\u4eac\u6c47\u62a5\u5de5\u4f5c\uff0c\u5df2\u7ecf\u5168\u9762\u4e3b\u6301\u56fd\u52a1\u9662\u7ecf\u6d4e\u5de5\u4f5c\u7684\u8d75\u6d4e\u4e1c\u526f\u603b\u7406\u5c31\u5c06\u8ba9\u56fd\u5bb6\u8ba1\u59d4\u526f\u4e3b\u4efb\u845b\u5efa\u5fb7\u3001\u90ae\u7535\u90e8\u526f\u90e8\u957f\u6613\u4e91\u98de\u4ee3\u66ff\u4ed6\u5148\u5230\u5efa\u90ba\u6765\u8003\u5bdf\u9879\u76ee\u7684\u8fdb\u5c55\u4ee5\u53ca\u5efa\u90ba\u5e02\u7533\u8bf7\u5b9e\u65bd\u7684\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\u60c5\u51b5\u3002</p><p>\u3000\u3000\u8d75\u6d4e\u4e1c\u526f\u603b\u7406\u6307\u793a\u4e00\u5b9a\u8981\u6293\u4f4f\u5f53\u524d\u7684\u6709\u5229\u65f6\u673a\uff0c\u8981\u5c11\u8d70\u8859\u5185\u8def\u7ebf\uff0c\u4e00\u5207\u90fd\u5f00\u7eff\u706f\uff0c\u514d\u5f97\u5ef6\u8bef\u5f53\u524d\u5efa\u8bbe\u6676\u5706\u5382\u9879\u76ee\u7684\u6700\u6709\u5229\u65f6\u673a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u90a3\u4e9b\u8dfa\u4e00\u8dfa\u5c31\u80fd\u8ba9\u4e1c\u6d77\u7701\u5927\u5730\u6296\u4e0a\u4e09\u6296\u7684\u5927\u4eba\u7269\u90fd\u5728\u6c5f\u5317\u65b0\u6d66\u9ad8\u65b0\u533a\u89c6\u5bdf\uff0c\u5f20\u606a\u5219\u5c06\u8fd9\u4e9b\u4e8b\u60c5\u653e\u4e0b\u53c8\u5f00\u59cb\u4ed6\u60a0\u95f2\u81ea\u5f97\u7684\u5927\u5b66\u6821\u56ed\u751f\u6d3b\u3002</p><p>\u3000\u3000\u519b\u8bad\u7ed3\u675f\uff0c\u4e5d\u4e03\u7ea7\u7684\u65b0\u751f\u4e5f\u6b63\u5f0f\u5f00\u8bfe\uff0c\u5f20\u606a\u4e5f\u53ea\u662f\u6709\u9009\u62e9\u7684\u5076\u5c14\u53bb\u542c\u4e9b\u8bfe\u7a0b\uff0c\u5927\u90e8\u5206\u65f6\u95f4\u90fd\u7a9d\u5728\u56fe\u4e66\u9986\u91cc\u770b\u4e66\u3002</p><p>\u3000\u3000\u5728\u5341\u6708\u4e2d\u65ec\u5165\u79cb\u540e\u7684\u4e0a\u5348\uff0c\u548c\u7199\u7684\u9633\u5149\u900f\u8fc7\u5357\u7a97\u843d\u5728\u501f\u9605\u5ba4\u7684\u957f\u4e66\u684c\u4e0a\uff0c\u770b\u7740\u4e66\uff0c\u8003\u8651\u9526\u6e56\u672a\u6765\u53d1\u5c55\u7684\u4e00\u4e9b\u95ee\u9898\uff0c\u601d\u7eea\u98d8\u5ffd\u5982\u79cb\u540e\u98ce\u4e2d\u7684\u843d\u53f6\uff0c\u4e0d\u9700\u8981\u5728\u9676\u664b\u3001\u674e\u8fdc\u6e56\u3001\u845b\u5efa\u5fb7\u8fd9\u4e9b\u5927\u4eba\u7269\u9762\u524d\u8868\u6f14\uff0c\u5f53\u7136\u8212\u9002\u81ea\u5728\u3002</p><p>\u3000\u3000\u7136\u800c\u751f\u6d3b\u603b\u975e\u4e8b\u4e8b\u90fd\u5982\u4ed6\u613f\u7684\uff0c\u770b\u5230\u59da\u6587\u76db\u7684\u53f7\u7801\u663e\u793a\u5728\u624b\u673a\u4e0a\uff0c\u5f20\u606a\u6709\u7740\u5c06\u624b\u673a\u4e22\u6389\u7684\u5ff5\u5934\uff0c\u59da\u6587\u76db\u8fd9\u65f6\u5019\u6b63\u966a\u7740\u90a3\u4e9b\u5927\u4eba\u7269\u5728\u6c5f\u5317\u53c2\u52a0\u5462\uff0c\u4ed6\u6253\u7535\u8bdd\u8fc7\u6765\u51c6\u6ca1\u6709\u4ec0\u4e48\u597d\u4e8b\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e07\u822c\u65e0\u5948\u7684\u8d70\u5230\u9605\u89c8\u5ba4\u5916\u7684\u8fc7\u9053\u91cc\u63a5\u59da\u6587\u76db\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u6613\u90e8\u957f\u8bf4\u8981\u89c1\u89c1\u4f60\uff0c\u4f60\u80fd\u4e0d\u80fd\u62bd\u51fa\u65f6\u95f4\u6765\uff1f\u201d</p><p>\u3000\u3000\u6613\u90e8\u957f\uff1f\u90ae\u7535\u90e8\u526f\u90e8\u957f\u6613\u4e91\u98de\uff0c\u4e1c\u822a\u7a7a\u59d0\u6613\u99a8\u7684\u7236\u4eb2\uff1f\u59da\u6587\u76db\u88ab\u8feb\u63a5\u53d7\u4e0e\u6613\u99a8\u76f8\u4eb2\u7684\u547d\u8fd0\uff0c\u8fd9\u4e5f\u662f\u4ed6\u80fd\u591f\u5230\u5efa\u90ba\u4efb\u804c\u7684\u6761\u4ef6\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u77a7\u4f60\u4e00\u672c\u6b63\u7ecf\u7684\u79f0\u547c\u672a\u6765\u7684\u5cb3\u7236\uff0c\u6211\u90fd\u5dee\u70b9\u6ca1\u6709\u53d1\u5e94\u8fc7\u6765\u2026\u2026\u201d\u542c\u7740\u59da\u6587\u76db\u5728\u7535\u8bdd\u90a3\u5934\u731b\u70c8\u7684\u54b3\u55fd\u4e86\u4e24\u58f0\uff0c\u5fc3\u60f3\u8fd9\u5c0f\u5b50\u4e0d\u4f1a\u5c31\u5728\u6613\u4e91\u98de\u9762\u524d\u901a\u7535\u8bdd\u5427\uff1f\u9526\u6e56\u80fd\u987a\u5229\u7684\u4ece\u90ae\u7535\u90e8\u62ff\u5230\u624b\u673a\u751f\u4ea7\u724c\u7167\uff0c\u6613\u4e91\u98de\u662f\u53d1\u6325\u4e86\u4f5c\u7528\u7684\uff0c\u65e2\u7136\u6613\u4e91\u98de\u63d0\u51fa\u8981\u89c1\u81ea\u5df1\uff0c\u5f53\u7136\u4e0d\u80fd\u62d2\u7edd\uff0c\u8fd9\u65f6\u5019\u8fd8\u4e0d\u662f\u5c06\u81ea\u5df1\u5f53\u6839\u8471\u7684\u65f6\u5019\uff0c\u8bf4\u9053\uff0c\u201c\u884c\u554a\uff0c\u65f6\u95f4\u5730\u70b9\u4f60\u6765\u5b89\u6392\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u521a\u521a\u5728\u5c0f\u6c5f\u4e8c\u6865\u7684\u5357\u5cb8\u5de5\u5730\u4e0a\u53c2\u89c2\u8fc7\uff0c\u6b63\u5728\u8f66\u91cc\u4f11\u606f\uff0c\u4e0b\u4e00\u7ad9\u5c31\u53bb\u5b66\u5e9c\u5df7\uff0c\u4e0a\u5348\u7684\u65e5\u7a0b\u5c31\u5b89\u6392\u5230\u5b66\u5e9c\u5df7\uff0c\u4f60\u8981\u4e0d\u5c31\u5230\u5b66\u5e9c\u5df7\u5427\uff1f\u6613\u90e8\u957f\u4e5f\u53ea\u6709\u4e2d\u5348\u80fd\u62bd\u51fa\u65f6\u95f4\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u5c31\u5728\u5bbf\u820d\u50cf\u65b0\u5ab3\u5987\u4e00\u6837\u7b49\u7740\uff0c\u5565\u65f6\u5019\u80fd\u62bd\u51fa\u65f6\u95f4\u6765\u5565\u65f6\u5019\u901a\u77e5\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c31\u5728\u5b66\u5e9c\u5df7\u5427\uff0c\u5927\u5bb6\u770b\u5230\u4e5f\u90fd\u4f1a\u89c6\u800c\u4e0d\u89c1\u7684\uff0c\u90a3\u4e9b\u4e2d\u8001\u5e74\u4eba\u8fd8\u6709\u8138\u8010\u7740\u6027\u5b50\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e0b\u8ddf\u4f60\u8fd9\u4eba\u6bdb\u5934\u5c0f\u4f19\u5b50\u4eb2\u5207\u7684\u5618\u5bd2\u95ee\u6696\uff1f\u201d</p><p>\u3000\u3000\u542c\u7740\u59da\u6587\u76db\u53c8\u6cb9\u8154\u6ed1\u8c03\u8d77\u6765\uff0c\u770b\u6765\u6613\u4e91\u98de\u4ece\u4ed6\u8eab\u8fb9\u8d70\u5f00\u4e86\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u8981\u4e0d\u8fd9\u6837\uff1f\u4f60\u5e2e\u6211\u8ddf\u9676\u664b\u4e66\u8bb0\u3001\u674e\u7701\u957f\u3001\u7f57\u541b\u4e66\u8bb0\u6253\u58f0\u62db\u547c\uff0c\u6211\u6df7\u5230\u4f60\u4eec\u53c2\u89c2\u961f\u4f0d\u91cc\u5c31\u884c\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u884c\uff0c\u6211\u6765\u53bb\u8bf4\uff0c\u201d\u59da\u6587\u76db\u53c8\u5f00\u73a9\u7b11\u8bf4\uff0c\u201c\u56fd\u5185\u7684\u5f53\u524d\u8206\u8bba\u73af\u5883\u4e5f\u4e0d\u5bb9\u8bb8\u4f60\u5c0f\u5b50\u592a\u51fa\u98ce\u5934\uff0c\u5976\u5976\u7684\uff0c\u6211\u4e5f\u4e0d\u723d\u98ce\u5934\u90fd\u8ba9\u4f60\u5c0f\u5b50\u4e00\u4eba\u62a2\u5149\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\u2026\u2026\u201d\u5f20\u606a\u8f7b\u7b11\u8d77\u6765\uff0c\u6302\u4e86\u7535\u8bdd\u3002</p><p>\u3000\u3000\u51fa\u4e86\u9605\u89c8\u5ba4\uff0c\u4fe1\u6b65\u8d70\u5230\u5b66\u5e9c\u5df7\uff0c\u770b\u5230\u5c0f\u53d4\u5f20\u77e5\u975e\u3001\u82cf\u6d25\u4e1c\u3001\u90b5\u81f3\u521a\u7b49\u4eba\u90fd\u8863\u51a0\u695a\u695a\u7684\u7ad9\u5728\u90a3\u91cc\u7b49\u5019\u53c2\u89c2\u8f66\u961f\u7684\u5230\u6765\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u90fd\u8ddf\u5c0f\u5ab3\u5987\u4f3c\u7684\u7ad9\u5728\u8fd9\u91cc\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u662f\u8bf4\u4e0d\u4f1a\u51fa\u5e2d\u5417\uff1f\u201d\u5f20\u77e5\u975e\u5947\u602a\u7684\u95ee\uff0c\u6653\u5f97\u8fd9\u4e48\u5927\u573a\u9762\uff0c\u4ed6\u8fd8\u7279\u610f\u4ece\u6d77\u5dde\u8d76\u8fc7\u6765\u51d1\u70ed\u95f9\uff0c\u4ed6\u8fd8\u8ddf\u90b5\u81f3\u521a\u8bf4\uff0c\u9526\u6e56\u641e\u51fa\u8fd9\u4e48\u5927\u7684\u52a8\u4f5c\uff0c\u7b49\u9879\u76ee\u771f\u6b63\u843d\u5b9e\u4e86\uff0c\u5c31\u7b97\u526f\u603b\u7406\u3001\u56fd\u52a1\u59d4\u5458\u7ea7\u522b\u7684\u4eba\u7269\u4e0b\u6765\u89c6\u5bdf\u4e5f\u4e0d\u8fc7\u4efd\uff0c\u5230\u65f6\u4ed6\u8fd8\u8981\u8fc7\u6765\u4e89\u7740\u9732\u4e00\u4e0b\u8138\u3002</p><p>\u3000\u3000\u201c\u6613\u90e8\u957f\u53ec\u89c1\u6211\uff0c\u603b\u4e0d\u80fd\u8fd8\u7a9d\u5728\u5b66\u6821\u91cc\u4e0d\u51fa\u9762\uff0c\u201d\u5f20\u606a\u7b11\u7740\u89e3\u91ca\u8bf4\uff0c\u201c\u6211\u7ad9\u4f60\u4eec\u8eab\u540e\uff0c\u957f\u8138\u7684\u673a\u4f1a\u7559\u7ed9\u4f60\u4eec\u3002\u201d\u770b\u7740\u8b66\u8f66\u5f15\u9886\u7684\u53c2\u89c2\u8f66\u961f\u4ece\u8fdc\u5904\u9a76\u6765\uff0c\u5f20\u606a\u5c31\u5f80\u5c0f\u53d4\u5f20\u77e5\u975e\u8eab\u540e\u4e00\u7ad9\uff0c\u4e0e\u6881\u519b\u3001\u8d75\u9e4f\u4ed6\u4eec\u5e76\u6392\u800c\u7acb\u3002</p><p>\u3000\u3000\u6709\u59da\u6587\u76db\u63d0\u524d\u6253\u8fc7\u62db\u547c\uff0c\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u4ed6\u4eec\u4ece\u8f66\u91cc\u4e0b\u6765\uff0c\u8fd8\u662f\u4f9d\u7167\u4e4b\u524d\u7684\u6b21\u5e8f\u8ba9\u5f20\u77e5\u975e\u3001\u82cf\u6d25\u4e1c\u3001\u90b5\u81f3\u521a\u7b49\u4eba\u5148\u4e0e\u56fd\u5bb6\u8ba1\u59d4\u526f\u4e3b\u4efb\u845b\u5efa\u5fb7\u3001\u90ae\u7535\u90e8\u526f\u90e8\u957f\u6613\u4e91\u98de\u7b49\u4eba\u5148\u63e1\u624b\uff0c\u5f20\u606a\u6742\u5939\u5728\u6881\u519b\u3001\u8d75\u9e4f\u4e4b\u95f4\u51fa\u573a\uff0c\u5f53\u7136\u662f\u76f4\u63a5\u4ecb\u7ecd\u4ed6\u7684\u8eab\u4efd\uff0c\u4e0d\u8fc7\u5728\u5916\u4eba\u770b\u6765\u5c31\u4e0d\u4f1a\u90a3\u4e48\u7a81\u5140\uff0c\u611f\u89c9\u5c31\u50cf\u9676\u664b\u8d77\u4e86\u5174\u8da3\u8981\u8ddf\u9526\u6e56\u7684\u67d0\u4e2a\u9752\u5e74\u5458\u5de5\u591a\u804a\u51e0\u53e5\u4f3c\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u662f\u7b2c\u4e00\u6b21\u5f53\u9762\u89c1\u5230\u845b\u5efa\u5fb7\u3002\u8fd9\u540d\u5b57\u4e0e\u845b\u660e\u5fb7\u53ea\u6709\u4e00\u5b57\u4e4b\u5dee\uff0c\u56fd\u4eba\u7684\u59d3\u540d\u6709\u7279\u522b\u82b1\u6837\u7684\u4e0d\u591a\uff0c\u5c31\u845b\u5efa\u5fb7\u8fd9\u4e2a\u540d\u5b57\u6765\u8bf4\uff0c\u5168\u56fd\u5c11\u8bf4\u4e5f\u6709\u51e0\u767e\u51e0\u5343\u7684\uff0c\u4f46\u8fd9\u4e2a\u845b\u5efa\u5fb7\u5374\u662f\u4e2a\u4e3e\u8db3\u8f7b\u91cd\u7684\u4eba\u7269\uff0c\u5bf9\u9526\u6e56\u6765\u8bf4\uff0c\u5c24\u5176\u5982\u6b64\uff0c\u56fd\u5bb6\u8ba1\u59d4\u526f\u4e3b\u4efb\uff0c\u56fd\u5bb6\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u9886\u5bfc\u5c0f\u7ec4\u7684\u7ec4\u957f\uff0c\u5b9e\u9645\u5e74\u9f84\u4e0d\u5927\uff0c\u4eca\u5e74\u624d\u4e94\u5341\u4e8c\u5c81\uff0c\u76f8\u8c8c\u770b\u4e0a\u53bb\u8fd8\u8981\u5e74\u8f7b\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u9648\u9759\u4e0d\u5728\u8fd9\u91cc\uff0c\u5728\u8fd9\u91cc\uff0c\u4e00\u5b9a\u4f1a\u89c9\u5f97\u845b\u5efa\u5fb7\u8fd9\u4eba\u770b\u4e0a\u53bb\u4f1a\u5f88\u9762\u719f\u3002</p></article>"}}}]);
