(window.webpackJsonp=window.webpackJsonp||[]).push([[831],{854:function(p){p.exports={chapterTitle:null,title:"\u7b2c66\u7ae0 \u6821\u82b1\u5bb6\u4e5f\u6709\u96be\u9898",content:"<article><h2>\u7b2c66\u7ae0 \u6821\u82b1\u5bb6\u4e5f\u6709\u96be\u9898</h2><p>\u3000\u3000\u201c\u8fd9\u4e2b\u5934\uff0c\u505a\u4e8b\u6bdb\u624b\u6bdb\u811a\u7684\u2026\u2026\u201d\u8001\u9648\u5927\u58f0\u62b1\u6028\uff0c\u201c\u89c1\u5230\u9b3c\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u626d\u8fc7\u5934\u6765\u770b\u9648\u5983\u84c9\uff0c\u89c1\u5979\u4e00\u8138\u60ca\u9a87\uff0c\u6015\u662f\u89c1\u4e86\u9b3c\uff1b\u5f20\u606a\u89c1\u9648\u5983\u84c9\u5de6\u624b\u81c2\u7cfb\u7740\u4e00\u5757\u624b\u5e15\uff0c\u60f3\u5fc5\u90a3\u91cc\u662f\u65e9\u4e0a\u7ed9\u522e\u7834\u7684\u5730\u65b9\u3002\u6068\u4e0d\u5f97\u53d8\u5f20\u8138\uff0c\u6216\u8005\u5c06\u5934\u57cb\u8fdb\u684c\u5b50\u91cc\u53bb\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5012\u662f\u770b\u660e\u767d\u4e86\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u4eec\u5c31\u8ba4\u8bc6\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u6d77\u5c71\u62cd\u62cd\u8111\u888b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u5f20\u606a\u4e5f\u662f\u6d77\u5dde\u4e00\u4e2d\u7684\u5b66\u751f\uff0c\u4f60\u4eec\u5bb6\u5983\u84c9\u8bfb\u9ad8\u4e8c\u5427\uff0c\u5f20\u606a\u521a\u521a\u8fdb\u4e00\u4e2d\u3002\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u60ca\u60f6\u5931\u63aa\u7684\u8e72\u4e0b\u6765\u6361\u68cb\u5b50\uff0c\u6ee1\u8138\u7f9e\u7ea2\uff0c\u4e0d\u6562\u62ac\u5934\u770b\u5f20\u606a\u3002</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u5c06\u68cb\u76d8\u6361\u8d77\u6765\uff0c\u53c8\u6361\u4e86\u4e00\u628a\u68cb\u5b50\uff0c\u5728\u68cb\u76d8\u4e0a\u5206\u5f00\u9ed1\u767d\u5b50\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u8981\u4e0d\u8981\u8ba9\u5b50\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\uff1a\u201c\u5148\u4e0b\u4e00\u76d8\u3002\u201d\u5c06\u9ed1\u5b50\u62e2\u81ea\u5df1\u8eab\u524d\uff0c\u5148\u5de6\u4e0a\u89d2\u661f\u4f4d\u843d\u4e00\u5b50\u3002\u5373\u4f7f\u4e0d\u8981\u6c42\u8ba9\u5b50\uff0c\u6267\u9ed1\u5b50\u5148\u884c\uff0c\u4e5f\u662f\u8868\u793a\u5bf9\u8bb8\u9e3f\u4f2f\u7684\u5c0a\u656c\u3002</p><p>\u3000\u3000\u8001\u9648\u5f00\u59cb\u8fd8\u5e2e\u5973\u513f\u6361\u68cb\uff0c\u89c1\u68cb\u5c40\u5f00\u59cb\uff0c\u4fbf\u7ad9\u5230\u4e00\u65c1\u89c2\u770b\u3002\u8bb8\u601d\u8e72\u4e0b\u6765\u5e2e\u9648\u5983\u84c9\u6361\u68cb\uff0c\u89c1\u5979\u8138\u8272\u4e0d\u5bf9\u52b2\uff0c\u95ee\u5979\uff1a\u201c\u4f60\u8138\u8272\u600e\u4e48\u4e0d\u597d\uff1f\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u62ac\u5934\u770b\u4e86\u770b\u5f20\u606a\uff0c\u62bf\u7740\u5634\u4e0d\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u8bb8\u601d\u56de\u5934\u671b\u4e86\u4e00\u773c\uff0c\u89c1\u5f20\u606a\u4e00\u672c\u6b63\u7ecf\u6267\u68cb\u7684\u6837\u5b50\uff0c\u5012\u4f3c\u6a21\u4f3c\u6837\u7684\uff0c\u5979\u770b\u4e0d\u61c2\uff0c\u8bb8\u9e3f\u4f2f\u4e00\u8138\u6de1\u5b9a\uff0c\u5634\u89d2\u8fd8\u6d6e\u7740\u7b11\uff0c\u89c1\u7236\u4eb2\u4e0e\u9648\u5983\u84c9\u7236\u4eb2\u8138\u4e0a\u51dd\u91cd\u7684\u795e\u8272\uff0c\u4f3c\u4e4e\u5f20\u606a\u7684\u6c34\u5e73\u4e0d\u5dee\u3002\u5728\u5f20\u606a\u8eab\u4e0a\u770b\u5230\u592a\u591a\u4ee4\u4eba\u9707\u60ca\u7684\u4e1c\u897f\u4e86\uff0c\u4e0b\u4e00\u624b\u597d\u68cb\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u5979\u7279\u522b\u60ca\u8bb6\uff0c\u5c0f\u58f0\u7684\u95ee\u9648\u5983\u84c9\uff1a\u201c\u4f60\u600e\u4e48\u8ba4\u8bc6\u5f20\u606a\u7684\uff0c\u53c8\u4e0d\u5728\u540c\u4e00\u5e74\u7ea7\uff1f\u201d</p><p>\u3000\u3000\u201c\u8bb8\u4f2f\u4f2f\u8bf4\u4f60\u8f85\u5bfc\u7684\u5c0f\u5b69\u5b50\u662f\u4ed6\uff1f\u201d\u9648\u5983\u84c9\u89c9\u5f97\u6709\u4e9b\u4e0d\u53ef\u7406\u8bae\uff0c\u538b\u4f4e\u58f0\u97f3\u8bf4\uff0c\u201c\u4f60\u77e5\u9053\u4ed6\u7238\u662f\u8c01\uff1f\u201d</p><p>\u3000\u3000\u6628\u5929\u8bf7\u8c22\u665a\u6674\u4e0e\u82b7\u5f64\u5230\u5bb6\u91cc\u5403\u996d\uff0c\u5c31\u4e3a\u4e86\u6253\u6d88\u7236\u6bcd\u4e0e\u90bb\u5c45\u7684\u7591\u8651\uff0c\u8bb8\u601d\u4e0d\u60f3\u8ba9\u522b\u4eba\u77e5\u9053\u5f20\u606a\u662f\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u7684\u513f\u5b50\uff0c\u8fd9\u4f1a\u5f15\u8d77\u522b\u4eba\u4e0d\u597d\u7684\u8054\u60f3\uff0c\u7ed9\u9648\u5983\u84c9\u6324\u6324\u773c\u775b\uff0c\u8ba9\u5979\u4e0d\u8981\u8bf4\u51fa\u6765\u3002\u5c06\u68cb\u5b50\u6361\u68cb\u76d2\u91cc\uff0c\u7ed9\u4ed6\u4eec\u6446\u684c\u4e86\uff0c\u5c31\u62c9\u7740\u9648\u5983\u84c9\u8dd1\u5230\u9662\u5b50\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\u4e0d\u60f3\u522b\u4eba\u77e5\u9053\u4ed6\u7238\u7238\u662f\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\uff0c\u4f60\u8c01\u4e5f\u4e0d\u8981\u8bf4\u3002\u201d\u8bb8\u601d\u7ad9\u5728\u9662\u5b50\u91cc\uff0c\u63a2\u5934\u671b\u91cc\u5c4b\u91cc\u770b\u4e86\u770b\uff0c\u201c\u4f60\u5988\u597d\u70b9\u6ca1\u6709\uff0c\u80fd\u4e0d\u80fd\u4e0b\u5e8a\u8d70\u52a8\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u662f\u90a3\u6837\u5b50\uff0c\u80fd\u5750\u8d77\u6765\u5403\u996d\uff0c\u4e0b\u5e8a\u8fd8\u8981\u52a8\u4e00\u6b21\u624b\u672f\u3002\u201d\u63d0\u8d77\u5988\u5988\u7684\u4f24\u75c5\uff0c\u9648\u5983\u84c9\u4e00\u8138\u6101\u5bb9\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u8ddf\u5f20\u606a\u8ba4\u8bc6\u7684\uff1f\u201d\u8bb8\u601d\u5f88\u5947\u602a\u9648\u5983\u84c9\u770b\u5230\u5f20\u606a\u4e3a\u4ec0\u4e48\u6709\u90a3\u4e48\u5f3a\u70c8\u7684\u53cd\u5e94\uff0c\u201c\u770b\u8d77\u6765\u4f60\u5f88\u6015\u4ed6\uff1f\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u626d\u634f\u4e0d\u5b89\uff0c\u5c0f\u58f0\u95ee\u8bb8\u601d\uff1a\u201c\u8bb8\u601d\u59d0\uff0c\u4f60\u8fd9\u4e48\u6f02\u4eae\uff0c\u7ed9\u4ed6\u8f85\u5bfc\u529f\u8bfe\u65f6\uff0c\u4ed6\u4f1a\u4e0d\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u4ec0\u4e48\uff1f\u201d\u8bb8\u601d\u8f6c\u773c\u95f4\u731c\u5230\u9648\u5983\u84c9\u4f1a\u95ee\u4ec0\u4e48\uff0c\u7c89\u8138\u4e00\u7ea2\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u60f3\u54ea\u91cc\u53bb\u4e86\uff0c\u5f20\u606a\u600e\u4e48\u662f\u8fd9\u79cd\u4eba\uff1f\u201d\u53c8\u60f3\u5230\u5f20\u606a\u6709\u610f\u65e0\u610f\u4e5f\u8981\u5360\u81ea\u5df1\u4e00\u4e9b\u4fbf\u5b9c\uff0c\u53ea\u662f\u4e00\u70b9\u4e0d\u8ba9\u4eba\u751f\u538c\uff0c\u8fd9\u4e48\u4e00\u60f3\uff0c\u5c31\u89c9\u5f97\u8138\u53d1\u70eb\u4e86\u3002</p><p>\u3000\u3000\u9662\u5b50\u91cc\u706f\u5149\u660f\u6697\uff0c\u9648\u5983\u84c9\u770b\u4e0d\u6e05\u8bb8\u601d\u8138\u4e0a\u7684\u7ea2\u6f6e\uff0c\u72b9\u8c6b\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u624d\u9f13\u8d77\u52c7\u6c14\u5bf9\u5979\u8bf4\uff1a\u201c\u8bb8\u601d\u59d0\uff0c\u4f60\u80fd\u4e0d\u80fd\u5e2e\u6211\u8ddf\u5f20\u606a\u8bf4\uff0c\u6211\u4e0d\u60f3\u8ddf\u4ed6\u8c08\u670b\u53cb\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u6123\u4e86\u6123\uff0c\u5979\u521a\u542c\u8c22\u665a\u6674\u8bf4\u5f20\u606a\u8ddf\u5510\u5b66\u8c26\u7684\u5973\u513f\u5510\u5a67\u9752\u6885\u7af9\u9a6c\uff0c\u8bb8\u601d\u53ea\u8fdc\u8fdc\u7684\u770b\u8fc7\u5510\u5a67\uff0c\u4e5f\u662f\u4e00\u4e2a\u7f8e\u4eba\u80da\u5b50\uff0c\u53ea\u662f\u4e0d\u77e5\u9053\u8ddf\u9648\u5983\u84c9\u8c01\u66f4\u6f02\u4eae\u4e9b\u3002\u501f\u7740\u5fae\u5f31\u7684\u706f\u5149\u91cc\uff0c\u9648\u5983\u84c9\u5ae9\u6ed1\u67d4\u817b\u7684\u8138\u86cb\u6807\u81f4\u5f97\u5f88\uff0c\u4e94\u5b98\u7cbe\u81f4\uff0c\u4e00\u53cc\u773c\u775b\u5c24\u5176\u7684\u660e\u8273\uff0c\u4e0d\u6bd4\u81ea\u5df1\u7a0d\u77ee\u7684\u8eab\u6750\u4fee\u957f\u633a\u62d4\uff0c\u4e5f\u6709\u5973\u4eba\u7684\u6a21\u6837\uff0c\u867d\u8bf4\u8fd8\u6709\u4e9b\u9752\u6da9\uff0c\u4f46\u662f\u9752\u6625\u6c14\u606f\u903c\u4eba\uff0c\u4e5f\u96be\u602a\u5f20\u606a\u8981\u7f20\u4e0a\u5979\u3002\u8bb8\u601d\u5fc3\u91cc\u60f3\uff1a\u8fd9\u5c0f\u5b50\uff0c\u539f\u6765\u8fd8\u633a\u82b1\u5fc3\u7684\uff0c\u6216\u8bb8\u4ed6\u89c9\u5f97\u9648\u5983\u84c9\u66f4\u6f02\u4eae\u4e9b\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u4ed6\u53ea\u662f\u628a\u5510\u5b66\u8c26\u7684\u5973\u513f\u5510\u5a67\u5f53\u59b9\u59b9\u770b\uff0c\u4ece\u5c0f\u5728\u4e00\u8d77\u957f\u5927\uff0c\u66f4\u50cf\u5144\u59b9\u4e00\u4e9b\uff0c\u8c22\u665a\u6674\u597d\u50cf\u4e5f\u662f\u8fd9\u4e48\u8bf4\u3002</p><p>\u3000\u3000\u8bb8\u601d\u89c9\u5f97\u6709\u5fc5\u8981\u5e2e\u5f20\u606a\u52a0\u628a\u6cb9\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5f20\u606a\uff0c\u633a\u597d\u7684\u7537\u5b69\u5b50\uff0c\u4eba\u957f\u5f97\u5e05\uff0c\u6027\u683c\u53c8\u597d\uff0c\u5b66\u4e60\u4e5f\u4e0d\u843d\u540e\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u62d2\u7edd\u4ed6\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4e0d\u559c\u6b22\u4ed6\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u4eba\uff0c\u4ed7\u7740\u5bb6\u91cc\u6709\u6743\u6709\u52bf\uff0c\u5728\u5b66\u6821\u91cc\u4ec0\u4e48\u597d\u4e8b\u90fd\u4e0d\u505a\uff0c\u5c3d\u6b3a\u8d1f\u5973\u5b69\u5b50\uff0c\u201d\u9648\u5983\u84c9\u65ad\u7edd\u62d2\u7edd\u8bb8\u601d\u7684\u6413\u548c\uff0c\u54c0\u6c42\u9053\uff0c\u201c\u8bb8\u601d\u59d0\uff0c\u4f60\u5c31\u5e2e\u6211\u8bf4\u8bf4\uff0c\u8ba9\u4ed6\u4e0d\u8981\u7ea0\u7f20\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u601d\u6709\u4e9b\u53d1\u8499\uff0c\u5979\u5370\u8c61\u4e2d\u7684\u5f20\u606a\u53ef\u7edd\u4e0d\u662f\u8fd9\u79cd\u4eba\uff0c\u89c1\u9648\u5983\u84c9\u7684\u58f0\u97f3\u53ef\u601c\u516e\u516e\u7684\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u81ea\u5df1\u544a\u8bc9\u4ed6\u5f97\u4e86\uff0c\u4e0d\u8fc7\u8fd9\u53ef\u80fd\u662f\u4ed6\u7b2c\u4e00\u6b21\u5bf9\u5973\u5b69\u5b50\u8868\u767d\uff0c\u4f60\u8981\u62d2\u7edd\u4ed6\uff0c\u53ef\u80fd\u86ee\u4f24\u4ed6\u5fc3\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u8bf4\u6ca1\u7528\u7684\uff0c\u4ed6\u4e00\u5b9a\u8fd8\u4f1a\u7ea0\u7f20\u6211\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u4eba\uff0c\u4ec0\u4e48\u4e8b\u90fd\u505a\u5f97\u51fa\u6765\u3002\u201d\u9648\u5983\u84c9\u6293\u7740\u8bb8\u601d\u7684\u624b\uff0c\u90fd\u5feb\u6025\u54ed\u4e86\u3002</p><p>\u3000\u3000\u201c\u597d\u597d\u597d\uff0c\u6211\u5e2e\u4f60\u8bf4\u5c31\u662f\u4e86\uff0c\u60f3\u4e0d\u901a\u4f60\u4e3a\u4ec0\u4e48\u5bf9\u4ed6\u6709\u8fd9\u4e48\u6df1\u7684\u6210\u89c1\uff0c\u5f20\u606a\u771f\u662f\u4e0d\u9519\u7684\u7537\u5b69\u5b50\uff0c\u4f60\u8981\u6ca1\u6709\u7537\u670b\u53cb\uff0c\u6211\u5012\u771f\u5efa\u8bae\u4f60\u8003\u8651\u8003\u8651\u4ed6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u522b\u544a\u8bc9\u4ed6\u662f\u6211\u8bf4\u7684\u2026\u2026\u201d\u9648\u5983\u84c9\u89c1\u8bb8\u601d\u4e00\u8138\u4e0d\u4fe1\uff0c\u4fbf\u5c06\u8fd9\u4e9b\u5929\u5b66\u6821\u91cc\u53d1\u751f\u7684\u4f20\u95fb\u8bf4\u7ed9\u5979\u542c\uff0c\u201c\u4eca\u5929\u5750\u516c\u4ea4\u8f66\uff0c\u4ed6\u8fd8\u5360\u4eba\u5bb6\u4fbf\u5b9c\uff0c\u6211\u90fd\u51c6\u5907\u9000\u5b66\u4e86\uff0c\u53cd\u6b63\u6574\u5929\u7ed9\u4ed6\u4eec\u7ea0\u7f20\u7740\uff0c\u4e5f\u8bfb\u4e0d\u8fdb\u4e66\uff0c\u53cd\u6b63\u6211\u5988\u4e5f\u9700\u8981\u4eba\u5728\u5bb6\u91cc\u7167\u987e\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u601d\u81ea\u7136\u4e0d\u4f1a\u76f8\u4fe1\u5f20\u606a\u662f\u90a3\u79cd\u4eba\uff0c\u518d\u8bf4\u8fd9\u4e9b\u5929\uff0c\u5e02\u573a\u63a8\u5e7f\u7684\u4e8b\u60c5\u4e00\u5927\u5806\uff0c\u5149\u65b9\u6848\u7684\u5236\u8ba2\uff0c\u5c31\u591f\u4ed6\u5fd9\u7684\uff0c\u54ea\u6709\u95f2\u65f6\u95f4\u5728\u5b66\u6821\u91cc\u62db\u60f9\u662f\u975e\uff1f\u8bf4\u8d77\u516c\u4ea4\u8f66\u5360\u4fbf\u5b9c\u7684\u4e8b\uff0c\u8bb8\u601d\u60f3\u8d77\u521a\u56de\u6d77\u5dde\u4e0e\u5f20\u606a\u5728\u516c\u4ea4\u8f66\u4e0a\u65d6\u65ce\u4e4b\u4e8b\uff0c\u73b0\u5728\u60f3\u8d77\u6765\uff0c\u8138\u90fd\u53d1\u70eb\uff0c\u5f20\u606a\u53ef\u4e0d\u662f\u90a3\u79cd\u4f1a\u4e71\u52a8\u624b\u811a\u7684\u4eba\uff0c\u4f46\u662f\u8fd9\u79cd\u4e8b\u4e5f\u65e0\u6cd5\u5e2e\u4ed6\u8fa9\u89e3\uff0c\u7ed9\u9648\u5983\u84c9\u60f3\u9000\u5b66\u7684\u5ff5\u5934\u5413\u4e86\u4e00\u8df3\uff1a\u201c\u4f60\u778e\u8bf4\u4ec0\u4e48\uff0c\u4f60\u4e0d\u8981\u6709\u9000\u5b66\u7684\u5ff5\u5934\uff0c\u5f20\u606a\u8fd8\u80fd\u542c\u6211\u7684\u8bdd\uff0c\u4f60\u8981\u4e0d\u60f3\u8ddf\u4ed6\u4ea4\u670b\u53cb\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u5f3a\u8feb\u4f60\u7684\u2026\u2026\u201d\u63fd\u7740\u5979\u7684\u80a9\u8180\uff0c\u201c\u8fdb\u5c4b\u770b\u4e86\u770b\u4f60\u5988\uff0c\u4f60\u4e0d\u8981\u778e\u60f3\u4ec0\u4e48\u4e1c\u897f\u3002\u201d</p><p>\u3000\u3000\u5c4b\u5b50\u91cc\u5f25\u6f2b\u7740\u4e2d\u836f\u7684\u5473\u9053\uff0c\u9648\u5983\u84c9\u5988\u5988\u5218\u82ac\u53bb\u5e74\u51fa\u8f66\u7978\u9020\u6210\u810a\u690e\u635f\u4f24\uff0c\u5367\u5e8a\u4f11\u517b\u5230\u73b0\u5728\uff0c\u8fd8\u6ca1\u80fd\u4e0b\u5e8a\u8d70\u52a8\uff0c\u5f53\u65f6\u591c\u91cc\u52a0\u73ed\u56de\u5bb6\uff0c\u8def\u4e0a\u6ca1\u6709\u522b\u7684\u884c\u4eba\uff0c\u5218\u82ac\u4e00\u4eba\u6ca1\u6321\u4f4f\u8087\u4e8b\u8f66\uff0c\u4e5d\u56db\u5e74\u666e\u901a\u804c\u5de5\u8fd8\u6ca1\u6709\u5de5\u4f24\u4fdd\u9669\u8fd9\u6982\u5ff5\uff0c\u90fd\u7531\u5382\u5b50\u91cc\u627f\u62c5\u3002\u5de5\u5382\u6548\u76ca\u597d\uff0c\u8fd8\u597d\u4e00\u70b9\uff0c\u5218\u82ac\u6240\u5728\u7684\u65b0\u5149\u9020\u7eb8\u5382\u8d37\u4e86\u6b3e\u53bb\u5efa\u65b0\u5382\uff0c\u5de5\u4eba\u5de5\u8d44\u90fd\u53d1\u4e0d\u51fa\uff0c\u54ea\u91cc\u6709\u94b1\u7ed9\u62a5\u533b\u836f\u8d39\uff1f\u7b2c\u4e00\u6b21\u7684\u624b\u672f\u8d39\u8fd8\u662f\u5de5\u53cb\u51d1\u7684\uff0c\u5e95\u4e0b\u7684\u5c31\u6ca1\u6709\u7740\u843d\uff0c\u5728\u5bb6\u91cc\u4e2d\u836f\u517b\u7740\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u7684\u7238\u7238\u9648\u5947\u7ecf\u8425\u8fd9\u5bb6\u5c0f\u9152\u9986\uff0c\u53ea\u662f\u52c9\u5f3a\u7ef4\u6301\uff0c\u60f3\u7740\u5c06\u9152\u9986\u76d8\u51fa\u53bb\uff0c\u5148\u5e2e\u5218\u82ac\u628a\u624b\u672f\u7ed9\u505a\u4e86\u3002\u5218\u82ac\u6ca1\u540c\u610f\uff0c\u8981\u662f\u624b\u672f\u505a\u4e86\uff0c\u8fd8\u514d\u4e0d\u4e86\u8981\u762b\u75ea\uff0c\u5c82\u4e0d\u662f\u4e00\u5bb6\u4eba\u7684\u6d3b\u8def\u90fd\u6ca1\u6709\u4e86\uff1f\u73b0\u5728\u81f3\u5c11\u8fd8\u6709\u4e2a\u9152\u9986\u5728\u8425\u4e1a\uff0c\u80fd\u7ef4\u6301\u751f\u6d3b\u3002</p><p>\u3000\u3000\u8bb8\u601d\u60f3\u8d77\u5f20\u606a\u4e4b\u524d\u8ba9\u4ed6\u6536\u96c6\u8fc7\u65b0\u5149\u9020\u7eb8\u5382\u7684\u8d44\u6599\uff0c\u627e\u9648\u5983\u84c9\u5988\u5988\u4e86\u89e3\uff0c\u90a3\u662f\u518d\u597d\u4e0d\u8fc7\uff0c\u9648\u5983\u84c9\u5988\u5988\u5728\u51fa\u8f66\u7978\u524d\uff0c\u662f\u65b0\u5149\u9020\u7eb8\u5382\u7684\u4f1a\u8ba1\u3002</p><p>\u3000\u3000\u201c\u82ac\u59e8\uff0c\u5728\u770b\u7535\u89c6\u5462\uff1f\u201d</p><p>\u3000\u3000\u5218\u82ac\u4f9d\u7740\u5e8a\u5934\uff0c\u5750\u4e0d\u8d77\u6765\uff0c\u5934\u6602\u7740\uff0c\u8ba9\u8bb8\u601d\u5750\uff1a\u201c\u5c0f\u601d\u554a\uff0c\u542c\u8bf4\u4f60\u73b0\u5728\u627e\u4e86\u4e00\u4efd\u597d\u5de5\u4f5c\uff0c\u6628\u5929\u516c\u53f8\u8001\u677f\u8fd8\u5230\u4f60\u5bb6\u5403\u996d\uff1f\u4f60\u82ac\u59e8\u5bb6\u4ec0\u4e48\u60c5\u51b5\u4f60\u6e05\u695a\u7684\uff0c\u4f60\u7238\u4e0a\u6b21\u4e0a\u95e8\u6765\uff0c\u4f60\u9648\u53d4\u5b9e\u5728\u62ff\u4e0d\u51fa\u94b1\u6765\u51d1\u7ed9\u4f60\u7238\uff0c\u4f60\u4e0d\u8981\u6028\u4f60\u9648\u53d4\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u773c\u775b\u6709\u4e9b\u6e7f\u6da6\uff1a\u201c\u6211\u7238\u4e5f\u662f\u4e3a\u6211\u7684\u4e8b\u6025\u7cca\u6d82\u4e86\uff0c\u4ed6\u8fd8\u5728\u8ddf\u9648\u53d4\u4e0b\u68cb\u5462\uff0c\u82ac\u59e8\uff0c\u4f60\u8bf4\u54ea\u91cc\u53bb\u4e86\uff1f\u201d</p></article>"}}}]);
