(window.webpackJsonp=window.webpackJsonp||[]).push([[831],{854:function(p){p.exports={chapterTitle:null,title:"\u7b2c828\u7ae0 \u4e94\u6708\u5370\u5c3c",content:"<article><h2>\u7b2c828\u7ae0 \u4e94\u6708\u5370\u5c3c</h2><p>\u3000\u3000\u5f20\u606a\u8fd4\u56de\u5efa\u90ba\u624d\u4e00\u5468\u7684\u65f6\u95f4\uff0c\u5510\u82f1\u57f9\u4e5f\u7531\u5efa\u90ba\u8b66\u65b9\u79fb\u4ea4\u7ed9\u6c5f\u5357\u7701\u516c\u5b89\u5385\u5e76\u6848\u4fa6\u67e5\u4e86\u3002\u9635\u5bb9\u5e9e\u5927\u3001\u6210\u5458\u7ecf\u8fc7\u8ba4\u8bc6\u6311\u9009\u7684\u8054\u5408\u8c03\u67e5\u5c0f\u7ec4\u4e0b\u53bb\u4e0d\u4e45\uff0c\u534e\u7a00\u96c6\u56e2\u6d89\u53ca\u5230\u7684\u591a\u5b97\u4eba\u8eab\u4f24\u5bb3\u6848\u4ef6\u5c31\u6d6e\u51fa\u6c34\u9762\uff0c\u591a\u5b97\u60ac\u6848\u4e5f\u76f4\u6307\u5411\u534e\u7a00\u96c6\u56e2\u3002</p><p>\u3000\u3000\u6709\u4e9b\u6848\u4ef6\u751a\u81f3\u5728\u516c\u5b89\u90e8\u90fd\u6709\u5907\u6848\uff0c\u53ea\u662f\u7531\u4e8e\u5730\u65b9\u4e0a\u7684\u61c8\u6020\uff0c\u4e00\u76f4\u90fd\u6ca1\u6cd5\u8ffd\u67e5\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u624d\u521d\u6b65\u610f\u8bc6\u5230\u95ee\u9898\u7684\u4e25\u91cd\uff0c\u534e\u7a00\u96c6\u56e2\u5c31\u662f\u5bc4\u751f\u5728\u7ae0\u5dde\u5730\u65b9\u4e0a\u7684\u4e00\u4e2a\u8d85\u5927\u89c4\u6a21\u7684\u9ed1\u793e\u4f1a\u6027\u8d28\u7ecf\u6d4e\u72af\u7f6a\u56e2\u4f19\uff0c\u6bcf\u5e74\u975e\u6cd5\u63a0\u593a\u7684\u8d22\u5bcc\u6570\u4ee5\u4ebf\u8ba1\uff0c\u751a\u81f3\u8981\u8d85\u8fc7\u7ae0\u5dde\u7684\u5730\u65b9\u8d22\u653f\u6536\u5165\uff0c\u6ecb\u751f\u5341\u591a\u5e74\uff0c\u7701\u91cc\u4f3c\u4e4e\u5bf9\u6b64\u6beb\u65e0\u5bdf\u89c9\uff0c\u90a3\u5c82\u4e0d\u662f\u8bf4\u660e\u8fd9\u4e2a\u72af\u7f6a\u56e2\u4f19\u7684\u4fdd\u62a4\u4f1e\u5f02\u5e38\u7684\u5e9e\u5927\uff1f</p><p>\u3000\u3000\u6c5f\u5357\u7701\u516c\u5b89\u5385\u5728\u5efa\u90ba\u8b66\u65b9\u5c06\u5510\u82f1\u57f9\u79fb\u4ea4\u8fc7\u6765\u7684\u540c\u65f6\uff0c\u8054\u5408\u7701\u6b66\u8b66\u603b\u961f\u7684\u5b98\u5175\u62d8\u6355\u534e\u7a00\u96c6\u56e2\u591a\u540d\u9aa8\u5e72\u6210\u5458\u62bc\u56de\u91d1\u5c71\u53d7\u5ba1\u3002</p><p>\u3000\u3000\u81ea\u7136\u4e5f\u4f1a\u6709\u4eba\u5728\u6697\u4e2d\u8bd5\u56fe\u963b\u6b62\u6848\u4ef6\u6df1\u67e5\u4e0b\u53bb\uff0c\u5c42\u5c42\u538b\u529b\u90fd\u6700\u540e\u4f20\u5bfc\u5230\u6881\u4f1f\u6cd5\u90a3\u91cc\uff0c\u6bd5\u7adf\u662f\u6881\u4f1f\u6cd5\u4e00\u624b\u63a8\u52a8\u7740\u8981\u53bb\u5f7b\u67e5\u7ae0\u5dde\u7684\u95ee\u9898\u3002\u6881\u4f1f\u6cd5\u5012\u4e0d\u7528\u523b\u610f\u7684\u53bb\u9876\u7740\u8fd9\u5c42\u5c42\u538b\u529b\uff0c\u81ea\u6709\u53e6\u6709\u4e00\u80a1\u529b\u91cf\u5728\u63a8\u52a8\u7740\u8981\u5c06\u7ae0\u5dde\u6848\u5f7b\u67e5\u4e0b\u53bb\uff0c\u4e5f\u6b63\u662f\u8fd9\u80a1\u529b\u91cf\u8ba9\u7701\u91cc\u7684\u5176\u4ed6\u5b98\u5458\u5728\u7ae0\u5dde\u95ee\u9898\u4fdd\u6301\u6c89\u9ed8\u3001\u552f\u6050\u60f9\u4e0a\u4e00\u8eab\u9a9a\uff0c\u5373\u4f7f\u6709\u4e9b\u7275\u626f\u7684\uff0c\u53ea\u8981\u7275\u626f\u4e0d\u6df1\uff0c\u8fd9\u65f6\u5019\u90fd\u53ea\u4f1a\u60f3\u65b9\u8bbe\u6cd5\u7684\u6d17\u8131\uff0c\u8fd8\u4e0d\u662f\u4e00\u6478\u9ed1\u6df1\u8e0f\u8fdb\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6574\u4e2a\u4e94\u6708\u4f3c\u4e4e\u4e4f\u5584\u53ef\u9648\uff0c\u5982\u679c\u6ca1\u67095\u67089\u65e5\u53d1\u751f\u5728\u5370\u5c3c\u7684\u6392\u534e\u5927\u9a9a\u4e71\u3002</p><p>\u3000\u30005\u67089\u65e5\uff0c\u5370\u5c3c\u4e00\u573a\u672c\u662f\u53cd\u5bf9\u82cf\u54c8\u6258\u653f\u6743\u7684\u653f\u6cbb\u8fd0\u52a8\uff0c\u7531\u4e8e\u5370\u5c3c\u519b\u65b9\u7684\u6697\u4e2d\u53c2\u4e0e\uff0c\u5728\u5370\u6c11\u9996\u90fd\u96c5\u52a0\u8fbe\u7b49\u5730\u6f14\u53d8\u4e3a\u4e25\u91cd\u7684\u6392\u534e\u5927\u9a9a\u4e71\uff0c\u6bd4\u66fe\u7ecf\u53d1\u751f\u8fc7\u7684\u5386\u53f2\u63d0\u524d\u4e86\u56db\u5929\u3002</p><p>\u3000\u3000\u6392\u534e\u9a9a\u4e71\u4e8b\u5148\u5e76\u975e\u6ca1\u6709\u5f81\u5146\uff0c\u5728\u6b64\u4e4b\u524d\u7684\u4e09\u6708\u3001\u56db\u6708\u95f4\uff0c\u5370\u5c3c\u5c31\u53d1\u751f\u591a\u8d77\u6709\u8ba1\u5212\u7684\u9488\u5bf9\u534e\u4eba\u7684\u66b4\u52a8\u884c\u4e3a\uff0c\u751a\u81f3\u66f4\u65e9\u80fd\u8ffd\u6eaf\u4e5d\u4e03\u5e74\u5e95\u5c31\u5bc6\u96c6\u53d1\u751f\u7684\u9488\u5bf9\u534e\u4eba\u7684\u66b4\u529b\u6848\u4ef6\u3002\u79cd\u65cf\u8bdd\u9898\u4e5f\u8d8a\u6765\u8d8a\u70ed\u5207\u7684\u5728\u5370\u5c3c\u88ab\u5a92\u4f53\u8c08\u8bba\u8d77\uff0c\u5370\u5c3c\u653f\u5e9c\u4e3a\u4e86\u8f6c\u79fb\u5728\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u6765\u4e34\u65f6\u7684\u65e0\u80fd\uff0c\u751a\u81f3\u591a\u6b21\u516c\u7136\u6697\u793a\u662f\u201c\u5370\u5c3c\u534e\u4eba\u5728\u8fd9\u6b21\u91d1\u878d\u5371\u673a\u4e2d\u63a0\u593a\u4e86\u5370\u5c3c\u4eba\u7684\u8d22\u5bcc\u201d\uff0c\u60f3\u7740\u5c06\u6c11\u4f17\u7684\u6ce8\u610f\u529b\u8f6c\u79fb\u5230\u79cd\u65cf\u77db\u76fe\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u53ea\u4e0d\u8fc7\u57285\u67089\u65e5\u4e4b\u524d\u7684\u66b4\u529b\u884c\u4e3a\u89c4\u6a21\u90fd\u6bd4\u8f83\u5c0f\uff0c\u6ca1\u6709\u5f15\u8fdb\u56fd\u9645\u793e\u4f1a\u7684\u8db3\u591f\u5173\u6ce8\u3002</p><p>\u3000\u3000\u4e0e\u66fe\u7ecf\u53d1\u751f\u8fc7\u7684\u5386\u53f2\u7565\u6709\u4e0d\u540c\u7684\uff0c\u5728\u4e09\u56db\u6708\u95f4\u5370\u5c3c\u53d1\u751f\u591a\u6b21\u9488\u5bf9\u534e\u4eba\u7684\u66b4\u529b\u4e8b\u4ef6\u4e4b\u540e\uff0c\u5c31\u6709\u79d8\u5bc6\u8d44\u91d1\u8d44\u52a9\u591a\u5bb6\u56fd\u9645\u4eba\u6743\u7ec4\u7ec7\u56e2\u4f53\u4e0e\u56fd\u9645\u5a92\u4f53\u8fdb\u5165\u5370\u5c3c\u8fdb\u884c\u4eba\u9053\u8c03\u67e5\u3002</p><p>\u3000\u3000\u4e2a\u4eba\u52aa\u529b\u80fd\u591f\u505a\u7684\u4e8b\u60c5\u5176\u5b9e\u5f88\u6709\u9650\uff0c\u5e76\u4e0d\u80fd\u963b\u6b62\u66b4\u884c\u9a9a\u4e71\u7684\u53d1\u751f\uff0c\u5f20\u606a\u8003\u8651\u8fc7\u4ed6\u552f\u4e00\u80fd\u505a\u7684\u4e8b\u60c5\u5c31\u662f\u5728\u66b4\u884c\u53d1\u751f\u540e\u5c06\u6d88\u606f\u53ca\u65f6\u4f20\u64ad\u51fa\u53bb\uff0c\u901a\u8fc7\u56fd\u9645\u5a92\u4f53\u62ab\u9732\u51fa\u6765\u5bf9\u5370\u5c3c\u653f\u5e9c\u5f62\u6210\u56fd\u9645\u8206\u8bba\u538b\u529b\u6765\u5236\u6b62\u66b4\u884c\u7684\u8fdb\u4e00\u6b65\u8513\u5ef6\u3002</p><p>\u3000\u30009\u65e5\u66b4\u4e71\u7684\u6d88\u606f\u5df2\u7ecf\u5f25\u6f2b\u5728\u6574\u4e2a\u96c5\u52a0\u8fbe\uff0c\u6b64\u65f6\u7684\u5370\u5c3c\u603b\u7edf\u82cf\u54c8\u6258\u975e\u5e38\u53ca\u65f6\u7684\u5728\u57c3\u53ca\u51fa\u5e2d\u4f1a\u8bae\uff0c\u5370\u5c3c\u519b\u65b9\u4e3b\u8981\u5c06\u9886\u53c8\u6070\u5de7\u7684\u96c6\u4e2d\u5230\u4e1c\u722a\u54c7\u53c2\u52a0\u4e00\u4e2a\u5178\u793c\uff0c\u591a\u540d\u56fd\u9645\u4eba\u6743\u7ec4\u7ec7\u6d3e\u9a7b\u5230\u96c5\u52a0\u8fbe\u7684\u89c2\u5bdf\u5458\u4e0e\u56fd\u9645\u5a92\u4f53\u8bb0\u8005\u90fd\u654f\u611f\u7684\u55c5\u5230\u5371\u9669\u7684\u6c14\u606f\u3002</p><p>\u3000\u30009\u65e5\u4e0b\u5348\u5230\u6df1\u591c\uff0c\u96c5\u52a0\u8fbe\u5c31\u9646\u9646\u7eed\u7eed\u7684\u53d1\u751f\u4e86\u591a\u6b21\u9488\u5bf9\u534e\u4eba\u7684\u66b4\u529b\u4e8b\u4ef6\uff0c\u7531\u4e8e\u8fd8\u6ca1\u6709\u6f14\u53d8\u6210\u5927\u9a9a\u4e71\uff0c\u5373\u4f7f\u6709\u6d88\u606f\u4ece\u96c5\u52a0\u8fbe\u4f20\u51fa\uff0c\u56fd\u9645\u5a92\u4f53\u7531\u4e8e\u65b0\u95fb\u6548\u5e94\u7684\u95ee\u9898\u5bf9\u8fd9\u4e9b\u6d88\u606f\u7ed9\u4e88\u4e86\u51b7\u5904\u7406\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u53ca\u65f6\u520a\u53d1\u4e86\u4e00\u4e9b\u65b0\u95fb\u7a3f\u4ef6\u4e5f\u6ca1\u6709\u5f15\u8d77\u8db3\u591f\u7684\u6ce8\u610f\uff0c\u4e2d\u56fd\u9a7b\u5370\u5c3c\u5927\u4f7f\u9986\u7684\u53cd\u5e94\u4e5f\u76f8\u5f53\u7684\u51b7\u6de1\u3002\u6b64\u65f6\u8fd8\u6ca1\u6709\u4efb\u4f55\u9488\u5bf9\u4e2d\u56fd\u9a7b\u5370\u5c3c\u5927\u4f7f\u9986\u7684\u66b4\u529b\u503e\u5411\uff0c\u5927\u4f7f\u9986\u7684\u5b98\u65b9\u56de\u5e94\u662f\u4e0d\u5e94\u5e72\u6d89\u5370\u5c3c\u5185\u653f\u3001\u4f1a\u5bf9\u540e\u7eed\u4e8b\u4ef6\u6301\u7eed\u5173\u6ce8\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u6562\u5728\u5982\u6b64\u7279\u6b8a\u65f6\u671f\u5b89\u6392\u5458\u5de5\u6ede\u7559\u5728\u5370\u5c3c\uff0c\u4e8b\u5b9e\u4e0a\u5728\u6b64\u4e4b\u524d\u5c31\u501f\u53e3\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5370\u5c3c\u662f\u6700\u60e8\u91cd\u7684\u707e\u533a\u5c06\u7231\u8fbe\u7535\u5b50\u5728\u5370\u5c3c\u7684\u4e1a\u52a1\u5168\u90e8\u64a4\u56de\u3002\u4ed6\u751a\u81f3\u4e0d\u80fd\u8ba9\u6606\u817e\u5728\u7ebf\u65b0\u95fb\u90e8\u95e8\u7684\u8bb0\u8005\u7279\u610f\u5728\u8fd9\u4e2a\u65f6\u673a\u524d\u5f80\u96c5\u52a0\u8fbe\uff0c\u66b4\u884c\u53d1\u751f\u671f\u95f4\uff0c\u4efb\u4f55\u4e00\u540d\u534e\u4eba\u9762\u5b54\u7684\u4eba\u51fa\u73b0\u5728\u96c5\u52a0\u8fbe\u8857\u5934\u90fd\u662f\u6781\u5176\u5371\u9669\u7684\uff0c\u66f4\u4e0d\u7528\u8bf4\u8fd8\u8981\u53bb\u8bb0\u5f55\u66b4\u884c\u3002\u6606\u817e\u5728\u7ebf\u65b0\u95fb\u90e8\u95e8\u4e0e\u4e00\u5bb6\u6b27\u6d32\u6c11\u8425\u901a\u8baf\u793e\u5171\u4eab\u4e86\u5728\u5370\u5c3c\u7684\u65b0\u95fb\u6765\u6e90\u3002</p><p>\u3000\u3000\u5c3c\u514b\u674e\u68ee\u5bf9\u5370\u5c3c\u8fd9\u4e2a\u56fd\u5bb6\u4e0d\u4f46\u8c08\u4e0d\u4e0a\u597d\u611f\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u538c\u6076\u4e86\uff0c\u4ed6\u8fdb\u5165\u91d1\u878d\u754c\u7684\u524d\u51e0\u5e74\u5c31\u662f\u5728\u5370\u5c3c\u96c5\u52a0\u8fbe\u7684\u91d1\u878d\u516c\u53f8\u4ece\u4e1a\uff0c\u60f3\u5fc5\u90a3\u6bb5\u65f6\u95f4\u6ca1\u6709\u7ed9\u4ed6\u7559\u4e0b\u597d\u5370\u8c61\u3002\u5c3c\u514b\u674e\u68ee\u66ff\u5f20\u606a\u4ed6\u4eec\u79d8\u5bc6\u6267\u638c\u5bf9\u51b2\u57fa\u91d1\u4e4b\u540e\uff0c\u5bf9\u5370\u5c3c\u76fe\u4e00\u76f4\u90fd\u5f88\u6709\u5174\u8da3\uff0c\u5373\u4f7f\u8fd9\u6bb5\u65f6\u95f4\u4e9a\u6d32\u8d27\u5e01\u5e02\u573a\u76f8\u5bf9\u5e73\u9759\uff0c\u4ed6\u90fd\u6ca1\u6709\u653e\u5f03\u76ef\u7d27\u5370\u5c3c\u76fe\u8fd9\u5757\u80a5\u8089\u3002\u4ed6\u8ddf\u4ed6\u7684\u56e2\u961f\u9664\u4e86\u9999\u6e2f\u4e4b\u5916\uff0c\u5f88\u5927\u4e00\u90e8\u5206\u65f6\u95f4\u90fd\u5728\u5370\u5c3c\u3002\u5370\u5c3c\u76fe\u72c2\u8dcc\uff0c\u62ff\u7740\u7f8e\u5143\u5728\u5370\u5c3c\u6d88\u8d39\u7b80\u76f4\u5c31\u5728\u5929\u5802\uff0c\u5c3c\u514b\u674e\u68ee\u5e74\u540e\u751a\u81f3\u5728\u5370\u5c3c\u4eb2\u773c\u76ee\u7779\u4e86\u51e0\u8d77\u9488\u5bf9\u534e\u4eba\u7684\u66b4\u884c\uff0c\u4e5f\u6e05\u695a\u5370\u5c3c\u793e\u4f1a\u4e0a\u6392\u534e\u7684\u79cd\u65cf\u6b67\u89c6\u73b0\u8c61\u6bd4\u8f83\u666e\u904d\uff0c\u4f46\u662f\u7eb5\u5bb9\u9488\u5bf9\u534e\u4eba\u66b4\u529b\u72af\u7f6a\u5374\u662f\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u4e4b\u540e\u51fa\u73b0\u7684\u65b0\u503e\u5411\u3002</p><p>\u3000\u3000\u5f20\u606a\u901a\u8fc7\u5c3c\u514b\u674e\u68ee\u5b89\u6392\u8d44\u91d1\u8d5e\u52a9\u56fd\u9645\u4eba\u6743\u7ec4\u7ec7\u4e0e\u5a92\u4f53\u5230\u5370\u5c3c\u8fdb\u884c\u4eba\u9053\u8c03\u67e5\u81ea\u7136\u4e5f\u662f\u987a\u7406\u6210\u7ae0\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u4e5d\u65e5\u591c\u95f4\uff0c\u5c3c\u514b\u674e\u68ee\u5c31\u4f4f\u5728\u5370\u5c3c\u9996\u90fd\u96c5\u52a0\u8fbe\u5e02\u4e2d\u5fc3\u7684\u5927\u9152\u5e97\u91cc\uff0c\u4ed6\u5012\u4e0d\u662f\u7279\u610f\u4e3a\u53ef\u80fd\u7206\u53d1\u7684\u9a9a\u4e71\u800c\u6765\uff0c\u4ed6\u654f\u9510\u7684\u611f\u89c9\u5230\u5370\u5c3c\u76fe\u8fd8\u6709\u53ef\u80fd\u4f1a\u8fdb\u4e00\u6b65\u7684\u66b4\u632b\uff0c\u9002\u9022\u5176\u65f6\uff0c\u5c31\u6210\u4e86\u5f20\u606a\u5728\u5370\u5c3c\u96c5\u52a0\u8fbe\u7684\u76f4\u63a5\u8033\u76ee\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u8ba9\u8eab\u8fb9\u5176\u4ed6\u4eba\u53c2\u4e0e\u8fdb\u6765\uff0c\u5728\u5404\u65b9\u9762\u6d88\u606f\u6c47\u96c6\u8d77\u6765\u90fd\u663e\u793a\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u5728\u8fd9\u4e00\u4e24\u5929\u65f6\u95f4\u91cc\u5c31\u4f1a\u7206\u53d1\uff0c\u4ed6\u90a3\u51e0\u5929\u5c31\u4f4f\u5230\u71d5\u5f52\u6e56\u5317\u5cb8\u7684\u6e56\u7554\u5c0f\u5c4b\u91cc\uff0c\u8fd8\u5c06\u7fdf\u4e39\u9752\u6253\u53d1\u56de\u65b0\u829c\u5904\u7406\u7535\u5b50\u4ea7\u4e1a\u914d\u5957\u56ed\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u4e5d\u65e5\u591c\u95f4\uff0c\u5728\u5927\u9a9a\u4e71\u4e00\u89e6\u5373\u53d1\u7684\u60c5\u51b5\u4e0b\uff0c\u5f20\u606a\u4e0e\u4eba\u5728\u9a6c\u6765\u897f\u4e9a\u7684\u90ed\u677e\u5ef6\u901a\u4e86\u7535\u8bdd\uff1a\u201c\u5370\u5c3c\u7684\u98ce\u5411\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u5bf9\u52b2\uff0c\u8fde\u6211\u5728\u56fd\u5185\u90fd\u542c\u5230\u9a9a\u4e71\u7684\u6d88\u606f\u5728\u96c5\u52a0\u8fbe\u7684\u8857\u5934\u5f25\u6f2b\u2026\u2026\u201d</p><p>\u3000\u3000\u4e09\u56db\u6708\u4efd\u5370\u5c3c\u5404\u5730\u9488\u5bf9\u534e\u4eba\u7684\u66b4\u529b\u72af\u7f6a\u592a\u5bc6\u96c6\u4e86\u4e00\u4e9b\uff0c\u5df2\u7ecf\u5f15\u8d77\u56fd\u9645\u4eba\u6743\u7ec4\u7ec7\u7684\u6ce8\u610f\uff1b\u4f46\u662f\u90ed\u677e\u5ef6\u5e76\u4e0d\u6653\u5f97\u4e09\u56db\u6708\u4efd\u5230\u5370\u5c3c\u8fdb\u884c\u8c03\u67e5\u7684\u56fd\u9645\u4eba\u6743\u7ec4\u7ec7\u4e0e\u56fd\u9645\u5a92\u4f53\u6709\u5927\u534a\u662f\u5f20\u606a\u901a\u8fc7\u6d77\u5916\u79d8\u5bc6\u8d26\u6237\u8d44\u52a9\u7684\u3002\u90ed\u677e\u5ef6\u5728\u6b64\u4e4b\u524d\u5df2\u7ecf\u63a5\u5230\u5173\u4e8e\u5370\u5c3c\u653f\u5e9c\u6709\u7eb5\u5bb9\u9488\u5bf9\u534e\u4eba\u66b4\u529b\u72af\u7f6a\u503e\u5411\u7684\u62a5\u544a\uff0c\u4e91\u6e90\u96c6\u56e2\u5728\u5370\u5c3c\u62e5\u6709\u5927\u91cf\u7684\u6295\u8d44\uff0c\u4e00\u5411\u90fd\u5f88\u5173\u6ce8\u5370\u5c3c\u7684\u52a8\u5411\uff0c\u4f46\u662f\u5728\u4e94\u6708\u4e4b\u524d\u4ed6\u8fd8\u6ca1\u6709\u610f\u8bc6\u5230\u5728\u5370\u5c3c\u915d\u917f\u7740\u9488\u5bf9\u534e\u4eba\u7684\u5927\u5371\u673a\u3002</p><p>\u3000\u3000\u52309\u65e5\u591c\u95f4\uff0c\u90ed\u677e\u5ef6\u5df2\u7ecf\u4e0d\u518d\u6000\u7591\u5927\u9a9a\u4e71\u7206\u53d1\u7684\u53ef\u80fd\uff0c\u4ed6\u6b64\u524d\u5df2\u7ecf\u901a\u77e5\u4e91\u6e90\u96c6\u56e2\u5728\u5370\u5c3c\u7684\u534e\u4eba\u5458\u5de5\u6ce8\u610f\u7559\u5b88\u5de5\u5382\u81ea\u4fdd\uff0c\u4e0e\u5f20\u606a\u5728\u8d8a\u6d0b\u7535\u8bdd\u91cc\u6c9f\u901a\uff0c\u5728\u5404\u65b9\u9762\u90fd\u51b7\u6de1\u7684\u60c5\u51b5\u4e0b\uff0c\u53ea\u80fd\u5148\u77e5\u4f1a\u65b0\u52a0\u5761\u653f\u5e9c\u3002\u9a6c\u6765\u897f\u4e9a\u653f\u5e9c\u5bf9\u534e\u4eba\u7684\u6001\u5ea6\u5e76\u4e0d\u80fd\u7b97\u975e\u5e38\u53cb\u5584\uff0c\u534e\u4eba\u5728\u9a6c\u6765\u897f\u4e9a\u7684\u653f\u6cbb\u5730\u4f4d\u5411\u6765\u4e0d\u9ad8\uff0c\u800c\u65b0\u52a0\u5761\u5219\u5b8c\u5168\u662f\u6d77\u5916\u534e\u4eba\u5728\u9a6c\u516d\u7532\u5efa\u7acb\u7684\u56fd\u5bb6\u3002\u5148\u77e5\u4f1a\u65b0\u52a0\u5761\u653f\u5e9c\u662f\u773c\u4e0b\u6700\u4f73\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u5927\u9a9a\u4e71\u662f10\u65e5\u65e9\u6668\u9aa4\u7136\u7206\u53d1\u7684\uff0c\u597d\u50cf9\u65e5\u591c\u95f4\u7684\u51e0\u8d77\u66b4\u529b\u4e8b\u4ef6\u53ea\u662f\u66b4\u98ce\u96e8\u6765\u4e34\u4e4b\u524d\u7684\u51e0\u6ef4\u9884\u544a\u6027\u7684\u96e8\u6ef4\u3002</p><p>\u3000\u3000\u5728\u6e56\u7554\u6728\u5c4b\u5750\u4e86\u4e00\u591c\u7684\u5f20\u606a\u770b\u5230\u6e05\u6668\u7684\u592a\u9633\u722c\u4e0a\u4e1c\u8fb9\u6e56\u7554\u6a61\u6811\u679d\u68a2\u4e4b\u540e\uff0c\u6253\u7b97\u7ed9\u81ea\u5df1\u716e\u4e00\u676f\u5496\u5561\u586b\u4e00\u586b\u809a\u5b50\u4e4b\u65f6\uff0c\u63a5\u5230\u5c3c\u514b\u674e\u68ee\u5728\u9152\u5e97\u6253\u6765\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5c31\u5728\u6253\u7535\u8bdd\u524d\u5341\u51e0\u5206\u949f\u8bb8\u591a\u5370\u5c3c\u4eba\u7a81\u7136\u6d8c\u8fdb\u5c3c\u514b\u674e\u68ee\u5165\u4f4f\u7684\u9152\u5e97\u524d\u7684\u4e00\u5bb6\u5927\u578b\u8d2d\u7269\u4e2d\u5fc3\u91cc\u62a2\u52ab\u3001\u5bf9\u5e97\u91cc\u7684\u534e\u4eba\u5e97\u5458\u4e0e\u987e\u5ba2\u5927\u6253\u51fa\u624b\uff0c\u542c\u8bf4\u8fd8\u5728\u8d2d\u7269\u4e2d\u5fc3\u516c\u7136\u5f3a\u5978\u4e86\u534e\u4eba\u5987\u5973\uff0c\u6700\u540e\u79bb\u5f00\u65f6\u7eb5\u706b\u711a\u70e7\u4e86\u8fd9\u5bb6\u8d2d\u7269\u4e2d\u5fc3\u3002</p><p>\u3000\u3000\u5728\u524d\u5c18\u5f80\u4e16\u65f6\uff0c\u5f20\u606a\u662f\u4e8b\u9694\u4e00\u5e74\u4e4b\u540e\u624d\u770b\u5230\u4e5d\u516b\u5e74\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u7684\u62a5\u9053\uff0c\u90a3\u65f6\u5df2\u7ecf\u662f\u51fa\u79bb\u60b2\u6124\u4e86\uff0c\u6b64\u65f6\u7684\u81ea\u5df1\u5374\u8981\u4ee5\u8fd9\u6837\u7684\u5f62\u5f0f\u4eb2\u5386\u4e00\u904d\u5370\u5c3c\u6392\u534e\u7684\u66b4\u884c\uff0c\u6ecb\u5473\u66f4\u4e0d\u597d\u53d7\uff01\u4e3a\u4e86\u8ba9\u81ea\u5df1\u597d\u53d7\u4e00\u4e9b\uff0c\u6216\u8005\u80fd\u51b7\u9759\u7684\u505a\u51fa\u4e9b\u5224\u65ad\uff0c\u5f20\u606a\u53ea\u5728\u7535\u8bdd\u542c\u8bed\u8a00\u4e0a\u7684\u63cf\u8ff0\uff0c\u800c\u6ca1\u6709\u53bb\u770b\u90a3\u4e9b\u4f20\u56de\u6765\u7684\u65b0\u95fb\u56fe\u7247\u3002</p><p>\u3000\u3000\u9a9a\u4e71\u51e0\u4e4e\u540c\u65f6\u5728\u96c5\u52a0\u8fbe\u7684\u4e3b\u8981\u8857\u533a\u7206\u53d1\uff0c\u96c5\u52a0\u8fbe\u9aa4\u7136\u95f4\u9677\u5165\u6df7\u4e71\u4e4b\u4e2d\u3002\u7531\u4e8e\u9a9a\u4e71\u521a\u521a\u53d1\u751f\uff0c\u8fd8\u6ca1\u6709\u8db3\u591f\u7684\u8bc1\u636e\u80fd\u8bc1\u660e\u8fd9\u662f\u4e00\u8d77\u9488\u5bf9\u534e\u4eba\u7684\u8840\u8165\u66b4\u884c\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u5728\u5348\u95f4\u65b0\u95fb\u91cc\u751a\u81f3\u8fd8\u64ad\u62a5\u4e86\u5370\u5c3c\u9a9a\u4e71\u4e8b\u4ef6\u3002</p><p>\u3000\u3000\u4e00\u76f4\u523010\u65e5\u4e0b\u5348\uff0c\u5927\u91cf\u4ece\u96c5\u52a0\u8fbe\u53ca\u5370\u5c3c\u5176\u4ed6\u5730\u533a\u53ca\u65f6\u4f20\u51fa\u7684\u65b0\u95fb\u8d44\u6599\u901a\u8fc7\u65b0\u52a0\u5761\u4f20\u51fa\u6765\u90fd\u5df2\u8bc1\u5b9e\u5370\u5c3c\u534e\u4eba\u662f\u8fd9\u573a\u8840\u8165\u66b4\u4e71\u7684\u4e3b\u8981\u76ee\u6807\u3002</p><p>\u3000\u3000\u5bf9\u534e\u4eba\u7684\u62a2\u52ab\u548c\u5bf9\u534e\u4eba\u5987\u5973\u7684\u5f3a\u5978\u7b49\u66b4\u884c\u5df2\u7ecf\u904d\u5e03\u53d1\u751f\u5728\u96c5\u52a0\u8fbe\u7684\u6bcf\u4e00\u4e2a\u89d2\u843d\uff0c\u4ece\u65e9\u6668\u5230\u4e0b\u5348\u5df2\u7ecf\u6709\u8fd1\u767e\u8d77\u66b4\u5f92\u5f3a\u5978\u6216\u8f6e\u5978\u534e\u88d4\u5987\u7684\u60e8\u6848\u53d1\u751f\u5728\u96c5\u52a0\u8fbe\u7684\u8857\u5934\uff0c\u96c5\u52a0\u8fbe\u5546\u4e1a\u533a\u51e0\u4e4e\u6240\u6709\u8f83\u8457\u540d\u7684\u534e\u5546\u8d2d\u7269\u4e2d\u5fc3\u90fd\u88ab\u66b4\u5f92\u7eb5\u706b\u6bc1\u635f\uff0c\u88ab\u6363\u6bc1\u7684\u5546\u4e1a\u5efa\u7b51\u3001\u6c11\u623f\u3001\u5546\u5e97\u66f4\u662f\u4e0d\u8ba1\u5176\u6570\uff0c\u6ee1\u8857\u90fd\u662f\u71c3\u70e7\u7684\u5efa\u7b51\u4e0e\u6c7d\u8f66\uff0c\u5230\u5904\u6d53\u70df\u6eda\u6eda\uff0c\u6574\u4e2a\u96c5\u52a0\u8fbe\u5730\u533a\u5374\u770b\u4e0d\u5230\u6709\u4efb\u4f55\u7684\u8b66\u5bdf\u4e0e\u519b\u961f\u51fa\u73b0\u2014\u2014\u8b66\u5bdf\u4e0e\u519b\u961f\u5728\u8896\u624b\u65c1\u89c2\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u624d\u6709\u4eba\u9661\u7136\u610f\u8bc6\u5230\u5370\u5c3c\u9a9a\u4e71\u7684\u6027\u8d28\u6076\u52a3\u4e0e\u4e25\u91cd\u3002</p><p>\u3000\u3000\u4e0b\u5348\u4e94\u70b9\u949f\uff0c\u5f20\u606a\u63a5\u5230\u9a6c\u5411\u4e1c\u5728\u5317\u4eac\u6253\u6765\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u6606\u817e\u5728\u7ebf\u63a5\u5230\u6709\u5173\u90e8\u95e8\u7684\u4f20\u771f\u901a\u77e5\uff0c\u8981\u6c42\u5220\u9664\u4e00\u5207\u5173\u4e8e\u5370\u5c3c\u9a9a\u4e71\u7684\u62a5\u9053\uff0c\u653f\u5e9c\u5df2\u7ecf\u51b3\u5b9a\u5728\u56fd\u5185\u5bf9\u6b64\u6b21\u4e8b\u4ef6\u8fdb\u884c\u51b7\u5904\u7406\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u9a6c\u5411\u4e1c\u6253\u7535\u8bdd\u6765\u7684\u610f\u601d\uff0c\u4ed6\u8fd9\u65f6\u5019\u53ea\u80fd\u4ee5\u51b7\u9759\u3001\u4f24\u4eba\u5fc3\u7684\u8bed\u6c14\u544a\u8bc9\u9a6c\u5411\u4e1c\uff1a\u201c\u56fd\u5185\u7f51\u7ad9\u4e0a\u7684\u62a5\u9053\u90fd\u5220\u5e72\u51c0\uff0c\u653f\u5e9c\u81ea\u6709\u653f\u5e9c\u7684\u8003\u91cf\uff0c\u4e0d\u662f\u6211\u4eec\u80fd\u63e3\u6d4b\u7684\u3002\u201d</p><p>\u3000\u3000\u4e2d\u56fd\u4e0e\u5370\u5c3c\u5728\u5370\u5c3c\u534e\u4eba\u95ee\u9898\u5386\u6765\u654f\u611f\uff0c\u800c\u4e5d\u5341\u5e74\u4ee3\u672b\u671f\uff0c\u4e2d\u56fd\u5728\u7f05\u7538\u3001\u6cf0\u56fd\u7684\u519b\u4e8b\u6218\u7565\u5e03\u5c40\u4ecd\u672a\u5b8c\u6210\uff0c\u9a6c\u516d\u7532\u6d77\u5ce1\u4f5c\u4e3a\u4e2d\u56fd\u6d77\u5916\u77f3\u6cb9\u8fd0\u8f93\u7684\u5fc5\u7ecf\u822a\u9053\uff0c\u5176\u4e00\u7aef\u63a7\u5236\u5728\u5370\u5c3c\u624b\u4e2d\u3002\u56fd\u5185\u7684\u6781\u5176\u6e29\u548c\u5916\u4ea4\u6001\u5ea6\u4f3c\u4e4e\u80fd\u591f\u7406\u89e3\uff0c\u5173\u952e\u8fd8\u662f\u56fd\u5bb6\u7684\u5e95\u6c14\u4e0d\u591f\u5f3a\u786c\uff0c\u5728\u4e2d\u56fd\u5d1b\u8d77\u7684\u8fc7\u7a0b\u4e2d\uff0c\u8fd8\u6709\u65e0\u6570\u50cf\u5468\u747e\u73ba\u8fd9\u6837\u7684\u4eba\u7269\u4e3a\u4e86\u4e00\u5df1\u4e4b\u79c1\u5229\u800c\u80cc\u53db\u56fd\u5bb6\u7684\u5229\u76ca\u3002</p><p>\u3000\u3000\u5f20\u606a\u73b0\u5728\u53ea\u80fd\u5bc4\u5e0c\u671b\u5927\u91cf\u7684\u65b0\u95fb\u8d44\u6599\u80fd\u591f\u53ca\u65f6\u4ece\u5370\u5c3c\u4f20\u64ad\u51fa\u53bb\u901a\u8fc7\u56fd\u9645\u5a92\u4f53\u4e88\u4ee5\u62ab\u9732\u5f15\u8d77\u56fd\u9645\u793e\u4f1a\u7684\u5e7f\u6cdb\u5173\u6ce8\uff0c\u54ea\u6015\u63d0\u524d\u4e00\u4e2a\u5c0f\u65f6\u3001\u63d0\u524d\u4e00\u523b\u949f\u5236\u6b62\u5370\u5c3c\u7684\u6392\u534e\u9a9a\u4e71\uff0c\u8fd9\u4e00\u5207\u7684\u52aa\u529b\u90fd\u662f\u503c\u5f97\u7684\u3002</p><p>\u3000\u3000\u53ea\u662f\u5f88\u53ef\u60dc\uff0c\u6606\u817e\u5728\u7ebf\u7684\u6d77\u5916\u7f51\u7ad9\u624d\u521a\u521a\u5efa\u8bbe\uff0c\u5728\u6d77\u5916\u534e\u4eba\u7fa4\u4f53\u91cc\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u5f71\u54cd\uff0c\u5f20\u606a\u6b64\u65f6\u5c24\u5176\u80fd\u6df1\u523b\u7684\u611f\u53d7\u5230\u4f20\u5a92\u7f51\u7edc\u5728\u5f53\u4ee3\u793e\u4f1a\u4f53\u7cfb\u91cc\u6df1\u5165\u9aa8\u9ad3\u7684\u6743\u529b\uff0c\u4ed6\u73b0\u5728\u80fd\u591f\u505a\u7684\u4e8b\u60c5\u8fd8\u6781\u4e3a\u6709\u9650\uff0c\u66f4\u591a\u7684\u662f\u770b\u7740\u90ed\u677e\u5ef6\u3001\u5b59\u5c1a\u4e49\u7b49\u4eba\u53bb\u8054\u7cfb\u6d77\u5916\u5a92\u4f53\u62ab\u9732\u6b63\u5728\u5370\u5c3c\u7206\u53d1\u7684\u6392\u534e\u66b4\u884c\uff0c\u7279\u522b\u662f\u8ba9\u6d77\u5916\u534e\u4eba\u7fa4\u4f53\u8fd9\u65f6\u5019\u56e2\u7ed3\u8d77\u6765\u963b\u6b62\u6b63\u5728\u5370\u5c3c\u7206\u53d1\u7684\u6392\u534e\u66b4\u884c\u3002</p><p>\u3000\u3000\u8fd1\u5728\u54ab\u5c3a\u7684\u65b0\u52a0\u5761\u5a92\u4f53\u4e0e\u653f\u5e9c\u662f\u53cd\u5e94\u6700\u4e3a\u53ca\u65f6\u7684\uff0c\u5728\u6606\u817e\u5728\u7ebf\u7684\u56fd\u5185\u7f51\u7ad9\u5220\u9664\u5bf9\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u7684\u62a5\u9053\u4e4b\u540e\uff0c\u5f20\u606a\u5927\u591a\u6570\u65f6\u95f4\u5c31\u662f\u901a\u8fc7\u65b0\u52a0\u5761\u56fd\u9645\u5728\u7ebf\u7684\u7f51\u7ad9\u5173\u6ce8\u96c5\u52a0\u8fbe\u5c40\u52bf\u7684\u53d8\u5316\uff0c\u5f53\u7136\u4e5f\u6709\u5c3c\u514b\u674e\u68ee\u8eab\u5904\u5176\u5883\u6216\u90ed\u677e\u5ef6\u5728\u9a6c\u6765\u897f\u4e9a\u6253\u6765\u7684\u7535\u8bdd\u901a\u62a5\uff0c\u9a6c\u6765\u897f\u4e9a\u7684\u534e\u4eba\u5a92\u4f53\u4e5f\u53ca\u65f6\u62a5\u9053\u53d1\u751f\u5728\u5370\u5c3c\u7684\u66b4\u884c\u3002\u53ea\u662f\u65b0\u52a0\u5761\u4e0e\u9a6c\u4e9a\u897f\u4e9a\u7684\u534e\u4eba\u5a92\u4f53\u5f71\u54cd\u529b\u4f9d\u65e7\u6709\u9650\u3002</p><p>\u3000\u3000\u65b0\u52a0\u5761\u653f\u5e9c\u5728\u5341\u65e5\u4e0b\u5348\u4e24\u70b9\u5c31\u51b3\u5b9a\u4e8c\u5341\u56db\u5c0f\u65f6\u5f00\u653e\u7ae0\u5b9c\u56fd\u9645\u673a\u573a\u63a5\u6536\u4ece\u96c5\u52a0\u8fbe\u53ca\u5370\u5c3c\u5176\u4ed6\u5730\u533a\u9003\u96be\u51fa\u6765\u7684\u534e\u4eba\u534e\u4fa8\uff0c\u800c\u7f8e\u65e5\u7b49\u4eba\u4e00\u76f4\u62d6\u5ef6\u5230\u5341\u65e5\u591c\u95f4\u770b\u5230\u96c5\u52a0\u8fbe\u5c40\u52bf\u6301\u7eed\u6076\u5316\u65e0\u6cd5\u63a7\u5236\u7684\u60c5\u51b5\u624d\u505a\u51fa\u64a4\u4fa8\u7684\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u66b4\u884c\u5728\u591c\u8272\u7684\u63a9\u62a4\u4e4b\u4e0b\u53d8\u5f97\u66f4\u52a0\u7684\u8086\u65e0\u5fcc\u60ee\u4e0e\u8840\u8165\uff0c\u771f\u6b63\u6709\u5f71\u54cd\u7684\u5bf9\u5370\u5c3c\u6392\u534e\u66b4\u884c\u7684\u62ab\u9732\u662f\u7ebd\u7ea6\u65f6\u62a5\u5728\u5341\u4e00\u65e5\u4e0a\u5348\u7684\u6574\u7248\u520a\u8f7d\uff0c\u7ebd\u7ea6\u65f6\u62a5\u7684\u62a5\u9053\u4f7f\u5f97\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u4e8b\u4ef6\u8fc5\u901f\u5728\u5168\u7403\u5e7f\u4e3a\u4f20\u64ad\uff0c\u66f4\u591a\u7684\u56fd\u9645\u5a92\u4f53\u5728\u770b\u5230\u7ebd\u7ea6\u65f6\u62a5\u7684\u62a5\u9053\u4e4b\u540e\u624d\u6d3e\u51fa\u8bb0\u8005\u6d8c\u5411\u96c5\u52a0\u8fbe\u3002</p><p>\u3000\u3000\u4e00\u76f4\u5230\u5341\u4e8c\u65e5\u7684\u51cc\u6668\uff0c\u5728\u56fd\u9645\u8206\u8bba\u7684\u538b\u529b\u4e0b\uff0c\u96c5\u52a0\u8fbe\u53ca\u5370\u5c3c\u5176\u4ed6\u5730\u533a\u7684\u8b66\u65b9\u5c31\u50cf\u624d\u63a5\u5230\u62a5\u8b66\u4f3c\u7684\u6d3e\u51fa\u8b66\u5458\u51fa\u73b0\u5728\u8857\u5934\u4e0a\u3002\u5373\u4f7f\u5982\u6b64\uff0c\u96c5\u52a0\u8fbe\u8857\u9053\u7684\u89d2\u843d\u91cc\u4ee5\u53ca\u5370\u5c3c\u7684\u8fb9\u8fdc\u5730\u533a\u4ecd\u7136\u8086\u65e0\u5fcc\u60ee\u7684\u5b9e\u65bd\u7740\u9488\u5bf9\u534e\u4eba\u7684\u66b4\u884c\u3002</p><p>\u3000\u3000\u5927\u89c4\u6a21\u7684\u6392\u534e\u66b4\u52a8\u6574\u6574\u65bd\u884c\u4e86\u56db\u5341\u4e2a\u5c0f\u65f6\uff0c\u867d\u7136\u6bd4\u66fe\u7ecf\u53d1\u751f\u7684\u5370\u5c3c\u6392\u534e\u5927\u9a9a\u4e71\u7f29\u77ed\u4e86\u8fd1\u4e00\u534a\u7684\u65f6\u95f4\uff0c\u4f46\u662f\u4f9d\u65e7\u6709\u6570\u5343\u5370\u5c3c\u534e\u4eba\u9677\u5165\u5669\u68a6\u822c\u7684\u51c4\u60e8\u5883\u5730\u3002</p><p>\u3000\u3000\u5370\u5c3c\u653f\u5e9c\u4e5f\u5728\u6b64\u65f6\u624d\u9661\u7136\u60f3\u8d77\u9700\u8981\u906e\u7f9e\u5e03\u63a9\u76d6\u4ed6\u4eec\u7684\u4e11\u964b\u9762\u76ee\uff0c\u5f00\u59cb\u9650\u5236\u65b0\u95fb\u6e20\u9053\u4e0e\u4e92\u8054\u7f51\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u660e\u767d\u4e3a\u4ec0\u4e48\u5728\u4e8b\u60c5\u7206\u53d1\u4e4b\u521d\u5c31\u6709\u5927\u91cf\u7684\u65b0\u95fb\u8d44\u6599\u53ca\u65f6\u4f20\u9001\u5230\u56fd\u5916\u53bb\u3002</p><p>\u3000\u3000\u5927\u6982\u6ca1\u6709\u4e00\u4e2a\u6bd4\u5370\u5c3c\u66f4\u80fd\u5f15\u8d77\u5168\u4e16\u754c\u534e\u4eba\u618e\u6068\u7684\u56fd\u5bb6\u4e86\uff0c\u5f20\u606a\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u4e5f\u59cb\u7ec8\u8fd9\u4e48\u8ba4\u4e3a\u7740\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5341\u4e8c\u65e5\u591c\u95f4\uff0c\u5370\u5c3c\u5927\u89c4\u6a21\u7684\u6392\u534e\u9a9a\u4e71\u5df2\u7ecf\u57fa\u672c\u5e73\u606f\u4e0b\u6765\uff0c\u4f46\u662f\u5c0f\u89c4\u6a21\u7684\u9488\u5bf9\u534e\u4eba\u7684\u66b4\u529b\u72af\u7f6a\u8fd8\u6ca1\u6709\u5f97\u5230\u5b8c\u5168\u7684\u904f\u5236\uff0c\u968f\u7740\u5927\u91cf\u7684\u56fd\u9645\u5a92\u4f53\u8bb0\u8005\u4e0e\u56fd\u9645\u4eba\u6743\u7ec4\u7ec7\u89c2\u5bdf\u5458\u7684\u6d8c\u5165\uff0c\u60c5\u51b5\u4f1a\u8fdb\u4e00\u6b65\u5f97\u5230\u63a7\u5236\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u51fa\u6e56\u7554\u5c0f\u5c4b\u6709\u7740\u8981\u865a\u8131\u7684\u611f\u89c9\uff0c\u5750\u5728\u6e56\u8fb9\u6a61\u6811\u4e0b\u7684\u957f\u6905\u4e0a\uff0c\u770b\u7740\u95ea\u7740\u5e7d\u6697\u6ce2\u5149\u7684\u6e56\u6c34\uff0c\u56de\u60f3\u7740\u8fc7\u53bb\u51e0\u5929\u867d\u7136\u8c08\u4e0d\u4e0a\u4eb2\u5386\u7684\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\uff0c\u5634\u95f4\u53ea\u89c9\u5f97\u82e6\u6da9\u3002</p><p>\u3000\u3000\u867d\u7136\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u5728\u6d77\u5916\u534e\u4eba\u793e\u533a\u5df2\u7ecf\u5f15\u8d77\u5f3a\u70c8\u7684\u6124\u6168\uff0c\u6606\u817e\u5728\u7ebf\u5728\u9999\u6e2f\u7684\u955c\u50cf\u7f51\u7ad9\u4e0e\u660e\u62a5\u7b49\u9999\u6e2f\u5a92\u4f53\u5168\u7a0b\u64ad\u62a5\u4e86\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u4e8b\u4ef6\uff0c\u5f15\u8d77\u9999\u6e2f\u6c11\u4f17\u5f3a\u70c8\u7684\u6124\u6168\u3002\u5341\u4e00\u65e5\u4e0b\u5348\uff0c\u5370\u5c3c\u9a7b\u6e2f\u9886\u4e8b\u9986\u5c31\u88ab\u6124\u6012\u7684\u9999\u6e2f\u6c11\u4f17\u56f4\u8d77\u6765\u8fdb\u884c\u793a\u5a01\u6297\u8bae\uff0c\u9886\u4e8b\u9986\u7684\u5927\u95e8\u4e5f\u7ed9\u6cfc\u4e0a\u4e86\u9ed1\u6f06\uff0c\u66f4\u662f\u6709\u65e0\u6570\u7684\u9e21\u86cb\u4e0e\u7816\u5757\u5c06\u9886\u4e8b\u9986\u7684\u7a97\u73bb\u7483\u7838\u5f97\u7c89\u788e\uff0c\u9999\u6e2f\u8b66\u65b9\u4e0d\u5f97\u4e0d\u51fa\u52a8\u8b66\u529b\u5c06\u6124\u6012\u7684\u6c11\u4f17\u6321\u5728\u8b66\u6212\u7ebf\u4e4b\u5916\u3002</p><p>\u3000\u3000\u5185\u5730\u5f02\u5e38\u7684\u5e73\u9759\uff0c\u4f3c\u4e4e\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u6839\u672c\u5c31\u6ca1\u6709\u53d1\u751f\u8fc7\u4f3c\u7684\u3002</p><p>\u3000\u3000\u63a5\u5230\u5510\u5a67\u7684\u7535\u8bdd\uff0c\u4e2b\u5934\u5728\u7535\u8bdd\u90a3\u91cc\u8fd8\u662f\u4f24\u5fc3\u7684\u62bd\u6ce3\u3002\u5f88\u62b1\u6b49\u8ba9\u5979\u770b\u5230\u4e00\u4e2a\u771f\u5b9e\u7684\u3001\u6781\u7aef\u4e11\u964b\u7684\u4e16\u754c\uff0c\u5f20\u606a\u53ea\u80fd\u5728\u7535\u8bdd\u91cc\u8f7b\u58f0\u7684\u5b89\u6170\u5979\uff1a\u201c\u6211\u4eec\u73b0\u5728\u80fd\u591f\u505a\u7684\u4e8b\u60c5\u5f88\u6709\u9650\uff0c\u6211\u4eec\u7684\u653f\u5e9c\u6001\u5ea6\u4e5f\u4e0d\u4f1a\u6c38\u8fdc\u90fd\u8fd9\u4e48\u6e29\u548c\u3002\u5669\u68a6\u5f88\u5feb\u4f1a\u8fc7\u53bb\u7684\uff0c\u6211\u660e\u5929\u53bb\u9999\u6e2f\u770b\u4f60\u3002\u201d</p><p>\u3000\u3000\u5efa\u90ba\u7684\u6625\u5929\u5f88\u77ed\u6682\uff0c\u77ed\u6682\u5230\u51e0\u4e4e\u8ba9\u4eba\u89c9\u5bdf\u4e0d\u51fa\u6625\u5929\u7684\u5230\u6765\uff0c\u5728\u6625\u5bd2\u9661\u5ced\u7684\u5929\u6c14\u8fc7\u53bb\uff0c\u5efa\u90ba\u7684\u6c14\u6e29\u5c31\u9661\u7136\u5347\u6e29\u4e86\uff0c\u4e94\u6708\u4e2d\u65ec\u7684\u591c\u91cc\u5373\u4f7f\u53ea\u7a7f\u4ef6\u957f\u8896\u886c\u886b\u4e5f\u4e0d\u4f1a\u89c9\u5f97\u5bd2\u51b7\uff0c\u7531\u4e8e\u8fd9\u51e0\u5929\u90fd\u6ca1\u6709\u6b63\u5f0f\u7684\u7761\u4e0a\u4e00\u56de\uff0c\u5f20\u606a\u7684\u8eab\u4f53\u6709\u4e9b\u865a\uff0c\u7a7f\u7740\u8fd0\u52a8\u7f69\u886b\u8fd8\u89c9\u5f97\u6709\u4e9b\u51b7\u3002\u8d70\u5230\u5b66\u5e9c\u5df7\uff0c\u5df7\u5b50\u53e3\u6709\u4e00\u5ea7\u591c\u5bb5\u644a\u8f66\uff0c\u63d0\u4f9b\u70ed\u51b7\u996e\u4e0e\u68d5\u5b50\u3001\u716e\u7389\u7c73\u3001\u4e94\u9999\u86cb\u4e4b\u7c7b\u7684\u7b80\u4fbf\u98df\u7269\uff0c\u644a\u4e3b\u662f\u4e2a\u4e09\u5341\u591a\u5c81\u7684\u6e05\u79c0\u5973\u5b50\uff0c\u6709\u4e2a\u516d\u4e03\u5c81\u7684\u5973\u5a03\u5b50\u5e2e\u7740\u5356\u4e1c\u897f\uff0c\u4f3c\u4e4e\u662f\u6bcd\u5973\u2014\u2014\u4e4b\u524d\u5012\u6ca1\u6709\u770b\u5230\u8fc7\u3002</p><p>\u3000\u3000\u5f20\u606a\u90fd\u4e0d\u8bb0\u5f97\u81ea\u5df1\u4eca\u5929\u6709\u6ca1\u6709\u5403\u4e1c\u897f\uff0c\u5085\u4fca\u5012\u662f\u5c3d\u804c\u51c6\u5907\u597d\u4f19\u98df\uff0c\u53ea\u662f\u4eba\u5728\u5c4b\u5b50\u91cc\u6ca1\u6709\u5fc3\u60c5\u7528\u9910\uff0c\u8d70\u51fa\u6765\u8eab\u4f53\u865a\u5f97\u5389\u5bb3\uff0c\u624d\u60f3\u7740\u8981\u5403\u4e9b\u4e1c\u897f\uff0c\u4e0d\u7136\u4ec0\u4e48\u4e8b\u60c5\u90fd\u505a\u4e0d\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u4e86\u4e00\u53ea\u68d5\u5b50\u3001\u4e00\u676f\u5496\u5561\uff0c\u591c\u5bb5\u644a\u8f66\u7adf\u7136\u6709\u5496\u5561\u63d0\u4f9b\uff0c\u5f20\u606a\u8fd8\u662f\u89c9\u5f97\u6709\u4e9b\u610f\u5916\uff0c\u644a\u4e3b\u5c06\u82a6\u53f6\u5265\u6389\u624d\u5c06\u68d5\u5b50\u62ff\u5c0f\u789f\u76db\u7740\u7aef\u7ed9\u5f20\u606a\u3002\u5c06\u68d5\u5b50\u4e0e\u5496\u5561\u586b\u4e0b\u809a\u5b50\uff0c\u5f20\u606a\u624d\u89c9\u5f97\u8eab\u4f53\u6709\u4e00\u79cd\u56de\u8fc7\u6765\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u201c\u591a\u5c11\u94b1\uff1f\u201d\u5f20\u606a\u95ee\u644a\u4e3b\uff0c\u624b\u4f38\u8fdb\u515c\u91cc\u624d\u53d1\u73b0\u6839\u672c\u5c31\u6ca1\u8bb0\u5f97\u62ff\u76ae\u5939\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u68d5\u5b50\u4e00\u5143\u3001\u5496\u5561\u4e09\u5143\uff0c\u4e00\u5171\u56db\u5143\u94b1\u2026\u2026\u201d\u644a\u4e3b\u5fd9\u7740\u62db\u547c\u522b\u4eba\uff0c\u6ca1\u6709\u6ce8\u610f\u5230\u5f20\u606a\u7684\u7a98\u8feb\uff0c\u8fd8\u662f\u7ed9\u90a3\u4e2a\u5c0f\u5973\u5b69\u5b50\u770b\u5230\u5f20\u606a\u7684\u6a21\u6837\u626f\u4e86\u626f\u5979\u7684\u8896\u7ba1\u624d\u660e\u767d\u8fc7\u6765\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u6ca1\u6709\u5e26\u94b1\u5728\u8eab\u4e0a\u662f\u5427\uff1f\u4e0d\u6253\u7d27\uff0c\u4ec0\u4e48\u65f6\u5019\u8bb0\u5f97\u9001\u8fc7\u6765\u5c31\u884c\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u54b3\u55fd\u4e86\u4e24\u58f0\uff0c\u9053\u4e86\u58f0\u8c22\uff0c\u5c31\u544a\u8f9e\u79bb\u5f00\uff0c\u8d70\u4e0d\u8fdc\u53c8\u54b3\u55fd\u8d77\u6765\u3002\u5012\u4e0d\u662f\u5c34\u5c2c\uff0c\u800c\u662f\u4ed6\u5bf9\u5efa\u90ba\u6625\u5929\u7684\u68a7\u6850\u98de\u7d6e\u6709\u4e9b\u8fc7\u654f\u3002\u4ece\u56db\u6708\u4e2d\u65ec\u5f80\u540e\u7684\u4e00\u4e2a\u6708\u65f6\u95f4\u91cc\uff0c\u5f20\u606a\u603b\u4f1a\u8fde\u7eed\u7684\u54b3\u55fd\u597d\u4e9b\u5929\uff0c\u836f\u7269\u6cbb\u7597\u7684\u6548\u679c\u6781\u5fae\uff0c\u53c8\u6ca1\u6709\u4e25\u91cd\u5230\u8981\u52a8\u5927\u624b\u672f\u7684\u7a0b\u5ea6\u3002\u5f20\u606a\u5bf9\u81ea\u5df1\u7684\u8fd9\u79cd\u60c5\u51b5\u5f88\u4e86\u89e3\uff0c\u533b\u751f\u5f00\u7684\u836f\u5076\u5c14\u4f1a\u5e94\u666f\u7684\u670d\u7528\u4e00\u4e24\u7c92\uff0c\u5927\u591a\u65f6\u5019\u4f1a\u9009\u62e9\u5c06\u5403\u836f\u8fd9\u4ef6\u4e8b\u7ed9\u5fd8\u6389\u3002\u5728\u7fdf\u4e39\u9752\u3001\u5b59\u9759\u6aac\u7b49\u4eba\u770b\u6765\uff0c\u5f20\u606a\u4f3c\u4e4e\u5bf9\u836f\u6709\u5fc3\u7406\u4e0a\u7684\u8fc7\u654f\uff0c\u6216\u8bb8\u6709\u7740\u8bb3\u75be\u5fcc\u533b\u7684\u987d\u56fa\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u7684\u5916\u8857\u79cd\u690d\u4e86\u56db\u6392\u9ad8\u5927\u7684\u6cd5\u56fd\u68a7\u6850\uff0c\u5f20\u606a\u8d70\u8fdb\u8fd9\u91cc\u81ea\u7136\u4e0d\u4f1a\u6709\u7279\u522b\u597d\u7684\u611f\u53d7\uff0c\u53c8\u4e0d\u60f3\u88c5\u6df1\u6c89\u6234\u4e0a\u53e3\u7f69\uff0c\u8ba9\u4eba\u8bef\u4f1a\u662f\u6253\u52ab\u5c31\u9ebb\u70e6\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u54b3\u55fd\u7740\u8d70\u8fdb\u7f51\u5427\uff0c\u770b\u7740\u6c88\u7b71\u5728\u7efc\u5408\u5427\u53f0\u524d\uff0c\u8ddf\u5979\u8bf4\uff1a\u201c\u5e2e\u6211\u5f00\u4e00\u53f0\u7535\u8111\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u8981\u5728\u8fd9\u91cc\u4e0a\u7f51\uff1f\u201d\u6c88\u7b71\u5947\u602a\u7684\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4e0d\u77e5\u9053\u600e\u4e48\u4e86\uff0c\u6211\u5728\u516c\u5bd3\u91cc\u4e0a\u6821\u56ed\u7f51\u592a\u6162\u2026\u2026\u6211\u8981\u4e0aBBS\uff0c\u201d\u5f20\u606a\u6307\u7740\u4e00\u697c\u7684\u7a7a\u673a\u4f4d\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5750\u90a3\u91cc\u5c31\u884c\u3002\u201d</p><p>\u3000\u3000\u4e1c\u5927BBS\u7684\u670d\u52a1\u5668\u5c31\u67b6\u8bbe\u5728\u521b\u57df\u7f51\u5427\u5185\uff0c\u5728\u7f51\u5427\u91cc\u53ef\u4ee5\u76f4\u63a5\u8fde\u4e0a\u4e1c\u5927BBS\u7684\u670d\u52a1\u5668\u3002</p><p>\u3000\u3000\u6c88\u7b71\u62ff\u51fa\u5458\u5de5\u5361\u7ed9\u5f20\u606a\u6253\u5f00\u4e00\u53f0\u7535\u8111\uff0c\u5f20\u606a\u5750\u4e0b\u6765\uff0c\u62ff\u51fa\u624b\u638c\u5927\u5c0f\u7684\u95ea\u5b58\u76d8\uff0c\u8fd9\u73a9\u827a\u513f\u8c01\u770b\u4e86\u90fd\u4e0d\u8ba4\u8bc6\uff0c\u5f20\u606a\u8fd8\u8981\u5c06\u673a\u7bb1\u53cd\u8f6c\u8fc7\u6765\u5c06\u8f6c\u63a5\u7ebf\u63d2\u5728\u540e\u9762\u7684\u63a5\u53e3\u4e0a\uff0c\u5728\u7b49\u95ea\u5b58\u81ea\u5b89\u88c5\u9a71\u52a8\u7a0b\u5e8f\u7684\u65f6\u95f4\uff0c\u6253\u5f00\u4e1c\u5927\u6821\u56ed\u7f51\u7684\u8bba\u575b\u3002</p><p>\u3000\u3000BBS\u679c\u7136\u4e00\u7247\u5b81\u9759\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u4e0e\u6606\u817e\u5728\u7ebf\u7b49\u5c11\u6570\u5a92\u4f53\u90fd\u53ea\u520a\u8f7d\u4e86\u5370\u5c3c\u53d1\u751f\u9a9a\u4e71\u7684\u65b0\u95fb\uff0c\u5e76\u6ca1\u6709\u62a5\u9053\u9a9a\u4e71\u662f\u9488\u5bf9\u5370\u5c3c\u534e\u4eba\uff0c\u5341\u65e5\u4e0b\u5348\u4e3b\u6d41\u5a92\u4f53\u53c8\u96c6\u4f53\u7684\u5931\u58f0\uff0c\u6d77\u5916\u7684\u6d88\u606f\u8fd8\u6ca1\u6709\u53ca\u65f6\u4f20\u5230\u56fd\u5185\u6765\uff0c\u666e\u901a\u6c11\u4f17\u53ca\u5b66\u751f\u6839\u672c\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002\u767b\u5f55\u597d\u4e45\u4e4b\u524d\u6ce8\u518c\u5374\u4e00\u76f4\u90fd\u6ca1\u6709\u4f7f\u7528\u7684\u8d26\u53f7\uff0c\u5728\u65b0\u5f00\u7684\u5e16\u5b50\u6807\u9898\u4e00\u680f\u5f55\u5165\u201c5.9\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u59cb\u672b\u201d\u7684\u6587\u5b57\uff0c\u7136\u540e\u5c06\u95ea\u5b58\u76d8\u91cc\u7684\u65b0\u95fb\u8d44\u6599\u9010\u4e00\u4ee5\u56de\u5e16\u7684\u5f62\u5f0f\u53d1\u5e03\u5230\u4e1c\u5927BBS\u7684\u8bba\u575b\u4e0a\u3002</p><p>\u3000\u3000\u53d1\u5e03\u5b8c\u4e4b\u540e\uff0c\u5f20\u606a\u5c06\u7535\u8111\u5173\u4e0a\uff0c\u8ddf\u6c88\u7b71\u62db\u547c\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u56de\u53bb\u7761\u89c9\u4e86\uff0c\u675c\u98de\u4ed6\u4eec\u8981\u662f\u60f3\u627e\u6211\uff0c\u5c31\u8bf4\u6211\u56de\u53bb\u7761\u89c9\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u5728\u65e0\u68a6\u7684\u6c89\u7761\u4e2d\u7ed9\u624b\u673a\u94c3\u58f0\u5435\u9192\uff0c\u7741\u5f00\u773c\u775b\uff0c\u592a\u9633\u5149\u4ece\u73bb\u7483\u7a97\u900f\u5c04\u8fdb\u6765\uff0c\u770b\u5149\u7ebf\u7684\u89d2\u5ea6\u5df2\u7ecf\u4e0d\u65e9\u4e86\uff0c\u4ece\u6795\u4e0b\u6478\u51fa\u624b\u673a\uff0c\u662f\u675c\u98de\u6253\u6765\u7684\u7535\u8bdd\uff0c\u63a5\u901a\u540e\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u8bf4\u4e86\u6211\u5728\u7761\u89c9\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u5916\u9762\u90fd\u95f9\u7ffb\u5929\u4e86\uff0c\u5b66\u6821\u4e00\u5927\u65e9\u5c31\u7535\u8bdd\u901a\u77e5\u8981\u6c42\u65bd\u65b0\u98de\u4ed6\u4eec\u5220\u9664\u4e00\u5207\u5173\u4e8e\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u7684\u5e16\u5b50\uff0c\u7ed9\u65bd\u65b0\u98de\u4ed6\u4eec\u9876\u4e86\u56de\u53bb\uff0c\u5b66\u5de5\u5904\u7684\u5904\u957f\u6c88\u5065\u8fd8\u6709\u56e2\u59d4\u7684\u5bab\u5982\u6625\u90fd\u4eb2\u81ea\u8dd1\u5230\u7f51\u5427\u53bb\u4e86\uff0c\u8fd8\u8981\u6c42\u65bd\u65b0\u98de\u4ed6\u4eec\u8ffd\u67e5\u8c01\u6700\u521d\u4e0a\u4f20\u4e86\u5e16\u5b50\u2026\u2026\u201d\u675c\u98de\u5728\u7535\u8bdd\u91cc\u8bf4\u9053\uff0c\u201c\u4f60\u770b\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u4e00\u4f1a\u513f\u8fc7\u6765\u3002\u201d\u5f20\u606a\u5148\u6302\u4e0a\u7535\u8bdd\u6d17\u6f31\u8fc7\u624d\u4e0b\u4e86\u697c\u3002</p><p>\u3000\u3000\u4ece\u6765\u90fd\u6ca1\u6709\u89c1\u8fc7\u7f51\u5427\u5468\u4e8c\u7684\u4e0a\u5348\u4f1a\u6709\u8fd9\u4e48\u591a\u4eba\uff0c\u4ec0\u4e48\u65f6\u5019\u90fd\u6ca1\u6709\uff0c\u6bcf\u4e00\u53f0\u7535\u8111\u524d\u90fd\u56f4\u6ee1\u4e86\u4eba\uff0c\u770b\u7740\u90a3\u4e9b\u56f4\u5728\u7535\u8111\u524d\u76ef\u7740\u5c4f\u5e55\u7684\u5b66\u751f\u7684\u8868\u60c5\uff0c\u8ddf\u524d\u5c18\u5f80\u4e16\u91cc\u7684\u81ea\u5df1\u521d\u770b\u5370\u5c3c\u6392\u534e\u4e8b\u4ef6\u65f6\u6beb\u65e0\u4e8c\u81f4\uff0c\u7ed9\u53d1\u751f\u5728\u5370\u5c3c\u8fd9\u6b21\u79cd\u65cf\u6b67\u89c6\u7684\u6b8b\u9177\u66b4\u884c\u6df1\u6df1\u7684\u9707\u60ca\u3001\u6fc0\u6012\u4e86\u3002\u5f20\u606a\u7ad9\u5728\u7f51\u5427\u900f\u660e\u7684\u843d\u5730\u7a97\u524d\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u4ece\u5916\u4fa7\u7684\u697c\u68af\u4e0a\u4e86\u4e09\u697c\u3002</p><p>\u3000\u3000\u6821\u56edBBS\u6700\u521d\u662f\u866b\u4ff1\u4e50\u90e8\u7684\u6210\u5458\u67b6\u8bbe\u7684\uff0c\u6821\u65b9\u4e5f\u51b3\u5b9a\u5f52\u866b\u4ff1\u4e50\u90e8\u7ba1\u7406\uff0c\u540e\u6765\u6210\u7acb\u521b\u57df\u7f51\u5427\u91cc\uff0c\u5c06BBS\u7684\u670d\u52a1\u5668\u67b6\u8bbe\u5728\u7f51\u5427\u5185\u90e8\uff0c\u866b\u4ff1\u4e50\u90e8\u867d\u7136\u89e3\u6563\u4e86\uff0c\u4f46\u662f\u53c8\u91cd\u65b0\u7ec4\u5efa\u4e86\u866b\u5b50\u5929\u7a7a\u5de5\u4f5c\u5ba4\uff0c\u8fd8\u662f\u7531\u65bd\u65b0\u98de\u4ed6\u4eec\u4e49\u52a1\u7ba1\u7406\u4e1c\u5927BBS\u3002</p><p>\u3000\u3000\u8d70\u5230\u4e09\u697c\uff0c\u5c31\u542c\u89c1\u4e00\u4e2a\u542c\u4e0a\u53bb\u5f88\u523a\u8033\u7684\u58f0\u97f3\u5728\u91cc\u9762\u5486\u54ee\uff1a\u201c\u4f60\u4eec\u8fd9\u662f\u81ea\u6bc1\u524d\u7a0b\uff0c\u4ec0\u4e48\u65f6\u5019\u8fd9\u4e48\u6ca1\u7ec4\u7ec7\u6ca1\u7eaa\u5f8b\uff1f\u6211\u8981\u4f60\u4eec\u5220\uff0c\u4f60\u4eec\u5c31\u8981\u8001\u8001\u5b9e\u5b9e\u7684\u7ed9\u5220\u6389\uff0c\u8fd8\u6709\u662f\u8c01\u53d1\u7684\u7b2c\u4e00\u4e2a\u5e16\u5b50\uff0c\u4f60\u4eec\u4e5f\u8981\u5c06\u53d1\u5e16\u4eba\u7684\u8d44\u6599\u7ed9\u6211\uff0c\u7b49\u5019\u5b66\u6821\u7684\u5904\u7406\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u9634\u7740\u8138\u63a8\u5f00\u95e8\uff0c\u5b66\u5de5\u5904\u5904\u957f\u6c88\u5065\u6b63\u7ad9\u5728\u5927\u529e\u516c\u5ba4\u91cc\u4e2d\u592e\u6c14\u52bf\u51cc\u4eba\u7684\u5486\u54ee\uff0c\u5bab\u5982\u6625\u7ad9\u5728\u4e00\u65c1\u9634\u6c89\u7740\u8138\u6ca1\u6709\u542d\u58f0\uff0c\u675c\u98de\u3001\u8499\u4e50\u3001\u65bd\u65b0\u98de\u3001\u5e2d\u82e5\u7433\u4ee5\u53ca\u4ee4\u5c0f\u71d5\u3001\u79e6\u521a\u90fd\u7ad9\u90a3\u91cc\uff0c\u96be\u4ee5\u53d6\u51b3\u3002</p><p>\u3000\u3000\u79e6\u521a\u5728\u4e00\u65c1\u8bf4\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u901a\u77e5\u8bf4\u7981\u6b62\u4f20\u64ad\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u7684\u6d88\u606f\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u5c06\u4e00\u4e9b\u8a00\u8bed\u8fc7\u4e8e\u6fc0\u70c8\u7684\u5e16\u5b50\u53ca\u65f6\u6e05\u7406\u6389\u5c31\u53ef\u4ee5\u4e86\uff0c\u81f3\u4e8e\u8c01\u53d1\u7684\u5e16\u5b50\uff0c\u6211\u770b\u4e5f\u6ca1\u6709\u5fc5\u8981\u8ffd\u7a76\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c0f\u79e6\uff0c\u4f60\u505a\u5b66\u751f\u5de5\u4f5c\u8fd9\u4e48\u591a\u5e74\u4e86\uff0c\u5c31\u4e00\u70b9\u8b66\u60d5\u6027\u90fd\u6ca1\u6709\uff0c\u5c31\u4e00\u70b9\u89c9\u609f\u90fd\u6ca1\u6709\uff1f\u51fa\u4e86\u7bd3\u5b50\uff0c\u8c01\u6765\u515c\u7740\uff1f\u4f60\u96be\u9053\u8fd8\u8981\u5bab\u4e66\u8bb0\u5e2e\u4f60\u4eec\u515c\u7740\uff1f\u4e0d\u7ba1\u901a\u77e5\u4e0a\u600e\u4e48\u8bf4\uff0c\u4e1c\u5927\u4e0d\u80fd\u51fa\u4e71\u5b50\uff0c\u8fd9\u5e16\u5b50\u575a\u51b3\u8981\u5220\uff01\u201d\u6c88\u5065\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5e16\u5b50\u662f\u6211\u4e0a\u4f20\u7684\uff0c\u201d\u5f20\u606a\u4f9d\u95e8\u800c\u7ad9\uff0c\u76ee\u5149\u626b\u8fc7\u4f17\u4eba\uff0c\u53c8\u505c\u5728\u6c88\u5065\u7684\u8138\u4e0a\uff0c\u201c\u6211\u53eb\u5f20\u606a\uff0c\u60f3\u5fc5\u4f60\u4e5f\u542c\u8fc7\u6211\u7684\u540d\u5b57\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u6c88\u5065\u8f6c\u8fc7\u8eab\u6765\uff0c\u7ed9\u5f20\u606a\u51cc\u4eba\u7684\u6c14\u52bf\u538b\u7740\uff0c\u4e00\u65f6\u95f4\u8bf4\u4e0d\u8bdd\u6765\u3002</p><p>\u3000\u3000\u201c\u7a0d\u9047\u5230\u4e8b\u60c5\u5c31\u60ca\u60f6\u5931\u63aa\uff0c\u552f\u6050\u5b66\u751f\u77e5\u9053\u5b9e\u60c5\u4f1a\u95f9\u51fa\u5927\u4e71\u5b50\u3002\u6709\u5173\u90e8\u95e8\u8981\u6c42\u5404\u5927\u5a92\u4f53\u5bf9\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u7684\u6d88\u606f\u8981\u51b7\u5904\u7406\uff0c\u4f46\u662f\u6ca1\u6709\u8981\u6c42\u7981\u6b62\u4f20\u64ad\u76f8\u5173\u4fe1\u606f\uff1b\u6709\u5173\u90e8\u95e8\u8981\u6c42\u5b66\u6821\u505a\u597d\u5b66\u751f\u7684\u7a33\u5b9a\u5de5\u4f5c\uff0c\u4f46\u6ca1\u6709\u8981\u6c42\u5c06\u5b66\u751f\u7684\u8033\u6735\u3001\u773c\u775b\u90fd\u6342\u8d77\u6765\u2014\u2014\u4f60\u4eec\u8fd9\u4e9b\u4eba\u6240\u8c13\u7684\u8b66\u60d5\u6027\uff0c\u6240\u8c13\u7684\u89c9\u609f\uff0c\u4e0d\u8fc7\u662f\u60f3\u7740\u5c06\u8fd9\u4ee3\u5927\u5b66\u751f\u90fd\u9609\u5272\u6389\uff0c\u8ba9\u4ed6\u4eec\u50cf\u7fa4\u7ed9\u9a9f\u6389\u7684\u9a74\u5b50\u6216\u6742\u4ea4\u7684\u9aa1\u5b50\u4e00\u6837\u4e0d\u7ed9\u4f60\u4eec\u60f9\u4e00\u70b9\u9ebb\u70e6\u624d\u7518\u5fc3\uff01\u201d\u5f20\u606a\u5484\u5484\u903c\u4eba\u7684\u76ef\u7740\u6c88\u5065\uff0c\u89c1\u4ed6\u54d1\u53e3\u65e0\u8bed\uff0c\u624d\u4fa7\u8fc7\u5934\u5bf9\u675c\u98de\u8bf4\u9053\uff0c\u201c\u5176\u4ed6\u7684\u5e16\u5b50\u8981\u4e0d\u8981\u6e05\u7406\u968f\u4f60\u4eec\uff0c\u6211\u7684\u5e16\u5b50\u4fdd\u7559\u7740\uff0c\u8c01\u60f3\u8ffd\u7a76\u8d23\u4efb\uff0c\u8ba9\u4ed6\u6765\u627e\u6211\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u770b\u6c88\u5065\u7684\u8138\u8272\uff0c\u4ed6\u81ea\u5df1\u7684\u8138\u8272\u4e5f\u662f\u94c1\u9752\uff0c\u76ee\u5149\u51b7\u7ae3\u7684\u626b\u8fc7\u529e\u516c\u5ba4\u7684\u4f17\u4eba\uff0c\u8f6c\u8eab\u53c8\u8d70\u4e86\u51fa\u53bb\u3002</p><p>\u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\u6001\u5ea6\uff01\u8fd9\u4e2a\u5b66\u751f\u662f\u56fd\u5546\u9662\u6709\u547d\u7684\u523a\u5934\uff0c\u6211\u7b49\u4f1a\u513f\u53bb\u627e\u5d14\u56fd\u6052\uff0c\u770b\u4ed6\u600e\u4e48\u5904\u7406\uff1f\u201d\u6c88\u5065\u5728\u5f20\u606a\u8f6c\u8eab\u8d70\u540e\u624d\u60f3\u8d77\u8981\u53d1\u706b\uff0c\u706b\u5192\u4e09\u4e08\u7684\u770b\u7740\u5bab\u5982\u6625\uff0c\u5374\u4e0d\u5f97\u4e0d\u538b\u7740\u58f0\u97f3\uff0c\u8bf4\u9053\uff0c\u201c\u8fd8\u6709\u8fd9\u5e16\u5b50\uff0c\u5bab\u4e66\u8bb0\uff0c\u4f60\u662f\u521b\u534f\u8ddf\u6821\u56ed\u7f51\u7684\u6307\u5bfc\u8001\u5e08\uff0c\u4f60\u8bf4\u600e\u4e48\u5904\u7406\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u4f9d\u636e\u554a\uff0c\u4e5f\u4e0d\u597d\u5f3a\u5236\u7981\u6b62\uff0c\u201d\u5bab\u5982\u6625\u6de1\u6de1\u7684\u8bf4\u9053\uff0c\u201c\u518d\u8bf4\u73b0\u5728\u5220\u5e16\u4e5f\u4e0d\u4e00\u5b9a\u5c31\u6709\u5229\u4e8e\u505a\u597d\u5b66\u751f\u7684\u7a33\u5b9a\u5de5\u4f5c\uff0c\u5927\u5b57\u62a5\u90fd\u8d34\u5230\u6d77\u62a5\u5899\u4e0a\u53bb\u4e86\uff0c\u8fd9\u4ef6\u4e8b\uff0c\u6211\u4eec\u8fd8\u662f\u8ddf\u674e\u6821\u957f\u6c47\u62a5\u4e4b\u540e\u518d\u505a\u51b3\u5b9a\u5427\u3002\u201d</p><p>\u3000\u3000\u6c88\u5065\u5c06\u6b64\u4e8b\u770b\u6210\u662f\u706b\u70e7\u7709\u6bdb\u7684\u5927\u4e8b\uff0c\u89c1\u5bab\u5982\u6625\u4e00\u4ed8\u6162\u817e\u817e\u7684\u6837\u5b50\uff0c\u5fc3\u91cc\u7126\u6025\u53c8\u4e0d\u80fd\u8ddf\u5bab\u5982\u6625\u53d1\u813e\u6c14\uff0c\u6b32\u8a00\u53c8\u6b62\uff0c\u7529\u4e86\u7529\u624b\u4e5f\u8d70\u6389\u4e86\u3002</p><p>\u3000\u3000\u5bab\u5982\u6625\u8fd9\u624d\u5429\u5490\u79e6\u521a\uff0c\u8bf4\u9053\uff1a\u201c\u5c06\u4e00\u4e9b\u8a00\u8f9e\u8fc7\u4e8e\u504f\u6fc0\u7684\u5e16\u5b50\u8981\u53ca\u65f6\u6e05\u7406\u6389\uff0c\u4f60\u6700\u597d\u80fd\u5b89\u6392\u4e24\u5230\u4e09\u4e2a\u4eba\u4e13\u95e8\u503c\u5b88\uff0c\u6211\u60f3\u5b66\u6821\u9a6c\u4e0a\u4f1a\u53ec\u5f00\u4e13\u95e8\u7684\u4f1a\u8bae\uff0c\u5c31\u7b97\u5b66\u6821\u4e0d\u53ec\u5f00\u4f1a\u8bae\uff0c\u56e2\u59d4\u91cc\u4e5f\u8981\u53ec\u5f00\u4f1a\u8bae\uff0c\u4f60\u8981\u8ba9\u6211\u80fd\u53ca\u65f6\u8054\u7edc\u4e0a\u4f60\u7684\u4eba\u3002\u201d</p><p>\u3000\u3000\u5bab\u5982\u6625\u867d\u8bf4\u4e0d\u81f3\u4e8e\u50cf\u6c88\u5065\u90a3\u4e48\u8f6f\u86cb\uff0c\u4f46\u662f\u63a7\u5236\u4e0d\u4f4f\u5b66\u751f\u60c5\u7eea\u6fc0\u52a8\u4e0a\u8857\u6e38\u884c\uff0c\u8fd8\u662f\u4e00\u4ef6\u76f8\u5f53\u9ebb\u70e6\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u8d70\u8fdb\u4e00\u5bb6\u7ca5\u5e97\u5403\u65e9\u996d\uff0c\u4ed6\u5c41\u80a1\u8fd8\u6ca1\u6709\u5750\u70ed\uff0c\u9646\u6587\u592b\u7684\u7535\u8bdd\u5c31\u6253\u4e86\u8fdb\u6765\u3002</p><p>\u3000\u3000\u6c88\u5065\u6253\u7535\u8bdd\u8ddf\u5d14\u56fd\u6052\u544a\u72b6\uff0c\u5f20\u606a\u6345\u4e86\u7bd3\u5b50\uff0c\u5d14\u56fd\u6052\u53ea\u80fd\u5148\u5c06\u4e8b\u60c5\u8f6c\u544a\u9646\u6587\u592b\uff0c\u8fd9\u4f3c\u4e4e\u5df2\u7ecf\u6210\u4e3a\u4e86\u4e60\u60ef\u3002</p><p>\u3000\u3000\u201c\u6606\u817e\u5728\u7ebf\u4e0e\u4e2d\u592e\u6709\u5173\u90e8\u95e8\u9ad8\u5ea6\u4fdd\u6301\u4e00\u81f4\uff0c\u53ca\u65f6\u7684\u5bf9\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u7684\u65b0\u95fb\u8fdb\u884c\u4e86\u51b7\u5904\u7406\uff0c\u6ca1\u6709\u8fdb\u884c\u9ad8\u5bc6\u5ea6\u7684\u8ffd\u8e2a\u62a5\u9053\uff1b\u6211\u73b0\u5728\u53ea\u662f\u4ee5\u4e00\u540d\u5b66\u751f\u7684\u8eab\u4efd\u5728\u4e1c\u5927BBS\u4e0a\u53d1\u90a3\u4e2a\u5e16\u5b50\uff0c\u8981\u662f\u4e1c\u5927\u6821\u65b9\u7ecf\u8fc7\u7814\u7a76\u505a\u51fa\u51b3\u5b9a\u8ba4\u4e3a\u53d1\u90a3\u6837\u7684\u5e16\u5b50\u4e0d\u662f\u5408\u9002\u7684\uff0c\u6211\u4e5f\u4f1a\u79ef\u6781\u7684\u914d\u5408\u5220\u5e16\u5b50\u3002\u201d\u5f20\u606a\u5c06\u624b\u673a\u5939\u5728\u8116\u5b50\u91cc\uff0c\u5750\u5728\u5851\u6599\u6905\u6b63\u7981\u5371\u5750\u7684\u5265\u4e94\u9999\u86cb\uff0c\u8ddf\u9646\u6587\u592b\u901a\u7740\u7535\u8bdd\uff0c\u5fc3\u91cc\u5374\u85cf\u7740\u5de8\u5927\u7684\u7591\u95ee\uff1a\u4e3a\u4ec0\u4e48\u90a3\u4e48\u591a\u4eba\u90fd\u9009\u62e9\u4ee5\u81ea\u6211\u9609\u5272\u7684\u65b9\u5f0f\u751f\u5b58\u7740\uff1f\u8fd9\u4e2a\u60ac\u5728\u6574\u4e2a\u6c11\u65cf\u4e0a\u7a7a\u7684\u5de8\u5927\u5fc3\u7406\u9634\u5f71\u5f80\u524d\u8ffd\u6eaf\u4f3c\u4e4e\u53ef\u4ee5\u8ffd\u6eaf\u5230\u6e05\u671d\u7684\u6587\u5b57\u72f1\u4f20\u7edf\u3002</p><p>\u3000\u3000\u6070\u5982\u5bab\u5982\u6625\u6240\u8bf4\uff0c\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u5220\u4e0d\u5220\u5e16\u7684\u95ee\u9898\uff0c\u5927\u5b57\u7eb8\u5df2\u7ecf\u98de\u5feb\u7684\u8d34\u904d\u6821\u56ed\u7684\u6bcf\u4e00\u4e2a\u89d2\u843d\uff0c\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u7684\u65b0\u95fb\u56fe\u7247\u4e5f\u5927\u91cf\u590d\u5370\u5728\u6821\u56ed\u91cc\u5927\u8086\u4f20\u53d1\u3002\u5230\u4e2d\u5348\u6765\uff0c\u60b2\u6124\u60c5\u7eea\u5f97\u4e0d\u5230\u53d1\u6cc4\u7684\u5b66\u751f\u4eec\u4f17\u60c5\u6c79\u6d8c\u7684\u8981\u4e0a\u8857\u6e38\u884c\uff0c\u6821\u957f\u674e\u9e3f\u94ed\u4eb2\u81ea\u5835\u5728\u6821\u95e8\u53e3\u529d\u963b\u5b66\u751f\u3002</p><p>\u3000\u3000\u5f20\u606a\u6b64\u65f6\u5df2\u7ecf\u5728\u98de\u5f80\u9999\u6e2f\u7684\u98de\u673a\u4e0a\u4e86\uff0c\u4e0b\u98de\u673a\u540e\u8ddf\u675c\u98de\u901a\u7535\u8bdd\u542c\u8bf4\u6700\u7ec8\u8fd8\u662f\u6ca1\u6709\u80fd\u591f\u4e0a\u8857\u6e38\u884c\uff0c\u5b66\u6821\u7ec4\u7ec7\u5404\u9662\u7cfb\u7684\u5b66\u751f\u5728\u5927\u64cd\u573a\u4e3e\u884c\u4e86\u6297\u8bae\u6d3b\u52a8\uff0c\u7b97\u662f\u53d1\u6cc4\u4e86\u60c5\u7eea\u3002</p></article>"}}}]);
